('083:030', 83, 30, 'وَإِذَا مَرُّوا۟ بِهِمْ يَتَغَامَزُونَ', 'وإذا مروا بهم يتغامزون', 30, 59, 588, 235),
('083:031', 83, 31, 'وَإِذَا ٱنقَلَبُوٓا۟ إِلَىٰٓ أَهْلِهِمُ ٱنقَلَبُوا۟ فَكِهِينَ', 'وإذا انقلبوا إلى أهلهم انقلبوا فكهين', 30, 59, 588, 235),
('083:032', 83, 32, 'وَإِذَا رَأَوْهُمْ قَالُوٓا۟ إِنَّ هَٰٓؤُلَآءِ لَضَآلُّونَ', 'وإذا رأوهم قالوا إن هؤلاء لضالون', 30, 59, 588, 235),
('083:033', 83, 33, 'وَمَآ أُرْسِلُوا۟ عَلَيْهِمْ حَٰفِظِينَ', 'وما أرسلوا عليهم حافظين', 30, 59, 588, 235),
('083:034', 83, 34, 'فَٱلْيَوْمَ ٱلَّذِينَ ءَامَنُوا۟ مِنَ ٱلْكُفَّارِ يَضْحَكُونَ', 'فاليوم الذين آمنوا من الكفار يضحكون', 30, 59, 588, 235),
('083:035', 83, 35, 'عَلَى ٱلْأَرَآئِكِ يَنظُرُونَ', 'على الأرائك ينظرون', 30, 59, 589, 235),
('083:036', 83, 36, 'هَلْ ثُوِّبَ ٱلْكُفَّارُ مَا كَانُوا۟ يَفْعَلُونَ', 'هل ثوب الكفار ما كانوا يفعلون', 30, 59, 589, 235),
('084:001', 84, 1, 'إِذَا ٱلسَّمَآءُ ٱنشَقَّتْ', 'إذا السماء انشقت', 30, 59, 589, 236),
('084:002', 84, 2, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'وأذنت لربها وحقت', 30, 59, 589, 236),
('084:003', 84, 3, 'وَإِذَا ٱلْأَرْضُ مُدَّتْ', 'وإذا الأرض مدت', 30, 59, 589, 236),
('084:004', 84, 4, 'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ', 'وألقت ما فيها وتخلت', 30, 59, 589, 236),
('084:005', 84, 5, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'وأذنت لربها وحقت', 30, 59, 589, 236),
('084:006', 84, 6, 'يَٰٓأَيُّهَا ٱلْإِنسَٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَٰقِيهِ', 'يا أيها الإنسان إنك كادح إلى ربك كدحا فملاقيه', 30, 59, 589, 236),
('084:007', 84, 7, 'فَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ بِيَمِينِهِۦ', 'فأما من أوتي كتابه بيمينه', 30, 59, 589, 236),
('084:008', 84, 8, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا', 'فسوف يحاسب حسابا يسيرا', 30, 59, 589, 236),
('084:009', 84, 9, 'وَيَنقَلِبُ إِلَىٰٓ أَهْلِهِۦ مَسْرُورًا', 'وينقلب إلى أهله مسرورا', 30, 59, 589, 236),
('084:010', 84, 10, 'وَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ وَرَآءَ ظَهْرِهِۦ', 'وأما من أوتي كتابه وراء ظهره', 30, 59, 589, 236),
('084:011', 84, 11, 'فَسَوْفَ يَدْعُوا۟ ثُبُورًا', 'فسوف يدعو ثبورا', 30, 59, 589, 236),
('084:012', 84, 12, 'وَيَصْلَىٰ سَعِيرًا', 'ويصلى سعيرا', 30, 59, 589, 236),
('084:013', 84, 13, 'إِنَّهُۥ كَانَ فِىٓ أَهْلِهِۦ مَسْرُورًا', 'إنه كان في أهله مسرورا', 30, 59, 589, 236),
('084:014', 84, 14, 'إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ', 'إنه ظن أن لن يحور', 30, 59, 589, 236),
('084:015', 84, 15, 'بَلَىٰٓ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرًا', 'بلى إن ربه كان به بصيرا', 30, 59, 589, 236),
('084:016', 84, 16, 'فَلَآ أُقْسِمُ بِٱلشَّفَقِ', 'فلا أقسم بالشفق', 30, 59, 589, 236),
('084:017', 84, 17, 'وَٱلَّيْلِ وَمَا وَسَقَ', 'والليل وما وسق', 30, 59, 589, 236),
('084:018', 84, 18, 'وَٱلْقَمَرِ إِذَا ٱتَّسَقَ', 'والقمر إذا اتسق', 30, 59, 589, 236),
('084:019', 84, 19, 'لَتَرْكَبُنَّ طَبَقًا عَن طَبَقٍ', 'لتركبن طبقا عن طبق', 30, 59, 589, 236),
('084:020', 84, 20, 'فَمَا لَهُمْ لَا يُؤْمِنُونَ', 'فما لهم لا يؤمنون', 30, 59, 589, 236),
('084:021', 84, 21, 'وَإِذَا قُرِئَ عَلَيْهِمُ ٱلْقُرْءَانُ لَا يَسْجُدُونَ', 'وإذا قرئ عليهم القرآن لا يسجدون', 30, 59, 589, 236),
('084:022', 84, 22, 'بَلِ ٱلَّذِينَ كَفَرُوا۟ يُكَذِّبُونَ', 'بل الذين كفروا يكذبون', 30, 59, 589, 236),
('084:023', 84, 23, 'وَٱللَّهُ أَعْلَمُ بِمَا يُوعُونَ', 'والله أعلم بما يوعون', 30, 59, 589, 236),
('084:024', 84, 24, 'فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ', 'فبشرهم بعذاب أليم', 30, 59, 589, 236),
('084:025', 84, 25, 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍۭ', 'إلا الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون', 30, 59, 589, 236),
('085:001', 85, 1, 'وَٱلسَّمَآءِ ذَاتِ ٱلْبُرُوجِ', 'والسماء ذات البروج', 30, 59, 590, 236),
('085:002', 85, 2, 'وَٱلْيَوْمِ ٱلْمَوْعُودِ', 'واليوم الموعود', 30, 59, 590, 236),
('085:003', 85, 3, 'وَشَاهِدٍ وَمَشْهُودٍ', 'وشاهد ومشهود', 30, 59, 590, 236),
('085:004', 85, 4, 'قُتِلَ أَصْحَٰبُ ٱلْأُخْدُودِ', 'قتل أصحاب الأخدود', 30, 59, 590, 236),
('085:005', 85, 5, 'ٱلنَّارِ ذَاتِ ٱلْوَقُودِ', 'النار ذات الوقود', 30, 59, 590, 236),
('085:006', 85, 6, 'إِذْ هُمْ عَلَيْهَا قُعُودٌ', 'إذ هم عليها قعود', 30, 59, 590, 236),
('085:007', 85, 7, 'وَهُمْ عَلَىٰ مَا يَفْعَلُونَ بِٱلْمُؤْمِنِينَ شُهُودٌ', 'وهم على ما يفعلون بالمؤمنين شهود', 30, 59, 590, 236),
('085:008', 85, 8, 'وَمَا نَقَمُوا۟ مِنْهُمْ إِلَّآ أَن يُؤْمِنُوا۟ بِٱللَّهِ ٱلْعَزِيزِ ٱلْحَمِيدِ', 'وما نقموا منهم إلا أن يؤمنوا بالله العزيز الحميد', 30, 59, 590, 236),
('085:009', 85, 9, 'ٱلَّذِى لَهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ', 'الذي له ملك السماوات والأرض والله على كل شيء شهيد', 30, 59, 590, 236),
('085:010', 85, 10, 'إِنَّ ٱلَّذِينَ فَتَنُوا۟ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ ثُمَّ لَمْ يَتُوبُوا۟ فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ ٱلْحَرِيقِ', 'إن الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق', 30, 59, 590, 236),
('085:011', 85, 11, 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ جَنَّٰتٌ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ ذَٰلِكَ ٱلْفَوْزُ ٱلْكَبِيرُ', 'إن الذين آمنوا وعملوا الصالحات لهم جنات تجري من تحتها الأنهار ذلك الفوز الكبير', 30, 59, 590, 236),
('085:012', 85, 12, 'إِنَّ بَطْشَ رَبِّكَ لَشَدِيدٌ', 'إن بطش ربك لشديد', 30, 59, 590, 236),
('085:013', 85, 13, 'إِنَّهُۥ هُوَ يُبْدِئُ وَيُعِيدُ', 'إنه هو يبدئ ويعيد', 30, 59, 590, 236),
('085:014', 85, 14, 'وَهُوَ ٱلْغَفُورُ ٱلْوَدُودُ', 'وهو الغفور الودود', 30, 59, 590, 236),
('085:015', 85, 15, 'ذُو ٱلْعَرْشِ ٱلْمَجِيدُ', 'ذو العرش المجيد', 30, 59, 590, 236),
('085:016', 85, 16, 'فَعَّالٌ لِّمَا يُرِيدُ', 'فعال لما يريد', 30, 59, 590, 236),
('085:017', 85, 17, 'هَلْ أَتَىٰكَ حَدِيثُ ٱلْجُنُودِ', 'هل أتاك حديث الجنود', 30, 59, 590, 236),
('085:018', 85, 18, 'فِرْعَوْنَ وَثَمُودَ', 'فرعون وثمود', 30, 59, 590, 236),
('085:019', 85, 19, 'بَلِ ٱلَّذِينَ كَفَرُوا۟ فِى تَكْذِيبٍ', 'بل الذين كفروا في تكذيب', 30, 59, 590, 236),
('085:020', 85, 20, 'وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطٌۢ', 'والله من ورائهم محيط', 30, 59, 590, 236),
('085:021', 85, 21, 'بَلْ هُوَ قُرْءَانٌ مَّجِيدٌ', 'بل هو قرآن مجيد', 30, 59, 590, 236),
('085:022', 85, 22, 'فِى لَوْحٍ مَّحْفُوظٍۭ', 'في لوح محفوظ', 30, 59, 590, 236),
('086:001', 86, 1, 'وَٱلسَّمَآءِ وَٱلطَّارِقِ', 'والسماء والطارق', 30, 59, 591, 236),
('086:002', 86, 2, 'وَمَآ أَدْرَىٰكَ مَا ٱلطَّارِقُ', 'وما أدراك ما الطارق', 30, 59, 591, 236),
('086:003', 86, 3, 'ٱلنَّجْمُ ٱلثَّاقِبُ', 'النجم الثاقب', 30, 59, 591, 236),
('086:004', 86, 4, 'إِن كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌ', 'إن كل نفس لما عليها حافظ', 30, 59, 591, 236),
('086:005', 86, 5, 'فَلْيَنظُرِ ٱلْإِنسَٰنُ مِمَّ خُلِقَ', 'فلينظر الإنسان مم خلق', 30, 59, 591, 236),
('086:006', 86, 6, 'خُلِقَ مِن مَّآءٍ دَافِقٍ', 'خلق من ماء دافق', 30, 59, 591, 236),
('086:007', 86, 7, 'يَخْرُجُ مِنۢ بَيْنِ ٱلصُّلْبِ وَٱلتَّرَآئِبِ', 'يخرج من بين الصلب والترائب', 30, 59, 591, 236),
('086:008', 86, 8, 'إِنَّهُۥ عَلَىٰ رَجْعِهِۦ لَقَادِرٌ', 'إنه على رجعه لقادر', 30, 59, 591, 236),
('086:009', 86, 9, 'يَوْمَ تُبْلَى ٱلسَّرَآئِرُ', 'يوم تبلى السرائر', 30, 59, 591, 236),
('086:010', 86, 10, 'فَمَا لَهُۥ مِن قُوَّةٍ وَلَا نَاصِرٍ', 'فما له من قوة ولا ناصر', 30, 59, 591, 236),
('086:011', 86, 11, 'وَٱلسَّمَآءِ ذَاتِ ٱلرَّجْعِ', 'والسماء ذات الرجع', 30, 59, 591, 236),
('086:012', 86, 12, 'وَٱلْأَرْضِ ذَاتِ ٱلصَّدْعِ', 'والأرض ذات الصدع', 30, 59, 591, 236),
('086:013', 86, 13, 'إِنَّهُۥ لَقَوْلٌ فَصْلٌ', 'إنه لقول فصل', 30, 59, 591, 236),
('086:014', 86, 14, 'وَمَا هُوَ بِٱلْهَزْلِ', 'وما هو بالهزل', 30, 59, 591, 236),
('086:015', 86, 15, 'إِنَّهُمْ يَكِيدُونَ كَيْدًا', 'إنهم يكيدون كيدا', 30, 59, 591, 236),
('086:016', 86, 16, 'وَأَكِيدُ كَيْدًا', 'وأكيد كيدا', 30, 59, 591, 236),
('086:017', 86, 17, 'فَمَهِّلِ ٱلْكَٰفِرِينَ أَمْهِلْهُمْ رُوَيْدًۢا', 'فمهل الكافرين أمهلهم رويدا', 30, 59, 591, 236),
('087:001', 87, 1, 'سَبِّحِ ٱسْمَ رَبِّكَ ٱلْأَعْلَى', 'سبح اسم ربك الأعلى', 30, 60, 591, 237),
('087:002', 87, 2, 'ٱلَّذِى خَلَقَ فَسَوَّىٰ', 'الذي خلق فسوى', 30, 60, 591, 237),
('087:003', 87, 3, 'وَٱلَّذِى قَدَّرَ فَهَدَىٰ', 'والذي قدر فهدى', 30, 60, 591, 237),
('087:004', 87, 4, 'وَٱلَّذِىٓ أَخْرَجَ ٱلْمَرْعَىٰ', 'والذي أخرج المرعى', 30, 60, 591, 237),
('087:005', 87, 5, 'فَجَعَلَهُۥ غُثَآءً أَحْوَىٰ', 'فجعله غثاء أحوى', 30, 60, 591, 237),
('087:006', 87, 6, 'سَنُقْرِئُكَ فَلَا تَنسَىٰٓ', 'سنقرئك فلا تنسى', 30, 60, 591, 237),
('087:007', 87, 7, 'إِلَّا مَا شَآءَ ٱللَّهُ إِنَّهُۥ يَعْلَمُ ٱلْجَهْرَ وَمَا يَخْفَىٰ', 'إلا ما شاء الله إنه يعلم الجهر وما يخفى', 30, 60, 591, 237),
('087:008', 87, 8, 'وَنُيَسِّرُكَ لِلْيُسْرَىٰ', 'ونيسرك لليسرى', 30, 60, 591, 237),
('087:009', 87, 9, 'فَذَكِّرْ إِن نَّفَعَتِ ٱلذِّكْرَىٰ', 'فذكر إن نفعت الذكرى', 30, 60, 591, 237),
('087:010', 87, 10, 'سَيَذَّكَّرُ مَن يَخْشَىٰ', 'سيذكر من يخشى', 30, 60, 591, 237);
INSERT INTO `quran_ayat` (`ayat_key`, `surat`, `ayat`, `text`, `simple`, `juz`, `hezb`, `page`, `rub`) VALUES
('087:011', 87, 11, 'وَيَتَجَنَّبُهَا ٱلْأَشْقَى', 'ويتجنبها الأشقى', 30, 60, 591, 237),
('087:012', 87, 12, 'ٱلَّذِى يَصْلَى ٱلنَّارَ ٱلْكُبْرَىٰ', 'الذي يصلى النار الكبرى', 30, 60, 591, 237),
('087:013', 87, 13, 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ', 'ثم لا يموت فيها ولا يحيى', 30, 60, 591, 237),
('087:014', 87, 14, 'قَدْ أَفْلَحَ مَن تَزَكَّىٰ', 'قد أفلح من تزكى', 30, 60, 591, 237),
('087:015', 87, 15, 'وَذَكَرَ ٱسْمَ رَبِّهِۦ فَصَلَّىٰ', 'وذكر اسم ربه فصلى', 30, 60, 591, 237),
('087:016', 87, 16, 'بَلْ تُؤْثِرُونَ ٱلْحَيَوٰةَ ٱلدُّنْيَا', 'بل تؤثرون الحياة الدنيا', 30, 60, 592, 237),
('087:017', 87, 17, 'وَٱلْءَاخِرَةُ خَيْرٌ وَأَبْقَىٰٓ', 'والآخرة خير وأبقى', 30, 60, 592, 237),
('087:018', 87, 18, 'إِنَّ هَٰذَا لَفِى ٱلصُّحُفِ ٱلْأُولَىٰ', 'إن هذا لفي الصحف الأولى', 30, 60, 592, 237),
('087:019', 87, 19, 'صُحُفِ إِبْرَٰهِيمَ وَمُوسَىٰ', 'صحف إبراهيم وموسى', 30, 60, 592, 237),
('088:001', 88, 1, 'هَلْ أَتَىٰكَ حَدِيثُ ٱلْغَٰشِيَةِ', 'هل أتاك حديث الغاشية', 30, 60, 592, 237),
('088:002', 88, 2, 'وُجُوهٌ يَوْمَئِذٍ خَٰشِعَةٌ', 'وجوه يومئذ خاشعة', 30, 60, 592, 237),
('088:003', 88, 3, 'عَامِلَةٌ نَّاصِبَةٌ', 'عاملة ناصبة', 30, 60, 592, 237),
('088:004', 88, 4, 'تَصْلَىٰ نَارًا حَامِيَةً', 'تصلى نارا حامية', 30, 60, 592, 237),
('088:005', 88, 5, 'تُسْقَىٰ مِنْ عَيْنٍ ءَانِيَةٍ', 'تسقى من عين آنية', 30, 60, 592, 237),
('088:006', 88, 6, 'لَّيْسَ لَهُمْ طَعَامٌ إِلَّا مِن ضَرِيعٍ', 'ليس لهم طعام إلا من ضريع', 30, 60, 592, 237),
