('101:005', 101, 5, 'وَتَكُونُ ٱلْجِبَالُ كَٱلْعِهْنِ ٱلْمَنفُوشِ', 'وتكون الجبال كالعهن المنفوش', 30, 60, 600, 240),
('101:006', 101, 6, 'فَأَمَّا مَن ثَقُلَتْ مَوَٰزِينُهُۥ', 'فأما من ثقلت موازينه', 30, 60, 600, 240),
('101:007', 101, 7, 'فَهُوَ فِى عِيشَةٍ رَّاضِيَةٍ', 'فهو في عيشة راضية', 30, 60, 600, 240),
('101:008', 101, 8, 'وَأَمَّا مَنْ خَفَّتْ مَوَٰزِينُهُۥ', 'وأما من خفت موازينه', 30, 60, 600, 240),
('101:009', 101, 9, 'فَأُمُّهُۥ هَاوِيَةٌ', 'فأمه هاوية', 30, 60, 600, 240),
('101:010', 101, 10, 'وَمَآ أَدْرَىٰكَ مَا هِيَهْ', 'وما أدراك ما هيه', 30, 60, 600, 240),
('101:011', 101, 11, 'نَارٌ حَامِيَةٌۢ', 'نار حامية', 30, 60, 600, 240),
('102:001', 102, 1, 'أَلْهَىٰكُمُ ٱلتَّكَاثُرُ', 'ألهاكم التكاثر', 30, 60, 600, 240),
('102:002', 102, 2, 'حَتَّىٰ زُرْتُمُ ٱلْمَقَابِرَ', 'حتى زرتم المقابر', 30, 60, 600, 240),
('102:003', 102, 3, 'كَلَّا سَوْفَ تَعْلَمُونَ', 'كلا سوف تعلمون', 30, 60, 600, 240),
('102:004', 102, 4, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ', 'ثم كلا سوف تعلمون', 30, 60, 600, 240),
('102:005', 102, 5, 'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ ٱلْيَقِينِ', 'كلا لو تعلمون علم اليقين', 30, 60, 600, 240),
('102:006', 102, 6, 'لَتَرَوُنَّ ٱلْجَحِيمَ', 'لترون الجحيم', 30, 60, 600, 240),
('102:007', 102, 7, 'ثُمَّ لَتَرَوُنَّهَا عَيْنَ ٱلْيَقِينِ', 'ثم لترونها عين اليقين', 30, 60, 600, 240),
('102:008', 102, 8, 'ثُمَّ لَتُسْـَٔلُنَّ يَوْمَئِذٍ عَنِ ٱلنَّعِيمِ', 'ثم لتسألن يومئذ عن النعيم', 30, 60, 600, 240),
('103:001', 103, 1, 'وَٱلْعَصْرِ', 'والعصر', 30, 60, 601, 240),
('103:002', 103, 2, 'إِنَّ ٱلْإِنسَٰنَ لَفِى خُسْرٍ', 'إن الإنسان لفي خسر', 30, 60, 601, 240),
('103:003', 103, 3, 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَتَوَاصَوْا۟ بِٱلْحَقِّ وَتَوَاصَوْا۟ بِٱلصَّبْرِ', 'إلا الذين آمنوا وعملوا الصالحات وتواصوا بالحق وتواصوا بالصبر', 30, 60, 601, 240),
('104:001', 104, 1, 'وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍ', 'ويل لكل همزة لمزة', 30, 60, 601, 240),
('104:002', 104, 2, 'ٱلَّذِى جَمَعَ مَالًا وَعَدَّدَهُۥ', 'الذي جمع مالا وعدده', 30, 60, 601, 240),
('104:003', 104, 3, 'يَحْسَبُ أَنَّ مَالَهُۥٓ أَخْلَدَهُۥ', 'يحسب أن ماله أخلده', 30, 60, 601, 240),
('104:004', 104, 4, 'كَلَّا لَيُنۢبَذَنَّ فِى ٱلْحُطَمَةِ', 'كلا لينبذن في الحطمة', 30, 60, 601, 240),
('104:005', 104, 5, 'وَمَآ أَدْرَىٰكَ مَا ٱلْحُطَمَةُ', 'وما أدراك ما الحطمة', 30, 60, 601, 240),
('104:006', 104, 6, 'نَارُ ٱللَّهِ ٱلْمُوقَدَةُ', 'نار الله الموقدة', 30, 60, 601, 240),
('104:007', 104, 7, 'ٱلَّتِى تَطَّلِعُ عَلَى ٱلْأَفْـِٔدَةِ', 'التي تطلع على الأفئدة', 30, 60, 601, 240),
('104:008', 104, 8, 'إِنَّهَا عَلَيْهِم مُّؤْصَدَةٌ', 'إنها عليهم مؤصدة', 30, 60, 601, 240),
('104:009', 104, 9, 'فِى عَمَدٍ مُّمَدَّدَةٍۭ', 'في عمد ممددة', 30, 60, 601, 240),
('105:001', 105, 1, 'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَٰبِ ٱلْفِيلِ', 'ألم تر كيف فعل ربك بأصحاب الفيل', 30, 60, 601, 240),
('105:002', 105, 2, 'أَلَمْ يَجْعَلْ كَيْدَهُمْ فِى تَضْلِيلٍ', 'ألم يجعل كيدهم في تضليل', 30, 60, 601, 240),
('105:003', 105, 3, 'وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ', 'وأرسل عليهم طيرا أبابيل', 30, 60, 601, 240),
('105:004', 105, 4, 'تَرْمِيهِم بِحِجَارَةٍ مِّن سِجِّيلٍ', 'ترميهم بحجارة من سجيل', 30, 60, 601, 240),
('105:005', 105, 5, 'فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُولٍۭ', 'فجعلهم كعصف مأكول', 30, 60, 601, 240),
('106:001', 106, 1, 'لِإِيلَٰفِ قُرَيْشٍ', 'لإيلاف قريش', 30, 60, 602, 240),
('106:002', 106, 2, 'إِۦلَٰفِهِمْ رِحْلَةَ ٱلشِّتَآءِ وَٱلصَّيْفِ', 'إيلافهم رحلة الشتاء والصيف', 30, 60, 602, 240),
('106:003', 106, 3, 'فَلْيَعْبُدُوا۟ رَبَّ هَٰذَا ٱلْبَيْتِ', 'فليعبدوا رب هذا البيت', 30, 60, 602, 240),
('106:004', 106, 4, 'ٱلَّذِىٓ أَطْعَمَهُم مِّن جُوعٍ وَءَامَنَهُم مِّنْ خَوْفٍۭ', 'الذي أطعمهم من جوع وآمنهم من خوف', 30, 60, 602, 240),
('107:001', 107, 1, 'أَرَءَيْتَ ٱلَّذِى يُكَذِّبُ بِٱلدِّينِ', 'أرأيت الذي يكذب بالدين', 30, 60, 602, 240),
('107:002', 107, 2, 'فَذَٰلِكَ ٱلَّذِى يَدُعُّ ٱلْيَتِيمَ', 'فذلك الذي يدع اليتيم', 30, 60, 602, 240),
('107:003', 107, 3, 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ', 'ولا يحض على طعام المسكين', 30, 60, 602, 240),
('107:004', 107, 4, 'فَوَيْلٌ لِّلْمُصَلِّينَ', 'فويل للمصلين', 30, 60, 602, 240),
('107:005', 107, 5, 'ٱلَّذِينَ هُمْ عَن صَلَاتِهِمْ سَاهُونَ', 'الذين هم عن صلاتهم ساهون', 30, 60, 602, 240),
('107:006', 107, 6, 'ٱلَّذِينَ هُمْ يُرَآءُونَ', 'الذين هم يراءون', 30, 60, 602, 240),
('107:007', 107, 7, 'وَيَمْنَعُونَ ٱلْمَاعُونَ', 'ويمنعون الماعون', 30, 60, 602, 240),
('108:001', 108, 1, 'إِنَّآ أَعْطَيْنَٰكَ ٱلْكَوْثَرَ', 'إنا أعطيناك الكوثر', 30, 60, 602, 240),
('108:002', 108, 2, 'فَصَلِّ لِرَبِّكَ وَٱنْحَرْ', 'فصل لربك وانحر', 30, 60, 602, 240),
('108:003', 108, 3, 'إِنَّ شَانِئَكَ هُوَ ٱلْأَبْتَرُ', 'إن شانئك هو الأبتر', 30, 60, 602, 240),
('109:001', 109, 1, 'قُلْ يَٰٓأَيُّهَا ٱلْكَٰفِرُونَ', 'قل يا أيها الكافرون', 30, 60, 603, 240),
('109:002', 109, 2, 'لَآ أَعْبُدُ مَا تَعْبُدُونَ', 'لا أعبد ما تعبدون', 30, 60, 603, 240),
('109:003', 109, 3, 'وَلَآ أَنتُمْ عَٰبِدُونَ مَآ أَعْبُدُ', 'ولا أنتم عابدون ما أعبد', 30, 60, 603, 240),
('109:004', 109, 4, 'وَلَآ أَنَا۠ عَابِدٌ مَّا عَبَدتُّمْ', 'ولا أنا عابد ما عبدتم', 30, 60, 603, 240),
('109:005', 109, 5, 'وَلَآ أَنتُمْ عَٰبِدُونَ مَآ أَعْبُدُ', 'ولا أنتم عابدون ما أعبد', 30, 60, 603, 240),
('109:006', 109, 6, 'لَكُمْ دِينُكُمْ وَلِىَ دِينِ', 'لكم دينكم ولي دين', 30, 60, 603, 240),
('110:001', 110, 1, 'إِذَا جَآءَ نَصْرُ ٱللَّهِ وَٱلْفَتْحُ', 'إذا جاء نصر الله والفتح', 30, 60, 603, 240),
('110:002', 110, 2, 'وَرَأَيْتَ ٱلنَّاسَ يَدْخُلُونَ فِى دِينِ ٱللَّهِ أَفْوَاجًا', 'ورأيت الناس يدخلون في دين الله أفواجا', 30, 60, 603, 240),
('110:003', 110, 3, 'فَسَبِّحْ بِحَمْدِ رَبِّكَ وَٱسْتَغْفِرْهُ إِنَّهُۥ كَانَ تَوَّابًۢا', 'فسبح بحمد ربك واستغفره إنه كان توابا', 30, 60, 603, 240),
('111:001', 111, 1, 'تَبَّتْ يَدَآ أَبِى لَهَبٍ وَتَبَّ', 'تبت يدا أبي لهب وتب', 30, 60, 603, 240),
('111:002', 111, 2, 'مَآ أَغْنَىٰ عَنْهُ مَالُهُۥ وَمَا كَسَبَ', 'ما أغنى عنه ماله وما كسب', 30, 60, 603, 240),
('111:003', 111, 3, 'سَيَصْلَىٰ نَارًا ذَاتَ لَهَبٍ', 'سيصلى نارا ذات لهب', 30, 60, 603, 240),
('111:004', 111, 4, 'وَٱمْرَأَتُهُۥ حَمَّالَةَ ٱلْحَطَبِ', 'وامرأته حمالة الحطب', 30, 60, 603, 240),
('111:005', 111, 5, 'فِى جِيدِهَا حَبْلٌ مِّن مَّسَدٍۭ', 'في جيدها حبل من مسد', 30, 60, 603, 240),
('112:001', 112, 1, 'قُلْ هُوَ ٱللَّهُ أَحَدٌ', 'قل هو الله أحد', 30, 60, 604, 240),
('112:002', 112, 2, 'ٱللَّهُ ٱلصَّمَدُ', 'الله الصمد', 30, 60, 604, 240),
('112:003', 112, 3, 'لَمْ يَلِدْ وَلَمْ يُولَدْ', 'لم يلد ولم يولد', 30, 60, 604, 240),
('112:004', 112, 4, 'وَلَمْ يَكُن لَّهُۥ كُفُوًا أَحَدٌۢ', 'ولم يكن له كفوا أحد', 30, 60, 604, 240),
('113:001', 113, 1, 'قُلْ أَعُوذُ بِرَبِّ ٱلْفَلَقِ', 'قل أعوذ برب الفلق', 30, 60, 604, 240),
('113:002', 113, 2, 'مِن شَرِّ مَا خَلَقَ', 'من شر ما خلق', 30, 60, 604, 240),
('113:003', 113, 3, 'وَمِن شَرِّ غَاسِقٍ إِذَا وَقَبَ', 'ومن شر غاسق إذا وقب', 30, 60, 604, 240),
('113:004', 113, 4, 'وَمِن شَرِّ ٱلنَّفَّٰثَٰتِ فِى ٱلْعُقَدِ', 'ومن شر النفاثات في العقد', 30, 60, 604, 240),
('113:005', 113, 5, 'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ', 'ومن شر حاسد إذا حسد', 30, 60, 604, 240),
('114:001', 114, 1, 'قُلْ أَعُوذُ بِرَبِّ ٱلنَّاسِ', 'قل أعوذ برب الناس', 30, 60, 604, 240),
('114:002', 114, 2, 'مَلِكِ ٱلنَّاسِ', 'ملك الناس', 30, 60, 604, 240),
('114:003', 114, 3, 'إِلَٰهِ ٱلنَّاسِ', 'إله الناس', 30, 60, 604, 240),
('114:004', 114, 4, 'مِن شَرِّ ٱلْوَسْوَاسِ ٱلْخَنَّاسِ', 'من شر الوسواس الخناس', 30, 60, 604, 240),
('114:005', 114, 5, 'ٱلَّذِى يُوَسْوِسُ فِى صُدُورِ ٱلنَّاسِ', 'الذي يوسوس في صدور الناس', 30, 60, 604, 240),
('114:006', 114, 6, 'مِنَ ٱلْجِنَّةِ وَٱلنَّاسِ', 'من الجنة والناس', 30, 60, 604, 240);
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1, '001:001', '68', 1, 1, 'Dengan menyebut nama Allah Yang Maha Pemurah lagi Maha Penyayang.'),
(2, '001:002', '68', 1, 2, 'Segala puji bagi Allah, Tuhan semesta alam.'),
(3, '001:003', '68', 1, 3, 'Maha Pemurah lagi Maha Penyayang.'),
(4, '001:004', '68', 1, 4, 'Yang menguasai di Hari Pembalasan.'),
(5, '001:005', '68', 1, 5, 'Hanya Engkaulah yang kami sembah, dan hanya kepada Engkaulah kami meminta pertolongan.'),
(6, '001:006', '68', 1, 6, 'Tunjukilah kami jalan yang lurus,'),
(7, '001:007', '68', 1, 7, '(yaitu) Jalan orang-orang yang telah Engkau beri nikmat kepada mereka; bukan (jalan) mereka yang dimurkai dan bukan (pula jalan) mereka yang sesat.'),
(8, '002:001', '68', 2, 1, 'Alif laam miim.'),
(9, '002:002', '68', 2, 2, 'Kitab (Al Quran) ini tidak ada keraguan padanya; petunjuk bagi mereka yang bertakwa,'),
(10, '002:003', '68', 2, 3, '(yaitu) mereka yang beriman kepada yang ghaib, yang mendirikan shalat, dan menafkahkan sebahagian rezeki yang Kami anugerahkan kepada mereka.'),
(11, '002:004', '68', 2, 4, 'dan mereka yang beriman kepada Kitab (Al Quran) yang telah diturunkan kepadamu dan Kitab-kitab yang telah diturunkan sebelummu, serta mereka yakin akan adanya (kehidupan) akhirat.'),
(12, '002:005', '68', 2, 5, 'Mereka itulah yang tetap mendapat petunjuk dari Tuhan mereka, dan merekalah orang-orang yang beruntung.'),
(13, '002:006', '68', 2, 6, 'Sesungguhnya orang-orang kafir, sama saja bagi mereka, kamu beri peringatan atau tidak kamu beri peringatan, mereka tidak juga akan beriman.'),
(14, '002:007', '68', 2, 7, 'Allah telah mengunci-mati hati dan pendengaran mereka, dan penglihatan mereka ditutup. Dan bagi mereka siksa yang amat berat.'),
(15, '002:008', '68', 2, 8, 'Di antara manusia ada yang mengatakan: \"Kami beriman kepada Allah dan Hari kemudian,\" pada hal mereka itu sesungguhnya bukan orang-orang yang beriman.'),
(16, '002:009', '68', 2, 9, 'Mereka hendak menipu Allah dan orang-orang yang beriman, padahal mereka hanya menipu dirinya sendiri sedang mereka tidak sadar.'),
(17, '002:010', '68', 2, 10, 'Dalam hati mereka ada penyakit, lalu ditambah Allah penyakitnya; dan bagi mereka siksa yang pedih, disebabkan mereka berdusta.'),
(18, '002:011', '68', 2, 11, 'Dan bila dikatakan kepada mereka: \"Janganlah kamu membuat kerusakan di muka bumi\". Mereka menjawab: \"Sesungguhnya kami orang-orang yang mengadakan perbaikan\".'),
(19, '002:012', '68', 2, 12, 'Ingatlah, sesungguhnya mereka itulah orang-orang yang membuat kerusakan, tetapi mereka tidak sadar.'),
(20, '002:013', '68', 2, 13, 'Apabila dikatakan kepada mereka: \"Berimanlah kamu sebagaimana orang-orang lain telah beriman\". Mereka menjawab: \"Akan berimankah kami sebagaimana orang-orang yang bodoh itu telah beriman?\" Ingatlah, sesungguhnya merekalah orang-orang yang bodoh; tetapi mereka tidak tahu.'),
(21, '002:014', '68', 2, 14, 'Dan bila mereka berjumpa dengan orang-orang yang beriman, mereka mengatakan: \"Kami telah beriman\". Dan bila mereka kembali kepada syaitan-syaitan mereka, mereka mengatakan: \"Sesungguhnya kami sependirian dengan kamu, kami hanyalah berolok-olok\".'),
(22, '002:015', '68', 2, 15, 'Allah akan (membalas) olok-olokan mereka dan membiarkan mereka terombang-ambing dalam kesesatan mereka.'),
(23, '002:016', '68', 2, 16, 'Mereka itulah orang yang membeli kesesatan dengan petunjuk, maka tidaklah beruntung perniagaan mereka dan tidaklah mereka mendapat petunjuk.'),
(24, '002:017', '68', 2, 17, 'Perumpamaan mereka adalah seperti orang yang menyalakan api, maka setelah api itu menerangi sekelilingnya Allah hilangkan cahaya (yang menyinari) mereka, dan membiarkan mereka dalam kegelapan, tidak dapat melihat.'),
(25, '002:018', '68', 2, 18, 'Mereka tuli, bisu dan buta, maka tidaklah mereka akan kembali (ke jalan yang benar),'),
(26, '002:019', '68', 2, 19, 'atau seperti (orang-orang yang ditimpa) hujan lebat dari langit disertai gelap gulita, guruh dan kilat; mereka menyumbat telinganya dengan anak jarinya, karena (mendengar suara) petir, sebab takut akan mati. Dan Allah meliputi orang-orang yang kafir.'),
(27, '002:020', '68', 2, 20, 'Hampir-hampir kilat itu menyambar penglihatan mereka. Setiap kali kilat itu menyinari mereka, mereka berjalan di bawah sinar itu, dan bila gelap menimpa mereka, mereka berhenti. Jikalau Allah menghendaki, niscaya Dia melenyapkan pendengaran dan penglihatan mereka. Sesungguhnya Allah berkuasa atas segala sesuatu.'),
(28, '002:021', '68', 2, 21, 'Hai manusia, sembahlah Tuhanmu yang telah menciptakanmu dan orang-orang yang sebelummu, agar kamu bertakwa,'),
(29, '002:022', '68', 2, 22, 'Dialah yang menjadikan bumi sebagai hamparan bagimu dan langit sebagai atap, dan Dia menurunkan air (hujan) dari langit, lalu Dia menghasilkan dengan hujan itu segala buah-buahan sebagai rezeki untukmu; karena itu janganlah kamu mengadakan sekutu-sekutu bagi Allah, padahal kamu mengetahui.'),
(30, '002:023', '68', 2, 23, 'Dan jika kamu (tetap) dalam keraguan tentang Al Quran yang Kami wahyukan kepada hamba Kami (Muhammad), buatlah satu surat (saja) yang semisal Al Quran itu dan ajaklah penolong-penolongmu selain Allah, jika kamu orang-orang yang benar.'),
(31, '002:024', '68', 2, 24, 'Maka jika kamu tidak dapat membuat(nya) -- dan pasti kamu tidak akan dapat membuat(nya), peliharalah dirimu dari neraka yang bahan bakarnya manusia dan batu, yang disediakan bagi orang-orang kafir.'),
(32, '002:025', '68', 2, 25, 'Dan sampaikanlah berita gembira kepada mereka yang beriman dan berbuat baik, bahwa bagi mereka disediakan surga-surga yang mengalir sungai-sungai di dalamnya. Setiap mereka diberi rezeki buah-buahan dalam surga-surga itu, mereka mengatakan: \"Inilah yang pernah diberikan kepada kami dahulu\". Mereka diberi buah-buahan yang serupa dan untuk mereka di dalamnya ada isteri-isteri yang suci dan mereka kekal di dalamnya.'),
(33, '002:026', '68', 2, 26, 'Sesungguhnya Allah tiada segan membuat perumpamaan berupa nyamuk atau yang lebih rendah dari itu. Adapun orang-orang yang beriman, maka mereka yakin bahwa perumpamaan itu benar dari Tuhan mereka, tetapi mereka yang kafir mengatakan: \"Apakah maksud Allah menjadikan ini untuk perumpamaan?\". Dengan perumpamaan itu banyak orang yang disesatkan Allah, dan dengan perumpamaan itu (pula) banyak orang yang diberi-Nya petunjuk. Dan tidak ada yang disesatkan Allah kecuali orang-orang yang fasik,'),
(34, '002:027', '68', 2, 27, '(yaitu) orang-orang yang melanggar perjanjian Allah sesudah perjanjian itu teguh, dan memutuskan apa yang diperintahkan Allah (kepada mereka) untuk menghubungkannya dan membuat kerusakan di muka bumi. Mereka itulah orang-orang yang rugi.'),
(35, '002:028', '68', 2, 28, 'Mengapa kamu kafir kepada Allah, padahal kamu tadinya mati, lalu Allah menghidupkan kamu, kemudian kamu dimatikan dan dihidupkan-Nya kembali, kemudian kepada-Nya-lah kamu dikembalikan?'),
(36, '002:029', '68', 2, 29, 'Dialah Allah, yang menjadikan segala yang ada di bumi untuk kamu dan Dia berkehendak (menciptakan) langit, lalu dijadikan-Nya tujuh langit. Dan Dia Maha Mengetahui segala sesuatu.'),
(37, '002:030', '68', 2, 30, 'Ingatlah ketika Tuhanmu berfirman kepada para Malaikat: \"Sesungguhnya Aku hendak menjadikan seorang khalifah di muka bumi\". Mereka berkata: \"Mengapa Engkau hendak menjadikan (khalifah) di bumi itu orang yang akan membuat kerusakan padanya dan menumpahkan darah, padahal kami senantiasa bertasbih dengan memuji Engkau dan mensucikan Engkau?\" Tuhan berfirman: \"Sesungguhnya Aku mengetahui apa yang tidak kamu ketahui\".'),
(38, '002:031', '68', 2, 31, 'Dan Dia mengajarkan kepada Adam nama-nama (benda-benda) seluruhnya, kemudian mengemukakannya kepada para Malaikat lalu berfirman: \"Sebutkanlah kepada-Ku nama benda-benda itu jika kamu mamang benar orang-orang yang benar!\"'),
(39, '002:032', '68', 2, 32, 'Mereka menjawab: \"Maha Suci Engkau, tidak ada yang kami ketahui selain dari apa yang telah Engkau ajarkan kepada kami; sesungguhnya Engkaulah Yang Maha Mengetahui lagi Maha Bijaksana\".'),
(40, '002:033', '68', 2, 33, 'Allah berfirman: \"Hai Adam, beritahukanlah kepada mereka nama-nama benda ini\". Maka setelah diberitahukannya kepada mereka nama-nama benda itu, Allah berfirman: \"Bukankah sudah Ku-katakan kepadamu, bahwa sesungguhnya Aku mengetahui rahasia langit dan bumi dan mengetahui apa yang kamu lahirkan dan apa yang kamu sembunyikan?\"'),
(41, '002:034', '68', 2, 34, 'Dan (ingatlah) ketika Kami berfirman kepada para malaikat: \"Sujudlah kamu kepada Adam,\" maka sujudlah mereka kecuali Iblis; ia enggan dan takabur dan adalah ia termasuk golongan orang-orang yang kafir.'),
(42, '002:035', '68', 2, 35, 'Dan Kami berfirman: \"Hai Adam, diamilah oleh kamu dan isterimu surga ini, dan makanlah makanan-makanannya yang banyak lagi baik dimana saja yang kamu sukai, dan janganlah kamu dekati pohon ini, yang menyebabkan kamu termasuk orang-orang yang zalim.'),
(43, '002:036', '68', 2, 36, 'Lalu keduanya digelincirkan oleh syaitan dari surga itu dan dikeluarkan dari keadaan semula dan Kami berfirman: \"Turunlah kamu! sebagian kamu menjadi musuh bagi yang lain, dan bagi kamu ada tempat kediaman di bumi, dan kesenangan hidup sampai waktu yang ditentukan\".'),
(44, '002:037', '68', 2, 37, 'Kemudian Adam menerima beberapa kalimat dari Tuhannya, maka Allah menerima taubatnya. Sesungguhnya Allah Maha Penerima taubat lagi Maha Penyayang.'),
(45, '002:038', '68', 2, 38, 'Kami berfirman: \"Turunlah kamu semuanya dari surga itu! Kemudian jika datang petunjuk-Ku kepadamu, maka barang siapa yang mengikuti petunjuk-Ku, niscaya tidak ada kekhawatiran atas mereka, dan tidak (pula) mereka bersedih hati\".'),
(46, '002:039', '68', 2, 39, 'Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu penghuni neraka; mereka kekal di dalamnya.'),
(47, '002:040', '68', 2, 40, 'Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Aku anugerahkan kepadamu, dan penuhilah janjimu kepada-Ku, niscaya Aku penuhi janji-Ku kepadamu; dan hanya kepada-Ku-lah kamu harus takut (tunduk).'),
(48, '002:041', '68', 2, 41, 'Dan berimanlah kamu kepada apa yang telah Aku turunkan (Al Quran) yang membenarkan apa yang ada padamu (Taurat), dan janganlah kamu menjadi orang yang pertama kafir kepadanya, dan janganlah kamu menukarkan ayat-ayat-Ku dengan harga yang rendah, dan hanya kepada Akulah kamu harus bertakwa.'),
(49, '002:042', '68', 2, 42, 'Dan janganlah kamu campur adukkan yang hak dengan yang bathil dan janganlah kamu sembunyikan yang hak itu, sedang kamu mengetahui.'),
(50, '002:043', '68', 2, 43, 'Dan dirikanlah shalat, tunaikanlah zakat dan ruku´lah beserta orang-orang yang ruku´.'),
(51, '002:044', '68', 2, 44, 'Mengapa kamu suruh orang lain (mengerjakan) kebaktian, sedang kamu melupakan diri (kewajiban)mu sendiri, padahal kamu membaca Al Kitab (Taurat)? Maka tidaklah kamu berpikir?'),
(52, '002:045', '68', 2, 45, 'Jadikanlah sabar dan shalat sebagai penolongmu. Dan sesungguhnya yang demikian itu sungguh berat, kecuali bagi orang-orang yang khusyu´,'),
(53, '002:046', '68', 2, 46, '(yaitu) orang-orang yang meyakini, bahwa mereka akan menemui Tuhannya, dan bahwa mereka akan kembali kepada-Nya.'),
(54, '002:047', '68', 2, 47, 'Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Aku anugerahkan kepadamu dan (ingatlah pula) bahwasanya Aku telah melebihkan kamu atas segala umat.'),
(55, '002:048', '68', 2, 48, 'Dan jagalah dirimu dari (azab) hari (kiamat, yang pada hari itu) seseorang tidak dapat membela orang lain, walau sedikitpun; dan (begitu pula) tidak diterima syafa´at dan tebusan dari padanya, dan tidaklah mereka akan ditolong.'),
(56, '002:049', '68', 2, 49, 'Dan (ingatlah) ketika Kami selamatkan kamu dari (Fir´aun) dan pengikut-pengikutnya; mereka menimpakan kepadamu siksaan yang seberat-beratnya, mereka menyembelih anak-anakmu yang laki-laki dan membiarkan hidup anak-anakmu yang perempuan. Dan pada yang demikian itu terdapat cobaan-cobaan yang besar dari Tuhanmu.'),
(57, '002:050', '68', 2, 50, 'Dan (ingatlah), ketika Kami belah laut untukmu, lalu Kami selamatkan kamu dan Kami tenggelamkan (Fir´aun) dan pengikut-pengikutnya sedang kamu sendiri menyaksikan.'),
(58, '002:051', '68', 2, 51, 'Dan (ingatlah), ketika Kami berjanji kepada Musa (memberikan Taurat, sesudah) empat puluh malam, lalu kamu menjadikan anak lembu (sembahan) sepeninggalnya dan kamu adalah orang-orang yang zalim.'),
(59, '002:052', '68', 2, 52, 'Kemudian sesudah itu Kami maafkan kesalahanmu, agar kamu bersyukur.'),
(60, '002:053', '68', 2, 53, 'Dan (ingatlah), ketika Kami berikan kepada Musa Al Kitab (Taurat) dan keterangan yang membedakan antara yang benar dan yang salah, agar kamu mendapat petunjuk.'),
(61, '002:054', '68', 2, 54, 'Dan (ingatlah), ketika Musa berkata kepada kaumnya: \"Hai kaumku, sesungguhnya kamu telah menganiaya dirimu sendiri karena kamu telah menjadikan anak lembu (sembahanmu), maka bertaubatlah kepada Tuhan yang menjadikan kamu dan bunuhlah dirimu. Hal itu adalah lebih baik bagimu pada sisi Tuhan yang menjadikan kamu; maka Allah akan menerima taubatmu. Sesungguhnya Dialah Yang Maha Penerima taubat lagi Maha Penyayang\".'),
(62, '002:055', '68', 2, 55, 'Dan (ingatlah), ketika kamu berkata: \"Hai Musa, kami tidak akan beriman kepadamu sebelum kami melihat Allah dengan terang, karena itu kamu disambar halilintar, sedang kamu menyaksikannya\".'),
(63, '002:056', '68', 2, 56, 'Setelah itu Kami bangkitkan kamu sesudah kamu mati, supaya kamu bersyukur.'),
(64, '002:057', '68', 2, 57, 'Dan Kami naungi kamu dengan awan, dan Kami turunkan kepadamu \"manna\" dan \"salwa\". Makanlah dari makanan yang baik-baik yang telah Kami berikan kepadamu; dan tidaklah mereka menganiaya Kami; akan tetapi merekalah yang menganiaya diri mereka sendiri.'),
(65, '002:058', '68', 2, 58, 'Dan (ingatlah), ketika Kami berfirman: \"Masuklah kamu ke negeri ini (Baitul Maqdis), dan makanlah dari hasil buminya, yang banyak lagi enak dimana yang kamu sukai, dan masukilah pintu gerbangnya sambil bersujud, dan katakanlah: \"Bebaskanlah kami dari dosa\", niscaya Kami ampuni kesalahan-kesalahanmu, dan kelak Kami akan menambah (pemberian Kami) kepada orang-orang yang berbuat baik\".'),
(66, '002:059', '68', 2, 59, 'Lalu orang-orang yang zalim mengganti perintah dengan (mengerjakan) yang tidak diperintahkan kepada mereka. Sebab itu Kami timpakan atas orang-orang yang zalim itu dari langit, karena mereka berbuat fasik.'),
(67, '002:060', '68', 2, 60, 'Dan (ingatlah) ketika Musa memohon air untuk kaumnya, lalu Kami berfirman: \"Pukullah batu itu dengan tongkatmu\". Lalu memancarlah daripadanya dua belas mata air. Sungguh tiap-tiap suku telah mengetahui tempat minumnya (masing-masing). Makan dan minumlah rezeki (yang diberikan) Allah, dan janganlah kamu berkeliaran di muka bumi dengan berbuat kerusakan.'),
(68, '002:061', '68', 2, 61, 'Dan (ingatlah), ketika kamu berkata: \"Hai Musa, kami tidak bisa sabar (tahan) dengan satu macam makanan saja. Sebab itu mohonkanlah untuk kami kepada Tuhanmu, agar Dia mengeluarkan bagi kami dari apa yang ditumbuhkan bumi, yaitu sayur-mayurnya, ketimunnya, bawang putihnya, kacang adasnya, dan bawang merahnya\". Musa berkata: \"Maukah kamu mengambil yang rendah sebagai pengganti yang lebih baik? Pergilah kamu ke suatu kota, pasti kamu memperoleh apa yang kamu minta\". Lalu ditimpahkanlah kepada mereka nista dan kehinaan, serta mereka mendapat kemurkaan dari Allah. Hal itu (terjadi) karena mereka selalu mengingkari ayat-ayat Allah dan membunuh para Nabi yang memang tidak dibenarkan. Demikian itu (terjadi) karena mereka selalu berbuat durhaka dan melampaui batas.'),
(69, '002:062', '68', 2, 62, 'Sesungguhnya orang-orang mukmin, orang-orang Yahudi, orang-orang Nasrani dan orang-orang Shabiin, siapa saja diantara mereka yang benar-benar beriman kepada Allah, hari kemudian dan beramal saleh, mereka akan menerima pahala dari Tuhan mereka, tidak ada kekhawatiran kepada mereka, dan tidak (pula) mereka bersedih hati.'),
(70, '002:063', '68', 2, 63, 'Dan (ingatlah), ketika Kami mengambil janji dari kamu dan Kami angkatkan gunung (Thursina) di atasmu (seraya Kami berfirman): \"Peganglah teguh-teguh apa yang Kami berikan kepadamu dan ingatlah selalu apa yang ada didalamnya, agar kamu bertakwa\".'),
(71, '002:064', '68', 2, 64, 'Kemudian kamu berpaling setelah (adanya perjanjian) itu, maka kalau tidak ada karunia Allah dan rahmat-Nya atasmu, niscaya kamu tergolong orang yang rugi.'),
(72, '002:065', '68', 2, 65, 'Dan sesungguhnya telah kamu ketahui orang-orang yang melanggar diantaramu pada hari Sabtu, lalu Kami berfirman kepada mereka: \"Jadilah kamu kera yang hina\".'),
(73, '002:066', '68', 2, 66, 'Maka Kami jadikan yang demikian itu peringatan bagi orang-orang dimasa itu, dan bagi mereka yang datang kemudian, serta menjadi pelajaran bagi orang-orang yang bertakwa.'),
(74, '002:067', '68', 2, 67, 'Dan (ingatlah), ketika Musa berkata kepada kaumnya: \"Sesungguhnya Allah menyuruh kamu menyembelih seekor sapi betina\". Mereka berkata: \"Apakah kamu hendak menjadikan kami buah ejekan?\" Musa menjawab: \"Aku berlindung kepada Allah agar tidak menjadi salah seorang dari orang-orang yang jahil\".'),
(75, '002:068', '68', 2, 68, 'Mereka menjawab: \"Mohonkanlah kepada Tuhanmu untuk kami, agar Dia menerangkan kepada kami; sapi betina apakah itu\". Musa menjawab: \"Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang tidak tua dan tidak muda; pertengahan antara itu; maka kerjakanlah apa yang diperintahkan kepadamu\".'),
(76, '002:069', '68', 2, 69, 'Mereka berkata: \"Mohonkanlah kepada Tuhanmu untuk kami agar Dia menerangkan kepada kami apa warnanya\". Musa menjawab: \"Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang kuning, yang kuning tua warnanya, lagi menyenangkan orang-orang yang memandangnya\".'),
(77, '002:070', '68', 2, 70, 'Mereka berkata: \"Mohonkanlah kepada Tuhanmu untuk kami agar Dia menerangkan kepada kami bagaimana hakikat sapi betina itu, karena sesungguhnya sapi itu (masih) samar bagi kami dan sesungguhnya kami insya Allah akan mendapat petunjuk (untuk memperoleh sapi itu)\".'),
(78, '002:071', '68', 2, 71, 'Musa berkata: \"Sesungguhnya Allah berfirman bahwa sapi betina itu adalah sapi betina yang belum pernah dipakai untuk membajak tanah dan tidak pula untuk mengairi tanaman, tidak bercacat, tidak ada belangnya\". Mereka berkata: \"Sekarang barulah kamu menerangkan hakikat sapi betina yang sebenarnya\". Kemudian mereka menyembelihnya dan hampir saja mereka tidak melaksanakan perintah itu.'),
(79, '002:072', '68', 2, 72, 'Dan (ingatlah), ketika kamu membunuh seorang manusia lalu kamu saling tuduh menuduh tentang itu. Dan Allah hendak menyingkapkan apa yang selama ini kamu sembunyikan.'),
(80, '002:073', '68', 2, 73, 'Lalu Kami berfirman: \"Pukullah mayat itu dengan sebahagian anggota sapi betina itu !\" Demikianlah Allah menghidupkan kembali orang-orang yang telah mati, dam memperlihatkan padamu tanda-tanda kekuasaan-Nya agar kamu mengerti.'),
(81, '002:074', '68', 2, 74, 'Kemudian setelah itu hatimu menjadi keras seperti batu, bahkan lebih keras lagi. Padahal diantara batu-batu itu sungguh ada yang mengalir sungai-sungai dari padanya dan diantaranya sungguh ada yang terbelah lalu keluarlah mata air dari padanya dan diantaranya sungguh ada yang meluncur jatuh, karena takut kepada Allah. Dan Allah sekali-sekali tidak lengah dari apa yang kamu kerjakan.'),
(82, '002:075', '68', 2, 75, 'Apakah kamu masih mengharapkan mereka akan percaya kepadamu, padahal segolongan dari mereka mendengar firman Allah, lalu mereka mengubahnya setelah mereka memahaminya, sedang mereka mengetahui?.'),
(83, '002:076', '68', 2, 76, 'Dan apabila mereka berjumpa dengan orang-orang yang beriman, mereka berkata: \"Kamipun telah beriman,\" tetapi apabila mereka berada sesama mereka saja, lalu mereka berkata: \"Apakah kamu menceritakan kepada mereka (orang-orang mukmin) apa yang telah diterangkan Allah kepadamu, supaya dengan demikian mereka dapat mengalahkan hujjahmu di hadapan Tuhanmu; tidakkah kamu mengerti?\"'),
(84, '002:077', '68', 2, 77, 'Tidakkah mereka mengetahui bahwa Allah mengetahui segala yang mereka sembunyikan dan segala yang mereka nyatakan?'),
(85, '002:078', '68', 2, 78, 'Dan diantara mereka ada yang buta huruf, tidak mengetahui Al Kitab (Taurat), kecuali dongengan bohong belaka dan mereka hanya menduga-duga.'),
(86, '002:079', '68', 2, 79, 'Maka kecelakaan yAng besarlah bagi orang-orang yang menulis Al Kitab dengan tangan mereka sendiri, lalu dikatakannya; \"Ini dari Allah\", (dengan maksud) untuk memperoleh keuntungan yang sedikit dengan perbuatan itu. Maka kecelakaan yang besarlah bagi mereka, akibat apa yang ditulis oleh tangan mereka sendiri, dan kecelakaan yang besarlah bagi mereka, akibat apa yang mereka kerjakan.'),
(87, '002:080', '68', 2, 80, 'Dan mereka berkata: \"Kami sekali-kali tidak akan disentuh oleh api neraka, kecuali selama beberapa hari saja\". Katakanlah: \"Sudahkah kamu menerima janji dari Allah sehingga Allah tidak akan memungkiri janji-Nya, ataukah kamu hanya mengatakan terhadap Allah apa yang tidak kamu ketahui?\"'),
(88, '002:081', '68', 2, 81, '(Bukan demikian), yang benar: barangsiapa berbuat dosa dan ia telah diliputi oleh dosanya, mereka itulah penghuni neraka, mereka kekal di dalamnya.'),
(89, '002:082', '68', 2, 82, 'Dan orang-orang yang beriman serta beramal saleh, mereka itu penghuni surga; mereka kekal di dalamnya.'),
(90, '002:083', '68', 2, 83, 'Dan (ingatlah), ketika Kami mengambil janji dari Bani Israil (yaitu): Janganlah kamu menyembah selain Allah, dan berbuat kebaikanlah kepada ibu bapa, kaum kerabat, anak-anak yatim, dan orang-orang miskin, serta ucapkanlah kata-kata yang baik kepada manusia, dirikanlah shalat dan tunaikanlah zakat. Kemudian kamu tidak memenuhi janji itu, kecuali sebahagian kecil daripada kamu, dan kamu selalu berpaling.'),
(91, '002:084', '68', 2, 84, 'Dan (ingatlah), ketika Kami mengambil janji dari kamu (yaitu): kamu tidak akan menumpahkan darahmu (membunuh orang), dan kamu tidak akan mengusir dirimu (saudaramu sebangsa) dari kampung halamanmu, kemudian kamu berikrar (akan memenuhinya) sedang kamu mempersaksikannya.'),
(92, '002:085', '68', 2, 85, 'Kemudian kamu (Bani Israil) membunuh dirimu (saudaramu sebangsa) dan mengusir segolongan daripada kamu dari kampung halamannya, kamu bantu membantu terhadap mereka dengan membuat dosa dan permusuhan; tetapi jika mereka datang kepadamu sebagai tawanan, kamu tebus mereka, padahal mengusir mereka itu (juga) terlarang bagimu. Apakah kamu beriman kepada sebahagian Al Kitab (Taurat) dan ingkar terhadap sebahagian yang lain? Tiadalah balasan bagi orang yang berbuat demikian daripadamu, melainkan kenistaan dalam kehidupan dunia, dan pada hari kiamat mereka dikembalikan kepada siksa yang sangat berat. Allah tidak lengah dari apa yang kamu perbuat.'),
(93, '002:086', '68', 2, 86, 'Itulah orang-orang yang membeli kehidupan dunia dengan (kehidupan) akhirat, maka tidak akan diringankan siksa mereka dan mereka tidak akan ditolong.'),
(94, '002:087', '68', 2, 87, 'Dan sesungguhnya Kami telah mendatangkan Al Kitab (Taurat) kepada Musa, dan Kami telah menyusulinya (berturut-turut) sesudah itu dengan rasul-rasul, dan telah Kami berikan bukti-bukti kebenaran (mukjizat) kepada Isa putera Maryam dan Kami memperkuatnya dengan Ruhul Qudus. Apakah setiap datang kepadamu seorang rasul membawa sesuatu (pelajaran) yang tidak sesuai dengan keinginanmu lalu kamu menyombong; maka beberapa orang (diantara mereka) kamu dustakan dan beberapa orang (yang lain) kamu bunuh?'),
(95, '002:088', '68', 2, 88, 'Dan mereka berkata: \"Hati kami tertutup\". Tetapi sebenarnya Allah telah mengutuk mereka karena keingkaran mereka; maka sedikit sekali mereka yang beriman.'),
(96, '002:089', '68', 2, 89, 'Dan setelah datang kepada mereka Al Quran dari Allah yang membenarkan apa yang ada pada mereka, padahal sebelumnya mereka biasa memohon (kedatangan Nabi) untuk mendapat kemenangan atas orang-orang kafir, maka setelah datang kepada mereka apa yang telah mereka ketahui, mereka lalu ingkar kepadanya. Maka laknat Allah-lah atas orang-orang yang ingkar itu.'),
(97, '002:090', '68', 2, 90, 'Alangkah buruknya (hasil perbuatan) mereka yang menjual dirinya sendiri dengan kekafiran kepada apa yang telah diturunkan Allah, karena dengki bahwa Allah menurunkan karunia-Nya kepada siapa yang dikehendaki-Nya diantara hamba-hamba-Nya. Karena itu mereka mendapat murka sesudah (mendapat) kemurkaan. Dan untuk orang-orang kafir siksaan yang menghinakan.'),
(98, '002:091', '68', 2, 91, 'Dan apabila dikatakan kepada mereka: \"Berimanlah kepada Al Quran yang diturunkan Allah,\" mereka berkata: \"Kami hanya beriman kepada apa yang diturunkan kepada kami\". Dan mereka kafir kepada Al Quran yang diturunkan sesudahnya, sedang Al Quran itu adalah (Kitab) yang hak; yang membenarkan apa yang ada pada mereka. Katakanlah: \"Mengapa kamu dahulu membunuh nabi-nabi Allah jika benar kamu orang-orang yang beriman?\"'),
(99, '002:092', '68', 2, 92, 'Sesungguhnya Musa telah datang kepadamu membawa bukti-bukti kebenaran (mukjizat), kemudian kamu jadikan anak sapi (sebagai sembahan) sesudah (kepergian)nya, dan sebenarnya kamu adalah orang-orang yang zalim.'),
(100, '002:093', '68', 2, 93, 'Dan (ingatlah), ketika Kami mengambil janji dari kamu dan Kami angkat bukit (Thursina) di atasmu (seraya Kami berfirman): \"Peganglah teguh-teguh apa yang Kami berikan kepadamu dan dengarkanlah!\" Mereka menjawab: \"Kami mendengar tetapi tidak mentaati\". Dan telah diresapkan ke dalam hati mereka itu (kecintaan menyembah) anak sapi karena kekafirannya. Katakanlah: \"Amat jahat perbuatan yang telah diperintahkan imanmu kepadamu jika betul kamu beriman (kepada Taurat).'),
(101, '002:094', '68', 2, 94, 'Katakanlah: \"Jika kamu (menganggap bahwa) kampung akhirat (surga) itu khusus untukmu di sisi Allah, bukan untuk orang lain, maka inginilah kematian(mu), jika kamu memang benar.'),
(102, '002:095', '68', 2, 95, 'Dan sekali-kali mereka tidak akan mengingini kematian itu selama-lamanya, karena kesalahan-kesalahan yang telah diperbuat oleh tangan mereka (sendiri), dan Allah Maha Mengetahui siapa orang-orang yang aniaya.'),
(103, '002:096', '68', 2, 96, 'Dan sungguh kamu akan mendapati mereka, manusia yang paling loba kepada kehidupan (di dunia), bahkan (lebih loba lagi) dari orang-orang musyrik. Masing-masing mereka ingin agar diberi umur seribu tahun, padahal umur panjang itu sekali-kali tidak akan menjauhkannya daripada siksa. Allah Maha Mengetahui apa yang mereka kerjakan.'),
(104, '002:097', '68', 2, 97, 'Katakanlah: \"Barang siapa yang menjadi musuh Jibril, maka Jibril itu telah menurunkannya (Al Quran) ke dalam hatimu dengan seizin Allah; membenarkan apa (kitab-kitab) yang sebelumnya dan menjadi petunjuk serta berita gembira bagi orang-orang yang beriman.'),
(105, '002:098', '68', 2, 98, 'Barang siapa yang menjadi musuh Allah, malaikat-malaikat-Nya, rasul-rasul-Nya, Jibril dan Mikail, maka sesungguhnya Allah adalah musuh orang-orang kafir.'),
(106, '002:099', '68', 2, 99, 'Dan sesungguhnya Kami telah menurunkan kepadamu ayat-ayat yang jelas; dan tak ada yang ingkar kepadanya, melainkan orang-orang yang fasik.'),
(107, '002:100', '68', 2, 100, 'Patutkah (mereka ingkar kepada ayat-ayat Allah), dan setiap kali mereka mengikat janji, segolongan mereka melemparkannya? Bahkan sebagian besar dari mereka tidak beriman.'),
(108, '002:101', '68', 2, 101, 'Dan setelah datang kepada mereka seorang Rasul dari sisi Allah yang membenarkan apa (kitab) yang ada pada mereka, sebahagian dari orang-orang yang diberi kitab (Taurat) melemparkan kitab Allah ke belakang (punggung)nya, seolah-olah mereka tidak mengetahui (bahwa itu adalah kitab Allah).'),
(109, '002:102', '68', 2, 102, 'Dan mereka mengikuti apa yang dibaca oleh syaitan-syaitan pada masa kerajaan Sulaiman (dan mereka mengatakan bahwa Sulaiman itu mengerjakan sihir), padahal Sulaiman tidak kafir (tidak mengerjakan sihir), hanya syaitan-syaitan lah yang kafir (mengerjakan sihir). Mereka mengajarkan sihir kepada manusia dan apa yang diturunkan kepada dua orang malaikat di negeri Babil yaitu Harut dan Marut, sedang keduanya tidak mengajarkan (sesuatu) kepada seorangpun sebelum mengatakan: \"Sesungguhnya kami hanya cobaan (bagimu), sebab itu janganlah kamu kafir\". Maka mereka mempelajari dari kedua malaikat itu apa yang dengan sihir itu, mereka dapat menceraikan antara seorang (suami) dengan isterinya. Dan mereka itu (ahli sihir) tidak memberi mudharat dengan sihirnya kepada seorangpun, kecuali dengan izin Allah. Dan mereka mempelajari sesuatu yang tidak memberi mudharat kepadanya dan tidak memberi manfaat. Demi, sesungguhnya mereka telah meyakini bahwa barangsiapa yang menukarnya (kitab Allah) dengan sihir itu, tiadalah baginya keuntungan di akhirat, dan amat jahatlah perbuatan mereka menjual dirinya dengan sihir, kalau mereka mengetahui.'),
(110, '002:103', '68', 2, 103, 'Sesungguhnya kalau mereka beriman dan bertakwa, (niscaya mereka akan mendapat pahala), dan sesungguhnya pahala dari sisi Allah adalah lebih baik, kalau mereka mengetahui.'),
(111, '002:104', '68', 2, 104, 'Hai orang-orang yang beriman, janganlah kamu katakan (kepada Muhammad): \"Raa´ina\", tetapi katakanlah: \"Unzhurna\", dan \"dengarlah\". Dan bagi orang-orang yang kafir siksaan yang pedih.'),
(112, '002:105', '68', 2, 105, 'Orang-orang kafir dari Ahli Kitab dan orang-orang musyrik tiada menginginkan diturunkannya sesuatu kebaikan kepadamu dari Tuhanmu. Dan Allah menentukan siapa yang dikehendaki-Nya (untuk diberi) rahmat-Nya (kenabian); dan Allah mempunyai karunia yang besar.'),
(113, '002:106', '68', 2, 106, 'Ayat mana saja yang Kami nasakhkan, atau Kami jadikan (manusia) lupa kepadanya, Kami datangkan yang lebih baik daripadanya atau yang sebanding dengannya. Tidakkah kamu mengetahui bahwa sesungguhnya Allah Maha Kuasa atas segala sesuatu?'),
(114, '002:107', '68', 2, 107, 'Tiadakah kamu mengetahui bahwa kerajaan langit dan bumi adalah kepunyaan Allah? Dan tiada bagimu selain Allah seorang pelindung maupun seorang penolong.'),
(115, '002:108', '68', 2, 108, 'Apakah kamu menghendaki untuk meminta kepada Rasul kamu seperti Bani Israil meminta kepada Musa pada jaman dahulu? Dan barangsiapa yang menukar iman dengan kekafiran, maka sungguh orang itu telah sesat dari jalan yang lurus.'),
(116, '002:109', '68', 2, 109, 'Sebahagian besar Ahli Kitab menginginkan agar mereka dapat mengembalikan kamu kepada kekafiran setelah kamu beriman, karena dengki yang (timbul) dari diri mereka sendiri, setelah nyata bagi mereka kebenaran. Maka maafkanlah dan biarkanlah mereka, sampai Allah mendatangkan perintah-Nya. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(117, '002:110', '68', 2, 110, 'Dan dirikanlah shalat dan tunaikanlah zakat. Dan kebaikan apa saja yang kamu usahakan bagi dirimu, tentu kamu akan mendapat pahala nya pada sisi Allah. Sesungguhnya Alah Maha Melihat apa-apa yang kamu kerjakan.'),
(118, '002:111', '68', 2, 111, 'Dan mereka (Yahudi dan Nasrani) berkata: \"Sekali-kali tidak akan masuk surga kecuali orang-orang (yang beragama) Yahudi atau Nasrani\". Demikian itu (hanya) angan-angan mereka yang kosong belaka. Katakanlah: \"Tunjukkanlah bukti kebenaranmu jika kamu adalah orang yang benar\".'),
(119, '002:112', '68', 2, 112, '(Tidak demikian) bahkan barangsiapa yang menyerahkan diri kepada Allah, sedang ia berbuat kebajikan, maka baginya pahala pada sisi Tuhannya dan tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(120, '002:113', '68', 2, 113, 'Dan orang-orang Yahudi berkata: \"Orang-orang Nasrani itu tidak mempunyai suatu pegangan\", dan orang-orang Nasrani berkata: \"Orang-orang Yahudi tidak mempunyai sesuatu pegangan,\" padahal mereka (sama-sama) membaca Al Kitab. Demikian pula orang-orang yang tidak mengetahui, mengatakan seperti ucapan mereka itu. Maka Allah akan mengadili diantara mereka pada hari Kiamat, tentang apa-apa yang mereka berselisih padanya.'),
(121, '002:114', '68', 2, 114, 'Dan siapakah yang lebih aniaya daripada orang yang menghalanghalangi menyebut nama Allah dalam mesjid-mesjid-Nya, dan berusaha untuk merobohkannya? Mereka itu tidak sepatutnya masuk ke dalamnya (mesjid Allah), kecuali dengan rasa takut (kepada Allah). Mereka di dunia mendapat kehinaan dan di akhirat mendapat siksa yang berat.'),
(122, '002:115', '68', 2, 115, 'Dan kepunyaan Allah-lah timur dan barat, maka kemanapun kamu menghadap di situlah wajah Allah. Sesungguhnya Allah Maha Luas (rahmat-Nya) lagi Maha Mengetahui.'),
(123, '002:116', '68', 2, 116, 'Mereka (orang-orang kafir) berkata: \"Allah mempunyai anak\". Maha Suci Allah, bahkan apa yang ada di langit dan di bumi adalah kepunyaan Allah; semua tunduk kepada-Nya.'),
(124, '002:117', '68', 2, 117, 'Allah Pencipta langit dan bumi, dan bila Dia berkehendak (untuk menciptakan) sesuatu, maka (cukuplah) Dia hanya mengatakan kepadanya: \"Jadilah!\" Lalu jadilah ia.'),
(125, '002:118', '68', 2, 118, 'Dan orang-orang yang tidak mengetahui berkata: \"Mengapa Allah tidak (langsung) berbicara dengan kami atau datang tanda-tanda kekuasaan-Nya kepada kami?\" Demikian pula orang-orang yang sebelum mereka telah mengatakan seperti ucapan mereka itu; hati mereka serupa. Sesungguhnya Kami telah menjelaskan tanda-tanda kekuasaan Kami kepada kaum yang yakin.'),
(126, '002:119', '68', 2, 119, 'Sesungguhnya Kami telah mengutusmu (Muhammad) dengan kebenaran; sebagai pembawa berita gembira dan pemberi peringatan, dan kamu tidak akan diminta (pertanggungan jawab) tentang penghuni-penghuni neraka.'),
(127, '002:120', '68', 2, 120, 'Orang-orang Yahudi dan Nasrani tidak akan senang kepada kamu hingga kamu mengikuti agama mereka. Katakanlah: \"Sesungguhnya petunjuk Allah itulah petunjuk (yang benar)\". Dan sesungguhnya jika kamu mengikuti kemauan mereka setelah pengetahuan datang kepadamu, maka Allah tidak lagi menjadi pelindung dan penolong bagimu.'),
(128, '002:121', '68', 2, 121, 'Orang-orang yang telah Kami berikan Al Kitab kepadanya, mereka membacanya dengan bacaan yang sebenarnya, mereka itu beriman kepadanya. Dan barangsiapa yang ingkar kepadanya, maka mereka itulah orang-orang yang rugi.'),
(129, '002:122', '68', 2, 122, 'Hai Bani Israil, ingatlah akan nikmat-Ku yang telah Ku-anugerahkan kepadamu dan Aku telah melabihkan kamu atas segala umat.'),
(130, '002:123', '68', 2, 123, 'Dan takutlah kamu kepada suatu hari di waktu seseorang tidak dapat menggantikan seseorang lain sedikitpun dan tidak akan diterima suatu tebusan daripadanya dan tidak akan memberi manfaat sesuatu syafa´at kepadanya dan tidak (pula) mereka akan ditolong.'),
(131, '002:124', '68', 2, 124, 'Dan (ingatlah), ketika Ibrahim diuji Tuhannya dengan beberapa kalimat (perintah dan larangan), lalu Ibrahim menunaikannya. Allah berfirman: \"Sesungguhnya Aku akan menjadikanmu imam bagi seluruh manusia\". Ibrahim berkata: \"(Dan saya mohon juga) dari keturunanku\". Allah berfirman: \"Janji-Ku (ini) tidak mengenai orang yang zalim\".'),
(132, '002:125', '68', 2, 125, 'Dan (ingatlah), ketika Kami menjadikan rumah itu (Baitullah) tempat berkumpul bagi manusia dan tempat yang aman. Dan jadikanlah sebahagian maqam Ibrahim tempat shalat. Dan telah Kami perintahkan kepada Ibrahim dan Ismail: \"Bersihkanlah rumah-Ku untuk orang-orang yang thawaf, yang i´tikaf, yang ruku´ dan yang sujud\".'),
(133, '002:126', '68', 2, 126, 'Dan (ingatlah), ketika Ibrahim berdoa: \"Ya Tuhanku, jadikanlah negeri ini, negeri yang aman sentosa, dan berikanlah rezeki dari buah-buahan kepada penduduknya yang beriman diantara mereka kepada Allah dan hari kemudian. Allah berfirman: \"Dan kepada orang yang kafirpun Aku beri kesenangan sementara, kemudian Aku paksa ia menjalani siksa neraka dan itulah seburuk-buruk tempat kembali\".'),
(134, '002:127', '68', 2, 127, 'Dan (ingatlah), ketika Ibrahim meninggikan (membina) dasar-dasar Baitullah bersama Ismail (seraya berdoa): \"Ya Tuhan kami terimalah daripada kami (amalan kami), sesungguhnya Engkaulah Yang Maha Mendengar lagi Maha Mengetahui\".'),
(135, '002:128', '68', 2, 128, 'Ya Tuhan kami, jadikanlah kami berdua orang yang tunduk patuh kepada Engkau dan (jadikanlah) diantara anak cucu kami umat yang tunduk patuh kepada Engkau dan tunjukkanlah kepada kami cara-cara dan tempat-tempat ibadat haji kami, dan terimalah taubat kami. Sesungguhnya Engkaulah Yang Maha Penerima taubat lagi Maha Penyayang.'),
(136, '002:129', '68', 2, 129, 'Ya Tuhan kami, utuslah untuk mereka sesorang Rasul dari kalangan mereka, yang akan membacakan kepada mereka ayat-ayat Engkau, dan mengajarkan kepada mereka Al Kitab (Al Quran) dan Al-Hikmah (As-Sunnah) serta mensucikan mereka. Sesungguhnya Engkaulah yang Maha Kuasa lagi Maha Bijaksana.'),
(137, '002:130', '68', 2, 130, 'Dan tidak ada yang benci kepada agama Ibrahim, melainkan orang yang memperbodoh dirinya sendiri, dan sungguh Kami telah memilihnya di dunia dan sesungguhnya dia di akhirat benar-benar termasuk orang-orang yang saleh.'),
(138, '002:131', '68', 2, 131, 'Ketika Tuhannya berfirman kepadanya: \"Tunduk patuhlah!\" Ibrahim menjawab: \"Aku tunduk patuh kepada Tuhan semesta alam\".'),
(139, '002:132', '68', 2, 132, 'Dan Ibrahim telah mewasiatkan ucapan itu kepada anak-anaknya, demikian pula Ya´qub. (Ibrahim berkata): \"Hai anak-anakku! Sesungguhnya Allah telah memilih agama ini bagimu, maka janganlah kamu mati kecuali dalam memeluk agama Islam\".'),
(140, '002:133', '68', 2, 133, 'Adakah kamu hadir ketika Ya´qub kedatangan (tanda-tanda) maut, ketika ia berkata kepada anak-anaknya: \"Apa yang kamu sembah sepeninggalku?\" Mereka menjawab: \"Kami akan menyembah Tuhanmu dan Tuhan nenek moyangmu, Ibrahim, Ismail dan Ishaq, (yaitu) Tuhan Yang Maha Esa dan kami hanya tunduk patuh kepada-Nya\".'),
(141, '002:134', '68', 2, 134, 'Itu adalah umat yang lalu; baginya apa yang telah diusahakannya dan bagimu apa yang sudah kamu usahakan, dan kamu tidak akan diminta pertanggungan jawab tentang apa yang telah mereka kerjakan.'),
(142, '002:135', '68', 2, 135, 'Dan mereka berkata: \"Hendaklah kamu menjadi penganut agama Yahudi atau Nasrani, niscaya kamu mendapat petunjuk\". Katakanlah: \"Tidak, melainkan (kami mengikuti) agama Ibrahim yang lurus. Dan bukanlah dia (Ibrahim) dari golongan orang musyrik\".'),
(143, '002:136', '68', 2, 136, 'Katakanlah (hai orang-orang mukmin): \"Kami beriman kepada Allah dan apa yang diturunkan kepada kami, dan apa yang diturunkan kepada Ibrahim, Isma´il, Ishaq, Ya´qub dan anak cucunya, dan apa yang diberikan kepada Musa dan Isa serta apa yang diberikan kepada nabi-nabi dari Tuhannya. Kami tidak membeda-bedakan seorangpun diantara mereka dan kami hanya tunduk patuh kepada-Nya\".'),
(144, '002:137', '68', 2, 137, 'Maka jika mereka beriman kepada apa yang kamu telah beriman kepadanya, sungguh mereka telah mendapat petunjuk; dan jika mereka berpaling, sesungguhnya mereka berada dalam permusuhan (dengan kamu). Maka Allah akan memelihara kamu dari mereka. Dan Dialah Yang Maha Mendengar lagi Maha Mengetahui.'),
(145, '002:138', '68', 2, 138, 'Shibghah Allah. Dan siapakah yang lebih baik shibghahnya dari pada Allah? Dan hanya kepada-Nya-lah kami menyembah.'),
(146, '002:139', '68', 2, 139, 'Katakanlah: \"Apakah kamu memperdebatkan dengan kami tentang Allah, padahal Dia adalah Tuhan kami dan Tuhan kamu; bagi kami amalan kami, dan bagi kamu amalan kamu dan hanya kepada-Nya kami mengikhlaskan hati,'),
(147, '002:140', '68', 2, 140, 'ataukah kamu (hai orang-orang Yahudi dan Nasrani) mengatakan bahwa Ibrahim, Isma´il, Ishaq, Ya´qub dan anak cucunya, adalah penganut agama Yahudi atau Nasrani?\" Katakanlah: \"Apakah kamu lebih mengetahui ataukah Allah, dan siapakah yang lebih zalim dari pada orang yang menyembunyikan syahadah dari Allah yang ada padanya?\" Dan Allah sekali-kali tiada lengah dari apa yang kamu kerjakan.'),
(148, '002:141', '68', 2, 141, 'Itu adalah umat yang telah lalu; baginya apa yang diusahakannya dan bagimu apa yang kamu usahakan; dan kamu tidak akan diminta pertanggungan jawab tentang apa yang telah mereka kerjakan.'),
(149, '002:142', '68', 2, 142, 'Orang-orang yang kurang akalnya diantara manusia akan berkata: \"Apakah yang memalingkan mereka (umat Islam) dari kiblatnya (Baitul Maqdis) yang dahulu mereka telah berkiblat kepadanya?\" Katakanlah: \"Kepunyaan Allah-lah timur dan barat; Dia memberi petunjuk kepada siapa yang dikehendaki-Nya ke jalan yang lurus\".'),
(150, '002:143', '68', 2, 143, 'Dan demikian (pula) Kami telah menjadikan kamu (umat Islam), umat yang adil dan pilihan agar kamu menjadi saksi atas (perbuatan) manusia dan agar Rasul (Muhammad) menjadi saksi atas (perbuatan) kamu. Dan Kami tidak menetapkan kiblat yang menjadi kiblatmu (sekarang) melainkan agar Kami mengetahui (supaya nyata) siapa yang mengikuti Rasul dan siapa yang membelot. Dan sungguh (pemindahan kiblat) itu terasa amat berat, kecuali bagi orang-orang yang telah diberi petunjuk oleh Allah; dan Allah tidak akan menyia-nyiakan imanmu. Sesungguhnya Allah Maha Pengasih lagi Maha Penyayang kepada manusia.'),
(151, '002:144', '68', 2, 144, 'Sungguh Kami (sering) melihat mukamu menengadah ke langit, maka sungguh Kami akan memalingkan kamu ke kiblat yang kamu sukai. Palingkanlah mukamu ke arah Masjidil Haram. Dan dimana saja kamu berada, palingkanlah mukamu ke arahnya. Dan sesungguhnya orang-orang (Yahudi dan Nasrani) yang diberi Al Kitab (Taurat dan Injil) memang mengetahui, bahwa berpaling ke Masjidil Haram itu adalah benar dari Tuhannya; dan Allah sekali-kali tidak lengah dari apa yang mereka kerjakan.'),
(152, '002:145', '68', 2, 145, 'Dan sesungguhnya jika kamu mendatangkan kepada orang-orang (Yahudi dan Nasrani) yang diberi Al Kitab (Taurat dan Injil), semua ayat (keterangan), mereka tidak akan mengikuti kiblatmu, dan kamupun tidak akan mengikuti kiblat mereka, dan sebahagian merekapun tidak akan mengikuti kiblat sebahagian yang lain. Dan sesungguhnya jika kamu mengikuti keinginan mereka setelah datang ilmu kepadamu, sesungguhnya kamu -- kalau begitu -- termasuk golongan orang-orang yang zalim.'),
(153, '002:146', '68', 2, 146, 'Orang-orang (Yahudi dan Nasrani) yang telah Kami beri Al Kitab (Taurat dan Injil) mengenal Muhammad seperti mereka mengenal anak-anaknya sendiri. Dan sesungguhnya sebahagian diantara mereka menyembunyikan kebenaran, padahal mereka mengetahui.'),
(154, '002:147', '68', 2, 147, 'Kebenaran itu adalah dari Tuhanmu, sebab itu jangan sekali-kali kamu termasuk orang-orang yang ragu.'),
(155, '002:148', '68', 2, 148, 'Dan bagi tiap-tiap umat ada kiblatnya (sendiri) yang ia menghadap kepadanya. Maka berlomba-lombalah (dalam membuat) kebaikan. Di mana saja kamu berada pasti Allah akan mengumpulkan kamu sekalian (pada hari kiamat). Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(156, '002:149', '68', 2, 149, 'Dan dari mana saja kamu keluar (datang), maka palingkanlah wajahmu ke arah Masjidil Haram, sesungguhnya ketentuan itu benar-benar sesuatu yang hak dari Tuhanmu. Dan Allah sekali-kali tidak lengah dari apa yang kamu kerjakan.'),
(157, '002:150', '68', 2, 150, 'Dan dari mana saja kamu (keluar), maka palingkanlah wajahmu ke arah Masjidil Haram. Dan dimana saja kamu (sekalian) berada, maka palingkanlah wajahmu ke arahnya, agar tidak ada hujjah bagi manusia atas kamu, kecuali orang-orang yang zalim diantara mereka. Maka janganlah kamu takut kepada mereka dan takutlah kepada-Ku (saja). Dan agar Ku-sempurnakan nikmat-Ku atasmu, dan supaya kamu mendapat petunjuk.'),
(158, '002:151', '68', 2, 151, 'Sebagaimana (Kami telah menyempurnakan nikmat Kami kepadamu) Kami telah mengutus kepadamu Rasul diantara kamu yang membacakan ayat-ayat Kami kepada kamu dan mensucikan kamu dan mengajarkan kepadamu Al Kitab dan Al-Hikmah, serta mengajarkan kepada kamu apa yang belum kamu ketahui.'),
(159, '002:152', '68', 2, 152, 'Karena itu, ingatlah kamu kepada-Ku niscaya Aku ingat (pula) kepadamu, dan bersyukurlah kepada-Ku, dan janganlah kamu mengingkari (nikmat)-Ku.'),
(160, '002:153', '68', 2, 153, 'Hai orang-orang yang beriman, jadikanlah sabar dan shalat sebagai penolongmu, sesungguhnya Allah beserta orang-orang yang sabar.'),
(161, '002:154', '68', 2, 154, 'Dan janganlah kamu mengatakan terhadap orang-orang yang gugur di jalan Allah, (bahwa mereka itu) mati; bahkan (sebenarnya) mereka itu hidup, tetapi kamu tidak menyadarinya.'),
(162, '002:155', '68', 2, 155, 'Dan sungguh akan Kami berikan cobaan kepadamu, dengan sedikit ketakutan, kelaparan, kekurangan harta, jiwa dan buah-buahan. Dan berikanlah berita gembira kepada orang-orang yang sabar.'),
(163, '002:156', '68', 2, 156, '(yaitu) orang-orang yang apabila ditimpa musibah, mereka mengucapkan: \"Inna lillaahi wa innaa ilaihi raaji´uun\".'),
(164, '002:157', '68', 2, 157, 'Mereka itulah yang mendapat keberkatan yang sempurna dan rahmat dari Tuhan mereka dan mereka itulah orang-orang yang mendapat petunjuk.'),
(165, '002:158', '68', 2, 158, 'Sesungguhnya Shafaa dan Marwa adalah sebahagian dari syi´ar Allah. Maka barangsiapa yang beribadah haji ke Baitullah atau ber´umrah, maka tidak ada dosa baginya mengerjakan sa´i antara keduanya. Dan barangsiapa yang mengerjakan suatu kebajikan dengan kerelaan hati, maka sesungguhnya Allah Maha Mensyukuri kebaikan lagi Maha Mengetahui.'),
(166, '002:159', '68', 2, 159, 'Sesungguhnya orang-orang yang menyembunyikan apa yang telah Kami turunkan berupa keterangan-keterangan (yang jelas) dan petunjuk, setelah Kami menerangkannya kepada manusia dalam Al Kitab, mereka itu dilaknati Allah dan dilaknati (pula) oleh semua (mahluk) yang dapat melaknati,'),
(167, '002:160', '68', 2, 160, 'kecuali mereka yang telah taubat dan mengadakan perbaikan dan menerangkan (kebenaran), maka terhadap mereka itulah Aku menerima taubatnya dan Akulah Yang Maha Menerima taubat lagi Maha Penyayang.'),
(168, '002:161', '68', 2, 161, 'Sesungguhnya orang-orang kafir dan mereka mati dalam keadaan kafir, mereka itu mendapat laknat Allah, para Malaikat dan manusia seluruhnya.'),
(169, '002:162', '68', 2, 162, 'Mereka kekal di dalam laknat itu; tidak akan diringankan siksa dari mereka dan tidak (pula) mereka diberi tangguh.'),
(170, '002:163', '68', 2, 163, 'Dan Tuhanmu adalah Tuhan Yang Maha Esa; tidak ada Tuhan melainkan Dia Yang Maha Pemurah lagi Maha Penyayang.'),
(171, '002:164', '68', 2, 164, 'Sesungguhnya dalam penciptaan langit dan bumi, silih bergantinya malam dan siang, bahtera yang berlayar di laut membawa apa yang berguna bagi manusia, dan apa yang Allah turunkan dari langit berupa air, lalu dengan air itu Dia hidupkan bumi sesudah mati (kering)-nya dan Dia sebarkan di bumi itu segala jenis hewan, dan pengisaran angin dan awan yang dikendalikan antara langit dan bumi; sungguh (terdapat) tanda-tanda (keesaan dan kebesaran Allah) bagi kaum yang memikirkan.'),
(172, '002:165', '68', 2, 165, 'Dan diantara manusia ada orang-orang yang menyembah tandingan-tandingan selain Allah; mereka mencintainya sebagaimana mereka mencintai Allah. Adapun orang-orang yang beriman amat sangat cintanya kepada Allah. Dan jika seandainya orang-orang yang berbuat zalim itu mengetahui ketika mereka melihat siksa (pada hari kiamat), bahwa kekuatan itu kepunyaan Allah semuanya, dan bahwa Allah amat berat siksaan-Nya (niscaya mereka menyesal).'),
(173, '002:166', '68', 2, 166, '(Yaitu) ketika orang-orang yang diikuti itu berlepas diri dari orang-orang yang mengikutinya, dan mereka melihat siksa; dan (ketika) segala hubungan antara mereka terputus sama sekali.'),
(174, '002:167', '68', 2, 167, 'Dan berkatalah orang-orang yang mengikuti: \"Seandainya kami dapat kembali (ke dunia), pasti kami akan berlepas diri dari mereka, sebagaimana mereka berlepas diri dari kami\". Demikianlah Allah memperlihatkan kepada mereka amal perbuatannya menjadi sesalan bagi mereka; dan sekali-kali mereka tidak akan keluar dari api neraka.'),
(175, '002:168', '68', 2, 168, 'Hai sekalian manusia, makanlah yang halal lagi baik dari apa yang terdapat di bumi, dan janganlah kamu mengikuti langkah-langkah syaitan; karena sesungguhnya syaitan itu adalah musuh yang nyata bagimu.'),
(176, '002:169', '68', 2, 169, 'Sesungguhnya syaitan itu hanya menyuruh kamu berbuat jahat dan keji, dan mengatakan terhadap Allah apa yang tidak kamu ketahui.'),
(177, '002:170', '68', 2, 170, 'Dan apabila dikatakan kepada mereka: \"Ikutilah apa yang telah diturunkan Allah,\" mereka menjawab: \"(Tidak), tetapi kami hanya mengikuti apa yang telah kami dapati dari (perbuatan) nenek moyang kami\". \"(Apakah mereka akan mengikuti juga), walaupun nenek moyang mereka itu tidak mengetahui suatu apapun, dan tidak mendapat petunjuk?\".'),
(178, '002:171', '68', 2, 171, 'Dan perumpamaan (orang-orang yang menyeru) orang-orang kafir adalah seperti penggembala yang memanggil binatang yang tidak mendengar selain panggilan dan seruan saja. Mereka tuli, bisu dan buta, maka (oleh sebab itu) mereka tidak mengerti.'),
(179, '002:172', '68', 2, 172, 'Hai orang-orang yang beriman, makanlah di antara rezeki yang baik-baik yang Kami berikan kepadamu dan bersyukurlah kepada Allah, jika benar-benar kepada-Nya kamu menyembah.'),
(180, '002:173', '68', 2, 173, 'Sesungguhnya Allah hanya mengharamkan bagimu bangkai, darah, daging babi, dan binatang yang (ketika disembelih) disebut (nama) selain Allah. Tetapi barangsiapa dalam keadaan terpaksa (memakannya) sedang dia tidak menginginkannya dan tidak (pula) melampaui batas, maka tidak ada dosa baginya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(181, '002:174', '68', 2, 174, 'Sesungguhnya orang-orang yang menyembunyikan apa yang telah diturunkan Allah, yaitu Al Kitab dan menjualnya dengan harga yang sedikit (murah), mereka itu sebenarnya tidak memakan (tidak menelan) ke dalam perutnya melainkan api, dan Allah tidak akan berbicara kepada mereka pada hari kiamat dan tidak mensucikan mereka dan bagi mereka siksa yang amat pedih.'),
(182, '002:175', '68', 2, 175, 'Mereka itulah orang-orang yang membeli kesesatan dengan petunjuk dan siksa dengan ampunan. Maka alangkah beraninya mereka menentang api neraka!');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(183, '002:176', '68', 2, 176, 'Yang demikian itu adalah karena Allah telah menurunkan Al Kitab dengan membawa kebenaran; dan sesungguhnya orang-orang yang berselisih tentang (kebenaran) Al Kitab itu, benar-benar dalam penyimpangan yang jauh (dari kebenaran).'),
(184, '002:177', '68', 2, 177, 'Bukanlah menghadapkan wajahmu ke arah timur dan barat itu suatu kebajikan, akan tetapi sesungguhnya kebajikan itu ialah beriman kepada Allah, hari kemudian, malaikat-malaikat, kitab-kitab, nabi-nabi dan memberikan harta yang dicintainya kepada kerabatnya, anak-anak yatim, orang-orang miskin, musafir (yang memerlukan pertolongan) dan orang-orang yang meminta-minta; dan (memerdekakan) hamba sahaya, mendirikan shalat, dan menunaikan zakat; dan orang-orang yang menepati janjinya apabila ia berjanji, dan orang-orang yang sabar dalam kesempitan, penderitaan dan dalam peperangan. Mereka itulah orang-orang yang benar (imannya); dan mereka itulah orang-orang yang bertakwa.'),
(185, '002:178', '68', 2, 178, 'Hai orang-orang yang beriman, diwajibkan atas kamu qishaash berkenaan dengan orang-orang yang dibunuh; orang merdeka dengan orang merdeka, hamba dengan hamba, dan wanita dengan wanita. Maka barangsiapa yang mendapat suatu pemaafan dari saudaranya, hendaklah (yang memaafkan) mengikuti dengan cara yang baik, dan hendaklah (yang diberi maaf) membayar (diat) kepada yang memberi maaf dengan cara yang baik (pula). Yang demikian itu adalah suatu keringanan dari Tuhan kamu dan suatu rahmat. Barangsiapa yang melampaui batas sesudah itu, maka baginya siksa yang sangat pedih.'),
(186, '002:179', '68', 2, 179, 'Dan dalam qishaash itu ada (jaminan kelangsungan) hidup bagimu, hai orang-orang yang berakal, supaya kamu bertakwa.'),
(187, '002:180', '68', 2, 180, 'Diwajibkan atas kamu, apabila seorang di antara kamu kedatangan (tanda-tanda) maut, jika ia meninggalkan harta yang banyak, berwasiat untuk ibu-bapak dan karib kerabatnya secara ma´ruf, (ini adalah) kewajiban atas orang-orang yang bertakwa.'),
(188, '002:181', '68', 2, 181, 'Maka barangsiapa yang mengubah wasiat itu, setelah ia mendengarnya, maka sesungguhnya dosanya adalah bagi orang-orang yang mengubahnya. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(189, '002:182', '68', 2, 182, '(Akan tetapi) barangsiapa khawatir terhadap orang yang berwasiat itu, berlaku berat sebelah atau berbuat dosa, lalu ia mendamaikan antara mereka, maka tidaklah ada dosa baginya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(190, '002:183', '68', 2, 183, 'Hai orang-orang yang beriman, diwajibkan atas kamu berpuasa sebagaimana diwajibkan atas orang-orang sebelum kamu agar kamu bertakwa,'),
(191, '002:184', '68', 2, 184, '(yaitu) dalam beberapa hari yang tertentu. Maka barangsiapa diantara kamu ada yang sakit atau dalam perjalanan (lalu ia berbuka), maka (wajiblah baginya berpuasa) sebanyak hari yang ditinggalkan itu pada hari-hari yang lain. Dan wajib bagi orang-orang yang berat menjalankannya (jika mereka tidak berpuasa) membayar fidyah, (yaitu): memberi makan seorang miskin. Barangsiapa yang dengan kerelaan hati mengerjakan kebajikan, maka itulah yang lebih baik baginya. Dan berpuasa lebih baik bagimu jika kamu mengetahui.'),
(192, '002:185', '68', 2, 185, '(Beberapa hari yang ditentukan itu ialah) bulan Ramadhan, bulan yang di dalamnya diturunkan (permulaan) Al Quran sebagai petunjuk bagi manusia dan penjelasan-penjelasan mengenai petunjuk itu dan pembeda (antara yang hak dan yang bathil). Karena itu, barangsiapa di antara kamu hadir (di negeri tempat tinggalnya) di bulan itu, maka hendaklah ia berpuasa pada bulan itu, dan barangsiapa sakit atau dalam perjalanan (lalu ia berbuka), maka (wajiblah baginya berpuasa), sebanyak hari yang ditinggalkannya itu, pada hari-hari yang lain. Allah menghendaki kemudahan bagimu, dan tidak menghendaki kesukaran bagimu. Dan hendaklah kamu mencukupkan bilangannya dan hendaklah kamu mengagungkan Allah atas petunjuk-Nya yang diberikan kepadamu, supaya kamu bersyukur.'),
(193, '002:186', '68', 2, 186, 'Dan apabila hamba-hamba-Ku bertanya kepadamu tentang Aku, maka (jawablah), bahwasanya Aku adalah dekat. Aku mengabulkan permohonan orang yang berdoa apabila ia memohon kepada-Ku, maka hendaklah mereka itu memenuhi (segala perintah-Ku) dan hendaklah mereka beriman kepada-Ku, agar mereka selalu berada dalam kebenaran.'),
(194, '002:187', '68', 2, 187, 'Dihalalkan bagi kamu pada malam hari bulan puasa bercampur dengan isteri-isteri kamu; mereka adalah pakaian bagimu, dan kamupun adalah pakaian bagi mereka. Allah mengetahui bahwasanya kamu tidak dapat menahan nafsumu, karena itu Allah mengampuni kamu dan memberi maaf kepadamu. Maka sekarang campurilah mereka dan ikutilah apa yang telah ditetapkan Allah untukmu, dan makan minumlah hingga terang bagimu benang putih dari benang hitam, yaitu fajar. Kemudian sempurnakanlah puasa itu sampai (datang) malam, (tetapi) janganlah kamu campuri mereka itu, sedang kamu beri´tikaf dalam mesjid. Itulah larangan Allah, maka janganlah kamu mendekatinya. Demikianlah Allah menerangkan ayat-ayat-Nya kepada manusia, supaya mereka bertakwa.'),
(195, '002:188', '68', 2, 188, 'Dan janganlah sebahagian kamu memakan harta sebahagian yang lain di antara kamu dengan jalan yang bathil dan (janganlah) kamu membawa (urusan) harta itu kepada hakim, supaya kamu dapat memakan sebahagian daripada harta benda orang lain itu dengan (jalan berbuat) dosa, padahal kamu mengetahui.'),
(196, '002:189', '68', 2, 189, 'Mereka bertanya kepadamu tentang bulan sabit. Katakanlah: \"Bulan sabit itu adalah tanda-tanda waktu bagi manusia dan (bagi ibadat) haji; Dan bukanlah kebajikan memasuki rumah-rumah dari belakangnya, akan tetapi kebajikan itu ialah kebajikan orang yang bertakwa. Dan masuklah ke rumah-rumah itu dari pintu-pintunya; dan bertakwalah kepada Allah agar kamu beruntung.'),
(197, '002:190', '68', 2, 190, 'Dan perangilah di jalan Allah orang-orang yang memerangi kamu, (tetapi) janganlah kamu melampaui batas, karena sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.'),
(198, '002:191', '68', 2, 191, 'Dan bunuhlah mereka di mana saja kamu jumpai mereka, dan usirlah mereka dari tempat mereka telah mengusir kamu (Mekah); dan fitnah itu lebih besar bahayanya dari pembunuhan, dan janganlah kamu memerangi mereka di Masjidil Haram, kecuali jika mereka memerangi kamu di tempat itu. Jika mereka memerangi kamu (di tempat itu), maka bunuhlah mereka. Demikanlah balasan bagi orang-orang kafir.'),
(199, '002:192', '68', 2, 192, 'Kemudian jika mereka berhenti (dari memusuhi kamu), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(200, '002:193', '68', 2, 193, 'Dan perangilah mereka itu, sehingga tidak ada fitnah lagi dan (sehingga) ketaatan itu hanya semata-mata untuk Allah. Jika mereka berhenti (dari memusuhi kamu), maka tidak ada permusuhan (lagi), kecuali terhadap orang-orang yang zalim.'),
(201, '002:194', '68', 2, 194, 'Bulan haram dengan bulan haram, dan pada sesuatu yang patut dihormati, berlaku hukum qishaash. Oleh sebab itu barangsiapa yang menyerang kamu, maka seranglah ia, seimbang dengan serangannya terhadapmu. Bertakwalah kepada Allah dan ketahuilah, bahwa Allah beserta orang-orang yang bertakwa.'),
(202, '002:195', '68', 2, 195, 'Dan belanjakanlah (harta bendamu) di jalan Allah, dan janganlah kamu menjatuhkan dirimu sendiri ke dalam kebinasaan, dan berbuat baiklah, karena sesungguhnya Allah menyukai orang-orang yang berbuat baik.'),
(203, '002:196', '68', 2, 196, 'Dan sempurnakanlah ibadah haji dan ´umrah karena Allah. Jika kamu terkepung (terhalang oleh musuh atau karena sakit), maka (sembelihlah) korban yang mudah didapat, dan jangan kamu mencukur kepalamu, sebelum korban sampai di tempat penyembelihannya. Jika ada di antaramu yang sakit atau ada gangguan di kepalanya (lalu ia bercukur), maka wajiblah atasnya berfid-yah, yaitu: berpuasa atau bersedekah atau berkorban. Apabila kamu telah (merasa) aman, maka bagi siapa yang ingin mengerjakan ´umrah sebelum haji (di dalam bulan haji), (wajiblah ia menyembelih) korban yang mudah didapat. Tetapi jika ia tidak menemukan (binatang korban atau tidak mampu), maka wajib berpuasa tiga hari dalam masa haji dan tujuh hari (lagi) apabila kamu telah pulang kembali. Itulah sepuluh (hari) yang sempurna. Demikian itu (kewajiban membayar fidyah) bagi orang-orang yang keluarganya tidak berada (di sekitar) Masjidil Haram (orang-orang yang bukan penduduk kota Mekah). Dan bertakwalah kepada Allah dan ketahuilah bahwa Allah sangat keras siksaan-Nya.'),
(204, '002:197', '68', 2, 197, '(Musim) haji adalah beberapa bulan yang dimaklumi, barangsiapa yang menetapkan niatnya dalam bulan itu akan mengerjakan haji, maka tidak boleh rafats, berbuat fasik dan berbantah-bantahan di dalam masa mengerjakan haji. Dan apa yang kamu kerjakan berupa kebaikan, niscaya Allah mengetahuinya. Berbekallah, dan sesungguhnya sebaik-baik bekal adalah takwa dan bertakwalah kepada-Ku hai orang-orang yang berakal.'),
(205, '002:198', '68', 2, 198, 'Tidak ada dosa bagimu untuk mencari karunia (rezeki hasil perniagaan) dari Tuhanmu. Maka apabila kamu telah bertolak dari ´Arafat, berdzikirlah kepada Allah di Masy´arilharam. Dan berdzikirlah (dengan menyebut) Allah sebagaimana yang ditunjukkan-Nya kepadamu; dan sesungguhnya kamu sebelum itu benar-benar termasuk orang-orang yang sesat.'),
(206, '002:199', '68', 2, 199, 'Kemudian bertolaklah kamu dari tempat bertolaknya orang-orang banyak (´Arafah) dan mohonlah ampun kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(207, '002:200', '68', 2, 200, 'Apabila kamu telah menyelesaikan ibadah hajimu, maka berdzikirlah dengan menyebut Allah, sebagaimana kamu menyebut-nyebut (membangga-banggakan) nenek moyangmu, atau (bahkan) berdzikirlah lebih banyak dari itu. Maka di antara manusia ada orang yang bendoa: \"Ya Tuhan kami, berilah kami (kebaikan) di dunia\", dan tiadalah baginya bahagian (yang menyenangkan) di akhirat.'),
(208, '002:201', '68', 2, 201, 'Dan di antara mereka ada orang yang berdoa: \"Ya Tuhan kami, berilah kami kebaikan di dunia dan kebaikan di akhirat dan peliharalah kami dari siksa neraka\".'),
(209, '002:202', '68', 2, 202, 'Mereka itulah orang-orang yang mendapat bahagian daripada yang mereka usahakan; dan Allah sangat cepat perhitungan-Nya.'),
(210, '002:203', '68', 2, 203, 'Dan berdzikirlah (dengan menyebut) Allah dalam beberapa hari yang berbilang. Barangsiapa yang ingin cepat berangkat (dari Mina) sesudah dua hari, maka tiada dosa baginya. Dan barangsiapa yang ingin menangguhkan (keberangkatannya dari dua hari itu), maka tidak ada dosa pula baginya, bagi orang yang bertakwa. Dan bertakwalah kepada Allah, dan ketahuilah, bahwa kamu akan dikumpulkan kepada-Nya.'),
(211, '002:204', '68', 2, 204, 'Dan di antara manusia ada orang yang ucapannya tentang kehidupan dunia menarik hatimu, dan dipersaksikannya kepada Allah (atas kebenaran) isi hatinya, padahal ia adalah penantang yang paling keras.'),
(212, '002:205', '68', 2, 205, 'Dan apabila ia berpaling (dari kamu), ia berjalan di bumi untuk mengadakan kerusakan padanya, dan merusak tanam-tanaman dan binatang ternak, dan Allah tidak menyukai kebinasaan.'),
(213, '002:206', '68', 2, 206, 'Dan apabila dikatakan kepadanya: \"Bertakwalah kepada Allah\", bangkitlah kesombongannya yang menyebabkannya berbuat dosa. Maka cukuplah (balasannya) neraka Jahannam. Dan sungguh neraka Jahannam itu tempat tinggal yang seburuk-buruknya.'),
(214, '002:207', '68', 2, 207, 'Dan di antara manusia ada orang yang mengorbankan dirinya karena mencari keridhaan Allah; dan Allah Maha Penyantun kepada hamba-hamba-Nya.'),
(215, '002:208', '68', 2, 208, 'Hai orang-orang yang beriman, masuklah kamu ke dalam Islam keseluruhan, dan janganlah kamu turut langkah-langkah syaitan. Sesungguhnya syaitan itu musuh yang nyata bagimu.'),
(216, '002:209', '68', 2, 209, 'Tetapi jika kamu menyimpang (dari jalan Allah) sesudah datang kepadamu bukti-bukti kebenaran, maka ketahuilah, bahwasanya Allah Maha Perkasa lagi Maha Bijaksana.'),
(217, '002:210', '68', 2, 210, 'Tiada yang mereka nanti-nantikan melainkan datangnya Allah dan malaikat (pada hari kiamat) dalam naungan awan, dan diputuskanlah perkaranya. Dan hanya kepada Allah dikembalikan segala urusan.'),
(218, '002:211', '68', 2, 211, 'Tanyakanlah kepada Bani Israil: \"Berapa banyaknya tanda-tanda (kebenaran) yang nyata, yang telah Kami berikan kepada mereka\". Dan barangsiapa yang menukar nikmat Allah setelah datang nikmat itu kepadanya, maka sesungguhnya Allah sangat keras siksa-Nya.'),
(219, '002:212', '68', 2, 212, 'Kehidupan dunia dijadikan indah dalam pandangan orang-orang kafir, dan mereka memandang hina orang-orang yang beriman. Padahal orang-orang yang bertakwa itu lebih mulia daripada mereka di hari kiamat. Dan Allah memberi rezeki kepada orang-orang yang dikehendaki-Nya tanpa batas.'),
(220, '002:213', '68', 2, 213, 'Manusia itu adalah umat yang satu. (setelah timbul perselisihan), maka Allah mengutus para nabi, sebagai pemberi peringatan, dan Allah menurunkan bersama mereka Kitab yang benar, untuk memberi keputusan di antara manusia tentang perkara yang mereka perselisihkan. Tidaklah berselisih tentang Kitab itu melainkan orang yang telah didatangkan kepada mereka Kitab, yaitu setelah datang kepada mereka keterangan-keterangan yang nyata, karena dengki antara mereka sendiri. Maka Allah memberi petunjuk orang-orang yang beriman kepada kebenaran tentang hal yang mereka perselisihkann itu dengan kehendak-Nya. Dan Allah selalu memberi petunjuk orang yang dikehendaki-Nya kepada jalan yang lurus.'),
(221, '002:214', '68', 2, 214, 'Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum datang kepadamu (cobaan) sebagaimana halnya orang-orang terdahulu sebelum kamu? Mereka ditimpa oleh malapetaka dan kesengsaraan, serta digoncangkan (dengan bermacam-macam cobaan) sehingga berkatalah Rasul dan orang-orang yang beriman bersamanya: \"Bilakah datangnya pertolongan Allah?\" Ingatlah, sesungguhnya pertolongan Allah itu amat dekat.'),
(222, '002:215', '68', 2, 215, 'Mereka bertanya tentang apa yang mereka nafkahkan. Jawablah: \"Apa saja harta yang kamu nafkahkan hendaklah diberikan kepada ibu-bapak, kaum kerabat, anak-anak yatim, orang-orang miskin dan orang-orang yang sedang dalam perjalanan\". Dan apa saja kebaikan yang kamu buat, maka sesungguhnya Allah Maha Mengetahuinya.'),
(223, '002:216', '68', 2, 216, 'Diwajibkan atas kamu berperang, padahal berperang itu adalah sesuatu yang kamu benci. Boleh jadi kamu membenci sesuatu, padahal ia amat baik bagimu, dan boleh jadi (pula) kamu menyukai sesuatu, padahal ia amat buruk bagimu; Allah mengetahui, sedang kamu tidak mengetahui.'),
(224, '002:217', '68', 2, 217, 'Mereka bertanya kepadamu tentang berperang pada bulan Haram. Katakanlah: \"Berperang dalam bulan itu adalah dosa besar; tetapi menghalangi (manusia) dari jalan Allah, kafir kepada Allah, (menghalangi masuk) Masjidilharam dan mengusir penduduknya dari sekitarnya, lebih besar (dosanya) di sisi Allah. Dan berbuat fitnah lebih besar (dosanya) daripada membunuh. Mereka tidak henti-hentinya memerangi kamu sampai mereka (dapat) mengembalikan kamu dari agamamu (kepada kekafiran), seandainya mereka sanggup. Barangsiapa yang murtad di antara kamu dari agamanya, lalu dia mati dalam kekafiran, maka mereka itulah yang sia-sia amalannya di dunia dan di akhirat, dan mereka itulah penghuni neraka, mereka kekal di dalamnya.'),
(225, '002:218', '68', 2, 218, 'Sesungguhnya orang-orang yang beriman, orang-orang yang berhijrah dan berjihad di jalan Allah, mereka itu mengharapkan rahmat Allah, dan Allah Maha Pengampun lagi Maha Penyayang.'),
(226, '002:219', '68', 2, 219, 'Mereka bertanya kepadamu tentang khamar dan judi. Katakanlah: \"Pada keduanya terdapat dosa yang besar dan beberapa manfaat bagi manusia, tetapi dosa keduanya lebih besar dari manfaatnya\". Dan mereka bertanya kepadamu apa yang mereka nafkahkan. Katakanlah: \"Yang lebih dari keperluan\". Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu supaya kamu berfikir,'),
(227, '002:220', '68', 2, 220, 'tentang dunia dan akhirat. Dan mereka bertanya kepadamu tentang anak yatim, katakalah: \"Mengurus urusan mereka secara patut adalah baik, dan jika kamu bergaul dengan mereka, maka mereka adalah saudaramu; dan Allah mengetahui siapa yang membuat kerusakan dari yang mengadakan perbaikan. Dan jikalau Allah menghendaki, niscaya Dia dapat mendatangkan kesulitan kepadamu. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.'),
(228, '002:221', '68', 2, 221, 'Dan janganlah kamu menikahi wanita-wanita musyrik, sebelum mereka beriman. Sesungguhnya wanita budak yang mukmin lebih baik dari wanita musyrik, walaupun dia menarik hatimu. Dan janganlah kamu menikahkan orang-orang musyrik (dengan wanita-wanita mukmin) sebelum mereka beriman. Sesungguhnya budak yang mukmin lebih baik dari orang musyrik, walaupun dia menarik hatimu. Mereka mengajak ke neraka, sedang Allah mengajak ke surga dan ampunan dengan izin-Nya. Dan Allah menerangkan ayat-ayat-Nya (perintah-perintah-Nya) kepada manusia supaya mereka mengambil pelajaran.'),
(229, '002:222', '68', 2, 222, 'Mereka bertanya kepadamu tentang haidh. Katakanlah: \"Haidh itu adalah suatu kotoran\". Oleh sebab itu hendaklah kamu menjauhkan diri dari wanita di waktu haidh; dan janganlah kamu mendekati mereka, sebelum mereka suci. Apabila mereka telah suci, maka campurilah mereka itu di tempat yang diperintahkan Allah kepadamu. Sesungguhnya Allah menyukai orang-orang yang bertaubat dan menyukai orang-orang yang mensucikan diri.'),
(230, '002:223', '68', 2, 223, 'Isteri-isterimu adalah (seperti) tanah tempat kamu bercocok tanam, maka datangilah tanah tempat bercocok-tanammu itu bagaimana saja kamu kehendaki. Dan kerjakanlah (amal yang baik) untuk dirimu, dan bertakwalah kepada Allah dan ketahuilah bahwa kamu kelak akan menemui-Nya. Dan berilah kabar gembira orang-orang yang beriman.'),
(231, '002:224', '68', 2, 224, 'Jangahlah kamu jadikan (nama) Allah dalam sumpahmu sebagai penghalang untuk berbuat kebajikan, bertakwa dan mengadakan ishlah di antara manusia. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(232, '002:225', '68', 2, 225, 'Allah tidak menghukum kamu disebabkan sumpahmu yang tidak dimaksud (untuk bersumpah), tetapi Allah menghukum kamu disebabkan (sumpahmu) yang disengaja (untuk bersumpah) oleh hatimu. Dan Allah Maha Pengampun lagi Maha Penyantun.'),
(233, '002:226', '68', 2, 226, 'Kepada orang-orang yang meng-ilaa´ isterinya diberi tangguh empat bulan (lamanya). Kemudian jika mereka kembali (kepada isterinya), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(234, '002:227', '68', 2, 227, 'Dan jika mereka berazam (bertetap hati untuk) talak, maka sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(235, '002:228', '68', 2, 228, 'Wanita-wanita yang ditalak handaklah menahan diri (menunggu) tiga kali quru´. Tidak boleh mereka menyembunyikan apa yang diciptakan Allah dalam rahimnya, jika mereka beriman kepada Allah dan hari akhirat. Dan suami-suaminya berhak merujukinya dalam masa menanti itu, jika mereka (para suami) menghendaki ishlah. Dan para wanita mempunyai hak yang seimbang dengan kewajibannya menurut cara yang ma´ruf. Akan tetapi para suami, mempunyai satu tingkatan kelebihan daripada isterinya. Dan Allah Maha Perkasa lagi Maha Bijaksana.'),
(236, '002:229', '68', 2, 229, 'Talak (yang dapat dirujuki) dua kali. Setelah itu boleh rujuk lagi dengan cara yang ma´ruf atau menceraikan dengan cara yang baik. Tidak halal bagi kamu mengambil kembali sesuatu dari yang telah kamu berikan kepada mereka, kecuali kalau keduanya khawatir tidak akan dapat menjalankan hukum-hukum Allah. Jika kamu khawatir bahwa keduanya (suami isteri) tidak dapat menjalankan hukum-hukum Allah, maka tidak ada dosa atas keduanya tentang bayaran yang diberikan oleh isteri untuk menebus dirinya. Itulah hukum-hukum Allah, maka janganlah kamu melanggarnya. Barangsiapa yang melanggar hukum-hukum Allah mereka itulah orang-orang yang zalim.'),
(237, '002:230', '68', 2, 230, 'Kemudian jika si suami mentalaknya (sesudah talak yang kedua), maka perempuan itu tidak lagi halal baginya hingga dia kawin dengan suami yang lain. Kemudian jika suami yang lain itu menceraikannya, maka tidak ada dosa bagi keduanya (bekas suami pertama dan isteri) untuk kawin kembali jika keduanya berpendapat akan dapat menjalankan hukum-hukum Allah. Itulah hukum-hukum Allah, diterangkan-Nya kepada kaum yang (mau) mengetahui.'),
(238, '002:231', '68', 2, 231, 'Apabila kamu mentalak isteri-isterimu, lalu mereka mendekati akhir iddahnya, maka rujukilah mereka dengan cara yang ma´ruf, atau ceraikanlah mereka dengan cara yang ma´ruf (pula). Janganlah kamu rujuki mereka untuk memberi kemudharatan, karena dengan demikian kamu menganiaya mereka. Barangsiapa berbuat demikian, maka sungguh ia telah berbuat zalim terhadap dirinya sendiri. Janganlah kamu jadikan hukum-hukum Allah permainan, dan ingatlah nikmat Allah padamu, dan apa yang telah diturunkan Allah kepadamu yaitu Al Kitab dan Al Hikmah (As Sunnah). Allah memberi pengajaran kepadamu dengan apa yang diturunkan-Nya itu. Dan bertakwalah kepada Allah serta ketahuilah bahwasanya Allah Maha Mengetahui segala sesuatu.'),
(239, '002:232', '68', 2, 232, 'Apabila kamu mentalak isteri-isterimu, lalu habis masa iddahnya, maka janganlah kamu (para wali) menghalangi mereka kawin lagi dengan bakal suaminya, apabila telah terdapat kerelaan di antara mereka dengan cara yang ma´ruf. Itulah yang dinasehatkan kepada orang-orang yang beriman di antara kamu kepada Allah dan hari kemudian. Itu lebih baik bagimu dan lebih suci. Allah mengetahui, sedang kamu tidak mengetahui.'),
(240, '002:233', '68', 2, 233, 'Para ibu hendaklah menyusukan anak-anaknya selama dua tahun penuh, yaitu bagi yang ingin menyempurnakan penyusuan. Dan kewajiban ayah memberi makan dan pakaian kepada para ibu dengan cara ma´ruf. Seseorang tidak dibebani melainkan menurut kadar kesanggupannya. Janganlah seorang ibu menderita kesengsaraan karena anaknya dan seorang ayah karena anaknya, dan warispun berkewajiban demikian. Apabila keduanya ingin menyapih (sebelum dua tahun) dengan kerelaan keduanya dan permusyawaratan, maka tidak ada dosa atas keduanya. Dan jika kamu ingin anakmu disusukan oleh orang lain, maka tidak ada dosa bagimu apabila kamu memberikan pembayaran menurut yang patut. Bertakwalah kamu kepada Allah dan ketahuilah bahwa Allah Maha Melihat apa yang kamu kerjakan.'),
(241, '002:234', '68', 2, 234, 'Orang-orang yang meninggal dunia di antaramu dengan meninggalkan isteri-isteri (hendaklah para isteri itu) menangguhkan dirinya (ber´iddah) empat bulan sepuluh hari. Kemudian apabila telah habis ´iddahnya, maka tiada dosa bagimu (para wali) membiarkan mereka berbuat terhadap diri mereka menurut yang patut. Allah mengetahui apa yang kamu perbuat.'),
(242, '002:235', '68', 2, 235, 'Dan tidak ada dosa bagi kamu meminang wanita-wanita itu dengan sindiran atau kamu menyembunyikan (keinginan mengawini mereka) dalam hatimu. Allah mengetahui bahwa kamu akan menyebut-nyebut mereka, dalam pada itu janganlah kamu mengadakan janji kawin dengan mereka secara rahasia, kecuali sekedar mengucapkan (kepada mereka) perkataan yang ma´ruf. Dan janganlah kamu berazam (bertetap hati) untuk beraqad nikah, sebelum habis ´iddahnya. Dan ketahuilah bahwasanya Allah mengetahui apa yang ada dalam hatimu; maka takutlah kepada-Nya, dan ketahuilah bahwa Allah Maha Pengampun lagi Maha Penyantun.'),
(243, '002:236', '68', 2, 236, 'Tidak ada kewajiban membayar (mahar) atas kamu, jika kamu menceraikan isteri-isteri kamu sebelum kamu bercampur dengan mereka dan sebelum kamu menentukan maharnya. Dan hendaklah kamu berikan suatu mut´ah (pemberian) kepada mereka. Orang yang mampu menurut kemampuannya dan orang yang miskin menurut kemampuannya (pula), yaitu pemberian menurut yang patut. Yang demikian itu merupakan ketentuan bagi orang-orang yang berbuat kebajikan.'),
(244, '002:237', '68', 2, 237, 'Jika kamu menceraikan isteri-isterimu sebelum kamu bercampur dengan mereka, padahal sesungguhnya kamu sudah menentukan maharnya, maka bayarlah seperdua dari mahar yang telah kamu tentukan itu, kecuali jika isteri-isterimu itu memaafkan atau dimaafkan oleh orang yang memegang ikatan nikah, dan pemaafan kamu itu lebih dekat kepada takwa. Dan janganlah kamu melupakan keutamaan di antara kamu. Sesungguhnya Allah Maha Melihat segala apa yang kamu kerjakan.'),
(245, '002:238', '68', 2, 238, 'Peliharalah semua shalat(mu), dan (peliharalah) shalat wusthaa. Berdirilah untuk Allah (dalam shalatmu) dengan khusyu´.'),
(246, '002:239', '68', 2, 239, 'Jika kamu dalam keadaan takut (bahaya), maka shalatlah sambil berjalan atau berkendaraan. Kemudian apabila kamu telah aman, maka sebutlah Allah (shalatlah), sebagaimana Allah telah mengajarkan kepada kamu apa yang belum kamu ketahui.'),
(247, '002:240', '68', 2, 240, 'Dan orang-orang yang akan meninggal dunia di antara kamu dan meninggalkan isteri, hendaklah berwasiat untuk isteri-isterinya, (yaitu) diberi nafkah hingga setahun lamanya dan tidak disuruh pindah (dari rumahnya). Akan tetapi jika mereka pindah (sendiri), maka tidak ada dosa bagimu (wali atau waris dari yang meninggal) membiarkan mereka berbuat yang ma´ruf terhadap diri mereka. Dan Allah Maha Perkasa lagi Maha Bijaksana.'),
(248, '002:241', '68', 2, 241, 'Kepada wanita-wanita yang diceraikan (hendaklah diberikan oleh suaminya) mut´ah menurut yang ma´ruf, sebagai suatu kewajiban bagi orang-orang yang bertakwa.'),
(249, '002:242', '68', 2, 242, 'Demikianlah Allah menerangkan kepadamu ayat-ayat-Nya (hukum-hukum-Nya) supaya kamu memahaminya.'),
(250, '002:243', '68', 2, 243, 'Apakah kamu tidak memperhatikan orang-orang yang ke luar dari kampung halaman mereka, sedang mereka beribu-ribu (jumlahnya) karena takut mati; maka Allah berfirman kepada mereka: \"Matilah kamu\", kemudian Allah menghidupkan mereka. Sesungguhnya Allah mempunyai karunia terhadap manusia tetapi kebanyakan manusia tidak bersyukur.'),
(251, '002:244', '68', 2, 244, 'Dan berperanglah kamu sekalian di jalan Allah, dan ketahuilah sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(252, '002:245', '68', 2, 245, 'Siapakah yang mau memberi pinjaman kepada Allah, pinjaman yang baik (menafkahkan hartanya di jalan Allah), maka Allah akan meperlipat gandakan pembayaran kepadanya dengan lipat ganda yang banyak. Dan Allah menyempitkan dan melapangkan (rezeki) dan kepada-Nya-lah kamu dikembalikan.'),
(253, '002:246', '68', 2, 246, 'Apakah kamu tidak memperhatikan pemuka-pemuka Bani Israil sesudah Nabi Musa, yaitu ketika mereka berkata kepada seorang Nabi mereka: \"Angkatlah untuk kami seorang raja supaya kami berperang (di bawah pimpinannya) di jalan Allah\". Nabi mereka menjawab: \"Mungkin sekali jika kamu nanti diwajibkan berperang, kamu tidak akan berperang\". Mereka menjawab: \"Mengapa kami tidak mau berperang di jalan Allah, padahal sesungguhnya kami telah diusir dari anak-anak kami?\". Maka tatkala perang itu diwajibkan atas mereka, merekapun berpaling, kecuali beberapa saja di antara mereka. Dan Allah Maha Mengetahui siapa orang-orang yang zalim.'),
(254, '002:247', '68', 2, 247, 'Nabi mereka mengatakan kepada mereka: \"Sesungguhnya Allah telah mengangkat Thalut menjadi rajamu\". Mereka menjawab: \"Bagaimana Thalut memerintah kami, padahal kami lebih berhak mengendalikan pemerintahan daripadanya, sedang diapun tidak diberi kekayaan yang cukup banyak?\" Nabi (mereka) berkata: \"Sesungguhnya Allah telah memilih rajamu dan menganugerahinya ilmu yang luas dan tubuh yang perkasa\". Allah memberikan pemerintahan kepada siapa yang dikehendaki-Nya. Dan Allah Maha Luas pemberian-Nya lagi Maha Mengetahui.'),
(255, '002:248', '68', 2, 248, 'Dan Nabi mereka mengatakan kepada mereka: \"Sesungguhnya tanda ia akan menjadi raja, ialah kembalinya tabut kepadamu, di dalamnya terdapat ketenangan dari Tuhanmu dan sisa dari peninggalan keluarga Musa dan keluarga Harun; tabut itu dibawa malaikat. Sesungguhnya pada yang demikian itu terdapat tanda bagimu, jika kamu orang yang beriman.'),
(256, '002:249', '68', 2, 249, 'Maka tatkala Thalut keluar membawa tentaranya, ia berkata: \"Sesungguhnya Allah akan menguji kamu dengan suatu sungai. Maka siapa di antara kamu meminum airnya; bukanlah ia pengikutku. Dan barangsiapa tiada meminumnya, kecuali menceduk seceduk tangan, maka dia adalah pengikutku\". Kemudian mereka meminumnya kecuali beberapa orang di antara mereka. Maka tatkala Thalut dan orang-orang yang beriman bersama dia telah menyeberangi sungai itu, orang-orang yang telah minum berkata: \"Tak ada kesanggupan kami pada hari ini untuk melawan Jalut dan tentaranya\". Orang-orang yang meyakini bahwa mereka akan menemui Allah, berkata: \"Berapa banyak terjadi golongan yang sedikit dapat mengalahkan golongan yang banyak dengan izin Allah. Dan Allah beserta orang-orang yang sabar\".'),
(257, '002:250', '68', 2, 250, 'Tatkala Jalut dan tentaranya telah nampak oleh mereka, merekapun (Thalut dan tentaranya) berdoa: \"Ya Tuhan kami, tuangkanlah kesabaran atas diri kami, dan kokohkanlah pendirian kami dan tolonglah kami terhadap orang-orang kafir\".'),
(258, '002:251', '68', 2, 251, 'Mereka (tentara Thalut) mengalahkan tentara Jalut dengan izin Allah dan (dalam peperangan itu) Daud membunuh Jalut, kemudian Allah memberikan kepadanya (Daud) pemerintahan dan hikmah (sesudah meninggalnya Thalut) dan mengajarkan kepadanya apa yang dikehendaki-Nya. Seandainya Allah tidak menolak (keganasan) sebahagian umat manusia dengan sebagian yang lain, pasti rusaklah bumi ini. Tetapi Allah mempunyai karunia (yang dicurahkan) atas semesta alam.'),
(259, '002:252', '68', 2, 252, 'Itu adalah ayat-ayat dari Allah, Kami bacakan kepadamu dengan hak (benar) dan sesungguhnya kamu benar-benar salah seorang di antara nabi-nabi yang diutus.'),
(260, '002:253', '68', 2, 253, 'Rasul-rasul itu Kami lebihkan sebagian (dari) mereka atas sebagian yang lain. Di antara mereka ada yang Allah berkata-kata (langsung dengan dia) dan sebagiannya Allah meninggikannya beberapa derajat. Dan Kami berikan kepada Isa putera Maryam beberapa mukjizat serta Kami perkuat dia dengan Ruhul Qudus. Dan kalau Allah menghendaki, niscaya tidaklah berbunuh-bunuhan orang-orang (yang datang) sesudah rasul-rasul itu, sesudah datang kepada mereka beberapa macam keterangan, akan tetapi mereka berselisih, maka ada di antara mereka yang beriman dan ada (pula) di antara mereka yang kafir. Seandainya Allah menghendaki, tidaklah mereka berbunuh-bunuhan. Akan tetapi Allah berbuat apa yang dikehendaki-Nya.'),
(261, '002:254', '68', 2, 254, 'Hai orang-orang yang beriman, belanjakanlah (di jalan Allah) sebagian dari rezeki yang telah Kami berikan kepadamu sebelum datang hari yang pada hari itu tidak ada lagi jual beli dan tidak ada lagi syafa´at. Dan orang-orang kafir itulah orang-orang yang zalim.'),
(262, '002:255', '68', 2, 255, 'Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia Yang Hidup kekal lagi terus menerus mengurus (makhluk-Nya); tidak mengantuk dan tidak tidur. Kepunyaan-Nya apa yang di langit dan di bumi. Tiada yang dapat memberi syafa´at di sisi Allah tanpa izin-Nya? Allah mengetahui apa-apa yang di hadapan mereka dan di belakang mereka, dan mereka tidak mengetahui apa-apa dari ilmu Allah melainkan apa yang dikehendaki-Nya. Kursi Allah meliputi langit dan bumi. Dan Allah tidak merasa berat memelihara keduanya, dan Allah Maha Tinggi lagi Maha Besar.'),
(263, '002:256', '68', 2, 256, 'Tidak ada paksaan untuk (memasuki) agama (Islam); sesungguhnya telah jelas jalan yang benar daripada jalan yang sesat. Karena itu barangsiapa yang ingkar kepada Thaghut dan beriman kepada Allah, maka sesungguhnya ia telah berpegang kepada buhul tali yang amat kuat yang tidak akan putus. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(264, '002:257', '68', 2, 257, 'Allah Pelindung orang-orang yang beriman; Dia mengeluarkan mereka dari kegelapan (kekafiran) kepada cahaya (iman). Dan orang-orang yang kafir, pelindung-pelindungnya ialah syaitan, yang mengeluarkan mereka daripada cahaya kepada kegelapan (kekafiran). Mereka itu adalah penghuni neraka; mereka kekal di dalamnya.'),
(265, '002:258', '68', 2, 258, 'Apakah kamu tidak memperhatikan orang yang mendebat Ibrahim tentang Tuhannya (Allah) karena Allah telah memberikan kepada orang itu pemerintahan (kekuasaan). Ketika Ibrahim mengatakan: \"Tuhanku ialah Yang menghidupkan dan mematikan,\" orang itu berkata: \"Saya dapat menghidupkan dan mematikan\".Ibrahim berkata: \"Sesungguhnya Allah menerbitkan matahari dari timur, maka terbitkanlah dia dari barat,\" lalu terdiamlah orang kafir itu; dan Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
(266, '002:259', '68', 2, 259, 'Atau apakah (kamu tidak memperhatikan) orang yang melalui suatu negeri yang (temboknya) telah roboh menutupi atapnya. Dia berkata: \"Bagaimana Allah menghidupkan kembali negeri ini setelah hancur?\" Maka Allah mematikan orang itu seratus tahun, kemudian menghidupkannya kembali. Allah bertanya: \"Berapakah lamanya kamu tinggal di sini?\" Ia menjawab: \"Saya tinggal di sini sehari atau setengah hari\". Allah berfirman: \"Sebenarnya kamu telah tinggal di sini seratus tahun lamanya; lihatlah kepada makanan dan minumanmu yang belum lagi beubah; dan lihatlah kepada keledai kamu (yang telah menjadi tulang belulang); Kami akan menjadikan kamu tanda kekuasaan Kami bagi manusia; dan lihatlah kepada tulang belulang keledai itu, kemudian Kami menyusunnya kembali, kemudian Kami membalutnya dengan daging\". Maka tatkala telah nyata kepadanya (bagaimana Allah menghidupkan yang telah mati) diapun berkata: \"Saya yakin bahwa Allah Maha Kuasa atas segala sesuatu\".'),
(267, '002:260', '68', 2, 260, 'Dan (ingatlah) ketika Ibrahim berkata: \"Ya Tuhanku, perlihatkanlah kepadaku bagaimana Engkau menghidupkan orang-orang mati\". Allah berfirman: \"Belum yakinkah kamu?\" Ibrahim menjawab: \"Aku telah meyakinkannya, akan tetapi agar hatiku tetap mantap (dengan imanku) Allah berfirman: \"(Kalau demikian) ambillah empat ekor burung, lalu cincanglah semuanya olehmu. (Allah berfirman): \"Lalu letakkan diatas tiap-tiap satu bukit satu bagian dari bagian-bagian itu, kemudian panggillah mereka, niscaya mereka datang kepadamu dengan segera\". Dan ketahuilah bahwa Allah Maha Perkasa lagi Maha Bijaksana.'),
(268, '002:261', '68', 2, 261, 'Perumpamaan (nafkah yang dikeluarkan oleh) orang-orang yang menafkahkan hartanya di jalan Allah adalah serupa dengan sebutir benih yang menumbuhkan tujuh bulir, pada tiap-tiap bulir seratus biji. Allah melipat gandakan (ganjaran) bagi siapa yang Dia kehendaki. Dan Allah Maha Luas (karunia-Nya) lagi Maha Mengetahui.'),
(269, '002:262', '68', 2, 262, 'Orang-orang yang menafkahkan hartanya di jalan Allah, kemudian mereka tidak mengiringi apa yang dinafkahkannya itu dengan menyebut-nyebut pemberiannya dan dengan tidak menyakiti (perasaan si penerima), mereka memperoleh pahala di sisi Tuhan mereka. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(270, '002:263', '68', 2, 263, 'Perkataan yang baik dan pemberian maaf lebih baik dari sedekah yang diiringi dengan sesuatu yang menyakitkan (perasaan si penerima). Allah Maha Kaya lagi Maha Penyantun.'),
(271, '002:264', '68', 2, 264, 'Hai orang-orang yang beriman, janganlah kamu menghilangkan (pahala) sedekahmu dengan menyebut-nyebutnya dan menyakiti (perasaan si penerima), seperti orang yang menafkahkan hartanya karena riya kepada manusia dan dia tidak beriman kepada Allah dan hari kemudian. Maka perumpamaan orang itu seperti batu licin yang di atasnya ada tanah, kemudian batu itu ditimpa hujan lebat, lalu menjadilah dia bersih (tidak bertanah). Mereka tidak menguasai sesuatupun dari apa yang mereka usahakan; dan Allah tidak memberi petunjuk kepada orang-orang yang kafir.'),
(272, '002:265', '68', 2, 265, 'Dan perumpamaan orang-orang yang membelanjakan hartanya karena mencari keridhaan Allah dan untuk keteguhan jiwa mereka, seperti sebuah kebun yang terletak di dataran tinggi yang disiram oleh hujan lebat, maka kebun itu menghasilkan buahnya dua kali lipat. Jika hujan lebat tidak menyiraminya, maka hujan gerimis (pun memadai). Dan Allah Maha Melihat apa yang kamu perbuat.'),
(273, '002:266', '68', 2, 266, 'Apakah ada salah seorang di antaramu yang ingin mempunyai kebun kurma dan anggur yang mengalir di bawahnya sungai-sungai; dia mempunyai dalam kebun itu segala macam buah-buahan, kemudian datanglah masa tua pada orang itu sedang dia mempunyai keturunan yang masih kecil-kecil. Maka kebun itu ditiup angin keras yang mengandung api, lalu terbakarlah. Demikianlah Allah menerangkan ayat-ayat-Nya kepada kamu supaya kamu memikirkannya.'),
(274, '002:267', '68', 2, 267, 'Hai orang-orang yang beriman, nafkahkanlah (di jalan allah) sebagian dari hasil usahamu yang baik-baik dan sebagian dari apa yang Kami keluarkan dari bumi untuk kamu. Dan janganlah kamu memilih yang buruk-buruk lalu kamu menafkahkan daripadanya, padahal kamu sendiri tidak mau mengambilnya melainkan dengan memincingkan mata terhadapnya. Dan ketahuilah, bahwa Allah Maha Kaya lagi Maha Terpuji.'),
(275, '002:268', '68', 2, 268, 'Syaitan menjanjikan (menakut-nakuti) kamu dengan kemiskinan dan menyuruh kamu berbuat kejahatan (kikir); sedang Allah menjadikan untukmu ampunan daripada-Nya dan karunia. Dan Allah Maha Luas (karunia-Nya) lagi Maha Mengatahui.'),
(276, '002:269', '68', 2, 269, 'Allah menganugerahkan al hikmah (kefahaman yang dalam tentang Al Quran dan As Sunnah) kepada siapa yang dikehendaki-Nya. Dan barangsiapa yang dianugerahi hikmah, ia benar-benar telah dianugerahi karunia yang banyak. Dan hanya orang-orang yang berakallah yang dapat mengambil pelajaran (dari firman Allah).'),
(277, '002:270', '68', 2, 270, 'Apa saja yang kamu nafkahkan atau apa saja yang kamu nazarkan, maka sesungguhnya Allah mengetahuinya. Orang-orang yang berbuat zalim tidak ada seorang penolongpun baginya.'),
(278, '002:271', '68', 2, 271, 'Jika kamu menampakkan sedekah(mu), maka itu adalah baik sekali. Dan jika kamu menyembunyikannya dan kamu berikan kepada orang-orang fakir, maka menyembunyikan itu lebih baik bagimu. Dan Allah akan menghapuskan dari kamu sebagian kesalahan-kesalahanmu; dan Allah mengetahui apa yang kamu kerjakan.'),
(279, '002:272', '68', 2, 272, 'Bukanlah kewajibanmu menjadikan mereka mendapat petunjuk, akan tetapi Allah-lah yang memberi petunjuk (memberi taufiq) siapa yang dikehendaki-Nya. Dan apa saja harta yang baik yang kamu nafkahkan (di jalan allah), maka pahalanya itu untuk kamu sendiri. Dan janganlah kamu membelanjakan sesuatu melainkan karena mencari keridhaan Allah. Dan apa saja harta yang baik yang kamu nafkahkan, niscaya kamu akan diberi pahalanya dengan cukup sedang kamu sedikitpun tidak akan dianiaya (dirugikan).'),
(280, '002:273', '68', 2, 273, '(Berinfaqlah) kepada orang-orang fakir yang terikat (oleh jihad) di jalan Allah; mereka tidak dapat (berusaha) di bumi; orang yang tidak tahu menyangka mereka orang kaya karena memelihara diri dari minta-minta. Kamu kenal mereka dengan melihat sifat-sifatnya, mereka tidak meminta kepada orang secara mendesak. Dan apa saja harta yang baik yang kamu nafkahkan (di jalan Allah), maka sesungguhnya Allah Maha Mengatahui.'),
(281, '002:274', '68', 2, 274, 'Orang-orang yang menafkahkan hartanya di malam dan di siang hari secara tersembunyi dan terang-terangan, maka mereka mendapat pahala di sisi Tuhannya. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(282, '002:275', '68', 2, 275, 'Orang-orang yang makan (mengambil) riba tidak dapat berdiri melainkan seperti berdirinya orang yang kemasukan syaitan lantaran (tekanan) penyakit gila. Keadaan mereka yang demikian itu, adalah disebabkan mereka berkata (berpendapat), sesungguhnya jual beli itu sama dengan riba, padahal Allah telah menghalalkan jual beli dan mengharamkan riba. Orang-orang yang telah sampai kepadanya larangan dari Tuhannya, lalu terus berhenti (dari mengambil riba), maka baginya apa yang telah diambilnya dahulu (sebelum datang larangan); dan urusannya (terserah) kepada Allah. Orang yang kembali (mengambil riba), maka orang itu adalah penghuni-penghuni neraka; mereka kekal di dalamnya.'),
(283, '002:276', '68', 2, 276, 'Allah memusnahkan riba dan menyuburkan sedekah. Dan Allah tidak menyukai setiap orang yang tetap dalam kekafiran, dan selalu berbuat dosa.'),
(284, '002:277', '68', 2, 277, 'Sesungguhnya orang-orang yang beriman, mengerjakan amal saleh, mendirikan shalat dan menunaikan zakat, mereka mendapat pahala di sisi Tuhannya. Tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(285, '002:278', '68', 2, 278, 'Hai orang-orang yang beriman, bertakwalah kepada Allah dan tinggalkan sisa riba (yang belum dipungut) jika kamu orang-orang yang beriman.'),
(286, '002:279', '68', 2, 279, 'Maka jika kamu tidak mengerjakan (meninggalkan sisa riba), maka ketahuilah, bahwa Allah dan Rasul-Nya akan memerangimu. Dan jika kamu bertaubat (dari pengambilan riba), maka bagimu pokok hartamu; kamu tidak menganiaya dan tidak (pula) dianiaya.'),
(287, '002:280', '68', 2, 280, 'Dan jika (orang yang berhutang itu) dalam kesukaran, maka berilah tangguh sampai dia berkelapangan. Dan menyedekahkan (sebagian atau semua utang) itu, lebih baik bagimu, jika kamu mengetahui.'),
(288, '002:281', '68', 2, 281, 'Dan peliharalah dirimu dari (azab yang terjadi pada) hari yang pada waktu itu kamu semua dikembalikan kepada Allah. Kemudian masing-masing diri diberi balasan yang sempurna terhadap apa yang telah dikerjakannya, sedang mereka sedikitpun tidak dianiaya (dirugikan).'),
(289, '002:282', '68', 2, 282, 'Hai orang-orang yang beriman, apabila kamu bermu´amalah tidak secara tunai untuk waktu yang ditentukan, hendaklah kamu menuliskannya. Dan hendaklah seorang penulis di antara kamu menuliskannya dengan benar. Dan janganlah penulis enggan menuliskannya sebagaimana Allah mengajarkannya, meka hendaklah ia menulis, dan hendaklah orang yang berhutang itu mengimlakkan (apa yang akan ditulis itu), dan hendaklah ia bertakwa kepada Allah Tuhannya, dan janganlah ia mengurangi sedikitpun daripada hutangnya. Jika yang berhutang itu orang yang lemah akalnya atau lemah (keadaannya) atau dia sendiri tidak mampu mengimlakkan, maka hendaklah walinya mengimlakkan dengan jujur. Dan persaksikanlah dengan dua orang saksi dari orang-orang lelaki (di antaramu). Jika tak ada dua oang lelaki, maka (boleh) seorang lelaki dan dua orang perempuan dari saksi-saksi yang kamu ridhai, supaya jika seorang lupa maka yang seorang mengingatkannya. Janganlah saksi-saksi itu enggan (memberi keterangan) apabila mereka dipanggil; dan janganlah kamu jemu menulis hutang itu, baik kecil maupun besar sampai batas waktu membayarnya. Yang demikian itu, lebih adil di sisi Allah dan lebih menguatkan persaksian dan lebih dekat kepada tidak (menimbulkan) keraguanmu. (Tulislah mu´amalahmu itu), kecuali jika mu´amalah itu perdagangan tunai yang kamu jalankan di antara kamu, maka tidak ada dosa bagi kamu, (jika) kamu tidak menulisnya. Dan persaksikanlah apabila kamu berjual beli; dan janganlah penulis dan saksi saling sulit menyulitkan. Jika kamu lakukan (yang demikian), maka sesungguhnya hal itu adalah suatu kefasikan pada dirimu. Dan bertakwalah kepada Allah; Allah mengajarmu; dan Allah Maha Mengetahui segala sesuatu.'),
(290, '002:283', '68', 2, 283, 'Jika kamu dalam perjalanan (dan bermu´amalah tidak secara tunai) sedang kamu tidak memperoleh seorang penulis, maka hendaklah ada barang tanggungan yang dipegang (oleh yang berpiutang). Akan tetapi jika sebagian kamu mempercayai sebagian yang lain, maka hendaklah yang dipercayai itu menunaikan amanatnya (hutangnya) dan hendaklah ia bertakwa kepada Allah Tuhannya; dan janganlah kamu (para saksi) menyembunyikan persaksian. Dan barangsiapa yang menyembunyikannya, maka sesungguhnya ia adalah orang yang berdosa hatinya; dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(291, '002:284', '68', 2, 284, 'Kepunyaan Allah-lah segala apa yang ada di langit dan apa yang ada di bumi. Dan jika kamu melahirkan apa yang ada di dalam hatimu atau kamu menyembunyikan, niscaya Allah akan membuat perhitungan dengan kamu tentang perbuatanmu itu. Maka Allah mengampuni siapa yang dikehendaki-Nya dan menyiksa siapa yang dikehendaki-Nya; dan Allah Maha Kuasa atas segala sesuatu.'),
(292, '002:285', '68', 2, 285, 'Rasul telah beriman kepada Al Quran yang diturunkan kepadanya dari Tuhannya, demikian pula orang-orang yang beriman. Semuanya beriman kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya dan rasul-rasul-Nya. (Mereka mengatakan): \"Kami tidak membeda-bedakan antara seseorangpun (dengan yang lain) dari rasul-rasul-Nya\", dan mereka mengatakan: \"Kami dengar dan kami taat\". (Mereka berdoa): \"Ampunilah kami ya Tuhan kami dan kepada Engkaulah tempat kembali\".'),
(293, '002:286', '68', 2, 286, 'Allah tidak membebani seseorang melainkan sesuai dengan kesanggupannya. Ia mendapat pahala (dari kebajikan) yang diusahakannya dan ia mendapat siksa (dari kejahatan) yang dikerjakannya. (Mereka berdoa): \"Ya Tuhan kami, janganlah Engkau hukum kami jika kami lupa atau kami tersalah. Ya Tuhan kami, janganlah Engkau bebankan kepada kami beban yang berat sebagaimana Engkau bebankan kepada orang-orang sebelum kami. Ya Tuhan kami, janganlah Engkau pikulkan kepada kami apa yang tak sanggup kami memikulnya. Beri maaflah kami; ampunilah kami; dan rahmatilah kami. Engkaulah Penolong kami, maka tolonglah kami terhadap kaum yang kafir\".'),
(294, '003:001', '68', 3, 1, 'Alif laam miim.'),
(295, '003:002', '68', 3, 2, 'Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia. Yang hidup kekal lagi terus menerus mengurus makhluk-Nya.'),
(296, '003:003', '68', 3, 3, 'Dia menurunkan Al Kitab (Al Quran) kepadamu dengan sebenarnya; membenarkan kitab yang telah diturunkan sebelumnya dan menurunkan Taurat dan Injil,'),
(297, '003:004', '68', 3, 4, 'sebelum (Al Quran), menjadi petunjuk bagi manusia, dan Dia menurunkan Al Furqaan. Sesungguhnya orang-orang yang kafir terhadap ayat-ayat Allah akan memperoleh siksa yang berat; dan Allah Maha Perkasa lagi mempunyai balasan (siksa).'),
(298, '003:005', '68', 3, 5, 'Sesungguhnya bagi Allah tidak ada satupun yang tersembunyi di bumi dan tidak (pula) di langit.'),
(299, '003:006', '68', 3, 6, 'Dialah yang membentuk kamu dalam rahim sebagaimana dikehendaki-Nya. Tak ada Tuhan (yang berhak disembah) melainkan Dia, Yang Maha Perkasa lagi Maha Bijaksana.'),
(300, '003:007', '68', 3, 7, 'Dialah yang menurunkan Al Kitab (Al Quran) kepada kamu. Di antara (isi)nya ada ayat-ayat yang muhkamaat, itulah pokok-pokok isi Al qur´an dan yang lain (ayat-ayat) mutasyaabihaat. Adapun orang-orang yang dalam hatinya condong kepada kesesatan, maka mereka mengikuti sebahagian ayat-ayat yang mutasyaabihaat daripadanya untuk menimbulkan fitnah untuk mencari-cari ta´wilnya, padahal tidak ada yang mengetahui ta´wilnya melainkan Allah. Dan orang-orang yang mendalam ilmunya berkata: \"Kami beriman kepada ayat-ayat yang mutasyaabihaat, semuanya itu dari sisi Tuhan kami\". Dan tidak dapat mengambil pelajaran (daripadanya) melainkan orang-orang yang berakal.'),
(301, '003:008', '68', 3, 8, '(Mereka berdoa): \"Ya Tuhan kami, janganlah Engkau jadikan hati kami condong kepada kesesatan sesudah Engkau beri petunjuk kepada kami, dan karuniakanlah kepada kami rahmat dari sisi Engkau; karena sesungguhnya Engkau-lah Maha Pemberi (karunia)\".'),
(302, '003:009', '68', 3, 9, '\"Ya Tuhan kami, sesungguhnya Engkau mengumpulkan manusia untuk (menerima pembalasan pada) hari yang tak ada keraguan padanya\". Sesungguhnya Allah tidak menyalahi janji.'),
(303, '003:010', '68', 3, 10, 'Sesungguhnya orang-orang yang kafir, harta benda dan anak-anak mereka, sedikitpun tidak dapat menolak (siksa) Allah dari mereka. Dan mereka itu adalah bahan bakar api neraka,'),
(304, '003:011', '68', 3, 11, '(keadaan mereka) adalah sebagai keadaan kaum Fir´aun dan orang-orang yang sebelumnya; mereka mendustakan ayat-ayat Kami; karena itu Allah menyiksa mereka disebabkan dosa-dosa mereka. Dan Allah sangat keras siksa-Nya.'),
(305, '003:012', '68', 3, 12, 'Katakanlah kepada orang-orang yang kafir: \"Kamu pasti akan dikalahkan (di dunia ini) dan akan digiring ke dalam neraka Jahannam. Dan itulah tempat yang seburuk-buruknya\".'),
(306, '003:013', '68', 3, 13, 'Sesungguhnya telah ada tanda bagi kamu pada dua golongan yang telah bertemu (bertempur). Segolongan berperang di jalan Allah dan (segolongan) yang lain kafir yang dengan mata kepala melihat (seakan-akan) orang-orang muslimin dua kali jumlah mereka. Allah menguatkan dengan bantuan-Nya siapa yang dikehendaki-Nya. Sesungguhnya pada yang demikian itu terdapat pelajaran bagi orang-orang yang mempunyai mata hati.'),
(307, '003:014', '68', 3, 14, 'Dijadikan indah pada (pandangan) manusia kecintaan kepada apa-apa yang diingini, yaitu: wanita-wanita, anak-anak, harta yang banyak dari jenis emas, perak, kuda pilihan, binatang-binatang ternak dan sawah ladang. Itulah kesenangan hidup di dunia, dan di sisi Allah-lah tempat kembali yang baik (surga).'),
(308, '003:015', '68', 3, 15, 'Katakanlah: \"Inginkah aku kabarkan kepadamu apa yang lebih baik dari yang demikian itu?\". Untuk orang-orang yang bertakwa (kepada Allah), pada sisi Tuhan mereka ada surga yang mengalir dibawahnya sungai-sungai; mereka kekal didalamnya. Dan (mereka dikaruniai) isteri-isteri yang disucikan serta keridhaan Allah. Dan Allah Maha Melihat akan hamba-hamba-Nya.'),
(309, '003:016', '68', 3, 16, '(Yaitu) orang-orang yang berdoa: Ya Tuhan kami, sesungguhnya kami telah beriman, maka ampunilah segala dosa kami dan peliharalah kami dari siksa neraka,\"'),
(310, '003:017', '68', 3, 17, '(yaitu) orang-orang yang sabar, yang benar, yang tetap taat, yang menafkahkan hartanya (di jalan Allah), dan yang memohon ampun di waktu sahur.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(311, '003:018', '68', 3, 18, 'Allah menyatakan bahwasanya tidak ada Tuhan melainkan Dia (yang berhak disembah), Yang menegakkan keadilan. Para Malaikat dan orang-orang yang berilmu (juga menyatakan yang demikian itu). Tak ada Tuhan melainkan Dia (yang berhak disembah), Yang Maha Perkasa lagi Maha Bijaksana.'),
(312, '003:019', '68', 3, 19, 'Sesungguhnya agama (yang diridhai) disisi Allah hanyalah Islam. Tiada berselisih orang-orang yang telah diberi Al Kitab kecuali sesudah datang pengetahuan kepada mereka, karena kedengkian (yang ada) di antara mereka. Barangsiapa yang kafir terhadap ayat-ayat Allah maka sesungguhnya Allah sangat cepat hisab-Nya.'),
(313, '003:020', '68', 3, 20, 'Kemudian jika mereka mendebat kamu (tentang kebenaran Islam), maka katakanlah: \"Aku menyerahkan diriku kepada Allah dan (demikian pula) orang-orang yang mengikutiku\". Dan katakanlah kepada orang-orang yang telah diberi Al Kitab dan kepada orang-orang yang ummi: \"Apakah kamu (mau) masuk Islam\". Jika mereka masuk Islam, sesungguhnya mereka telah mendapat petunjuk, dan jika mereka berpaling, maka kewajiban kamu hanyalah menyampaikan (ayat-ayat Allah). Dan Allah Maha Melihat akan hamba-hamba-Nya.'),
(314, '003:021', '68', 3, 21, 'Sesungguhnya orang-orang yang kafir kepada ayat-ayat Allah dan membunuh para nabi yang memamg tak dibenarkan dan membunuh orang-orang yang menyuruh manusia berbuat adil, maka gembirakanlah mereka bahwa mereka akan menerima siksa yg pedih.'),
(315, '003:022', '68', 3, 22, 'Mereka itu adalah orang-orang yang lenyap (pahala) amal-amalnya di dunia dan akhirat, dan mereka sekali-kali tidak memperoleh penolong.'),
(316, '003:023', '68', 3, 23, 'Tidakkah kamu memperhatikan orang-orang yang telah diberi bahagian yaitu Al Kitab (Taurat), mereka diseru kepada kitab Allah supaya kitab itu menetapkan hukum diantara mereka; kemudian sebahagian dari mereka berpaling, dan mereka selalu membelakangi (kebenaran).'),
(317, '003:024', '68', 3, 24, 'Hal itu adalah karena mereka mengaku: \"Kami tidak akan disentuh oleh api neraka kecuali beberapa hari yang dapat dihitung\". Mereka diperdayakan dalam agama mereka oleh apa yang selalu mereka ada-adakan.'),
(318, '003:025', '68', 3, 25, 'Bagaimanakah nanti apabila mereka Kami kumpulkan di hari (kiamat) yang tidak ada keraguan tentang adanya. Dan disempurnakan kepada tiap-tiap diri balasan apa yang diusahakannya sedang mereka tidak dianiaya (dirugikan).'),
(319, '003:026', '68', 3, 26, 'Katakanlah: \"Wahai Tuhan Yang mempunyai kerajaan, Engkau berikan kerajaan kepada orang yang Engkau kehendaki dan Engkau cabut kerajaan dari orang yang Engkau kehendaki. Engkau muliakan orang yang Engkau kehendaki dan Engkau hinakan orang yang Engkau kehendaki. Di tangan Engkaulah segala kebajikan. Sesungguhnya Engkau Maha Kuasa atas segala sesuatu.'),
(320, '003:027', '68', 3, 27, 'Engkau masukkan malam ke dalam siang dan Engkau masukkan siang ke dalam malam. Engkau keluarkan yang hidup dari yang mati, dan Engkau keluarkan yang mati dari yang hidup. Dan Engkau beri rezeki siapa yang Engkau kehendaki tanpa hisab (batas)\".'),
(321, '003:028', '68', 3, 28, 'Janganlah orang-orang mukmin mengambil orang-orang kafir menjadi wali dengan meninggalkan orang-orang mukmin. Barang siapa berbuat demikian, niscaya lepaslah ia dari pertolongan Allah, kecuali karena (siasat) memelihara diri dari sesuatu yang ditakuti dari mereka. Dan Allah memperingatkan kamu terhadap diri (siksa)-Nya. Dan hanya kepada Allah kembali(mu).'),
(322, '003:029', '68', 3, 29, 'Katakanlah: \"Jika kamu menyembunyikan apa yang ada dalam hatimu atau kamu melahirkannya, pasti Allah mengetahui\". Allah mengetahui apa-apa yang ada di langit dan apa-apa yang ada di bumi. Dan Allah Maha Kuasa atas segala sesuatu.'),
(323, '003:030', '68', 3, 30, 'Pada hari ketika tiap-tiap diri mendapati segala kebajikan dihadapkan (dimukanya), begitu (juga) kejahatan yang telah dikerjakannya; ia ingin kalau kiranya antara ia dengan hari itu ada masa yang jauh; dan Allah memperingatkan kamu terhadap siksa-Nya. Dan Allah sangat Penyayang kepada hamba-hamba-Nya.'),
(324, '003:031', '68', 3, 31, 'Katakanlah: \"Jika kamu (benar-benar) mencintai Allah, ikutilah aku, niscaya Allah mengasihi dan mengampuni dosa-dosamu\". Allah Maha Pengampun lagi Maha Penyayang.'),
(325, '003:032', '68', 3, 32, 'Katakanlah: \"Taatilah Allah dan Rasul-Nya; jika kamu berpaling, maka sesungguhnya Allah tidak menyukai orang-orang kafir\".'),
(326, '003:033', '68', 3, 33, 'Sesungguhnya Allah telah memilih Adam, Nuh, keluarga Ibrahim dan keluarga ´Imran melebihi segala umat (di masa mereka masing-masing),'),
(327, '003:034', '68', 3, 34, '(sebagai) satu keturunan yang sebagiannya (turunan) dari yang lain. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(328, '003:035', '68', 3, 35, '(Ingatlah), ketika isteri ´Imran berkata: \"Ya Tuhanku, sesungguhnya aku menazarkan kepada Engkau anak yang dalam kandunganku menjadi hamba yang saleh dan berkhidmat (di Baitul Maqdis). Karena itu terimalah (nazar) itu dari padaku. Sesungguhnya Engkaulah Yang Maha Mendengar lagi Maha Mengetahui\".'),
(329, '003:036', '68', 3, 36, 'Maka tatkala isteri ´Imran melahirkan anaknya, diapun berkata: \"Ya Tuhanku, sesunguhnya aku melahirkannya seorang anak perempuan; dan Allah lebih mengetahui apa yang dilahirkannya itu; dan anak laki-laki tidaklah seperti anak perempuan. Sesungguhnya aku telah menamai dia Maryam dan aku mohon perlindungan untuknya serta anak-anak keturunannya kepada (pemeliharaan) Engkau daripada syaitan yang terkutuk\".'),
(330, '003:037', '68', 3, 37, 'Maka Tuhannya menerimanya (sebagai nazar) dengan penerimaan yang baik, dan mendidiknya dengan pendidikan yang baik dan Allah menjadikan Zakariya pemeliharanya. Setiap Zakariya masuk untuk menemui Maryam di mihrab, ia dapati makanan di sisinya. Zakariya berkata: \"Hai Maryam dari mana kamu memperoleh (makanan) ini?\" Maryam menjawab: \"Makanan itu dari sisi Allah\". Sesungguhnya Allah memberi rezeki kepada siapa yang dikehendaki-Nya tanpa hisab.'),
(331, '003:038', '68', 3, 38, 'Di sanalah Zakariya mendoa kepada Tuhannya seraya berkata: \"Ya Tuhanku, berilah aku dari sisi Engkau seorang anak yang baik. Sesungguhnya Engkau Maha Pendengar doa\".'),
(332, '003:039', '68', 3, 39, 'Kemudian Malaikat (Jibril) memanggil Zakariya, sedang ia tengah berdiri melakukan shalat di mihrab (katanya): \"Sesungguhnya Allah menggembirakan kamu dengan kelahiran (seorang puteramu) Yahya, yang membenarkan kalimat (yang datang) dari Allah, menjadi ikutan, menahan diri (dari hawa nafsu) dan seorang Nabi termasuk keturunan orang-orang saleh\".'),
(333, '003:040', '68', 3, 40, 'Zakariya berkata: \"Ya Tuhanku, bagaimana aku bisa mendapat anak sedang aku telah sangat tua dan isteriku pun seorang yang mandul?\". Berfirman Allah: \"Demikianlah, Allah berbuat apa yang dikehendaki-Nya\".'),
(334, '003:041', '68', 3, 41, 'Berkata Zakariya: \"Berilah aku suatu tanda (bahwa isteriku telah mengandung)\". Allah berfirman: \"Tandanya bagimu, kamu tidak dapat berkata-kata dengan manusia selama tiga hari, kecuali dengan isyarat. Dan sebutlah (nama) Tuhanmu sebanyak-banyaknya serta bertasbihlah di waktu petang dan pagi hari\".'),
(335, '003:042', '68', 3, 42, 'Dan (ingatlah) ketika Malaikat (Jibril) berkata: \"Hai Maryam, sesungguhnya Allah telah memilih kamu, mensucikan kamu dan melebihkan kamu atas segala wanita di dunia (yang semasa dengan kamu).'),
(336, '003:043', '68', 3, 43, 'Hai Maryam, taatlah kepada Tuhanmu, sujud dan ruku´lah bersama orang-orang yang ruku´.'),
(337, '003:044', '68', 3, 44, 'Yang demikian itu adalah sebagian dari berita-berita ghaib yang Kami wahyukan kepada kamu (ya Muhammad); padahal kamu tidak hadir beserta mereka, ketika mereka melemparkan anak-anak panah mereka (untuk mengundi) siapa di antara mereka yang akan memelihara Maryam. Dan kamu tidak hadir di sisi mereka ketika mereka bersengketa.'),
(338, '003:045', '68', 3, 45, '(Ingatlah), ketika Malaikat berkata: \"Hai Maryam, seungguhnya Allah menggembirakan kamu (dengan kelahiran seorang putera yang diciptakan) dengan kalimat (yang datang) daripada-Nya, namanya Al Masih Isa putera Maryam, seorang terkemuka di dunia dan di akhirat dan termasuk orang-orang yang didekatkan (kepada Allah),'),
(339, '003:046', '68', 3, 46, 'dan dia berbicara dengan manusia dalam buaian dan ketika sudah dewasa dan dia adalah termasuk orang-orang yang saleh\".'),
(340, '003:047', '68', 3, 47, 'Maryam berkata: \"Ya Tuhanku, betapa mungkin aku mempunyai anak, padahal aku belum pernah disentuh oleh seorang laki-lakipun\". Allah berfirman (dengan perantaraan Jibril): \"Demikianlah Allah menciptakan apa yang dikehendaki-Nya. Apabila Allah berkehendak menetapkan sesuatu, maka Allah hanya cukup berkata kepadanya: \"Jadilah\", lalu jadilah dia.'),
(341, '003:048', '68', 3, 48, 'Dan Allah akan mengajarkan kepadanya Al Kitab, Hikmah, Taurat dan Injil.'),
(342, '003:049', '68', 3, 49, 'Dan (sebagai) Rasul kepada Bani Israil (yang berkata kepada mereka): \"Sesungguhnya aku telah datang kepadamu dengan membawa sesuatu tanda (mukjizat) dari Tuhanmu, yaitu aku membuat untuk kamu dari tanah berbentuk burung; kemudian aku meniupnya, maka ia menjadi seekor burung dengan seizin Allah; dan aku menyembuhkan orang yang buta sejak dari lahirnya dan orang yang berpenyakit sopak; dan aku menghidupkan orang mati dengan seizin Allah; dan aku kabarkan kepadamu apa yang kamu makan dan apa yang kamu simpan di rumahmu. Sesungguhnya pada yang demikian itu adalah suatu tanda (kebenaran kerasulanku) bagimu, jika kamu sungguh-sungguh beriman.'),
(343, '003:050', '68', 3, 50, 'Dan (aku datang kepadamu) membenarkan Taurat yang datang sebelumku, dan untuk menghalalkan bagimu sebagian yang telah diharamkan untukmu, dan aku datang kepadamu dengan membawa suatu tanda (mukjizat) daripada Tuhanmu. Karena itu bertakwalah kepada Allah dan taatlah kepadaku.'),
(344, '003:051', '68', 3, 51, 'Sesungguhnya Allah, Tuhanku dan Tuhanmu, karena itu sembahlah Dia. Inilah jalan yang lurus\".'),
(345, '003:052', '68', 3, 52, 'Maka tatkala Isa mengetahui keingkaran mereka (Bani lsrail) berkatalah dia: \"Siapakah yang akan menjadi penolong-penolongku untuk (menegakkan agama) Allah?\" Para hawariyyin (sahabat-sahabat setia) menjawab: \"Kamilah penolong-penolong (agama) Allah, kami beriman kepada Allah; dan saksikanlah bahwa sesungguhnya kami adalah orang-orang yang berserah diri.'),
(346, '003:053', '68', 3, 53, 'Ya Tuhan kami, kami telah beriman kepada apa yang telah Engkau turunkan dan telah kami ikuti rasul, karena itu masukanlah kami ke dalam golongan orang-orang yang menjadi saksi (tentang keesaan Allah)\".'),
(347, '003:054', '68', 3, 54, 'Orang-orang kafir itu membuat tipu daya, dan Allah membalas tipu daya mereka itu. Dan Allah sebaik-baik pembalas tipu daya.'),
(348, '003:055', '68', 3, 55, '(Ingatlah), ketika Allah berfirman: \"Hai Isa, sesungguhnya Aku akan menyampaikan kamu kepada akhir ajalmu dan mengangkat kamu kepada-Ku serta membersihkan kamu dari orang-orang yang kafir, dan menjadikan orang-orang yang mengikuti kamu di atas orang-orang yang kafir hingga hari kiamat. Kemudian hanya kepada Akulah kembalimu, lalu Aku memutuskan diantaramu tentang hal-hal yang selalu kamu berselisih padanya\".'),
(349, '003:056', '68', 3, 56, 'Adapun orang-orang yang kafir, maka akan Ku-siksa mereka dengan siksa yang sangat keras di dunia dan di akhirat, dan mereka tidak memperoleh penolong.'),
(350, '003:057', '68', 3, 57, 'Adapun orang-orang yang beriman dan mengerjakan amalan-amalan yang saleh, maka Allah akan memberikan kepada mereka dengan sempurna pahala amalan-amalan mereka; dan Allah tidak menyukai orang-orang yang zalim.'),
(351, '003:058', '68', 3, 58, 'Demikianlah (kisah Isa), Kami membacakannya kepada kamu sebagian dari bukti-bukti (kerasulannya) dan (membacakan) Al Quran yang penuh hikmah.'),
(352, '003:059', '68', 3, 59, 'Sesungguhnya misal (penciptaan) Isa di sisi AllAh, adalah seperti (penciptaan) Adam. Allah menciptakan Adam dari tanah, kemudian Allah berfirman kepadanya: \"Jadilah\" (seorang manusia), maka jadilah dia.'),
(353, '003:060', '68', 3, 60, '(Apa yang telah Kami ceritakan itu), itulah yang benar, yang datang dari Tuhanmu, karena itu janganlah kamu termasuk orang-orang yang ragu-ragu.'),
(354, '003:061', '68', 3, 61, 'Siapa yang membantahmu tentang kisah Isa sesudah datang ilmu (yang meyakinkan kamu), maka katakanlah (kepadanya): \"Marilah kita memanggil anak-anak kami dan anak-anak kamu, isteri-isteri kami dan isteri-isteri kamu, diri kami dan diri kamu; kemudian marilah kita bermubahalah kepada Allah dan kita minta supaya laknat Allah ditimpakan kepada orang-orang yang dusta.'),
(355, '003:062', '68', 3, 62, 'Sesungguhnya ini adalah kisah yang benar, dan tak ada Tuhan (yang berhak disembah) selain Allah; dan sesungguhnya Allah, Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(356, '003:063', '68', 3, 63, 'Kemudian jika mereka berpaling (dari kebenaran), maka sesunguhnya Allah Maha Mengetahui orang-orang yang berbuat kerusakan.'),
(357, '003:064', '68', 3, 64, 'Katakanlah: \"Hai Ahli Kitab, marilah (berpegang) kepada suatu kalimat (ketetapan) yang tidak ada perselisihan antara kami dan kamu, bahwa tidak kita sembah kecuali Allah dan tidak kita persekutukan Dia dengan sesuatupun dan tidak (pula) sebagian kita menjadikan sebagian yang lain sebagai tuhan selain Allah\". Jika mereka berpaling maka katakanlah kepada mereka: \"Saksikanlah, bahwa kami adalah orang-orang yang berserah diri (kepada Allah)\".'),
(358, '003:065', '68', 3, 65, 'Hai Ahli Kitab, mengapa kamu bantah membantah tentang hal Ibrahim, padahal Taurat dan Injil tidak diturunkan melainkan sesudah Ibrahim. Apakah kamu tidak berpikir?'),
(359, '003:066', '68', 3, 66, 'Beginilah kamu, kamu ini (sewajarnya) bantah membantah tentang hal yang kamu ketahui, maka kenapa kamu bantah membantah tentang hal yang tidak kamu ketahui? Allah mengetahui sedang kamu tidak mengetahui.'),
(360, '003:067', '68', 3, 67, 'Ibrahim bukan seorang Yahudi dan bukan (pula) seorang Nasrani, akan tetapi dia adalah seorang yang lurus lagi berserah diri (kepada Allah) dan sekali-kali bukanlah dia termasuk golongan orang-orang musyrik.'),
(361, '003:068', '68', 3, 68, 'Sesungguhnya orang yang paling dekat kepada Ibrahim ialah orang-orang yang mengikutinya dan Nabi ini (Muhammad), beserta orang-orang yang beriman (kepada Muhammad), dan Allah adalah Pelindung semua orang-orang yang beriman.'),
(362, '003:069', '68', 3, 69, 'Segolongan dari Ahli Kitab ingin menyesatkan kamu, padahal mereka (sebenarnya) tidak menyesatkan melainkan dirinya sendiri, dan mereka tidak menyadarinya.'),
(363, '003:070', '68', 3, 70, 'Hai Ahli Kitab, mengapa kamu mengingkari ayat-ayat Allah, padahal kamu mengetahui (kebenarannya).'),
(364, '003:071', '68', 3, 71, 'Hai Ahli Kitab, mengapa kamu mencampur adukkan yang haq dengan yang bathil, dan menyembunyikan kebenaran, padahal kamu mengetahuinya?'),
(365, '003:072', '68', 3, 72, 'Segolongan (lain) dari Ahli Kitab berkata (kepada sesamanya): \"Perlihatkanlah (seolah-olah) kamu beriman kepada apa yang diturunkan kepada orang-orang beriman (sahabat-sahabat Rasul) pada permulaan siang dan ingkarilah ia pada akhirnya, supaya mereka (orang-orang mukmin) kembali (kepada kekafiran).'),
(366, '003:073', '68', 3, 73, 'Dan janganlah kamu percaya melainkan kepada orang yang mengikuti agamamu. Katakanlah: \"Sesungguhnya petunjuk (yang harus diikuti) ialah petunjuk Allah, dan (janganlah kamu percaya) bahwa akan diberikan kepada seseorang seperti apa yang diberikan kepadamu, dan (jangan pula kamu percaya) bahwa mereka akan mengalahkan hujjahmu di sisi Tuhanmu\". Katakanlah: \"Sesungguhnya karunia itu di tangan Allah, Allah memberikan karunia-Nya kepada siapa yang dikehendaki-Nya; dan Allah Maha Luas karunia-Nya) lagi Maha Mengetahui\";'),
(367, '003:074', '68', 3, 74, 'Allah menentukan rahmat-Nya (kenabian) kepada siapa yang dikehendaki-Nya dan Allah mempunyai karunia yang besar.'),
(368, '003:075', '68', 3, 75, 'Di antara Ahli kitab ada orang yang jika kamu mempercayakan kepadanya harta yang banyak, dikembalikannya kepadamu; dan di antara mereka ada orang yang jika kamu mempercayakan kepadanya satu dinar, tidak dikembalikannya kepadamu kecuali jika kamu selalu menagihnya. Yang demikian itu lantaran mereka mengatakan: \"tidak ada dosa bagi kami terhadap orang-orang ummi. Mereka berkata dusta terhadap Allah, padahal mereka mengetahui.'),
(369, '003:076', '68', 3, 76, '(Bukan demikian), sebenarnya siapa yang menepati janji (yang dibuat)nya dan bertakwa, maka sesungguhnya Allah menyukai orang-orang yang bertakwa.'),
(370, '003:077', '68', 3, 77, 'Sesungguhnya orang-orang yang menukar janji (nya dengan) Allah dan sumpah-sumpah mereka dengan harga yang sedikit, mereka itu tidak mendapat bahagian (pahala) di akhirat, dan Allah tidak akan berkata-kata dengan mereka dan tidak akan melihat kepada mereka pada hari kiamat dan tidak (pula) akan mensucikan mereka. Bagi mereka azab yang pedih.'),
(371, '003:078', '68', 3, 78, 'Sesungguhnya diantara mereka ada segolongan yang memutar-mutar lidahnya membaca Al Kitab, supaya kamu menyangka yang dibacanya itu sebagian dari Al Kitab, padahal ia bukan dari Al Kitab dan mereka mengatakan: \"Ia (yang dibaca itu datang) dari sisi Allah\", padahal ia bukan dari sisi Allah. Mereka berkata dusta terhadap Allah sedang mereka mengetahui.'),
(372, '003:079', '68', 3, 79, 'Tidak wajar bagi seseorang manusia yang Allah berikan kepadanya Al Kitab, hikmah dan kenabian, lalu dia berkata kepada manusia: \"Hendaklah kamu menjadi penyembah-penyembahku bukan penyembah Allah\". Akan tetapi (dia berkata): \"Hendaklah kamu menjadi orang-orang rabbani, karena kamu selalu mengajarkan Al Kitab dan disebabkan kamu tetap mempelajarinya.'),
(373, '003:080', '68', 3, 80, 'dan (tidak wajar pula baginya) menyuruhmu menjadikan malaikat dan para nabi sebagai tuhan. Apakah (patut) dia menyuruhmu berbuat kekafiran di waktu kamu sudah (menganut agama) Islam?\".'),
(374, '003:081', '68', 3, 81, 'Dan (ingatlah), ketika Allah mengambil perjanjian dari para nabi: \"Sungguh, apa saja yang Aku berikan kepadamu berupa kitab dan hikmah kemudian datang kepadamu seorang rasul yang membenarkan apa yang ada padamu, niscaya kamu akan sungguh-sungguh beriman kepadanya dan menolongnya\". Allah berfirman: \"Apakah kamu mengakui dan menerima perjanjian-Ku terhadap yang demikian itu?\" Mereka menjawab: \"Kami mengakui\". Allah berfirman: \"Kalau begitu saksikanlah (hai para nabi) dan Aku menjadi saksi (pula) bersama kamu\".'),
(375, '003:082', '68', 3, 82, 'Barang siapa yang berpaling sesudah itu, maka mereka itulah orang-orang yang fasik.'),
(376, '003:083', '68', 3, 83, 'Maka apakah mereka mencari agama yang lain dari agama Allah, padahal kepada-Nya-lah menyerahkan diri segala apa yang di langit dan di bumi, baik dengan suka maupun terpaksa dan hanya kepada Allahlah mereka dikembalikan.'),
(377, '003:084', '68', 3, 84, 'Katakanlah: \"Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami dan yang diturunkan kepada Ibrahim, Ismail, Ishaq, Ya´qub, dan anak-anaknya, dan apa yang diberikan kepada Musa, Isa dan para nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorangpun di antara mereka dan hanya kepada-Nya-lah kami menyerahkan diri\".'),
(378, '003:085', '68', 3, 85, 'Barangsiapa mencari agama selain agama Islam, maka sekali-kali tidaklah akan diterima (agama itu)daripadanya, dan dia di akhirat termasuk orang-orang yang rugi.'),
(379, '003:086', '68', 3, 86, 'Bagaimana Allah akan menunjuki suatu kaum yang kafir sesudah mereka beriman, serta mereka telah mengakui bahwa Rasul itu (Muhammad) benar-benar rasul, dan keterangan-keteranganpun telah datang kepada mereka? Allah tidak menunjuki orang-orang yang zalim.'),
(380, '003:087', '68', 3, 87, 'Mereka itu, balasannya ialah: bahwasanya laknat Allah ditimpakan kepada mereka, (demikian pula) laknat para malaikat dan manusia seluruhnya,'),
(381, '003:088', '68', 3, 88, 'mereka kekal di dalamnya, tidak diringankan siksa dari mereka, dan tidak (pula) mereka diberi tangguh,'),
(382, '003:089', '68', 3, 89, 'kecuali orang-orang yang taubat, sesudah (kafir) itu dan mengadakan perbaikan. Karena sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(383, '003:090', '68', 3, 90, 'Sesungguhnya orang-orang kafir sesudah beriman, kemudian bertambah kekafirannya, sekali-kali tidak akan diterima taubatnya; dan mereka itulah orang-orang yang sesat.'),
(384, '003:091', '68', 3, 91, 'Sesungguhnya orang-orang yang kafir dan mati sedang mereka tetap dalam kekafirannya, maka tidaklah akan diterima dari seseorang diantara mereka emas sepenuh bumi, walaupun dia menebus diri dengan emas (yang sebanyak) itu. Bagi mereka itulah siksa yang pedih dan sekali-kali mereka tidak memperoleh penolong.'),
(385, '003:092', '68', 3, 92, 'Kamu sekali-kali tidak sampai kepada kebajikan (yang sempurna), sebelum kamu menafkahkan sehahagian harta yang kamu cintai. Dan apa saja yang kamu nafkahkan maka sesungguhnya Allah mengetahuinya.'),
(386, '003:093', '68', 3, 93, 'Semua makanan adalah halal bagi Bani Israil melainkan makanan yang diharamkan oleh Israil (Ya´qub) untuk dirinya sendiri sebelum Taurat diturunkan. Katakanlah: \"(Jika kamu mengatakan ada makanan yang diharamkan sebelum turun Taurat), maka bawalah Taurat itu, lalu bacalah dia jika kamu orang-orang yang benar\".'),
(387, '003:094', '68', 3, 94, 'Maka barangsiapa mengada-adakan dusta terhadap Allah sesudah itu, maka merekalah orang-orang yang zalim.'),
(388, '003:095', '68', 3, 95, 'Katakanlah: \"Benarlah (apa yang difirmankan) Allah\". Maka ikutilah agama Ibrahim yang lurus, dan bukanlah dia termasuk orang-orang yang musyrik.'),
(389, '003:096', '68', 3, 96, 'Sesungguhnya rumah yang mula-mula dibangun untuk (tempat beribadat) manusia, ialah Baitullah yang di Bakkah (Mekah) yang diberkahi dan menjadi petunjuk bagi semua manusia.'),
(390, '003:097', '68', 3, 97, 'Padanya terdapat tanda-tanda yang nyata, (di antaranya) maqam Ibrahim; barangsiapa memasukinya (Baitullah itu) menjadi amanlah dia; mengerjakan haji adalah kewajiban manusia terhadap Allah, yaitu (bagi) orang yang sanggup mengadakan perjalanan ke Baitullah. Barangsiapa mengingkari (kewajiban haji), maka sesungguhnya Allah Maha Kaya (tidak memerlukan sesuatu) dari semesta alam.'),
(391, '003:098', '68', 3, 98, 'Katakanlah: \"Hai Ahli Kitab, mengapa kamu ingkari ayat-ayat Allah, padahal Allah Maha menyaksikan apa yang kamu kerjakan?\".'),
(392, '003:099', '68', 3, 99, 'Katakanlah: \"Hai Ahli Kitab, mengapa kamu menghalang-halangi dari jalan Allah orang-orang yang telah beriman, kamu menghendakinya menjadi bengkok, padahal kamu menyaksikan?\". Allah sekali-kali tidak lalai dari apa yang kamu kerjakan.'),
(393, '003:100', '68', 3, 100, 'Hai orang-orang yang beriman, jika kamu mengikuti sebahagian dari orang-orang yang diberi Al Kitab, niscaya mereka akan mengembalikan kamu menjadi orang kafir sesudah kamu beriman.'),
(394, '003:101', '68', 3, 101, 'Bagaimanakah kamu (sampai) menjadi kafir, padahal ayat-ayat Allah dibacakan kepada kamu, dan Rasul-Nya pun berada di tengah-tengah kamu? Barangsiapa yang berpegang teguh kepada (agama) Allah, maka sesungguhnya ia telah diberi petunjuk kepada jalan yang lurus.'),
(395, '003:102', '68', 3, 102, 'Hai orang-orang yang beriman, bertakwalah kepada Allah sebenar-benar takwa kepada-Nya; dan janganlah sekali-kali kamu mati melainkan dalam keadaan beragama Islam.'),
(396, '003:103', '68', 3, 103, 'Dan berpeganglah kamu semuanya kepada tali (agama) Allah, dan janganlah kamu bercerai berai, dan ingatlah akan nikmat Allah kepadamu ketika kamu dahulu (masa Jahiliyah) bermusuh-musuhan, maka Allah mempersatukan hatimu, lalu menjadilah kamu karena nikmat Allah, orang-orang yang bersaudara; dan kamu telah berada di tepi jurang neraka, lalu Allah menyelamatkan kamu dari padanya. Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu, agar kamu mendapat petunjuk.'),
(397, '003:104', '68', 3, 104, 'Dan hendaklah ada di antara kamu segolongan umat yang menyeru kepada kebajikan, menyuruh kepada yang ma´ruf dan mencegah dari yang munkar; merekalah orang-orang yang beruntung.'),
(398, '003:105', '68', 3, 105, 'Dan janganlah kamu menyerupai orang-orang yang bercerai-berai dan berselisih sesudah datang keterangan yang jelas kepada mereka. Mereka itulah orang-orang yang mendapat siksa yang berat,'),
(399, '003:106', '68', 3, 106, 'pada hari yang di waktu itu ada muka yang putih berseri, dan ada pula muka yang hitam muram. Adapun orang-orang yang hitam muram mukanya (kepada mereka dikatakan): \"Kenapa kamu kafir sesudah kamu beriman? Karena itu rasakanlah azab disebabkan kekafiranmu itu\".'),
(400, '003:107', '68', 3, 107, 'Adapun orang-orang yang putih berseri mukanya, maka mereka berada dalam rahmat Allah (surga); mereka kekal di dalamnya.'),
(401, '003:108', '68', 3, 108, 'Itulah ayat-ayat Allah. Kami bacakan ayat-ayat itu kepadamu dengan benar; dan tiadalah Allah berkehendak untuk menganiaya hamba-hamba-Nya.'),
(402, '003:109', '68', 3, 109, 'Kepunyaan Allah-lah segala yang ada di langit dan di bumi; dan kepada Allahlah dikembalikan segala urusan.'),
(403, '003:110', '68', 3, 110, 'Kamu adalah umat yang terbaik yang dilahirkan untuk manusia, menyuruh kepada yang ma´ruf, dan mencegah dari yang munkar, dan beriman kepada Allah. Sekiranya Ahli Kitab beriman, tentulah itu lebih baik bagi mereka, di antara mereka ada yang beriman, dan kebanyakan mereka adalah orang-orang yang fasik.'),
(404, '003:111', '68', 3, 111, 'Mereka sekali-kali tidak akan dapat membuat mudharat kepada kamu, selain dari gangguan-gangguan celaan saja, dan jika mereka berperang dengan kamu, pastilah mereka berbalik melarikan diri ke belakang (kalah). Kemudian mereka tidak mendapat pertolongan.'),
(405, '003:112', '68', 3, 112, 'Mereka diliputi kehinaan di mana saja mereka berada, kecuali jika mereka berpegang kepada tali (agama) Allah dan tali (perjanjian) dengan manusia, dan mereka kembali mendapat kemurkaan dari Allah dan mereka diliputi kerendahan. Yang demikian itu karena mereka kafir kepada ayat-ayat Allah dan membunuh para nabi tanpa alasan yang benar. Yang demikian itu disebabkan mereka durhaka dan melampaui batas.'),
(406, '003:113', '68', 3, 113, 'Mereka itu tidak sama; di antara Ahli Kitab itu ada golongan yang berlaku lurus, mereka membaca ayat-ayat Allah pada beberapa waktu di malam hari, sedang mereka juga bersujud (sembahyang).'),
(407, '003:114', '68', 3, 114, 'Mereka beriman kepada Allah dan hari penghabisan, mereka menyuruh kepada yang ma´ruf, dan mencegah dari yang munkar dan bersegera kepada (mengerjakan) pelbagai kebajikan; mereka itu termasuk orang-orang yang saleh.'),
(408, '003:115', '68', 3, 115, 'Dan apa saja kebajikan yang mereka kerjakan, maka sekali-kali mereka tidak dihalangi (menenerima pahala)nya; dan Allah Maha Mengetahui orang-orang yang bertakwa.'),
(409, '003:116', '68', 3, 116, 'Sesungguhnya orang-orang yang kafir baik harta mereka maupun anak-anak mereka, sekali-kali tidak dapat menolak azab Allah dari mereka sedikitpun. Dan mereka adalah penghuni neraka; mereka kekal di dalamnya.'),
(410, '003:117', '68', 3, 117, 'Perumpamaan harta yang mereka nafkahkan di dalam kehidupan dunia ini, adalah seperti perumpamaan angin yang mengandung hawa yang sangat dingin, yang menimpa tanaman kaum yang menganiaya diri sendiri, lalu angin itu merusaknya. Allah tidak menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.'),
(411, '003:118', '68', 3, 118, 'Hai orang-orang yang beriman, janganlah kamu ambil menjadi teman kepercayaanmu orang-orang yang, di luar kalanganmu (karena) mereka tidak henti-hentinya (menimbulkan) kemudharatan bagimu. Mereka menyukai apa yang menyusahkan kamu. Telah nyata kebencian dari mulut mereka, dan apa yang disembunyikan oleh hati mereka adalah lebih besar lagi. Sungguh telah Kami terangkan kepadamu ayat-ayat (Kami), jika kamu memahaminya.'),
(412, '003:119', '68', 3, 119, 'Beginilah kamu, kamu menyukai mereka, padahal mereka tidak menyukai kamu, dan kamu beriman kepada kitab-kitab semuanya. Apabila mereka menjumpai kamu, mereka berkata \"Kami beriman\", dan apabila mereka menyendiri, mereka menggigit ujung jari antaran marah bercampur benci terhadap kamu. Katakanlah (kepada mereka): \"Matilah kamu karena kemarahanmu itu\". Sesungguhnya Allah mengetahui segala isi hati.'),
(413, '003:120', '68', 3, 120, 'Jika kamu memperoleh kebaikan, niscaya mereka bersedih hati, tetapi Jika kamu mendapat bencana, mereka bergembira karenanya. Jika kamu bersabar dan bertakwa, niscaya tipu daya mereka sedikitpun tidak mendatangkan kemudharatan kepadamu. Sesungguhnya Allah mengetahui segala apa yang mereka kerjakan.'),
(414, '003:121', '68', 3, 121, 'Dan (ingatlah), ketika kamu berangkat pada pagi hari dari (rumah) keluargamu akan menempatkan para mukmin pada beberapa tempat untuk berperang. Dan Allah Maha Mendengar lagi Maha Mengetahui,'),
(415, '003:122', '68', 3, 122, 'ketika dua golongan dari padamu ingin (mundur) karena takut, padahal Allah adalah penolong bagi kedua golongan itu. Karena itu hendaklah kepada Allah saja orang-orang mukmin bertawakkal.'),
(416, '003:123', '68', 3, 123, 'Sungguh Allah telah menolong kamu dalam peperangan Badar, padahal kamu adalah (ketika itu) orang-orang yang lemah. Karena itu bertakwalah kepada Allah, supaya kamu mensyukuri-Nya.'),
(417, '003:124', '68', 3, 124, '(Ingatlah), ketika kamu mengatakan kepada orang mukmin: \"Apakah tidak cukup bagi kamu Allah membantu kamu dengan tiga ribu malaikat yang diturunkan (dari langit)?\"'),
(418, '003:125', '68', 3, 125, 'Ya (cukup), jika kamu bersabar dan bersiap-siaga, dan mereka datang menyerang kamu dengan seketika itu juga, niscaya Allah menolong kamu dengan lima ribu Malaikat yang memakai tanda.'),
(419, '003:126', '68', 3, 126, 'Dan Allah tidak menjadikan pemberian bala bantuan itu melainkan sebagai khabar gembira bagi (kemenangan)mu, dan agar tenteram hatimu karenanya. Dan kemenanganmu itu hanyalah dari Allah Yang Maha Perkasa lagi Maha Bijaksana.'),
(420, '003:127', '68', 3, 127, '(Allah menolong kamu dalam perang Badar dan memberi bala bantuan itu) untuk membinasakan segolongan orang-orang yang kafir, atau untuk menjadikan mereka hina, lalu mereka kembali dengan tiada memperoleh apa-apa.'),
(421, '003:128', '68', 3, 128, 'Tak ada sedikitpun campur tanganmu dalam urusan mereka itu atau Allah menerima taubat mereka, atau mengazab mereka karena sesungguhnya mereka itu orang-orang yang zalim.'),
(422, '003:129', '68', 3, 129, 'Kepunyaan Allah apa yang ada di langit dan yang ada di bumi. Dia memberi ampun kepada siapa yang Dia kehendaki; Dia menyiksa siapa yang Dia kehendaki, dan Allah Maha Pengampun lagi Maha Penyayang.'),
(423, '003:130', '68', 3, 130, 'Hai orang-orang yang beriman, janganlah kamu memakan riba dengan berlipat ganda] dan bertakwalah kamu kepada Allah supaya kamu mendapat keberuntungan.'),
(424, '003:131', '68', 3, 131, 'Dan peliharalah dirimu dari api neraka, yang disediakan untuk orang-orang yang kafir.'),
(425, '003:132', '68', 3, 132, 'Dan taatilah Allah dan Rasul, supaya kamu diberi rahmat.'),
(426, '003:133', '68', 3, 133, 'Dan bersegeralah kamu kepada ampunan dari Tuhanmu dan kepada surga yang luasnya seluas langit dan bumi yang disediakan untuk orang-orang yang bertakwa,'),
(427, '003:134', '68', 3, 134, '(yaitu) orang-orang yang menafkahkan (hartanya), baik di waktu lapang maupun sempit, dan orang-orang yang menahan amarahnya dan memaafkan (kesalahan) orang. Allah menyukai orang-orang yang berbuat kebajikan.'),
(428, '003:135', '68', 3, 135, 'Dan (juga) orang-orang yang apabila mengerjakan perbuatan keji atau menganiaya diri sendiri, mereka ingat akan Allah, lalu memohon ampun terhadap dosa-dosa mereka dan siapa lagi yang dapat mengampuni dosa selain dari pada Allah? Dan mereka tidak meneruskan perbuatan kejinya itu, sedang mereka mengetahui.'),
(429, '003:136', '68', 3, 136, 'Mereka itu balasannya ialah ampunan dari Tuhan mereka dan surga yang di dalamnya mengalir sungai-sungai, sedang mereka kekal di dalamnya; dan itulah sebaik-baik pahala orang-orang yang beramal.'),
(430, '003:137', '68', 3, 137, 'Sesungguhnya telah berlalu sebelum kamu sunnah-sunnah Allah; Karena itu berjalanlah kamu di muka bumi dan perhatikanlah bagaimana akibat orang-orang yang mendustakan (rasul-rasul).'),
(431, '003:138', '68', 3, 138, '(Al Quran) ini adalah penerangan bagi seluruh manusia, dan petunjuk serta pelajaran bagi orang-orang yang bertakwa.'),
(432, '003:139', '68', 3, 139, 'Janganlah kamu bersikap lemah, dan janganlah (pula) kamu bersedih hati, padahal kamulah orang-orang yang paling tinggi (derajatnya), jika kamu orang-orang yang beriman.'),
(433, '003:140', '68', 3, 140, 'Jika kamu (pada perang Uhud) mendapat luka, maka sesungguhnya kaum (kafir) itupun (pada perang Badar) mendapat luka yang serupa. Dan masa (kejayaan dan kehancuran) itu Kami pergilirkan diantara manusia (agar mereka mendapat pelajaran); dan supaya Allah membedakan orang-orang yang beriman (dengan orang-orang kafir) supaya sebagian kamu dijadikan-Nya (gugur sebagai) syuhada´. Dan Allah tidak menyukai orang-orang yang zalim,'),
(434, '003:141', '68', 3, 141, 'Dan agar Allah membersihkan orang-orang yang beriman (dari dosa mereka) dan membinasakan orang-orang yang kafir.'),
(435, '003:142', '68', 3, 142, 'Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum nyata bagi Allah orang-orang yang berjihad diantaramu dan belum nyata orang-orang yang sabar.'),
(436, '003:143', '68', 3, 143, 'Sesungguhnya kamu mengharapkan mati (syahid) sebelum kamu menghadapinya; (sekarang) sungguh kamu telah melihatnya dan kamu menyaksikannya.'),
(437, '003:144', '68', 3, 144, 'Muhammad itu tidak lain hanyalah seorang rasul, sungguh telah berlalu sebelumnya beberapa orang rasul. Apakah Jika dia wafat atau dibunuh kamu berbalik ke belakang (murtad)? Barangsiapa yang berbalik ke belakang, maka ia tidak dapat mendatangkan mudharat kepada Allah sedikitpun, dan Allah akan memberi balasan kepada orang-orang yang bersyukur.'),
(438, '003:145', '68', 3, 145, 'Sesuatu yang bernyawa tidak akan mati melainkan dengan izin Allah, sebagai ketetapan yang telah ditentukan waktunya. Barang siapa menghendaki pahala dunia, niscaya Kami berikan kepadanya pahala dunia itu, dan barang siapa menghendaki pahala akhirat, Kami berikan (pula) kepadanya pahala akhirat itu. Dan kami akan memberi balasan kepada orang-orang yang bersyukur.'),
(439, '003:146', '68', 3, 146, 'Dan berapa banyaknya nabi yang berperang bersama-sama mereka sejumlah besar dari pengikut(nya) yang bertakwa. Mereka tidak menjadi lemah karena bencana yang menimpa mereka di jalan Allah, dan tidak lesu dan tidak (pula) menyerah (kepada musuh). Allah menyukai orang-orang yang sabar.'),
(440, '003:147', '68', 3, 147, 'Tidak ada doa mereka selain ucapan: \"Ya Tuhan kami, ampunilah dosa-dosa kami dan tindakan-tindakan kami yang berlebih-lebihan dalam urusan kami dan tetapkanlah pendirian kami, dan tolonglah kami terhadap kaum yang kafir\".'),
(441, '003:148', '68', 3, 148, 'Karena itu Allah memberikan kepada mereka pahala di dunia dan pahala yang baik di akhirat. Dan Allah menyukai orang-orang yang berbuat kebaikan.'),
(442, '003:149', '68', 3, 149, 'Hai orang-orang yang beriman, jika kamu mentaati orang-orang yang kafir itu, niscaya mereka mengembalikan kamu ke belakang (kepada kekafiran), lalu jadilah kamu orang-orang yang rugi.'),
(443, '003:150', '68', 3, 150, 'Tetapi (ikutilah Allah), Allahlah Pelindungmu, dan Dialah sebaik-baik Penolong.'),
(444, '003:151', '68', 3, 151, 'Akan Kami masukkan ke dalam hati orang-orang kafir rasa takut, disebabkan mereka mempersekutukan Allah dengan sesuatu yang Allah sendiri tidak menurunkan keterangan tentang itu. Tempat kembali mereka ialah neraka; dan itulah seburuk-buruk tempat tinggal orang-orang yang zalim.'),
(445, '003:152', '68', 3, 152, 'Dan sesungguhnya Allah telah memenuhi janji-Nya kepada kamu, ketika kamu membunuh mereka dengan izin-Nya sampai pada saat kamu lemah dan berselisih dalam urusan itu dan mendurhakai perintah (Rasul) sesudah Allah memperlihatkan kepadamu apa yang kamu sukai. Di antaramu ada orang yang menghendaki dunia dan diantara kamu ada orang yang menghendaki akhirat. Kemudian Allah memalingkan kamu dari mereka untuk menguji kamu, dan sesunguhnya Allah telah memaafkan kamu. Dan Allah mempunyai karunia (yang dilimpahkan) atas orang orang yang beriman.'),
(446, '003:153', '68', 3, 153, '(Ingatlah) ketika kamu lari dan tidak menoleh kepada seseorangpun, sedang Rasul yang berada di antara kawan-kawanmu yang lain memanggil kamu, karena itu Allah menimpakan atas kamu kesedihan atas kesedihan, supaya kamu jangan bersedih hati terhadap apa yang luput dari pada kamu dan terhadap apa yang menimpa kamu. Allah Maha Mengetahui apa yang kamu kerjakan.'),
(447, '003:154', '68', 3, 154, 'Kemudian setelah kamu berdukacita, Allah menurunkan kepada kamu keamanan (berupa) kantuk yang meliputi segolongan dari pada kamu, sedang segolongan lagi telah dicemaskan oleh diri mereka sendiri, mereka menyangka yang tidak benar terhadap Allah seperti sangkaan jahiliyah. Mereka berkata: \"Apakah ada bagi kita barang sesuatu (hak campur tangan) dalam urusan ini?\". Katakanlah: \"Sesungguhnya urusan itu seluruhnya di tangan Allah\". Mereka menyembunyikan dalam hati mereka apa yang tidak mereka terangkan kepadamu; mereka berkata: \"Sekiranya ada bagi kita barang sesuatu (hak campur tangan) dalam urusan ini, niscaya kita tidak akan dibunuh (dikalahkan) di sini\". Katakanlah: \"Sekiranya kamu berada di rumahmu, niscaya orang-orang yang telah ditakdirkan akan mati terbunuh itu keluar (juga) ke tempat mereka terbunuh\". Dan Allah (berbuat demikian) untuk menguji apa yang ada dalam dadamu dan untuk membersihkan apa yang ada dalam hatimu. Allah Maha Mengetahui isi hati.'),
(448, '003:155', '68', 3, 155, 'Sesungguhnya orang-orang yang berpaling di antaramu pada hari bertemu dua pasukan itu, hanya saja mereka digelincirkan oleh syaitan, disebabkan sebagian kesalahan yang telah mereka perbuat (di masa lampau) dan sesungguhnya Allah telah memberi maaf kepada mereka. Sesungguhnya Allah Maha Pengampun lagi Maha Penyantun.'),
(449, '003:156', '68', 3, 156, 'Hai orang-orang yang beriman, janganlah kamu seperti orang-orang kafir (orang-orang munafik) itu, yang mengatakan kepada saudara-saudara mereka apabila mereka mengadakan perjalanan di muka bumi atau mereka berperang: \"Kalau mereka tetap bersama-sama kita tentulah mereka tidak mati dan tidak dibunuh\". Akibat (dari perkataan dan keyakinan mereka) yang demikian itu, Allah menimbulkan rasa penyesalan yang sangat di dalam hati mereka. Allah menghidupkan dan mematikan. Dan Allah melihat apa yang kamu kerjakan.'),
(450, '003:157', '68', 3, 157, 'Dan sungguh kalau kamu gugur di jalan Allah atau meninggal, tentulah ampunan Allah dan rahmat-Nya lebih baik (bagimu) dari harta rampasan yang mereka kumpulkan.'),
(451, '003:158', '68', 3, 158, 'Dan sungguh jika kamu meninggal atau gugur, tentulah kepada Allah saja kamu dikumpulkan.'),
(452, '003:159', '68', 3, 159, 'Maka disebabkan rahmat dari Allah-lah kamu berlaku lemah lembut terhadap mereka. Sekiranya kamu bersikap keras lagi berhati kasar, tentulah mereka menjauhkan diri dari sekelilingmu. Karena itu maafkanlah mereka, mohonkanlah ampun bagi mereka, dan bermusyawaratlah dengan mereka dalam urusan itu. Kemudian apabila kamu telah membulatkan tekad, maka bertawakkallah kepada Allah. Sesungguhnya Allah menyukai orang-orang yang bertawakkal kepada-Nya.'),
(453, '003:160', '68', 3, 160, 'Jika Allah menolong kamu, maka tak adalah orang yang dapat mengalahkan kamu; jika Allah membiarkan kamu (tidak memberi pertolongan), maka siapakah gerangan yang dapat menolong kamu (selain) dari Allah sesudah itu? Karena itu hendaklah kepada Allah saja orang-orang mukmin bertawakkal.'),
(454, '003:161', '68', 3, 161, 'Tidak mungkin seorang nabi berkhianat dalam urusan harta rampasan perang. Barangsiapa yang berkhianat dalam urusan rampasan perang itu, maka pada hari kiamat ia akan datang membawa apa yang dikhianatkannya itu, kemudian tiap-tiap diri akan diberi pembalasan tentang apa yang ia kerjakan dengan (pembalasan) setimpal, sedang mereka tidak dianiaya.'),
(455, '003:162', '68', 3, 162, 'Apakah orang yang mengikuti keridhaan Allah sama dengan orang yang kembali membawa kemurkaan (yang besar) dari Allah dan tempatnya adalah Jahannam? Dan itulah seburuk-buruk tempat kembali.'),
(456, '003:163', '68', 3, 163, '(Kedudukan) mereka itu bertingkat-tingkat di sisi Allah, dan Allah Maha Melihat apa yang mereka kerjakan.'),
(457, '003:164', '68', 3, 164, 'Sungguh Allah telah memberi karunia kepada orang-orang yang beriman ketika Allah mengutus diantara mereka seorang rasul dari golongan mereka sendiri, yang membacakan kepada mereka ayat-ayat Allah, membersihkan (jiwa) mereka, dan mengajarkan kepada mereka Al Kitab dan Al Hikmah. Dan sesungguhnya sebelum (kedatangan Nabi) itu, mereka adalah benar-benar dalam kesesatan yang nyata.'),
(458, '003:165', '68', 3, 165, 'Dan mengapa ketika kamu ditimpa musibah (pada peperangan Uhud), padahal kamu telah menimpakan kekalahan dua kali lipat kepada musuh-musuhmu (pada peperangan Badar), kamu berkata: \"Darimana datangnya (kekalahan) ini?\" Katakanlah: \"Itu dari (kesalahan) dirimu sendiri\". Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(459, '003:166', '68', 3, 166, 'Dan apa yang menimpa kamu pada hari bertemunya dua pasukan, maka (kekalahan) itu adalah dengan izin (takdir) Allah, dan agar Allah mengetahui siapa orang-orang yang beriman.'),
(460, '003:167', '68', 3, 167, 'Dan supaya Allah mengetahui siapa orang-orang yang munafik. Kepada mereka dikatakan: \"Marilah berperang di jalan Allah atau pertahankanlah (dirimu)\". Mereka berkata: \"Sekiranya kami mengetahui akan terjadi peperangan, tentulah kami mengikuti kamu\". Mereka pada hari itu lebih dekat kepada kekafiran dari pada keimanan. Mereka mengatakan dengan mulutnya apa yang tidak terkandung dalam hatinya. Dan Allah lebih mengetahui dalam hatinya. Dan Allah lebih mengetahui apa yang mereka sembunyikan.'),
(461, '003:168', '68', 3, 168, 'Orang-orang yang mengatakan kepada saudara-saudaranya dan mereka tidak turut pergi berperang: \"Sekiranya mereka mengikuti kita, tentulah mereka tidak terbunuh\". Katakanlah: \"Tolaklah kematian itu dari dirimu, jika kamu orang-orang yang benar\".'),
(462, '003:169', '68', 3, 169, 'Janganlah kamu mengira bahwa orang-orang yang gugur di jalan Allah itu mati; bahkan mereka itu hidup disisi Tuhannya dengan mendapat rezeki.'),
(463, '003:170', '68', 3, 170, 'Mereka dalam keadaan gembira disebabkan karunia Allah yang diberikan-Nya kepada mereka, dan mereka bergirang hati terhadap orang-orang yang masih tinggal di belakang yang belum menyusul mereka, bahwa tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(464, '003:171', '68', 3, 171, 'Mereka bergirang hati dengan nikmat dan karunia yang yang besar dari Allah, dan bahwa Allah tidak menyia-nyiakan pahala orang-orang yang beriman.'),
(465, '003:172', '68', 3, 172, '(Yaitu) orang-orang yang mentaati perintah Allah dan Rasul-Nya sesudah mereka mendapat luka (dalam peperangan Uhud). Bagi orang-orang yang berbuat kebaikan diantara mereka dan yang bertakwa ada pahala yang besar.'),
(466, '003:173', '68', 3, 173, '(Yaitu) orang-orang (yang mentaati Allah dan Rasul) yang kepada mereka ada orang-orang yang mengatakan: \"Sesungguhnya manusia telah mengumpulkan pasukan untuk menyerang kamu, karena itu takutlah kepada mereka\", maka perkataan itu menambah keimanan mereka dan mereka menjawab: \"Cukuplah Allah menjadi Penolong kami dan Allah adalah sebaik-baik Pelindung\".'),
(467, '003:174', '68', 3, 174, 'Maka mereka kembali dengan nikmat dan karunia (yang besar) dari Allah, mereka tidak mendapat bencana apa-apa, mereka mengikuti keridhaan Allah. Dan Allah mempunyai karunia yang besar.'),
(468, '003:175', '68', 3, 175, 'Sesungguhnya mereka itu tidak lain hanyalah syaitan yang menakut-nakuti (kamu) dengan kawan-kawannya (orang-orang musyrik Quraisy), karena itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, jika kamu benar-benar orang yang beriman.'),
(469, '003:176', '68', 3, 176, 'Janganlah kamu disedihkan oleh orang-orang yang segera menjadi kafir; sesungguhnya mereka tidak sekali-kali dapat memberi mudharat kepada Allah sedikitpun. Allah berkehendak tidak akan memberi sesuatu bahagian (dari pahala) kepada mereka di hari akhirat, dan bagi mereka azab yang besar.'),
(470, '003:177', '68', 3, 177, 'Sesungguhnya orang-orang yang menukar iman dengan kekafiran, sekali-kali mereka tidak dapat memberi mudharat kepada Allah sedikitpun; dan bagi mereka azab yang pedih.'),
(471, '003:178', '68', 3, 178, 'Dan janganlah sekali-kali orang-orang kafir menyangka, bahwa pemberian tangguh Kami kepada mereka adalah lebih baik bagi mereka. Sesungguhnya Kami memberi tangguh kepada mereka hanyalah supaya bertambah-tambah dosa mereka; dan bagi mereka azab yang menghinakan.'),
(472, '003:179', '68', 3, 179, 'Allah sekali-kali tidak akan membiarkan orang-orang yang beriman dalam keadaan kamu sekarang ini, sehingga Dia menyisihkan yang buruk (munafik) dari yang baik (mukmin). Dan Allah sekali-kali tidak akan memperlihatkan kepada kamu hal-hal yang ghaib, akan tetapi Allah memilih siapa yang dikehendaki-Nya di antara rasul-rasul-Nya. Karena itu berimanlah kepada Allah dan rasul-rasul-Nya; dan jika kamu beriman dan bertakwa, maka bagimu pahala yang besar.'),
(473, '003:180', '68', 3, 180, 'Sekali-kali janganlah orang-orang yang bakhil dengan harta yang Allah berikan kepada mereka dari karunia-Nya menyangka, bahwa kebakhilan itu baik bagi mereka. Sebenarnya kebakhilan itu adalah buruk bagi mereka. Harta yang mereka bakhilkan itu akan dikalungkan kelak di lehernya di hari kiamat. Dan kepunyaan Allah-lah segala warisan (yang ada) di langit dan di bumi. Dan Allah mengetahui apa yang kamu kerjakan.'),
(474, '003:181', '68', 3, 181, 'Sesungguhnya Allah telah mendengar perkatan orang-orang yang mengatakan: \"Sesunguhnya Allah miskin dan kami kaya\". Kami akan mencatat perkataan mereka itu dan perbuatan mereka membunuh nabi-nabi tanpa alasan yang benar, dan Kami akan mengatakan (kepada mereka): \"Rasakanlah olehmu azab yang mem bakar\".'),
(475, '003:182', '68', 3, 182, '(Azab) yang demikian itu adalah disebabkan perbuatan tanganmu sendiri, dan bahwasanya Allah sekali-kali tidak menganiaya hamba-hamba-Nya.'),
(476, '003:183', '68', 3, 183, '(Yaitu) orang-orang (Yahudi) yang mengatakan: \"Sesungguhnya Allah telah memerintahkan kepada kami, supaya kami jangan beriman kepada seseorang rasul, sebelum dia mendatangkan kepada kami korban yang dimakan api\". Katakanlah: \"Sesungguhnya telah datang kepada kamu beberapa orang rasul sebelumku membawa keterangan-keterangan yang nyata dan membawa apa yang kamu sebutkan, maka mengapa kamu membunuh mereka jika kamu adalah orang-orang yang benar\".'),
(477, '003:184', '68', 3, 184, 'Jika mereka mendustakan kamu, maka sesungguhnya rasul-rasul sebelum kamupun telah didustakan (pula), mereka membawa mukjizat-mukjizat yang nyata, Zabur dan kitab yang memberi penjelasan yang sempurna.'),
(478, '003:185', '68', 3, 185, 'Tiap-tiap yang berjiwa akan merasakan mati. Dan sesungguhnya pada hari kiamat sajalah disempurnakan pahalamu. Barangsiapa dijauhkan dari neraka dan dimasukkan ke dalam surga, maka sungguh ia telah beruntung. Kehidupan dunia itu tidak lain hanyalah kesenangan yang memperdayakan.'),
(479, '003:186', '68', 3, 186, 'Kamu sungguh-sungguh akan diuji terhadap hartamu dan dirimu. Dan (juga) kamu sungguh-sungguh akan mendengar dari orang-orang yang diberi kitab sebelum kamu dan dari orang-orang yang mempersekutukan Allah, gangguan yang banyak yang menyakitkan hati. Jika kamu bersabar dan bertakwa, maka sesungguhnya yang demikian itu termasuk urusan yang patut diutamakan.'),
(480, '003:187', '68', 3, 187, 'Dan (ingatlah), ketika Allah mengambil janji dari orang-orang yang telah diberi kitab (yaitu): \"Hendaklah kamu menerangkan isi kitab itu kepada manusia, dan jangan kamu menyembunyikannya,\" lalu mereka melemparkan janji itu ke belakang punggung mereka dan mereka menukarnya dengan harga yang sedikit. Amatlah buruknya tukaran yang mereka terima.'),
(481, '003:188', '68', 3, 188, 'Janganlah sekali-kali kamu menyangka, hahwa orang-orang yang gembira dengan apa yang telah mereka kerjakan dan mereka suka supaya dipuji terhadap perbuatan yang belum mereka kerjakan janganlah kamu menyangka bahwa mereka terlepas dari siksa, dan bagi mereka siksa yang pedih.'),
(482, '003:189', '68', 3, 189, 'kepunyaan Allah-lah kerajaan langit dan bumi, dan Allah Maha Perkasa atas segala sesuatu.'),
(483, '003:190', '68', 3, 190, 'Sesungguhnya dalam penciptaan langit dan bumi, dan silih bergantinya malam dan siang terdapat tanda-tanda bagi orang-orang yang berakal,'),
(484, '003:191', '68', 3, 191, '(yaitu) orang-orang yang mengingat Allah sambil berdiri atau duduk atau dalam keadan berbaring dan mereka memikirkan tentang penciptaan langit dan bumi (seraya berkata): \"Ya Tuhan kami, tiadalah Engkau menciptakan ini dengan sia-sia, Maha Suci Engkau, maka peliharalah kami dari siksa neraka.'),
(485, '003:192', '68', 3, 192, 'Ya Tuhan kami, sesungguhnya barangsiapa yang Engkau masukkan ke dalam neraka, maka sungguh telah Engkau hinakan ia, dan tidak ada bagi orang-orang yang zalim seorang penolongpun.'),
(486, '003:193', '68', 3, 193, 'Ya Tuhan kami, sesungguhnya kami mendengar (seruan) yang menyeru kepada iman, (yaitu): \"Berimanlah kamu kepada Tuhanmu\", maka kamipun beriman. Ya Tuhan kami, ampunilah bagi kami dosa-dosa kami dan hapuskanlah dari kami kesalahan-kesalahan kami, dan wafatkanlah kami beserta orang-orang yang banyak berbakti.'),
(487, '003:194', '68', 3, 194, 'Ya Tuhan kami, berilah kami apa yang telah Engkau janjikan kepada kami dengan perantaraan rasul-rasul Engkau. Dan janganlah Engkau hinakan kami di hari kiamat. Sesungguhnya Engkau tidak menyalahi janji\".');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(488, '003:195', '68', 3, 195, 'Maka Tuhan mereka memperkenankan permohonannya (dengan berfirman): \"Sesungguhnya Aku tidak menyia-nyiakan amal orang-orang yang beramal di antara kamu, baik laki-laki atau perempuan, (karena) sebagian kamu adalah turunan dari sebagian yang lain. Maka orang-orang yang berhijrah, yang diusir dari kampung halamannya, yang disakiti pada jalan-Ku, yang berperang dan yang dibunuh, pastilah akan Ku-hapuskan kesalahan-kesalahan mereka dan pastilah Aku masukkan mereka ke dalam surga yang mengalir sungai-sungai di bawahnya, sebagai pahala di sisi Allah. Dan Allah pada sisi-Nya pahala yang baik\".'),
(489, '003:196', '68', 3, 196, 'Janganlah sekali-kali kamu terperdaya oleh kebebasan orang-orang kafir bergerak di dalam negeri.'),
(490, '003:197', '68', 3, 197, 'Itu hanyalah kesenangan sementara, kemudian tempat tinggal mereka ialah Jahannam; dan Jahannam itu adalah tempat yang seburuk-buruknya.'),
(491, '003:198', '68', 3, 198, 'Akan tetapi orang-orang yang bertakwa kepada Tuhannya, bagi mereka surga yang mengalir sungai-sungai di dalamnya, sedang mereka kekal di dalamnya sebagai tempat tinggal (anugerah) dari sisi Allah. Dan apa yang di sisi Allah adalah lebih baik bagi orang-orang yang berbakti.'),
(492, '003:199', '68', 3, 199, 'Dan sesungguhnya diantara ahli kitab ada orang yang beriman kepada Allah dan kepada apa yang diturunkan kepada kamu dan yang diturunkan kepada mereka sedang mereka berendah hati kepada Allah dan mereka tidak menukarkan ayat-ayat Allah dengan harga yang sedikit. Mereka memperoleh pahala di sisi Tuhannya. Sesungguhnya Allah amat cepat perhitungan-Nya.'),
(493, '003:200', '68', 3, 200, 'Hai orang-orang yang beriman, bersabarlah kamu dan kuatkanlah kesabaranmu dan tetaplah bersiap siaga (di perbatasan negerimu) dan bertakwalah kepada Allah, supaya kamu beruntung.'),
(494, '004:001', '68', 4, 1, 'Hai sekalian manusia, bertakwalah kepada Tuhan-mu yang telah menciptakan kamu dari seorang diri, dan dari padanya Allah menciptakan isterinya; dan dari pada keduanya Allah memperkembang biakkan laki-laki dan perempuan yang banyak. Dan bertakwalah kepada Allah yang dengan (mempergunakan) nama-Nya kamu saling meminta satu sama lain, dan (peliharalah) hubungan silaturrahim. Sesungguhnya Allah selalu menjaga dan mengawasi kamu.'),
(495, '004:002', '68', 4, 2, 'Dan berikanlah kepada anak-anak yatim (yang sudah balig) harta mereka, jangan kamu menukar yang baik dengan yang buruk dan jangan kamu makan harta mereka bersama hartamu. Sesungguhnya tindakan-tindakan (menukar dan memakan) itu, adalah dosa yang besar.'),
(496, '004:003', '68', 4, 3, 'Dan jika kamu takut tidak akan dapat berlaku adil terhadap (hak-hak) perempuan yang yatim (bilamana kamu mengawininya), maka kawinilah wanita-wanita (lain) yang kamu senangi: dua, tiga atau empat. Kemudian jika kamu takut tidak akan dapat berlaku adil, maka (kawinilah) seorang saja, atau budak-budak yang kamu miliki. Yang demikian itu adalah lebih dekat kepada tidak berbuat aniaya.'),
(497, '004:004', '68', 4, 4, 'Berikanlah maskawin (mahar) kepada wanita (yang kamu nikahi) sebagai pemberian dengan penuh kerelaan. Kemudian jika mereka menyerahkan kepada kamu sebagian dari maskawin itu dengan senang hati, maka makanlah (ambillah) pemberian itu (sebagai makanan) yang sedap lagi baik akibatnya.'),
(498, '004:005', '68', 4, 5, 'Dan janganlah kamu serahkan kepada orang-orang yang belum sempurna akalnya, harta (mereka yang ada dalam kekuasaanmu) yang dijadikan Allah sebagai pokok kehidupan. Berilah mereka belanja dan pakaian (dari hasil harta itu) dan ucapkanlah kepada mereka kata-kata yang baik.'),
(499, '004:006', '68', 4, 6, 'Dan ujilah anak yatim itu sampai mereka cukup umur untuk kawin. Kemudian jika menurut pendapatmu mereka telah cerdas (pandai memelihara harta), maka serahkanlah kepada mereka harta-hartanya. Dan janganlah kamu makan harta anak yatim lebih dari batas kepatutan dan (janganlah kamu) tergesa-gesa (membelanjakannya) sebelum mereka dewasa. Barang siapa (di antara pemelihara itu) mampu, maka hendaklah ia menahan diri (dari memakan harta anak yatim itu) dan barangsiapa yang miskin, maka bolehlah ia makan harta itu menurut yang patut. Kemudian apabila kamu menyerahkan harta kepada mereka, maka hendaklah kamu adakan saksi-saksi (tentang penyerahan itu) bagi mereka. Dan cukuplah Allah sebagai Pengawas (atas persaksian itu).'),
(500, '004:007', '68', 4, 7, 'Bagi orang laki-laki ada hak bagian dari harta peninggalan ibu-bapa dan kerabatnya, dan bagi orang wanita ada hak bagian (pula) dari harta peninggalan ibu-bapa dan kerabatnya, baik sedikit atau banyak menurut bahagian yang telah ditetapkan.'),
(501, '004:008', '68', 4, 8, 'Dan apabila sewaktu pembagian itu hadir kerabat, anak yatim dan orang miskin, maka berilah mereka dari harta itu (sekedarnya) dan ucapkanlah kepada mereka perkataan yang baik.'),
(502, '004:009', '68', 4, 9, 'Dan hendaklah takut kepada Allah orang-orang yang seandainya meninggalkan dibelakang mereka anak-anak yang lemah, yang mereka khawatir terhadap (kesejahteraan) mereka. Oleh sebab itu hendaklah mereka bertakwa kepada Allah dan hendaklah mereka mengucapkan perkataan yang benar.'),
(503, '004:010', '68', 4, 10, 'Sesungguhnya orang-orang yang memakan harta anak yatim secara zalim, sebenarnya mereka itu menelan api sepenuh perutnya dan mereka akan masuk ke dalam api yang menyala-nyala (neraka).'),
(504, '004:011', '68', 4, 11, 'Allah mensyari´atkan bagimu tentang (pembagian pusaka untuk) anak-anakmu. Yaitu: bahagian seorang anak lelaki sama dengan bagahian dua orang anak perempuan; dan jika anak itu semuanya perempuan lebih dari dua, maka bagi mereka dua pertiga dari harta yang ditinggalkan; jika anak perempuan itu seorang saja, maka ia memperoleh separo harta. Dan untuk dua orang ibu-bapa, bagi masing-masingnya seperenam dari harta yang ditinggalkan, jika yang meninggal itu mempunyai anak; jika orang yang meninggal tidak mempunyai anak dan ia diwarisi oleh ibu-bapanya (saja), maka ibunya mendapat sepertiga; jika yang meninggal itu mempunyai beberapa saudara, maka ibunya mendapat seperenam. (Pembagian-pembagian tersebut di atas) sesudah dipenuhi wasiat yang ia buat atau (dan) sesudah dibayar hutangnya. (Tentang) orang tuamu dan anak-anakmu, kamu tidak mengetahui siapa di antara mereka yang lebih dekat (banyak) manfaatnya bagimu. Ini adalah ketetapan dari Allah. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.'),
(505, '004:012', '68', 4, 12, 'Dan bagimu (suami-suami) seperdua dari harta yang ditinggalkan oleh isteri-isterimu, jika mereka tidak mempunyai anak. Jika isteri-isterimu itu mempunyai anak, maka kamu mendapat seperempat dari harta yang ditinggalkannya sesudah dipenuhi wasiat yang mereka buat atau (dan) seduah dibayar hutangnya. Para isteri memperoleh seperempat harta yang kamu tinggalkan jika kamu tidak mempunyai anak. Jika kamu mempunyai anak, maka para isteri memperoleh seperdelapan dari harta yang kamu tinggalkan sesudah dipenuhi wasiat yang kamu buat atau (dan) sesudah dibayar hutang-hutangmu. Jika seseorang mati, baik laki-laki maupun perempuan yang tidak meninggalkan ayah dan tidak meninggalkan anak, tetapi mempunyai seorang saudara laki-laki (seibu saja) atau seorang saudara perempuan (seibu saja), maka bagi masing-masing dari kedua jenis saudara itu seperenam harta. Tetapi jika saudara-saudara seibu itu lebih dari seorang, maka mereka bersekutu dalam yang sepertiga itu, sesudah dipenuhi wasiat yang dibuat olehnya atau sesudah dibayar hutangnya dengan tidak memberi mudharat (kepada ahli waris). (Allah menetapkan yang demikian itu sebagai) syari´at yang benar-benar dari Allah, dan Allah Maha Mengetahui lagi Maha Penyantun.'),
(506, '004:013', '68', 4, 13, '(Hukum-hukum tersebut) itu adalah ketentuan-ketentuan dari Allah. Barangsiapa taat kepada Allah dan Rasul-Nya, niscaya Allah memasukkannya kedalam surga yang mengalir didalamnya sungai-sungai, sedang mereka kekal di dalamnya; dan itulah kemenangan yang besar.'),
(507, '004:014', '68', 4, 14, 'Dan barangsiapa yang mendurhakai Allah dan Rasul-Nya dan melanggar ketentuan-ketentuan-Nya, niscaya Allah memasukkannya ke dalam api neraka sedang ia kekal di dalamnya; dan baginya siksa yang menghinakan.'),
(508, '004:015', '68', 4, 15, 'Dan (terhadap) para wanita yang mengerjakan perbuatan keji , hendaklah ada empat orang saksi diantara kamu (yang menyaksikannya). Kemudian apabila mereka telah memberi persaksian, maka kurunglah mereka (wanita-wanita itu) dalam rumah sampai mereka menemui ajalnya, atau sampai Allah memberi jalan lain kepadanya.'),
(509, '004:016', '68', 4, 16, 'Dan terhadap dua orang yang melakukan perbuatan keji di antara kamu, maka berilah hukuman kepada keduanya, kemudian jika keduanya bertaubat dan memperbaiki diri, maka biarkanlah mereka. Sesungguhnya Allah Maha Penerima taubat lagi Maha Penyayang.'),
(510, '004:017', '68', 4, 17, 'Sesungguhnya taubat di sisi Allah hanyalah taubat bagi orang-orang yang mengerjakan kejahatan lantaran kejahilan, yang kemudian mereka bertaubat dengan segera, maka mereka itulah yang diterima Allah taubatnya; dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(511, '004:018', '68', 4, 18, 'Dan tidaklah taubat itu diterima Allah dari orang-orang yang mengerjakan kejahatan (yang) hingga apabila datang ajal kepada seseorang di antara mereka, (barulah) ia mengatakan: \"Sesungguhnya saya bertaubat sekarang\". Dan tidak (pula diterima taubat) orang-orang yang mati sedang mereka di dalam kekafiran. Bagi orang-orang itu telah Kami sediakan siksa yang pedih.'),
(512, '004:019', '68', 4, 19, 'Hai orang-orang yang beriman, tidak halal bagi kamu mempusakai wanita dengan jalan paksa dan janganlah kamu menyusahkan mereka karena hendak mengambil kembali sebagian dari apa yang telah kamu berikan kepadanya, terkecuali bila mereka melakukan pekerjaan keji yang nyata. Dan bergaullah dengan mereka secara patut. Kemudian bila kamu tidak menyukai mereka, (maka bersabarlah) karena mungkin kamu tidak menyukai sesuatu, padahal Allah menjadikan padanya kebaikan yang banyak.'),
(513, '004:020', '68', 4, 20, 'Dan jika kamu ingin mengganti isterimu dengan isteri yang lain , sedang kamu telah memberikan kepada seseorang di antara mereka harta yang banyak, maka janganlah kamu mengambil kembali dari padanya barang sedikitpun. Apakah kamu akan mengambilnya kembali dengan jalan tuduhan yang dusta dan dengan (menanggung) dosa yang nyata?'),
(514, '004:021', '68', 4, 21, 'Bagaimana kamu akan mengambilnya kembali, padahal sebagian kamu telah bergaul (bercampur) dengan yang lain sebagai suami-isteri. Dan mereka (isteri-isterimu) telah mengambil dari kamu perjanjian yang kuat.'),
(515, '004:022', '68', 4, 22, 'Dan janganlah kamu kawini wanita-wanita yang telah dikawini oleh ayahmu, terkecuali pada masa yang telah lampau. Sesungguhnya perbuatan itu amat keji dan dibenci Allah dan seburuk-buruk jalan (yang ditempuh).'),
(516, '004:023', '68', 4, 23, 'Diharamkan atas kamu (mengawini) ibu-ibumu; anak-anakmu yang perempuan; saudara-saudaramu yang perempuan, saudara-saudara bapakmu yang perempuan; saudara-saudara ibumu yang perempuan; anak-anak perempuan dari saudara-saudaramu yang laki-laki; anak-anak perempuan dari saudara-saudaramu yang perempuan; ibu-ibumu yang menyusui kamu; saudara perempuan sepersusuan; ibu-ibu isterimu (mertua); anak-anak isterimu yang dalam pemeliharaanmu dari isteri yang telah kamu campuri, tetapi jika kamu belum campur dengan isterimu itu (dan sudah kamu ceraikan), maka tidak berdosa kamu mengawininya; (dan diharamkan bagimu) isteri-isteri anak kandungmu (menantu); dan menghimpunkan (dalam perkawinan) dua perempuan yang bersaudara, kecuali yang telah terjadi pada masa lampau; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(517, '004:024', '68', 4, 24, 'dan (diharamkan juga kamu mengawini) wanita yang bersuami, kecuali budak-budak yang kamu miliki (Allah telah menetapkan hukum itu) sebagai ketetapan-Nya atas kamu. Dan dihalalkan bagi kamu selain yang demikian (yaitu) mencari isteri-isteri dengan hartamu untuk dikawini bukan untuk berzina. Maka isteri-isteri yang telah kamu nikmati (campuri) di antara mereka, berikanlah kepada mereka maharnya (dengan sempurna), sebagai suatu kewajiban; dan tiadalah mengapa bagi kamu terhadap sesuatu yang kamu telah saling merelakannya, sesudah menentukan mahar itu. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.'),
(518, '004:025', '68', 4, 25, 'Dan barangsiapa diantara kamu (orang merdeka) yang tidak cukup perbelanjaannya untuk mengawini wanita merdeka lagi beriman, ia boleh mengawini wanita yang beriman, dari budak-budak yang kamu miliki. Allah mengetahui keimananmu; sebahagian kamu adalah dari sebahagian yang lain, karena itu kawinilah mereka dengan seizin tuan mereka, dan berilah maskawin mereka menurut yang patut, sedang merekapun wanita-wanita yang memelihara diri, bukan pezina dan bukan (pula) wanita yang mengambil laki-laki lain sebagai piaraannya; dan apabila mereka telah menjaga diri dengan kawin, kemudian mereka melakukan perbuatan yang keji (zina), maka atas mereka separo hukuman dari hukuman wanita-wanita merdeka yang bersuami. (Kebolehan mengawini budak) itu, adalah bagi orang-orang yang takut kepada kemasyakatan menjaga diri (dari perbuatan zina) di antara kamu, dan kesabaran itu lebih baik bagimu. Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(519, '004:026', '68', 4, 26, 'Allah hendak menerangkan (hukum syari´at-Nya) kepadamu, dan menunjukimu kepada jalan-jalan orang yang sebelum kamu (para nabi dan shalihin) dan (hendak) menerima taubatmu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(520, '004:027', '68', 4, 27, 'Dan Allah hendak menerima taubatmu, sedang orang-orang yang mengikuti hawa nafsunya bermaksud supaya kamu berpaling sejauh-jauhnya (dari kebenaran).'),
(521, '004:028', '68', 4, 28, 'Allah hendak memberikan keringanan kepadamu, dan manusia dijadikan bersifat lemah.'),
(522, '004:029', '68', 4, 29, 'Hai orang-orang yang beriman, janganlah kamu saling memakan harta sesamamu dengan jalan yang batil, kecuali dengan jalan perniagaan yang berlaku dengan suka sama-suka di antara kamu. Dan janganlah kamu membunuh dirimu; sesungguhnya Allah adalah Maha Penyayang kepadamu.'),
(523, '004:030', '68', 4, 30, 'Dan barangsiapa berbuat demikian dengan melanggar hak dan aniaya, maka Kami kelak akan memasukkannya ke dalam neraka. Yang demikian itu adalah mudah bagi Allah.'),
(524, '004:031', '68', 4, 31, 'Jika kamu menjauhi dosa-dosa besar di antara dosa-dosa yang dilarang kamu mengerjakannya, niscaya Kami hapus kesalahan-kesalahanmu (dosa-dosamu yang kecil) dan Kami masukkan kamu ke tempat yang mulia (surga).'),
(525, '004:032', '68', 4, 32, 'Dan janganlah kamu iri hati terhadap apa yang dikaruniakan Allah kepada sebahagian kamu lebih banyak dari sebahagian yang lain. (Karena) bagi orang laki-laki ada bahagian dari pada apa yang mereka usahakan, dan bagi para wanita (pun) ada bahagian dari apa yang mereka usahakan, dan mohonlah kepada Allah sebagian dari karunia-Nya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
(526, '004:033', '68', 4, 33, 'Bagi tiap-tiap harta peninggalan dari harta yang ditinggalkan ibu bapak dan karib kerabat, Kami jadikan pewaris-pewarisnya. Dan (jika ada) orang-orang yang kamu telah bersumpah setia dengan mereka, maka berilah kepada mereka bahagiannya. Sesungguhnya Allah menyaksikan segala sesuatu.'),
(527, '004:034', '68', 4, 34, 'Kaum laki-laki itu adalah pemimpin bagi kaum wanita, oleh karena Allah telah melebihkan sebahagian mereka (laki-laki) atas sebahagian yang lain (wanita), dan karena mereka (laki-laki) telah menafkahkan sebagian dari harta mereka. Sebab itu maka wanita yang saleh, ialah yang taat kepada Allah lagi memelihara diri ketika suaminya tidak ada, oleh karena Allah telah memelihara (mereka). Wanita-wanita yang kamu khawatirkan nusyuznya, maka nasehatilah mereka dan pisahkanlah mereka di tempat tidur mereka, dan pukullah mereka. Kemudian jika mereka mentaatimu, maka janganlah kamu mencari-cari jalan untuk menyusahkannya. Sesungguhnya Allah Maha Tinggi lagi Maha Besar.'),
(528, '004:035', '68', 4, 35, 'Dan jika kamu khawatirkan ada persengketaan antara keduanya, maka kirimlah seorang hakam dari keluarga laki-laki dan seorang hakam dari keluarga perempuan. Jika kedua orang hakam itu bermaksud mengadakan perbaikan, niscaya Allah memberi taufik kepada suami-isteri itu. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.'),
(529, '004:036', '68', 4, 36, 'Sembahlah Allah dan janganlah kamu mempersekutukan-Nya dengan sesuatupun. Dan berbuat baiklah kepada dua orang ibu-bapa, karib-kerabat, anak-anak yatim, orang-orang miskin, tetangga yang dekat dan tetangga yang jauh, dan teman sejawat, ibnu sabil dan hamba sahayamu. Sesungguhnya Allah tidak menyukai orang-orang yang sombong dan membangga-banggakan diri,'),
(530, '004:037', '68', 4, 37, '(yaitu) orang-orang yang kikir, dan menyuruh orang lain berbuat kikir, dan menyembunyikan karunia Allah yang telah diberikan-Nya kepada mereka. Dan Kami telah menyediakan untuk orang-orang kafir siksa yang menghinakan.'),
(531, '004:038', '68', 4, 38, 'Dan (juga) orang-orang yang menafkahkan harta-harta mereka karena riya kepada manusia, dan orang-orang yang tidak beriman kepada Allah dan kepada hari kemudian. Barangsiapa yang mengambil syaitan itu menjadi temannya, maka syaitan itu adalah teman yang seburuk-buruknya.'),
(532, '004:039', '68', 4, 39, 'Apakah kemudharatannya bagi mereka, kalau mereka beriman kepada Allah dan hari kemudian dan menafkahkan sebahagian rezeki yang telah diberikan Allah kepada mereka? Dan adalah Allah Maha Mengetahui keadaan mereka.'),
(533, '004:040', '68', 4, 40, 'Sesungguhnya Allah tidak menganiaya seseorang walaupun sebesar zarrah, dan jika ada kebajikan sebesar zarrah, niscaya Allah akan melipat gandakannya dan memberikan dari sisi-Nya pahala yang besar.'),
(534, '004:041', '68', 4, 41, 'Maka bagaimanakah (halnya orang kafir nanti), apabila Kami mendatangkan seseorang saksi (rasul) dari tiap-tiap umat dan Kami mendatangkan kamu (Muhammad) sebagai saksi atas mereka itu (sebagai umatmu).'),
(535, '004:042', '68', 4, 42, 'Di hari itu orang-orang kafir dan orang-orang yang mendurhakai rasul, ingin supaya mereka disamaratakan dengan tanah, dan mereka tidak dapat menyembunyikan (dari Allah) sesuatu kejadianpun.'),
(536, '004:043', '68', 4, 43, 'Hai orang-orang yang beriman, janganlah kamu shalat, sedang kamu dalam keadaan mabuk, sehingga kamu mengerti apa yang kamu ucapkan, (jangan pula hampiri mesjid) sedang kamu dalam keadaan junub, terkecuali sekedar berlalu saja, hingga kamu mandi. Dan jika kamu sakit atau sedang dalam musafir atau datang dari tempat buang air atau kamu telah menyentuh perempuan, kemudian kamu tidak mendapat air, maka bertayamumlah kamu dengan tanah yang baik (suci); sapulah mukamu dan tanganmu. Sesungguhnya Allah Maha Pemaaf lagi Maha Pengampun.'),
(537, '004:044', '68', 4, 44, 'Apakah kamu tidak melihat orang-orang yang telah diberi bahagian dari Al Kitab (Taurat)? Mereka membeli (memilih) kesesatan (dengan petunjuk) dan mereka bermaksud supaya kamu tersesat (menyimpang) dari jalan (yang benar).'),
(538, '004:045', '68', 4, 45, 'Dan Allah lebih mengetahui (dari pada kamu) tentang musuh-musuhmu. Dan cukuplah Allah menjadi Pelindung (bagimu). Dan cukuplah Allah menjadi Penolong (bagimu).'),
(539, '004:046', '68', 4, 46, 'Yaitu orang-orang Yahudi, mereka mengubah perkataan dari tempat-tempatnya. Mereka berkata: \"Kami mendengar\", tetapi kami tidak mau menurutinya. Dan (mereka mengatakan pula): \"Dengarlah\" sedang kamu sebenarnya tidak mendengar apa-apa. Dan (mereka mengatakan): \"Raa´ina\", dengan memutar-mutar lidahnya dan mencela agama. Sekiranya mereka mengatakan: \"Kami mendengar dan menurut, dan dengarlah, dan perhatikanlah kami\", tentulah itu lebih baik bagi mereka dan lebih tepat, akan tetapi Allah mengutuk mereka, karena kekafiran mereka. Mereka tidak beriman kecuali iman yang sangat tipis.'),
(540, '004:047', '68', 4, 47, 'Hai orang-orang yang telah diberi Al Kitab, berimanlah kamu kepada apa yang telah Kami turunkan (Al Quran) yang membenarkan Kitab yang ada pada kamu sebelum Kami mengubah muka(mu), lalu Kami putarkan ke belakang atau Kami kutuki mereka sebagaimana Kami telah mengutuki orang-orang (yang berbuat maksiat) pada hari Sabtu. Dan ketetapan Allah pasti berlaku.'),
(541, '004:048', '68', 4, 48, 'Sesungguhnya Allah tidak akan mengampuni dosa syirik, dan Dia mengampuni segala dosa yang selain dari (syirik) itu, bagi siapa yang dikehendaki-Nya. Barangsiapa yang mempersekutukan Allah, maka sungguh ia telah berbuat dosa yang besar.'),
(542, '004:049', '68', 4, 49, 'Apakah kamu tidak memperhatikan orang yang menganggap dirinya bersih?. Sebenarnya Allah membersihkan siapa yang dikehendaki-Nya dan mereka tidak aniaya sedikitpun.'),
(543, '004:050', '68', 4, 50, 'Perhatikanlah, betapakah mereka mengada-adakan dusta terhadap Allah? Dan cukuplah perbuatan itu menjadi dosa yang nyata (bagi mereka).'),
(544, '004:051', '68', 4, 51, 'Apakah kamu tidak memperhatikan orang-orang yang diberi bahagian dari Al kitab? Mereka percaya kepada jibt dan thaghut, dan mengatakan kepada orang-orang Kafir (musyrik Mekah), bahwa mereka itu lebih benar jalannya dari orang-orang yang beriman.'),
(545, '004:052', '68', 4, 52, 'Mereka itulah orang yang dikutuki Allah. Barangsiapa yang dikutuki Allah, niscaya kamu sekali-kali tidak akan memperoleh penolong baginya.'),
(546, '004:053', '68', 4, 53, 'Ataukah ada bagi mereka bahagian dari kerajaan (kekuasaan)? Kendatipun ada, mereka tidak akan memberikan sedikitpun (kebajikan) kepada manusia.'),
(547, '004:054', '68', 4, 54, 'ataukah mereka dengki kepada manusia (Muhammad) lantaran karunia yang Allah telah berikan kepadanya? Sesungguhnya Kami telah memberikan Kitab dan Hikmah kepada keluarga Ibrahim, dan Kami telah memberikan kepadanya kerajaan yang besar.'),
(548, '004:055', '68', 4, 55, 'Maka di antara mereka (orang-orang yang dengki itu), ada orang-orang yang beriman kepadanya, dan di antara mereka ada orang-orang yang menghalangi (manusia) dari beriman kepadanya. Dan cukuplah (bagi mereka) Jahannam yang menyala-nyala apinya.'),
(549, '004:056', '68', 4, 56, 'Sesungguhnya orang-orang yang kafir kepada ayat-ayat Kami, kelak akan Kami masukkan mereka ke dalam neraka. Setiap kali kulit mereka hangus, Kami ganti kulit mereka dengan kulit yang lain, supaya mereka merasakan azab. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.'),
(550, '004:057', '68', 4, 57, 'Dan orang-orang yang beriman dan mengerjakan amalan-amalan yang shaleh, kelak akan Kami masukkan mereka ke dalam surga yang di dalamnya mengalir sungai-sungai; kekal mereka di dalamnya; mereka di dalamnya mempunyai isteri-isteri yang suci, dan Kami masukkan mereka ke tempat yang teduh lagi nyaman.'),
(551, '004:058', '68', 4, 58, 'Sesungguhnya Allah menyuruh kamu menyampaikan amanat kepada yang berhak menerimanya, dan (menyuruh kamu) apabila menetapkan hukum di antara manusia supaya kamu menetapkan dengan adil. Sesungguhnya Allah memberi pengajaran yang sebaik-baiknya kepadamu. Sesungguhnya Allah adalah Maha Mendengar lagi Maha Melihat.'),
(552, '004:059', '68', 4, 59, 'Hai orang-orang yang beriman, taatilah Allah dan taatilah Rasul (Nya), dan ulil amri di antara kamu. Kemudian jika kamu berlainan pendapat tentang sesuatu, maka kembalikanlah ia kepada Allah (Al Quran) dan Rasul (sunnahnya), jika kamu benar-benar beriman kepada Allah dan hari kemudian. Yang demikian itu lebih utama (bagimu) dan lebih baik akibatnya.'),
(553, '004:060', '68', 4, 60, 'Apakah kamu tidak memperhatikan orang-orang yang mengaku dirinya telah beriman kepada apa yang diturunkan kepadamu dan kepada apa yang diturunkan sebelum kamu? Mereka hendak berhakim kepada thaghut, padahal mereka telah diperintah mengingkari thaghut itu. Dan syaitan bermaksud menyesatkan mereka (dengan) penyesatan yang sejauh-jauhnya.'),
(554, '004:061', '68', 4, 61, 'Apabila dikatakan kepada mereka: \"Marilah kamu (tunduk) kepada hukum yang Allah telah turunkan dan kepada hukum Rasul\", niscaya kamu lihat orang-orang munafik menghalangi (manusia) dengan sekuat-kuatnya dari (mendekati) kamu.'),
(555, '004:062', '68', 4, 62, 'Maka bagaimanakah halnya apabila mereka (orang-orang munafik) ditimpa sesuatu musibah disebabkan perbuatan tangan mereka sendiri, kemudian mereka datang kepadamu sambil bersumpah: \"Demi Allah, kami sekali-kali tidak menghendaki selain penyelesaian yang baik dan perdamaian yang sempurna\".'),
(556, '004:063', '68', 4, 63, 'Mereka itu adalah orang-orang yang Allah mengetahui apa yang di dalam hati mereka. Karena itu berpalinglah kamu dari mereka, dan berilah mereka pelajaran, dan katakanlah kepada mereka perkataan yang berbekas pada jiwa mereka.'),
(557, '004:064', '68', 4, 64, 'Dan Kami tidak mengutus seseorang rasul melainkan untuk ditaati dengan seizin Allah. Sesungguhnya jikalau mereka ketika menganiaya dirinya datang kepadamu, lalu memohon ampun kepada Allah, dan Rasulpun memohonkan ampun untuk mereka, tentulah mereka mendapati Allah Maha Penerima Taubat lagi Maha Penyayang.'),
(558, '004:065', '68', 4, 65, 'Maka demi Tuhanmu, mereka (pada hakekatnya) tidak beriman hingga mereka menjadikan kamu hakim terhadap perkara yang mereka perselisihkan, kemudian mereka tidak merasa dalam hati mereka sesuatu keberatan terhadap putusan yang kamu berikan, dan mereka menerima dengan sepenuhnya.'),
(559, '004:066', '68', 4, 66, 'Dan sesungguhnya kalau Kami perintahkan kepada mereka: \"Bunuhlah dirimu atau keluarlah kamu dari kampungmu\", niscaya mereka tidak akan melakukannya kecuali sebagian kecil dari mereka. Dan sesungguhnya kalau mereka melaksanakan pelajaran yang diberikan kepada mereka, tentulah hal yang demikian itu lebih baik bagi mereka dan lebih menguatkan (iman mereka),'),
(560, '004:067', '68', 4, 67, 'dan kalau demikian, pasti Kami berikan kepada mereka pahala yang besar dari sisi Kami,'),
(561, '004:068', '68', 4, 68, 'dan pasti Kami tunjuki mereka kepada jalan yang lurus.'),
(562, '004:069', '68', 4, 69, 'Dan barangsiapa yang mentaati Allah dan Rasul(Nya), mereka itu akan bersama-sama dengan orang-orang yang dianugerahi nikmat oleh Allah, yaitu: Nabi-nabi, para shiddiiqiin, orang-orang yang mati syahid, dan orang-orang saleh. Dan mereka itulah teman yang sebaik-baiknya.'),
(563, '004:070', '68', 4, 70, 'Yang demikian itu adalah karunia dari Allah, dan Allah cukup mengetahui.'),
(564, '004:071', '68', 4, 71, 'Hai orang-orang yang beriman, bersiap siagalah kamu, dan majulah (ke medan pertempuran) berkelompok-kelompok, atau majulah bersama-sama!'),
(565, '004:072', '68', 4, 72, 'Dan sesungguhnya di antara kamu ada orang yang sangat berlambat-lambat (ke medan pertempuran). Maka jika kamu ditimpa musibah ia berkata: \"Sesungguhnya Tuhan telah menganugerahkan nikmat kepada saya karena saya tidak ikut berperang bersama mereka.'),
(566, '004:073', '68', 4, 73, 'Dan sungguh jika kamu beroleh karunia (kemenangan) dari Allah, tentulah dia mengatakan seolah-oleh belum pernah ada hubungan kasih sayang antara kamu dengan dia: \"Wahai kiranya saya ada bersama-sama mereka, tentu saya mendapat kemenangan yang besar (pula)\".'),
(567, '004:074', '68', 4, 74, 'Karena itu hendaklah orang-orang yang menukar kehidupan dunia dengan kehidupan akhirat berperang di jalan Allah. Barangsiapa yang berperang di jalan Allah, lalu gugur atau memperoleh kemenangan maka kelak akan Kami berikan kepadanya pahala yang besar.'),
(568, '004:075', '68', 4, 75, 'Mengapa kamu tidak mau berperang di jalan Allah dan (membela) orang-orang yang lemah baik laki-laki, wanita-wanita maupun anak-anak yang semuanya berdoa: \"Ya Tuhan kami, keluarkanlah kami dari negeri ini (Mekah) yang zalim penduduknya dan berilah kami pelindung dari sisi Engkau, dan berilah kami penolong dari sisi Engkau!\".'),
(569, '004:076', '68', 4, 76, 'Orang-orang yang beriman berperang di jalan Allah, dan orang-orang yang kafir berperang di jalan thaghut, sebab itu perangilah kawan-kawan syaitan itu, karena sesungguhnya tipu daya syaitan itu adalah lemah.'),
(570, '004:077', '68', 4, 77, 'Tidakkah kamu perhatikan orang-orang yang dikatakan kepada mereka: \"Tahanlah tanganmu (dari berperang), dirikanlah sembahyang dan tunaikanlah zakat!\" Setelah diwajibkan kepada mereka berperang, tiba-tiba sebahagian dari mereka (golongan munafik) takut kepada manusia (musuh), seperti takutnya kepada Allah, bahkan lebih sangat dari itu takutnya. Mereka berkata: \"Ya Tuhan kami, mengapa Engkau wajibkan berperang kepada kami? Mengapa tidak Engkau tangguhkan (kewajiban berperang) kepada kami sampai kepada beberapa waktu lagi?\" Katakanlah: \"Kesenangan di dunia ini hanya sebentar dan akhirat itu lebih baik untuk orang-orang yang bertakwa, dan kamu tidak akan dianiaya sedikitpun.'),
(571, '004:078', '68', 4, 78, 'Di mana saja kamu berada, kematian akan mendapatkan kamu, kendatipun kamu di dalam benteng yang tinggi lagi kokoh, dan jika mereka memperoleh kebaikan, mereka mengatakan: \"Ini adalah dari sisi Allah\", dan kalau mereka ditimpa sesuatu bencana mereka mengatakan: \"Ini (datangnya) dari sisi kamu (Muhammad)\". Katakanlah: \"Semuanya (datang) dari sisi Allah\". Maka mengapa orang-orang itu (orang munafik) hampir-hampir tidak memahami pembicaraan sedikitpun?'),
(572, '004:079', '68', 4, 79, 'Apa saja nikmat yang kamu peroleh adalah dari Allah, dan apa saja bencana yang menimpamu, maka dari (kesalahan) dirimu sendiri. Kami mengutusmu menjadi Rasul kepada segenap manusia. Dan cukuplah Allah menjadi saksi.'),
(573, '004:080', '68', 4, 80, 'Barangsiapa yang mentaati Rasul itu, sesungguhnya ia telah mentaati Allah. Dan barangsiapa yang berpaling (dari ketaatan itu), maka Kami tidak mengutusmu untuk menjadi pemelihara bagi mereka.'),
(574, '004:081', '68', 4, 81, 'Dan mereka (orang-orang munafik) mengatakan: \"(Kewajiban kami hanyalah) taat\". Tetapi apabila mereka telah pergi dari sisimu, sebahagian dari mereka mengatur siasat di malam hari (mengambil keputusan) lain dari yang telah mereka katakan tadi. Allah menulis siasat yang mereka atur di malam hari itu, maka berpalinglah kamu dari mereka dan tawakallah kepada Allah. Cukuplah Allah menjadi Pelindung.'),
(575, '004:082', '68', 4, 82, 'Maka apakah mereka tidak memperhatikan Al Quran? Kalau kiranya Al Quran itu bukan dari sisi Allah, tentulah mereka mendapat pertentangan yang banyak di dalamnya.'),
(576, '004:083', '68', 4, 83, 'Dan apabila datang kepada mereka suatu berita tentang keamanan ataupun ketakutan, mereka lalu menyiarkannya. Dan kalau mereka menyerahkannya kepada Rasul dan Ulil Amri di antara mereka, tentulah orang-orang yang ingin mengetahui kebenarannya (akan dapat) mengetahuinya dari mereka (Rasul dan Ulil Amri). Kalau tidaklah karena karunia dan rahmat Allah kepada kamu, tentulah kamu mengikut syaitan, kecuali sebahagian kecil saja (di antaramu).'),
(577, '004:084', '68', 4, 84, 'Maka berperanglah kamu pada jalan Allah, tidaklah kamu dibebani melainkan dengan kewajiban kamu sendiri. Kobarkanlah semangat para mukmin (untuk berperang). Mudah-mudahan Allah menolak serangan orang-orang yang kafir itu. Allah amat besar kekuatan dan amat keras siksaan(Nya).'),
(578, '004:085', '68', 4, 85, 'Barangsiapa yang memberikan syafa´at yang baik, niscaya ia akan memperoleh bahagian (pahala) dari padanya. Dan barangsiapa memberi syafa´at yang buruk, niscaya ia akan memikul bahagian (dosa) dari padanya. Allah Maha Kuasa atas segala sesuatu.'),
(579, '004:086', '68', 4, 86, 'Apabila kamu diberi penghormatan dengan sesuatu penghormatan, maka balaslah penghormatan itu dengan yang lebih baik dari padanya, atau balaslah penghormatan itu (dengan yang serupa). Sesungguhnya Allah memperhitungankan segala sesuatu.'),
(580, '004:087', '68', 4, 87, 'Allah, tidak ada Tuhan (yang berhak disembah) selain Dia. Sesungguhnya Dia akan mengumpulkan kamu di hari kiamat, yang tidak ada keraguan terjadinya. Dan siapakah orang yang lebih benar perkataan(nya) dari pada Allah?'),
(581, '004:088', '68', 4, 88, 'Maka mengapa kamu (terpecah) menjadi dua golongan dalam (menghadapi) orang-orang munafik, padahal Allah telah membalikkan mereka kepada kekafiran, disebabkan usaha mereka sendiri? Apakah kamu bermaksud memberi petunjuk kepada orang-orang yang telah disesatkan Allah? Barangsiapa yang disesatkan Allah, sekali-kali kamu tidak mendapatkan jalan (untuk memberi petunjuk) kepadanya.'),
(582, '004:089', '68', 4, 89, 'Mereka ingin supaya kamu menjadi kafir sebagaimana mereka telah menjadi kafir, lalu kamu menjadi sama (dengan mereka). Maka janganlah kamu jadikan di antara mereka penolong-penolong(mu), hingga mereka berhijrah pada jalan Allah. Maka jika mereka berpaling, tawan dan bunuhlah mereka di mana saja kamu menemuinya, dan janganlah kamu ambil seorangpun di antara mereka menjadi pelindung, dan jangan (pula) menjadi penolong,'),
(583, '004:090', '68', 4, 90, 'kecuali orang-orang yang meminta perlindungan kepada sesuatu kaum, yang antara kamu dan kaum itu telah ada perjanjian (damai) atau orang-orang yang datang kepada kamu sedang hati mereka merasa keberatan untuk memerangi kamu dan memerangi kaumnya. Kalau Allah menghendaki, tentu Dia memberi kekuasaan kepada mereka terhadap kamu, lalu pastilah mereka memerangimu. tetapi jika mereka membiarkan kamu, dan tidak memerangi kamu serta mengemukakan perdamaian kepadamu maka Allah tidak memberi jalan bagimu (untuk menawan dan membunuh) mereka.'),
(584, '004:091', '68', 4, 91, 'Kelak kamu akan dapati (golongan-golongan) yang lain, yang bermaksud supaya mereka aman dari pada kamu dan aman (pula) dari kaumnya. Setiap mereka diajak kembali kepada fitnah (syirik), merekapun terjun kedalamnya. Karena itu jika mereka tidak membiarkan kamu dan (tidak) mau mengemukakan perdamaian kepadamu, serta (tidak) menahan tangan mereka (dari memerangimu), maka tawanlah mereka dan bunuhlah mereka dan merekalah orang-orang yang Kami berikan kepadamu alasan yang nyata (untuk menawan dan membunuh) mereka.'),
(585, '004:092', '68', 4, 92, 'Dan tidak layak bagi seorang mukmin membunuh seorang mukmin (yang lain), kecuali karena tersalah (tidak sengaja), dan barangsiapa membunuh seorang mukmin karena tersalah (hendaklah) ia memerdekakan seorang hamba sahaya yang beriman serta membayar diat yang diserahkan kepada keluarganya (si terbunuh itu), kecuali jika mereka (keluarga terbunuh) bersedekah. Jika ia (si terbunuh) dari kaum (kafir) yang ada perjanjian (damai) antara mereka dengan kamu, maka (hendaklah si pembunuh) membayar diat yang diserahkan kepada keluarganya (si terbunuh) serta memerdekakan hamba sahaya yang beriman. Barangsiapa yang tidak memperolehnya, maka hendaklah ia (si pembunuh) berpuasa dua bulan berturut-turut untuk penerimaan taubat dari pada Allah. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.'),
(586, '004:093', '68', 4, 93, 'Dan barangsiapa yang membunuh seorang mukmin dengan sengaja maka balasannya ialah Jahannam, kekal ia di dalamnya dan Allah murka kepadanya, dan mengutukinya serta menyediakan azab yang besar baginya.'),
(587, '004:094', '68', 4, 94, 'Hai orang-orang yang beriman, apabila kamu pergi (berperang) di jalan Allah, maka telitilah dan janganlah kamu mengatakan kepada orang yang mengucapkan \"salam\" kepadamu: \"Kamu bukan seorang mukmin\" (lalu kamu membunuhnya), dengan maksud mencari harta benda kehidupan di dunia, karena di sisi Allah ada harta yang banyak. Begitu jugalah keadaan kamu dahulu, lalu Allah menganugerahkan nikmat-Nya atas kamu, maka telitilah. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(588, '004:095', '68', 4, 95, 'Tidaklah sama antara mukmin yang duduk (yang tidak ikut berperang) yang tidak mempunyai ´uzur dengan orang-orang yang berjihad di jalan Allah dengan harta mereka dan jiwanya. Allah melebihkan orang-orang yang berjihad dengan harta dan jiwanya atas orang-orang yang duduk satu derajat. Kepada masing-masing mereka Allah menjanjikan pahala yang baik (surga) dan Allah melebihkan orang-orang yang berjihad atas orang yang duduk dengan pahala yang besar,'),
(589, '004:096', '68', 4, 96, '(yaitu) beberapa derajat dari pada-Nya, ampunan serta rahmat. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(590, '004:097', '68', 4, 97, 'Sesungguhnya orang-orang yang diwafatkan malaikat dalam keadaan menganiaya diri sendiri, (kepada mereka) malaikat bertanya: \"Dalam keadaan bagaimana kamu ini?\". Mereka menjawab: \"Adalah kami orang-orang yang tertindas di negeri (Mekah)\". Para malaikat berkata: \"Bukankah bumi Allah itu luas, sehingga kamu dapat berhijrah di bumi itu?\". Orang-orang itu tempatnya neraka Jahannam, dan Jahannam itu seburuk-buruk tempat kembali,'),
(591, '004:098', '68', 4, 98, 'kecuali mereka yang tertindas baik laki-laki atau wanita ataupun anak-anak yang tidak mampu berdaya upaya dan tidak mengetahui jalan (untuk hijrah),'),
(592, '004:099', '68', 4, 99, 'mereka itu, mudah-mudahan Allah memaafkannya. Dan adalah Allah Maha Pemaaf lagi Maha Pengampun.'),
(593, '004:100', '68', 4, 100, 'Barangsiapa berhijrah di jalan Allah, niscaya mereka mendapati di muka bumi ini tempat hijrah yang luas dan rezeki yang banyak. Barangsiapa keluar dari rumahnya dengan maksud berhijrah kepada Allah dan Rasul-Nya, kemudian kematian menimpanya (sebelum sampai ke tempat yang dituju), maka sungguh telah tetap pahalanya di sisi Allah. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(594, '004:101', '68', 4, 101, 'Dan apabila kamu bepergian di muka bumi, maka tidaklah mengapa kamu men-qashar sembahyang(mu), jika kamu takut diserang orang-orang kafir. Sesungguhnya orang-orang kafir itu adalah musuh yang nyata bagimu.'),
(595, '004:102', '68', 4, 102, 'Dan apabila kamu berada di tengah-tengah mereka (sahabatmu) lalu kamu hendak mendirikan shalat bersama-sama mereka, maka hendaklah segolongan dari mereka berdiri (shalat) besertamu dan menyandang senjata, kemudian apabila mereka (yang shalat besertamu) sujud (telah menyempurnakan serakaat), maka hendaklah mereka pindah dari belakangmu (untuk menghadapi musuh) dan hendaklah datang golongan yang kedua yang belum bersembahyang, lalu bersembahyanglah mereka denganmu], dan hendaklah mereka bersiap siaga dan menyandang senjata. Orang-orang kafir ingin supaya kamu lengah terhadap senjatamu dan harta bendamu, lalu mereka menyerbu kamu dengan sekaligus. Dan tidak ada dosa atasmu meletakkan senjata-senjatamu, jika kamu mendapat sesuatu kesusahan karena hujan atau karena kamu memang sakit; dan siap siagalah kamu. Sesungguhnya Allah telah menyediakan azab yang menghinakan bagi orang-orang kafir itu.'),
(596, '004:103', '68', 4, 103, 'Maka apabila kamu telah menyelesaikan shalat(mu), ingatlah Allah di waktu berdiri, di waktu duduk dan di waktu berbaring. Kemudian apabila kamu telah merasa aman, maka dirikanlah shalat itu (sebagaimana biasa). Sesungguhnya shalat itu adalah fardhu yang ditentukan waktunya atas orang-orang yang beriman.'),
(597, '004:104', '68', 4, 104, 'Janganlah kamu berhati lemah dalam mengejar mereka (musuhmu). Jika kamu menderita kesakitan, maka sesungguhnya merekapun menderita kesakitan (pula), sebagaimana kamu menderitanya, sedang kamu mengharap dari pada Allah apa yang tidak mereka harapkan. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.'),
(598, '004:105', '68', 4, 105, 'Sesungguhnya Kami telah menurunkan kitab kepadamu dengan membawa kebenaran, supaya kamu mengadili antara manusia dengan apa yang telah Allah wahyukan kepadamu, dan janganlah kamu menjadi penantang (orang yang tidak bersalah), karena (membela) orang-orang yang khianat,'),
(599, '004:106', '68', 4, 106, 'dan mohonlah ampun kepada Allah. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(600, '004:107', '68', 4, 107, 'Dan janganlah kamu berdebat (untuk membela) orang-orang yang mengkhianati dirinya. Sesungguhnya Allah tidak menyukai orang-orang yang selalu berkhianat lagi bergelimang dosa,'),
(601, '004:108', '68', 4, 108, 'mereka bersembunyi dari manusia, tetapi mereka tidak bersembunyi dari Allah, padahal Allah beserta mereka, ketika pada suatu malam mereka menetapkan keputusan rahasia yang Allah tidak redlai. Dan adalah Allah Maha Meliputi (ilmu-Nya) terhadap apa yang mereka kerjakan.'),
(602, '004:109', '68', 4, 109, 'Beginilah kamu, kamu sekalian adalah orang-orang yang berdebat untuk (membela) mereka dalam kehidupan dunia ini. Maka siapakah yang akan mendebat Allah untuk (membela) mereka pada hari kiamat? Atau siapakah yang menjadi pelindung mereka (terhadap siksa Allah)?'),
(603, '004:110', '68', 4, 110, 'Dan barangsiapa yang mengerjakan kejahatan dan menganiaya dirinya, kemudian ia mohon ampun kepada Allah, niscaya ia mendapati Allah Maha Pengampun lagi Maha Penyayang.'),
(604, '004:111', '68', 4, 111, 'Barangsiapa yang mengerjakan dosa, maka sesungguhnya ia mengerjakannya untuk (kemudharatan) dirinya sendiri. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(605, '004:112', '68', 4, 112, 'Dan barangsiapa yang mengerjakan kesalahan atau dosa, kemudian dituduhkannya kepada orang yang tidak bersalah, maka sesungguhnya ia telah berbuat suatu kebohongan dan dosa yang nyata.'),
(606, '004:113', '68', 4, 113, 'Sekiranya bukan karena karunia Allah dan rahmat-Nya kepadamu, tentulah segolongan dari mereka berkeinginan keras untuk menyesatkanmu. Tetapi mereka tidak menyesatkan melainkan dirinya sendiri, dan mereka tidak dapat membahayakanmu sedikitpun kepadamu. Dan (juga karena) Allah telah menurunkan Kitab dan hikmah kepadamu, dan telah mengajarkan kepadamu apa yang belum kamu ketahui. Dan adalah karunia Allah sangat besar atasmu.'),
(607, '004:114', '68', 4, 114, 'Tidak ada kebaikan pada kebanyakan bisikan-bisikan mereka, kecuali bisikan-bisikan dari orang yang menyuruh (manusia) memberi sedekah, atau berbuat ma´ruf, atau mengadakan perdamaian di antara manusia. Dan barangsiapa yang berbuat demikian karena mencari keridhaan Allah, maka kelak Kami memberi kepadanya pahala yang besar.'),
(608, '004:115', '68', 4, 115, 'Dan barangsiapa yang menentang Rasul sesudah jelas kebenaran baginya, dan mengikuti jalan yang bukan jalan orang-orang mukmin, Kami biarkan ia leluasa terhadap kesesatan yang telah dikuasainya itu dan Kami masukkan ia ke dalam Jahannam, dan Jahannam itu seburuk-buruk tempat kembali.'),
(609, '004:116', '68', 4, 116, 'Sesungguhnya Allah tidak mengampuni dosa mempersekutukan (sesuatu) dengan Dia, dan dia mengampuni dosa yang selain syirik bagi siapa yang dikehendaki-Nya. Barangsiapa yang mempersekutukan (sesuatu) dengan Allah, maka sesungguhnya ia telah tersesat sejauh-jauhnya.'),
(610, '004:117', '68', 4, 117, 'Yang mereka sembah selain Allah itu, tidak lain hanyalah berhala, dan (dengan menyembah berhala itu) mereka tidak lain hanyalah menyembah syaitan yang durhaka,'),
(611, '004:118', '68', 4, 118, 'yang dilaknati Allah dan syaitan itu mengatakan: \"Saya benar-benar akan mengambil dari hamba-hamba Engkau bahagian yang sudah ditentukan (untuk saya),'),
(612, '004:119', '68', 4, 119, 'dan aku benar-benar akan menyesatkan mereka, dan akan membangkitkan angan-angan kosong pada mereka dan menyuruh mereka (memotong telinga-telinga binatang ternak), lalu mereka benar-benar memotongnya, dan akan aku suruh mereka (mengubah ciptaan Allah), lalu benar-benar mereka meubahnya\". Barangsiapa yang menjadikan syaitan menjadi pelindung selain Allah, maka sesungguhnya ia menderita kerugian yang nyata.'),
(613, '004:120', '68', 4, 120, 'Syaitan itu memberikan janji-janji kepada mereka dan membangkitkan angan-angan kosong pada mereka, padahal syaitan itu tidak menjanjikan kepada mereka selain dari tipuan belaka.'),
(614, '004:121', '68', 4, 121, 'Mereka itu tempatnya Jahannam dan mereka tidak memperoleh tempat lari dari padanya.'),
(615, '004:122', '68', 4, 122, 'Orang-orang yang beriman dan mengerjakan amalan saleh, kelak akan Kami masukkan ke dalam surga yang mengalir sungai-sungai di dalamnya, mereka kekal di dalamnya selama-lamanya. Allah telah membuat suatu janji yang benar. Dan siapakah yang lebih benar perkataannya dari pada Allah?'),
(616, '004:123', '68', 4, 123, '(Pahala dari Allah) itu bukanlah menurut angan-anganmu yang kosong dan tidak (pula) menurut angan-angan Ahli Kitab. Barangsiapa yang mengerjakan kejahatan, niscaya akan diberi pembalasan dengan kejahatan itu dan ia tidak mendapat pelindung dan tidak (pula) penolong baginya selain dari Allah.'),
(617, '004:124', '68', 4, 124, 'Barangsiapa yang mengerjakan amal-amal saleh, baik laki-laki maupun wanita sedang ia orang yang beriman, maka mereka itu masuk ke dalam surga dan mereka tidak dianiaya walau sedikitpun.'),
(618, '004:125', '68', 4, 125, 'Dan siapakah yang lebih baik agamanya dari pada orang yang ikhlas menyerahkan dirinya kepada Allah, sedang diapun mengerjakan kebaikan, dan ia mengikuti agama Ibrahim yang lurus? Dan Allah mengambil Ibrahim menjadi kesayangan-Nya.'),
(619, '004:126', '68', 4, 126, 'Kepunyaan Allah-lah apa yang di langit dan apa yang di bumi, dan adalah (pengetahuan) Allah Maha Meliputi segala sesuatu.'),
(620, '004:127', '68', 4, 127, 'Dan mereka minta fatwa kepadamu tentang para wanita. Katakanlah: \"Allah memberi fatwa kepadamu tentang mereka, dan apa yang dibacakan kepadamu dalam Al Quran (juga memfatwakan) tentang para wanita yatim yang kamu tidak memberikan kepada mereka apa yang ditetapkan untuk mereka, sedang kamu ingin mengawini mereka dan tentang anak-anak yang masih dipandang lemah. Dan (Allah menyuruh kamu) supaya kamu mengurus anak-anak yatim secara adil. Dan kebajikan apa saja yang kamu kerjakan, maka sesungguhnya Allah adalah Maha Mengetahuinya.'),
(621, '004:128', '68', 4, 128, 'Dan jika seorang wanita khawatir akan nusyuz atau sikap tidak acuh dari suaminya, maka tidak mengapa bagi keduanya mengadakan perdamaian yang sebenar-benarnya, dan perdamaian itu lebih baik (bagi mereka) walaupun manusia itu menurut tabiatnya kikir. Dan jika kamu bergaul dengan isterimu secara baik dan memelihara dirimu (dari nusyuz dan sikap tak acuh), maka sesungguhnya Allah adalah Maha Mengetahui apa yang kamu kerjakan.'),
(622, '004:129', '68', 4, 129, 'Dan kamu sekali-kali tidak akan dapat berlaku adil di antara isteri-isteri(mu), walaupun kamu sangat ingin berbuat demikian, karena itu janganlah kamu terlalu cenderung (kepada yang kamu cintai), sehingga kamu biarkan yang lain terkatung-katung. Dan jika kamu mengadakan perbaikan dan memelihara diri (dari kecurangan), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(623, '004:130', '68', 4, 130, 'Jika keduanya bercerai, maka Allah akan memberi kecukupan kepada masing-masingnya dari limpahan karunia-Nya. Dan adalah Allah Maha Luas (karunia-Nya) lagi Maha Bijaksana.'),
(624, '004:131', '68', 4, 131, 'Dan kepunyaan Allah-lah apa yang di langit dan yang di bumi, dan sungguh Kami telah memerintahkan kepada orang-orang yang diberi kitab sebelum kamu dan (juga) kepada kamu; bertakwalah kepada Allah. Tetapi jika kamu kafir maka (ketahuilah), sesungguhnya apa yang di langit dan apa yang di bumi hanyalah kepunyaan Allah dan Allah Maha Kaya dan Maha Terpuji.'),
(625, '004:132', '68', 4, 132, 'Dan kepunyaan Allah-lah apa yang di langit dan apa yang di bumi. Cukuplah Allah sebagai Pemelihara.'),
(626, '004:133', '68', 4, 133, 'Jika Allah menghendaki, niscaya Dia musnahkan kamu wahai manusia, dan Dia datangkan umat yang lain (sebagai penggantimu). Dan adalah Allah Maha Kuasa berbuat demikian.'),
(627, '004:134', '68', 4, 134, 'Barangsiapa yang menghendaki pahala di dunia saja (maka ia merugi), karena di sisi Allah ada pahala dunia dan akhirat. Dan Allah Maha Mendengar lagi Maha Melihat.'),
(628, '004:135', '68', 4, 135, 'Wahai orang-orang yang beriman, jadilah kamu orang yang benar-benar penegak keadilan, menjadi saksi karena Allah biarpun terhadap dirimu sendiri atau ibu bapa dan kaum kerabatmu. Jika ia kaya ataupun miskin, maka Allah lebih tahu kemaslahatannya. Maka janganlah kamu mengikuti hawa nafsu karena ingin menyimpang dari kebenaran. Dan jika kamu memutar balikkan (kata-kata) atau enggan menjadi saksi, maka sesungguhnya Allah adalah Maha Mengetahui segala apa yang kamu kerjakan.'),
(629, '004:136', '68', 4, 136, 'Wahai orang-orang yang beriman, tetaplah beriman kepada Allah dan Rasul-Nya dan kepada kitab yang Allah turunkan kepada Rasul-Nya serta kitab yang Allah turunkan sebelumnya. Barangsiapa yang kafir kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya, rasul-rasul-Nya, dan hari kemudian, maka sesungguhnya orang itu telah sesat sejauh-jauhnya.'),
(630, '004:137', '68', 4, 137, 'Sesungguhnya orang-orang yang beriman kemudian kafir, kemudian beriman (pula), kamudian kafir lagi, kemudian bertambah kekafirannya, maka sekali-kali Allah tidak akan memberi ampunan kepada mereka, dan tidak (pula) menunjuki mereka kepada jalan yang lurus.'),
(631, '004:138', '68', 4, 138, 'Kabarkanlah kepada orang-orang munafik bahwa mereka akan mendapat siksaan yang pedih,'),
(632, '004:139', '68', 4, 139, '(yaitu) orang-orang yang mengambil orang-orang kafir menjadi teman-teman penolong dengan meninggalkan orang-orang mukmin. Apakah mereka mencari kekuatan di sisi orang kafir itu? Maka sesungguhnya semua kekuatan kepunyaan Allah.'),
(633, '004:140', '68', 4, 140, 'Dan sungguh Allah telah menurunkan kekuatan kepada kamu di dalam Al Quran bahwa apabila kamu mendengar ayat-ayat Allah diingkari dan diperolok-olokkan (oleh orang-orang kafir), maka janganlah kamu duduk beserta mereka, sehingga mereka memasuki pembicaraan yang lain. Karena sesungguhnya (kalau kamu berbuat demikian), tentulah kamu serupa dengan mereka. Sesungguhnya Allah akan mengumpulkan semua orang-orang munafik dan orang-orang kafir di dalam Jahannam,');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(634, '004:141', '68', 4, 141, '(yaitu) orang-orang yang menunggu-nunggu (peristiwa) yang akan terjadi pada dirimu (hai orang-orang mukmin). Maka jika terjadi bagimu kemenangan dari Allah mereka berkata: \"Bukankah kami (turut berperang) beserta kamu?\" Dan jika orang-orang kafir mendapat keberuntungan (kemenangan) mereka berkata: \"Bukankah kami turut memenangkanmu, dan membela kamu dari orang-orang mukmin?\" Maka Allah akan memberi keputusan di antara kamu di hari kiamat dan Allah sekali-kali tidak akan memberi jalan kepada orang-orang kafir untuk memusnahkan orang-orang yang beriman.'),
(635, '004:142', '68', 4, 142, 'Sesungguhnya orang-orang munafik itu menipu Allah, dan Allah akan membalas tipuan mereka. Dan apabila mereka berdiri untuk shalat mereka berdiri dengan malas. Mereka bermaksud riya (dengan shalat) di hadapan manusia. Dan tidaklah mereka menyebut Allah kecuali sedikit sekali.'),
(636, '004:143', '68', 4, 143, 'Mereka dalam keadaan ragu-ragu antara yang demikian (iman atau kafir): tidak masuk kepada golongan ini (orang-orang beriman) dan tidak (pula) kepada golongan itu (orang-orang kafir), maka kamu sekali-kali tidak akan mendapat jalan (untuk memberi petunjuk) baginya.'),
(637, '004:144', '68', 4, 144, 'Hai orang-orang yang beriman, janganlah kamu mengambil orang-orang kafir menjadi wali dengan meninggalkan orang-orang mukmin. Inginkah kamu mengadakan alasan yang nyata bagi Allah (untuk menyiksamu)?'),
(638, '004:145', '68', 4, 145, 'Sesungguhnya orang-orang munafik itu (ditempatkan) pada tingkatan yang paling bawah dari neraka. Dan kamu sekali-kali tidak akan mendapat seorang penolongpun bagi mereka.'),
(639, '004:146', '68', 4, 146, 'Kecuali orang-orang yang taubat dan mengadakan perbaikan dan berpegang teguh pada (agama) Allah dan tulus ikhlas (mengerjakan) agama mereka karena Allah. Maka mereka itu adalah bersama-sama orang yang beriman dan kelak Allah akan memberikan kepada orang-orang yang beriman pahala yang besar.'),
(640, '004:147', '68', 4, 147, 'Mengapa Allah akan menyiksamu, jika kamu bersyukur dan beriman? Dan Allah adalah Maha Mensyukuri lagi Maha Mengetahui.'),
(641, '004:148', '68', 4, 148, 'Allah tidak menyukai ucapan buruk, (yang diucapkan) dengan terus terang kecuali oleh orang yang dianiaya. Allah adalah Maha Mendengar lagi Maha Mengetahui.'),
(642, '004:149', '68', 4, 149, 'Jika kamu melahirkan sesuatu kebaikan atau menyembunyikan atau memaafkan sesuatu kesalahan (orang lain), maka sesungguhnya Allah Maha Pemaaf lagi Maha Kuasa.'),
(643, '004:150', '68', 4, 150, 'Sesungguhnya orang-orang yang kafir kepada Allah dan rasul-rasul-Nya, dan bermaksud memperbedakan antara (keimanan kepada) Allah dan rasul-rasul-Nya, dengan mengatakan: \"Kami beriman kepada yang sebahagian dan kami kafir terhadap sebahagian (yang lain)\", serta bermaksud (dengan perkataan itu) mengambil jalan (tengah) di antara yang demikian (iman atau kafir),'),
(644, '004:151', '68', 4, 151, 'merekalah orang-orang yang kafir sebenar-benarnya. Kami telah menyediakan untuk orang-orang yang kafir itu siksaan yang menghinakan.'),
(645, '004:152', '68', 4, 152, 'Orang-orang yang beriman kepada Allah dan para rasul-Nya dan tidak membeda-bedakan seorangpun di antara mereka, kelak Allah akan memberikan kepada mereka pahalanya. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(646, '004:153', '68', 4, 153, 'Ahli Kitab meminta kepadamu agar kamu menurunkan kepada mereka sebuah Kitab dari langit. Maka sesungguhnya mereka telah meminta kepada Musa yang lebih besar dari itu. Mereka berkata: \"Perlihatkanlah Allah kepada kami dengan nyata\". Maka mereka disambar petir karena kezalimannya, dan mereka menyembah anak sapi, sesudah datang kepada mereka bukti-bukti yang nyata, lalu Kami maafkan (mereka) dari yang demikian. Dan telah Kami berikan kepada Musa keterangan yang nyata.'),
(647, '004:154', '68', 4, 154, 'Dan telah Kami angkat ke atas (kepala) mereka bukit Thursina untuk (menerima) perjanjian (yang telah Kami ambil dari) mereka. Dan kami perintahkan kepada mereka: \"Masuklah pintu gerbang itu sambil bersujud\", dan Kami perintahkan (pula) kepada mereka: \"Janganlah kamu melanggar peraturan mengenai hari Sabtu\", dan Kami telah mengambil dari mereka perjanjian yang kokoh.'),
(648, '004:155', '68', 4, 155, 'Maka (Kami lakukan terhadap mereka beberapa tindakan), disebabkan mereka melanggar perjanjian itu, dan karena kekafiran mereka terhadap keterangan-keterangan Allah dan mereka membunuh nabi-nabi tanpa (alasan) yang benar dan mengatakan: \"Hati kami tertutup\". Bahkan, sebenarnya Allah telah mengunci mati hati mereka karena kekafirannya, karena itu mereka tidak beriman kecuali sebahagian kecil dari mereka.'),
(649, '004:156', '68', 4, 156, 'Dan karena kekafiran mereka (terhadap Isa) dan tuduhan mereka terhadap Maryam dengan kedustaan besar (zina),'),
(650, '004:157', '68', 4, 157, 'dan karena ucapan mereka: \"Sesungguhnya kami telah membunuh Al Masih, Isa putra Maryam, Rasul Allah\", padahal mereka tidak membunuhnya dan tidak (pula) menyalibnya, tetapi (yang mereka bunuh ialah) orang yang diserupakan dengan Isa bagi mereka. Sesungguhnya orang-orang yang berselisih paham tentang (pembunuhan) Isa, benar-benar dalam keragu-raguan tentang yang dibunuh itu. Mereka tidak mempunyai keyakinan tentang siapa yang dibunuh itu, kecuali mengikuti persangkaan belaka, mereka tidak (pula) yakin bahwa yang mereka bunuh itu adalah Isa.'),
(651, '004:158', '68', 4, 158, 'Tetapi (yang sebenarnya), Allah telah mengangkat Isa kepada-Nya. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.'),
(652, '004:159', '68', 4, 159, 'Tidak ada seorangpun dari Ahli Kitab, kecuali akan beriman kepadanya (Isa) sebelum kematiannya. Dan di hari kiamat nanti Isa itu akan menjadi saksi terhadap mereka.'),
(653, '004:160', '68', 4, 160, 'Maka disebabkan kezaliman orang-orang Yahudi, kami haramkan atas (memakan makanan) yang baik-baik (yang dahulunya) dihalalkan bagi mereka, dan karena mereka banyak menghalangi (manusia) dari jalan Allah,'),
(654, '004:161', '68', 4, 161, 'dan disebabkan mereka memakan riba, padahal sesungguhnya mereka telah dilarang daripadanya, dan karena mereka memakan harta benda orang dengan jalan yang batil. Kami telah menyediakan untuk orang-orang yang kafir di antara mereka itu siksa yang pedih.'),
(655, '004:162', '68', 4, 162, 'Tetapi orang-orang yang mendalam ilmunya di antara mereka dan orang-orang mukmin, mereka beriman kepada apa yang telah diturunkan kepadamu (Al Quran), dan apa yang telah diturunkan sebelummu dan orang-orang yang mendirikan shalat, menunaikan zakat, dan yang beriman kepada Allah dan hari kemudian. Orang-orang itulah yang akan Kami berikan kepada mereka pahala yang besar.'),
(656, '004:163', '68', 4, 163, 'Sesungguhnya Kami telah memberikan wahyu kepadamu sebagaimana Kami telah memberikan wahyu kepada Nuh dan nabi-nabi yang kemudiannya, dan Kami telah memberikan wahyu (pula) kepada Ibrahim, Isma´il, Ishak, Ya´qub dan anak cucunya, Isa, Ayyub, Yunus, Harun dan Sulaiman. Dan Kami berikan Zabur kepada Daud.'),
(657, '004:164', '68', 4, 164, 'Dan (Kami telah mengutus) rasul-rasul yang sungguh telah Kami kisahkan tentang mereka kepadamu dahulu, dan rasul-rasul yang tidak Kami kisahkan tentang mereka kepadamu. Dan Allah telah berbicara kepada Musa dengan langsung.'),
(658, '004:165', '68', 4, 165, '(Mereka Kami utus) selaku rasul-rasul pembawa berita gembira dan pemberi peringatan agar supaya tidak ada alasan bagi manusia membantah Allah sesudah diutusnya rasul-rasul itu. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.'),
(659, '004:166', '68', 4, 166, '(Mereka tidak mau mengakui yang diturunkan kepadamu itu), tetapi Allah mengakui Al Quran yang diturunkan-Nya kepadamu. Allah menurunkannya dengan ilmu-Nya; dan malaikat-malaikat pun menjadi saksi (pula). Cukuplah Allah yang mengakuinya.'),
(660, '004:167', '68', 4, 167, 'Sesungguhnya orang-orang yang kafir dan menghalang-halangi (manusia) dari jalan Allah, benar-benar telah sesat sejauh-jauhnya.'),
(661, '004:168', '68', 4, 168, 'Sesungguhnya orang-orang yang kafir dan melakukan kezaliman, Allah sekali-kali tidak akan mengampuni (dosa) mereka dan tidak (pula) akan menunjukkan jalan kepada mereka,'),
(662, '004:169', '68', 4, 169, 'kecuali jalan ke neraka Jahannam; mereka kekal di dalamnya selama-lamanya. Dan yang demikian itu adalah mudah bagi Allah.'),
(663, '004:170', '68', 4, 170, 'Wahai manusia, sesungguhnya telah datang Rasul (Muhammad) itu kepadamu dengan (membawa) kebenaran dari Tuhanmu, maka berimanlah kamu, itulah yang lebih baik bagimu. Dan jika kamu kafir, (maka kekafiran itu tidak merugikan Allah sedikitpun) karena sesungguhnya apa yang di langit dan di bumi itu adalah kepunyaan Allah. Dan adalah Allah Maha Mengetahui lagi Maha Bijaksana.'),
(664, '004:171', '68', 4, 171, 'Wahai Ahli Kitab, janganlah kamu melampaui batas dalam agamamu, dan janganlah kamu mengatakan terhadap Allah kecuali yang benar. Sesungguhnya Al Masih, Isa putera Maryam itu, adalah utusan Allah dan (yang diciptakan dengan) kalimat-Nya yang disampaikan-Nya kepada Maryam, dan (dengan tiupan) roh dari-Nya. Maka berimanlah kamu kepada Allah dan rasul-rasul-Nya dan janganlah kamu mengatakan: \"(Tuhan itu) tiga\", berhentilah (dari ucapan itu). (Itu) lebih baik bagimu. Sesungguhnya Allah Tuhan Yang Maha Esa, Maha Suci Allah dari mempunyai anak, segala yang di langit dan di bumi adalah kepunyaan-Nya. Cukuplah Allah menjadi Pemelihara.'),
(665, '004:172', '68', 4, 172, 'Al Masih sekali-kali tidak enggan menjadi hamba bagi Allah, dan tidak (pula enggan) malaikat-malaikat yang terdekat (kepada Allah). Barangsiapa yang enggan dari menyembah-Nya, dan menyombongkan diri, nanti Allah akan mengumpulkan mereka semua kepada-Nya.'),
(666, '004:173', '68', 4, 173, 'Adapun orang-orang yang beriman dan berbuat amal saleh, maka Allah akan menyempurnakan pahala mereka dan menambah untuk mereka sebagian dari karunia-Nya. Adapun orang-orang yang enggan dan menyombongkan diri, maka Allah akan menyiksa mereka dengan siksaan yang pedih, dan mereka tidak akan memperoleh bagi diri mereka, pelindung dan penolong selain dari pada Allah.'),
(667, '004:174', '68', 4, 174, 'Hai manusia, sesungguhnya telah datang kepadamu bukti kebenaran dari Tuhanmu. (Muhammad dengan mukjizatnya) dan telah Kami turunkan kepadamu cahaya yang terang benderang (Al Quran).'),
(668, '004:175', '68', 4, 175, 'Adapun orang-orang yang beriman kepada Allah dan berpegang teguh kepada (agama)-Nya niscaya Allah akan memasukkan mereka ke dalam rahmat yang besar dari-Nya (surga) dan limpahan karunia-Nya. Dan menunjuki mereka kepada jalan yang lurus (untuk sampai) kepada-Nya.'),
(669, '004:176', '68', 4, 176, 'Mereka meminta fatwa kepadamu (tentang kalalah). Katakanlah: \"Allah memberi fatwa kepadamu tentang kalalah (yaitu): jika seorang meninggal dunia, dan ia tidak mempunyai anak dan mempunyai saudara perempuan, maka bagi saudaranya yang perempuan itu seperdua dari harta yang ditinggalkannya, dan saudaranya yang laki-laki mempusakai (seluruh harta saudara perempuan), jika ia tidak mempunyai anak; tetapi jika saudara perempuan itu dua orang, maka bagi keduanya dua pertiga dari harta yang ditinggalkan oleh yang meninggal. Dan jika mereka (ahli waris itu terdiri dari) saudara-saudara laki dan perempuan, maka bahagian seorang saudara laki-laki sebanyak bahagian dua orang saudara perempuan. Allah menerangkan (hukum ini) kepadamu, supaya kamu tidak sesat. Dan Allah Maha Mengetahui segala sesuatu.'),
(670, '005:001', '68', 5, 1, 'Hai orang-orang yang beriman, penuhilah aqad-aqad itu. Dihalalkan bagimu binatang ternak, kecuali yang akan dibacakan kepadamu. (Yang demikian itu) dengan tidak menghalalkan berburu ketika kamu sedang mengerjakan haji. Sesungguhnya Allah menetapkan hukum-hukum menurut yang dikehendaki-Nya.'),
(671, '005:002', '68', 5, 2, 'Hai orang-orang yang beriman, janganlah kamu melanggar syi´ar-syi´ar Allah, dan jangan melanggar kehormatan bulan-bulan haram, jangan (mengganggu) binatang-binatang had-ya, dan binatang-binatang qalaa-id, dan jangan (pula) mengganggu orang-orang yang mengunjungi Baitullah sedang mereka mencari kurnia dan keridhaan dari Tuhannya dan apabila kamu telah menyelesaikan ibadah haji, maka bolehlah berburu. Dan janganlah sekali-kali kebencian(mu) kepada sesuatu kaum karena mereka menghalang-halangi kamu dari Masjidilharam, mendorongmu berbuat aniaya (kepada mereka). Dan tolong-menolonglah kamu dalam (mengerjakan) kebajikan dan takwa, dan jangan tolong-menolong dalam berbuat dosa dan pelanggaran. Dan bertakwalah kamu kepada Allah, sesungguhnya Allah amat berat siksa-Nya.'),
(672, '005:003', '68', 5, 3, 'Diharamkan bagimu (memakan) bangkai, darah, daging babi, (daging hewan) yang disembelih atas nama selain Allah, yang tercekik, yang terpukul, yang jatuh, yang ditanduk, dan diterkam binatang buas, kecuali yang sempat kamu menyembelihnya, dan (diharamkan bagimu) yang disembelih untuk berhala. Dan (diharamkan juga) mengundi nasib dengan anak panah, (mengundi nasib dengan anak panah itu) adalah kefasikan. Pada hari ini orang-orang kafir telah putus asa untuk (mengalahkan) agamamu, sebab itu janganlah kamu takut kepada mereka dan takutlah kepada-Ku. Pada hari ini telah Kusempurnakan untuk kamu agamamu, dan telah Ku-cukupkan kepadamu nikmat-Ku, dan telah Ku-ridhai Islam itu jadi agama bagimu. Maka barang siapa terpaksa karena kelaparan tanpa sengaja berbuat dosa, sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(673, '005:004', '68', 5, 4, 'Mereka menanyakan kepadamu: \"Apakah yang dihalalkan bagi mereka?\". Katakanlah: \"Dihalalkan bagimu yang baik-baik dan (buruan yang ditangkap) oleh binatang buas yang telah kamu ajar dengan melatih nya untuk berburu; kamu mengajarnya menurut apa yang telah diajarkan Allah kepadamu. Maka makanlah dari apa yang ditangkapnya untukmu, dan sebutlah nama Allah atas binatang buas itu (waktu melepaskannya). Dan bertakwalah kepada Allah, sesungguhnya Allah amat cepat hisab-Nya.'),
(674, '005:005', '68', 5, 5, 'Pada hari ini dihalalkan bagimu yang baik-baik. Makanan (sembelihan) orang-orang yang diberi Al Kitab itu halal bagimu, dan makanan kamu halal (pula) bagi mereka. (Dan dihalalkan mangawini) wanita yang menjaga kehormatan diantara wanita-wanita yang beriman dan wanita-wanita yang menjaga kehormatan di antara orang-orang yang diberi Al Kitab sebelum kamu, bila kamu telah membayar mas kawin mereka dengan maksud menikahinya, tidak dengan maksud berzina dan tidak (pula) menjadikannya gundik-gundik. Barangsiapa yang kafir sesudah beriman (tidak menerima hukum-hukum Islam) maka hapuslah amalannya dan ia di hari kiamat termasuk orang-orang merugi.'),
(675, '005:006', '68', 5, 6, 'Hai orang-orang yang beriman, apabila kamu hendak mengerjakan shalat, maka basuhlah mukamu dan tanganmu sampai dengan siku, dan sapulah kepalamu dan (basuh) kakimu sampai dengan kedua mata kaki, dan jika kamu junub maka mandilah, dan jika kamu sakit atau dalam perjalanan atau kembali dari tempat buang air (kakus) atau menyentuh perempuan, lalu kamu tidak memperoleh air, maka bertayammumlah dengan tanah yang baik (bersih); sapulah mukamu dan tanganmu dengan tanah itu. Allah tidak hendak menyulitkan kamu, tetapi Dia hendak membersihkan kamu dan menyempurnakan nikmat-Nya bagimu, supaya kamu bersyukur.'),
(676, '005:007', '68', 5, 7, 'Dan ingatlah karunia Allah kepadamu dan perjanjian-Nya yang telah diikat-Nya dengan kamu, ketika kamu mengatakan: \"Kami dengar dan kami taati\". Dan bertakwalah kepada Allah, sesungguhnya Allah Mengetahui isi hati(mu).'),
(677, '005:008', '68', 5, 8, 'Hai orang-orang yang beriman hendaklah kamu jadi orang-orang yang selalu menegakkan (kebenaran) karena Allah, menjadi saksi dengan adil. Dan janganlah sekali-kali kebencianmu terhadap sesuatu kaum, mendorong kamu untuk berlaku tidak adil. Berlaku adillah, karena adil itu lebih dekat kepada takwa. Dan bertakwalah kepada Allah, sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(678, '005:009', '68', 5, 9, 'Allah telah menjanjikan kepada orang-orang yang beriman dan yang beramal saleh, (bahwa) untuk mereka ampunan dan pahala yang besar.'),
(679, '005:010', '68', 5, 10, 'Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu adalah penghuni neraka.'),
(680, '005:011', '68', 5, 11, 'Hai orang-orang yang beriman, ingatlah kamu akan nikmat Allah (yang diberikan-Nya) kepadamu, di waktu suatu kaum bermaksud hendak menggerakkan tangannya kepadamu (untuk berbuat jahat), maka Allah menahan tangan mereka dari kamu. Dan bertakwalah kepada Allah, dan hanya kepada Allah sajalah orang-orang mukmin itu harus bertawakkal.'),
(681, '005:012', '68', 5, 12, 'Dan sesungguhnya Allah telah mengambil perjanjian (dari) Bani Israil dan telah Kami angkat diantara mereka 12 orang pemimpin dan Allah berfirman: \"Sesungguhnya Aku beserta kamu, sesungguhnya jika kamu mendirikan shalat dan menunaikan zakat serta beriman kepada rasul-rasul-Ku dan kamu bantu mereka dan kamu pinjamkan kepada Allah pinjaman yang baik sesungguhnya Aku akan menutupi dosa-dosamu. Dan sesungguhnya kamu akan Kumasukkan ke dalam surga yang mengalir air didalamnya sungai-sungai. Maka barangsiapa yang kafir di antaramu sesudah itu, sesungguhnya ia telah tersesat dari jalan yang lurus.'),
(682, '005:013', '68', 5, 13, '(Tetapi) karena mereka melanggar janjinya, Kami kutuki mereka, dan Kami jadikan hati mereka keras membatu. Mereka suka merubah perkataan (Allah) dari tempat-tempatnya, dan mereka (sengaja) melupakan sebagian dari apa yang mereka telah diperingatkan dengannya, dan kamu (Muhammad) senantiasa akan melihat kekhianatan dari mereka kecuali sedikit diantara mereka (yang tidak berkhianat), maka maafkanlah mereka dan biarkan mereka, sesungguhnya Allah menyukai orang-orang yang berbuat baik.'),
(683, '005:014', '68', 5, 14, 'Dan diantara orang-orang yang mengatakan: \"Sesungguhnya kami ini orang-orang Nasrani\", ada yang telah kami ambil perjanjian mereka, tetapi mereka (sengaja) melupakan sebagian dari apa yang mereka telah diberi peringatan dengannya; maka Kami timbulkan di antara mereka permusuhan dan kebencian sampai hari kiamat. Dan kelak Allah akan memberitakan kepada mereka apa yang mereka kerjakan.'),
(684, '005:015', '68', 5, 15, 'Hai Ahli Kitab, sesungguhnya telah datang kepadamu Rasul Kami, menjelaskan kepadamu banyak dari isi Al Kitab yang kamu sembunyi kan, dan banyak (pula yang) dibiarkannya. Sesungguhnya telah datang kepadamu cahaya dari Allah, dan Kitab yang menerangkan.'),
(685, '005:016', '68', 5, 16, 'Dengan kitab itulah Allah menunjuki orang-orang yang mengikuti keridhaan-Nya ke jalan keselamatan, dan (dengan kitab itu pula) Allah mengeluarkan orang-orang itu dari gelap gulita kepada cahaya yang terang benderang dengan seizin-Nya, dan menunjuki mereka ke jalan yang lurus.'),
(686, '005:017', '68', 5, 17, 'Sesungguhnya telah kafirlah orang-orang yang berkata: \"Sesungguhnya Allah itu ialah Al Masih putera Maryam\". Katakanlah: \"Maka siapakah (gerangan) yang dapat menghalang-halangi kehendak Allah, jika Dia hendak membinasakan Al Masih putera Maryam itu beserta ibunya dan seluruh orang-orang yang berada di bumi kesemuanya?\". Kepunyaan Allahlah kerajaan langit dan bumi dan apa yang ada diantara keduanya; Dia menciptakan apa yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.'),
(687, '005:018', '68', 5, 18, 'Orang-orang Yahudi dan Nasrani mengatakan: \"Kami ini adalah anak-anak Allah dan kekasih-kekasih-Nya\". Katakanlah: \"Maka mengapa Allah menyiksa kamu karena dosa-dosamu?\" (Kamu bukanlah anak-anak Allah dan kekasih-kekasih-Nya), tetapi kamu adalah manusia(biasa) diantara orang-orang yang diciptakan-Nya. Dia mengampuni bagi siapa yang dikehendaki-Nya dan menyiksa siapa yang dikehendaki-Nya. Dan Kepunyaan Allah-lah kerajaan antara keduanya. Dan kepada Allah-lah kembali (segala sesuatu).'),
(688, '005:019', '68', 5, 19, 'Hai Ahli Kitab, sesungguhnya telah datang kepada kamu Rasul Kami, menjelaskan (syari´at Kami) kepadamu ketika terputus (pengiriman) rasul-rasul agar kamu tidak mengatakan: \"Tidak ada datang kepada kami baik seorang pembawa berita gembira maupun seorang pemberi peringatan\". Sesungguhnya telah datang kepadamu pembawa berita gembira dan pemberi peringatan. Allah Maha Kuasa atas segala sesuatu.'),
(689, '005:020', '68', 5, 20, 'Dan (ingatlah) ketika Musa berkata kepada kaumnya: \"Hai kaumku, ingatlah nikmat Allah atasmu ketika Dia mengangkat nabi nabi diantaramu, dan dijadikan-Nya kamu orang-orang merdeka, dan diberikan-Nya kepadamu apa yang belum pernah diberikan-Nya kepada seorangpun diantara umat-umat yang lain\".'),
(690, '005:021', '68', 5, 21, 'Hai kaumku, masuklah ke tanah suci (Palestina) yang telah ditentukan Allah bagimu, dan janganlah kamu lari kebelakang (karena takut kepada musuh), maka kamu menjadi orang-orang yang merugi.'),
(691, '005:022', '68', 5, 22, 'Mereka berkata: \"Hai Musa, sesungguhnya dalam negeri itu ada orang-orang yang gagah perkasa, sesungguhnya kami sekali-kali tidak akan memasukinya sebelum mereka ke luar daripadanya. Jika mereka ke luar daripadanya, pasti kami akan memasukinya\".'),
(692, '005:023', '68', 5, 23, 'Berkatalah dua orang diantara orang-orang yang takut (kepada Allah) yang Allah telah memberi nikmat atas keduanya: \"Serbulah mereka dengan melalui pintu gerbang (kota) itu, maka bila kamu memasukinya niscaya kamu akan menang. Dan hanya kepada Allah hendaknya kamu bertawakkal, jika kamu benar-benar orang yang beriman\".'),
(693, '005:024', '68', 5, 24, 'Mereka berkata: \"Hai Musa, kami sekali sekali tidak akan memasukinya selama-lamanya, selagi mereka ada didalamnya, karena itu pergilah kamu bersama Tuhanmu, dan berperanglah kamu berdua, sesungguhnya kami hanya duduk menanti disini saja\".'),
(694, '005:025', '68', 5, 25, 'Berkata Musa: \"Ya Tuhanku, aku tidak menguasai kecuali diriku sendiri dan saudaraku. Sebab itu pisahkanlah antara kami dengan orang-orang yang fasik itu\".'),
(695, '005:026', '68', 5, 26, 'Allah berfirman: \"(Jika demikian), maka sesungguhnya negeri itu diharamkan atas mereka selama empat puluh tahun, (selama itu) mereka akan berputar-putar kebingungan di bumi (padang Tiih) itu. Maka janganlah kamu bersedih hati (memikirkan nasib) orang-orang yang fasik itu\".'),
(696, '005:027', '68', 5, 27, 'Ceritakanlah kepada mereka kisah kedua putera Adam (Habil dan Qabil) menurut yang sebenarnya, ketika keduanya mempersembahkan korban, maka diterima dari salah seorang dari mereka berdua (Habil) dan tidak diterima dari yang lain (Qabil). Ia berkata (Qabil): \"Aku pasti membunuhmu!\". Berkata Habil: \"Sesungguhnya Allah hanya menerima (korban) dari orang-orang yang bertakwa\".'),
(697, '005:028', '68', 5, 28, '\"Sungguh kalau kamu menggerakkan tanganmu kepadaku untuk membunuhku, aku sekali-kali tidak akan menggerakkan tanganku kepadamu untuk membunuhmu. Sesungguhnya aku takut kepada Allah, Tuhan seru sekalian alam\".'),
(698, '005:029', '68', 5, 29, '\"Sesungguhnya aku ingin agar kamu kembali dengan (membawa) dosa (membunuh)ku dan dosamu sendiri, maka kamu akan menjadi penghuni neraka, dan yang demikian itulah pembalasan bagi orang-orang yang zalim\".'),
(699, '005:030', '68', 5, 30, 'Maka hawa nafsu Qabil menjadikannya menganggap mudah membunuh saudaranya, sebab itu dibunuhnyalah, maka jadilah ia seorang diantara orang-orang yang merugi.'),
(700, '005:031', '68', 5, 31, 'Kemudian Allah menyuruh seekor burung gagak menggali-gali di bumi untuk memperlihatkan kepadanya (Qabil) bagaimana seharusnya menguburkan mayat saudaranya. Berkata Qabil: \"Aduhai celaka aku, mengapa aku tidak mampu berbuat seperti burung gagak ini, lalu aku dapat menguburkan mayat saudaraku ini?\" Karena itu jadilah dia seorang diantara orang-orang yang menyesal.'),
(701, '005:032', '68', 5, 32, 'Oleh karena itu Kami tetapkan (suatu hukum) bagi Bani Israil, bahwa: barangsiapa yang membunuh seorang manusia, bukan karena orang itu (membunuh) orang lain, atau bukan karena membuat kerusakan dimuka bumi, maka seakan-akan dia telah membunuh manusia seluruhnya. Dan barangsiapa yang memelihara kehidupan seorang manusia, maka seolah-olah dia telah memelihara kehidupan manusia semuanya. Dan sesungguhnya telah datang kepada mereka rasul-rasul Kami dengan (membawa) keterangan-keterangan yang jelas, kemudian banyak diantara mereka sesudah itu sungguh-sungguh melampaui batas dalam berbuat kerusakan dimuka bumi.'),
(702, '005:033', '68', 5, 33, 'Sesungguhnya pembalasan terhadap orang-orang yang memerangi Allah dan Rasul-Nya dan membuat kerusakan di muka bumi, hanyalah mereka dibunuh atau disalib, atau dipotong tangan dan kaki mereka dengan bertimbal balik, atau dibuang dari negeri (tempat kediamannya). Yang demikian itu (sebagai) suatu penghinaan untuk mereka didunia, dan di akhirat mereka beroleh siksaan yang besar,'),
(703, '005:034', '68', 5, 34, 'kecuali orang-orang yang taubat (di antara mereka) sebelum kamu dapat menguasai (menangkap) mereka; maka ketahuilah bahwasanya Allah Maha Pengampun lagi Maha Penyayang.'),
(704, '005:035', '68', 5, 35, 'Hai orang-orang yang beriman, bertakwalah kepada Allah dan carilah jalan yang mendekatkan diri kepada-Nya, dan berjihadlah pada jalan-Nya, supaya kamu mendapat keberuntungan.'),
(705, '005:036', '68', 5, 36, 'Sesungguhnya orang-orang yang kafir sekiranya mereka mempunyai apa yang dibumi ini seluruhnya dan mempunyai yang sebanyak itu (pula) untuk menebusi diri mereka dengan itu dari azab hari kiamat, niscaya (tebusan itu) tidak akan diterima dari mereka, dan mereka beroleh azab yang pedih.'),
(706, '005:037', '68', 5, 37, 'Mereka ingin keluar dari neraka, padahal mereka sekali-kali tidak dapat keluar daripadanya, dan mereka beroleh azab yang kekal.'),
(707, '005:038', '68', 5, 38, 'Laki-laki yang mencuri dan perempuan yang mencuri, potonglah tangan keduanya (sebagai) pembalasan bagi apa yang mereka kerjakan dan sebagai siksaan dari Allah. Dan Allah Maha Perkasa lagi Maha Bijaksana.'),
(708, '005:039', '68', 5, 39, 'Maka barangsiapa bertaubat (di antara pencuri-pencuri itu) sesudah melakukan kejahatan itu dan memperbaiki diri, maka sesungguhnya Allah menerima taubatnya. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(709, '005:040', '68', 5, 40, 'Tidakkah kamu tahu, sesungguhnya Allah-lah yang mempunyai kerajaan langit dan bumi, disiksa-Nya siapa yang dikehendaki-Nya dan diampuni-Nya bagi siapa yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.'),
(710, '005:041', '68', 5, 41, 'Hari Rasul, janganlah hendaknya kamu disedihkan oleh orang-orang yang bersegera (memperlihatkan) kekafirannya, yaitu diantara orang-orang yang mengatakan dengan mulut mereka: \"Kami telah beriman\", padahal hati mereka belum beriman; dan (juga) di antara orang-orang Yahudi. (Orang-orang Yahudi itu) amat suka mendengar (berita-berita) bohong dan amat suka mendengar perkataan-perkataan orang lain yang belum pernah datang kepadamu; mereka merubah perkataan-perkataan (Taurat) dari tempat-tempatnya. Mereka mengatakan: \"Jika diberikan ini (yang sudah di rubah-rubah oleh mereka) kepada kamu, maka terimalah, dan jika kamu diberi yang bukan ini maka hati-hatilah\". Barangsiapa yang Allah menghendaki kesesatannya, maka sekali-kali kamu tidak akan mampu menolak sesuatupun (yang datang) daripada Allah. Mereka itu adalah orang-orang yang Allah tidak hendak mensucikan hati mereka. Mereka beroleh kehinaan di dunia dan di akhirat mereka beroleh siksaan yang besar.'),
(711, '005:042', '68', 5, 42, 'Mereka itu adalah orang-orang yang suka mendengar berita bohong, banyak memakan yang haram. Jika mereka (orang Yahudi) datang kepadamu (untuk meminta putusan), maka putuskanlah (perkara itu) diantara mereka, atau berpalinglah dari mereka; jika kamu berpaling dari mereka maka mereka tidak akan memberi mudharat kepadamu sedikitpun. Dan jika kamu memutuskan perkara mereka, maka putuskanlah (perkara itu) diantara mereka dengan adil, sesungguhnya Allah menyukai orang-orang yang adil.'),
(712, '005:043', '68', 5, 43, 'Dan bagaimanakah mereka mengangkatmu menjadi hakim mereka, padahal mereka mempunyai Taurat yang didalamnya (ada) hukum Allah, kemudian mereka berpaling sesudah itu (dari putusanmu)? Dan mereka sungguh-sungguh bukan orang yang beriman.'),
(713, '005:044', '68', 5, 44, 'Sesungguhnya Kami telah menurunkan Kitab Taurat di dalamnya (ada) petunjuk dan cahaya (yang menerangi), yang dengan Kitab itu diputuskan perkara orang-orang Yahudi oleh nabi-nabi yang menyerah diri kepada Allah, oleh orang-orang alim mereka dan pendeta-pendeta mereka, disebabkan mereka diperintahkan memelihara kitab-kitab Allah dan mereka menjadi saksi terhadapnya. Karena itu janganlah kamu takut kepada manusia, (tetapi) takutlah kepada-Ku. Dan janganlah kamu menukar ayat-ayat-Ku dengan harga yang sedikit. Barangsiapa yang tidak memutuskan menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang kafir.'),
(714, '005:045', '68', 5, 45, 'Dan Kami telah tetapkan terhadap mereka di dalamnya (At Taurat) bahwasanya jiwa (dibalas) dengan jiwa, mata dengan mata, hidung dengan hidung, telinga dengan telinga, gigi dengan gigi, dan luka luka (pun) ada qishaashnya. Barangsiapa yang melepaskan (hak qishaash)nya, maka melepaskan hak itu (menjadi) penebus dosa baginya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang zalim.'),
(715, '005:046', '68', 5, 46, 'Dan Kami iringkan jejak mereka (nabi nabi Bani Israil) dengan Isa putera Maryam, membenarkan Kitab yang sebelumnya, yaitu: Taurat. Dan Kami telah memberikan kepadanya Kitab Injil sedang didalamnya (ada) petunjuk dan dan cahaya (yang menerangi), dan membenarkan kitab yang sebelumnya, yaitu Kitab Taurat. Dan menjadi petunjuk serta pengajaran untuk orang-orang yang bertakwa.'),
(716, '005:047', '68', 5, 47, 'Dan hendaklah orang-orang pengikut Injil, memutuskan perkara menurut apa yang diturunkan Allah didalamnya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itu adalah orang-orang yang fasik.'),
(717, '005:048', '68', 5, 48, 'Dan Kami telah turunkan kepadamu Al Quran dengan membawa kebenaran, membenarkan apa yang sebelumnya, yaitu kitab-kitab (yang diturunkan sebelumnya) dan batu ujian terhadap kitab-kitab yang lain itu; maka putuskanlah perkara mereka menurut apa yang Allah turunkan dan janganlah kamu mengikuti hawa nafsu mereka dengan meninggalkan kebenaran yang telah datang kepadamu. Untuk tiap-tiap umat diantara kamu, Kami berikan aturan dan jalan yang terang. Sekiranya Allah menghendaki, niscaya kamu dijadikan-Nya satu umat (saja), tetapi Allah hendak menguji kamu terhadap pemberian-Nya kepadamu, maka berlomba-lombalah berbuat kebajikan. Hanya kepada Allah-lah kembali kamu semuanya, lalu diberitahukan-Nya kepadamu apa yang telah kamu perselisihkan itu,'),
(718, '005:049', '68', 5, 49, 'dan hendaklah kamu memutuskan perkara di antara mereka menurut apa yang diturunkan Allah, dan janganlah kamu mengikuti hawa nafsu mereka. Dan berhati-hatilah kamu terhadap mereka, supaya mereka tidak memalingkan kamu dari sebahagian apa yang telah diturunkan Allah kepadamu. Jika mereka berpaling (dari hukum yang telah diturunkan Allah), maka ketahuilah bahwa sesungguhnya Allah menghendaki akan menimpakan mushibah kepada mereka disebabkan sebahagian dosa-dosa mereka. Dan sesungguhnya kebanyakan manusia adalah orang-orang yang fasik.'),
(719, '005:050', '68', 5, 50, 'Apakah hukum Jahiliyah yang mereka kehendaki, dan (hukum) siapakah yang lebih baik daripada (hukum) Allah bagi orang-orang yang yakin?'),
(720, '005:051', '68', 5, 51, 'Hai orang-orang yang beriman, janganlah kamu mengambil orang-orang Yahudi dan Nasrani menjadi pemimpin-pemimpin(mu); sebahagian mereka adalah pemimpin bagi sebahagian yang lain. Barangsiapa diantara kamu mengambil mereka menjadi pemimpin, maka sesungguhnya orang itu termasuk golongan mereka. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
(721, '005:052', '68', 5, 52, 'Maka kamu akan melihat orang-orang yang ada penyakit dalam hatinya (orang-orang munafik) bersegera mendekati mereka (Yahudi dan Nasrani), seraya berkata: \"Kami takut akan mendapat bencana\". Mudah-mudahan Allah akan mendatangkan kemenangan (kepada Rasul-Nya), atau sesuatu keputusan dari sisi-Nya. Maka karena itu, mereka menjadi menyesal terhadap apa yang mereka rahasiakan dalam diri mereka.'),
(722, '005:053', '68', 5, 53, 'Dan orang-orang yang beriman akan mengatakan: \"Inikah orang-orang yang bersumpah sungguh-sungguh dengan nama Allah, bahwasanya mereka benar-benar beserta kamu?\" Rusak binasalah segala amal mereka, lalu mereka menjadi orang-orang yang merugi.'),
(723, '005:054', '68', 5, 54, 'Hai orang-orang yang beriman, barangsiapa di antara kamu yang murtad dari agamanya, maka kelak Allah akan mendatangkan suatu kaum yang Allah mencintai mereka dan merekapun mencintai-Nya, yang bersikap lemah lembut terhadap orang yang mukmin, yang bersikap keras terhadap orang-orang kafir, yang berjihad dijalan Allah, dan yang tidak takut kepada celaan orang yang suka mencela. Itulah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya, dan Allah Maha Luas (pemberian-Nya), lagi Maha Mengetahui.'),
(724, '005:055', '68', 5, 55, 'Sesungguhnya penolong kamu hanyalah Allah, Rasul-Nya, dan orang-orang yang beriman, yang mendirikan shalat dan menunaikan zakat, seraya mereka tunduk (kepada Allah).'),
(725, '005:056', '68', 5, 56, 'Dan barangsiapa mengambil Allah, Rasul-Nya dan orang-orang yang beriman menjadi penolongnya, maka sesungguhnya pengikut (agama) Allah itulah yang pasti menang.'),
(726, '005:057', '68', 5, 57, 'Hai orang-orang yang beriman, janganlah kamu mengambil jadi pemimpinmu, orang-orang yang membuat agamamu jadi buah ejekan dan permainan, (yaitu) di antara orang-orang yang telah diberi kitab sebelummu, dan orang-orang yang kafir (orang-orang musyrik). Dan bertakwalah kepada Allah jika kamu betul-betul orang-orang yang beriman.'),
(727, '005:058', '68', 5, 58, 'Dan apabila kamu menyeru (mereka) untuk (mengerjakan) sembahyang, mereka menjadikannya buah ejekan dan permainan. Yang demikian itu adalah karena mereka benar-benar kaum yang tidak mau mempergunakan akal.'),
(728, '005:059', '68', 5, 59, 'Katakanlah: \"Hai Ahli Kitab, apakah kamu memandang kami salah, hanya lantaran kami beriman kepada Allah, kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan sebelumnya, sedang kebanyakan di antara kamu benar-benar orang-orang yang fasik?'),
(729, '005:060', '68', 5, 60, 'Katakanlah: \"Apakah akan aku beritakan kepadamu tentang orang-orang yang lebih buruk pembalasannya dari (orang-orang fasik) itu disisi Allah, yaitu orang-orang yang dikutuki dan dimurkai Allah, di antara mereka (ada) yang dijadikan kera dan babi dan (orang yang) menyembah thaghut?\". Mereka itu lebih buruk tempatnya dan lebih tersesat dari jalan yang lurus.'),
(730, '005:061', '68', 5, 61, 'Dan apabila orang-orang (Yahudi atau munafik) datang kepadamu, mereka mengatakan: \"Kami telah beriman\", padahal mereka datang kepadamu dengan kekafirannya dan mereka pergi (daripada kamu) dengan kekafirannya (pula); dan Allah lebih mengetahui apa yang mereka sembunyikan.'),
(731, '005:062', '68', 5, 62, 'Dan kamu akan melihat kebanyakan dari mereka (orang-orang Yahudi) bersegera membuat dosa, permusuhan dan memakan yang haram. Sesungguhnya amat buruk apa yang mereka telah kerjakan itu.'),
(732, '005:063', '68', 5, 63, 'Mengapa orang-orang alim mereka, pendeta-pendeta mereka tidak melarang mereka mengucapkan perkataan bohong dan memakan yang haram? Sesungguhnya amat buruk apa yang telah mereka kerjakan itu.'),
(733, '005:064', '68', 5, 64, 'Orang-orang Yahudi berkata: \"Tangan Allah terbelenggu\", sebenarnya tangan merekalah yang dibelenggu dan merekalah yang dilaknat disebabkan apa yang telah mereka katakan itu. (Tidak demikian), tetapi kedua-dua tangan Allah terbuka; Dia menafkahkan sebagaimana Dia kehendaki. Dan Al Quran yang diturunkan kepadamu dari Tuhanmu sungguh-sungguh akan menambah kedurhakaan dan kekafiran bagi kebanyakan di antara mereka. Dan Kami telah timbulkan permusuhan dan kebencian di antara mereka sampai hari kiamat. Setiap mereka menyalakan api peperangan Allah memadamkannya dan mereka berbuat kerusakan dimuka bumi dan Allah tidak menyukai orang-orang yang membuat kerusakan.'),
(734, '005:065', '68', 5, 65, 'Dan sekiranya Ahli Kitab beriman dan bertakwa, tentulah Kami tutup (hapus) kesalahan-kesalahan mereka dan tentulah Kami masukkan mereka kedalam surga-surga yang penuh kenikmatan.'),
(735, '005:066', '68', 5, 66, 'Dan sekiranya mereka sungguh-sungguh menjalankan (hukum) Taurat dan Injil dan (Al Quran) yang diturunkan kepada mereka dari Tuhannya, niscaya mereka akan mendapat makanan dari atas dan dari bawah kaki mereka. Diantara mereka ada golongan yang pertengahan. Dan alangkah buruknya apa yang dikerjakan oleh kebanyakan mereka.'),
(736, '005:067', '68', 5, 67, 'Hai Rasul, sampaikanlah apa yang diturunkan kepadamu dari Tuhanmu. Dan jika tidak kamu kerjakan (apa yang diperintahkan itu, berarti) kamu tidak menyampaikan amanat-Nya. Allah memelihara kamu dari (gangguan) manusia. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang kafir.'),
(737, '005:068', '68', 5, 68, 'Katakanlah: \"Hai Ahli Kitab, kamu tidak dipandang beragama sedikitpun hingga kamu menegakkan ajaran-ajaran Taurat, Injil, dan Al Quran yang diturunkan kepadamu dari Tuhanmu\". Sesungguhnya apa yang diturunkan kepadamu (Muhammad) dari Tuhanmu akan menambah kedurhakaan dan kekafiran kepada kebanyakan dari mereka; maka janganlah kamu bersedih hati terhadap orang-orang yang kafir itu.'),
(738, '005:069', '68', 5, 69, 'Sesungguhnya orang-orang mukmin, orang-orang Yahudi, Shabiin dan orang-orang Nasrani, siapa saja (diantara mereka) yang benar-benar saleh, maka tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(739, '005:070', '68', 5, 70, 'Sesungguhnya Kami telah mengambil perjanjian dari Bani Israil, dan telah Kami utus kepada mereka rasul-rasul. Tetapi setiap datang seorang rasul kepada mereka dengan membawa apa yang yang tidak diingini oleh hawa nafsu mereka, (maka) sebagian dari rasul-rasul itu mereka dustakan dan sebagian yang lain mereka bunuh.'),
(740, '005:071', '68', 5, 71, 'Dan mereka mengira bahwa tidak akan terjadi suatu bencanapun (terhadap mereka dengan membunuh nabi-nabi itu), maka (karena itu) mereka menjadi buta dan pekak, kemudian Allah menerima taubat mereka, kemudian kebanyakan dari mereka buta dan tuli (lagi). Dan Allah Maha Melihat apa yang mereka kerjakan.'),
(741, '005:072', '68', 5, 72, 'Sesungguhnya telah kafirlah orang-orang yang berkata: \"Sesungguhnya Allah ialah Al Masih putera Maryam\", padahal Al Masih (sendiri) berkata: \"Hai Bani Israil, sembahlah Allah Tuhanku dan Tuhanmu\". Sesungguhnya orang yang mempersekutukan (sesuatu dengan) Allah, maka pasti Allah mengharamkan kepadanya surga, dan tempatnya ialah neraka, tidaklah ada bagi orang-orang zalim itu seorang penolongpun.'),
(742, '005:073', '68', 5, 73, 'Sesungguhnya kafirlah orang0orang yang mengatakan: \"Bahwasanya Allah salah seorang dari yang tiga\", padahal sekali-kali tidak ada Tuhan selain dari Tuhan Yang Esa. Jika mereka tidak berhenti dari apa yang mereka katakan itu, pasti orang-orang yang kafir diantara mereka akan ditimpa siksaan yang pedih.'),
(743, '005:074', '68', 5, 74, 'Maka mengapa mereka tidak bertaubat kepada Allah dan memohon ampun kepada-Nya?. Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(744, '005:075', '68', 5, 75, 'Al Masih putera Maryam itu hanyalah seorang Rasul yang sesungguhnya telah berlalu sebelumnya beberapa rasul, dan ibunya seorang yang sangat benar, kedua-duanya biasa memakan makanan. Perhatikan bagaimana Kami menjelaskan kepada mereka (ahli kitab) tanda-tanda kekuasaan (Kami), kemudian perhatikanlah bagaimana mereka berpaling (dari memperhatikan ayat-ayat Kami itu).'),
(745, '005:076', '68', 5, 76, 'Katakanlah: \"Mengapa kamu menyembah selain daripada Allah, sesuatu yang tidak dapat memberi mudharat kepadamu dan tidak (pula) memberi manfaat?\" Dan Allah-lah Yang Maha Mendengar lagi Maha Mengetahui.'),
(746, '005:077', '68', 5, 77, 'Katakanlah: \"Hai Ahli Kitab, janganlah kamu berlebih-lebihan (melampaui batas) dengan cara tidak benar dalam agamamu. Dan janganlah kamu mengikuti hawa nafsu orang-orang yang telah sesat dahulunya (sebelum kedatangan Muhammad) dan mereka telah menyesatkan kebanyakan (manusia), dan mereka tersesat dari jalan yang lurus\".'),
(747, '005:078', '68', 5, 78, 'Telah dilaknati orang-orang kafir dari Bani Israil dengan lisan Daud dan Isa putera Maryam. Yang demikian itu, disebabkan mereka durhaka dan selalu melampaui batas.'),
(748, '005:079', '68', 5, 79, 'Mereka satu sama lain selalu tidak melarang tindakan munkar yang mereka perbuat. Sesungguhnya amat buruklah apa yang selalu mereka perbuat itu.'),
(749, '005:080', '68', 5, 80, 'Kamu melihat kebanyakan dari mereka tolong-menolong dengan orang-orang yang kafir (musyrik). Sesungguhnya amat buruklah apa yang mereka sediakan untuk diri mereka, yaitu kemurkaan Allah kepada mereka; dan mereka akan kekal dalam siksaan.'),
(750, '005:081', '68', 5, 81, 'Sekiranya mereka beriman kepada Allah, kepada Nabi (Musa) dan kepada apa yang diturunkan kepadanya (Nabi), niscaya mereka tidak akan mengambil orang-orang musyrikin itu menjadi penolong-penolong, tapi kebanyakan dari mereka adalah orang-orang yang fasik.'),
(751, '005:082', '68', 5, 82, 'Sesungguhnya kamu dapati orang-orang yang paling keras permusuhannya terhadap orang-orang yang beriman ialah orang-orang Yahudi dan orang-orang musyrik. Dan sesungguhnya kamu dapati yang paling dekat persahabatannya dengan orang-orang yang beriman ialah orang-orang yang berkata: \"Sesungguhnya kami ini orang Nasrani\". Yang demikian itu disebabkan karena di antara mereka itu (orang-orang Nasrani) terdapat pendeta-pendeta dan rahib-rahib, (juga) karena sesungguhnya mereka tidak menymbongkan diri.'),
(752, '005:083', '68', 5, 83, 'Dan apabila mereka mendengarkan apa yang diturunkan kepada Rasul (Muhammad), kamu lihat mata mereka mencucurkan air mata disebabkan kebenaran (Al Quran) yang telah mereka ketahui (dari kitab-kitab mereka sendiri); seraya berkata: \"Ya Tuhan kami, kami telah beriman, maka catatlah kami bersama orang-orang yang menjadi saksi (atas kebenaran Al Quran dan kenabian Muhammad s.a.w.).'),
(753, '005:084', '68', 5, 84, 'Mengapa kami tidak akan beriman kepada Allah dan kepada kebenaran yang datang kepada kami, padahal kami sangat ingin agar Tuhan kami memasukkan kami ke dalam golongan orang-orang yang saleh?\".'),
(754, '005:085', '68', 5, 85, 'Maka Allah memberi mereka pahala terhadap perkataan yang mereka ucapkan, (yaitu) surga yang mengalir sungai-sungai di dalamnya, sedang mereka kekal di dalamnya. Dan itulah balasan (bagi) orang-orang yang berbuat kebaikan (yang ikhlas keimanannya).'),
(755, '005:086', '68', 5, 86, 'Dan orang-orang kafir serta mendustakan ayat-ayat Kami, mereka itulah penghuni neraka.'),
(756, '005:087', '68', 5, 87, 'Hai orang-orang yang beriman, janganlah kamu haramkan apa-apa yang baik yang telah Allah halalkan bagi kamu, dan janganlah kamu melampaui batas. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.'),
(757, '005:088', '68', 5, 88, 'Dan makanlah makanan yang halal lagi baik dari apa yang Allah telah rezekikan kepadamu, dan bertakwalah kepada Allah yang kamu beriman kepada-Nya.'),
(758, '005:089', '68', 5, 89, 'Allah tidak menghukum kamu disebabkan sumpah-sumpahmu yang tidak dimaksud (untuk bersumpah), tetapi Dia menghukum kamu disebabkan sumpah-sumpah yang kamu sengaja, maka kaffarat (melanggar) sumpah itu, ialah memberi makan sepuluh orang miskin, yaitu dari makanan yang biasa kamu berikan kepada keluargamu, atau memberi pakaian kepada mereka atau memerdekakan seorang budak. Barang siapa tidak sanggup melakukan yang demikian, maka kaffaratnya puasa selama tiga hari. Yang demikian itu adalah kaffarat sumpah-sumpahmu bila kamu bersumpah (dan kamu langgar). Dan jagalah sumpahmu. Demikianlah Allah menerangkan kepadamu hukum-hukum-Nya agar kamu bersyukur (kepada-Nya).'),
(759, '005:090', '68', 5, 90, 'Hai orang-orang yang beriman, sesungguhnya (meminum) khamar, berjudi, (berkorban untuk) berhala, mengundi nasib dengan panah, adalah termasuk perbuatan syaitan. Maka jauhilah perbuatan-perbuatan itu agar kamu mendapat keberuntungan.'),
(760, '005:091', '68', 5, 91, 'Sesungguhnya syaitan itu bermaksud hendak menimbulkan permusuhan dan kebencian di antara kamu lantaran (meminum) khamar dan berjudi itu, dan menghalangi kamu dari mengingat Allah dan sembahyang; maka berhentilah kamu (dari mengerjakan pekerjaan itu).'),
(761, '005:092', '68', 5, 92, 'Dan taatlah kamu kepada Allah dan taatlah kamu kepada Rasul-(Nya) dan berhati-hatilah. Jika kamu berpaling, maka ketahuilah bahwa sesungguhnya kewajiban Rasul Kami, hanyalah menyampaikan (amanat Allah) dengan terang.'),
(762, '005:093', '68', 5, 93, 'Tidak ada dosa bagi orang-orang yang beriman dan mengerjakan amalan yang saleh karena memakan makanan yang telah mereka makan dahulu, apabila mereka bertakwa serta beriman, dan mengerjakan amalan-amalan yang saleh, kemudian mereka tetap bertakwa dan beriman, kemudian mereka (tetap juga) bertakwa dan berbuat kebajikan. Dan Allah menyukai orang-orang yang berbuat kebajikan.'),
(763, '005:094', '68', 5, 94, 'Hai orang-orang yang beriman, sesungguhnya Allah akan menguji kamu dengan sesuatu dari binatang buruan yang mudah didapat oleh tangan dan tombakmu supaya Allah mengetahui orang yang takut kepada-Nya, biarpun ia tidak dapat melihat-Nya. Barang siapa yang melanggar batas sesudah itu, maka baginya azab yang pedih.'),
(764, '005:095', '68', 5, 95, 'Hai orang-orang yang beriman, janganlah kamu membunuh binatang buruan, ketika kamu sedang ihram. Barangsiapa di antara kamu membunuhnya dengan sengaja, maka dendanya ialah mengganti dengan binatang ternak seimbang dengan buruan yang dibunuhnya, menurut putusan dua orang yang adil di antara kamu sebagai had-yad yang dibawa sampai ke Ka´bah atau (dendanya) membayar kaffarat dengan memberi makan orang-orang miskin atau berpuasa seimbang dengan makanan yang dikeluarkan itu, supaya dia merasakan akibat buruk dari perbuatannya. Allah telah memaafkan apa yang telah lalu. Dan barangsiapa yang kembali mengerjakannya, niscaya Allah akan menyiksanya. Allah Maha Kuasa lagi mempunyai (kekuasaan untuk) menyiksa.'),
(765, '005:096', '68', 5, 96, 'Dihalalkan bagimu binatang buruan laut dan makanan (yang berasal) dari laut sebagai makanan yang lezat bagimu, dan bagi orang-orang yang dalam perjalanan; dan diharamkan atasmu (menangkap) binatang buruan darat, selama kamu dalam ihram. Dan bertakwalah kepada Allah Yang kepada-Nya-lah kamu akan dikumpulkan.'),
(766, '005:097', '68', 5, 97, 'Allah telah menjadikan Ka´bah, rumah suci itu sebagai pusat (peribadatan dan urusan dunia) bagi manusia, dan (demikian pula) bulan Haram, had-ya, qalaid. (Allah menjadikan yang) demikian itu agar kamu tahu, bahwa sesungguhnya Allah mengetahui apa yang ada di langit dan apa yang ada di bumi dan bahwa sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
(767, '005:098', '68', 5, 98, 'Ketahuilah, bahwa sesungguhnya Allah amat berat siksa-Nya dan bahwa sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(768, '005:099', '68', 5, 99, 'Kewajiban Rasul tidak lain hanyalah menyampaikan, dan Allah mengetahui apa yang kamu lahirkan dan apa yang kamu sembunyikan.'),
(769, '005:100', '68', 5, 100, 'Katakanlah: \"Tidak sama yang buruk dengan yang baik, meskipun banyaknya yang buruk itu menarik hatimu, maka bertakwalah kepada Allah hai orang-orang berakal, agar kamu mendapat keberuntungan\".'),
(770, '005:101', '68', 5, 101, 'Hai orang-orang yang beriman, janganlah kamu menanyakan (kepada Nabimu) hal-hal yang jika diterangkan kepadamu akan menyusahkan kamu dan jika kamu menanyakan di waktu Al Quran itu diturunkan, niscaya akan diterangkan kepadamu, Allah memaafkan (kamu) tentang hal-hal itu. Allah Maha Pengampun lagi Maha Penyantun.'),
(771, '005:102', '68', 5, 102, 'Sesungguhnya telah ada segolongsn manusia sebelum kamu menanyakan hal-hal yang serupa itu (kepada Nabi mereka), kemudian mereka tidak percaya kepadanya.'),
(772, '005:103', '68', 5, 103, 'Allah sekali-kali tidak pernah mensyari´atkan adanya bahiirah, saaibah, washiilah dan haam. Akan tetapi orang-orang kafir membuat-buat kedustaan terhadap Allah, dan kebanyakan mereka tidak mengerti.'),
(773, '005:104', '68', 5, 104, 'Apabila dikatakan kepada mereka: \"Marilah mengikuti apa yang diturunkan Allah dan mengikuti Rasul\". Mereka menjawab: \"Cukuplah untuk kami apa yang kami dapati bapak-bapak kami mengerjakannya\". Dan apakah mereka itu akan mengikuti nenek moyang mereka walaupun nenek moyang mereka itu tidak mengetahui apa-apa dan tidak (pula) mendapat petunjuk?.'),
(774, '005:105', '68', 5, 105, 'Hai orang-orang yang beriman, jagalah dirimu; tiadalah orang yang sesat itu akan memberi mudharat kepadamu apabila kamu telah mendapat petunjuk. Hanya kepada Allah kamu kembali semuanya, maka Dia akan menerangkan kepadamu apa yang telah kamu kerjakan.'),
(775, '005:106', '68', 5, 106, 'Hai orang-orang yang beriman, apabila salah seorang kamu menghadapi kematian, sedang dia akan berwasiat, maka hendaklah (wasiat itu) disaksikan oleh dua orang yang adil di antara kamu, atau dua orang yang berlainan agama dengan kamu, jika kamu dalam perjalanan dimuka bumi lalu kamu ditimpa bahaya kematian. Kamu tahan kedua saksi itu sesudah sembahyang (untuk bersumpah), lalu mereka keduanya bersumpah dengan nama Allah, jika kamu ragu-ragu: \"(Demi Allah) kami tidak akan membeli dengan sumpah ini harga yang sedikit (untuk kepentingan seseorang), walaupun dia karib kerabat, dan tidak (pula) kami menyembunyikan persaksian Allah; sesungguhnya kami kalau demikian tentulah termasuk orang-orang yang berdosa\".');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(776, '005:107', '68', 5, 107, 'Jika diketahui bahwa kedua (saksi itu) membuat dosa, maka dua orang yang lain di antara ahli waris yang berhak yang lebih dekat kepada orang yang meninggal (memajukan tuntutan) untuk menggantikannya, lalu keduanya bersumpah dengan nama Allah: \"Sesungguhnya persaksian kami labih layak diterima daripada persaksian kedua saksi itu, dan kami tidak melanggar batas, sesungguhnya kami kalau demikian tentulah termasuk orang yang menganiaya diri sendiri\".'),
(777, '005:108', '68', 5, 108, 'Itu lebih dekat untuk (menjadikan para saksi) mengemukakan persaksiannya menurut apa yang sebenarnya, dan (lebih dekat untuk menjadikan mereka) merasa takut akan dikembalikan sumpahnya (kepada ahli waris) sesudah mereka bersumpah. Dan bertakwalah kepada Allah dan dengarkanlah (perintah-Nya). Allah tidak memberi petunjuk kepada orang-orang yang fasik.'),
(778, '005:109', '68', 5, 109, '(Ingatlah), hari di waktu Allah mengumpulkan para rasul lalu Allah bertanya (kepada mereka): \"Apa jawaban kaummu terhadap (seruan)mu?\". Para rasul menjawab: \"Tidak ada pengetahuan kami (tentang itu); sesungguhnya Engkau-lah yang mengetahui perkara yang ghaib\".'),
(779, '005:110', '68', 5, 110, '(Ingatlah), ketika Allah mengatakan: \"Hai Isa putra Maryam, ingatlah nikmat-Ku kepadamu dan kepada ibumu di waktu Aku menguatkan kamu dengan ruhul qudus. Kamu dapat berbicara dengan manusia di waktu masih dalam buaian dan sesudah dewasa; dan (ingatlah) di waktu Aku mengajar kamu menulis, hikmah, Taurat dan Injil, dan (ingatlah pula) diwaktu kamu membentuk dari tanah (suatu bentuk) yang berupa burung dengan ijin-Ku, kemudian kamu meniup kepadanya, lalu bentuk itu menjadi burung (yang sebenarnya) dengan seizin-Ku. Dan (ingatlah) di waktu kamu menyembuhkan orang yang buta sejak dalam kandungan ibu dan orang yang berpenyakit sopak dengan seizin-Ku, dan (ingatlah) di waktu kamu mengeluarkan orang mati dari kubur (menjadi hidup) dengan seizin-Ku, dan (ingatlah) di waktu Aku menghalangi Bani Israil (dari keinginan mereka membunuh kamu) di kala kamu mengemukakan kepada mereka keterangan-keterangan yang nyata, lalu orang-orang kafir diantara mereka berkata: \"Ini tidak lain melainkan sihir yang nyata\".'),
(780, '005:111', '68', 5, 111, 'Dan (ingatlah), ketika Aku ilhamkan kepada pengikut Isa yang setia: \"Berimanlah kamu kepada-Ku dan kepada rasul-Ku\". Mereka menjawab: Kami telah beriman dan saksikanlah (wahai rasul) bahwa sesungguhnya kami adalah orang-orang yang patuh (kepada seruanmu)\".'),
(781, '005:112', '68', 5, 112, '(Ingatlah), ketika pengikut-pengikut Isa berkata: \"Hai Isa putera Maryam, sanggupkah Tuhanmu menurunkan hidangan dari langit kepada kami?\". Isa menjawab: \"Bertakwalah kepada Allah jika kamu betul-betul orang yang beriman\".'),
(782, '005:113', '68', 5, 113, 'Mereka berkata: \"Kami ingin memakan hidangan itu dan supaya tenteram hati kami dan supaya kami yakin bahwa kamu telah berkata benar kepada kami, dan kami menjadi orang-orang yang menyaksikan hidangan itu\".'),
(783, '005:114', '68', 5, 114, 'Isa putera Maryam berdoa: \"Ya Tuhan kami turunkanlah kiranya kepada kami suatu hidangan dari langit (yang hari turunnya) akan menjadi hari raya bagi kami yaitu orang-orang yang bersama kami dan yang datang sesudah kami, dan menjadi tanda bagi kekuasaan Engkau; beri rzekilah kami, dan Engkaulah pemberi rezeki Yang Paling Utama\".'),
(784, '005:115', '68', 5, 115, 'Allah berfirman: \"Sesungguhnya Aku akan menurunkan hidangan itu kepadamu, barangsiapa yang kafir di antaramu sesudah (turun hidangan itu), maka sesungguhnya Aku akan menyiksanya dengan siksaan yang tidak pernah Aku timpakan kepada seorangpun di antara umat manusia\".'),
(785, '005:116', '68', 5, 116, 'Dan (ingatlah) ketika Allah berfirman: \"Hai Isa putera Maryam, adakah kamu mengatakan kepada manusia: \"Jadikanlah aku dan ibuku dua orang tuhan selain Allah?\". Isa menjawab: \"Maha Suci Engkau, tidaklah patut bagiku mengatakan apa yang bukan hakku (mengatakannya). Jika aku pernah mengatakan maka tentulah Engkau mengetahui apa yang ada pada diriku dan aku tidak mengetahui apa yang ada pada diri Engkau. Sesungguhnya Engkau Maha Mengetahui perkara yang ghaib-ghaib\".'),
(786, '005:117', '68', 5, 117, 'Aku tidak pernah mengatakan kepada mereka kecuali apa yang Engkau perintahkan kepadaku (mengatakan)nya yaitu: \"Sembahlah Allah, Tuhanku dan Tuhanmu\", dan adalah aku menjadi saksi terhadap mereka, selama aku berada di antara mereka. Maka setelah Engkau wafatkan aku, Engkau-lah yang mengawasi mereka. Dan Engkau adalah Maha Menyaksikan atas segala sesuatu.'),
(787, '005:118', '68', 5, 118, 'Jika Engkau menyiksa mereka, maka sesungguhnya mereka adalah hamba-hamba Engkau, dan jika Engkau mengampuni mereka, maka sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana.'),
(788, '005:119', '68', 5, 119, 'Allah berfirman: \"Ini adalah suatu hari yang bermanfaat bagi orang-orang yang benar kebenaran mereka. Bagi mereka surga yang dibawahnya mengalir sungai-sungai; mereka kekal di dalamnya selama-lamanya; Allah ridha terhadap-Nya. Itulah keberuntungan yang paling besar\".'),
(789, '005:120', '68', 5, 120, 'Kepunyaan Allah-lah kerajaan langit dan bumi dan apa yang ada di dalamnya; dan Dia Maha Kuasa atas segala sesuatu.'),
(790, '006:001', '68', 6, 1, 'Segala puji bagi Allah Yang telah menciptakan langit dan bumi dan mengadakan gelap dan terang, namun orang-orang yang kafir mempersekutukan (sesuatu) dengan Tuhan mereka.'),
(791, '006:002', '68', 6, 2, 'Dialah Yang menciptakan kamu dari tanah, sesudah itu ditentukannya ajal (kematianmu), dan ada lagi suatu ajal yang ada pada sisi-Nya (yang Dia sendirilah mengetahuinya), kemudian kamu masih ragu-ragu (tentang berbangkit itu).'),
(792, '006:003', '68', 6, 3, 'Dan Dialah Allah (yang disembah), baik di langit maupun di bumi; Dia mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan dan mengetahui (pula) apa yang kamu usahakan.'),
(793, '006:004', '68', 6, 4, 'Dan tidak ada suatu ayatpun dari ayat-ayat Tuhan sampai kepada mereka, melainkan mereka selalu berpaling dari padanya (mendustakannya).'),
(794, '006:005', '68', 6, 5, 'Sesungguhnya mereka telah mendustakan yang haq (Al-Quran) tatkala sampai kepada mereka, maka kelak akan sampai kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan.'),
(795, '006:006', '68', 6, 6, 'Apakah mereka tidak memperhatikan berapa banyak generasi yang telah Kami binasakan sebelum mereka, padahal (generasi itu) telah Kami teguhkan kedudukan mereka di muka bumi, yaitu keteguhan yang belum pernah Kami berikan kepadamu, dan Kami curahkan hujan yang lebat atas mereka dan Kami jadikan sungai-sungai mengalir di bawah mereka, kemudian Kami binasakan mereka karena dosa mereka sendiri, dan Kami ciptakan sesudah mereka generasi yang lain.'),
(796, '006:007', '68', 6, 7, 'Dan kalau Kami turunkan kepadamu tulisan di atas kertas, lalu mereka dapat menyentuhnya dengan tangan mereka sendiri, tentulah orang-orang kafir itu berkata: \"Ini tidak lain hanyalah sihir yang nyata\".'),
(797, '006:008', '68', 6, 8, 'Dan mereka berkata: \"Mengapa tidak diturunkan kepadanya (Muhammad) malaikat?\" dan kalau Kami turunkan (kepadanya) malaikat, tentulah selesai urusan itu, kemudian mereka tidak diberi tangguh (sedikitpun).'),
(798, '006:009', '68', 6, 9, 'Dan kalau Kami jadikan rasul itu malaikat, tentulah Kami jadikan dia seorang laki-laki dan (kalau Kami jadikan ia seorang laki-laki), tentulah Kami meragu-ragukan atas mereka apa yang mereka ragu-ragukan atas diri mereka sendiri.'),
(799, '006:010', '68', 6, 10, 'Dan sungguh telah diperolok-olokkan beberapa rasul sebelum kamu, maka turunlah kepada orang-orang yang mencemoohkan di antara mereka balasan (azab) olok-olokan mereka.'),
(800, '006:011', '68', 6, 11, 'Katakanlah: \"Berjalanlah di muka bumi, kemudian perhatikanlah bagaimana kesudahan orang-orang yang mendustakan itu\".'),
(801, '006:012', '68', 6, 12, 'Katakanlah: \"Kepunyaan siapakah apa yang ada di langit dan di bumi\". Katakanlah: \"Kepunyaan Allah\". Dia telah menetapkan atas Diri-Nya kasih sayang. Dia sungguh akan menghimpun kamu pada hari kiamat yang tidak ada keraguan padanya. Orang-orang yang meragukan dirinya mereka itu tidak beriman.'),
(802, '006:013', '68', 6, 13, 'Dan kepunyaan Allah-lah segala yang ada pada malam dan siang. Dan Dialah Yang Maha Mendengar lagi Maha Mengetahui.'),
(803, '006:014', '68', 6, 14, 'Katakanlah: \"Apakah akan aku jadikan pelindung selain dari Allah yang menjadikan langit dan bumi, padahal Dia memberi makan dan tidak memberi makan?\" Katakanlah: \"Sesungguhnya aku diperintah supaya aku menjadi orang yang pertama kali menyerah diri (kepada Allah), dan jangan sekali-kali kamu masuk golongan orang musyrik\".'),
(804, '006:015', '68', 6, 15, 'Katakanlah: \"Sesungguhnya aku takut akan azab hari yang besar (hari kiamat), jika aku mendurhakai Tuhanku\".'),
(805, '006:016', '68', 6, 16, 'Barang siapa yang dijauhkan azab dari padanya pada hari itu, maka sungguh Allah telah memberikan rahmat kepadanya. Dan itulah keberuntungan yang nyata.'),
(806, '006:017', '68', 6, 17, 'Dan jika Allah menimpakan sesuatu kemudharatan kepadamu, maka tidak ada yang menghilangkannya melainkan Dia sendiri. Dan jika Dia mendatangkan kebaikan kepadamu, maka Dia Maha Kuasa atas tiap-tiap sesuatu.'),
(807, '006:018', '68', 6, 18, 'Dan Dialah yang berkuasa atas sekalian hamba-hamba-Nya. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.'),
(808, '006:019', '68', 6, 19, 'Katakanlah: \"Siapakah yang lebih kuat persaksiannya?\" Katakanlah: \"Allah\". Dia menjadi saksi antara aku dan kamu. Dan Al Quran ini diwahyukan kepadaku supaya dengan dia aku memberi peringatan kepadamu dan kepada orang-orang yang sampai Al-Quran (kepadanya). Apakah sesungguhnya kamu mengakui bahwa ada tuhan-tuhan lain di samping Allah?\" Katakanlah: \"Aku tidak mengakui\". Katakanlah: \"Sesungguhnya Dia adalah Tuhan Yang Maha Esa dan sesungguhnya aku berlepas diri dari apa yang kamu persekutukan (dengan Allah)\".'),
(809, '006:020', '68', 6, 20, 'Orang-orang yang telah Kami berikan kitab kepadanya, mereka mengenalnya (Muhammad) seperti mereka mengenal anak-anaknya sendiri. Orang-orang yang merugikan dirinya, mereka itu tidak beriman (kepada Allah).'),
(810, '006:021', '68', 6, 21, 'Dan siapakah yang lebih aniaya daripada orang yang membuat-buat suatu kedustaan terhadap Allah, atau mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang aniaya itu tidak mendapat keberuntungan.'),
(811, '006:022', '68', 6, 22, 'Dan (ingatlah), hari yang di waktu itu Kami menghimpun mereka semuanya kemudian Kami berkata kepada orang-orang musyrik: \"Di manakah sembahan-sembahan kamu yang dulu kamu katakan (sekutu-sekutu) Kami?\".'),
(812, '006:023', '68', 6, 23, 'Kemudian tiadalah fitnah mereka, kecuali mengatakan: \"Demi Allah, Tuhan kami, tiadalah kami mempersekutukan Allah\".'),
(813, '006:024', '68', 6, 24, 'Lihatlah bagaimana mereka telah berdusta kepada diri mereka sendiri dan hilanglah daripada mereka sembahan-sembahan yang dahulu mereka ada-adakan.'),
(814, '006:025', '68', 6, 25, 'Dan di antara mereka ada orang yang mendengarkani (bacaan)mu, padahal Kami telah meletakkan tutupan di atas hati mereka (sehingga mereka tidak) memahaminya dan (Kami letakkan) sumbatan di telinganya. Dan jikapun mereka melihat segala tanda (kebenaran), mereka tetap tidak mau beriman kepadanya. Sehingga apabila mereka datang kepadamu untuk membantahmu, orang-orang kafir itu berkata: \"Al-Quran ini tidak lain hanyalah dongengan orang-orang dahulu\".'),
(815, '006:026', '68', 6, 26, 'Dan mereka melarang (orang lain) mendengarkan Al-Quran dan mereka sendiri menjauhkan diri daripadanya, dan mereka hanyalah membinasakan diri mereka sendiri, sedang mereka tidak menyadari.'),
(816, '006:027', '68', 6, 27, 'Dan jika kamu (Muhammad) melihat ketika mereka dihadapkan ke neraka, lalu mereka berkata: \"Kiranya kami dikembalikan (ke dunia) dan tidak mendustakan ayat-ayat Tuhan kami, serta menjadi orang-orang yang beriman\", (tentulah kamu melihat suatu peristiwa yang mengharukan).'),
(817, '006:028', '68', 6, 28, 'Tetapi (sebenarnya) telah nyata bagi mereka kejahatan yang mereka dahulu selalu menyembunyikannya. Sekiranya mereka dikembalikan ke dunia, tentulah mereka kembali kepada apa yang mereka telah dilarang mengerjakannya. Dan sesungguhnya mereka itu adalah pendusta belaka.'),
(818, '006:029', '68', 6, 29, 'Dan tentu mereka akan mengatakan (pula): \"Hidup hanyalah kehidupan kita di dunia ini saja, dan kita sekali-sekali tidak akan dibangkitkan\".'),
(819, '006:030', '68', 6, 30, 'Dan seandainya kamu melihat ketika mereka dihadapkan kepada Tuhannya (tentulah kamu melihat peristiwa yang mengharukan). Berfirman Allah: \"Bukankah (kebangkitan ini benar?\" Mereka menjawab: \"Sungguh benar, demi Tuhan kami\". Berfirman Allah: \"Karena itu rasakanlah azab ini, disebabkan kamu mengingkari(nya)\".'),
(820, '006:031', '68', 6, 31, 'Sungguh telah rugilah orang-orang yang mendustakan pertemuan mereka dengan Tuhan; sehingga apabila kiamat datang kepada mereka dengan tiba-tiba, mereka berkata: \"Alangkah besarnya penyesalan kami, terhadap kelalaian kami tentang kiamat itu!\", sambil mereka memikul dosa-dosa di atas punggungnya. Ingatlah, amat buruklah apa yang mereka pikul itu.'),
(821, '006:032', '68', 6, 32, 'Dan tiadalah kehidupan dunia ini, selain dari main-main dan senda gurau belaka. Dan sungguh kampung akhirat itu lebih baik bagi orang-orang yang bertakwa. Maka tidakkah kamu memahaminya?'),
(822, '006:033', '68', 6, 33, 'Sesungguhnya Kami mengetahui bahwasanya apa yang mereka katakan itu menyedihkan hatimu, (janganlah kamu bersedih hati), karena mereka sebenarnya bukan mendustakan kamu, akan tetapi orang-orang yang zalim itu mengingkari ayat-ayat Allah.'),
(823, '006:034', '68', 6, 34, 'Dan sesungguhnya telah didustakan (pula) rasul-rasul sebelum kamu, akan tetapi mereka sabar terhadap pendustaan dan penganiayaan (yang dilakukan) terhadap mereka, sampai datang pertolongan Allah kepada mereka. Tak ada seorangpun yang dapat merubah kalimat-kalimat (janji-janji) Allah. Dan sesungguhnya telah datang kepadamu sebahagian dari berita rasul-rasul itu.'),
(824, '006:035', '68', 6, 35, 'Dan jika perpalingan mereka (darimu) terasa amat berat bagimu, maka jika kamu dapat membuat lobang di bumi atau tangga ke langit lalu kamu dapat mendatangkan mukjizat kepada mereka (maka buatlah). Kalau Allah menghendaki, tentu saja Allah menjadikan mereka semua dalam petunjuk sebab itu janganlah sekali-kali kamu termasuk orang-orang yang jahil'),
(825, '006:036', '68', 6, 36, 'Hanya mereka yang mendengar sajalah yang mematuhi (seruan Allah), dan orang-orang yang mati (hatinya), akan dibangkitkan oleh Allah, kemudian kepada-Nya-lah mereka dikembalikan.'),
(826, '006:037', '68', 6, 37, 'Dan mereka (orang-orang musyrik Mekah) berkata: \"Mengapa tidak diturunkan kepadanya (Muhammad) suatu mukjizat dari Tuhannya?\" Katakanlah: \"Sesungguhnya Allah kuasa menurunkan suatu mukjizat, tetapi kebanyakan mereka tidak mengetahui\".'),
(827, '006:038', '68', 6, 38, 'Dan tiadalah binatang-binatang yang ada di bumi dan burung-burung yang terbang dengan kedua sayapnya, melainkan umat (juga) seperti kamu. Tiadalah Kami alpakan sesuatupun dalam Al-Kitab, kemudian kepada Tuhanlah mereka dihimpunkan.'),
(828, '006:039', '68', 6, 39, 'Dan orang-orang yang mendustakan ayat-ayat Kami adalah pekak, bisu dan berada dalam gelap gulita. Barangsiapa yang dikehendaki Allah (kesesatannya), niscaya disesatkan-Nya. Dan barangsiapa yang dikehendaki Allah (untuk diberi-Nya petunjuk), niscaya Dia menjadikan-Nya berada di atas jalan yang lurus.'),
(829, '006:040', '68', 6, 40, 'Katakanlah: \"Terangkanlah kepadaku jika datang siksaan Allah kepadamu, atau datang kepadamu hari kiamat, apakah kamu menyeru (tuhan) selain Allah; jika kamu orang-orang yang benar!\"'),
(830, '006:041', '68', 6, 41, '(Tidak), tetapi hanya Dialah yang kamu seru, maka Dia menghilangkan bahaya yang karenanya kamu berdoa kepada-Nya, jika Dia menghendaki, dan kamu tinggalkan sembahan-sembahan yang kamu sekutukan (dengan Allah).'),
(831, '006:042', '68', 6, 42, 'Dan sesungguhnya Kami telah mengutus (rasul-rasul) kepada umat-umat yang sebelum kamu, kemudian Kami siksa mereka dengan (menimpakan) kesengsaraan dan kemelaratan, supaya mereka memohon (kepada Allah) dengan tunduk merendahkan diri.'),
(832, '006:043', '68', 6, 43, 'Maka mengapa mereka tidak memohon (kepada Allah) dengan tunduk merendahkan diri ketika datang siksaan Kami kepada mereka, bahkan hati mereka telah menjadi keras, dan syaitanpun menampakkan kepada mereka kebagusan apa yang selalu mereka kerjakan.'),
(833, '006:044', '68', 6, 44, 'Maka tatkala mereka melupakan peringatan yang telah diberikan kepada mereka, Kamipun membukakan semua pintu-pintu kesenangan untuk mereka; sehingga apabila mereka bergembira dengan apa yang telah diberikan kepada mereka, Kami siksa mereka dengan sekonyong-konyong, maka ketika itu mereka terdiam berputus asa.'),
(834, '006:045', '68', 6, 45, 'Maka orang-orang yang zalim itu dimusnahkan sampai ke akar-akarnya. Segala puji bagi Allah, Tuhan semesta alam.'),
(835, '006:046', '68', 6, 46, 'Katakanlah: \"Terangkanlah kepadaku jika Allah mencabut pendengaran dan penglihatan serta menutup hatimu, siapakah tuhan selain Allah yang kuasa mengembalikannya kepadamu?\" Perhatikanlah bagaimana Kami berkali-kali memperlihatkan tanda-tanda kebesaran (Kami), kemudian mereka tetap berpaling (juga).'),
(836, '006:047', '68', 6, 47, 'Katakanlah: \"Terangkanlah kepadaku jika datang siksaan Allah kepadamu dengan sekonyong-konyong, atau terang-terangan, maka adakah yang dibinasakan (Allah) selain dari orang yang zalim?\"'),
(837, '006:048', '68', 6, 48, 'Dan tidaklah Kami mengutus para rasul itu melainkan untuk memberikan kabar gembira dan memberi peringatan. Barangsiapa yang beriman dan mengadakan perbaikan, maka tak ada kekhawatiran terhadap mereka dan tidak pula mereka bersedih hati.'),
(838, '006:049', '68', 6, 49, 'Dan orang-orang yang mendustakan ayat-ayat Kami, mereka akan ditimpa siksa disebabkan mereka selalu berbuat fasik.'),
(839, '006:050', '68', 6, 50, 'Katakanlah: Aku tidak mengatakan kepadamu, bahwa perbendaharaan Allah ada padaku, dan tidak (pula) aku mengetahui yang ghaib dan tidak (pula) aku mengatakan kepadamu bahwa aku seorang malaikat. Aku tidak mengikuti kecuali apa yang diwahyukan kepadaku. Katakanlah: \"Apakah sama orang yang buta dengan yang melihat?\" Maka apakah kamu tidak memikirkan(nya)?\"'),
(840, '006:051', '68', 6, 51, 'Dan berilah peringatan dengan apa yang diwahyukan itu kepada orang-orang yang takut akan dihimpunkan kepada Tuhannya (pada hari kiamat), sedang bagi mereka tidak ada seorang pelindung dan pemberi syafa´atpun selain daripada Allah, agar mereka bertakwa.'),
(841, '006:052', '68', 6, 52, 'Dan janganlah kamu mengusir orang-orang yang menyeru Tuhannya di pagi dan petang hari, sedang mereka menghendaki keridhaan-Nya. Kamu tidak memikul tanggung jawab sedikitpun terhadap perbuatan mereka dan merekapun tidak memikul tanggung jawab sedikitpun terhadap perbuatanmu, yang menyebabkan kamu (berhak) mengusir mereka, (sehingga kamu termasuk orang-orang yang zalim).'),
(842, '006:053', '68', 6, 53, 'Dan demikianlah telah Kami uji sebahagian mereka (orang-orang kaya) dengan sebahagian mereka (orang-orang miskin), supaya (orang-orang yang kaya itu) berkata: \"Orang-orang semacam inikah di antara kita yang diberi anugerah Allah kepada mereka?\" (Allah berfirman): \"Tidakkah Allah lebih mengetahui tentang orang-orang yang bersyukur (kepada-Nya)?\"'),
(843, '006:054', '68', 6, 54, 'Apabila orang-orang yang beriman kepada ayat-ayat Kami itu datang kepadamu, maka katakanlah: \"Salaamun alaikum. Tuhanmu telah menetapkan atas diri-Nya kasih sayang, (yaitu) bahwasanya barang siapa yang berbuat kejahatan di antara kamu lantaran kejahilan, kemudian ia bertaubat setelah mengerjakannya dan mengadakan perbaikan, maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(844, '006:055', '68', 6, 55, 'Dan demikianlah Kami terangkan ayat-ayat Al-Quran (supaya jelas jalan orang-orang yang saleh, dan supaya jelas (pula) jalan orang-orang yang berdosa.'),
(845, '006:056', '68', 6, 56, 'Katakanlah: \"Sesungguhnya aku dilarang menyembah tuhan-tuhan yang kamu sembah selain Allah\". Katakanlah: \"Aku tidak akan mengikuti hawa nafsumu, sungguh tersesatlah aku jika berbuat demikian dan tidaklah (pula) aku termasuk orang-orang yang mendapat petunjuk\".'),
(846, '006:057', '68', 6, 57, 'Katakanlah: \"Sesungguhnya aku berada di atas hujjah yang nyata (Al Quran) dari Tuhanku, sedang kamu mendustakannya. Tidak ada padaku apa (azab) yang kamu minta supaya disegerakan kedatangannya. Menetapkan hukum itu hanyalah hak Allah. Dia menerangkan yang sebenarnya dan Dia Pemberi keputusan yang paling baik\".'),
(847, '006:058', '68', 6, 58, 'Katakanlah: \"Kalau sekiranya ada padaku apa (azab) yang kamu minta supaya disegerakan, tentu telah diselesaikan Allah urusan yang ada antara aku dan kamu. Dan Allah lebih mengetahui tentang orang-orang yang zalim.'),
(848, '006:059', '68', 6, 59, 'Dan pada sisi Allah-lah kunci-kunci semua yang ghaib; tidak ada yang mengetahuinya kecuali Dia sendiri, dan Dia mengetahui apa yang di daratan dan di lautan, dan tiada sehelai daun pun yang gugur melainkan Dia mengetahuinya (pula), dan tidak jatuh sebutir biji-pun dalam kegelapan bumi, dan tidak sesuatu yang basah atau yang kering, melainkan tertulis dalam kitab yang nyata (Lauh Mahfudz)\"'),
(849, '006:060', '68', 6, 60, 'Dan Dialah yang menidurkan kamu di malam hari dan Dia mengetahui apa yang kamu kerjakan di siang hari, kemudian Dia membangunkan kamu pada siang hari untuk disempurnakan umur(mu) yang telah ditentukan, kemudian kepada Allah-lah kamu kembali, lalu Dia memberitahukan kepadamu apa yang dahulu kamu kerjakan.'),
(850, '006:061', '68', 6, 61, 'Dan Dialah yang mempunyai kekuasaan tertinggi di atas semua hamba-Nya, dan diutus-Nya kepadamu malaikat-malaikat penjaga, sehingga apabila datang kematian kepada salah seorang di antara kamu, ia diwafatkan oleh malaikat-malaikat Kami, dan malaikat-malaikat Kami itu tidak melalaikan kewajibannya.'),
(851, '006:062', '68', 6, 62, 'Kemudian mereka (hamba Allah) dikembalikan kepada Allah, Penguasa mereka yang sebenarnya. Ketahuilah bahwa segala hukum (pada hari itu) kepunyaan-Nya. Dan Dialah Pembuat Perhitungan yang paling cepat.'),
(852, '006:063', '68', 6, 63, 'Katakanlah: \"Siapakah yang dapat menyelamatkan kamu dari bencana di darat dan di laut, yang kamu berdoa kepada-Nya dengan rendah diri dengan suara yang lembut (dengan mengatakan: \"Sesungguhnya jika Dia menyelamatkan kami dari (bencana) ini, tentulah kami menjadi orang-orang yang bersyukur\"\".'),
(853, '006:064', '68', 6, 64, 'Katakanlah: \"Allah menyelamatkan kamu dari bencana itu dan dari segala macam kesusahan, kemudian kamu kembali mempersekutukan-Nya\".'),
(854, '006:065', '68', 6, 65, 'Katakanlah: \"Dialah yang berkuasa untuk mengirimkan azab kepadamu, dari atas kamu atau dari bawah kakimu atau Dia mencampurkan kamu dalam golongan-golongan (yang saling bertentangan) dan merasakan kepada sebahagian kamu keganasan sebahagian yang lain. Perhatikanlah, betapa Kami mendatangkan tanda-tanda kebesaran Kami silih berganti agar mereka memahami(nya)\".'),
(855, '006:066', '68', 6, 66, 'Dan kaummu mendustakannya (azab) padahal azab itu benar adanya. Katakanlah: \"Aku ini bukanlah orang yang diserahi mengurus urusanmu\".'),
(856, '006:067', '68', 6, 67, 'Untuk setiap berita (yang dibawa oleh rasul-rasul) ada (waktu) terjadinya dan kelak kamu akan mengetahui.'),
(857, '006:068', '68', 6, 68, 'Dan apabila kamu melihat orang-orang memperolok-olokkan ayat-ayat Kami, maka tinggalkanlah mereka sehingga mereka membicarakan pembicaraan yang lain. Dan jika syaitan menjadikan kamu lupa (akan larangan ini), maka janganlah kamu duduk bersama orang-orang yang zalim itu sesudah teringat (akan larangan itu).'),
(858, '006:069', '68', 6, 69, 'Dan tidak ada pertanggungjawaban sedikitpun atas orang-orang yang bertakwa terhadap dosa mereka; akan tetapi (kewajiban mereka ialah) mengingatkan agar mereka bertakwa.'),
(859, '006:070', '68', 6, 70, 'Dan tinggalkan lah orang-orang yang menjadikan agama mereka sebagai main-main dan senda gurau, dan mereka telah ditipu oleh kehidupan dunia. Peringatkanlah (mereka) dengan Al-Quran itu agar masing-masing diri tidak dijerumuskan ke dalam neraka, karena perbuatannya sendiri. Tidak akan ada baginya pelindung dan tidak pula pemberi syafa´at selain daripada Allah. Dan jika ia menebus dengan segala macam tebusanpun, niscaya tidak akan diterima itu daripadanya. Mereka itulah orang-orang yang dijerumuskan ke dalam neraka. Bagi mereka (disediakan) minuman dari air yang sedang mendidih dan azab yang pedih disebabkan kekafiran mereka dahulu.'),
(860, '006:071', '68', 6, 71, 'Katakanlah: \"Apakah kita akan menyeru selain daripada Allah, sesuatu yang tidak dapat mendatangkan kemanfaatan kepada kita dan tidak (pula) mendatangkan kemudharatan kepada kita dan (apakah) kita akan kembali ke belakang, sesudah Allah memberi petunjuk kepada kita, seperti orang yang telah disesatkan oleh syaitan di pesawangan yang menakutkan; dalam keadaan bingung, dia mempunyai kawan-kawan yang memanggilnya kepada jalan yang lurus (dengan mengatakan): \"Marilah ikuti kami\". Katakanlah: \"Sesungguhnya petunjuk Allah itulah (yang sebenarnya) petunjuk; dan kita disuruh agar menyerahkan diri kepada Tuhan semesta alam,'),
(861, '006:072', '68', 6, 72, 'dan agar mendirikan sembahyang serta bertakwa kepada-Nya\". Dan Dialah Tuhan yang kepada-Nya-lah kamu akan dihimpunkan.'),
(862, '006:073', '68', 6, 73, 'Dan Dialah yang menciptakan langit dan bumi dengan benar. Dan benarlah perkataan-Nya di waktu Dia mengatakan: \"Jadilah, lalu terjadilah\", dan di tangan-Nya-lah segala kekuasaan di waktu sangkakala ditiup. Dia mengetahui yang ghaib dan yang nampak. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.'),
(863, '006:074', '68', 6, 74, 'Dan (ingatlah) di waktu Ibrahim berkata kepada bapaknya, Aazar, \"Pantaskah kamu menjadikan berhala-berhala sebagai tuhan-tuhan? Sesungguhnya aku melihat kamu dan kaummu dalam kesesatan yang nyata\".'),
(864, '006:075', '68', 6, 75, 'Dan demikianlah Kami perlihatkan kepada Ibrahim tanda-tanda keagungan (Kami yang terdapat) di langit dan bumi dan (Kami memperlihatkannya) agar dia termasuk orang yang yakin.'),
(865, '006:076', '68', 6, 76, 'Ketika malam telah gelap, dia melihat sebuah bintang (lalu) dia berkata: \"Inilah Tuhanku\", tetapi tatkala bintang itu tenggelam dia berkata: \"Saya tidak suka kepada yang tenggelam\".'),
(866, '006:077', '68', 6, 77, 'Kemudian tatkala dia melihat bulan terbit dia berkata: \"Inilah Tuhanku\". Tetapi setelah bulan itu terbenam, dia berkata: \"Sesungguhnya jika Tuhanku tidak memberi petunjuk kepadaku, pastilah aku termasuk orang yang sesat\".'),
(867, '006:078', '68', 6, 78, 'Kemudian tatkala ia melihat matahari terbit, dia berkata: \"Inilah Tuhanku, ini yang lebih besar\". Maka tatkala matahari itu terbenam, dia berkata: \"Hai kaumku, sesungguhnya aku berlepas diri dari apa yang kamu persekutukan.'),
(868, '006:079', '68', 6, 79, 'Sesungguhnya aku menghadapkan diriku kepada Rabb yang menciptakan langit dan bumi, dengan cenderung kepada agama yang benar, dan aku bukanlah termasuk orang-orang yang mempersekutukan Tuhan.'),
(869, '006:080', '68', 6, 80, 'Dan dia dibantah oleh kaumnya. Dia berkata: \"Apakah kamu hendak membantah tentang Allah, padahal sesungguhnya Allah telah memberi petunjuk kepadaku\". Dan aku tidak takut kepada (malapetaka dari) sembahan-sembahan yang kamu persekutukan dengan Allah, kecuali di kala Tuhanku menghendaki sesuatu (dari malapetaka) itu. Pengetahuan Tuhanku meliputi segala sesuatu. Maka apakah kamu tidak dapat mengambil pelajaran (daripadanya)?\"'),
(870, '006:081', '68', 6, 81, 'Bagaimana aku takut kepada sembahan-sembahan yang kamu persekutukan (dengan Allah), padahal kamu tidak mempersekutukan Allah dengan sembahan-sembahan yang Allah sendiri tidak menurunkan hujjah kepadamu untuk mempersekutukan-Nya. Maka manakah di antara dua golongan itu yang lebih berhak memperoleh keamanan (dari malapetaka), jika kamu mengetahui?'),
(871, '006:082', '68', 6, 82, 'Orang-orang yang beriman dan tidak mencampuradukkan iman mereka dengan kezaliman (syirik), mereka itulah yang mendapat keamanan dan mereka itu adalah orang-orang yang mendapat petunjuk.'),
(872, '006:083', '68', 6, 83, 'Dan itulah hujjah Kami yang Kami berikan kepada Ibrahim untuk menghadapi kaumnya. Kami tinggikan siapa yang Kami kehendaki beberapa derajat. Sesungguhnya Tuhanmu Maha Bijaksana lagi Maha Mengetahui.'),
(873, '006:084', '68', 6, 84, 'Dan Kami telah menganugerahkan Ishak dan Ya´qub kepadanya. Kepada keduanya masing-masing telah Kami beri petunjuk; dan kepada Nuh sebelum itu (juga) telah Kami beri petunjuk, dan kepada sebahagian dari keturunannya (Nuh) yaitu Daud, Sulaiman, Ayyub, Yusuf, Musa dan Harun. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(874, '006:085', '68', 6, 85, 'dan Zakaria, Yahya, Isa dan Ilyas. Semuanya termasuk orang-orang yang shaleh.'),
(875, '006:086', '68', 6, 86, 'dan Ismail, Alyasa´, Yunus dan Luth. Masing-masing Kami lebihkan derajatnya di atas umat (di masanya),'),
(876, '006:087', '68', 6, 87, 'Dan Kami lebihkan (pula) derajat sebahagian dari bapak-bapak mereka, keturunan dan saudara-saudara mereka. Dan Kami telah memilih mereka (untuk menjadi nabi-nabi dan rasul-rasul) dan Kami menunjuki mereka ke jalan yang lurus.'),
(877, '006:088', '68', 6, 88, 'Itulah petunjuk Allah, yang dengannya Dia memberi petunjuk kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya. Seandainya mereka mempersekutukan Allah, niscaya lenyaplah dari mereka amalan yang telah mereka kerjakan.'),
(878, '006:089', '68', 6, 89, 'Mereka itulah orang-orang yang telah Kami berikan kitab, hikmat dan kenabian Jika orang-orang (Quraisy) itu mengingkarinya, maka sesungguhnya Kami akan menyerahkannya kepada kaum yang sekali-kali tidak akan mengingkarinya.'),
(879, '006:090', '68', 6, 90, 'Mereka itulah orang-orang yang telah diberi petunjuk oleh Allah, maka ikutilah petunjuk mereka. Katakanlah: \"Aku tidak meminta upah kepadamu dalam menyampaikan (Al-Quran)\". Al-Quran itu tidak lain hanyalah peringatan untuk seluruh ummat.'),
(880, '006:091', '68', 6, 91, 'Dan mereka tidak menghormati Allah dengan penghormatan yang semestinya, di kala mereka berkata: \"Allah tidak menurunkan sesuatupun kepada manusia\". Katakanlah: \"Siapakah yang menurunkan kitab (Taurat) yang dibawa oleh Musa sebagai cahaya dan petunjuk bagi manusia, kamu jadikan kitab itu lembaran-lembaran kertas yang bercerai-berai, kamu perlihatkan (sebahagiannya) dan kamu sembunyikan sebahagian besarnya, padahal telah diajarkan kepadamu apa yang kamu dan bapak-bapak kamu tidak mengetahui(nya)?\" Katakanlah: \"Allah-lah (yang menurunkannya)\", kemudian (sesudah kamu menyampaikan Al Quran kepada mereka), biarkanlah mereka bermain-main dalam kesesatannya.'),
(881, '006:092', '68', 6, 92, 'Dan ini (Al Quran) adalah kitab yang telah Kami turunkan yang diberkahi; membenarkan kitab-kitab yang (diturunkan) sebelumnya dan agar kamu memberi peringatan kepada (penduduk) Ummul Qura (Mekah) dan orang-orang yang di luar lingkungannya. Orang-orang yang beriman kepada adanya kehidupan akhirat tentu beriman kepadanya (Al Quran) dan mereka selalu memelihara sembahyangnya.'),
(882, '006:093', '68', 6, 93, 'Dan siapakah yang lebih zalim daripada orang yang membuat kedustaan terhadap Allah atau yang berkata: \"Telah diwahyukan kepada saya\", padahal tidak ada diwahyukan sesuatupun kepadanya, dan orang yang berkata: \"Saya akan menurunkan seperti apa yang diturunkan Allah\". Alangkah dahsyatnya sekiranya kamu melihat di waktu orang-orang yang zalim berada dalam tekanan sakratul maut, sedang para malaikat memukul dengan tangannya, (sambil berkata): \"Keluarkanlah nyawamu\" Di hari ini kamu dibalas dengan siksa yang sangat menghinakan, karena kamu selalu mengatakan terhadap Allah (perkataan) yang tidak benar dan (karena) kamu selalu menyombongkan diri terhadap ayat-ayat-Nya.'),
(883, '006:094', '68', 6, 94, 'Dan sesungguhnya kamu datang kepada Kami sendiri-sendiri sebagaimana kamu Kami ciptakan pada mulanya, dan kamu tinggalkan di belakangmu (di dunia) apa yang telah Kami karuniakan kepadamu; dan Kami tiada melihat besertamu pemberi syafa´at yang kamu anggap bahwa mereka itu sekutu-sekutu Tuhan di antara kamu. Sungguh telah terputuslah (pertalian) antara kamu dan telah lenyap daripada kamu apa yang dahulu kamu anggap (sebagai sekutu Allah).'),
(884, '006:095', '68', 6, 95, 'Sesungguhnya Allah menumbuhkan butir tumbuh-tumbuhan dan biji buah-buahan. Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup. (Yang memiliki sifat-sifat) demikian ialah Allah, maka mengapa kamu masih berpaling?'),
(885, '006:096', '68', 6, 96, 'Dia menyingsingkan pagi dan menjadikan malam untuk beristirahat, dan (menjadikan) matahari dan bulan untuk perhitungan. Itulah ketentuan Allah Yang Maha Perkasa lagi Maha Mengetahui.'),
(886, '006:097', '68', 6, 97, 'Dan Dialah yang menjadikan bintang-bintang bagimu, agar kamu menjadikannya petunjuk dalam kegelapan di darat dan di laut. Sesungguhnya Kami telah menjelaskan tanda-tanda kebesaran (Kami) kepada orang-orang yang mengetahui.'),
(887, '006:098', '68', 6, 98, 'Dan Dialah yang menciptakan kamu dari seorang diri, maka (bagimu) ada tempat tetap dan tempat simpanan. Sesungguhnya telah Kami jelaskan tanda-tanda kebesaran Kami kepada orang-orang yang mengetahui.'),
(888, '006:099', '68', 6, 99, 'Dan Dialah yang menurunkan air hujan dari langit, lalu Kami tumbuhkan dengan air itu segala macam tumbuh-tumbuhan maka Kami keluarkan dari tumbuh-tumbuhan itu tanaman yang menghijau. Kami keluarkan dari tanaman yang menghijau itu butir yang banyak; dan dari mayang korma mengurai tangkai-tangkai yang menjulai, dan kebun-kebun anggur, dan (Kami keluarkan pula) zaitun dan delima yang serupa dan yang tidak serupa. Perhatikanlah buahnya di waktu pohonnya berbuah dan (perhatikan pulalah) kematangannya. Sesungguhnya pada yang demikian itu ada tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.'),
(889, '006:100', '68', 6, 100, 'Dan mereka (orang-orang musyrik) menjadikan jin itu sekutu bagi Allah, padahal Allah-lah yang menciptakan jin-jin itu, dan mereka membohong (dengan mengatakan): \"Bahwasanya Allah mempunyai anak laki-laki dan perempuan\", tanpa (berdasar) ilmu pengetahuan. Maha Suci Allah dan Maha Tinggi dari sifat-sifat yang mereka berikan.'),
(890, '006:101', '68', 6, 101, 'Dia Pencipta langit dan bumi. Bagaimana Dia mempunyai anak padahal Dia tidak mempunyai isteri. Dia menciptakan segala sesuatu; dan Dia mengetahui segala sesuatu.'),
(891, '006:102', '68', 6, 102, '(Yang memiliki sifat-sifat yang) demikian itu ialah Allah Tuhan kamu; tidak ada Tuhan selain Dia; Pencipta segala sesuatu, maka sembahlah Dia; dan Dia adalah Pemelihara segala sesuatu.'),
(892, '006:103', '68', 6, 103, 'Dia tidak dapat dicapai oleh penglihatan mata, sedang Dia dapat melihat segala yang kelihatan; dan Dialah Yang Maha Halus lagi Maha Mengetahui.'),
(893, '006:104', '68', 6, 104, 'Sesungguhnya telah datang dari Tuhanmu bukti-bukti yang terang; maka barangsiapa melihat (kebenaran itu), maka (manfaatnya) bagi dirinya sendiri; dan barangsiapa buta (tidak melihat kebenaran itu), maka kemudharatannya kembali kepadanya. Dan aku (Muhammad) sekali-kali bukanlah pemelihara(mu).'),
(894, '006:105', '68', 6, 105, 'Demikianlah Kami mengulang-ulangi ayat-ayat Kami supaya (orang-orang yang beriman mendapat petunjuk) dan supaya orang-orang musyrik mengatakan: \"Kamu telah mempelajari ayat-ayat itu (dari Ahli Kitab)\", dan supaya Kami menjelaskan Al Quran itu kepada orang-orang yang mengetahui.'),
(895, '006:106', '68', 6, 106, 'Ikutilah apa yang telah diwahyukan kepadamu dari Tuhanmu; tidak ada Tuhan selain Dia; dan berpalinglah dari orang-orang musyrik.'),
(896, '006:107', '68', 6, 107, 'Dan kalau Allah menghendaki, niscaya mereka tidak memperkutukan(Nya). Dan Kami tidak menjadikan kamu pemelihara bagi mereka; dan kamu sekali-kali bukanlah pemelihara bagi mereka.'),
(897, '006:108', '68', 6, 108, 'Dan janganlah kamu memaki sembahan-sembahan yang mereka sembah selain Allah, karena mereka nanti akan memaki Allah dengan melampaui batas tanpa pengetahuan. Demikianlah Kami jadikan setiap umat menganggap baik pekerjaan mereka. Kemudian kepada Tuhan merekalah kembali mereka, lalu Dia memberitakan kepada mereka apa yang dahulu mereka kerjakan.'),
(898, '006:109', '68', 6, 109, 'Mereka bersumpah dengan nama Allah dengan segala kesungguhan, bahwa sungguh jika datang kepada mereka sesuatu mu jizat, pastilah mereka beriman kepada-Nya. Katakanlah: \"Sesungguhnya mukjizat-mukjizat itu hanya berada di sisi Allah\". Dan apakah yang memberitahukan kepadamu bahwa apabila mukjizat datang mereka tidak akan beriman.'),
(899, '006:110', '68', 6, 110, 'Dan (begitu pula) Kami memalingkan hati dan penglihatan mereka seperti mereka belum pernah beriman kepadanya (Al Quran) pada permulaannya, dan Kami biarkan mereka bergelimang dalam kesesatannya yang sangat.'),
(900, '006:111', '68', 6, 111, 'Kalau sekiranya Kami turunkan malaikat kepada mereka, dan orang-orang yang telah mati berbicara dengan mereka dan Kami kumpulkan (pula) segala sesuatu ke hadapan mereka, niscaya mereka tidak (juga) akan beriman, kecuali jika Allah menghendaki, tetapi kebanyakan mereka tidak mengetahui.'),
(901, '006:112', '68', 6, 112, 'Dan demikianlah Kami jadikan bagi tiap-tiap nabi itu musuh, yaitu syaitan-syaitan (dari jenis) manusia dan (dan jenis) jin, sebahagian mereka membisikkan kepada sebahagian yang lain perkataan-perkataan yang indah-indah untuk menipu (manusia). Jikalau Tuhanmu menghendaki, niscaya mereka tidak mengerjakannya, maka tinggalkanlah mereka dan apa yang mereka ada-adakan.'),
(902, '006:113', '68', 6, 113, 'Dan (juga) agar hati kecil orang-orang yang tidak beriman kepada kehidupan akhirat cenderung kepada bisikan itu, mereka merasa senang kepadanya dan supaya mereka mengerjakan apa yang mereka (syaitan) kerjakan.'),
(903, '006:114', '68', 6, 114, 'Maka patutkah aku mencari hakim selain daripada Allah, padahal Dialah yang telah menurunkan kitab (Al Quran) kepadamu dengan terperinci? Orang-orang yang telah Kami datangkan kitab kepada mereka, mereka mengetahui bahwa Al Quran itu diturunkan dari Tuhanmu dengan sebenarnya. Maka janganlah kamu sekali-kali termasuk orang yang ragu-ragu.'),
(904, '006:115', '68', 6, 115, 'Telah sempurnalah kalimat Tuhanmu (Al-Quran) sebagai kalimat yang benar dan adil. Tidak ada yang dapat merubah rubah kalimat-kalimat-Nya dan Dialah yang Maha Mendenyar lagi Maha Mengetahui.'),
(905, '006:116', '68', 6, 116, 'Dan jika kamu menuruti kebanyakan orang-orang yang di muka bumi ini, niscaya mereka akan menyesatkanmu dari jalan Allah. Mereka tidak lain hanyalah mengikuti persangkaan belaka, dan mereka tidak lain hanyalah berdusta (terhadap Allah).'),
(906, '006:117', '68', 6, 117, 'Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui tentang orang yang tersesat dari jalan-Nya dan Dia lebih mengetahui tentang orang orang yang mendapat petunjuk.'),
(907, '006:118', '68', 6, 118, 'Maka makanlah binatang-binatang (yang halal) yang disebut nama Allah ketika menyembelihnya, jika kamu beriman kepada ayat-ayat-Nya.'),
(908, '006:119', '68', 6, 119, 'Mengapa kamu tidak mau memakan (binatang-binatang yang halal) yang disebut nama Allah ketika menyembelihnya, padahal sesungguhnya Allah telah menjelaskan kepada kamu apa yang diharamkan-Nya atasmu, kecuali apa yang terpaksa kamu memakannya. Dan sesungguhnya kebanyakan (dari manusia) benar benar hendak menyesatkan (orang lain) dengan hawa nafsu mereka tanpa pengetahuan. Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui orang-orang yang melampaui batas.'),
(909, '006:120', '68', 6, 120, 'Dan tinggalkanlah dosa yang nampak dan yang tersembunyi. Sesungguhnya orang yang mengerjakan dosa, kelak akan diberi pembalasan (pada hari kiamat), disebabkan apa yang mereka telah kerjakan.'),
(910, '006:121', '68', 6, 121, 'Dan janganlah kamu memakan binatang-binatang yang tidak disebut nama Allah ketika menyembelihnya. Sesungguhnya perbuatan yang semacam itu adalah suatu kefasikan. Sesungguhnya syaitan itu membisikkan kepada kawan-kawannya agar mereka membantah kamu; dan jika kamu menuruti mereka, sesungguhnya kamu tentulah menjadi orang-orang yang musyrik.'),
(911, '006:122', '68', 6, 122, 'Dan apakah orang yang sudah mati kemudian dia Kami hidupkan dan Kami berikan kepadanya cahaya yang terang, yang dengan cahaya itu dia dapat berjalan di tengah-tengah masyarakat manusia, serupa dengan orang yang keadaannya berada dalam gelap gulita yang sekali-kali tidak dapat keluar dari padanya? Demikianlah Kami jadikan orang yang kafir itu memandang baik apa yang telah mereka kerjakan.'),
(912, '006:123', '68', 6, 123, 'Dan demikianlah Kami adakan pada tiap-tiap negeri penjahat-penjahat yang terbesar agar mereka melakukan tipu daya dalam negeri itu. Dan mereka tidak memperdayakan melainkan dirinya sendiri, sedang mereka tidak menyadarinya.'),
(913, '006:124', '68', 6, 124, 'Apabila datang sesuatu ayat kepada mereka, mereka berkata: \"Kami tidak akan beriman sehingga diberikan kepada kami yang serupa dengan apa yang telah diberikan kepada utusan-utusan Allah\". Allah lebih mengetahui di mana Dia menempatkan tugas kerasulan. Orang-orang yang berdosa, nanti akan ditimpa kehinaan di sisi Allah dan siksa yang keras disebabkan mereka selalu membuat tipu daya.'),
(914, '006:125', '68', 6, 125, 'Barangsiapa yang Allah menghendaki akan memberikan kepadanya petunjuk, niscaya Dia melapangkan dadanya untuk (memeluk agama) Islam. Dan barangsiapa yang dikehendaki Allah kesesatannya, niscaya Allah menjadikan dadanya sesak lagi sempit, seolah-olah ia sedang mendaki langit. Begitulah Allah menimpakan siksa kepada orang-orang yang tidak beriman.'),
(915, '006:126', '68', 6, 126, 'Dan inilah jalan Tuhanmu; (jalan) yang lurus. Sesungguhnya Kami telah menjelaskan ayat-ayat (Kami) kepada orang-orang yang mengambil pelajaran.'),
(916, '006:127', '68', 6, 127, 'Bagi mereka (disediakan) darussalam (surga) pada sisi Tuhannya dan Dialah Pelindung mereka disebabkan amal-amal saleh yang selalu mereka kerjakan.'),
(917, '006:128', '68', 6, 128, 'Dan (ingatlah) hari diwaktu Allah menghimpunkan mereka semuanya (dan Allah berfirman): \"Hai golongan jin, sesungguhnya kamu telah banyak menyesatkan manusia\", lalu berkatalah kawan-kawan meraka dari golongan manusia: \"Ya Tuhan kami, sesungguhnya sebahagian daripada kami telah dapat kesenangan dari sebahagian (yang lain) dan kami telah sampai kepada waktu yang telah Engkau tentukan bagi kami\". Allah berfirman: \"Neraka itulah tempat diam kamu, sedang kamu kekal di dalamnya, kecuali kalau Allah menghendaki (yang lain)\". Sesungguhnya Tuhanmu Maha Bijaksana lagi Maha Mengetahui.'),
(918, '006:129', '68', 6, 129, 'Dan demikianlah Kami jadikan sebahagian orang-orang yang zalim itu menjadi teman bagi sebahagian yang lain disebabkan apa yang mereka usahakan.'),
(919, '006:130', '68', 6, 130, 'Hai golongan jin dan manusia, apakah belum datang kepadamu rasul-rasul dari golongan kamu sendiri, yang menyampaikan kepadamu ayat-ayat-Ku dan memberi peringatan kepadamu terhadap pertemuanmu dengan hari ini? Mereka berkata: \"Kami menjadi saksi atas diri kami sendiri\", kehidupan dunia telah menipu mereka, dan mereka menjadi saksi atas diri mereka sendiri, bahwa mereka adalah orang-orang yang kafir.'),
(920, '006:131', '68', 6, 131, 'Yang demikian itu adalah karena Tuhanmu tidaklah membinasakan kota-kota secara aniaya, sedang penduduknya dalam keadaan lengah.'),
(921, '006:132', '68', 6, 132, 'Dan masing-masing orang memperoleh derajat-derajat (seimbang) dengan apa yang dikerjakannya. Dan Tuhanmu tidak lengah dari apa yang mereka kerjakan.'),
(922, '006:133', '68', 6, 133, 'Dan Tuhanmu Maha Kaya lagi mempunyai rahmat. Jika Dia menghendaki niscaya Dia memusnahkan kamu dan menggantimu dengan siapa yang dikehendaki-Nya setelah kamu (musnah), sebagaimana Dia telah menjadikan kamu dari keturunan orang-orang lain.'),
(923, '006:134', '68', 6, 134, 'Sesungguhnya apa yang dijanjikan kepadamu pasti datang, dan kamu sekali-kali tidak sanggup menolaknya.'),
(924, '006:135', '68', 6, 135, 'Katakanlah: \"Hai kaumku, berbuatlah sepenuh kemampuanmu, sesungguhnya akupun berbuat (pula). Kelak kamu akan mengetahui, siapakah (di antara kita) yang akan memperoleh hasil yang baik di dunia ini. Sesungguhnya orang-orang yang zalim itu tidak akan mendapatkan keberuntungan.'),
(925, '006:136', '68', 6, 136, 'Dan mereka memperuntukkan bagi Allah satu bagian dari tanaman dan ternak yang telah diciptakan Allah, lalu mereka berkata sesuai dengan persangkaan mereka: \"Ini untuk Allah dan ini untuk berhala-berhala kami\". Maka saji-sajian yang diperuntukkan bagi berhala-berhala mereka tidak sampai kepada Allah; dan saji-sajian yang diperuntukkan bagi Allah, maka sajian itu sampai kepada berhala-berhala mereka. Amat buruklah ketetapan mereka itu.'),
(926, '006:137', '68', 6, 137, 'Dan demikianlah pemimpin-pemimpin mereka telah menjadikan kebanyakan dari orang-orang musyrik itu memandang baik membunuh anak-anak mereka untuk membinasakan mereka dan untuk mengaburkan bagi mereka agama-Nya. Dan kalau Allah menghendaki, niscaya mereka tidak mengerjakannya, maka tinggallah mereka dan apa yang mereka ada-adakan.'),
(927, '006:138', '68', 6, 138, 'Dan mereka mengatakan: \"Inilah hewan ternak dan tanaman yang dilarang; tidak boleh memakannya, kecuali orang yang kami kehendaki\", menurut anggapan mereka, dan ada binatang ternak yang diharamkan menungganginya dan ada binatang ternak yang mereka tidak menyebut nama Allah waktu menyembelihnya, semata-mata membuat-buat kedustaan terhadap Allah. Kelak Allah akan membalas mereka terhadap apa yang selalu mereka ada-adakan.'),
(928, '006:139', '68', 6, 139, 'Dan mereka mengatakan: \"Apa yang ada dalam perut binatang ternak ini adalah khusus untuk pria kami dan diharamkan atas wanita kami,\" dan jika yang dalam perut itu dilahirkan mati, maka pria dan wanita sama-sama boleh memakannya. Kelak Allah akan membalas mereka terhadap ketetapan mereka. Sesungguhnya Allah Maha Bijaksana lagi Maha Mengetahui.'),
(929, '006:140', '68', 6, 140, 'Sesungguhnya rugilah orang yang membunuh anak-anak mereka, karena kebodohan lagi tidak mengetahui dan mereka mengharamkan apa yang Allah telah rezeki-kan pada mereka dengan semata-mata mengada-adakan terhadap Allah. Sesungguhnya mereka telah sesat dan tidaklah mereka mendapat petunjuk.'),
(930, '006:141', '68', 6, 141, 'Dan Dialah yang menjadikan kebun-kebun yang berjunjung dan yang tidak berjunjung, pohon korma, tanam-tanaman yang bermacam-macam buahnya, zaitun dan delima yang serupa (bentuk dan warnanya) dan tidak sama (rasanya). Makanlah dari buahnya (yang bermacam-macam itu) bila dia berbuah, dan tunaikanlah haknya di hari memetik hasilnya (dengan disedekahkan kepada fakir miskin); dan janganlah kamu berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang yang berlebih-lebihan.'),
(931, '006:142', '68', 6, 142, 'Dan di antara hewan ternak itu ada yang dijadikan untuk pengangkutan dan ada yang untuk disembelih. Makanlah dari rezeki yang telah diberikan Allah kepadamu, dan janganlah kamu mengikuti langkah-langkah syaitan. Sesungguhnya syaitan itu musuh yang nyata bagimu.'),
(932, '006:143', '68', 6, 143, '(yaitu) delapan binatang yang berpasangan, sepasang domba, sepasang dari kambing. Katakanlah: \"Apakah dua yang jantan yang diharamkan Allah ataukah dua yang betina, ataukah yang ada dalam kandungan dua betinanya?\" Terangkanlah kepadaku dengan berdasar pengetahuan jika kamu memang orang-orang yang benar,'),
(933, '006:144', '68', 6, 144, 'dan sepasang dari unta dan sepasang dari lembu. Katakanlah: \"Apakah dua yang jantan yang diharamkan ataukah dua yang betina, ataukah yang ada dalam kandungan dua betinanya? Apakah kamu menyaksikan di waktu Allah menetapkan ini bagimu? Maka siapakah yang lebih zalim daripada orang-orang yang membuat-buat dusta terhadap Allah untuk menyesatkan manusia tanpa pengetahuan?\" Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
(934, '006:145', '68', 6, 145, 'Katakanlah: \"Tiadalah aku peroleh dalam wahyu yang diwahyukan kepadaku, sesuatu yang diharamkan bagi orang yang hendak memakannya, kecuali kalau makanan itu bangkai, atau darah yang mengalir atau daging babi -- karena sesungguhnya semua itu kotor -- atau binatang yang disembelih atas nama selain Allah. Barangsiapa yang dalam keadaan terpaksa, sedang dia tidak menginginkannya dan tidak (pula) melampaui batas, maka sesungguhnya Tuhanmu Maha Pengampun lagi Maha Penyayang\".'),
(935, '006:146', '68', 6, 146, 'Dan kepada orang-orang Yahudi, Kami haramkan segala binatang yang berkuku dan dari sapi dan domba, Kami haramkan atas mereka lemak dari kedua binatang itu, selain lemak yang melekat di punggung keduanya atau yang di perut besar dan usus atau yang bercampur dengan tulang. Demikianlah Kami hukum mereka disebabkan kedurhakaan mereka; dan sesungguhnya Kami adalah Maha Benar.'),
(936, '006:147', '68', 6, 147, 'Maka jika mereka mendustakan kamu, katakanlah: \"Tuhanmu mempunyai rahmat yang luas; dan siksa-Nya tidak dapat ditolak dari kaum yang berdosa\".'),
(937, '006:148', '68', 6, 148, 'Orang-orang yang mempersekutukan Tuhan, akan mengatakan: \"Jika Allah menghendaki, niscaya kami dan bapak-bapak kami tidak mempersekutukan-Nya dan tidak (pula) kami mengharamkan barang sesuatu apapun\". Demikian pulalah orang-orang sebelum mereka telah mendustakan (para rasul) sampai mereka merasakan siksaan Kami. Katakanlah: \"Adakah kamu mempunyai sesuatu pengetahuan sehingga dapat kamu mengemukakannya kepada Kami?\" Kamu tidak mengikuti kecuali persangkaan belaka, dan kamu tidak lain hanyalah berdusta.'),
(938, '006:149', '68', 6, 149, 'Katakanlah: \"Allah mempunyai hujjah yang jelas lagi kuat; maka jika Dia menghendaki, pasti Dia memberi petunjuk kepada kamu semuanya\".');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(939, '006:150', '68', 6, 150, 'Katakanlah: \"Bawalah kemari saksi-saksi kamu yang dapat mempersaksikan bahwasanya Allah telah mengharamkan (makanan yang kamu) haramkan ini\" Jika mereka mempersaksikan, maka janganlah kamu ikut pula menjadi saksi bersama mereka; dan janganlah kamu mengikuti hawa nafsu orang-orang yang mendustakan ayat-ayat Kami, dan orang-orang yang tidak beriman kepada kehidupan akhirat, sedang mereka mempersekutukan Tuhan mereka.'),
(940, '006:151', '68', 6, 151, 'Katakanlah: \"Marilah kubacakan apa yang diharamkan atas kamu oleh Tuhanmu yaitu: janganlah kamu mempersekutukan sesuatu dengan Dia, berbuat baiklah terhadap kedua orang ibu bapa, dan janganlah kamu membunuh anak-anak kamu karena takut kemiskinan, Kami akan memberi rezeki kepadamu dan kepada mereka, dan janganlah kamu mendekati perbuatan-perbuatan yang keji, baik yang nampak di antaranya maupun yang tersembunyi, dan janganlah kamu membunuh jiwa yang diharamkan Allah (membunuhnya) melainkan dengan sesuatu (sebab) yang benar\". Demikian itu yang diperintahkan kepadamu supaya kamu memahami(nya).'),
(941, '006:152', '68', 6, 152, 'Dan janganlah kamu dekati harta anak yatim, kecuali dengan cara yang lebih bermanfaat, hingga sampai ia dewasa. Dan sempurnakanlah takaran dan timbangan dengan adil. Kami tidak memikulkan beban kepada sesorang melainkan sekedar kesanggupannya. Dan apabila kamu berkata, maka hendaklah kamu berlaku adil, kendatipun ia adalah kerabat(mu), dan penuhilah janji Allah. Yang demikian itu diperintahkan Allah kepadamu agar kamu ingat.'),
(942, '006:153', '68', 6, 153, 'dan bahwa (yang Kami perintahkan ini) adalah jalan-Ku yang lurus, maka ikutilah dia, dan janganlah kamu mengikuti jalan-jalan (yang lain), karena jalan-jalan itu mencerai beraikan kamu dari jalan-Nya. Yang demikian itu diperintahkan Allah agar kamu bertakwa.'),
(943, '006:154', '68', 6, 154, 'Kemudian Kami telah memberikan Al Kitab (Taurat) kepada Musa untuk menyempurnakan (nikmat Kami) kepada orang yang berbuat kebaikan, dan untuk menjelaskan segala sesuatu dan sebagai petunjuk dan rahmat, agar mereka beriman (bahwa) mereka akan menemui Tuhan mereka.'),
(944, '006:155', '68', 6, 155, 'Dan Al-Quran itu adalah kitab yang Kami turunkan yang diberkati, maka ikutilah dia dan bertakwalah agar kamu diberi rahmat.'),
(945, '006:156', '68', 6, 156, '(Kami turunkan al-Quran itu) agar kamu (tidak) mengatakan: \"Bahwa kitab itu hanya diturunkan kepada dua golongan saja sebelum kami, dan sesungguhnya kami tidak memperhatikan apa yang mereka baca.'),
(946, '006:157', '68', 6, 157, 'Atau agar kamu (tidak) mengatakan: \"Sesungguhnya jikalau kitab ini diturunkan kepada kami, tentulah kami lebih mendapat petunjuk dari mereka\". Sesungguhnya telah datang kepada kamu keterangan yang nyata dari Tuhanmu, petunjuk dan rahmat. Maka siapakah yang lebih zalim daripada orang yang mendustakan ayat-ayat Allah dan berpaling daripadanya? Kelak Kami akan memberi balasan kepada orang-orang yang berpaling dari ayat-ayat Kami dengan siksa yang buruk, disebabkan mereka selalu berpaling.'),
(947, '006:158', '68', 6, 158, 'Yang mereka nanti-nanti tidak lain hanyalah kedatangan malaikat kepada mereka (untuk mencabut nyawa mereka) atau kedatangan (siksa) Tuhanmu atau kedatangan beberapa ayat Tuhanmu. Pada hari datangnya ayat dari Tuhanmu, tidaklah bermanfaat lagi iman seseorang kepada dirinya sendiri yang belum beriman sebelum itu, atau dia (belum) mengusahakan kebaikan dalam masa imannya. Katakanlah: \"Tunggulah olehmu sesungguhnya Kamipun menunggu (pula)\".'),
(948, '006:159', '68', 6, 159, 'Sesungguhnya orang-orang yang memecah belah agama-Nya dan mereka menjadi bergolongan, tidak ada sedikitpun tanggung jawabmu kepada mereka. Sesungguhnya urusan mereka hanyalah terserah kepada Allah, kemudian Allah akan memberitahukan kepada mereka apa yang telah mereka perbuat.'),
(949, '006:160', '68', 6, 160, 'Barangsiapa membawa amal yang baik, maka baginya (pahala) sepuluh kali lipat amalnya; dan barangsiapa yang membawa perbuatan jahat maka dia tidak diberi pembalasan melainkan seimbang dengan kejahatannya, sedang mereka sedikitpun tidak dianiaya (dirugikan).'),
(950, '006:161', '68', 6, 161, 'Katakanlah: \"Sesungguhnya aku telah ditunjuki oleh Tuhanku kepada jalan yang lurus, (yaitu) agama yang benar, agama Ibrahim yang lurus, dan Ibrahim itu bukanlah termasuk orang-orang musyrik\".'),
(951, '006:162', '68', 6, 162, 'Katakanlah: sesungguhnya sembahyangku, ibadatku, hidupku dan matiku hanyalah untuk Allah, Tuhan semesta alam.'),
(952, '006:163', '68', 6, 163, 'Tiada sekutu bagi-Nya; dan demikian itulah yang diperintahkan kepadaku dan aku adalah orang yang pertama-tama menyerahkan diri (kepada Allah)\".'),
(953, '006:164', '68', 6, 164, 'Katakanlah: \"Apakah aku akan mencari Tuhan selain Allah, padahal Dia adalah Tuhan bagi segala sesuatu. Dan tidaklah seorang membuat dosa melainkan kemudharatannya kembali kepada dirinya sendiri; dan seorang yang berdosa tidak akan memikul dosa orang lain. Kemudian kepada Tuhanmulah kamu kembali, dan akan diberitakan-Nya kepadamu apa yang kamu perselisihkan\".'),
(954, '006:165', '68', 6, 165, 'Dan Dialah yang menjadikan kamu penguasa-penguasa di bumi dan Dia meninggikan sebahagian kamu atas sebahagian (yang lain) beberapa derajat, untuk mengujimu tentang apa yang diberikan-Nya kepadamu. Sesungguhnya Tuhanmu amat cepat siksaan-Nya dan sesungguhnya Dia Maha Pengampun lagi Maha Penyayang.'),
(955, '007:001', '68', 7, 1, 'Alif laam mim shaad.'),
(956, '007:002', '68', 7, 2, 'Ini adalah sebuah kitab yang diturunkan kepadamu, maka janganlah ada kesempitan di dalam dadamu karenanya, supaya kamu memberi peringatan dengan kitab itu (kepada orang kafir), dan menjadi pelajaran bagi orang-orang yang beriman.'),
(957, '007:003', '68', 7, 3, 'Ikutilah apa yang diturunkan kepadamu dari Tuhanmu dan janganlah kamu mengikuti pemimpin-pemimpin selain-Nya. Amat sedikitlah kamu mengambil pelajaran (daripadanya).'),
(958, '007:004', '68', 7, 4, 'Betapa banyaknya negeri yang telah Kami binasakan, maka datanglah siksaan Kami (menimpa penduduk)nya di waktu mereka berada di malam hari, atau di waktu mereka beristirahat di tengah hari.'),
(959, '007:005', '68', 7, 5, 'Maka tidak adalah keluhan mereka di waktu datang kepada mereka siksaan Kami, kecuali mengatakan: \"Sesungguhnya kami adalah orang-orang yang zalim\".'),
(960, '007:006', '68', 7, 6, 'Maka sesungguhnya Kami akan menanyai umat-umat yang telah diutus rasul-rasul kepada mereka dan sesungguhnya Kami akan menanyai (pula) rasul-rasul (Kami),'),
(961, '007:007', '68', 7, 7, 'maka sesungguhnya akan Kami kabarkan kepada mereka (apa-apa yang telah mereka perbuat), sedang (Kami) mengetahui (keadaan mereka), dan Kami sekali-kali tidak jauh (dari mereka).'),
(962, '007:008', '68', 7, 8, 'Timbangan pada hari itu ialah kebenaran (keadilan), maka barangsiapa berat timbangan kebaikannya, maka mereka itulah orang-orang yang beruntung.'),
(963, '007:009', '68', 7, 9, 'Dan siapa yang ringan timbangan kebaikannya, maka itulah orang-orang yang merugikan dirinya sendiri, disebabkan mereka selalu mengingkari ayat-ayat Kami.'),
(964, '007:010', '68', 7, 10, 'Sesungguhnya Kami telah menempatkan kamu sekalian di muka bumi dan Kami adakan bagimu di muka bumi (sumber) penghidupan. Amat sedikitlah kamu bersyukur.'),
(965, '007:011', '68', 7, 11, 'Sesungguhnya Kami telah menciptakan kamu (Adam), lalu Kami bentuk tubuhmu, kemudian Kami katakan kepada para malaikat: \"Bersujudlah kamu kepada Adam\", maka merekapun bersujud kecuali iblis. Dia tidak termasuk mereka yang bersujud.'),
(966, '007:012', '68', 7, 12, 'Allah berfirman: \"Apakah yang menghalangimu untuk bersujud (kepada Adam) di waktu Aku menyuruhmu?\" Menjawab iblis \"Saya lebih baik daripadanya: Engkau ciptakan saya dari api sedang dia Engkau ciptakan dari tanah\".'),
(967, '007:013', '68', 7, 13, 'Allah berfirman: \"Turunlah kamu dari surga itu; karena kamu sepatutnya menyombongkan diri di dalamnya, maka keluarlah, sesungguhnya kamu termasuk orang-orang yang hina\".'),
(968, '007:014', '68', 7, 14, 'Iblis menjawab: \"Beri tangguhlah saya sampai waktu mereka dibangkitkan\".'),
(969, '007:015', '68', 7, 15, 'Allah berfirman: \"Sesungguhnya kamu termasuk mereka yang diberi tangguh\".'),
(970, '007:016', '68', 7, 16, 'Iblis menjawab: \"Karena Engkau telah menghukum saya tersesat, saya benar-benar akan (menghalang-halangi) mereka dari jalan Engkau yang lurus,'),
(971, '007:017', '68', 7, 17, 'kemudian saya akan mendatangi mereka dari muka dan dari belakang mereka, dari kanan dan dari kiri mereka. Dan Engkau tidak akan mendapati kebanyakan mereka bersyukur (taat).'),
(972, '007:018', '68', 7, 18, 'Allah berfirman: \"Keluarlah kamu dari surga itu sebagai orang terhina lagi terusir. Sesungguhnya barangsiapa di antara mereka mengikuti kamu, benar-benar Aku akan mengisi neraka Jahannam dengan kamu semuanya\".'),
(973, '007:019', '68', 7, 19, '(Dan Allah berfirman): \"Hai Adam bertempat tinggallah kamu dan isterimu di surga serta makanlah olehmu berdua (buah-buahan) di mana saja yang kamu sukai, dan janganlah kamu berdua mendekati pohon ini, lalu menjadilah kamu berdua termasuk orang-orang yang zalim\".'),
(974, '007:020', '68', 7, 20, 'Maka syaitan membisikkan pikiran jahat kepada keduanya untuk menampakkan kepada keduanya apa yang tertutup dari mereka yaitu auratnya dan syaitan berkata: \"Tuhan kamu tidak melarangmu dan mendekati pohon ini, melainkan supaya kamu berdua tidak menjadi malaikat atau tidak menjadi orang-orang yang kekal (dalam surga)\".'),
(975, '007:021', '68', 7, 21, 'Dan dia (syaitan) bersumpah kepada keduanya. \"Sesungguhnya saya adalah termasuk orang yang memberi nasehat kepada kamu berdua\",'),
(976, '007:022', '68', 7, 22, 'maka syaitan membujuk keduanya (untuk memakan buah itu) dengan tipu daya. Tatkala keduanya telah merasai buah kayu itu, nampaklah bagi keduanya aurat-auratnya, dan mulailah keduanya menutupinya dengan daun-daun surga. Kemudian Tuhan mereka menyeru mereka: \"Bukankah Aku telah melarang kamu berdua dari pohon kayu itu dan Aku katakan kepadamu: \"Sesungguhnya syaitan itu adalah musuh yang nyata bagi kamu berdua?\"'),
(977, '007:023', '68', 7, 23, 'Keduanya berkata: \"Ya Tuhan kami, kami telah menganiaya diri kami sendiri, dan jika Engkau tidak mengampuni kami dan memberi rahmat kepada kami, niscaya pastilah kami termasuk orang-orang yang merugi.'),
(978, '007:024', '68', 7, 24, 'Allah berfirman: \"Turunlah kamu sekalian, sebahagian kamu menjadi musuh bagi sebahagian yang lain. Dan kamu mempunyai tempat kediaman dan kesenangan (tempat mencari kehidupan) di muka bumi sampai waktu yang telah ditentukan\".'),
(979, '007:025', '68', 7, 25, 'Allah berfirman: \"Di bumi itu kamu hidup dan di bumi itu kamu mati, dan dari bumi itu (pula) kamu akan dibangkitkan.'),
(980, '007:026', '68', 7, 26, 'Hai anak Adam, sesungguhnya Kami telah menurunkan kepadamu pakaian untuk menutup auratmu dan pakaian indah untuk perhiasan. Dan pakaian takwa itulah yang paling baik. Yang demikian itu adalah sebahagian dari tanda-tanda kekuasaan Allah, mudah-mudahan mereka selalu ingat.'),
(981, '007:027', '68', 7, 27, 'Hai anak Adam, janganlah sekali-kali kamu dapat ditipu oleh syaitan sebagaimana ia telah mengeluarkan kedua ibu bapamu dari surga, ia menanggalkan dari keduanya pakaiannya untuk memperlihatkan kepada keduanya auratnya. Sesungguhnya ia dan pengikut-pengikutnya melihat kamu dan suatu tempat yang kamu tidak bisa melihat mereka. Sesungguhnya Kami telah menjadikan syaitan-syaitan itu pemimpin-pemimpim bagi orang-orang yang tidak beriman.'),
(982, '007:028', '68', 7, 28, 'Dan apabila mereka melakukan perbuatan keji, mereka berkata: \"Kami mendapati nenek moyang kami mengerjakan yang demikian itu, dan Allah menyuruh kami mengerjakannya\". Katakanlah: \"Sesungguhnya Allah tidak menyuruh (mengerjakan) perbuatan yang keji\". Mengapa kamu mengada-adakan terhadap Allah apa yang tidak kamu ketahui?'),
(983, '007:029', '68', 7, 29, 'Katakanlah: \"Tuhanku menyuruh menjalankan keadilan\". Dan (katakanlah): \"Luruskanlah muka (diri)mu di setiap sembahyang dan sembahlah Allah dengan mengikhlaskan ketaatanmu kepada-Nya. Sebagaimana Dia telah menciptakan kamu pada permulaan (demikian pulalah kamu akan kembali kepada-Nya)\".'),
(984, '007:030', '68', 7, 30, 'Sebahagian diberi-Nya petunjuk dan sebahagian lagi telah pasti kesesatan bagi mereka. Sesungguhnya mereka menjadikan syaitan-syaitan pelindung (mereka) selain Allah, dan mereka mengira bahwa mereka mendapat petunjuk.'),
(985, '007:031', '68', 7, 31, 'Hai anak Adam, pakailah pakaianmu yang indah di setiap (memasuki) mesjid, makan dan minumlah, dan janganlah berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang-orang yang berlebih-lebihan.'),
(986, '007:032', '68', 7, 32, 'Katakanlah: \"Siapakah yang mengharamkan perhiasan dari Allah yang telah dikeluarkan-Nya untuk hamba-hamba-Nya dan (siapa pulakah yang mengharamkan) rezeki yang baik?\" Katakanlah: \"Semuanya itu (disediakan) bagi orang-orang yang beriman dalam kehidupan dunia, khusus (untuk mereka saja) di hari kiamat\". Demikianlah Kami menjelaskan ayat-ayat itu bagi orang-orang yang mengetahui.'),
(987, '007:033', '68', 7, 33, 'Katakanlah: \"Tuhanku hanya mengharamkan perbuatan yang keji, baik yang nampak ataupun yang tersembunyi, dan perbuatan dosa, melanggar hak manusia tanpa alasan yang benar, (mengharamkan) mempersekutukan Allah dengan sesuatu yang Allah tidak menurunkan hujjah untuk itu dan (mengharamkan) mengada-adakan terhadap Allah apa yang tidak kamu ketahui\".'),
(988, '007:034', '68', 7, 34, 'Tiap-tiap umat mempunyai batas waktu; maka apabila telah datang waktunya mereka tidak dapat mengundurkannya barang sesaatpun dan tidak dapat (pula) memajukannya.'),
(989, '007:035', '68', 7, 35, 'Hai anak-anak Adam, jika datang kepadamu rasul-rasul daripada kamu yang menceritakan kepadamu ayat-ayat-Ku, maka barangsiapa yang bertakwa dan mengadakan perbaikan, tidaklah ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(990, '007:036', '68', 7, 36, 'Dan orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, mereka itu penghuni-penghuni neraka; mereka kekal di dalamnya.'),
(991, '007:037', '68', 7, 37, 'Maka siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah atau mendustakan ayat-ayat-Nya? Orang-orang itu akan memperoleh bahagian yang telah ditentukan untuknya dalam Kitab (Lauh Mahfuzh); hingga bila datang kepada mereka utusan-utusan Kami (malaikat) untuk mengambil nyawanya, (di waktu itu) utusan Kami bertanya: \"Di mana (berhala-berhala) yang biasa kamu sembah selain Allah?\" Orang-orang musyrik itu menjawab: \"Berhala-berhala itu semuanya telah lenyap dari kami,\" dan mereka mengakui terhadap diri mereka bahwa mereka adalah orang-orang yang kafir.'),
(992, '007:038', '68', 7, 38, 'Allah berfirman: \"Masuklah kamu sekalian ke dalam neraka bersama umat-umat jin dan manusia yang telah terdahulu sebelum kamu. Setiap suatu umat masuk (ke dalam neraka), dia mengutuk kawannya (menyesatkannya); sehingga apabila mereka masuk semuanya berkatalah orang-orang yang masuk kemudian di antara mereka kepada orang-orang yang masuk terdahulu: \"Ya Tuhan kami, mereka telah menyesatkan kami, sebab itu datangkanlah kepada mereka siksaan yang berlipat ganda dari neraka\". Allah berfirman: \"Masing-masing mendapat (siksaan) yang berlipat ganda, akan tetapi kamu tidak mengetahui\".'),
(993, '007:039', '68', 7, 39, 'Dan berkata orang-orang yang masuk terdahulu di antara mereka kepada orang-orang yang masuk kemudian: \"Kamu tidak mempunyai kelebihan sedikitpun atas kami, maka rasakanlah siksaan karena perbuatan yang telah kamu lakukan\".'),
(994, '007:040', '68', 7, 40, 'Sesungguhnya orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, sekali-kali tidak akan dibukakan bagi mereka pintu-pintu langit dan tidak (pula) mereka masuk surga, hingga unta masuk ke lubang jarum. Demikianlah Kami memberi pembalasan kepada orang-orang yang berbuat kejahatan.'),
(995, '007:041', '68', 7, 41, 'Mereka mempunyai tikar tidur dari api neraka dan di atas mereka ada selimut (api neraka). Demikianlah Kami memberi balasan kepada orang-orang yang zalim,'),
(996, '007:042', '68', 7, 42, 'dan orang-orang yang beriman dan mengerjakan amal-amal yang saleh, Kami tidak memikulkan kewajiban kepada diri seseorang melainkan sekedar kesanggupannya, mereka itulah penghuni-penghuni surga; mereka kekal di dalamnya.'),
(997, '007:043', '68', 7, 43, 'Dan Kami cabut segala macam dendam yang berada di dalam dada mereka; mengalir di bawah mereka sungai-sungai dan mereka berkata: \"Segala puji bagi Allah yang telah menunjuki kami kepada (surga) ini. Dan kami sekali-kali tidak akan mendapat petunjuk kalau Allah tidak memberi kami petunjuk. Sesungguhnya telah datang rasul-rasul Tuhan kami, membawa kebenaran\". Dan diserukan kepada mereka: \"ltulah surga yang diwariskan kepadamu, disebabkan apa yang dahulu kamu kerjakan\".'),
(998, '007:044', '68', 7, 44, 'Dan penghuni-penghuni surga berseru kepada Penghuni-penghuni neraka (dengan mengatakan): \"Sesungguhnya kami dengan sebenarnya telah memperoleh apa yang Tuhan kami menjanjikannya kepada kami. Maka apakah kamu telah memperoleh dengan sebenarnya apa (azab) yang Tuhan kamu menjanjikannya (kepadamu)?\" Mereka (penduduk neraka) menjawab: \"Betul\". Kemudian seorang penyeru (malaikat) mengumumkan di antara kedua golongan itu: \"Kutukan Allah ditimpakan kepada orang-orang yang zalim,'),
(999, '007:045', '68', 7, 45, '(yaitu) orang-orang yang menghalang-halangi (manusia) dari jalan Allah dan menginginkan agar jalan itu menjadi bengkok, dan mereka kafir kepada kehidupan akhirat\".'),
(1000, '007:046', '68', 7, 46, 'Dan di antara keduanya (penghuni surga dan neraka) ada batas; dan di atas A´raaf itu ada orang-orang yang mengenal masing-masing dari dua golongan itu dengan tanda-tanda mereka. Dan mereka menyeru penduduk surga: \"Salaamun ´alaikum\". Mereka belum lagi memasukinya, sedang mereka ingin segera (memasukinya).'),
(1001, '007:047', '68', 7, 47, 'Dan apabila pandangan mereka dialihkan ke arah penghuni neraka, mereka berkata: \"Ya Tuhan kami, janganlah Engkau tempatkan kami bersama-sama orang-orang yang zalim itu\".'),
(1002, '007:048', '68', 7, 48, 'Dan orang-orang yang di atas A´raaf memanggil beberapa orang (pemuka-pemuka orang kafir) yang mereka mengenalnya dengan tanda-tandanya dengan mengatakan: \"Harta yang kamu kumpulkan dan apa yang selalu kamu sombongkan itu, tidaklah memberi manfaat kepadamu\".'),
(1003, '007:049', '68', 7, 49, '(Orang-orang di atas A´raaf bertanya kepada penghuni neraka): \"Itukah orang-orang yang kamu telah bersumpah bahwa mereka tidak akan mendapat rahmat Allah?\". (Kepada orang mukmin itu dikatakan): \"Masuklah ke dalam surga, tidak ada kekhawatiran terhadapmu dan tidak (pula) kamu bersedih hati\".'),
(1004, '007:050', '68', 7, 50, 'Dan penghuni neraka menyeru penghuni surga: \"Limpahkanlah kepada kami sedikit air atau makanan yang telah direzekikan Allah kepadamu\". Mereka (penghuni surga) menjawab: \"Sesungguhnya Allah telah mengharamkan keduanya itu atas orang-orang kafir,'),
(1005, '007:051', '68', 7, 51, '(yaitu) orang-orang yang menjadikan agama mereka sebagai main-main dan senda gurau, dan kehidupan dunia telah menipu mereka\". Maka pada hari (kiamat) ini, Kami melupakan mereka sebagaimana mereka melupakan pertemuan mereka dengan hari ini, dan (sebagaimana) mereka selalu mengingkari ayat-ayat Kami.'),
(1006, '007:052', '68', 7, 52, 'Dan sesungguhnya Kami telah mendatangkan sebuah Kitab (Al Quran) kepada mereka yang Kami telah menjelaskannya atas dasar pengetahuan Kami; menjadi petunjuk dan rahmat bagi orang-orang yang beriman.'),
(1007, '007:053', '68', 7, 53, 'Tiadalah mereka menunggu-nunggu kecuali (terlaksananya kebenaran) Al Quran itu. Pada hari datangnya kebenaran pemberitaan Al Quran itu, berkatalah orang-orang yang melupakannya sebelum itu: \"Sesungguhnya telah datang rasul-rasul Tuhan kami membawa yang hak, maka adakah bagi kami pemberi syafa´at yang akan memberi syafa´at bagi kami, atau dapatkah kami dikembalikan (ke dunia) sehingga kami dapat beramal yang lain dari yang pernah kami amalkan?\". Sungguh mereka telah merugikan diri mereka sendiri dan telah lenyaplah dari mereka tuhan-tuhan yang mereka ada-adakan.'),
(1008, '007:054', '68', 7, 54, 'Sesungguhnya Tuhan kamu ialah Allah yang telah menciptakan langit dan bumi dalam enam masa, lalu Dia bersemayam di atas ´Arsy. Dia menutupkan malam kepada siang yang mengikutinya dengan cepat, dan (diciptakan-Nya pula) matahari, bulan dan bintang-bintang (masing-masing) tunduk kepada perintah-Nya. Ingatlah, menciptakan dan memerintah hanyalah hak Allah. Maha Suci Allah, Tuhan semesta alam.'),
(1009, '007:055', '68', 7, 55, 'Berdoalah kepada Tuhanmu dengan berendah diri dan suara yang lembut. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas.'),
(1010, '007:056', '68', 7, 56, 'Dan janganlah kamu membuat kerusakan di muka bumi, sesudah (Allah) memperbaikinya dan berdoalah kepada-Nya dengan rasa takut (tidak akan diterima) dan harapan (akan dikabulkan). Sesungguhnya rahmat Allah amat dekat kepada orang-orang yang berbuat baik.'),
(1011, '007:057', '68', 7, 57, 'Dan Dialah yang meniupkan angin sebagai pembawa berita gembira sebelum kedatangan rahmat-Nya (hujan); hingga apabila angin itu telah membawa awan mendung, Kami halau ke suatu daerah yang tandus, lalu Kami turunkan hujan di daerah itu, maka Kami keluarkan dengan sebab hujan itu pelbagai macam buah-buahan. Seperti itulah Kami membangkitkan orang-orang yang telah mati, mudah-mudahan kamu mengambil pelajaran.'),
(1012, '007:058', '68', 7, 58, 'Dan tanah yang baik, tanaman-tanamannya tumbuh subur dengan seizin Allah; dan tanah yang tidak subur, tanaman-tanamannya hanya tumbuh merana. Demikianlah Kami mengulangi tanda-tanda kebesaran (Kami) bagi orang-orang yang bersyukur.'),
(1013, '007:059', '68', 7, 59, 'Sesungguhnya Kami telah mengutus Nuh kepada kaumnya lalu ia berkata: \"Wahai kaumku sembahlah Allah, sekali-kali tak ada Tuhan bagimu selain-Nya\". Sesungguhnya (kalau kamu tidak menyembah Allah), aku takut kamu akan ditimpa azab hari yang besar (kiamat).'),
(1014, '007:060', '68', 7, 60, 'Pemuka-pemuka dari kaumnya berkata: \"Sesungguhnya kami memandang kamu berada dalam kesesatan yang nyata\".'),
(1015, '007:061', '68', 7, 61, 'Nuh menjawab: \"Hai kaumku, tak ada padaku kesesatan sedikitpun tetapi aku adalah utusan dari Tuhan semesta alam\".'),
(1016, '007:062', '68', 7, 62, '\"Aku sampaikan kepadamu amanat-amanat Tuhanku dan aku memberi nasehat kepadamu. dan aku mengetahui dari Allah apa yang tidak kamu ketahui\".'),
(1017, '007:063', '68', 7, 63, 'Dan apakah kamu (tidak percaya) dan heran bahwa datang kepada kamu peringatan dari Tuhanmu dengan perantaraan seorang laki-laki dari golonganmu agar dia memberi peringatan kepadamu dan mudah-mudahan kamu bertakwa dan supaya kamu mendapat rahmat?'),
(1018, '007:064', '68', 7, 64, 'Maka mereka mendustakan Nuh, kemudian Kami selamatkan dia dan orang-orang yang bersamanya dalam bahtera, dan Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang buta (mata hatinya).'),
(1019, '007:065', '68', 7, 65, 'Dan (Kami telah mengutus) kepada kaum ´Aad saudara mereka, Hud. Ia berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain dari-Nya. Maka mengapa kamu tidak bertakwa kepada-Nya?\"'),
(1020, '007:066', '68', 7, 66, 'Pemuka-pemuka yang kafir dari kaumnya berkata: \"Sesungguhnya kami benar benar memandang kamu dalam keadaan kurang akal dan sesungguhnya kami menganggap kamu termasuk orang orang yang berdusta\".'),
(1021, '007:067', '68', 7, 67, 'Hud herkata \"Hai kaumku, tidak ada padaku kekurangan akal sedikitpun, tetapi aku ini adalah utusan dari Tuhan semesta alam.'),
(1022, '007:068', '68', 7, 68, 'Aku menyampaikan amanat-amanat Tuhanku kepadamu dan aku hanyalah pemberi nasehat yang terpercaya bagimu\".'),
(1023, '007:069', '68', 7, 69, 'Apakah kamu (tidak percaya) dan heran bahwa datang kepadamu peringatan dari Tuhanmu yang dibawa oleh seorang laki-laki di antaramu untuk memberi peringatan kepadamu? Dan ingatlah oleh kamu sekalian di waktu Allah menjadikan kamu sebagai pengganti-pengganti (yang berkuasa) sesudah lenyapnya kaum Nuh, dan Tuhan telah melebihkan kekuatan tubuh dan perawakanmu (daripada kaum Nuh itu). Maka ingatlah nikmat-nikmat Allah supaya kamu mendapat keberuntungan.'),
(1024, '007:070', '68', 7, 70, 'Mereka berkata: \"Apakah kamu datang kepada kami, agar kami hanya menyembah Allah saja dan meninggalkan apa yang biasa disembah oleh bapak-bapak kami? maka datangkanlah azab yang kamu ancamkan kepada kami jika kamu termasuk orang-orang yang benar\".'),
(1025, '007:071', '68', 7, 71, 'Ia berkata: \"Sungguh sudah pasti kamu akan ditimpa azab dan kemarahan dari Tuhanmu\". Apakah kamu sekalian hendak berbantah dengan aku tentang nama-nama (berhala) yang kamu beserta nenek moyangmu menamakannya, padahal Allah sekali-kali tidak menurunkan hujjah untuk itu? Maka tunggulah (azab itu), sesungguhnya aku juga termasuk orang yamg menunggu bersama kamu\".'),
(1026, '007:072', '68', 7, 72, 'Maka kami selamatkan Hud beserta orang-orang yang bersamanya dengan rahmat yang besar dari Kami, dan Kami tumpas orang-orang yang mendustakan ayat-ayat Kami, dan tiadalah mereka orang-orang yang beriman.'),
(1027, '007:073', '68', 7, 73, 'Dan (Kami telah mengutus) kepada kaum Tsamud saudara mereka Shaleh. Ia berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain-Nya. Sesungguhnya telah datang bukti yang nyata kepadamu dari Tuhanmu. Unta betina Allah ini menjadi tanda bagimu, maka biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apapun, (yang karenanya) kamu akan ditimpa siksaan yang pedih\".'),
(1028, '007:074', '68', 7, 74, 'Dan ingatlah olehmu di waktu Tuhan menjadikam kamu pengganti-pengganti (yang berkuasa) sesudah kaum ´Aad dan memberikan tempat bagimu di bumi. Kamu dirikan istana-istana di tanah-tanahnya yang datar dan kamu pahat gunung-gunungnya untuk dijadikan rumah; maka ingatlah nikmat-nikmat Allah dan janganlah kamu merajalela di muka bumi membuat kerusakan.'),
(1029, '007:075', '68', 7, 75, 'Pemuka-pemuka yang menyombongkan diri di antara kaumnya berkata kepada orang-orang yang dianggap lemah yang telah beriman di antara mereka: \"Tahukah kamu bahwa Shaleh di utus (menjadi rasul) oleh Tuhannya?\". Mereka menjawab: \"Sesungguhnya kami beriman kepada wahyu, yang Shaleh diutus untuk menyampaikannya\".'),
(1030, '007:076', '68', 7, 76, 'Orang-orang yang menyombongkan diri berkata: \"Sesungguhnya kami adalah orang yang tidak percaya kepada apa yang kamu imani itu\".'),
(1031, '007:077', '68', 7, 77, 'Kemudian mereka sembelih unta betina itu, dan mereka berlaku angkuh terhadap perintah Tuhan. Dan mereka berkata: \"Hai Shaleh, datangkanlah apa yang kamu ancamkan itu kepada kami, jika (betul) kamu termasuk orang-orang yang diutus (Allah)\".'),
(1032, '007:078', '68', 7, 78, 'Karena itu mereka ditimpa gempa, maka jadilah mereka mayat-mayat yang bergelimpangan di tempat tinggal mereka.'),
(1033, '007:079', '68', 7, 79, 'Maka Shaleh meninggalkan mereka seraya berkata: \"Hai kaumku sesungguhnya aku telah menyampaikan kepadamu amanat Tuhanku, dan aku telah memberi nasehat kepadamu, tetapi kamu tidak menyukai orang-orang yang memberi nasehat\".'),
(1034, '007:080', '68', 7, 80, 'Dan (Kami juga telah mengutus) Luth (kepada kaumnya). (Ingatlah) tatkala dia berkata kepada mereka: \"Mengapa kamu mengerjakan perbuatan faahisyah itu, yang belum pernah dikerjakan oleh seorangpun (di dunia ini) sebelummu?\"'),
(1035, '007:081', '68', 7, 81, 'Sesungguhnya kamu mendatangi lelaki untuk melepaskan nafsumu (kepada mereka), bukan kepada wanita, malah kamu ini adalah kaum yang melampaui batas.'),
(1036, '007:082', '68', 7, 82, 'Jawab kaumnya tidak lain hanya mengatakan: \"Usirlah mereka (Luth dan pengikut-pengikutnya) dari kotamu ini; sesungguhnya mereka adalah orang-orang yang berpura-pura mensucikan diri\".'),
(1037, '007:083', '68', 7, 83, 'Kemudian Kami selamatkan dia dan pengikut-pengikutnya kecuali isterinya; dia termasuk orang-orang yang tertinggal (dibinasakan).'),
(1038, '007:084', '68', 7, 84, 'Dan Kami turunkan kepada mereka hujan (batu); maka perhatikanlah bagaimana kesudahan orang-orang yang berdosa itu.'),
(1039, '007:085', '68', 7, 85, 'Dan (Kami telah mengutus) kepada penduduk Mad-yan saudara mereka, Syu´aib. Ia berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tidak ada Tuhan bagimu selain-Nya. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Maka sempurnakanlah takaran dan timbangan dan janganlah kamu kurangkan bagi manusia barang-barang takaran dan timbangannya, dan janganlah kamu membuat kerusakan di muka bumi sesudah Tuhan memperbaikinya. Yang demikian itu lebih baik bagimu jika betul-betul kamu orang-orang yang beriman\".'),
(1040, '007:086', '68', 7, 86, 'Dan janganlah kamu duduk di tiap-tiap jalan dengan menakut-nakuti dan menghalang-halangi orang yang beriman dari jalan Allah, dan menginginkan agar jalan Allah itu menjadi bengkok. Dan ingatlah di waktu dahulunya kamu berjumlah sedikit, lalu Allah memperbanyak jumlah kamu. Dan perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan.'),
(1041, '007:087', '68', 7, 87, 'Jika ada segolongan daripada kamu beriman kepada apa yang aku diutus untuk menyampaikannya dan ada (pula) segolongan yang tidak beriman, maka bersabarlah, hingga Allah menetapkan hukumnya di antara kita; dan Dia adalah Hakim yang sebaik-baiknya.'),
(1042, '007:088', '68', 7, 88, 'Pemuka-pemuka dan kaum Syu´aib yang menyombongkan dan berkata: \"Sesungguhnya kami akan mengusir kamu hai Syu´aib dan orang-orang yang beriman bersamamu dari kota kami, atau kamu kembali kepada agama kami\". Berkata Syu´aib: \"Dan apakah (kamu akan mengusir kami), kendatipun kami tidak menyukainya?\"'),
(1043, '007:089', '68', 7, 89, 'Sungguh kami mengada-adakan kebohongan yang benar terhadap Allah, jika kami kembali kepada agamamu, sesudah Allah melepaskan kami dari padanya. Dan tidaklah patut kami kembali kepadanya, kecuali jika Allah, Tuhan kami menghendaki(nya). Pengetahuan Tuhan kami meliputi segala sesuatu. Kepada Allah sajalah kami bertawakkal. Ya Tuhan kami, berilah keputusan antara kami dan kaum kami dengan hak (adil) dan Engkaulah Pemberi keputusan yang sebaik-baiknya.'),
(1044, '007:090', '68', 7, 90, 'Pemuka-pemuka kaum Syu´aib yang kafir berkata (kepada sesamanya): \"Sesungguhnya jika kamu mengikuti Syu´aib, tentu kamu jika berbuat demikian (menjadi) orang-orang yang merugi\".'),
(1045, '007:091', '68', 7, 91, 'Kemudian mereka ditimpa gempa, maka jadilah mereka mayat-mayat yang bergelimpangan di dalam rumah-rumah mereka,'),
(1046, '007:092', '68', 7, 92, '(yaitu) orang-orang yang mendustakan Syu´aib seolah-olah mereka belum pernah berdiam di kota itu; orang-orang yang mendustakan Syu´aib mereka itulah orang-orang yang merugi.'),
(1047, '007:093', '68', 7, 93, 'Maka Syu´aib meninggalkan mereka seraya berkata: \"Hai kaumku, sesungguhnya aku telah menyampaikan kepadamu amanat-amanat Tuhanku dan aku telah memberi nasehat kepadamu. Maka bagaimana aku akan bersedih hati terhadap orang-orang yang kafir?\"'),
(1048, '007:094', '68', 7, 94, 'Kami tidaklah mengutus seseorang nabipun kepada sesuatu negeri, (lalu penduduknya mendustakan nabi itu), melainkan Kami timpakan kepada penduduknya kesempitan dan penderitaan supaya mereka tunduk dengan merendahkan diri.'),
(1049, '007:095', '68', 7, 95, 'Kemudian Kami ganti kesusahan itu dengan kesenangan hingga keturunan dan harta mereka bertambah banyak, dan mereka berkata: \"Sesungguhnya nenek moyang kamipun telah merasai penderitaan dan kesenangan\", maka Kami timpakan siksaan atas mereka dengan sekonyong-konyong sedang mereka tidak menyadarinya.'),
(1050, '007:096', '68', 7, 96, 'Jikalau sekiranya penduduk negeri-negeri beriman dan bertakwa, pastilah Kami akan melimpahkan kepada mereka berkah dari langit dan bumi, tetapi mereka mendustakan (ayat-ayat Kami) itu, maka Kami siksa mereka disebabkan perbuatannya.'),
(1051, '007:097', '68', 7, 97, 'Maka apakah penduduk negeri-negeri itu merasa aman dari kedatangan siksaan Kami kepada mereka di malam hari di waktu mereka sedang tidur?'),
(1052, '007:098', '68', 7, 98, 'Atau apakah penduduk negeri-negeri itu merasa aman dari kedatangan siksaan Kami kepada mereka di waktu matahari sepenggalahan naik ketika mereka sedang bermain?'),
(1053, '007:099', '68', 7, 99, 'Maka apakah mereka merasa aman dari azab Allah (yang tidak terduga-duga)? Tiada yang merasa aman dan azab Allah kecuali orang-orang yang merugi.'),
(1054, '007:100', '68', 7, 100, 'Dan apakah belum jelas bagi orang-orang yang mempusakai suatu negeri sesudah (lenyap) penduduknya, bahwa kalau Kami menghendaki tentu Kami azab mereka karena dosa-dosanya; dan Kami kunci mati hati mereka sehingga mereka tidak dapat mendengar (pelajaran lagi)?'),
(1055, '007:101', '68', 7, 101, 'Negeri-negeri (yang telah Kami binasakan) itu, Kami ceritakan sebagian dari berita-beritanya kepadamu. Dan sungguh telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata, maka mereka (juga) tidak beriman kepada apa yang dahulunya mereka telah mendustakannya. Demikianlah Allah mengunci mata hati orang-orang kafir.'),
(1056, '007:102', '68', 7, 102, 'Dan Kami tidak mendapati kebanyakan mereka memenuhi janji. Sesungguhnya Kami mendapati kebanyakan mereka orang-orang yang fasik.'),
(1057, '007:103', '68', 7, 103, 'Kemudian Kami utus Musa sesudah rasul-rasul itu dengan membawa ayat-ayat Kami kepada Fir´aun dan pemuka-pemuka kaumnya, lalu mereka mengingkari ayat-ayat itu. Maka perhatikanlah bagaimana akibat orang-orang yang membuat kerusakan.'),
(1058, '007:104', '68', 7, 104, 'Dan Musa berkata: \"Hai Fir´aun, sesungguhnya aku ini adalah seorang utusan dari Tuhan semesta alam,'),
(1059, '007:105', '68', 7, 105, 'wajib atasku tidak mengatakan sesuatu terhadap Allah, kecuali yang hak. Sesungguhnya aku datang kepadamu dengan membawa bukti yang nyata dari Tuhanmu, maka lepaskanlah Bani Israil (pergi) bersama aku\".'),
(1060, '007:106', '68', 7, 106, 'Fir´aun menjawab: \"Jika benar kamu membawa sesuatu bukti, maka datangkanlah bukti itu jika (betul) kamu termasuk orang-orang yang benar\".'),
(1061, '007:107', '68', 7, 107, 'Maka Musa menjatuhkan tongkat-nya, lalu seketika itu juga tongkat itu menjadi ular yang sebenarnya.'),
(1062, '007:108', '68', 7, 108, 'Dan ia mengeluarkan tangannya, maka ketika itu juga tangan itu menjadi putih bercahaya (kelihatan) oleh orang-orang yang melihatnya.'),
(1063, '007:109', '68', 7, 109, 'Pemuka-pemuka kaum Fir´aun berkata: \"Sesungguhnya Musa ini adalah ahli sihir yang pandai,'),
(1064, '007:110', '68', 7, 110, 'yang bermaksud hendak mengeluarkan kamu dari negerimu\". (Fir´aun berkata): \"Maka apakah yang kamu anjurkan?\"'),
(1065, '007:111', '68', 7, 111, 'Pemuka-pemuka itu menjawab: \"Beri tangguhlah dia dan saudaranya serta kirimlah ke kota-kota beberapa orang yang akan mengumpulkan (ahli-ahli sihir),'),
(1066, '007:112', '68', 7, 112, 'supaya mereka membawa kepadamu semua ahli sihir yang pandai\".'),
(1067, '007:113', '68', 7, 113, 'Dan beberapa ahli sihir itu datang kepada Fir´aun mengatakan: \"(Apakah) sesungguhnya kami akan mendapat upah, jika kamilah yang menang?\"'),
(1068, '007:114', '68', 7, 114, 'Fir´aun menjawab: \"Ya, dan sesungguhnya kamu benar-benar akan termasuk orang-orang yang dekat (kepadaku)\".'),
(1069, '007:115', '68', 7, 115, 'Ahli-ahli sihir berkata: \"Hai Musa, kamukah yang akan melemparkan lebih dahulu, ataukah kami yang akan melemparkan?\"'),
(1070, '007:116', '68', 7, 116, 'Musa menjawab: \"Lemparkanlah (lebih dahulu)!\" Maka tatkala mereka melemparkan, mereka menyulap mata orang dan menjadikan orang banyak itu takut, serta mereka mendatangkan sihir yang besar (mena´jubkan).'),
(1071, '007:117', '68', 7, 117, 'Dan Kami wahyukan kepada Musa: \"Lemparkanlah tongkatmu!\". Maka sekonyong-konyong tongkat itu menelan apa yang mereka sulapkan.'),
(1072, '007:118', '68', 7, 118, 'Karena itu nyatalah yang benar dan batallah yang selalu mereka kerjakan.'),
(1073, '007:119', '68', 7, 119, 'Maka mereka kalah di tempat itu dan jadilah mereka orang-orang yang hina.'),
(1074, '007:120', '68', 7, 120, 'Dan ahli-ahli sihir itu serta merta meniarapkan diri dengan bersujud.'),
(1075, '007:121', '68', 7, 121, 'Mereka berkata: \"Kami beriman kepada Tuhan semesta alam,'),
(1076, '007:122', '68', 7, 122, '\"(yaitu) Tuhan Musa dan Harun\".'),
(1077, '007:123', '68', 7, 123, 'Fir´aun berkata: \"Apakah kamu beriman kepadanya sebelum aku memberi izin kepadamu?, sesungguhnya (perbuatan ini) adalah suatu muslihat yang telah kamu rencanakan di dalam kota ini, untuk mengeluarkan penduduknya dari padanya; maka kelak kamu akan mengetahui (akibat perbuatanmu ini);'),
(1078, '007:124', '68', 7, 124, 'demi, sesungguhnya aku akan memotong tangan dan kakimu dengan bersilang secara bertimbal balik, kemudian sungguh-sungguh aku akan menyalib kamu semuanya\".'),
(1079, '007:125', '68', 7, 125, 'Ahli-ahli sihir itu menjawab: \"Sesungguhnya kepada Tuhanlah kami kembali.'),
(1080, '007:126', '68', 7, 126, 'Dan kamu tidak menyalahkan kami, melainkan karena kami telah beriman kepada ayat-ayat Tuhan kami ketika ayat-ayat itu datang kepada kami\". (Mereka berdoa): \"Ya Tuhan kami, limpahkanlah kesabaran kepada kami dan wafatkanlah kami dalam keadaan berserah diri (kepada-Mu)\".'),
(1081, '007:127', '68', 7, 127, 'Berkatalah pembesar-pembesar dari kaum Fir´aun (kepada Fir´aun): \"Apakah kamu membiarkan Musa dan kaumnya untuk membuat kerusakan di negeri ini (Mesir) dan meninggalkan kamu serta tuhan-tuhanmu?\". Fir´aun menjawab: \"Akan kita bunuh anak-anak lelaki mereka dan kita biarkan hidup perempuan-perempuan mereka; dan sesungguhnya kita berkuasa penuh di atas mereka\".'),
(1082, '007:128', '68', 7, 128, 'Musa berkata kepada kaumnya: \"Mohonlah pertolongan kepada Allah dan bersabarlah; sesungguhnya bumi (ini) kepunyaan Allah; dipusakakan-Nya kepada siapa yang dihendaki-Nya dari hamba-hamba-Nya. Dan kesudahan yang baik adalah bagi orang-orang yang bertakwa\".'),
(1083, '007:129', '68', 7, 129, 'Kaum Musa berkata: \"Kami telah ditindas (oleh Fir´aun) sebelum kamu datang kepada kami dan sesudah kamu datang. Musa menjawab: \"Mudah-mudahan Allah membinasakan musuhmu dan menjadikan kamu khalifah di bumi(Nya), maka Allah akan melihat bagaimana perbuatanmu.'),
(1084, '007:130', '68', 7, 130, 'Dan sesungguhnya Kami telah menghukum (Fir´aun dan) kaumnya dengan (mendatangkan) musim kemarau yang panjang dan kekurangan buah-buahan, supaya mereka mengambil pelajaran.'),
(1085, '007:131', '68', 7, 131, 'Kemudian apabila datang kepada mereka kemakmuran, mereka berkata: \"Itu adalah karena (usaha) kami\". Dan jika mereka ditimpa kesusahan, mereka lemparkan sebab kesialan itu kepada Musa dan orang-orang yang besertanya. Ketahuilah, sesungguhnya kesialan mereka itu adalah ketetapan dari Allah, akan tetapi kebanyakan mereka tidak mengetahui.'),
(1086, '007:132', '68', 7, 132, 'Mereka berkata: \"Bagaimanapun kamu mendatangkan keterangan kepada kami untuk menyihir kami dengan keterangan itu, maka kami sekali-kali tidak akan beriman kepadamu\".'),
(1087, '007:133', '68', 7, 133, 'Maka Kami kirimkan kepada mereka taufan, belalang, kutu, katak dan darah sebagai bukti yang jelas, tetapi mereka tetap menyombongkan diri dan mereka adalah kaum yang berdosa.'),
(1088, '007:134', '68', 7, 134, 'Dan ketika mereka ditimpa azab (yang telah diterangkan itu) merekapun berkata: \"Hai Musa, mohonkanlah untuk kami kepada Tuhamnu dengan (perantaraan) kenabian yang diketahui Allah ada pada sisimu. Sesungguhnya jika kamu dapat menghilangkan azab itu dan pada kami, pasti kami akan beriman kepadamu dan akan kami biarkan Bani Israil pergi bersamamu\".'),
(1089, '007:135', '68', 7, 135, 'Maka setelah Kami hilangkan azab itu dari mereka hingga batas waktu yang mereka sampai kepadanya, tiba-tiba mereka mengingkarinya.'),
(1090, '007:136', '68', 7, 136, 'Kemudian Kami menghukum mereka, maka Kami tenggelamkan mereka di laut disebabkan mereka mendustakan ayat-ayat Kami dan mereka adalah orang-orang yang melalaikan ayat-ayat Kami itu.'),
(1091, '007:137', '68', 7, 137, 'Dan Kami pusakakan kepada kaum yang telah ditindas itu, negeri-negeri bahagian timur bumi dan bahagian baratnya yang telah Kami beri berkah padanya. Dan telah sempurnalah perkataan Tuhanmu yang baik (sebagai janji) untuk Bani Israil disebabkan kesabaran mereka. Dan Kami hancurkan apa yang telah dibuat Fir´aun dan kaumnya dan apa yang telah dibangun mereka.'),
(1092, '007:138', '68', 7, 138, 'Dan Kami seberangkan Bani Israil ke seberang lautan itu, maka setelah mereka sampai kepada suatu kaum yang tetap menyembah berhala mereka, Bani lsrail berkata: \"Hai Musa. buatlah untuk kami sebuah tuhan (berhala) sebagaimana mereka mempunyai beberapa tuhan (berhala)\". Musa menjawab: \"Sesungguh-nya kamu ini adalah kaum yang tidak mengetahui (sifat-sifat Tuhan)\".'),
(1093, '007:139', '68', 7, 139, 'Sesungguhnya mereka itu akan dihancurkan kepercayaan yang dianutnya dan akan batal apa yang seIalu mereka kerjakan.'),
(1094, '007:140', '68', 7, 140, 'Musa menjawab: \"Patutkah aku mencari Tuhan untuk kamu yang selain dari pada Allah, padahal Dialah yang telah melebihkan kamu atas segala umat.'),
(1095, '007:141', '68', 7, 141, 'Dan (ingatlah hai Bani Israil), ketika Kami menyelamatkan kamu dari (Fir´aun) dan kaumnya, yang mengazab kamu dengan azab yang sangat jahat, yaitu mereka membunuh anak-anak lelakimu dan membiarkan hidup wanita-wanitamu. Dan pada yang demikian itu cobaan yang besar dari Tuhanmu\".'),
(1096, '007:142', '68', 7, 142, 'Dan telah Kami janjikan kepada Musa (memberikan Taurat) sesudah berlalu waktu tiga puluh malam, dan Kami sempurnakan jumlah malam itu dengan sepuluh (malam lagi), maka sempurnalah waktu yang telah ditentukan Tuhannya empat puluh malam. Dan berkata Musa kepada saudaranya yaitu Harun: \"Gantikanlah aku dalam (memimpin) kaumku, dan perbaikilah, dan janganlah kamu mengikuti jalan orang-orang yang membuat kerusakan\".'),
(1097, '007:143', '68', 7, 143, 'Dan tatkala Musa datang untuk (munajat dengan Kami) pada waktu yang telah Kami tentukan dan Tuhan telah berfirman (langsung) kepadanya, berkatalah Musa: \"Ya Tuhanku, nampakkanlah (diri Engkau) kepadaku agar aku dapat melihat kepada Engkau\". Tuhan berfirman: \"Kamu sekali-kali tidak sanggup melihat-Ku, tapi lihatlah ke bukit itu, maka jika ia tetap di tempatnya (sebagai sediakala) niscaya kamu dapat melihat-Ku\". Tatkala Tuhannya menampakkan diri kepada gunung itu, dijadikannya gunung itu hancur luluh dan Musa pun jatuh pingsan. Maka setelah Musa sadar kembali, dia berkata: \"Maha Suci Engkau, aku bertaubat kepada Engkau dan aku orang yang pertama-tama beriman\".'),
(1098, '007:144', '68', 7, 144, 'Allah berfirman: \"Hai Musa, sesungguhnya Aku memilih (melebihkan) kamu dan manusia yang lain (di masamu) untuk membawa risalah-Ku dan untuk berbicara langsung dengan-Ku, sebab itu berpegang teguhlah kepada apa yang Aku berikan kepadamu dan hendaklah kamu termasuk orang-orang yang bersyukur\".'),
(1099, '007:145', '68', 7, 145, 'Dan telah Kami tuliskan untuk Musa pada luh-luh (Taurat) segala sesuatu sebagai pelajaran dan penjelasan bagi segala sesuatu; maka (Kami berfirman): \"Berpeganglah kepadanya dengan teguh dan suruhlah kaummu berpegang kepada (perintah-perintahnya) dengan sebaik-baiknya, nanti Aku akan memperlihatkan kepadamu negeri orang-orang yang fasik.'),
(1100, '007:146', '68', 7, 146, 'Aku akan memalingkan orang-orang yang menyombongkan dirinya di muka bumi tanpa alasan yang benar dari tanda-tanda kekuasaan-Ku. Mereka jika melihat tiap-tiap ayat(Ku), mereka tidak beriman kepadanya. Dan jika mereka melihat jalan yang membawa kepada petunjuk, mereka tidak mau menempuhnya, tetapi jika mereka melihat jalan kesesatan, mereka terus memenempuhnya. Yang demikian itu adalah karena mereka mendustakan ayat-ayat Kami dan mereka selalu lalai dari padanya.'),
(1101, '007:147', '68', 7, 147, 'Dan orang-orang yang mendustakan ayat-ayat Kami dan mendustakan akan menemui akhirat, sia-sialah perbuatan mereka. Mereka tidak diberi balasan selain dari apa yang telah mereka kerjakan.'),
(1102, '007:148', '68', 7, 148, 'Dan kaum Musa, setelah kepergian Musa ke gunung Thur membuat dari perhiasan-perhiasan (emas) mereka anak lembu yang bertubuh dan bersuara. Apakah mereka tidak mengetahui bahwa anak lembu itu tidak dapat berbicara dengan mereka dan tidak dapat (pula) menunjukkan jalan kepada mereka? Mereka menjadikannya (sebagai sembahan) dan mereka adalah orang-orang yang zalim.'),
(1103, '007:149', '68', 7, 149, 'Dan setelah mereka sangat menyesali perbuatannya dan mengetahui bahwa mereka telah sesat, merekapun berkata: \"Sungguh jika Tuhan kami tidak memberi rahmat kepada kami dan tidak mengampuni kami, pastilah kami menjadi orang-orang yang merugi\".'),
(1104, '007:150', '68', 7, 150, 'Dan tatkala Musa telah kembali kepada kaumnya dengan marah dan sedih hati berkatalah dia: \"Alangkah buruknya perbuatan yang kamu kerjakan sesudah kepergianku! Apakah kamu hendak mendahului janji Tuhanmu? Dan Musapun melemparkan luh-luh (Taurat) itu dan memegang (rambut) kepala saudaranya (Harun) sambil menariknya ke arahnya, Harun berkata: \"Hai anak ibuku, sesungguhnya kaum ini telah menganggapku lemah dan hampir-hampir mereka membunuhku, sebab itu janganlah kamu menjadikan musuh-musuh gembira melihatku, dan janganlah kamu masukkan aku ke dalam golongan orang-orang yang zalim\"'),
(1105, '007:151', '68', 7, 151, 'Musa berdoa: \"Ya Tuhanku, ampunilah aku dan saudaraku dan masukkanlah kami ke dalam rahmat Engkau, dan Engkau adalah Maha Penyayang di antara para penyayang\".'),
(1106, '007:152', '68', 7, 152, 'Sesungguhnya orang-orang yang menjadikan anak lembu (sebagai sembahannya), kelak akan menimpa mereka kemurkaan dari Tuhan mereka dan kehinaan dalam kehidupan di dunia. Demikianlah Kami memberi balasan kepada orang-orang yang membuat-buat kebohongan.'),
(1107, '007:153', '68', 7, 153, 'Orang-orang yang mengerjakan kejahatan, kemudian bertaubat sesudah itu dan beriman; sesungguhnya Tuhan kamu sesudah taubat yang disertai dengan iman itu adalah Maha Pengampun lagi Maha Penyayang.'),
(1108, '007:154', '68', 7, 154, 'Sesudah amarah Musa menjadi reda, lalu diambilnya (kembali) luh-luh (Taurat) itu; dan dalam tulisannya terdapat petunjuk dan rahmat untuk orang-orang yang takut kepada Tuhannya.'),
(1109, '007:155', '68', 7, 155, 'Dan Musa memilih tujuh puluh orang dari kaumnya untuk (memohonkan taubat kepada Kami) pada waktu yang telah Kami tentukan. Maka ketika mereka digoncang gempa bumi, Musa berkata: \"Ya Tuhanku, kalau Engkau kehendaki, tentulah Engkau membinasakan mereka dan aku sebelum ini. Apakah Engkau membinasakan kami karena perbuatan orang-orang yang kurang akal di antara kami? Itu hanyalah cobaan dari Engkau, Engkau sesatkan dengan cobaan itu siapa yang Engkau kehendaki dan Engkau beri petunjuk kepada siapa yang Engkau kehendaki. Engkaulah Yang memimpin kami, maka ampunilah kami dan berilah kami rahmat dan Engkaulah Pemberi ampun yang sebaik-baiknya\".'),
(1110, '007:156', '68', 7, 156, 'Dan tetapkanlah untuk kami kebajikan di dunia ini dan di akhirat; sesungguhnya kami kembali (bertaubat) kepada Engkau. Allah berfirman: \"Siksa-Ku akan Kutimpakan kepada siapa yang Aku kehendaki dan rahmat-Ku meliputi segala sesuatu. Maka akan Aku tetapkan rahmat-Ku untuk orang-orang yang bertakwa, yang menunaikan zakat dan orang-orang yang beriman kepada ayat-ayat Kami\".'),
(1111, '007:157', '68', 7, 157, '(Yaitu) orang-orang yang mengikut Rasul, Nabi yang ummi yang (namanya) mereka dapati tertulis di dalam Taurat dan Injil yang ada di sisi mereka, yang menyuruh mereka mengerjakan yang ma´ruf dan melarang mereka dari mengerjakan yang mungkar dan menghalalkan bagi mereka segala yang baik dan mengharamkan bagi mereka segala yang buruk dan membuang dari mereka beban-beban dan belenggu-belenggu yang ada pada mereka. Maka orang-orang yang beriman kepadanya. memuliakannya, menolongnya dan mengikuti cahaya yang terang yang diturunkan kepadanya (Al Quran), mereka itulah orang-orang yang beruntung.'),
(1112, '007:158', '68', 7, 158, 'Katakanlah: \"Hai manusia sesungguhnya aku adalah utusan Allah kepadamu semua, yaitu Allah Yang mempunyai kerajaan langit dan bumi; tidak ada Tuhan (yang berhak disembah) selain Dia, Yang menghidupkan dan mematikan, maka berimanlah kamu kepada Allah dan Rasul-Nya, Nabi yang ummi yang beriman kepada Allah dan kepada kalimat-kalimat-Nya (kitab-kitab-Nya) dan ikutilah dia, supaya kamu mendapat petunjuk\".'),
(1113, '007:159', '68', 7, 159, 'Dan di antara kaum Musa itu terdapat suatu umat yang memberi petunjuk (kepada manusia) dengan hak dan dengan yang hak itulah mereka menjalankan keadilan.'),
(1114, '007:160', '68', 7, 160, 'Dan mereka Kami bagi menjadi dua belas suku yang masing-masingnya berjumlah besar dan Kami wahyukan kepada Musa ketika kaumnya meminta air kepadanya: \"Pukullah batu itu dengan tongkatmu!\". Maka memancarlah dari padanya duabelas mata air. Sesungguhnya tiap-tiap suku mengetahui tempat minum masing-masing. Dan Kami naungkan awan di atas mereka dan Kami turunkan kepada mereka manna dan salwa. (Kami berfirman): \"Makanlah yang baik-baik dari apa yang telah Kami rezekikan kepadamu\". Mereka tidak menganiaya Kami, tapi merekalah yang selalu menganiaya dirinya sendiri.'),
(1115, '007:161', '68', 7, 161, 'Dan (ingatlah), ketika dikatakan kepada mereka (Bani Israil): \"Diamlah di negeri ini saja (Baitul Maqdis) dan makanlah dari (hasil bumi)nya di mana saja kamu kehendaki\". Dan katakanlah: \"Bebaskanlah kami dari dosa kami dan masukilah pintu gerbangnya sambil membungkuk, niscaya Kami ampuni kesalahan-kesalahanmu\". Kelak akan Kami tambah (pahala) kepada orang-orang yang berbuat baik.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1116, '007:162', '68', 7, 162, 'Maka orang-orang yang zalim di antara mereka itu mengganti (perkataan itu) dengan perkataan yang tidak dikatakan kepada mereka, maka Kami timpakan kepada mereka azab dari langit disebabkan kezaliman mereka.'),
(1117, '007:163', '68', 7, 163, 'Dan tanyakanlah kepada Bani Israil tentang negeri yang terletak di dekat laut ketika mereka melanggar aturan pada hari Sabtu, di waktu datang kepada mereka ikan-ikan (yang berada di sekitar) mereka terapung-apung di permukaan air, dan di hari-hari yang bukan Sabtu, ikan-ikan itu tidak datang kepada mereka. Demikianlah Kami mencoba mereka disebabkan mereka berlaku fasik.'),
(1118, '007:164', '68', 7, 164, 'Dan (ingatlah) ketika suatu umat di antara mereka berkata: \"Mengapa kamu menasehati kaum yang Allah akan membinasakan mereka atau mengazab mereka dengan azab yang amat keras?\" Mereka menjawab: \"Agar kami mempunyai alasan (pelepas tanggung jawab) kepada Tuhanmu, dan supaya mereka bertakwa.'),
(1119, '007:165', '68', 7, 165, 'Maka tatkala mereka melupakan apa yang diperingatkan kepada mereka, Kami selamatkan orang-orang yang melarang dari perbuatan jahat dan Kami timpakan kepada orang-orang yang zalim siksaan yang keras, disebabkan mereka selalu berbuat fasik.'),
(1120, '007:166', '68', 7, 166, 'Maka tatkala mereka bersikap sombong terhadap apa yang dilarang mereka mengerjakannya, Kami katakan kepadanya: \"Jadilah kamu kera yang hina.'),
(1121, '007:167', '68', 7, 167, 'Dan (ingatlah), ketika Tuhanmu memberitahukan, bahwa sesungguhnya Dia akan mengirim kepada mereka (orang-orang Yahudi) sampai hari kiamat orang-orang yang akan menimpakan kepada mereka azab yang seburuk-buruknya. Sesungguhnya Tuhanmu amat cepat siksa-Nya, dan sesungguhnya Dia adalah Maha Pengampun lagi Maha Penyayang.'),
(1122, '007:168', '68', 7, 168, 'Dan Kami bagi-bagi mereka di dunia ini menjadi beberapa golongan; di antaranya ada orang-orang yang saleh dan di antaranya ada yang tidak demikian. Dan Kami coba mereka dengan (nikmat) yang baik-baik dan (bencana) yang buruk-buruk, agar mereka kembali (kepada kebenaran).'),
(1123, '007:169', '68', 7, 169, 'Maka datanglah sesudah mereka generasi (yang jahat) yang mewarisi Taurat, yang mengambil harta benda dunia yang rendah ini, dan berkata: \"Kami akan diberi ampun\". Dan kelak jika datang kepada mereka harta benda dunia sebanyak itu (pula), niscaya mereka akan mengambilnya (juga). Bukankah perjanjian Taurat sudah diambil dari mereka, yaitu bahwa mereka tidak akan mengatakan terhadap Allah kecuali yang benar, padahal mereka telah mempelajari apa yang tersebut di dalamnya?. Dan kampung akhirat itu lebih bagi mereka yang bertakwa. Maka apakah kamu sekalian tidak mengerti?'),
(1124, '007:170', '68', 7, 170, 'Dan orang-orang yang berpegang teguh dengan Al Kitab (Taurat) serta mendirikan shalat, (akan diberi pahala) karena sesungguhnya Kami tidak menyia-nyiakan pahala orang-orang yang mengadakan perbaikan.'),
(1125, '007:171', '68', 7, 171, 'Dan (ingatlah), ketika Kami mengangkat bukit ke atas mereka seakan-akan bukit itu naungan awan dan mereka yakin bahwa bukit itu akan jatuh menimpa mereka. (Dan Kami katakan kepada mereka): \"Peganglah dengan teguh apa yang telah Kami berikan kepadamu, serta ingatlah selalu (amalkanlah) apa yang tersebut di dalamnya supaya kamu menjadi orang-orang yang bertakwa\".'),
(1126, '007:172', '68', 7, 172, 'Dan (ingatlah), ketika Tuhanmu mengeluarkan keturunan anak-anak Adam dari sulbi mereka dan Allah mengambil kesaksian terhadap jiwa mereka (seraya berfirman): \"Bukankah Aku ini Tuhanmu?\" Mereka menjawab: \"Betul (Engkau Tuhan kami), kami menjadi saksi\". (Kami lakukan yang demikian itu) agar di hari kiamat kamu tidak mengatakan: \"Sesungguhnya kami (bani Adam) adalah orang-orang yang lengah terhadap ini (keesaan Tuhan)\",'),
(1127, '007:173', '68', 7, 173, 'atau agar kamu tidak mengatakan: \"Sesungguhnya orang-orang tua kami telah mempersekutukan Tuhan sejak dahulu, sedang kami ini adalah anak-anak keturunan yang (datang) sesudah mereka. Maka apakah Engkau akan membinasakan kami karena perbuatan orang-orang yang sesat dahulu?\"'),
(1128, '007:174', '68', 7, 174, 'Dan demikianlah Kami menjelaskan ayat-ayat itu, agar mereka kembali (kepada kebenaran).'),
(1129, '007:175', '68', 7, 175, 'Dan bacakanlah kepada mereka berita orang yang telah Kami berikan kepadanya ayat-ayat Kami (pengetahuan tentang isi Al Kitab), kemudian dia melepaskan diri dari pada ayat-ayat itu, lalu dia diikuti oleh syaitan (sampai dia tergoda), maka jadilah dia termasuk orang-orang yang sesat.'),
(1130, '007:176', '68', 7, 176, 'Dan kalau Kami menghendaki, sesungguhnya Kami tinggikan (derajat)nya dengan ayat-ayat itu, tetapi dia cenderung kepada dunia dan menurutkan hawa nafsunya yang rendah, maka perumpamaannya seperti anjing jika kamu menghalaunya diulurkannya lidahnya dan jika kamu membiarkannya dia mengulurkan lidahnya (juga). Demikian itulah perumpamaan orang-orang yang mendustakan ayat-ayat Kami. Maka ceritakanlah (kepada mereka) kisah-kisah itu agar mereka berfikir.'),
(1131, '007:177', '68', 7, 177, 'Amat buruklah perumpamaan orang-orang yang mendustakan ayat-ayat Kami dan kepada diri mereka sendirilah mereka berbuat zalim.'),
(1132, '007:178', '68', 7, 178, 'Barangsiapa yang diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa yang disesatkan Allah, maka merekalah orang-orang yang merugi.'),
(1133, '007:179', '68', 7, 179, 'Dan sesungguhnya Kami jadikan untuk (isi neraka Jahannam) kebanyakan dari jin dan manusia, mereka mempunyai hati, tetapi tidak dipergunakannya untuk memahami (ayat-ayat Allah) dan mereka mempunyai mata (tetapi) tidak dipergunakannya untuk melihat (tanda-tanda kekuasaan Allah), dan mereka mempunyai telinga (tetapi) tidak dipergunakannya untuk mendengar (ayat-ayat Allah). Mereka itu sebagai binatang ternak, bahkan mereka lebih sesat lagi. Mereka itulah orang-orang yang lalai.'),
(1134, '007:180', '68', 7, 180, 'Hanya milik Allah asmaa-ul husna, maka bermohonlah kepada-Nya dengan menyebut asmaa-ul husna itu dan tinggalkanlah orang-orang yang menyimpang dari kebenaran dalam (menyebut) nama-nama-Nya. Nanti mereka akan mendapat balasan terhadap apa yang telah mereka kerjakan.'),
(1135, '007:181', '68', 7, 181, 'Dan di antara orang-orang yang Kami ciptakan ada umat yang memberi petunjuk dengan hak, dan dengan yang hak itu (pula) mereka menjalankan keadilan.'),
(1136, '007:182', '68', 7, 182, 'Dan orang-orang yang mendustakan ayat-ayat Kami, nanti Kami akan menarik mereka dengan berangaur-angsur (ke arah kebinasaan), dengan cara yang tidak mereka ketahui.'),
(1137, '007:183', '68', 7, 183, 'Dan Aku memberi tangguh kepada mereka. Sesungguhnya rencana-Ku amat teguh.'),
(1138, '007:184', '68', 7, 184, 'Apakah (mereka lalai) dan tidak memikirkan bahwa teman mereka (Muhammad) tidak berpenyakit gila. Dia (Muhammad itu) tidak lain hanyalah seorang pemberi peringatan lagi pemberi penjelasan.'),
(1139, '007:185', '68', 7, 185, 'Dan apakah mereka tidak memperhatikan kerajaan langit dan bumi dan segala sesuatu yang diciptakan Allah, dan kemungkinan telah dekatnya kebinasaan mereka? Maka kepada berita manakah lagi mereka akan beriman sesudah Al Quran itu?'),
(1140, '007:186', '68', 7, 186, 'Barangsiapa yang Allah sesatkan, maka baginya tak ada orang yang akan memberi petunjuk. Dan Allah membiarkan mereka terombang-ambing dalam kesesatan.'),
(1141, '007:187', '68', 7, 187, 'Mereka menanyakan kepadamu tentang kiamat: \"Bilakah terjadinya?\" Katakanlah: \"Sesungguhnya pengetahuan tentang kiamat itu adalah pada sisi Tuhanku; tidak seorangpun yang dapat menjelaskan waktu kedatangannya selain Dia. Kiamat itu amat berat (huru haranya bagi makhluk) yang di langit dan di bumi. Kiamat itu tidak akan datang kepadamu melainkan dengan tiba-tiba\". Mereka bertanya kepadamu seakan-akan kamu benar-benar mengetahuinya. Katakanlah: \"Sesungguhnya pengetahuan tentang bari kiamat itu adalah di sisi Allah, tetapi kebanyakan manusia tidak mengetahui\".'),
(1142, '007:188', '68', 7, 188, 'Katakanlah: \"Aku tidak berkuasa menarik kemanfaatan bagi diriku dan tidak (pula) menolak kemudharatan kecuali yang dikehendaki Allah. Dan sekiranya aku mengetahui yang ghaib, tentulah aku membuat kebajikan sebanyak-banyaknya dan aku tidak akan ditimpa kemudharatan. Aku tidak lain hanyalah pemberi peringatan, dan pembawa berita gembira bagi orang-orang yang beriman\".'),
(1143, '007:189', '68', 7, 189, 'Dialah Yang menciptakan kamu dari diri yang satu dan dari padanya Dia menciptakan isterinya, agar dia merasa senang kepadanya. Maka setelah dicampurinya, isterinya itu mengandung kandungan yang ringan, dan teruslah dia merasa ringan (beberapa waktu). Kemudian tatkala dia merasa berat, keduanya (suami-isteri) bermohon kepada Allah, Tuhannya seraya berkata: \"Sesungguhnya jika Engkau memberi kami anak yang saleh, tentulah kami termasuk orang-orang yang bersyukur\".'),
(1144, '007:190', '68', 7, 190, 'Tatkala Allah memberi kepada keduanya seorang anak yang sempurna, maka keduanya menjadikan sekutu bagi Allah terhadap anak yang telah dianugerahkan-Nya kepada keduanya itu. Maka Maha Tinggi Allah dari apa yang mereka persekutukan.'),
(1145, '007:191', '68', 7, 191, 'Apakah mereka mempersekutukan (Allah dengan) berhada-berhala yang tak dapat menciptakan sesuatupun? Sedangkan berhala-berhala itu sendiri buatan orang.'),
(1146, '007:192', '68', 7, 192, 'Dan berhala-berhala itu tidak mampu memberi pertolongan kepada penyembah-penyembahnya dan kepada dirinya sendiripun berhala-berha]a itu tidak dapat memberi pertolongan.'),
(1147, '007:193', '68', 7, 193, 'Dan jika kamu (hai orang-orang musyrik) menyerunya (berhala) untuk memberi petunjuk kepadamu, tidaklah berhala-berhala itu dapat memperkenankan seruanmu; sama saja (hasilnya) buat kamu menyeru mereka ataupun kamu herdiam diri.'),
(1148, '007:194', '68', 7, 194, 'Sesungguhnya berhala-berhala yang kamu seru selain Allah itu adalah makhluk (yang lemah) yang serupa juga dengan kamu. Maka serulah berhala-berhala itu lalu biarkanlah mereka mmperkenankan permintaanmu, jika kamu memang orang-orang yang benar.'),
(1149, '007:195', '68', 7, 195, 'Apakah berhala-berhala mempunyai kaki yang dengan itu ia dapat berjalan, atau mempunyai tangan yang dengan itu ia dapat memegang dengan keras, atau mempunyai mata yang dengan itu ia dapat melihat, atau mempunyai telinga yang dengan itu ia dapat mendengar? Katakanlah: \"Panggillah berhala-berhalamu yang kamu jadikan sekutu Allah, kemudian lakukanlah tipu daya (untuk mencelakakan)-ku. tanpa memberi tangguh (kepada-ku)\".'),
(1150, '007:196', '68', 7, 196, 'Sesungguhnya pelindungku ialahlah Yang telah menurunkan Al Kitab (Al Quran) dan Dia melindungi orang-orang yang saleh.'),
(1151, '007:197', '68', 7, 197, 'Dan berhala-berhala yang kamu seru selain Allah tidaklah sanggup menolongmu, bahkan tidak dapat menolong dirinya sendiri.'),
(1152, '007:198', '68', 7, 198, 'Dan jika kamu sekalian menyeru (berhala-berhala) untuk memberi petunjuk, niscaya berhala-herhala itu tidak dapat mendengarnya. Dan kamu melihat berhala-berhala itu memandang kepadamu padahal ia tidak melihat.'),
(1153, '007:199', '68', 7, 199, 'Jadilah engkau pemaaf dan suruhlah orang mengerjakan yang ma´ruf, serta berpalinglah dari pada orang-orang yang bodoh.'),
(1154, '007:200', '68', 7, 200, 'Dan jika kamu ditimpa sesuatu godaan syaitan maka berlindunglah kepada Allah'),
(1155, '007:201', '68', 7, 201, 'Sesungguhnya orang-orang yang bertakwa bila mereka ditimpa was-was dari syaitan, mereka ingat kepada Allah, maka ketika itu juga mereka melihat kesalahan-kesalahannya.'),
(1156, '007:202', '68', 7, 202, 'Dan teman-teman mereka (orang-orang kafir dan fasik) membantu syaitan-syaitan dalam menyesatkan dan mereka tidak henti-hentinya (menyesatkan).'),
(1157, '007:203', '68', 7, 203, 'Dan apabila kamu tidak membawa suatu ayat Al Quran kepada mereka, mereka berkata: \"Mengapa tidak kamu buat sendiri ayat itu?\" Katakanlah: \"Sesungguhnya aku hanya mengikut apa yang diwahyukan dari Tuhanku kepadaku. Al Quran ini adalah bukti-bukti yang nyata dari Tuhanmu, petunjuk dan rahmat bagi orang-orang yang beriman\".'),
(1158, '007:204', '68', 7, 204, 'Dan apabila dibacakan Al Quran, maka dengarkanlah baik-baik, dan perhatikanlah dengan tenang agar kamu mendapat rahmat.'),
(1159, '007:205', '68', 7, 205, 'Dan sebutlah (nama) Tuhanmu dalam hatimu dengan merendahkan diri dan rasa takut, dan dengan tidak mengeraskan suara, di waktu pagi dan petang, dan janganlah kamu termasuk orang-orang yang lalai.'),
(1160, '007:206', '68', 7, 206, 'Sesungguhnya malaikat-malaikat yang ada di sisi Tuhanmu tidaklah merasa enggan menyembah Allah dan mereka mentasbihkan-Nya dan hanya kepada-Nya-lah mereka bersujud.'),
(1161, '008:001', '68', 8, 1, 'Mereka menanyakan kepadamu tentang (pembagian) harta rampasan perang. Katakanlah: \"Harta rampasan perang kepunyaan Allah dan Rasul, oleh sebab itu bertakwalah kepada Allah dan perbaikilah perhubungan di antara sesamamu; dan taatlah kepada Allah dan Rasul-Nya jika kamu adalah orang-orang yang beriman\".'),
(1162, '008:002', '68', 8, 2, 'Sesungguhnya orang-orang yang beriman ialah mereka yang bila disebut nama Allah gemetarlah hati mereka, dan apabila dibacakan ayat-ayat-Nya bertambahlah iman mereka (karenanya), dan hanya kepada Tuhanlah mereka bertawakkal.'),
(1163, '008:003', '68', 8, 3, '(yaitu) orang-orang yang mendirikan shalat dan yang menafkahkan sebagian dari rezeki yang Kami berikan kepada mereka.'),
(1164, '008:004', '68', 8, 4, 'Itulah orang-orang yang beriman dengan sebenar-benarnya. Mereka akan memperoleh beberapa derajat ketinggian di sisi Tuhannya dan ampunan serta rezeki (nikmat) yang mulia.'),
(1165, '008:005', '68', 8, 5, 'Sebagaimana Tuhanmu menyuruhmu pergi dan rumahmu dengan kebenaran, padahal sesungguhnya sebagian dari orang-orang yang beriman itu tidak menyukainya,'),
(1166, '008:006', '68', 8, 6, 'mereka membantahmu tentang kebenaran sesudah nyata (bahwa mereka pasti menang), seolah-olah mereka dihalau kepada kematian, sedang mereka melihat (sebab-sebab kematian itu).'),
(1167, '008:007', '68', 8, 7, 'Dan (ingatlah), ketika Allah menjanjikan kepadamu bahwa salah satu dari dua golongan (yang kamu hadapi) adalah untukmu, sedang kamu menginginkan bahwa yang tidak mempunyai kekekuatan senjatalah yang untukmu, dan Allah menghendaki untuk membenarkan yang benar dengan ayat-ayat-Nya dan memusnahkan orang-orang kafir,'),
(1168, '008:008', '68', 8, 8, 'agar Allah menetapkan yang hak (Islam) dan membatalkan yang batil (syirik) walaupun orang-orang yang berdosa (musyrik) itu tidak menyukainya.'),
(1169, '008:009', '68', 8, 9, '(Ingatlah), ketika kamu memohon pertolongan kepada Tuhanmu, lalu diperkenankan-Nya bagimu: \"Sesungguhnya Aku akan mendatangkan bala bantuan kepada kamu dengan seribu malaikat yang datang berturut-turut\".'),
(1170, '008:010', '68', 8, 10, 'Dan Allah tidak menjadikannya (mengirim bala bantuan itu), melainkan sebagai kabar gembira dan agar hatimu menjadi tenteram karenanya. Dan kemenangan itu hanyalah dari sisi Allah. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.'),
(1171, '008:011', '68', 8, 11, '(Ingatlah), ketika Allah menjadikan kamu mengantuk sebagai suatu penenteraman daripada-Nya, dan Allah menurunkan kepadamu hujan dari langit untuk mensucikan kamu dengan hujan itu dan menghilangkan dari kamu gangguan-gangguan syaitan dan untuk menguatkan hatimu dan mesmperteguh dengannya telapak kaki(mu).'),
(1172, '008:012', '68', 8, 12, '(Ingatlah), ketika Tuhanmu mewahyukan kepada para malaikat: \"Sesungguhnya Aku bersama kamu, maka teguhkan (pendirian) orang-orang yang telah beriman\". Kelak akan Aku jatuhkan rasa ketakutan ke dalam hati orang-orang kafir, maka penggallah kepala mereka dan pancunglah tiap-tiap ujung jari mereka.'),
(1173, '008:013', '68', 8, 13, '(Ketentuan) yang demikian itu adalah karena sesungguhnya mereka menentang Allah dan Rasul-Nya; dan barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya Allah amat keras siksaan-Nya.'),
(1174, '008:014', '68', 8, 14, 'Itulah (hukum dunia yang ditimpakan atasmu), maka rasakanlah hukuman itu. Sesungguhnya bagi orang-orang yang kafir itu ada (lagi) azab neraka.'),
(1175, '008:015', '68', 8, 15, 'Hai orang-orang yang beriman, apabila kamu bertemu dengan orang-orang yang kafir yang sedang menyerangmu, maka janganlah kamu membelakangi mereka (mundur).'),
(1176, '008:016', '68', 8, 16, 'Barangsiapa yang membelakangi mereka (mundur) di waktu itu, kecuali berbelok untuk (sisat) perang atau hendak menggabungkan diri dengan pasukan yang lain, maka sesungguhnya orang itu kembali dengan membawa kemurkaan dari Allah, dan tempatnya ialah neraka Jahannam. Dan amat buruklah tempat kembalinya.'),
(1177, '008:017', '68', 8, 17, 'Maka (yang sebenarnya) bukan kamu yang membunuh mereka, akan tetapi Allahlah yang membunuh mereka, dan bukan kamu yang melempar ketika kamu melempar, tetapi Allah-lah yang melempar. (Allah berbuat demikian untuk membinasakan mereka) dan untuk memberi kemenangan kepada orang-orang mukmin, dengan kemenangan yang baik. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(1178, '008:018', '68', 8, 18, 'Itulah (karunia Allah yang dilimpahkan kepadamu), dan sesungguhnya Allah melemahkan tipu daya orang-orang yang kafir.'),
(1179, '008:019', '68', 8, 19, 'Jika kamu (orang-orang musyrikin) mencari keputusan, maka telah datang keputusan kepadamu; dan jika kamu berhenti; maka itulah yang lehih baik bagimu; dan jika kamu kembali, niscaya Kami kembali (pula); dan angkatan perangmu sekali-kali tidak akan dapat menolak dari kamu sesuatu bahayapun, biarpun dia banyak dan sesungguhnya Allah beserta orang-orang yang beriman.'),
(1180, '008:020', '68', 8, 20, 'Hai orang-orang yang beriman, taatlah kepada Allah dan Rasul-Nya, dan janganlah kamu berpaling dari pada-Nya, sedang kamu mendengar (perintah-perintah-Nya),'),
(1181, '008:021', '68', 8, 21, 'dan janganlah kamu menjadi seperti orang-orang (munafik) vang berkata \"Kami mendengarkan, padahal mereka tidak mendengarkan.'),
(1182, '008:022', '68', 8, 22, 'Sesungguhnya binatang (makhluk) yang seburuk-buruknya pada sisi Allah ialah; orang-orang yang pekak dan tuli yang tidak mengerti apa-apapun.'),
(1183, '008:023', '68', 8, 23, 'Kalau sekiranya Allah mengetahui kebaikan ada pada mereka, tentulah Allah menjadikan mereka dapat mendengar. Dan jikalau Allah menjadikan mereka dapat mendengar, niscaya mereka pasti berpaling juga, sedang mereka memalingkan diri (dari apa yang mereka dengar itu).'),
(1184, '008:024', '68', 8, 24, 'Hai orang-orang yang beriman, penuhilah seruan Allah dan seruan Rasul apabila Rasul menyeru kamu kepada suatu yang memberi kehidupan kepada kamu, ketahuilah bahwa sesungguhnya Allah membatasi antara manusia dan hatinya dan sesungguhnya kepada-Nya-lah kamu akan dikumpulkan.'),
(1185, '008:025', '68', 8, 25, 'Dan peliharalah dirimu dari pada siksaan yang tidak khusus menimpa orang-orang yang zalim saja di antara kamu. Dan ketahuilah bahwa Allah amat keras siksaan-Nya.'),
(1186, '008:026', '68', 8, 26, 'Dan ingatlah (hai para muhajirin) ketika kamu masih berjumlah sedikit, lagi tertindas di muka bumi (Mekah), kamu takut orang-orang (Mekah) akan menculik kamu, maka Allah memberi kamu tempat menetap (Madinah) dan dijadikan-Nya kamu kuat dengan pertolongan-Nya dan diberi-Nya kamu rezeki dari yang baik-baik agar kamu bersyukur.'),
(1187, '008:027', '68', 8, 27, 'Hai orang-orang yang beriman, janganlah kamu mengkhianati Allah dan Rasul (Muhammad) dan (juga) janganlah kamu mengkhianati amanat-amanat yang dipercayakan kepadamu, sedang kamu mengetahui.'),
(1188, '008:028', '68', 8, 28, 'Dan ketahuilah, bahwa hartamu dan anak-anakmu itu hanyalah sebagai cobaan dan sesungguhnya di sisi Allah-lah pahala yang besar.'),
(1189, '008:029', '68', 8, 29, 'Hai orang-orang beriman, jika kamu bertakwa kepada Allah, Kami akan memberikan kepadamu Furqaan. Dan kami akan jauhkan dirimu dari kesalahan-kesalahanmu, dan mengampuni (dosa-dosa)mu. Dan Allah mempunyai karunia yang besar.'),
(1190, '008:030', '68', 8, 30, 'Dan (ingatlah), ketika orang-orang kafir (Quraisy) memikirkan daya upaya terhadapmu untuk menangkap dan memenjarakanmu atau membunuhmu, atau mengusirmu. Mereka memikirkan tipu daya dan Allah menggagalkan tipu daya itu. Dan Allah sebaik-baik Pembalas tipu daya.'),
(1191, '008:031', '68', 8, 31, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami, mereka berkata: \"Sesungguhnya kami telah mendengar (ayat-ayat yang seperti ini), kalau kami menhendaki niscaya kami dapat membacakan yang seperti ini, (Al Quran) ini tidak lain hanyalah dongeng-dongengan orang-orang purbakala\".'),
(1192, '008:032', '68', 8, 32, 'Dan (ingatlah), ketika mereka (orang-orang musyrik) berkata: \"Ya Allah, jika betul (Al Quran) ini, dialah yang benar dari sisi Engkau, maka hujanilah kami dengan batu dari langit, atau datangkanlah kepada kami azab yang pedih\".'),
(1193, '008:033', '68', 8, 33, 'Dan Allah sekali-kali tidak akan mengazab mereka, sedang kamu berada di antara mereka. Dan tidaklah (pula) Allah akan mengazab mereka, sedang mereka meminta ampun'),
(1194, '008:034', '68', 8, 34, 'Kenapa Allah tidak mengazab mereka padahal mereka menghalangi orang untuk (mendatangi) Masjidilharam, dan mereka bukanlah orang-orang yang berhak menguasainya? Orang-orang yang berhak menguasai(nya) hanyalah orang-orang yang bertakwa. tetapi kebanyakan mereka tidak mengetahui.'),
(1195, '008:035', '68', 8, 35, 'Sembahyang mereka di sekitar Baitullah itu, lain tidak hanyalah siulan dan tepukan tangan. Maka rasakanlah azab disebabkan kekafiranmu itu.'),
(1196, '008:036', '68', 8, 36, 'Sesungguhnya orang-orang yang kafir menafkahkan harta mereka untuk menghalangi (orang) dari jalan Allah. Mereka akan menafkahkan harta itu, kemudian menjadi sesalan bagi mereka, dan mereka akan dikalahkan. Dan ke dalam Jahannamlah orang-orang yang kafir itu dikumpulkan,'),
(1197, '008:037', '68', 8, 37, 'supaya Allah memisahkan (golongan) yang buruk dari yang baik dan menjadikan (golongan) yang buruk itu sebagiannya di atas sebagian yang lain, lalu kesemuanya ditumpukkan-Nya, dan dimasukkan-Nya ke dalam neraka Jahannam. Mereka itulah orang-orang yang merugi.'),
(1198, '008:038', '68', 8, 38, 'Katakanlah kepada orang-orang yang kafir itu: \"Jika mereka berhenti (dari kekafirannya), niscaya Allah akan mengampuni mereka tentang dosa-dosa mereka yang sudah lalu; dan jika mereka kembali lagi sesungguhnya akan berlaku (kepada mereka) sunnah (Allah tenhadap) orang-orang dahulu\".'),
(1199, '008:039', '68', 8, 39, 'Dan perangilah mereka, supaya jangan ada fitnah dan supaya agama itu semata-mata untuk Allah. Jika mereka berhenti (dari kekafiran), maka sesungguhnya Allah Maha Melihat apa yang mereka kerjakan.'),
(1200, '008:040', '68', 8, 40, 'Dan jika mereka berpaling, maka ketahuilah bahwasanya Allah Pelindungmu. Dia adalah sebaik-baik Pelindung dan sebaik-baik Penolong.'),
(1201, '008:041', '68', 8, 41, 'Ketahuilah, sesungguhnya apa saja yang dapat kamu peroleh sebagai rampasan perang, maka sesungguhnya seperlima untuk Allah, Rasul, kerabat Rasul, anak-anak yatim, orang-orang miskin dan ibnussabil, jika kamu beriman kepada Allah dan kepada apa yang kami turunkan kepada hamba Kami (Muhammad) di hari Furqaan, yaitu di hari bertemunya dua pasukan. Dan Allah Maha Kuasa atas segala sesuatu.'),
(1202, '008:042', '68', 8, 42, '(Yaitu di hari) ketika kamu berada di pinggir lembah yang dekat dan mereka berada di pinggir lembah yang jauh sedang kafilah itu berada di bawah kamu. Sekiranya kamu mengadakan persetujuan (untuk menentukan hari pertempuran), pastilah kamu tidak sependapat dalam menentukan hari pertempuran itu, akan tetapi (Allah mempertemukan dua pasukan itu) agar Dia melakukan suatu urusan yang mesti dilaksanakan, yaitu agar orang yang binasa itu binasanya dengan keterangan yang nyata dan agar orang yang hidup itu hidupnya dengan keterangan yang nyata (pula). Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui,'),
(1203, '008:043', '68', 8, 43, '(yaitu) ketika Allah menampakkan mereka kepadamu di dalam mimpimu (berjumlah) sedikit. Dan sekiranya Allah memperlihatkan mereka kepada kamu (berjumlah) banyak tentu saja kamu menjadi gentar dan tentu saja kamu akan berbantah-bantahan dalam urusan itu, akan tetapi Allah telah menyelamatkan kamu. Sesungguhnya Allah Maha Mengetahui segala isi hati.'),
(1204, '008:044', '68', 8, 44, 'Dan ketika Allah menampakkan mereka kepada kamu sekalian, ketika kamu berjumpa dengan mereka berjumlah sedikit pada penglihatan matamu dan kamu ditampakkan-Nya berjumlah sedikit pada penglihatan mata mereka, karena Allah hendak melakukan suatu urusan yang mesti dilaksanakan. Dan hanyalah kepada Allahlah dikembalikan segala urusan.'),
(1205, '008:045', '68', 8, 45, 'Hai orang-orang yang beriman. apabila kamu memerangi pasukan (musuh), maka berteguh hatilah kamu dan sebutlah (nama) Allah sebanyak-banyaknya agar kamu beruntung.'),
(1206, '008:046', '68', 8, 46, 'Dan taatlah kepada Allah dan Rasul-Nya dan janganlah kamu berbantah-bantahan, yang menyebabkan kamu menjadi gentar dan hilang kekuatanmu dan bersabarlah. Sesungguhnya Allah beserta orang-orang yang sabar.'),
(1207, '008:047', '68', 8, 47, 'Dan janganlah kamu menjadi seperti orang-orang yang keluar dari kampungnya dengan rasa angkuh dan dengan maksud riya´ kepada manusia serta menghalangi (orang) dari jalan Allah. Dan (ilmu) Allah meliputi apa yang mereka kerjakan.'),
(1208, '008:048', '68', 8, 48, 'Dan ketika syaitan menjadikan mereka memandang baik pekerjaan mereka dan mengatakan: \"Tidak ada seorang manusiapun yang dapat menang terhadapmu pada hari ini, dan sesungguhnya saya ini adalah pelindungmu\". Maka tatkala kedua pasukan itu telah dapat saling lihat melihat (berhadapan), syaitan itu balik ke belakang seraya berkata: \"Sesungguhnya saya berlepas diri daripada kamu, sesungguhnya saya dapat melihat apa yang kamu sekalian tidak dapat melihat; sesungguhnya saya takut kepada Allah\". Dan Allah sangat keras siksa-Nya.'),
(1209, '008:049', '68', 8, 49, '(Ingatlah), ketika orang-orang munafik dan orang-orang yang ada penyakit di dalam hatinya berkata: \"Mereka itu (orang-orang mukmin) ditipu oleh agamanya\". (Allah berfirman): \"Barangsiapa yang bertawakkal kepada Allah, maka sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana\".'),
(1210, '008:050', '68', 8, 50, 'Kalau kamu melihat ketika para malaikat mencabut jiwa orang-orang yang kafir seraya memukul muka dan belakang mereka (dan berkata): \"Rasakanlah olehmu siksa neraka yang membakar\", (tentulah kamu akan merasa ngeri).'),
(1211, '008:051', '68', 8, 51, 'Demikian itu disebabkan oleh perbuatan tanganmu sendiri. Sesungguhnya Allah sekali-kali tidak menganiaya hamba-Nya,'),
(1212, '008:052', '68', 8, 52, '(keadaan mereka) serupa dengan keadaan Fir´aun dan pengikut-pengikutnya serta orang-orang yang sebelumnya. Mereka mengingkari ayat-ayat Allah, maka Allah menyiksa mereka disebabkan dosa-dosanya. Sesungguhnya Allah Maha Kuat lagi amat keras siksaan-Nya.'),
(1213, '008:053', '68', 8, 53, '(Siksaan) yang demikian itu adalah karena sesungguhnya Allah sekali-kali tidak akan meubah sesuatu nikmat yang telah dianugerahkan-Nya kepada suatu kaum, hingga kaum itu meubah apa-apa yang ada pada diri mereka sendiri, dan sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(1214, '008:054', '68', 8, 54, '(keadaan mereka) serupa dengan keadaan Fir´aun dan pengikut-pengikutnya serta orang-orang yang sebelumnya. Mereka mendustakan ayat-ayat Tuhannya maka Kami membinasakan mereka disebabkan dosa-dosanya dan Kami tenggelamkan Fir´aun dan pengikut-pengikutnya; dan kesemuanya adalah orang-orang yang zalim.'),
(1215, '008:055', '68', 8, 55, 'Sesungguhnya binatang (makhluk) yang paling buruk di sisi Allah ialah orang-orang yang kafir, karena mereka itu tidak beriman.'),
(1216, '008:056', '68', 8, 56, '(Yaitu) orang-orang yang kamu telah mengambil perjanjian dari mereka, sesudah itu mereka mengkhianati janjinya pada setiap kalinya, dan mereka tidak takut (akibat-akibatnya).'),
(1217, '008:057', '68', 8, 57, 'Jika kamu menemui mereka dalam peperangan, maka cerai beraikanlah orang-orang yang di belakang mereka dengan (menumpas) mereka, supaya mereka mengambil pelajaran.'),
(1218, '008:058', '68', 8, 58, 'Dan jika kamu khawatir akan (terjadinya) pengkhianatan dari suatu golongan, maka kembalikanlah perjanjian itu kepada mereka dengan cara yang jujur. Sesungguhnya Allah tidak menyukai orang-orang yang berkhianat.'),
(1219, '008:059', '68', 8, 59, 'Dan janganlah orang-orang yang kafir itu mengira, bahwa mereka akan dapat lolos (dari kekuasaan Allah). Sesungguhnya mereka tidak dapat melemahkan (Allah).'),
(1220, '008:060', '68', 8, 60, 'Dan siapkanlah untuk menghadapi mereka kekuatan apa saja yang kamu sanggupi dan dari kuda-kuda yang ditambat untuk berperang (yang dengan persiapan itu) kamu menggentarkan musuh Allah dan musuhmu dan orang orang selain mereka yang kamu tidak mengetahuinya; sedang Allah mengetahuinya. Apa saja yang kamu nafkahkan pada jalan Allah niscaya akan dibalasi dengan cukup kepadamu dan kamu tidak akan dianiaya (dirugikan).'),
(1221, '008:061', '68', 8, 61, 'Dan jika mereka condong kepada perdamaian, maka condonglah kepadanya dan bertawakkallah kepada Allah. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui.'),
(1222, '008:062', '68', 8, 62, 'Dan jika mereka bermaksud menipumu, maka sesungguhnya cukuplah Allah (menjadi pelindungmu). Dialah yang memperkuatmu dengan pertolongan-Nya dan dengan para mukmin,'),
(1223, '008:063', '68', 8, 63, 'dan Yang mempersatukan hati mereka (orang-orang yang beriman). Walaupun kamu membelanjakan semua (kekayaan) yang berada di bumi, niscaya kamu tidak dapat mempersatukan hati mereka, akan tetapi Allah telah mempersatukan hati mereka. Sesungguhnya Dia Maha Gagah lagi Maha Bijaksana.'),
(1224, '008:064', '68', 8, 64, 'Hai Nabi, cukuplah Allah (menjadi Pelindung) bagimu dan bagi orang-orang mukmin yang mengikutimu.'),
(1225, '008:065', '68', 8, 65, 'Hai Nabi, kobarkanlah semangat para mukmin untuk berperang. Jika ada dua puluh orang yang sabar diantaramu, niscaya mereka akan dapat mengalahkan dua ratus orang musuh. Dan jika ada seratus orang yang sabar diantaramu, niscaya mereka akan dapat mengalahkan seribu dari pada orang kafir, disebabkan orang-orang kafir itu kaum yang tidak mengerti.'),
(1226, '008:066', '68', 8, 66, 'Sekarang Allah telah meringankan kepadamu dan dia telah mengetahui bahwa padamu ada kelemahan. Maka jika ada diantaramu seratus orang yang sabar, niscaya mereka akan dapat mengalahkan dua ratus orang kafir; dan jika diantaramu ada seribu orang (yang sabar), niscaya mereka akan dapat mengalahkan dua ribu orang, dengan seizin Allah. Dan Allah beserta orang-orang yang sabar.'),
(1227, '008:067', '68', 8, 67, 'Tidak patut, bagi seorang Nabi mempunyai tawanan sebelum ia dapat melumpuhkan musuhnya di muka bumi. Kamu menghendaki harta benda duniawiyah sedangkan Allah menghendaki (pahala) akhirat (untukmu). Dan Allah Maha Perkasa lagi Maha Bijaksana.'),
(1228, '008:068', '68', 8, 68, 'Kalau sekiranya tidak ada ketetapan yang telah terdahulu dari Allah, niscaya kamu ditimpa siksaan yang besar karena tebusan yang kamu ambil.'),
(1229, '008:069', '68', 8, 69, 'Maka makanlah dari sebagian rampasan perang yang telah kamu ambil itu, sebagai makanan yang halal lagi baik, dan bertakwalah kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(1230, '008:070', '68', 8, 70, 'Hai Nabi, katakanlah kepada tawanan-tawanan yang ada di tanganmu: \"Jika Allah mengetahui ada kebaikan dalam hatimu, niscaya Dia akan memberikan kepadamu yang lebih baik dari apa yang telah diambil daripadamu dan Dia akan mengampuni kamu\". Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(1231, '008:071', '68', 8, 71, 'Akan tetapi jika mereka (tawanan-tawanan itu) bermaksud hendak berkhianat kepadamu, maka sesungguhnya mereka telah berkhianat kepada Allah sebelum ini, lalu Allah menjadikan(mu) berkuasa terhadap mereka. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(1232, '008:072', '68', 8, 72, 'Sesungguhnya orang-orang yang beriman dan berhijrah serta berjihad dengan harta dan jiwanya pada jalan Allah dan orang-orang yang memberikan tempat kediaman dan pertoIongan (kepada orang-orang muhajirin), mereka itu satu sama lain lindung-melindungi. Dan (terhadap) orang-orang yang beriman, tetapi belum berhijrah, maka tidak ada kewajiban sedikitpun atasmu melindungi mereka, sebelum mereka berhijrah. (Akan tetapi) jika mereka meminta pertolongan kepadamu dalam (urusan pembelaan) agama, maka kamu wajib memberikan pertolongan kecuali terhadap kaum yang telah ada perjanjian antara kamu dengan mereka. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
(1233, '008:073', '68', 8, 73, 'Adapun orang-orang yang kafir, sebagian mereka menjadi pelindung bagi sebagian yang lain. Jika kamu (hai para muslimin) tidak melaksanakan apa yang telah diperintahkan Allah itu, niscaya akan terjadi kekacauan di muka bumi dan kerusakan yang besar.'),
(1234, '008:074', '68', 8, 74, 'Dan orang-orang yang beriman dan berhijrah serta berjihad pada jalan Allah, dan orang-orang yang memberi tempat kediaman dan memberi pertolongan (kepada orang-orang muhajirin), mereka itulah orang-orang yang benar-benar beriman. Mereka memperoleh ampunan dan rezeki (nikmat) yang mulia.'),
(1235, '008:075', '68', 8, 75, 'Dan orang-orang yang beriman sesudah itu kemudian berhijrah serta berjihad bersamamu maka orang-orang itu termasuk golonganmu (juga). Orang-orang yang mempunyai hubungan kerabat itu sebagiannya lebih berhak terhadap sesamanya (daripada yang bukan kerabat) di dalam kitab Allah. Sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
(1236, '009:001', '68', 9, 1, '(Inilah pernyataan) pemutusan hubungan dari Allah dan Rasul-Nya (yang dihadapkan) kepada orang-orang musyrikin yang kamu (kaum muslimin) telah mengadakan perjanjian (dengan mereka).'),
(1237, '009:002', '68', 9, 2, 'Maka berjalanlah kamu (kaum musyrikin) di muka bumi selama empat bulan dan ketahuilah bahwa sesungguhnya kamu tidak akan dapat melemahkan Allah, dan sesungguhnya Allah menghinakan orang-orang kafir.'),
(1238, '009:003', '68', 9, 3, 'Dan (inilah) suatu permakluman daripada Allah dan Rasul-Nya kepada umat manusia pada hari haji akbar bahwa sesungguhnya Allah dan Rasul-Nya berlepas diri dari orang-orang musyrikin. Kemudian jika kamu (kaum musyrikin) bertobat, maka bertaubat itu lebih baik bagimu; dan jika kamu berpaling, maka ketahuilah bahwa sesungguhnya kamu tidak dapat melemahkan Allah. Dan beritakanlah kepada orang-orang kafir (bahwa mereka akan mendapat) siksa yang pedih.'),
(1239, '009:004', '68', 9, 4, 'kecuali orang-orang musyrikin yang kamu telah mengadakan perjanjian (dengan mereka) dan mereka tidak mengurangi sesuatu pun (dari isi perjanjian)mu dan tidak (pula) mereka membantu seseorang yang memusuhi kamu, maka terhadap mereka itu penuhilah janjinya sampai batas waktunya. Sesungguhnya Allah menyukai orang-orang yang bertakwa.'),
(1240, '009:005', '68', 9, 5, 'Apabila sudah habis bulan-bulan Haram itu, maka bunuhlah orang-orang musyrikin itu dimana saja kamu jumpai mereka, dan tangkaplah mereka. Kepunglah mereka dan intailah ditempat pengintaian. Jika mereka bertaubat dan mendirikan sholat dan menunaikan zakat, maka berilah kebebasan kepada mereka untuk berjalan. Sesungguhnya Allah Maha Pengampun lagi maha Penyayang.'),
(1241, '009:006', '68', 9, 6, 'Dan jika seorang diantara orang-orang musyrikin itu meminta perlindungan kepadamu, maka lindungilah ia supaya ia sempat mendengar firman Allah, kemudian antarkanlah ia ketempat yang aman baginya. Demikian itu disebabkan mereka kaum yang tidak mengetahui.'),
(1242, '009:007', '68', 9, 7, 'Bagaimana bisa ada perjanjian (aman) dari sisi Allah dan Rasul-Nya dengan orang-orang musyrikin, kecuali orang-orang yang kamu telah mengadakan perjanjian (dengan mereka) di dekat Masjidil haraam? maka selama mereka berlaku lurus terhadapmu, hendaklah kamu berlaku lurus (pula) terhadap mereka. Sesungguhnya Allah menyukai orang-orang yang bertakwa.'),
(1243, '009:008', '68', 9, 8, 'Bagaimana bisa (ada perjanjian dari sisi Allah dan Rasul-Nya dengan orang-orang musyrikin), padahal jika mereka memperoleh kemenangan terhadap kamu, mereka tidak memelihara hubungan kekerabatan terhadap kamu dan tidak (pula mengindahkan) perjanjian. Mereka menyenangkan hatimu dengan mulutnya, sedang hatinya menolak. Dan kebanyakan mereka adalah orang-orang yang fasik (tidak menepati perjanjian).'),
(1244, '009:009', '68', 9, 9, 'Mereka menukarkan ayat-ayat Allah dengan harga yang sedikit, lalu mereka menghalangi (manusia) dari jalan Allah. Sesungguhnya amat buruklah apa yang mereka kerjakan itu.'),
(1245, '009:010', '68', 9, 10, 'Mereka tidak memelihara (hubungan) kerabat terhadap orang-orang mukmin dan tidak (pula mengindahkan) perjanjian. Dan mereka itulah orang-orang yang melampaui batas.'),
(1246, '009:011', '68', 9, 11, 'Jika mereka bertaubat, mendirikan sholat dan menunaikan zakat, maka (mereka itu) adalah saudara-saudaramu seagama. Dan Kami menjelaskan ayat-ayat itu bagi kaum yang mengetahui.'),
(1247, '009:012', '68', 9, 12, 'Jika mereka merusak sumpah (janji)nya sesudah mereka berjanji, dan mereka mencerca agamamu, maka perangilah pemimpin-pemimpin orang-orang kafir itu, karena sesungguhnya mereka itu adalah orang-orang (yang tidak dapat dipegang) janjinya, agar supaya mereka berhenti.'),
(1248, '009:013', '68', 9, 13, 'Mengapakah kamu tidak memerangi orang-orang yang merusak sumpah (janjinya), padahal mereka telah keras kemauannya untuk mengusir Rasul dan merekalah yang pertama mulai memerangi kamu? Mengapakah kamu takut kepada mereka padahal Allah-lah yang berhak untuk kamu takuti, jika kamu benar-benar orang yang beriman.'),
(1249, '009:014', '68', 9, 14, 'Perangilah mereka, niscaya Allah akan menghancurkan mereka dengan (perantaraan) tangan-tanganmu dan Allah akan menghinakan mereka dan menolong kamu terhadap mereka, serta melegakan hati orang-orang yang beriman.'),
(1250, '009:015', '68', 9, 15, 'dan menghilangkan panas hati orang-orang mukmin. Dan Allah menerima taubat orang yang dikehendaki-Nya. Allah maha Mengetahui lagi Maha Bijaksana.'),
(1251, '009:016', '68', 9, 16, 'Apakah kamu mengira bahwa kamu akan dibiarkan, sedang Allah belum mengetahui (dalam kenyataan) orang-orang yang berjihad di antara kamu dan tidak mengambil menjadi teman yang setia selain Allah, Rasul-Nya dan orang-orang yang beriman. Dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(1252, '009:017', '68', 9, 17, 'Tidaklah pantas orang-orang musyrik itu memakmurkan mesjid-mesjid Allah, sedang mereka mengakui bahwa mereka sendiri kafir. Itulah orang-orang yang sia-sia pekerjaannya, dan mereka kekal di dalam neraka.'),
(1253, '009:018', '68', 9, 18, 'Hanya yang memakmurkan masjid-masjid Allah ialah orang-orang yang beriman kepada Allah dan Hari kemudian, serta tetap mendirikan shalat, menunaikan zakat dan tidak takut (kepada siapapun) selain kepada Allah, maka merekalah orang-orang yang diharapkan termasuk golongan orang-orang yang mendapat petunjuk.'),
(1254, '009:019', '68', 9, 19, 'Apakah (orang-orang) yang memberi minuman orang-orang yang mengerjakan haji dan mengurus Masjidilharam kamu samakan dengan orang-orang yang beriman kepada Allah dan hari kemudian serta bejihad di jalan Allah? Mereka tidak sama di sisi Allah; dan Allah tidak memberi petunjuk kepada kaum yang zalim.'),
(1255, '009:020', '68', 9, 20, 'orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah dengan harta, benda dan diri mereka, adalah lebih tinggi derajatnya di sisi Allah; dan itulah orang-orang yang mendapat kemenangan.'),
(1256, '009:021', '68', 9, 21, 'Tuhan mereka menggembirakan mereka dengan memberikan rahmat dari pada-Nya, keridhaan dan surga, mereka memperoleh didalamnya kesenangan yang kekal,'),
(1257, '009:022', '68', 9, 22, 'mereka kekal di dalamnya selama-lamanya. Sesungguhnya di sisi Allah-lah pahala yang besar.'),
(1258, '009:023', '68', 9, 23, 'Hai orang-orang beriman, janganlah kamu jadikan bapa-bapa dan saudara-saudaramu menjadi wali(mu), jika mereka lebih mengutamakan kekafiran atas keimanan dan siapa di antara kamu yang menjadikan mereka wali, maka mereka itulah orang-orang yang zalim.'),
(1259, '009:024', '68', 9, 24, 'Katakanlah: \"jika bapa-bapa, anak-anak, saudara-saudara, isteri-isteri, kaum keluargamu, harta kekayaan yang kamu usahakan, perniagaan yang kamu khawatiri kerugiannya, dan tempat tinggal yang kamu sukai, adalah lebih kamu cintai dari Allah dan Rasul-Nya dan dari berjihad di jalan-Nya, maka tunggulah sampai Allah mendatangkan keputusan-Nya\". Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik.'),
(1260, '009:025', '68', 9, 25, 'Sesungguhnya Allah telah menolong kamu (hai para mukminin) di medan peperangan yang banyak, dan (ingatlah) peperangan Hunain, yaitu diwaktu kamu menjadi congkak karena banyaknya jumlah(mu), maka jumlah yang banyak itu tidak memberi manfaat kepadamu sedikitpun, dan bumi yang luas itu telah terasa sempit olehmu, kemudian kamu lari kebelakang dengan bercerai-berai.'),
(1261, '009:026', '68', 9, 26, 'Kemudian Allah menurunkan ketenangan kepada Rasul-Nya dan kepada orang-orang yang beriman, dan Allah menurunkan bala tentara yang kamu tiada melihatnya, dan Allah menimpakan bencana kepada orang-orang yang kafir, dan demikianlah pembalasan kepada orang-orang yang kafir.'),
(1262, '009:027', '68', 9, 27, 'Sesudah itu Allah menerima taubat dari orang-orang yang dikehendaki-Nya. Allah Maha Pengampun lagi Maha Penyayang.'),
(1263, '009:028', '68', 9, 28, 'Hai orang-orang yang beriman, Sesungguhnya orang-orang yang musyrik itu najis, maka janganlah mereka mendekati Masjidilharam sesudah tahun ini. Dan jika kamu khawatir menjadi miskin, maka Allah nanti akan memberimu kekayaan kepadamu dari karunia-Nya, jika Dia menghendaki. Sesungguhnya Allah Maha Mengetahui lagi Maha Bijaksana.'),
(1264, '009:029', '68', 9, 29, 'Perangilah orang-orang yang tidak beriman kepada Allah dan tidak (pula) kepada hari kemudian, dan mereka tidak mengharamkan apa yang diharamkan oleh Allah dan Rasul-Nya dan tidak beragama dengan agama yang benar (agama Allah), (yaitu orang-orang) yang diberikan Al-Kitab kepada mereka, sampai mereka membayar jizyah dengan patuh sedang mereka dalam keadaan tunduk.'),
(1265, '009:030', '68', 9, 30, 'Orang-orang Yahudi berkata: \"Uzair itu putera Allah\" dan orang-orang Nasrani berkata: \"Al Masih itu putera Allah\". Demikianlah itu ucapan mereka dengan mulut mereka, mereka meniru perkataan orang-orang kafir yang terdahulu. Dilaknati Allah mereka , bagaimana mereka sampai berpaling?'),
(1266, '009:031', '68', 9, 31, 'Mereka menjadikan orang-orang alimnya dan rahib-rahib mereka sebagai tuhan selain Allah dan (juga mereka mempertuhankan) Al Masih putera Maryam, padahal mereka hanya disuruh menyembah Tuhan yang Esa, tidak ada Tuhan (yang berhak disembah) selain Dia. Maha suci Allah dari apa yang mereka persekutukan.'),
(1267, '009:032', '68', 9, 32, 'Mereka berkehendak memadamkan cahaya (agama) Allah dengan mulut (ucapan-ucapan) mereka, dan Allah tidak menghendaki selain menyempurnakan cahaya-Nya, walaupun orang-orang yang kafir tidak menyukai.'),
(1268, '009:033', '68', 9, 33, 'Dialah yang telah mengutus Rasul-Nya (dengan membawa) petunjuk (Al-Quran) dan agama yang benar untuk dimenangkan-Nya atas segala agama, walaupun orang-orang musyrikin tidak menyukai.'),
(1269, '009:034', '68', 9, 34, 'Hai orang-orang yang beriman, sesungguhnya sebahagian besar dari orang-orang alim Yahudi dan rahib-rahib Nasrani benar-benar memakan harta orang dengan jalan batil dan mereka menghalang-halangi (manusia) dari jalan Allah. Dan orang-orang yang menyimpan emas dan perak dan tidak menafkahkannya pada jalan Allah, maka beritahukanlah kepada mereka, (bahwa mereka akan mendapat) siksa yang pedih,'),
(1270, '009:035', '68', 9, 35, 'pada hari dipanaskan emas perak itu dalam neraka jahannam, lalu dibakar dengannya dahi mereka, lambung dan punggung mereka (lalu dikatakan) kepada mereka: \"Inilah harta bendamu yang kamu simpan untuk dirimu sendiri, maka rasakanlah sekarang (akibat dari) apa yang kamu simpan itu\".'),
(1271, '009:036', '68', 9, 36, 'Sesungguhnya bilangan bulan pada sisi Allah adalah dua belas bulan, dalam ketetapan Allah di waktu Dia menciptakan langit dan bumi, di antaranya empat bulan haram. Itulah (ketetapan) agama yang lurus, maka janganlah kamu menganiaya diri kamu dalam bulan yang empat itu, dan perangilah kaum musyrikin itu semuanya sebagaimana merekapun memerangi kamu semuanya, dan ketahuilah bahwasanya Allah beserta orang-orang yang bertakwa.'),
(1272, '009:037', '68', 9, 37, 'Sesungguhnya mengundur-undurkan bulan haram itu adalah menambah kekafiran. Disesatkan orang-orang yang kafir dengan mengundur-undurkan itu, mereka menghalalkannya pada suatu tahun dan mengharamkannya pada tahun yang lain, agar mereka dapat mempersesuaikan dengan bilangan yang Allah mengharamkannya, maka mereka menghalalkan apa yang diharamkan Allah. (Syaitan) menjadikan mereka memandang perbuatan mereka yang buruk itu. Dan Allah tidak memberi petunjuk kepada orang-orang yang kafir.'),
(1273, '009:038', '68', 9, 38, 'Hai orang-orang yang beriman, apakah sebabnya bila dikatakan kepadamu: \"Berangkatlah (untuk berperang) pada jalan Allah\" kamu merasa berat dan ingin tinggal di tempatmu? Apakah kamu puas dengan kehidupan di dunia sebagai ganti kehidupan di akhirat? Padahal kenikmatan hidup di dunia ini (dibandingkan dengan kehidupan) diakhirat hanyalah sedikit.'),
(1274, '009:039', '68', 9, 39, 'Jika kamu tidak berangkat untuk berperang, niscaya Allah menyiksa kamu dengan siksa yang pedih dan digantinya (kamu) dengan kaum yang lain, dan kamu tidak akan dapat memberi kemudharatan kepada-Nya sedikitpun. Allah Maha Kuasa atas segala sesuatu.'),
(1275, '009:040', '68', 9, 40, 'Jikalau kamu tidak menolongnya (Muhammad) maka sesungguhnya Allah telah menolongnya (yaitu) ketika orang-orang kafir (musyrikin Mekah) mengeluarkannya (dari Mekah) sedang dia salah seorang dari dua orang ketika keduanya berada dalam gua, di waktu dia berkata kepada temannya: \"Janganlah kamu berduka cita, sesungguhnya Allah beserta kita\". Maka Allah menurunkan keterangan-Nya kepada (Muhammad) dan membantunya dengan tentara yang kamu tidak melihatnya, dan Al-Quran menjadikan orang-orang kafir itulah yang rendah. Dan kalimat Allah itulah yang tinggi. Allah Maha Perkasa lagi Maha Bijaksana.'),
(1276, '009:041', '68', 9, 41, 'Berangkatlah kamu baik dalam keadaan merasa ringan maupun berat, dan berjihadlah kamu dengan harta dan dirimu di jalan Allah. Yang demikian itu adalah lebih baik bagimu, jika kamu mengetahui.'),
(1277, '009:042', '68', 9, 42, 'Kalau yang kamu serukan kepada mereka itu keuntungan yang mudah diperoleh dan perjalanan yang tidak seberapa jauh, pastilah mereka mengikutimu, tetapi tempat yang dituju itu amat jauh terasa oleh mereka. Mereka akan bersumpah dengan (nama) Allah: \"Jikalau kami sanggup tentulah kami berangkat bersama-samamu\". Mereka membinasakan diri mereka sendiri dan Allah mengetahui bahwa sesungguhnya mereka benar-benar orang-orang yang berdusta.'),
(1278, '009:043', '68', 9, 43, 'Semoga Allah memaafkanmu. Mengapa kamu memberi izin kepada mereka (untuk tidak pergi berperang), sebelum jelas bagimu orang-orang yang benar (dalam keuzurannya) dan sebelum kamu ketahui orang-orang yang berdusta?'),
(1279, '009:044', '68', 9, 44, 'Orang-orang yang beriman kepada Allah dan hari kemudian, tidak akan meminta izin kepadamu untuk tidak ikut berjihad dengan harta dan diri mereka. Dan Allah mengetahui orang-orang yang bertakwa.'),
(1280, '009:045', '68', 9, 45, 'Sesungguhnya yang akan meminta izin kepadamu, hanyalah orang-orang yang tidak beriman kepada Allah dan hari kemudian, dan hati mereka ragu-ragu, karena itu mereka selalu bimbang dalam keraguannya.'),
(1281, '009:046', '68', 9, 46, 'Dan jika mereka mau berangkat, tentulah mereka menyiapkan persiapan untuk keberangkatan itu, tetapi Allah tidak menyukai keberangkatan mereka, maka Allah melemahkan keinginan mereka. dan dikatakan kepada mereka: \"Tinggallah kamu bersama orang-orang yang tinggal itu\".'),
(1282, '009:047', '68', 9, 47, 'Jika mereka berangkat bersama-sama kamu, niscaya mereka tidak menambah kamu selain dari kerusakan belaka, dan tentu mereka akan bergegas maju ke muka di celah-celah barisanmu, untuk mengadakan kekacauan di antara kamu; sedang di antara kamu ada orang-orang yang amat suka mendengarkan perkataan mereka. Dan Allah mengetahui orang-orang yang zalim.'),
(1283, '009:048', '68', 9, 48, 'Sesungguhnya dari dahulupun mereka telah mencari-cari kekacauan dan mereka mengatur pelbagai macam tipu daya untuk (merusakkan)mu, hingga datanglah kebenaran (pertolongan Allah) dan menanglah agama Allah, padahal mereka tidak menyukainya.'),
(1284, '009:049', '68', 9, 49, 'Di antara mereka ada orang yang berkata: \"Berilah saya keizinan (tidak pergi berperang) dan janganlah kamu menjadikan saya terjerumus dalam fitnah\". Ketahuilah bahwa mereka telah terjerumus ke dalam fitnah. Dan sesungguhnya Jahannam itu benar-benar meliputi orang-orang yang kafir.'),
(1285, '009:050', '68', 9, 50, 'Jika kamu mendapat suatu kebaikan, mereka menjadi tidak senang karenanya; dan jika kamu ditimpa oleh sesuatu bencana, mereka berkata: \"Sesungguhnya kami sebelumnya telah memperhatikan urusan kami (tidak pergi perang)\" dan mereka berpaling dengan rasa gembira.'),
(1286, '009:051', '68', 9, 51, 'Katakanlah: \"Sekali-kali tidak akan menimpa kami melainkan apa yang telah ditetapkan Allah untuk kami. Dialah Pelindung kami, dan hanya kepada Allah orang-orang yang beriman harus bertawakal\".'),
(1287, '009:052', '68', 9, 52, 'Katakanlah: \"tidak ada yang kamu tunggu-tunggu bagi kami, kecuali salah satu dari dua kebaikan. Dan Kami menunggu-nunggu bagi kamu bahwa Allah akan menimpakan kepadamu azab (yang besar) dari sisi-Nya. Sebab itu tunggulah, sesungguhnya kami menunggu-nunggu bersamamu\".'),
(1288, '009:053', '68', 9, 53, 'Katakanlah: \"Nafkahkanlah hartamu, baik dengan sukarela ataupun dengan terpaksa, namun nafkah itu sekali-kali tidak akan diterima dari kamu. Sesungguhnya kamu adalah orang-orang yang fasik.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1289, '009:054', '68', 9, 54, 'Dan tidak ada yang menghalangi mereka untuk diterima dari mereka nafkah-nafkahnya melainkan karena mereka kafir kepada Allah dan Rasul-Nya dan mereka tidak mengerjakan sembahyang, melainkan dengan malas dan tidak (pula) menafkahkan (harta) mereka, melainkan dengan rasa enggan.'),
(1290, '009:055', '68', 9, 55, 'Maka janganlah harta benda dan anak-anak mereka menarik hatimu. Sesungguhnya Allah menghendaki dengan (memberi) harta benda dan anak-anak itu untuk menyiksa mereka dalam kehidupan di dunia dan kelak akan melayang nyawa mereka, sedang mereka dalam keadaan kafir.'),
(1291, '009:056', '68', 9, 56, 'Dan mereka (orang-orang munafik) bersumpah dengan (nama) Allah, bahwa sesungguhnya mereka termasuk golonganmu; padahal mereka bukanlah dari golonganmu, akan tetapi mereka adalah orang-orang yang sangat takut (kepadamu).'),
(1292, '009:057', '68', 9, 57, 'Jikalau mereka memperoleh tempat perlindunganmu atau gua-gua atau lobang-lobang (dalam tanah) niscaya mereka pergi kepadanya dengan secepat-cepatnya.'),
(1293, '009:058', '68', 9, 58, 'Dan di antara mereka ada orang yang mencelamu tentang (distribusi) zakat; jika mereka diberi sebahagian dari padanya, mereka bersenang hati, dan jika mereka tidak diberi sebahagian dari padanya, dengan serta merta mereka menjadi marah.'),
(1294, '009:059', '68', 9, 59, 'Jikalau mereka sungguh-sungguh ridha dengan apa yang diberikan Allah dan Rasul-Nya kepada mereka, dan berkata: \"Cukuplah Allah bagi kami, Allah akan memberikan sebagian dari karunia-Nya dan demikian (pula) Rasul-Nya, sesungguhnya kami adalah orang-orang yang berharap kepada Allah,\" (tentulah yang demikian itu lebih baik bagi mereka).'),
(1295, '009:060', '68', 9, 60, 'Sesungguhnya zakat-zakat itu, hanyalah untuk orang-orang fakir, orang-orang miskin, pengurus-pengurus zakat, para mu´allaf yang dibujuk hatinya, untuk (memerdekakan) budak, orang-orang yang berhutang, untuk jalan Allah dan untuk mereka yuang sedang dalam perjalanan, sebagai suatu ketetapan yang diwajibkan Allah, dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(1296, '009:061', '68', 9, 61, 'Di antara mereka (orang-orang munafik) ada yang menyakiti Nabi dan mengatakan: \"Nabi mempercayai semua apa yang didengarnya\". Katakanlah: \"Ia mempercayai semua yang baik bagi kamu, ia beriman kepada Allah, mempercayai orang-orang mukmin, dan menjadi rahmat bagi orang-orang yang beriman di antara kamu\". Dan orang-orang yang menyakiti Rasulullah itu, bagi mereka azab yang pedih.'),
(1297, '009:062', '68', 9, 62, 'Mereka bersumpah kepada kamu dengan (nama) Allah untuk mencari keridhaanmu, padahal Allah dan Rasul-Nya itulah yang lebih patut mereka cari keridhaannya jika mereka adalah orang-orang yang mukmin.'),
(1298, '009:063', '68', 9, 63, 'Tidaklah mereka (orang-orang munafik itu) mengetahui bahwasanya barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya nerakan jahannamlah baginya, kekal mereka di dalamnya. Itu adalah kehinaan yang besar.'),
(1299, '009:064', '68', 9, 64, 'Orang-orang yang munafik itu takut akan diturunkan terhadap mereka sesuatu surat yang menerangkan apa yang tersembunyi dalam hati mereka. Katakanlah kepada mereka: \"Teruskanlah ejekan-ejekanmu (terhadap Allah dan rasul-Nya)\". Sesungguhnya Allah akan menyatakan apa yang kamu takuti itu.'),
(1300, '009:065', '68', 9, 65, 'Dan jika kamu tanyakan kepada mereka (tentang apa yang mereka lakukan itu), tentulah mereka akan manjawab, \"Sesungguhnya kami hanyalah bersenda gurau dan bermain-main saja\". Katakanlah: \"Apakah dengan Allah, ayat-ayat-Nya dan Rasul-Nya kamu selalu berolok-olok?\"'),
(1301, '009:066', '68', 9, 66, 'Tidak usah kamu minta maaf, karena kamu kafir sesudah beriman. Jika Kami memaafkan segolongan kamu (lantaran mereka taubat), niscaya Kami akan mengazab golongan (yang lain) disebabkan mereka adalah orang-orang yang selalu berbuat dosa.'),
(1302, '009:067', '68', 9, 67, 'Orang-orang munafik laki-laki dan perempuan. sebagian dengan sebagian yang lain adalah sama, mereka menyuruh membuat yang munkar dan melarang berbuat yang ma´ruf dan mereka menggenggamkan tangannya. Mereka telah lupa kepada Allah, maka Allah melupakan mereka. Sesungguhnya orang-orang munafik itu adalah orang-orang yang fasik.'),
(1303, '009:068', '68', 9, 68, 'Allah mengancam orang-orang munafik laki-laki dan perempuan dan orang-orang kafir dengan neraka Jahannam, mereka kekal di dalamnya. Cukuplah neraka itu bagi mereka, dan Allah melaknati mereka, dan bagi mereka azab yang kekal.'),
(1304, '009:069', '68', 9, 69, '(keadaan kamu hai orang-orang munafik dan musyrikin) adalah seperti keadaan orang-orang sebelum kamu, mereka lebih kuat daripada kamu, dan lebih banyak harta dan anak-anaknya dari kamu. Maka mereka telah menikmati bagian mereka, dan kamu telah menikmati bagian kamu sebagaimana orang-orang yang sebelummu menikmati bagiannya, dan kamu mempercakapkan (hal yang batil) sebagaimana mereka mempercakapkannya. Mereka itu amalannya menjadi sia-sia di dunia dan di akhirat; dan mereka itulah orang-orang yang merugi.'),
(1305, '009:070', '68', 9, 70, 'Belumkah datang kepada mereka berita penting tentang orang-orang yang sebelum mereka, (yaitu) kaum Nuh, ´Aad, Tsamud, kaum Ibrahim, penduduk Madyan dan negeri-negeri yang telah musnah?. Telah datang kepada mereka rasul-rasul dengan membawa keterangan yang nyata, maka Allah tidaklah sekali-kali menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.'),
(1306, '009:071', '68', 9, 71, 'Dan orang-orang yang beriman, lelaki dan perempuan, sebahagian mereka (adalah) menjadi penolong bagi sebahagian yang lain. Mereka menyuruh (mengerjakan) yang ma´ruf, mencegah dari yang munkar, mendirikan shalat, menunaikan zakat dan mereka taat pada Allah dan Rasul-Nya. Mereka itu akan diberi rahmat oleh Allah; sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.'),
(1307, '009:072', '68', 9, 72, 'Allah menjanjikan kepada orang-orang mukmin, lelaki dan perempuan, (akan mendapat) surga yang dibawahnya mengalir sungai-sungai, kekal mereka di dalamnya, dan (mendapat) tempat-tempat yang bagus di surga ´Adn. Dan keridhaan Allah adalah lebih besar; itu adalah keberuntungan yang besar.'),
(1308, '009:073', '68', 9, 73, 'Hai Nabi, berjihadlah (melawan) orang-orang kafir dan orang-orang munafik itu, dan bersikap keraslah terhadap mereka. Tempat mereka ialah jahannam. Dan itu adalah tempat kembali yang seburuk-buruknya.'),
(1309, '009:074', '68', 9, 74, 'Mereka (orang-orang munafik itu) bersumpah dengan (nama) Allah, bahwa mereka tidak mengatakan (sesuatu yang menyakitimu). Sesungguhnya mereka telah mengucapkan perkataan kekafiran, dan telah menjadi kafir sesudah Islam dan mengingini apa yang mereka tidak dapat mencapainya, dan mereka tidak mencela (Allah dan Rasul-Nya), kecuali karena Allah dan Rasul-Nya telah melimpahkan karunia-Nya kepada mereka. Maka jika mereka bertaubat, itu adalah lebih baik bagi mereka, dan jika mereka berpaling, niscaya Allah akan mengazab mereka dengan azab yang pedih di dunia dan akhirat; dan mereka sekali-kali tidaklah mempunyai pelindung dan tidak (pula) penolong di muka bumi.'),
(1310, '009:075', '68', 9, 75, 'Dan diantara mereka ada orang yang telah berikrar kepada Allah: \"Sesungguhnya jika Allah memberikan sebahagian karunia-Nya kepada kami, pastilah kami akan bersedekah dan pastilah kami termasuk orang-orang yang saleh.'),
(1311, '009:076', '68', 9, 76, 'Maka setelah Allah memberikan kepada mereka sebahagian dari karunia-Nya, mereka kikir dengan karunia itu, dan berpaling, dan mereka memanglah orang-orang yang selalu membelakangi (kebenaran).'),
(1312, '009:077', '68', 9, 77, 'Maka Allah menimbulkan kemunafikan pada hati mereka sampai kepada waktu mereka menemui Allah, karena mereka telah memungkiri terhadap Allah apa yang telah mereka ikrarkan kepada-Nya dan juga karena mereka selalu berdusta.'),
(1313, '009:078', '68', 9, 78, 'Tidaklah mereka tahu bahwasanya Allah mengetahui rahasia dan bisikan mereka, dan bahwasanya Allah amat mengetahui segala yang ghaib.'),
(1314, '009:079', '68', 9, 79, '(Orang-orang munafik itu) yaitu orang-orang yang mencela orang-orang mukmin yang memberi sedekah dengan sukarela dan (mencela) orang-orang yang tidak memperoleh (untuk disedekahkan) selain sekedar kesanggupannya, maka orang-orang munafik itu menghina mereka. Allah akan membalas penghinaan mereka itu, dan untuk mereka azab yang pedih.'),
(1315, '009:080', '68', 9, 80, 'Kamu memohonkan ampun bagi mereka atau tidak kamu mohonkan ampun bagi mereka (adalah sama saja). Kendatipun kamu memohonkan ampun bagi mereka tujuh puluh kali, namun Allah sekali-kali tidak akan memberi ampunan kepada mereka. Yang demikian itu adalah karena mereka kafir kepada Allah dan Rasul-Nya. Dan Allah tidak memberi petunjuk kepada kaum yang fasik.'),
(1316, '009:081', '68', 9, 81, 'Orang-orang yang ditinggalkan (tidak ikut perang) itu, merasa gembira dengan tinggalnya mereka di belakang Rasulullah, dan mereka tidak suka berjihad dengan harta dan jiwa mereka pada jalan Allah dan mereka berkata: \"Janganlah kamu berangkat (pergi berperang) dalam panas terik ini\". Katakanlah: \"Api neraka jahannam itu lebih sangat panas(nya)\" jika mereka mengetahui.'),
(1317, '009:082', '68', 9, 82, 'Maka hendaklah mereka tertawa sedikit dan menangis banyak, sebagai pembalasan dari apa yang selalu mereka kerjakan.'),
(1318, '009:083', '68', 9, 83, 'Maka jika Allah mengembalikanmu kepada suatu golongan dari mereka, kemudian mereka minta izin kepadamu untuk keluar (pergi berperang), maka Katakanlah: \"Kamu tidak boleh keluar bersamaku selama-lamanya dan tidak boleh memerangi musuh bersamaku. Sesungguhnya kamu telah rela tidak pergi berperang kali yang pertama. Karena itu duduklah bersama orang-orang yang tidak ikut berperang\".'),
(1319, '009:084', '68', 9, 84, 'Dan janganlah kamu sekali-kali menyembahyangkan (jenazah) seorang yang mati di antara mereka, dan janganlah kamu berdiri (mendoakan) di kuburnya. Sesungguhnya mereka telah kafir kepada Allah dan Rasul-Nya dan mereka mati dalam keadaan fasik.'),
(1320, '009:085', '68', 9, 85, 'Dan janganlah harta benda dan anak-anak mereka menarik hatimu. Sesungguhnya Allah menghendaki akan mengazab mereka di dunia dengan harta dan anak-anak itu dan agar melayang nyawa mereka, dalam keadaan kafir.'),
(1321, '009:086', '68', 9, 86, 'Dan apabila diturunkan suatu surat (yang memerintahkan kepada orang munafik itu): \"Berimanlah kamu kepada Allah dan berjihadlah beserta Rasul-Nya\", niscaya orang-orang yang sanggup di antara mereka meminta izin kepadamu (untuk tidak berjihad) dan mereka berkata: \"Biarkanlah kami berada bersama orang-orang yang duduk\".'),
(1322, '009:087', '68', 9, 87, 'Mereka rela berada bersama orang-orang yang tidak berperang, dan hati mereka telah dikunci mati maka mereka tidak mengetahui (kebahagiaan beriman dan berjihad).'),
(1323, '009:088', '68', 9, 88, 'Tetapi Rasul dan orang-orang yang beriman bersama dia, mereka berjihad dengan harta dan diri mereka. Dan mereka itulah orang-orang yang memperoleh kebaikan, dan mereka itulah orang-orang yang beruntung.'),
(1324, '009:089', '68', 9, 89, 'Allah telah menyediakan bagi mereka surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah kemenangan yang besar.'),
(1325, '009:090', '68', 9, 90, 'Dan datang (kepada Nabi) orang-orang yang mengemukakan ´uzur, yaitu orang-orang Arab Baswi agar diberi izin bagi mereka (untuk tidak berjihad), sedang orang-orang yang mendustakan Allah dan Rasul-Nya, duduk berdiam diri saja. Kelak orang-orang yang kafir di antara mereka itu akan ditimpa azab yang pedih.'),
(1326, '009:091', '68', 9, 91, 'Tiada dosa (lantaran tidak pergi berjihad) atas orang-orang yang lemah, orang-orang yang sakit dan atas orang-orang yang tidak memperoleh apa yang akan mereka nafkahkan, apabila mereka berlaku ikhlas kepada Allah dan Rasul-Nya. Tidak ada jalan sedikitpun untuk menyalahkan orang-orang yang berbuat baik. Dan Allah Maha Pengampun lagi Maha Penyayang,'),
(1327, '009:092', '68', 9, 92, 'dan tiada (pula) berdosa atas orang-orang yang apabila mereka datang kepadamu, supaya kamu memberi mereka kendaraan, lalu kamu berkata: \"Aku tidak memperoleh kendaraan untuk membawamu\". lalu mereka kembali, sedang mata mereka bercucuran air mata karena kesedihan, lantaran mereka tidak memperoleh apa yang akan mereka nafkahkan.'),
(1328, '009:093', '68', 9, 93, 'Sesungguhnya jalan (untuk menyalahkan) hanyalah terhadap orang-orang yang meminta izin kepadamu, padahal mereka itu orang-orang kaya. Mereka rela berada bersama orang-orang yang tidak ikut berperang dan Allah telah mengunci mati hati mereka, maka mereka tidak mengetahui (akibat perbuatan mereka).'),
(1329, '009:094', '68', 9, 94, 'Mereka (orang-orang munafik) mengemukakan ´uzurnya kepadamu, apabila kamu telah kembali kepada mereka (dari medan perang). Katakanlah: \"Janganlah kamu mengemukakan ´uzur; kami tidak percaya lagi kepadamu, (karena) sesungguhnya Allah telah memberitahukan kepada kami beritamu yang sebenarnya. Dan Allah serta Rasul-Nya akan melihat pekerjaanmu, kemudian kamu dikembalikan kepada Yang mengetahui yang ghaib dan yang nyata, lalu Dia memberitahukan kepadamu apa yang telah kamu kerjakan.'),
(1330, '009:095', '68', 9, 95, 'Kelak mereka akan bersumpah kepadamu dengan nama Allah, apabila kamu kembali kepada mereka, supaya kamu berpaling dari mereka. Maka berpalinglah dari mereka; karena sesungguhnya mereka itu adalah najis dan tempat mereka jahannam; sebagai balasan atas apa yang telah mereka kerjakan.'),
(1331, '009:096', '68', 9, 96, 'Mereka akan bersumpah kepadamu, agar kamu ridha kepada mereka. Tetapi jika sekiranya kamu ridha kepada mereka, sesungguhnya Allah tidak ridha kepada orang-orang yang fasik itu.'),
(1332, '009:097', '68', 9, 97, 'Orang-orang Arab Badwi itu, lebih sangat kekafiran dan kemunafikannya, dan lebih wajar tidak mengetahui hukum-hukum yang diturunkan Allah kepada Rasul-Nya. Dan Allah Maha mengetahui lagi Maha Bijaksana.'),
(1333, '009:098', '68', 9, 98, 'Di antara orang-orang Arab Badwi itu ada orang yang memandang apa yang dinafkahkannya (di jalan Allah), sebagi suatu kerugian, dan dia menanti-nanti marabahaya menimpamu, merekalah yang akan ditimpa marabahaya. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(1334, '009:099', '68', 9, 99, 'Di antara orang-orang Arab Badwi itu ada orang yang beriman kepada Allah dan hari kemudian, dan memandang apa yang dinafkahkannya (di jalan Allah) itu, sebagai jalan untuk mendekatkannya kepada Allah dan sebagai jalan untuk memperoleh doa Rasul. Ketahuilah, sesungguhnya nafkah itu adalah suatu jalan bagi mereka untuk mendekatkan diri (kepada Allah). Kelak Allah akan memasukan mereka kedalam rahmat (surga)Nya; Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(1335, '009:100', '68', 9, 100, 'Orang-orang yang terdahulu lagi yang pertama-tama (masuk Islam) dari golongan muhajirin dan anshar dan orang-orang yang mengikuti mereka dengan baik, Allah ridha kepada mereka dan merekapun ridha kepada Allah dan Allah menyediakan bagi mereka surga-surga yang mengalir sungai-sungai di dalamnya selama-lamanya. Mereka kekal di dalamnya. Itulah kemenangan yang besar.'),
(1336, '009:101', '68', 9, 101, 'Di antara orang-orang Arab Badwi yang di sekelilingmu itu, ada orang-orang munafik; dan (juga) di antara penduduk Madinah. Mereka keterlaluan dalam kemunafikannya. Kamu (Muhammad) tidak mengetahui mereka, (tetapi) Kamilah yang mengetahui mereka. Nanti mereka akan Kami siksa dua kali kemudian mereka akan dikembalikan kepada azab yang besar.'),
(1337, '009:102', '68', 9, 102, 'Dan (ada pula) orang-orang lain yang mengakui dosa-dosa mereka, mereka mencampurbaurkan pekerjaan yang baik dengan pekerjaan lain yang buruk. Mudah-mudahan Allah menerima taubat mereka. Sesungguhnya Allah Maha Pengampun lagi maha Penyayang.'),
(1338, '009:103', '68', 9, 103, 'Ambillah zakat dari sebagian harta mereka, dengan zakat itu kamu membersihkan dan mensucikan mereka dan mendoalah untuk mereka. Sesungguhnya doa kamu itu (menjadi) ketenteraman jiwa bagi mereka. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(1339, '009:104', '68', 9, 104, 'Tidaklah mereka mengetahui, bahwasanya Allah menerima taubat dari hamba-hamba-Nya dan menerima zakat dan bahwasanya Allah Maha Penerima taubat lagi Maha Penyayang?'),
(1340, '009:105', '68', 9, 105, 'Dan Katakanlah: \"Bekerjalah kamu, maka Allah dan Rasul-Nya serta orang-orang mukmin akan melihat pekerjaanmu itu, dan kamu akan dikembalikan kepada (Allah) Yang Mengetahui akan yang ghaib dan yang nyata, lalu diberitakan-Nya kepada kamu apa yang telah kamu kerjakan.'),
(1341, '009:106', '68', 9, 106, 'Dan ada (pula) orang-orang lain yang ditangguhkan sampai ada keputusan Allah; adakalanya Allah akan mengazab mereka dan adakalanya Allah akan menerima taubat mereka. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(1342, '009:107', '68', 9, 107, 'Dan (di antara orang-orang munafik itu) ada orang-orang yang mendirikan masjid untuk menimbulkan kemudharatan (pada orang-orang mukmin), untuk kekafiran dan untuk memecah belah antara orang-orang mukmin serta menunggu kedatangan orang-orang yang telah memerangi Allah dan Rasul-Nya sejak dahulu. Mereka Sesungguhnya bersumpah: \"Kami tidak menghendaki selain kebaikan\". Dan Allah menjadi saksi bahwa sesungguhnya mereka itu adalah pendusta (dalam sumpahnya).'),
(1343, '009:108', '68', 9, 108, 'Janganlah kamu bersembahyang dalam mesjid itu selama-lamanya. Sesungguhnya mesjid yang didirikan atas dasar takwa (mesjid Quba), sejak hari pertama adalah lebih patut kamu sholat di dalamnya. Di dalamnya mesjid itu ada orang-orang yang ingin membersihkan diri. Dan sesungguhnya Allah menyukai orang-orang yang bersih.'),
(1344, '009:109', '68', 9, 109, 'Maka apakah orang-orang yang mendirikan mesjidnya di atas dasar takwa kepada Allah dan keridhaan-(Nya) itu yang baik, ataukah orang-orang yang mendirikan bangunannya di tepi jurang yang runtuh, lalu bangunannya itu jatuh bersama-sama dengan dia ke dalam neraka Jahannam. Dan Allah tidak memberikan petunjuk kepada orang-orang yang zalim.'),
(1345, '009:110', '68', 9, 110, 'Bangunan-bangunan yang mereka dirikan itu senantiasa menjadi pangkal keraguan dalam hati mereka, kecuali bila hati mereka itu telah hancur. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(1346, '009:111', '68', 9, 111, 'Sesungguhnya Allah telah membeli dari orang-orang mukmin diri dan harta mereka dengan memberikan surga untuk mereka. Mereka berperang pada jalan Allah; lalu mereka membunuh atau terbunuh. (Itu telah menjadi) janji yang benar dari Allah di dalam Taurat, Injil dan Al Quran. Dan siapakah yang lebih menepati janjinya (selain) daripada Allah? Maka bergembiralah dengan jual beli yang telah kamu lakukan itu, dan itulah kemenangan yang besar.'),
(1347, '009:112', '68', 9, 112, 'Mereka itu adalah orang-orang yang bertaubat, yang beribadat, yang memuji, yang melawat, yang ruku´, yang sujud, yang menyuruh berbuat ma´ruf dan mencegah berbuat munkar dan yang memelihara hukum-hukum Allah. Dan gembirakanlah orang-orang mukmin itu.'),
(1348, '009:113', '68', 9, 113, 'Tiadalah sepatutnya bagi Nabi dan orang-orang yang beriman memintakan ampun (kepada Allah) bagi orang-orang musyrik, walaupun orang-orang musyrik itu adalah kaum kerabat(nya), sesudah jelas bagi mereka, bahwasanya orang-orang musyrik itu adalah penghuni neraka jahanam.'),
(1349, '009:114', '68', 9, 114, 'Dan permintaan ampun dari Ibrahim (kepada Allah) untuk bapaknya tidak lain hanyalah karena suatu janji yang telah diikrarkannya kepada bapaknya itu. Maka, tatkala jelas bagi Ibrahim bahwa bapaknya itu adalah musuh Allah, maka Ibrahim berlepas diri dari padanya. Sesungguhnya Ibrahim adalah seorang yang sangat lembut hatinya lagi penyantun.'),
(1350, '009:115', '68', 9, 115, 'Dan Allah sekali-kali tidak akan menyesatkan suatu kaum, sesudah Allah memberi petunjuk kepada mereka sehingga dijelaskan-Nya kepada mereka apa yang harus mereka jauhi. Sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
(1351, '009:116', '68', 9, 116, 'Sesungguhnya kepunyaan Allah-lah kerajaan langit dan bumi. Dia menghidupkan dan mematikan. Dan sekali-kali tidak ada pelindung dan penolong bagimu selain Allah.'),
(1352, '009:117', '68', 9, 117, 'Sesungguhnya Allah telah menerima taubat Nabi, orang-orang muhajirin dan orang-orang anshar yang mengikuti Nabi dalam masa kesulitan, setelah hati segolongan dari mereka hampir berpaling, kemudian Allah menerima taubat mereka itu. Sesungguhnya Allah Maha Pengasih lagi Maha Penyayang kepada mereka,'),
(1353, '009:118', '68', 9, 118, 'dan terhadap tiga orang yang ditangguhkan (penerimaan taubat) mereka, hingga apabila bumi telah menjadi sempit bagi mereka, padahal bumi itu luas dan jiwa merekapun telah sempit (pula terasa) oleh mereka, serta mereka telah mengetahui bahwa tidak ada tempat lari dari (siksa) Allah, melainkan kepada-Nya saja. Kemudian Allah menerima taubat mereka agar mereka tetap dalam taubatnya. Sesungguhnya Allah-lah Yang maha Penerima taubat lagi Maha Penyayang.'),
(1354, '009:119', '68', 9, 119, 'Hai orang-orang yang beriman bertakwalah kepada Allah, dan hendaklah kamu bersama orang-orang yang benar.'),
(1355, '009:120', '68', 9, 120, 'Tidaklah sepatutnya bagi penduduk Madinah dan orang-orang Arab Badwi yang berdiam di sekitar mereka, tidak turut menyertai Rasulullah (berperang) dan tidak patut (pula) bagi mereka lebih mencintai diri mereka daripada mencintai diri Rasul. Yang demikian itu ialah karena mereka tidak ditimpa kehausan, kepayahan dan kelaparan pada jalan Allah, dan tidak (pula) menginjak suatu tempat yang membangkitkan amarah orang-orang kafir, dan tidak menimpakan sesuatu bencana kepada musuh, melainkan dituliskanlah bagi mereka dengan yang demikian itu suatu amal saleh. Sesungguhnya Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik,'),
(1356, '009:121', '68', 9, 121, 'dan mereka tiada menafkahkan suatu nafkah yang kecil dan tidak (pula) yang besar dan tidak melintasi suatu lembah, melainkan dituliskan bagi mereka (amal saleh pula) karena Allah akan memberi balasan kepada mereka yang lebih baik dari apa yang telah mereka kerjakan.'),
(1357, '009:122', '68', 9, 122, 'Tidak sepatutnya bagi mukminin itu pergi semuanya (ke medan perang). Mengapa tidak pergi dari tiap-tiap golongan di antara mereka beberapa orang untuk memperdalam pengetahuan mereka tentang agama dan untuk memberi peringatan kepada kaumnya apabila mereka telah kembali kepadanya, supaya mereka itu dapat menjaga dirinya.'),
(1358, '009:123', '68', 9, 123, 'Hai orang-orang yang beriman, perangilah orang-orang kafir yang di sekitar kamu itu, dan hendaklah mereka menemui kekerasan daripadamu, dan ketahuilah, bahwasanya Allah bersama orang-orang yang bertakwa.'),
(1359, '009:124', '68', 9, 124, 'Dan apabila diturunkan suatu surat, maka di antara mereka (orang-orang munafik) ada yang berkata: \"Siapakah di antara kamu yang bertambah imannya dengan (turannya) surat ini?\" Adapun orang-orang yang beriman, maka surat ini menambah imannya, dan mereka merasa gembira.'),
(1360, '009:125', '68', 9, 125, 'Dan adapun orang-orang yang di dalam hati mereka ada penyakit, maka dengan surat itu bertambah kekafiran mereka, disamping kekafirannya (yang telah ada) dan mereka mati dalam keadaan kafir.'),
(1361, '009:126', '68', 9, 126, 'Dan tidaklah mereka (orang-orang munafik) memperhatikan bahwa mereka diuji sekali atau dua kali setiap tahun, dan mereka tidak (juga) bertaubat dan tidak (pula) mengambil pelajaran?'),
(1362, '009:127', '68', 9, 127, 'Dan apabila diturunkan satu surat, sebagian mereka memandang kepada yang lain (sambil berkata): \"Adakah seorang dari (orang-orang muslimin) yang melihat kamu?\" Sesudah itu merekapun pergi. Allah telah memalingkan hati mereka disebabkan mereka adalah kaum yang tidak mengerti.'),
(1363, '009:128', '68', 9, 128, 'Sungguh telah datang kepadamu seorang Rasul dari kaummu sendiri, berat terasa olehnya penderitaanmu, sangat menginginkan (keimanan dan keselamatan) bagimu, amat belas kasihan lagi penyayang terhadap orang-orang mukmin.'),
(1364, '009:129', '68', 9, 129, 'Jika mereka berpaling (dari keimanan), maka katakanlah: \"Cukuplah Allah bagiku; tidak ada Tuhan selain Dia. Hanya kepada-Nya aku bertawakkal dan Dia adalah Tuhan yang memiliki ´Arsy yang agung\".'),
(1365, '010:001', '68', 10, 1, 'Alif laam raa. Inilah ayat-ayat Al Quran yang mengandung hikmah.'),
(1366, '010:002', '68', 10, 2, 'Patutkah menjadi keheranan bagi manusia bahwa Kami mewahyukan kepada seorang laki-laki di antara mereka: \"Berilah peringatan kepada manusia dan gembirakanlah orang-orang beriman bahwa mereka mempunyai kedudukan yang tinggi di sisi Tuhan mereka\". Orang-orang kafir berkata: \"Sesungguhnya orang ini (Muhammad) benar-benar adalah tukang sihir yang nyata\".'),
(1367, '010:003', '68', 10, 3, 'Sesungguhnya Tuhan kamu ialah Allah Yang menciptakan langit dan bumi dalam enam masa, kemudian Dia bersemayam di atas ´Arsy untuk mengatur segala urusan. Tiada seorangpun yang akan memberi syafa´at kecuali sesudah ada izin-Nya. (Dzat) yang demikian itulah Allah, Tuhan kamu, maka sembahlah Dia. Maka apakah kamu tidak mengambil pelajaran?'),
(1368, '010:004', '68', 10, 4, 'Hanya kepada-Nya-lah kamu semuanya akan kembali; sebagai janji yang benar daripada Allah, sesungguhnya Allah menciptakan makhluk pada permulaannya kemudian mengulanginya (menghidupkannya) kembali (sesudah berbangkit), agar Dia memberi pembalasan kepada orang-orang yang beriman dan yang mengerjakan amal saleh dengan adil. Dan untuk orang-orang kafir disediakan minuman air yang panas dan azab yang pedih disebabkan kekafiran mereka.'),
(1369, '010:005', '68', 10, 5, 'Dialah yang menjadikan matahari bersinar dan bulan bercahaya dan ditetapkan-Nya manzilah-manzilah (tempat-tempat) bagi perjalanan bulan itu, supaya kamu mengetahui bilangan tahun dan perhitungan (waktu). Allah tidak menciptakan yang demikian itu melainkan dengan hak. Dia menjelaskan tanda-tanda (kebesaran-Nya) kepada orang-orang yang mengetahui.'),
(1370, '010:006', '68', 10, 6, 'Sesungguhnya pada pertukaran malam dan siang itu dan pada apa yang diciptakan Allah di langit dan di bumi, benar-benar terdapat tanda-tanda (kekuasaan-Nya) bagi orang-orang yang bertakwa.'),
(1371, '010:007', '68', 10, 7, 'Sesungguhnya orang-orang yang tidak mengharapkan (tidak percaya akan) pertemuan dengan Kami, dan merasa puas dengan kehidupan dunia serta merasa tenteram dengan kehidupan itu dan orang-orang yang melalaikan ayat-ayat Kami,'),
(1372, '010:008', '68', 10, 8, 'mereka itu tempatnya ialah neraka, disebabkan apa yang selalu mereka kerjakan.'),
(1373, '010:009', '68', 10, 9, 'Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh, mereka diberi petunjuk oleh Tuhan mereka karena keimanannya, di bawah mereka mengalir sungai-sungai di dalam surga yang penuh kenikmatan.'),
(1374, '010:010', '68', 10, 10, 'Do´a mereka di dalamnya ialah: \"Subhanakallahumma\", dan salam penghormatan mereka ialah: \"Salam\". Dan penutup doa mereka ialah: \"Alhamdulilaahi Rabbil ´aalamin\".'),
(1375, '010:011', '68', 10, 11, 'Dan kalau sekiranya Allah menyegerakan kejahatan bagi manusia seperti permintaan mereka untuk menyegerakan kebaikan, pastilah diakhiri umur mereka. Maka Kami biarkan orang-orang yang tidak mengharapkan pertemuan dengan Kami, bergelimangan di dalam kesesatan mereka.'),
(1376, '010:012', '68', 10, 12, 'Dan apabila manusia ditimpa bahaya dia berdoa kepada Kami dalam keadaan berbaring, duduk atau berdiri, tetapi setelah Kami hilangkan bahaya itu daripadanya, dia (kembali) melalui (jalannya yang sesat), seolah-olah dia tidak pernah berdoa kepada Kami untuk (menghilangkan) bahaya yang telah menimpanya. Begitulah orang-orang yang melampaui batas itu memandang baik apa yang selalu mereka kerjakan.'),
(1377, '010:013', '68', 10, 13, 'Dan sesungguhnya Kami telah membinasakan umat-umat sebelum kamu, ketika mereka berbuat kezaliman, padahal rasul-rasul mereka telah datang kepada mereka dengan membawa keterangan-keterangan yang nyata, tetapi mereka sekali-kali tidak hendak beriman. Demikianlah Kami memberi pembalasan kepada orang-orang yang berbuat dosa.'),
(1378, '010:014', '68', 10, 14, 'Kemudian Kami jadikan kamu pengganti-pengganti (mereka) di muka bumi sesudah mereka, supaya Kami memperhatikan bagaimana kamu berbuat.'),
(1379, '010:015', '68', 10, 15, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang nyata, orang-orang yang tidak mengharapkan pertemuan dengan Kami berkata: \"Datangkanlah Al Quran yang lain dari ini atau gantilah dia\". Katakanlah: \"Tidaklah patut bagiku menggantinya dari pihak diriku sendiri. Aku tidak mengikut kecuali apa yang diwahyukan kepadaku. Sesungguhnya aku takut jika mendurhakai Tuhanku kepada siksa hari yang besar (kiamat)\".'),
(1380, '010:016', '68', 10, 16, 'Katakanlah: \"Jikalau Allah menghendaki, niscaya aku tidak membacakannya kepadamu dan Allah tidak (pula) memberitahukannya kepadamu\". Sesungguhnya aku telah tinggal bersamamu beberapa lama sebelumnya. Maka apakah kamu tidak memikirkannya?'),
(1381, '010:017', '68', 10, 17, 'Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kedustaan terhadap Allah atau mendustakan ayat-ayat-Nya? Sesungguhnya, tiadalah beruntung orang-orang yang berbuat dosa.'),
(1382, '010:018', '68', 10, 18, 'Dan mereka menyembah selain daripada Allah apa yang tidak dapat mendatangkan kemudharatan kepada mereka dan tidak (pula) kemanfaatan, dan mereka berkata: \"Mereka itu adalah pemberi syafa´at kepada kami di sisi Allah\". Katakanlah: \"Apakah kamu mengabarkan kepada Allah apa yang tidak diketahui-Nya baik di langit dan tidak (pula) dibumi?\" Maha Suci Allah dan Maha Tinggi dan apa yang mereka mempersekutukan (itu).'),
(1383, '010:019', '68', 10, 19, 'Manusia dahulunya hanyalah satu umat, kemudian mereka berselisih. Kalau tidaklah karena suatu ketetapan yang telah ada dari Tuhanmu dahulu, pastilah telah diberi keputusan di antara mereka, tentang apa yang mereka perselisihkan itu.'),
(1384, '010:020', '68', 10, 20, 'Dan mereka berkata: \"Mepada tidak diturunkan kepadanya (Muhammad) suatu keterangan (mukjizat) dari Tuhannya?\" Maka katakanlah: \"Sesungguhnya yang ghaib itu kepunyaan Allah, sebab itu tunggu (sajalah) olehmu, sesungguhnya aku bersama kamu termasuk orang-orang yang manunggu.'),
(1385, '010:021', '68', 10, 21, 'Dan apabila Kami merasakan kepada manusia suatu rahmat, sesudah (datangnya) bahaya menimpa mereka, tiba-tiba mereka mempunyai tipu daya dalam (menentang) tanda-tanda kekuasaan Kami. Katakanlah: \"Allah lebih cepat pembalasannya (atas tipu daya itu)\". Sesungguhnya malaikat-malaikat Kami menuliskan tipu dayamu.'),
(1386, '010:022', '68', 10, 22, 'Dialah Tuhan yang menjadikan kamu dapat berjalan di daratan, (berlayar) di lautan. Sehingga apabila kamu berada di dalam bahtera, dan meluncurlah bahtera itu membawa orang-orang yang ada di dalamnya dengan tiupan angin yang baik, dan mereka bergembira karenanya, datanglah angin badai, dan (apabila) gelombang dari segenap penjuru menimpanya, dan mereka yakin bahwa mereka telah terkepung (bahaya), maka mereka berdoa kepada Allah dengan mengikhlaskan ketaatan kepada-Nya semata-mata. (Mereka berkata): \"Sesungguhnya jika Engkau menyelamatkan kami dari bahaya ini, pastilah kami akan termasuk orang-orang yang bersyukur\".'),
(1387, '010:023', '68', 10, 23, 'Maka tatkala Allah menyelamatkan mereka, tiba-tiba mereka membuat kezaliman di muka bumi tanpa (alasan) yang benar. Hai manusia, sesungguhnya (bencana) kezalimanmu akan menimpa dirimu sendiri; (hasil kezalimanmu) itu hanyalah kenikmatan hidup duniawi, kemudian kepada Kami-lah kembalimu, lalu Kami kabarkan kepadamu apa yang telah kamu kerjakan.'),
(1388, '010:024', '68', 10, 24, 'Sesungguhnya perumpamaan kehidupan duniawi itu, adalah seperti air (hujan) yang Kami turunkan dan langit, lalu tumbuhlah dengan suburnya karena air itu tanam-tanaman bumi, di antaranya ada yang dimakan manusia dan binatang ternak. Hingga apabila bumi itu telah sempurna keindahannya, dan memakai (pula) perhiasannya, dan pemilik-permliknya mengira bahwa mereka pasti menguasasinya, tiba-tiba datanglah kepadanya azab Kami di waktu malam atau siang, lalu Kami jadikan (tanam-tanamannya) laksana tanam-tanaman yang sudah disabit, seakan-akan belum pernah tumbuh kemarin. Demikianlah Kami menjelaskan tanda-tanda kekuasaan (Kami) kepada orang-orang berfikir.'),
(1389, '010:025', '68', 10, 25, 'Allah menyeru (manusia) ke Darussalam (surga), dan menunjuki orang yang dikehendaki-Nya kepada jalan yang lurus (Islam).'),
(1390, '010:026', '68', 10, 26, 'Bagi orang-orang yang berbuat baik, ada pahala yang terbaik (surga) dan tambahannya. Dan muka mereka tidak ditutupi debu hitam dan tidak (pula) kehinaan. Mereka itulah penghuni surga, mereka kekal di dalamnya.'),
(1391, '010:027', '68', 10, 27, 'Dan orang-orang yang mengerjakan kejahatan (mendapat) balasan yang setimpal dan mereka ditutupi kehinaan. Tidak ada bagi mereka seorang pelindungpun dari (azab) Allah, seakan-akan muka mereka ditutupi dengan kepingan-kepingan malam yang gelap gelita. Mereka itulah penghuni neraka; mereka kekal di dalamnya.'),
(1392, '010:028', '68', 10, 28, '(Ingatlah) suatu hari (ketika itu). Kami mengumpulkan mereka semuanya, kemudian Kami berkata kepada orang-orang yang mempersekutukan (Tuhan): \"Tetaplah kamu dan sekutu-sekutumu di tempatmu itu\". Lalu Kami pisahkan mereka dan berkatalah sekutu-sekutu mereka: \"Kamu sekali-kali tidak pernah menyembah kami.'),
(1393, '010:029', '68', 10, 29, 'Dan cukuplah Allah menjadi saksi antara kami dengan kamu, bahwa kami tidak tahu-menahu tentang penyembahan kamu (kepada kami).'),
(1394, '010:030', '68', 10, 30, 'Di tempat itu (padang Mahsyar), tiap-tiap diri merasakan pembalasan dari apa yang telah dikerjakannya dahulu dan mereka dikembalikan kepada Allah Pelindung mereka yang sebenarnya dan lenyaplah dari mereka apa yang mereka ada-adakan.'),
(1395, '010:031', '68', 10, 31, 'Katakanlah: \"Siapakah yang memberi rezeki kepadamu dari langit dan bumi, atau siapakah yang kuasa (menciptakan) pendengaran dan penglihatan, dan siapakah yang mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan siapakah yang mengatur segala urusan?\" Maka mereka akan menjawab: \"Allah\". Maka katakanlah \"Mangapa kamu tidak bertakwa kepada-Nya)?\"'),
(1396, '010:032', '68', 10, 32, 'Maka (Zat yang demikian) itulah Allah Tuhan kamu yang sebenarnya; maka tidak ada sesudah kebenaran itu, melainkan kesesatan. Maka bagaimanakah kamu dipalingkan (dari kebenaran)?'),
(1397, '010:033', '68', 10, 33, 'Demikianlah telah tetap hukuman Tuhanmu terhadap orang-orang yang fasik, karena sesungguhnya mereka tidak beriman.'),
(1398, '010:034', '68', 10, 34, 'Katakanlah: \"Apakah di antara sekutu-sekutumu ada yang dapat memulai penciptaan makhluk, kemudian mengulanginya (menghidupkannya) kembali?\" katakanlah: \"Allah-lah yang memulai penciptaan makhluk, kemudian mengulanginya (menghidupkannya) kembali; maka bagaimanakah kamu dipalingkan (kepada menyembah yang selain Allah)?\"'),
(1399, '010:035', '68', 10, 35, 'Katakanlah: \"Apakah di antara sekutu-sekuturmu ada yang menunjuki kepada kebenaran?\" Katakanlah \"Allah-lah yang menunjuki kepada kebenaran\". Maka apakah orang-orang yang menunjuki kepada kebenaran itu lebih berhak diikuti ataukah orang yang tidak dapat memberi petunjuk kecuali (bila) diberi petunjuk? Mengapa kamu (berbuat demikian)? Bagaimanakah kamu mengambil keputusan?'),
(1400, '010:036', '68', 10, 36, 'Dan kebanyakan mereka tidak mengikuti kecuali persangkaan saja. Sesungguhnya persangkaan itu tidak sedikitpun berguna untuk mencapai kebenaran. Sesungguhnya Allah Maha Mengetahui apa yang mereka kerjakan.'),
(1401, '010:037', '68', 10, 37, 'Tidaklah mungkin Al Quran ini dibuat oleh selain Allah; akan tetapi (Al Quran itu) membenarkan kitab-kitab yang sebelumnya dan menjelaskan hukum-hukum yang telah ditetapkannya, tidak ada keraguan di dalamnya, (diturunkan) dari Tuhan semesta alam.'),
(1402, '010:038', '68', 10, 38, 'Atau (patutkah) mereka mengatakan \"Muhammad membuat-buatnya\". Katakanlah: \"(Kalau benar yang kamu katakan itu), maka cobalah datangkan sebuah surat seumpamanya dan panggillah siapa-siapa yang dapat kamu panggil (untuk membuatnya) selain Allah, jika kamu orang yang benar\".'),
(1403, '010:039', '68', 10, 39, 'Bahkan yang sebenarnya, mereka mendustakan apa yang mereka belum mengetahuinya dengan sempurna padahal belum datang kepada mereka penjelasannya. Demikianlah orang-orang yang sebelum mereka telah mendustakan (rasul). Maka perhatikanlah bagaimana akibat orang-orang yang zalim itu.'),
(1404, '010:040', '68', 10, 40, 'Di antara mereka ada orang-orang yang beriman kepada Al Quran, dan di antaranya ada (pula) orang-orang yang tidak beriman kepadanya. Tuhanmu lebih mengetahui tentang orang-orang yang berbuat kerusakan.'),
(1405, '010:041', '68', 10, 41, 'Jika mereka mendustakan kamu, maka katakanlah: \"Bagiku pekerjaanku dan bagimu pekerjaanmu. Kamu berlepas diri terhadap apa yang aku kerjakan dan akupun berlepas diri terhadap apa yang kamu kerjakan\".'),
(1406, '010:042', '68', 10, 42, 'Dan di antara mereka ada orang yang mendengarkanmu. Apakah kamu dapat menjadikan orang-orang tuli itu mendengar walaupun mereka tidak mengerti.'),
(1407, '010:043', '68', 10, 43, 'Dan di antara mereka ada orang yang melihat kepadamu, apakah dapat kamu memberi petunjuk kepada orang-orang yang buta, walaupun mereka tidak dapat memperhatikan.'),
(1408, '010:044', '68', 10, 44, 'Sesungguhnya Allah tidak berbuat zalim kepada manusia sedikitpun, akan tetapi manusia itulah yang berbuat zalim kepada diri mereka sendiri.'),
(1409, '010:045', '68', 10, 45, 'Dan (ingatlah) akan hari (yang di waktu itu) Allah mengumpulkan mereka, (mereka merasa di hari itu) seakan-akan mereka tidak pernah berdiam (di dunia) hanya sesaat di siang hari, (di waktu itu) mereka saling berkenalan. Sesungguhnya rugilah orang-orang yang mendustakan pertemuan mereka dengan Allah dan mereka tidak mendapat petunjuk'),
(1410, '010:046', '68', 10, 46, 'Dan jika Kami perlihatkan kepadamu sebahagian dari (siksa) yang Kami ancamkan kepada mereka, (tentulah kamu akan melihatnya) atau (jika) Kami wafatkan kamu (sebelum itu), maka kepada Kami jualah mereka kembali, dan Allah menjadi saksi atas apa yang mereka kerjakan.'),
(1411, '010:047', '68', 10, 47, 'Tiap-tiap umat mempunyai rasul; maka apabila telah datang rasul mereka, diberikanlah keputusan antara mereka dengan adil dan mereka (sedikitpun) tidak dianiaya.'),
(1412, '010:048', '68', 10, 48, 'Mereka mengatakan: \"Bilakah (datangnya) ancaman itu, jika memang kamu orang-orang yang benar?\"'),
(1413, '010:049', '68', 10, 49, 'Katakanlah: \"Aku tidak berkuasa mendatangkan kemudharatan dan tidak (pula) kemanfaatan kepada diriku, melainkan apa yang dikehendaki Allah\". Tiap-tiap umat mempunyai ajal. Apabila telah datang ajal mereka, maka mereka tidak dapat mengundurkannya barang sesaatpun dan tidak (pula) mendahulukan(nya).'),
(1414, '010:050', '68', 10, 50, 'Katakanlah: \"Terangkan kepadaku, jika datang kepada kamu sekalian sikaaan-Nya di waktu malam atau di siang hari, apakah orang-orang yang berdosa itu meminta disegerakan juga?\"'),
(1415, '010:051', '68', 10, 51, 'Kemudian apakah setelah terjadinya (azab itu), kemudian itu kamu baru mempercayainya? Apakah sekarang (baru kamu mempercayai), padahal sebelumnya kamu selalu meminta supaya disegerakan?'),
(1416, '010:052', '68', 10, 52, 'Kemudian dikatakan kepada orang-orang yang zalim (musyrik) itu: \"Rasakanlah olehmu siksaan yang kekal; kamu tidak diberi balasan melainkan dengan apa yang telah kamu kerjakan\".'),
(1417, '010:053', '68', 10, 53, 'Dan mereka menanyakan kepadamu: \"Benarkah (azab yang dijanjikan) itu? Katakanlah: \"Ya, demi Tuhanku, sesungguhnya azab itu adalah benar dan kamu sekali-kali tidak bisa luput (daripadanya)\".'),
(1418, '010:054', '68', 10, 54, 'Dan kalau setiap diri yang zalim (muayrik) itu mempunyai segala apa yang ada di bumi ini, tentu dia menebus dirinya dengan itu, dan mereka membunyikan penyesalannya ketika mereka telah menyaksikan azab itu. Dan telah diberi keputusan di antara mereka dengan adil, sedang mereka tidak dianiaya.'),
(1419, '010:055', '68', 10, 55, 'Ingatlah, sesungguhnya kepunyaan Allah apa yang ada di langit dan di bumi. Ingatlah, sesungguhnya janji Allah itu benar, tetapi kebanyakan mereka tidak mengetahui(nya).'),
(1420, '010:056', '68', 10, 56, 'Dialah yang menghidupkan dan mematikan dan hanya kepada-Nya-lah kamu dikembalikan.'),
(1421, '010:057', '68', 10, 57, 'Hai manusia, sesungguhnya telah datang kepadamu pelajaran dari Tuhanmu dan penyembuh bagi penyakit-penyakit (yang berada) dalam dada dan petunjuk serta rahmat bagi orang-orang yang beriman.'),
(1422, '010:058', '68', 10, 58, 'Katakanlah: \"Dengan kurnia Allah dan rahmat-Nya, hendaklah dengan itu mereka bergembira. Kurnia Allah dan rahmat-Nya itu adalah lebih baik dari apa yang mereka kumpulkan\".'),
(1423, '010:059', '68', 10, 59, 'Katakanlah: \"Terangkanlah kepadaku tentang rezeki yang diturunkan Allah kepadamu, lalu kamu jadikan sebagiannya haram dan (sebagiannya) halal\". Katakanlah: \"Apakah Allah telah memberikan izin kepadamu (tentang ini) atau kamu mengada-adakan saja terhadap Allah?\"'),
(1424, '010:060', '68', 10, 60, 'Apakah dugaan orang-orang yang mengada-adakan kebohongan terhadap Allah pada hari kiamat? Sesungguhnya Allah benar-benar mempunyai karunia (yang dilimpahkan) atas manusia, tetapi kebanyakan mereka tidak mensyukuri(nya).'),
(1425, '010:061', '68', 10, 61, 'Kamu tidak berada dalam suatu keadaan dan tidak membaca suatu ayat dari Al Quran dan kamu tidak mengerjakan suatu pekerjaan, melainkan Kami menjadi saksi atasmu di waktu kamu melakukannya. Tidak luput dari pengetahuan Tuhanmu biarpun sebesar zarrah (atom) di bumi ataupun di langit. Tidak ada yang lebih kecil dan tidak (pula) yang lebih besar dari itu, melainkan (semua tercatat) dalam kitab yang nyata (Lauh Mahfuzh).'),
(1426, '010:062', '68', 10, 62, 'Ingatlah, sesungguhnya wali-wali Allah itu, tidak ada kekhawatiran terhadap mereka dan tidak (pula) mereka bersedih hati.'),
(1427, '010:063', '68', 10, 63, '(Yaitu) orang-orang yang beriman dan mereka selalu bertakwa.'),
(1428, '010:064', '68', 10, 64, 'Bagi mereka berita gembira di dalam kehidupan di dunia dan (dalam kehidupan} di akhirat. Tidak ada perubahan bagi kalimat-kalimat (janji-janji) Allah. Yang demikian itu adalah kemenangan yang besar.'),
(1429, '010:065', '68', 10, 65, 'Janganlah kamu sedih oleh perkataan mereka. Sesungguhnya kekuasaan itu seluruhnya adalah kepunyaan Allah. Dialah Yang Maha Mendengar lagi Maha Mengetahui.'),
(1430, '010:066', '68', 10, 66, 'Ingatlah, sesungguhnya kepunyaan Allah semua yang ada di langit dan semua yang ada di bumi. Dan orang-orang yang menyeru sekutu-sekutu selain Allah, tidaklah mengikuti (suatu keyakinan). Mereka tidak mengikuti kecuali prasangka belaka, dan mereka hanyalah menduga-duga.'),
(1431, '010:067', '68', 10, 67, 'Dialah yang menjadikan malam bagi kamu supaya kamu beristirahat padanya dan (menjadikan) siang terang benderang (supaya kamu mencari karunia Allah). Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang mendengar.'),
(1432, '010:068', '68', 10, 68, 'Mereka (orang-orang Yahudi dan Nasrani) berkata: \"Allah mempuyai anak\". Maha Suci Allah; Dialah Yang Maha Kaya; kepunyaan-Nya apa yang ada di langit dan apa yang di bumi. Kamu tidak mempunyai hujjah tentang ini. Pantaskah kamu mengatakan terhadap Allah apa yang tidak kamu ketahui?'),
(1433, '010:069', '68', 10, 69, 'Katakanlah: \"Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tidak beruntung\".'),
(1434, '010:070', '68', 10, 70, '(Bagi mereka) kesenangan (sementara) di dunia, kemudian kepada Kami-lah mereka kembali, kemudian Kami rasakan kepada mereka siksa yang berat, disebabkan kekafiran mereka.'),
(1435, '010:071', '68', 10, 71, 'Dan bacakanIah kepada mereka berita penting tentang Nuh di waktu dia berkata kepada kaumnya: \"Hai kaumku, jika terasa berat bagimu tinggal (bersamaku) dan peringatanku (kepadamu) dengan ayat-ayat Allah, maka kepada Allah-lah aku bertawakal, karena itu bulatkanlah keputusanmu dan (kumpulkanlah) sekutu-sekutumu (untuk membinasakanku). Kemudian janganlah keputusanmu itu dirahasiakan, lalu lakukanlah terhadap diriku, dan janganlah kamu memberi tangguh kepadaku.'),
(1436, '010:072', '68', 10, 72, 'Jika kamu berpaling (dari peringatanku), aku tidak meminta upah sedikitpun dari padamu. Upahku tidak lain hanyalah dari Allah belaka, dan aku disuruh supaya aku termasuk golongan orang-orang yang berserah diri (kepada-Nya)\".'),
(1437, '010:073', '68', 10, 73, 'Lalu mereka mendustakan Nuh, maka Kami selamatkan dia dan orang-orang yang bersamanya di dalam bahtera, dan Kami jadikan mereka itu pemegang kekuasaan dan Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Maka perhatikanlah bagaimana kesesudahan orang-orang yang diberi peringatan itu.'),
(1438, '010:074', '68', 10, 74, 'Kemudian sesudah Nuh, Kami utus beberapa rasul kepada kaum mereka (masing-masing), maka rasul-rasul itu datang kepada mereka dengan membawa keterangan-keterangan yang nyata, tetapi mereka tidak hendak beriman karena mereka dahulu telah (biasa) mendustakannya. Demikianlah Kami mengunci mati hati orang-orang yang melampaui batas.'),
(1439, '010:075', '68', 10, 75, 'Kemudian sesudah rasul-rasul itu, Kami utus Musa dan Harun kepada Fir´aun dan pemuka-pemuka kaumnya, dengan (membawa) tanda-tanda (mukjizat-mukjizat) Kami, maka mereka menyombongkan diri dan mereka adalah orang-orang yang berdosa.'),
(1440, '010:076', '68', 10, 76, 'Dan tatkala telah datang kepada mereka kebenaran dari sisi Kami, mereka berkata: \"Sesungguhnya ini adalah sihir yang nyata\".'),
(1441, '010:077', '68', 10, 77, 'Musa berkata: \"Apakah kamu mengatakan terhadap kebenaran waktu ia datang kepadamu, sihirkah ini?\" padahal ahli-ahli sihir itu tidaklah mendapat kemenangan\".'),
(1442, '010:078', '68', 10, 78, 'Mereka berkata: \"Apakah kamu datang kepada kami untuk memalingkan kami dari apa yang kami dapati nenek moyang kami mengerjakannya, dan supaya kamu berdua mempunyai kekuasaan di muka bumi? Kami tidak akan mempercayai kamu berdua\".'),
(1443, '010:079', '68', 10, 79, 'Fir´aun berkata (kepada pemuka kaumnya): \"Datangkanlah kepadaku semua ahli-ahli sihir yang pandai!\"'),
(1444, '010:080', '68', 10, 80, 'Maka tatkala ahli-ahli sihir itu datang, Musa berkata kepada mereka: \"Lemparkanlah apa yang hendak kamu lemparkan\".'),
(1445, '010:081', '68', 10, 81, 'Maka setelah mereka lemparkan, Musa berkata: \"Apa yang kamu lakukan itu, itulah yang sihir, sesungguhnya Allah akan menampakkan ketidak benarannya\" Sesungguhnya Allah tidak akan membiarkan terus berlangsungnya pekerjaan orang-yang membuat kerusakan.'),
(1446, '010:082', '68', 10, 82, 'Dan Allah akan mengokohkan yang benar dengan ketetapan-Nya, walaupun orang-orang yang berbuat dosa tidak menyukai(nya).'),
(1447, '010:083', '68', 10, 83, 'Maka tidak ada yang beriman kepada Musa, melainkan pemuda-pemuda dari kaumnya (Musa) dalam keadaan takut bahwa Fir´aun dan pemuka-pemuka kaumnya akan menyiksa mereka. Sesungguhnya Fir´aun itu berbuat sewenang-wenang di muka bumi. Dan sesungguhnya dia termasuk orang-orang yang melampaui batas.'),
(1448, '010:084', '68', 10, 84, 'Berkata Musa: \"Hai kaumku, jika kamu beriman kepada Allah, maka bertawakkallah kepada-Nya saja, jika kamu benar-benar orang yang berserah diri\".'),
(1449, '010:085', '68', 10, 85, 'Lalu mereka berkata: \"Kepada Allahlah kami bertawakkal! Ya Tuhan kami; janganlah Engkau jadikan kami sasaran fitnah bagi kaum yang´zalim,'),
(1450, '010:086', '68', 10, 86, 'dan selamatkanlah kami dengan rahmat Engkau dari (tipu daya) orang-orang yang kafir\".'),
(1451, '010:087', '68', 10, 87, 'Dan Kami wahyukan kepada Musa dan saudaranya: \"Ambillah olehmu berdua beberapa buah rumah di Mesir untuk tempat tinggal bagi kaummu dan jadikanlah olehmu rumah-rumahmu itu tempat shalat dan dirikanlah olehmu sembahyang serta gembirakanlah orang-orang yang beriman\".'),
(1452, '010:088', '68', 10, 88, 'Musa berkata: \"Ya Tuhan kami, sesungguhnya Engkau telah memberi kepada Fir´aun dan pemuka-pemuka kaumnya perhiasan dan harta kekayaan dalam kehidupan dunia, ya Tuhan Kami -- akibatnya mereka menyesatkan (manusia) dari jalan Engkau. Ya Tuhan kami, binasakanlah harta benda mereka, dan kunci matilah hati mereka, maka mereka tidak beriman hingga mereka melihat siksaan yang pedih\".'),
(1453, '010:089', '68', 10, 89, 'AlIah berfirman: \"Sesungguhnya telah diperkenankan permohonan kamu berdua, sebab itu tetaplah kamu berdua pada jalan yang lurus dan janganlah sekali-kali kamu mengikuti jalan orang-orang yang tidak mengetahui\".'),
(1454, '010:090', '68', 10, 90, 'Dan Kami memungkinkan Bani Israil melintasi laut, lalu mereka diikuti oleh Fir´aun dan bala tentaranya, karena hendak menganiaya dan menindas (mereka); hingga bila Fir´aun itu telah hampir tenggelam berkatalah dia: \"Saya percaya bahwa tidak ada Tuhan melainkan Tuhan yang dipercayai oleh Bani Israil, dan saya termasuk orang-orang yang berserah diri (kepada Allah)\".'),
(1455, '010:091', '68', 10, 91, 'Apakah sekarang (baru kamu percaya), padahal sesungguhnya kamu telah durhaka sejak dahulu, dan kamu termasuk orang-orang yang berbuat kerusakan.'),
(1456, '010:092', '68', 10, 92, 'Maka pada hari ini Kami selamatkan badanmu supaya kamu dapat menjadi pelajaran bagi orang-orang yang datang sesudahmu dan sesungguhnya kebanyakan dari manusia lengah dari tanda-tanda kekuasaan Kami.'),
(1457, '010:093', '68', 10, 93, 'Dan sesungguhnya Kami telah menempatkan Bani Israil di ternpat kediaman yang bagus dan Kami beri mereka rezeki dari yang baik-baik. Maka mereka tidak berselisih, kecuali setelah datang kepada mereka pengetahuan (yang tersebut dalam Taurat). Sesungguhnya Tuhan kamu akan memutuskan antara mereka di hari kiamat tentang apa yang mereka perselisihkan itu.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1458, '010:094', '68', 10, 94, 'Maka jika kamu (Muhammad) berada dalam keragu-raguan tentang apa yang Kami turunkan kepadamu, maka tanyakanlah kepada orang-orang yang membaca kitab sebelum kamu. Sesungguhnya telah datang kebenaran kepadamu dari Tuhanmu, sebab itu janganlah sekali-kali kamu temasuk orang-orang yang ragu-ragu.'),
(1459, '010:095', '68', 10, 95, 'Dan sekali-kali janganlah kamu termasuk orang-orang yang mendustakan ayat-ayat Allah yang menyebabkan kamu termasuk orang-orang yang rugi.'),
(1460, '010:096', '68', 10, 96, 'Sesungguhnya orang-orang yang telah pasti terhadap mereka kalimat Tuhanmu, tidaklah akan beriman,'),
(1461, '010:097', '68', 10, 97, 'meskipun datang kepada mereka segala macam keterangan, hingga mereka menyaksikan azab yang pedih.'),
(1462, '010:098', '68', 10, 98, 'Dan mengapa tidak ada (penduduk) suatu kota yang beriman, lalu imannya itu bermanfaat kepadanya selain kaum Yunus? Tatkala mereka (kaum Yunus itu), beriman, Kami hilangkan dari mereka azab yang menghinakan dalam kehidupan dunia, dan Kami beri kesenangan kepada mereka sampai kepada waktu yang tertentu.'),
(1463, '010:099', '68', 10, 99, 'Dan jikalau Tuhanmu menghendaki, tentulah beriman semua orang yang di muka bumi seluruhnya. Maka apakah kamu (hendak) memaksa manusia supaya mereka menjadi orang-orang yang beriman semuanya?'),
(1464, '010:100', '68', 10, 100, 'Dan tidak ada seorangpun akan beriman kecuali dengan izin Allah; dan Allah menimpakan kemurkaan kepada orang-orang yang tidak mempergunakan akalnya.'),
(1465, '010:101', '68', 10, 101, 'Katakanlah: \"Perhatikanlah apa yaag ada di langit dan di bumi. Tidaklah bermanfaat tanda kekuasaan Allah dan rasul-rasul yang memberi peringatan bagi orang-orang yang tidak beriman\".'),
(1466, '010:102', '68', 10, 102, 'Mereka tidak menunggu-nunggu kecuali (kejadian-kejadian) yang sama dengan kejadian-kejadian (yang menimpa) orang-orang yang telah terdahulu sebelum mereka. Katakanlah: \"Maka tunggulah, sesungguhnya akupun termasuk orang-orang yang menunggu bersama kamu\".'),
(1467, '010:103', '68', 10, 103, 'Kemudian Kami selamatkan rasul-rasul Kami dan orang-orang yang beriman, demikianlah menjadi kewajiban atas Kami menyelamatkan orang-orang yang beriman.'),
(1468, '010:104', '68', 10, 104, 'Katakanlah: \"Hai manusia, jika kamu masih dalam keragu-raguan tentang agamaku, maka (ketahuilah) aku tidak menyembah yang kamu sembah selain Allah, tetapi aku menyembah Allah yang akan mematikan kamu dan aku telah diperintah supaya termasuk orang-orang yang beriman\",'),
(1469, '010:105', '68', 10, 105, 'dan (aku telah diperintah): \"Hadapkanlah mukamu kepada agama dengan tulus dan ikhlas dan janganlah kamu termasuk orang-orang yang musyrik.'),
(1470, '010:106', '68', 10, 106, 'Dan janganlah kamu menyembah apa-apa yang tidak memberi manfaat dan tidak (pula) memberi mudharat kepadamu selain Allah; sebab jika kamu berbuat (yang demikian), itu, maka sesungguhnya kamu kalau begitu termasuk orang-orang yang zalim\".'),
(1471, '010:107', '68', 10, 107, 'Jika Allah menimpakan sesuatu kemudharatan kepadamu, maka tidak ada yang dapat menghilangkannya kecuali Dia. Dan jika Allah menghendaki kebaikan bagi kamu, maka tak ada yang dapat menolak kurnia-Nya. Dia memberikan kebaikan itu kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan Dialah Yang Maha Pengampun lagi Maha Penyayang.'),
(1472, '010:108', '68', 10, 108, 'Katakanlah: \"Hai manusia, sesungguhnya teIah datang kepadamu kebenaran (Al Quran) dari Tuhanmu, sebab itu barangsiapa yang mendapat petunjuk maka sesungguhnya (petunjuk itu) untuk kebaikan dirinya sendiri. Dan barangsiapa yang sesat, maka sesungguhnya kesesatannya itu mencelakakan dirinya sendiri. Dan aku bukanlah seorang penjaga terhadap dirimu\".'),
(1473, '010:109', '68', 10, 109, 'Dan ikutilah apa yang diwahyukan kepadamu, dan bersabarlah hingga Allah memberi keputusan dan Dia adalah Hakim yang sebaik-baiknya.'),
(1474, '011:001', '68', 11, 1, 'Alif laam raa, (inilah) suatu kitab yang ayat-ayatnya disusun dengan rapi serta dijelaskan secara terperinci, yang diturunkan dari sisi (Allah) Yang Maha Bijaksana lagi Maha Tahu,'),
(1475, '011:002', '68', 11, 2, 'agar kamu tidak menyembah selain Allah. Sesungguhnya aku (Muhammad) adalah pemberi peringatan dan pembawa khabar gembira kepadamu daripada-Nya,'),
(1476, '011:003', '68', 11, 3, 'dan hendaklah kamu meminta ampun kepada Tuhanmu dan bertaubat kepada-Nya. (Jika kamu mengerjakan yang demikian), niscaya Dia akan memberi kenikmatan yang baik (terus menerus) kepadamu sampai kepada waktu yang telah ditentukan dan Dia akan memberikan kepada tiap-tiap orang yang mempunyai keutamaan (balasan) keutamaannya. Jika kamu berpaling, maka sesungguhnya aku takut kamu akan ditimpa siksa hari kiamat.'),
(1477, '011:004', '68', 11, 4, 'Kepada Allah-lah kembalimu, dan Dia Maha Kuasa atas segala sesuatu.'),
(1478, '011:005', '68', 11, 5, 'Ingatlah, sesungguhnya (orang munafik itu) memalingkan dada mereka untuk menyembunyikan diri daripadanya (Muhammad). Ingatlah, di waktu mereka menyelimuti dirinya dengan kain, Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka lahirkan, sesungguhnya Allah Maha Mengetahui segala isi hati.'),
(1479, '011:006', '68', 11, 6, 'Dan tidak ada suatu binatang melata pun di bumi melainkan Allah-lah yang memberi rezekinya, dan Dia mengetahui tempat berdiam binatang itu dan tempat penyimpanannya. Semuanya tertulis dalam Kitab yang nyata (Lauh mahfuzh).'),
(1480, '011:007', '68', 11, 7, 'Dan Dialah yang menciptakan langit dan bumi dalam enam masa, dan adalah singgasana-Nya (sebelum itu) di atas air, agar Dia menguji siapakah di antara kamu yang lebih baik amalnya, dan jika kamu berkata (kepada penduduk Mekah): \"Sesungguhnya kamu akan dibangkitkan sesudah mati\", niscaya orang-orang yang kafir itu akan berkata: \"Ini tidak lain hanyalah sihir yang nyata\".'),
(1481, '011:008', '68', 11, 8, 'Dan sesungguhnya jika Kami undurkan azab dari mereka sampai kepada suatu waktu yang ditentukan. niscaya mereka akan berkata: \"Apakah yang menghalanginya?\" lngatlah, diwaktu azab itu datang kepada mereka tidaklah dapat dipalingkan dari mereka dan mereka diliputi oleh azab yang dahulunya mereka selalu memperolok-olokkannya.'),
(1482, '011:009', '68', 11, 9, 'Dan jika Kami rasakan kepada manusia suatu rahmat (nikmat) dari Kami, kemudian rahmat itu Kami cabut daripadanya, pastilah dia menjadi putus asa lagi tidak berterima kasih.'),
(1483, '011:010', '68', 11, 10, 'Dan jika Kami rasakan kepadanya kebahagiaan sesudah bencana yang menimpanya, niscaya dia akan berkata: \"Telah hilang bencana-bencana itu daripadaku\"; sesungguhnya dia sangat gembira lagi bangga,'),
(1484, '011:011', '68', 11, 11, 'kecuali orang-orang yang sabar (terhadap bencana), dan mengerjakan amal-amal saleh; mereka itu beroleh ampunan dan pahala yang besar.'),
(1485, '011:012', '68', 11, 12, 'Maka boleh jadi kamu hendak meninggalkan sebahagian dari apa yang diwahyukan kepadamu dan sempit karenanya dadamu, karena khawatir bahwa mereka akan mengatakan: \"Mengapa tidak diturunkan kepadanya perbendaharaan (kekayaan) atau datang bersama-sama dengan dia seorang malaikat?\" Sesungguhnya kamu hanyalah seorang pemberi peringatan dan Allah Pemelihara segala sesuatu.'),
(1486, '011:013', '68', 11, 13, 'Bahkan mereka mengatakan: \"Muhammad telah membuat-buat Al Quran itu\", Katakanlah: \"(Kalau demikian), maka datangkanlah sepuluh surat-surat yang dibuat-buat yang menyamainya, dan panggillah orang-orang yang kamu sanggup (memanggilnya) selain Allah, jika kamu memang orang-orang yang benar\".'),
(1487, '011:014', '68', 11, 14, 'Jika mereka yang kamu seru itu tidak menerima seruanmu (ajakanmu) itu maka ketahuilah, sesungguhnya Al Quran itu diturunkan dengan ilmu Allah, dan bahwasanya tidak ada Tuhan selain Dia, maka maukah kamu berserah diri (kepada Allah)?'),
(1488, '011:015', '68', 11, 15, 'Barangsiapa yang menghendaki kehidupan dunia dan perhiasannya, niscaya Kami berikan kepada mereka balasan pekerjaan mereka di dunia dengan sempurna dan mereka di dunia itu tidak akan dirugikan.'),
(1489, '011:016', '68', 11, 16, 'Itulah orang-orang yang tidak memperoleh di akhirat, kecuali neraka dan lenyaplah di akhirat itu apa yang telah mereka usahakan di dunia dan sia-sialah apa yang telah mereka kerjakan.'),
(1490, '011:017', '68', 11, 17, 'Apakah (orang-orang kafir itu sama dengan) orang-orang yang ada mempunyai bukti yang nyata (Al Quran) dari Tuhannya, dan diikuti pula oleh seorang saksi (Muhammad) dari Allah dan sebelum Al Quran itu telah ada Kitab Musa yang menjadi pedoman dan rahmat? Mereka itu beriman kepada Al Quran. Dan barangsiapa di antara mereka (orang-orang Quraisy) dan sekutu-sekutunya yang kafir kepada Al Quran, maka nerakalah tempat yang diancamkan baginya, karena itu janganlah kamu ragu-ragu terhadap Al Quran itu. Sesungguhnya (Al Quran) itu benar-benar dari Tuhanmu, tetapi kebanyakan manusia tidak beriman.'),
(1491, '011:018', '68', 11, 18, 'Dan siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah? Mereka itu akan dihadapkan kepada Tuhan mereka, dan para saksi akan berkata: \"Orang-orang inilah yang telah berdusta terhadap Tuhan mereka\". Ingatlah, kutukan Allah (ditimpakan) atas orang-orang yang zalim,'),
(1492, '011:019', '68', 11, 19, '(yaitu) orang-orang yang menghalangi (manusia) dari jalan Allah dan menghendaki (supaya) jalan itu bengkok. Dan mereka itulah orang-orang yang tidak percaya akan adanya hari akhirat.'),
(1493, '011:020', '68', 11, 20, 'Orang-orang itu tidak mampu menghalang-halangi Allah untuk (mengazab mereka) di bumi ini, dan sekali-kali tidak adalah bagi mereka penolong selain Allah. Siksaan itu dilipat gandakan kepada mereka. Mereka selalu tidak dapat mendengar (kebenaran) dan mereka selalu tidak dapat melihat(nya).'),
(1494, '011:021', '68', 11, 21, 'Mereka itulah orang-orang yang merugikan dirinya sendiri, dan lenyaplah dari mereka apa yang selalu mereka ada-adakan.'),
(1495, '011:022', '68', 11, 22, 'Pasti mereka itu di akhirat menjadi orang-orang yang paling merugi.'),
(1496, '011:023', '68', 11, 23, 'Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh dan merendahkan diri kepada Tuhan mereka, mereka itu adalah penghuni-penghuni surga; mereka kekal di dalamnya.'),
(1497, '011:024', '68', 11, 24, 'Perbandingan kedua golongan itu (orang-orang kafir dan orang-orang mukmin), seperti orang buta dan tuli dengan orang yang dapat melihat dan dapat mendengar. Adakah kedua golongan itu sama keadaan dan sifatnya? Maka tidakkah kamu mengambil pelajaran (daripada perbandingan itu)?'),
(1498, '011:025', '68', 11, 25, 'Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, (dia berkata): \"Sesungguhnya aku adalah pemberi peringatan yang nyata bagi kamu,'),
(1499, '011:026', '68', 11, 26, 'agar kamu tidak menyembah selain Allah. Sesungguhnya aku takut kamu akan ditimpa azab (pada) hari yang sangat menyedihkan\".'),
(1500, '011:027', '68', 11, 27, 'Maka berkatalah pemimpin-pemimpin yang kafir dari kaumnya: \"Kami tidak melihat kamu, melainkan (sebagai) seorang manusia (biasa) seperti kami, dan kami tidak melihat orang-orang yang mengikuti kamu, melainkan orang-orang yang hina dina di antara kami yang lekas percaya saja, dan kami tidak melihat kamu memiliki sesuatu kelebihan apapun atas kami, bahkan kami yakin bahwa kamu adalah orang-orang yang dusta\".'),
(1501, '011:028', '68', 11, 28, 'Berkata Nuh: \"Hai kaumku, bagaimana pikiranmu, jika aku ada mempunyai bukti yang nyata dari Tuhanku, dan diberinya aku rahmat dari sisi-Nya, tetapi rahmat itu disamarkan bagimu. Apa akan kami paksakankah kamu menerimanya, padahal kamu tiada menyukainya?\"'),
(1502, '011:029', '68', 11, 29, 'Dan (dia berkata): \"Hai kaumku, aku tiada meminta harta benda kepada kamu (sebagai upah) bagi seruanku. Upahku hanyalah dari Allah dan aku sekali-kali tidak akan mengusir orang-orang yang telah beriman. Sesungguhnya mereka akan bertemu dengan Tuhannya, akan tetapi aku memandangmu suatu kaum yang tidak mengetahui\".'),
(1503, '011:030', '68', 11, 30, 'Dan (dia berkata): \"Hai kaumku, siapakah yang akan menolongku dari (azab) Allah jika aku mengusir mereka. Maka tidakkah kamu mengambil pelajaran?'),
(1504, '011:031', '68', 11, 31, 'Dan aku tidak mengatakan kepada kamu (bahwa): \"Aku mempunyai gudang-gudang rezeki dan kekayaan dari Allah, dan aku tiada mengetahui yang ghaib\", dan tidak (pula) aku mengatakan: \"Bahwa sesungguhnya aku adalah malaikat\", dan tidak juga aku mengatakan kepada orang-orang yang dipandang hina oleh penglihatanmu: \"Sekali-kali Allah tidak akan mendatangkan kebaikan kepada mereka\". Allah lebih mengetahui apa yang ada pada diri mereka; sesungguhnya aku, kalau begitu benar-benar termasuk orang-orang yang zalim.'),
(1505, '011:032', '68', 11, 32, 'Mereka berkata \"Hai Nuh, sesungguhnya kamu telah berbantah dengan kami, dan kamu telah memperpanjang bantahanmu terhadap kami, maka datangkanlah kepada kami azab yang kamu ancamkan kepada kami, jika kamu termasuk orang-orang yang benar\".'),
(1506, '011:033', '68', 11, 33, 'Nuh menjawab: \"Hanyalah Allah yang akan mendatangkan azab itu kepadamu jika Dia menghendaki, dan kamu sekali-kali tidak dapat melepaskan diri.'),
(1507, '011:034', '68', 11, 34, 'Dan tidaklah bermanfaat kepadamu nasehatku jika aku hendak memberi nasehat kepada kamu, sekiranya Allah hendak menyesatkan kamu, Dia adalah Tuhanmu, dan kepada-Nya-lah kamu dikembalikan\".'),
(1508, '011:035', '68', 11, 35, 'Malahan kaum Nuh itu berkata: \"Dia cuma membuat-buat nasihatnya saja\". Katakanlah: \"Jika aku membuat-buat nasihat itu, maka hanya akulah yang memikul dosaku, dan aku berlepas diri dari dosa yang kamu perbuat\".'),
(1509, '011:036', '68', 11, 36, 'Dan diwahyukan kepada Nuh, bahwasanya sekali-kali tidak akan beriman di antara kaummu, kecuali orang yang telah beriman (saja), karena itu janganlah kamu bersedih hati tentang apa yang selalu mereka kerjakan.'),
(1510, '011:037', '68', 11, 37, 'Dan buatlah bahtera itu dengan pengawasan dan petunjuk wahyu Kami, dan janganlah kamu bicarakan dengan Aku tentang orang-orang yang zalim itu; sesungguhnya mereka itu akan ditenggelamkan.'),
(1511, '011:038', '68', 11, 38, 'Dan mulailah Nuh membuat bahtera. Dan setiap kali pemimpin kaumnya berjalan meliwati Nuh, mereka mengejeknya. Berkatalah Nuh: \"Jika kamu mengejek kami, maka sesungguhnya kami (pun) mengejekmu sebagaimana kamu sekalian mengejek (kami).'),
(1512, '011:039', '68', 11, 39, 'Kelak kamu akan mengetahui siapa yang akan ditimpa oleh azab yang menghinakannya dan yang akan ditimpa azab yang kekal\".'),
(1513, '011:040', '68', 11, 40, 'Hingga apabila perintah Kami datang dan dapur telah memancarkan air, Kami berfirman: \"Muatkanlah ke dalam bahtera itu dari masing-masing binatang sepasang (jantan dan betina), dan keluargamu kecuali orang yang telah terdahulu ketetapan terhadapnya dan (muatkan pula) orang-orang yang beriman\". Dan tidak beriman bersama dengan Nuh itu kecuali sedikit.'),
(1514, '011:041', '68', 11, 41, 'Dan Nuh berkata: \"Naiklah kamu sekalian ke dalamnya dengan menyebut nama Allah di waktu berlayar dan berlabuhnya\". Sesungguhnya Tuhanku benar-benar Maha Pengampun lagi Maha Penyayang.'),
(1515, '011:042', '68', 11, 42, 'Dan bahtera itu berlayar membawa mereka dalam gelombang laksana gunung. Dan Nuh memanggil anaknya, sedang anak itu berada di tempat yang jauh terpencil: \"Hai anakku, naiklah (ke kapal) bersama kami dan janganlah kamu berada bersama orang-orang yang kafir\".'),
(1516, '011:043', '68', 11, 43, 'Anaknya menjawab: \"Aku akan mencari perlindungan ke gunung yang dapat memeliharaku dari air bah!\" Nuh berkata: \"Tidak ada yang melindungi hari ini dari azab Allah selain Allah (saja) Yang Maha Penyayang\". Dan gelombang menjadi penghalang antara keduanya; maka jadilah anak itu termasuk orang-orang yang ditenggelamkan.'),
(1517, '011:044', '68', 11, 44, 'Dan difirmankan: \"Hai bumi telanlah airmu, dan hai langit (hujan) berhentilah,\" dan airpun disurutkan, perintahpun diselesaikan dan bahtera itupun berlabuh di atas bukit Judi, dan dikatakan: \"Binasalah orang-orang yang zalim\".'),
(1518, '011:045', '68', 11, 45, 'Dan Nuh berseru kepada Tuhannya sambil berkata: \"Ya Tuhanku, sesungguhnya anakku termasuk keluargaku, dan sesungguhnya janji Engkau itulah yang benar. Dan Engkau adalah Hakim yang seadil-adilnya\".'),
(1519, '011:046', '68', 11, 46, 'Allah berfirman: \"Hai Nuh, sesungguhnya dia bukanlah termasuk keluargamu (yang dijanjikan akan diselamatkan), sesungguhnya (perbuatan)nya perbuatan yang tidak baik. Sebab itu janganlah kamu memohon kepada-Ku sesuatu yang kamu tidak mengetahui (hakekat)nya. Sesungguhnya Aku memperingatkan kepadamu supaya kamu jangan termasuk orang-orang yang tidak berpengetahuan\".'),
(1520, '011:047', '68', 11, 47, 'Nuh berkata: Ya Tuhanku, sesungguhnya aku berlindung kepada Engkau dari memohon kepada Engkau sesuatu yang aku tiada mengetahui (hakekat)nya. Dan sekiranya Engkau tidak memberi ampun kepadaku, dan (tidak) menaruh belas kasihan kepadaku, niscaya aku akan termasuk orang-orang yang merugi\".'),
(1521, '011:048', '68', 11, 48, 'Difirmankan: \"Hai Nuh, turunlah dengan selamat sejahtera dan penuh keberkatan dari Kami atasmu dan atas umat-umat (yang mukmin) dari orang-orang yang bersamamu. Dan ada (pula) umat-umat yang Kami beri kesenangan pada mereka (dalam kehidupan dunia), kemudian mereka akan ditimpa azab yang pedih dari Kami\".'),
(1522, '011:049', '68', 11, 49, 'Itu adalah di antara berita-berita penting tentang yang ghaib yang Kami wahyukan kepadamu (Muhammad); tidak pernah kamu mengetahuinya dan tidak (pula) kaummu sebelum ini. Maka bersabarlah; sesungguhnya kesudahan yang baik adalah bagi orang-orang yang bertakwa.'),
(1523, '011:050', '68', 11, 50, 'Dan kepada kaum ´Ad (Kami utus) saudara mereka, Huud. Ia berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tidak ada bagimu Tuhan selain Dia. Kamu hanyalah mengada-adakan saja.'),
(1524, '011:051', '68', 11, 51, 'Hai kaumku, aku tidak meminta upah kepadamu bagi seruanku ini. Upahku tidak lain hanyalah dari Allah yang telah menciptakanku. Maka tidakkah kamu memikirkan(nya)?\"'),
(1525, '011:052', '68', 11, 52, 'Dan (dia berkata): \"Hai kaumku, mohonlah ampun kepada Tuhanmu lalu bertobatlah kepada-Nya, niscaya Dia menurunkan hujan yang sangat deras atasmu, dan Dia akan menambahkan kekuatan kepada kekuatanmu, dan janganlah kamu berpaling dengan berbuat dosa\".'),
(1526, '011:053', '68', 11, 53, 'Kaum ´Ad berkata: \"Hai Huud, kamu tidak mendatangkan kepada kami suatu bukti yang nyata, dan kami sekali-kali tidak akan meninggalkan sembahan-sembahan kami karena perkataanmu, dan kami sekali-kali tidak akan mempercayai kamu.'),
(1527, '011:054', '68', 11, 54, 'Kami tidak mengatakan melainkan bahwa sebagian sembahan kami telah menimpakan penyakit gila atas dirimu\". Huud menjawab: \"Sesungguhnya aku bersaksi kepada Allah dan saksikanlah olehmu sekalian bahwa sesungguhnya aku berlepas diri dari apa yang kamu persekutukan,'),
(1528, '011:055', '68', 11, 55, 'dari selain-Nya, sebab itu jalankanlah tipu dayamu semuanya terhadapku dan janganlah kamu memberi tangguh kepadaku.'),
(1529, '011:056', '68', 11, 56, 'Sesungguhnya aku bertawakkal kepada Allah Tuhanku dan Tuhanmu. Tidak ada suatu binatang melatapun melainkan Dialah yang memegang ubun-ubunnya. Sesungguhnya Tuhanku di atas jalan yang lurus\".'),
(1530, '011:057', '68', 11, 57, 'Jika kamu berpaling, maka sesungguhnya aku telah menyampaikan kepadamu apa (amanat) yang aku diutus (untuk menyampaikan)nya kepadamu. Dan Tuhanku akan mengganti (kamu) dengan kaum yang lain (dari) kamu; dan kamu tidak dapat membuat mudharat kepada-Nya sedikitpun. Sesungguhnya Tuhanku adalah Maha Pemelihara segala sesuatu.'),
(1531, '011:058', '68', 11, 58, 'Dan tatkala datang azab Kami, Kami selamatkan Huud dan orang-orang yang beriman bersama dia dengan rahmat dari Kami; dan Kami selamatkan (pula) mereka (di akhirat) dari azab yang berat.'),
(1532, '011:059', '68', 11, 59, 'Dan itulah (kisah) kaum ´Ad yang mengingkari tanda-tanda kekuasaan Tuhan mereka, dan mendurhakai rasul-rasul Allah dan mereka menuruti perintah semua penguasa yang sewenang-wenang lagi menentang (kebenaran).'),
(1533, '011:060', '68', 11, 60, 'Dan mereka selalu diikuti dengan kutukan di dunia ini dan (begitu pula) di hari kiamat. Ingatlah, sesungguhnya kaum ´Ad itu kafir kepada Tuhan mereka. Ingatlah kebinasaanlah bagi kaum ´Ad (yaitu) kaum Huud itu.'),
(1534, '011:061', '68', 11, 61, 'Dan kepada Tsamud (Kami utus) saudara mereka Shaleh. Shaleh berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tidak ada bagimu Tuhan selain Dia. Dia telah menciptakan kamu dari bumi (tanah) dan menjadikan kamu pemakmurnya, karena itu mohonlah ampunan-Nya, kemudian bertobatlah kepada-Nya, Sesungguhnya Tuhanku amat dekat (rahmat-Nya) lagi memperkenankan (doa hamba-Nya)\".'),
(1535, '011:062', '68', 11, 62, 'Kaum Tsamud berkata: \"Hai Shaleh, sesungguhnya kamu sebelum ini adalah seorang di antara kami yang kami harapkan, apakah kamu melarang kami untuk menyembah apa yang disembah oleh bapak-bapak kami? dan sesungguhnya kami betul-betul dalam keraguan yang menggelisahkan terhadap agama yang kamu serukan kepada kami\".'),
(1536, '011:063', '68', 11, 63, 'Shaleh berkata: \"Hai kaumku, bagaimana pikiranmu jika aku mempunyai bukti yang nyata dari Tuhanku dan diberi-Nya aku rahmat (kenabian) dari-Nya, maka siapakah yang akan menolong aku dari (azab) Allah jika aku mendurhakai-Nya. Sebab itu kamu tidak menambah apapun kepadaku selain daripada kerugian.'),
(1537, '011:064', '68', 11, 64, 'Hai kaumku, inilah unta betina dari Allah, sebagai mukjizat (yang menunjukkan kebenaran) untukmu, sebab itu biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apapun yang akan menyebabkan kamu ditimpa azab yang dekat\".'),
(1538, '011:065', '68', 11, 65, 'Mereka membunuh unta itu, maka berkata Shaleh: \"Bersukarialah kamu sekalian di rumahmu selama tiga hari, itu adalah janji yang tidak dapat didustakan\".'),
(1539, '011:066', '68', 11, 66, 'Maka tatkala datang azab Kami, Kami selamatkan Shaleh beserta orang-orang yang beriman bersama dia dengan rahmat dari Kami dan dari kehinaan di hari itu. Sesungguhnya Tuhanmu Dia-Lah yang Maha Kuat lagi Maha Perkasa.'),
(1540, '011:067', '68', 11, 67, 'Dan satu suara keras yang mengguntur menimpa orang-orang yang zalim itu, lalu mereka mati bergelimpangan di rumahnya,'),
(1541, '011:068', '68', 11, 68, 'seolah-olah mereka belum pernah berdiam di tempat itu. Ingatlah, sesungguhnya kaum Tsamud mengingkari Tuhan mereka. Ingatlah, kebinasaanlah bagi kaum Tsamud.'),
(1542, '011:069', '68', 11, 69, 'Dan sesungguhnya utusan-utusan Kami (malaikat-malaikat) telah datang kepada lbrahim dengan membawa kabar gembira, mereka mengucapkan: \"Selamat\". Ibrahim menjawab: \"Selamatlah,\" maka tidak lama kemudian Ibrahim menyuguhkan daging anak sapi yang dipanggang.'),
(1543, '011:070', '68', 11, 70, 'Maka tatkala dilihatnya tangan mereka tidak menjamahnya, Ibrahim memandang aneh perbuatan mereka, dan merasa takut kepada mereka. Malaikat itu berkata: \"Jangan kamu takut, sesungguhnya kami adalah (malaikat-ma]aikat) yang diutus kepada kaum Luth\".'),
(1544, '011:071', '68', 11, 71, 'Dan isterinya berdiri (dibalik tirai) lalu dia tersenyum, maka Kami sampaikan kepadanya berita gembira tentang (kelahiran) Ishak dan dari Ishak (akan lahir puteranya) Ya´qub.'),
(1545, '011:072', '68', 11, 72, 'Isterinya berkata: \"Sungguh mengherankan, apakah aku akan melahirkan anak padahal aku adalah seorang perempuan tua, dan ini suamikupun dalam keadaan yang sudah tua pula?. Sesungguhnya ini benar-benar suatu yang sangat aneh\".'),
(1546, '011:073', '68', 11, 73, 'Para malaikat itu berkata: \"Apakah kamu merasa heran tentang ketetapan Allah? (Itu adalah) rahmat Allah dan keberkatan-Nya, dicurahkan atas kamu, hai ahlulbait! Sesungguhnya Allah Maha Terpuji lagi Maha Pemurah\".'),
(1547, '011:074', '68', 11, 74, 'Maka tatkala rasa takut hilang dari Ibrahim dan berita gembira telah datang kepadanya, diapun bersoal jawab dengan (malaikat-malaikat) Kami tentang kaum Luth.'),
(1548, '011:075', '68', 11, 75, 'Sesungguhnya Ibrahim itu benar-benar seorang yang penyantun lagi penghiba dan suka kembali kepada Allah.'),
(1549, '011:076', '68', 11, 76, 'Hai Ibrahim, tinggalkanlah soal jawab ini, sesungguhnya telah datang ketetapan Tuhanmu, dan sesungguhnya mereka itu akan didatangi azab yang tidak dapat ditolak.'),
(1550, '011:077', '68', 11, 77, 'Dan tatkala datang utusan-utusan Kami (para malaikat) itu kepada Luth, dia merasa susah dan merasa sempit dadanya karena kedatangan mereka, dan dia berkata: \"Ini adalah hari yang amat sulit\".'),
(1551, '011:078', '68', 11, 78, 'Dan datanglah kepadanya kaumnya dengan bergegas-gegas. Dan sejak dahulu mereka selalu melakukan perbuatan-perbuatan yang keji. Luth berkata: \"Hai kaumku, inilah puteri-puteriku, mereka lebih suci bagimu, maka bertakwalah kepada Allah dan janganlah kamu mencemarkan (nama)ku terhadap tamuku ini. Tidak adakah di antaramu seorang yang berakal?\"'),
(1552, '011:079', '68', 11, 79, 'Mereka menjawab: \"Sesungguhnya kamu telah tahu bahwa kami tidak mempunyai keinginan terhadap puteri-puterimu; dan sesungguhnya kamu tentu mengetahui apa yang sebenarnya kami kehendaki\".'),
(1553, '011:080', '68', 11, 80, 'Luth berkata: \"Seandainya aku ada mempunyai kekuatan (untuk menolakmu) atau kalau aku dapat berlindung kepada keluarga yang kuat (tentu aku lakukan)\".'),
(1554, '011:081', '68', 11, 81, 'Para utusan (malaikat) berkata: \"Hai Luth, sesungguhnya kami adalah utusan-utusan Tuhanmu, sekali-kali mereka tidak akan dapat mengganggu kamu, sebab itu pergilah dengan membawa keluarga dan pengikut-pengikut kamu di akhir malam dan janganlah ada seorangpun di antara kamu yang tertinggal, kecuali isterimu. Sesungguhnya dia akan ditimpa azab yang menimpa mereka karena sesungguhnya saat jatuhnya azab kepada mereka ialah di waktu subuh; bukankah subuh itu sudah dekat?\".'),
(1555, '011:082', '68', 11, 82, 'Maka tatkala datang azab Kami, Kami jadikan negeri kaum Luth itu yang di atas ke bawah (Kami balikkan), dan Kami hujani mereka dengan batu dari tanah yang terbakar dengan bertubi-tubi,'),
(1556, '011:083', '68', 11, 83, 'Yang diberi tanda oleh Tuhanmu, dan siksaan itu tiadalah jauh dari orang-orang yang zalim.'),
(1557, '011:084', '68', 11, 84, 'Dan kepada (penduduk) Mad-yan (Kami utus) saudara mereka, Syu´aib. Ia berkata: \"Hai kaumku, sembahlah Allah, sekali-kali tiada Tuhan bagimu selain Dia. Dan janganlah kamu kurangi takaran dan timbangan, sesungguhnya aku melihat kamu dalam keadaan yang baik (mampu) dan sesungguhnya aku khawatir terhadapmu akan azab hari yang membinasakan (kiamat)\".'),
(1558, '011:085', '68', 11, 85, 'Dan Syu´aib berkata: \"Hai kaumku, cukupkanlah takaran dan timbangan dengan adil, dan janganlah kamu merugikan manusia terhadap hak-hak mereka dan janganlah kamu membuat kejahatan di muka bumi dengan membuat kerusakan.'),
(1559, '011:086', '68', 11, 86, 'Sisa (keuntungan) dari Allah adalah lebih baik bagimu jika kamu orang-orang yang beriman. Dan aku bukanlah seorang penjaga atas dirimu\"'),
(1560, '011:087', '68', 11, 87, 'Mereka berkata: \"Hai Syu´aib, apakah sembahyangmu menyuruh kamu agar kami meninggalkan apa yang disembah oleh bapak-bapak kami atau melarang kami memperbuat apa yang kami kehendaki tentang harta kami. Sesungguhnya kamu adalah orang yang sangat penyantun lagi berakal\".'),
(1561, '011:088', '68', 11, 88, 'Syu´aib berkata: \"Hai kaumku, bagaimana pikiranmu jika aku mempunyai bukti yang nyata dari Tuhanku dan dianugerahi-Nya aku dari pada-Nya rezeki yang baik (patutkah aku menyalahi perintah-Nya)? Dan aku tidak berkehendak menyalahi kamu (dengan mengerjakan) apa yang aku larang. Aku tidak bermaksud kecuali (mendatangkan) perbaikan selama aku masih berkesanggupan. Dan tidak ada taufik bagiku melainkan dengan (pertolongan) Allah. Hanya kepada Allah aku bertawakkal dan hanya kepada-Nya-lah aku kembali.'),
(1562, '011:089', '68', 11, 89, 'Hai kaumku, janganlah hendaknya pertentangan antara aku (dengan kamu) menyebabkan kamu menjadi jahat hingga kamu ditimpa azab seperti yang menimpa kaum Nuh atau kaum Hud atau kaum Shaleh, sedang kaum Luth tidak (pula) jauh (tempatnya) dari kamu.'),
(1563, '011:090', '68', 11, 90, 'Dan mohonlah ampun kepada Tuhanmu kemudian bertaubatlah kepada-Nya. Sesungguhnya Tuhanku Maha Penyayang lagi Maha Pengasih.'),
(1564, '011:091', '68', 11, 91, 'Mereka berkata: \"Hai Syu´aib, kami tidak banyak mengerti tentang apa yang kamu katakan itu dan sesungguhnya kami benar-benar melihat kamu seorang yang lemah di antara kami; kalau tidaklah karena keluargamu tentulah kami telah merajam kamu, sedang kamupun bukanlah seorang yang berwibawa di sisi kami\".'),
(1565, '011:092', '68', 11, 92, 'Syu´aib menjawab: \"Hai kaumku, apakah keluargaku lebih terhormat menurut pandanganmu daripada Allah, sedang Allah kamu jadikan sesuatu yang terbuang di belakangmu?. Sesungguhnya (pengetahuan) Tuhanku meliputi apa yang kamu kerjakan\".'),
(1566, '011:093', '68', 11, 93, 'Dan (dia berkata): \"Hai kaumku, berbuatlah menurut kemampuanmu, sesungguhnya akupun berbuat (pula). Kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakannya dan siapa yang berdusta. Dan tunggulah azab (Tuhan), sesungguhnya akupun menunggu bersama kamu\".'),
(1567, '011:094', '68', 11, 94, 'Dan tatkala datang azab Kami, Kami selamatkan Syu´aib dan orang-orang yang beriman bersama-sama dengan dia dengan rahmat dari Kami, dan orang-orang yang zalim dibinasakan oleh satu suara yang mengguntur, lalu jadilah mereka mati bergelimpangan di rumahnya.'),
(1568, '011:095', '68', 11, 95, 'Seolah-olah mereka belum pernah berdiam di tempat itu. Ingatlah, kebinasaanlah bagi penduduk Mad-yan sebagaimana kaum Tsamud telah binasa.'),
(1569, '011:096', '68', 11, 96, 'Dan sesungguhnya Kami telah mengutus Musa dengan tanda-tanda (kekuasaan) Kami dan mukjizat yang nyata,'),
(1570, '011:097', '68', 11, 97, 'kepada Fir´aun dan pemimpin-pemimpin kaumnya, tetapi mereka mengikut perintah Fir´aun, padahal perintah Fir´aun sekali-kali bukanlah (perintah) yang benar.'),
(1571, '011:098', '68', 11, 98, 'Ia berjalan di muka kaumnya di hari kiamat lalu memasukkan mereka ke dalam neraka. Neraka itu seburuk-buruk tempat yang didatangi.'),
(1572, '011:099', '68', 11, 99, 'Dan mereka selalu diikuti dengan kutukan di dunia ini dan (begitu pula) di hari kiamat. La´nat itu seburuk-buruk pemberian yang diberikan.'),
(1573, '011:100', '68', 11, 100, 'Itu adalah sebahagian dan berita-berita negeri (yang telah dibinasakan) yang Kami ceritakan kepadamu (Muhammad); di antara negeri-negeri itu ada yang masih kedapatan bekas-bekasnya dan ada (pula) yang telah musnah.'),
(1574, '011:101', '68', 11, 101, 'Dan Kami tidaklah menganiaya mereka tetapi merekalah yang menganiaya diri mereka sendiri, karena itu tiadalah bermanfaat sedikitpun kepada mereka sembahan-sembahan yang mereka seru selain Allah, di waktu azab Tuhanmu datang. Dan sembahan-sembahan itu tidaklah menambah kepada mereka kecuali kebinasaan belaka.'),
(1575, '011:102', '68', 11, 102, 'Dan begitulah azab Tuhanmu, apabila Dia mengazab penduduk negeri-negeri yang berbuat zalim. Sesungguhnya azab-Nya itu adalah sangat pedih lagi keras.'),
(1576, '011:103', '68', 11, 103, 'Sesungguhnya pada yang demikian itu benar-benar terdapat pelajaran bagi orang-orang yang takut kepada azab akhirat. Hari kiamat itu adalah suatu hari yang semua manusia dikumpulkan untuk (menghadapi)nya, dan hari itu adalah suatu hari yang disaksikan (oleh segala makhluk).'),
(1577, '011:104', '68', 11, 104, 'Dan Kami tiadalah mengundurkannya, melainkan sampai waktu yang tertentu.'),
(1578, '011:105', '68', 11, 105, 'Di kala datang hari itu, tidak ada seorangun yang berbicara, melainkan dengan izin-Nya; maka di antara mereka ada yang celaka dan ada yang berbahagia.'),
(1579, '011:106', '68', 11, 106, 'Adapun orang-orang yang celaka, maka (tempatnya) di dalam neraka, di dalamnya mereka mengeluarkan dan menarik nafas (dengan merintih),'),
(1580, '011:107', '68', 11, 107, 'mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain). Sesungguhnya Tuhanmu Maha Pelaksana terhadap apa yang Dia kehendaki.'),
(1581, '011:108', '68', 11, 108, 'Adapun orang-orang yang berbahagia, maka tempatnya di dalam surga, mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain); sebagai karunia yang tiada putus-putusnya.'),
(1582, '011:109', '68', 11, 109, 'Maka janganlah kamu berada dalam keragu-raguan tentang apa yang disembah oleh mereka. Mereka tidak menyembah melainkan sebagaimana nenek moyang mereka menyembah dahulu. Dan sesungguhnya Kami pasti akan menyempurnakan dengan secukup-cukupnya pembalasan (terhadap) mereka dengan tidak dikurangi sedikitpun.'),
(1583, '011:110', '68', 11, 110, 'Dan sesungguhnya Kami telah memberikan Kitab (Taurat) kepada Musa, lalu diperselisihkan tentang Kitab itu. Dan seandainya tidak ada ketetapan yang telah terdahulu dari Tuhanmu, niscaya telah ditetapkan hukuman di antara mereka. Dan sesungguhnya mereka (orang-orang kafir Mekah) dalam keraguan yang menggelisahkan terhadap Al Quran.'),
(1584, '011:111', '68', 11, 111, 'Dan sesungguhnya kepada masing-masing (mereka yang berselisih itu) pasti Tuhanmu akan menyempurnakan dengan cukup, (balasan) pekerjaan mereka. Sesungguhnya Dia Maha Mengetahui apa yang mereka kerjakan.'),
(1585, '011:112', '68', 11, 112, 'Maka tetaplah kamu pada jalan yang benar, sebagaimana diperintahkan kepadamu dan (juga) orang yang telah taubat beserta kamu dan janganlah kamu melampaui batas. Sesungguhnya Dia Maha Melihat apa yang kamu kerjakan.'),
(1586, '011:113', '68', 11, 113, 'Dan janganlah kamu cenderung kepada orang-orang yang zalim yang menyebabkan kamu disentuh api neraka, dan sekali-kali kamu tiada mempunyai seorang penolongpun selain daripada Allah, kemudian kamu tidak akan diberi pertolongan.'),
(1587, '011:114', '68', 11, 114, 'Dan dirikanlah sembahyang itu pada kedua tepi siang (pagi dan petang) dan pada bahagian permulaan daripada malam. Sesungguhnya perbuatan-perbuatan yang baik itu menghapuskan (dosa) perbuatan-perbuatan yang buruk. Itulah peringatan bagi orang-orang yang ingat.'),
(1588, '011:115', '68', 11, 115, 'Dan bersabarlah, karena sesungguhnya Allah tiada menyia-nyiakan pahala orang-orang yang berbuat kebaikan.'),
(1589, '011:116', '68', 11, 116, 'Maka mengapa tidak ada dari umat-umat yang sebelum kamu orang-orang yang mempunyai keutamaan yang melarang daripada (mengerjakan) kerusakan di muka bumi, kecuali sebahagian kecil di antara orang-orang yang telah Kami selamatkan di antara mereka, dan orang-orang yang zalim hanya mementingkan kenikmatan yang mewah yang ada pada mereka, dan mereka adalah orang-orang yang berdosa.'),
(1590, '011:117', '68', 11, 117, 'Dan Tuhanmu sekali-kali tidak akan membinasakan negeri-negeri secara zalim, sedang penduduknya orang-orang yang berbuat kebaikan.'),
(1591, '011:118', '68', 11, 118, 'Jikalau Tuhanmu menghendaki, tentu Dia menjadikan manusia umat yang satu, tetapi mereka senantiasa berselisih pendapat,'),
(1592, '011:119', '68', 11, 119, 'kecuali orang-orang yang diberi rahmat oleh Tuhanmu. Dan untuk itulah Allah menciptakan mereka. Kalimat Tuhanmu (keputusan-Nya) telah ditetapkan: sesungguhnya Aku akan memenuhi neraka Jahannam dengan jin dan manusia (yang durhaka) semuanya.'),
(1593, '011:120', '68', 11, 120, 'Dan semua kisah dari rasul-rasul Kami ceritakan kepadamu, ialah kisah-kisah yang dengannya Kami teguhkan hatimu; dan dalam surat ini telah datang kepadamu kebenaran serta pengajaran dan peringatan bagi orang-orang yang beriman.'),
(1594, '011:121', '68', 11, 121, 'Dan katakanlah kepada orang-orang yang tidak beriman: \"Berbuatlah menurut kemampuanmu; sesungguhnya Kami-pun berbuat (pula)\".'),
(1595, '011:122', '68', 11, 122, 'Dan tunggulah (akibat perbuatanmu); sesungguhnya kamipun menunggu (pula)\".'),
(1596, '011:123', '68', 11, 123, 'Dan kepunyaan Allah-lah apa yang ghaib di langit dan di bumi dan kepada-Nya-lah dikembalikan urusan-urusan semuanya, maka sembahlah Dia, dan bertawakkallah kepada-Nya. Dan sekali-kali Tuhanmu tidak lalai dari apa yang kamu kerjakan.'),
(1597, '012:001', '68', 12, 1, 'Alif, laam, raa. Ini adalah ayat-ayat Kitab (Al Quran) yang nyata (dari Allah).'),
(1598, '012:002', '68', 12, 2, 'Sesungguhnya Kami menurunkannya berupa Al Quran dengan berbahasa Arab, agar kamu memahaminya.'),
(1599, '012:003', '68', 12, 3, 'Kami menceritakan kepadamu kisah yang paling baik dengan mewahyukan Al Quran ini kepadamu, dan sesungguhnya kamu sebelum (Kami mewahyukan)nya adalah termasuk orang-orang yang belum mengetahui.'),
(1600, '012:004', '68', 12, 4, '(Ingatlah), ketika Yusuf berkata kepada ayahnya: \"Wahai ayahku, sesungguhnya aku bermimpi melihat sebelas bintang, matahari dan bulan; kulihat semuanya sujud kepadaku\".'),
(1601, '012:005', '68', 12, 5, 'Ayahnya berkata: \"Hai anakku, janganlah kamu ceritakan mimpimu itu kepada saudara-saudaramu, maka mereka membuat makar (untuk membinasakan)mu. Sesungguhnya syaitan itu adalah musuh yang nyata bagi manusia\".'),
(1602, '012:006', '68', 12, 6, 'Dan demikianlah Tuhanmu, memilih kamu (untuk menjadi Nabi) dan diajarkan-Nya kepadamu sebahagian dari ta´bir mimpi-mimpi dan disempurnakan-Nya nikmat-Nya kepadamu dan kepada keluarga Ya´qub, sebagaimana Dia telah menyempurnakan nikmat-Nya kepada dua orang bapakmu sebelum itu, (yaitu) Ibrahim dan Ishak. Sesungguhnya Tuhanmu Maha Mengetahui lagi Maha Bijaksana.'),
(1603, '012:007', '68', 12, 7, 'Sesungguhnya ada beberapa tanda-tanda kekuasaan Allah pada (kisah) Yusuf dan saudara-saudaranya bagi orang-orang yang bertanya.'),
(1604, '012:008', '68', 12, 8, '(Yaitu) ketika mereka berkata: \"Sesungguhnya Yusuf dan saudara kandungnya (Bunyamin) lebih dicintai oleh ayah kita dari pada kita sendiri, padahal kita (ini) adalah satu golongan (yang kuat). Sesungguhnya ayah kita adalah dalam kekeliruan yang nyata.'),
(1605, '012:009', '68', 12, 9, 'Bunuhlah Yusuf atau buanglah dia kesuatu daerah (yang tak dikenal) supaya perhatian ayahmu tertumpah kepadamu saja, dan sesudah itu hendaklah kamu menjadi orang-orang yang baik\".'),
(1606, '012:010', '68', 12, 10, 'Seorang diantara mereka berkata: \"Janganlah kamu bunuh Yusuf, tetapi masukkanlah dia ke dasar sumur supaya dia dipungut oleh beberapa orang musafir, jika kamu hendak berbuat\".'),
(1607, '012:011', '68', 12, 11, 'Mereka berkata: \"Wahai ayah kami, apa sebabnya kamu tidak mempercayai kami terhadap Yusuf, padahal sesungguhnya kami adalah orang-orang yang mengingini kebaikan baginya.'),
(1608, '012:012', '68', 12, 12, 'Biarkanlah dia pergi bersama kami besok pagi, agar dia (dapat) bersenang-senang dan (dapat) bermain-main, dan sesungguhnya kami pasti menjaganya\".'),
(1609, '012:013', '68', 12, 13, 'Berkata Ya´qub: \"Sesungguhnya kepergian kamu bersama Yusuf amat menyedihkanku dan aku khawatir kalau-kalau dia dimakan serigala, sedang kamu lengah dari padanya\".'),
(1610, '012:014', '68', 12, 14, 'Mereka berkata: \"Jika ia benar-benar dimakan serigala, sedang kami golongan (yang kuat), sesungguhnya kami kalau demikian adalah orang-orang yang merugi\".'),
(1611, '012:015', '68', 12, 15, 'Maka tatkala mereka membawanya dan sepakat memasukkannya ke dasar sumur (lalu mereka masukkan dia), dan (di waktu dia sudah dalam sumur) Kami wahyukan kepada Yusuf: \"Sesungguhnya kamu akan menceritakan kepada mereka perbuatan mereka ini, sedang mereka tiada ingat lagi\".'),
(1612, '012:016', '68', 12, 16, 'Kemudian mereka datang kepada ayah mereka di sore hari sambil menangis.'),
(1613, '012:017', '68', 12, 17, 'Mereka berkata: \"Wahai ayah kami, sesungguhnya kami pergi berlomba-lomba dan kami tinggalkan Yusuf di dekat barang-barang kami, lalu dia dimakan serigala; dan kamu sekali-kali tidak akan percaya kepada kami, sekalipun kami adalah orang-orang yang benar\".'),
(1614, '012:018', '68', 12, 18, 'Mereka datang membawa baju gamisnya (yang berlumuran) dengan darah palsu. Ya´qub berkata: \"Sebenarnya dirimu sendirilah yang memandang baik perbuatan (yang buruk) itu; maka kesabaran yang baik itulah (kesabaranku). Dan Allah sajalah yang dimohon pertolongan-Nya terhadap apa yang kamu ceritakan\".'),
(1615, '012:019', '68', 12, 19, 'Kemudian datanglah kelompok orang-orang musafir, lalu mereka menyuruh seorang pengambil air, maka dia menurunkan timbanya, dia berkata: \"Oh; kabar gembira, ini seorang anak muda!\" Kemudian mereka menyembunyikan dia sebagai barang dagangan. Dan Allah Maha Mengetahui apa yang mereka kerjakan.'),
(1616, '012:020', '68', 12, 20, 'Dan mereka menjual Yusuf dengan harga yang murah, yaitu beberapa dirham saja, dan mereka merasa tidak tertarik hatinya kepada Yusuf.'),
(1617, '012:021', '68', 12, 21, 'Dan orang Mesir yang membelinya berkata kepada isterinya: \"Berikanlah kepadanya tempat (dan layanan) yang baik, boleh jadi dia bermanfaat kepada kita atau kita pungut dia sebagai anak\". Dan demikian pulalah Kami memberikan kedudukan yang baik kepada Yusuf di muka bumi (Mesir), dan agar Kami ajarkan kepadanya ta´bir mimpi. Dan Allah berkuasa terhadap urusan-Nya, tetapi kebanyakan manusia tiada mengetahuinya.'),
(1618, '012:022', '68', 12, 22, 'Dan tatkala dia cukup dewasa Kami berikan kepadanya hikmah dan ilmu. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(1619, '012:023', '68', 12, 23, 'Dan wanita (Zulaikha) yang Yusuf tinggal di rumahnya menggoda Yusuf untuk menundukkan dirinya (kepadanya) dan dia menutup pintu-pintu, seraya berkata: \"Marilah ke sini\". Yusuf berkata: \"Aku berlindung kepada Allah, sungguh tuanku telah memperlakukan aku dengan baik\". Sesungguhnya orang-orang yang zalim tiada akan beruntung.'),
(1620, '012:024', '68', 12, 24, 'Sesungguhnya wanita itu telah bermaksud (melakukan perbuatan itu) dengan Yusuf, dan Yusufpun bermaksud (melakukan pula) dengan wanita itu andaikata dia tidak melihat tanda (dari) Tuhannya. Demikianlah, agar Kami memalingkan dari padanya kemungkaran dan kekejian. Sesungguhnya Yusuf itu termasuk hamba-hamba Kami yang terpilih.'),
(1621, '012:025', '68', 12, 25, 'Dan keduanya berlomba-lomba menuju pintu dan wanita itu menarik baju gamis Yusuf dari belakang hingga koyak dan kedua-duanya mendapati suami wanita itu di muka pintu. Wanita itu berkata: \"Apakah pembalasan terhadap orang yang bermaksud berbuat serong dengan isterimu, selain dipenjarakan atau (dihukum) dengan azab yang pedih?\"'),
(1622, '012:026', '68', 12, 26, 'Yusuf berkata: \"Dia menggodaku untuk menundukkan diriku (kepadanya)\", dan seorang saksi dari keluarga wanita itu memberikan kesaksiannya: \"Jika baju gamisnya koyak di muka, maka wanita itu benar dan Yusuf termasuk orang-orang yang dusta.'),
(1623, '012:027', '68', 12, 27, 'Dan jika baju gamisnya koyak di belakang, maka wanita itulah yang dusta, dan Yusuf termasuk orang-orang yang benar\".'),
(1624, '012:028', '68', 12, 28, 'Maka tatkala suami wanita itu melihat baju gamis Yusuf koyak di belakang berkatalah dia: \"Sesungguhnya (kejadian) itu adalah diantara tipu daya kamu, sesungguhnya tipu daya kamu adalah besar\".'),
(1625, '012:029', '68', 12, 29, '(Hai) Yusuf: \"Berpalinglah dari ini, dan (kamu hai isteriku) mohon ampunlah atas dosamu itu, karena kamu sesungguhnya termasuk orang-orang yang berbuat salah\".'),
(1626, '012:030', '68', 12, 30, 'Dan wanita-wanita di kota berkata: \"Isteri Al Aziz menggoda bujangnya untuk menundukkan dirinya (kepadanya), sesungguhnya cintanya kepada bujangnya itu adalah sangat mendalam. Sesungguhnya kami memandangnya dalam kesesatan yang nyata\".'),
(1627, '012:031', '68', 12, 31, 'Maka tatkala wanita itu (Zulaikha) mendengar cercaan mereka, diundangnyalah wanita-wanita itu dan disediakannya bagi mereka tempat duduk, dan diberikannya kepada masing-masing mereka sebuah pisau (untuk memotong jamuan), kemudian dia berkata (kepada Yusuf): \"Keluarlah (nampakkanlah dirimu) kepada mereka\". Maka tatkala wanita-wanita itu melihatnya, mereka kagum kepada (keelokan rupa)nya, dan mereka melukai (jari) tangannya dan berkata: \"Maha sempurna Allah, ini bukanlah manusia. Sesungguhnya ini tidak lain hanyalah malaikat yang mulia\".'),
(1628, '012:032', '68', 12, 32, 'Wanita itu berkata: \"Itulah dia orang yang kamu cela aku karena (tertarik) kepadanya, dan sesungguhnya aku telah menggoda dia untuk menundukkan dirinya (kepadaku) akan tetapi dia menolak. Dan sesungguhnya jika dia tidak mentaati apa yang aku perintahkan kepadanya, niscaya dia akan dipenjarakan dan dia akan termasuk golongan orang-orang yang hina\".'),
(1629, '012:033', '68', 12, 33, 'Yusuf berkata: \"Wahai Tuhanku, penjara lebih aku sukai daripada memenuhi ajakan mereka kepadaku. Dan jika tidak Engkau hindarkan dari padaku tipu daya mereka, tentu aku akan cenderung untuk (memenuhi keinginan mereka) dan tentulah aku termasuk orang-orang yang bodoh\".'),
(1630, '012:034', '68', 12, 34, 'Maka Tuhannya memperkenankan doa Yusuf dan Dia menghindarkan Yusuf dari tipu daya mereka. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui.'),
(1631, '012:035', '68', 12, 35, 'Kemudian timbul pikiran pada mereka setelah melihat tanda-tanda (kebenaran Yusuf) bahwa mereka harus memenjarakannya sampai sesuatu waktu.'),
(1632, '012:036', '68', 12, 36, 'Dan bersama dengan dia masuk pula ke dalam penjara dua orang pemuda. Berkatalah salah seorang diantara keduanya: \"Sesungguhnya aku bermimpi, bahwa aku memeras anggur\". Dan yang lainnya berkata: \"Sesungguhnya aku bermimpi, bahwa aku membawa roti di atas kepalaku, sebahagiannya dimakan burung\". Berikanlah kepada kami ta´birnya; sesungguhnya kami memandang kamu termasuk orang-orang yang pandai (mena´birkan mimpi).'),
(1633, '012:037', '68', 12, 37, 'Yusuf berkata: \"Tidak disampaikan kepada kamu berdua makanan yang akan diberikan kepadamu melainkan aku telah dapat menerangkan jenis makanan itu, sebelum makanan itu sampai kepadamu. Yang demikian itu adalah sebagian dari apa yang diajarkan kepadaku oleh Tuhanku. Sesungguhnya aku telah meninggalkan agama orang-orang yang tidak beriman kepada Allah, sedang mereka ingkar kepada hari kemudian.'),
(1634, '012:038', '68', 12, 38, 'Dan aku pengikut agama bapak-bapakku yaitu Ibrahim, Ishak dan Ya´qub. Tiadalah patut bagi kami (para Nabi) mempersekutukan sesuatu apapun dengan Allah. Yang demikian itu adalah dari karunia Allah kepada kami dan kepada manusia (seluruhnya); tetapi kebanyakan manusia tidak mensyukuri (Nya).'),
(1635, '012:039', '68', 12, 39, 'Hai kedua penghuni penjara, manakah yang baik, tuhan-tuhan yang bermacam-macam itu ataukah Allah Yang Maha Esa lagi Maha Perkasa?'),
(1636, '012:040', '68', 12, 40, 'Kamu tidak menyembah yang selain Allah kecuali hanya (menyembah) nama-nama yang kamu dan nenek moyangmu membuat-buatnya. Allah tidak menurunkan suatu keteranganpun tentang nama-nama itu. Keputusan itu hanyalah kepunyaan Allah. Dia telah memerintahkan agar kamu tidak menyembah selain Dia. Itulah agama yang lurus, tetapi kebanyakan manusia tidak mengetahui\".'),
(1637, '012:041', '68', 12, 41, 'Hai kedua penghuni penjara: \"Adapun salah seorang diantara kamu berdua, akan memberi minuman tuannya dengan khamar; adapun yang seorang lagi maka ia akan disalib, lalu burung memakan sebagian dari kepalanya. Telah diputuskan perkara yang kamu berdua menanyakannya (kepadaku)\".'),
(1638, '012:042', '68', 12, 42, 'Dan Yusuf berkata kepada orang yang diketahuinya akan selamat diantara mereka berdua: \"Terangkanlah keadaanku kepada tuanmu\". Maka syaitan menjadikan dia lupa menerangkan (keadaan Yusuf) kepada tuannya. Karena itu tetaplah dia (Yusuf) dalam penjara beberapa tahun lamanya.'),
(1639, '012:043', '68', 12, 43, 'Raja berkata (kepada orang-orang terkemuka dari kaumnya): \"Sesungguhnya aku bermimpi melihat tujuh ekor sapi betina yang gemuk-gemuk dimakan oleh tujuh ekor sapi betina yang kurus-kurus dan tujuh bulir (gandum) yang hijau dan tujuh bulir lainnya yang kering\". Hai orang-orang yang terkemuka: \"Terangkanlah kepadaku tentang ta´bir mimpiku itu jika kamu dapat mena´birkan mimpi\".'),
(1640, '012:044', '68', 12, 44, 'Mereka menjawab: \"(Itu) adalah mimpi-mimpi yang kosong dan kami sekali-kali tidak tahu menta´birkan mimpi itu\".'),
(1641, '012:045', '68', 12, 45, 'Dan berkatalah orang yang selamat diantara mereka berdua dan teringat (kepada Yusuf) sesudah beberapa waktu lamanya: \"Aku akan memberitakan kepadamu tentang (orang yang pandai) mena´birkan mimpi itu, maka utuslah aku (kepadanya)\".'),
(1642, '012:046', '68', 12, 46, '(Setelah pelayan itu berjumpa dengan Yusuf dia berseru): \"Yusuf, hai orang yang amat dipercaya, terangkanlah kepada kami tentang tujuh ekor sapi betina yang gemuk-gemuk yang dimakan oleh tujuh ekor sapi betina yang kurus-kurus dan tujuh bulir (gandum) yang hijau dan (tujuh) lainnya yang kering agar aku kembali kepada orang-orang itu, agar mereka mengetahuinya\".'),
(1643, '012:047', '68', 12, 47, 'Yusuf berkata: \"Supaya kamu bertanam tujuh tahun (lamanya) sebagaimana biasa; maka apa yang kamu tuai hendaklah kamu biarkan dibulirnya kecuali sedikit untuk kamu makan.'),
(1644, '012:048', '68', 12, 48, 'Kemudian sesudah itu akan datang tujuh tahun yang amat sulit, yang menghabiskan apa yang kamu simpan untuk menghadapinya (tahun sulit), kecuali sedikit dari (bibit gandum) yang kamu simpan.'),
(1645, '012:049', '68', 12, 49, 'Kemudian setelah itu akan datang tahun yang padanya manusia diberi hujan (dengan cukup) dan dimasa itu mereka memeras anggur\".'),
(1646, '012:050', '68', 12, 50, 'Raja berkata: \"Bawalah dia kepadaku\". Maka tatkala utusan itu datang kepada Yusuf, berkatalah Yusuf: \"Kembalilah kepada tuanmu dan tanyakanlah kepadanya bagaimana halnya wanita-wanita yang telah melukai tangannya. Sesungguhnya Tuhanku, Maha Mengetahui tipu daya mereka\".');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1647, '012:051', '68', 12, 51, 'Raja berkata (kepada wanita-wanita itu): \"Bagaimana keadaanmu ketika kamu menggoda Yusuf untuk menundukkan dirinya (kepadamu)?\" Mereka berkata: \"Maha Sempurna Allah, kami tiada mengetahui sesuatu keburukan dari padanya\". Berkata isteri Al Aziz: \"Sekarang jelaslah kebenaran itu, akulah yang menggodanya untuk menundukkan dirinya (kepadaku), dan sesungguhnya dia termasuk orang-orang yang benar\".'),
(1648, '012:052', '68', 12, 52, '(Yusuf berkata): \"Yang demikian itu agar dia (Al Aziz) mengetahui bahwa sesungguhnya aku tidak berkhianat kepadanya di belakangnya, dan bahwasanya Allah tidak meridhai tipu daya orang-orang yang berkhianat.'),
(1649, '012:053', '68', 12, 53, 'Dan aku tidak membebaskan diriku (dari kesalahan), karena sesungguhnya nafsu itu selalu menyuruh kepada kejahatan, kecuali nafsu yang diberi rahmat oleh Tuhanku. Sesungguhnya Tuhanku Maha Pengampun lagi Maha Penyanyang.'),
(1650, '012:054', '68', 12, 54, 'Dan raja berkata: \"Bawalah Yusuf kepadaku, agar aku memilih dia sebagai orang yang rapat kepadaku\". Maka tatkala raja telah bercakap-cakap dengan dia, dia berkata: \"Sesungguhnya kamu (mulai) hari ini menjadi seorang yang berkedudukan tinggi lagi dipercayai pada sisi kami\".'),
(1651, '012:055', '68', 12, 55, 'Berkata Yusuf: \"Jadikanlah aku bendaharawan negara (Mesir); sesungguhnya aku adalah orang yang pandai menjaga, lagi berpengetahuan\".'),
(1652, '012:056', '68', 12, 56, 'Dan demikianlah Kami memberi kedudukan kepada Yusuf di negeri Mesir; (dia berkuasa penuh) pergi menuju kemana saja ia kehendaki di bumi Mesir itu. Kami melimpahkan rahmat Kami kepada siapa yang Kami kehendaki dan Kami tidak menyia-nyiakan pahala orang-orang yang berbuat baik.'),
(1653, '012:057', '68', 12, 57, 'Dan sesungguhnya pahala di akhirat itu lebih baik, bagi orang-orang yang beriman dan selalu bertakwa.'),
(1654, '012:058', '68', 12, 58, 'Dan saudara-saudara Yusuf datang (ke Mesir} lalu mereka masuk ke (tempat)nya. Maka Yusuf mengenal mereka, sedang mereka tidak kenal (lagi) kepadanya.'),
(1655, '012:059', '68', 12, 59, 'Dan tatkala Yusuf menyiapkan untuk mereka bahan makanannya, ia berkata: \"Bawalah kepadaku saudaramu yang seayah dengan kamu (Bunyamin), tidakkah kamu melihat bahwa aku menyempurnakan sukatan dan aku adalah sebaik-baik penerima tamu?'),
(1656, '012:060', '68', 12, 60, 'Jika kamu tidak membawanya kepadaku, maka kamu tidak akan mendapat sukatan lagi dari padaku dan jangan kamu mendekatiku\".'),
(1657, '012:061', '68', 12, 61, 'Mereka berkata: \"Kami akan membujuk ayahnya untuk membawanya (ke mari) dan sesungguhnya kami benar-benar akan melaksanakannya\".'),
(1658, '012:062', '68', 12, 62, 'Yusuf berkata kepada bujang-bujangnya: \"Masukkanlah barang-barang (penukar kepunyaan mereka) ke dalam karung-karung mereka, supaya mereka mengetahuinya apabila mereka telah kembali kepada keluarganya, mudah-mudahan mereka kembali lagi\".'),
(1659, '012:063', '68', 12, 63, 'Maka tatkala mereka telah kembali kepada ayah mereka (Ya´qub) mereka berkata: \"Wahai ayah kami, kami tidak akan mendapat sukatan (gandum) lagi, (jika tidak membawa saudara kami), sebab itu biarkanlah saudara kami pergi bersama-sama kami supaya kami mendapat sukatan, dan sesungguhnya kami benar benar akan menjaganya\".'),
(1660, '012:064', '68', 12, 64, 'Berkata Ya´qub: \"Bagaimana aku akan mempercayakannya (Bunyamin) kepadamu, kecuali seperti aku telah mempercayakan saudaranya (Yusuf) kepada kamu dahulu?\". Maka Allah adalah sebaik-baik Penjaga dan Dia adalah Maha Penyanyang diantara para penyanyang.'),
(1661, '012:065', '68', 12, 65, 'Tatkala mereka membuka barang-barangnya, mereka menemukan kembali barang-barang (penukaran) mereka, dikembalikan kepada mereka. Mereka berkata: \"Wahai ayah kami apa lagi yang kita inginkan. Ini barang-barang kita dikembalikan kepada kita, dan kami akan dapat memberi makan keluarga kami, dan kami akan dapat memelihara saudara kami, dan kami akan mendapat tambahan sukatan (gandum) seberat beban seekor unta. Itu adalah sukatan yang mudah (bagi raja Mesir)\".'),
(1662, '012:066', '68', 12, 66, 'Ya´qub berkata: \"Aku sekali-kali tidak akan melepaskannya (pergi) bersama-sama kamu, sebelum kamu memberikan kepadaku janji yang teguh atas nama Allah, bahwa kamu pasti akan membawanya kepadaku kembali, kecuali jika kamu dikepung musuh\". Tatkala mereka memberikan janji mereka, maka Ya´qub berkata: \"Allah adalah saksi terhadap apa yang kita ucapkan (ini)\".'),
(1663, '012:067', '68', 12, 67, 'Dan Ya´qub berkata: \"Hai anak-anakku janganlah kamu (bersama-sama) masuk dari satu pintu gerbang, dan masuklah dari pintu-pintu gerbang yang berlain-lain; namun demikian aku tiada dapat melepaskan kamu barang sedikitpun dari pada (takdir) Allah. Keputusan menetapkan (sesuatu) hanyalah hak Allah; kepada-Nya-lah aku bertawakkal dan hendaklah kepada-Nya saja orang-orang yang bertawakkal berserah diri\".'),
(1664, '012:068', '68', 12, 68, 'Dan tatkala mereka masuk menurut yang diperintahkan ayah mereka, maka (cara yang mereka lakukan itu) tiadalah melepaskan mereka sedikitpun dari takdir Allah, akan tetapi itu hanya suatu keinginan pada diri Ya´qub yang telah ditetapkannya. Dan sesungguhnya dia mempunyai pengetahuan, karena Kami telah mengajarkan kepadanya. Akan tetapi kebanyakan manusia tiada mengetahui.'),
(1665, '012:069', '68', 12, 69, 'Dan tatkala mereka masuk ke (tempat) Yusuf. Yusuf membawa saudaranya (Bunyamin) ke tempatnya, Yusuf berkata: \"Sesungguhnya aku (ini) adalah saudaramu, maka janganlah kamu berdukacita terhadap apa yang telah mereka kerjakan\".'),
(1666, '012:070', '68', 12, 70, 'Maka tatkala telah disiapkan untuk mereka bahan makanan mereka, Yusuf memasukkan piala (tempat minum) ke dalam karung saudaranya. Kemudian berteriaklah seseorang yang menyerukan: \"Hai kafilah, sesungguhnya kamu adalah orang-orang yang mencuri\".'),
(1667, '012:071', '68', 12, 71, 'Mereka menjawab, sambil menghadap kepada penyeru-penyeru itu: \"Barang apakah yang hilang dari pada kamu?\"'),
(1668, '012:072', '68', 12, 72, 'Penyeru-penyeru itu berkata: \"Kami kehilangan piala raja, dan siapa yang dapat mengembalikannya akan memperoleh bahan makanan (seberat) beban unta, dan aku menjamin terhadapnya\".'),
(1669, '012:073', '68', 12, 73, 'Saudara-saudara Yusuf menjawab \"Demi Allah sesungguhnya kamu mengetahui bahwa kami datang bukan untuk membuat kerusakan di negeri (ini) dan kami bukanlah para pencuri\".'),
(1670, '012:074', '68', 12, 74, 'Mereka berkata: \"Tetapi apa balasannya jikalau kamu betul-betul pendusta? \"'),
(1671, '012:075', '68', 12, 75, 'Mereka menjawab: \"Balasannya, ialah pada siapa diketemukan (barang yang hilang) dalam karungnya, maka dia sendirilah balasannya (tebusannya)\". Demikianlah kami memberi pembalasan kepada orang-orang yang zalim.'),
(1672, '012:076', '68', 12, 76, 'Maka mulailah Yusuf (memeriksa) karung-karung mereka sebelum (memeriksa) karung saudaranya sendiri, kemudian dia mengeluarkan piala raja itu dari karung saudaranya. Demikianlah Kami atur untuk (mencapai maksud) Yusuf. Tiadalah patut Yusuf menghukum saudaranya menurut undang-undang raja, kecuali Allah menghendaki-Nya. Kami tinggikan derajat orang yang Kami kehendaki; dan di atas tiap-tiap orang yang berpengetahuan itu ada lagi Yang Maha Mengetahui.'),
(1673, '012:077', '68', 12, 77, 'Mereka berkata: \"Jika ia mencuri, maka sesungguhnya, telah pernah mencuri pula saudaranya sebelum itu\". Maka Yusuf menyembunyikan kejengkelan itu pada dirinya dan tidak menampakkannya kepada mereka. Dia berkata (dalam hatinya): \"Kamu lebih buruk kedudukanmu (sifat-sifatmu) dan Allah Maha Mengetahui apa yang kamu terangkan itu\".'),
(1674, '012:078', '68', 12, 78, 'Mereka berkata: \"Wahai Al Aziz, sesungguhnya ia mempunyai ayah yang sudah lanjut usianya, lantaran itu ambillah salah seorang diantara kami sebagai gantinya, sesungguhnya kami melihat kamu termasuk oranng-orang yang berbuat baik\".'),
(1675, '012:079', '68', 12, 79, 'Berkata Yusuf: \"Aku mohon perlindungan kepada Allah daripada menahan seorang, kecuali orang yang kami ketemukan harta benda kami padanya, jika kami berbuat demikian, maka benar-benarlah kami orang-orang yang zalim\".'),
(1676, '012:080', '68', 12, 80, 'Maka tatkala mereka berputus asa dari pada (putusan) Yusuf mereka menyendiri sambil berunding dengan berbisik-bisik. Berkatalah yang tertua diantara mereka: \"Tidakkah kamu ketahui bahwa sesungguhnya ayahmu telah mengambil janji dari kamu dengan nama Allah dan sebelum itu kamu telah menyia-nyiakan Yusuf. Sebab itu aku tidak akan meninggalkan negeri Mesir, sampai ayahku mengizinkan kepadaku (untuk kembali), atau Allah memberi keputusan terhadapku. Dan Dia adalah Hakim yang sebaik-baiknya\".'),
(1677, '012:081', '68', 12, 81, 'Kembalilah kepada ayahmu dan katakanlah: \"Wahai ayah kami! Sesungguhnya anakmu telah mencuri, dan kami hanya menyaksikan apa yang kami ketahui, dan sekali-kali kami tidak dapat menjaga (mengetahui) barang yang ghaib.'),
(1678, '012:082', '68', 12, 82, 'Dan tanyalah (penduduk) negeri yang kami berada disitu, dan kafilah yang kami datang bersamanya, dan sesungguhnya kami adalah orang-orang yang benar\".'),
(1679, '012:083', '68', 12, 83, 'Ya´qub berkata: \"Hanya dirimu sendirilah yang memandang baik perbuatan (yang buruk) itu. Maka kesabaran yang baik itulah (kesabaranku). Mudah-mudahan Allah mendatangkan mereka semuanya kepadaku; sesungguhnya Dialah Yang Maha Mengetahui lagi Maha Bijaksana\".'),
(1680, '012:084', '68', 12, 84, 'Dan Ya´qub berpaling dari mereka (anak-anaknya) seraya berkata: \"Aduhai duka citaku terhadap Yusuf\", dan kedua matanya menjadi putih karena kesedihan dan dia adalah seorang yang menahan amarahnya (terhadap anak-anaknya).'),
(1681, '012:085', '68', 12, 85, 'Mereka berkata: \"Demi Allah, senantiasa kamu mengingati Yusuf, sehingga kamu mengidapkan penyakit yang berat atau termasuk orang-orang yang binasa\".'),
(1682, '012:086', '68', 12, 86, 'Ya´qub menjawab: \"Sesungguhnya hanyalah kepada Allah aku mengadukan kesusahan dan kesedihanku, dan aku mengetahui dari Allah apa yang kamu tiada mengetahuinya\".'),
(1683, '012:087', '68', 12, 87, 'Hai anak-anakku, pergilah kamu, maka carilah berita tentang Yusuf dan saudaranya dan jangan kamu berputus asa dari rahmat Allah. Sesungguhnya tiada berputus asa dari rahmat Allah, melainkan kaum yang kafir\".'),
(1684, '012:088', '68', 12, 88, 'Maka ketika mereka masuk ke (tempat) Yusuf, mereka berkata: \"Hai Al Aziz, kami dan keluarga kami telah ditimpa kesengsaraan dan kami datang membawa barang-barang yang tak berharga, maka sempurnakanlah sukatan untuk kami, dan bersedekahlah kepada kami, sesungguhnya Allah memberi balasan kepada orang-orang yang bersedekah\".'),
(1685, '012:089', '68', 12, 89, 'Yusuf berkata: \"Apakah kamu mengetahui (kejelekan) apa yang telah kamu lakukan terhadap Yusuf dan saudaranya ketika kamu tidak mengetahui (akibat) perbuatanmu itu?\".'),
(1686, '012:090', '68', 12, 90, 'Mereka berkata: \"Apakah kamu ini benar-benar Yusuf?\". Yusuf menjawab: \"Akulah Yusuf dan ini saudaraku. Sesungguhnya Allah telah melimpahkan karunia-Nya kepada kami\". Sesungguhnya barang siapa yang bertakwa dan bersabar, maka sesungguhnya Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik\"'),
(1687, '012:091', '68', 12, 91, 'Mereka berkata: \"Demi Allah, sesungguhnya Allah telah melebihkan kamu atas kami, dan sesungguhnya kami adalah orang-orang yang bersalah (berdosa)\".'),
(1688, '012:092', '68', 12, 92, 'Dia (Yusuf) berkata: \"Pada hari ini tak ada cercaan terhadap kamu, mudah-mudahan Allah mengampuni (kamu), dan Dia adalah Maha Penyayang diantara para penyayang\".'),
(1689, '012:093', '68', 12, 93, 'Pergilah kamu dengan membawa baju gamisku ini, lalu letakkanlah dia kewajah ayahku, nanti ia akan melihat kembali; dan bawalah keluargamu semuanya kepadaku\".'),
(1690, '012:094', '68', 12, 94, 'Tatkala kafilah itu telah ke luar (dari negeri Mesir) berkata ayah mereka: \"Sesungguhnya aku mencium bau Yusuf, sekiranya kamu tidak menuduhku lemah akal (tentu kamu membenarkan aku)\".'),
(1691, '012:095', '68', 12, 95, 'Keluarganya berkata: \"Demi Allah, sesungguhnya kamu masih dalam kekeliruanmu yang dahulu\".'),
(1692, '012:096', '68', 12, 96, 'Tatkala telah tiba pembawa kabar gembira itu, maka diletakkannya baju gamis itu ke wajah Ya´qub, lalu kembalilah dia dapat melihat. Berkata Ya´qub: \"Tidakkah aku katakan kepadamu, bahwa aku mengetahui dari Allah apa yang kamu tidak mengetahuinya\".'),
(1693, '012:097', '68', 12, 97, 'Mereka berkata: \"Wahai ayah kami, mohonkanlah ampun bagi kami terhadap dosa-dosa kami, sesungguhnya kami adalah orang-orang yang bersalah (berdosa)\".'),
(1694, '012:098', '68', 12, 98, 'Ya´qub berkata: \"Aku akan memohonkan ampun bagimu kepada Tuhanku. Sesungguhnya Dialah Yang Maha Pengampun lagi Maha Penyayang\".'),
(1695, '012:099', '68', 12, 99, 'Maka tatkala mereka masuk ke (tempat) Yusuf: Yusuf merangkul ibu bapanya dan dia berkata: \"Masuklah kamu ke negeri Mesir, insya Allah dalam keadaan aman\".'),
(1696, '012:100', '68', 12, 100, 'Dan ia menaikkan kedua ibu-bapanya ke atas singgasana. Dan mereka (semuanya) merebahkan diri seraya sujud kepada Yusuf. Dan berkata Yusuf: \"Wahai ayahku inilah ta´bir mimpiku yang dahulu itu; sesungguhnya Tuhanku telah menjadikannya suatu kenyataan. Dan sesungguhnya Tuhanku telah berbuat baik kepadaku, ketika Dia membebaskan aku dari rumah penjara dan ketika membawa kamu dari dusun padang pasir, setelah syaitan merusakkan (hubungan) antaraku dan saudara-saudaraku. Sesungguhnya Tuhanku Maha Lembut terhadap apa yang Dia kehendaki. Sesungguhnya Dialah Yang Maha Mengetahui lagi Maha Bijaksana.'),
(1697, '012:101', '68', 12, 101, 'Ya Tuhanku, sesungguhnya Engkau telah menganugerahkan kepadaku sebahagian kerajaan dan telah mengajarkan kepadaku sebahagian ta´bir mimpi. (Ya Tuhan) Pencipta langit dan bumi. Engkaulah Pelindungku di dunia dan di akhirat, wafatkanlah aku dalam keadaan Islam dan gabungkanlah aku dengan orang-orang yang saleh.'),
(1698, '012:102', '68', 12, 102, 'Demikian itu (adalah) diantara berita-berita yang ghaib yang Kami wahyukan kepadamu (Muhammad); padahal kamu tidak berada pada sisi mereka, ketika mereka memutuskan rencananya (untuk memasukkan Yusuf ke dalam sumur) dan mereka sedang mengatur tipu daya.'),
(1699, '012:103', '68', 12, 103, 'Dan sebahagian besar manusia tidak akan beriman -- walaupun kamu sangat menginginkannya.'),
(1700, '012:104', '68', 12, 104, 'Dan kamu sekali-kali tidak meminta upah kepada mereka (terhadap seruanmu ini), itu tidak lain hanyalah pengajaran bagi semesta alam.'),
(1701, '012:105', '68', 12, 105, 'Dan banyak sekali tanda-tanda (kekuasaan Allah) di langit dan di bumi yang mereka melaluinya, sedang mereka berpaling dari padanya.'),
(1702, '012:106', '68', 12, 106, 'Dan sebahagian besar dari mereka tidak beriman kepada Allah, melainkan dalam keadaan mempersekutukan Allah (dengan sembahan-sembahan lain).'),
(1703, '012:107', '68', 12, 107, 'Apakah mereka merasa aman dari kedatangan siksa Allah yang meliputi mereka, atau kedatangan kiamat kepada mereka secara mendadak, sedang mereka tidak menyadarinya?'),
(1704, '012:108', '68', 12, 108, 'Katakanlah: \"Inilah jalan (agama)ku, aku dan orang-orang yang mengikutiku mengajak (kamu) kepada Allah dengan hujjah yang nyata, Maha Suci Allah, dan aku tiada termasuk orang-orang yang musyrik\".'),
(1705, '012:109', '68', 12, 109, 'Kami tidak mengutus sebelum kamu, melainkan orang laki-laki yang Kami berikan wahyu kepadanya diantara penduduk negeri. Maka tidakkah mereka bepergian di muka bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul) dan sesungguhnya kampung akhirat adalah lebih baik bagi orang-orang yang bertakwa. Maka tidakkah kamu memikirkannya?'),
(1706, '012:110', '68', 12, 110, 'Sehingga apabila para rasul tidak mempunyai harapan lagi (tentang keimanan mereka) dan telah meyakini bahwa mereka telah didustakan, datanglah kepada para rasul itu pertolongan Kami, lalu diselamatkan orang-orang yang Kami kehendaki. Dan tidak dapat ditolak siksa Kami dari pada orang-orang yang berdosa.'),
(1707, '012:111', '68', 12, 111, 'Sesungguhnya pada kisah-kisah mereka itu terdapat pengajaran bagi orang-orang yang mempunyai akal. Al Quran itu bukanlah cerita yang dibuat-buat, akan tetapi membenarkan (kitab-kitab) yang sebelumnya dan menjelaskan segala sesuatu, dan sebagai petunjuk dan rahmat bagi kaum yang beriman.'),
(1708, '013:001', '68', 13, 1, 'Alif laam miim raa. Ini adalah ayat-ayat Al Kitab (Al Quran). Dan Kitab yang diturunkan kepadamu daripada Tuhanmu itu adalah benar: akan tetapi kebanyakan manusia tidak beriman (kepadanya).'),
(1709, '013:002', '68', 13, 2, 'Allah-lah Yang meninggikan langit tanpa tiang (sebagaimana) yang kamu lihat, kemudian Dia bersemayam di atas ´Arasy, dan menundukkan matahari dan bulan. Masing-masing beredar hingga waktu yang ditentukan. Allah mengatur urusan (makhluk-Nya), menjelaskan tanda-tanda (kebesaran-Nya), supaya kamu meyakini pertemuan(mu) dengan Tuhanmu.'),
(1710, '013:003', '68', 13, 3, 'Dan Dialah Tuhan yang membentangkan bumi dan menjadikan gunung-gunung dan sungai-sungai padanya. Dan menjadikan padanya semua buah-buahan berpasang-pasangan, Allah menutupkan malam kepada siang. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi kaum yang memikirkan.'),
(1711, '013:004', '68', 13, 4, 'Dan di bumi ini terdapat bagian-bagian yang berdampingan, dan kebun-kebun anggur, tanaman-tanaman dan pohon korma yang bercabang dan yang tidak bercabang, disirami dengan air yang sama. Kami melebihkan sebahagian tanam-tanaman itu atas sebahagian yang lain tentang rasanya. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi kaum yang berfikir.'),
(1712, '013:005', '68', 13, 5, 'Dan jika (ada sesuatu) yang kamu herankan, maka yang patut mengherankan adalah ucapan mereka: \"Apabila kami telah menjadi tanah, apakah kami sesungguhnya akan (dikembalikan) menjadi makhluk yang baru?\" Orang-orang itulah yang kafir kepada Tuhannya; dan orang-orang itulah (yang dilekatkan) belenggu di lehernya; mereka itulah penghuni neraka, mereka kekal di dalamnya.'),
(1713, '013:006', '68', 13, 6, 'Mereka meminta kepadamu supaya disegerakan (datangnya) siksa, sebelum (mereka meminta) kebaikan, padahal telah terjadi bermacam-macam contoh siksa sebelum mereka. Sesungguhnya Tuhanmu benar-benar mempunyai ampunan (yang luas) bagi manusia sekalipun mereka zalim, dan sesungguhnya Tuhanmu benar-benar sangat keras siksanya.'),
(1714, '013:007', '68', 13, 7, 'Orang-orang yang kafir berkata: \"Mengapa tidak diturunkan kepadanya (Muhammad) suatu tanda (kebesaran) dari Tuhannya?\" Sesungguhnya kamu hanyalah seorang pemberi peringatan; dan bagi tiap-tiap kaum ada orang yang memberi petunjuk.'),
(1715, '013:008', '68', 13, 8, 'Allah mengetahui apa yang dikandung oleh setiap perempuan, dan kandungan rahim yang kurang sempurna dan yang bertambah. Dan segala sesuatu pada sisi-Nya ada ukurannya.'),
(1716, '013:009', '68', 13, 9, 'Yang mengetahui semua yang ghaib dan yang nampak; Yang Maha Besar lagi Maha Tinggi.'),
(1717, '013:010', '68', 13, 10, 'Sama saja (bagi Tuhan), siapa diantaramu yang merahasiakan ucapannya, dan siapa yang berterus-terang dengan ucapan itu, dan siapa yang bersembunyi di malam hari dan yang berjalan (menampakkan diri) di siang hari.'),
(1718, '013:011', '68', 13, 11, 'Bagi manusia ada malaikat-malaikat yang selalu mengikutinya bergiliran, di muka dan di belakangnya, mereka menjaganya atas perintah Allah. Sesungguhnya Allah tidak merubah keadaan sesuatu kaum sehingga mereka merubah keadaan yang ada pada diri mereka sendiri. Dan apabila Allah menghendaki keburukan terhadap sesuatu kaum, maka tak ada yang dapat menolaknya; dan sekali-kali tak ada pelindung bagi mereka selain Dia.'),
(1719, '013:012', '68', 13, 12, 'Dialah Tuhan yang memperlihatkan kilat kepadamu untuk menimbulkan ketakutan dan harapan, dan Dia mengadakan awan mendung.'),
(1720, '013:013', '68', 13, 13, 'Dan guruh itu bertasbih dengan memuji Allah, (demikian pula) para malaikat karena takut kepada-Nya, dan Allah melepaskan halilintar, lalu menimpakannya kepada siapa yang Dia kehendaki, dan mereka berbantah-bantahan tentang Allah, dan Dialah Tuhan Yang Maha keras siksa-Nya.'),
(1721, '013:014', '68', 13, 14, 'Hanya bagi Allah-lah (hak mengabulkan) doa yang benar. Dan berhala-berhala yang mereka sembah selain Allah tidak dapat memperkenankan sesuatupun bagi mereka, melainkan seperti orang yang membukakan kedua telapak tangannya ke dalam air supaya sampai air ke mulutnya, padahal air itu tidak dapat sampai ke mulutnya. Dan doa (ibadat) orang-orang kafir itu, hanyalah sia-sia belaka.'),
(1722, '013:015', '68', 13, 15, 'Hanya kepada Allah-lah sujud (patuh) segala apa yang di langit dan di bumi, baik dengan kemauan sendiri ataupun terpaksa (dan sujud pula) bayang-bayangnya di waktu pagi dan petang hari.'),
(1723, '013:016', '68', 13, 16, 'Katakanlah: \"Siapakah Tuhan langit dan bumi?\" Jawabnya: \"Allah\". Katakanlah: \"Maka patutkah kamu mengambil pelindung-pelindungmu dari selain Allah, padahal mereka tidak menguasai kemanfaatan dan tidak (pula) kemudharatan bagi diri mereka sendiri?\". Katakanlah: \"Adakah sama orang buta dan yang dapat melihat, atau samakah gelap gulita dan terang benderang; apakah mereka menjadikan beberapa sekutu bagi Allah yang dapat menciptakan seperti ciptaan-Nya sehingga kedua ciptaan itu serupa menurut pandangan mereka?\" Katakanlah: \"Allah adalah Pencipta segala sesuatu dan Dialah Tuhan Yang Maha Esa lagi Maha Perkasa\".'),
(1724, '013:017', '68', 13, 17, 'Allah telah menurunkan air (hujan) dari langit, maka mengalirlah air di lembah-lembah menurut ukurannya, maka arus itu membawa buih yang mengambang. Dan dari apa (logam) yang mereka lebur dalam api untuk membuat perhiasan atau alat-alat, ada (pula) buihnya seperti buih arus itu. Demikianlah Allah membuat perumpamaan (bagi) yang benar dan yang bathil. Adapun buih itu, akan hilang sebagai sesuatu yang tak ada harganya; adapun yang memberi manfaat kepada manusia, maka ia tetap di bumi. Demikianlah Allah membuat perumpamaan-perumpamaan.'),
(1725, '013:018', '68', 13, 18, 'Bagi orang-orang yang memenuhi seruan Tuhannya, (disediakan) pembalasan yang baik. Dan orang-orang yang tidak memenuhi seruan Tuhan, sekiranya mereka mempunyai semua (kekayaan) yang ada di bumi dan (ditambah) sebanyak isi bumi itu lagi besertanya, niscaya mereka akan menebus dirinya dengan kekayaan itu. Orang-orang itu disediakan baginya hisab yang buruk dan tempat kediaman mereka ialah Jahanam dan itulah seburuk-buruk tempat kediaman.'),
(1726, '013:019', '68', 13, 19, 'Adakah orang yang mengetahui bahwasanya apa yang diturunkan kepadamu dari Tuhanmu itu benar sama dengan orang yang buta? Hanyalah orang-orang yang berakal saja yang dapat mengambil pelajaran,'),
(1727, '013:020', '68', 13, 20, '(yaitu) orang-orang yang memenuhi janji Allah dan tidak merusak perjanjian,'),
(1728, '013:021', '68', 13, 21, 'dan orang-orang yang menghubungkan apa-apa yang Allah perintahkan supaya dihubungkan, dan mereka takut kepada Tuhannya dan takut kepada hisab yang buruk.'),
(1729, '013:022', '68', 13, 22, 'Dan orang-orang yang sabar karena mencari keridhaan Tuhannya, mendirikan shalat, dan menafkahkan sebagian rezeki yang Kami berikan kepada mereka, secara sembunyi atau terang-terangan serta menolak kejahatan dengan kebaikan; orang-orang itulah yang mendapat tempat kesudahan (yang baik),'),
(1730, '013:023', '68', 13, 23, '(yaitu) surga ´Adn yang mereka masuk ke dalamnya bersama-sama dengan orang-orang yang saleh dari bapak-bapaknya, isteri-isterinya dan anak cucunya, sedang malaikat-malaikat masuk ke tempat-tempat mereka dari semua pintu;'),
(1731, '013:024', '68', 13, 24, '(sambil mengucapkan): \"Salamun ´alaikum bima shabartum\". Maka alangkah baiknya tempat kesudahan itu.'),
(1732, '013:025', '68', 13, 25, 'Orang-orang yang merusak janji Allah setelah diikrarkan dengan teguh dan memutuskan apa-apa yang Allah perintahkan supaya dihubungkan dan mengadakan kerusakan di bumi, orang-orang itulah yang memperoleh kutukan dan bagi mereka tempat kediaman yang buruk (Jahannam).'),
(1733, '013:026', '68', 13, 26, 'Allah meluaskan rezeki dan menyempitkannya bagi siapa yang Dia kehendaki. Mereka bergembira dengan kehidupan di dunia, padahal kehidupan dunia itu (dibanding dengan) kehidupan akhirat, hanyalah kesenangan (yang sedikit).'),
(1734, '013:027', '68', 13, 27, 'Orang-orang kafir berkata: \"Mengapa tidak diturunkan kepadanya (Muhammad) tanda (mukjizat) dari Tuhannya?\" Katakanlah: \"Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan menunjuki orang-orang yang bertaubat kepada-Nya\",'),
(1735, '013:028', '68', 13, 28, '(yaitu) orang-orang yang beriman dan hati mereka manjadi tenteram dengan mengingat Allah. Ingatlah, hanya dengan mengingati Allah-lah hati menjadi tenteram.'),
(1736, '013:029', '68', 13, 29, 'Orang-orang yang beriman dan beramal saleh, bagi mereka kebahagiaan dan tempat kembali yang baik.'),
(1737, '013:030', '68', 13, 30, 'Demikianlah, Kami telah mengutus kamu pada suatu umat yang sungguh telah berlalu beberapa umat sebelumnya, supaya kamu membacakan kepada mereka (Al Quran) yang Kami wahyukan kepadamu, padahal mereka kafir kepada Tuhan Yang Maha Pemurah. Katakanlah: \"Dialah Tuhanku tidak ada Tuhan selain Dia; hanya kepada-Nya aku bertawakkal dan hanya kepada-Nya aku bertaubat\".'),
(1738, '013:031', '68', 13, 31, 'Dan sekiranya ada suatu bacaan (kitab suci) yang dengan bacaan itu gunung-gunung dapat digoncangkan atau bumi jadi terbelah atau oleh karenanya orang-orang yang sudah mati dapat berbicara, (tentulah Al Quran itulah dia). Sebenarnya segala urusan itu adalah kepunyaan Allah. Maka tidakkah orang-orang yang beriman itu mengetahui bahwa seandainya Allah menghendaki (semua manusia beriman), tentu Allah memberi petunjuk kepada manusia semuanya. Dan orang-orang yang kafir senantiasa ditimpa bencana disebabkan perbuatan mereka sendiri atau bencana itu terjadi dekat tempat kediaman mereka, sehingga datanglah janji Allah. Sesungguhnya Allah tidak menyalahi janji.'),
(1739, '013:032', '68', 13, 32, 'Dan sesungguhnya telah diperolok-olokkan beberapa rasul sebelum kamu, maka Aku beri tangguh kepada orang-orang kafir itu kemudian Aku binasakan mereka. Alangkah hebatnya siksaan-Ku itu!'),
(1740, '013:033', '68', 13, 33, 'Maka apakah Tuhan yang menjaga setiap diri terhadap apa yang diperbuatnya (sama dengan yang tidak demikian sifatnya)? Mereka menjadikan beberapa sekutu bagi Allah. Katakanlah: \"Sebutkanlah sifat-sifat mereka itu\". Atau apakah kamu hendak memberitakan kepada Allah apa yang tidak diketahui-Nya di bumi, atau kamu mengatakan (tentang hal itu) sekadar perkataan pada lahirnya saja. Sebenarnya orang-orang kafir itu dijadikan (oleh syaitan) memandang baik tipu daya mereka dan dihalanginya dari jalan (yang benar). Dan barangsiapa yang disesatkan Allah, maka baginya tak ada seorangpun yang akan memberi petunjuk.'),
(1741, '013:034', '68', 13, 34, 'Bagi mereka azab dalam kehidupan dunia dan sesungguhnya azab akhirat adalah lebih keras dan tak ada bagi mereka seorang pelindungpun dari (azab) Allah.'),
(1742, '013:035', '68', 13, 35, 'Perumpamaan surga yang dijanjikan kepada orang-orang yang takwa ialah (seperti taman); mengalir sungai-sungai di dalamnya; buahnya tak henti-henti sedang naungannya (demikian pula). Itulah tempat kesudahan bagi orang-orang yang bertakwa, sedang tempat kesudahan bagi orang-orang kafir ialah neraka.'),
(1743, '013:036', '68', 13, 36, 'Orang-orang yang telah Kami berikan kitab kepada mereka bergembira dengan kitab yang diturunkan kepadamu, dan di antara golongan-golongan (Yahudi dan Nasrani) yang bersekutu, ada yang mengingkari sebahagiannya. Katakanlah \"Sesungguhnya aku hanya diperintah untuk menyembah Allah dan tidak mempersekutukan sesuatupun dengan Dia. Hanya kepada-Nya aku seru (manusia) dan hanya kepada-Nya aku kembali\".'),
(1744, '013:037', '68', 13, 37, 'Dan demikianlah, Kami telah menurunkan Al Quran itu sebagai peraturan (yang benar) dalam bahasa Arab. Dan seandainya kamu mengikuti hawa nafsu mereka setelah datang pengetahuan kepadamu, maka sekali-kali tidak ada pelindung dan pemelihara bagimu terhadap (siksa) Allah.'),
(1745, '013:038', '68', 13, 38, 'Dan sesungguhnya Kami telah mengutus beberapa Rasul sebelum kamu dan Kami memberikan kepada mereka isteri-isteri dan keturunan. Dan tidak ada hak bagi seorang Rasul mendatangkan sesuatu ayat (mukjizat) melainkan dengan izin Allah. Bagi tiap-tiap masa ada Kitab (yang tertentu).'),
(1746, '013:039', '68', 13, 39, 'Allah menghapuskan apa yang Dia kehendaki dan menetapkan (apa yang Dia kehendaki), dan di sisi-Nya-lah terdapat Ummul-Kitab (Lauh mahfuzh).'),
(1747, '013:040', '68', 13, 40, 'Dan jika Kami perlihatkan kepadamu sebahagian (siksa) yang Kami ancamkan kepada mereka atau Kami wafatkan kamu (hal itu tidak penting bagimu) karena sesungguhnya tugasmu hanya menyampaikan saja, sedang Kami-lah yang menghisab amalan mereka.'),
(1748, '013:041', '68', 13, 41, 'Dan apakah mereka tidak melihat bahwa sesungguhnya Kami mendatangi daerah-daerah (orang-orang kafir), lalu Kami kurangi daerah-daerah itu (sedikit demi sedikit) dari tepi-tepinya? Dan Allah menetapkan hukum (menurut kehendak-Nya), tidak ada yang dapat menolak ketetapan-Nya; dan Dialah Yang Maha cepat hisab-Nya.'),
(1749, '013:042', '68', 13, 42, 'Dan sungguh orang-orang kafir yang sebelum mereka (kafir Mekah) telah mengadakan tipu daya, tetapi semua tipu daya itu adalah dalam kekuasaan Allah. Dia mengetahui apa yang diusahakan oleh setiap diri, dan orang-orang kafir akan mengetahui untuk siapa tempat kesudahan (yang baik) itu.'),
(1750, '013:043', '68', 13, 43, 'Berkatalah orang-orang kafir: \"Kamu bukan seorang yang dijadikan Rasul\". Katakanlah: \"Cukuplah Allah menjadi saksi antaraku dan kamu, dan antara orang yang mempunyai ilmu Al Kitab\".'),
(1751, '014:001', '68', 14, 1, 'Alif, laam raa. (Ini adalah) Kitab yang Kami turunkan kepadamu supaya kamu mengeluarkan manusia dari gelap gulita kepada cahaya terang benderang dengan izin Tuhan mereka, (yaitu) menuju jalan Tuhan Yang Maha Perkasa lagi Maha Terpuji.'),
(1752, '014:002', '68', 14, 2, 'Allah-lah yang memiliki segala apa yang di langit dan di bumi. Dan kecelakaanlah bagi orang-orang kafir karena siksaan yang sangat pedih,'),
(1753, '014:003', '68', 14, 3, '(yaitu) orang-orang yang lebih menyukai kehidupan dunia dari pada kehidupan akhirat, dan menghalang-halangi (manusia) dari jalan Allah dan menginginkan agar jalan Allah itu bengkok. Mereka itu berada dalam kesesatan yang jauh.'),
(1754, '014:004', '68', 14, 4, 'Kami tidak mengutus seorang rasulpun, melainkan dengan bahasa kaumnya, supaya ia dapat memberi penjelasan dengan terang kepada mereka. Maka Allah menyesatkan siapa yang Dia kehendaki, dan memberi petunjuk kepada siapa yang Dia kehendaki. Dan Dialah Tuhan Yang Maha Kuasa lagi Maha Bijaksana.'),
(1755, '014:005', '68', 14, 5, 'Dan sesungguhnya Kami telah mengutus Musa dengan membawa ayat-ayat Kami, (dan Kami perintahkan kepadanya): \"Keluarkanlah kaummu dari gelap gulita kepada cahaya terang benderang dan ingatkanlah mereka kepada hari-hari Allah\". Sesunguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang penyabar dan banyak bersyukur.'),
(1756, '014:006', '68', 14, 6, 'Dan (ingatlah), ketika Musa berkata kepada kaumnya: \"Ingatlah nikmat Allah atasmu ketika Dia menyelamatkan kamu dari (Fir´aun dan) pengikut-pengikutnya, mereka menyiksa kamu dengan siksa yang pedih, mereka menyembelih anak-anak laki-lakimu, membiarkan hidup anak-anak perempuanmu; dan pada yang demikian itu ada cobaan yang besar dari Tuhanmu\".'),
(1757, '014:007', '68', 14, 7, 'Dan (ingatlah juga), tatkala Tuhanmu memaklumkan; \"Sesungguhnya jika kamu bersyukur, pasti Kami akan menambah (nikmat) kepadamu, dan jika kamu mengingkari (nikmat-Ku), maka sesungguhnya azab-Ku sangat pedih\".'),
(1758, '014:008', '68', 14, 8, 'Dan Musa berkata: \"Jika kamu dan orang-orang yang ada di muka bumi semuanya mengingkari (nikmat Allah) maka sesungguhnya Allah Maha Kaya lagi Maha Terpuji\".'),
(1759, '014:009', '68', 14, 9, 'Belumkah sampai kepadamu berita orang-orang sebelum kamu (yaitu) kaum Nuh, ´Ad, Tsamud dan orang-orang sesudah mereka. Tidak ada yang mengetahui mereka selain Allah. Telah datang rasul-rasul kepada mereka (membawa) bukti-bukti yang nyata lalu mereka menutupkan tangannya ke mulutnya (karena kebencian), dan berkata: \"Sesungguhnya kami mengingkari apa yang kamu disuruh menyampaikannya (kepada kami), dan sesungguhnya kami benar-benar dalam keragu-raguan yang menggelisahkan terhadap apa yang kamu ajak kami kepadanya\".'),
(1760, '014:010', '68', 14, 10, 'Berkata rasul-rasul mereka: \"Apakah ada keragu-raguan terhadap Allah, Pencipta langit dan bumi? Dia menyeru kamu untuk memberi ampunan kepadamu dari dosa-dosamu dan menangguhkan (siksaan)mu sampai masa yang ditentukan?\" Mereka berkata: \"Kamu tidak lain hanyalah manusia seperti kami juga. Kamu menghendaki untuk menghalang-halangi (membelokkan) kami dari apa yang selalu disembah nenek moyang kami, karena itu datangkanlah kepada kami, bukti yang nyata\".'),
(1761, '014:011', '68', 14, 11, 'Rasul-rasul mereka berkata kepada mereka: \"Kami tidak lain hanyalah manusia seperti kamu, akan tetapi Allah memberi karunia kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Dan tidak patut bagi kami mendatangkan suatu bukti kepada kamu melainkan dengan izin Allah. Dan hanya kepada Allah sajalah hendaknya orang-orang mukmin bertawakkal.'),
(1762, '014:012', '68', 14, 12, 'Mengapa kami tidak akan bertawakkal kepada Allah padahal Dia telah menunjukkan jalan kepada kami, dan kami sungguh-sungguh akan bersabar terhadap gangguan-gangguan yang kamu lakukan kepada kami. Dan hanya kepada Allah saja orang-orang yang bertawakkal itu, berserah diri\".'),
(1763, '014:013', '68', 14, 13, 'Orang-orang kafir berkata kepada Rasul-rasul mereka: \"Kami sungguh-sungguh akan mengusir kamu dari negeri kami atau kamu kembali kepada agama kami\". Maka Tuhan mewahyukan kepada mereka: \"Kami pasti akan membinasakan orang-orang yang zalim itu,'),
(1764, '014:014', '68', 14, 14, 'dan Kami pasti akan menempatkan kamu di negeri-negeri itu sesudah mereka. Yang demikian itu (adalah untuk) orang-orang yang takut (akan menghadap) kehadirat-Ku dan yang takut kepada ancaman-Ku\".'),
(1765, '014:015', '68', 14, 15, 'Dan mereka memohon kemenangan (atas musuh-musuh mereka) dan binasalah semua orang yang berlaku sewenang-wenang lagi keras kepala,'),
(1766, '014:016', '68', 14, 16, 'di hadapannya ada Jahannam dan dia akan diberi minuman dengan air nanah,'),
(1767, '014:017', '68', 14, 17, 'diminumnnya air nanah itu dan hampir dia tidak bisa menelannya dan datanglah (bahaya) maut kepadanya dari segenap penjuru, tetapi dia tidak juga mati, dan dihadapannya masih ada azab yang berat.'),
(1768, '014:018', '68', 14, 18, 'Orang-orang yang kafir kepada Tuhannya, amalan-amalan mereka adalah seperti abu yang ditiup angin dengan keras pada suatu hari yang berangin kencang. Mereka tidak dapat mengambil manfaat sedikitpun dari apa yang telah mereka usahakan (di dunia). Yang demikian itu adalah kesesatan yang jauh.'),
(1769, '014:019', '68', 14, 19, 'Tidakkah kamu perhatikan, bahwa sesungguhnya Allah telah menciptakan langit dan bumi dengan hak? Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mengganti(mu) dengan makhluk yang baru,'),
(1770, '014:020', '68', 14, 20, 'dan yang demikian itu sekali-kali tidak sukar bagi Allah.'),
(1771, '014:021', '68', 14, 21, 'Dan mereka semuanya (di padang Mahsyar) akan berkumpul menghadap ke hadirat Allah, lalu berkatalah orang-orang yang lemah kepada orang-orang yang sombong: \"Sesungguhnya kami dahulu adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan daripada kami azab Allah (walaupun) sedikit saja? Mereka menjawab: \"Seandainya Allah memberi petunjuk kepada kami, niscaya kami dapat memberi petunjuk kepadamu. Sama saja bagi kita, apakah kita mengeluh ataukah bersabar. Sekali-kali kita tidak mempunyai tempat untuk melarikan diri\".'),
(1772, '014:022', '68', 14, 22, 'Dan berkatalah syaitan tatkala perkara (hisab) telah diselesaikan: \"Sesungguhnya Allah telah menjanjikan kepadamu janji yang benar, dan akupun telah menjanjikan kepadamu tetapi aku menyalahinya. Sekali-kali tidak ada kekuasaan bagiku terhadapmu, melainkan (sekedar) aku menyeru kamu lalu kamu mematuhi seruanku, oleh sebab itu janganlah kamu mencerca aku akan tetapi cercalah dirimu sendiri. Aku sekali-kali tidak dapat menolongmu dan kamupun sekali-kali tidak dapat menolongku. Sesungguhnya aku tidak membenarkan perbuatanmu mempersekutukan aku (dengan Allah) sejak dahulu\". Sesungguhnya orang-orang yang zalim itu mendapat siksaan yang pedih.'),
(1773, '014:023', '68', 14, 23, 'Dan dimasukkanlah orang-orang yang beriman dan beramal saleh ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya dengan seizin Tuhan mereka. Ucapan penghormatan mereka dalam surga itu ialah \"salaam\".'),
(1774, '014:024', '68', 14, 24, 'Tidakkah kamu perhatikan bagaimana Allah telah membuat perumpamaan kalimat yang baik seperti pohon yang baik, akarnya teguh dan cabangnya (menjulang) ke langit,'),
(1775, '014:025', '68', 14, 25, 'pohon itu memberikan buahnya pada setiap musim dengan seizin Tuhannya. Allah membuat perumpamaan-perumpamaan itu untuk manusia supaya mereka selalu ingat.'),
(1776, '014:026', '68', 14, 26, 'Dan perumpamaan kalimat yang buruk seperti pohon yang buruk, yang telah dicabut dengan akar-akarnya dari permukaan bumi; tidak dapat tetap (tegak) sedikitpun.'),
(1777, '014:027', '68', 14, 27, 'Allah meneguhkan (iman) orang-orang yang beriman dengan ucapan yang teguh itu dalam kehidupan di dunia dan di akhirat; dan Allah menyesatkan orang-orang yang zalim dan memperbuat apa yang Dia kehendaki.'),
(1778, '014:028', '68', 14, 28, 'Tidakkah kamu perhatikan orang-orang yang telah menukar nikmat Allah dengan kekafiran dan menjatuhkan kaumnya ke lembah kebinasaan?,'),
(1779, '014:029', '68', 14, 29, 'yaitu neraka jahannam; mereka masuk kedalamnya; dan itulah seburuk-buruk tempat kediaman.'),
(1780, '014:030', '68', 14, 30, 'Orang-orang kafir itu telah menjadikan sekutu-sekutu bagi Allah supaya mereka menyesatkan (manusia) dari jalan-Nya. Katakanlah: \"Bersenang-senanglah kamu, karena sesungguhnya tempat kembalimu ialah neraka\".'),
(1781, '014:031', '68', 14, 31, 'Katakanlah kepada hamba-hamba-Ku yang telah beriman: \"Hendaklah mereka mendirikan shalat, menafkahkan sebahagian rezeki yang Kami berikan kepada mereka secara sembunyi ataupun terang-terangan sebelum datang hari (kiamat) yang pada bari itu tidak ada jual beli dan persahabatan.'),
(1782, '014:032', '68', 14, 32, 'Allah-lah yang telah menciptakan langit dan bumi dan menurunkan air hujan dari langit, kemudian Dia mengeluarkan dengan air hujan itu berbagai buah-buahan menjadi rezeki untukmu; dan Dia telah menundukkan bahtera bagimu supaya bahtera itu, berlayar di lautan dengan kehendak-Nya, dan Dia telah menundukkan (pula) bagimu sungai-sungai.'),
(1783, '014:033', '68', 14, 33, 'Dan Dia telah menundukkan (pula) bagimu matahari dan bulan yang terus menerus beredar (dalam orbitnya); dan telah menundukkan bagimu malam dan siang.'),
(1784, '014:034', '68', 14, 34, 'Dan Dia telah memberikan kepadamu (keperluanmu) dan segala apa yang kamu mohonkan kepadanya. Dan jika kamu menghitung nikmat Allah, tidaklah dapat kamu menghinggakannya. Sesungguhnya manusia itu, sangat zalim dan sangat mengingkari (nikmat Allah).'),
(1785, '014:035', '68', 14, 35, 'Dan (ingatlah), ketika Ibrahim berkata: \"Ya Tuhanku, jadikanlah negeri ini (Mekah), negeri yang aman, dan jauhkanlah aku beserta anak cucuku daripada menyembah berhala-berhala.'),
(1786, '014:036', '68', 14, 36, 'Ya Tuhanku, sesungguhnya berhala-berhala itu telah menyesatkan kebanyakan daripada manusia, maka barangsiapa yang mengikutiku, maka sesungguhnya orang itu termasuk golonganku, dan barangsiapa yang mendurhakai aku, maka sesungguhnya Engkau, Maha Pengampun lagi Maha Penyayang.'),
(1787, '014:037', '68', 14, 37, 'Ya Tuhan kami, sesungguhnya aku telah menempatkan sebahagian keturunanku di lembah yang tidak mempunyai tanam-tanaman di dekat rumah Engkau (Baitullah) yang dihormati, ya Tuhan kami (yang demikian itu) agar mereka mendirikan shalat, maka jadikanlah hati sebagian manusia cenderung kepada mereka dan beri rezekilah mereka dari buah-buahan, mudah-mudahan mereka bersyukur.'),
(1788, '014:038', '68', 14, 38, 'Ya Tuhan kami, sesungguhnya Engkau mengetahui apa yang kami sembunyikan dan apa yang kami lahirkan; dan tidak ada sesuatupun yang tersembunyi bagi Allah, baik yang ada di bumi maupun yang ada di langit.'),
(1789, '014:039', '68', 14, 39, 'Segala puji bagi Allah yang telah menganugerahkan kepadaku di hari tua(ku) Ismail dan Ishaq. Sesungguhnya Tuhanku, benar-benar Maha Mendengar (memperkenankan) doa.'),
(1790, '014:040', '68', 14, 40, 'Ya Tuhanku, jadikanlah aku dan anak cucuku orang-orang yang tetap mendirikan shalat, ya Tuhan kami, perkenankanlah doaku.'),
(1791, '014:041', '68', 14, 41, 'Ya Tuhan kami, beri ampunlah aku dan kedua ibu bapaku dan sekalian orang-orang mukmin pada hari terjadinya hisab (hari kiamat)\".'),
(1792, '014:042', '68', 14, 42, 'Dan janganlah sekali-kali kamu (Muhammad) mengira, bahwa Allah lalai dari apa yang diperbuat oleh orang-orang yang zalim. Sesungguhnya Allah memberi tangguh kepada mereka sampai hari yang pada waktu itu mata (mereka) terbelalak,'),
(1793, '014:043', '68', 14, 43, 'mereka datang bergegas-gegas memenuhi panggilan dengan mangangkat kepalanya, sedang mata mereka tidak berkedip-kedip dan hati mereka kosong.'),
(1794, '014:044', '68', 14, 44, 'Dan berikanlah peringatan kepada manusia terhadap hari (yang pada waktu itu) datang azab kepada mereka, maka berkatalah orang-orang yang zalim: \"Ya Tuhan kami, beri tangguhlah kami (kembalikanlah kami ke dunia) walaupun dalam waktu yang sedikit, niscaya kami akan mematuhi seruan Engkau dan akan mengikuti rasul-rasul\". (Kepada mereka dikatakan): \"Bukankah kamu telah bersumpah dahulu (di dunia) bahwa sekali-kali kamu tidak akan binasa?'),
(1795, '014:045', '68', 14, 45, 'dan kamu telah berdiam di tempat-tempat kediaman orang-orang yang menganiaya diri mereka sendiri, dan telah nyata bagimu bagaimana Kami telah berbuat terhadap mereka dan telah Kami berikan kepadamu beberapa perumpamaan\".'),
(1796, '014:046', '68', 14, 46, 'Dan sesungguhnya mereka telah membuat makar yang besar padahal di sisi Allah-lah (balasan) makar mereka itu. Dan sesungguhnya makar mereka itu (amat besar) sehingga gunung-gunung dapat lenyap karenanya.'),
(1797, '014:047', '68', 14, 47, 'Karena itu janganlah sekali-kali kamu mengira Allah akan menyalahi janji-Nya kepada rasul-raaul-Nya; sesungguhnya Allah Maha Perkasa, lagi mempunyai pembalasan.'),
(1798, '014:048', '68', 14, 48, '(Yaitu) pada hari (ketika) bumi diganti dengan bumi yang lain dan (demikian pula) langit, dan meraka semuanya (di padang Mahsyar) berkumpul menghadap ke hadirat Allah yang Maha Esa lagi Maha Perkasa.'),
(1799, '014:049', '68', 14, 49, 'Dan kamu akan melihat orang-orang yang berdosa pada hari itu diikat bersama-sama dengan belenggu.'),
(1800, '014:050', '68', 14, 50, 'Pakaian mereka adalah dari pelangkin (ter) dan muka mereka ditutup oleh api neraka,'),
(1801, '014:051', '68', 14, 51, 'agar Allah memberi pembalasan kepada tiap-tiap orang terhadap apa yang ia usahakan. Sesungguhnya Allah Maha cepat hisab-Nya.'),
(1802, '014:052', '68', 14, 52, '(Al Quran) ini adalah penjelasan yang sempurna bagi manusia, dan supaya mereka diberi peringatan dengan-Nya, dan supaya mereka mengetahui bahwasanya Dia adalah Tuhan Yang Maha Esa dan agar orang-orang yang berakal mengambil pelajaran.'),
(1803, '015:001', '68', 15, 1, 'Alif, laam, raa. (Surat) ini adalah (sebagian dari) ayat-ayat Al-Kitab (yang sempurna), yaitu (ayat-ayat) Al Quran yang memberi penjelasan.'),
(1804, '015:002', '68', 15, 2, 'Orang-orang yang kafir itu seringkali (nanti di akhirat) menginginkan, kiranya mereka dahulu (di dunia) menjadi orang-orang muslim.'),
(1805, '015:003', '68', 15, 3, 'Biarkanlah mereka (di dunia ini) makan dan bersenang-senang dan dilalaikan oleh angan-angan (kosong), maka kelak mereka akan mengetahui (akibat perbuatan mereka).'),
(1806, '015:004', '68', 15, 4, 'Dan Kami tiada membinasakan sesuatu negeripun, melainkan ada baginya ketentuan masa yang telah ditetapkan.'),
(1807, '015:005', '68', 15, 5, 'Tidak ada suatu umatpun yang dapat mendahului ajalnya, dan tidak (pula) dapat mengundurkan(nya).'),
(1808, '015:006', '68', 15, 6, 'Mereka berkata: \"Hai orang yang diturunkan Al Quran kepadanya, sesungguhnya kamu benar-benar orang yang gila.'),
(1809, '015:007', '68', 15, 7, 'Mengapa kamu tidak mendatangkan malaikat kepada kami, jika kamu termasuk orang-orang yang benar?\"'),
(1810, '015:008', '68', 15, 8, 'Kami tidak menurunkan malaikat melainkan dengan benar (untuk membawa azab) dan tiadalah mereka ketika itu diberi tangguh.'),
(1811, '015:009', '68', 15, 9, 'Sesungguhnya Kami-lah yang menurunkan Al Quran, dan sesungguhnya Kami benar-benar memeliharanya.'),
(1812, '015:010', '68', 15, 10, 'Dan sesungguhnya Kami telah mengutus (beberapa rasul) sebelum kamu kepada umat-umat yang terdahulu.'),
(1813, '015:011', '68', 15, 11, 'Dan tidak datang seorang rasulpun kepada mereka, melainkan mereka selalu memperolok-olokkannya.'),
(1814, '015:012', '68', 15, 12, 'Demikianlah, Kami mamasukkan (rasa ingkar dan memperolok-olokkan itu) kedalam hati orang-orang yang berdosa (orang-orang kafir),'),
(1815, '015:013', '68', 15, 13, 'mereka tidak beriman kepadanya (Al Quran) dan sesungguhnya telah berlalu sunnatullah terhadap orang-orang dahulu.'),
(1816, '015:014', '68', 15, 14, 'Dan jika seandainya Kami membukakan kepada mereka salah satu dari (pintu-pintu) langit, lalu mereka terus menerus naik ke atasnya,'),
(1817, '015:015', '68', 15, 15, 'tentulah mereka berkata: \"Sesungguhnya pandangan kamilah yang dikaburkan, bahkan kami adalah orang orang yang kena sihir\".'),
(1818, '015:016', '68', 15, 16, 'Dan sesungguhnya Kami telah menciptakan gugusan bintang-bintang (di langit) dan Kami telah menghiasi langit itu bagi orang-orang yang memandang(nya),'),
(1819, '015:017', '68', 15, 17, 'dan Kami menjaganya dari tiap-tiap syaitan yang terkutuk,'),
(1820, '015:018', '68', 15, 18, 'kecuali syaitan yang mencuri-curi (berita) yang dapat didengar (dari malaikat) lalu dia dikejar oleh semburan api yang terang.'),
(1821, '015:019', '68', 15, 19, 'Dan Kami telah menghamparkan bumi dan menjadikan padanya gunung-gunung dan Kami tumbuhkan padanya segala sesuatu menurut ukuran.'),
(1822, '015:020', '68', 15, 20, 'Dan Kami telah menjadikan untukmu di bumi keperluan-keperluan hidup, dan (Kami menciptakan pula) makhluk-makhluk yang kamu sekali-kali bukan pemberi rezeki kepadanya.'),
(1823, '015:021', '68', 15, 21, 'Dan tidak ada sesuatupun melainkan pada sisi Kami-lah khazanahnya; dan Kami tidak menurunkannya melainkan dengan ukuran yang tertentu.'),
(1824, '015:022', '68', 15, 22, 'Dan Kami telah meniupkan angin untuk mengawinkan (tumbuh-tumbuhan) dan Kami turunkan hujan dari langit, lalu Kami beri minum kamu dengan air itu, dan sekali-kali bukanlah kamu yang menyimpannya.'),
(1825, '015:023', '68', 15, 23, 'Dan sesungguhnya benar-benar Kami-lah yang menghidupkan dan mematikan dan Kami (pulalah) yang mewarisi.'),
(1826, '015:024', '68', 15, 24, 'Dan sesungguhnya Kami telah mengetahui orang-orang yang terdahulu daripada-mu dan sesungguhnya Kami mengetahui pula orang-orang yang terkemudian (daripadamu).'),
(1827, '015:025', '68', 15, 25, 'Sesungguhnya Tuhanmu, Dialah yang akan menghimpunkan mereka. Sesungguhnya Dia adalah Maha Bijaksana lagi Maha Mengetahui.'),
(1828, '015:026', '68', 15, 26, 'Dan sesungguhnya Kami telah menciptakan manusia (Adam) dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk.'),
(1829, '015:027', '68', 15, 27, 'Dan Kami telah menciptakan jin sebelum (Adam) dari api yang sangat panas.'),
(1830, '015:028', '68', 15, 28, 'Dan (ingatlah), ketika Tuhanmu berfirman kepada para malaikat: \"Sesungguhnya Aku akan menciptakan seorang manusia dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk,'),
(1831, '015:029', '68', 15, 29, 'Maka apabila Aku telah menyempurnakan kejadiannya, dan telah meniup kan kedalamnya ruh (ciptaan)-Ku, maka tunduklah kamu kepadanya dengan bersujud.'),
(1832, '015:030', '68', 15, 30, 'Maka bersujudlah para malaikat itu semuanya bersama-sama,'),
(1833, '015:031', '68', 15, 31, 'kecuali iblis. Ia enggan ikut besama-sama (malaikat) yang sujud itu.'),
(1834, '015:032', '68', 15, 32, 'Allah berfirman: \"Hai iblis, apa sebabnya kamu tidak (ikut sujud) bersama-sama mereka yang sujud itu?\"'),
(1835, '015:033', '68', 15, 33, 'Berkata Iblis: \"Aku sekali-kali tidak akan sujud kepada manusia yang Engkau telah menciptakannya dari tanah liat kering (yang berasal) dari lumpur hitam yang diberi bentuk\"'),
(1836, '015:034', '68', 15, 34, 'Allah berfirman: \"Keluarlah dari surga, karena sesungguhnya kamu terkutuk,'),
(1837, '015:035', '68', 15, 35, 'dan sesungguhnya kutukan itu tetap menimpamu sampai hari kiamat\".'),
(1838, '015:036', '68', 15, 36, 'Berkata iblis: \"Ya Tuhanku, (kalau begitu) maka beri tangguhlah kepadaku sampai hari (manusia) dibangkitkan,'),
(1839, '015:037', '68', 15, 37, 'Allah berfirman: \"(Kalau begitu) maka sesungguhnya kamu termasuk orang-orang yang diberi tangguh,'),
(1840, '015:038', '68', 15, 38, 'sampai hari (suatu) waktu yang telah ditentukan,'),
(1841, '015:039', '68', 15, 39, 'Iblis berkata: \"Ya Tuhanku, oleh sebab Engkau telah memutuskan bahwa aku sesat, pasti aku akan menjadikan mereka memandang baik (perbuatan maksiat) di muka bumi, dan pasti aku akan menyesatkan mereka semuanya,'),
(1842, '015:040', '68', 15, 40, 'kecuali hamba-hamba Engkau yang mukhlis di antara mereka\".'),
(1843, '015:041', '68', 15, 41, 'Allah berfirman: \"Ini adalah jalan yang lurus, kewajiban Aku-lah (menjaganya).');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(1844, '015:042', '68', 15, 42, 'Sesungguhnya hamba-hamba-Ku tidak ada kekuasaan bagimu terhadap mereka, kecuali orang-orang yang mengikut kamu, yaitu orang-orang yang sesat.'),
(1845, '015:043', '68', 15, 43, 'Dan sesungguhnya Jahannam itu benar-benar tempat yang telah diancamkan kepada mereka (pengikut-pengikut syaitan) semuanya.'),
(1846, '015:044', '68', 15, 44, 'Jahannam itu mempunyai tujuh pintu. Tiap-tiap pintu (telah ditetapkan) untuk golongan yang tertentu dari mereka.'),
(1847, '015:045', '68', 15, 45, 'Sesungguhnya orang-orang yang bertakwa itu berada dalam surga (taman-taman) dan (di dekat) mata air-mata air (yang mengalir).'),
(1848, '015:046', '68', 15, 46, '(Dikatakan kepada mereka): \"Masuklah ke dalamnya dengan sejahtera lagi aman\"'),
(1849, '015:047', '68', 15, 47, 'Dan Kami lenyapkan segala rasa dendam yang berada dalam hati mereka, sedang mereka merasa bersaudara duduk berhadap-hadapan di atas dipan-dipan.'),
(1850, '015:048', '68', 15, 48, 'Mereka tidak merasa lelah di dalamnya dan mereka sekali-kali tidak akan dikeluarkan daripadanya.'),
(1851, '015:049', '68', 15, 49, 'Kabarkanlah kepada hamba-hamba-Ku, bahwa sesungguhnya Aku-lah Yang Maha Pengampun lagi Maha Penyayang,'),
(1852, '015:050', '68', 15, 50, 'dan bahwa sesungguhnya azab-Ku adalah azab yang sangat pedih.'),
(1853, '015:051', '68', 15, 51, 'Dan kabarkanlah kepada mereka tentang tamu-tamu Ibrahim.'),
(1854, '015:052', '68', 15, 52, 'Ketika mereka masuk ke tempatnya, lalu mereka mengucapkan: \"Salaam\". Berkata Ibrahim: \"Sesungguhnya kami merasa takut kepadamu\".'),
(1855, '015:053', '68', 15, 53, 'Mereka berkata: \"Janganlah kamu merasa takut, sesungguhnya kami memberi kabar gembira kepadamu dengan (kelahiran seorang) anak laki-laki (yang akan menjadi) orang yang alim\".'),
(1856, '015:054', '68', 15, 54, 'Berkata Ibrahim: \"Apakah kamu memberi kabar gembira kepadaku padahal usiaku telah lanjut, maka dengan cara bagaimanakah (terlaksananya) berita gembira yang kamu kabarkan ini?\"'),
(1857, '015:055', '68', 15, 55, 'Mereka menjawab: \"Kami menyampaikan kabar gembira kepadamu dengan benar, maka janganlah kamu termasuk orang-orang yang berputus asa\".'),
(1858, '015:056', '68', 15, 56, 'Ibrahim berkata: \"Tidak ada orang yang berputus asa dari rahmat Tuhan-nya, kecuali orang-orang yang sesat\".'),
(1859, '015:057', '68', 15, 57, 'Berkata (pula) Ibrahim: \"Apakah urusanmu yang penting (selain itu), hai para utusan?\"'),
(1860, '015:058', '68', 15, 58, 'Mereka menjawab: \"Kami sesungguhnya diutus kepada kaum yang berdosa,'),
(1861, '015:059', '68', 15, 59, 'kecuali Luth beserta pengikut-pengikutnya. Sesungguhnya Kami akan menyelamatkan mereka semuanya,'),
(1862, '015:060', '68', 15, 60, 'kecuali istrinya. Kami telah menentukan, bahwa sesungguhnya ia itu termasuk orang-orang yang tertinggal (bersama-sama dengan orang kafir lainnya)\".'),
(1863, '015:061', '68', 15, 61, 'Maka tatkala para utusan itu datang kepada kaum Luth, beserta pengikut pengikutnya,'),
(1864, '015:062', '68', 15, 62, 'ia berkata: \"Sesungguhnya kamu adalah orang-orang yang tidak dikenal\".'),
(1865, '015:063', '68', 15, 63, 'Para utusan menjawab: \"Sebenarnya kami ini datang kepadamu dengan membawa azab yang selalu mereka dustakan.'),
(1866, '015:064', '68', 15, 64, 'Dan kami datang kepadamu membawa kebenaran dan sesungguhnya kami betul-betul orang-orang benar.'),
(1867, '015:065', '68', 15, 65, 'Maka pergilah kamu di akhir malam dengan membawa keluargamu, dan ikutlah mereka dari belakang dan janganlah seorangpun di antara kamu menoleh kebelakang dan teruskanlah perjalanan ke tempat yang di perintahkan kepadamu\".'),
(1868, '015:066', '68', 15, 66, 'Dan telah Kami wahyukan kepadanya (Luth) perkara itu, yaitu bahwa mereka akan ditumpas habis di waktu subuh.'),
(1869, '015:067', '68', 15, 67, 'Dan datanglah penduduk kota itu (ke rumah Luth) dengan gembira (karena) kedatangan tamu-tamu itu.'),
(1870, '015:068', '68', 15, 68, 'Luth berkata: \"Sesungguhnya mereka adalah tamuku; maka janganlah kamu memberi malu (kepadaku),'),
(1871, '015:069', '68', 15, 69, 'dan bertakwalah kepada Allah dan janganlah kamu membuat aku terhina\".'),
(1872, '015:070', '68', 15, 70, 'Mereka berkata: \"Dan bukankah kami telah melarangmu dari (melindungi) manusia?\"'),
(1873, '015:071', '68', 15, 71, 'Luth berkata: \"Inilah puteri-puteriku (kawinlah dengan mereka), jika kamu hendak berbuat (secara yang halal)\".'),
(1874, '015:072', '68', 15, 72, '(Allah berfirman): \"Demi umurmu (Muhammad), sesungguhnya mereka terombang-ambing di dalam kemabukan (kesesatan)\".'),
(1875, '015:073', '68', 15, 73, 'Maka mereka dibinasakan oleh suara keras yang mengguntur, ketika matahari akan terbit.'),
(1876, '015:074', '68', 15, 74, 'Maka Kami jadikan bahagian atas kota itu terbalik ke bawah dan Kami hujani mereka dengan batu dari tanah yang keras.'),
(1877, '015:075', '68', 15, 75, 'Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang memperhatikan tanda-tanda.'),
(1878, '015:076', '68', 15, 76, 'Dan sesungguhnya kota itu benar-benar terletak di jalan yang masih tetap (dilalui manusia).'),
(1879, '015:077', '68', 15, 77, 'Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.'),
(1880, '015:078', '68', 15, 78, 'Dan sesungguhnya adalah penduduk Aikah itu benar-benar kaum yang zalim,'),
(1881, '015:079', '68', 15, 79, 'maka Kami membinasakan mereka. Dan sesungguhnya kedua kota itu benar-benar terletak di jalan umum yang terang.'),
(1882, '015:080', '68', 15, 80, 'Dan sesungguhnya penduduk-penduduk kota Al Hijr telah mendustakan rasul-rasul,'),
(1883, '015:081', '68', 15, 81, 'dan Kami telah mendatangkan kepada mereka tanda-tanda (kekuasaan) Kami, tetapi mereka selalu berpaling daripadanya,'),
(1884, '015:082', '68', 15, 82, 'dan mereka memahat rumah-rumah dari gunung-gunung batu (yang didiami) dengan aman.'),
(1885, '015:083', '68', 15, 83, 'Maka mereka dibinasakan oleh suara keras yang mengguntur di waktu pagi,'),
(1886, '015:084', '68', 15, 84, 'maka tak dapat menolong mereka, apa yang telah mereka usahakan.'),
(1887, '015:085', '68', 15, 85, 'Dan tidaklah Kami ciptakan langit dan bumi dan apa yang ada di antara keduanya, melainkan dengan benar. Dan sesungguhnya saat (kiamat) itu pasti akan datang, maka maafkanlah (mereka) dengan cara yang baik.'),
(1888, '015:086', '68', 15, 86, 'Sesungguhnya Tuhanmu, Dialah Yang Maha Pencipta lagi Maha Mengetahui.'),
(1889, '015:087', '68', 15, 87, 'Dan sesungguhnya Kami telah berikan kepadamu tujuh ayat yang dibaca berulang-ulang dan Al Quran yang agung.'),
(1890, '015:088', '68', 15, 88, 'Janganlah sekali-kali kamu menunjukkan pandanganmu kepada kenikmatan hidup yang telah Kami berikan kepada beberapa golongan di antara mereka (orang-orang kafir itu), dan janganlah kamu bersedih hati terhadap mereka dan berendah dirilah kamu terhadap orang-orang yang beriman.'),
(1891, '015:089', '68', 15, 89, 'Dan katakanlah: \"Sesungguhnya aku adalah pemberi peringatan yang menjelaskan\".'),
(1892, '015:090', '68', 15, 90, 'Sebagaimana (Kami telah memberi peringatan), Kami telah menurunkan (azab) kepada orang-orang yang membagi-bagi (Kitab Allah),'),
(1893, '015:091', '68', 15, 91, '(yaitu) orang-orang yang telah menjadikan Al Quran itu terbagi-bagi.'),
(1894, '015:092', '68', 15, 92, 'Maka demi Tuhanmu, Kami pasti akan menanyai mereka semua,'),
(1895, '015:093', '68', 15, 93, 'tentang apa yang telah mereka kerjakan dahulu.'),
(1896, '015:094', '68', 15, 94, 'Maka sampaikanlah olehmu secara terang-terangan segala apa yang diperintahkan (kepadamu) dan berpalinglah dari orang-orang yang musyrik.'),
(1897, '015:095', '68', 15, 95, 'Sesungguhnya Kami memelihara kamu daripada (kejahatan) orang-orang yang memperolok-olokkan (kamu),'),
(1898, '015:096', '68', 15, 96, '(Yaitu) orang-orang yang menganggap adanya tuhan yang lain di samping Allah; maka mereka kelak akan mengetahui (akibat-akibatnya).'),
(1899, '015:097', '68', 15, 97, 'Dan Kami sungguh-sungguh mengetahui, bahwa dadamu menjadi sempit disebabkan apa yang mereka ucapkan,'),
(1900, '015:098', '68', 15, 98, 'maka bertasbihlah dengan memuji Tuhanmu dan jadilah kamu di antara orang-orang yang bersujud (shalat),'),
(1901, '015:099', '68', 15, 99, 'dan sembahlah Tuhanmu sampai datang kepadamu yang diyakini (ajal).'),
(1902, '016:001', '68', 16, 1, 'Telah pasti datangnya ketetapan Allah maka janganlah kamu meminta agar disegerakan (datang)nya. Maha Suci Allah dan Maha Tinggi dari apa yang mereka persekutukan.'),
(1903, '016:002', '68', 16, 2, 'Dia menurunkan para malaikat dengan (membawa) wahyu dengan perintah-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, yaitu: \"Peringatkanlah olehmu sekalian, bahwasanya tidak ada Tuhan (yang hak) melainkan Aku, maka hendaklah kamu bertakwa kepada-Ku\".'),
(1904, '016:003', '68', 16, 3, 'Dia menciptakan langit dan bumi dengan hak. Maha Tinggi Allah daripada apa yang mereka persekutukan.'),
(1905, '016:004', '68', 16, 4, 'Dia telah menciptakan manusia dari mani, tiba-tiba ia menjadi pembantah yang nyata.'),
(1906, '016:005', '68', 16, 5, 'Dan Dia telah menciptakan binatang ternak untuk kamu; padanya ada (bulu) yang menghangatkan dan berbagai-bagai manfaat, dan sebahagiannya kamu makan.'),
(1907, '016:006', '68', 16, 6, 'Dan kamu memperoleh pandangan yang indah padanya, ketika kamu membawanya kembali ke kandang dan ketika kamu melepaskannya ke tempat penggembalaan.'),
(1908, '016:007', '68', 16, 7, 'Dan ia memikul beban-bebanmu ke suatu negeri yang kamu tidak sanggup sampai kepadanya, melainkan dengan kesukaran-kesukaran (yang memayahkan) diri. Sesungguhnya Tuhanmu benar-benar Maha Pengasih lagi Maha Penyayang,'),
(1909, '016:008', '68', 16, 8, 'dan (Dia telah menciptakan) kuda, bagal dan keledai, agar kamu menungganginya dan (menjadikannya) perhiasan. Dan Allah menciptakan apa yang kamu tidak mengetahuinya.'),
(1910, '016:009', '68', 16, 9, 'Dan hak bagi Allah (menerangkan) jalan yang lurus, dan di antara jalan-jalan ada yang bengkok. Dan jikalau Dia menghendaki, tentulah Dia memimpin kamu semuanya (kepada jalan yang benar).'),
(1911, '016:010', '68', 16, 10, 'Dialah, Yang telah menurunkan air hujan dari langit untuk kamu, sebahagiannya menjadi minuman dan sebahagiannya (menyuburkan) tumbuh-tumbuhan, yang pada (tempat tumbuhnya) kamu menggembalakan ternakmu.'),
(1912, '016:011', '68', 16, 11, 'Dia menumbuhkan bagi kamu dengan air hujan itu tanam-tanaman; zaitun, korma, anggur dan segala macam buah-buahan. Sesungguhnya pada yang demikian itu benar-benar ada tanda (kekuasaan Allah) bagi kaum yang memikirkan.'),
(1913, '016:012', '68', 16, 12, 'Dan Dia menundukkan malam dan siang, matahari dan bulan untukmu. Dan bintang-bintang itu ditundukkan (untukmu) dengan perintah-Nya. Sesungguhnya pada yang demikian itu benar-benar ada tanda-tanda (kekuasaan Allah) bagi kaum yang memahami(nya),'),
(1914, '016:013', '68', 16, 13, 'dan Dia (menundukkan pula) apa yang Dia ciptakan untuk kamu di bumi ini dengan berlain-lainan macamnya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi kaum yang mengambil pelajaran.'),
(1915, '016:014', '68', 16, 14, 'Dan Dialah, Allah yang menundukkan lautan (untukmu), agar kamu dapat memakan daripadanya daging yang segar (ikan), dan kamu mengeluarkan dari lautan itu perhiasan yang kamu pakai; dan kamu melihat bahtera berlayar padanya, dan supaya kamu mencari (keuntungan) dari karunia-Nya, dan supaya kamu bersyukur.'),
(1916, '016:015', '68', 16, 15, 'Dan Dia menancapkan gunung-gunung di bumi supaya bumi itu tidak goncang bersama kamu, (dan Dia menciptakan) sungai-sungai dan jalan-jalan agar kamu mendapat petunjuk,'),
(1917, '016:016', '68', 16, 16, 'dan (Dia ciptakan) tanda-tanda (penunjuk jalan). Dan dengan bintang-bintang itulah mereka mendapat petunjuk.'),
(1918, '016:017', '68', 16, 17, 'Maka apakah (Allah) yang menciptakan itu sama dengan yang tidak dapat menciptakan (apa-apa)?. Maka mengapa kamu tidak mengambil pelajaran.'),
(1919, '016:018', '68', 16, 18, 'Dan jika kamu menghitung-hitung nikmat Allah, niscaya kamu tak dapat menentukan jumlahnya. Sesungguhnya Allah benar-benar Maha Pengampun lagi Maha Penyayang.'),
(1920, '016:019', '68', 16, 19, 'Dan Allah mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan.'),
(1921, '016:020', '68', 16, 20, 'Dan berhala-berhala yang mereka seru selain Allah, tidak dapat membuat sesuatu apapun, sedang berhala-berhala itu (sendiri) dibuat orang.'),
(1922, '016:021', '68', 16, 21, '(Berhala-berhala itu) benda mati tidak hidup, dan berhala-berhala tidak mengetahui bilakah penyembah-penyembahnya akan dibangkitkan.'),
(1923, '016:022', '68', 16, 22, 'Tuhan kamu adalah Tuhan Yang Maha Esa. Maka orang-orang yang tidak beriman kepada akhirat, hati mereka mengingkari (keesaaan Allah), sedangkan mereka sendiri adalah orang-orang yang sombong.'),
(1924, '016:023', '68', 16, 23, 'Tidak diragukan lagi bahwa sesungguhnya Allah mengetahui apa yang mereka rahasiakan dan apa yang mereka lahirkan. Sesungguhnya Allah tidak menyukai orang-orang yang sombong.'),
(1925, '016:024', '68', 16, 24, 'Dan apabila dikatakan kepada mereka \"Apakah yang telah diturunkan Tuhanmu?\" Mereka menjawab: \"Dongeng-dongengan orang-orang dahulu\",'),
(1926, '016:025', '68', 16, 25, '(ucapan mereka) menyebabkan mereka memikul dosa-dosanya dengan sepenuh-penuhnya pada hari kiamat, dan sebahagian dosa-dosa orang yang mereka sesatkan yang tidak mengetahui sedikitpun (bahwa mereka disesatkan). Ingatlah, amat buruklah dosa yang mereka pikul itu.'),
(1927, '016:026', '68', 16, 26, 'Sesungguhnya orang-orang yang sebelum mereka telah mengadakan makar, maka Allah menghancurkan rumah-rumah mereka dari fondasinya, lalu atap (rumah itu) jatuh menimpa mereka dari atas, dan datanglah azab itu kepada mereka dari tempat yang tidak mereka sadari.'),
(1928, '016:027', '68', 16, 27, 'Kemudian Allah menghinakan mereka di hari kiamat, dan berfirman: \"Di manakah sekutu-sekutu-Ku itu (yang karena membelanya) kamu selalu memusuhi mereka (nabi-nabi dan orang-orang mukmin)?\" Berkatalah orang-orang yang telah diberi ilmu: \"Sesungguhnya kehinaan dan azab hari ini ditimpakan atas orang-orang yang kafir\",'),
(1929, '016:028', '68', 16, 28, '(yaitu) orang-orang yang dimatikan oleh para malaikat dalam keadaan berbuat zalim kepada diri mereka sendiri, lalu mereka menyerah diri (sambil berkata); \"Kami sekali-kali tidak ada mengerjakan sesuatu kejahatanpun\". (Malaikat menjawab): \"Ada, sesungguhnya Allah Maha Mengetahui apa yang telah kamu kerjakan\".'),
(1930, '016:029', '68', 16, 29, 'Maka masukilah pintu-pintu neraka Jahannam, kamu kekal di dalamnya. Maka amat buruklah tempat orang-orang yang menyombongkan diri itu.'),
(1931, '016:030', '68', 16, 30, 'Dan dikatakan kepada orang-orang yang bertakwa: \"Apakah yang telah diturunkan oleh Tuhanmu?\" Mereka menjawab: \"(Allah telah menurunkan) kebaikan\". Orang-orang yang berbuat baik di dunia ini mendapat (pembalasan) yang baik. Dan sesungguhnya kampung akhirat adalah lebih baik dan itulah sebaik-baik tempat bagi orang yang bertakwa,'),
(1932, '016:031', '68', 16, 31, '(yaitu) surga ´Adn yang mereka masuk ke dalamnya, mengalir di bawahnya sungai-sungai, di dalam surga itu mereka mendapat segala apa yang mereka kehendaki. Demikianlah Allah memberi balasan kepada orang-orang yang bertakwa,'),
(1933, '016:032', '68', 16, 32, '(yaitu) orang-orang yang diwafatkan dalam keadaan baik oleh para malaikat dengan mengatakan (kepada mereka): \"Salaamun´alaikum, masuklah kamu ke dalam surga itu disebabkan apa yang telah kamu kerjakan\".'),
(1934, '016:033', '68', 16, 33, 'Tidak ada yang ditunggu-tunggu orang kafir selain dari datangnya para malaikat kepada mereka atau datangnya perintah Tuhanmu. Demikianlah yang telah diperbuat oleh orang-orang (kafir) sebelum mereka. Dan Allah tidak menganiaya mereka, akan tetapi merekalah yang selalu menganiaya diri mereka sendiri,'),
(1935, '016:034', '68', 16, 34, 'Maka mereka ditimpa oleh (akibat) kejahatan perbuatan mereka dan mereka diliputi oleh azab yang selalu mereka perolok-olokan.'),
(1936, '016:035', '68', 16, 35, 'Dan berkatalah orang-orang musyrik: \"Jika Allah menghendaki, niscaya kami tidak akan menyembah sesuatu apapun selain Dia, baik kami maupun bapak-bapak kami, dan tidak pula kami mengharamkan sesuatupun tanpa (izin)-Nya\". Demikianlah yang diperbuat orang-orang sebelum mereka; maka tidak ada kewajiban atas para rasul, selain dari menyampaikan (amanat Allah) dengan terang.'),
(1937, '016:036', '68', 16, 36, 'Dan sungguhnya Kami telah mengutus rasul pada tiap-tiap umat (untuk menyerukan): \"Sembahlah Allah (saja), dan jauhilah Thaghut itu\", maka di antara umat itu ada orang-orang yang diberi petunjuk oleh Allah dan ada pula di antaranya orang-orang yang telah pasti kesesatan baginya. Maka berjalanlah kamu dimuka bumi dan perhatikanlah bagaimana kesudahan orang-orang yang mendustakan (rasul-rasul).'),
(1938, '016:037', '68', 16, 37, 'Jika kamu sangat mengharapkan agar mereka dapat petunjuk, maka sesungguhnya Allah tiada memberi petunjuk kepada orang yang disesatkan-Nya, dan sekali-kali mereka tiada mempunyai penolong.'),
(1939, '016:038', '68', 16, 38, 'Mereka bersumpah dengan nama Allah dengan sumpahnya yang sungguh-sungguh: \"Allah tidak akan akan membangkitkan orang yang mati\". (Tidak demikian), bahkan (pasti Allah akan membangkitnya), sebagai suatu janji yang benar dari Allah, akan tetapi kebanyakan manusia tiada mengetahui,'),
(1940, '016:039', '68', 16, 39, 'agar Allah menjelaskan kepada mereka apa yang mereka perselisihkan itu, agar orang-orang kafir itu mengetahui bahwasanya mereka adalah orang-orang yang berdusta.'),
(1941, '016:040', '68', 16, 40, 'Sesungguhnya perkataan Kami terhadap sesuatu apabila Kami menghendakinya, Kami hanya mengatakan kepadanya: \"kun (jadilah)\", maka jadilah ia.'),
(1942, '016:041', '68', 16, 41, 'Dan orang-orang yang berhijrah karena Allah sesudah mereka dianiaya, pasti Kami akan memberikan tempat yang bagus kepada mereka di dunia. Dan sesungguhnya pahala di akhirat adalah lebih besar, kalau mereka mengetahui,'),
(1943, '016:042', '68', 16, 42, '(yaitu) orang-orang yang sabar dan hanya kepada Tuhan saja mereka bertawakkal.'),
(1944, '016:043', '68', 16, 43, 'Dan Kami tidak mengutus sebelum kamu, kecuali orang-orang lelaki yang Kami beri wahyu kepada mereka; maka bertanyalah kepada orang yang mempunyai pengetahuan jika kamu tidak mengetahui,'),
(1945, '016:044', '68', 16, 44, 'keterangan-keterangan (mukjizat) dan kitab-kitab. Dan Kami turunkan kepadamu Al Quran, agar kamu menerangkan pada umat manusia apa yang telah diturunkan kepada mereka dan supaya mereka memikirkan,'),
(1946, '016:045', '68', 16, 45, 'maka apakah orang-orang yang membuat makar yang jahat itu, merasa aman (dari bencana) ditenggelamkannya bumi oleh Allah bersama mereka, atau datangnya azab kepada mereka dari tempat yang tidak mereka sadari,'),
(1947, '016:046', '68', 16, 46, 'atau Allah mengazab mereka diwaktu mereka dalam perjalanan, maka sekali-kali mereka tidak dapat menolak (azab itu),'),
(1948, '016:047', '68', 16, 47, 'atau Allah mengazab mereka dengan berangsur-angsur (sampai binasa). Maka sesungguhnya Tuhanmu adalah Maha Pengasih lagi Maha Penyayang.'),
(1949, '016:048', '68', 16, 48, 'Dan apakah mereka tidak memperhatikan segala sesuatu yang telah diciptakan Allah yang bayangannya berbolak-balik ke kanan dan ke kiri dalam keadaan sujud kepada Allah, sedang mereka berendah diri?'),
(1950, '016:049', '68', 16, 49, 'Dan kepada Allah sajalah bersujud segala apa yang berada di langit dan semua makhluk yang melata di bumi dan (juga) para ma]aikat, sedang mereka (malaikat) tidak menyombongkan diri.'),
(1951, '016:050', '68', 16, 50, 'Mereka takut kepada Tuhan mereka yang di atas mereka dan melaksanakan apa yang diperintahkan (kepada mereka).'),
(1952, '016:051', '68', 16, 51, 'Allah berfirman: \"Janganlah kamu menyembah dua tuhan; sesungguhnya Dialah Tuhan Yang Maha Esa, maka hendaklah kepada-Ku saja kamu takut\".'),
(1953, '016:052', '68', 16, 52, 'Dan kepunyaan-Nya-lah segala apa yang ada di langit dan di bumi, dan untuk-Nya-lah ketaatan itu selama-lamanya. Maka mengapa kamu bertakwa kepada selain Allah?'),
(1954, '016:053', '68', 16, 53, 'Dan apa saja nikmat yang ada pada kamu, maka dari Allah-lah (datangnya), dan bila kamu ditimpa oleh kemudharatan, maka hanya kepada-Nya-lah kamu meminta pertolongan.'),
(1955, '016:054', '68', 16, 54, 'Kemudian apabila Dia telah menghilangkan kemudharatan itu dari pada kamu, tiba-tiba sebahagian dari pada kamu mempersekutukan Tuhannya dengan (yang lain),'),
(1956, '016:055', '68', 16, 55, 'Biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka; maka bersenang-senanglah kamu. Kelak kamu akan mengetahui (akibatnya).'),
(1957, '016:056', '68', 16, 56, 'Dan mereka sediakan untuk berhala-berhala yang mereka tiada mengetahui (kekuasaannya), satu bahagian dari rezeki yang telah Kami berikan kepada mereka. Demi Allah, sesungguhnya kamu akan ditanyai tentang apa yang telah kamu ada-adakan.'),
(1958, '016:057', '68', 16, 57, 'Dan mereka menetapkan bagi Allah anak-anak perempuan. Maha Suci Allah, sedang untuk mereka sendiri (mereka tetapkan) apa yang mereka sukai (yaitu anak-anak laki-laki).'),
(1959, '016:058', '68', 16, 58, 'Dan apabila seseorang dari mereka diberi kabar dengan (kelahiran) anak perempuan, hitamlah (merah padamlah) mukanya, dan dia sangat marah.'),
(1960, '016:059', '68', 16, 59, 'Ia menyembunyikan dirinya dari orang banyak, disebabkan buruknya berita yang disampaikan kepadanya. Apakah dia akan memeliharanya dengan menanggung kehinaan ataukah akan menguburkannya ke dalam tanah (hidup-hidup)?. Ketahuilah, alangkah buruknya apa yang mereka tetapkan itu.'),
(1961, '016:060', '68', 16, 60, 'Orang-orang yang tidak beriman kepada kehidupan akhirat, mempunyai sifat yang buruk; dan Allah mempunyai sifat yang Maha Tinggi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(1962, '016:061', '68', 16, 61, 'Jikalau Allah menghukum manusia karena kezalimannya, niscaya tidak akan ditinggalkan-Nya di muka bumi sesuatupun dari makhluk yang melata, tetapi Allah menangguhkan mereka sampai kepada waktu yang ditentukan. Maka apabila telah tiba waktunya (yang ditentukan) bagi mereka, tidaklah mereka dapat mengundurkannya barang sesaatpun dan tidak (pula) mendahulukannya.'),
(1963, '016:062', '68', 16, 62, 'Dan mereka menetapkan bagi Allah apa yang mereka sendiri membencinya, dan lidah mereka mengucapkan kedustaan, yaitu bahwa sesungguhnya merekalah yang akan mendapat kebaikan. Tiadalah diragukan bahwa nerakalah bagi mereka, dan sesungguhnya mereka segera dimasukkan (ke dalamnya).'),
(1964, '016:063', '68', 16, 63, 'Demi Allah, sesungguhnya Kami telah mengutus rasul-rasul Kami kepada umat-umat sebelum kamu, tetapi syaitan menjadikan umat-umat itu memandang baik perbuatan mereka (yang buruk), maka syaitan menjadi pemimpin mereka di hari itu dan bagi mereka azab yang sangat pedih.'),
(1965, '016:064', '68', 16, 64, 'Dan Kami tidak menurunkan kepadamu Al-Kitab (Al Quran) ini, melainkan agar kamu dapat menjelaskan kepada mereka apa yang mereka perselisihkan itu dan menjadi petunjuk dan rahmat bagi kaum yang beriman.'),
(1966, '016:065', '68', 16, 65, 'Dan Allah menurunkan dari langit air (hujan) dan dengan air itu dihidupkan-Nya bumi sesudah matinya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Tuhan) bagi orang-orang yang mendengarkan (pelajaran).'),
(1967, '016:066', '68', 16, 66, 'Dan sesungguhnya pada binatang ternak itu benar-benar terdapat pelajaran bagi kamu. Kami memberimu minum dari pada apa yang berada dalam perutnya (berupa) susu yang bersih antara tahi dan darah, yang mudah ditelan bagi orang-orang yang meminumnya.'),
(1968, '016:067', '68', 16, 67, 'Dan dari buah korma dan anggur, kamu buat minimuman yang memabukkan dan rezeki yang baik. Sesunggguhnya pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang memikirkan.'),
(1969, '016:068', '68', 16, 68, 'Dan Tuhanmu mewahyukan kepada lebah: \"Buatlah sarang-sarang di bukit-bukit, di pohon-pohon kayu, dan di tempat-tempat yang dibikin manusia\",'),
(1970, '016:069', '68', 16, 69, 'kemudian makanlah dari tiap-tiap (macam) buah-buahan dan tempuhlah jalan Tuhanmu yang telah dimudahkan (bagimu). Dari perut lebah itu ke luar minuman (madu) yang bermacam-macam warnanya, di dalamnya terdapat obat yang menyembuhkan bagi manusia. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kebesaran Tuhan) bagi orang-orang yang memikirkan.'),
(1971, '016:070', '68', 16, 70, 'Allah menciptakan kamu, kemudian mewafatkan kamu; dan di antara kamu ada yang dikembalikan kepada umur yang paling lemah (pikun), supaya dia tidak mengetahui lagi sesuatupun yang pernah diketahuinya. Sesungguhnya Allah Maha Mengetahui lagi Maha Kuasa.'),
(1972, '016:071', '68', 16, 71, 'Dan Allah melebihkan sebahagian kamu dari sebagian yang lain dalam hal rezeki, tetapi orang-orang yang dilebihkan (rezekinya itu) tidak mau memberikan rezeki mereka kepada budak-budak yang mereka miliki, agar mereka sama (merasakan) rezeki itu. Maka mengapa mereka mengingkari nikmat Allah?.'),
(1973, '016:072', '68', 16, 72, 'Allah menjadikan bagi kamu isteri-isteri dari jenis kamu sendiri dan menjadikan bagimu dari isteri-isteri kamu itu, anak-anak dan cucu-cucu, dan memberimu rezeki dari yang baik-baik. Maka mengapakah mereka beriman kepada yang bathil dan mengingkari nikmat Allah?\"'),
(1974, '016:073', '68', 16, 73, 'Dan mereka menyembah selain Allah, sesuatu yang tidak dapat memberikan rezeki kepada mereka sedikitpun dari langit dan bumi, dan tidak berkuasa (sedikit juapun).'),
(1975, '016:074', '68', 16, 74, 'Maka janganlah kamu mengadakan sekutu-sekutu bagi Allah. Sesungguhnya Allah mengetahui, sedang kamu tidak mengetahui.'),
(1976, '016:075', '68', 16, 75, 'Allah membuat perumpamaan dengan seorang hamba sahaya yang dimiliki yang tidak dapat bertindak terhadap sesuatupun dan seorang yang Kami beri rezeki yang baik dari Kami, lalu dia menafkahkan sebagian dari rezeki itu secara sembunyi dan secara terang-terangan, adakah mereka itu sama? Segala puji hanya bagi Allah, tetapi kebanyakan mereka tiada mengetahui.'),
(1977, '016:076', '68', 16, 76, 'Dan Allah membuat (pula) perumpamaan: dua orang lelaki yang seorang bisu, tidak dapat berbuat sesuatupun dan dia menjadi beban atas penanggungnya, ke mana saja dia disuruh oleh penanggungnya itu, dia tidak dapat mendatangkan suatu kebajikanpun. Samakah orang itu dengan orang yang menyuruh berbuat keadilan, dan dia berada pula di atas jalan yang lurus?'),
(1978, '016:077', '68', 16, 77, 'Dan kepunyaan Allah-lah segala apa yang tersembunyi di langit dan di bumi. Tidak adalah kejadian kiamat itu, melainkan seperti sekejap mata atau lebih cepat (lagi). Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(1979, '016:078', '68', 16, 78, 'Dan Allah mengeluarkan kamu dari perut ibumu dalam keadaan tidak mengetahui sesuatupun, dan Dia memberi kamu pendengaran, penglihatan dan hati, agar kamu bersyukur.'),
(1980, '016:079', '68', 16, 79, 'Tidakkah mereka memperhatikan burung-burung yang dimudahkan terbang diangkasa bebas. Tidak ada yang menahannya selain daripada Allah. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Tuhan) bagi orang-orang yang beriman.'),
(1981, '016:080', '68', 16, 80, 'Dan Allah menjadikan bagimu rumah-rumahmu sebagai tempat tinggal dan Dia menjadikan bagi kamu rumah-rumah (kemah-kemah) dari kulit binatang ternak yang kamu merasa ringan (membawa)nya di waktu kamu berjalan dan waktu kamu bermukim dan (dijadikan-Nya pula) dari bulu domba, bulu unta dan bulu kambing, alat-alat rumah tangga dan perhiasan (yang kamu pakai) sampai waktu (tertentu).'),
(1982, '016:081', '68', 16, 81, 'Dan Allah menjadikan bagimu tempat bernaung dari apa yang telah Dia ciptakan, dan Dia jadikan bagimu tempat-tempat tinggal di gunung-gunung, dan Dia jadikan bagimu pakaian yang memeliharamu dari panas dan pakaian (baju besi) yang memelihara kamu dalam peperangan. Demikianlah Allah menyempurnakan nikmat-Nya atasmu agar kamu berserah diri (kepada-Nya).'),
(1983, '016:082', '68', 16, 82, 'Jika mereka tetap berpaling, maka sesungguhnya kewajiban yang dibebankan atasmu (Muhammad) hanyalah menyampaikan (amanat Allah) dengan terang.'),
(1984, '016:083', '68', 16, 83, 'Mereka mengetahui nikmat Allah, kemudian mereka mengingkarinya dan kebanyakan mereka adalah orang-orang yang kafir.'),
(1985, '016:084', '68', 16, 84, 'Dan (ingatlah) akan hari (ketika) Kami bangkitkan dari tiap-tiap umat seorang saksi (rasul), kemudian tidak diizinkan kepada orang-orang yang kafir (untuk membela diri) dan tidak (pula) mereka dibolehkan meminta maaf.'),
(1986, '016:085', '68', 16, 85, 'Dan apabila orang-orang zalim telah menyaksikan azab, maka tidaklah diringankan azab bagi mereka dan tidak puIa mereka diberi tangguh.'),
(1987, '016:086', '68', 16, 86, 'Dan apabila orang-orang yang mempersekutukan (Allah) melihat sekutu-sekutu mereka, mereka berkata: \"Ya Tuhan kami mereka inilah sekutu-sekutu kami yang dahulu kami sembah selain dari Engkau\". Lalu sekutu-sekutu mereka mengatakan kepada mereka: \"Sesungguhnya kamu benar-benar orang-orang yang dusta\".'),
(1988, '016:087', '68', 16, 87, 'Dan mereka menyatakan ketundukannya kepada Allah pada hari itu dan hilanglah dari mereka apa yang selalu mereka ada-adakan.'),
(1989, '016:088', '68', 16, 88, 'Orang-orang yang kafir dan menghalangi (manusia) dari jalan Allah, Kami tambahkan kepada mereka siksaan di atas siksaan disebabkan mereka selalu berbuat kerusakan.'),
(1990, '016:089', '68', 16, 89, '(Dan ingatlah) akan hari (ketika) Kami bangkitkan pada tiap-tiap umat seorang saksi atas mereka dari mereka sendiri dan Kami datangkan kamu (Muhammad) menjadi saksi atas seluruh umat manusia. Dan Kami turunkan kepadamu Al Kitab (Al Quran) untuk menjelaskan segala sesuatu dan petunjuk serta rahmat dan kabar gembira bagi orang-orang yang berserah diri.'),
(1991, '016:090', '68', 16, 90, 'Sesungguhnya Allah menyuruh (kamu) berlaku adil dan berbuat kebajikan, memberi kepada kaum kerabat, dan Allah melarang dari perbuatan keji, kemungkaran dan permusuhan. Dia memberi pengajaran kepadamu agar kamu dapat mengambil pelajaran.'),
(1992, '016:091', '68', 16, 91, 'Dan tepatilah perjanjian dengan Allah apabila kamu berjanji dan janganlah kamu membatalkan sumpah-sumpah(mu) itu, sesudah meneguhkannya, sedang kamu telah menjadikan Allah sebagai saksimu (terhadap sumpah-sumpahmu itu). Sesungguhnya Allah mengetahui apa yang kamu perbuat.'),
(1993, '016:092', '68', 16, 92, 'Dan janganlah kamu seperti seorang perempuan yang menguraikan benangnya yang sudah dipintal dengan kuat, menjadi cerai berai kembali, kamu menjadikan sumpah (perjanjian)mu sebagai alat penipu di antaramu, disebabkan adanya satu golongan yang lebih banyak jumlahnya dari golongan yang lain. Sesungguhnya Allah hanya menguji kamu dengan hal itu. Dan sesungguhnya di hari kiamat akan dijelaskan-Nya kepadamu apa yang dahulu kamu perselisihkan itu.'),
(1994, '016:093', '68', 16, 93, 'Dan kalau Allah menghendaki, niscaya Dia menjadikan kamu satu umat (saja), tetapi Allah menyesatkan siapa yang dikehendaki-Nya dan memberi petunjuk kepada siapa yang dikehendaki-Nya. Dan sesungguhnya kamu akan ditanya tentang apa yang telah kamu kerjakan.'),
(1995, '016:094', '68', 16, 94, 'Dan janganlah kamu jadikan sumpah-sumpahmu sebagai alat penipu di antaramu, yang menyebabkan tergelincir kaki(mu) sesudah kokoh tegaknya, dan kamu rasakan kemelaratan (di dunia) karena kamu menghalangi (manusia) dari jalan Allah; dan bagimu azab yang besar.'),
(1996, '016:095', '68', 16, 95, 'Dan janganlah kamu tukar perjanjianmu dengan Allah dengan harga yang sedikit (murah), sesungguhnya apa yang ada di sisi Allah, itulah yang lebih baik bagimu jika kamu mengetahui.'),
(1997, '016:096', '68', 16, 96, 'Apa yang di sisimu akan lenyap, dan apa yang ada di sisi Allah adalah kekal. Dan sesungguhnya Kami akan memberi balasan kepada orang-orang yang sabar dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.'),
(1998, '016:097', '68', 16, 97, 'Barangsiapa yang mengerjakan amal saleh, baik laki-laki maupun perempuan dalam keadaan beriman, maka sesungguhnya akan Kami berikan kepadanya kehidupan yang baik dan sesungguhnya akan Kami beri balasan kepada mereka dengan pahala yang lebih baik dari apa yang telah mereka kerjakan.'),
(1999, '016:098', '68', 16, 98, 'Apabila kamu membaca Al Quran hendaklah kamu meminta perlindungan kepada Allah dari syaitan yang terkutuk.'),
(2000, '016:099', '68', 16, 99, 'Sesungguhnya syaitan itu tidak ada kekuasaannya atas orang-orang yang beriman dan bertawakkal kepada Tuhannya.'),
(2001, '016:100', '68', 16, 100, 'Sesungguhnya kekuasaannya (syaitan) hanyalah atas orang-orang yang mengambilnya jadi pemimpin dan atas orang-orang yang mempersekutukannya dengan Allah.'),
(2002, '016:101', '68', 16, 101, 'Dan apabila Kami letakkan suatu ayat di tempat ayat yang lain sebagai penggantinya padahal Allah lebih mengetahui apa yang diturunkan-Nya, mereka berkata: \"Sesungguhnya kamu adalah orang yang mengada-adakan saja\". Bahkan kebanyakan mereka tiada mengetahui.'),
(2003, '016:102', '68', 16, 102, 'Katakanlah: \"Ruhul Qudus (Jibril) menurunkan Al Quran itu dari Tuhanmu dengan benar, untuk meneguhkan (hati) orang-orang yang telah beriman, dan menjadi petunjuk serta kabar gembira bagi orang-orang yang berserah diri (kepada Allah)\".'),
(2004, '016:103', '68', 16, 103, 'Dan sesungguhnya Kami mengetahui bahwa mereka berkata: \"Sesungguhnya Al Quran itu diajarkan oleh seorang manusia kepadanya (Muhammad)\". Padahal bahasa orang yang mereka tuduhkan (bahwa) Muhammad belajar kepadanya bahasa ´Ajam, sedang Al Quran adalah dalam bahasa Arab yang terang.'),
(2005, '016:104', '68', 16, 104, 'Sesungguhnya orang-orang yang tidak beriman kepada ayat-ayat Allah (Al Quran), Allah tidak akan memberi petunjuk kepada mereka dan bagi mereka azab yang pedih.'),
(2006, '016:105', '68', 16, 105, 'Sesungguhnya yang mengada-adakan kebohongan, hanyalah orang-orang yang tidak beriman kepada ayat-ayat Allah, dan mereka itulah orang-orang pendusta.'),
(2007, '016:106', '68', 16, 106, 'Barangsiapa yang kafir kepada Allah sesudah dia beriman (dia mendapat kemurkaan Allah), kecuali orang yang dipaksa kafir padahal hatinya tetap tenang dalam beriman (dia tidak berdosa), akan tetapi orang yang melapangkan dadanya untuk kekafiran, maka kemurkaan Allah menimpanya dan baginya azab yang besar.'),
(2008, '016:107', '68', 16, 107, 'Yang demikian itu disebabkan karena sesungguhnya mereka mencintai kehidupan di dunia lebih dari akhirat, dan bahwasanya Allah tiada memberi petunjuk kepada kaum yang kafir.'),
(2009, '016:108', '68', 16, 108, 'Mereka itulah orang-orang yang hati, pendengaran dan penglihatannya telah dikunci mati oleh Allah, dan mereka itulah orang-orang yang lalai.'),
(2010, '016:109', '68', 16, 109, 'Pastilah bahwa mereka di akhirat nanti adalah orang-orang yang merugi.'),
(2011, '016:110', '68', 16, 110, 'Dan sesungguhnya Tuhanmu (pelindung) bagi orang-orang yang berhijrah sesudah menderita cobaan, kemudian mereka berjihad dan sabar; sesungguhnya Tuhanmu sesudah itu benar-benar Maha Pengampun lagi Maha Penyayang.'),
(2012, '016:111', '68', 16, 111, '(Ingatlah) suatu hari (ketika) tiap-tiap diri datang untuk membela dirinya sendiri dan bagi tiap-tiap diri disempurnakan (balasan) apa yang telah dikerjakannya, sedangkan mereka tidak dianiaya (dirugikan).'),
(2013, '016:112', '68', 16, 112, 'Dan Allah telah membuat suatu perumpamaan (dengan) sebuah negeri yang dahulunya aman lagi tenteram, rezekinya datang kepadanya melimpah ruah dari segenap tempat, tetapi (penduduk)nya mengingkari nikmat-nikmat Allah; karena itu Allah merasakan kepada mereka pakaian kelaparan dan ketakutan, disebabkan apa yang selalu mereka perbuat.'),
(2014, '016:113', '68', 16, 113, 'Dan sesungguhnya telah datang kepada mereka seorang rasul dari mereka sendiri, tetapi mereka mendustakannya; karena itu mereka dimusnahkan azab dan mereka adalah orang-orang yang zalim.'),
(2015, '016:114', '68', 16, 114, 'Maka makanlah yang halal lagi baik dari rezeki yang telah diberikan Allah kepadamu; dan syukurilah nikmat Allah, jika kamu hanya kepada-Nya saja menyembah.'),
(2016, '016:115', '68', 16, 115, 'Sesungguhnya Allah hanya mengharamkan atasmu (memakan) bangkai, darah, daging babi dan apa yang disembelih dengan menyebut nama selain Allah; tetapi barangsiapa yang terpaksa memakannya dengan tidak menganiaya dan tidak pula melampaui batas, maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(2017, '016:116', '68', 16, 116, 'Dan janganlah kamu mengatakan terhadap apa yang disebut-sebut oleh lidahmu secara dusta \"ini halal dan ini haram\", untuk mengada-adakan kebohongan terhadap Allah. Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tiadalah beruntung.'),
(2018, '016:117', '68', 16, 117, '(Itu adalah) kesenangan yang sedikit, dan bagi mereka azab yang pedih.'),
(2019, '016:118', '68', 16, 118, 'Dan terhadap orang-orang Yahudi, Kami haramkan apa yang telah Kami ceritakan dahulu kepadamu; dan Kami tiada menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.'),
(2020, '016:119', '68', 16, 119, 'Kemudian, sesungguhnya Tuhanmu (mengampuni) bagi orang-orang yang mengerjakan kesalahan karena kebodohannya, kemudian mereka bertaubat sesudah itu dan memperbaiki (dirinya), sesungguhnya Tuhanmu sesudah itu benar-benar Maha Pengampun lagi Maha Penyayang.'),
(2021, '016:120', '68', 16, 120, 'Sesungguhnya Ibrahim adalah seorang imam yang dapat dijadikan teladan lagi patuh kepada Allah dan hanif. Dan sekali-kali bukanlah dia termasuk orang-orang yang mempersekutukan (Tuhan),'),
(2022, '016:121', '68', 16, 121, '(lagi) yang mensyukuri nikmat-nikmat Allah. Allah telah memilihnya dan menunjukinya kepada jalan yang lurus.'),
(2023, '016:122', '68', 16, 122, 'Dan Kami berikan kepadanya kebaikan di dunia. Dan sesungguhnya dia di akhirat benar-benar termasuk orang-orang yang saleh.'),
(2024, '016:123', '68', 16, 123, 'Kemudian Kami wahyukan kepadamu (Muhammad): \"Ikutilah agama Ibrahim seorang yang hanif\" dan bukanlah dia termasuk orang-orang yang mempersekutukan Tuhan.'),
(2025, '016:124', '68', 16, 124, 'Sesungguhnya diwajibkan (menghormati) hari Sabtu atas orang-orang (Yahudi) yang berselisih padanya. Dan sesungguhnya Tuhanmu benar-benar akan memberi putusan di antara mereka di hari kiamat terhadap apa yang telah mereka perselisihkan itu.'),
(2026, '016:125', '68', 16, 125, 'Serulah (manusia) kepada jalan Tuhan-mu dengan hikmah dan pelajaran yang baik dan bantahlah mereka dengan cara yang baik. Sesungguhnya Tuhanmu Dialah yang lebih mengetahui tentang siapa yang tersesat dari jalan-Nya dan Dialah yang lebih mengetahui orang-orang yang mendapat petunjuk.'),
(2027, '016:126', '68', 16, 126, 'Dan jika kamu memberikan balasan, maka balaslah dengan balasan yang sama dengan siksaan yang ditimpakan kepadamu. Akan tetapi jika kamu bersabar, sesungguhnya itulah yang lebih baik bagi orang-orang yang sabar.'),
(2028, '016:127', '68', 16, 127, 'Bersabarlah (hai Muhammad) dan tiadalah kesabaranmu itu melainkan dengan pertolongan Allah dan janganlah kamu bersedih hati terhadap (kekafiran) mereka dan janganlah kamu bersempit dada terhadap apa yang mereka tipu dayakan.'),
(2029, '016:128', '68', 16, 128, 'Sesungguhnya Allah beserta orang-orang yang bertakwa dan orang-orang yang berbuat kebaikan.'),
(2030, '017:001', '68', 17, 1, 'Maha Suci Allah, yang telah memperjalankan hamba-Nya pada suatu malam dari Al Masjidil Haram ke Al Masjidil Aqsha yang telah Kami berkahi sekelilingnya agar Kami perlihatkan kepadanya sebagian dari tanda-tanda (kebesaran) Kami. Sesungguhnya Dia adalah Maha Mendengar lagi Maha Mengetahui.'),
(2031, '017:002', '68', 17, 2, 'Dan Kami berikan kepada Musa kitab (Taurat) dan Kami jadikan kitab Taurat itu petunjuk bagi Bani Israil (dengan firman): \"Janganlah kamu mengambil penolong selain Aku,'),
(2032, '017:003', '68', 17, 3, '(yaitu) anak cucu dari orang-orang yang Kami bawa bersama-sama Nuh. Sesungguhnya dia adalah hamba (Allah) yang banyak bersyukur.'),
(2033, '017:004', '68', 17, 4, 'Dan telah Kami tetapkan terhadap Bani Israil dalam Kitab itu: \"Sesungguhnya kamu akan membuat kerusakan di muka bumi ini dua kali dan pasti kamu akan menyombongkan diri dengan kesombongan yang besar\".'),
(2034, '017:005', '68', 17, 5, 'Maka apabila datang saat hukuman bagi (kejahatan) pertama dari kedua (kejahatan) itu, Kami datangkan kepadamu hamba-hamba Kami yang mempunyai kekuatan yang besar, lalu mereka merajalela di kampung-kampung, dan itulah ketetapan yang pasti terlaksana.'),
(2035, '017:006', '68', 17, 6, 'Kemudian Kami berikan kepadamu giliran untuk mengalahkan mereka kembali dan Kami membantumu dengan harta kekayaan dan anak-anak dan Kami jadikan kamu kelompok yang lebih besar.'),
(2036, '017:007', '68', 17, 7, 'Jika kamu berbuat baik (berarti) kamu berbuat baik bagi dirimu sendiri dan jika kamu berbuat jahat, maka (kejahatan) itu bagi dirimu sendiri, dan apabila datang saat hukuman bagi (kejahatan) yang kedua, (Kami datangkan orang-orang lain) untuk menyuramkan muka-muka kamu dan mereka masuk ke dalam mesjid, sebagaimana musuh-musuhmu memasukinya pada kali pertama dan untuk membinasakan sehabis-habisnya apa saja yang mereka kuasai.'),
(2037, '017:008', '68', 17, 8, 'Mudah-mudahan Tuhanmu akan melimpahkan rahmat(Nya) kepadamu; dan sekiranya kamu kembali kepada (kedurhakaan) niscaya Kami kembali (mengazabmu) dan Kami jadikan neraka Jahannam penjara bagi orang-orang yang tidak beriman.'),
(2038, '017:009', '68', 17, 9, 'Sesungguhnya Al Quran ini memberikan petunjuk kepada (jalan) yang lebih lurus dan memberi khabar gembira kepada orang-orang Mu´min yang mengerjakan amal saleh bahwa bagi mereka ada pahala yang besar,'),
(2039, '017:010', '68', 17, 10, 'dan sesungguhnya orang-orang yang tidak beriman kepada kehidupan akhirat, Kami sediakan bagi mereka azab yang pedih.'),
(2040, '017:011', '68', 17, 11, 'Dan manusia mendoa untuk kejahatan sebagaimana ia mendoa untuk kebaikan. Dan adalah manusia bersifat tergesa-gesa.'),
(2041, '017:012', '68', 17, 12, 'Dan Kami jadikan malam dan siang sebagai dua tanda, lalu Kami hapuskan tanda malam dan Kami jadikan tanda siang itu terang, agar kamu mencari kurnia dari Tuhanmu, dan supaya kamu mengetahui bilangan tahun-tahun dan perhitungan. Dan segala sesuatu telah Kami terangkan dengan jelas.'),
(2042, '017:013', '68', 17, 13, 'Dan tiap-tiap manusia itu telah Kami tetapkan amal perbuatannya (sebagaimana tetapnya kalung) pada lehernya. Dan Kami keluarkan baginya pada hari kiamat sebuah kitab yang dijumpainya terbuka.'),
(2043, '017:014', '68', 17, 14, '\"Bacalah kitabmu, cukuplah dirimu sendiri pada waktu ini sebagai penghisab terhadapmu\".'),
(2044, '017:015', '68', 17, 15, 'Barangsiapa yang berbuat sesuai dengan hidayah (Allah), maka sesungguhnya dia berbuat itu untuk (keselamatan) dirinya sendiri; dan barangsiapa yang sesat maka sesungguhnya dia tersesat bagi (kerugian) dirinya sendiri. Dan seorang yang berdosa tidak dapat memikul dosa orang lain, dan Kami tidak akan mengazab sebelum Kami mengutus seorang rasul.'),
(2045, '017:016', '68', 17, 16, 'Dan jika Kami hendak membinasakan suatu negeri, maka Kami perintahkan kepada orang-orang yang hidup mewah di negeri itu (supaya mentaati Allah) tetapi mereka melakukan kedurhakaan dalam negeri itu, maka sudah sepantasnya berlaku terhadapnya perkataan (ketentuan Kami), kemudian Kami hancurkan negeri itu sehancur-hancurnya.'),
(2046, '017:017', '68', 17, 17, 'Dan berapa banyaknya kaum sesudah Nuh telah Kami binasakan. Dan cukuplah Tuhanmu Maha Mengetahui lagi Maha Melihat dosa hamba-hamba-Nya.'),
(2047, '017:018', '68', 17, 18, 'Barangsiapa menghendaki kehidupan sekarang (duniawi), maka Kami segerakan baginya di dunia itu apa yang kami kehendaki bagi orang yang kami kehendaki dan Kami tentukan baginya neraka jahannam; ia akan memasukinya dalam keadaan tercela dan terusir.'),
(2048, '017:019', '68', 17, 19, 'Dan barangsiapa yang menghendaki kehidupan akhirat dan berusaha ke arah itu dengan sungguh-sungguh sedang ia adalah mukmin, maka mereka itu adalah orang-orang yang usahanya dibalasi dengan baik.'),
(2049, '017:020', '68', 17, 20, 'Kepada masing-masing golongan baik golongan ini maupun golongan itu Kami berikan bantuan dari kemurahan Tuhanmu. Dan kemurahan Tuhanmu tidak dapat dihalangi.'),
(2050, '017:021', '68', 17, 21, 'Perhatikanlah bagaimana Kami lebihkan sebagian dari mereka atas sebagian (yang lain). Dan pasti kehidupan akhirat lebih tinggi tingkatnya dan lebih besar keutamaannya.'),
(2051, '017:022', '68', 17, 22, 'Janganlah kamu adakan tuhan yang lain di samping Allah, agar kamu tidak menjadi tercela dan tidak ditinggalkan (Allah).'),
(2052, '017:023', '68', 17, 23, 'Dan Tuhanmu telah memerintahkan supaya kamu jangan menyembah selain Dia dan hendaklah kamu berbuat baik pada ibu bapakmu dengan sebaik-baiknya. Jika salah seorang di antara keduanya atau kedua-duanya sampai berumur lanjut dalam pemeliharaanmu, maka sekali-kali janganlah kamu mengatakan kepada keduanya perkataan \"ah\" dan janganlah kamu membentak mereka dan ucapkanlah kepada mereka perkataan yang mulia.'),
(2053, '017:024', '68', 17, 24, 'Dan rendahkanlah dirimu terhadap mereka berdua dengan penuh kesayangan dan ucapkanlah: \"Wahai Tuhanku, kasihilah mereka keduanya, sebagaimana mereka berdua telah mendidik aku waktu kecil\".'),
(2054, '017:025', '68', 17, 25, 'Tuhanmu lebih mengetahui apa yang ada dalam hatimu; jika kamu orang-orang yang baik, maka sesungguhnya Dia Maha Pengampun bagi orang-orang yang bertaubat.'),
(2055, '017:026', '68', 17, 26, 'Dan berikanlah kepada keluarga-keluarga yang dekat akan haknya, kepada orang miskin dan orang yang dalam perjalanan dan janganlah kamu menghambur-hamburkan (hartamu) secara boros.'),
(2056, '017:027', '68', 17, 27, 'Sesungguhnya pemboros-pemboros itu adalah saudara-saudara syaitan dan syaitan itu adalah sangat ingkar kepada Tuhannya.'),
(2057, '017:028', '68', 17, 28, 'Dan jika kamu berpaling dari mereka untuk memperoleh rahmat dari Tuhanmu yang kamu harapkan, maka katakanlah kepada mereka ucapan yang pantas.'),
(2058, '017:029', '68', 17, 29, 'Dan janganlah kamu jadikan tanganmu terbelenggu pada lehermu dan janganlah kamu terlalu mengulurkannya karena itu kamu menjadi tercela dan menyesal.'),
(2059, '017:030', '68', 17, 30, 'Sesungguhnya Tuhanmu melapangkan rezeki kepada siapa yang Dia kehendaki dan menyempitkannya; sesungguhnya Dia Maha Mengetahui lagi Maha Melihat akan hamba-hamba-Nya.'),
(2060, '017:031', '68', 17, 31, 'Dan janganlah kamu membunuh anak-anakmu karena takut kemiskinan. Kamilah yang akan memberi rezeki kepada mereka dan juga kepadamu. Sesungguhnya membunuh mereka adalah suatu dosa yang besar.'),
(2061, '017:032', '68', 17, 32, 'Dan janganlah kamu mendekati zina; sesungguhnya zina itu adalah suatu perbuatan yang keji. Dan suatu jalan yang buruk.'),
(2062, '017:033', '68', 17, 33, 'Dan janganlah kamu membunuh jiwa yang diharamkan Allah (membunuhnya), melainkan dengan suatu (alasan) yang benar. Dan barangsiapa dibunuh secara zalim, maka sesungguhnya Kami telah memberi kekuasaan kepada ahli warisnya, tetapi janganlah ahli waris itu melampaui batas dalam membunuh. Sesungguhnya ia adalah orang yang mendapat pertolongan.'),
(2063, '017:034', '68', 17, 34, 'Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih baik (bermanfaat) sampai ia dewasa dan penuhilah janji; sesungguhnya janji itu pasti diminta pertanggungan jawabnya.'),
(2064, '017:035', '68', 17, 35, 'Dan sempurnakanlah takaran apabila kamu menakar, dan timbanglah dengan neraca yang benar. Itulah yang lebih utama (bagimu) dan lebih baik akibatnya.'),
(2065, '017:036', '68', 17, 36, 'Dan janganlah kamu mengikuti apa yang kamu tidak mempunyai pengetahuan tentangnya. Sesungguhnya pendengaran, penglihatan dan hati, semuanya itu akan diminta pertanggungan jawabnya.'),
(2066, '017:037', '68', 17, 37, 'Dan janganlah kamu berjalan di muka bumi ini dengan sombong, karena sesungguhnya kamu sekali-kali tidak dapat menembus bumi dan sekali-kali kamu tidak akan sampai setinggi gunung.'),
(2067, '017:038', '68', 17, 38, 'Semua itu kejahatannya amat dibenci di sisi Tuhanmu.'),
(2068, '017:039', '68', 17, 39, 'Itulah sebagian hikmah yang diwahyukan Tuhanmu kepadamu. Dan janganlah kamu mengadakan tuhan yang lain di samping Allah, yang menyebabkan kamu dilemparkan ke dalam neraka dalam keadaan tercela lagi dijauhkan (dari rahmat Allah).'),
(2069, '017:040', '68', 17, 40, 'Maka apakah patut Tuhan memilihkan bagimu anak-anak laki-laki sedang Dia sendiri mengambil anak-anak perempuan di antara para malaikat? Sesungguhnya kamu benar-benar mengucapkan kata-kata yang besar (dosanya).'),
(2070, '017:041', '68', 17, 41, 'Dan sesungguhnya dalam Al Quran ini Kami telah ulang-ulangi (peringatan-peringatan), agar mereka selalu ingat. Dan ulangan peringatan itu tidak lain hanyalah menambah mereka lari (dari kebenaran).'),
(2071, '017:042', '68', 17, 42, 'Katakanlah: \"Jikalau ada tuhan-tuhan di samping-Nya, sebagaimana yang mereka katakan, niscaya tuhan-tuhan itu mencari jalan kepada Tuhan yang mempunyai ´Arsy\".'),
(2072, '017:043', '68', 17, 43, 'Maha Suci dan Maha Tinggi Dia dari apa yang mereka katakan dengan ketinggian yang sebesar-besarnya.'),
(2073, '017:044', '68', 17, 44, 'Langit yang tujuh, bumi dan semua yang ada di dalamnya bertasbih kepada Allah. Dan tak ada suatupun melainkan bertasbih dengan memuji-Nya, tetapi kamu sekalian tidak mengerti tasbih mereka. Sesungguhnya Dia adalah Maha Penyantun lagi Maha Pengampun.'),
(2074, '017:045', '68', 17, 45, 'Dan apabila kamu membaca Al Quran niscaya Kami adakan antara kamu dan orang-orang yang tidak beriman kepada kehidupan akhirat, suatu dinding yang tertutup,'),
(2075, '017:046', '68', 17, 46, 'dan Kami adakan tutupan di atas hati mereka dan sumbatan di telinga mereka, agar mereka tidak dapat memahaminya. Dan apabila kamu menyebut Tuhanmu saja dalam Al Quran, niscaya mereka berpaling ke belakang karena bencinya,');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(2076, '017:047', '68', 17, 47, 'Kami lebih mengetahui dalam keadaan bagaimana mereka mendengarkan sewaktu mereka mendengarkan kamu, dan sewaktu mereka berbisik-bisik (yaitu) ketika orang-orang zalim itu berkata: \"Kamu tidak lain hanyalah mengikuti seorang laki-laki yang kena sihir\".'),
(2077, '017:048', '68', 17, 48, 'Lihatlah bagaimana mereka membuat perumpamaan-perumpamaan terhadapmu; karena itu mereka menjadi sesat dan tidak dapat lagi menemukan jalan (yang benar).'),
(2078, '017:049', '68', 17, 49, 'Dan mereka berkata: \"Apakah bila kami telah menjadi tulang belulang dan benda-benda yang hancur, apa benar-benarkah kami akan dibangkitkan kembali sebagai makhluk yang baru?\"'),
(2079, '017:050', '68', 17, 50, 'Katakanlah: \"Jadilah kamu sekalian batu atau besi,'),
(2080, '017:051', '68', 17, 51, 'atau suatu makhluk dari makhluk yang tidak mungkin (hidup) menurut pikiranmu\". Maka mereka akan bertanya: \"Siapa yang akan menghidupkan kami kembali?\" Katakanlah: \"Yang telah menciptakan kamu pada kali yang pertama\". Lalu mereka akan menggeleng-gelengkan kepala mereka kepadamu dan berkata: \"Kapan itu (akan terjadi)?\" Katakanlah: \"Mudah-mudahan waktu berbangkit itu dekat\",'),
(2081, '017:052', '68', 17, 52, 'yaitu pada hari Dia memanggil kamu, lalu kamu mematuhi-Nya sambil memuji-Nya dan kamu mengira, bahwa kamu tidak berdiam (di dalam kubur) kecuali sebentar saja.'),
(2082, '017:053', '68', 17, 53, 'Dan katakanlah kepada hamha-hamba-Ku: \"Hendaklah mereka mengucapkan perkataan yang lebih baik (benar). Sesungguhnya syaitan itu menimbulkan perselisihan di antara mereka. Sesungguhnya syaitan itu adalah musuh yang nyata bagi manusia.'),
(2083, '017:054', '68', 17, 54, 'Tuhanmu lebih mengetahui tentang kamu. Dia akan memberi rahmat kepadamu jika Dia menghendaki dan Dia akan mengazabmu, jika Dia menghendaki. Dan, Kami tidaklah mengutusmu untuk menjadi penjaga bagi mereka.'),
(2084, '017:055', '68', 17, 55, 'Dan Tuhan-mu lebih mengetahui siapa yang (ada) di langit dan di bumi. Dan sesungguhnya telah Kami lebihkan sebagian nabi-nabi itu atas sebagian (yang lain), dan Kami berikan Zabur kepada Daud.'),
(2085, '017:056', '68', 17, 56, 'Katakanlah: \"Panggillah mereka yang kamu anggap (tuhan) selain Allah, maka mereka tidak akan mempunyai kekuasaan untuk menghilangkan bahaya daripadamu dan tidak pula memindahkannya\".'),
(2086, '017:057', '68', 17, 57, 'Orang-orang yang mereka seru itu, mereka sendiri mencari jalan kepada Tuhan mereka siapa di antara mereka yang lebih dekat (kepada Allah) dan mengharapkan rahmat-Nya dan takut akan azab-Nya; sesungguhnya azab Tuhanmu adalah suatu yang (harus) ditakuti.'),
(2087, '017:058', '68', 17, 58, 'Tak ada suatu negeripun (yang durhaka penduduknya), melainkan Kami membinasakannya sebelum hari kiamat atau Kami azab (penduduknya) dengan azab yang sangat keras. Yang demikian itu telah tertulis di dalam kitab (Lauh Mahfuzh).'),
(2088, '017:059', '68', 17, 59, 'Dan sekali-kali tidak ada yang menghalangi Kami untuk mengirimkan (kepadamu) tanda-tanda (kekuasan Kami), melainkan karena tanda-tanda itu telah didustakan oleh orang-orang dahulu. Dan telah Kami berikan kepada Tsamud unta betina itu (sebagai mukjizat) yang dapat dilihat, tetapi mereka menganiaya unta betina itu. Dan Kami tidak memberi tanda-tanda itu melainkan untuk menakuti.'),
(2089, '017:060', '68', 17, 60, 'Dan (ingatlah), ketika Kami wahyukan kepadamu: \"Sesungguhnya (ilmu) Tuhanmu meliputi segala manusia\". Dan Kami tidak menjadikan mimpi yang telah Kami perlihatkan kepadamu, melainkan sebagai ujian bagi manusia dan (begitu pula) pohon kayu yang terkutuk dalam Al Quran. Dan Kami menakut-nakuti mereka, tetapi yang demikian itu hanyalah menambah besar kedurhakaan mereka.'),
(2090, '017:061', '68', 17, 61, 'Dan (ingatlah), tatkala Kami berfirman kepada para malaikat: \"Sujudlah kamu semua kepada Adam\", lalu mereka sujud kecuali iblis. Dia berkata: \"Apakah aku akan sujud kepada orang yang Engkau ciptakan dari tanah?\"'),
(2091, '017:062', '68', 17, 62, 'Dia (iblis) berkata: \"Terangkanlah kepadaku inikah orangnya yang Engkau muliakan atas diriku? Sesungguhnya jika Engkau memberi tangguh kepadaku sampai hari kiamat, niscaya benar-benar akan aku sesatkan keturunannya, kecuali sebahagian kecil\".'),
(2092, '017:063', '68', 17, 63, 'Tuhan berfirman: \"Pergilah, barangsiapa di antara mereka yang mengikuti kamu, maka sesungguhnya neraka Jahannam adalah balasanmu semua, sebagai suatu pembalasan yang cukup.'),
(2093, '017:064', '68', 17, 64, 'Dan hasunglah siapa yang kamu sanggupi di antara mereka dengan ajakanmu, dan kerahkanlah terhadap mereka pasukan berkuda dan pasukanmu yang berjalan kaki dan berserikatlah dengan mereka pada harta dan anak-anak dan beri janjilah mereka. Dan tidak ada yang dijanjikan oleh syaitan kepada mereka melainkan tipuan belaka.'),
(2094, '017:065', '68', 17, 65, 'Sesungguhnya hamba-hamba-Ku, kamu tidak dapat berkuasa atas mereka. Dan cukuplah Tuhan-mu sebagai Penjaga\".'),
(2095, '017:066', '68', 17, 66, 'Tuhan-mu adalah yang melayarkan kapal-kapal di lautan untukmu, agar kamu mencari sebahagian dari karunia-Nya. Sesungguhnya Dia adalah Maha Penyayang terhadapmu.'),
(2096, '017:067', '68', 17, 67, 'Dan apabila kamu ditimpa bahaya di lautan, niscaya hilanglah siapa yang kamu seru kecuali Dia, Maka tatkala Dia menyelamatkan kamu ke daratan, kamu berpaling. Dan manusia itu adalah selalu tidak berterima kasih.'),
(2097, '017:068', '68', 17, 68, 'Maka apakah kamu merasa aman (dari hukuman Tuhan) yang menjungkir balikkan sebagian daratan bersama kamu atau Dia meniupkan (angin keras yang membawa) batu-batu kecil? dan kamu tidak akan mendapat seorang pelindungpun bagi kamu,'),
(2098, '017:069', '68', 17, 69, 'atau apakah kamu merasa aman dari dikembalikan-Nya kamu ke laut sekali lagi, lalu Dia meniupkan atas kamu angin taupan dan ditenggelamkan-Nya kamu disebabkan kekafiranmu. Dan kamu tidak akan mendapat seorang penolongpun dalam hal ini terhadap (siksaan) Kami.'),
(2099, '017:070', '68', 17, 70, 'Dan sesungguhnya telah Kami muliakan anak-anak Adam, Kami angkut mereka di daratan dan di lautan, Kami beri mereka rezeki dari yang baik-baik dan Kami lebihkan mereka dengan kelebihan yang sempurna atas kebanyakan makhluk yang telah Kami ciptakan.'),
(2100, '017:071', '68', 17, 71, '(Ingatlah) suatu hari (yang di hari itu) Kami panggil tiap umat dengan pemimpinnya; dan barangsiapa yang diberikan kitab amalannya di tangan kanannya maka mereka ini akan membaca kitabnya itu, dan mereka tidak dianiaya sedikitpun.'),
(2101, '017:072', '68', 17, 72, 'Dan barangsiapa yang buta (hatinya) di dunia ini, niscaya di akhirat (nanti) ia akan lebih buta (pula) dan lebih tersesat dari jalan (yang benar).'),
(2102, '017:073', '68', 17, 73, 'Dan sesungguhnya mereka hampir memalingkan kamu dari apa yang telah Kami wahyukan kepadamu, agar kamu membuat yang lain secara bohong terhadap Kami; dan kalau sudah begitu tentu|ah mereka mengambil kamu jadi sahabat yang setia.'),
(2103, '017:074', '68', 17, 74, 'Dan kalau Kami tidak memperkuat (hati)mu, niscaya kamu hampir-hampir condong sedikit kepada mereka,'),
(2104, '017:075', '68', 17, 75, 'kalau terjadi demikian, benar-benarlah Kami akan rasakan kepadamu (siksaan) berlipat ganda di dunia ini dan begitu (pula siksaan) berlipat ganda sesudah mati, dan kamu tidak akan mendapat seorang penolongpun terhadap Kami.'),
(2105, '017:076', '68', 17, 76, 'Dan sesungguhnya benar-benar mereka hampir membuatmu gelisah di negeri (Mekah) untuk mengusirmu daripadanya dan kalau terjadi demikian, niscaya sepeninggalmu mereka tidak tinggal, melainkan sebentar saja.'),
(2106, '017:077', '68', 17, 77, '(Kami menetapkan yang demikian) sebagai suatu ketetapan terhadap rasul-rasul Kami yang Kami utus sebelum kamu dan tidak akan kamu dapati perubahan bagi ketetapan Kami itu.'),
(2107, '017:078', '68', 17, 78, 'Dirikanlah shalat dari sesudah matahari tergelincir sampai gelap malam dan (dirikanlah pula shalat) subuh. Sesungguhnya shalat subuh itu disaksikan (oleh malaikat).'),
(2108, '017:079', '68', 17, 79, 'Dan pada sebahagian malam hari bersembahyang tahajudlah kamu sebagai suatu ibadah tambahan bagimu; mudah-mudahan Tuhan-mu mengangkat kamu ke tempat yang terpuji.'),
(2109, '017:080', '68', 17, 80, 'Dan katakanlah: \"Ya Tuhan-ku, masukkanlah aku secara masuk yang benar dan keluarkanlah (pula) aku secara keluar yang benar dan berikanlah kepadaku dari sisi Engkau kekuasaan yang menolong.'),
(2110, '017:081', '68', 17, 81, 'Dan katakanlah: \"Yang benar telah datang dan yang batil telah lenyap\". Sesungguhnya yang batil itu adalah sesuatu yang pasti lenyap.'),
(2111, '017:082', '68', 17, 82, 'Dan Kami turunkan dari Al Quran suatu yang menjadi penawar dan rahmat bagi orang-orang yang beriman dan Al Quran itu tidaklah menambah kepada orang-orang yang zalim selain kerugian.'),
(2112, '017:083', '68', 17, 83, 'Dan apabila Kami berikan kesenangan kepada manusia niscaya berpalinglah dia; dan membelakang dengan sikap yang sombong; dan apabila dia ditimpa kesusahan niscaya dia berputus asa.'),
(2113, '017:084', '68', 17, 84, 'Katakanlah: \"Tiap-tiap orang berbuat menurut keadaannya masing-masing\". Maka Tuhanmu lebih mengetahui siapa yang lebih benar jalannya.'),
(2114, '017:085', '68', 17, 85, 'Dan mereka bertanya kepadamu tentang roh. Katakanlah: \"Roh itu termasuk urusan Tuhan-ku, dan tidaklah kamu diberi pengetahuan melainkan sedikit\".'),
(2115, '017:086', '68', 17, 86, 'Dan sesungguhnya jika Kami menghendaki, niscaya Kami lenyapkan apa yang telah Kami wahyukan kepadamu, dan dengan pelenyapan itu, kamu tidak akan mendapatkan seorang pembelapun terhadap Kami,'),
(2116, '017:087', '68', 17, 87, 'kecuali karena rahmat dari Tuhanmu. Sesungguhnya karunia-Nya atasmu adalah besar.'),
(2117, '017:088', '68', 17, 88, 'Katakanlah: \"Sesungguhnya jika manusia dan jin berkumpul untuk membuat yang serupa Al Quran ini, niscaya mereka tidak akan dapat membuat yang serupa dengan dia, sekalipun sebagian mereka menjadi pembantu bagi sebagian yang lain\".'),
(2118, '017:089', '68', 17, 89, 'Dan sesungguhnya Kami telah mengulang-ulang kepada manusia dalam Al Quran ini tiap-tiap macam perumpamaan, tapi kebanyakan manusia tidak menyukai kecuali mengingkari(nya).'),
(2119, '017:090', '68', 17, 90, 'Dan mereka berkata: \"Kami sekali-kali tidak percaya kepadamu hingga kamu memancarkan mata air dan bumi untuk kami,'),
(2120, '017:091', '68', 17, 91, 'atau kamu mempunyai sebuah kebun korma dan anggur, lalu kamu alirkan sungai-sungai di celah kebun yang deras alirannya,'),
(2121, '017:092', '68', 17, 92, 'atau kamu jatuhkan langit berkeping-keping atas kami, sebagaimana kamu katakan atau kamu datangkan Allah dan malaikat-malaikat berhadapan muka dengan kami.'),
(2122, '017:093', '68', 17, 93, 'Atau kamu mempunyai sebuah rumah dari emas, atau kamu naik ke langit. Dan kami sekali-kali tidak akan mempercayai kenaikanmu itu hingga kamu turunkan atas kami sebuah kitab yang kami baca\". Katakanlah: \"Maha Suci Tuhanku, bukankah aku ini hanya seorang manusia yang menjadi rasul?\"'),
(2123, '017:094', '68', 17, 94, 'Dan tidak ada sesuatu yang menghalangi manusia untuk beriman tatkala datang petunjuk kepadanya, kecuali perkataan mereka: \"Adakah Allah mengutus seorang manusia menjadi rasuI?\"'),
(2124, '017:095', '68', 17, 95, 'Katakanlah: \"Kalau seandainya ada malaikat-malaikat yang berjalan-jalan sebagai penghuni di bumi, niscaya Kami turunkan dari langit kepada mereka seorang malaikat menjadi rasul\".'),
(2125, '017:096', '68', 17, 96, 'Katakanlah: \"Cukuplah Allah menjadi saksi antara aku dan kamu sekalian. Sesungguhnya Dia adalah Maha Mengetahui lagi Maha Melihat akan hamba-hamba-Nya\".'),
(2126, '017:097', '68', 17, 97, 'Dan barangsiapa yang ditunjuki Allah, dialah yang mendapat petunjuk dan barangsiapa yang Dia sesatkan maka sekali-kali kamu tidak akan mendapat penolong-penolong bagi mereka selain dari Dia. Dan Kami akan mengumpulkan mereka pada hari kiamat (diseret) atas muka mereka dalam keadaan buta, bisu dan pekak. Tempat kediaman mereka adalah neraka jahannam. Tiap-tiap kali nyala api Jahannam itu akan padam, Kami tambah lagi bagi mereka nyalanya.'),
(2127, '017:098', '68', 17, 98, 'Itulah balasan bagi mereka, karena sesungguhnya mereka kafir kepada ayat-ayat Kami dan (karena mereka) berkata: \"Apakah bila kami telah menjadi tulang belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk baru?\"'),
(2128, '017:099', '68', 17, 99, 'Dan apakah mereka tidak memperhatikan bahwasanya Allah yang menciptakan langit dan bumi adalah kuasa (pula) menciptakan yang serupa dengan mereka, dan telah menetapkan waktu yang tertentu bagi mereka yang tidak ada keraguan padanya? Maka orang-orang zalim itu tidak menghendaki kecuali kekafiran.'),
(2129, '017:100', '68', 17, 100, 'Katakanlah: \"Kalau seandainya kamu menguasai perbendaharaan-perbendaharaan rahmat Tuhanku, niscaya perbendaharaan itu kamu tahan, karena takut membelanjakannya\". Dan adalah manusia itu sangat kikir.'),
(2130, '017:101', '68', 17, 101, 'Dan sesungguhnya Kami telah memberikan kepada Musa sembilan buah mukjizat yang nyata, maka tanyakanlah kepada Bani Israil, tatkala Musa datang kepada mereka lalu Fir´aun berkata kepadanya: \"Sesungguhnya aku sangka kamu, hai Musa, seorang yang kena sihir\".'),
(2131, '017:102', '68', 17, 102, 'Musa menjawab: \"Sesungguhnya kamu telah mengetahui, bahwa tiada yang menurunkan mukjizat-mukjizat itu kecuali Tuhan Yang memelihara langit dan bumi sebagai bukti-bukti yang nyata; dan sesungguhnya aku mengira kamu, hai Fir´aun, seorang yang akan binasa\".'),
(2132, '017:103', '68', 17, 103, 'Kemudian (Fir´aun) hendak mengusir mereka (Musa dan pengikut-pengikutnya) dari bumi (Mesir) itu, maka Kami tenggelamkan dia (Fir´aun) serta orang-orang yang bersama-sama dia seluruhnya,'),
(2133, '017:104', '68', 17, 104, 'dan Kami berfirman sesudah itu kepada Bani Israil: \"Diamlah di negeri ini, maka apabila datang masa berbangkit, niscaya Kami datangkan kamu dalam keadaan bercampur baur (dengan musuhmu)\".'),
(2134, '017:105', '68', 17, 105, 'Dan Kami turunkan (Al Quran) itu dengan sebenar-benarnya dan Al Quran itu telah turun dengan (membawa) kebenaran. Dan Kami tidak mengutus kamu, melainkan sebagai pembawa berita gembira dan pemberi peringatan.'),
(2135, '017:106', '68', 17, 106, 'Dan Al Quran itu telah Kami turunkan dengan berangsur-angsur agar kamu membacakannya perlahan-lahan kepada manusia dan Kami menurunkannya bagian demi bagian.'),
(2136, '017:107', '68', 17, 107, 'Katakanlah: \"Berimanlah kamu kepadanya atau tidak usah beriman (sama saja bagi Allah). Sesungguhnya orang-orang yang diberi pengetahuan sebelumnya apabila Al Quran dibacakan kepada mereka, mereka menyungkur atas muka mereka sambil bersujud,'),
(2137, '017:108', '68', 17, 108, 'dan mereka berkata: \"Maha Suci Tuhan kami, sesungguhnya janji Tuhan kami pasti dipenuhi\".'),
(2138, '017:109', '68', 17, 109, 'Dan mereka menyungkur atas muka mereka sambil menangis dan mereka bertambah khusyu´.'),
(2139, '017:110', '68', 17, 110, 'Katakanlah: \"Serulah Allah atau serulah Ar-Rahman. Dengan nama yang mana saja kamu seru, Dia mempunyai al asmaaul husna (nama-nama yang terbaik) dan janganlah kamu mengeraskan suaramu dalam shalatmu dan janganlah pula merendahkannya dan carilah jalan tengah di antara kedua itu\".'),
(2140, '017:111', '68', 17, 111, 'Dan katakanlah: \"Segala puji bagi Allah Yang tidak mempunyai anak dan tidak mempunyai sekutu dalam kerajaan-Nya dan Dia bukan pula hina yang memerlukan penolong dan agungkanlah Dia dengan pengagungan yang sebesar-besarnya.'),
(2141, '018:001', '68', 18, 1, 'Segala puji bagi Allah yang telah menurunkan kepada hamba-Nya Al Kitab (Al-Quran) dan Dia tidak mengadakan kebengkokan di dalamnya;'),
(2142, '018:002', '68', 18, 2, 'sebagai bimbingan yang lurus, untuk memperingatkan siksaan yang sangat pedih dari sisi Allah dan memberi berita gembira kepada orang-orang yang beriman, yang mengerjakan amal saleh, bahwa mereka akan mendapat pembalasan yang baik,'),
(2143, '018:003', '68', 18, 3, 'mereka kekal di dalamnya untuk selama-lamanya.'),
(2144, '018:004', '68', 18, 4, 'Dan untuk memperingatkan kepada orang-orang yang berkata: \"Allah mengambil seorang anak\".'),
(2145, '018:005', '68', 18, 5, 'Mereka sekali-kali tidak mempunyai pengetahuan tentang hal itu, begitu pula nenek moyang mereka. Alangkah buruknya kata-kata yang keluar dari mulut mereka; mereka tidak mengatakan (sesuatu) kecuali dusta.'),
(2146, '018:006', '68', 18, 6, 'Maka (apakah) barangkali kamu akan membunuh dirimu karena bersedih hati setelah mereka berpaling, sekiranya mereka tidak beriman kepada keterangan ini (Al-Quran).'),
(2147, '018:007', '68', 18, 7, 'Sesungguhnya Kami telah menjadikan apa yang di bumi sebagai perhiasan baginya, agar Kami menguji mereka siapakah di antara mereka yang terbaik perbuatannya.'),
(2148, '018:008', '68', 18, 8, 'Dan sesungguhnya Kami benar-benar akan menjadikan (pula) apa yang di atasnya menjadi tanah rata lagi tandus.'),
(2149, '018:009', '68', 18, 9, 'Atau kamu mengira bahwa orang-orang yang mendiami gua dan (yang mempunyai) raqim itu, mereka termasuk tanda-tanda kekuasaan Kami yang mengherankan?'),
(2150, '018:010', '68', 18, 10, '(Ingatlah) tatkala para pemuda itu mencari tempat berlindung ke dalam gua, lalu mereka berdoa: \"Wahai Tuhan kami, berikanlah rahmat kepada kami dari sisi-Mu dan sempurnakanlah bagi kami petunjuk yang lurus dalam urusan kami (ini)\".'),
(2151, '018:011', '68', 18, 11, 'Maka Kami tutup telinga mereka beberapa tahun dalam gua itu,'),
(2152, '018:012', '68', 18, 12, 'Kemudian Kami bangunkan mereka, agar Kami mengetahui manakah di antara kedua golongan itu] yang lebih tepat dalam menghitung berapa lama mereka tinggal (dalam gua itu).'),
(2153, '018:013', '68', 18, 13, 'Kami kisahkan kepadamu (Muhammad) cerita ini dengan benar. Sesungguhnya mereka adalah pemuda-pemuda yang beriman kepada Tuhan mereka, dan Kami tambah pula untuk mereka petunjuk.'),
(2154, '018:014', '68', 18, 14, 'Dan Kami meneguhkan hati mereka diwaktu mereka berdiri, lalu mereka pun berkata, \"Tuhan kami adalah Tuhan seluruh langit dan bumi; kami sekali-kali tidak menyeru Tuhan selain Dia, sesungguhnya kami kalau demikian telah mengucapkan perkataan yang amat jauh dari kebenaran\".'),
(2155, '018:015', '68', 18, 15, 'Kaum kami ini telah menjadikan selain Dia sebagai tuhan-tuhan (untuk disembah). Mengapa mereka tidak mengemukakan alasan yang terang (tentang kepercayaan mereka)? Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kebohongan terhadap Allah?'),
(2156, '018:016', '68', 18, 16, 'Dan apabila kamu meninggalkan mereka dan apa yang mereka sembah selain Allah, maka carilah tempat berlindung ke dalam gua itu, niscaya Tuhanmu akan melimpahkan sebagian rahmat-Nya kepadamu dan menyediakan sesuatu yang berguna bagimu dalam urusan kamu.'),
(2157, '018:017', '68', 18, 17, 'Dan kamu akan melihat matahari ketika terbit, condong dari gua mereka ke sebelah kanan, dan bila matahari terbenam menjauhi mereka ke sebelah kiri sedang mereka berada dalam tempat yang luas dalam gua itu. Itu adalah sebagian dari tanda-tanda (kebesaran) Allah. Barangsiapa yang diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa yang disesatkan-Nya, maka kamu tidak akan mendapatkan seorang pemimpinpun yang dapat memberi petunjuk kepadanya.'),
(2158, '018:018', '68', 18, 18, 'Dan kamu mengira mereka itu bangun, padahal mereka tidur; Dan kami balik-balikkan mereka ke kanan dan ke kiri, sedang anjing mereka mengunjurkan kedua lengannya di muka pintu gua. Dan jika kamu menyaksikan mereka tentulah kamu akan berpaling dari mereka dengan melarikan diri dan tentulah (hati) kamu akan dipenuhi oleh ketakutan terhadap mereka.'),
(2159, '018:019', '68', 18, 19, 'Dan demikianlah Kami bangunkan mereka agar mereka saling bertanya di antara mereka sendiri. Berkatalah salah seorang di antara mereka: Sudah berapa lamakah kamu berada (disini?)\". Mereka menjawab: \"Kita berada (disini) sehari atau setengah hari\". Berkata (yang lain lagi): \"Tuhan kamu lebih mengetahui berapa lamanya kamu berada (di sini). Maka suruhlah salah seorang di antara kamu untuk pergi ke kota dengan membawa uang perakmu ini, dan hendaklah dia lihat manakah makanan yang lebih baik, maka hendaklah ia membawa makanan itu untukmu, dan hendaklah ia berlaku lemah-lembut dan janganlah sekali-kali menceritakan halmu kepada seorangpun.'),
(2160, '018:020', '68', 18, 20, 'Sesungguhnya jika mereka dapat mengetahui tempatmu, niscaya mereka akan melempar kamu dengan batu, atau memaksamu kembali kepada agama mereka, dan jika demikian niscaya kamu tidak akan beruntung selama lamanya\".'),
(2161, '018:021', '68', 18, 21, 'Dan demikian (pula) Kami mempertemukan (manusia) dengan mereka, agar manusia itu mengetahui, bahwa janji Allah itu benar, dan bahwa kedatangan hari kiamat tidak ada keraguan padanya. Ketika orang-orang itu berselisih tentang urusan mereka, orang-orang itu berkata: \"Dirikan sebuah bangunan di atas (gua) mereka, Tuhan mereka lebih mengetahui tentang mereka\". Orang-orang yang berkuasa atas urusan mereka berkata: \"Sesungguhnya kami akan mendirikan sebuah rumah peribadatan di atasnya\".'),
(2162, '018:022', '68', 18, 22, 'Nanti (ada orang yang akan) mengatakan (jumlah mereka) adalah tiga orang yang keempat adalah anjingnya, dan (yang lain) mengatakan: \"(jumlah mereka) adalah lima orang yang keenam adalah anjing nya\", sebagai terkaan terhadap barang yang gaib; dan (yang lain lagi) mengatakan: \"(jumlah mereka) tujuh orang, yang ke delapan adalah anjingnya\". Katakanlah: \"Tuhanku lebih mengetahui jumlah mereka; tidak ada orang yang mengetahui (bilangan) mereka kecuali sedikit\". Karena itu janganlah kamu (Muhammad) bertengkar tentang hal mereka, kecuali pertengkaran lahir saja dan jangan kamu menanyakan tentang mereka (pemuda-pemuda itu) kepada seorangpun di antara mereka.'),
(2163, '018:023', '68', 18, 23, 'Dan jangan sekali-kali kamu mengatakan tentang sesuatu: \"Sesungguhnya aku akan mengerjakan ini besok pagi,'),
(2164, '018:024', '68', 18, 24, 'kecuali (dengan menyebut): \"Insya Allah\". Dan ingatlah kepada Tuhanmu jika kamu lupa dan katakanlah: \"Mudah-mudahan Tuhanku akan memberiku petunjuk kepada yang lebih dekat kebenarannya dari pada ini\".'),
(2165, '018:025', '68', 18, 25, 'Dan mereka tinggal dalam gua mereka tiga ratus tahun dan ditambah sembilan tahun (lagi).'),
(2166, '018:026', '68', 18, 26, 'Katakanlah: \"Allah lebih mengetahui berapa lamanya mereka tinggal (di gua); kepunyaan-Nya-lah semua yang tersembunyi di langit dan di bumi. Alangkah terang penglihatan-Nya dan alangkah tajam pendengaran-Nya; tak ada seorang pelindungpun bagi mereka selain dari pada-Nya; dan Dia tidak mengambil seorangpun menjadi sekutu-Nya dalam menetapkan keputusan\".'),
(2167, '018:027', '68', 18, 27, 'Dan bacakanlah apa yang diwahyukan kepadamu, yaitu kitab Tuhanmu (Al Quran). Tidak ada (seorangpun) yang dapat merubah kalimat-kalimat-Nya. Dan kamu tidak akan dapat menemukan tempat berlindung selain dari pada-Nya.'),
(2168, '018:028', '68', 18, 28, 'Dan bersabarlah kamu bersama-sama dengan orang-orang yang menyeru Tuhannya di pagi dan senja hari dengan mengharap keridhaan-Nya; dan janganlah kedua matamu berpaling dari mereka (karena) mengharapkan perhiasan dunia ini; dan janganlah kamu mengikuti orang yang hatinya telah Kami lalaikan dari mengingati Kami, serta menuruti hawa nafsunya dan adalah keadaannya itu melewati batas.'),
(2169, '018:029', '68', 18, 29, 'Dan katakanlah: \"Kebenaran itu datangnya dari Tuhanmu; maka barangsiapa yang ingin (beriman) hendaklah ia beriman, dan barangsiapa yang ingin (kafir) biarlah ia kafir\". Sesungguhnya Kami telah sediakan bagi orang orang zalim itu neraka, yang gejolaknya mengepung mereka. Dan jika mereka meminta minum, niscaya mereka akan diberi minum dengan air seperti besi yang mendidih yang menghanguskan muka. Itulah minuman yang paling buruk dan tempat istirahat yang paling jelek.'),
(2170, '018:030', '68', 18, 30, 'Sesunggunya mereka yang beriman dan beramal saleh, tentulah Kami tidak akan menyia-nyiakan pahala orang-orang yang mengerjakan amalan(nya) dengan yang baik.'),
(2171, '018:031', '68', 18, 31, 'Mereka itulah (orang-orang yang) bagi mereka surga ´Adn, mengalir sungai-sungai di bawahnya; dalam surga itu mereka dihiasi dengan gelang mas dan mereka memakai pakaian hijau dari sutera halus dan sutera tebal, sedang mereka duduk sambil bersandar di atas dipan-dipan yang indah. Itulah pahala yang sebaik-baiknya, dan tempat istirahat yang indah;'),
(2172, '018:032', '68', 18, 32, 'Dan berikanlah kepada mereka sebuah perumpamaan dua orang laki-laki, Kami jadikan bagi seorang di antara keduanya (yang kafir) dua buah kebun anggur dan kami kelilingi kedua kebun itu dengan pohon-pohon korma dan di antara kedua kebun itu Kami buatkan ladang.'),
(2173, '018:033', '68', 18, 33, 'Kedua buah kebun itu menghasilkan buahnya, dan kebun itu tiada kurang buahnya sedikitpun, dan Kami alirkan sungai di celah-celah kedua kebun itu,'),
(2174, '018:034', '68', 18, 34, 'dan dia mempunyai kekayaan besar, maka ia berkata kepada kawannya (yang mukmin) ketika bercakap-cakap dengan dia: \"Hartaku lebih banyak dari pada hartamu dan pengikut-pengikutku lebih kuat\"'),
(2175, '018:035', '68', 18, 35, 'Dan dia memasuki kebunnya sedang dia zalim terhadap dirinya sendiri; ia berkata: \"Aku kira kebun ini tidak akan binasa selama-lamanya,'),
(2176, '018:036', '68', 18, 36, 'dan aku tidak mengira hari kiamat itu akan datang, dan jika sekiranya aku kembalikan kepada Tuhanku, pasti aku akan mendapat tempat kembali yang lebih baik dari pada kebun-kebun itu\".'),
(2177, '018:037', '68', 18, 37, 'Kawannya (yang mukmin) berkata kepadanya -- sedang dia bercakap-cakap dengannya: \"Apakah kamu kafir kepada (Tuhan) yang menciptakan kamu dari tanah, kemudian dari setetes air mani, lalu Dia menjadikan kamu seorang laki-laki yang sempurna?'),
(2178, '018:038', '68', 18, 38, 'Tetapi aku (percaya bahwa): Dialah Allah, Tuhanku, dan aku tidak mempersekutukan seorangpun dengan Tuhanku.'),
(2179, '018:039', '68', 18, 39, 'Dan mengapa kamu tidak mengatakan waktu kamu memasuki kebunmu \"maasyaallaah, laa quwwata illaa billaah (sungguh atas kehendak Allah semua ini terwujud, tiada kekuatan kecuali dengan pertolongan Allah). Sekiranya kamu anggap aku lebih sedikit darimu dalam hal harta dan keturunan,'),
(2180, '018:040', '68', 18, 40, 'maka mudah-mudahan Tuhanku, akan memberi kepadaku (kebun) yang lebih baik dari pada kebunmu (ini); dan mudah-mudahan Dia mengirimkan ketentuan (petir) dari langit kepada kebunmu; hingga (kebun itu) menjadi tanah yang licin;'),
(2181, '018:041', '68', 18, 41, 'atau airnya menjadi surut ke dalam tanah, maka sekali-kali kamu tidak dapat menemukannya lagi\".'),
(2182, '018:042', '68', 18, 42, 'Dan harta kekayaannya dibinasakan; lalu ia membulak-balikkan kedua tangannya (tanda menyesal) terhadap apa yang ia telah belanjakan untuk itu, sedang pohon anggur itu roboh bersama para-paranya dan dia berkata: \"Aduhai kiranya dulu aku tidak mempersekutukan seorangpun dengan Tuhanku\".'),
(2183, '018:043', '68', 18, 43, 'Dan tidak ada bagi dia segolonganpun yang akan menolongnya selain Allah; dan sekali-kali ia tidak dapat membela dirinya.'),
(2184, '018:044', '68', 18, 44, 'Di sana pertolongan itu hanya dari Allah Yang Hak. Dia adalah sebaik-baik Pemberi pahala dan sebaik-baik Pemberi balasan.'),
(2185, '018:045', '68', 18, 45, 'Dan berilah perumpamaan kepada mereka (manusia), kehidupan dunia sebagai air hujan yang Kami turunkan dari langit, maka menjadi subur karenanya tumbuh-tumbuhan di muka bumi, kemudian tumbuh-tumbuhan itu menjadi kering yang diterbangkan oleh angin. Dan adalah Allah, Maha Kuasa atas segala sesuatu.'),
(2186, '018:046', '68', 18, 46, 'Harta dan anak-anak adalah perhiasan kehidupan dunia tetapi amalan-amalan yang kekal lagi saleh adalah lebih baik pahalanya di sisi Tuhanmu serta lebih baik untuk menjadi harapan.'),
(2187, '018:047', '68', 18, 47, 'Dan (ingatlah) akan hari (yang ketika itu) Kami perjalankan gunung-gunung dan kamu akan dapat melihat bumi itu datar dan Kami kumpulkan seluruh manusia, dan tidak kami tinggalkan seorangpun dari mereka.'),
(2188, '018:048', '68', 18, 48, 'Dan mereka akan dibawa ke hadapan Tuhanmu dengan berbaris. Sesungguhnya kamu datang kepada Kami, sebagaimana Kami menciptakan kamu pada kali yang pertama; bahkan kamu mengatakan bahwa Kami sekali-kali tidak akan menetapkan bagi kamu waktu (memenuhi) perjanjian.'),
(2189, '018:049', '68', 18, 49, 'Dan diletakkanlah kitab, lalu kamu akan melihat orang-orang bersalah ketakutan terhadap apa yang (tertulis) di dalamnya, dan mereka berkata: \"Aduhai celaka kami, kitab apakah ini yang tidak meninggalkan yang kecil dan tidak (pula) yang besar, melainkan ia mencatat semuanya; dan mereka dapati apa yang telah mereka kerjakan ada (tertulis). Dan Tuhanmu tidak menganiaya seorang juapun\".'),
(2190, '018:050', '68', 18, 50, 'Dan (ingatlah) ketika Kami berfirman kepada para malaikat: \"Sujudlah kamu kepada Adam, maka sujudlah mereka kecuali Iblis. Dia adalah dari golongan jin, maka ia mendurhakai perintah Tuhannya. Patutkah kamu mengambil dia dan turanan-turunannya sebagai pemimpin selain daripada-Ku, sedang mereka adalah musuhmu? Amat buruklah iblis itu sebagai pengganti (dari Allah) bagi orang-orang yang zalim.'),
(2191, '018:051', '68', 18, 51, 'Aku tidak menghadirkan mereka (iblis dan anak cucunya) untuk menyaksikan penciptaan langit dan bumi dan tidak (pula) penciptaan diri mereka sendiri; dan tidaklah Aku mengambil orang-orang yang menyesatkan itu sebagai penolong.'),
(2192, '018:052', '68', 18, 52, 'Dan (ingatlah) akan hari (yang ketika itu) Dia berfirman: \"Serulah olehmu sekalian sekutu-sekutu-Ku yang kamu katakan itu\". Mereka lalu memanggilnya tetapi sekutu-sekutu itu tidak membalas seruan mereka dan Kami adakan untuk mereka tempat kebinasaan (neraka).'),
(2193, '018:053', '68', 18, 53, 'Dan orang-orang yang berdosa melihat neraka, maka mereka meyakini, bahwa mereka akan jatuh ke dalamnya dan mereka tidak menemukan tempat berpaling dari padanya.'),
(2194, '018:054', '68', 18, 54, 'Dan sesungguhnya Kami telah mengulang-ulangi bagi manusia dalam Al Quran ini bermacam-macam perumpamaan. Dan manusia adalah makhluk yang paling banyak membantah.'),
(2195, '018:055', '68', 18, 55, 'Dam tidak ada sesuatupun yang menghalangi manusia dari beriman, ketika petunjuk telah datang kepada mereka, dan dari memohon ampun kepada Tuhannya, kecuali (keinginan menanti) datangnya hukum (Allah yang telah berlalu pada) umat-umat yang dahulu atau datangnya azab atas mereka dengan nyata.'),
(2196, '018:056', '68', 18, 56, 'Dan tidaklah Kami mengutus rasul-rasul hanyalah sebagai pembawa berita gembira dan sebagai pemberi peringatan; tetapi orang-orang yang kafir membantah dengan yang batil agar dengan demikian mereka dapat melenyap kan yang hak, dan mereka menganggap ayat-ayat kami dan peringatan-peringatan terhadap mereka sebagai olok-olokan.'),
(2197, '018:057', '68', 18, 57, 'Dan siapakah yang lebih zalim dari pada orang yang telah diperingatkan dengan ayat-ayat Tuhannya lalu dia berpaling dari padanya dan melupakan apa yang telah dikerjakan oleh kedua tangannya? Sesungguhnya Kami telah meletakkan tutupan di atas hati mereka, (sehingga mereka tidak) memahaminya, dan (Kami letakkan pula) sumbatan di telinga mereka; dan kendatipun kamu menyeru mereka kepada petunjuk, niscaya mereka tidak akan mendapat petunjuk selama-lamanya.'),
(2198, '018:058', '68', 18, 58, 'Dan Tuhanmulah yang Maha Pengampun, lagi mempunyai rahmat. Jika Dia mengazab mereka karena perbuatan mereka, tentu Dia akan menyegerakan azab bagi mereka. Tetapi bagi mereka ada waktu yang tertentu (untuk mendapat azab) yang mereka sekali-kali tidak akan menemukan tempat berlindung dari padanya.'),
(2199, '018:059', '68', 18, 59, 'Dan (penduduk) negeri telah Kami binasakan ketika mereka berbuat zalim, dan telah Kami tetapkan waktu tertentu bagi kebinasaan mereka.'),
(2200, '018:060', '68', 18, 60, 'Dan (ingatlah) ketika Musa berkata kepada muridnya: \"Aku tidak akan berhenti (berjalan) sebelum sampai ke pertemuan dua buah lautan; atau aku akan berjalan sampai bertahun-tahun\".'),
(2201, '018:061', '68', 18, 61, 'Maka tatkala mereka sampai ke pertemuan dua buah laut itu, mereka lalai akan ikannya, lalu ikan itu melompat mengambil jalannya ke laut itu.'),
(2202, '018:062', '68', 18, 62, 'Maka tatkala mereka berjalan lebih jauh, berkatalah Musa kepada muridnya: \"Bawalah kemari makanan kita; sesungguhnya kita telah merasa letih karena perjalanan kita ini\".'),
(2203, '018:063', '68', 18, 63, 'Muridnya menjawab: \"Tahukah kamu tatkala kita mencari tempat berlindung di batu tadi, maka sesungguhnya aku lupa (menceritakan tentang) ikan itu dan tidak adalah yang melupakan aku untuk menceritakannya kecuali syaitan dan ikan itu mengambil jalannya ke laut dengan cara yang aneh sekali\".'),
(2204, '018:064', '68', 18, 64, 'Musa berkata: \"Itulah (tempat) yang kita cari\". Lalu keduanya kembali, mengikuti jejak mereka semula.'),
(2205, '018:065', '68', 18, 65, 'Lalu mereka bertemu dengan seorang hamba di antara hamba-hamba Kami, yang telah Kami berikan kepadanya rahmat dari sisi Kami, dan yang telah Kami ajarkan kepadanya ilmu dari sisi Kami.'),
(2206, '018:066', '68', 18, 66, 'Musa berkata kepada Khidhr: \"Bolehkah aku mengikutimu supaya kamu mengajarkan kepadaku ilmu yang benar di antara ilmu-ilmu yang telah diajarkan kepadamu?\"'),
(2207, '018:067', '68', 18, 67, 'Dia menjawab: \"Sesungguhnya kamu sekali-kali tidak akan sanggup sabar bersama aku.'),
(2208, '018:068', '68', 18, 68, 'Dan bagaimana kamu dapat sabar atas sesuatu, yang kamu belum mempunyai pengetahuan yang cukup tentang hal itu?\"'),
(2209, '018:069', '68', 18, 69, 'Musa berkata: \"Insya Allah kamu akan mendapati aku sebagai orang yang sabar, dan aku tidak akan menentangmu dalam sesuatu urusanpun\".'),
(2210, '018:070', '68', 18, 70, 'Dia berkata: \"Jika kamu mengikutiku, maka janganlah kamu menanyakan kepadaku tentang sesuatu apapun, sampai aku sendiri menerangkannya kepadamu\".'),
(2211, '018:071', '68', 18, 71, 'Maka berjalanlah keduanya, hingga tatkala keduanya menaiki perahu lalu Khidhr melobanginya. Musa berkata: \"Mengapa kamu melobangi perahu itu akibatnya kamu menenggelamkan penumpangnya?\" Sesungguhnya kamu telah berbuat sesuatu kesalahan yang besar.'),
(2212, '018:072', '68', 18, 72, 'Dia (Khidhr) berkata: \"Bukankah aku telah berkata: \"Sesungguhnya kamu sekali-kali tidak akan sabar bersama dengan aku\".'),
(2213, '018:073', '68', 18, 73, 'Musa berkata: \"Janganlah kamu menghukum aku karena kelupaanku dan janganlah kamu membebani aku dengan sesuatu kesulitan dalam urusanku\".'),
(2214, '018:074', '68', 18, 74, 'Maka berjalanlah keduanya; hingga tatkala keduanya berjumpa dengan seorang anak, maka Khidhr membunuhnya. Musa berkata: \"Mengapa kamu membunuh jiwa yang bersih, bukan karena dia membunuh orang lain? Sesungguhnya kamu telah melakukan suatu yang mungkar\".'),
(2215, '018:075', '68', 18, 75, 'Khidhr berkata: \"Bukankah sudah kukatakan kepadamu, bahwa sesungguhnya kamu tidak akan dapat sabar bersamaku?\"'),
(2216, '018:076', '68', 18, 76, 'Musa berkata: \"Jika aku bertanya kepadamu tentang sesuatu sesudah (kali) ini, maka janganlah kamu memperbolehkan aku menyertaimu, sesungguhnya kamu sudah cukup memberikan uzur padaku\".'),
(2217, '018:077', '68', 18, 77, 'Maka keduanya berjalan; hingga tatkala keduanya sampai kepada penduduk suatu negeri, mereka minta dijamu kepada penduduk negeri itu, tetapi penduduk negeri itu tidak mau menjamu mereka, kemudian keduanya mendapatkan dalam negeri itu dinding rumah yang hampir roboh, maka Khidhr menegakkan dinding itu. Musa berkata: \"Jikalau kamu mau, niscaya kamu mengambil upah untuk itu\".'),
(2218, '018:078', '68', 18, 78, 'Khidhr berkata: \"Inilah perpisahan antara aku dengan kamu; kelak akan kuberitahukan kepadamu tujuan perbuatan-perbuatan yang kamu tidak dapat sabar terhadapnya.'),
(2219, '018:079', '68', 18, 79, 'Adapun bahtera itu adalah kepunyaan orang-orang miskin yang bekerja di laut, dan aku bertujuan merusakkan bahtera itu, karena di hadapan mereka ada seorang raja yang merampas tiap-tiap bahtera.'),
(2220, '018:080', '68', 18, 80, 'Dan adapun anak muda itu, maka keduanya adalah orang-orang mukmin, dan kami khawatir bahwa dia akan mendorong kedua orang tuanya itu kepada kesesatan dan kekafiran.'),
(2221, '018:081', '68', 18, 81, 'Dan kami menghendaki, supaya Tuhan mereka mengganti bagi mereka dengan anak lain yang lebih baik kesuciannya dari anaknya itu dan lebih dalam kasih sayangnya (kepada ibu bapaknya).'),
(2222, '018:082', '68', 18, 82, 'Adapun dinding rumah adalah kepunyaan dua orang anak yatim di kota itu, dan di bawahnya ada harta benda simpanan bagi mereka berdua, sedang ayahnya adalah seorang yang saleh, maka Tuhanmu menghendaki agar supaya mereka sampai kepada kedewasaannya dan mengeluarkan simpanannya itu, sebagai rahmat dari Tuhanmu; dan bukanlah aku melakukannya itu menurut kemauanku sendiri. Demikian itu adalah tujuan perbuatan-perbuatan yang kamu tidak dapat sabar terhadapnya\".'),
(2223, '018:083', '68', 18, 83, 'Mereka akan bertanya kepadamu (Muhammad) tentang Dzulkarnain. Katakanlah: \"Aku akan bacakan kepadamu cerita tantangnya\".'),
(2224, '018:084', '68', 18, 84, 'Sesungguhnya Kami telah memberi kekuasaan kepadanya di (muka) bumi, dan Kami telah memberikan kepadanya jalan (untuk mencapai) segala sesuatu,'),
(2225, '018:085', '68', 18, 85, 'maka diapun menempuh suatu jalan.'),
(2226, '018:086', '68', 18, 86, 'Hingga apabila dia telah sampai ketempat terbenam matahari, dia melihat matahari terbenam di dalam laut yang berlumpur hitam, dan dia mendapati di situ segolongan umat. Kami berkata: \"Hai Dzulkarnain, kamu boleh menyiksa atau boleh berbuat kebaikan terhadap mereka.'),
(2227, '018:087', '68', 18, 87, 'Berkata Dzulkarnain: \"Adapun orang yang aniaya, maka kami kelak akan mengazabnya, kemudian dia kembalikan kepada Tuhannya, lalu Tuhan mengazabnya dengan azab yang tidak ada taranya.'),
(2228, '018:088', '68', 18, 88, 'Adapun orang-orang yang beriman dan beramal saleh, maka baginya pahala yang terbaik sebagai balasan, dan akan kami titahkan kepadanya (perintah) yang mudah dari perintah-perintah kami\".'),
(2229, '018:089', '68', 18, 89, 'Kemudian dia menempuh jalan (yang lain).'),
(2230, '018:090', '68', 18, 90, 'Hingga apabila dia telah sampai ke tempat terbit matahari (sebelah Timur) dia mendapati matahari itu menyinari segolongan umat yang Kami tidak menjadikan bagi mereka sesuatu yang melindunginya dari (cahaya) matahari itu,'),
(2231, '018:091', '68', 18, 91, 'demikianlah. dan sesungguhnya ilmu Kami meliputi segala apa yang ada padanya.'),
(2232, '018:092', '68', 18, 92, 'Kemudian dia menempuh suatu jalan (yang lain lagi).'),
(2233, '018:093', '68', 18, 93, 'Hingga apabila dia telah sampai di antara dua buah gunung, dia mendapati di hadapan kedua bukit itu suatu kaum yang hampir tidak mengerti pembicaraan.'),
(2234, '018:094', '68', 18, 94, 'Mereka berkata: \"Hai Dzulkarnain, sesungguhnya Ya´juj dan Ma´juj itu orang-orang yang membuat kerusakan di muka bumi, maka dapatkah kami memberikan sesuatu pembayaran kepadamu, supaya kamu membuat dinding antara kami dan mereka?\"'),
(2235, '018:095', '68', 18, 95, 'Dzulkarnain berkata: \"Apa yang telah dikuasakan oleh Tuhanku kepadaku terhadapnya adalah lebih baik, maka tolonglah aku dengan kekuatan (manusia dan alat-alat), agar aku membuatkan dinding antara kamu dan mereka,'),
(2236, '018:096', '68', 18, 96, 'berilah aku potongan-potongan besi\". Hingga apabila besi itu telah sama rata dengan kedua (puncak) gunung itu, berkatalah Dzulkarnain: \"Tiuplah (api itu)\". Hingga apabila besi itu sudah menjadi (merah seperti) api, diapun berkata: \"Berilah aku tembaga (yang mendidih) agar aku kutuangkan ke atas besi panas itu\".'),
(2237, '018:097', '68', 18, 97, 'Maka mereka tidak bisa mendakinya dan mereka tidak bisa (pula) melobanginya.'),
(2238, '018:098', '68', 18, 98, 'Dzulkarnain berkata: \"Ini (dinding) adalah rahmat dari Tuhanku, maka apabila sudah datang janji Tuhanku, Dia akan menjadikannya hancur luluh; dan janji Tuhanku itu adalah benar\".'),
(2239, '018:099', '68', 18, 99, 'Kami biarkan mereka di hari itu bercampur aduk antara satu dengan yang lain, kemudian ditiup lagi sangkakala, lalu Kami kumpulkan mereka itu semuanya,'),
(2240, '018:100', '68', 18, 100, 'dan Kami nampakkan Jahannam pada hari itu kepada orang-orang kafir dengan jelas,'),
(2241, '018:101', '68', 18, 101, 'yaitu orang-orang yang matanya dalam keadaan tertutup dari memperhatikan tanda-tanda kebesaran-Ku, dan adalah mereka tidak sanggup mendengar.'),
(2242, '018:102', '68', 18, 102, 'maka apakah orang-orang kafir menyangka bahwa mereka (dapat) mengambil hamba-hamba-Ku menjadi penolong selain Aku? Sesungguhnya Kami telah menyediakan neraka Jahannam tempat tinggal bagi orang-orang kafir.'),
(2243, '018:103', '68', 18, 103, 'Katakanlah: \"Apakah akan Kami beritahukan kepadamu tentang orang-orang yang paling merugi perbuatannya?\"'),
(2244, '018:104', '68', 18, 104, 'Yaitu orang-orang yang telah sia-sia perbuatannya dalam kehidupan dunia ini, sedangkan mereka menyangka bahwa mereka berbuat sebaik-baiknya.'),
(2245, '018:105', '68', 18, 105, 'Mereka itu orang-orang yang telah kufur terhadap ayat-ayat Tuhan mereka dan (kufur terhadap) perjumpaan dengan Dia, maka hapuslah amalan-amalan mereka, dan Kami tidak mengadakan suatu penilaian bagi (amalan) mereka pada hari kiamat.'),
(2246, '018:106', '68', 18, 106, 'Demikianlah balasan mereka itu neraka Jahannam, disebabkan kekafiran mereka dan disebabkan mereka menjadikan ayat-ayat-Ku dan rasul-rasul-Ku sebagai olok-olok.'),
(2247, '018:107', '68', 18, 107, 'Sesungguhnya orang-orang yang beriman dan beramal saleh, bagi mereka adalah surga Firdaus menjadi tempat tinggal,'),
(2248, '018:108', '68', 18, 108, 'mereka kekal di dalamnya, mereka tidak ingin berpindah dari padanya.'),
(2249, '018:109', '68', 18, 109, 'Katakanlah: Sekiranya lautan menjadi tinta untuk (menulis) kalimat-kalimat Tuhanku, sungguh habislah lautan itu sebelum habis (ditulis) kalimat-kalimat Tuhanku, meskipun Kami datangkan tambahan sebanyak itu (pula)\".'),
(2250, '018:110', '68', 18, 110, 'Katakanlah: Sesungguhnya aku ini manusia biasa seperti kamu, yang diwahyukan kepadaku: \"Bahwa sesungguhnya Tuhan kamu itu adalah Tuhan yang Esa\". Barangsiapa mengharap perjumpaan dengan Tuhannya, maka hendaklah ia mengerjakan amal yang saleh dan janganlah ia mempersekutukan seorangpun dalam beribadat kepada Tuhannya\".'),
(2251, '019:001', '68', 19, 1, 'Kaaf Haa Yaa ´Ain Shaad.'),
(2252, '019:002', '68', 19, 2, '(Yang dibacakan ini adalah) penjelasan tentang rahmat Tuhan kamu kepada hamba-Nya, Zakaria,'),
(2253, '019:003', '68', 19, 3, 'yaitu tatkala ia berdoa kepada Tuhannya dengan suara yang lembut.'),
(2254, '019:004', '68', 19, 4, 'Ia berkata \"Ya Tuhanku, sesungguhnya tulangku telah lemah dan kepalaku telah ditumbuhi uban, dan aku belum pernah kecewa dalam berdoa kepada Engkau, ya Tuhanku.'),
(2255, '019:005', '68', 19, 5, 'Dan sesungguhnya aku khawatir terhadap mawaliku sepeninggalku, sedang isteriku adalah seorang yang mandul, maka anugerahilah aku dari sisi Engkau seorang putera,'),
(2256, '019:006', '68', 19, 6, 'yang akan mewarisi aku dan mewarisi sebahagian keluarga Ya´qub; dan jadikanlah ia, ya Tuhanku, seorang yang diridhai\".'),
(2257, '019:007', '68', 19, 7, 'Hai Zakaria, sesungguhnya Kami memberi kabar gembira kepadamu akan (beroleh) seorang anak yang namanya Yahya, yang sebelumnya Kami belum pernah menciptakan orang yang serupa dengan dia.'),
(2258, '019:008', '68', 19, 8, 'Zakaria berkata: \"Ya Tuhanku, bagaimana akan ada anak bagiku, padahal isteriku adalah seorang yang mandul dan aku (sendiri) sesungguhnya sudah mencapai umur yang sangat tua\".'),
(2259, '019:009', '68', 19, 9, 'Tuhan berfirman: \"Demikianlah\". Tuhan berfirman: \"Hal itu adalah mudah bagi-Ku; dan sesunguhnya telah Aku ciptakan kamu sebelum itu, padahal kamu (di waktu itu) belum ada sama sekali\".'),
(2260, '019:010', '68', 19, 10, 'Zakaria berkata: \"Ya Tuhanku, berilah aku suatu tanda\". Tuhan berfirman: \"Tanda bagimu ialah bahwa kamu tidak dapat bercakap-cakap dengan manusia selama tiga malam, padahal kamu sehat\".'),
(2261, '019:011', '68', 19, 11, 'Maka ia keluar dari mihrab menuju kaumnya, lalu ia memberi isyarat kepada mereka; hendaklah kamu bertasbih di waktu pagi dan petang.'),
(2262, '019:012', '68', 19, 12, 'Hai Yahya, ambillah Al Kitab (Taurat) itu dengan sungguh-sungguh. Dan kami berikan kepadanya hikmah selagi ia masih kanak-kanak,'),
(2263, '019:013', '68', 19, 13, 'dan rasa belas kasihan yang mendalam dari sisi Kami dan kesucian (dan dosa). Dan ia adalah seorang yang bertakwa,'),
(2264, '019:014', '68', 19, 14, 'dan seorang yang berbakti kepada kedua orang tuanya, dan bukanlah ia orang yang sombong lagi durhaka.'),
(2265, '019:015', '68', 19, 15, 'Kesejahteraan atas dirinya pada hari ia dilahirkan dan pada hari ia meninggal dan pada hari ia dibangkitkan hidup kembali.'),
(2266, '019:016', '68', 19, 16, 'Dan ceritakanlah (kisah) Maryam di dalam Al Quran, yaitu ketika ia menjauhkan diri dari keluarganya ke suatu tempat di sebelah timur,'),
(2267, '019:017', '68', 19, 17, 'maka ia mengadakan tabir (yang melindunginya) dari mereka; lalu Kami mengutus roh Kami kepadanya, maka ia menjelma di hadapannya (dalam bentuk) manusia yang sempurna.'),
(2268, '019:018', '68', 19, 18, 'Maryam berkata: \"Sesungguhnya aku berlindung dari padamu kepada Tuhan Yang Maha pemurah, jika kamu seorang yang bertakwa\".'),
(2269, '019:019', '68', 19, 19, 'Ia (jibril) berkata: \"Sesungguhnya aku ini hanyalah seorang utusan Tuhanmu, untuk memberimu seorang anak laki-laki yang suci\".'),
(2270, '019:020', '68', 19, 20, 'Maryam berkata: \"Bagaimana akan ada bagiku seorang anak laki-laki, sedang tidak pernah seorang manusiapun menyentuhku dan aku bukan (pula) seorang pezina!\"'),
(2271, '019:021', '68', 19, 21, 'Jibril berkata: \"Demikianlah\". Tuhanmu berfirman: \"Hal itu adalah mudah bagi-Ku; dan agar dapat Kami menjadikannya suatu tanda bagi manusia dan sebagai rahmat dari Kami; dan hal itu adalah suatu perkara yang sudah diputuskan\".'),
(2272, '019:022', '68', 19, 22, 'Maka Maryam mengandungnya, lalu ia menyisihkan diri dengan kandungannya itu ke tempat yang jauh.'),
(2273, '019:023', '68', 19, 23, 'Maka rasa sakit akan melahirkan anak memaksa ia (bersandar) pada pangkal pohon kurma, dia berkata: \"Aduhai, alangkah baiknya aku mati sebelum ini, dan aku menjadi barang yang tidak berarti, lagi dilupakan\".'),
(2274, '019:024', '68', 19, 24, 'Maka Jibril menyerunya dari tempat yang rendah: \"Janganlah kamu bersedih hati, sesungguhnya Tuhanmu telah menjadikan anak sungai di bawahmu.'),
(2275, '019:025', '68', 19, 25, 'Dan goyanglah pangkal pohon kurma itu ke arahmu, niscaya pohon itu akan menggugurkan buah kurma yang masak kepadamu,'),
(2276, '019:026', '68', 19, 26, 'maka makan, minum dan bersenang hatilah kamu. Jika kamu melihat seorang manusia, maka katakanlah: \"Sesungguhnya aku telah bernazar berpuasa untuk Tuhan Yang Maha Pemurah, maka aku tidak akan berbicara dengan seorang manusiapun pada hari ini\".'),
(2277, '019:027', '68', 19, 27, 'Maka Maryam membawa anak itu kepada kaumnya dengan menggendongnya. Kaumnya berkata: \"Hai Maryam, sesungguhnya kamu telah melakukan sesuatu yang amat mungkar.'),
(2278, '019:028', '68', 19, 28, 'Hai saudara perempuan Harun, ayahmu sekali-kali bukanlah seorang yang jahat dan ibumu sekali-kali bukanlah seorang pezina\",'),
(2279, '019:029', '68', 19, 29, 'maka Maryam menunjuk kepada anaknya. Mereka berkata: \"Bagaimana kami akan berbicara dengan anak kecil yang masih di dalam ayunan?\"'),
(2280, '019:030', '68', 19, 30, 'Berkata Isa: \"Sesungguhnya aku ini hamba Allah, Dia memberiku Al Kitab (Injil) dan Dia menjadikan aku seorang nabi,'),
(2281, '019:031', '68', 19, 31, 'dan Dia menjadikan aku seorang yang diberkati di mana saja aku berada, dan Dia memerintahkan kepadaku (mendirikan) shalat dan (menunaikan) zakat selama aku hidup;'),
(2282, '019:032', '68', 19, 32, 'dan berbakti kepada ibuku, dan Dia tidak menjadikan aku seorang yang sombong lagi celaka.'),
(2283, '019:033', '68', 19, 33, 'Dan kesejahteraan semoga dilimpahkan kepadaku, pada hari aku dilahirkan, pada hari aku meninggal dan pada hari aku dibangkitkan hidup kembali\".'),
(2284, '019:034', '68', 19, 34, 'Itulah Isa putera Maryam, yang mengatakan perkataan yang benar, yang mereka berbantah-bantahan tentang kebenarannya.'),
(2285, '019:035', '68', 19, 35, 'Tidak layak bagi Allah mempunyai anak, Maha Suci Dia. Apabila Dia telah menetapkan sesuatu, maka Dia hanya berkata kepadanya: \"Jadilah\", maka jadilah ia.'),
(2286, '019:036', '68', 19, 36, 'Sesungguhnya Allah adalah Tuhanku dan Tuhanmu, maka sembahIah Dia oleh kamu sekalian. Ini adalah jalan yang lurus.'),
(2287, '019:037', '68', 19, 37, 'Maka berselisihlah golongan-golongan (yang ada) di antara mereka. Maka kecelakaanlah bagi orang-orang kafir pada waktu menyaksikan hari yang besar.'),
(2288, '019:038', '68', 19, 38, 'Alangkah terangnya pendengaran mereka dan alangkah tajamnya penglihatan mereka pada hari mereka datang kepada Kami. Tetapi orang-orang yang zalim pada hari ini (di dunia) berada dalam kesesatan yang nyata.'),
(2289, '019:039', '68', 19, 39, 'Dan berilah mereka peringatan tentang hari penyesalan, (yaitu) ketika segala perkara telah diputus. Dan mereka dalam kelalaian dan mereka tidak (pula) beriman.'),
(2290, '019:040', '68', 19, 40, 'Sesungguhnya Kami mewarisi bumi dan semua orang-orang yang ada di atasnya, dan hanya kepada Kamilah mereka dikembalikan.'),
(2291, '019:041', '68', 19, 41, 'Ceritakanlah (Hai Muhammad) kisah Ibrahim di dalam Al Kitab (Al Quran) ini. Sesungguhnya ia adalah seorang yang sangat membenarkan lagi seorang Nabi.'),
(2292, '019:042', '68', 19, 42, 'Ingatlah ketika ia berkata kepada bapaknya; \"Wahai bapakku, mengapa kamu menyembah sesuatu yang tidak mendengar, tidak melihat dan tidak dapat menolong kamu sedikitpun?');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(2293, '019:043', '68', 19, 43, 'Wahai bapakku, sesungguhnya telah datang kepadaku sebahagian ilmu pengetahuan yang tidak datang kepadamu, maka ikutilah aku, niscaya aku akan menunjukkan kepadamu jalan yang lurus.'),
(2294, '019:044', '68', 19, 44, 'Wahai bapakku, janganlah kamu menyembah syaitan. Sesungguhnya syaitan itu durhaka kepada Tuhan Yang Maha Pemurah.'),
(2295, '019:045', '68', 19, 45, 'Wahai bapakku, sesungguhnya aku khawatir bahwa kamu akan ditimpa azab dari Tuhan Yang Maha Pemurah, maka kamu menjadi kawan bagi syaitan\".'),
(2296, '019:046', '68', 19, 46, 'Berkata bapaknya: \"Bencikah kamu kepada tuhan-tuhanku, hai Ibrahim? Jika kamu tidak berhenti, maka niscaya kamu akan kurajam, dan tinggalkanlah aku buat waktu yang lama\".'),
(2297, '019:047', '68', 19, 47, 'Berkata Ibrahim: \"Semoga keselamatan dilimpahkan kepadamu, aku akan memintakan ampun bagimu kepada Tuhanku. Sesungguhnya Dia sangat baik kepadaku.'),
(2298, '019:048', '68', 19, 48, 'Dan aku akan menjauhkan diri darimu dan dari apa yang kamu seru selain Allah, dan aku akan berdoa kepada Tuhanku, mudah-mudahan aku tidak akan kecewa dengan berdoa kepada Tuhanku\".'),
(2299, '019:049', '68', 19, 49, 'Maka ketika Ibrahim sudah menjauhkan diri dari mereka dan dari apa yang mereka sembah selain Allah, Kami anugerahkan kepadanya Ishak, dan Ya´qub. Dan masing-masingnya Kami angkat menjadi nabi.'),
(2300, '019:050', '68', 19, 50, 'Dan Kami anugerahkan kepada mereka sebagian dari rahmat Kami dan Kami jadikan mereka buah tutur yang baik lagi tinggi.'),
(2301, '019:051', '68', 19, 51, 'Dan ceritakanlah (hai Muhammad kepada mereka), kisah Musa di dalam Al Kitab (Al Quran) ini. Sesungguhnya ia adalah seorang yang dipilih dan seorang rasul dan nabi.'),
(2302, '019:052', '68', 19, 52, 'Dan Kami telah memanggilnya dari sebelah kanan gunung Thur dan Kami telah mendekatkannya kepada Kami di waktu dia munajat (kepada Kami).'),
(2303, '019:053', '68', 19, 53, 'Dan Kami telah menganugerahkan kepadanya sebagian rahmat Kami, yaitu saudaranya, Harun menjadi seorang nabi.'),
(2304, '019:054', '68', 19, 54, 'Dan ceritakanlah (hai Muhammad kepada mereka) kisah Ismail (yang tersebut) di dalam Al Quran. Sesungguhnya ia adalah seorang yang benar janjinya, dan dia adalah seorang rasul dan nabi.'),
(2305, '019:055', '68', 19, 55, 'Dan ia menyuruh ahlinya untuk bersembahyang dan menunaikan zakat, dan ia adalah seorang yang diridhai di sisi Tuhannya.'),
(2306, '019:056', '68', 19, 56, 'Dan ceritakanlah (hai Muhammad kepada mereka, kisah) Idris (yang tersebut) di dalam Al Quran. Sesungguhnya ia adalah seorang yang sangat membenarkan dan seorang nabi.'),
(2307, '019:057', '68', 19, 57, 'Dan Kami telah mengangkatnya ke martabat yang tinggi.'),
(2308, '019:058', '68', 19, 58, 'Mereka itu adalah orang-orang yang telah diberi nikmat oleh Allah, yaitu para nabi dari keturunan Adam, dan dari orang-orang yang Kami angkat bersama Nuh, dan dari keturunan Ibrahim dan Israil, dan dari orang-orang yang telah Kami beri petunjuk dan telah Kami pilih. Apabila dibacakan ayat-ayat Allah Yang Maha Pemurah kepada mereka, maka mereka menyungkur dengan bersujud dan menangis.'),
(2309, '019:059', '68', 19, 59, 'Maka datanglah sesudah mereka, pengganti (yang jelek) yang menyia-nyiakan shalat dan memperturutkan hawa nafsunya, maka mereka kelak akan menemui kesesatan,'),
(2310, '019:060', '68', 19, 60, 'kecuali orang yang bertaubat, beriman dan beramal saleh, maka mereka itu akan masuk surga dan tidak dianiaya (dirugikan) sedikitpun,'),
(2311, '019:061', '68', 19, 61, 'yaitu surga ´Adn yang telah dijanjikan oleh Tuhan Yang Maha Pemurah kepada hamba-hamba-Nya, sekalipun (surga itu) tidak nampak. Sesungguhnya janji Allah itu pasti akan ditepati.'),
(2312, '019:062', '68', 19, 62, 'Mereka tidak mendengar perkataan yang tak berguna di dalam surga, kecuali ucapan salam. Bagi mereka rezekinya di surga itu tiap-tiap pagi dan petang.'),
(2313, '019:063', '68', 19, 63, 'Itulah surga yang akan Kami wariskan kepada hamba-hamba Kami yang selalu bertakwa.'),
(2314, '019:064', '68', 19, 64, 'Dan tidaklah kami (Jibril) turun, kecuali dengan perintah Tuhanmu. Kepunyaan-Nya-lah apa-apa yang ada di hadapan kita, apa-apa yang ada di belakang kita dan apa-apa yang ada di antara keduanya, dan tidaklah Tuhanmu lupa.'),
(2315, '019:065', '68', 19, 65, 'Tuhan (yang menguasai) langit dan bumi dan apa-apa yang ada di antara keduanya, maka sembahlah Dia dan berteguh hatilah dalam beribadat kepada-Nya. Apakah kamu mengetahui ada seorang yang sama dengan Dia (yang patut disembah)?'),
(2316, '019:066', '68', 19, 66, 'Dan berkata manusia: \"Betulkah apabila aku telah mati, bahwa aku sungguh-sungguh akan dibangkitkan menjadi hidup kembali?\"'),
(2317, '019:067', '68', 19, 67, 'Dan tidakkah manusia itu memikirkan bahwa sesungguhnya Kami telah menciptakannya dahulu, sedang ia tidak ada sama sekali?'),
(2318, '019:068', '68', 19, 68, 'Demi Tuhanmu, sesungguhnya akan Kami bangkitkan mereka bersama syaitan, kemudian akan Kami datangkan mereka ke sekeliling Jahannam dengan berlutut.'),
(2319, '019:069', '68', 19, 69, 'Kemudian pasti akan Kami tarik dari tiap-tiap golongan siapa di antara mereka yang sangat durhaka kepada Tuhan Yang Maha Pemurah.'),
(2320, '019:070', '68', 19, 70, 'Dan kemudian Kami sungguh lebih mengetahui orang-orang yang seharusnya dimasukkan ke dalam neraka.'),
(2321, '019:071', '68', 19, 71, 'Dan tidak ada seorangpun dari padamu, melainkan mendatangi neraka itu. Hal itu bagi Tuhanmu adalah suatu kemestian yang sudah ditetapkan.'),
(2322, '019:072', '68', 19, 72, 'Kemudian Kami akan menyelamatkan orang-orang yang bertakwa dan membiarkan orang-orang yang zalim di dalam neraka dalam keadaan berlutut.'),
(2323, '019:073', '68', 19, 73, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang (maksudnya), niscaya orang-orang yang kafir berkata kepada orang-orang yang beriman: \"Manakah di antara kedua golongan (kafir dan mukmin) yang lebih baik tempat tinggalnya dan lebih indah tempat pertemuan(nya)?\"'),
(2324, '019:074', '68', 19, 74, 'Berapa banyak umat yang telah Kami binasakan sebelum mereka, sedang mereka adalah lebih bagus alat rumah tangganya dan lebih sedap di pandang mata.'),
(2325, '019:075', '68', 19, 75, 'Katakanlah: \"Barang siapa yang berada di dalam kesesatan, maka biarlah Tuhan yang Maha Pemurah memperpanjang tempo baginya; sehingga apabila mereka telah melihat apa yang diancamkan kepadanya, baik siksa maupun kiamat, maka mereka akan mengetahui siapa yang lebih jelek kedudukannya dan lebih lemah penolong-penolongnya\".'),
(2326, '019:076', '68', 19, 76, 'Dan Allah akan menambah petunjuk kepada mereka yang telah mendapat petunjuk. Dan amal-amal saleh yang kekal itu lebih baik pahalanya di sisi Tuhanmu dan lebih baik kesudahannya.'),
(2327, '019:077', '68', 19, 77, 'Maka apakah kamu telah melihat orang yang kafir kepada ayat-ayat Kami dan ia mengatakan: \"Pasti aku akan diberi harta dan anak\".'),
(2328, '019:078', '68', 19, 78, 'Adakah ia melihat yang ghaib atau ia telah membuat perjanjian di sisi Tuhan Yang Maha Pemurah?,'),
(2329, '019:079', '68', 19, 79, 'sekali-kali tidak, Kami akan menulis apa yang ia katakan, dan benar-benar Kami akan memperpanjang azab untuknya,'),
(2330, '019:080', '68', 19, 80, 'dan Kami akan mewarisi apa yang ia katakan itu, dan ia akan datang kepada Kami dengan seorang diri.'),
(2331, '019:081', '68', 19, 81, 'Dan mereka telah mengambil sembahan-sembahan selain Allah, agar sembahan-sembahan itu menjadi pelindung bagi mereka,'),
(2332, '019:082', '68', 19, 82, 'sekali-kali tidak. Kelak mereka (sembahan-sembahan) itu akan mengingkari penyembahan (pengikut-pengikutnya) terhadapnya, dan mereka (sembahan-sembahan) itu akan menjadi musuh bagi mereka.'),
(2333, '019:083', '68', 19, 83, 'Tidakkah kamu lihat, bahwasanya Kami telah mengirim syaitan-syaitan itu kepada orang-orang kafir untuk menghasung mereka berbuat maksiat dengan sungguh-sungguh?,'),
(2334, '019:084', '68', 19, 84, 'maka janganlah kamu tergesa-gesa memintakan siksa terhadap mereka, karena sesungguhnya Kami hanya menghitung datangnya (hari siksaan) untuk mereka dengan perhitungan yang teliti.'),
(2335, '019:085', '68', 19, 85, '(Ingatlah) hari (ketika) Kami mengumpulkan orang-orang yang takwa kepada Tuhan Yang Maha Pemurah sebagai perutusan yang terhormat,'),
(2336, '019:086', '68', 19, 86, 'dan Kami akan menghalau orang-orang yang durhaka ke neraka Jahannam dalam keadaan dahaga.'),
(2337, '019:087', '68', 19, 87, 'Mereka tidak berhak mendapat syafa´at kecuali orang yang telah mengadakan perjanjian di sisi Tuhan Yang Maha Pemurah.'),
(2338, '019:088', '68', 19, 88, 'Dan mereka berkata: \"Tuhan Yang Maha Pemurah mengambil (mempunyai) anak\".'),
(2339, '019:089', '68', 19, 89, 'Sesungguhnya kamu telah mendatangkan sesuatu perkara yang sangat mungkar,'),
(2340, '019:090', '68', 19, 90, 'hampir-hampir langit pecah karena ucapan itu, dan bumi belah, dan gunung-gunung runtuh,'),
(2341, '019:091', '68', 19, 91, 'karena mereka mendakwakan Allah Yang Maha Pemurah mempunyai anak.'),
(2342, '019:092', '68', 19, 92, 'Dan tidak layak bagi Tuhan Yang Maha Pemurah mengambil (mempunyai) anak.'),
(2343, '019:093', '68', 19, 93, 'Tidak ada seorangpun di langit dan di bumi, kecuali akan datang kepada Tuhan Yang Maha Pemurah selaku seorang hamba.'),
(2344, '019:094', '68', 19, 94, 'Sesungguhnya Allah telah menentukan jumlah mereka dan menghitung mereka dengan hitungan yang teliti.'),
(2345, '019:095', '68', 19, 95, 'Dan tiap-tiap mereka akan datang kepada Allah pada hari kiamat dengan sendiri-sendiri.'),
(2346, '019:096', '68', 19, 96, 'Sesungguhnya orang-orang yang beriman dan beramal saleh, kelak Allah Yang Maha Pemurah akan menanamkan dalam (hati) mereka rasa kasih sayang.'),
(2347, '019:097', '68', 19, 97, 'Maka sesungguhnya telah Kami mudahkan Al Quran itu dengan bahasamu, agar kamu dapat memberi kabar gembira dengan Al Quran itu kepada orang-orang yang bertakwa, dan agar kamu memberi peringatan dengannya kepada kaum yang membangkang.'),
(2348, '019:098', '68', 19, 98, 'Dan berapa banyak telah Kami binasakan umat-umat sebelum mereka. Adakah kamu melihat seorangpun dari mereka atau kamu dengar suara mereka yang samar-samar?'),
(2349, '020:001', '68', 20, 1, 'Thaahaa.'),
(2350, '020:002', '68', 20, 2, 'Kami tidak menurunkan Al Quran ini kepadamu agar kamu menjadi susah;'),
(2351, '020:003', '68', 20, 3, 'tetapi sebagai peringatan bagi orang yang takut (kepada Allah),'),
(2352, '020:004', '68', 20, 4, 'yaitu diturunkan dari Allah yang menciptakan bumi dan langit yang tinggi.'),
(2353, '020:005', '68', 20, 5, '(Yaitu) Tuhan Yang Maha Pemurah. Yang bersemayam di atas ´Arsy.'),
(2354, '020:006', '68', 20, 6, 'Kepunyaan-Nya-lah semua yang ada di langit, semua yang di bumi, semua yang di antara keduanya dan semua yang di bawah tanah.'),
(2355, '020:007', '68', 20, 7, 'Dan jika kamu mengeraskan ucapanmu, maka sesungguhnya Dia mengetahui rahasia dan yang lebih tersembunyi.'),
(2356, '020:008', '68', 20, 8, 'Dialah Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia. Dia mempunyai al asmaaul husna (nama-nama yang baik),'),
(2357, '020:009', '68', 20, 9, 'Apakah telah sampai kepadamu kisah Musa?'),
(2358, '020:010', '68', 20, 10, 'Ketika ia melihat api, lalu berkatalah ia kepada keluarganya: \"Tinggallah kamu (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa sedikit daripadanya kepadamu atau aku akan mendapat petunjuk di tempat api itu\".'),
(2359, '020:011', '68', 20, 11, 'Maka ketika ia datang ke tempat api itu ia dipanggil: \"Hai Musa.'),
(2360, '020:012', '68', 20, 12, 'Sesungguhnya Aku inilah Tuhanmu, maka tanggalkanlah kedua terompahmu; sesungguhnya kamu berada dilembah yang suci, Thuwa.'),
(2361, '020:013', '68', 20, 13, 'Dan Aku telah memilih kamu, maka dengarkanlah apa yang akan diwahyukan (kepadamu).'),
(2362, '020:014', '68', 20, 14, 'Sesungguhnya Aku ini adalah Allah, tidak ada Tuhan (yang hak) selain Aku, maka sembahlah Aku dan dirikanlah shalat untuk mengingat Aku.'),
(2363, '020:015', '68', 20, 15, 'Segungguhnya hari kiamat itu akan datang Aku merahasiakan (waktunya) agar supaya tiap-tiap diri itu dibalas dengan apa yang ia usahakan.'),
(2364, '020:016', '68', 20, 16, 'Maka sekali-kali janganlah kamu dipalingkan daripadanya oleh orang yang tidak beriman kepadanya dan oleh orang yang mengikuti hawa nafsunya, yang menyebabkan kamu jadi binasa\".'),
(2365, '020:017', '68', 20, 17, 'Apakah itu yang di tangan kananmu, hai Musa?'),
(2366, '020:018', '68', 20, 18, 'Berkata Musa: \"Ini adalah tongkatku, aku bertelekan padanya, dan aku pukul (daun) dengannya untuk kambingku, dan bagiku ada lagi keperluan yang lain padanya\".'),
(2367, '020:019', '68', 20, 19, 'Allah berfirman: \"Lemparkanlah ia, hai Musa!\"'),
(2368, '020:020', '68', 20, 20, 'Lalu dilemparkannyalah tongkat itu, maka tiba-tiba ia menjadi seekor ular yang merayap dengan cepat.'),
(2369, '020:021', '68', 20, 21, 'Allah berfirman: \"Peganglah ia dan jangan takut, Kami akan mengembalikannya kepada keadaannya semula,'),
(2370, '020:022', '68', 20, 22, 'dan kepitkanlah tanganmu ke ketiakmu, niscaya ia ke luar menjadi putih cemerlang tanpa cacad, sebagai mukjizat yang lain (pula),'),
(2371, '020:023', '68', 20, 23, 'untuk Kami perlihatkan kepadamu sebahagian dari tanda-tanda kekuasaan Kami yang sangat besar,'),
(2372, '020:024', '68', 20, 24, 'Pergilah kepada Fir´aun; sesungguhnya ia telah melampaui batas\".'),
(2373, '020:025', '68', 20, 25, 'Berkata Musa: \"Ya Tuhanku, lapangkanlah untukku dadaku,'),
(2374, '020:026', '68', 20, 26, 'dan mudahkanlah untukku urusanku,'),
(2375, '020:027', '68', 20, 27, 'dan lepaskanlah kekakuan dari lidahku,'),
(2376, '020:028', '68', 20, 28, 'supaya mereka mengerti perkataanku,'),
(2377, '020:029', '68', 20, 29, 'dan jadikanlah untukku seorang pembantu dari keluargaku,'),
(2378, '020:030', '68', 20, 30, '(yaitu) Harun, saudaraku,'),
(2379, '020:031', '68', 20, 31, 'teguhkanlah dengan dia kekuatanku,'),
(2380, '020:032', '68', 20, 32, 'dan jadikankanlah dia sekutu dalam urusanku,'),
(2381, '020:033', '68', 20, 33, 'supaya kami banyak bertasbih kepada Engkau,'),
(2382, '020:034', '68', 20, 34, 'dan banyak mengingat Engkau.'),
(2383, '020:035', '68', 20, 35, 'Sesungguhnya Engkau adalah Maha Melihat (keadaan) kami\".'),
(2384, '020:036', '68', 20, 36, 'Allah berfirman: \"Sesungguhnya telah diperkenankan permintaanmu, hai Musa\".'),
(2385, '020:037', '68', 20, 37, 'Dan sesungguhnya Kami telah memberi nikmat kepadamu pada kali yang lain,'),
(2386, '020:038', '68', 20, 38, 'yaitu ketika Kami mengilhamkan kepada ibumu suatu yang diilhamkan,'),
(2387, '020:039', '68', 20, 39, 'Yaitu: \"Letakkanlah ia (Musa) didalam peti, kemudian lemparkanlah ia ke sungai (Nil), maka pasti sungai itu membawanya ke tepi, supaya diambil oleh (Fir´aun) musuh-Ku dan musuhnya. Dan Aku telah melimpahkan kepadamu kasih sayang yang datang dari-Ku; dan supaya kamu diasuh di bawah pengawasan-Ku,'),
(2388, '020:040', '68', 20, 40, '(yaitu) ketika saudaramu yang perempuan berjalan, lalu ia berkata kepada (keluarga Fir´aun): \"Bolehkah saya menunjukkan kepadamu orang yang akan memeliharanya?\" Maka Kami mengembalikanmu kepada ibumu, agar senang hatinya dan tidak berduka cita. Dan kamu pernah membunuh seorang manusia, lalu Kami selamatkan kamu dari kesusahan dan Kami telah mencobamu dengan beberapa cobaan; maka kamu tinggal beberapa tahun diantara penduduk Madyan, kemudian kamu datang menurut waktu yang ditetapkan hai Musa,'),
(2389, '020:041', '68', 20, 41, 'dan Aku telah memilihmu untuk diri-Ku.'),
(2390, '020:042', '68', 20, 42, 'Pergilah kamu beserta saudaramu dengan membawa ayat-ayat-Ku, dan janganlah kamu berdua lalai dalam mengingat-Ku;'),
(2391, '020:043', '68', 20, 43, 'Pergilah kamu berdua kepada Fir´aun, sesungguhnya dia telah melampaui batas;'),
(2392, '020:044', '68', 20, 44, 'maka berbicaralah kamu berdua kepadanya dengan kata-kata yang lemah lembut, mudah-mudahan ia ingat atau takut\".'),
(2393, '020:045', '68', 20, 45, 'Berkatalah mereka berdua: \"Ya Tuhan kami, sesungguhnya kami khawatir bahwa ia segera menyiksa kami atau akan bertambah melampaui batas\".'),
(2394, '020:046', '68', 20, 46, 'Allah berfirman: \"Janganlah kamu berdua khawatir, sesungguhnya Aku beserta kamu berdua, Aku mendengar dan melihat\".'),
(2395, '020:047', '68', 20, 47, 'Maka datanglah kamu berdua kepadanya (Fir´aun) dan katakanlah: \"Sesungguhnya kami berdua adalah utusan Tuhanmu, maka lepaskanlah Bani Israil bersama kami dan janganlah kamu menyiksa mereka. Sesungguhnya kami telah datang kepadamu dengan membawa bukti (atas kerasulan kami) dari Tuhanmu. Dan keselamatan itu dilimpahkan kepada orang yang mengikuti petunjuk.'),
(2396, '020:048', '68', 20, 48, 'Sesungguhnya telah diwahyukan kepada kami bahwa siksa itu (ditimpakan) atas orang-orang yang mendustakan dan berpaling.'),
(2397, '020:049', '68', 20, 49, 'Berkata Fir´aun: \"Maka siapakah Tuhanmu berdua, hai Musa?.'),
(2398, '020:050', '68', 20, 50, 'Musa berkata: \"Tuhan kami ialah (Tuhan) yang telah memberikan kepada tiap-tiap sesuatu bentuk kejadiannya, kemudian memberinya petunjuk.'),
(2399, '020:051', '68', 20, 51, 'Berkata Fir´aun: \"Maka bagaimanakah keadaan umat-umat yang dahulu?\"'),
(2400, '020:052', '68', 20, 52, 'Musa menjawab: \"Pengetahuan tentang itu ada di sisi Tuhanku, di dalam sebuah kitab, Tuhan kami tidak akan salah dan tidak (pula) lupa;'),
(2401, '020:053', '68', 20, 53, 'Yang telah menjadikan bagimu bumi sebagai hamparan dan Yang telah menjadikan bagimu di bumi itu jalan-ja]an, dan menurunkan dari langit air hujan. Maka Kami tumbuhkan dengan air hujan itu berjenis-jenis dari tumbuh-tumbuhan yang bermacam-macam.'),
(2402, '020:054', '68', 20, 54, 'Makanlah dan gembalakanlah binatang-binatangmu. Sesungguhnya pada yang demikian itu, terdapat tanda-tanda kekuasaan Allah bagi orang-orang yang berakal.'),
(2403, '020:055', '68', 20, 55, 'Dari bumi (tanah) itulah Kami menjadikan kamu dan kepadanya Kami akan mengembalikan kamu dan daripadanya Kami akan mengeluarkan kamu pada kali yang lain,'),
(2404, '020:056', '68', 20, 56, 'Dan sesungguhnya Kami telah perlihatkan kepadanya (Fir´aun) tanda-tanda kekuasaan Kami semuanya maka ia mendustakan dan enggan (menerima kebenaran).'),
(2405, '020:057', '68', 20, 57, 'Berkata Fir´aun: \"Adakah kamu datang kepada kami untuk mengusir kami dari negeri kami (ini) dengan sihirmu, hai Musa?'),
(2406, '020:058', '68', 20, 58, 'Dan kamipun pasti akan mendatangkan (pula) kepadamu sihir semacam itu, maka buatlah suatu waktu untuk pertemuan antara kami dan kamu, yang kami tidak akan menyalahinya dan tidak (pula) kamu di suatu tempat yang pertengahan (letaknya).'),
(2407, '020:059', '68', 20, 59, 'Berkata Musa: \"Waktu untuk pertemuan (kami dengan) kamu itu ialah di hari raya dan hendaklah dikumpulkan manusia pada waktu matahari sepenggalahan naik\".'),
(2408, '020:060', '68', 20, 60, 'Maka Fir´aun meninggalkan (tempat itu), lalu mengatur tipu dayanya, kemudian dia datang.'),
(2409, '020:061', '68', 20, 61, 'Berkata Musa kepada mereka: \"Celakalah kamu, janganlah kamu mengada-adakan kedustaan terhadap Allah, maka Dia membinasakan kamu dengan siksa\". Dan sesungguhnya telah merugi orang yang mengada-adakan kedustaan.'),
(2410, '020:062', '68', 20, 62, 'Maka mereka berbantah-bantahan tentang urusan mereka di antara mereka dan mereka merahasiakan percakapan (mereka).'),
(2411, '020:063', '68', 20, 63, 'Mereka berkata: \"Sesungguhnya dua orang ini adalah benar-benar ahli sihir yang hendak mengusir kamu dari negeri kamu dengan sihirnya dan hendak melenyapkan kedudukan kamu yang utama.'),
(2412, '020:064', '68', 20, 64, 'Maka himpunkanlah segala daya (sihir) kamu sekalian, kemudian datanglah dengan berbaris. dan sesungguhnya beruntunglah oran yang menang pada hari ini.'),
(2413, '020:065', '68', 20, 65, '(Setelah mereka berkumpul) mereka berkata: \"Hai Musa (pilihlah), apakah kamu yang melemparkan (dahulu) atau kamikah orang yang mula-mula melemparkan?\"'),
(2414, '020:066', '68', 20, 66, 'Berkata Musa: \"Silahkan kamu sekalian melemparkan\". Maka tiba-tiba tali-tali dan tongkat-tongkat mereka, terbayang kepada Musa seakan-akan ia merayap cepat, lantaran sihir mereka.'),
(2415, '020:067', '68', 20, 67, 'Maka Musa merasa takut dalam hatinya.'),
(2416, '020:068', '68', 20, 68, 'Kami berkata: \"janganlah kamu takut, sesungguhnya kamulah yang paling unggul (menang).'),
(2417, '020:069', '68', 20, 69, 'Dan lemparkanlah apa yang ada ditangan kananmu, niscaya ia akan menelan apa yang mereka perbuat. \"Sesungguhnya apa yang mereka perbuat itu adalah tipu daya tukang sihir (belaka). Dan tidak akan menang tukang sihir itu, dari mana saja ia datang\".'),
(2418, '020:070', '68', 20, 70, 'Lalu tukang-tukang sihir itu tersungkur dengan bersujud, seraya berkata: \"Kami telah percaya kepada Tuhan Harun dan Musa\".'),
(2419, '020:071', '68', 20, 71, 'Berkata Fir´aun: \"Apakah kamu telah beriman kepadanya (Musa) sebelum aku memberi izin kepadamu sekalian. Sesungguhnya ia adalah pemimpinmu yang mengajarkan sihir kepadamu sekalian. Maka sesungguhnya aku akan memotong tangan dan kaki kamu sekalian dengan bersilang secara bertimbal balik, dan sesungguhnya aku akan menyalib kamu sekalian pada pangkal pohon kurma dan sesungguhnya kamu akan mengetahui siapa di antara kita yang lebih pedih dan lebih kekal siksanya\".'),
(2420, '020:072', '68', 20, 72, 'Mereka berkata: \"Kami sekali-kali tidak akan mengutamakan kamu daripada bukti-bukti yang nyata (mukjizat), yang telah datang kepada kami dan daripada Tuhan yang telah menciptakan kami; maka putuskanlah apa yang hendak kamu putuskan. Sesungguhnya kamu hanya akan dapat memutuskan pada kehidupan di dunia ini saja.'),
(2421, '020:073', '68', 20, 73, 'Sesungguhnya kami telah beriman kepada Tuhan kami, agar Dia mengampuni kesalahan-kesalahan kami dan sihir yang telah kamu paksakan kepada kami melakukannya. Dan Allah lebih baik (pahala-Nya) dan lebih kekal (azab-Nya)\".'),
(2422, '020:074', '68', 20, 74, 'Sesungguhnya barangsiapa datang kepada Tuhannya dalam keadaan berdosa, maka sesungguhnya baginya neraka Jahannam. Ia tidak mati di dalamnya dan tidak (pula) hidup.'),
(2423, '020:075', '68', 20, 75, 'Dan barangsiapa datang kepada Tuhannya dalam keadaan beriman, lagi sungguh-sungguh telah beramal saleh, maka mereka itulah orang-orang yang memperoleh tempat-tempat yang tinggi (mulia),'),
(2424, '020:076', '68', 20, 76, '(yaitu) surga ´Adn yang mengalir sungai-sungai di bawahnya, mereka kekal di dalamnya. Dan itu adalah balasan bagi orang yang bersih (dari kekafiran dan kemaksiatan).'),
(2425, '020:077', '68', 20, 77, 'Dan sesungguhnya telah Kami wahyukan kepada Musa: \"Pergilah kamu dengan hamba-hamba-Ku (Bani Israil) di malam hari, maka buatlah untuk mereka jalan yang kering dilaut itu, kamu tak usah khawatir akan tersusul dan tidak usah takut (akan tenggelam)\".'),
(2426, '020:078', '68', 20, 78, 'Maka Fir´aun dengan bala tentaranya mengejar mereka, lalu mereka ditutup oleh laut yang menenggelamkan mereka.'),
(2427, '020:079', '68', 20, 79, 'Dan Fir´aun telah menyesatkan kaumnya dan tidak memberi petunjuk.'),
(2428, '020:080', '68', 20, 80, 'Hai Bani Israil, sesungguhnya Kami telah menyelamatkan kamu sekalian dari musuhmu, dan Kami telah mengadakan perjanjian dengan kamu sekalian (untuk munajat) di sebelah kanan gunung itu dan Kami telah menurunkan kepada kamu sekalian manna dan salwa.'),
(2429, '020:081', '68', 20, 81, 'Makanlah di antara rezeki yang baik yang telah Kami berikan kepadamu, dan janganlah melampaui batas padanya, yang menyebabkan kemurkaan-Ku menimpamu. Dan barangsiapa ditimpa oleh kemurkaan-Ku, maka sesungguhnya binasalah ia.'),
(2430, '020:082', '68', 20, 82, 'Dan sesungguhnya Aku Maha Pengampun bagi orang yang bertaubat, beriman, beramal saleh, kemudian tetap di jalan yang benar.'),
(2431, '020:083', '68', 20, 83, 'Mengapa kamu datang lebih cepat daripada kaummu, hai Musa?'),
(2432, '020:084', '68', 20, 84, 'Berkata, Musa: \"Itulah mereka sedang menyusuli aku dan aku bersegera kepada-Mu. Ya Tuhanku, agar supaya Engkau ridha (kepadaku)\".'),
(2433, '020:085', '68', 20, 85, 'Allah berfirman: \"Maka sesungguhnya Kami telah menguji kaummu sesudah kamu tinggalkan, dan mereka telah disesatkan oleh Samiri.'),
(2434, '020:086', '68', 20, 86, 'Kemudian Musa kembali kepada kaumnya dengan marah dan bersedih hati. Berkata Musa: \"Hai kaumku, bukankah Tuhanmu telah menjanjikan kepadamu suatu janji yang baik? Maka apakah terasa lama masa yang berlalu itu bagimu atau kamu menghendaki agar kemurkaan dari Tuhanmu menimpamu, dan kamu melanggar perjanjianmu dengan aku?\".'),
(2435, '020:087', '68', 20, 87, 'Mereka berkata: \"Kami sekali-kali tidak melanggar perjanjianmu dengan kemauan kami sendiri, tetapi kami disuruh membawa beban-beban dari perhiasan kaum itu, maka kami telah melemparkannya, dan demikian pula Samiri melemparkannya\",'),
(2436, '020:088', '68', 20, 88, 'kemudian Samiri mengeluarkan untuk mereka (dari lobang itu) anak lembu yang bertubuh dan bersuara, maka mereka berkata: \"Inilah Tuhanmu dan Tuhan Musa, tetapi Musa telah lupa\".'),
(2437, '020:089', '68', 20, 89, 'Maka apakah mereka tidak memperhatikan bahwa patung anak lembu itu tidak dapat memberi jawaban kepada mereka, dan tidak dapat memberi kemudharatan kepada mereka dan tidak (pula) kemanfaatan?'),
(2438, '020:090', '68', 20, 90, 'Dan sesungguhnya Harun telah berkata kepada mereka sebelumnya: \"Hai kaumku, sesungguhnya kamu hanya diberi cobaan dengan anak lembu. itu dan sesungguhnya Tuhanmu ialah (Tuhan) Yang Maha Pemurah, maka ikutilah aku dan taatilah perintahku\".'),
(2439, '020:091', '68', 20, 91, 'Mereka menjawab: \"Kami akan tetap menyembah patung anak lembu ini, hingga Musa kembali kepada kami\".'),
(2440, '020:092', '68', 20, 92, 'Berkata Musa: \"Hai Harun, apa yang menghalangi kamu ketika kamu melihat mereka telah sesat,'),
(2441, '020:093', '68', 20, 93, '(sehingga) kamu tidak mengikuti aku? Maka apakah kamu telah (sengaja) mendurhakai perintahku?\"'),
(2442, '020:094', '68', 20, 94, 'Harun menjawab´ \"Hai putera ibuku, janganlah kamu pegang janggutku dan jangan (pula) kepalaku; sesungguhnya aku khawatir bahwa kamu akan berkata (kepadaku): \"Kamu telah memecah antara Bani Israil dan kamu tidak memelihara amanatku\".'),
(2443, '020:095', '68', 20, 95, 'Berkata Musa: \"Apakah yang mendorongmu (berbuat demikian) hai Samiri?\"'),
(2444, '020:096', '68', 20, 96, 'Samiri menjawab: \"Aku mengetahui sesuatu yang mereka tidak mengetahuinya, maka aku ambil segenggam dari jejak rasul lalu aku melemparkannya, dan demikianlah nafsuku membujukku\".'),
(2445, '020:097', '68', 20, 97, 'Berkata Musa: \"Pergilah kamu, maka sesungguhnya bagimu di dalam kehidupan di dunia ini (hanya dapat) mengatakan: \"Janganlah menyentuh (aku)\". Dan sesungguhnya bagimu hukuman (di akhirat) yang kamu sekali-kali tidak dapat menghindarinya, dan lihatlah tuhanmu itu yang kamu tetap menyembahnya. Sesungguhnya kami akan membakarnya, kemudian kami sungguh-sungguh akan menghamburkannya ke dalam laut (berupa abu yang berserakan).'),
(2446, '020:098', '68', 20, 98, 'Sesungguhnya Tuhanmu hanyalah Allah, yang tidak ada Tuhan selain Dia. Pengetahuan-Nya meliputi segala sesuatu\".'),
(2447, '020:099', '68', 20, 99, 'Demikianlah kami kisahkan kepadamu (Muhammad) sebagian kisah umat yang telah lalu, dan sesungguhnya telah Kami berikan kepadamu dari sisi Kami suatu peringatan (Al Quran).'),
(2448, '020:100', '68', 20, 100, 'Barangsiapa berpaling dari pada Al qur´an maka sesungguhnya ia akan memikul dosa yang besar di hari kiamat,'),
(2449, '020:101', '68', 20, 101, 'mereka kekal di dalam keadaan itu. Dan amat buruklah dosa itu sebagai beban bagi mereka di hari kiamat,'),
(2450, '020:102', '68', 20, 102, '(yaitu) di hari (yang di waktu itu) ditiup sangkakala dan Kami akan mengumpulkan pada hari itu orang-orang yang berdosa dengan muka yang biru muram;'),
(2451, '020:103', '68', 20, 103, 'mereka berbisik-bisik di antara mereka: \"Kamu tidak berdiam (di dunia) melainkan hanyalah sepuluh (hari)\"'),
(2452, '020:104', '68', 20, 104, 'Kami lebih mengetahui apa yang mereka katakan, ketika berkata orang yang paling lurus jalannya di antara mereka: \"Kamu tidak berdiam (di dunia), melainkan hanyalah sehari saja\".'),
(2453, '020:105', '68', 20, 105, 'Dan mereka bertanya kepadamu tentang gunung-gunung, maka katakanlah: \"Tuhanku akan menghancurkannya (di hari kiamat) sehancur-hancurnya,'),
(2454, '020:106', '68', 20, 106, 'maka Dia akan menjadikan (bekas) gunung-gunung itu datar sama sekali,'),
(2455, '020:107', '68', 20, 107, 'tidak ada sedikitpun kamu lihat padanya tempat yang rendah dan yang tinggi-tinggi.'),
(2456, '020:108', '68', 20, 108, 'Pada hari itu manusia mengikuti (menuju kepada suara) penyeru dengan tidak berbelok-belok; dan merendahlah semua suara kepada Tuhan Yang Maha Pemurah, maka kamu tidak mendengar kecuali bisikan saja.'),
(2457, '020:109', '68', 20, 109, 'Pada hari itu tidak berguna syafa´at, kecuali (syafa´at) orang yang Allah Maha Pemurah telah memberi izin kepadanya, dan Dia telah meridhai perkataannya.'),
(2458, '020:110', '68', 20, 110, 'Dia mengetahui apa yang ada di hadapan mereka dan apa yang ada di belakang mereka, sedang ilmu mereka tidak dapat meliputi ilmu-Nya.'),
(2459, '020:111', '68', 20, 111, 'Dan tunduklah semua muka (dengan berendah diri) kepada Tuhan Yang Hidup Kekal lagi senantiasa mengurus (makhluk-Nya). Dan sesungguhnya telah merugilah orang yang melakukan kezaliman.'),
(2460, '020:112', '68', 20, 112, 'Dan barangsiapa mengerjakan amal-amal yang saleh dan ia dalam keadaan beriman, maka ia tidak khawatir akan perlakuan yang tidak adil (terhadapnya) dan tidak (pula) akan pengurangan haknya.'),
(2461, '020:113', '68', 20, 113, 'Dan demikianlah Kami menurunkan Al Quran dalam bahasa Arab, dan Kami telah menerangkan dengan berulang kali, di dalamnya sebahagian dari ancaman, agar mereka bertakwa atau (agar) Al Quran itu menimbulkan pengajaran bagi mereka.'),
(2462, '020:114', '68', 20, 114, 'Maka Maha Tinggi Allah Raja Yang sebenar-benarnya, dan janganlah kamu tergesa-gesa membaca Al qur´an sebelum disempurnakan mewahyukannya kepadamu, dan katakanlah: \"Ya Tuhanku, tambahkanlah kepadaku ilmu pengetahuan\".'),
(2463, '020:115', '68', 20, 115, 'Dan sesungguhnya telah Kami perintahkan kepada Adam dahulu, maka ia lupa (akan perintah itu), dan tidak Kami dapati padanya kemauan yang kuat.'),
(2464, '020:116', '68', 20, 116, 'Dan (ingatlah) ketika Kami berkata kepada malaikat: \"Sujudlah kamu kepada Adam\", maka mereka sujud kecuali iblis. Ia membangkang.'),
(2465, '020:117', '68', 20, 117, 'Maka Kami berkata: \"Hai Adam, sesungguhnya ini (iblis) adalah musuh bagimu dan bagi isterimu, maka sekali-kali janganlah sampai ia mengeluarkan kamu berdua dari surga, yang menyebabkan kamu menjadi celaka.'),
(2466, '020:118', '68', 20, 118, 'Sesungguhnya kamu tidak akan kelaparan di dalamnya dan tidak akan telanjang,'),
(2467, '020:119', '68', 20, 119, 'dan sesungguhnya kamu tidak akan merasa dahaga dan tidak (pula) akan ditimpa panas matahari di dalamnya\".'),
(2468, '020:120', '68', 20, 120, 'Kemudian syaitan membisikkan pikiran jahat kepadanya, dengan berkata: \"Hai Adam, maukah saya tunjukkan kepada kamu pohon khuldi dan kerajaan yang tidak akan binasa?\"'),
(2469, '020:121', '68', 20, 121, 'Maka keduanya memakan dari buah pohon itu, lalu nampaklah bagi keduanya aurat-auratnya dan mulailah keduanya menutupinya dengan daun-daun (yang ada di) surga, dan durhakalah Adam kepada Tuhan dan sesatlah ia.'),
(2470, '020:122', '68', 20, 122, 'Kemudian Tuhannya memilihnya maka Dia menerima taubatnya dan memberinya petunjuk.'),
(2471, '020:123', '68', 20, 123, 'Allah berfirman: \"Turunlah kamu berdua dari surga bersama-sama, sebagian kamu menjadi musuh bagi sebagian yang lain. Maka jika datang kepadamu petunjuk daripada-Ku, lalu barangsiapa yang mengikut petunjuk-Ku, ia tidak akan sesat dan tidak akan celaka.'),
(2472, '020:124', '68', 20, 124, 'Dan barangsiapa berpaling dari peringatan-Ku, maka sesungguhnya baginya penghidupan yang sempit, dan Kami akan menghimpunkannya pada hari kiamat dalam keadaan buta\".'),
(2473, '020:125', '68', 20, 125, 'Berkatalah ia: \"Ya Tuhanku, mengapa Engkau menghimpunkan aku dalam keadaan buta, padahal aku dahulunya adalah seorang yang melihat?\"'),
(2474, '020:126', '68', 20, 126, 'Allah berfirman: \"Demikianlah, telah datang kepadamu ayat-ayat Kami, maka kamu melupakannya, dan begitu (pula) pada hari ini kamupun dilupakan\".'),
(2475, '020:127', '68', 20, 127, 'Dan demikianlah Kami membalas orang yang melampaui batas dan tidak percaya kepada ayat-ayat Tuhannya. Dan sesungguhnya azab di akhirat itu lebih berat dan lebih kekal.'),
(2476, '020:128', '68', 20, 128, 'Maka tidakkah menjadi petunjuk bagi mereka (kaum musyrikin) berapa banyaknya Kami membinasakan umat-umat sebelum mereka, padahal mereka berjalan (di bekas-bekas) tempat tinggal umat-umat itu? Sesungguhnya pada yang demikian itu terdapat tanda-tanda bagi orang yang berakal.'),
(2477, '020:129', '68', 20, 129, 'Dan sekiranya tidak ada suatu ketetapan dari Allah yang telah terdahulu atau tidak ada ajal yang telah ditentukan, pasti (azab itu) menimpa mereka.'),
(2478, '020:130', '68', 20, 130, 'Maka sabarlah kamu atas apa yang mereka katakan, dan bertasbihlah dengan memuji Tuhanmu, sebelum terbit matahari dan sebelum terbenamnya dan bertasbih pulalah pada waktu-waktu di malam hari dan pada waktu-waktu di siang hari, supaya kamu merasa senang,'),
(2479, '020:131', '68', 20, 131, 'Dan janganlah kamu tujukan kedua matamu kepada apa yang telah Kami berikan kepada golongan-golongan dari mereka, sebagai bunga kehidupan dunia untuk Kami cobai mereka dengannya. Dan karunia Tuhan kamu adalah lebih baik dan lebih kekal.'),
(2480, '020:132', '68', 20, 132, 'Dan perintahkanlah kepada keluargamu mendirikan shalat dan bersabarlah kamu dalam mengerjakannya. Kami tidak meminta rezeki kepadamu, Kamilah yang memberi rezeki kepadamu. Dan akibat (yang baik) itu adalah bagi orang yang bertakwa.'),
(2481, '020:133', '68', 20, 133, 'Dan mereka berkata: \"Mengapa ia tidak membawa bukti kepada kami dari Tuhannya?\" Dan apakah belum datang kepada mereka bukti yang nyata dari apa yang tersebut di dalam kitab-kitab yang dahulu?'),
(2482, '020:134', '68', 20, 134, 'Dan sekiranya Kami binasakan mereka dengan suatu azab sebelum Al Quran itu (diturunkan), tentulah mereka berkata: \"Ya Tuhan kami, mengapa tidak Engkau utus seorang rasul kepada kami, lalu kami mengikuti ayat-ayat Engkau sebelum kami menjadi hina dan rendah?\"'),
(2483, '020:135', '68', 20, 135, 'Katakanlah: \"Masing-masing (kita) menanti, maka nantikanlah oleh kamu sekalian! Maka kamu kelak akan mengetahui, siapa yang menempuh jalan yang lurus dan siapa yang telah mendapat petunjuk\".'),
(2484, '021:001', '68', 21, 1, 'Telah dekat kepada manusia hari menghisab segala amalan mereka, sedang mereka berada dalam kelalaian lagi berpaling (daripadanya).'),
(2485, '021:002', '68', 21, 2, 'Tidak datang kepada mereka suatu ayat Al Quran pun yang baru (di-turunkan) dari Tuhan mereka, melainkan mereka mendengarnya, sedang mereka bermain-main,'),
(2486, '021:003', '68', 21, 3, '(lagi) hati mereka dalam keadaan lalai. Dan mereka yang zalim itu merahasiakan pembicaraan mereka: \"Orang ini tidak lain hanyalah seorang manusia (jua) seperti kamu, maka apakah kamu menerima sihir itu, padahal kamu menyaksikannya?\"'),
(2487, '021:004', '68', 21, 4, 'Berkatalah Muhammad (kepada mereka): \"Tuhanku mengetahui semua perkataan di langit dan di bumi dan Dialah Yang Maha Mendengar lagi Maha Mengetahui\".'),
(2488, '021:005', '68', 21, 5, 'Bahkan mereka berkata (pula): \"(Al Quran itu adalah) mimpi-mimpi yang kalut, malah diada-adakannya, bahkan dia sendiri seorang penyair, maka hendaknya ia mendatangkan kepada kita suatu mukjizat, sebagai-mana rasul-rasul yang telah lalu di-utus\".'),
(2489, '021:006', '68', 21, 6, 'Tidak ada (penduduk) suatu negeripun yang beriman yang Kami telah membinasakannya sebeIum mereka; maka apakah mereka akan beriman?'),
(2490, '021:007', '68', 21, 7, 'Kami tiada mengutus rasul rasul sebelum kamu (Muhammad), melainkan beberapa orang-laki-laki yang Kami beri wahyu kepada mereka, maka tanyakanlah olehmu kepada orang-orang yang berilmu, jika kamu tiada mengetahui.'),
(2491, '021:008', '68', 21, 8, 'Dan tidaklah Kami jadikan mereka tubuh-tubuh yang tiada memakan makanan, dan tidak (pula) mereka itu orang-orang yang kekal.'),
(2492, '021:009', '68', 21, 9, 'Kemudian Kami tepati janji (yang telah Kami janjikan) kepada mereka. Maka Kami selamatkan mereka dan orang-orang yang Kami kehendaki dan Kami binasakan orang-orang yang melampaui batas.'),
(2493, '021:010', '68', 21, 10, 'Sesungguhnya telah Kami turunkan kepada kamu sebuah kitab yang di dalamnya terdapat sebab-sebab kemuliaan bagimu. Maka apakah kamu tiada memahaminya?'),
(2494, '021:011', '68', 21, 11, 'Dan berapa banyaknya (penduduk) negeri yang zalim yang teIah Kami binasakan, dan Kami adakan sesudah mereka itu kaum yang lain (sebagai penggantinya).'),
(2495, '021:012', '68', 21, 12, 'Maka tatkala mereka merasakan azab Kami, tiba-tiba mereka melarikan diri dari negerinya.'),
(2496, '021:013', '68', 21, 13, 'Janganlah kamu lari tergesa-gesa; kembalilah kamu kepada nikmat yang telah kamu rasakan dan kepada tempat-tempat kediamanmu (yang baik), supaya kamu ditanya.'),
(2497, '021:014', '68', 21, 14, 'Mereka berkata: \"Aduhai, celaka kami, sesungguhnya kami adalah orang-orang yang zaIim\".'),
(2498, '021:015', '68', 21, 15, 'Maka tetaplah demikian keluhan mereka, sehingga Kami jadikan mereka sebagai tanaman yang telah dituai, yang tidak dapat hidup lagi.'),
(2499, '021:016', '68', 21, 16, 'Dan tidaklah Kami ciptakan Iangit dan bumi dan segala yang ada di antara keduanya dengan bermain-main.'),
(2500, '021:017', '68', 21, 17, 'Sekiranya Kami hendak membuat sesuatu permainan, (isteri dan anak), tentulah Kami membuatnya dari sisi Kami. Jika Kami menghendaki berbuat demikian, (tentulah Kami telah melakukannya).'),
(2501, '021:018', '68', 21, 18, 'Sebenarya Kami melontarkan yang hak kepada yang batil lalu yang hak itu menghancurkannya, maka dengan serta merta yang batil itu lenyap. Dan kecelakaanlah bagimu disebabkan kamu mensifati (Allah dengan sifat-sifat yang tidak layak bagi-Nya).'),
(2502, '021:019', '68', 21, 19, 'Dan kepunyaan-Nya-lah segala yang di langit dan di bumi. Dan malaikat-malaikat yang di sisi-Nya, mereka tiada mempunyai rasa angkuh untuk menyembah-Nya dan tiada (pula) merasa letih.'),
(2503, '021:020', '68', 21, 20, 'Mereka selalu bertasbih malam dan siang tiada henti-hentinya.'),
(2504, '021:021', '68', 21, 21, 'Apakah mereka mengambil tuhan-tuhan dari bumi, yang dapat menghidupkan (orang-orang mati)?'),
(2505, '021:022', '68', 21, 22, 'Sekiranya ada di langit dan di bumi tuhan-tuhan selain Allah, tentulah keduanya itu telah rusak binasa. Maka Maha Suci Allah yang mempunyai ´Arsy daripada apa yang mereka sifatkan.'),
(2506, '021:023', '68', 21, 23, 'Dia tidak ditanya tentang apa yang diperbuat-Nya dan merekalah yang akan ditanyai.'),
(2507, '021:024', '68', 21, 24, 'Apakah mereka mengambil tuhan-tuhan selain-Nya? Katakanlah: \"Unjukkanlah hujjahmu! (Al Quran) ini adalah peringatan bagi orang-orang yang bersamaku, dan peringatan bagi orang-orang yang sebelumku\". Sebenarnya kebanyakan mereka tiada mengetahui yang hak, karena itu mereka berpaling.'),
(2508, '021:025', '68', 21, 25, 'Dan Kami tidak mengutus seorang rasulpun sebelum kamu melainkan Kami wahyukan kepadanya: \"Bahwasanya tidak ada Tuhan (yang hak) melainkan Aku, maka sembahlah olehmu sekalian akan Aku\".'),
(2509, '021:026', '68', 21, 26, 'Dan mereka berkata: \"Tuhan Yang Maha Pemurah telah mengambil (mempunyai) anak\", Maha Suci Allah. Sebenarnya (malaikat-malaikat itu), adalah hamba-hamba yang dimuliakan,'),
(2510, '021:027', '68', 21, 27, 'mereka itu tidak mendahului-Nya dengan perkataan dan mereka mengerjakan perintah-perintah-Nya.'),
(2511, '021:028', '68', 21, 28, 'Allah mengetahui segala sesuatu yang dihadapan mereka (malaikat) dan yang di belakang mereka, dan mereka tiada memberi syafa´at melainkan kepada orang yang diridhai Allah, dan mereka itu selalu berhati-hati karena takut kepada-Nya.'),
(2512, '021:029', '68', 21, 29, 'Dan barangsiapa di antara mereka, mengatakan: \"Sesungguhnya Aku adalah tuhan selain daripada Allah\", maka orang itu Kami beri balasan dengan Jahannam, demikian Kami memberikan pembalasan kepada orang-orang zalim.'),
(2513, '021:030', '68', 21, 30, 'Dan apakah orang-orang yang kafir tidak mengetahui bahwasanya langit dan bumi itu keduanya dahulu adalah suatu yang padu, kemudian Kami pisahkan antara keduanya. Dan dari air Kami jadikan segala sesuatu yang hidup. Maka mengapakah mereka tiada juga beriman?'),
(2514, '021:031', '68', 21, 31, 'Dan telah Kami jadikan di bumi ini gunung-gunung yang kokoh supaya bumi itu (tidak) goncang bersama mereka dan telah Kami jadikan (pula) di bumi itu jalan-jalan yang luas, agar mereka mendapat petunjuk.'),
(2515, '021:032', '68', 21, 32, 'Dan Kami menjadikan langit itu sebagai atap yang terpelihara, sedang mereka berpaling dari segala tanda-tanda (kekuasaan Allah) yang terdapat padanya.'),
(2516, '021:033', '68', 21, 33, 'Dan Dialah yang telah menciptakan malam dan siang, matahari dan bulan. Masing-masing dari keduanya itu beredar di dalam garis edarnya.'),
(2517, '021:034', '68', 21, 34, 'Kami tidak menjadikan hidup abadi bagi seorang manusiapun sebelum kamu (Muhammad); maka jikalau kamu mati, apakah mereka akan kekal?'),
(2518, '021:035', '68', 21, 35, 'Tiap-tiap yang berjiwa akan merasakan mati. Kami akan menguji kamu dengan keburukan dan kebaikan sebagai cobaan (yang sebenar-benarnya). Dan hanya kepada Kamilah kamu dikembalikan.'),
(2519, '021:036', '68', 21, 36, 'Dan apahila orang-orang kafir itu melihat kamu, mereka hanya membuat kamu menjadi olok-olok. (Mereka mengatakan): \"Apakah ini orang yang mencela tuhan-tuhan-mu?\", padahal mereka adaIah orang-orang yang ingkar mengingat Allah Yang Maha Pemurah.'),
(2520, '021:037', '68', 21, 37, 'Manusia telah dijadikan (bertabiat) tergesa-gesa. Kelak akan Aku perIihatkan kepadamu tanda-tanda azab-Ku. Maka janganlah kamu minta kepada-Ku mendatangkannya dengan segera.'),
(2521, '021:038', '68', 21, 38, 'Mereka berkata: \"Kapankah janji itu akan datang, jika kamu sekaIian adalah orang-orang yang benar?\"'),
(2522, '021:039', '68', 21, 39, 'Andaikata orang-orang kafir itu mengetahui, waktu (di mana) mereka itu tidak mampu mengelakkan api neraka dari muka mereka dan (tidak pula) dari punggung mereka, sedang mereka (tidak pula) mendapat pertolongan, (tentulah mereka tiada meminta disegerakan).'),
(2523, '021:040', '68', 21, 40, 'Sebenarnya (azab) itu akan datang kepada mereka dengan sekonyong-konyong lalu membuat mereka menjadi panik, maka mereka tidak sanggup menolaknya dan tidak (pula) mereka diberi tangguh.'),
(2524, '021:041', '68', 21, 41, 'Dan sungguh telah diperolok-olokkan beberapa orang rasul sebelum kamu maka turunlah kepada orang yang mencemoohkan rasul-rasul itu azab yang selalu mereka perolok-olokkan.'),
(2525, '021:042', '68', 21, 42, 'Katakanlah: \"Siapakah yang dapat memelihara kamu di waktu malam dan siang hari dari (azab Allah) Yang Maha Pemurah?\" Sebenarnya mereka adalah orang-orang yang berpaling dari mengingati Tuhan mereka.'),
(2526, '021:043', '68', 21, 43, 'Atau adakah mereka mempunyai tuhan-tuhan yang dapat memelihara mereka dari (azab) Kami. Tuhan-tuhan itu tidak sanggup menolong diri mereka sendiri dan tidak (pula) mereka dilindungi dari (azab) Kami itu?'),
(2527, '021:044', '68', 21, 44, 'Sebenarnya Kami telah memberi mereka dan bapak-bapak mereka kenikmatan (hidup di dunia) hingga panjanglah umur mereka. Maka apakah mereka tidak melihat bahwasanya Kami mendatangi negeri (orang kafir), lalu Kami kurangi luasnya dari segala penjurunya. Maka apakah mereka yang menang?'),
(2528, '021:045', '68', 21, 45, 'Katakanlah (hai Muhammad): \"Sesungguhnya aku hanya memberi peringatan kepada kamu sekalian dengan wahyu dan tiadalah orang-orang yang tuli mendengar seruan, apabila mereka diberi peringatan\"'),
(2529, '021:046', '68', 21, 46, 'Dan sesungguhnya, jika mereka ditimpa sedikit saja dari azab Tuhan-mu, pastilah mereka berkata: \"Aduhai, celakalah kami, bahwasanya kami adalah orang yang menganiaya diri sendiri\".'),
(2530, '021:047', '68', 21, 47, 'Kami akan memasang timbangan yang tepat pada hari kiamat, maka tiadalah dirugikan seseorang barang sedikitpun. Dan jika (amalan itu) hanya seberat biji sawipun pasti Kami mendatangkan (pahala)nya. Dan cukuplah Kami sebagai pembuat perhitungan.'),
(2531, '021:048', '68', 21, 48, 'Dan sesungguhnya telah Kami berikan kepada Musa dan Harun Kitab Taurat dan penerangan serta pengajaran bagi orang-orang yang bertakwa.'),
(2532, '021:049', '68', 21, 49, '(yaitu) orang-orang yang takut akan (azab) Tuhan mereka, sedang mereka tidak melihat-Nya, dan mereka merasa takut akan (tibanya) hari kiamat.'),
(2533, '021:050', '68', 21, 50, 'Dan Al Quran ini adalah suatu kitab (peringatan) yang mempunyai berkah yang telah Kami turunkan. Maka mengapakah kamu mengingkarinya?'),
(2534, '021:051', '68', 21, 51, 'Dan sesungguhnya telah Kami anugerahkan kepada Ibrahim hidayah kebenaran sebelum (Musa dan Harun), dan adalah Kami mengetahui (keadaan)nya.'),
(2535, '021:052', '68', 21, 52, '(Ingatlah), ketika Ibrahim berkata kepada bapaknya dan kaumnya: \"Patung-patung apakah ini yang kamu tekun beribadat kepadanya?\"'),
(2536, '021:053', '68', 21, 53, 'Mereka menjawab: \"Kami mendapati bapak-bapak kami menyembahnya\".'),
(2537, '021:054', '68', 21, 54, 'Ibrahim berkata: \"Sesungguhnya kamu dan bapak-bapakmu berada dalam kesesatan yang nyata\".'),
(2538, '021:055', '68', 21, 55, 'Mereka menjawab: \"Apakah kamu datang kepada kami dengan sungguh-sungguh ataukah kamu termasuk orang-orang yang bermain-main?\"'),
(2539, '021:056', '68', 21, 56, 'Ibrahim berkata: \"Sebenarnya Tuhan kamu ialah Tuhan langit dan bumi yang telah menciptakannya: dan aku termasuk orang-orang yang dapat memberikan bukti atas yang demikian itu\".'),
(2540, '021:057', '68', 21, 57, 'Demi Allah, sesungguhnya aku akan melakukan tipu daya terhadap berhala-berhalamu sesudah kamu pergi meninggalkannya.'),
(2541, '021:058', '68', 21, 58, 'Maka Ibrahim membuat berhala-berhala itu hancur berpotong-potong, kecuali yang terbesar (induk) dari patung-patung yang lain; agar mereka kembali (untuk bertanya) kepadanya.'),
(2542, '021:059', '68', 21, 59, 'Mereka berkata: \"Siapakah yang melakukan perbuatan ini terhadap tuhan-tuhan kami, sesungguhnya dia termasuk orang-orang yang zalim\".'),
(2543, '021:060', '68', 21, 60, 'Mereka berkata: \"Kami dengar ada seorang pemuda yang mencela berhala-berhala ini yang bernama Ibrahim\".'),
(2544, '021:061', '68', 21, 61, 'Mereka berkata: \"(Kalau demikian) bawalah dia dengan cara yang dapat dilihat orang banyak, agar mereka menyaksikan\".'),
(2545, '021:062', '68', 21, 62, 'Mereka bertanya: \"Apakah kamu, yang melakukan perbuatan ini terhadap tuhan-tuhan kami, hai Ibrahim?\"'),
(2546, '021:063', '68', 21, 63, 'Ibrahim menjawab: \"Sebenarnya patung yang besar itulah yang melakukannya, maka tanyakanlah kepada berhala itu, jika mereka dapat berbicara\".'),
(2547, '021:064', '68', 21, 64, 'Maka mereka telah kembali kepada kesadaran dan lalu berkata: \"Sesungguhnya kamu sekalian adalah orang-orang yang menganiaya (diri sendiri)\",'),
(2548, '021:065', '68', 21, 65, 'kemudian kepala mereka jadi tertunduk (lalu berkata): \"Sesungguhnya kamu (hai Ibrahim) telah mengetahui bahwa berhala-berhala itu tidak dapat berbicara\".'),
(2549, '021:066', '68', 21, 66, 'Ibrahim berkata: Maka mengapakah kamu menyembah selain Allah sesuatu yang tidak dapat memberi manfaat sedikitpun dan tidak (pula) memberi mudharat kepada kamu?\"'),
(2550, '021:067', '68', 21, 67, 'Ah (celakalah) kamu dan apa yang kamu sembah selain Allah. Maka apakah kamu tidak memahami?'),
(2551, '021:068', '68', 21, 68, 'Mereka berkata: \"Bakarlah dia dan bantulah tuhan-tuhan kamu, jika kamu benar-benar hendak bertindak\".'),
(2552, '021:069', '68', 21, 69, 'Kami berfirman: \"Hai api menjadi dinginlah, dan menjadi keselamatanlah bagi Ibrahim\",'),
(2553, '021:070', '68', 21, 70, 'mereka hendak berbuat makar terhadap Ibrahim, maka Kami menjadikan mereka itu orang-orang yang paling merugi.'),
(2554, '021:071', '68', 21, 71, 'Dan Kami seIamatkan Ibrahim dan Luth ke sebuah negeri yang Kami telah memberkahinya untuk sekalian manusia.'),
(2555, '021:072', '68', 21, 72, 'Dan Kami telah memberikan kepada-nya (Ibrahim) lshak dan Ya´qub, sebagai suatu anugerah (daripada Kami). Dan masing-masingnya Kami jadikan orang-orang yang saleh'),
(2556, '021:073', '68', 21, 73, 'Kami telah menjadikan mereka itu sebagai pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami dan telah Kami wahyukan kepada, mereka mengerjakan kebajikan, mendirikan sembahyang, menunaikan zakat, dan hanya kepada Kamilah mereka selalu menyembah,'),
(2557, '021:074', '68', 21, 74, 'dan kepada Luth, Kami telah berikan hikmah dan ilmu, dan telah Kami selamatkan dia dari (azab yang telah menimpa penduduk) kota yang mengerjakan perbuatan keji. Sesungguhnya mereka adalah kaum yang jahat lagi fasik,'),
(2558, '021:075', '68', 21, 75, 'dan Kami masukkan dia ke dalam rahmat Kami; karena sesungguhnya dia termasuk orang-orang yang saleh.'),
(2559, '021:076', '68', 21, 76, 'Dan (ingatlah kisah) Nuh, sebelum itu ketika dia berdoa, dan Kami memperkenankan doanya, lalu Kami selamatkan dia beserta keluarganya dari bencana yang besar.'),
(2560, '021:077', '68', 21, 77, 'Dan Kami telah menolongnya dari kaum yang telah mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang jahat, maka Kami tenggelamkan mereka semuanya.'),
(2561, '021:078', '68', 21, 78, 'Dan (ingatlah kisah) Daud dan Sulaiman, di waktu keduanya memberikan keputusan mengenai tanaman, karena tanaman itu dirusak oleh kambing-kambing kepunyaan kaumnya. Dan adalah Kami menyaksikan keputusan yang diberikan oleh mereka itu,'),
(2562, '021:079', '68', 21, 79, 'maka Kami telah memberikan pengertian kepada Sulaiman tentang hukum (yang lebih tepat); dan kepada masing-masing mereka telah Kami berikan hikmah dan ilmu dan telah Kami tundukkan gunung-gunung dan burung-burung, semua bertasbih bersama Daud. Dan kamilah yang melakukannya.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(2563, '021:080', '68', 21, 80, 'Dan telah Kami ajarkan kepada Daud membuat baju besi untuk kamu, guna memelihara kamu dalam peperanganmu; Maka hendaklah kamu bersyukur (kepada Allah).'),
(2564, '021:081', '68', 21, 81, 'Dan (telah Kami tundukkan) untuk Sulaiman angin yang sangat kencang tiupannya yang berhembus dengan perintahnya ke negeri yang kami telah memberkatinya. Dan adalah Kami Maha Mengetahui segala sesuatu.'),
(2565, '021:082', '68', 21, 82, 'Dan Kami telah tundukkan (pula kepada Sulaiman) segolongan syaitan-syaitan yang menyelam (ke dalam laut) untuknya dan mengerjakan pekerjaan selain daripada itu, dan adalah Kami memelihara mereka itu,'),
(2566, '021:083', '68', 21, 83, 'dan (ingatlah kisah) Ayub, ketika ia menyeru Tuhannya: \"(Ya Tuhanku), sesungguhnya aku telah ditimpa penyakit dan Engkau adalah Tuhan Yang Maha Penyayang di antara semua penyayang\".'),
(2567, '021:084', '68', 21, 84, 'Maka Kamipun memperkenankan seruannya itu, lalu Kami lenyapkan penyakit yang ada padanya dan Kami kembalikan keluarganya kepadanya, dan Kami lipat gandakan bilangan mereka, sebagai suatu rahmat dari sisi Kami dan untuk menjadi peringatan bagi semua yang menyembah Allah.'),
(2568, '021:085', '68', 21, 85, 'Dan (ingatlah kisah) Ismail, Idris dan Dzulkifli. Semua mereka termasuk orang-orang yang sabar.'),
(2569, '021:086', '68', 21, 86, 'Kami telah memasukkan mereka kedalam rahmat Kami. Sesungguhnya mereka termasuk orang-orang yang saleh.'),
(2570, '021:087', '68', 21, 87, 'Dan (ingatlah kisah) Dzun Nun (Yunus), ketika ia pergi dalam keadaan marah, lalu ia menyangka bahwa Kami tidak akan mempersempitnya (menyulitkannya), maka ia menyeru dalam keadaan yang sangat gelap: \"Bahwa tidak ada Tuhan selain Engkau. Maha Suci Engkau, sesungguhnya aku adalah termasuk orang-orang yang zalim\".'),
(2571, '021:088', '68', 21, 88, 'Maka Kami telah memperkenankan doanya dan menyelamatkannya dari pada kedukaan. Dan demikianlah Kami selamatkan orang-orang yang beriman.'),
(2572, '021:089', '68', 21, 89, 'Dan (ingatlah kisah) Zakaria, tatkala ia menyeru Tuhannya: \"Ya Tuhanku janganlah Engkau membiarkan aku hidup seorang diri dan Engkaulah Waris Yang Paling Baik.'),
(2573, '021:090', '68', 21, 90, 'Maka Kami memperkenankan doanya, dan Kami anugerahkan kepada nya Yahya dan Kami jadikan isterinya dapat mengandung. Sesungguhnya mereka adalah orang-orang yang selalu bersegera dalam (mengerjakan) perbuatan-perbuatan yang baik dan mereka berdoa kepada Kami dengan harap dan cemas. Dan mereka adalah orang-orang yang khusyu´ kepada Kami.'),
(2574, '021:091', '68', 21, 91, 'Dan (ingatlah kisah) Maryam yang telah memelihara kehormatannya, lalu Kami tiupkan ke dalam (tubuh)nya ruh dari Kami dan Kami jadikan dia dan anaknya tanda (kekuasaan Allah) yang besar bagi semesta alam.'),
(2575, '021:092', '68', 21, 92, 'Sesungguhnya (agama Tauhid) ini adalah agama kamu semua; agama yang satu dan Aku adalah Tuhanmu, maka sembahlah Aku.'),
(2576, '021:093', '68', 21, 93, 'Dan mereka telah memotong-motong urusan (agama) mereka di antara mereka. Kepada Kamilah masing-masing golongan itu akan kembali.'),
(2577, '021:094', '68', 21, 94, 'Maka barang siapa yang mengerjakan amal saleh, sedang ia beriman, maka tidak ada pengingkaran terhadap amalannya itu dan sesungguhnya Kami menuliskan amalannya itu untuknya.'),
(2578, '021:095', '68', 21, 95, 'Sungguh tidak mungkin atas (penduduk) suatu negeri yang telah Kami binasakan, bahwa mereka tidak akan kembali (kepada Kami).'),
(2579, '021:096', '68', 21, 96, 'Hingga apabila dibukakan (tembok) Ya´juj dan Ma´juj, dan mereka turun dengan cepat dari seluruh tempat yang tinggi.'),
(2580, '021:097', '68', 21, 97, 'Dan telah dekatlah kedatangan janji yang benar (hari berbangkit), maka tiba-tiba terbelalaklah mata orang-orang yang kafir. (Mereka berkata): \"Aduhai, celakalah kami, sesungguhnya kami adalah dalam kelalaian tentang ini, bahkan kami adalah orang-orang yang zalim\".'),
(2581, '021:098', '68', 21, 98, 'Sesungguhnya kamu dan apa yang kamu sembah selain Allah, adalah umpan Jahannam, kamu pasti masuk ke dalamnya.'),
(2582, '021:099', '68', 21, 99, 'Andaikata berhala-berhala itu Tuhan, tentulah mereka tidak masuk neraka. Dan semuanya akan kekal di dalamnya.'),
(2583, '021:100', '68', 21, 100, 'Mereka merintih di dalam api dan mereka di dalamnya tidak bisa mendengar.'),
(2584, '021:101', '68', 21, 101, 'Bahwasanya orang-orang yang telah ada untuk mereka ketetapan yang baik dari Kami, mereka itu dijauhkan dari neraka,'),
(2585, '021:102', '68', 21, 102, 'mereka tidak mendengar sedikitpun suara api neraka, dan mereka kekal dalam menikmati apa yang diingini oleh mereka.'),
(2586, '021:103', '68', 21, 103, 'Mereka tidak disusahkan oleh kedahsyatan yang besar (pada hari kiamat), dan mereka disambut oleh para malaikat. (Malaikat berkata): \"Inilah harimu yang telah dijanjikan kepadamu\".'),
(2587, '021:104', '68', 21, 104, '(Yaitu) pada hari Kami gulung langit sebagai menggulung lembaran-lembaran kertas. Sebagaimana Kami telah memulai panciptaan pertama begitulah Kami akan mengulanginya. Itulah suatu janji yang pasti Kami tepati; sesungguhnya Kamilah yang akan melaksanakannya.'),
(2588, '021:105', '68', 21, 105, 'Dan sungguh telah Kami tulis didalam Zabur sesudah (Kami tulis dalam) Lauh Mahfuzh, bahwasanya bumi ini dipusakai hamba-hamba-Ku yang saleh.'),
(2589, '021:106', '68', 21, 106, 'Sesungguhnya (apa yang disebutkan) dalam (surat) ini, benar-benar menjadi peringatan bagi kaum yang menyembah (Allah).'),
(2590, '021:107', '68', 21, 107, 'Dan tiadalah Kami mengutus kamu, melainkan untuk (menjadi) rahmat bagi semesta alam.'),
(2591, '021:108', '68', 21, 108, 'Katakanlah: \"Sesungguhnya yang diwahyukan kepadaku adalah: \"Bahwasanya Tuhanmu adalah Tuhan Yang Esa. maka hendaklah kamu berserah diri (kepada-Nya)\".'),
(2592, '021:109', '68', 21, 109, 'Jika mereka berpaling, maka katakanlah: \"Aku telah menyampaikan kepada kamu sekalian (ajaran) yang sama (antara kita) dan aku tidak mengetahui apakah yang diancamkan kepadamu itu sudah dekat atau masih jauh?\".'),
(2593, '021:110', '68', 21, 110, 'Sesungguhnya Dia mengetahui perkataan (yang kamu ucapkan) dengan terang-terangan dan Dia mengetahui apa yang kamu rahasiakan.'),
(2594, '021:111', '68', 21, 111, 'Dan aku tiada mengetahui, boleh jadi hal itu cobaan bagi kamu dan kesenangan sampai kepada suatu waktu.'),
(2595, '021:112', '68', 21, 112, '(Muhammad) berkata: \"Ya Tuhanku, berilah keputusan dengan adil. Dan Tuhan kami ialah Tuhan Yang Maha Pemurah lagi Yang dimohonkan pertolongan-Nya terhadap apa yang kamu katakan\".'),
(2596, '022:001', '68', 22, 1, 'Hai manusia, bertakwalah kepada Tuhanmu; sesungguhnya kegoncangan hari kiamat itu adalah suatu kejadian yang sangat besar (dahsyat).'),
(2597, '022:002', '68', 22, 2, '(Ingatlah) pada hari (ketika) kamu melihat kegoncangan itu, lalailah semua wanita yang menyusui anaknya dari anak yang disusuinya dan gugurlah kandungan segala wanita yang hamil, dan kamu lihat manusia dalam keadaan mabuk, padahal sebenarnya mereka tidak mabuk, akan tetapi azab Allah itu sangat kerasnya.'),
(2598, '022:003', '68', 22, 3, 'Di antara manusia ada orang yang membantah tentang Allah tanpa ilmu pengetahuan dan mengikuti setiap syaitan yang jahat,'),
(2599, '022:004', '68', 22, 4, 'yang telah ditetapkan terhadap syaitan itu, bahwa barangsiapa yang berkawan dengan dia, tentu dia akan menyesatkannya, dan membawanya ke azab neraka.'),
(2600, '022:005', '68', 22, 5, 'Hai manusia, jika kamu dalam keraguan tentang kebangkitan (dari kubur), maka (ketahuilah) sesungguhnya Kami telah menjadikan kamu dari tanah, kemudian dari setetes mani, kemudian dari segumpal darah, kemudian dari segumpal daging yang sempurna kejadiannya dan yang tidak sempurna, agar Kami jelaskan kepada kamu dan Kami tetapkan dalam rahim, apa yang Kami kehendaki sampai waktu yang sudah ditentukan, kemudian Kami keluarkan kamu sebagai bayi, kemudian (dengan berangsur-angsur) kamu sampailah kepada kedewasaan, dan di antara kamu ada yang diwafatkan dan (adapula) di antara kamu yang dipanjangkan umurnya sampai pikun, supaya dia tidak mengetahui lagi sesuatupun yang dahulunya telah diketahuinya. Dan kamu lihat bumi ini kering, kemudian apabila telah Kami turunkan air di atasnya, hiduplah bumi itu dan suburlah dan menumbuhkan berbagai macam tumbuh-tumbuhan yang indah.'),
(2601, '022:006', '68', 22, 6, 'Yang demikian itu, karena sesungguhnya Allah, Dialah yang haq dan sesungguhnya Dialah yang menghidupkan segala yang mati dan sesungguhnya Allah Maha Kuasa atas segala sesuatu,'),
(2602, '022:007', '68', 22, 7, 'dan sesungguhnya hari kiamat itu pastilah datang, tak ada keraguan padanya; dan bahwasanya Allah membangkitkan semua orang di dalam kubur.'),
(2603, '022:008', '68', 22, 8, 'Dan di antara manusia ada orang-orang yang membantah tentang Allah tanpa ilmu pengetahuan, tanpa petunjuk dan tanpa kitab (wahyu) yang bercahaya,'),
(2604, '022:009', '68', 22, 9, 'dengan memalingkan lambungnya untuk menyesatkan manusia dari jalan Allah. Ia mendapat kehinaan di dunia dan dihari kiamat Kami merasakan kepadanya azab neraka yang membakar.'),
(2605, '022:010', '68', 22, 10, '(Akan dikatakan kepadanya): \"Yang demikian itu, adalah disebabkan perbuatan yang dikerjakan oleh kedua tangan kamu dahulu dan sesungguhnya Allah sekali-kali bukanlah penganiaya hamba-hamba-Nya\".'),
(2606, '022:011', '68', 22, 11, 'Dan di antara manusia ada orang yang menyembah Allah dengan berada di tepi; maka jika ia memperoleh kebajikan, tetaplah ia dalam keadaan itu, dan jika ia ditimpa oleh suatu bencana, berbaliklah ia ke belakang. Rugilah ia di dunia dan di akhirat. Yang demikian itu adalah kerugian yang nyata.'),
(2607, '022:012', '68', 22, 12, 'Ia menyeru selain Allah, sesuatu yang tidak dapat memberi mudharat dan tidak (pula) memberi manfaat kepadanya. Yang demikian itu adalah kesesatan yang jauh.'),
(2608, '022:013', '68', 22, 13, 'Ia menyeru sesuatu yang sebenarnya mudharatnya lebih dekat dari manfaatnya. Sesungguhnya yang diserunya itu adalah sejahat-jahat kawan.'),
(2609, '022:014', '68', 22, 14, 'Sesungguhnya Allah memasukkan orang-orang yang beriman dan mengerjakan amal yang saleh ke dalam surga-surga yang di bawahnya mengalir sungai-sungai. Sesungguhnya Allah berbuat apa yang Dia kehendaki.'),
(2610, '022:015', '68', 22, 15, 'Barangsiapa yang menyangka bahwa Allah sekali-kali tiada menolongnya (Muhammad) di dunia dan akhirat, maka hendaklah ia merentangkan tali ke langit, kemudian hendaklah ia melaluinya, kemudian hendaklah ia pikirkan apakah tipu dayanya itu dapat melenyapkan apa yang menyakitkan hatinya.'),
(2611, '022:016', '68', 22, 16, 'Dan demikianlah Kami telah menurunkan Al Quran yang merupakan ayat-ayat yang nyata, dan bahwasanya Allah memberikan petunjuk kepada siapa yang Dia kehendaki.'),
(2612, '022:017', '68', 22, 17, 'Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, orang-orang Shaabi-iin orang-orang Nasrani, orang-orang Majusi dan orang-orang musyrik, Allah akan memberi keputusan di antara mereka pada hari kiamat. Sesungguhnya Allah menyaksikan segala sesuatu.'),
(2613, '022:018', '68', 22, 18, 'Apakah kamu tiada mengetahui, bahwa kepada Allah bersujud apa yang ada di langit, di bumi, matahari, bulan, bintang, gunung, pohon-pohonan, binatang-binatang yang melata dan sebagian besar daripada manusia? Dan banyak di antara manusia yang telah ditetapkan azab atasnya. Dan barangsiapa yang dihinakan Allah maka tidak seorangpun yang memuliakannya. Sesungguhnya Allah berbuat apa yang Dia kehendaki.'),
(2614, '022:019', '68', 22, 19, 'Inilah dua golongan (golongan mukmin dan golongan kafir) yang bertengkar, mereka saling bertengkar mengenai Tuhan mereka. Maka orang kafir akan dibuatkan untuk mereka pakaian-pakaian dari api neraka. Disiramkan air yang sedang mendidih ke atas kepala mereka.'),
(2615, '022:020', '68', 22, 20, 'Dengan air itu dihancur luluhkan segala apa yang ada dalam perut mereka dan juga kulit (mereka).'),
(2616, '022:021', '68', 22, 21, 'Dan untuk mereka cambuk-cambuk dari besi.'),
(2617, '022:022', '68', 22, 22, 'Setiap kali mereka hendak ke luar dari neraka lantaran kesengsaraan mereka, niscaya mereka dikembalikan ke dalamnya. (Kepada mereka dikatakan), \"Rasailah azab yang membakar ini\".'),
(2618, '022:023', '68', 22, 23, 'Sesungguhnya Allah memasukkan orang-orang beriman dan mengerjakan amal yang saleh ke dalam surga-surga yang di bawahnya mengalir sungai-sungai. Di surga itu mereka diberi perhiasan dengan gelang-gelang dari emas dan mutiara, dan pakaian mereka adalah sutera.'),
(2619, '022:024', '68', 22, 24, 'Dan mereka diberi petunjuk kepada ucapan-ucapan yang baik dan ditunjuki (pula) kepada jalan (Allah) yang terpuji.'),
(2620, '022:025', '68', 22, 25, 'Sesungguhnya orang-orang yang kafir dan menghalangi manusia dari jalan Allah dan Masjidilharam yang telah Kami jadikan untuk semua manusia, baik yang bermukim di situ maupun di padang pasir dan siapa yang bermaksud di dalamnya melakukan kejahatan secara zalim, niscaya akan Kami rasakan kepadanya sebahagian siksa yang pedih.'),
(2621, '022:026', '68', 22, 26, 'Dan (ingatlah), ketika Kami memberikan tempat kepada Ibrahim di tempat Baitullah (dengan mengatakan): \"Janganlah kamu memperserikatkan sesuatupun dengan Aku dan sucikanlah rumah-Ku ini bagi orang-orang yang thawaf, dan orang-orang yang beribadat dan orang-orang yang ruku´ dan sujud.'),
(2622, '022:027', '68', 22, 27, 'Dan berserulah kepada manusia untuk mengerjakan haji, niscaya mereka akan datang kepadamu dengan berjalan kaki, dan mengendarai unta yang kurus yang datang dari segenap penjuru yang jauh,'),
(2623, '022:028', '68', 22, 28, 'supaya mereka menyaksikan berbagai manfaat bagi mereka dan supaya mereka menyebut nama Allah pada hari yang telah ditentukan atas rezeki yang Allah telah berikan kepada mereka berupa binatang ternak. Maka makanlah sebahagian daripadanya dan (sebahagian lagi) berikanlah untuk dimakan orang-orang yang sengsara dan fakir.'),
(2624, '022:029', '68', 22, 29, 'Kemudian, hendaklah mereka menghilangkan kotoran yang ada pada badan mereka dan hendaklah mereka menyempurnakan nazar-nazar mereka dan hendaklah mereka melakukan melakukan thawaf sekeliling rumah yang tua itu (Baitullah).'),
(2625, '022:030', '68', 22, 30, 'Demikianlah (perintah Allah). Dan barangsiapa mengagungkan apa-apa yang terhormat di sisi Allah maka itu adalah lebih baik baginya di sisi Tuhannya. Dan telah dihalalkan bagi kamu semua binatang ternak, terkecuali yang diterangkan kepadamu keharamannya, maka jauhilah olehmu berhala-berhala yang najis itu dan jauhilah perkataan-perkataan dusta.'),
(2626, '022:031', '68', 22, 31, 'dengan ikhlas kepada Allah, tidak mempersekutukan sesuatu dengan Dia. Barangsiapa mempersekutukan sesuatu dengan Allah, maka adalah ia seolah-olah jatuh dari langit lalu disambar oleh burung, atau diterbangkan angin ke tempat yang jauh.'),
(2627, '022:032', '68', 22, 32, 'Demikianlah (perintah Allah). Dan barangsiapa mengagungkan syi´ar-syi´ar Allah, maka sesungguhnya itu timbul dari ketakwaan hati.'),
(2628, '022:033', '68', 22, 33, 'Bagi kamu pada binatang-binatang hadyu itu ada beberapa manfaat, sampai kepada waktu yang ditentukan, kemudian tempat wajib (serta akhir masa) menyembelihnya ialah setelah sampai ke Baitul Atiq (Baitullah).'),
(2629, '022:034', '68', 22, 34, 'Dan bagi tiap-tiap umat telah Kami syariatkan penyembelihan (kurban), supaya mereka menyebut nama Allah terhadap binatang ternak yang telah direzekikan Allah kepada mereka, maka Tuhanmu ialah Tuhan Yang Maha Esa, karena itu berserah dirilah kamu kepada-Nya. Dan berilah kabar gembira kepada orang-orang yang tunduk patuh (kepada Allah),'),
(2630, '022:035', '68', 22, 35, '(yaitu) orang-orang yang apabila disebut nama Allah gemetarlah hati mereka, orang-orang yang sabar terhadap apa yang menimpa mereka, orang-orang yang mendirikan sembahyang dan orang-orang yang menafkahkan sebagian dari apa yang telah Kami rezekikan kepada mereka.'),
(2631, '022:036', '68', 22, 36, 'Dan telah Kami jadikan untuk kamu unta-unta itu sebahagian dari syi´ar Allah, kamu memperoleh kebaikan yang banyak padanya, maka sebutlah olehmu nama Allah ketika kamu menyembelihnya dalam keadaan berdiri (dan telah terikat). Kemudian apabila telah roboh (mati), maka makanlah sebahagiannya dan beri makanlah orang yang rela dengan apa yang ada padanya (yang tidak meminta-minta) dan orang yang meminta. Demikianlah Kami telah menundukkan untua-unta itu kepada kamu, mudah-mudahan kamu bersyukur.'),
(2632, '022:037', '68', 22, 37, 'Daging-daging unta dan darahnya itu sekali-kali tidak dapat mencapai (keridhaan) Allah, tetapi ketakwaan dari kamulah yang dapat mencapainya. Demikianlah Allah telah menundukkannya untuk kamu supaya kamu mengagungkan Allah terhadap hidayah-Nya kepada kamu. Dan berilah kabar gembira kepada orang-orang yang berbuat baik.'),
(2633, '022:038', '68', 22, 38, 'Sesungguhnya Allah membela orang-orang yang telah beriman. Sesungguhnya Allah tidak menyukai tiap-tiap orang yang berkhianat lagi mengingkari nikmat.'),
(2634, '022:039', '68', 22, 39, 'Telah diizinkan (berperang) bagi orang-orang yang diperangi, karena sesungguhnya mereka telah dianiaya. Dan sesungguhnya Allah, benar-benar Maha Kuasa menolong mereka itu,'),
(2635, '022:040', '68', 22, 40, '(yaitu) orang-orang yang telah diusir dari kampung halaman mereka tanpa alasan yang benar, kecuali karena mereka berkata: \"Tuhan kami hanyalah Allah\". Dan sekiranya Allah tiada menolak (keganasan) sebagian manusia dengan sebagian yang lain, tentulah telah dirobohkan biara-biara Nasrani, gereja-gereja, rumah-rumah ibadat orang Yahudi dan masjid-masjid, yang di dalamnya banyak disebut nama Allah. Sesungguhnya Allah pasti menolong orang yang menolong (agama)-Nya. Sesungguhnya Allah benar-benar Maha Kuat lagi Maha Perkasa,'),
(2636, '022:041', '68', 22, 41, '(yaitu) orang-orang yang jika Kami teguhkan kedudukan mereka di muka bumi niscaya mereka mendirikan sembahyang, menunaikan zakat, menyuruh berbuat ma´ruf dan mencegah dari perbuatan yang mungkar; dan kepada Allah-lah kembali segala urusan.'),
(2637, '022:042', '68', 22, 42, 'Dan jika mereka (orang-orang musyrik) mendustakan kamu, maka sesungguhnya telah mendustakan juga sebelum mereka kaum Nuh, ´Aad dan Tsamud,'),
(2638, '022:043', '68', 22, 43, 'dan kaum Ibrahim dan kaum Luth,'),
(2639, '022:044', '68', 22, 44, 'dan penduduk Madyan, dan telah didustakan Musa, lalu Aku tangguhkan (azab-Ku) untuk orang-orang kafir, kemudian Aku azab mereka, maka (lihatlah) bagaimana besarnya kebencian-Ku (kepada mereka itu).'),
(2640, '022:045', '68', 22, 45, 'Berapalah banyaknya kota yang Kami telah membinasakannya, yang penduduknya dalam keadaan zalim, maka (tembok-tembok) kota itu roboh menutupi atap-atapnya dan (berapa banyak pula) sumur yang telah ditinggalkan dan istana yang tinggi,'),
(2641, '022:046', '68', 22, 46, 'maka apakah mereka tidak berjalan di muka bumi, lalu mereka mempunyai hati yang dengan itu mereka dapat memahami atau mempunyai telinga yang dengan itu mereka dapat mendengar? Karena sesungguhnya bukanlah mata itu yang buta, tetapi yang buta, ialah hati yang di dalam dada.'),
(2642, '022:047', '68', 22, 47, 'Dan mereka meminta kepadamu agar azab itu disegerakan, padahal Allah sekali-kali tidak akan menyalahi janji-Nya. Sesungguhnya sehari disisi Tuhanmu adalah seperti seribu tahun menurut perhitunganmu.'),
(2643, '022:048', '68', 22, 48, 'Dan berapalah banyaknya kota yang Aku tangguhkan (azab-Ku) kepadanya, yang penduduknya berbuat zalim, kemudian Aku azab mereka, dan hanya kepada-Kulah kembalinya (segala sesuatu).'),
(2644, '022:049', '68', 22, 49, 'Katakanlah: \"Hai manusia, sesungguhnya aku adalah seorang pemberi peringatan yang nyata kepada kamu\".'),
(2645, '022:050', '68', 22, 50, 'Maka orang-orang yang beriman dan beramal saleh, bagi mereka ampunan dan rezeki yang mulia.'),
(2646, '022:051', '68', 22, 51, 'Dan orang-orang yang berusaha dengan maksud menentang ayat-ayat Kami dengan melemahkan (kemauan untuk beriman); mereka itu adalah penghuni-penghuni neraka.'),
(2647, '022:052', '68', 22, 52, 'Dan Kami tidak mengutus sebelum kamu seorang rasulpun dan tidak (pula) seorang nabi, melainkan apabila ia mempunyai sesuatu keinginan, syaitanpun memasukkan godaan-godaan terhadap keinginan itu, Allah menghilangkan apa yang dimasukkan oleh syaitan itu, dan Allah menguatkan ayat-ayat-Nya. Dan Allah Maha Mengetahui lagi Maha Bijaksana,'),
(2648, '022:053', '68', 22, 53, 'agar Dia menjadikan apa yang dimasukkan oleh syaitan itu, sebagai cobaan bagi orang-orang yang di dalam hatinya ada penyakit dan yang kasar hatinya. Dan sesungguhnya orang-orang yang zalim itu, benar-benar dalam permusuhan yang sangat,'),
(2649, '022:054', '68', 22, 54, 'dan agar orang-orang yang telah diberi ilmu, meyakini bahwasanya Al Quran itulah yang hak dari Tuhan-mu lalu mereka beriman dan tunduk hati mereka kepadanya dan sesungguhnya Allah adalah Pemberi Petunjuk bagi orang-orang yang beriman kepada jalan yang lurus.'),
(2650, '022:055', '68', 22, 55, 'Dan senantiasalah orang-orang kafir itu berada dalam keragu-raguan terhadap Al Quran, hingga datang kepada mereka saat (kematiannya) dengan tiba-tiba atau datang kepada mereka azab hari kiamat.'),
(2651, '022:056', '68', 22, 56, 'Kekuasaan di hari itu ada pada Allah, Dia memberi keputusan di antara mereka. Maka orang-orang yang beriman dan beramal saleh adalah di dalam surga yang penuh kenikmatan.'),
(2652, '022:057', '68', 22, 57, 'Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, maka bagi mereka azab yang menghinakan.'),
(2653, '022:058', '68', 22, 58, 'Dan orang-orang yang berhijrah di jalan Allah, kemudian mereka di bunuh atau mati, benar-benar Allah akan memberikan kepada mereka rezeki yang baik (surga). Dan sesungguhnya Allah adalah sebaik-baik pemberi rezeki.'),
(2654, '022:059', '68', 22, 59, 'Sesungguhnya Allah akan memasukkan mereka ke dalam suatu tempat (surga) yang mereka menyukainya. Dan sesungguhnya Allah Maha mengetahui lagi Maha Penyantun.'),
(2655, '022:060', '68', 22, 60, 'Demikianlah, dan barangsiapa membalas seimbang dengan penganiayaan yang pernah ia derita kemudian ia dianiaya (lagi), pasti Allah akan menolongnya. Sesungguhnya Allah benar-benar Maha Pemaaf lagi Maha Pengampun.'),
(2656, '022:061', '68', 22, 61, 'Yang demikian itu, adalah karena sesungguhnya Allah (kuasa) memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan bahwasanya Allah Maha Mendengar lagi Maha Melihat.'),
(2657, '022:062', '68', 22, 62, '(Kuasa Allah) yang demikian itu, adalah karena sesungguhnya Allah, Dialah (Tuhan) Yang Haq dan sesungguhnya apa saja yang mereka seru selain dari Allah, itulah yang batil, dan sesungguhnya Allah, Dialah Yang Maha Tinggi lagi Maha Besar.'),
(2658, '022:063', '68', 22, 63, 'Apakah kamu tiada melihat, bahwasanya Allah menurunkan air dari langit, lalu jadilah bumi itu hijau? Sesungguhnya Allah Maha Halus lagi Maha Mengetahui.'),
(2659, '022:064', '68', 22, 64, 'Kepunyaan Allah-lah segala yang ada di langit dan segala yang ada di bumi. Dan sesungguhnya Allah benar-benar Maha Kaya lagi Maha Terpuji.'),
(2660, '022:065', '68', 22, 65, 'Apakah kamu tiada melihat bahwasanya Allah menundukkan bagimu apa yang ada di bumi dan bahtera yang berlayar di lautan dengan perintah-Nya. Dan Dia menahan (benda-benda) langit jatuh ke bumi, melainkan dengan izin-Nya? Sesungguhnya Allah benar-benar Maha Pengasih lagi Maha Penyayang kepada Manusia.'),
(2661, '022:066', '68', 22, 66, 'Dan Dialah Allah yang telah menghidupkan kamu, kemudian mematikan kamu, kemudian menghidupkan kamu (lagi), sesungguhnya manusia itu, benar-benar sangat mengingkari nikmat.'),
(2662, '022:067', '68', 22, 67, 'Bagi tiap-tiap umat telah Kami tetapkan syari´at tertentu yang mereka lakukan, maka janganlah sekali-kali mereka membantah kamu dalam urusan (syari´at) ini dan serulah kepada (agama) Tuhanmu. Sesungguhnya kamu benar-benar berada pada jalan yang lurus.'),
(2663, '022:068', '68', 22, 68, 'Dan jika mereka membantah kamu, maka katakanlah: \"Allah lebih mengetahui tentang apa yang kamu kerjakan\".'),
(2664, '022:069', '68', 22, 69, 'Allah akan mengadili di antara kamu pada hari kiamat tentang apa yang kamu dahulu selalu berselisih padanya.'),
(2665, '022:070', '68', 22, 70, 'Apakah kamu tidak mengetahui bahwa sesungguhnya Allah mengetahui apa saja yang ada di langit dan di bumi?; bahwasanya yang demikian itu terdapat dalam sebuah kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu amat mudah bagi Allah.'),
(2666, '022:071', '68', 22, 71, 'Dan mereka menyembah selain Allah, apa yang Allah tidak menurunkan keterangan tentang itu, dan apa yang mereka sendiri tiada mempunyai pengetahuan terhadapnya. Dan bagi orang-orang yang zalim sekali-kali tidak ada seorang penolongpun.'),
(2667, '022:072', '68', 22, 72, 'Dan apabila dibacakan di hadapan mereka ayat-ayat Kami yang terang, niscaya kamu melihat tanda-tanda keingkaran pada muka orang-orang yang kafir itu. Hampir-hampir mereka menyerang orang-orang yang membacakan ayat-ayat Kami di hadapan mereka. Katakanlah: \"Apakah akan aku kabarkan kepadamu yang lebih buruk daripada itu, yaitu neraka?\" Allah telah mengancamkannya kepada orang-orang yang kafir. Dan neraka itu adalah seburuk-buruknya tempat kembali.'),
(2668, '022:073', '68', 22, 73, 'Hai manusia, telah dibuat perumpamaan, maka dengarkanlah olehmu perumpamaan itu. Sesungguhnya segala yang kamu seru selain Allah sekali-kali tidak dapat menciptakan seekor lalatpun, walaupun mereka bersatu menciptakannya. Dan jika lalat itu merampas sesuatu dari mereka, tiadalah mereka dapat merebutnya kembali dari lalat itu. Amat lemahlah yang menyembah dan amat lemah (pulalah) yang disembah.'),
(2669, '022:074', '68', 22, 74, 'Mereka tidak mengenal Allah dengan sebenar-benarnya. Sesungguhnya Allah benar-benar Maha Kuat lagi Maha Perkasa.'),
(2670, '022:075', '68', 22, 75, 'Allah memilih utusan-utusan-(Nya) dari malaikat dan dari manusia; sesungguhnya Allah Maha Mendengar lagi Maha Melihat.'),
(2671, '022:076', '68', 22, 76, 'Allah mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka. Dan hanya kepada Allah dikembalikan semua urusan.'),
(2672, '022:077', '68', 22, 77, 'Hai orang-orang yang beriman, ruku´lah kamu, sujudlah kamu, sembahlah Tuhanmu dan perbuatlah kebajikan, supaya kamu mendapat kemenangan.'),
(2673, '022:078', '68', 22, 78, 'Dan berjihadlah kamu pada jalan Allah dengan jihad yang sebenar-benarnya. Dia telah memilih kamu dan Dia sekali-kali tidak menjadikan untuk kamu dalam agama suatu kesempitan. (Ikutilah) agama orang tuamu Ibrahim. Dia (Allah) telah menamai kamu sekalian orang-orang muslim dari dahulu, dan (begitu pula) dalam (Al Quran) ini, supaya Rasul itu menjadi saksi atas dirimu dan supaya kamu semua menjadi saksi atas segenap manusia, maka dirikanlah sembahyang, tunaikanlah zakat dan berpeganglah kamu pada tali Allah. Dia adalah Pelindungmu, maka Dialah sebaik-baik Pelindung dan sebaik-baik Penolong.'),
(2674, '023:001', '68', 23, 1, 'Sesungguhnya beruntunglah orang-orang yang beriman,'),
(2675, '023:002', '68', 23, 2, '(yaitu) orang-orang yang khusyu´ dalam sembahyangnya,'),
(2676, '023:003', '68', 23, 3, 'dan orang-orang yang menjauhkan diri dari (perbuatan dan perkataan) yang tiada berguna,'),
(2677, '023:004', '68', 23, 4, 'dan orang-orang yang menunaikan zakat,'),
(2678, '023:005', '68', 23, 5, 'dan orang-orang yang menjaga kemaluannya,'),
(2679, '023:006', '68', 23, 6, 'kecuali terhadap isteri-isteri mereka atau budak yang mereka miliki; maka sesungguhnya mereka dalam hal ini tiada tercela.'),
(2680, '023:007', '68', 23, 7, 'Barangsiapa mencari yang di balik itu maka mereka itulah orang-orang yang melampaui batas.'),
(2681, '023:008', '68', 23, 8, 'Dan orang-orang yang memelihara amanat-amanat (yang dipikulnya) dan janjinya.'),
(2682, '023:009', '68', 23, 9, 'dan orang-orang yang memelihara sembahyangnya.'),
(2683, '023:010', '68', 23, 10, 'Mereka itulah orang-orang yang akan mewarisi,'),
(2684, '023:011', '68', 23, 11, '(yakni) yang akan mewarisi surga Firdaus. Mereka kekal di dalamnya.'),
(2685, '023:012', '68', 23, 12, 'Dan sesungguhnya Kami telah menciptakan manusia dari suatu saripati (berasal) dari tanah.'),
(2686, '023:013', '68', 23, 13, 'Kemudian Kami jadikan saripati itu air mani (yang disimpan) dalam tempat yang kokoh (rahim).'),
(2687, '023:014', '68', 23, 14, 'Kemudian air mani itu Kami jadikan segumpal darah, lalu segumpal darah itu Kami jadikan segumpal daging, dan segumpal daging itu Kami jadikan tulang belulang, lalu tulang belulang itu Kami bungkus dengan daging. Kemudian Kami jadikan dia makhluk yang (berbentuk) lain. Maka Maha sucilah Allah, Pencipta Yang Paling Baik.'),
(2688, '023:015', '68', 23, 15, 'Kemudian, sesudah itu, sesungguhnya kamu sekalian benar-benar akan mati.'),
(2689, '023:016', '68', 23, 16, 'Kemudian, sesungguhnya kamu sekalian akan dibangkitkan (dari kuburmu) di hari kiamat.'),
(2690, '023:017', '68', 23, 17, 'Dan sesungguhnya Kami telah menciptakan di atas kamu tujuh buah jalan (tujuh buah langit); dan Kami tidaklah lengah terhadap ciptaan (Kami).'),
(2691, '023:018', '68', 23, 18, 'Dan Kami turunkan air dari langit menurut suatu ukuran; lalu Kami jadikan air itu menetap di bumi, dan sesungguhnya Kami benar-benar berkuasa menghilangkannya.'),
(2692, '023:019', '68', 23, 19, 'Lalu dengan air itu, Kami tumbuhkan untuk kamu kebun-kebun kurma dan anggur; di dalam kebun-kebun itu kamu peroleh buah-buahan yang banyak dan sebahagian dari buah-buahan itu kamu makan,'),
(2693, '023:020', '68', 23, 20, 'dan pohon kayu keluar dari Thursina (pohon zaitun), yang menghasilkan minyak, dan pemakan makanan bagi orang-orang yang makan.'),
(2694, '023:021', '68', 23, 21, 'Dan sesungguhnya pada binatang-binatang ternak, benar-benar terdapat pelajaran yang penting bagi kamu, Kami memberi minum kamu dari air susu yang ada dalam perutnya, dan (juga) pada binatang-binatang ternak itu terdapat faedah yang banyak untuk kamu, dan sebagian daripadanya kamu makan,'),
(2695, '023:022', '68', 23, 22, 'dan di atas punggung binatang-binatang ternak itu dan (juga) di atas perahu-perahu kamu diangkut.'),
(2696, '023:023', '68', 23, 23, 'Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, lalu ia berkata: \"Hai kaumku, sembahlah oleh kamu Allah, (karena) sekali-kali tidak ada Tuhan bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?\"'),
(2697, '023:024', '68', 23, 24, 'Maka pemuka-pemuka orang yang kafir di antara kaumnya menjawab: \"Orang ini tidak lain hanyalah manusia seperti kamu, yang bermaksud hendak menjadi seorang yang lebih tinggi dari kamu. Dan kalau Allah menghendaki, tentu Dia mengutus beberapa orang malaikat. Belum pernah kami mendengar (seruan yang seperti) ini pada masa nenek moyang kami yang dahulu.'),
(2698, '023:025', '68', 23, 25, 'la tidak lain hanyalah seorang laki-laki yang berpenyakit gila, maka tunggulah (sabarlah) terhadapnya sampai suatu waktu\".'),
(2699, '023:026', '68', 23, 26, 'Nuh berdoa: \"Ya Tuhanku, tolonglah aku, karena mereka mendustakan aku\".'),
(2700, '023:027', '68', 23, 27, 'Lalu Kami wahyukan kepadanya: \"Buatlah bahtera di bawah penilikan dan petunjuk Kami, maka apabila perintah Kami telah datang dan tanur telah memancarkan air, maka masukkanlah ke dalam bahtera itu sepasang dari tiap-tiap (jenis), dan (juga) keluargamu, kecuali orang yang telah lebih dahulu ditetapkan (akan ditimpa azab) di antara mereka. Dan janganlah kamu bicarakan dengan Aku tentang orang-orang yang zalim, karena sesungguhnya mereka itu akan ditenggelamkan.'),
(2701, '023:028', '68', 23, 28, 'Apabila kamu dan orang-orang yang bersamamu telah berada di atas bahtera itu, maka ucapkanlah: \"Segala puji bagi Allah yang telah menyelamatkan kami dari orang-orang yang zalim\".'),
(2702, '023:029', '68', 23, 29, 'Dan berdoalah: Ya Tuhanku, tempatkanlah aku pada tempat yang diberkati, dan Engkau adalah sebaik-baik Yang memberi tempat\".'),
(2703, '023:030', '68', 23, 30, 'Sesungguhnya pada (kejadian) itu benar-benar terdapat beberapa tanda (kebesaran Allah), dan sesungguhnya Kami menimpakan azab (kepada kaum Nuh itu).'),
(2704, '023:031', '68', 23, 31, 'Kemudian, Kami jadikan sesudah mereka umat yang lain.'),
(2705, '023:032', '68', 23, 32, 'Lalu Kami utus kepada mereka, seorang rasul dari kalangan mereka sendiri (yang berkata): \"Sembahlah Allah oleh kamu sekalian, sekali-kali tidak ada Tuhan selain daripada-Nya. Maka mengapa kamu tidak bertakwa (kepada-Nya).'),
(2706, '023:033', '68', 23, 33, 'Dan berkatalah pemuka-pemuka yang kafir di antara kaumnya dan yang mendustakan akan menemui hari akhirat (kelak) dan yang telah Kami mewahkan mereka dalam kehidupan di dunia: \"(Orang) ini tidak lain hanyalah manusia seperti kamu, dia makan dari apa yang kamu makan, dan meminum dari apa yang kamu minum.'),
(2707, '023:034', '68', 23, 34, 'Dan sesungguhnya jika kamu sekalian mentaati manusia yang seperti kamu, niscaya bila demikian, kamu benar-benar (menjadi) orang-orang yang merugi.'),
(2708, '023:035', '68', 23, 35, 'Apakah ia menjanjikan kepada kamu sekalian, bahwa bila kamu telah mati dan telah menjadi tanah dan tulang belulang, kamu sesungguhnya akan dikeluarkan (dari kuburmu)?'),
(2709, '023:036', '68', 23, 36, 'jauh, jauh sekali (dari kebenaran) apa yang diancamkan kepada kamu itu,'),
(2710, '023:037', '68', 23, 37, 'kehidupan itu tidak lain hanyalah kehidupan kita di dunia ini, kita mati dan kita hidup dan sekali-kali tidak akan dibangkitkan lagi,'),
(2711, '023:038', '68', 23, 38, 'Ia tidak lain hanyalah seorang yang mengada-adakan kebohongan terhadap Allah, dan kami sekali-kali tidak akan beriman kepadanya\".'),
(2712, '023:039', '68', 23, 39, 'Rasul itu berdoa: \"Ya Tuhanku, tolonglah aku karena mereka mendustakanku\".'),
(2713, '023:040', '68', 23, 40, 'Allah berfirman: \"Dalam sedikit waktu lagi pasti mereka akan menjadi orang-orang yang menyesal\".'),
(2714, '023:041', '68', 23, 41, 'Maka dimusnahkanlah mereka oleh suara yang mengguntur dengan hak dan Kami jadikan mereka (sebagai) sampah banjir maka kebinasaanlah bagi orang-orang yang zalim itu.'),
(2715, '023:042', '68', 23, 42, 'Kemudian Kami ciptakan sesudah mereka umat-umat yang lain.'),
(2716, '023:043', '68', 23, 43, 'Tidak (dapat) sesuatu umatpun mendahului ajalnya, dan tidak (dapat pula) mereka terlambat (dari ajalnya itu).'),
(2717, '023:044', '68', 23, 44, 'Kemudian Kami utus (kepada umat-umat itu) rasul-rasul Kami berturut-turut. Tiap-tiap seorang rasul datang kepada umatnya, umat itu mendustakannya, maka Kami perikutkan sebagian mereka dengan sebagian yang lain. Dan Kami jadikan mereka buah tutur (manusia), maka kebinasaanlah bagi orang-orang yang tidak beriman.'),
(2718, '023:045', '68', 23, 45, 'Kemudian Kami utus Musa dan saudaranya Harun dengan membawa tanda-tanda (Kebesaran) Kami, dan bukti yang nyata,'),
(2719, '023:046', '68', 23, 46, 'kepada Fir´aun dan pembesar-pembesar kaumnya, maka mereka ini takbur dan mereka adalah orang-orang yang sombong.'),
(2720, '023:047', '68', 23, 47, 'Dan mereka berkata: \"Apakah (patut) kita percaya kepada dua orang manusia seperti kita (juga), padahal kaum mereka (Bani Israil) adalah orang-orang yang menghambakan diri kepada kita?\"'),
(2721, '023:048', '68', 23, 48, 'Maka (tetaplah) mereka mendustakan keduanya, sebab itu mereka adalah termasuk orang-orang yang dibinasakan.'),
(2722, '023:049', '68', 23, 49, 'Dan sesungguhnya telah Kami berikan Al Kitab (Taurat) kepada Musa, agar mereka (Bani Israil) mendapat petunjuk.'),
(2723, '023:050', '68', 23, 50, 'Dan telah Kami jadikan (Isa) putera Maryam beserta ibunya suatu bukti yang nyata bagi (kekuasaan Kami), dan Kami melindungi mereka di suatu tanah tinggi yang datar yang banyak terdapat padang-padang rumput dan sumber-sumber air bersih yang mengalir.'),
(2724, '023:051', '68', 23, 51, 'Hai rasul-rasul, makanlah dari makanan yang baik-baik, dan kerjakanlah amal yang saleh. Sesungguhnya Aku Maha Mengetahui apa yang kamu kerjakan.'),
(2725, '023:052', '68', 23, 52, 'Sesungguhnya (agama tauhid) ini, adalah agama kamu semua, agama yang satu, dan Aku adalah Tuhanmu, maka bertakwalah kepada-Ku.'),
(2726, '023:053', '68', 23, 53, 'Kemudian mereka (pengikut-pengikut rasul itu) menjadikan agama mereka terpecah belah menjadi beberapa pecahan. Tiap-tiap golongan merasa bangga dengan apa yang ada pada sisi mereka (masing-masing).'),
(2727, '023:054', '68', 23, 54, 'Maka biarkanlah mereka dalam kesesatannya sampai suatu waktu.'),
(2728, '023:055', '68', 23, 55, 'Apakah mereka mengira bahwa harta dan anak-anak yang Kami berikan kepada mereka itu (berarti bahwa),'),
(2729, '023:056', '68', 23, 56, 'Kami bersegera memberikan kebaikan-kebaikan kepada mereka? Tidak, sebenarnya mereka tidak sadar.'),
(2730, '023:057', '68', 23, 57, 'Sesungguhnya orang-orang yang berhati-hati karena takut akan (azab) Tuhan mereka,'),
(2731, '023:058', '68', 23, 58, 'Dan orang-orang yang beriman dengan ayat-ayat Tuhan mereka,'),
(2732, '023:059', '68', 23, 59, 'Dan orang-orang yang tidak mempersekutukan dengan Tuhan mereka (sesuatu apapun),'),
(2733, '023:060', '68', 23, 60, 'Dan orang-orang yang memberikan apa yang telah mereka berikan, dengan hati yang takut, (karena mereka tahu bahwa) sesungguhnya mereka akan kembali kepada Tuhan mereka,'),
(2734, '023:061', '68', 23, 61, 'mereka itu bersegera untuk mendapat kebaikan-kebaikan, dan merekalah orang-orang yang segera memperolehnya.'),
(2735, '023:062', '68', 23, 62, 'Kami tiada membebani seseorang melainkan menurut kesanggupannya, dan pada sisi Kami ada suatu kitab yang membicarakan kebenaran, dan mereka tidak dianiaya.'),
(2736, '023:063', '68', 23, 63, 'Tetapi hati orang-orang kafir itu dalam kesesatan dari (memahami kenyataan) ini, dan mereka banyak mengerjakan perbuatan-perbuatan (buruk) selain daripada itu, mereka tetap mengerjakannya.'),
(2737, '023:064', '68', 23, 64, 'Hingga apabila Kami timpakan azab, kepada orang-orang yang hidup mewah di antara mereka, dengan serta merta mereka memekik minta tolong.'),
(2738, '023:065', '68', 23, 65, 'Janganlah kamu memekik minta tolong pada hari ini. Sesungguhnya kamu tiada akan mendapat pertolongan dari Kami.'),
(2739, '023:066', '68', 23, 66, 'Sesungguhnya ayat-ayat-Ku (Al Quran) selalu dibacakan kepada kamu sekalian, maka kamu selalu berpaling ke belakang,'),
(2740, '023:067', '68', 23, 67, 'dengan menyombongkan diri terhadap Al Quran itu dan mengucapkan perkataan-perkataan keji terhadapnya di waktu kamu bercakap-cakap di malam hari.'),
(2741, '023:068', '68', 23, 68, 'Maka apakah mereka tidak memperhatikan perkataan (Kami), atau apakah telah datang kepada mereka apa yang tidak pernah datang kepada nenek moyang mereka dahulu?'),
(2742, '023:069', '68', 23, 69, 'Ataukah mereka tidak mengenal rasul mereka, karena itu mereka memungkirinya?'),
(2743, '023:070', '68', 23, 70, 'Atau (apakah patut) mereka berkata: \"Padanya (Muhammad) ada penyakit gila\". Sebenarnya dia telah membawa kebenaran kepada mereka, dan kebanyakan mereka benci kepada kebenaran itu.'),
(2744, '023:071', '68', 23, 71, 'Andaikata kebenaran itu menuruti hawa nafsu mereka, pasti binasalah langit dan bumi ini, dan semua yang ada di dalamnya. Sebenarnya Kami telah mendatangkan kepada mereka kebanggaan (Al Quran) mereka tetapi mereka berpaling dari kebanggaan itu.'),
(2745, '023:072', '68', 23, 72, 'Atau kamu meminta upah kepada mereka?\", maka upah dari Tuhanmu adalah lebih baik, dan Dia adalah Pemberi rezeki Yang Paling Baik.'),
(2746, '023:073', '68', 23, 73, 'Dan sesungguhnya kamu benar-benar menyeru mereka kepada jalan yang lurus.'),
(2747, '023:074', '68', 23, 74, 'Dan sesungguhnya orang-orang yang tidak beriman kepada negeri akhirat benar-benar menyimpang dari jalan (yang lurus).'),
(2748, '023:075', '68', 23, 75, 'Andaikata mereka Kami belas kasihani, dan Kami lenyapkan kemudharatan yang mereka alami, benar-benar mereka akan terus menerus terombang-ambing dalam keterlaluan mereka.'),
(2749, '023:076', '68', 23, 76, 'Dan sesungguhnya Kami telah pernah menimpakan azab kepada mereka, maka mereka tidak tunduk kepada Tuhan mereka, dan (juga) tidak memohon (kepada-Nya) dengan merendahkan diri.'),
(2750, '023:077', '68', 23, 77, 'Hingga apabila Kami bukakan untuk mereka suatu pintu tempat azab yang amat sangat (di waktu itulah) tiba-tiba mereka menjadi putus asa.'),
(2751, '023:078', '68', 23, 78, 'Dan Dialah yang telah menciptakan bagi kamu sekalian, pendengaran, penglihatan dan hati. Amat sedikitlah kamu bersyukur.'),
(2752, '023:079', '68', 23, 79, 'Dan Dialah yang menciptakan serta mengembang biakkan kamu di bumi ini dan kepada-Nya-lah kamu akan dihimpunkan.'),
(2753, '023:080', '68', 23, 80, 'Dan Dialah yang menghidupkan dan mematikan, dan Dialah yang (mengatur) pertukaran malam dan siang. Maka apakah kamu tidak memahaminya?'),
(2754, '023:081', '68', 23, 81, 'Sebenarnya mereka mengucapkan perkataan yang serupa dengan perkataan yang diucapkan oleh orang-orang dahulu kala.'),
(2755, '023:082', '68', 23, 82, 'Mereka berkata: \"Apakah betul, apabila kami telah mati dan kami telah menjadi tanah dan tulang belulang, apakah sesungguhnya kami benar-benar akan dibangkitkan?'),
(2756, '023:083', '68', 23, 83, 'Sesungguhnya kami dan bapak-bapak kami telah diberi ancaman (dengan) ini dahulu, ini tidak lain hanyalah dongengan orang-orang dahulu kala!\".'),
(2757, '023:084', '68', 23, 84, 'Katakanlah: \"Kepunyaan siapakah bumi ini, dan semua yang ada padanya, jika kamu mengetahui?\"'),
(2758, '023:085', '68', 23, 85, 'Mereka akan menjawab: \"Kepunyaan Allah\". Katakanlah: \"Maka apakah kamu tidak ingat?\"'),
(2759, '023:086', '68', 23, 86, 'Katakanlah: \"Siapakah Yang Empunya langit yang tujuh dan Yang Empunya ´Arsy yang besar?\"'),
(2760, '023:087', '68', 23, 87, 'Mereka akan menjawab: \"Kepunyaan Allah\". Katakanlah: \"Maka apakah kamu tidak bertakwa?\"'),
(2761, '023:088', '68', 23, 88, 'Katakanlah: \"Siapakah yang di tangan-Nya berada kekuasaan atas segala sesuatu sedang Dia melindungi, tetapi tidak ada yang dapat dilindungi dari (azab)-Nya, jika kamu mengetahui?\"'),
(2762, '023:089', '68', 23, 89, 'Mereka akan menjawab: \"Kepunyaan Allah\". Katakanlah: \"(Kalau demikian), maka dari jalan manakah kamu ditipu?\"'),
(2763, '023:090', '68', 23, 90, 'Sebenarnya Kami telah membawa kebenaran kepada mereka, dan sesungguhnya mereka benar-benar orang-orang yang berdusta.'),
(2764, '023:091', '68', 23, 91, 'Allah sekali-kali tidak mempunyai anak, dan sekali-kali tidak ada tuhan (yang lain) beserta-Nya, kalau ada tuhan beserta-Nya, masing-masing tuhan itu akan membawa makhluk yang diciptakannya, dan sebagian dari tuhan-tuhan itu akan mengalahkan sebagian yang lain. Maha Suci Allah dari apa yang mereka sifatkan itu,'),
(2765, '023:092', '68', 23, 92, 'Yang mengetahui semua yang ghaib dan semua yang nampak, maka Maha Tinggilah Dia dari apa yang mereka persekutukan.'),
(2766, '023:093', '68', 23, 93, 'Katakanlah: \"Ya Tuhanku, jika Engkau sungguh-sungguh hendak memperlihatkan kepadaku azab yang diancamkan kepada mereka,'),
(2767, '023:094', '68', 23, 94, 'ya Tuhanku, maka janganlah Engkau jadikan aku berada di antara orang-orang yang zalim\".'),
(2768, '023:095', '68', 23, 95, 'Dan sesungguhnya Kami benar-benar kuasa untuk memperlihatkan kepadamu apa yang Kami ancamkan kepada mereka.'),
(2769, '023:096', '68', 23, 96, 'Tolaklah perbuatan buruk mereka dengan yang lebih baik. Kami lebih mengetahui apa yang mereka sifatkan.'),
(2770, '023:097', '68', 23, 97, 'Dan katakanlah: \"Ya Tuhanku aku berlindung kepada Engkau dari bisikan-bisikan syaitan.'),
(2771, '023:098', '68', 23, 98, 'Dan aku berlindung (pula) kepada Engkau ya Tuhanku, dari kedatangan mereka kepadaku\".'),
(2772, '023:099', '68', 23, 99, '(Demikianlah keadaan orang-orang kafir itu), hingga apabila datang kematian kepada seseorang dari mereka, dia berkata: \"Ya Tuhanku kembalikanlah aku (ke dunia),'),
(2773, '023:100', '68', 23, 100, 'agar aku berbuat amal yang saleh terhadap yang telah aku tinggalkan. Sekali-kali tidak. Sesungguhnya itu adalah perkataan yang diucapkannya saja. Dan di hadapan mereka ada dinding sampal hari mereka dibangkitkan.'),
(2774, '023:101', '68', 23, 101, 'Apabila sangkakala ditiup maka tidaklah ada lagi pertalian nasab di antara mereka pada hari itu, dan tidak ada pula mereka saling bertanya.'),
(2775, '023:102', '68', 23, 102, 'Barangsiapa yang berat timbangan (kebaikan)nya, maka mereka itulah orang-orang yang dapat keberuntungan.'),
(2776, '023:103', '68', 23, 103, 'Dan barangsiapa yang ringan timbangannya, maka mereka itulah orang-orang yang merugikan dirinya sendiri, mereka kekal di dalam neraka Jahannam.'),
(2777, '023:104', '68', 23, 104, 'Muka mereka dibakar api neraka, dan mereka di dalam neraka itu dalam keadaan cacat.'),
(2778, '023:105', '68', 23, 105, 'Bukankah ayat-ayat-Ku telah dibacakan kepadamu sekalian, tetapi kamu selalu mendustakannya?'),
(2779, '023:106', '68', 23, 106, 'Mereka berkata: \"Ya Tuhan kami, kami telah dikuasai oleh kejahatan kami, dan adalah kami orang-orang yang sesat.'),
(2780, '023:107', '68', 23, 107, 'Ya Tuhan kami, keluarkanlah kami daripadanya (dan kembalikanlah kami ke dunia), maka jika kami kembali (juga kepada kekafiran), sesungguhnya kami adalah orang-orang yang zalim\".'),
(2781, '023:108', '68', 23, 108, 'Allah berfirman: \"Tinggallah dengan hina di dalamnya, dan janganlah kamu berbicara dengan Aku.'),
(2782, '023:109', '68', 23, 109, 'Sesungguhnya, ada segolongan dari hamba-hamba-Ku berdoa (di dunia): \"Ya Tuhan kami, kami telah beriman, maka ampunilah kami dan berilah kami rahmat dan Engkau adalah Pemberi rahmat Yang Paling Baik.'),
(2783, '023:110', '68', 23, 110, 'Lalu kamu menjadikan mereka buah ejekan, sehingga (kesibukan) kamu mengejek mereka, menjadikan kamu lupa mengingat Aku, dan adalah kamu selalu mentertawakan mereka,'),
(2784, '023:111', '68', 23, 111, 'Sesungguhnya Aku memberi balasan kepada mereka di hari ini, karena kesabaran mereka; sesungguhnya mereka itulah orang-orang yang menang\".'),
(2785, '023:112', '68', 23, 112, 'Allah bertanya: \"Berapa tahunkah lamanya kamu tinggal di bumi?\"'),
(2786, '023:113', '68', 23, 113, 'Mereka menjawab: \"Kami tinggal (di bumi) sehari atau setengah hari, maka tanyakanlah kepada orang-orang yang menghitung\".'),
(2787, '023:114', '68', 23, 114, 'Allah berfirman: \"Kamu tidak tinggal (di bumi) melainkan sebentar saja, kalau kamu sesungguhnya mengetahui\"'),
(2788, '023:115', '68', 23, 115, 'Maka apakah kamu mengira, bahwa sesungguhnya Kami menciptakan kamu secara main-main (saja), dan bahwa kamu tidak akan dikembalikan kepada Kami?'),
(2789, '023:116', '68', 23, 116, 'Maka Maha Tinggi Allah, Raja Yang Sebenarnya; tidak ada Tuhan selain Dia, Tuhan (Yang mempunyai) ´Arsy yang mulia.'),
(2790, '023:117', '68', 23, 117, 'Dan barangsiapa menyembah tuhan yang lain di samping Allah, padahal tidak ada suatu dalilpun baginya tentang itu, maka sesungguhnya perhitungannya di sisi Tuhannya. Sesungguhnya orang-orang yang kafir itu tiada beruntung.'),
(2791, '023:118', '68', 23, 118, 'Dan katakanlah: \"Ya Tuhanku berilah ampun dan berilah rahmat, dan Engkau adalah Pemberi rahmat Yang Paling baik\".'),
(2792, '024:001', '68', 24, 1, '(Ini adalah) satu surat yang Kami turunkan dan Kami wajibkan (menjalankan hukum-hukum yang ada di dalam)nya, dan Kami turunkan di dalamnya ayat ayat yang jelas, agar kamu selalu mengingatinya.'),
(2793, '024:002', '68', 24, 2, 'Perempuan yang berzina dan laki-laki yang berzina, maka deralah tiap-tiap seorang dari keduanya seratus dali dera, dan janganlah belas kasihan kepada keduanya mencegah kamu untuk (menjalankan) agama Allah, jika kamu beriman kepada Allah, dan hari akhirat, dan hendaklah (pelaksanaan) hukuman mereka disaksikan oleh sekumpulan orang-orang yang beriman.'),
(2794, '024:003', '68', 24, 3, 'Laki-laki yang berzina tidak mengawini melainkan perempuan yang berzina, atau perempuan yang musyrik; dan perempuan yang berzina tidak dikawini melainkan oleh laki-laki yang berzina atau laki-laki musyrik, dan yang demikian itu diharamkan atas oran-orang yang mukmin.'),
(2795, '024:004', '68', 24, 4, 'Dan orang-orang yang menuduh wanita-wanita yang baik-baik (berbuat zina) dan mereka tidak mendatangkan empat orang saksi, maka deralah mereka (yang menuduh itu) delapan puluh kali dera, dan janganlah kamu terima kesaksian mereka buat selama-lamanya. Dan mereka itulah orang-orang yang fasik.'),
(2796, '024:005', '68', 24, 5, 'kecuali orang-orang yang bertaubat sesudah itu dan memperbaiki (dirinya), maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(2797, '024:006', '68', 24, 6, 'Dan orang-orang yang menuduh isterinya (berzina), padahal mereka tidak ada mempunyai saksi-saksi selain diri mereka sendiri, maka persaksian orang itu ialah empat kali bersumpah dengan nama Allah, sesungguhnya dia adalah termasuk orang-orang yang benar.'),
(2798, '024:007', '68', 24, 7, 'Dan (sumpah) yang kelima: bahwa laknat Allah atasnya, jika dia termasuk orang-orang yang berdusta.'),
(2799, '024:008', '68', 24, 8, 'Istrinya itu dihindarkan dari hukuman oleh sumpahnya empat kali atas nama Allah sesungguhnya suaminya itu benar-benar termasuk orang-orang yang dusta.'),
(2800, '024:009', '68', 24, 9, 'dan (sumpah) yang kelima: bahwa laknat Allah atasnya jika suaminya itu termasuk orang-orang yang benar.'),
(2801, '024:010', '68', 24, 10, 'Dan andaikata tidak ada kurnia Allah dan rahmat-Nya atas dirimu dan (andaikata) Allah bukan Penerima Taubat lagi Maha Bijaksana, (niscaya kamu akan mengalami kesulitan-kesulitan).'),
(2802, '024:011', '68', 24, 11, 'Sesungguhnya orang-orang yang membawa berita bohong itu adalah dari golongan kamu juga. Janganlah kamu kira bahwa berita bohong itu buruk bagi kamu bahkan ia adalah baik bagi kamu. Tiap-tiap seseorang dari mereka mendapat balasan dari dosa yang dikerjakannya. Dan siapa di antara mereka yang mengambil bahagian yang terbesar dalam penyiaran berita bohong itu baginya azab yang besar.'),
(2803, '024:012', '68', 24, 12, 'Mengapa di waktu kamu mendengar berita bohon itu orang-orang mukminin dan mukminat tidak bersangka baik terhadap diri mereka sendiri, dan (mengapa tidak) berkata: \"Ini adalah suatu berita bohong yang nyata\".');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(2804, '024:013', '68', 24, 13, 'Mengapa mereka (yang menuduh itu) tidak mendatangkan empat orang saksi atas berita bohong itu? Olah karena mereka tidak mendatangkan saksi-saksi maka mereka itulah pada sisi Allah orang-orang yang dusta.'),
(2805, '024:014', '68', 24, 14, 'Sekiranya tidak ada kurnia Allah dan rahmat-Nya kepada kamu semua di dunia dan di akhirat, niscaya kamu ditimpa azab yang besar, karena pembicaraan kamu tentang berita bohong itu.'),
(2806, '024:015', '68', 24, 15, '(Ingatlah) di waktu kamu menerima berita bohong itu dari mulut ke mulut dan kamu katakan dengan mulutmu apa yang tidak kamu ketahui sedikit juga, dan kamu menganggapnya suatu yang ringan saja. Padahal dia pada sisi Allah adalah besar.'),
(2807, '024:016', '68', 24, 16, 'Dan mengapa kamu tidak berkata, diwaktu mendengar berita bohong itu: \"Sekali-kali tidaklah pantas bagi kita memperkatakan ini, Maha Suci Engkau (Ya Tuhan kami), ini adalah dusta yang besar\".'),
(2808, '024:017', '68', 24, 17, 'Allah memperingatkan kamu agar (jangan) kembali memperbuat yang seperti itu selama-lamanya, jika kamu orang-orang yang beriman.'),
(2809, '024:018', '68', 24, 18, 'dan Allah menerangkan ayat-ayat-Nya kepada kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(2810, '024:019', '68', 24, 19, 'Sesungguhnya orang-orang yang ingin agar (berita) perbuatan yang amat keji itu tersiar di kalangan orang-orang yang beriman, bagi mereka azab yang pedih di dunia dan di akhirat. Dan Allah mengetahui, sedang, kamu tidak mengetahui.'),
(2811, '024:020', '68', 24, 20, 'Dan sekiranya tidaklah karena kurnia Allah dan rahmat-Nya kepada kamu semua, dan Allah Maha Penyantun dan Maha Penyayang, (niscaya kamu akan ditimpa azab yang besar).'),
(2812, '024:021', '68', 24, 21, 'Hai orang-orang yang beriman, janganlah kamu mengikuti langkah-langkah syaitan. Barangsiapa yang mengikuti langkah-langkah syaitan, maka sesungguhnya syaitan itu menyuruh mengerjakan perbuatan yang keji dan yang mungkar. Sekiranya tidaklah karena kurnia Allah dan rahmat-Nya kepada kamu sekalian, niscaya tidak seorangpun dari kamu bersih (dari perbuatan-perbuatan keji dan mungkar itu) selama-lamanya, tetapi Allah membersihkan siapa yang dikehendaki-Nya. Dan Allah Maha Mendengar lagi Maha Mengetahui.'),
(2813, '024:022', '68', 24, 22, 'Dan janganlah orang-orang yang mempunyai kelebihan dan kelapangan di antara kamu bersumpah bahwa mereka (tidak) akan memberi (bantuan) kepada kaum kerabat(nya), orang-orang yang miskin dan orang-orang yang berhijrah pada jalan Allah, dan hendaklah mereka memaafkan dan berlapang dada. Apakah kamu tidak ingin bahwa Allah mengampunimu? Dan Allah adalah Maha Pengampun lagi Maha Penyayang,'),
(2814, '024:023', '68', 24, 23, 'Sesungguhnya orang-orang yang menuduh wanita yang baik-baik, yang lengah lagi beriman (berbuat zina), mereka kena laknat di dunia dan akhirat, dan bagi mereka azab yang besar,'),
(2815, '024:024', '68', 24, 24, 'pada hari (ketika), lidah, tangan dan kaki mereka menjadi saksi atas mereka terhadap apa yang dahulu mereka kerjakan.'),
(2816, '024:025', '68', 24, 25, 'Di hari itu, Allah akan memberi mereka balasan yag setimpal menurut semestinya, dan tahulah mereka bahwa Allah-lah yang Benar, lagi Yang menjelaskan (segala sesutatu menurut hakikat yang sebenarnya).'),
(2817, '024:026', '68', 24, 26, 'Wanita-wanita yang keji adalah untuk laki-laki yang keji, dan laki-laki yang keji adalah buat wanita-wanita yang keji (pula), dan wanita-wanita yang baik adalah untuk laki-laki yang baik dan laki-laki yang baik adalah untuk wanita-wanita yang baik (pula). Mereka (yang dituduh) itu bersih dari apa yang dituduhkan oleh mereka (yang menuduh itu). Bagi mereka ampunan dan rezeki yang mulia (surga).'),
(2818, '024:027', '68', 24, 27, 'Hai orang-orang yang beriman, janganlah kamu memasuki rumah yang bukan rumahmu sebelum meminta izin dan memberi salam kepada penghuninya. Yang demikian itu lebih baik bagimu, agar kamu (selalu) ingat.'),
(2819, '024:028', '68', 24, 28, 'Jika kamu tidak menemui seorangpun didalamnya, maka janganlah kamu masuk sebelum kamu mendapat izin. Dan jika dikatakan kepadamu: \"Kembali (saja)lah, maka hendaklah kamu kembali. Itu bersih bagimu dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(2820, '024:029', '68', 24, 29, 'Tidak ada dosa atasmu memasuki rumah yang tidak disediakan untuk didiami, yang di dalamnya ada keperluanmu, dan Allah mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan.'),
(2821, '024:030', '68', 24, 30, 'Katakanlah kepada orang laki-laki yang beriman: \"Hendaklah mereka menahan pandanganya, dan memelihara kemaluannya; yang demikian itu adalah lebih suci bagi mereka, sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat\".'),
(2822, '024:031', '68', 24, 31, 'Katakanlah kepada wanita yang beriman: \"Hendaklah mereka menahan pandangannya, dan kemaluannya, dan janganlah mereka menampakkan perhiasannya, kecuali yang (biasa) nampak dari padanya. Dan hendaklah mereka menutupkan kain kudung kedadanya, dan janganlah menampakkan perhiasannya kecuali kepada suami mereka, atau ayah mereka, atau ayah suami mereka, atau putera-putera mereka, atau putera-putera suami mereka, atau saudara-saudara laki-laki mereka, atau putera-putera saudara lelaki mereka, atau putera-putera saudara perempuan mereka, atau wanita-wanita islam, atau budak-budak yang mereka miliki, atau pelayan-pelayan laki-laki yang tidak mempunyai keinginan (terhadap wanita) atau anak-anak yang belum mengerti tentang aurat wanita. Dan janganlah mereka memukulkan kakinyua agar diketahui perhiasan yang mereka sembunyikan. Dan bertaubatlah kamu sekalian kepada Allah, hai orang-orang yang beriman supaya kamu beruntung.'),
(2823, '024:032', '68', 24, 32, 'Dan kawinkanlah orang-orang yang sedirian diantara kamu, dan orang-orang yang layak (berkawin) dari hamba-hamba sahayamu yang lelaki dan hamba-hamba sahayamu yang perempuan. Jika mereka miskin Allah akan memampukan mereka dengan kurnia-Nya. Dan Allah Maha luas (pemberian-Nya) lagi Maha Mengetahui.'),
(2824, '024:033', '68', 24, 33, 'Dan orang-orang yang tidak mampu kawin hendaklah menjaga kesucian (diri)nya, sehingga Allah memampukan mereka dengan karunia-Nya. Dan budak-budak yang kamu miliki yang memginginkan perjanjian, hendaklah kamu buat perjanjian dengan mereka, jika kamu mengetahui ada kebaikan pada mereka, dan berikanlah kepada mereka sebahagian dari harta Allah yang dikaruniakan-Nya kepadamu. Dan janganlah kamu paksa budak-budak wanitamu untuk melakukan pelacuran, sedang mereka sendiri mengingini kesucian, karena kamu hendak mencari keuntungan duniawi. Dan barangsiapa yang memaksa mereka, maka sesungguhnya Allah adalah Maha Pengampun lagi Maha Penyayang (kepada mereka) sesudah mereka dipaksa itu.'),
(2825, '024:034', '68', 24, 34, 'Dan sesungguhnya Kami telah menurunkan kepada kamu ayat-ayat yang memberi penerangan, dan contoh-contoh dari orang-orang yang terdahulu sebelum kamu dan pelajaran bagi orang-orang yang bertakwa.'),
(2826, '024:035', '68', 24, 35, 'Allah (Pemberi) cahaya (kepada) langit dan bumi. Perumpamaan cahaya Allah, adalah seperti sebuah lubang yang tak tembus, yang di dalamnya ada pelita besar. Pelita itu di dalam kaca (dan) kaca itu seakan-akan bintang (yang bercahaya) seperti mutiara, yang dinyalakan dengan minyak dari pohon yang berkahnya, (yaitu) pohon zaitun yang tumbuh tidak di sebelah timur (sesuatu) dan tidak pula di sebelah barat(nya), yang minyaknya (saja) hampir-hampir menerangi, walaupun tidak disentuh api. Cahaya di atas cahaya (berlapis-lapis), Allah membimbing kepada cahaya-Nya siapa yang dia kehendaki, dan Allah memperbuat perumpamaan-perumpamaan bagi manusia, dan Allah Maha Mengetahui segala sesuatu.'),
(2827, '024:036', '68', 24, 36, 'Bertasbih kepada Allah di masjid-masjid yang telah diperintahkan untuk dimuliakan dan disebut nama-Nya di dalamnya, pada waktu pagi dan waktu petang,'),
(2828, '024:037', '68', 24, 37, 'laki-laki yang tidak dilalaikan oleh perniagaan dan tidak (pula) oleh jual beli dari mengingati Allah, dan (dari) mendirikan sembahyang, dan (dari) membayarkan zakat. Mereka takut kepada suatu hari yang (di hari itu) hati dan penglihatan menjadi goncang.'),
(2829, '024:038', '68', 24, 38, '(Meraka mengerjakan yang demikian itu) supaya Allah memberikan balasan kepada mereka (dengan balasan) yang lebih baik dari apa yang telah mereka kerjakan, dan supaya Allah menambah karunia-Nya kepada mereka. Dan Allah memberi rezeki kepada siapa yang dikehendaki-Nya tanpa batas.'),
(2830, '024:039', '68', 24, 39, 'Dan orang-orang kafir amal-amal mereka adalah laksana fatamorgana di tanah yang datar, yang disangka air oleh orang-orang yang dahaga, tetapi bila didatanginya air itu dia tidak mendapatinya sesuatu apapun. Dan didapatinya (ketetapan) Allah disisinya, lalu Allah memberikan kepadanya perhitungan amal-amal dengan cukup dan Allah adalah sangat cepat perhitungan-Nya.'),
(2831, '024:040', '68', 24, 40, 'Atau seperti gelap gulita di lautan yang dalam, yang diliputi oleh ombak, yang di atasnya ombak (pula), di atasnya (lagi) awan; gelap gulita yang tindih-bertindih, apabila dia mengeluarkan tangannya, tiadalah dia dapat melihatnya, (dan) barangsiapa yang tiada diberi cahaya (petunjuk) oleh Allah tiadalah dia mempunyai cahaya sedikitpun.'),
(2832, '024:041', '68', 24, 41, 'Tidaklah kamu tahu bahwasanya Allah: kepada-Nya bertasbih apa yang di langit dan di bumi dan (juga) burung dengan mengembangkan sayapnya. Masing-masing telah mengetahui (cara) sembahyang dan tasbihnya, dan Allah Maha Mengetahui apa yang mereka kerjakan.'),
(2833, '024:042', '68', 24, 42, 'Dan kepunyaan Allah-lah kerajaan langit dan bumi dan kepada Allah-lah kembali (semua makhluk).'),
(2834, '024:043', '68', 24, 43, 'Tidaklah kamu melihat bahwa Allah mengarak awan, kemudian mengumpulkan antara (bagian-bagian)nya, kemudian menjadikannya bertindih-tindih, maka kelihatanlah olehmu hujan keluar dari celah-celahnya dan Allah (juga) menurunkan (butiran-butiran) es dari langit, (yaitu) dari (gumpalan-gumpalan awan seperti) gunung-gunung, maka ditimpakan-Nya (butiran-butiran) es itu kepada siapa yang dikehendaki-Nya dan dipalingkan-Nya dari siapa yang dikehendaki-Nya. Kilauan kilat awan itu hampir-hampir menghilangkan penglihatan.'),
(2835, '024:044', '68', 24, 44, 'Allah mempergantikan malam dan siang. Sesungguhnya pada yang demikian itu terdapat pelajaran yang besar bagi orang-orang yang mempunyai penglihatan.'),
(2836, '024:045', '68', 24, 45, 'Dan Allah telah menciptakan semua jenis hewan dari air, maka sebagian dari hewan itu ada yang berjalan di atas perutnya dan sebagian berjalan dengan dua kaki sedang sebagian (yang lain) berjalan dengan empat kaki. Allah menciptakan apa yang dikehendaki-Nya, sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(2837, '024:046', '68', 24, 46, 'Sesungguhnya Kami telah menurunkan ayat-ayat yang menjelaskan. Dan Allah memimpin siapa yang dikehendaki-Nya kepada jalan yang lurus.'),
(2838, '024:047', '68', 24, 47, 'Dan mereka berkata: \"Kami telah beriman kepada Allah dan rasul, dan kami mentaati (keduanya)\". Kemudian sebagian dari mereka berpaling sesudah itu, sekali-kali mereka itu bukanlah orang-orang yang beriman.'),
(2839, '024:048', '68', 24, 48, 'Dan apabila mereka dipanggil kepada Allah dan rasul-Nya, agar rasul menghukum (mengadili) di antara mereka, tiba-tiba sebagian dari mereka menolak untuk datang.'),
(2840, '024:049', '68', 24, 49, 'Tetapi jika keputusan itu untuk (kemaslahatan) mereka, mereka datang kepada rasul dengan patuh.'),
(2841, '024:050', '68', 24, 50, 'Apakah (ketidak datangan mereka itu karena) dalam hati mereka ada penyakit, atau (karena) mereka ragu-ragu ataukah (karena) takut kalau-kalau Allah dan rasul-Nya berlaku zalim kepada mereka? Sebenarnya, mereka itulah orang-orang yang zalim.'),
(2842, '024:051', '68', 24, 51, 'Sesungguhnya jawaban oran-orang mukmin, bila mereka dipanggil kepada Allah dan rasul-Nya agar rasul menghukum (mengadili) di antara mereka ialah ucapan. \"Kami mendengar, dan kami patuh\". Dan mereka itulah orang-orang yang beruntung.'),
(2843, '024:052', '68', 24, 52, 'Dan barang siapa yang taat kepada Allah dan rasul-Nya dan takut kepada Allah dan bertakwa kepada-Nya, maka mereka adalah orang-orang yang mendapat kemenangan.'),
(2844, '024:053', '68', 24, 53, 'Dan mereka bersumpah dengan nama Allah sekuat-kuat sumpah, jika kamu suruh mereka berperang, pastilah mereka akan pergi. Katakanlah: \"Janganlah kamu bersumpah, (karena ketaatan yang diminta ialah) ketaatan yang sudah dikenal. Sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(2845, '024:054', '68', 24, 54, 'Katakanlah: \"Taat kepada Allah dan taatlah kepada rasul; dan jika kamu berpaling maka sesungguhnya kewajiban rasul itu adalah apa yang dibebankan kepadanya, dan kewajiban kamu sekalian adalah semata-mata apa yang dibebankan kepadamu. Dan jika kamu taat kepadanya, niscaya kamu mendapat petunjuk. Dan tidak lain kewajiban rasul itu melainkan menyampaikan (amanat Allah) dengan terang\".'),
(2846, '024:055', '68', 24, 55, 'Dan Allah telah berjanji kepada orang-orang yang beriman di antara kamu dan mengerjakan amal-amal yang saleh bahwa Dia sungguh-sungguh akan menjadikan mereka berkuasa dimuka bumi, sebagaimana Dia telah menjadikan orang-orang sebelum mereka berkuasa, dan sungguh Dia akan meneguhkan bagi mereka agama yang telah diridhai-Nya untuk mereka, dan Dia benar-benar akan menukar (keadaan) mereka, sesudah mereka dalam ketakutan menjadi aman sentausa. Mereka tetap menyembahku-Ku dengan tiada mempersekutukan sesuatu apapun dengan Aku. Dan barangsiapa yang (tetap) kafir sesudah (janji) itu, maka mereka itulah orang-orang yang fasik.'),
(2847, '024:056', '68', 24, 56, 'Dan dirikanlah sembahyang, tunaikanlah zakat, dan taatlah kepada rasul, supaya kamu diberi rahmat.'),
(2848, '024:057', '68', 24, 57, 'Janganlah kamu kira bahwa orang-orang yang kafir itu dapat melemahkan (Allah dari mengazab mereka) di bumi ini, sedang tempat tinggal mereka (di akhirat) adalah neraka. Dan sungguh amat jeleklah tempat kembali itu.'),
(2849, '024:058', '68', 24, 58, 'Hai orang-orang yang beriman, hendaklah budak-budak (lelaki dan wanita) yang kamu miliki, dan orang-orang yang belum balig di antara kamu, meminta izin kepada kamu tiga kali (dalam satu hari) yaitu: sebelum sembahyang subuh, ketika kamu menanggalkan pakaian (luar)mu di tengah hari dan sesudah sembahyang Isya´. (Itulah) tiga aurat bagi kamu. Tidak ada dosa atasmu dan tidak (pula) atas mereka selain dari (tiga waktu) itu. Mereka melayani kamu, sebahagian kamu (ada keperluan) kepada sebahagian (yang lain). Demikianlah Allah menjelaskan ayat-ayat bagi kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(2850, '024:059', '68', 24, 59, 'Dan apabila anak-anakmu telah sampai umur balig, maka hendaklah mereka meminta izin, seperti orang-orang yang sebelum mereka meminta izin. Demikianlah Allah menjelaskan ayat-ayat-Nya. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(2851, '024:060', '68', 24, 60, 'Dan perempuan-perempuan tua yang telah terhenti (dari haid dan mengandung) yang tiada ingin kawin (lagi), tiadalah atas mereka dosa menanggalkan pakaian mereka dengan tidak (bermaksud) menampakkan perhiasan, dan berlaku sopan adalah lebih baik bagi mereka. Dan Allah Maha Mendengar lagi Maha Bijaksana.'),
(2852, '024:061', '68', 24, 61, 'Tidak ada halangan bagi orang buta, tidak (pula) bagi orang pincang, tidak (pula) bagi orang sakit, dan tidak (pula) bagi dirimu sendiri, makan (bersama-sama mereka) dirumah kamu sendiri atau dirumah bapak-bapakmu, dirumah ibu-ibumu, dirumah saudara-saudaramu yang laki-laki, di rumah saudaramu yang perempuan, dirumah saudara bapakmu yang laki-laki, dirumah saudara bapakmu yang perempuan, dirumah saudara ibumu yang laki-laki, dirumah saudara ibumu yang perempuan, dirumah yang kamu miliki kuncinya atau dirumah kawan-kawanmu. Tidak ada halangan bagi kamu makan bersama-sama mereka atau sendirian. Maka apabila kamu memasuki (suatu rumah dari) rumah-rumah (ini) hendaklah kamu memberi salam kepada (penghuninya yang berarti memberi salam) kepada dirimu sendiri, salam yang ditetapkan dari sisi Allah, yang diberi berkat lagi baik. Demikianlah Allah menjelaskan ayat-ayatnya(Nya) bagimu, agar kamu memahaminya.'),
(2853, '024:062', '68', 24, 62, 'Sesungguhnya yang sebenar-benar orang mukmin ialah orang-orang yang beriman kepada Allah dan Rasul-Nya, dan apabila mereka berada bersama-sama Rasulullah dalam sesuatu urusan yang memerlukan pertemuan, mereka tidak meninggalkan (Rasulullah) sebelum meminta izin kepadanya. Sesungguhnya orang-orang yang meminta izin kepadamu (Muhammad) mereka itulah orang-orang yang beriman kepada Allah dan rasul-Nya, maka apabila mereka meminta izin kepadamu karena sesuatu keperluan, berilah izin kepada siapa yang kamu kehendaki di antara mereka, dan mohonkanlah ampunan untuk mereka kepada Allah. Sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(2854, '024:063', '68', 24, 63, 'Janganlah kamu jadikan panggilan Rasul diantara kamu seperti panggilan sebahagian kamu kepada sebahagian (yang lain). Sesungguhnya Allah telah mengetahui orang-orang yang berangsur-angsur pergi di antara kamu dengan berlindung (kepada kawannya), maka hendaklah orang-orang yang menyalahi perintah Rasul takut akan ditimpa cobaan atau ditimpa azab yang pedih.'),
(2855, '024:064', '68', 24, 64, 'Ketahuilah sesungguhnya kepunyaan Allahlah apa yang di langit dan di bumi. Sesungguhnya Dia mengetahui keadaan yang kamu berada di dalamnya (sekarang). Dan (mengetahui pula) hati (manusia) dikembalikan kepada-Nya, lalu diterangkan-Nya kepada mereka apa yang telah mereka kerjakan. Dan Allah Maha mengehui segala sesuatu.'),
(2856, '025:001', '68', 25, 1, 'Maha suci Allah yang telah menurunkan Al Furqaan (Al Quran) kepada hamba-Nya, agar dia menjadi pemberi peringatan kepada seluruh alam,'),
(2857, '025:002', '68', 25, 2, 'yang kepunyaan-Nya-lah kerajaan langit dan bumi, dan Dia tidak mempunyai anak, dan tidak ada sekutu bagi-Nya dalam kekuasaan(Nya), dan dia telah menciptakan segala sesuatu, dan Dia menetapkan ukuran-ukurannya dengan serapi-rapinya.'),
(2858, '025:003', '68', 25, 3, 'Kemudian mereka mengambil tuhan-tuhan selain daripada-Nya (untuk disembah), yang tuhan-tuhan itu tidak menciptakan apapun, bahkan mereka sendiri diciptakan dan tidak kuasa untuk (menolak) sesuatu kemudharatan dari dirinya dan tidak (pula untuk mengambil) suatu kemanfaatanpun dan (juga) tidak kuasa mematikan, menghidupkan dan tidak (pula) membangkitkan.'),
(2859, '025:004', '68', 25, 4, 'Dan orang-orang kafir berkata: \"Al Quran ini tidak lain hanyalah kebohongan yang diada-adakan oleh Muhammad dan dia dibantu oleh kaum yang lain\"; maka sesungguhnya mereka telah berbuat suatu kezaliman dan dusta yang besar.'),
(2860, '025:005', '68', 25, 5, 'Dan mereka berkata: \"Dongengan-dongengan orang-orang dahulu, dimintanya supaya dituliskan, maka dibacakanlah dongengan itu kepadanya setiap pagi dan petang\".'),
(2861, '025:006', '68', 25, 6, 'Katakanlah: \"Al Quran itu diturunkan oleh (Allah) yang mengetahui rahasia di langit dan di bumi. Sesungguhnya Dia adalah Maha Pengampun lagi Maha Penyayang\".'),
(2862, '025:007', '68', 25, 7, 'Dan mereka berkata: \"Mengapa rasul itu memakan makanan dan berjalan di pasar-pasar? Mengapa tidak diturunkan kepadanya seorang malaikat agar malaikat itu memberikan peringatan bersama-sama dengan dia?,'),
(2863, '025:008', '68', 25, 8, 'atau (mengapa tidak) diturunkan kepadanya perbendaharaan, atau (mengapa tidak) ada kebun baginya, yang dia dapat makan dari (hasil)nya?\" Dan orang-orang yang zalim itu berkata: \"Kamu sekalian tidak lain hanyalah mengikuti seorang lelaki yang kena sihir\".'),
(2864, '025:009', '68', 25, 9, 'Perhatikanlah, bagaimana mereka membuat perbandingan-perbandingan tentang kamu, lalu sesatlah mereka, mereka tidak sanggup (mendapatkan) jalan (untuk menentang kerasulanmu).'),
(2865, '025:010', '68', 25, 10, 'Maha Suci (Allah) yang jika Dia menghendaki, niscaya dijadikan-Nya bagimu yang lebih baik dari yang demikian, (yaitu) surga-surga yang mengalir sungai-sungai di bawahnya, dan dijadikan-Nya (pula) untukmu istana-istana.'),
(2866, '025:011', '68', 25, 11, 'Bahkan mereka mendustakan hari kiamat. Dan kami menyediakan neraka yang menyala-nyala bagi siapa yang mendustakan hari kiamat.'),
(2867, '025:012', '68', 25, 12, 'Apabila neraka itu melihat mereka dari tempat yang jauh, mereka mendengar kegeramannya dan suara nyalanya.'),
(2868, '025:013', '68', 25, 13, 'Dan apabila mereka dilemparkan ke tempat yang sempit di neraka itu dengan dibelenggu, mereka di sana mengharapkan kebinasaan.'),
(2869, '025:014', '68', 25, 14, '(Akan dikatakan kepada mereka): \"Jangan kamu sekalian mengharapkan satu kebinasaan, melainkan harapkanlah kebinasaan yang banyak\".'),
(2870, '025:015', '68', 25, 15, 'Katakanlah: \"Apa (azab) yang demikian itukah yang baik, atau surga yang kekal yang telah dijanjikan kepada orang-orang yang bertakwa?\" Dia menjadi balasan dan tempat kembali bagi mereka?\".'),
(2871, '025:016', '68', 25, 16, 'Bagi mereka di dalam surga itu apa yang mereka kehendaki, sedang mereka kekal (di dalamnya). (Hal itu) adalah janji dari Tuhanmu yang patut dimohonkan (kepada-Nya).'),
(2872, '025:017', '68', 25, 17, 'Dan (ingatlah) suatu hari (ketika) Allah menghimpunkan mereka beserta apa yang mereka sembah selain Allah, lalu Allah berkata (kepada yang disembah); \"Apakah kamu yang menyesatkan hamba-hamba-Ku itu, atau mereka sendirikah yang sesat dari jalan (yang benar)?\".'),
(2873, '025:018', '68', 25, 18, 'Mereka (yang disembah itu) menjawab: \"Maha Suci Engkau, tidaklah patut bagi kami mengambil selain engkau (untuk jadi) pelindung, akan tetapi Engkau telah memberi mereka dan bapak-bapak mereka kenikmatan hidup, sampai mereka lupa mengingati (Engkau); dan mereka adalah kaum yang binasa\".'),
(2874, '025:019', '68', 25, 19, 'maka sesungguhnya mereka (yang disembah itu) telah mendustakan kamu tentang apa yang kamu katakan maka kamu tidak akan dapat menolak (azab) dan tidak (pula) menolong (dirimu), dan barang siapa di antara kamu yang berbuat zalim, niscaya Kami rasakan kepadanya azab yang besar.'),
(2875, '025:020', '68', 25, 20, 'Dan Kami tidak mengutus rasul-rasul sebelummu, melainkan mereka sungguh memakan makanan dan berjalan di pasar-pasar. Dan kami jadikan sebahagian kamu cobaan bagi sebahagian yang lain. Maukah kamu bersabar?; dan adalah Tuhanmu maha Melihat.'),
(2876, '025:021', '68', 25, 21, 'Berkatalah orang-orang yang tidak menanti-nanti pertemuan(nya) dengan Kami: \"Mengapakah tidak diturunkan kepada kita malaikat atau (mengapa) kita (tidak) melihat Tuhan kita?\" Sesungguhnya mereka memandang besar tentang diri mereka dan mereka benar-benar telah melampaui batas(dalam melakukan) kezaliman\".'),
(2877, '025:022', '68', 25, 22, 'Pada hari mereka melihat malaikat dihari itu tidak ada kabar gembira bagi orang-orang yang berdosa mereka berkata: \"Hijraan mahjuuraa.'),
(2878, '025:023', '68', 25, 23, 'Dan kami hadapi segala amal yang mereka kerjakan, lalu kami jadikan amal itu (bagaikan) debu yang berterbangan.'),
(2879, '025:024', '68', 25, 24, 'Penghuni-penghuni surga pada hari itu palig baik tempat tinggalnya dan paling indah tempat istirahatnya.'),
(2880, '025:025', '68', 25, 25, 'Dan (ingatlah) hari (ketika) langit pecah belah mengeluarkan kabut putih dan diturunkanlah malaikat bergelombang-gelombang.'),
(2881, '025:026', '68', 25, 26, 'Kerajaan yang hak pada hari itu adalah kepunyaan Tuhan Yang Maha Pemurah. Dan adalah (hari itu), satu hari penuh kesukaran bagi orang-orang kafir.'),
(2882, '025:027', '68', 25, 27, 'Dan (ingatlah) hari (ketika itu) orang yang zalim menggigit dua tangannya, seraya berkata: \"Aduhai kiranya (dulu) aku mengambil jalan bersama-sama Rasul\".'),
(2883, '025:028', '68', 25, 28, 'Kecelakaan besarlah bagiku; kiranya aku (dulu) tidak menjadikan sifulan itu teman akrab(ku).'),
(2884, '025:029', '68', 25, 29, 'Sesungguhnya dia telah menyesatkan aku dari Al Quran ketika Al Quran itu telah datang kepadaku. Dan adalah syaitan itu tidak mau menolong manusia.'),
(2885, '025:030', '68', 25, 30, 'Berkatalah Rasul: \"Ya Tuhanku, sesungguhnya kaumku menjadikan Al Quran itu sesuatu yang tidak diacuhkan\".'),
(2886, '025:031', '68', 25, 31, 'Dan seperti itulah, telah Kami adakan bagi tiap-tiap nabi, musuh dari orang-orang yang berdosa. Dan cukuplah Tuhanmu menjadi Pemberi petunjuk dan Penolong.'),
(2887, '025:032', '68', 25, 32, 'Berkatalah orang-orang yang kafir: \"Mengapa Al Quran itu tidak diturunkan kepadanya sekali turun saja?\"; demikianlah supaya Kami perkuat hatimu dengannya dan Kami membacanya secara tartil (teratur dan benar).'),
(2888, '025:033', '68', 25, 33, 'Tidaklah orang-orang kafir itu datang kepadamu (membawa) sesuatu yang ganjil, melainkan Kami datangkan kepadamu suatu yang benar dan yang paling baik penjelasannya.'),
(2889, '025:034', '68', 25, 34, 'Orang-orang yang dihimpunkan ke neraka Jahannam dengan diseret atas muka-muka mereka, mereka itulah orang yang paling buruk tempatnya dan paling sesat jalannya.'),
(2890, '025:035', '68', 25, 35, 'Dan sesungguhnya kami telah memberikan Al Kitab (Taurat) kepada Musa dan Kami telah menjadikan Harun saudaranya, menyertai dia sebagai wazir (pembantu).'),
(2891, '025:036', '68', 25, 36, 'Kemudian Kami berfirman kepada keduanya: \"Pergilah kamu berdua kepada kaum yang mendustakan ayat-ayat Kami\". Maka Kami membinasakan mereka sehancur-hancurnya.'),
(2892, '025:037', '68', 25, 37, 'Dan (telah Kami binasakan) kaum Nuh tatkala mereka mendustakan rasul-rasul. Kami tenggelamkan mereka dan kami jadikan (cerita) mereka itu pelajaran bagi manusia. Dan Kami telah menyediakan bagi orang-orang zalim azab yang pedih;'),
(2893, '025:038', '68', 25, 38, 'dan (Kami binasakan) kaum ´Aad dan Tsamud dan penduduk Rass dan banyak (lagi) generasi-generasi di antara kaum-kaum tersebut.'),
(2894, '025:039', '68', 25, 39, 'Dan Kami jadikan bagi masing-masing mereka perumpamaan dan masing-masing mereka itu benar benar telah Kami binasakan dengan sehancur-hancurnya.'),
(2895, '025:040', '68', 25, 40, 'Dan sesungguhnya mereka (kaum musyrik Mekah) telah melalui sebuah negeri (Sadum) yang (dulu) dihujani dengan hujan yang sejelek-jeleknya (hujan batu). Maka apakah mereka tidak menyaksikan runtuhan itu; bahkan adalah mereka itu tidak mengharapkan akan kebangkitan.'),
(2896, '025:041', '68', 25, 41, 'Dan apabila mereka melihat kamu (Muhammad), mereka hanyalah menjadikan kamu sebagai ejekan (dengan mengatakan): \"Inikah orangnya yang di utus Allah sebagai Rasul?.'),
(2897, '025:042', '68', 25, 42, 'Sesungguhnya hampirlah ia menyesatkan kita dari sembahan-sembahan kita, seandainya kita tidak sabar(menyembah)nya\" dan mereka kelak akan mengetahui di saat mereka melihat azab, siapa yang paling sesat jalannya.'),
(2898, '025:043', '68', 25, 43, 'Terangkanlah kepadaku tentang orang yang menjadikan hawa nafsunya sebagai tuhannya. Maka apakah kamu dapat menjadi pemelihara atasnya?,'),
(2899, '025:044', '68', 25, 44, 'atau apakah kamu mengira bahwa kebanyakan mereka itu mendengar atau memahami. Mereka itu tidak lain, hanyalah seperti binatang ternak, bahkan mereka lebih sesat jalannya (dari binatang ternak itu).'),
(2900, '025:045', '68', 25, 45, 'Apakah kamu tidak memperhatikan (penciptaan) Tuhanmu, bagaimana Dia memanjangkan (dan memendekkan) bayang-bayang dan kalau Dia menghendaki niscaya Dia menjadikan tetap bayang-bayang itu, kemudian Kami jadikan matahari sebagai petunjuk atas bayang-bayang itu,'),
(2901, '025:046', '68', 25, 46, 'kemudian Kami menarik bayang-bayang itu kepada kami dengan tarikan yang perlahan-lahan.'),
(2902, '025:047', '68', 25, 47, 'Dialah yang menjadikan untukmu malam (sebagai) pakaian, dan tidur untuk istirahat, dan Dia menjadikan siang untuk bangun berusaha.'),
(2903, '025:048', '68', 25, 48, 'Dialah yang meniupkan angin (sebagai) pembawa kabar gembira dekat sebelum kedatangan rahmat-nya (hujan); dan Kami turunkan dari langit air yang amat bersih,'),
(2904, '025:049', '68', 25, 49, 'agar Kami menghidupkan dengan air itu negeri (tanah) yang mati, dan agar Kami memberi minum dengan air itu sebagian besar dari makhluk Kami, binatang-binatang ternak dan manusia yang banyak.'),
(2905, '025:050', '68', 25, 50, 'Dan sesungguhnya Kami telah mempergilirkan hujan itu diantara manusia supaya mereka mengambil pelajaran (dari padanya); maka kebanyakan manusia itu tidak mau kecuali mengingkari (nikmat).'),
(2906, '025:051', '68', 25, 51, 'Dan andaikata Kami menghendaki benar-benarlah Kami utus pada tiap-tiap negeri seorang yang memberi peringatan (rasul).'),
(2907, '025:052', '68', 25, 52, 'Maka janganlah kamu mengikuti orang-orang kafir, dan berjihadlah terhadap mereka dengan Al Quran dengan jihad yang besar.'),
(2908, '025:053', '68', 25, 53, 'Dan Dialah yang membiarkan dua laut yang mengalir (berdampingan); yang ini tawar lagi segar dan yang lain asin lagi pahit; dan Dia jadikan antara keduanya dinding dan batas yang menghalangi.'),
(2909, '025:054', '68', 25, 54, 'Dan Dia (pula) yang menciptakan manusia dari air lalu dia jadikan manusia itu (punya) keturunan dan mushaharah dan adalah Tuhanmu Maha Kuasa.'),
(2910, '025:055', '68', 25, 55, 'Dan mereka menyembah selain Allah apa yang tidak memberi manfaat kepada mereka dan tidak (pula) memberi mudharat kepada mereka. Adalah orang-orang kafir itu penolong (syaitan untuk berbuat durhaka) terhadap Tuhannya.'),
(2911, '025:056', '68', 25, 56, 'Dan tidaklah Kami mengutus kamu melainkan hanya sebagai pembawa kabar gembira dan pemberi peringatan.'),
(2912, '025:057', '68', 25, 57, 'Katakanlah: \"Aku tidak meminta upah sedikitpun kepada kamu dalam menyampaikan risalah itu, melainkan (mengharapkan kepatuhan) orang-orang yang mau mengambil jalan kepada Tuhan nya.'),
(2913, '025:058', '68', 25, 58, 'Dan bertawakkallah kepada Allah yang hidup (kekal) Yang tidak mati, dan bertasbihlah dengan memuji-Nya. Dan cukuplah Dia Maha Mengetahui dosa-dosa hamba-hamba-Nya.'),
(2914, '025:059', '68', 25, 59, 'Yang menciptakan langit dan bumi dan apa yang ada antara keduanya dalam enam masa, kemudian dia bersemayam di atas ´Arsy, (Dialah) Yang Maha Pemurah, maka tanyakanlah (tentang Allah) kepada yang lebih mengetahui (Muhammad) tentang Dia.'),
(2915, '025:060', '68', 25, 60, 'Dan apabila dikatakan kepada mereka: \"Sujudlah kamu sekalian kepada yang Maha Penyayang\", mereka menjawab: \"Siapakah yang Maha Penyayang itu? Apakah kami akan sujud kepada Tuhan Yang kamu perintahkan kami(bersujud kepada-Nya)?\", dan (perintah sujud itu) menambah mereka jauh (dari iman).'),
(2916, '025:061', '68', 25, 61, 'Maha Suci Allah yang menjadikan di langit gugusan-gugusan bintang dan Dia menjadikan juga padanya matahari dan bulan yang bercahaya.'),
(2917, '025:062', '68', 25, 62, 'Dan Dia (pula) yang menjadikan malam dan siang silih berganti bagi orang yang ingin mengambil pelajaran atau orang yang ingin bersyukur.'),
(2918, '025:063', '68', 25, 63, 'Dan hamba-hamba Tuhan yang Maha Penyayang itu (ialah) orang-orang yang berjalan di atas bumi dengan rendah hati dan apabila orang-orang jahil menyapa mereka, mereka mengucapkan kata-kata (yang mengandung) keselamatan.'),
(2919, '025:064', '68', 25, 64, 'Dan orang yang melalui malam hari dengan bersujud dan berdiri untuk Tuhan mereka.'),
(2920, '025:065', '68', 25, 65, 'Dan orang-orang yang berkata: \"Ya Tuhan kami, jauhkan azab jahannam dari kami, sesungguhnya azabnya itu adalah kebinasaan yang kekal\".'),
(2921, '025:066', '68', 25, 66, 'Sesungguhnya jahannam itu seburuk-buruk tempat menetap dan tempat kediaman.'),
(2922, '025:067', '68', 25, 67, 'Dan orang-orang yang apabila membelanjakan (harta), mereka tidak berlebihan, dan tidak (pula) kikir, dan adalah (pembelanjaan itu) di tengah-tengah antara yang demikian.'),
(2923, '025:068', '68', 25, 68, 'Dan orang-orang yang tidak menyembah tuhan yang lain beserta Allah dan tidak membunuh jiwa yang diharamkan Allah (membunuhnya) kecuali dengan (alasan) yang benar, dan tidak berzina, barang siapa yang melakukan yang demikian itu, niscaya dia mendapat (pembalasan) dosa(nya),'),
(2924, '025:069', '68', 25, 69, '(yakni) akan dilipat gandakan azab untuknya pada hari kiamat dan dia akan kekal dalam azab itu, dalam keadaan terhina,'),
(2925, '025:070', '68', 25, 70, 'kecuali orang-orang yang bertaubat, beriman dan mengerjakan amal saleh; maka itu kejahatan mereka diganti Allah dengan kebajikan. Dan adalah Allah maha Pengampun lagi Maha Penyayang.'),
(2926, '025:071', '68', 25, 71, 'Dan orang-orang yang bertaubat dan mengerjakan amal saleh, maka sesungguhnya dia bertaubat kepada Allah dengan taubat yang sebenar-benarnya.'),
(2927, '025:072', '68', 25, 72, 'Dan orang-orang yang tidak memberikan persaksian palsu, dan apabila mereka bertemu dengan (orang-orang) yang mengerjakan perbuatan-perbuatan yang tidak berfaedah, mereka lalui (saja) dengan menjaga kehormatan dirinya.'),
(2928, '025:073', '68', 25, 73, 'Dan orang-orang yang apabila diberi peringatan dengan ayat-ayat Tuhan mereka, mereka tidaklah menghadapinya sebagai orang-orang yang tuli dan buta.'),
(2929, '025:074', '68', 25, 74, 'Dan orang orang yang berkata: \"Ya Tuhan kami, anugrahkanlah kepada kami isteri-isteri kami dan keturunan kami sebagai penyenang hati (kami), dan jadikanlah kami imam bagi orang-orang yang bertakwa.'),
(2930, '025:075', '68', 25, 75, 'Mereka itulah orang yang dibalasi dengan martabat yang tinggi (dalam surga) karena kesabaran mereka dan mereka disambut dengan penghormatan dan ucapan selamat di dalamnya,'),
(2931, '025:076', '68', 25, 76, 'mereka kekal di dalamnya. Surga itu sebaik-baik tempat menetap dan tempat kediaman.'),
(2932, '025:077', '68', 25, 77, 'Katakanlah (kepada orang-orang musyrik): \"Tuhanku tidak mengindahkan kamu, melainkan kalau ada ibadatmu. (Tetapi bagaimana kamu beribadat kepada-Nya), padahal kamu sungguh telah mendustakan-Nya? karena itu kelak (azab) pasti (menimpamu)\".'),
(2933, '026:001', '68', 26, 1, 'Thaa Siim Miim'),
(2934, '026:002', '68', 26, 2, 'Inilah ayat-ayat Al Quran yang menerangkan.'),
(2935, '026:003', '68', 26, 3, 'Boleh jadi kamu (Muhammad) akan membinasakan dirimu, karena mereka tidak beriman.'),
(2936, '026:004', '68', 26, 4, 'Jika kami kehendaki niscaya Kami menurunkan kepada mereka mukjizat dari langit, maka senantiasa kuduk-kuduk mereka tunduk kepadanya.'),
(2937, '026:005', '68', 26, 5, 'Dan sekali-kali tidak datang kepada mereka suatu peringatan baru dari Tuhan Yang Maha Pemurah, melainkan mereka selalu berpaling daripadanya.'),
(2938, '026:006', '68', 26, 6, 'Sungguh mereka telah mendustakan (Al Quran), maka kelak akan datang kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan.'),
(2939, '026:007', '68', 26, 7, 'Dan apakah mereka tidak memperhatikan bumi, berapakah banyaknya Kami tumbuhkan di bumi itu pelbagai macam tumbuh-tumbuhan yang baik?'),
(2940, '026:008', '68', 26, 8, 'Sesungguhnya pada yang demikian itu benar-benar terdapat suatu tanda kekuasaan Allah. Dan kebanyakan mereka tidak beriman.'),
(2941, '026:009', '68', 26, 9, 'Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(2942, '026:010', '68', 26, 10, 'Dan (ingatlah) ketika Tuhanmu menyeru Musa (dengan firman-Nya): \"Datangilah kaum yang zalim itu,'),
(2943, '026:011', '68', 26, 11, '(yaitu) kaum Fir´aun. Mengapa mereka tidak bertakwa?\"'),
(2944, '026:012', '68', 26, 12, 'Berkata Musa: \"Ya Tuhanku, sesungguhnya aku takut bahwa mereka akan mendustakan aku.'),
(2945, '026:013', '68', 26, 13, 'Dan (karenanya) sempitlah dadaku dan tidak lancar lidahku maka utuslah (Jibril) kepada Harun.'),
(2946, '026:014', '68', 26, 14, 'Dan aku berdosa terhadap mereka, maka aku takut mereka akan membunuhku\".'),
(2947, '026:015', '68', 26, 15, 'Allah berfirman: \"Jangan takut (mereka tidak akan dapat membunuhmu), maka pergilah kamu berdua dengan membawa ayat-ayat Kami (mukjizat-mukjizat); sesungguhnya Kami bersamamu mendengarkan (apa-apa yang mereka katakan),'),
(2948, '026:016', '68', 26, 16, 'Maka datanglah kamu berdua kepada Fir´aun dan katakanlah olehmu: \"Sesungguhnya Kami adalah Rasul Tuhan semesta alam,'),
(2949, '026:017', '68', 26, 17, 'lepaskanlah Bani Israil (pergi) beserta kami\".'),
(2950, '026:018', '68', 26, 18, 'Fir´aun menjawab: \"Bukankah kami telah mengasuhmu di antara (keluarga) kami, waktu kamu masih kanak-kanak dan kamu tinggal bersama kami beberapa tahun dari umurmu.'),
(2951, '026:019', '68', 26, 19, 'dan kamu telah berbuat suatu perbuatan yang telah kamu lakukan itu dan kamu termasuk golongan orang-orang yang tidak membalas guna.'),
(2952, '026:020', '68', 26, 20, 'Berkata Musa: \"Aku telah melakukannya, sedang aku di waktu itu termasuk orang-orang yang khilaf.'),
(2953, '026:021', '68', 26, 21, 'Lalu aku lari meninggalkan kamu ketika aku takut kepadamu, kemudian Tuhanku memberikan kepadaku ilmu serta Dia menjadikanku salah seorang di antara rasul-rasul.'),
(2954, '026:022', '68', 26, 22, 'Budi yang kamu limpahkan kepadaku itu adalah (disebabkan) kamu telah memperbudak Bani Israil\".'),
(2955, '026:023', '68', 26, 23, 'Fir´aun bertanya: \"Siapa Tuhan semesta alam itu?\"'),
(2956, '026:024', '68', 26, 24, 'Musa menjawab: \"Tuhan Pencipta langit dan bumi dan apa-apa yang di antara keduanya (Itulah Tuhanmu), jika kamu sekalian (orang-orang) mempercayai-Nya\".'),
(2957, '026:025', '68', 26, 25, 'Berkata Fir´aun kepada orang-orang sekelilingnya: \"Apakah kamu tidak mendengarkan?\"'),
(2958, '026:026', '68', 26, 26, 'Musa berkata (pula): \"Tuhan kamu dan Tuhan nenek-nenek moyang kamu yang dahulu\".'),
(2959, '026:027', '68', 26, 27, 'Fir´aun berkata: \"Sesungguhnya Rasulmu yang diutus kepada kamu sekalian benar-benar orang gila\".'),
(2960, '026:028', '68', 26, 28, 'Musa berkata: \"Tuhan yang menguasai timur dan barat dan apa yang ada di antara keduanya: (Itulah Tuhanmu) jika kamu mempergunakan akal\".'),
(2961, '026:029', '68', 26, 29, 'Fir´aun berkata: \"Sungguh jika kamu menyembah Tuhan selain aku, benar-benar aku akan menjadikan kamu salah seorang yang dipenjarakan\".'),
(2962, '026:030', '68', 26, 30, 'Musa berkata: \"Dan apakah (kamu akan melakukan itu) kendatipun aku tunjukkan kepadamu sesuatu (keterangan) yang nyata?\"'),
(2963, '026:031', '68', 26, 31, 'Fir´aun berkata: \"Datangkanlah sesuatu (keterangan) yang nyata itu, jika kamu adalah termasuk orang-orang yang benar\".'),
(2964, '026:032', '68', 26, 32, 'Maka Musa melemparkan tongkatnya, lalu tiba-tiba tongkat itu (menjadi) ular yang nyata.'),
(2965, '026:033', '68', 26, 33, 'Dan ia menarik tangannya (dari dalam bajunya), maka tiba-tiba tangan itu jadi putih (bersinar) bagi orang-orang yang melihatnya.'),
(2966, '026:034', '68', 26, 34, 'Fir´aun berkata kepada pembesar-pembesar yang berada sekelilingnya: Sesungguhnya Musa ini benar-benar seorang ahli sihir yang pandai,'),
(2967, '026:035', '68', 26, 35, 'ia hendak mengusir kamu dari negerimu sendiri dengan sihirnya; maka karena itu apakah yang kamu anjurkan?\"'),
(2968, '026:036', '68', 26, 36, 'Mereka menjawab: \"Tundalah (urusan) dia dan saudaranya dan kirimkanlah ke seluruh negeri orang-orang yang akan mengumpulkan (ahli sihir),'),
(2969, '026:037', '68', 26, 37, 'niscaya mereka akan mendatangkan semua ahli sihir yang pandai kepadamu\".'),
(2970, '026:038', '68', 26, 38, 'Lalu dikumpulkan ahli-ahli sihir pada waktu yang ditetapkan di hari yang maklum,'),
(2971, '026:039', '68', 26, 39, 'dan dikatakan kepada orang banyak: \"Berkumpullah kamu sekalian.'),
(2972, '026:040', '68', 26, 40, 'semoga kita mengikuti ahli-ahli sihir jika mereka adalah orang-orang yang menang\"'),
(2973, '026:041', '68', 26, 41, 'Maka tatkala ahli-ahli sihir datang, merekapun bertanya kepada Fir´aun: \"Apakah kami sungguh-sungguh mendapat upah yang besar jika kami adalah orang-orang yang menang?\"'),
(2974, '026:042', '68', 26, 42, 'Fir´aun menjawab: \"Ya, kalau demikian, sesungguhnya kamu sekalian benar-benar akan menjadi orang yang didekatkan (kepadaku)\".'),
(2975, '026:043', '68', 26, 43, 'Berkatalah Musa kepada mereka: \"Lemparkanlah apa yang hendak kamu lemparkan\".'),
(2976, '026:044', '68', 26, 44, 'Lalu mereka melemparkan tali temali dan tongkat-tongkat mereka dan berkata: \"Demi kekuasaan Fir´aun, sesungguhnya kami benar-benar akan menang\".'),
(2977, '026:045', '68', 26, 45, 'Kemudian Musa menjatuhkan tongkatnya maka tiba-tiba ia menelan benda-benda palsu yang mereka ada-adakan itu.'),
(2978, '026:046', '68', 26, 46, 'Maka tersungkurlah ahli-ahli sihir sambil bersujud (kepada Allah),'),
(2979, '026:047', '68', 26, 47, 'mereka berkata: \"Kami beriman kepada Tuhan semesta alam,'),
(2980, '026:048', '68', 26, 48, '(yaitu) Tuhan Musa dan Harun\".'),
(2981, '026:049', '68', 26, 49, 'Fir´aun berkata: \"Apakah kamu sekalian beriman kepada Musa sebelum aku memberi izin kepadamu? Sesungguhnya dia benar-benar pemimpinmu yang mengajarkan sihir kepadamu maka kamu nanti pasti benar-benar akan mengetahui (akibat perbuatanmu); sesungguhnya aku akan memotong tanganmu dan kakimu dengan bersilangan dan aku akan menyalibmu semuanya\".'),
(2982, '026:050', '68', 26, 50, 'Mereka berkata: \"Tidak ada kemudharatan (bagi kami); sesungguhnya kami akan kembali kepada Tuhan kami,'),
(2983, '026:051', '68', 26, 51, 'sesungguhnya kami amat menginginkan bahwa Tuhan kami akan mengampuni kesalahan kami, karena kami adalah orang-orang yang pertama-tama beriman\".'),
(2984, '026:052', '68', 26, 52, 'Dan Kami wahyukan (perintahkan) kepada Musa: \"Pergilah di malam hari dengan membawa hamba-hamba-Ku (Bani Israil), karena sesungguhnya kamu sekalian akan disusuli\".'),
(2985, '026:053', '68', 26, 53, 'Kemudian Fir´aun mengirimkan orang yang mengumpulkan (tentaranya) ke kota-kota.'),
(2986, '026:054', '68', 26, 54, '(Fir´aun berkata): \"Sesungguhnya mereka (Bani Israil) benar-benar golongan kecil,'),
(2987, '026:055', '68', 26, 55, 'dan sesungguhnya mereka membuat hal-hal yang menimbulkan amarah kita,'),
(2988, '026:056', '68', 26, 56, 'dan sesungguhnya kita benar-benar golongan yang selalu berjaga-jaga\".'),
(2989, '026:057', '68', 26, 57, 'Maka Kami keluarkan Fir´aun dan kaumnya dari taman-taman dan mata air,'),
(2990, '026:058', '68', 26, 58, 'dan (dari) perbendaharaan dan kedudukan yang mulia,'),
(2991, '026:059', '68', 26, 59, 'demikianlah halnya dan Kami anugerahkan semuanya (itu) kepada Bani Israil.'),
(2992, '026:060', '68', 26, 60, 'Maka Fir´aun dan bala tentaranya dapat menyusuli mereka di waktu matahari terbit.'),
(2993, '026:061', '68', 26, 61, 'Maka setelah kedua golongan itu saling melihat, berkatalah pengikut-pengikut Musa: \"Sesungguhnya kita benar-benar akan tersusul\".'),
(2994, '026:062', '68', 26, 62, 'Musa menjawab: \"Sekali-kali tidak akan tersusul; sesungguhnya Tuhanku besertaku, kelak Dia akan memberi petunjuk kepadaku\".'),
(2995, '026:063', '68', 26, 63, 'Lalu Kami wahyukan kepada Musa: \"Pukullah lautan itu dengan tongkatmu\". Maka terbelahlah lautan itu dan tiap-tiap belahan adalah seperti gunung yang besar.'),
(2996, '026:064', '68', 26, 64, 'Dan di sanalah Kami dekatkan golongan yang lain.'),
(2997, '026:065', '68', 26, 65, 'Dan Kami selamatkan Musa dan orang-orang yang besertanya semuanya.'),
(2998, '026:066', '68', 26, 66, 'Dan Kami tenggelamkan golongan yang lain itu.'),
(2999, '026:067', '68', 26, 67, 'Sesungguhnya pada yang demikian itu benar-benar merupakan suatu tanda yang besar (mukjizat) dan tetapi adalah kebanyakan mereka tidak beriman.'),
(3000, '026:068', '68', 26, 68, 'Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3001, '026:069', '68', 26, 69, 'Dan bacakanlah kepada mereka kisah Ibrahim.'),
(3002, '026:070', '68', 26, 70, 'Ketika ia berkata kepada bapaknya dan kaumnya: \"Apakah yang kamu sembah?\"'),
(3003, '026:071', '68', 26, 71, 'Mereka menjawab: \"Kami menyembah berhala-berhala dan kami senantiasa tekun menyembahnya\".'),
(3004, '026:072', '68', 26, 72, 'Berkata Ibrahim: \"Apakah berhala-berhala itu mendengar (doa)mu sewaktu kamu berdoa (kepadanya)?,'),
(3005, '026:073', '68', 26, 73, 'atau (dapatkah) mereka memberi manfaat kepadamu atau memberi mudharat?\"'),
(3006, '026:074', '68', 26, 74, 'Mereka menjawab: \"(Bukan karena itu) sebenarnya kami mendapati nenek moyang kami berbuat demikian\".'),
(3007, '026:075', '68', 26, 75, 'Ibrahim berkata: \"Maka apakah kamu telah memperhatikan apa yang selalu kamu sembah,'),
(3008, '026:076', '68', 26, 76, 'kamu dan nenek moyang kamu yang dahulu?,'),
(3009, '026:077', '68', 26, 77, 'karena sesungguhnya apa yang kamu sembah itu adalah musuhku, kecuali Tuhan Semesta Alam,'),
(3010, '026:078', '68', 26, 78, '(yaitu Tuhan) Yang telah menciptakan aku, maka Dialah yang menunjuki aku,'),
(3011, '026:079', '68', 26, 79, 'dan Tuhanku, Yang Dia memberi makan dan minum kepadaku,'),
(3012, '026:080', '68', 26, 80, 'dan apabila aku sakit, Dialah Yang menyembuhkan aku,'),
(3013, '026:081', '68', 26, 81, 'dan Yang akan mematikan aku, kemudian akan menghidupkan aku (kembali),'),
(3014, '026:082', '68', 26, 82, 'dan Yang amat kuinginkan akan mengampuni kesalahanku pada hari kiamat\".'),
(3015, '026:083', '68', 26, 83, '(Ibrahim berdoa): \"Ya Tuhanku, berikanlah kepadaku hikmah dan masukkanlah aku ke dalam golongan orang-orang yang saleh,'),
(3016, '026:084', '68', 26, 84, 'dan jadikanlah aku buah tutur yang baik bagi orang-orang (yang datang) kemudian,'),
(3017, '026:085', '68', 26, 85, 'dan jadikanlah aku termasuk orang-orang yang mempusakai surga yang penuh kenikmatan,'),
(3018, '026:086', '68', 26, 86, 'dan ampunilah bapakku, karena sesungguhnya ia adalah termasuk golongan orang-orang yang sesat,'),
(3019, '026:087', '68', 26, 87, 'dan janganlah Engkau hinakan aku pada hari mereka dibangkitkan,'),
(3020, '026:088', '68', 26, 88, '(yaitu) di hari harta dan anak-anak laki-laki tidak berguna,'),
(3021, '026:089', '68', 26, 89, 'kecuali orang-orang yang menghadap Allah dengan hati yang bersih,'),
(3022, '026:090', '68', 26, 90, 'dan (di hari itu) didekatkanlah surga kepada orang-orang yang bertakwa,'),
(3023, '026:091', '68', 26, 91, 'dan diperlihatkan dengan jelas neraka Jahim kepada orang-orang yang sesat\",'),
(3024, '026:092', '68', 26, 92, 'dan dikatakan kepada mereka: \"Dimanakah berhala-berhala yang dahulu kamu selalu menyembah(nya)'),
(3025, '026:093', '68', 26, 93, 'selain dari Allah? Dapatkah mereka menolong kamu atau menolong diri mereka sendiri?\"'),
(3026, '026:094', '68', 26, 94, 'Maka mereka (sembahan-sembahan itu) dijungkirkan ke dalam neraka bersama-sama orang-orang yang sesat,'),
(3027, '026:095', '68', 26, 95, 'dan bala tentara iblis semuanya.'),
(3028, '026:096', '68', 26, 96, 'Mereka berkata sedang mereka bertengkar di dalam neraka:'),
(3029, '026:097', '68', 26, 97, '\"demi Allah: sungguh kita dahulu (di dunia) dalam kesesatan yang nyata,'),
(3030, '026:098', '68', 26, 98, 'karena kita mempersamakan kamu dengan Tuhan semesta alam\".'),
(3031, '026:099', '68', 26, 99, 'Dan tiadalah yang menyesatkan kami kecuali orang-orang yang berdosa.'),
(3032, '026:100', '68', 26, 100, 'Maka kami tidak mempunyai pemberi syafa´at seorangpun,'),
(3033, '026:101', '68', 26, 101, 'dan tidak pula mempunyai teman yang akrab,'),
(3034, '026:102', '68', 26, 102, 'maka sekiranya kita dapat kembali sekali lagi (ke dunia) niscaya kami menjadi orang-orang yang beriman\".'),
(3035, '026:103', '68', 26, 103, 'Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
(3036, '026:104', '68', 26, 104, 'Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3037, '026:105', '68', 26, 105, 'Kaum Nuh telah mendustakan para rasul.'),
(3038, '026:106', '68', 26, 106, 'Ketika saudara mereka (Nuh) berkata kepada mereka: \"Mengapa kamu tidak bertakwa?'),
(3039, '026:107', '68', 26, 107, 'Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,'),
(3040, '026:108', '68', 26, 108, 'maka bertakwalah kepada Allah dan taatlah kepadaku.'),
(3041, '026:109', '68', 26, 109, 'Dan aku sekali-kali tidak minta upah kepadamu atas ajakan-ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.'),
(3042, '026:110', '68', 26, 110, 'Maka bertakwalah kepada Allah dan taatlah kepadaku\".'),
(3043, '026:111', '68', 26, 111, 'Mereka berkata: \"Apakah kami akan beriman kepadamu, padahal yang mengikuti kamu ialah orang-orang yang hina?\".'),
(3044, '026:112', '68', 26, 112, 'Nuh menjawab: \"Bagaimana aku mengetahui apa yang telah mereka kerjakan?'),
(3045, '026:113', '68', 26, 113, 'Perhitungan (amal perbuatan) mereka tidak lain hanyalah kepada Tuhanku, kalau kamu menyadari.'),
(3046, '026:114', '68', 26, 114, 'Dan aku sekali-kali tidak akan mengusir orang-orang yang beriman.'),
(3047, '026:115', '68', 26, 115, 'Aku (ini) tidak lain melainkan pemberi peringatan yang menjelaskan\".'),
(3048, '026:116', '68', 26, 116, 'Mereka berkata: \"Sungguh jika kamu tidak (mau) berhenti hai Nuh, niscaya benar-benar kamu akan termasuk orang-orang yang dirajam\".'),
(3049, '026:117', '68', 26, 117, 'Nuh berkata: \"Ya Tuhanku, sesungguhnya kaumku telah mendustakan aku;'),
(3050, '026:118', '68', 26, 118, 'maka itu adakanlah suatu keputusan antaraku dan antara mereka, dan selamatkanlah aku dan orang-orang yang mukmin besertaku\".'),
(3051, '026:119', '68', 26, 119, 'Maka Kami selamatkan Nuh dan orang-orang yang besertanya di dalam kapal yang penuh muatan.'),
(3052, '026:120', '68', 26, 120, 'Kemudian sesudah itu Kami tenggelamkan orang-orang yang tinggal.'),
(3053, '026:121', '68', 26, 121, 'Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
(3054, '026:122', '68', 26, 122, 'Dan sesungguhnya Tuhanmu Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3055, '026:123', '68', 26, 123, 'Kaum ´Aad telah mendustakan para rasul.'),
(3056, '026:124', '68', 26, 124, 'Ketika saudara mereka Hud berkata kepada mereka: \"Mengapa kamu tidak bertakwa?'),
(3057, '026:125', '68', 26, 125, 'Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,'),
(3058, '026:126', '68', 26, 126, 'maka bertakwalah kepada Allah dan taatlah kepadaku.'),
(3059, '026:127', '68', 26, 127, 'Dan sekali-kali aku tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(3060, '026:128', '68', 26, 128, 'Apakah kamu mendirikan pada tiap-tiap tanah tinggi bangunan untuk bermain-main,'),
(3061, '026:129', '68', 26, 129, 'dan kamu membuat benteng-benteng dengan maksud supaya kamu kekal (di dunia)?'),
(3062, '026:130', '68', 26, 130, 'Dan apabila kamu menyiksa, maka kamu menyiksa sebagai orang-orang kejam dan bengis.'),
(3063, '026:131', '68', 26, 131, 'Maka bertakwalah kepada Allah dan taatlah kepadaku.'),
(3064, '026:132', '68', 26, 132, 'Dan bertakwalah kepada Allah yang telah menganugerahkan kepadamu apa yang kamu ketahui.'),
(3065, '026:133', '68', 26, 133, 'Dia telah menganugerahkan kepadamu binatang-binatang ternak, dan anak-anak,'),
(3066, '026:134', '68', 26, 134, 'dan kebun-kebun dan mata air,'),
(3067, '026:135', '68', 26, 135, 'sesungguhnya aku takut kamu akan ditimpa azab hari yang besar\".'),
(3068, '026:136', '68', 26, 136, 'Mereka menjawab: \"Adalah sama saja bagi kami, apakah kamu memberi nasehat atau tidak memberi nasehat,'),
(3069, '026:137', '68', 26, 137, '(agama kami) ini tidak lain hanyalah adat kebiasaan orang dahulu.'),
(3070, '026:138', '68', 26, 138, 'dan kami sekali-kali tidak akan di \"azab\".'),
(3071, '026:139', '68', 26, 139, 'Maka mereka mendustakan Hud, lalu Kami binasakan mereka. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
(3072, '026:140', '68', 26, 140, 'Dan sesungguhnya Tuhanmu, Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3073, '026:141', '68', 26, 141, 'Kaum Tsamud telah mendustakan rasul-rasul.'),
(3074, '026:142', '68', 26, 142, 'Ketika saudara mereka, Shaleh, berkata kepada mereka: \"Mengapa kamu tidak bertakwa?'),
(3075, '026:143', '68', 26, 143, 'Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,'),
(3076, '026:144', '68', 26, 144, 'maka bertakwalah kepada Allah dan taatlah kepadaku.'),
(3077, '026:145', '68', 26, 145, 'Dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu, upahku tidak lain hanyalah dari Tuhan semesta alam.'),
(3078, '026:146', '68', 26, 146, 'Adakah kamu akan dibiarkan tinggal disini (di negeri kamu ini) dengan aman,'),
(3079, '026:147', '68', 26, 147, 'di dalam kebun-kebun serta mata air,'),
(3080, '026:148', '68', 26, 148, 'dan tanam-tanaman dan pohon-pohon korma yang mayangnya lembut.'),
(3081, '026:149', '68', 26, 149, 'Dan kamu pahat sebagian dari gunung-gunung untuk dijadikan rumah-rumah dengan rajin;'),
(3082, '026:150', '68', 26, 150, 'maka bertakwalah kepada Allah dan taatlah kepadaku;'),
(3083, '026:151', '68', 26, 151, 'dan janganlah kamu mentaati perintah orang-orang yang melewati batas,'),
(3084, '026:152', '68', 26, 152, 'yang membuat kerusakan di muka bumi dan tidak mengadakan perbaikan\".'),
(3085, '026:153', '68', 26, 153, 'Mereka berkata: \"Sesungguhnya kamu adalah salah seorang dari orang-orang yang kena sihir;'),
(3086, '026:154', '68', 26, 154, 'Kamu tidak lain melainkan seorang manusia seperti kami; maka datangkanlah sesuatu mukjizat, jika kamu memang termasuk orang-orang yang benar\".'),
(3087, '026:155', '68', 26, 155, 'Shaleh menjawab: \"Ini seekor unta betina, ia mempunyai giliran untuk mendapatkan air, dan kamu mempunyai giliran pula untuk mendapatkan air di hari yang tertentu.'),
(3088, '026:156', '68', 26, 156, 'Dan janganlah kamu sentuh unta betina itu dengan sesuatu kejahatan, yang menyebabkan kamu akan ditimpa oleh azab hari yang besar\".'),
(3089, '026:157', '68', 26, 157, 'Kemudian mereka membunuhnya, lalu mereka menjadi menyesal,'),
(3090, '026:158', '68', 26, 158, 'maka mereka ditimpa azab. Sesungguhnya pada yang demikian itu benar-benar terdapat bukti yang nyata. Dan adalah kebanyakan mereka tidak beriman.'),
(3091, '026:159', '68', 26, 159, 'Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3092, '026:160', '68', 26, 160, 'Kaum Luth telah mendustakan rasul-rasul,'),
(3093, '026:161', '68', 26, 161, 'ketika saudara mereka, Luth, berkata kepada mereka: mengapa kamu tidak bertakwa?\"'),
(3094, '026:162', '68', 26, 162, 'Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu,'),
(3095, '026:163', '68', 26, 163, 'maka bertakwalah kepada Allah dan taatlah kepadaku.'),
(3096, '026:164', '68', 26, 164, 'Dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semeta alam.'),
(3097, '026:165', '68', 26, 165, 'Mengapa kamu mendatangi jenis lelaki di antara manusia,'),
(3098, '026:166', '68', 26, 166, 'dan kamu tinggalkan isteri-isteri yang dijadikan oleh Tuhanmu untukmu, bahkan kamu adalah orang-orang yang melampaui batas\".'),
(3099, '026:167', '68', 26, 167, 'Mereka menjawab: \"Hai Luth, sesungguhnya jika kamu tidak berhenti, benar-benar kamu termasuk orang-orang yang diusir\"'),
(3100, '026:168', '68', 26, 168, 'Luth berkata: \"Sesungguhnya aku sangat benci kepada perbuatanmu\".'),
(3101, '026:169', '68', 26, 169, '(Luth berdoa): \"Ya Tuhanku selamatkanlah aku beserta keluargaku dari (akibat) perbuatan yang mereka kerjakan\".'),
(3102, '026:170', '68', 26, 170, 'Lalu Kami selamatkan ia beserta keluarganya semua,'),
(3103, '026:171', '68', 26, 171, 'kecuali seorang perempuan tua (isterinya), yang termasuk dalam golongan yang tinggal.'),
(3104, '026:172', '68', 26, 172, 'Kemudian Kami binasakan yang lain.'),
(3105, '026:173', '68', 26, 173, 'Dan Kami hujani mereka dengan hujan (batu) maka amat jeleklah hujan yang menimpa orang-orang yang telah diberi peringatan itu.'),
(3106, '026:174', '68', 26, 174, 'Sesunguhnya pada yang demikian itu benar-benar terdapat bukti-bukti yang nyata. Dan adalah kebanyakan mereka tidak beriman.'),
(3107, '026:175', '68', 26, 175, 'Dan sesungguhnya Tuhanmu, benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3108, '026:176', '68', 26, 176, 'Penduduk Aikah telah mendustakan rasul-rasul;'),
(3109, '026:177', '68', 26, 177, 'ketika Syu´aib berkata kepada mereka: \"Mengapa kamu tidak bertakwa?,'),
(3110, '026:178', '68', 26, 178, 'Sesungguhnya aku adalah seorang rasul kepercayaan (yang diutus) kepadamu.'),
(3111, '026:179', '68', 26, 179, 'maka bertakwalah kepada Allah dan taatlah kepadaku;'),
(3112, '026:180', '68', 26, 180, 'dan aku sekali-kali tidak minta upah kepadamu atas ajakan itu; upahku tidak lain hanyalah dari Tuhan semesta alam.'),
(3113, '026:181', '68', 26, 181, 'Sempurnakanlah takaran dan janganlah kamu termasuk orang-orang yang merugikan;'),
(3114, '026:182', '68', 26, 182, 'dan timbanglah dengan timbangan yang lurus.'),
(3115, '026:183', '68', 26, 183, 'Dan janganlah kamu merugikan manusia pada hak-haknya dan janganlah kamu merajalela di muka bumi dengan membuat kerusakan;'),
(3116, '026:184', '68', 26, 184, 'dan bertakwalah kepada Allah yang telah menciptakan kamu dan umat-umat yang dahulu\".'),
(3117, '026:185', '68', 26, 185, 'Mereka berkata: \"Sesungguhnya kamu adalah salah seorang dari orang-orang yang kena sihir,'),
(3118, '026:186', '68', 26, 186, 'dan kamu tidak lain melainkan seorang manusia seperti kami, dan sesungguhnya kami yakin bahwa kamu benar-benar termasuk orang-orang yang berdusta.'),
(3119, '026:187', '68', 26, 187, 'Maka jatuhkanlah atas kami gumpalan dari langit, jika kamu termasuk orang-orang yang benar.'),
(3120, '026:188', '68', 26, 188, 'Syu´aib berkata: \"Tuhanku lebih mengetahui apa yang kamu kerjakan\".'),
(3121, '026:189', '68', 26, 189, 'Kemudian mereka mendustakan Syu´aib, lalu mereka ditimpa azab pada hari mereka dinaungi awan. Sesungguhnya azab itu adalah azab hari yang besar.'),
(3122, '026:190', '68', 26, 190, 'Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman.'),
(3123, '026:191', '68', 26, 191, 'Dan sesungguhnya Tuhanmu benar-benar Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(3124, '026:192', '68', 26, 192, 'Dan sesungguhnya Al Quran ini benar-benar diturunkan oleh Tuhan semesta alam,'),
(3125, '026:193', '68', 26, 193, 'dia dibawa turun oleh Ar-Ruh Al-Amin (Jibril),'),
(3126, '026:194', '68', 26, 194, 'ke dalam hatimu (Muhammad) agar kamu menjadi salah seorang di antara orang-orang yang memberi peringatan,'),
(3127, '026:195', '68', 26, 195, 'dengan bahasa Arab yang jelas.'),
(3128, '026:196', '68', 26, 196, 'Dan sesungguhnya Al Quran itu benar-benar (tersebut) dalam Kitab-kitab orang yang dahulu.'),
(3129, '026:197', '68', 26, 197, 'Dan apakah tidak cukup menjadi bukti bagi mereka, bahwa para ulama Bani Israil mengetahuinya?'),
(3130, '026:198', '68', 26, 198, 'Dan kalau Al Quran itu Kami turunkan kepada salah seorang dari golongan bukan Arab,'),
(3131, '026:199', '68', 26, 199, 'lalu ia membacakannya kepada mereka (orang-orang kafir); niscaya mereka tidak akan beriman kepadanya.'),
(3132, '026:200', '68', 26, 200, 'Demikianlah Kami masukkan Al Quran ke dalam hati orang-orang yang durhaka.'),
(3133, '026:201', '68', 26, 201, 'Mereka tidak beriman kepadanya, hingga mereka melihat azab yang pedih,'),
(3134, '026:202', '68', 26, 202, 'maka datanglah azab kepada mereka dengan mendadak, sedang mereka tidak menyadarinya,'),
(3135, '026:203', '68', 26, 203, 'lalu mereka berkata: \"Apakah kami dapat diberi tangguh?\"'),
(3136, '026:204', '68', 26, 204, 'Maka apakah mereka meminta supaya disegerakan azab Kami?'),
(3137, '026:205', '68', 26, 205, 'Maka bagaimana pendapatmu jika Kami berikan kepada mereka kenikmatan hidup bertahun-tahun,'),
(3138, '026:206', '68', 26, 206, 'Kemudian datang kepada mereka azab yang telah diancamkan kepada mereka,'),
(3139, '026:207', '68', 26, 207, 'niscaya tidak berguna bagi mereka apa yang mereka selalu menikmatinya.'),
(3140, '026:208', '68', 26, 208, 'Dan Kami tidak membinasakan sesuatu negeripun, melainkan sesudah ada baginya orang-orang yang memberi peringatan;'),
(3141, '026:209', '68', 26, 209, 'untuk menjadi peringatan. Dan Kami sekali-kali tidak berlaku zalim.'),
(3142, '026:210', '68', 26, 210, 'Dan Al Quran itu bukanlah dibawa turun oleh syaitan-syaitan.'),
(3143, '026:211', '68', 26, 211, 'Dan tidaklah patut mereka membawa turun Al Quran itu, dan merekapun tidak akan kuasa.'),
(3144, '026:212', '68', 26, 212, 'Sesungguhnya mereka benar-benar dijauhkan daripada mendengar Al Quran itu.'),
(3145, '026:213', '68', 26, 213, 'Maka janganlah kamu menyeru (menyembah) tuhan yang lain di samping Allah, yang menyebabkan kamu termasuk orang-orang yang diazab.'),
(3146, '026:214', '68', 26, 214, 'Dan berilah peringatan kepada kerabat-kerabatmu yang terdekat,'),
(3147, '026:215', '68', 26, 215, 'dan rendahkanlah dirimu terhadap orang-orang yang mengikutimu, yaitu orang-orang yang beriman.'),
(3148, '026:216', '68', 26, 216, 'Jika mereka mendurhakaimu maka katakanlah: \"Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu kerjakan\";'),
(3149, '026:217', '68', 26, 217, 'Dan bertawakkallah kepada (Allah) Yang Maha Perkasa lagi Maha Penyayang,'),
(3150, '026:218', '68', 26, 218, 'Yang melihat kamu ketika kamu berdiri (untuk sembahyang),'),
(3151, '026:219', '68', 26, 219, 'dan (melihat pula) perubahan gerak badanmu di antara orang-orang yang sujud.'),
(3152, '026:220', '68', 26, 220, 'Sesungguhnya Dia adalah Yang Maha Mendengar lagi Maha Mengetahui.'),
(3153, '026:221', '68', 26, 221, 'Apakah akan Aku beritakan kepadamu, kepada siapa syaitan-syaitan itu turun?'),
(3154, '026:222', '68', 26, 222, 'Mereka turun kepada tiap-tiap pendusta lagi yang banyak dosa,'),
(3155, '026:223', '68', 26, 223, 'mereka menghadapkan pendengaran (kepada syaitan) itu, dan kebanyakan mereka adalah orang-orang pendusta.'),
(3156, '026:224', '68', 26, 224, 'Dan penyair-penyair itu diikuti oleh orang-orang yang sesat.'),
(3157, '026:225', '68', 26, 225, 'Tidakkah kamu melihat bahwasanya mereka mengembara di tiap-tiap lembah,'),
(3158, '026:226', '68', 26, 226, 'dan bahwasanya mereka suka mengatakan apa yang mereka sendiri tidak mengerjakan(nya)?'),
(3159, '026:227', '68', 26, 227, 'kecuali orang-orang (penyair-penyair) yang beriman dan beramal saleh dan banyak menyebut Allah dan mendapat kemenangan sesudah menderita kezaliman. Dan orang-orang yang zalim itu kelak akan mengetahui ke tempat mana mereka akan kembali.'),
(3160, '027:001', '68', 27, 1, 'Thaa Siin (Surat) ini adalah ayat-ayat Al Quran, dan (ayat-ayat) Kitab yang menjelaskan,'),
(3161, '027:002', '68', 27, 2, 'untuk menjadi petunjuk dan berita gembira untuk orang-orang yang beriman,'),
(3162, '027:003', '68', 27, 3, '(yaitu) orang-orang yang mendirikan sembahyang dan menunaikan zakat dan mereka yakin akan adanya negeri akhirat.'),
(3163, '027:004', '68', 27, 4, 'Sesungguhnya orang-orang yang tidak beriman kepada negeri akhirat, Kami jadikan mereka memandang indah perbuatan-perbuatan mereka, maka mereka bergelimang (dalam kesesatan).'),
(3164, '027:005', '68', 27, 5, 'Mereka itulah orang-orang yang mendapat (di dunia) azab yang buruk dan mereka di akhirat adalah orang-orang yang paling merugi.'),
(3165, '027:006', '68', 27, 6, 'Dan sesungguhnya kamu benar-benar diberi Al Quran dari sisi (Allah) Yang Maha Bijaksana lagi Maha Mengetahui.'),
(3166, '027:007', '68', 27, 7, '(Ingatlah) ketika Musa berkata kepada keluarganya: \"Sesungguhnya aku melihat api. Aku kelak akan membawa kepadamu khabar daripadanya, atau aku membawa kepadamu suluh api supaya kamu dapat berdiang\".'),
(3167, '027:008', '68', 27, 8, 'Maka tatkala dia tiba di (tempat) api itu, diserulah dia: \"Bahwa telah diberkati orang-orang yang berada di dekat api itu, dan orang-orang yang berada di sekitarnya. Dan Maha Suci Allah, Tuhan semesta alam\".'),
(3168, '027:009', '68', 27, 9, '(Allah berfirman): \"Hai Musa, sesungguhnya, Akulah Allah, Yang Maha Perkasa lagi Maha Bijaksana.'),
(3169, '027:010', '68', 27, 10, 'dan lemparkanlah tongkatmu\". Maka tatkala (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seperti dia seekor ular yang gesit, larilah ia berbalik ke belakang tanpa menoleh. \"Hai Musa, janganlah kamu takut. Sesungguhnya orang yang dijadikan rasul, tidak takut di hadapan-Ku.'),
(3170, '027:011', '68', 27, 11, 'tetapi orang yang berlaku zalim, kemudian ditukarnya kezalimannya dengan kebaikan (Allah akan mengampuninya); maka seaungguhnya Aku Maha Pangampun lagi Maha Penyayang.'),
(3171, '027:012', '68', 27, 12, 'Dan masukkanlah tanganmu ke leher bajumu, niscaya ia akan ke luar putih (bersinar) bukan karena penyakit. (Kedua mukjizat ini) termasuk sembilan buah mukjizat (yang akan dikemukakan) kepada Fir´aun dan kaumnya. Sesungguhnya mereka adalah kaum yang fasik\".'),
(3172, '027:013', '68', 27, 13, 'Maka tatkala mukjizat-mukjizat Kami yang jelas itu sampai kepada mereka, berkatalah mereka: \"Ini adalah sihir yang nyata\".'),
(3173, '027:014', '68', 27, 14, 'Dan mereka mengingkarinya karena kezaliman dan kesombongan (mereka) padahal hati mereka meyakini (kebenaran)nya. Maka perhatikanlah betapa kesudahan orang-orang yang berbuat kebinasaan.'),
(3174, '027:015', '68', 27, 15, 'Dan sesungguhnya Kami telah memberi ilmu kepada Daud dan Sulaiman; dan keduanya mengucapkan: \"Segala puji bagi Allah yang melebihkan kami dari kebanyakan hamba-hambanya yang beriman\".'),
(3175, '027:016', '68', 27, 16, 'Dan Sulaiman telah mewarisi Daud, dan dia berkata: \"Hai Manusia, kami telah diberi pengertian tentang suara burung dan kami diberi segala sesuatu. Sesungguhnya (semua) ini benar-benar suatu kurnia yang nyata\".'),
(3176, '027:017', '68', 27, 17, 'Dan dihimpunkan untuk Sulaiman tentaranya dari jin, manusia dan burung lalu mereka itu diatur dengan tertib (dalam barisan).'),
(3177, '027:018', '68', 27, 18, 'Hingga apabila mereka sampai di lembah semut berkatalah seekor semut: Hai semut-semut, masuklah ke dalam sarang-sarangmu, agar kamu tidak diinjak oleh Sulaiman dan tentaranya, sedangkan mereka tidak menyadari\";'),
(3178, '027:019', '68', 27, 19, 'maka dia tersenyum dengan tertawa karena (mendengar) perkataan semut itu. Dan dia berdoa: \"Ya Tuhanku berilah aku ilham untuk tetap mensyukuri nikmat Mu yang telah Engkau anugerahkan kepadaku dan kepada dua orang ibu bapakku dan untuk mengerjakan amal saleh yang Engkau ridhai; dan masukkanlah aku dengan rahmat-Mu ke dalam golongan hamba-hamba-Mu yang saleh\".'),
(3179, '027:020', '68', 27, 20, 'Dan dia memeriksa burung-burung lalu berkata: \"Mengapa aku tidak melihat hud-hud, apakah dia termasuk yang tidak hadir.'),
(3180, '027:021', '68', 27, 21, 'Sungguh aku benar-benar akan mengazabnya dengan azab yang keras atau benar-benar menyembelihnya kecuali jika benar-benar dia datang kepadaku dengan alasan yang terang\".'),
(3181, '027:022', '68', 27, 22, 'Maka tidak lama kemudian (datanglah hud-hud), lalu ia berkata: \"Aku telah mengetahui sesuatu yang kamu belum mengetahuinya; dan kubawa kepadamu dari negeri Saba suatu berita penting yang diyakini.'),
(3182, '027:023', '68', 27, 23, 'Sesungguhnya aku menjumpai seorang wanita yang memerintah mereka, dan dia dianugerahi segala sesuatu serta mempunyai singgasana yang besar.'),
(3183, '027:024', '68', 27, 24, 'Aku mendapati dia dan kaumnya menyembah matahari, selain Allah; dan syaitan telah menjadikan mereka memandang indah perbuatan-perbuatan mereka lalu menghalangi mereka dari jalan (Allah), sehingga mereka tidak dapat petunjuk,'),
(3184, '027:025', '68', 27, 25, 'agar mereka tidak menyembah Allah Yang mengeluarkan apa yang terpendam di langit dan di bumi dan Yang mengetahui apa yang kamu sembunyikan dan apa yang kamu nyatakan.'),
(3185, '027:026', '68', 27, 26, 'Allah, tiada Tuhan Yang disembah kecuali Dia, Tuhan Yang mempunyai ´Arsy yang besar\".'),
(3186, '027:027', '68', 27, 27, 'Berkata Sulaiman: \"Akan kami lihat, apa kamu benar, ataukah kamu termasuk orang-orang yang berdusta.'),
(3187, '027:028', '68', 27, 28, 'Pergilah dengan (membawa) suratku ini, lalu jatuhkan kepada mereka, kemudian berpalinglah dari mereka, lalu perhatikanlah apa yang mereka bicarakan\"'),
(3188, '027:029', '68', 27, 29, 'Berkata ia (Balqis): \"Hai pembesar-pembesar, sesungguhnya telah dijatuhkan kepadaku sebuah surat yang mulia.'),
(3189, '027:030', '68', 27, 30, 'Sesungguhnya surat itu, dari SuIaiman dan sesungguhnya (isi)nya: \"Dengan menyebut nama Allah Yang Maha Pemurah lagi Maha Penyayang.'),
(3190, '027:031', '68', 27, 31, 'Bahwa janganlah kamu sekalian berlaku sombong terhadapku dan datanglah kepadaku sebagai orang-orang yang berserah diri\".'),
(3191, '027:032', '68', 27, 32, 'Berkata dia (Balqis): \"Hai para pembesar berilah aku pertimbangan dalam urusanku (ini) aku tidak pernah memutuskan sesuatu persoalan sebelum kamu berada dalam majelis(ku)\".'),
(3192, '027:033', '68', 27, 33, 'Mereka menjawab: \"Kita adalah orang-orang yang memiliki kekuatan dan (juga) memiliki keberanian yang sangat (dalam peperangan), dan keputusan berada ditanganmu: maka pertimbangkanlah apa yang akan kamu perintahkan\".'),
(3193, '027:034', '68', 27, 34, 'Dia berkata: \"Sesungguhnya raja-raja apabila memasuki suatu negeri, niscaya mereka membinasakannya, dan menjadikan penduduknya yang mulia jadi hina; dan demikian pulalah yang akan mereka perbuat.'),
(3194, '027:035', '68', 27, 35, 'Dan sesungguhnya aku akan mengirim utusan kepada mereka dengan (membawa) hadiah, dan (aku akan) menunggu apa yang akan dibawa kembali oleh utusan-utusan itu\".'),
(3195, '027:036', '68', 27, 36, 'Maka tatkala utusan itu sampai kepada Sulaiman, Sulaiman berkata: \"Apakah (patut) kamu menolong aku dengan harta? maka apa yang diberikan Allah kepadaku lebih baik daripada apa yang diberikan-Nya kepadamu; tetapi kamu merasa bangga dengan hadiahmu.'),
(3196, '027:037', '68', 27, 37, 'Kembalilah kepada mereka sungguh kami akan mendatangi mereka dengan balatentara yang mereka tidak kuasa melawannya, dan pasti kami akan mengusir mereka dari negeri itu (Saba) dengan terhina dan mereka menjadi (tawanan-tawanan) yang hina dina\".'),
(3197, '027:038', '68', 27, 38, 'Berkata Sulaiman: \"Hai pembesar-pembesar, siapakah di antara kamu sekalian yang sanggup membawa singgasananya kepadaku sebelum mereka datang kepadaku sebagai orang-orang yang berserah diri\".'),
(3198, '027:039', '68', 27, 39, 'Berkata ´Ifrit (yang cerdik) dari golongan jin: \"Aku akan datang kepadamu dengan membawa singgsana itu kepadamu sebelum kamu berdiri dari tempat dudukmu; sesungguhnya aku benar-benar kuat untuk membawanya lagi dapat dipercaya\".'),
(3199, '027:040', '68', 27, 40, 'Berkatalah seorang yang mempunyai ilmu dari Al Kitab: \"Aku akan membawa singgasana itu kepadamu sebelum matamu berkedip\". Maka tatkala Sulaiman melihat singgasana itu terletak di hadapannya, iapun berkata: \"Ini termasuk kurnia Tuhanku untuk mencoba aku apakah aku bersyukur atau mengingkari (akan nikmat-Nya). Dan barangsiapa yang bersyukur maka sesungguhnya dia bersyukur untuk (kebaikan) dirinya sendiri dan barangsiapa yang ingkar, maka sesungguhnya Tuhanku Maha Kaya lagi Maha Mulia\".'),
(3200, '027:041', '68', 27, 41, 'Dia berkata: \"Rubahlah baginya singgasananya; maka kita akan melihat apakah dia mengenal ataukah dia termasuk orang-orang yang tidak mengenal(nya)\".'),
(3201, '027:042', '68', 27, 42, 'Dan ketika Balqis datang, ditanyakanlah kepadanya: \"Serupa inikah singgasanamu?\" Dia menjawab: \"Seakan-akan singgasana ini singgasanaku, kami telah diberi pengetahuan sebelumnya dan kami adalah orang-orang yang berserah diri\".'),
(3202, '027:043', '68', 27, 43, 'Dan apa yang disembahnya selama ini selain Allah, mencegahnya (untuk melahirkan keislamannya), karena sesungguhnya dia dahulunya termasuk orang-orang yang kafir.'),
(3203, '027:044', '68', 27, 44, 'Dikatakan kepadanya: \"Masuklah ke dalam istana\". Maka tatkala dia melihat lantai istana itu, dikiranya kolam air yang besar, dan disingkapkannya kedua betisnya. Berkatalah Sulaiman: \"Sesungguhnya ia adalah istana licin terbuat dari kaca\". Berkatalah Balqis: \"Ya Tuhanku, sesungguhnya aku telah berbuat zalim terhadap diriku dan aku berserah diri bersama Sulaiman kepada Allah, Tuhan semesta alam\".'),
(3204, '027:045', '68', 27, 45, 'Dan sesungguhnya Kami telah mengutus kepada (kaum) Tsamud saudara mereka Shaleh (yang berseru): \"Sembahlah Allah\". Tetapi tiba-tiba mereka (jadi) dua golongan yang bermusuhan.'),
(3205, '027:046', '68', 27, 46, 'Dia berkata: \"Hai kaumku mengapa kamu minta disegerakan keburukan sebelum (kamu minta) kebaikan? Hendaklah kamu meminta ampun kepada Allah, agar kamu mendapat rahmat\".'),
(3206, '027:047', '68', 27, 47, 'Mereka menjawab: \"Kami mendapat nasib yang malang, disebabkan kamu dan orang-orang yang besertamu\". Shaleh berkata: \"Nasibmu ada pada sisi Allah, (bukan kami yang menjadi sebab), tetapi kamu kaum yang diuji\".'),
(3207, '027:048', '68', 27, 48, 'Dan adalah di kota itu sembilan orang laki-laki yang membuat kerusakan di muka bumi, dan mereka tidak berbuat kebaikan.'),
(3208, '027:049', '68', 27, 49, 'Mereka berkata: \"Bersumpahlah kamu dengan nama Allah, bahwa kita sungguh-sungguh akan menyerangnya dengan tiba-tiba beserta keluarganya di malam hari, kemudian kita katakan kepada warisnya (bahwa) kita tidak menyaksikan kematian keluarganya itu, dan sesungguhnya kita adalah orang-orang yang benar\".'),
(3209, '027:050', '68', 27, 50, 'Dan merekapun merencanakan makar dengan sungguh-sungguh dan Kami merencanakan makar (pula), sedang mereka tidak menyadari.'),
(3210, '027:051', '68', 27, 51, 'Maka perhatikanlah betapa sesungguhnya akibat makar mereka itu, bahwasanya Kami membinasakan mereka dan kaum mereka semuanya.'),
(3211, '027:052', '68', 27, 52, 'Maka itulah rumah-rumah mereka dalam keadaan runtuh disebabkan kezaliman mereka. Sesungguhnya pada yang demikian itu (terdapat) pelajaran bagi kaum yang mengetahui.'),
(3212, '027:053', '68', 27, 53, 'Dan telah Kami selamatkan orang-orang yang beriman dan mereka itu selalu bertakwa.'),
(3213, '027:054', '68', 27, 54, 'Dan (ingatlah kisah) Luth, ketika dia berkata kepada kaumnya: \"Mengapa kamu mengerjakan perbuatan fahisyah itu sedang kamu memperlihatkan(nya)?\"'),
(3214, '027:055', '68', 27, 55, '\"Mengapa kamu mendatangi laki-laki untuk (memenuhi) nafsu(mu), bukan (mendatangi) wanita? Sebenarnya kamu adalah kaum yang tidak mengetahui (akibat perbuatanmu)\".'),
(3215, '027:056', '68', 27, 56, 'Maka tidak lain jawaban kaumnya melainkan mengatakan: \"Usirlah Luth beserta keluarganya dari negerimu; karena sesungguhnya mereka itu orang-orang yang (mendakwakan dirinya) bersih\".'),
(3216, '027:057', '68', 27, 57, 'Maka Kami selamatkan dia beserta keluarganya, kecuali isterinya. Kami telah mentakdirkan dia termasuk orang-orang yang tertinggal (dibinasakan).'),
(3217, '027:058', '68', 27, 58, 'Dan Kami turunkan hujan atas mereka (hujan batu), maka amat buruklah hujan yang ditimpakan atas orang-orang yang diberi peringatan itu.'),
(3218, '027:059', '68', 27, 59, 'Katakanlah: \"Segala puji bagi Allah dan kesejahteraan atas hamba-hamba-Nya yang dipilih-Nya. Apakah Allah yang lebih baik, ataukah apa yang mereka persekutukan dengan Dia?\"'),
(3219, '027:060', '68', 27, 60, 'Atau siapakah yang telah menciptakan langit dan bumi dan yang menurunkan air untukmu dari langit, lalu Kami tumbuhkan dengan air itu kebun-kebun yang berpemandangan indah, yang kamu sekali-kali tidak mampu menumbuhkan pohon-pohonnya? Apakah disamping Allah ada tuhan (yang lain)? Bahkan (sebenarnya) mereka adalah orang-orang yang menyimpang (dari kebenaran).'),
(3220, '027:061', '68', 27, 61, 'Atau siapakah yang telah menjadikan bumi sebagai tempat berdiam, dan yang menjadikan sungai-sungai di celah-celahnya, dan yang menjadikan gunung-gunung untuk (mengkokohkan)nya dan menjadikan suatu pemisah antara dua laut? Apakah disamping Allah ada tuhan (yang lain)? Bahkan (sebenarnya) kebanyakan dari mereka tidak mengetahui.'),
(3221, '027:062', '68', 27, 62, 'Atau siapakah yang memperkenankan (doa) orang yang dalam kesulitan apabila ia berdoa kepada-Nya, dan yang menghilangkan kesusahan dan yang menjadikan kamu (manusia) sebagai khalifah di bumi? Apakah disamping Allah ada tuhan (yang lain)? Amat sedikitlah kamu mengingati(Nya).'),
(3222, '027:063', '68', 27, 63, 'Atau siapakah yang memimpin kamu dalam kegelapan di dataran dan lautan dan siapa (pula)kah yang mendatangkan angin sebagai kabar gembira sebelum (kedatangan) rahmat-Nya? Apakah disamping Allah ada tuhan (yang lain)? Maha Tinggi Allah terhadap apa yang mereka persekutukan (dengan-Nya).'),
(3223, '027:064', '68', 27, 64, 'Atau siapakah yang menciptakan (manusia dari permulaannya), kemudian mengulanginya (lagi), dan siapa (pula) yang memberikan rezeki kepadamu dari langit dan bumi? Apakah disamping Allah ada tuhan (yang lain)?. Katakanlah: \"Unjukkanlah bukti kebenaranmu, jika kamu memang orang-orang yang benar\".'),
(3224, '027:065', '68', 27, 65, 'Katakanlah: \"Tidak ada seorangpun di langit dan di bumi yang mengetahui perkara yang ghaib, kecuali Allah\", dan mereka tidak mengetahui bila mereka akan dibangkitkan.'),
(3225, '027:066', '68', 27, 66, 'Sebenarnya pengetahuan mereka tentang akhirat tidak sampai (kesana) malahan mereka ragu-ragu tentang akhirat itu, lebih-lebih lagi mereka buta daripadanya.'),
(3226, '027:067', '68', 27, 67, 'Berkatalah orang-orang yang kafir: \"Apakah setelah kita menjadi tanah dan (begitu pula) bapak-bapak kita; apakah sesungguhnya kita akan dikeluarkan (dari kubur)?'),
(3227, '027:068', '68', 27, 68, 'Sesungguhnya kami telah diberi ancaman dengan ini dan (juga) bapak-bapak kami dahulu; ini tidak lain hanyalah dongengan-dongengan orang dahulu kala\".'),
(3228, '027:069', '68', 27, 69, 'Katakanlah: \"Berjalanlah kamu (di muka) bumi, lalu perhatikanlah bagaimana akibat orang-orang yang berdosa.'),
(3229, '027:070', '68', 27, 70, 'Dan janganlah kamu berduka cita terhadap mereka, dan janganlah (dadamu) merasa sempit terhadap apa yang mereka tipudayakan\".'),
(3230, '027:071', '68', 27, 71, 'Dan mereka (orang-orang kafir) berkata: \"Bilakah datangnya azab itu, jika memang kamu orang-orang yang benar\".'),
(3231, '027:072', '68', 27, 72, 'Katakanlah: \"Mungkin telah hampir datang kepadamu sebagian dari (azab) yang kamu minta (supaya) disegerakan itu.'),
(3232, '027:073', '68', 27, 73, 'Dan sesungguhnya Tuhanmu benar-benar mempunyai kurnia yang besar (yang diberikan-Nya) kepada manusia, tetapi kebanyakan mereka tidak mensyukuri(nya).'),
(3233, '027:074', '68', 27, 74, 'Dan sesungguhnya Tuhanmu, benar-benar mengetahui apa yang disembunyikan hati mereka dan apa yang mereka nyatakan.'),
(3234, '027:075', '68', 27, 75, 'Tiada sesuatupun yang ghaib di langit dan di bumi, melainkan (terdapat) dalam kitab yang nyata (Lauhul Mahfuzh).'),
(3235, '027:076', '68', 27, 76, 'Sesungguhnya Al Quran ini menjelaskan kepada Bani lsrail sebahagian besar dari (perkara-perkara) yang mereka berselisih tentangnya.'),
(3236, '027:077', '68', 27, 77, 'Dan sesungguhnya Al Quran itu benar-benar menjadi petunjuk dan rahmat bagi orang-orang yang beriman.'),
(3237, '027:078', '68', 27, 78, 'Sesungguhnya Tuhanmu akan menyelesaikan perkara antara mereka dengan keputusan-Nya, dan Dia Maha Perkasa lagi Maha Mengetahui.'),
(3238, '027:079', '68', 27, 79, 'Sebab itu bertakwalah kepada Allah, sesungguhnya kamu berada di atas kebenaran yang nyata.'),
(3239, '027:080', '68', 27, 80, 'Sesungguhnya kamu tidak dapat menjadikan orang-orang yang mati mendengar dan (tidak pula) menjadikan orang-orang yang tuli mendengar panggilan, apabila mereka telah berpaling membelakang.'),
(3240, '027:081', '68', 27, 81, 'Dan kamu sekali-kali tidak dapat memimpin (memalingkan) orang-orang buta dari kesesatan mereka. Kamu tidak dapat menjadikan (seorangpun) mendengar, kecuali orang-orang yang beriman kepada ayat-ayat Kami, lalu mereka berserah diri.'),
(3241, '027:082', '68', 27, 82, 'Dan apabila perkataan telah jatuh atas mereka, Kami keluarkan sejenis binatang melata dari bumi yang akan mengatakan kepada mereka, bahwa sesungguhnya manusia dahulu tidak yakin kepada ayat-ayat Kami.'),
(3242, '027:083', '68', 27, 83, 'Dan (ingatlah) hari (ketika) Kami kumpulkan dari tiap-tiap umat segolongan orang-orang yang mendustakan ayat-ayat Kami, lalu mereka dibagi-bagi (dalam kelompok-kelompok).'),
(3243, '027:084', '68', 27, 84, 'Hingga apabila mereka datang, Allah berfirman: \"Apakah kamu telah mendustakan ayat-ayat-Ku, padahal ilmu kamu tidak meliputinya, atau apakah yang telah kamu kerjakan?\".'),
(3244, '027:085', '68', 27, 85, 'Dan jatuhlah perkataan (azab) atas mereka disebabkan kezaliman mereka, maka mereka tidak dapat berkata (apa-apa).'),
(3245, '027:086', '68', 27, 86, 'Apakah mereka tidak memperhatikan, bahwa sesungguhnya Kami telah menjadikan malam supaya mereka beristirahat padanya dan siang yang menerangi? Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman.'),
(3246, '027:087', '68', 27, 87, 'Dan (ingatlah) hari (ketika) ditiup sangkakala, maka terkejutlah segala yang di langit dan segala yang di bumi, kecuali siapa yang dikehendaki Allah. Dan semua mereka datang menghadap-Nya dengan merendahkan diri.'),
(3247, '027:088', '68', 27, 88, 'Dan kamu lihat gunung-gunung itu, kamu sangka dia tetap di tempatnya, padahal ia berjalan sebagai jalannya awan. (Begitulah) perbuatan Allah yang membuat dengan kokoh tiap-tiap sesuatu; sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(3248, '027:089', '68', 27, 89, 'Barangsiapa yang membawa kebaikan, maka ia memperoleh (balasan) yang lebih baik dari padanya, sedang mereka itu adalah orang-orang yang aman tenteram dari pada kejutan yang dahsyat pada hari itu.'),
(3249, '027:090', '68', 27, 90, 'Dan barang siapa yang membawa kejahatan, maka disungkurkanlah muka mereka ke dalam neraka. Tiadalah kamu dibalasi, melainkan (setimpal) dengan apa yang dahulu kamu kerjakan.'),
(3250, '027:091', '68', 27, 91, 'Aku hanya diperintahkan untuk menyembah Tuhan negeri ini (Mekah) Yang telah menjadikannya suci dan kepunyaan-Nya-lah segala sesuatu, dan aku diperintahkan supaya aku termasuk orang-orang yang berserah diri.'),
(3251, '027:092', '68', 27, 92, 'Dan supaya aku membacakan Al Quran (kepada manusia). Maka barangsiapa yang mendapat petunjuk maka sesungguhnya ia hanyalah mendapat petunjuk untuk (kebaikan) dirinya, dan barangsiapa yang sesat maka katakanlah: \"Sesungguhnya aku (ini) tidak lain hanyalah salah seorang pemberi peringatan\".'),
(3252, '027:093', '68', 27, 93, 'Dan katakanlah: \"Segala puji bagi Allah, Dia akan memperlihatkan kepadamu tanda-tanda kebesaran-Nya, maka kamu akan mengetahuinya. Dan Tuhanmu tiada lalai dari apa yang kamu kerjakan\".'),
(3253, '028:001', '68', 28, 1, 'Thaa Siin Miim'),
(3254, '028:002', '68', 28, 2, 'Ini adalah ayat-ayat Kitab (Al Quran) yang nyata (dari Allah).'),
(3255, '028:003', '68', 28, 3, 'Kami membacakan kepadamu sebagian dari kisah Musa dan Fir´aun dengan benar untuk orang-orang yang beriman.'),
(3256, '028:004', '68', 28, 4, 'Sesungguhnya Fir´aun telah berbuat sewenang-wenang di muka bumi dan menjadikan penduduknya berpecah belah, dengan menindas segolongan dari mereka, menyembelih anak laki-laki mereka dan membiarkan hidup anak-anak perempuan mereka. Sesungguhnya Fir´aun termasuk orang-orang yang berbuat kerusakan.'),
(3257, '028:005', '68', 28, 5, 'Dan Kami hendak memberi karunia kepada orang-orang yang tertindas di bumi (Mesir) itu dan hendak menjadikan mereka pemimpin dan menjadikan mereka orang-orang yang mewarisi (bumi),'),
(3258, '028:006', '68', 28, 6, 'dan akan Kami teguhkan kedudukan mereka di muka bumi dan akan Kami perlihatkan kepada Fir´aun dan Haman beserta tentaranya apa yang selalu mereka khawatirkan dari mereka itu.'),
(3259, '028:007', '68', 28, 7, 'Dan kami ilhamkan kepada ibu Musa; \"Susuilah dia, dan apabila kamu khawatir terhadapnya maka jatuhkanlah dia ke sungai (Nil). Dan janganlah kamu khawatir dan janganlah (pula) bersedih hati, karena sesungguhnya Kami akan mengembalikannya kepadamu, dan menjadikannya (salah seorang) dari para rasul.'),
(3260, '028:008', '68', 28, 8, 'Maka dipungutlah ia oleh keluarga Fir´aun yang akibatnya dia menjadi musuh dan kesedihan bagi mereka. Sesungguhnya Fir´aun dan Haman beserta tentaranya adalah orang-orang yang bersalah.'),
(3261, '028:009', '68', 28, 9, 'Dan berkatalah isteri Fir´aun: \"(Ia) adalah penyejuk mata hati bagiku dan bagimu. Janganlah kamu membunuhnya, mudah-mudahan ia bermanfaat kepada kita atau kita ambil ia menjadi anak\", sedang mereka tiada menyadari.'),
(3262, '028:010', '68', 28, 10, 'Dan menjadi kosonglah hati ibu Musa. Sesungguhnya hampir saja ia menyatakan rahasia tentang Musa, seandainya tidak Kami teguhkan hatinya, supaya ia termasuk orang-orang yang percaya (kepada janji Allah).'),
(3263, '028:011', '68', 28, 11, 'Dan berkatalah ibu Musa kepada saudara Musa yang perempuan: \"Ikutilah dia\" Maka kelihatanlah olehnya Musa dari jauh, sedang mereka tidak mengetahuinya,'),
(3264, '028:012', '68', 28, 12, 'dan Kami cegah Musa dari menyusu kepada perempuan-perempuan yang mau menyusui(nya) sebelum itu; maka berkatalah saudara Musa: \"Maukah kamu aku tunjukkan kepadamu ahlul bait yang akan memeliharanya untukmu dan mereka dapat berlaku baik kepadanya?\".'),
(3265, '028:013', '68', 28, 13, 'Maka kami kembalikan Musa kepada ibunya, supaya senang hatinya dan tidak berduka cita dan supaya ia mengetahui bahwa janji Allah itu adalah benar, tetapi kebanyakan manusia tidak mengetahuinya.'),
(3266, '028:014', '68', 28, 14, 'Dan setelah Musa cukup umur dan sempurna akalnya, Kami berikan kepadanya hikmah (kenabian) dan pengetahuan. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3267, '028:015', '68', 28, 15, 'Dan Musa masuk ke kota (Memphis) ketika penduduknya sedang lengah, maka didapatinya di dalam kota itu dua orang laki-laki yang berkelahi; yang seorang dari golongannya (Bani Israil) dan seorang (lagi) dari musuhnya (kaum Fir´aun). Maka orang yang dari golongannya meminta pertolongan kepadanya, untuk mengalahkan orang yang dari musuhnya lalu Musa meninjunya, dan matilah musuhnya itu. Musa berkata: \"Ini adalah perbuatan syaitan sesungguhnya syaitan itu adalah musuh yang menyesatkan lagi nyata (permusuhannya).'),
(3268, '028:016', '68', 28, 16, 'Musa mendoa: \"Ya Tuhanku, sesungguhnya aku telah menganiaya diriku sendiri karena itu ampunilah aku\". Maka Allah mengampuninya, sesungguhnya Allah Dialah Yang Maha Pengampun lagi Maha Penyayang.'),
(3269, '028:017', '68', 28, 17, 'Musa berkata: \"Ya Tuhanku, demi nikmat yang telah Engkau anugerahkan kepadaku, aku sekali-kali tiada akan menjadi penolong bagi orang-orang yang berdosa\".'),
(3270, '028:018', '68', 28, 18, 'Karena itu, jadilah Musa di kota itu merasa takut menunggu-nunggu dengan khawatir (akibat perbuatannya), maka tiba-tiba orang yang meminta pertolongan kemarin berteriak meminta pertolongan kepadanya. Musa berkata kepadanya: \"Sesungguhnya kamu benar-benar orang sesat yang nyata (kesesatannya)\".'),
(3271, '028:019', '68', 28, 19, 'Maka tatkala Musa hendak memegang dengan keras orang yang menjadi musuh keduanya, musuhnya berkata: \"Hai Musa, apakah kamu bermaksud hendak membunuhku, sebagaimana kamu kemarin telah membunuh seorang manusia? Kamu tidak bermaksud melainkan hendak menjadi orang yang berbuat sewenang-wenang di negeri (ini), dan tiadalah kamu hendak menjadi salah seorang dari orang-orang yang mengadakan perdamaian\".'),
(3272, '028:020', '68', 28, 20, 'Dan datanglah seorang laki-laki dari ujung kota bergegas-gegas seraya berkata: \"Hai Musa, sesungguhnya pembesar negeri sedang berunding tentang kamu untuk membunuhmu, sebab itu keluarlah (dari kota ini) sesungguhnya aku termasuk orang-orang yang memberi nasehat kepadamu\".'),
(3273, '028:021', '68', 28, 21, 'Maka keluarlah Musa dari kota itu dengan rasa takut menunggu-nunggu dengan khawatir, dia berdoa: \"Ya Tuhanku, selamatkanlah aku dari orang-orang yang zalim itu\".'),
(3274, '028:022', '68', 28, 22, 'Dan tatkala ia menghadap kejurusan negeri Mad-yan ia berdoa (lagi): \"Mudah-mudahan Tuhanku memimpinku ke jalan yang benar\".'),
(3275, '028:023', '68', 28, 23, 'Dan tatkala ia sampai di sumber air negeri Mad-yan ia menjumpai di sana sekumpulan orang yang sedang meminumkan (ternaknya), dan ia menjumpai di belakang orang banyak itu, dua orang wanita yang sedang menghambat (ternaknya). Musa berkata: \"Apakah maksudmu (dengan berbuat at begitu)?\" Kedua wanita itu menjawab: \"Kami tidak dapat meminumkan (ternak kami), sebelum pengembala-pengembala itu memulangkan (ternaknya), sedang bapak kami adalah orang tua yang telah lanjut umurnya\".'),
(3276, '028:024', '68', 28, 24, 'Maka Musa memberi minum ternak itu untuk (menolong) keduanya, kemudian dia kembali ke tempat yang teduh lalu berdoa: \"Ya Tuhanku sesungguhnya aku sangat memerlukan sesuatu kebaikan yang Engkau turunkan kepadaku\".'),
(3277, '028:025', '68', 28, 25, 'Kemudian datanglah kepada Musa salah seorang dari kedua wanita itu berjalan kemalu-maluan, ia berkata: \"Sesungguhnya bapakku memanggil kamu agar ia memberikan balasan terhadap (kebaikan)mu memberi minum (ternak) kami\". Maka tatkala Musa mendatangi bapaknya (Syu´aib) dan menceritakan kepadanya cerita (mengenai dirinya), Syu´aib berkata: \"Janganlah kamu takut. Kamu telah selamat dari orang-orang yang zalim itu\".'),
(3278, '028:026', '68', 28, 26, 'Salah seorang dari kedua wanita itu berkata: \"Ya bapakku ambillah ia sebagai orang yang bekerja (pada kita), karena sesungguhnya orang yang paling baik yang kamu ambil untuk bekerja (pada kita) ialah orang yang kuat lagi dapat dipercaya\".'),
(3279, '028:027', '68', 28, 27, 'Berkatalah dia (Syu´aib): \"Sesungguhnya aku bermaksud menikahkan kamu dengan salah seorang dari kedua anakku ini, atas dasar bahwa kamu bekerja denganku delapan tahun dan jika kamu cukupkan sepuluh tahun maka itu adalah (suatu kebaikan) dari kamu, maka aku tidak hendak memberati kamu. Dan kamu Insya Allah akan mendapatiku termasuk orang-orang yang baik\".'),
(3280, '028:028', '68', 28, 28, 'Dia (Musa) berkata: \"Itulah (perjanjian) antara aku dan kamu. Mana saja dari kedua waktu yang ditentukan itu aku sempurnakan, maka tidak ada tuntutan tambahan atas diriku (lagi). Dan Allah adalah saksi atas apa yang kita ucapkan\".'),
(3281, '028:029', '68', 28, 29, 'Maka tatkala Musa telah menyelesaikan waktu yang ditentukan dan dia berangkat dengan keluarganya, dilihatnyalah api di lereng gunung ia berkata kepada keluarganya: \"Tunggulah (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa suatu berita kepadamu dari (tempat) api itu atau (membawa) sesuluh api, agar kamu dapat menghangatkan badan\".'),
(3282, '028:030', '68', 28, 30, 'Maka tatkala Musa sampai ke (tempat) api itu, diserulah dia dari (arah) pinggir lembah yang sebelah kanan(nya) pada tempat yang diberkahi, dari sebatang pohon kayu, yaitu: \"Ya Musa, sesungguhnya aku adalah Allah, Tuhan semesta alam.'),
(3283, '028:031', '68', 28, 31, 'dan lemparkanlah tongkatmu. Maka tatkala (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seolah-olah dia seekor ular yang gesit, larilah ia berbalik ke belakang tanpa menoleh. (Kemudian Musa diseru): \"Hai Musa datanglah kepada-Ku dan janganlah kamu takut. Sesungguhnya kamu termasuk orang-orang yang aman.'),
(3284, '028:032', '68', 28, 32, 'Masukkanlah tanganmu ke leher bajumu, niscaya ia keluar putih tidak bercacat bukan karena penyakit, dan dekapkanlah kedua tanganmu (ke dada)mu bila ketakutan, maka yang demikian itu adalah dua mukjizat dari Tuhanmu (yang akan kamu hadapkan kepada Fir´aun dan pembesar-pembesarnya). Sesungguhnya mereka adalah orang-orang yang fasik\".'),
(3285, '028:033', '68', 28, 33, 'Musa berkata: \"Ya Tuhanku sesungguhnya aku, telah membunuh seorang manusia dari golongan mereka, maka aku takut mereka akan membunuhku.'),
(3286, '028:034', '68', 28, 34, 'Dan saudaraku Harun dia lebih fasih lidahnya daripadaku, maka utuslah dia bersamaku sebagai pembantuku untuk membenarkan (perkataan)ku; sesungguhnya aku khawatir mereka akan mendustakanku\".'),
(3287, '028:035', '68', 28, 35, 'Allah berfirman: \"Kami akan membantumu dengan saudaramu, dan Kami berikan kepadamu berdua kekuasaan yang besar, maka mereka tidak dapat mencapaimu; (berangkatlah kamu berdua) dengan membawa mukjizat Kami, kamu berdua dan orang yang mengikuti kamulah yang akan menang.'),
(3288, '028:036', '68', 28, 36, 'Maka tatkala Musa datang kepada mereka dengan (membawa) mukjizat-mukjizat Kami yang nyata, mereka berkata: \"Ini tidak lain hanyalah sihir yang dibuat-buat dan kami belum pernah mendengar (seruan yang seperti) ini pada nenek moyang kami dahulu\".'),
(3289, '028:037', '68', 28, 37, 'Musa menjawab: \"Tuhanku lebih mengetahui orang yang (patut) membawa petunjuk dari sisi-Nya dan siapa yang akan mendapat kesudahan (yang baik) di negeri akhirat. Sesungguhnya tidaklah akan mendapat kemenangan orang-orang yang zalim\".'),
(3290, '028:038', '68', 28, 38, 'Dan berkata Fir´aun: \"Hai pembesar kaumku, aku tidak mengetahui tuhan bagimu selain aku. Maka bakarlah hai Haman untukku tanah liat kemudian buatkanlah untukku bangunan yang tinggi supaya aku dapat naik melihat Tuhan Musa, dan sesungguhnya aku benar-benar yakin bahwa dia termasuk orang-orang pendusta\".'),
(3291, '028:039', '68', 28, 39, 'dan berlaku angkuhlah Fir´aun dan bala tentaranya di bumi (Mesir) tanpa alasan yang benar dan mereka menyangka bahwa mereka tidak akan dikembalikan kepada Kami.'),
(3292, '028:040', '68', 28, 40, 'Maka Kami hukumlah Fir´aun dan bala tentaranya, lalu Kami lemparkan mereka ke dalam laut. Maka lihatlah bagaimana akibat orang-orang yang zalim.'),
(3293, '028:041', '68', 28, 41, 'Dan Kami jadikan mereka pemimpin-pemimpin yang menyeru (manusia) ke neraka dan pada hari kiamat mereka tidak akan ditolong.'),
(3294, '028:042', '68', 28, 42, 'Dan Kami ikutkanlah laknat kepada mereka di dunia ini; dan pada hari kiamat mereka termasuk orang-orang yang dijauhkan (dari rahmat Allah).'),
(3295, '028:043', '68', 28, 43, 'Dan sesungguhnya telah Kami berikan kepada Musa Al-Kitab (Taurat) sesudah Kami binasakan generasi-generasi yang terdahulu, untuk menjadi pelita bagi manusia dan petunjuk dan rahmat, agar mereka ingat.'),
(3296, '028:044', '68', 28, 44, 'Dan tidaklah kamu (Muhammad) berada di sisi yang sebelah barat ketika Kami menyampaikan perintah kepada Musa, dan tiada pula kamu termasuk orang-orang yang menyaksikan.'),
(3297, '028:045', '68', 28, 45, 'Tetapi Kami telah mengadakan beberapa generasi, dan berlalulah atas mereka masa yang panjang, dan tiadalah kamu tinggal bersama-sama penduduk Mad-yan dengan membacakan ayat-ayat Kami kepada mereka, tetapi Kami telah mengutus rasul-rasul.'),
(3298, '028:046', '68', 28, 46, 'Dan tiadalah kamu berada di dekat gunung Thur ketika Kami menyeru (Musa), tetapi (Kami beritahukan itu kepadamu) sebagai rahmat dari Tuhanmu, supaya kamu memberi peringatan kepada kaum (Quraisy) yang sekali-kali belum datang kepada mereka pemberi peringatan sebelum kamu agar mereka ingat.'),
(3299, '028:047', '68', 28, 47, 'Dan agar mereka tidak mengatakan ketika azab menimpa mereka disebabkan apa yang mereka kerjakan: \"Ya Tuhan kami, mengapa Engkau tidak mengutus seorang rasul kepada kami, lalu kami mengikuti ayat-ayat Engkau dan jadilah kami termasuk orang-orang mukmin\".'),
(3300, '028:048', '68', 28, 48, 'Maka tatkala datang kepada mereka kebenaran dari sisi Kami, mereka berkata: \"Mengapakah tidak diberikan kepadanya (Muhammad) seperti yang telah diberikan kepada Musa dahulu?\". Dan bukankah mereka itu telah ingkar (juga) kepada apa yang telah diberikan kepada Musa dahulu?; mereka dahulu telah berkata: \"Musa dan Harun adalah dua ahli sihir yang bantu membantu\". Dan mereka (juga) berkata: \"Sesungguhnya kami tidak mempercayai masing-masing mereka itu\".'),
(3301, '028:049', '68', 28, 49, 'Katakanlah: \"Datangkanlah olehmu sebuah kitab dari sisi Allah yang kitab itu lebih (dapat) memberi petunjuk daripada keduanya (Taurat dan Al Quran) niscaya aku mengikutinya, jika kamu sungguh orang-orang yang benar\".'),
(3302, '028:050', '68', 28, 50, 'Maka jika mereka tidak menjawab (tantanganmu) ketahuilah bahwa sesungguhnya mereka hanyalah mengikuti hawa nafsu mereka (belaka). Dan siapakah yang lebih sesat daripada orang yang mengikuti hawa nafsunya dengan tidak mendapat petunjuk dari Allah sedikitpun. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang zalim.'),
(3303, '028:051', '68', 28, 51, 'Dan sesungguhnya telah Kami turunkan berturut-turut perkataan ini (Al Quran) kepada mereka agar mereka mendapat pelajaran.'),
(3304, '028:052', '68', 28, 52, 'Orang-orang yang telah Kami datangkan kepada mereka Al Kitab sebelum Al Quran, mereka beriman (pula) dengan Al Quran itu.'),
(3305, '028:053', '68', 28, 53, 'Dan apabila dibacakan (Al Quran itu) kepada mereka, mereka berkata: \"Kami beriman kepadanya; sesungguhnya; Al Quran itu adalah suatu kebenaran dari Tuhan kami, sesungguhnya kami sebelumnya adalah orang-orang yang membenarkan(nya).'),
(3306, '028:054', '68', 28, 54, 'Mereka itu diberi pahala dua kali disebabkan kesabaran mereka, dan mereka menolak kejahatan dengan kebaikan, dan sebagian dari apa yang telah Kami rezekikan kepada mereka, mereka nafkahkan.'),
(3307, '028:055', '68', 28, 55, 'Dan apabila mereka mendengar perkataan yang tidak bermanfaat, mereka berpaling daripadanya dan mereka berkata: \"Bagi kami amal-amal kami dan bagimu amal-amalmu, kesejahteraan atas dirimu, kami tidak ingin bergaul dengan orang-orang jahil\".'),
(3308, '028:056', '68', 28, 56, 'Sesungguhnya kamu tidak akan dapat memberi petunjuk kepada orang yang kamu kasihi, tetapi Allah memberi petunjuk kepada orang yang dikehendaki-Nya, dan Allah lebih mengetahui orang-orang yang mau menerima petunjuk.'),
(3309, '028:057', '68', 28, 57, 'Dan mereka berkata: \"Jika kami mengikuti petunjuk bersama kamu, niscaya kami akan diusir dari negeri kami\". Dan apakah Kami tidak meneguhkan kedudukan mereka dalam daerah haram (tanah suci) yang aman, yang didatangkan ke tempat itu buah-buahan dari segala macam (tumbuh-tumbuhan) untuk menjadi rezeki (bagimu) dari sisi Kami?. Tetapi kebanyakan mereka tidak mengetahui.'),
(3310, '028:058', '68', 28, 58, 'Dan berapa banyaknya (penduduk) negeri yang telah Kami binasakan, yang sudah bersenang-senang dalam kehidupannya; maka itulah tempat kediaman mereka yang tiada di diami (lagi) sesudah mereka, kecuali sebahagian kecil. Dan Kami adalah Pewaris(nya).'),
(3311, '028:059', '68', 28, 59, 'Dan tidak adalah Tuhanmu membinasakan kota-kota, sebelum Dia mengutus di ibukota itu seorang rasul yang membacakan ayat-ayat Kami kepada mereka; dan tidak pernah (pula) Kami membinasakan kota-kota; kecuali penduduknya dalam keadaan melakukan kezaliman.'),
(3312, '028:060', '68', 28, 60, 'Dan apa saja yang diberikan kepada kamu, maka itu adalah kenikmatan hidup duniawi dan perhiasannya; sedang apa yang di sisi Allah adalah lebih baik dan lebih kekal. Maka apakah kamu tidak memahaminya?'),
(3313, '028:061', '68', 28, 61, 'Maka apakah orang yang Kami janjikan kepadanya suatu janji yang baik (surga) lalu ia memperolehnya, sama dengan orang yang Kami berikan kepadanya kenikmatan hidup duniawi; kemudian dia pada hari kiamat termasuk orang-orang yang diseret (ke dalam neraka)?'),
(3314, '028:062', '68', 28, 62, 'Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: \"Di manakah sekutu-sekutu-Ku yang dahulu kamu katakan?\"'),
(3315, '028:063', '68', 28, 63, 'Berkatalah orang-orang yang telah tetap hukuman atas mereka; \"Ya Tuhan kami, mereka inilah orang-orang yang kami sesatkan itu; kami telah menyesatkan mereka sebagaimana kami (sendiri) sesat, kami menyatakan berlepas diri (dari mereka) kepada Engkau, mereka sekali-kali tidak menyembah kami\".'),
(3316, '028:064', '68', 28, 64, 'Dikatakan (kepada mereka) \"Serulah olehmu sekutu-sekutu kamu\", lalu mereka menyerunya, maka sekutu-sekutu itu tidak memperkenankan (seruan) mereka, dan mereka melihat azab. (Mereka ketika itu berkeinginan) kiranya mereka dahulu menerima petunjuk.'),
(3317, '028:065', '68', 28, 65, 'Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: \"Apakah jawabanmu kepada para rasul?\"'),
(3318, '028:066', '68', 28, 66, 'Maka gelaplah bagi mereka segala macam alasan pada hari itu, karena itu mereka tidak saling tanya menanya.'),
(3319, '028:067', '68', 28, 67, 'Adapun orang yang bertaubat dan beriman, serta mengerjakan amal yang saleh, semoga dia termasuk orang-orang yang beruntung.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(3320, '028:068', '68', 28, 68, 'Dan Tuhanmu menciptakan apa yang Dia kehendaki dan memilihnya. Sekali-kali tidak ada pilihan bagi mereka. Maha Suci Allah dan Maha Tinggi dari apa yang mereka persekutukan (dengan Dia).'),
(3321, '028:069', '68', 28, 69, 'Dan Tuhanmu mengetahui apa yang disembunyikan (dalam) dada mereka dan apa yang mereka nyatakan.'),
(3322, '028:070', '68', 28, 70, 'Dan Dialah Allah, tidak ada Tuhan (yang berhak disembah) melainkan Dia, bagi-Nya-lah segala puji di dunia dan di akhirat, dan bagi-Nya-lah segala penentuan dan hanya kepada-Nya-lah kamu dikembalikan.'),
(3323, '028:071', '68', 28, 71, 'Katakanlah: \"Terangkanlah kepadaku, jika Allah menjadikan untukmu malam itu terus menerus sampai hari kiamat, siapakah Tuhan selain Allah yang akan mendatangkan sinar terang kepadamu? Maka apakah kamu tidak mendengar?\"'),
(3324, '028:072', '68', 28, 72, 'Katakanlah: \"Terangkanlah kepadaku, jika Allah menjadikan untukmu siang itu terus menerus sampai hari kiamat, siapakah Tuhan selain Allah yang akan mendatangkan malam kepadamu yang kamu beristirahat padanya? Maka apakah kamu tidak memperhatikan?\"'),
(3325, '028:073', '68', 28, 73, 'Dan karena rahmat-Nya, Dia jadikan untukmu malam dan siang, supaya kamu beristirahat pada malam itu dan supaya kamu mencari sebahagian dari karunia-Nya (pada siang hari) dan agar kamu bersyukur kepada-Nya.'),
(3326, '028:074', '68', 28, 74, 'Dan (ingatlah) hari (di waktu) Allah menyeru mereka, seraya berkata: \"Di manakah sekutu-sekutu-Ku yang dahulu kamu katakan?\"'),
(3327, '028:075', '68', 28, 75, 'Dan Kami datangkan dari tiap-tiap umat seorang saksi, lalu Kami berkata \"Tunjukkanlah bukti kebenaranmu\", maka tahulah mereka bahwasanya yang hak itu kepunyaan Allah dan lenyaplah dari mereka apa yang dahulunya mereka ada-adakan.'),
(3328, '028:076', '68', 28, 76, 'Sesungguhnya Karun adalah termasuk kaum Musa, maka ia berlaku aniaya terhadap mereka, dan Kami telah menganugerahkan kepadanya perbendaharaan harta yang kunci-kuncinya sungguh berat dipikul oleh sejumlah orang yang kuat-kuat. (Ingatlah) ketika kaumnya berkata kepadanya: \"Janganlah kamu terlalu bangga; sesungguhnya Allah tidak menyukai orang-orang yang terlalu membanggakan diri\".'),
(3329, '028:077', '68', 28, 77, 'Dan carilah pada apa yang telah dianugerahkan Allah kepadamu (kebahagiaan) negeri akhirat, dan janganlah kamu melupakan bahagianmu dari (kenikmatan) duniawi dan berbuat baiklah (kepada orang lain) sebagaimana Allah telah berbuat baik, kepadamu, dan janganlah kamu berbuat kerusakan di (muka) bumi. Sesungguhnya Allah tidak menyukai orang-orang yang berbuat kerusakan.'),
(3330, '028:078', '68', 28, 78, 'Karun berkata: \"Sesungguhnya aku hanya diberi harta itu, karena ilmu yang ada padaku\". Dan apakah ia tidak mengetahui, bahwasanya Allah sungguh telah membinasakan umat-umat sebelumnya yang lebih kuat daripadanya, dan lebih banyak mengumpulkan harta? Dan tidaklah perlu ditanya kepada orang-orang yang berdosa itu, tentang dosa-dosa mereka.'),
(3331, '028:079', '68', 28, 79, 'Maka keluarlah Karun kepada kaumnya dalam kemegahannya. Berkatalah orang-orang yang menghendaki kehidupan dunia: \"Moga-moga kiranya kita mempunyai seperti apa yang telah diberikan kepada Karun; sesungguhnya ia benar-benar mempunyai keberuntungan yang besar\".'),
(3332, '028:080', '68', 28, 80, 'Berkatalah orang-orang yang dianugerahi ilmu: \"Kecelakaan yang besarlah bagimu, pahala Allah adalah lebih baik bagi orang-orang yang beriman dan beramal saleh, dan tidak diperoleh pahala itu, kecuali oleh orang-orang yang sabar\".'),
(3333, '028:081', '68', 28, 81, 'Maka Kami benamkanlah Karun beserta rumahnya ke dalam bumi. Maka tidak ada baginya suatu golonganpun yang menolongnya terhadap azab Allah. Dan tiadalah ia termasuk orang-orang (yang dapat) membela (dirinya).'),
(3334, '028:082', '68', 28, 82, 'Dan jadilah orang-orang yang kemarin mencita-citakan kedudukan Karun itu, berkata: \"Aduhai, benarlah Allah melapangkan rezeki bagi siapa yang Dia kehendaki dari hamba-hambanya dan menyempitkannya; kalau Allah tidak melimpahkan karunia-Nya atas kita benar-benar Dia telah membenamkan kita (pula). Aduhai benarlah, tidak beruntung orang-orang yang mengingkari (nikmat Allah)\".'),
(3335, '028:083', '68', 28, 83, 'Negeri akhirat itu, Kami jadikan untuk orang-orang yang tidak ingin menyombongkan diri dan berbuat kerusakan di (muka) bumi. Dan kesudahan (yang baik) itu adalah bagi orang-orang yang bertakwa.'),
(3336, '028:084', '68', 28, 84, 'Barangsiapa yang datang dengan (membawa) kebaikan, maka baginya (pahala) yang lebih baik daripada kebaikannya itu; dan barangsiapa yang datang dengan (membawa) kejahatan, maka tidaklah diberi pembalasan kepada orang-orang yang telah mengerjakan kejahatan itu, melainkan (seimbang) dengan apa yang dahulu mereka kerjakan.'),
(3337, '028:085', '68', 28, 85, 'Sesungguhnya yang mewajibkan atasmu (melaksanakan hukum-hukum) Al Quran, benar-benar akan mengembalikan kamu ke tempat kembali. Katakanlah: \"Tuhanku mengetahui orang yang membawa petunjuk dan orang yang dalam kesesatan yang nyata\".'),
(3338, '028:086', '68', 28, 86, 'Dan kamu tidak pernah mengharap agar Al Quran diturunkan kepadamu, tetapi ia (diturunkan) karena suatu rahmat yang besar dari Tuhanmu , sebab itu janganlah sekali-kali kamu menjadi penolong bagi orang-orang kafir.'),
(3339, '028:087', '68', 28, 87, 'Dan janganlah sekali-kali mereka dapat menghalangimu dari (menyampaikan) ayat-ayat Allah, sesudah ayat-ayat itu diturunkan kepadamu, dan serulah mereka kepada (jalan) Tuhanmu, dan janganlah sekali-sekali kamu termasuk orang-orang yang mempersekutukan Tuhan.'),
(3340, '028:088', '68', 28, 88, 'Janganlah kamu sembah di samping (menyembah) Allah, tuhan apapun yang lain. Tidak ada Tuhan (yang berhak disembah) melainkan Dia. Tiap-tiap sesuatu pasti binasa, kecuali Allah. Bagi-Nya-lah segala penentuan, dan hanya kepada-Nya-lah kamu dikembalikan.'),
(3341, '029:001', '68', 29, 1, 'Alif laam miim'),
(3342, '029:002', '68', 29, 2, 'Apakah manusia itu mengira bahwa mereka dibiarkan (saja) mengatakan: \"Kami telah beriman\", sedang mereka tidak diuji lagi?'),
(3343, '029:003', '68', 29, 3, 'Dan sesungguhnya kami telah menguji orang-orang yang sebelum mereka, maka sesungguhnya Allah mengetahui orang-orang yang benar dan sesungguhnya Dia mengetahui orang-orang yang dusta.'),
(3344, '029:004', '68', 29, 4, 'Ataukah orang-orang yang mengerjakan kejahatan itu mengira bahwa mereka akan luput (dari azab) Kami? Amatlah buruk apa yang mereka tetapkan itu.'),
(3345, '029:005', '68', 29, 5, 'Barangsiapa yang mengharap pertemuan dengan Allah, maka sesungguhnya waktu (yang dijanjikan) Allah itu, pasti datang. Dan Dialah Yang Maha mendengar lagi Maha Mengetahui.'),
(3346, '029:006', '68', 29, 6, 'Dan barangsiapa yang berjihad, maka sesungguhnya jihadnya itu adalah untuk dirinya sendiri. Sesungguhnya Allah benar-benar Maha Kaya (tidak memerlukan sesuatu) dari semesta alam.'),
(3347, '029:007', '68', 29, 7, 'Dan orang-orang yang beriman dan beramal saleh, benar-benar akan Kami hapuskan dari mereka dosa-dosa mereka dan benar-benar akan Kami beri mereka balasan yang lebih baik dari apa yang mereka kerjakan.'),
(3348, '029:008', '68', 29, 8, 'Dan Kami wajibkan manusia (berbuat) kebaikan kepada dua orang ibu-bapaknya. Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang tidak ada pengetahuanmu tentang itu, maka janganlah kamu mengikuti keduanya. Hanya kepada-Ku-lah kembalimu, lalu Aku kabarkan kepadamu apa yang telah kamu kerjakan.'),
(3349, '029:009', '68', 29, 9, 'Dan orang-orang yang beriman dan mengerjakan amal saleh benar-benar akan Kami masukkan mereka ke dalam (golongan) orang-orang yang saleh.'),
(3350, '029:010', '68', 29, 10, 'Dan di antara manusia ada orang yang berkata: \"Kami beriman kepada Allah\", maka apabila ia disakiti (karena ia beriman) kepada Allah, ia menganggap fitnah manusia itu sebagai azab Allah. Dan sungguh jika datang pertolongan dari Tuhanmu, mereka pasti akan berkata: \"Sesungguhnya kami adalah besertamu\". Bukankah Allah lebih mengetahui apa yang ada dalam dada semua manusia?'),
(3351, '029:011', '68', 29, 11, 'Dan sesungguhnya Allah benar-benar mengetahui orang-orang yang beriman: dan sesungguhnya Dia mengetahui orang-orang yang munafik.'),
(3352, '029:012', '68', 29, 12, 'Dan berkatalah orang-orang kafir kepada orang-orang yang beriman: \"Ikutilah jalan kami, dan nanti kami akan memikul dosa-dosamu\", dan mereka (sendiri) sedikitpun tidak (sanggup), memikul dosa-dosa mereka. Sesungguhnya mereka adalah benar-benar orang pendusta.'),
(3353, '029:013', '68', 29, 13, 'Dan sesungguhnya mereka akan memikul beban (dosa) mereka, dan beban-beban (dosa yang lain) di samping beban-beban mereka sendiri, dan sesungguhnya mereka akan ditanya pada hari kiamat tentang apa yang selalu mereka ada-adakan.'),
(3354, '029:014', '68', 29, 14, 'Dan sesungguhnya Kami telah mengutus Nuh kepada kaumnya, maka ia tinggal di antara mereka seribu tahun kurang lima puluh tahun. Maka mereka ditimpa banjir besar, dan mereka adalah orang-orang yang zalim.'),
(3355, '029:015', '68', 29, 15, 'Maka Kami selamatkan Nuh dan penumpang-penumpang bahtera itu dan Kami jadikan peristiwa itu pelajaran bagi semua umat manusia.'),
(3356, '029:016', '68', 29, 16, 'Dan (ingatlah) Ibrahim, ketika ia berkata kepada kaumnya: \"Sembahlah olehmu Allah dan bertakwalah kepada-Nya. Yang demikian itu adalah lebih baik bagimu, jika kamu mengetahui.'),
(3357, '029:017', '68', 29, 17, 'Sesungguhnya apa yang kamu sembah selain Allah itu adalah berhala, dan kamu membuat dusta. Sesungguhnya yang kamu sembah selain Allah itu tidak mampu memberikan rezeki kepadamu; maka mintalah rezeki itu di sisi Allah, dan sembahlah Dia dan bersyukurlah kepada-Nya. Hanya kepada-Nya-lah kamu akan dikembalikan.'),
(3358, '029:018', '68', 29, 18, 'Dan jika kamu (orang kafir) mendustakan, maka umat yang sebelum kamu juga telah mendustakan. Dan kewajiban rasul itu, tidak lain hanyalah menyampaikan (agama Allah) dengan seterang-terangnya\".'),
(3359, '029:019', '68', 29, 19, 'Dan apakah mereka tidak memperhatikan bagaimana Allah menciptakan (manusia) dari permulaannya, kemudian mengulanginya (kembali). Sesungguhnya yang demikian itu adalah mudah bagi Allah.'),
(3360, '029:020', '68', 29, 20, 'Katakanlah: \"Berjalanlah di (muka) bumi, maka perhatikanlah bagaimana Allah menciptakan (manusia) dari permulaannya, kemudian Allah menjadikannya sekali lagi. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(3361, '029:021', '68', 29, 21, 'Allah mengazab siapa yang dikehendaki-Nya, dan memberi rahmat kepada siapa yang dikehendaki-Nya, dan hanya kepada-Nya-lah kamu akan dikembalikan.'),
(3362, '029:022', '68', 29, 22, 'Dan kamu sekali-kali tidak dapat melepaskan diri (dari azab Allah) di bumi dan tidak (pula) di langit dan sekali-kali tiadalah bagimu pelindung dan penolong selain Allah.'),
(3363, '029:023', '68', 29, 23, 'Dan orang-orang yang kafir terhadap ayat-ayat Allah dan pertemuan dengan Dia, mereka putus asa dari rahmat-Ku, dan mereka itu mendapat azab yang pedih.'),
(3364, '029:024', '68', 29, 24, 'Maka tidak adalah jawaban kaum Ibrahim, selain mengatakan: \"Bunuhlah atau bakarlah dia\", lalu Allah menyelamatkannya dari api. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda kebesaran Allah bagi orang-orang yang beriman.'),
(3365, '029:025', '68', 29, 25, 'Dan berkata Ibrahim: \"Sesungguhnya berhala-berhala yang kamu sembah selain Allah adalah untuk menciptakan perasaan kasih sayang di antara kamu dalam kehidupan dunia ini kemudian di hari kiamat sebahagian kamu mengingkari sebahagian (yang lain) dan sebahagian kamu melaknati sebahagian (yang lain); dan tempat kembalimu ialah neraka, dan sekali-kali tak ada bagimu para penolongpun.'),
(3366, '029:026', '68', 29, 26, 'Maka Luth membenarkan (kenabian)nya. Dan berkatalah Ibrahim: \"Sesungguhnya aku akan berpindah ke (tempat yang diperintahkan) Tuhanku (kepadaku); sesungguhnya Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(3367, '029:027', '68', 29, 27, 'Dan Kami anugrahkan kepda Ibrahim, Ishak dan Ya´qub, dan Kami jadikan kenabian dan Al Kitab pada keturunannya, dan Kami berikan kepadanya balasannya di dunia; dan sesungguhnya dia di akhirat, benar-benar termasuk orang-orang yang saleh.'),
(3368, '029:028', '68', 29, 28, 'Dan (ingatlah) ketika Luth berkata pepada kaumnya: \"Sesungguhnya kamu benar-benar mengerjakan perbuatan yang amat keji yang belum pernah dikerjakan oleh seorangpun dari umat-umat sebelum kamu\".'),
(3369, '029:029', '68', 29, 29, 'Apakah sesungguhnya kamu patut mendatangi laki-laki, menyamun dan mengerjakan kemungkaran di tempat-tempat pertemuanmu? Maka jawaban kaumnya tidak lain hanya mengatakan: \"Datangkanlah kepada kami azab Allah, jika kamu termasuk orang-orang yang benar\".'),
(3370, '029:030', '68', 29, 30, 'Luth berdoa: \"Ya Tuhanku, tolonglah aku (dengan menimpakan azab) atas kaum yang berbuat kerusakan itu\".'),
(3371, '029:031', '68', 29, 31, 'Dan tatkala utusan Kami (para malaikat) datang kepada Ibrahim membawa kabar gembira, mereka mengatakan: \"Sesungguhnya kami akan menghancurkan penduduk negeri (Sodom) ini; sesungguhnya penduduknya adalah orang-orang yang zalim\".'),
(3372, '029:032', '68', 29, 32, 'Berkata Ibrahim: \"Sesungguhnya di kota itu ada Luth\". Para malaikat berkata: \"Kami lebih mengetahui siapa yang ada di kota itu. Kami sungguh-sungguh akan menyelamatkan dia dan pengikut-pengikutnya kecuali isterinya. Dia adalah termasuk orang-orang yang tertinggal (dibinasakan).'),
(3373, '029:033', '68', 29, 33, 'Dan tatkala datang utusan-utusan Kami (para malaikat) itu kepada Luth, dia merasa susah karena (kedatangan) mereka, dan (merasa) tidak punya kekuatan untuk melindungi mereka dan mereka berkata: \"Janganlah kamu takut dan jangan (pula) susah. Sesungguhnya kami akan menyelamatkan kamu dan pengikut-pengikutmu, kecuali isterimu, dia adalah termasuk orang-orang yang tertinggal (dibinasakan)\".'),
(3374, '029:034', '68', 29, 34, 'Sesungguhnya Kami akan menurunkan azab dari langit atas penduduk kota ini karena mereka berbuat fasik.'),
(3375, '029:035', '68', 29, 35, 'Dan sesungguhnya Kami tinggalkan daripadanya satu tanda yang nyata bagi orang-orang yang berakal.'),
(3376, '029:036', '68', 29, 36, 'Dan (Kami telah mengutus) kepada penduduk Mad-yan, saudara mereka Syu´aib, maka ia berkata: \"Hai kaumku, sembahlah olehmu Allah, harapkanlah (pahala) hari akhir, dan jangan kamu berkeliaran di muka bumi berbuat kerusakan\".'),
(3377, '029:037', '68', 29, 37, 'Maka mereka mendustakan Syu´aib, lalu mereka ditimpa gempa yang dahsyat, dan jadilah mereka mayat-mayat yang bergelimpangan di tempat-tempat tinggal mereka.'),
(3378, '029:038', '68', 29, 38, 'Dan (juga) kaum ´Aad dan Tsamud, dan sungguh telah nyata bagi kamu (kehancuran mereka) dari (puing-puing) tempat tinggal mereka. Dan syaitan menjadikan mereka memandang baik perbuatan-perbuatan mereka, lalu ia menghalangi mereka dari jalan (Allah), sedangkan mereka adalah orang-orang berpandangan tajam,'),
(3379, '029:039', '68', 29, 39, 'dan (juga) Karun, Fir´aun dan Haman. Dan sesungguhnya telah datang kepada mereka Musa dengan (membawa bukti-bukti) keterangan-keterangan yang nyata. Akan tetapi mereka berlaku sombong di (muka) bumi, dan tiadalah mereka orang-orang yang luput (dari kehancuran itu).'),
(3380, '029:040', '68', 29, 40, 'Maka masing-masing (mereka itu) Kami siksa disebabkan dosanya, maka di antara mereka ada yang Kami timpakan kepadanya hujan batu kerikil dan di antara mereka ada yang ditimpa suara keras yang mengguntur, dan di antara mereka ada yang Kami benamkan ke dalam bumi, dan di antara mereka ada yang Kami tenggelamkan, dan Allah sekali-kali tidak hendak menganiaya mereka, akan tetapi merekalah yang menganiaya diri mereka sendiri.'),
(3381, '029:041', '68', 29, 41, 'Perumpamaan orang-orang yang mengambil pelindung-pelindung selain Allah adalah seperti laba-laba yang membuat rumah. Dan sesungguhnya rumah yang paling lemah adalah rumah laba-laba kalau mereka mengetahui.'),
(3382, '029:042', '68', 29, 42, 'Sesungguhnya Allah mengetahui apa saja yang mereka seru selain Allah. Dan Dia Maha Perkasa lagi Maha Bijaksana.'),
(3383, '029:043', '68', 29, 43, 'Dan perumpamaan-perumpamaan ini Kami buat untuk manusia; dan tiada yang memahaminya kecuali orang-orang yang berilmu.'),
(3384, '029:044', '68', 29, 44, 'Allah menciptakan langit dan bumi dengan hak. Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi orang-orang mukmin.'),
(3385, '029:045', '68', 29, 45, 'Bacalah apa yang telah diwahyukan kepadamu, yaitu Al Kitab (Al Quran) dan dirikanlah shalat. Sesungguhnya shalat itu mencegah dari (perbuatan-perbuatan) keji dan mungkar. Dan sesungguhnya mengingat Allah (shalat) adalah lebih besar (keutamaannya dari ibadat-ibadat yang lain). Dan Allah mengetahui apa yang kamu kerjakan.'),
(3386, '029:046', '68', 29, 46, 'Dan janganlah kamu berdebat denganAhli Kitab, melainkan dengan cara yang paling baik, kecuali dengan orang-orang zalim di antara mereka, dan katakanlah: \"Kami telah beriman kepada (kitab-kitab) yang diturunkan kepada kami dan yang diturunkan kepadamu; Tuhan kami dan Tuhanmu adalah satu; dan kami hanya kepada-Nya berserah diri\".'),
(3387, '029:047', '68', 29, 47, 'Dan demikian (pulalah) Kami turunkan kepadamu Al Kitab (Al Quran). Maka orang-orang yang telah kami berikan kepada mereka Al Kitab (Taurat) mereka beriman kepadanya (Al Quran); dan di antara mereka (orang-orang kafir Mekah) ada yang beriman kepadanya. Dan tiadalah yang mengingkari ayat-ayat kami selain orang-orang kafir.'),
(3388, '029:048', '68', 29, 48, 'Dan kamu tidak pernah membaca sebelumnya (Al Quran) sesuatu Kitabpun dan kamu tidak (pernah) menulis suatu Kitab dengan tangan kananmu; andaikata (kamu pernah membaca dan menulis), benar-benar ragulah orang yang mengingkari(mu).'),
(3389, '029:049', '68', 29, 49, 'Sebenarnya, Al Quran itu adalah ayat-ayat yang nyata di dalam dada orang-orang yang diberi ilmu. Dan tidak ada yang mengingkari ayat-ayat Kami kecuali orang-orang yang zalim.'),
(3390, '029:050', '68', 29, 50, 'Dan orang-orang kafir Mekah berkata: \"Mengapa tidak diturunkan kepadanya mukjizat-mukjizat dari Tuhannya?\" Katakanlah: \"Sesungguhnya mukjizat-mukjizat itu terserah kepada Allah. Dan sesungguhnya aku hanya seorang pemberi peringatan yang nyata\".'),
(3391, '029:051', '68', 29, 51, 'Dan apakah tidak cukup bagi mereka bahwasanya Kami telah menurunkan kepadamu Al Kitab (Al Quran) sedang dia dibacakan kepada mereka? Sesungguhnya dalam (Al Quran) itu terdapat rahmat yang besar dan pelajaran bagi orang-orang yang beriman.'),
(3392, '029:052', '68', 29, 52, 'Katakanlah: \"Cukuplah Allah menjadi saksi antaraku dan antaramu. Dia mengetahui apa yang di langit dan di bumi. Dan orang-orang yang percaya kepada yang batil dan ingkar kepada Allah, mereka itulah orang-orang yang merugi.'),
(3393, '029:053', '68', 29, 53, 'Dan mereka meminta kepadamu supaya segera diturunkan azab. Kalau tidaklah karena waktu yang telah ditetapkan, benar-benar telah datang azab kepada mereka, dan azab itu benar-benar akan datang kepada mereka dengan tiba-tiba, sedang mereka tidak menyadarinya.'),
(3394, '029:054', '68', 29, 54, 'Mereka meminta kepadamu supaya segera diturunkan azab. Dan sesungguhnya Jahannam benar-benar meliputi orang-orang yang kafir,'),
(3395, '029:055', '68', 29, 55, 'pada hari mereka ditutup oleh azab dari atas mereka dan dari bawah kaki mereka dan Allah berkata (kepada mereka): \"Rasailah (pembalasan dari) apa yang telah kamu kerjakan\".'),
(3396, '029:056', '68', 29, 56, 'Hai hamba-hamba-Ku yang beriman, sesungguhnya bumi-Ku luas, maka sembahlah Aku saja.'),
(3397, '029:057', '68', 29, 57, 'Tiap-tiap yang berjiwa akan merasakan mati. Kemudian hanyalah kepada Kami kamu dikembalikan.'),
(3398, '029:058', '68', 29, 58, 'Dan orang-orang yang beriman dan mengerjakan amal-amal yang saleh, sesungguhnya akan Kami tempatkan mereka pada tempat-tempat yang tinggi di dalam surga, yang mengalir sungai-sungai di bawahnya, mereka kekal di dalamnya. Itulah sebaik-baik pembalasan bagi orang-orang yang beramal,'),
(3399, '029:059', '68', 29, 59, '(yaitu) yang bersabar dan bertawakkal kepada Tuhannya.'),
(3400, '029:060', '68', 29, 60, 'Dan berapa banyak binatang yang tidak (dapat) membawa (mengurus) rezekinya sendiri. Allah-lah yang memberi rezeki kepadanya dan kepadamu dan Dia Maha Mendengar lagi Maha Mengetahui.'),
(3401, '029:061', '68', 29, 61, 'Dan sesungguhnya jika kamu tanyakan kepada mereka: \"Siapakah yang menjadikan langit dan bumi dan menundukkan matahari dan bulan?\" Tentu mereka akan menjawab: \"Allah\", maka betapakah mereka (dapat) dipalingkan (dari jalan yang benar).'),
(3402, '029:062', '68', 29, 62, 'Allah melapangkan rezeki bagi siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan Dia (pula) yang menyempitkan baginya. Sesungguhnya Allah Maha Mengetahui segala sesuatu.'),
(3403, '029:063', '68', 29, 63, 'Dan sesungguhnya jika kamu menanyakan kepada mereka: \"Siapakah yang menurunkan air dari langit lalu menghidupkan dengan air itu bumi sesudah matinya?\" Tentu mereka akan menjawab: \"Allah\", Katakanlah: \"Segala puji bagi Allah\", tetapi kebanyakan mereka tidak memahami(nya).'),
(3404, '029:064', '68', 29, 64, 'Dan tiadalah kehidupan dunia ini melainkan senda gurau dan main-main. Dan sesungguhnya akhirat itulah yang sebenarnya kehidupan, kalau mereka mengetahui.'),
(3405, '029:065', '68', 29, 65, 'Maka apabila mereka naik kapal mereka mendoa kepada Allah dengan memurnikan ketaatan kepada-Nya; maka tatkala Allah menyelamatkan mereka sampai ke darat, tiba-tiba mereka (kembali) mempersekutukan (Allah)'),
(3406, '029:066', '68', 29, 66, 'agar mereka mengingkari nikmat yang telah Kami berikan kepada mereka dan agar mereka (hidup) bersenang-senang (dalam kekafiran). Kelak mereka akan mengetahui (akibat perbuatannya).'),
(3407, '029:067', '68', 29, 67, 'Dan apakah mereka tidak memperhatikan, bahwa sesungguhnya Kami telah menjadikan (negeri mereka) tanah suci yang aman, sedang manusia sekitarnya rampok-merampok. Maka mengapa (sesudah nyata kebenaran) mereka masih percaya kepada yang bathil dan ingkar kepada nikmat Allah?'),
(3408, '029:068', '68', 29, 68, 'Dan siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kedustaan terhadap Allah atau mendustakan yang hak tatkala yang hak itu datang kepadanya? Bukankah dalam neraka Jahannam itu ada tempat bagi orang-orang yang kafir?'),
(3409, '029:069', '68', 29, 69, 'Dan orang-orang yang berjihad untuk (mencari keridhaan) Kami, benar-benar akan Kami tunjukkan kepada mereka jalan-jalan Kami. Dan sesungguhnya Allah benar-benar beserta orang-orang yang berbuat baik.'),
(3410, '030:001', '68', 30, 1, 'Alif Laam Miim'),
(3411, '030:002', '68', 30, 2, 'Telah dikalahkan bangsa Rumawi,'),
(3412, '030:003', '68', 30, 3, 'di negeri yang terdekat dan mereka sesudah dikalahkan itu akan menang'),
(3413, '030:004', '68', 30, 4, 'dalam beberapa tahun lagi. Bagi Allah-lah urusan sebelum dan sesudah (mereka menang). Dan di hari (kemenangan bangsa Rumawi) itu bergembiralah orang-orang yang beriman,'),
(3414, '030:005', '68', 30, 5, 'Karena pertolongan Allah. Dia menolong siapa yang dikehendaki-Nya. Dan Dialah Maha Perkasa lagi Penyayang.'),
(3415, '030:006', '68', 30, 6, '(Sebagai) janji yang sebenarnya dari Allah. Allah tidak akan menyalahi janji-Nya, tetapi kebanyakan manusia tidak mengetahui.'),
(3416, '030:007', '68', 30, 7, 'Mereka hanya mengetahui yang lahir (saja) dari kehidupan dunia; sedang mereka tentang (kehidupan) akhirat adalah lalai.'),
(3417, '030:008', '68', 30, 8, 'Dan mengapa mereka tidak memikirkan tentang (kejadian) diri mereka? Allah tidak menjadikan langit dan bumi dan apa yang ada diantara keduanya melainkan dengan (tujuan) yang benar dan waktu yang ditentukan. Dan sesungguhnya kebanyakan di antara manusia benar-benar ingkar akan pertemuan dengan Tuhannya.'),
(3418, '030:009', '68', 30, 9, 'Dan apakah mereka tidak mengadakan perjalanan di muka bumi dan memperhatikan bagaimana akibat (yang diderita) oleh orang-orang sebelum mereka? orang-orang itu adalah lebihkuat dari mereka (sendiri) dan telah mengolah bumi (tanah) serta memakmurkannya lebih banyak dari apa yang telah mereka makmurkan. Dan telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata. Maka Allah sekali-kali tidak berlaku zalim kepada mereka, akan tetapi merekalah yang berlaku zalim kepada diri sendiri.'),
(3419, '030:010', '68', 30, 10, 'Kemudian, akibat orang-orang yang mengerjakan kejahatan adalah (azab) yang lebih buruk, karena mereka mendustakan ayat-ayat Allah dan mereka selalu memperolok-oloknya.'),
(3420, '030:011', '68', 30, 11, 'Allah menciptakan (manusia) dari permulaan, kemudian mengembalikan (menghidupkan)nya kembali; kemudian kepada-Nya-lah kamu dikembalikan'),
(3421, '030:012', '68', 30, 12, 'Dan pada hari terjadinya kiamat, orang-orang yang berdosa terdiam berputus asa.'),
(3422, '030:013', '68', 30, 13, 'Dan sekali-kali tidak ada pemberi syafa´at bagi mereka dari berhala-berhala mereka dan adalah mereka mengingkari berhala mereka itu.'),
(3423, '030:014', '68', 30, 14, 'Dan pada hari terjadinya kiamat, di hari itu mereka (manusia) bergolong-golongan.'),
(3424, '030:015', '68', 30, 15, 'Adapun orang-orang yang beriman dan mengerjakan amal saleh, maka mereka di dalam taman (surga) bergembira.'),
(3425, '030:016', '68', 30, 16, 'Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami (Al Quran) serta (mendustakan) menemui hari akhirat, maka mereka tetap berada di dalam siksaan (neraka).'),
(3426, '030:017', '68', 30, 17, 'Maka bertasbihlah kepada Allah di waktu kamu berada di petang hari dan waktu kamu berada di waktu subuh,'),
(3427, '030:018', '68', 30, 18, 'dan bagi-Nya-lah segala puji di langit dan di bumi dan di waktu kamu berada pada petang hari dan di waktu kamu berada di waktu Zuhur.'),
(3428, '030:019', '68', 30, 19, 'Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan menghidupkan bumi sesudah matinya. Dan seperti itulah kamu akan dikeluarkan (dari kubur).'),
(3429, '030:020', '68', 30, 20, 'Dan di antara tanda-tanda kekuasaan-Nya ialah Dia menciptakan kamu dari tanah, kemudian tiba-tiba kamu (menjadi) manusia yang berkembang biak.'),
(3430, '030:021', '68', 30, 21, 'Dan di antara tanda-tanda kekuasaan-Nya ialah Dia menciptakan untukmu isteri-isteri dari jenismu sendiri, supaya kamu cenderung dan merasa tenteram kepadanya, dan dijadikan-Nya diantaramu rasa kasih dan sayang. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang berfikir.'),
(3431, '030:022', '68', 30, 22, 'Dan di antara tanda-tanda kekuasaan-Nya ialah menciptakan langit dan bumi dan berlain-lainan bahasamu dan warna kulitmu. Sesungguhnya pada yang demikan itu benar-benar terdapat tanda-tanda bagi orang-orang yang mengetahui.'),
(3432, '030:023', '68', 30, 23, 'Dan di antara tanda-tanda kekuasaan-Nya ialah tidurmu di waktu malam dan siang hari dan usahamu mencari sebagian dari karunia-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mendengarkan.'),
(3433, '030:024', '68', 30, 24, 'Dan di antara tanda-tanda kekuasaan-Nya, Dia memperlihatkan kepadamu kilat untuk (menimbulkan) ketakutan dan harapan, dan Dia menurunkan hujan dari langit, lalu menghidupkan bumi dengan air itu sesudah matinya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mempergunakan akalnya.'),
(3434, '030:025', '68', 30, 25, 'Dan di antara tanda-tanda kekuasaan-Nya ialah berdirinya langit dan bumi dengan iradat-Nya. Kemudian apabila Dia memanggil kamu sekali panggil dari bumi, seketika itu (juga) kamu keluar (dari kubur).'),
(3435, '030:026', '68', 30, 26, 'Dan kepunyaan-Nya-lah siapa saja yang ada di langit dan di bumi. Semuanya hanya kepada-Nya tunduk.'),
(3436, '030:027', '68', 30, 27, 'Dan Dialah yang menciptakan (manusia) dari permulaan, kemudian mengembalikan (menghidupkan)nya kembali, dan menghidupkan kembali itu adalah lebih mudah bagi-Nya. Dan bagi-Nya-lah sifat yang Maha Tinggi di langit dan di bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(3437, '030:028', '68', 30, 28, 'Dia membuat perumpamaan untuk kamu dari dirimu sendiri. Apakah ada diantara hamba-sahaya yang dimiliki oleh tangan kananmu, sekutu bagimu dalam (memiliki) rezeki yang telah Kami berikan kepadamu; maka kamu sama dengan mereka dalam (hak mempergunakan) rezeki itu, kamu takut kepada mereka sebagaimana kamu takut kepada dirimu sendiri? Demikianlah Kami jelaskan ayat-ayat bagi kaum yang berakal.'),
(3438, '030:029', '68', 30, 29, 'Tetapi orang-orang yang zalim, mengikuti hawa nafsunya tanpa ilmu pengetahuan; maka siapakah yang akan menunjuki orang yang telah disesatkan Allah? Dan tiadalah bagi mereka seorang penolongpun.'),
(3439, '030:030', '68', 30, 30, 'Maka hadapkanlah wajahmu dengan lurus kepada agama Allah; (tetaplah atas) fitrah Allah yang telah menciptakan manusia menurut fitrah itu. Tidak ada peubahan pada fitrah Allah. (Itulah) agama yang lurus; tetapi kebanyakan manusia tidak mengetahui,'),
(3440, '030:031', '68', 30, 31, 'dengan kembali bertaubat kepada-Nya dan bertakwalah kepada-Nya serta dirikanlah shalat dan janganlah kamu termasuk orang-orang yang mempersekutukan Allah,'),
(3441, '030:032', '68', 30, 32, 'yaitu orang-orang yang memecah-belah agama mereka dan mereka menjadi beberapa golongan. Tiap-tiap golongan merasa bangga dengan apa yang ada pada golongan mereka.'),
(3442, '030:033', '68', 30, 33, 'Dan apabila manusia disentuh oleh suatu bahaya, mereka menyeru Tuhannya dengan kembali bertaubat kepada-Nya, kemudian apabila Tuhan merasakan kepada mereka barang sedikit rahmat daripada-Nya, tiba-tiba sebagian dari mereka mempersekutukan Tuhannya,'),
(3443, '030:034', '68', 30, 34, 'sehingga mereka mengingkari akan rahmat yang telah Kami berikan kepada mereka. Maka bersenang-senanglah kamu sekalian, kelak kamu akan mengetahui (akibat perbuatanmu).'),
(3444, '030:035', '68', 30, 35, 'Atau pernahkah Kami menurunkan kepada mereka keterangan, lalu keterangan itu menunjukkan (kebenaran) apa yang mereka selalu mempersekutukan dengan Tuhan?'),
(3445, '030:036', '68', 30, 36, 'Dan apabila Kami rasakan sesuatu rahmat kepada manusia, niscaya mereka gembira dengan rahmat itu. Dan apabila mereka ditimpa suatu musibah (bahaya) disebabkan kesalahan yang telah dikerjakan oleh tangan mereka sendiri, tiba-tiba mereka itu berputus asa.'),
(3446, '030:037', '68', 30, 37, 'Dan apakah mereka tidak memperhatikan bahwa Sesungguhnya Allah melapangkan rezeki bagi siapa yang dikehendaki-Nya dan Dia (pula) yang menyempitkan (rezeki itu). Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang beriman.'),
(3447, '030:038', '68', 30, 38, 'Maka berikanlah kepada kerabat yang terdekat akan haknya, demikian (pula) kepada fakir miskin dan orang-orang yang dalam perjalanan. Itulah yang lebih baik bagi orang-orang yang mencari keridhaan Allah; dan mereka itulah orang-orang beruntung.'),
(3448, '030:039', '68', 30, 39, 'Dan sesuatu riba (tambahan) yang kamu berikan agar dia bertambah pada harta manusia, maka riba itu tidak menambah pada sisi Allah. Dan apa yang kamu berikan berupa zakat yang kamu maksudkan untuk mencapai keridhaan Allah, maka (yang berbuat demikian) itulah orang-orang yang melipat gandakan (pahalanya).'),
(3449, '030:040', '68', 30, 40, 'Allah-lah yang menciptakan kamu, kemudian memberimu rezeki, kemudian mematikanmu, kemudian menghidupkanmu (kembali). Adakah di antara yang kamu sekutukan dengan Allah itu yang dapat berbuat sesuatu dari yang demikian itu? Maha Sucilah Dia dan Maha Tinggi dari apa yang mereka persekutukan.'),
(3450, '030:041', '68', 30, 41, 'Telah nampak kerusakan di darat dan di laut disebabkan karena perbuatan tangan manusi, supay Allah merasakan kepada mereka sebahagian dari (akibat) perbuatan mereka, agar mereka kembali (ke jalan yang benar).'),
(3451, '030:042', '68', 30, 42, 'Katakanlah: \"Adakanlah perjalanan di muka bumi dan perhatikanlah bagaimana kesudahan orang-orang yang terdahulu. Kebanyakan dari mereka itu adalah orang-orang yang mempersekutukan (Allah)\".'),
(3452, '030:043', '68', 30, 43, 'Oleh karena itu, hadapkanlah wajahmu kepada agama yang lurus (Islam) sebelum datang dari Allah suatu hari yang tidak dapat ditolak (kedatangannya): pada hari itu mereka terpisah-pisah.'),
(3453, '030:044', '68', 30, 44, 'Barangsiapa yang kafir maka dia sendirilah yang menanggung (akibat) kekafirannya itu; dan barangsiapa yang beramal saleh maka untuk diri mereka sendirilah mereka menyiapkan (tempat yang menyenangkan),'),
(3454, '030:045', '68', 30, 45, 'agar Allah memberi pahala kepada orang-orang yang beriman dan beramal saleh dari karunia-Nya. Sesungguhnya Dia tidak menyukai orang-orang yang ingkar.'),
(3455, '030:046', '68', 30, 46, 'Dan di antara tanda-tanda kekuasan-Nya adalah bahwa Dia mengirimkan angin sebagai pembawa berita gembira dan untuk merasakan kepadamu sebagian dari rahmat-Nya dan supaya kapal dapat berlayar dengan perintah-Nya dan (juga) supaya kamu dapat mencari karunia-Nya; mudah-mudahn kamu bersyukur.'),
(3456, '030:047', '68', 30, 47, 'Dan Sesungguhnya Kami telah mengutus sebelum kamu beberapa orang rasul kepada kaumnya, mereka datang kepadanya dengan membawa keterangan-keterangan (yang cukup), lalu Kami melakukan pembalasan terhadap orang-orang yang berdosa. Dan Kami selalu berkewajiban menolong orang-orang yang beriman.'),
(3457, '030:048', '68', 30, 48, 'Allah, Dialah yang mengirim angin, lalu angin itu menggerakkan awan dan Allah membentangkannya di langit menurut yang dikehendaki-Nya, dan menjadikannya bergumpal-gumpal; lalu kamu lihat hujan keluar dari celah-celahnya, maka apabila hujan itu turun mengenai hamba-hamba-Nya yang dikehendaki-Nya, tiba-tiba mereka menjadi gembira.'),
(3458, '030:049', '68', 30, 49, 'Dan Sesungguhnya sebelum hujan diturunkan kepada mereka, mereka benar-benar telah berputus asa.'),
(3459, '030:050', '68', 30, 50, 'Maka perhatikanlah bekas-bekas rahmat Allah, bagaimana Allah menghidupkan bumi yang sudah mati. Sesungguhnya (Tuhan yang berkuasa seperti) demikian benar-benar (berkuasa) menghidupkan orang-orang yang telah mati. Dan Dia Maha Kuasa atas segala sesuatu.'),
(3460, '030:051', '68', 30, 51, 'Dan sungguh, jika Kami mengirimkan angin (kepada tumbuh-tumbuhan) lalu mereka melihat (tumbuh-tumbuhan itu) menjadi kuning (kering), benar-benar tetaplah mereka sesudah itu menjadi orang yang ingkar.'),
(3461, '030:052', '68', 30, 52, 'Maka Sesungguhnya kamu tidak akan sanggup menjadikan orang-orang yang mati itu dapat mendengar, dan menjadikan orang-orang yang tuli dapat mendengar seruan, apabila mereka itu berpaling membelakang.'),
(3462, '030:053', '68', 30, 53, 'Dan kamu sekali-kali tidak akan dapat memberi petunjuk kepada orang-orang yang buta (mata hatinya) dari kesesatannya. Dan kamu tidak dapat memperdengarkan (petunjuk Tuhan) melainkan kepada orang-orang yang beriman dengan ayat-ayat Kami, mereka itulah orang-orang yang berserah diri (kepada Kami).'),
(3463, '030:054', '68', 30, 54, 'Allah, Dialah yang menciptakan kamu dari keadaan lemah, kemudian Dia menjadikan (kamu) sesudah keadaan lemah itu menjadi kuat, kemudian Dia menjadikan (kamu) sesudah kuat itu lemah (kembali) dan beruban. Dia menciptakan apa yang dikehendaki-Nya dan Dialah Yang Maha Mengetahui lagi Maha Kuasa.'),
(3464, '030:055', '68', 30, 55, 'Dan pada hari terjadinya kiamat, bersumpahlah orang-orang yang berdosa; \"mereka tidak berdiam (dalam kubur) melainkan sesaat (saja)\". Seperti demikianlah mereka selalu dipalingkan (dari kebenaran).'),
(3465, '030:056', '68', 30, 56, 'Dan berkata orang-orang yang diberi ilmu pengetahuan dan keimanan (kepada orang-orang yang kafir): \"Sesungguhnya kamu telah berdiam (dalam kubur) menurut ketetapan Allah, sampai hari berbangkit; maka inilah hari berbangkit itu akan tetapi kamu selalu tidak meyakini(nya)\".'),
(3466, '030:057', '68', 30, 57, 'Maka pada hari itu tidak bermanfaat (lagi) bagi orang-orang yang zalim permintaan uzur mereka, dan tidak pula mereka diberi kesempatan bertaubat lagi.'),
(3467, '030:058', '68', 30, 58, 'Dan Sesungguhnya telah Kami buat dalam Al Quran ini segala macam perumpamaan untuk manusia. Dan Sesungguhnya jika kamu membawa kepada mereka suatu ayat, pastilah orang-orang yang kafir itu akan berkata: \"Kamu tidak lain hanyalah orang-orang yang membuat kepalsuan belaka\".'),
(3468, '030:059', '68', 30, 59, 'Demikianlah Allah mengunci mati hati orang-orang yang tidak (mau) memahami.'),
(3469, '030:060', '68', 30, 60, 'Dan bersabarlah kamu, sesungguhnya janji Allah adalah benar dan sekali-kali janganlah orang-orang yang tidak meyakini (kebenaran ayat-ayat Allah) itu menggelisahkan kamu.'),
(3470, '031:001', '68', 31, 1, 'Alif Laam Miim'),
(3471, '031:002', '68', 31, 2, 'Inilah ayat-ayat Al Quran yang mengandung hikmat,'),
(3472, '031:003', '68', 31, 3, 'menjadi petunjuk dan rahmat bagi orang-orang yang berbuat kebaikan,'),
(3473, '031:004', '68', 31, 4, '(yaitu) orang-orang yang mendirikan shalat, menunaikan zakat dan mereka yakin akan adanya negeri akhirat.'),
(3474, '031:005', '68', 31, 5, 'Mereka itulah orang-orang yang tetap mendapat petunjuk dari Tuhannya dan mereka itulah orang-orang yang beruntung.'),
(3475, '031:006', '68', 31, 6, 'Dan di antara manusia (ada) orang yang mempergunakan perkataan yang tidak berguna untuk menyesatkan (manusia) dari jalan Allah tanpa pengetahuan dan menjadikan jalan Allah itu olok-olokan. Mereka itu akan memperoleh azab yang menghinakan.'),
(3476, '031:007', '68', 31, 7, 'Dan apabila dibacakan kepadanya ayat-ayat Kami dia berpaling dengan menyombongkan diri seolah-olah dia belum mendengarnya, seakan-akan ada sumbat di kedua telinganya; maka beri kabar gembiralah dia dengan azab yang pedih.'),
(3477, '031:008', '68', 31, 8, 'Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal saleh, bagi mereka surga-surga yang penuh kenikmatan,'),
(3478, '031:009', '68', 31, 9, 'Kekal mereka di dalamnya; sebagai janji Allah yang benar. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(3479, '031:010', '68', 31, 10, 'Dia menciptakan langit tanpa tiang yang kamu melihatnya dan Dia meletakkan gunung-gunung (di permukaan) bumi supaya bumi itu tidak menggoyangkan kamu; dan memperkembang biakkan padanya segala macam jenis binatang. Dan Kami turunkan air hujan dari langit, lalu Kami tumbuhkan padanya segala macam tumbuh-tumbuhan yang baik.'),
(3480, '031:011', '68', 31, 11, 'Inilah ciptaan Allah, maka perlihatkanlah olehmu kepadaku apa yang telah diciptakan oleh sembahan-sembahan(mu) selain Allah. Sebenarnya orang-orang yang zalim itu berada di dalam kesesatan yang nyata.'),
(3481, '031:012', '68', 31, 12, 'Dan sesungguhnya telah Kami berikan hikmat kepada Luqman, yaitu: \"Bersyukurlah kepada Allah. Dan barangsiapa yang bersyukur (kepada Allah), maka sesungguhnya ia bersyukur untuk dirinya sendiri; dan barangsiapa yang tidak bersyukur, maka sesungguhnya Allah Maha Kaya lagi Maha Terpuji\".'),
(3482, '031:013', '68', 31, 13, 'Dan (ingatlah) ketika Luqman berkata kepada anaknya, di waktu ia memberi pelajaran kepadanya: \"Hai anakku, janganlah kamu mempersekutukan Allah, sesungguhnya mempersekutukan (Allah) adalah benar-benar kezaliman yang besar\".'),
(3483, '031:014', '68', 31, 14, 'Dan Kami perintahkan kepada manusia (berbuat baik) kepada dua orang ibu-bapaknya; ibunya telah mengandungnya dalam keadaan lemah yang bertambah-tambah, dan menyapihnya dalam dua tahun. Bersyukurlah kepada-Ku dan kepada dua orang ibu bapakmu, hanya kepada-Kulah kembalimu.'),
(3484, '031:015', '68', 31, 15, 'Dan jika keduanya memaksamu untuk mempersekutukan dengan Aku sesuatu yang tidak ada pengetahuanmu tentang itu, maka janganlah kamu mengikuti keduanya, dan pergaulilah keduanya di dunia dengan baik, dan ikutilah jalan orang yang kembali kepada-Ku, kemudian hanya kepada-Kulah kembalimu, maka Kuberitakan kepadamu apa yang telah kamu kerjakan.'),
(3485, '031:016', '68', 31, 16, '(Luqman berkata): \"Hai anakku, sesungguhnya jika ada (sesuatu perbuatan) seberat biji sawi, dan berada dalam batu atau di langit atau di dalam bumi, niscaya Allah akan mendatangkannya (membalasinya). Sesungguhnya Allah Maha Halus lagi Maha Mengetahui.'),
(3486, '031:017', '68', 31, 17, 'Hai anakku, dirikanlah shalat dan suruhlah (manusia) mengerjakan yang baik dan cegahlah (mereka) dari perbuatan yang mungkar dan bersabarlah terhadap apa yang menimpa kamu. Sesungguhnya yang demikian itu termasuk hal-hal yang diwajibkan (oleh Allah).'),
(3487, '031:018', '68', 31, 18, 'Dan janganlah kamu memalingkan mukamu dari manusia (karena sombong) dan janganlah kamu berjalan di muka bumi dengan angkuh. Sesungguhnya Allah tidak menyukai orang-orang yang sombong lagi membanggakan diri.'),
(3488, '031:019', '68', 31, 19, 'Dan sederhanalah kamu dalam berjalan dan lunakkanlah suaramu. Sesungguhnya seburuk-buruk suara ialah suara keledai.'),
(3489, '031:020', '68', 31, 20, 'Tidakkah kamu perhatikan sesungguhnya Allah telah menundukkan untuk (kepentingan)mu apa yang di langit dan apa yang di bumi dan menyempurnakan untukmu nikmat-Nya lahir dan batin. Dan di antara manusia ada yang membantah tentang (keesaan) Allah tanpa ilmu pengetahuan atau petunjuk dan tanpa Kitab yang memberi penerangan.'),
(3490, '031:021', '68', 31, 21, 'Dan apabila dikatakan kepada mereka: \"Ikutilah apa yang diturunkan Allah\". Mereka menjawab: \"(Tidak), tapi kami (hanya) mengikuti apa yang kami dapati bapak-bapak kami mengerjakannya\". Dan apakah mereka (akan mengikuti bapak-bapak mereka) walaupun syaitan itu menyeru mereka ke dalam siksa api yang menyala-nyala (neraka)?'),
(3491, '031:022', '68', 31, 22, 'Dan barangsiapa yang menyerahkan dirinya kepada Allah, sedang dia orang yang berbuat kebaikan, maka sesungguhnya ia telah berpegang kepada buhul tali yang kokoh. Dan hanya kepada Allah-lah kesudahan segala urusan.'),
(3492, '031:023', '68', 31, 23, 'Dan barangsiapa kafir maka kekafirannya itu janganlah menyedihkanmu. Hanya kepada Kami-lah mereka kembali, lalu Kami beritakan kepada mereka apa yang telah mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala isi hati.'),
(3493, '031:024', '68', 31, 24, 'Kami biarkan mereka bersenang-senang sebentar, kemudian Kami paksa mereka (masuk) ke dalam siksa yang keras.'),
(3494, '031:025', '68', 31, 25, 'Dan sesungguhnya jika kamu tanyakan kepada mereka: \"Siapakah yang menciptakan langit dan bumi?\" Tentu mereka akan menjawab: \"Allah\". Katakanlah: \"Segala puji bagi Allah\"; tetapi kebanyakan mereka tidak mengetahui.'),
(3495, '031:026', '68', 31, 26, 'Kepunyaan Allah-lah apa yang di langit dan yang di bumi. Sesungguhnya Allah Dialah Yang Maha Kaya lagi Maha Terpuji.'),
(3496, '031:027', '68', 31, 27, 'Dan seandainya pohon-pohon di bumi menjadi pena dan laut (menjadi tinta), ditambahkan kepadanya tujuh laut (lagi) sesudah (kering)nya, niscaya tidak akan habis-habisnya (dituliskan) kalimat Allah. Sesungguhnya Allah Maha Perkasa lagi Maha Bijaksana.'),
(3497, '031:028', '68', 31, 28, 'Tidaklah Allah menciptakan dan membangkitkan kamu (dari dalam kubur) itu melainkan hanyalah seperti (menciptakan dan membangkitkan) satu jiwa saja. Sesungguhnya Allah Maha Mendengar lagi Maha Melihat.'),
(3498, '031:029', '68', 31, 29, 'Tidakkah kamu memperhatikan, bahwa sesungguhnya Allah memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan Dia tundukkan matahari dan bulan masing-masing berjalan sampai kepada waktu yang ditentukan, dan sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(3499, '031:030', '68', 31, 30, 'Demikianlah, karena sesungguhnya Allah, Dialah yang hak dan sesungguhnya apa saja yang mereka seru selain dari Allah itulah yang batil; dan sesungguhnya Allah Dialah Yang Maha Tinggi lagi Maha Besar.'),
(3500, '031:031', '68', 31, 31, 'Tidakkah kamu memperhatikan bahwa sesungguhnya kapal itu berlayar di laut dengan nikmat Allah, supaya diperlihatkan-Nya kepadamu sebahagian dari tanda-tanda (kekuasaan)-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda bagi semua orang yang sangat sabar lagi banyak bersyukur.'),
(3501, '031:032', '68', 31, 32, 'Dan apabila mereka dilamun ombak yang besar seperti gunung, mereka menyeru Allah dengan memurnikan ketaatan kepada-Nya maka tatkala Allah menyelamatkan mereka sampai di daratan, lalu sebagian mereka tetap menempuh jalan yang lurus. Dan tidak ada yang mengingkari ayat-ayat Kami selain orang-orang yang tidak setia lagi ingkar.'),
(3502, '031:033', '68', 31, 33, 'Hai manusia, bertakwalah kepada Tuhanmu dan takutilah suatu hari yang (pada hari itu) seorang bapak tidak dapat menolong anaknya dan seorang anak tidak dapat (pula) menolong bapaknya sedikitpun. Sesungguhnya janji Allah adalah benar, maka janganlah sekali-kali kehidupan dunia memperdayakan kamu, dan jangan (pula) penipu (syaitan) memperdayakan kamu dalam (mentaati) Allah.'),
(3503, '031:034', '68', 31, 34, 'Sesungguhnya Allah, hanya pada sisi-Nya sajalah pengetahuan tentang Hari Kiamat; dan Dialah Yang menurunkan hujan, dan mengetahui apa yang ada dalam rahim. Dan tiada seorangpun yang dapat mengetahui (dengan pasti) apa yang akan diusahakannya besok. Dan tiada seorangpun yang dapat mengetahui di bumi mana dia akan mati. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.'),
(3504, '032:001', '68', 32, 1, 'Alif Laam Miim'),
(3505, '032:002', '68', 32, 2, 'Turunnya Al-Quran yang tidak ada keraguan di dalamnya, (adalah) dari Tuhan semesta alam.'),
(3506, '032:003', '68', 32, 3, 'Tetapi mengapa mereka (orang kafir) mengatakan: \"Dia Muhammad mengada-adakannya\". Sebenarnya Al-Quran itu adalah kebenaran dari Rabbmu, agar kamu memberi peringatan kepada kaum yang belum datang kepada mereka orang yang memberi peringatan sebelum kamu; mudah-mudahan mereka mendapat petunjuk.'),
(3507, '032:004', '68', 32, 4, 'Allah lah yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ´Arsy. Tidak ada bagi kamu selain dari pada-Nya seorang penolongpun dan tidak (pula) seorang pemberi syafa´at. Maka apakah kamu tidak memperhatikan?'),
(3508, '032:005', '68', 32, 5, 'Dia mengatur urusan dari langit ke bumi, kemudian (urusan) itu naik kepada-Nya dalam satu hari yang kadarnya adalah seribu tahun menurut perhitunganmu'),
(3509, '032:006', '68', 32, 6, 'Yang demikian itu ialah Tuhan Yang mengetahui yang ghaib dan yang nyata, Yang Maha Perkasa lagi Maha Penyayang.'),
(3510, '032:007', '68', 32, 7, 'Yang membuat segala sesuatu yang Dia ciptakan sebaik-baiknya dan Yang memulai penciptaan manusia dari tanah.'),
(3511, '032:008', '68', 32, 8, 'Kemudian Dia menjadikan keturunannya dari saripati air yang hina.'),
(3512, '032:009', '68', 32, 9, 'Kemudian Dia menyempurnakan dan meniupkan ke dalamnya roh (ciptaan)-Nya dan Dia menjadikan bagi kamu pendengaran, penglihatan dan hati; (tetapi) kamu sedikit sekali bersyukur.'),
(3513, '032:010', '68', 32, 10, 'Dan mereka berkata: \"Apakah bila kami telah lenyap (hancur) dalam tanah, kami benar-benar akan berada dalam ciptaan yang baru?\" Bahkan mereka ingkar akan menemui Tuhannya.'),
(3514, '032:011', '68', 32, 11, 'Katakanlah: \"Malaikat maut yang diserahi untuk (mencabut nyawa)mu akan mematikanmu, kemudian hanya kepada Tuhanmulah kamu akan dikembalikan\".'),
(3515, '032:012', '68', 32, 12, 'Dan, jika sekiranya kamu melihat mereka ketika orang-orang yang berdosa itu menundukkan kepalanya di hadapan Tuhannya, (mereka berkata): \"Ya Tuhan kami, kami telah melihat dan mendengar, maka kembalikanlah kami (ke dunia), kami akan mengerjakan amal saleh, sesungguhnya kami adalah orang-orang yang yakin\".'),
(3516, '032:013', '68', 32, 13, 'Dan kalau Kami menghendaki niscaya Kami akan berikan kepada tiap-tiap jiwa petunjuk, akan tetapi telah tetaplah perkataan dari pada-Ku: \"Sesungguhnya akan Aku penuhi neraka jahannam itu dengan jin dan manusia bersama-sama\".'),
(3517, '032:014', '68', 32, 14, 'Maka rasailah olehmu (siksa ini) disebabkan kamu melupakan akan pertemuan dengan harimu ini. Sesungguhnya Kami telah melupakan kamu (pula) dan rasakanlah siksa yang kekal, disebabkan apa yang selalu kamu kerjakan.'),
(3518, '032:015', '68', 32, 15, 'Sesungguhnya orang yang benar-benar percaya kepada ayat-ayat Kami adalah mereka yang apabila diperingatkan dengan ayat-ayat itu mereka segera bersujud seraya bertasbih dan memuji Rabbnya, dan lagi pula mereka tidaklah sombong.'),
(3519, '032:016', '68', 32, 16, 'Lambung mereka jauh dari tempat tidurnya dan mereka selalu berdoa kepada Rabbnya dengan penuh rasa takut dan harap, serta mereka menafkahkan apa apa rezeki yang Kami berikan.'),
(3520, '032:017', '68', 32, 17, 'Tak seorangpun mengetahui berbagai nikmat yang menanti, yang indah dipandang sebagai balasan bagi mereka, atas apa yang mereka kerjakan.'),
(3521, '032:018', '68', 32, 18, 'Apakah orang-orang beriman itu sama dengan orang-orang yang fasik? Mereka tidak sama.'),
(3522, '032:019', '68', 32, 19, 'Adapun orang-orang yang beriman dan mengerjakan amal saleh, maka bagi mereka jannah tempat kediaman, sebagai pahala terhadap apa yang mereka kerjakan.'),
(3523, '032:020', '68', 32, 20, 'Dan adapun orang-orang yang fasik (kafir) maka tempat mereka adalah jahannam. Setiap kali mereka hendak keluar daripadanya, mereka dikembalikan ke dalamnya dan dikatakan kepada mereka: \"Rasakanlah siksa neraka yang dahulu kamu mendustakannya\".'),
(3524, '032:021', '68', 32, 21, 'Dan Sesungguhnya Kami merasakan kepada mereka sebahagian azab yang dekat (di dunia) sebelum azab yang lebih besar (di akhirat), mudah-mudahan mereka kembali (ke jalan yang benar).'),
(3525, '032:022', '68', 32, 22, 'Dan siapakah yang lebih zalim daripada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, kemudian ia berpaling daripadanya? Sesungguhnya Kami akan memberikan pembalasan kepada orang-orang yang berdosa.'),
(3526, '032:023', '68', 32, 23, 'Dan Sesungguhnya Kami telah berikan kepada Musa Al-Kitab (Taurat), maka janganlah kamu (Muhammad) ragu menerima (Al-Quran itu) dan Kami jadikan Al-Kitab (Taurat) itu petunjuk bagi Bani Israil.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(3527, '032:024', '68', 32, 24, 'Dan Kami jadikan di antara mereka itu pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami ketika mereka sabar. Dan adalah mereka meyakini ayat-ayat Kami.'),
(3528, '032:025', '68', 32, 25, 'Sesungguhnya Tuhanmu Dialah yang memberikan keputusan di antara mereka pada hari kiamat tentang apa yang selalu mereka perselisihkan padanya.'),
(3529, '032:026', '68', 32, 26, 'Dan apakah tidak menjadi petunjuk bagi mereka, berapa banyak umat sebelum mereka yang telah Kami binasakan sedangkan mereka sendiri berjalan di tempat-tempat kediaman mereka itu. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah). Maka apakah mereka tidak mendengarkan?'),
(3530, '032:027', '68', 32, 27, 'Dan apakah mereka tidak memperhatikan, bahwasanya Kami menghalau (awan yang mengandung) air ke bumi yang tandus, lalu Kami tumbuhkan dengan air hujan itu tanaman yang daripadanya makan hewan ternak mereka dan mereka sendiri. Maka apakah mereka tidak memperhatikan?'),
(3531, '032:028', '68', 32, 28, 'Dan mereka bertanya: \"Bilakah kemenangan itu (datang) jika kamu memang orang-orang yang benar?\"'),
(3532, '032:029', '68', 32, 29, 'Katakanlah: \"Pada hari kemenangan itu tidak berguna bagi orang-orang kafir, iman mereka dan tidak pula mereka diberi tangguh\".'),
(3533, '032:030', '68', 32, 30, 'Maka berpalinglah kamu dari mereka dan tunggulah, sesungguhnya mereka (juga) menunggu.'),
(3534, '033:001', '68', 33, 1, 'Hai Nabi, bertakwalah kepada Allah dan janganlah kamu menuruti (keinginan) orang-orang kafir dan orang-orang munafik. Sesungguhnya Allah adalah Maha Mengetahui lagi Maha Bijaksana,'),
(3535, '033:002', '68', 33, 2, 'dan ikutilah apa yang diwahyukan Tuhan kepadamu. Sesungguhnya Allah adalah Maha Mengetahui apa yang kamu kerjakan.'),
(3536, '033:003', '68', 33, 3, 'dan bertawakkallah kepada Allah. Dan cukuplah Allah sebagai Pemelihara.'),
(3537, '033:004', '68', 33, 4, 'Allah sekali-kali tidak menjadikan bagi seseorang dua buah hati dalam rongganya; dan Dia tidak menjadikan istri-istrimu yang kamu zhihar itu sebagai ibumu, dan Dia tidak menjadikan anak-anak angkatmu sebagai anak kandungmu (sendiri). Yang demikian itu hanyalah perkataanmu di mulutmu saja. Dan Allah mengatakan yang sebenarnya dan Dia menunjukkan jalan (yang benar).'),
(3538, '033:005', '68', 33, 5, 'Panggilah mereka (anak-anak angkat itu) dengan (memakai) nama bapak-bapak mereka; itulah yang lebih adil pada sisi Allah, dan jika kamu tidak mengetahui bapak-bapak mereka, maka (panggilah mereka sebagai) saudara-saudaramu seagama dan maula-maulamu. Dan tidak ada dosa atasmu terhadap apa yang kamu khilaf padanya, tetapi (yang ada dosanya) apa yang disengaja oleh hatimu. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(3539, '033:006', '68', 33, 6, 'Nabi itu (hendaknya) lebih utama bagi orang-orang mukmin dari diri mereka sendiri dan isteri-isterinya adalah ibu-ibu mereka. Dan orang-orang yang mempunyai hubungan darah satu sama lain lebih berhak (waris-mewarisi) di dalam Kitab Allah daripada orang-orang mukmim dan orang-orang Muhajirin, kecuali kalau kamu berbuat baik kepada saudara-saudaramu (seagama). Adalah yang demikian itu telah tertulis di dalam Kitab (Allah).'),
(3540, '033:007', '68', 33, 7, 'Dan (ingatlah) ketika Kami mengambil perjanjian dari nabi-nabi dan dari kamu (sendiri) dari Nuh, Ibrahim, Musa dan Isa putra Maryam, dan Kami telah mengambil dari mereka perjanjian yang teguh.'),
(3541, '033:008', '68', 33, 8, 'agar Dia menanyakan kepada orang-orang yang benar tentang kebenaran mereka dan Dia menyediakan bagi orang-orang kafir siksa yang pedih.'),
(3542, '033:009', '68', 33, 9, 'Hai orang-orang yang beriman, ingatlah akan nikmat Allah (yang telah dikurniakan) kepadamu ketika datang kepadamu tentara-tentara, lalu Kami kirimkan kepada mereka angin topan dan tentara yang tidak dapat kamu melihatnya. Dan adalah Allah Maha Melihat akan apa yang kamu kerjakan.'),
(3543, '033:010', '68', 33, 10, '(Yaitu) ketika mereka datang kepadamu dari atas dan dari bawahmu, dan ketika tidak tetap lagi penglihatan(mu) dan hatimu naik menyesak sampai ke tenggorokan dan kamu menyangka terhadap Allah dengan bermacam-macam purbasangka.'),
(3544, '033:011', '68', 33, 11, 'Disitulah diuji orang-orang mukmin dan digoncangkan (hatinya) dengan goncangan yang sangat.'),
(3545, '033:012', '68', 33, 12, 'Dan (ingatlah) ketika orang-orang munafik dan orang-orang yang berpenyakit dalam hatinya berkata: \"Allah dan Rasul-Nya tidak menjanjikan kepada kami melainkan tipu daya\".'),
(3546, '033:013', '68', 33, 13, 'Dan (ingatlah) ketika segolongan di antara mreka berkata: \"Hai penduduk Yatsrib (Madinah), tidak ada tempat bagimu, maka kembalilah kamu\". Dan sebahagian dari mereka minta izin kepada Nabi (untuk kembali pulang) dengan berkata: \"Sesungguhnya rumah-rumah kami terbuka (tidak ada penjaga)\". Dan rumah-rumah itu sekali-kali tidak terbuka, mereka tidak lain hanya hendak lari.'),
(3547, '033:014', '68', 33, 14, 'Kalau (Yatsrib) diserang dari segala penjuru, kemudian diminta kepada mereka supaya murtad, niscaya mereka mengerjakannya; dan mereka tiada akan bertangguh untuk murtad itu melainkan dalam waktu yang singkat.'),
(3548, '033:015', '68', 33, 15, 'Dan sesungguhnya mereka sebelum itu telah berjanji kepada Allah: \"Mereka tidak akan berbalik ke belakang (mundur)\". Dan adalah perjanjian dengan Allah akan diminta pertanggungan jawabnya.'),
(3549, '033:016', '68', 33, 16, 'Katakanlah: \"Lari itu sekali-kali tidaklah berguna bagimu, jika kamu melarikan diri dari kematian atau pembunuhan, dan jika (kamu terhindar dari kematian) kamu tidak juga akan mengecap kesenangan kecuali sebentar saja\".'),
(3550, '033:017', '68', 33, 17, 'Katakanlah: \"Siapakah yang dapat melindungi kamu dari (takdir) Allah jika Dia menghendaki bencana atasmu atau menghendaki rahmat untuk dirimu?\" Dan orang-orang munafik itu tidak memperoleh bagi mereka pelindung dan penolong selain Allah.'),
(3551, '033:018', '68', 33, 18, 'Sesungguhnya Allah mengetahui orang-orang yang menghalang-halangi di antara kamu dan orang-orang yang berkata kepada saudara-saudaranya: \"Marilah kepada kami\". Dan mereka tidak mendatangi peperangan melainkan sebentar.'),
(3552, '033:019', '68', 33, 19, 'Mereka bakhil terhadapmu, apabila datang ketakutan (bahaya), kamu lihat mereka itu memandang kepadamu dengan mata yang terbalik-balik seperti orang yang pingsan karena akan mati, dan apabila ketakutan telah hilang, mereka mencaci kamu dengan lidah yang tajam, sedang mereka bakhil untuk berbuat kebaikan. Mereka itu tidak beriman, maka Allah menghapuskan (pahala) amalnya. Dan yang demikian itu adalah mudah bagi Allah.'),
(3553, '033:020', '68', 33, 20, 'Mereka mengira (bahwa) golongan-golongan yang bersekutu itu belum pergi; dan jika golongan-golongan yang bersekutu itu datang kembali, niscaya mereka ingin berada di dusun-dusun bersama-sama orang Arab Badwi, sambil menanya-nanyakan tentang berita-beritamu. Dan sekiranya mereka berada bersama kamu, mereka tidak akan berperang, melainkan sebentar saja.'),
(3554, '033:021', '68', 33, 21, 'Sesungguhnya telah ada pada (diri) Rasulullah itu suri teladan yang baik bagimu (yaitu) bagi orang yang mengharap (rahmat) Allah dan (kedatangan) hari kiamat dan dia banyak menyebut Allah.'),
(3555, '033:022', '68', 33, 22, 'Dan tatkala orang-orang mukmin melihat golongan-golongan yang bersekutu itu, mereka berkata: \"Inilah yang dijanjikan Allah dan Rasul-Nya kepada kita\". Dan benarlah Allah dan Rasul-Nya. Dan yang demikian itu tidaklah menambah kepada mereka kecuali iman dan ketundukan.'),
(3556, '033:023', '68', 33, 23, 'Di antara orang-orang mukmin itu ada orang-orang yang menepati apa yang telah mereka janjikan kepada Allah; maka di antara mereka ada yang gugur. Dan di antara mereka ada (pula) yang menunggu-nunggu dan mereka tidak merubah (janjinya),'),
(3557, '033:024', '68', 33, 24, 'supaya Allah memberikan balasan kepada orang-orang yang benar itu karena kebenarannya, dan menyiksa orang munafik jika dikehendaki-Nya, atau menerima taubat mereka. Sesungguhnya Allah adalah Maha Pengampun lagi Maha Penyayang.'),
(3558, '033:025', '68', 33, 25, 'Dan Allah menghalau orang-orang yang kafir itu yang keadaan mereka penuh kejengkelan, (lagi) mereka tidak memperoleh keuntungan apapun. Dan Allah menghindarkan orang-orang mukmin dari peperangan. Dan adalah Allah Maha Kuat lagi Maha Perkasa.'),
(3559, '033:026', '68', 33, 26, 'Dan Dia menurunkan orang-orang Ahli Kitab (Bani Quraizhah) yang membantu golongan-golongan yang bersekutu dari benteng-benteng mereka, dan Dia memesukkan rasa takut ke dalam hati mereka. Sebahagian mereka kamu bunuh dan sebahagian yang lain kamu tawan.'),
(3560, '033:027', '68', 33, 27, 'Dan Dia mewariskan kepada kamu tanah-tanah, rumah-rumah dan harta benda mereka, dan (begitu pula) tanah yang belum kamu injak. Dan adalah Allah Maha Kuasa terhadap segala sesuatu.'),
(3561, '033:028', '68', 33, 28, 'Hai Nabi, katakanlah kepada isteri-isterimu: \"Jika kamu sekalian mengingini kehidupan dunia dan perhiasannya, maka marilah supaya kuberikan kepadamu mut´ah dan aku ceraikan kamu dengan cara yang baik.'),
(3562, '033:029', '68', 33, 29, 'Dan jika kamu sekalian menghendaki (keridhaan) Allah dan Rasulnya-Nya serta (kesenangan) di negeri akhirat, maka sesungguhnya Allah menyediakan bagi siapa yang berbuat baik diantaramu pahala yang besar.'),
(3563, '033:030', '68', 33, 30, 'Hai isteri-isteri Nabi, siapa-siapa di antaramu yang mengerjakan perbuatan keji yang nyata, niscaya akan di lipat gandakan siksaan kepada mereka dua kali lipat. Dan adalah yang demikian itu mudah bagi Allah.'),
(3564, '033:031', '68', 33, 31, 'Dan barang siapa diantara kamu sekalian (isteri-isteri nabi) tetap taat kepada Allah dan Rasul-Nya dan mengerjakan amal yang saleh, niscata Kami memberikan kepadanya pahala dua kali lipat dan Kami sediakan baginya rezeki yang mulia.'),
(3565, '033:032', '68', 33, 32, 'Hai isteri-isteri Nabi, kamu sekalian tidaklah seperti wanita yang lain, jika kamu bertakwa. Maka janganlah kamu tunduk dalam berbicara sehingga berkeinginanlah orang yang ada penyakit dalam hatinya dan ucapkanlah perkataan yang baik,'),
(3566, '033:033', '68', 33, 33, 'dan hendaklah kamu tetap di rumahmu dan janganlah kamu berhias dan bertingkah laku seperti orang-orang Jahiliyah yang dahulu dan dirikanlah shalat, tunaikanlah zakat dan taatilah Allah dan Rasul-Nya. Sesungguhnya Allah bermaksud hendak menghilangkan dosa dari kamu, hai ahlul bait dan membersihkan kamu sebersih-bersihnya.'),
(3567, '033:034', '68', 33, 34, 'Dan ingatlah apa yang dibacakan di rumahmu dari ayat-ayat Allah dan hikmah (sunnah nabimu). Sesungguhnya Allah adalah Maha Lembut lagi Maha Mengetahui.'),
(3568, '033:035', '68', 33, 35, 'Sesungguhnya laki-laki dan perempuan yang muslim, laki-laki dan perempuan yang mukmin, laki-laki dan perempuan yang tetap dalam ketaatannya, laki-laki dan perempuan yang benar, laki-laki dan perempuan yang sabar, laki-laki dan perempuan yang khusyu´, laki-laki dan perempuan yang bersedekah, laki-laki dan perempuan yang berpuasa, laki-laki dan perempuan yang memelihara kehormatannya, laki-laki dan perempuan yang banyak menyebut (nama) Allah, Allah telah menyediakan untuk mereka ampunan dan pahala yang besar.'),
(3569, '033:036', '68', 33, 36, 'Dan tidaklah patut bagi laki-laki yang mukmin dan tidak (pula) bagi perempuan yang mukmin, apabila Allah dan Rasul-Nya telah menetapkan suatu ketetapan, akan ada bagi mereka pilihan (yang lain) tentang urusan mereka. Dan barangsiapa mendurhakai Allah dan Rasul-Nya maka sungguhlah dia telah sesat, sesat yang nyata.'),
(3570, '033:037', '68', 33, 37, 'Dan (ingatlah), ketika kamu berkata kepada orang yang Allah telah melimpahkan nikmat kepadanya dan kamu (juga) telah memberi nikmat kepadanya: \"Tahanlah terus isterimu dan bertakwalah kepada Allah\", sedang kamu menyembunyikan di dalam hatimu apa yang Allah akan menyatakannya, dan kamu takut kepada manusia, sedang Allah-lah yang lebih berhak untuk kamu takuti. Maka tatkala Zaid telah mengakhiri keperluan terhadap istrinya (menceraikannya), Kami kawinkan kamu dengan dia supaya tidak ada keberatan bagi orang mukmin untuk (mengawini) isteri-isteri anak-anak angkat mereka, apabila anak-anak angkat itu telah menyelesaikan keperluannya daripada isterinya. Dan adalah ketetapan Allah itu pasti terjadi.'),
(3571, '033:038', '68', 33, 38, 'Tidak ada suatu keberatanpun atas Nabi tentang apa yang telah ditetapkan Allah baginya. (Allah telah menetapkan yang demikian) sebagai sunnah-Nya pada nabi-nabi yang telah berlalu dahulu. Dan adalah ketetapan Allah itu suatu ketetapan yang pasti berlaku,'),
(3572, '033:039', '68', 33, 39, '(yaitu) orang-orang yang menyapaikan risalah-risalah Allah, mereka takut kepada-Nya dan mereka tiada merasa takut kepada seorang(pun) selain kepada Allah. Dan cukuplah Allah sebagai Pembuat Perhitungan.'),
(3573, '033:040', '68', 33, 40, 'Muhammad itu sekali-kali bukanlah bapak dari seorang laki-laki di antara kamu, tetapi dia adalah Rasulullah dan penutup nabi-nabi. Dan adalah Allah Maha Mengetahui segala sesuatu.'),
(3574, '033:041', '68', 33, 41, 'Hai orang-orang yang beriman, berzdikirlah (dengan menyebut nama) Allah, zikir yang sebanyak-banyaknya.'),
(3575, '033:042', '68', 33, 42, 'Dan bertasbihlah kepada-Nya diwaktu pagi dan petang.'),
(3576, '033:043', '68', 33, 43, 'Dialah yang memberi rahmat kepadamu dan malaikat-Nya (memohonkan ampunan untukmu), supaya Dia mengeluarkan kamu dari kegelapan kepada cahaya (yang terang). Dan adalah Dia Maha Penyayang kepada orang-orang yang beriman.'),
(3577, '033:044', '68', 33, 44, 'Salam penghormatan kepada mereka (orang-orang mukmin itu) pada hari mereka menemui-Nya ialah: Salam; dan Dia menyediakan pahala yang mulia bagi mereka.'),
(3578, '033:045', '68', 33, 45, 'Hai Nabi, sesungguhnya Kami mengutusmu untuk jadi saksi, dan pembawa kabar gemgira dan pemberi peringatan,'),
(3579, '033:046', '68', 33, 46, 'dan untuk jadi penyeru kepada Agama Allah dengan izin-Nya dan untuk jadi cahaya yang menerangi.'),
(3580, '033:047', '68', 33, 47, 'Dan sampaikanlah berita gembira kepada orang-orang mukmin bahwa sesungguhnya bagi mereka karunia yang besar dari Allah.'),
(3581, '033:048', '68', 33, 48, 'Dan janganlah kamu menuruti orang-orang yang kafir dan orang-orang munafik itu, janganlah kamu hiraukan gangguan mereka dan bertawakkallah kepada Allah. Dan cukuplah Allah sebagai Pelindung.'),
(3582, '033:049', '68', 33, 49, 'Hai orang-orang yang beriman, apabila kamu menikahi perempuan-perempuan yang beriman, kemudian kamu ceraikan mereka sebelum kamu mencampurinya maka sekali-sekali tidak wajib atas mereka ´iddah bagimu yang kamu minta menyempurnakannya. Maka berilah mereka mut´ah dan lepaskanlah mereka itu dengan cara yang sebaik-baiknya.'),
(3583, '033:050', '68', 33, 50, 'Hai Nabi, sesungguhnya Kami telah menghalalkan bagimu isteri-isterimu yang telah kamu berikan mas kawinnya dan hamba sahaya yang kamu miliki yang termasuk apa yang kamu peroleh dalam peperangan yang dikaruniakan Allah untukmu, dan (demikian pula) anak-anak perempuan dari saudara laki-laki bapakmu, anak-anak perempuan dari saudara perempuan bapakmu, anak-anak perempuan dari saudara laki-laki ibumu dan anak-anak perempuan dari saudara perempuan ibumu yang turut hijrah bersama kamu dan perempuan mukmin yang menyerahkan dirinya kepada Nabi kalau Nabi mau mengawininya, sebagai pengkhususan bagimu, bukan untuk semua orang mukmin. Sesungguhnya Kami telah mengetahui apa yang Kami wajibkan kepada mereka tentang isteri-isteri mereka dan hamba sahaya yang mereka miliki supaya tidak menjadi kesempitan bagimu. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(3584, '033:051', '68', 33, 51, 'Kamu boleh menangguhkan menggauli siapa yang kamu kehendaki di antara mereka (isteri-isterimu) dan (boleh pula) menggauli siapa yang kamu kehendaki. Dan siapa-siapa yang kamu ingini untuk menggaulinya kembali dari perempuan yang telah kamu cerai, maka tidak ada dosa bagimu. Yang demikian itu adalah lebih dekat untuk ketenangan hati mereka, dan mereka tidak merasa sedih, dan semuanya rela dengan apa yang telah kamu berikan kepada mereka. Dan Allah mengetahui apa yang (tersimpan) dalam hatimu. Dan adalah Allah Maha Mengetahui lagi Maha Penyantun.'),
(3585, '033:052', '68', 33, 52, 'Tidak halal bagimu mengawini perempuan-perempuan sesudah itu dan tidak boleh (pula) mengganti mereka dengan isteri-isteri (yang lain), meskipun kecantikannya menarik hatimu kecuali perempuan-perempuan (hamba sahaya) yang kamu miliki. Dan adalah Allah Maha Mengawasi segala sesuatu.'),
(3586, '033:053', '68', 33, 53, 'Hai orang-orang yang beriman, janganlah kamu memasuki rumah-rumah Nabi kecuali bila kamu diizinkan untuk makan dengan tidak menunggu-nunggu waktu masak (makanannya), tetapi jika kamu diundang maka masuklah dan bila kamu selesai makan, keluarlah kamu tanpa asyik memperpanjang percakapan. Sesungguhnya yang demikian itu akan mengganggu Nabi lalu Nabi malu kepadamu (untuk menyuruh kamu keluar), dan Allah tidak malu (menerangkan) yang benar. Apabila kamu meminta sesuatu (keperluan) kepada mereka (isteri-isteri Nabi), maka mintalah dari belakang tabir. Cara yang demikian itu lebih suci bagi hatimu dan hati mereka. Dan tidak boleh kamu menyakiti (hati) Rasulullah dan tidak (pula) mengawini isteri-isterinya selama-lamanya sesudah ia wafat. Sesungguhnya perbuatan itu adalah amat besar (dosanya) di sisi Allah.'),
(3587, '033:054', '68', 33, 54, 'Jika kamu melahirkan sesuatu atau menyembunyikannya, maka sesungguhnya Allah adalah Maha Mengetahui segala sesuatu.'),
(3588, '033:055', '68', 33, 55, 'Tidak ada dosa atas isteri-isteri Nabi (untuk berjumpa tanpa tabir) dengan bapak-bapak mereka, anak-anak laki-laki mereka, saudara laki-laki mereka, anak laki-laki dari saudara laki-laki mereka, anak laki-laki dari saudara mereka yang perempuan yang beriman dan hamba sahaya yang mereka miliki, dan bertakwalah kamu (hai isteri-isteri Nabi) kepada Allah. Sesungguhnya Allah Maha Menyaksikan segala sesuatu.'),
(3589, '033:056', '68', 33, 56, 'Sesungguhnya Allah dan malaikat-malaikat-Nya bershalawat untuk Nabi. Hai orang-orang yang beriman, bershalawatlah kamu untuk Nabi dan ucapkanlah salam penghormatan kepadanya.'),
(3590, '033:057', '68', 33, 57, 'Sesungguhnya orang-orang yang menyakiti Allah dan Rasul-Nya. Allah akan melaknatinya di dunia dan di akhirat, dan menyediakan baginya siksa yang menghinakan.'),
(3591, '033:058', '68', 33, 58, 'Dan orang-orang yang menyakiti orang-orang yang mukmin dan mukminat tanpa kesalahan yang mereka perbuat, maka sesungguhnya mereka telah memikul kebohongan dan dosa yang nyata.'),
(3592, '033:059', '68', 33, 59, 'Hai Nabi, katakanlah kepada isteri-isterimu, anak-anak perempuanmu dan isteri-isteri orang mukmin: \"Hendaklah mereka mengulurkan jilbabnya ke seluruh tubuh mereka\". Yang demikian itu supaya mereka lebih mudah untuk dikenal, karena itu mereka tidak di ganggu. Dan Allah adalah Maha Pengampun lagi Maha Penyayang.'),
(3593, '033:060', '68', 33, 60, 'Sesungguhnya jika tidak berhenti orang-orang munafik, orang-orang yang berpenyakit dalam hatinya dan orang-orang yang menyebarkan kabar bohong di Madinah (dari menyakitimu), niscaya Kami perintahkan kamu (untuk memerangi) mereka, kemudian mereka tidak menjadi tetanggamu (di Madinah) melainkan dalam waktu yang sebentar,'),
(3594, '033:061', '68', 33, 61, 'dalam keadaan terlaknat. Di mana saja mereka dijumpai, mereka ditangkap dan dibunuh dengan sehebat-hebatnya.'),
(3595, '033:062', '68', 33, 62, 'Sebagai sunnah Allah yang berlaku atas orang-orang yang telah terdahulu sebelum(mu), dan kamu sekali-kali tiada akan mendapati peubahan pada sunnah Allah.'),
(3596, '033:063', '68', 33, 63, 'Manusia bertanya kepadamu tentang hari berbangkit. Katakanlah: \"Sesungguhnya pengetahuan tentang hari berbangkit itu hanya di sisi Allah\". Dan tahukah kamu (hai Muhammad), boleh jadi hari berbangkit itu sudah dekat waktunya.'),
(3597, '033:064', '68', 33, 64, 'Sesungguhnya Allah melaknati orang-orang kafir dan menyediakan bagi mereka api yang menyala-nyala (neraka),'),
(3598, '033:065', '68', 33, 65, 'mereka kekal di dalamnya selama-lamanya; mereka tidak memperoleh seorang pelindungpun dan tidak (pula) seorang penolong.'),
(3599, '033:066', '68', 33, 66, 'Pada hari ketika muka mereka dibolak-balikan dalam neraka, mereka berkata: \"Alangkah baiknya, andaikata kami taat kepada Allah dan taat (pula) kepada Rasul\".'),
(3600, '033:067', '68', 33, 67, 'Dan mereka berkata: \"Ya Tuhan kami, sesungguhnya kami telah mentaati pemimpin-pemimpin dan pembesar-pembesar kami, lalu mereka menyesatkan kami dari jalan (yang benar).'),
(3601, '033:068', '68', 33, 68, 'Ya Tuhan kami, timpakanlah kepada mereka azab dua kali lipat dan kutuklah mereka dengan kutukan yang besar\".'),
(3602, '033:069', '68', 33, 69, 'Hai orang-orang yang beriman, janganlah kamu menjadi seperti orang-orang yang menyakiti Musa; maka Allah membersihkannya dari tuduhan-tuduhan yang mereka katakan. Dan adalah dia seorang yang mempunyai kedudukan terhormat di sisi Allah.'),
(3603, '033:070', '68', 33, 70, 'Hai orang-orang yang beriman, bertakwalah kamu kepada Allah dan katakanlah perkataan yang benar,'),
(3604, '033:071', '68', 33, 71, 'niscaya Allah memperbaiki bagimu amalan-amalanmu dan mengampuni bagimu dosa-dosamu. Dan barangsiapa mentaati Allah dan Rasul-Nya, maka sesungguhnya ia telah mendapat kemenangan yang besar.'),
(3605, '033:072', '68', 33, 72, 'Sesungguhnya Kami telah mengemukakan amanat kepada langit, bumi dan gunung-gunung, maka semuanya enggan untuk memikul amanat itu dan mereka khawatir akan mengkhianatinya, dan dipikullah amanat itu oleh manusia. Sesungguhnya manusia itu amat zalim dan amat bodoh,'),
(3606, '033:073', '68', 33, 73, 'sehingga Allah mengazab orang-orang munafik laki-laki dan perempuan dan orang-orang musyrikin laki-laki dan perempuan; dan sehingga Allah menerima taubat orang-orang mukmin laki-laki dan perempuan. Dan adalah Allah Maha Pengampun lagi Maha Penyayang.'),
(3607, '034:001', '68', 34, 1, 'Segala puji bagi Allah yang memiliki apa yang di langit dan apa yang di bumi dan bagi-Nya (pula) segala puji di akhirat. Dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.'),
(3608, '034:002', '68', 34, 2, 'Dia mengetahui apa yang masuk ke dalam bumi, apa yang ke luar daripadanya, apa yang turun dari langit dan apa yang naik kepadanya. Dan Dialah Yang Maha Penyayang lagi Maha Pengampun.'),
(3609, '034:003', '68', 34, 3, 'Dan orang-orang yang kafir berkata: \"Hari berbangkit itu tidak akan datang kepada kami\". Katakanlah: \"Pasti datang, demi Tuhanku Yang Mengetahui yang ghaib, sesungguhnya kiamat itu pasti akan datang kepadamu. Tidak ada tersembunyi daripada-Nya sebesar zarrahpun yang ada di langit dan yang ada di bumi dan tidak ada (pula) yang lebih kecil dari itu dan yang lebih besar, melainkan tersebut dalam Kitab yang nyata (Lauh Mahfuzh)\",'),
(3610, '034:004', '68', 34, 4, 'supaya Allah memberi balasan kepada orang-orang yang beriman dan mengerjakan amal yang saleh. Mereka itu adalah orang-orang yang baginya ampunan dan rezeki yang mulia.'),
(3611, '034:005', '68', 34, 5, 'Dan orang-orang yang berusaha untuk (menentang) ayat-ayat Kami dengan anggapan mereka dapat melemahkan (menggagalkan azab Kami), mereka itu memperoleh azab, yaitu (jenis) azab yang pedih.'),
(3612, '034:006', '68', 34, 6, 'Dan orang-orang yang diberi ilmu (Ahli Kitab) berpendapat bahwa wahyu yang diturunkan kepadamu dari Tuhanmu itulah yang benar dan menunjuki (manusia) kepada jalan Tuhan Yang Maha Perkasa lagi Maha Terpuji.'),
(3613, '034:007', '68', 34, 7, 'Dan orang-orang kafir berkata (kepada teman-temannya). \"Maukah kamu kami tunjukkan kepadamu seorang laki-laki yang memberitakan kepadamu bahwa apabila badanmu telah hancur sehancur-hancurnya, sesungguhnya kamu benar-benar (akan dibangkitkan kembali) dalam ciptaan yang baru?'),
(3614, '034:008', '68', 34, 8, 'Apakah dia mengada-adakan kebohongan terhadap Allah ataukah ada padanya penyakit gila?\" (Tidak), tetapi orang-orang yang tidak beriman kepada negeri akhirat berada dalam siksaan dan kesesatan yang jauh.'),
(3615, '034:009', '68', 34, 9, 'Maka apakah mereka tidak melihat langit dan bumi yang ada di hadapan dan di belakang mereka? Jika Kami menghendaki, niscaya Kami benamkan mereka di bumi atau Kami jatuhkan kepada mereka gumpalan dari langit. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda (kekuasaan Tuhan) bagi setiap hamba yang kembali (kepada-Nya).'),
(3616, '034:010', '68', 34, 10, 'Dan sesungguhnya telah Kami berikan kepada Daud kurnia dari Kami. (Kami berfirman): \"Hai gunung-gunung dan burung-burung, bertasbihlah berulang-ulang bersama Daud\", dan Kami telah melunakkan besi untuknya,'),
(3617, '034:011', '68', 34, 11, '(yaitu) buatlah baju besi yang besar-besar dan ukurlah anyamannya; dan kerjakanlah amalan yang saleh. Sesungguhnya Aku melihat apa yang kamu kerjakan.'),
(3618, '034:012', '68', 34, 12, 'Dan Kami (tundukkan) angin bagi Sulaiman, yang perjalanannya di waktu pagi sama dengan perjalanan sebulan dan perjalanannya di waktu sore sama dengan perjalanan sebulan (pula) dan Kami alirkan cairan tembaga baginya. Dan sebahagian dari jin ada yang bekerja di hadapannya (di bawah kekuasaannya) dengan izin Tuhannya. Dan siapa yang menyimpang di antara mereka dari perintah Kami, Kami rasakan kepadanya azab neraka yang apinya menyala-nyala.'),
(3619, '034:013', '68', 34, 13, 'Para jin itu membuat untuk Sulaiman apa yang dikehendakinya dari gedung-gedung yang tinggi dan patung-patung dan piring-piring yang (besarnya) seperti kolam dan periuk yang tetap (berada di atas tungku). Bekerjalah hai keluarga Daud untuk bersyukur (kepada Allah). Dan sedikit sekali dari hamba-hamba-Ku yang berterima kasih.'),
(3620, '034:014', '68', 34, 14, 'Maka tatkala Kami telah menetapkan kematian Sulaiman, tidak ada yang menunjukkan kepada mereka kematiannya itu kecuali rayap yang memakan tongkatnya. Maka tatkala ia telah tersungkur, tahulah jin itu bahwa kalau sekiranya mereka mengetahui yang ghaib tentulah mereka tidak akan tetap dalam siksa yang menghinakan.'),
(3621, '034:015', '68', 34, 15, 'Sesungguhnya bagi kaum Saba´ ada tanda (kekuasaan Tuhan) di tempat kediaman mereka yaitu dua buah kebun di sebelah kanan dan di sebelah kiri. (kepada mereka dikatakan): \"Makanlah olehmu dari rezeki yang (dianugerahkan) Tuhanmu dan bersyukurlah kamu kepada-Nya. (Negerimu) adalah negeri yang baik dan (Tuhanmu) adalah Tuhan Yang Maha Pengampun\".'),
(3622, '034:016', '68', 34, 16, 'Tetapi mereka berpaling, maka Kami datangkan kepada mereka banjir yang besar dan Kami ganti kedua kebun mereka dengan dua kebun yang ditumbuhi (pohon-pohon) yang berbuah pahit, pohon Atsl dan sedikit dari pohon Sidr.'),
(3623, '034:017', '68', 34, 17, 'Demikianlah Kami memberi balasan kepada mereka karena kekafiran mereka. Dan Kami tidak menjatuhkan azab (yang demikian itu), melainkan hanya kepada orang-orang yang sangat kafir.'),
(3624, '034:018', '68', 34, 18, 'Dan Kami jadikan antara mereka dan antara negeri-negeri yang Kami limpahkan berkat kepadanya, beberapa negeri yang berdekatan dan Kami tetapkan antara negeri-negeri itu (jarak-jarak) perjalanan. Berjalanlah kamu di kota-kota itu pada malam hari dan siang hari dengan dengan aman.'),
(3625, '034:019', '68', 34, 19, 'Maka mereka berkata: \"Ya Tuhan kami jauhkanlah jarak perjalanan kami\", dan mereka menganiaya diri mereka sendiri; maka Kami jadikan mereka buah mulut dan Kami hancurkan mereka sehancur-hancurnya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda kekuasaan Allah bagi setiap orang yang sabar lagi bersyukur.'),
(3626, '034:020', '68', 34, 20, 'Dan sesungguhnya iblis telah dapat membuktikan kebenaran sangkaannya terhadap mereka lalu mereka mengikutinya, kecuali sebahagian orang-orang yang beriman.'),
(3627, '034:021', '68', 34, 21, 'Dan tidak adalah kekuasaan iblis terhadap mereka, melainkan hanyalah agar Kami dapat membedakan siapa yang beriman kepada adanya kehidupan akhirat dari siapa yang ragu-ragu tentang itu. Dan Tuhanmu Maha Memelihara segala sesuatu.'),
(3628, '034:022', '68', 34, 22, 'Katakanlah: \"Serulah mereka yang kamu anggap (sebagai tuhan) selain Allah, mereka tidak memiliki (kekuasaan) seberat zarrahpun di langit dan di bumi, dan mereka tidak mempunyai suatu sahampun dalam (penciptaan) langit dan bumi dan sekali-kali tidak ada di antara mereka yang menjadi pembantu bagi-Nya.'),
(3629, '034:023', '68', 34, 23, 'Dan tiadalah berguna syafa´at di sisi Allah melainkan bagi orang yang telah diizinkan-Nya memperoleh syafa´at itu, sehingga apabila telah dihilangkan ketakutan dari hati mereka, mereka berkata \"Apakah yang telah difirmankan oleh Tuhan-mu?\" Mereka menjawab: (Perkataan) yang benar\", dan Dialah Yang Maha Tinggi lagi Maha Besar.'),
(3630, '034:024', '68', 34, 24, 'Katakanlah: \"Siapakan yang memberi rezeki kepadamu dari langit dan dari bumi?\" Katakanlah: \"Allah\", dan sesungguhnya kami atau kamu (orang-orang musyrik), pasti berada dalam kebenaran atau dalam kesesatan yang nyata.'),
(3631, '034:025', '68', 34, 25, 'Katakanlah: \"Kamu tidak akan ditanya (bertanggung jawab) tentang dosa yang kami perbuat dan kami tidak akan ditanya (pula) tentang apa yang kamu perbuat\".'),
(3632, '034:026', '68', 34, 26, 'Katakanlah: \"Tuhan kita akan mengumpulkan kita semua, kemudian Dia memberi keputusan antara kita dengan benar. Dan Dialah Maha Pemberi keputusan lagi Maha Mengetahui\".'),
(3633, '034:027', '68', 34, 27, 'Katakanlah: \"Perlihatkanlah kepadaku sembah-sembahan yang kamu hubungkan dengan Dia sebagai sekutu-sekutu-Nya, sekali-kali tidak mungkin! Sebenarnya Dialah Allah Yang Maha Perkasa lagi Maha Bijaksana.'),
(3634, '034:028', '68', 34, 28, 'Dan Kami tidak mengutus kamu, melainkan kepada umat manusia seluruhnya sebagai pembawa berita gembira dan sebagai pemberi peringatan, tetapi kebanyakan manusia tiada mengetahui.'),
(3635, '034:029', '68', 34, 29, 'Dan mereka berkata: \"Kapankah (datangnya) janji ini, jika kamu adalah orang-orang yang benar?\".'),
(3636, '034:030', '68', 34, 30, 'Katakanlah: \"Bagimu ada hari yang telah dijanjikan (hari kiamat) yang tiada dapat kamu minta mundur daripadanya barang sesaatpun dan tidak (pula) kamu dapat meminta supaya diajukan\".'),
(3637, '034:031', '68', 34, 31, 'Dan orang-orang kafir berkata: \"Kami sekali-kali tidak akan beriman kepada Al Quran ini dan tidak (pula) kepada kitab yang sebelumnya\". Dan (alangkah hebatnya) kalau kamu lihat ketika orang-orang yang zalim itu dihadapkan kepada Tuhannya, sebahagian dari mereka menghadap kan perkataan kepada sebagian yang lain; orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri: \"Kalau tidaklah karena kamu tentulah kami menjadi orang-orang yang beriman\".'),
(3638, '034:032', '68', 34, 32, 'Orang-orang yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah: \"Kamikah yang telah menghalangi kamu dari petunjuk sesudah petunjuk itu datang kepadamu? (Tidak), sebenarnya kamu sendirilah orang-orang yang berdosa\".'),
(3639, '034:033', '68', 34, 33, 'Dan orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri: \"(Tidak) sebenarnya tipu daya(mu) di waktu malam dan siang (yang menghalangi kami), ketika kamu menyeru kami supaya kami kafir kepada Allah dan menjadikan sekutu-sekutu bagi-Nya\". Kedua belah pihak menyatakan penyesalan tatkala mereka melihat azab. Dan kami pasang belenggu di leher orang-orang yang kafir. Mereka tidak dibalas melainkan dengan apa yang telah mereka kerjakan.'),
(3640, '034:034', '68', 34, 34, 'Dan Kami tidak mengutus kepada suatu negeri seorang pemberi peringatanpun, melainkan orang-orang yang hidup mewah di negeri itu berkata: \"Sesungguhnya kami mengingkari apa yang kamu diutus untuk menyampaikannya\".'),
(3641, '034:035', '68', 34, 35, 'Dan mereka berkata: \"Kami lebih banyak mempunyai harta dan anak-anak (daripada kamu) dan kami sekali-kali tidak akan diazab.'),
(3642, '034:036', '68', 34, 36, 'Katakanlah: \"Sesungguhnya Tuhanku melapangkan rezeki bagi siapa yang dikehendaki-Nya dan menyempitkan (bagi siapa yang dikehendaki-Nya). akan tetapi kebanyakan manusia tidak mengetahui\".'),
(3643, '034:037', '68', 34, 37, 'Dan sekali-kali bukanlah harta dan bukan (pula) anak-anak kamu yang mendekatkan kamu kepada Kami sedikitpun; tetapi orang-orang yang beriman dan mengerjakan amal-amal (saleh, mereka itulah yang memperoleh balasan yang berlipat ganda disebabkan apa yang telah mereka kerjakan; dan mereka aman sentosa di tempat-tempat yang tinggi (dalam surga).'),
(3644, '034:038', '68', 34, 38, 'Dan orang-orang yang berusaha (menentang) ayat-ayat Kami dengan anggapan untuk dapat melemahkan (menggagalkan azab Kami), mereka itu dimasukkan ke dalam azab.'),
(3645, '034:039', '68', 34, 39, 'Katakanlah: \"Sesungguhnya Tuhanku melapangkan rezeki bagi siapa yang dikehendaki-Nya di antara hamba-hamba-Nya dan menyempitkan bagi (siapa yang dikehendaki-Nya)\". Dan barang apa saja yang kamu nafkahkan, maka Allah akan menggantinya dan Dialah Pemberi rezeki yang sebaik-baiknya.'),
(3646, '034:040', '68', 34, 40, 'Dan (ingatlah) hari (yang di waktu itu) Allah mengumpulkan mereka semuanya kemudian Allah berfirman kepada malaikat: \"Apakah mereka ini dahulu menyembah kamu?\".'),
(3647, '034:041', '68', 34, 41, 'Malaikat-malaikat itu menjawab: \"Maha Suci Engkau. Engkaulah pelindung kami, bukan mereka; bahkan mereka telah menyembah jin; kebanyakan mereka beriman kepada jin itu\".'),
(3648, '034:042', '68', 34, 42, 'Maka pada hari ini sebahagian kamu tidak berkuasa (untuk memberikan) kemanfaatan dan tidak pula kemudharatan kepada sebahagian yang lain. Dan Kami katakan kepada orang-orang yang zalim: \"Rasakanlah olehmu azab neraka yang dahulunya kamu dustakan itu\".'),
(3649, '034:043', '68', 34, 43, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang, mereka berkata: \"Orang ini tiada lain hanyalah seorang laki-laki yang ingin menghalangi kamu dari apa yang disembah oleh bapak-bapakmu\", dan mereka berkata: \"(Al Quran) ini tidak lain hanyalah kebohongan yang diada-adakan saja\". Dan orang-orang kafir berkata terhadap kebenaran tatkala kebenaran itu datang kepada mereka: \"Ini tidak lain hanyalah sihir yang nyata\".'),
(3650, '034:044', '68', 34, 44, 'Dan Kami tidak pernah memberikan kepada mereka kitab-kitab yang mereka baca dan sekali-kali tidak pernah (pula) mengutus kepada mereka sebelum kamu seorang pemberi peringatanpun.'),
(3651, '034:045', '68', 34, 45, 'Dan orang-orang yang sebelum mereka telah mendustakan sedang orang-orang kafir Mekah itu belum sampai menerima sepersepuluh dari apa yang telah Kami berikan kepada orang-orang dahulu itu lalu mereka mendustakan rasul-rasul-Ku. Maka alangkah hebatnya akibat kemurkaan-Ku.'),
(3652, '034:046', '68', 34, 46, 'Katakanlah: \"Sesungguhnya aku hendak memperingatkan kepadamu suatu hal saja, yaitu supaya kamu menghadap Allah (dengan ikhlas) berdua-dua atau sendiri-sendiri; kemudian kamu fikirkan (tentang Muhammad) tidak ada penyakit gila sedikitpun pada kawanmu itu. Dia tidak lain hanyalah pemberi peringatan bagi kamu sebelum (menghadapi) azab yang keras.'),
(3653, '034:047', '68', 34, 47, 'Katakanlah: \"Upah apapun yang aku minta kepadamu, maka itu untuk kamu. Upahku hanyalah dari Allah, dan Dia Maha Mengetahui segala sesuatu\".'),
(3654, '034:048', '68', 34, 48, 'Katakanlah: \"Sesungguhnya Tuhanku mewahyukan kebenaran. Dia Maha Mengetahui segala yang ghaib\".'),
(3655, '034:049', '68', 34, 49, 'Katakanlah: \"Kebenaran telah datang dan yang batil itu tidak akan memulai dan tidak (pula) akan mengulangi\".'),
(3656, '034:050', '68', 34, 50, 'Katakanlah: \"Jika aku sesat maka sesungguhnya aku sesat atas kemudharatan diriku sendiri; dan jika aku mendapat petunjuk maka itu adalah disebabkan apa yang diwahyukan Tuhanku kepadaku. Sesungguhnya Dia Maha Mendengar lagi Maha Dekat\".'),
(3657, '034:051', '68', 34, 51, 'Dan (alangkah hebatnya) jikalau kamu melihat ketika mereka (orang-orang kafir) terperanjat ketakutan (pada hari kiamat); maka mereka tidak dapat melepaskan diri dan mereka ditangkap dari tempat yang dekat (untuk dibawa ke neraka),'),
(3658, '034:052', '68', 34, 52, 'dan (di waktu itu) mereka berkata: \"Kami beriman kepada Allah\", bagaimanakah mereka dapat mencapai (keimanan) dari tempat yang jauh itu.'),
(3659, '034:053', '68', 34, 53, 'Dan sesungguhnya mereka telah mengingkari Allah sebelum itu; dan mereka menduga-duga tentang yang ghaib dari tempat yang jauh.'),
(3660, '034:054', '68', 34, 54, 'Dan dihalangi antara mereka dengan apa yang mereka ingini sebagaimana yang dilakukan terhadap orang-orang yang serupa dengan mereka pada masa dahulu. Sesungguhnya mereka dahulu (di dunia) dalam keraguan yang mendalam.'),
(3661, '035:001', '68', 35, 1, 'Segala puji bagi Allah Pencipta langit dan bumi, Yang menjadikan malaikat sebagai utusan-utusan (untuk mengurus berbagai macam urusan) yang mempunyai sayap, masing-masing (ada yang) dua, tiga dan empat. Allah menambahkan pada ciptaan-Nya apa yang dikehendaki-Nya. Sesungguhnya Allah Maha Kuasa atas segala sesuatu.'),
(3662, '035:002', '68', 35, 2, 'Apa saja yang Allah anugerahkan kepada manusia berupa rahmat, maka tidak ada seorangpun yang dapat menahannya; dan apa saja yang ditahan oleh Allah maka tidak seorangpun yang sanggup melepaskannya sesudah itu. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(3663, '035:003', '68', 35, 3, 'Hai manusia, ingatlah akan nikmat Allah kepadamu. Adakah pencipta selain Allah yang dapat memberikan rezeki kepada kamu dari langit dan bumi? Tidak ada Tuhan selain Dia; maka mengapakah kamu berpaling (dari ketauhidan)?'),
(3664, '035:004', '68', 35, 4, 'Dan jika mereka mendustakan kamu (sesudah kamu beri peringatan) maka sungguh telah didustakan pula rasul-rasul sebelum kamu. Dan hanya kepada Allahlah dikembalikan segala urusan.'),
(3665, '035:005', '68', 35, 5, 'Hai manusia, sesungguhnya janji Allah adalah benar, maka sekali-kali janganlah kehidupan dunia memperdayakan kamu dan sekali-kali janganlah syaitan yang pandai menipu, memperdayakan kamu tentang Allah.'),
(3666, '035:006', '68', 35, 6, 'Sesungguhnya syaitan itu adalah musuh bagimu, maka anggaplah ia musuh(mu), karena sesungguhnya syaitan-syaitan itu hanya mengajak golongannya supaya mereka menjadi penghuni neraka yang menyala-nyala'),
(3667, '035:007', '68', 35, 7, 'Orang-orang yang kafir bagi mereka azab yang keras. Dan orang-orang yang beriman dan mengerjakan amal saleh bagi mereka ampunan dan pahala yang besar.'),
(3668, '035:008', '68', 35, 8, 'Maka apakah orang yang dijadikan (syaitan) menganggap baik pekerjaannya yang buruk lalu dia meyakini pekerjaan itu baik, (sama dengan orang yang tidak ditipu oleh syaitan)? Maka sesungguhnya Allah menyesatkan siapa yang dikehendaki-Nya dan menunjuki siapa yang dikehendaki-Nya; maka janganlah dirimu binasa karena kesedihan terhadap mereka. Sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat.'),
(3669, '035:009', '68', 35, 9, 'Dan Allah, Dialah Yang mengirimkan angin; lalu angin itu menggerakkan awan, maka Kami halau awan itu kesuatu negeri yang mati lalu Kami hidupkan bumi setelah matinya dengan hujan itu. Demikianlah kebangkitan itu.'),
(3670, '035:010', '68', 35, 10, 'Barangsiapa yang menghendaki kemuliaan, maka bagi Allah-lah kemuliaan itu semuanya. Kepada-Nya-lah naik perkataan-perkataan yang baik dan amal yang saleh dinaikkan-Nya. Dan orang-orang yang merencanakan kejahatan bagi mereka azab yang keras. Dan rencana jahat mereka akan hancur.'),
(3671, '035:011', '68', 35, 11, 'Dan Allah menciptakan kamu dari tanah kemudian dari air mani, kemudian Dia menjadikan kamu berpasangan (laki-laki dan perempuan). Dan tidak ada seorang perempuanpun mengandung dan tidak (pula) melahirkan melainkan dengan sepengetahuan-Nya. Dan sekali-kali tidak dipanjangkan umur seorang yang berumur panjang dan tidak pula dikurangi umurnya, melainkan (sudah ditetapkan) dalam Kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu bagi Allah adalah mudah.'),
(3672, '035:012', '68', 35, 12, 'Dan tiada sama (antara) dua laut; yang ini tawar, segar, sedap diminum dan yang lain asin lagi pahit. Dan dari masing-masing laut itu kamu dapat memakan daging yang segar dan kamu dapat mengeluarkan perhiasan yang dapat kamu memakainya, dan pada masing-masingnya kamu lihat kapal-kapal berlayar membelah laut supaya kamu dapat mencari karunia-Nya dan supaya kamu bersyukur.'),
(3673, '035:013', '68', 35, 13, 'Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan menundukkan matahari dan bulan, masing-masing berjalan menurut waktu yang ditentukan. Yang (berbuat) demikian itulah Allah Tuhanmu, kepunyaan-Nya-lah kerajaan. Dan orang-orang yang kamu seru (sembah) selain Allah tiada mempunyai apa-apa walaupun setipis kulit ari.'),
(3674, '035:014', '68', 35, 14, 'Jika kamu menyeru mereka, mereka tiada mendengar seruanmu; dan kalau mereka mendengar, mereka tidak dapat memperkenankan permintaanmu. Dan dihari kiamat mereka akan mengingkari kemusyirikanmu dan tidak ada yang dapat memberi keterangan kepadamu sebagai yang diberikan oleh Yang Maha Mengetahui.'),
(3675, '035:015', '68', 35, 15, 'Hai manusia, kamulah yang berkehendak kepada Allah; dan Allah Dialah Yang Maha Kaya (tidak memerlukan sesuatu) lagi Maha Terpuji.'),
(3676, '035:016', '68', 35, 16, 'Jika Dia menghendaki, niscaya Dia memusnahkan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu).'),
(3677, '035:017', '68', 35, 17, 'Dan yang demikian itu sekali-kali tidak sulit bagi Allah.'),
(3678, '035:018', '68', 35, 18, 'Dan orang yang berdosa tidak akan memikul dosa orang lain. Dan jika seseorang yang berat dosanya memanggil (orang lain) untuk memikul dosanya itu tiadalah akan dipikulkan untuknya sedikitpun meskipun (yang dipanggilnya itu) kaum kerabatnya. Sesungguhnya yang dapat kamu beri peringatan hanya orang-orang yang takut kepada azab Tuhannya (sekalipun) mereka tidak melihat-Nya dan mereka mendirikan sembahyang. Dan barangsiapa yang mensucikan dirinya, sesungguhnya ia mensucikan diri untuk kebaikan dirinya sendiri. Dan kepada Allahlah kembali(mu).'),
(3679, '035:019', '68', 35, 19, 'Dan tidaklah sama orang yang buta dengan orang yang melihat.'),
(3680, '035:020', '68', 35, 20, 'dan tidak (pula) sama gelap gulita dengan cahaya,'),
(3681, '035:021', '68', 35, 21, 'dan tidak (pula) sama yang teduh dengan yang panas,'),
(3682, '035:022', '68', 35, 22, 'dan tidak (pula) sama orang-orang yang hidup dan orang-orang yang mati. Sesungguhnya Allah memberi pendengaran kepada siapa yang dikehendaki-Nya dan kamu sekali-kali tiada sanggup menjadikan orang yang didalam kubur dapat mendengar.'),
(3683, '035:023', '68', 35, 23, 'Kamu tidak lain hanyalah seorang pemberi peringatan.'),
(3684, '035:024', '68', 35, 24, 'Sesungguhnya Kami mengutus kamu dengan membawa kebenaran sebagai pembawa berita gembira dan sebagai pemberi peringatan. Dan tidak ada suatu umatpun melainkan telah ada padanya seorang pemberi peringatan.'),
(3685, '035:025', '68', 35, 25, 'Dan jika mereka mendustakan kamu, maka sesungguhnya orang-orang yang sebelum mereka telah mendustakan (rasul-rasulnya); kepada mereka telah datang rasul-rasulnya dengan membawa mukjizat yang nyata, zubur, dan kitab yang memberi penjelasan yang sempurna.'),
(3686, '035:026', '68', 35, 26, 'Kemudian Aku azab orang-orang yang kafir; maka (lihatlah) bagaimana (hebatnya) akibat kemurkaan-Ku.'),
(3687, '035:027', '68', 35, 27, 'Tidakkah kamu melihat bahwasanya Allah menurunkan hujan dari langit lalu Kami hasilkan dengan hujan itu buah-buahan yang beraneka macam jenisnya. Dan di antara gunung-gunung itu ada garis-garis putih dan merah yang beraneka macam warnanya dan ada (pula) yang hitam pekat.'),
(3688, '035:028', '68', 35, 28, 'Dan demikian (pula) di antara manusia, binatang-binatang melata dan binatang-binatang ternak ada yang bermacam-macam warnanya (dan jenisnya). Sesungguhnya yang takut kepada Allah di antara hamba-hamba-Nya, hanyalah ulama. Sesungguhnya Allah Maha Perkasa lagi Maha Pengampun.'),
(3689, '035:029', '68', 35, 29, 'Sesungguhnya orang-orang yang selalu membaca kitab Allah dan mendirikan shalat dan menafkahkan sebahagian dari rezeki yang Kami anugerahkan kepada mereka dengan diam-diam dan terang-terangan, mereka itu mengharapkan perniagaan yang tidak akan merugi,'),
(3690, '035:030', '68', 35, 30, 'agar Allah menyempurnakan kepada mereka pahala mereka dan menambah kepada mereka dari karunia-Nya. Sesungguhnya Allah Maha Pengampun lagi Maha Mensyukuri.'),
(3691, '035:031', '68', 35, 31, 'Dan apa yang telah Kami wahyukan kepadamu yaitu Al Kitab (Al Quran) itulah yang benar, dengan membenarkan kitab-kitab yang sebelumnya. Sesungguhnya Allah benar-benar Maha Mengetahui lagi Maha Melihat (keadaan) hamba-hamba-Nya.'),
(3692, '035:032', '68', 35, 32, 'Kemudian Kitab itu Kami wariskan kepada orang-orang yang Kami pilih di antara hamba-hamba Kami, lalu di antara mereka ada yang menganiaya diri mereka sendiri dan di antara mereka ada yang pertengahan dan diantara mereka ada (pula) yang lebih dahulu berbuat kebaikan dengan izin Allah. Yang demikian itu adalah karunia yang amat besar.'),
(3693, '035:033', '68', 35, 33, '(Bagi mereka) surga ´Adn mereka masuk ke dalamnya, di dalamnya mereka diberi perhiasan dengan gelang-gelang dari emas, dan dengan mutiara, dan pakaian mereka didalamnya adalah sutera.'),
(3694, '035:034', '68', 35, 34, 'Dan mereka berkata: \"Segala puji bagi Allah yang telah menghilangkan duka cita dari kami. Sesungguhnya Tuhan kami benar-benar Maha Pengampum lagi Maha Mensyukuri.'),
(3695, '035:035', '68', 35, 35, 'Yang menempatkan kami dalam tempat yang kekal (surga) dari karunia-Nya; didalamnya kami tiada merasa lelah dan tiada pula merasa lesu\".'),
(3696, '035:036', '68', 35, 36, 'Dan orang-orang kafir bagi mereka neraka Jahannam. Mereka tidak dibinasakan sehingga mereka mati dan tidak (pula) diringankan dari mereka azabnya. Demikianlah Kami membalas setiap orang yang sangat kafir.'),
(3697, '035:037', '68', 35, 37, 'Dan mereka berteriak di dalam neraka itu: \"Ya Tuhan kami, keluarkanlah kami niscaya kami akan mengerjakan amal yang saleh berlainan dengan yang telah kami kerjakan\". Dan apakah Kami tidak memanjangkan umurmu dalam masa yang cukup untuk berfikir bagi orang yang mau berfikir, dan (apakah tidak) datang kepada kamu pemberi peringatan? maka rasakanlah (azab Kami) dan tidak ada bagi orang-orang yang zalim seorang penolongpun.'),
(3698, '035:038', '68', 35, 38, 'Sesungguhnya Allah mengetahui yang tersembunyi di langit dan di bumi. Sesungguhnya Dia Maha Mengetahui segala isi hati.'),
(3699, '035:039', '68', 35, 39, 'Dialah yang menjadikan kamu khalifah-khalifah di muka bumi. Barangsiapa yang kafir, maka (akibat) kekafirannya menimpa dirinya sendiri. Dan kekafiran orang-orang yang kafir itu tidak lain hanyalah akan menambah kemurkaan pada sisi Tuhannya dan kekafiran orang-orang yang kafir itu tidak lain hanyalah akan menambah kerugian mereka belaka.'),
(3700, '035:040', '68', 35, 40, 'Katakanlah: \"Terangkanlah kepada-Ku tentang sekutu-sekutumu yang kamu seru selain Allah. Perlihatkanlah kepada-Ku (bahagian) manakah dari bumi ini yang telah mereka ciptakan ataukah mereka mempunyai saham dalam (penciptaan) langit atau adakah Kami memberi kepada mereka sebuah Kitab sehingga mereka mendapat keterangan-keterangan yang jelas daripadanya? Sebenarnya orang-orang yang zalim itu sebahagian dari mereka tidak menjanjikan kepada sebahagian yang lain, melainkan tipuan belaka\".'),
(3701, '035:041', '68', 35, 41, 'Sesungguhnya Allah menahan langit dan bumi supaya jangan lenyap; dan sungguh jika keduanya akan lenyap tidak ada seorangpun yang dapat menahan keduanya selain Allah. Sesungguhnya Dia adalah Maha Penyantun lagi Maha Pengampun.'),
(3702, '035:042', '68', 35, 42, 'Dan mereka bersumpah dengan nama Allah dengan sekuat-kuat sumpah; sesungguhnya jika datang kepada mereka seorang pemberi peringatan, niscaya mereka akan lebih mendapat petunjuk dari salah satu umat-umat (yang lain). Tatkala datang kepada mereka pemberi peringatan, maka kedatangannya itu tidak menambah kepada mereka, kecuali jauhnya mereka dari (kebenaran),'),
(3703, '035:043', '68', 35, 43, 'karena kesombongan (mereka) di muka bumi dan karena rencana (mereka) yang jahat. Rencana yang jahat itu tidak akan menimpa selain orang yang merencanakannya sendiri. Tiadalah yang mereka nanti-nantikan melainkan (berlakunya) sunnah (Allah yang telah berlaku) kepada orang-orang yang terdahulu. Maka sekali-kali kamu tidak akan mendapat penggantian bagi sunnah Allah, dan sekali-kali tidak (pula) akan menemui penyimpangan bagi sunnah Allah itu.'),
(3704, '035:044', '68', 35, 44, 'Dan apakah mereka tidak berjalan di muka bumi, lalu melihat bagaimana kesudahan orang-orang yang sebelum mereka, sedangkan orang-orang itu adalah lebih besar kekuatannya dari mereka? Dan tiada sesuatupun yang dapat melemahkan Allah baik di langit maupun di bumi. Sesungguhnya Allah Maha Mengetahui lagi Maha Kuasa.'),
(3705, '035:045', '68', 35, 45, 'Dan kalau sekiranya Allah menyiksa manusia disebabkan usahanya, niscaya Dia tidak akan meninggalkan di atas permukaan bumi suatu mahluk yang melatapun akan tetapi Allah menangguhkan (penyiksaan) mereka, sampai waktu yang tertentu; maka apabila datang ajal mereka, maka sesungguhnya Allah adalah Maha Melihat (keadaan) hamba-hamba-Nya.'),
(3706, '036:001', '68', 36, 1, 'Yaa siin'),
(3707, '036:002', '68', 36, 2, 'Demi Al Quran yang penuh hikmah,'),
(3708, '036:003', '68', 36, 3, 'Sesungguhnya kamu salah seorang dari rasul-rasul,'),
(3709, '036:004', '68', 36, 4, '(yang berada) diatas jalan yang lurus,'),
(3710, '036:005', '68', 36, 5, '(sebagai wahyu) yang diturunkan oleh Yang Maha Perkasa lagi Maha Penyayang,'),
(3711, '036:006', '68', 36, 6, 'Agar kamu memberi peringatan kepada kaum yang bapak-bapak mereka belum pernah diberi peringatan, karena itu mereka lalai.'),
(3712, '036:007', '68', 36, 7, 'Sesungguhnya telah pasti berlaku perkataan (ketentuan Allah) terhadap kebanyakan mereka, kerena mereka tidak beriman.'),
(3713, '036:008', '68', 36, 8, 'Sesungguhnya Kami telah memasang belenggu dileher mereka, lalu tangan mereka (diangkat) ke dagu, maka karena itu mereka tertengadah.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(3714, '036:009', '68', 36, 9, 'Dan Kami adakan di hadapan mereka dinding dan di belakang mereka dinding (pula), dan Kami tutup (mata) mereka sehingga mereka tidak dapat melihat.'),
(3715, '036:010', '68', 36, 10, 'Sama saja bagi mereka apakah kamu memberi peringatan kepada mereka ataukah kamu tidak memberi peringatan kepada mereka, mereka tidak akan beriman.'),
(3716, '036:011', '68', 36, 11, 'Sesungguhnya kamu hanya memberi peringatan kepada orang-orang yang mau mengikuti peringatan dan yang takut kepada Tuhan Yang Maha Pemurah walaupun dia tidak melihatnya. Maka berilah mereka kabar gembira dengan ampunan dan pahala yang mulia.'),
(3717, '036:012', '68', 36, 12, 'Sesungguhnya Kami menghidupkan orang-orang mati dan Kami menuliskan apa yang telah mereka kerjakan dan bekas-bekas yang mereka tinggalkan. Dan segala sesuatu Kami kumpulkan dalam Kitab Induk yang nyata (Lauh Mahfuzh).'),
(3718, '036:013', '68', 36, 13, 'Dan buatlah bagi mereka suatu perumpamaan, yaitu penduduk suatu negeri ketika utusan-utusan datang kepada mereka.'),
(3719, '036:014', '68', 36, 14, '(yaitu) ketika Kami mengutus kepada mereka dua orang utusan, lalu mereka mendustakan keduanya; kemudian Kami kuatkan dengan (utusan) yang ketiga, maka ketiga utusan itu berkata: \"Sesungguhnya kami adalah orang-orang di utus kepadamu\".'),
(3720, '036:015', '68', 36, 15, 'Mereka menjawab: \"Kamu tidak lain hanyalah manusia seperti kami dan Allah Yang Maha Pemurah tidak menurunkan sesuatupun, kamu tidak lain hanyalah pendusta belaka\".'),
(3721, '036:016', '68', 36, 16, 'Mereka berkata: \"Tuhan kami mengetahui bahwa sesungguhnya kami adalah orang yang diutus kepada kamu\".'),
(3722, '036:017', '68', 36, 17, 'Dan kewajiban kami tidak lain hanyalah menyampaikan (perintah Allah) dengan jelas\".'),
(3723, '036:018', '68', 36, 18, 'Mereka menjawab: \"Sesungguhnya kami bernasib malang karena kamu, sesungguhnya jika kamu tidak berhenti (menyeru kami), niscaya kami akan merajam kamu dan kamu pasti akan mendapat siksa yang pedih dari kami\".'),
(3724, '036:019', '68', 36, 19, 'Utusan-utusan itu berkata: \"Kemalangan kamu adalah karena kamu sendiri. Apakah jika kamu diberi peringatan (kamu bernasib malang)? Sebenarnya kamu adalah kaum yang melampui batas\".'),
(3725, '036:020', '68', 36, 20, 'Dan datanglah dari ujung kota, seorang laki-laki dengan bergegas-gegas ia berkata: \"Hai kaumku, ikutilah utusan-utusan itu\".'),
(3726, '036:021', '68', 36, 21, 'Ikutilah orang yang tiada minta balasan kepadamu; dan mereka adalah orang-orang yang mendapat petunjuk.'),
(3727, '036:022', '68', 36, 22, 'Mengapa aku tidak menyembah (Tuhan) yang telah menciptakanku dan yang hanya kepada-Nya-lah kamu (semua) akan dikembalikan?'),
(3728, '036:023', '68', 36, 23, 'Mengapa aku akan menyembah tuhan-tuhan selain-Nya jika (Allah) Yang Maha Pemurah menghendaki kemudharatan terhadapku, niscaya syafa´at mereka tidak memberi manfaat sedikitpun bagi diriku dan mereka tidak (pula) dapat menyelamatkanku?'),
(3729, '036:024', '68', 36, 24, 'Sesungguhnya aku kalau begitu pasti berada dalam kesesatan yang nyata.'),
(3730, '036:025', '68', 36, 25, 'Sesungguhnya aku telah beriman kepada Tuhanmu; maka dengarkanlah (pengakuan keimanan)ku.'),
(3731, '036:026', '68', 36, 26, 'Dikatakan (kepadanya): \"Masuklah ke surga\". Ia berkata: \"Alangkah baiknya sekiranya kamumku mengetahui.'),
(3732, '036:027', '68', 36, 27, 'Apa yang menyebabkan Tuhanku memberi ampun kepadaku dan menjadikan aku termasuk orang-orang yang dimuliakan\".'),
(3733, '036:028', '68', 36, 28, 'Dan kami tidak menurunkan kepada kaumnya sesudah dia (meninggal) suatu pasukanpun dari langit dan tidak layak Kami menurunkannya.'),
(3734, '036:029', '68', 36, 29, 'Tidak ada siksaan atas mereka melainkan satu teriakan suara saja; maka tiba-tiba mereka semuanya mati.'),
(3735, '036:030', '68', 36, 30, 'Alangkah besarnya penyesalan terhadap hamba-hamba itu, tiada datang seorang rasulpun kepada mereka melainkan mereka selalu memperolok-olokkannya.'),
(3736, '036:031', '68', 36, 31, 'Tidakkah mereka mengetahui berapa banyaknya umat-umat sebelum mereka yang telah Kami binasakan, bahwasanya orang-orang (yang telah Kami binasakan) itu tiada kembali kepada mereka.'),
(3737, '036:032', '68', 36, 32, 'Dan setiap mereka semuanya akan dikumpulkan lagi kepada Kami.'),
(3738, '036:033', '68', 36, 33, 'Dan suatu tanda (kekuasaan Allah yang besar) bagi mereka adalah bumi yang mati. Kami hidupkan bumi itu dan Kami keluarkan dari padanya biji-bijian, maka daripadanya mereka makan.'),
(3739, '036:034', '68', 36, 34, 'Dan Kami jadikan padanya kebun-kebun kurma dan anggur dan Kami pancarkan padanya beberapa mata air,'),
(3740, '036:035', '68', 36, 35, 'supaya mereka dapat makan dari buahnya, dan dari apa yang diusahakan oleh tangan mereka. Maka mengapakah mereka tidak bersyukur?'),
(3741, '036:036', '68', 36, 36, 'Maha Suci Tuhan yang telah menciptakan pasangan-pasangan semuanya, baik dari apa yang ditumbuhkan oleh bumi dan dari diri mereka maupun dari apa yang tidak mereka ketahui.'),
(3742, '036:037', '68', 36, 37, 'Dan suatu tanda (kekuasaan Allah yang besar) bagi mereka adalah malam; Kami tanggalkan siang dari malam itu, maka dengan serta merta mereka berada dalam kegelapan.'),
(3743, '036:038', '68', 36, 38, 'dan matahari berjalan ditempat peredarannya. Demikianlah ketetapan Yang Maha Perkasa lagi Maha Mengetahui.'),
(3744, '036:039', '68', 36, 39, 'Dan telah Kami tetapkan bagi bulan manzilah-manzilah, sehingga (setelah dia sampai ke manzilah yang terakhir) kembalilah dia sebagai bentuk tandan yang tua.'),
(3745, '036:040', '68', 36, 40, 'Tidaklah mungkin bagi matahari mendapatkan bulan dan malampun tidak dapat mendahului siang. Dan masing-masing beredar pada garis edarnya.'),
(3746, '036:041', '68', 36, 41, 'Dan suatu tanda (kebesaran Allah yang besar) bagi mereka adalah bahwa Kami angkut keturunan mereka dalam bahtera yang penuh muatan.'),
(3747, '036:042', '68', 36, 42, 'dan Kami ciptakan untuk mereka yang akan mereka kendarai seperti bahtera itu.'),
(3748, '036:043', '68', 36, 43, 'Dan jika Kami menghendaki niscaya Kami tenggelamkan mereka, maka tiadalah bagi mereka penolong dan tidak pula mereka diselamatkan.'),
(3749, '036:044', '68', 36, 44, 'Tetapi (Kami selamatkan mereka) karena rahmat yang besar dari Kami dan untuk memberikan kesenangan hidup sampai kepada suatu ketika.'),
(3750, '036:045', '68', 36, 45, 'Dan apabila dikatakan kepada mereka: \"Takutlah kamu akan siksa yang dihadapanmu dan siksa yang akan datang supaya kamu mendapat rahmat\", (niscaya mereka berpaling).'),
(3751, '036:046', '68', 36, 46, 'Dan sekali-kali tiada datang kepada mereka suatu tanda dari tanda tanda kekuasaan Tuhan mereka, melainkan mereka selalu berpaling daripadanya.'),
(3752, '036:047', '68', 36, 47, 'Dan apabila dikatakakan kepada mereka: \"Nafkahkanlah sebahagian dari reski yang diberikan Allah kepadamu\", maka orang-orang yang kafir itu berkata kepada orang-orang yang beriman: \"Apakah kami akan memberi makan kepada orang-orang yang jika Allah menghendaki tentulah Dia akan memberinya makan, tiadalah kamu melainkan dalam kesesatan yang nyata\".'),
(3753, '036:048', '68', 36, 48, 'Dan mereka berkata: \"Bilakah (terjadinya) janji ini (hari berbangkit) jika kamu adalah orang-orang yang benar?\".'),
(3754, '036:049', '68', 36, 49, 'Mereka tidak menunggu melainkan satu teriakan saja yang akan membinasakan mereka ketika mereka sedang bertengkar.'),
(3755, '036:050', '68', 36, 50, 'lalu mereka tidak kuasa membuat suatu wasiatpun dan tidak (pula) dapat kembali kepada keluarganya.'),
(3756, '036:051', '68', 36, 51, 'Dan ditiuplah sangkalala, maka tiba-tiba mereka keluar dengan segera dari kuburnya (menuju) kepada Tuhan mereka.'),
(3757, '036:052', '68', 36, 52, 'Mereka berkata: \"Aduhai celakalah kami! Siapakah yang membangkitkan kami dari tempat-tidur kami (kubur)?\". Inilah yang dijanjikan (Tuhan) Yang Maha Pemurah dan benarlah Rasul-rasul(Nya).'),
(3758, '036:053', '68', 36, 53, 'Tidak adalah teriakan itu selain sekali teriakan saja, maka tiba-tiba mereka semua dikumpulkan kepada Kami.'),
(3759, '036:054', '68', 36, 54, 'Maka pada hari itu seseorang tidak akan dirugikan sedikitpun dan kamu tidak dibalasi, kecuali dengan apa yang telah kamu kerjakan.'),
(3760, '036:055', '68', 36, 55, 'Sesungguhnya penghuni surga pada hari itu bersenang-senang dalam kesibukan (mereka).'),
(3761, '036:056', '68', 36, 56, 'Mereka dan isteri-isteri mereka berada dalam tempat yang teduh, bertelekan di atas dipan-dipan.'),
(3762, '036:057', '68', 36, 57, 'Di surga itu mereka memperoleh buah-buahan dan memperoleh apa yang mereka minta.'),
(3763, '036:058', '68', 36, 58, '(Kepada mereka dikatakan): \"Salam\", sebagai ucapan selamat dari Tuhan Yang Maha Penyayang.'),
(3764, '036:059', '68', 36, 59, 'Dan (dikatakan kepada orang-orang kafir): \"Berpisahlah kamu (dari orang-orang mukmin) pada hari ini, hai orang-orang yang berbuat jahat.'),
(3765, '036:060', '68', 36, 60, 'Bukankah Aku telah memerintahkan kepadamu hai Bani Adam supaya kamu tidak menyembah syaitan? Sesungguhnya syaitan itu adalah musuh yang nyata bagi kamu\",'),
(3766, '036:061', '68', 36, 61, 'dan hendaklah kamu menyembah-Ku. Inilah jalan yang lurus.'),
(3767, '036:062', '68', 36, 62, 'Sesungguhnya syaitan itu telah menyesatkan sebahagian besar diantaramu, Maka apakah kamu tidak memikirkan?.'),
(3768, '036:063', '68', 36, 63, 'Inilah Jahannam yang dahulu kamu diancam (dengannya).'),
(3769, '036:064', '68', 36, 64, 'Masuklah ke dalamnya pada hari ini disebabkan kamu dahulu mengingkarinya.'),
(3770, '036:065', '68', 36, 65, 'Pada hari ini Kami tutup mulut mereka; dan berkatalah kepada Kami tangan mereka dan memberi kesaksianlah kaki mereka terhadap apa yang dahulu mereka usahakan.'),
(3771, '036:066', '68', 36, 66, 'Dan jikalau Kami menghendaki pastilah Kami hapuskan penglihatan mata mereka; lalu mereka berlomba-lomba (mencari) jalan, Maka betapakah mereka dapat melihat(nya).'),
(3772, '036:067', '68', 36, 67, 'Dan jikalau Kami menghendaki pastilah Kami ubah mereka di tempat mereka berada; maka mereka tidak sanggup berjalan lagi dan tidak (pula) sanggup kembali.'),
(3773, '036:068', '68', 36, 68, 'Dan barangsiapa yang Kami panjangkan umurnya niscaya Kami kembalikan dia kepada kejadian(nya). Maka apakah mereka tidak memikirkan?'),
(3774, '036:069', '68', 36, 69, 'Dan Kami tidak mengajarkan syair kepadanya (Muhammad) dan bersyair itu tidaklah layak baginya. Al Quran itu tidak lain hanyalah pelajaran dan kitab yang memberi penerangan.'),
(3775, '036:070', '68', 36, 70, 'supaya dia (Muhammad) memberi peringatan kepada orang-orang yang hidup (hatinya) dan supaya pastilah (ketetapan azab) terhadap orang-orang kafir.'),
(3776, '036:071', '68', 36, 71, 'Dan apakah mereka tidak melihat bahwa sesungguhnya Kami telah menciptakan binatang ternak untuk mereka yaitu sebahagian dari apa yang telah Kami ciptakan dengan kekuasaan Kami sendiri, lalu mereka menguasainya?'),
(3777, '036:072', '68', 36, 72, 'Dan Kami tundukkan binatang-binatang itu untuk mereka; maka sebahagiannya menjadi tunggangan mereka dan sebahagiannya mereka makan.'),
(3778, '036:073', '68', 36, 73, 'Dan mereka memperoleh padanya manfaat-manfaat dan minuman. Maka mengapakah mereka tidak bersyukur?'),
(3779, '036:074', '68', 36, 74, 'Mereka mengambil sembahan-sembahan selain Allah, agar mereka mendapat pertolongan.'),
(3780, '036:075', '68', 36, 75, 'Berhala-berhala itu tiada dapat menolong mereka; padahal berhala-berhala itu menjadi tentara yang disiapkan untuk menjaga mereka.'),
(3781, '036:076', '68', 36, 76, 'Maka janganlah ucapan mereka menyedihkan kamu. Sesungguhnya Kami mengetahui apa yang mereka rahasiakan dan apa yang mereka nyatakan.'),
(3782, '036:077', '68', 36, 77, 'Dan apakah manusia tidak memperhatikan bahwa Kami menciptakannya dari setitik air (mani), maka tiba-tiba ia menjadi penantang yang nyata!'),
(3783, '036:078', '68', 36, 78, 'Dan ia membuat perumpamaan bagi Kami; dan dia lupa kepada kejadiannya; ia berkata: \"Siapakah yang dapat menghidupkan tulang belulang, yang telah hancur luluh?\"'),
(3784, '036:079', '68', 36, 79, 'Katakanlah: \"Ia akan dihidupkan oleh Tuhan yang menciptakannya kali yang pertama. Dan Dia Maha Mengetahui tentang segala makhluk.'),
(3785, '036:080', '68', 36, 80, 'yaitu Tuhan yang menjadikan untukmu api dari kayu yang hijau, maka tiba-tiba kamu nyalakan (api) dari kayu itu\".'),
(3786, '036:081', '68', 36, 81, 'Dan tidaklah Tuhan yang menciptakan langit dan bumi itu berkuasa menciptakan yang serupa dengan itu? Benar, Dia berkuasa. Dan Dialah Maha Pencipta lagi Maha Mengetahui.'),
(3787, '036:082', '68', 36, 82, 'Sesungguhnya keadaan-Nya apabila Dia menghendaki sesuatu hanyalah berkata kepadanya: \"Jadilah!\" maka terjadilah ia.'),
(3788, '036:083', '68', 36, 83, 'Maka Maha Suci (Allah) yang di tangan-Nya kekuasaaan atas segala sesuatu dan kepada-Nya-lah kamu dikembalikan.'),
(3789, '037:001', '68', 37, 1, 'Demi (rombongan) yang ber shaf-shaf dengan sebenar-benarnya],'),
(3790, '037:002', '68', 37, 2, 'dan demi (rombongan) yang melarang dengan sebenar-benarnya (dari perbuatan-perbuatan maksiat),'),
(3791, '037:003', '68', 37, 3, 'dan demi (rombongan) yang membacakan pelajaran,'),
(3792, '037:004', '68', 37, 4, 'Sesungguhnya Tuhanmu benar-benar Esa.'),
(3793, '037:005', '68', 37, 5, 'Tuhan langit dan bumi dan apa yang berada di antara keduanya dan Tuhan tempat-tempat terbit matahari.'),
(3794, '037:006', '68', 37, 6, 'Sesungguhnya Kami telah menghias langit yang terdekat dengan hiasan, yaitu bintang-bintang,'),
(3795, '037:007', '68', 37, 7, 'dan telah memeliharanya (sebenar-benarnya) dari setiap syaitan yang sangat durhaka,'),
(3796, '037:008', '68', 37, 8, 'syaitan syaitan itu tidak dapat mendengar-dengarkan (pembicaraan) para malaikat dan mereka dilempari dari segala penjuru.'),
(3797, '037:009', '68', 37, 9, 'Untuk mengusir mereka dan bagi mereka siksaan yang kekal,'),
(3798, '037:010', '68', 37, 10, 'akan tetapi barangsiapa (di antara mereka) yang mencuri-curi (pembicaraan); maka ia dikejar oleh suluh api yang cemerlang.'),
(3799, '037:011', '68', 37, 11, 'Maka tanyakanlah kepada mereka (musyrik Mekah): \"Apakah mereka yang lebih kukuh kejadiannya ataukah apa yang telah Kami ciptakan itu?\" Sesungguhnya Kami telah menciptakan mereka dari tanah liat.'),
(3800, '037:012', '68', 37, 12, 'Bahkan kamu menjadi heran (terhadap keingkaran mereka) dan mereka menghinakan kamu.'),
(3801, '037:013', '68', 37, 13, 'Dan apabila mereka diberi pelajaran mereka tiada mengingatnya.'),
(3802, '037:014', '68', 37, 14, 'Dan apabila mereka melihat sesuatu tanda kebesaran Allah, mereka sangat menghinakan.'),
(3803, '037:015', '68', 37, 15, 'Dan mereka berkata \"Ini tiada lain hanyalah sihir yang nyata.'),
(3804, '037:016', '68', 37, 16, 'Apakah apabila kami telah mati dan telah menjadi tanah serta menjadi tulang belulang, apakah benar-benar kami akan dibangkitkan (kembali)?'),
(3805, '037:017', '68', 37, 17, 'Dan apakah bapak-bapak kami yang telah terdahulu (akan dibangkitkan pula)\"?'),
(3806, '037:018', '68', 37, 18, 'Katakanlah: \"Ya, dan kamu akan terhina\"'),
(3807, '037:019', '68', 37, 19, 'Maka sesungguhnya kebangkitan itu hanya dengan satu teriakan saja; maka tiba-tiba mereka meIihatnya.'),
(3808, '037:020', '68', 37, 20, 'Dan mereka berkata: \"Aduhai celakalah kita!\" Inilah hari pembalasan.'),
(3809, '037:021', '68', 37, 21, 'Inilah hari keputusan yang kamu selalu mendustakannya.'),
(3810, '037:022', '68', 37, 22, '(kepada malaikat diperintahkan): \"Kumpulkanlah orang-orang yang zalim beserta teman sejawat mereka dan sembahan-sembahan yang selalu mereka sembah,'),
(3811, '037:023', '68', 37, 23, 'selain Allah; maka tunjukkanlah kepada mereka jalan ke neraka.'),
(3812, '037:024', '68', 37, 24, 'Dan tahanlah mereka (di tempat perhentian) karena sesungguhnya mereka akan ditanya:'),
(3813, '037:025', '68', 37, 25, '\"Kenapa kamu tidak tolong menolong?\"'),
(3814, '037:026', '68', 37, 26, 'Bahkan mereka pada hari itu menyerah diri.'),
(3815, '037:027', '68', 37, 27, 'Sebahagian dan mereka menghadap kepada sebahagian yang lain berbantah-bantahan.'),
(3816, '037:028', '68', 37, 28, 'Pengikut-pengikut mereka berkata (kepada pemimpin-pemimpin mereka): \"Sesungguhnya kamulah yang datang kepada kami dan kanan.'),
(3817, '037:029', '68', 37, 29, 'Pemimpin-pemimpin mereka menjawab: \"Sebenarnya kamulah yang tidak beriman\".'),
(3818, '037:030', '68', 37, 30, 'Dan sekali-kali kami tidak berkuasa terhadapmu, bahkan kamulah kaum yang melampaui batas.'),
(3819, '037:031', '68', 37, 31, 'Maka pastilah putusan (azab) Tuhan kita menimpa atas kita; sesungguhnya kita akan merasakan (azab itu).'),
(3820, '037:032', '68', 37, 32, 'Maka kami telah menyesatkan kamu, sesungguhnya kami adalah orang-orang yang sesat.'),
(3821, '037:033', '68', 37, 33, 'Maka sesungguhnya mereka pada hari itu bersama-sama dalam azab.'),
(3822, '037:034', '68', 37, 34, 'Sesungguhnya demikianlah Kami berbuat terhadap orang-orang yang berbuat jahat.'),
(3823, '037:035', '68', 37, 35, 'Sesungguhnya mereka dahulu apabila dikatakan kepada mereka: \"Laa ilaaha illallah\" (Tiada Tuhan yang berhak disembah melainkan Allah) mereka menyombongkan diri,'),
(3824, '037:036', '68', 37, 36, 'dan mereka berkata: \"Apakah sesungguhnya kami harus meninggalkan sembahan-sembahan kami karena seorang penyair gila?\"'),
(3825, '037:037', '68', 37, 37, 'Sebenarnya dia (Muhammad) telah datang membawa kebenaran dan membenarkan rasul-rasul (sebelumnya).'),
(3826, '037:038', '68', 37, 38, 'Sesungguhnya kamu pasti akan merasakan azab yang pedih.'),
(3827, '037:039', '68', 37, 39, 'Dan kamu tidak diberi pembalasan melainkan terhadap kejahatan yang telah kamu kerjakan,'),
(3828, '037:040', '68', 37, 40, 'tetapi hamba-hamba Allah yang dibersihkan (dari dosa).'),
(3829, '037:041', '68', 37, 41, 'Mereka itu memperoleh rezeki yang tertentu,'),
(3830, '037:042', '68', 37, 42, 'yaitu buah-buahan. Dan mereka adalah orang-orang yang dimuliakan,'),
(3831, '037:043', '68', 37, 43, 'di dalam surga-surga yang penuh nikmat.'),
(3832, '037:044', '68', 37, 44, 'di atas takhta-takhta kebesaran berhadap-hadapan.'),
(3833, '037:045', '68', 37, 45, 'Diedarkan kepada mereka gelas yang berisi khamar dari sungai yang mengalir.'),
(3834, '037:046', '68', 37, 46, '(Warnanya) putih bersih, sedap rasanya bagi orang-orang yang minum.'),
(3835, '037:047', '68', 37, 47, 'Tidak ada dalam khamar itu alkohol dan mereka tiada mabuk karenanya.'),
(3836, '037:048', '68', 37, 48, 'Di sisi mereka ada bidadari-bidadari yang tidak liar pandangannya dan jelita matanya,'),
(3837, '037:049', '68', 37, 49, 'seakan-akan mereka adalah telur (burung unta) yang tersimpan dengan baik.'),
(3838, '037:050', '68', 37, 50, 'Lalu sebahagian mereka menghadap kepada sebahagian yang lain sambil bercakap-cakap.'),
(3839, '037:051', '68', 37, 51, 'Berkatalah salah seorang di antara mereka: \"Sesungguhnya aku dahulu (di dunia) mempunyai seorang teman,'),
(3840, '037:052', '68', 37, 52, 'yang berkata: \"Apakah kamu sungguh-sungguh termasuk orang-orang yang membenarkan (hari berbangkit)?'),
(3841, '037:053', '68', 37, 53, 'Apakah bila kita telah mati dan kita telah menjadi tanah dan tulang belulang, apakah sesungguhnya kita benar-benar (akan dibangkitkan) untuk diberi pembalasan?\"'),
(3842, '037:054', '68', 37, 54, 'Berkata pulalah ia: \"Maukah kamu meninjau (temanku itu)?\"'),
(3843, '037:055', '68', 37, 55, 'Maka ia meninjaunya, lalu dia melihat temannya itu di tengah-tengah neraka menyala-nyala.'),
(3844, '037:056', '68', 37, 56, 'Ia berkata (pula): \"Demi Allah, sesungguhnya kamu benar-benar hampir mencelakakanku,'),
(3845, '037:057', '68', 37, 57, 'jikalau tidaklah karena nikmat Tuhanku pastilah aku termasuk orang-orang yang diseret (ke neraka).'),
(3846, '037:058', '68', 37, 58, 'Maka apakah kita tidak akan mati?,'),
(3847, '037:059', '68', 37, 59, 'melainkan hanya kematian kita yang pertama saja (di dunia), dan kita tidak akan disiksa (di akhirat ini)?'),
(3848, '037:060', '68', 37, 60, 'Sesungguhnya ini benar-benar kemenangan yang besar.'),
(3849, '037:061', '68', 37, 61, 'Untuk kemenangan serupa ini hendaklah berusaha orang-orang yang bekerja\"'),
(3850, '037:062', '68', 37, 62, '(Makanan surga) itukah hidangan yang lebih baik ataukah pohon zaqqum.'),
(3851, '037:063', '68', 37, 63, 'Sesungguhnya Kami menjadikan pohon zaqqum itu sebagai siksaan bagi orang-orang yang zalim.'),
(3852, '037:064', '68', 37, 64, 'Sesungguhnya dia adalah sebatang pohon yang ke luar dan dasar neraka yang menyala.'),
(3853, '037:065', '68', 37, 65, 'mayangnya seperti kepala syaitan-syaitan.'),
(3854, '037:066', '68', 37, 66, 'Maka sesungguhnya mereka benar-benar memakan sebagian dari buah pohon itu, maka mereka memenuhi perutnya dengan buah zaqqum itu.'),
(3855, '037:067', '68', 37, 67, 'Kemudian sesudah makan buah pohon zaqqum itu pasti mereka mendapat minuman yang bercampur dengan air yang sangat panas.'),
(3856, '037:068', '68', 37, 68, 'Kemudian sesungguhnya tempat kembali mereka benar-benar ke neraka Jahim.'),
(3857, '037:069', '68', 37, 69, 'Karena sesungguhnya mereka mendapati bapak-bapak mereka dalam Keadaaan sesat.'),
(3858, '037:070', '68', 37, 70, 'Lalu mereka sangat tergesa-gesa mengikuti jejak orang-orang tua mereka itu.'),
(3859, '037:071', '68', 37, 71, 'Dan sesungguhnya telah sesat sebelum mereka (Quraisy) sebagian besar dari orang-orang yang dahulu,'),
(3860, '037:072', '68', 37, 72, 'dan sesungguhnya telah Kami utus pemberi-pemberi peringatan (rasul-rasul) di kalangan mereka.'),
(3861, '037:073', '68', 37, 73, 'Maka perhatikanlah bagaimana kesudahan orang-orang yang diberi peringatan itu.'),
(3862, '037:074', '68', 37, 74, 'Tetapi hamba-hamba Allah yang bersihkan (dari dosa tidak akan diazab).'),
(3863, '037:075', '68', 37, 75, 'Sesungguhnya Nuh telah menyeru Kami: maka sesungguhnya sebaik-baik yang memperkenankan (adalah Kami).'),
(3864, '037:076', '68', 37, 76, 'Dan Kami telah menyelamatkannya dan pengikutnya dari bencana yang besar.'),
(3865, '037:077', '68', 37, 77, 'Dan Kami jadikan anak cucunya orang-orang yang melanjutkan keturunan.'),
(3866, '037:078', '68', 37, 78, 'Dan Kami abadikan untuk Nuh itu (pujian yang baik) di kalangan orang-orang yang datang kemudian;'),
(3867, '037:079', '68', 37, 79, '\"Kesejahteraan dilimpahkan atas Nuh di seluruh alam\".'),
(3868, '037:080', '68', 37, 80, 'Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3869, '037:081', '68', 37, 81, 'Sesungguhnya dia termasuk di antara hamba-hamba Kami yang beriman.'),
(3870, '037:082', '68', 37, 82, 'Kemudian Kami tenggelamkan orang-orang yang lain.'),
(3871, '037:083', '68', 37, 83, 'Dan sesungguhnya Ibrahim benar-benar termasuk golongannya (Nuh).'),
(3872, '037:084', '68', 37, 84, '(lngatlah) ketika ia datang kepada Tuhannya dengan hati yang suci:'),
(3873, '037:085', '68', 37, 85, '(Ingatlah) ketika ia berkata kepada bapaknya dan kaumnya: \"Apakah yang kamu sembah itu?'),
(3874, '037:086', '68', 37, 86, 'Apakah kamu menghendaki sembahan-sembahan selain Allah dengan jalan berbohong?'),
(3875, '037:087', '68', 37, 87, 'Maka apakah anggapanmu terhadap Tuhan semesta alam?\"'),
(3876, '037:088', '68', 37, 88, 'Lalu ia memandang sekali pandang ke bintang-bintang.'),
(3877, '037:089', '68', 37, 89, 'Kemudian ia berkata: \"Sesungguhnya aku sakit\".'),
(3878, '037:090', '68', 37, 90, 'Lalu mereka berpaling daripadanya dengan membelakang.'),
(3879, '037:091', '68', 37, 91, 'Kemudian ia pergi dengan diam-diam kepada berhala-berhala mereka; lalu ia berkata: \"Apakah kamu tidak makan?'),
(3880, '037:092', '68', 37, 92, 'Kenapa kamu tidak menjawab?\"'),
(3881, '037:093', '68', 37, 93, 'Lalu dihadapinya berhala-berhala itu sambil memukulnya dengan tangan kanannya (dengan kuat).'),
(3882, '037:094', '68', 37, 94, 'Kemudian kaumnya datang kepadanya dengan bergegas.'),
(3883, '037:095', '68', 37, 95, 'Ibrahim berkata: \"Apakah kamu menyembah patung-patung yang kamu pahat itu?'),
(3884, '037:096', '68', 37, 96, 'Padahal Allah-lah yang menciptakan kamu dan apa yang kamu perbuat itu\".'),
(3885, '037:097', '68', 37, 97, 'Mereka berkata: \"Dirikanlah suatu bangunan untuk (membakar) Ibrahim;lalu lemparkanlah dia ke dalam api yang menyala-nyala itu\".'),
(3886, '037:098', '68', 37, 98, 'Mereka hendak melakukan tipu muslihat kepadanya, maka Kami jadikan mereka orang-orang yang hina.'),
(3887, '037:099', '68', 37, 99, 'Dan Ibrahim berkata: \"Sesungguhnya aku pergi menghadap kepada Tuhanku, dan Dia akan memberi petunjuk kepadaku.'),
(3888, '037:100', '68', 37, 100, 'Ya Tuhanku, anugrahkanlah kepadaku (seorang anak) yang termasuk orang-orang yang saleh.'),
(3889, '037:101', '68', 37, 101, 'Maka Kami beri dia khabar gembira dengan seorang anak yang amat sabar.'),
(3890, '037:102', '68', 37, 102, 'Maka tatkala anak itu sampai (pada umur sanggup) berusaha bersama-sama Ibrahim, Ibrahim berkata: \"Hai anakku sesungguhnya aku melihat dalam mimpi bahwa aku menyembelihmu. Maka fikirkanlah apa pendapatmu!\" Ia menjawab: \"Hai bapakku, kerjakanlah apa yang diperintahkan kepadamu; insya Allah kamu akan mendapatiku termasuk orang-orang yang sabar\".'),
(3891, '037:103', '68', 37, 103, 'Tatkala keduanya telah berserah diri dan Ibrahim membaringkan anaknya atas pelipis(nya), (nyatalah kesabaran keduanya).'),
(3892, '037:104', '68', 37, 104, 'Dan Kami panggillah dia: \"Hai Ibrahim,'),
(3893, '037:105', '68', 37, 105, 'sesungguhnya kamu telah membenarkan mimpi itu sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3894, '037:106', '68', 37, 106, 'Sesungguhnya ini benar-benar suatu ujian yang nyata.'),
(3895, '037:107', '68', 37, 107, 'Dan Kami tebus anak itu dengan seekor sembelihan yang besar.'),
(3896, '037:108', '68', 37, 108, 'Kami abadikan untuk Ibrahim itu (pujian yang baik) di kalangan orang-orang yang datang kemudian,'),
(3897, '037:109', '68', 37, 109, '(yaitu)\"Kesejahteraan dilimpahkan atas Ibrahim\".'),
(3898, '037:110', '68', 37, 110, 'Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3899, '037:111', '68', 37, 111, 'Sesungguhnya ia termasuk hamba-hamba Kami yang beriman.'),
(3900, '037:112', '68', 37, 112, 'Dan Kami beri dia kabar gembira dengan (kelahiran) Ishaq seorang nabi yang termasuk orang-orang yang saleh.'),
(3901, '037:113', '68', 37, 113, 'Kami limpahkan keberkatan atasnya dan atas Ishaq. Dan diantara anak cucunya ada yang berbuat baik dan ada (pula) yang Zalim terhadap dirinya sendiri dengan nyata.'),
(3902, '037:114', '68', 37, 114, 'Dan sesungguhnya Kami telah melimpahkan nikmat atas Musa dan Harun.'),
(3903, '037:115', '68', 37, 115, 'Dan Kami selamatkan keduanya dan kaumnya dari bencana yang besar.'),
(3904, '037:116', '68', 37, 116, 'Dan Kami tolong mereka, maka jadilah mereka orang-orang yang menang.'),
(3905, '037:117', '68', 37, 117, 'Dan Kami berikan kepada keduanya kitab yang sangat jelas.'),
(3906, '037:118', '68', 37, 118, 'Dan Kami tunjuki keduanya ke jalan yang lurus.'),
(3907, '037:119', '68', 37, 119, 'Dan Kami abadikan untuk keduanya (pujian yang baik) dikalangan orang-orang yang datang kemudian;'),
(3908, '037:120', '68', 37, 120, '(yaitu): \"Kesejahteraan dilimpahkan atas Musa dan Harun\".'),
(3909, '037:121', '68', 37, 121, 'Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3910, '037:122', '68', 37, 122, 'Sesungguhnya keduanya termasuk hamba-hamba Kami yang beriman.'),
(3911, '037:123', '68', 37, 123, 'Dan sesungguhnya Ilyas benar-benar termasuk salah seorang rasul-rasul.'),
(3912, '037:124', '68', 37, 124, '(ingatlah) ketika ia berkata kepada kaumnya: \"Mengapa kamu tidak bertakwa?'),
(3913, '037:125', '68', 37, 125, 'Patutkah kamu menyembah Ba´l dan kamu tinggalkan sebaik-baik Pencipta,'),
(3914, '037:126', '68', 37, 126, '(yaitu) Allah Tuhanmu dan Tuhan bapak-bapakmu yang terdahulu?\"'),
(3915, '037:127', '68', 37, 127, 'Maka mereka mendustakannya, karena itu mereka akan diseret (ke neraka),'),
(3916, '037:128', '68', 37, 128, 'kecuali hamba-hamba Allah yang dibersihkan (dari dosa).'),
(3917, '037:129', '68', 37, 129, 'Dan Kami abadikan untuk Ilyas (pujian yang baik) di kalangan orang-orang yang datang kemudian.'),
(3918, '037:130', '68', 37, 130, '(yaitu): \"Kesejahteraan dilimpahkan atas Ilyas?\"'),
(3919, '037:131', '68', 37, 131, 'Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(3920, '037:132', '68', 37, 132, 'Sesungguhnya dia termasuk hamba-hamba Kami yang beriman.'),
(3921, '037:133', '68', 37, 133, 'Sesungguhnya Luth benar-benar salah seorang rasul.'),
(3922, '037:134', '68', 37, 134, '(Ingatlah) ketika Kami selamatkan dia dan keluarganya (pengikut-pengikutnya) semua,'),
(3923, '037:135', '68', 37, 135, 'kecuali seorang perempuan tua (isterinya yang berada) bersama-sama orang yang tinggal.'),
(3924, '037:136', '68', 37, 136, 'Kemudian Kami binasakan orang-orang yang lain.'),
(3925, '037:137', '68', 37, 137, 'Dan sesungguhnya kamu (hai penduduk Mekah) benar-benar akan melalui (bekas-bekas) mereka di waktu pagi,'),
(3926, '037:138', '68', 37, 138, 'dan di waktu malam. Maka apakah kamu tidak memikirkan?'),
(3927, '037:139', '68', 37, 139, 'Sesungguhnya Yunus benar-benar salah seorang rasul,'),
(3928, '037:140', '68', 37, 140, '(ingatlah) ketika ia lari, ke kapal yang penuh muatan,'),
(3929, '037:141', '68', 37, 141, 'kemudian ia ikut berundi lalu dia termasuk orang-orang yang kalah dalam undian.'),
(3930, '037:142', '68', 37, 142, 'Maka ia ditelan oleh ikan besar dalam keadaan tercela.'),
(3931, '037:143', '68', 37, 143, 'Maka kalau sekiranya dia tidak termasuk orang-orang yang banyak mengingat Allah,'),
(3932, '037:144', '68', 37, 144, 'niscaya ia akan tetap tinggal di perut ikan itu sampai hari berbangkit.'),
(3933, '037:145', '68', 37, 145, 'Kemudian Kami lemparkan dia ke daerah yang tandus, sedang ia dalam keadaan sakit.'),
(3934, '037:146', '68', 37, 146, 'Dan Kami tumbuhkan untuk dia sebatang pohon dari jenis labu.'),
(3935, '037:147', '68', 37, 147, 'Dan Kami utus dia kepada seratus ribu orang atau lebih.'),
(3936, '037:148', '68', 37, 148, 'Lalu mereka beriman, karena itu Kami anugerahkan kenikmatan hidup kepada mereka hingga waktu yang tertentu.'),
(3937, '037:149', '68', 37, 149, 'Tanyakanlah (ya Muhammad) kepada mereka (orang-orang kafir Mekah): \"Apakah untuk Tuhanmu anak-anak perempuan dan untuk mereka anak laki-laki,'),
(3938, '037:150', '68', 37, 150, 'atau apakah Kami menciptakan malaikat-malaikat berupa perempuan dan mereka menyaksikan(nya)?'),
(3939, '037:151', '68', 37, 151, 'Ketahuilah bahwa sesungguhnya mereka dengan kebohongannya benar-benar mengatakan:'),
(3940, '037:152', '68', 37, 152, '\"Allah beranak\". Dan sesungguhnya mereka benar-benar orang yang berdusta.'),
(3941, '037:153', '68', 37, 153, 'Apakah Tuhan memilih (mengutamakan) anak-anak perempuan daripada anak laki-laki?'),
(3942, '037:154', '68', 37, 154, 'Apakah yang terjadi padamu? Bagaimana (caranya) kamu menetapkan?'),
(3943, '037:155', '68', 37, 155, 'Maka apakah kamu tidak memikirkan?'),
(3944, '037:156', '68', 37, 156, 'Atau apakah kamu mempunyai bukti yang nyata?'),
(3945, '037:157', '68', 37, 157, 'Maka bawalah kitabmu jika kamu memang orang-orang yang benar.'),
(3946, '037:158', '68', 37, 158, 'Dan mereka adakan (hubungan) nasab antara Allah dan antara jin. Dan sesungguhnya jin mengetahui bahwa mereka benar-benar akan diseret (ke neraka),'),
(3947, '037:159', '68', 37, 159, 'Maha Suci Allah dari apa yang mereka sifatkan,'),
(3948, '037:160', '68', 37, 160, 'Kecuali hamba-hamba Allah yang dibersihkan dari (dosa).'),
(3949, '037:161', '68', 37, 161, 'Maka sesungguhnya kamu dan apa-apa yang kamu sembah itu,'),
(3950, '037:162', '68', 37, 162, 'Sekali-kali tidak dapat menyesatkan (seseorang) terhadap Allah,'),
(3951, '037:163', '68', 37, 163, 'kecuali orang-orang yang akan masuk neraka yang menyala.'),
(3952, '037:164', '68', 37, 164, 'Tiada seorangpun di antara kami (malaikat) melainkan mempunyai kedudukan yang tertentu,'),
(3953, '037:165', '68', 37, 165, 'dan sesungguhnya kami benar-benar bershaf-shaf (dalam menunaikan perintah Allah).'),
(3954, '037:166', '68', 37, 166, 'Dan sesungguhnya kami benar-benar bertasbih (kepada Allah).'),
(3955, '037:167', '68', 37, 167, 'Sesungguhnya mereka benar-benar akan berkata:'),
(3956, '037:168', '68', 37, 168, '\"Kalau sekiranya di sksi kami ada sebuah kitab dari (kitab-kitab yang diturunkan) kepada orang-orang dahulu,'),
(3957, '037:169', '68', 37, 169, 'benar-benar kami akan jadi hamba Allah yang dibersihkan (dari dosa)\".'),
(3958, '037:170', '68', 37, 170, 'Tetapi mereka mengingkarinya (Al Quran); maka kelak mereka akan mengetahui (akibat keingkarannya itu).'),
(3959, '037:171', '68', 37, 171, 'Dan sesungguhnya telah tetap janji Kami kepada hamba-hamba Kami yang menjadi rasul,'),
(3960, '037:172', '68', 37, 172, '(yaitu) sesungguhnya mereka itulah yang pasti mendapat pertolongan.'),
(3961, '037:173', '68', 37, 173, 'Dan sesungguhnya tentara Kami itulah yang pasti menang,'),
(3962, '037:174', '68', 37, 174, 'Maka berpalinglah kamu (Muhammad) dari mereka sampai suatu ketika.'),
(3963, '037:175', '68', 37, 175, 'Dan lihatlah mereka, maka kelak mereka akan melihat (azab itu).'),
(3964, '037:176', '68', 37, 176, 'Maka apakah mereka meminta supaya siksa Kami disegerakan?'),
(3965, '037:177', '68', 37, 177, 'Maka apabila siksaan itu turun dihalaman mereka, maka amat buruklah pagi hari yang dialami oleh orang-orang yang diperingatkan itu.'),
(3966, '037:178', '68', 37, 178, 'Dan berpalinglah kamu dari mereka hingga suatu ketika.'),
(3967, '037:179', '68', 37, 179, 'Dan lihatlah, maka kelak mereka juga akan melihat.'),
(3968, '037:180', '68', 37, 180, 'Maha Suci Tuhanmu Yang mempunyai keperkasaan dari apa yang mereka katakan.'),
(3969, '037:181', '68', 37, 181, 'Dan kesejahteraan dilimpahkan atas para rasul.'),
(3970, '037:182', '68', 37, 182, 'Dan segala puji bagi Allah Tuhan seru sekalian alam.'),
(3971, '038:001', '68', 38, 1, 'Shaad, demi Al Quran yang mempunyai keagungan.'),
(3972, '038:002', '68', 38, 2, 'Sebenarnya orang-orang kafir itu (berada) dalam kesombongan dan permusuhan yang sengit.'),
(3973, '038:003', '68', 38, 3, 'Betapa banyaknya umat sebelum mereka yang telah Kami binasakan, lalu mereka meminta tolong padahal (waktu itu) bukanlah saat untuk lari melepaskan diri.'),
(3974, '038:004', '68', 38, 4, 'Dan mereka heran karena mereka kedatangan seorang pemberi peringatan (rasul) dari kalangan mereka; dan orang-orang kafir berkata: \"Ini adalah seorang ahli sihir yang banyak berdusta\".'),
(3975, '038:005', '68', 38, 5, 'Mengapa ia menjadikan tuhan-tuhan itu Tuhan Yang Satu saja? Sesungguhnya ini benar-benar suatu hal yang sangat mengherankan.'),
(3976, '038:006', '68', 38, 6, 'Dan pergilah pemimpin-pemimpin mereka (seraya berkata): \"Pergilah kamu dan tetaplah (menyembah) tuhan-tuhanmu, sesungguhnya ini benar-benar suatu hal yang dikehendaki.'),
(3977, '038:007', '68', 38, 7, 'Kami tidak pernah mendengar hal ini dalam agama yang terakhir; ini (mengesakan Allah), tidak lain hanyalah (dusta) yang diada-adakan,'),
(3978, '038:008', '68', 38, 8, 'mengapa Al Quran itu diturunkan kepadanya di antara kita?\" Sebenarnya mereka ragu-ragu terhadap Al Quran-Ku, dan sebenarnya mereka belum merasakan azab-Ku.'),
(3979, '038:009', '68', 38, 9, 'Atau apakah mereka itu mempunyai perbendaharaan rahmat Tuhanmu Yang Maha Perkasa lagi Maha Pemberi?'),
(3980, '038:010', '68', 38, 10, 'Atau apakah bagi mereka kerajaan langit dan bumi dan yang ada di antara keduanya? (Jika ada), maka hendaklah mereka menaiki tangga-tangga (ke langit).'),
(3981, '038:011', '68', 38, 11, 'Suatu tentara yang besar yang berada disana dari golongan-golongan yang berserikat, pasti akan dikalahkan.'),
(3982, '038:012', '68', 38, 12, 'Telah mendustakan (rasul-rasul pula) sebelum mereka itu kaum Nuh, ´Aad, Fir´aun yang mempunyai tentara yang banyak,'),
(3983, '038:013', '68', 38, 13, 'dan Tsamud, kaum Luth dan penduduk Aikah. Mereka itulah golongan-golongan yang bersekutu (menentang rasul-rasul).'),
(3984, '038:014', '68', 38, 14, 'Semua mereka itu tidak lain hanyalah mendustakan rasul-rasul, maka pastilah (bagi mereka) azab-Ku.'),
(3985, '038:015', '68', 38, 15, 'Tidaklah yang mereka tunggu melainkan hanya satu teriakan saja yang tidak ada baginya saat berselang.'),
(3986, '038:016', '68', 38, 16, 'Dan mereka berkata: \"Ya Tuhan kami cepatkanlah untuk kami azab yang diperuntukkan bagi kami sebelum hari berhisab\".'),
(3987, '038:017', '68', 38, 17, 'Bersabarlah atas segala apa yang mereka katakan; dan ingatlah hamba Kami Daud yang mempunyai kekuatan; sesungguhnya dia amat taat (kepada Tuhan).'),
(3988, '038:018', '68', 38, 18, 'Sesungguhnya Kami menundukkan gunung-gunung untuk bertasbih bersama dia (Daud) di waktu petang dan pagi,'),
(3989, '038:019', '68', 38, 19, 'dan (Kami tundukkan pula) burung-burung dalam keadaan terkumpul. Masing-masingnya amat taat kepada Allah.'),
(3990, '038:020', '68', 38, 20, 'Dan Kami kuatkan kerajaannya dan Kami berikan kepadanya hikmahdan kebijaksanaan dalam menyelesaikan perselisihan.'),
(3991, '038:021', '68', 38, 21, 'Dan adakah sampai kepadamu berita orang-orang yang berperkara ketika mereka memanjat pagar?'),
(3992, '038:022', '68', 38, 22, 'Ketika mereka masuk (menemui) Daud lalu ia terkejut karena kedatangan) mereka. Mereka berkata: \"Janganlah kamu merasa takut; (kami) adalah dua orang yang berperkara yang salah seorang dari kami berbuat zalim kepada yang lain; maka berilah keputusan antara kami dengan adil dan janganlah kamu menyimpang dari kebenaran dan tunjukilah kami ke jalan yang lurus.'),
(3993, '038:023', '68', 38, 23, 'Sesungguhnya saudaraku ini mempunyai sembilan puluh sembilan ekor kambing betina dan aku mempunyai seekor saja. Maka dia berkata: \"Serahkanlah kambingmu itu kepadaku dan dia mengalahkan aku dalam perdebatan\".'),
(3994, '038:024', '68', 38, 24, 'Daud berkata: \"Sesungguhnya dia telah berbuat zalim kepadamu dengan meminta kambingmu itu untuk ditambahkan kepada kambingnya. Dan sesungguhnya kebanyakan dari orang-orang yang berserikat itu sebahagian mereka berbuat zalim kepada sebahagian yang lain, kecuali orang-orang yang beriman dan mengerjakan amal yang saleh; dan amat sedikitlah mereka ini\". Dan Daud mengetahui bahwa Kami mengujinya; maka ia meminta ampun kepada Tuhannya lalu menyungkur sujud dan bertaubat.'),
(3995, '038:025', '68', 38, 25, 'Maka Kami ampuni baginya kesalahannya itu. Dan sesungguhnya dia mempunyai kedudukan dekat pada sisi Kami dan tempat kembali yang baik.'),
(3996, '038:026', '68', 38, 26, 'Hai Daud, sesungguhnya Kami menjadikan kamu khalifah (penguasa) di muka bumi, maka berilah keputusan (perkara) di antara manusia dengan adil dan janganlah kamu mengikuti hawa nafsu, karena ia akan menyesatkan kamu dari jalan Allah. Sesungguhnya orang-orang yang sesat darin jalan Allah akan mendapat azab yang berat, karena mereka melupakan hari perhitungan.'),
(3997, '038:027', '68', 38, 27, 'Dan Kami tidak menciptakan langit dan bumi dan apa yang ada antara keduanya tanpa hikmah. Yang demikian itu adalah anggapan orang-orang kafir, maka celakalah orang-orang kafir itu karena mereka akan masuk neraka.'),
(3998, '038:028', '68', 38, 28, 'Patutkah Kami menganggap orang-orang yang beriman dan mengerjakan amal yang saleh sama dengan orang-orang yang berbuat kerusakan di muka bumi? Patutkah (pula) Kami menganggap orang-orang yang bertakwa sama dengan orang-orang yang berbuat maksiat?'),
(3999, '038:029', '68', 38, 29, 'Ini adalah sebuah kitab yang Kami turunkan kepadamu penuh dengan berkah supaya mereka memperhatikan ayat-ayatnya dan supaya mendapat pelajaran orang-orang yang mempunyai fikiran.'),
(4000, '038:030', '68', 38, 30, 'Dan Kami karuniakan kepada Daud, Sulaiman, dia adalah sebaik-baik hamba. Sesungguhnya dia amat taat (kepada Tuhannya),'),
(4001, '038:031', '68', 38, 31, '(ingatlah) ketika dipertunjukkan kepadanya kuda-kuda yang tenang di waktu berhenti dan cepat waktu berlari pada waktu sore,'),
(4002, '038:032', '68', 38, 32, 'maka ia berkata: \"Sesungguhnya aku menyukai kesenangan terhadap barang yang baik (kuda) sehingga aku lalai mengingat Tuhanku sampai kuda itu hilang dari pandangan\".'),
(4003, '038:033', '68', 38, 33, '\"Bawalah kuda-kuda itu kembali kepadaku\". Lalu ia potong kaki dan leher kuda itu.'),
(4004, '038:034', '68', 38, 34, 'Dan sesungguhnya Kami telah menguji Sulaiman dan Kami jadikan (dia) tergeletak di atas kursinya sebagai tubuh (yang lemah karena sakit), kemudian ia bertaubat.'),
(4005, '038:035', '68', 38, 35, 'Ia berkata: \"Ya Tuhanku, ampunilah aku dan anugerahkanlah kepadaku kerajaan yang tidak dimiliki oleh seorang juapun sesudahku, sesungguhnya Engkaulah Yang Maha Pemberi\".'),
(4006, '038:036', '68', 38, 36, 'Kemudian Kami tundukkan kepadanya angin yang berhembus dengan baik menurut ke mana saja yang dikehendakinya,'),
(4007, '038:037', '68', 38, 37, 'dan (Kami tundukkan pula kepadanya) syaitan-syaitan semuanya ahli bangunan dan penyelam,'),
(4008, '038:038', '68', 38, 38, 'dan syaitan yang lain yang terikat dalam belenggu.'),
(4009, '038:039', '68', 38, 39, 'Inilah anugerah Kami; maka berikanlah (kepada orang lain) atau tahanlah (untuk dirimu sendiri) dengan tiada pertanggungan jawab.'),
(4010, '038:040', '68', 38, 40, 'Dan sesungguhnya dia mempunyai kedudukan yang dekat pada sisi Kami dan tempat kembali yang baik.'),
(4011, '038:041', '68', 38, 41, 'Dan ingatlah akan hamba Kami Ayyub ketika ia menyeru Tuhan-nya: \"Sesungguhnya aku diganggu syaitan dengan kepayahan dan siksaan\".'),
(4012, '038:042', '68', 38, 42, '(Allah berfirman): \"Hantamkanlah kakimu; inilah air yang sejuk untuk mandi dan untuk minum\".'),
(4013, '038:043', '68', 38, 43, 'Dan Kami anugerahi dia (dengan mengumpulkan kembali) keluarganya dan (Kami tambahkan) kepada mereka sebanyak mereka pula sebagai rahmat dari Kami dan pelajaran bagi orang-orang yang mempunyai fikiran.'),
(4014, '038:044', '68', 38, 44, 'Dan ambillah dengan tanganmu seikat (rumput), maka pukullah dengan itu dan janganlah kamu melanggar sumpah. Sesungguhnya Kami dapati dia (Ayyub) seorang yang sabar. Dialah sebaik-baik hamba. Sesungguhnya dia amat taat (kepada Tuhan-nya).'),
(4015, '038:045', '68', 38, 45, 'Dan ingatlah hamba-hamba Kami: Ibrahim, Ishaq dan Ya´qub yang mempunyai perbuatan-perbuatan yang besar dan ilmu-ilmu yang tinggi.'),
(4016, '038:046', '68', 38, 46, 'Sesungguhnya Kami telah mensucikan mereka dengan (menganugerahkan kepada mereka) akhlak yang tinggi yaitu selalu mengingatkan (manusia) kepada negeri akhirat.'),
(4017, '038:047', '68', 38, 47, 'Dan sesungguhnya mereka pada sisi Kami benar-benar termasuk orang-orang pilihan yang paling baik.'),
(4018, '038:048', '68', 38, 48, 'Dan ingatlah akan Ismail, Ilyasa´ dan Zulkifli. Semuanya termasuk orang-orang yang paling baik.'),
(4019, '038:049', '68', 38, 49, 'Ini adalah kehormatan (bagi mereka). Dan sesungguhnya bagi orang-orang yang bertakwa benar-benar (disediakan) tempat kembali yang baik,'),
(4020, '038:050', '68', 38, 50, '(yaitu) surga ´Adn yang pintu-pintunya terbuka bagi mereka,'),
(4021, '038:051', '68', 38, 51, 'di dalamnya mereka bertelekan (diatas dipan-dipan) sambil meminta buah-buahan yang banyak dan minuman di surga itu.'),
(4022, '038:052', '68', 38, 52, 'Dan pada sisi mereka (ada bidadari-bidadari) yang tidak liar pandangannya dan sebaya umurnya.'),
(4023, '038:053', '68', 38, 53, 'Inilah apa yang dijanjikan kepadamu pada hari berhisab.'),
(4024, '038:054', '68', 38, 54, 'Sesungguhnya ini adalah benar-benar rezeki dari Kami yang tiada habis-habisnya.'),
(4025, '038:055', '68', 38, 55, 'Beginilah (keadaan mereka). Dan sesungguhnya bagi orang-orang yang durhaka benar-benar (disediakan) tempat kembali yang buruk,'),
(4026, '038:056', '68', 38, 56, '(yaitu) neraka Jahannam, yang mereka masuk ke dalamnya; maka amat buruklah Jahannam itu sebagai tempat tinggal.'),
(4027, '038:057', '68', 38, 57, 'Inilah (azab neraka), biarlah mereka merasakannya, (minuman mereka) air yang sangat panas dan air yang sangat dingin.'),
(4028, '038:058', '68', 38, 58, 'Dan azab yang lain yang serupa itu berbagai macam.'),
(4029, '038:059', '68', 38, 59, '(Dikatakan kepada mereka): \"Ini adalah suatu rombongan (pengikut-pengikutmu) yang masuk berdesak-desak bersama kamu (ke neraka)\". (Berkata pemimpin-pemimpin mereka yang durhaka): \"Tiadalah ucapan selamat datang kepada mereka karena sesungguhnya mereka akan masuk neraka\".'),
(4030, '038:060', '68', 38, 60, 'Pengikut-pengikut mereka menjawab: \"Sebenarnya kamulah. Tiada ucapan selamat datang bagimu, karena kamulah yang menjerumuskan kami ke dalam azab, maka amat buruklah Jahannam itu sebagai tempat menetap\".'),
(4031, '038:061', '68', 38, 61, 'Mereka berkata (lagi): \"Ya Tuhan kami; barang siapa yang menjerumuskan kami ke dalam azab ini maka tambahkanlah azab kepadanya dengan berlipat ganda di dalam neraka\".'),
(4032, '038:062', '68', 38, 62, 'Dan (orang-orang durhaka) berkata: \"Mengapa kami tidak melihat orang-orang yang dahulu (di dunia) kami anggap sebagai orang-orang yang jahat (hina).'),
(4033, '038:063', '68', 38, 63, 'Apakah kami dahulu menjadikan mereka olok-olokan, ataukah karena mata kami tidak melihat mereka?\"'),
(4034, '038:064', '68', 38, 64, 'Sesungguhnya yang demikian itu pasti terjadi, (yaitu) pertengkaran penghuni neraka.'),
(4035, '038:065', '68', 38, 65, 'Katakanlah (ya Muhammad): \"Sesungguhnya aku hanya seorang pemberi peringatan, dan sekali-kali tidak ada Tuhan selain Allah Yang Maha Esa dan Maha Mengalahkan.'),
(4036, '038:066', '68', 38, 66, 'Tuhan langit dan bumi dan apa yang ada di antara keduanya Yang Maha Perkasa lagi Maha Pengampun.'),
(4037, '038:067', '68', 38, 67, 'Katakanlah: \"Berita itu adalah berita yang besar,'),
(4038, '038:068', '68', 38, 68, 'yang kamu berpaling daripadanya.'),
(4039, '038:069', '68', 38, 69, 'Aku tiada mempunyai pengetahuan sedikitpun tentang al mala´ul a´la (malaikat) itu ketika mereka berbantah-bantahan.'),
(4040, '038:070', '68', 38, 70, 'Tidak diwahyukan kepadaku, melainkan bahwa sesungguhnya aku hanyalah seorang pemberi peringatan yang nyata\".'),
(4041, '038:071', '68', 38, 71, '(Ingatlah) ketika Tuhanmu berfirman kepada malaikat: \"Sesungguhnya Aku akan menciptakan manusia dari tanah\".'),
(4042, '038:072', '68', 38, 72, 'Maka apabila telah Kusempurnakan kejadiannya dan Kutiupkan kepadanya roh (ciptaan)Ku; maka hendaklah kamu tersungkur dengan bersujud kepadanya\".'),
(4043, '038:073', '68', 38, 73, 'Lalu seluruh malaikat-malaikat itu bersujud semuanya,'),
(4044, '038:074', '68', 38, 74, 'kecuali iblis; dia menyombongkan diri dan adalah dia termasuk orang-orang yang kafir.'),
(4045, '038:075', '68', 38, 75, 'Allah berfirman: \"Hai iblis, apakah yang menghalangi kamu sujud kepada yang telah Ku-ciptakan dengan kedua tangan-Ku. Apakah kamu menyombongkan diri ataukah kamu (merasa) termasuk orang-orang yang (lebih) tinggi?\".'),
(4046, '038:076', '68', 38, 76, 'Iblis berkata: \"Aku lebih baik daripadanya, karena Engkau ciptakan aku dari api, sedangkan dia Engkau ciptakan dari tanah\".'),
(4047, '038:077', '68', 38, 77, 'Allah berfirman: \"Maka keluarlah kamu dari surga; sesungguhnya kamu adalah orang yang terkutuk,'),
(4048, '038:078', '68', 38, 78, 'Sesungguhnya kutukan-Ku tetap atasmu sampai hari pembalasan\".'),
(4049, '038:079', '68', 38, 79, 'Iblis berkata: \"Ya Tuhanku, beri tangguhlah aku sampai hari mereka dibangkitkan\".'),
(4050, '038:080', '68', 38, 80, 'Allah berfirman: \"Sesungguhnya kamu termasuk orang-orang yang diberi tangguh,'),
(4051, '038:081', '68', 38, 81, 'sampai kepada hari yang telah ditentukan waktunya (hari Kiamat)\".'),
(4052, '038:082', '68', 38, 82, 'Iblis menjawab: \"Demi kekuasaan Engkau aku akan menyesatkan mereka semuanya,'),
(4053, '038:083', '68', 38, 83, 'kecuali hamba-hamba-Mu yang mukhlis di antara mereka.'),
(4054, '038:084', '68', 38, 84, 'Allah berfirman: \"Maka yang benar (adalah sumpah-Ku) dan hanya kebenaran itulah yang Ku-katakan\".'),
(4055, '038:085', '68', 38, 85, 'Sesungguhnya Aku pasti akan memenuhi neraka Jahannam dengan jenis kamu dan dengan orang-orang yang mengikuti kamu di antara mereka kesemuanya.'),
(4056, '038:086', '68', 38, 86, 'Katakanlah (hai Muhammad): \"Aku tidak meminta upah sedikitpun padamu atas dakwahku dan bukanlah aku termasuk orang-orang yang mengada-adakan.'),
(4057, '038:087', '68', 38, 87, 'Al Quran ini tidak lain hanyalah peringatan bagi semesta alam.'),
(4058, '038:088', '68', 38, 88, 'Dan sesungguhnya kamu akan mengetahui (kebenaran) berita Al Quran setelah beberapa waktu lagi.'),
(4059, '039:001', '68', 39, 1, 'Kitab (Al Quran ini) diturunkan oleh Allah Yang Maha Perkasa lagi Maha Bijaksana.'),
(4060, '039:002', '68', 39, 2, 'Sesunguhnya Kami menurunkan kepadamu Kitab (Al Quran) dengan (membawa) kebenaran. Maka sembahlah Allah dengan memurnikan ketaatan kepada-Nya.'),
(4061, '039:003', '68', 39, 3, 'Ingatlah, hanya kepunyaan Allah-lah agama yang bersih (dari syirik). Dan orang-orang yang mengambil pelindung selain Allah (berkata): \"Kami tidak menyembah mereka melainkan supaya mereka mendekatkan kami kepada Allah dengan sedekat-dekatnya\". Sesungguhnya Allah akan memutuskan di antara mereka tentang apa yang mereka berselisih padanya. Sesungguhnya Allah tidak menunjuki orang-orang yang pendusta dan sangat ingkar.'),
(4062, '039:004', '68', 39, 4, 'Kalau sekiranya Allah hendak mengambil anak, tentu Dia akan memilih apa yang dikehendaki-Nya di antara ciptaan-ciptaan yang telah diciptakan-Nya. Maha Suci Allah. Dialah Allah Yang Maha Esa lagi Maha Mengalahkan.'),
(4063, '039:005', '68', 39, 5, 'Dia menciptakan langit dan bumi dengan (tujuan) yang benar; Dia menutupkan malam atas siang dan menutupkan siang atas malam dan menundukkan matahari dan bulan, masing-masing berjalan menurut waktu yang ditentukan. Ingatlah Dialah Yang Maha Perkasa lagi Maha Pengampun.'),
(4064, '039:006', '68', 39, 6, 'Dia menciptakan kamu dari seorang diri kemudian Dia jadikan daripadanya isterinya dan Dia menurunkan untuk kamu delapan ekor yang berpasangan dari binatang ternak. Dia menjadikan kamu dalam perut ibumu kejadian demi kejadian dalam tiga kegelapan. Yang (berbuat) demikian itu adalah Allah, Tuhan kamu, Tuhan Yang mempunyai kerajaan. Tidak ada Tuhan selain Dia; maka bagaimana kamu dapat dipalingkan?');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(4065, '039:007', '68', 39, 7, 'Jika kamu kafir maka sesungguhnya Allah tidak memerlukan (iman)mu dan Dia tidak meridhai kekafiran bagi hamba-Nya; dan jika kamu bersyukur, niscaya Dia meridhai bagimu kesyukuranmu itu; dan seorang yang berdosa tidak akan memikul dosa orang lain. Kemudian kepada Tuhanmulah kembalimu lalu Dia memberitakan kepadamu apa yang telah kamu kerjakan. Sesungguhnya Dia Maha Mengetahui apa yang tersimpan dalam (dada)mu.'),
(4066, '039:008', '68', 39, 8, 'Dan apabila manusia itu ditimpa kemudharatan, dia memohon (pertolongan) kepada Tuhannya dengan kembali kepada-Nya; kemudian apabila Tuhan memberikan nikmat-Nya kepadanya lupalah dia akan kemudharatan yang pernah dia berdoa (kepada Allah) untuk (menghilangkannya) sebelum itu, dan dia mengada-adakan sekutu-sekutu bagi Allah untuk menyesatkan (manusia) dari jalan-Nya. Katakanlah: \"Bersenang-senanglah dengan kekafiranmu itu sementara waktu; sesungguhnya kamu termasuk penghuni neraka\".'),
(4067, '039:009', '68', 39, 9, '(Apakah kamu hai orang musyrik yang lebih beruntung) ataukah orang yang beribadat di waktu-waktu malam dengan sujud dan berdiri, sedang ia takut kepada (azab) akhirat dan mengharapkan rahmat Tuhannya? Katakanlah: \"Adakah sama orang-orang yang mengetahui dengan orang-orang yang tidak mengetahui?\" Sesungguhnya orang yang berakallah yang dapat menerima pelajaran.'),
(4068, '039:010', '68', 39, 10, 'Katakanlah: \"Hai hamba-hamba-Ku yang beriman. bertakwalah kepada Tuhanmu\". Orang-orang yang berbuat baik di dunia ini memperoleh kebaikan. Dan bumi Allah itu adalah luas. Sesungguhnya hanya orang-orang yang bersabarlah Yang dicukupkan pahala mereka tanpa batas.'),
(4069, '039:011', '68', 39, 11, 'Katakanlah: \"Sesungguhnya aku diperintahkan supaya menyembah Allah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agama.'),
(4070, '039:012', '68', 39, 12, 'Dan aku diperintahkan supaya menjadi orang yang pertama-tama berserah diri\".'),
(4071, '039:013', '68', 39, 13, 'Katakanlah: \"Sesungguhnya aku takut akan siksaan hari yang besar jika aku durhaka kepada Tuhanku\".'),
(4072, '039:014', '68', 39, 14, 'Katakanlah: \"Hanya Allah saja Yang aku sembah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agamaku\".'),
(4073, '039:015', '68', 39, 15, 'Maka sembahlah olehmu (hai orang-orang musyrik) apa yang kamu kehendaki selain Dia. Katakanlah: \"Sesungguhnya orang-orang yang rugi ialah orang-orang yang merugikan diri mereka sendiri dan keluarganya pada hari kiamat\". Ingatlah yang demikian itu adalah kerugian yang nyata.'),
(4074, '039:016', '68', 39, 16, 'Bagi mereka lapisan-lapisan dari api di atas mereka dan di bawah merekapun lapisan-lapisan (dari api). Demikianlah Allah mempertakuti hamba-hamba-Nya dengan azab itu. Maka bertakwalah kepada-Ku hai hamba-hamba-Ku.'),
(4075, '039:017', '68', 39, 17, 'Dan orang-orang yang menjauhi thaghut (yaitu) tidak menyembahnya dan kembali kepada Allah, bagi mereka berita gembira; sebab itu sampaikanlah berita itu kepada hamba-hamba-Ku,'),
(4076, '039:018', '68', 39, 18, 'yang mendengarkan perkataan lalu mengikuti apa yang paling baik di antaranya. Mereka itulah orang-orang yang telah diberi Allah petunjuk dan mereka itulah orang-orang yang mempunyai akal.'),
(4077, '039:019', '68', 39, 19, 'Apakah (kamu hendak merubah nasib) orang-orang yang telah pasti ketentuan azab atasnya? Apakah kamu akan menyelamatkan orang yang berada dalam api neraka?'),
(4078, '039:020', '68', 39, 20, 'Tetapi orang-orang yang bertakwa kepada Tuhannya mereka mendapat tempat-tempat yang tinggi, di atasnya dibangun pula tempat-tempat yang tinggi yang di bawahnya mengalir sungai-sungai. Allah telah berjanji dengan sebenar-benarnya. Allah tidak akan memungkiri janji-Nya.'),
(4079, '039:021', '68', 39, 21, 'Apakah kamu tidak memperhatikan, bahwa sesungguhnya Allah menurunkan air dari langit, maka diaturnya menjadi sumber-sumber air di bumi kemudian ditumbuhkan-Nya dengan air itu tanam-tanaman yang bermacam-macam warnanya, lalu menjadi kering lalu kamu melihatnya kekuning-kuningan, kemudian dijadikan-Nya hancur berderai-derai. Sesungguhnya pada yang demikian itu benar-benar terdapat pelajaran bagi orang-orang yang mempunyai akal.'),
(4080, '039:022', '68', 39, 22, 'Maka apakah orang-orang yang dibukakan Allah hatinya untuk (menerima) agama Islam lalu ia mendapat cahaya dari Tuhannya (sama dengan orang yang membatu hatinya)? Maka kecelakaan yang besarlah bagi mereka yang telah membatu hatinya untuk mengingat Allah. Mereka itu dalam kesesatan yang nyata.'),
(4081, '039:023', '68', 39, 23, 'Allah telah menurunkan perkataan yang paling baik (yaitu) Al Quran yang serupa (mutu ayat-ayatnya) lagi berulang-ulang , gemetar karenanya kulit orang-orang yang takut kepada Tuhannya, kemudian menjadi tenang kulit dan hati mereka di waktu mengingat Allah. Itulah petunjuk Allah, dengan kitab itu Dia menunjuki siapa yang dikehendaki-Nya. Dan barangsiapa yang disesatkan Allah, niscaya tak ada baginya seorang pemimpinpun.'),
(4082, '039:024', '68', 39, 24, 'Maka apakah orang-orang yang menoleh dengan mukanya menghindari azab yang buruk pada hari kiamat (sama dengan orang mukmin yang tidak kena azab)? Dan dikatakan kepada orang-orang yang zalim: \"Rasakanlah olehmu balasan apa yang telah kamu kerjakan\".'),
(4083, '039:025', '68', 39, 25, 'Orang-orang yang sebelum mereka telah mendustakan (rasul-rasul), maka datanglah kepada mereka azab dari arah yang tidak mereka sangka.'),
(4084, '039:026', '68', 39, 26, 'Maka Allah merasakan kepada mereka kehinaan pada kehidupan dunia. Dan sesungguhnya azab pada hari akhirat lebih besar kalau mereka mengetahui.'),
(4085, '039:027', '68', 39, 27, 'Sesungguhnya telah Kami buatkan bagi manusia dalam Al Quran ini setiap macam perumpamaan supaya mereka dapat pelajaran.'),
(4086, '039:028', '68', 39, 28, '(Ialah) Al Quran dalam bahasa Arab yang tidak ada kebengkokan (di dalamnya) supaya mereka bertakwa.'),
(4087, '039:029', '68', 39, 29, 'Allah membuat perumpamaan (yaitu) seorang laki-laki (budak) yang dimiliki oleh beberapa orang yang berserikat yang dalam perselisihan dan seorang budak yang menjadi milik penuh dari seorang laki-laki (saja); Adakah kedua budak itu sama halnya? Segala puji bagi Allah tetapi kebanyakan mereka tidak mengetahui.'),
(4088, '039:030', '68', 39, 30, 'Sesungguhnya kamu akan mati dan sesungguhnya mereka akan mati (pula).'),
(4089, '039:031', '68', 39, 31, 'Kemudian sesungguhnya kamu pada hari kiamat akan berbantah-bantah di hadapan Tuhanmu.'),
(4090, '039:032', '68', 39, 32, 'Maka siapakah yang lebih zalim daripada orang yang membuat-buat dusta terhadap Allah dan mendustakan kebenaran ketika datang kepadanya? Bukankah di neraka Jahannam tersedia tempat tinggal bagi orang-orang yang kafir?'),
(4091, '039:033', '68', 39, 33, 'Dan orang yang membawa kebenaran (Muhammad) dan membenarkannya, mereka itulah orang-orang yang bertakwa.'),
(4092, '039:034', '68', 39, 34, 'Mereka memperoleh apa yang mereka kehendaki pada sisi Tuhan mereka. Demikianlah balasan orang-orang yang berbuat baik,'),
(4093, '039:035', '68', 39, 35, 'agar Allah akan menutupi (mengampuni) bagi mereka perbuatan yang paling buruk yang mereka kerjakan dan membalas mereka dengan upah yang lebih baik dari apa yang telah mereka kerjakan.'),
(4094, '039:036', '68', 39, 36, 'Bukankah Allah cukup untuk melindungi hamba-hamba-Nya. Dan mereka mempertakuti kamu dengan (sembahan-sembahan) yang selain Allah? Dan siapa yang disesatkan Allah maka tidak seorangpun pemberi petunjuk baginya.'),
(4095, '039:037', '68', 39, 37, 'Dan barangsiapa yang diberi petunjuk oleh Allah, maka tidak seorangpun yang dapat menyesatkannya. Bukankah Allah Maha Perkasa lagi mempunyai (kekuasaan untuk) mengazab?'),
(4096, '039:038', '68', 39, 38, 'Dan sungguh jika kamu bertanya kepada mereka: \"Siapakah yang menciptakan langit dan bumi?\", niscaya mereka menjawab: \"Allah\". Katakanlah: \"Maka terangkanlah kepadaku tentang apa yang kamu seru selain Allah, jika Allah hendak mendatangkan kemudharatan kepadaku, apakah berhala-berhalamu itu dapat menghilangkan kemudharatan itu, atau jika Allah hendak memberi rahmat kepadaku, apakah mereka dapat menahan rahmat-Nya?. Katakanlah: \"Cukuplah Allah bagiku\". Kepada-Nya-lah bertawakkal orang-orang yang berserah diri.'),
(4097, '039:039', '68', 39, 39, 'Katakanlah: \"Hai kaumku, bekerjalah sesuai dengan keadaanmu, sesungguhnya aku akan bekerja (pula), maka kelak kamu akan mengetahui,'),
(4098, '039:040', '68', 39, 40, 'siapa yang akan mendapat siksa yang menghinakannya dan lagi ditimpa oleh azab yang kekal\".'),
(4099, '039:041', '68', 39, 41, 'Sesungguhnya Kami menurunkan kepadamu Al Kitab (Al Quran) untuk manusia dengan membawa kebenaran; siapa yang mendapat petunjuk maka (petunjuk itu) untuk dirinya sendiri, dan siapa yang sesat maka sesungguhnya dia semata-mata sesat buat (kerugian) dirinya sendiri, dan kamu sekali-kali bukanlah orang yang bertanggung jawab terhadap mereka.'),
(4100, '039:042', '68', 39, 42, 'Allah memegang jiwa (orang) ketika matinya dan (memegang) jiwa (orang) yang belum mati di waktu tidurnya; maka Dia tahanlah jiwa (orang) yang telah Dia tetapkan kematiannya dan Dia melepaskan jiwa yang lain sampai waktu yang ditetapkan. Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi kaum yang berfikir.'),
(4101, '039:043', '68', 39, 43, 'Bahkan mereka mengambil pemberi syafa´at selain Allah. Katakanlah: \"Dan apakah (kamu mengambilnya juga) meskipun mereka tidak memiliki sesuatupun dan tidak berakal?\"'),
(4102, '039:044', '68', 39, 44, 'Katakanlah: \"Hanya kepunyaan Allah syafa´at itu semuanya. Kepunyaan-Nya kerajaan langit dan bumi. Kemudian kepada-Nya-lah kamu dikembalikan\"'),
(4103, '039:045', '68', 39, 45, 'Dan apabila hanya nama Allah saja disebut, kesallah hati orang-orang yang tidak beriman kepada kehidupan akhirat; dan apabila nama sembahan-sembahan selain Allah yang disebut, tiba-tiba mereka bergirang hati.'),
(4104, '039:046', '68', 39, 46, 'Katakanlah: \"Wahai Allah, Pencipta langit dan bumi, Yang mengetahui barang ghaib dan yang nyata, Engkaulah Yang memutuskan antara hamba-hamba-Mu tentang apa yang selalu mereka memperselisihkannya\".'),
(4105, '039:047', '68', 39, 47, 'Dan sekiranya orang-orang yang zalim mempunyai apa yang ada di bumi semuanya dan (ada pula) sebanyak itu besertanya, niscaya mereka akan menebus dirinya dengan itu dari siksa yang buruk pada hari kiamat. Dan jelaslah bagi mereka azab dari Allah yang belum pernah mereka perkirakan.'),
(4106, '039:048', '68', 39, 48, 'Dan (jelaslah) bagi mereka akibat buruk dari apa yang telah mereka perbuat dan mereka diliputi oleh pembalasan yang mereka dahulu selalu memperolok-olokkannya.'),
(4107, '039:049', '68', 39, 49, 'Maka apabila manusia ditimpa bahaya ia menyeru Kami, kemudian apabila Kami berikan kepadanya nikmat dari Kami ia berkata: \"Sesungguhnya aku diberi nikmat itu hanyalah karena kepintaranku\". Sebenarnya itu adalah ujian, tetapi kebanyakan mereka itu tidak mengetahui.'),
(4108, '039:050', '68', 39, 50, 'Sungguh orang-orang yang sebelum mereka (juga) telah mengatakan itu pula, maka tiadalah berguna bagi mereka apa yang dahulu mereka usahakan.'),
(4109, '039:051', '68', 39, 51, 'Maka mereka ditimpa oleh akibat buruk dari apa yang mereka usahakan. Dan orang-orang yang zalim di antara mereka akan ditimpa akibat buruk dari usahanya dan mereka tidak dapat melepaskan diri.'),
(4110, '039:052', '68', 39, 52, 'Dan tidakkah mereka mengetahui bahwa Allah melapangkan rezeki dan menyempitkannya bagi siapa yang dikehendaki-Nya? Sesungguhnya pada yang demikian itu terdapat tanda-tanda kekuasaan Allah bagi kaum yang beriman.'),
(4111, '039:053', '68', 39, 53, 'Katakanlah: \"Hai hamba-hamba-Ku yang malampaui batas terhadap diri mereka sendiri, janganlah kamu berputus asa dari rahmat Allah. Sesungguhnya Allah mengampuni dosa-dosa semuanya. Sesungguhnya Dialah Yang Maha Pengampun lagi Maha Penyayang.'),
(4112, '039:054', '68', 39, 54, 'Dan kembalilah kamu kepada Tuhanmu, dan berserah dirilah kepada-Nya sebelum datang azab kepadamu kemudian kamu tidak dapat ditolong (lagi).'),
(4113, '039:055', '68', 39, 55, 'Dan ikutilah sebaik-baik apa yang telah diturunkan kepadamu dari Tuhanmu sebelum datang azab kepadamu dengan tiba-tiba, sedang kamu tidak menyadarinya,'),
(4114, '039:056', '68', 39, 56, 'supaya jangan ada orang yang mengatakan: \"Amat besar penyesalanku atas kelalaianku dalam (menunaikan kewajiban) terhadap Allah, sedang aku sesungguhnya termasuk orang-orang yang memperolok-olokkan (agama Allah),'),
(4115, '039:057', '68', 39, 57, 'atau supaya jangan ada yang berkata: ´Kalau sekiranya Allah memberi petunjuk kepadaku tentulah aku termasuk orang-orang yang bertakwa´.'),
(4116, '039:058', '68', 39, 58, 'Atau supaya jangan ada yang berkata ketika ia melihat azab ´Kalau sekiranya aku dapat kemnbali (ke dunia), niscaya aku akan termasuk orang-orang berbuat baik´.'),
(4117, '039:059', '68', 39, 59, '(Bukan demikian) sebenarya telah datang keterangan-keterangan-Ku kepadamu lalu kamu mendustakannya dan kamu menyombongkan diri dan adalah kamu termasuk orang-orang yang kafir\".'),
(4118, '039:060', '68', 39, 60, 'Dan pada hari kiamat kamu akan melihat orang-orang yang berbuat dusta terhadap Allah, mukanya menjadi hitam. Bukankah dalam neraka Jahannam itu ada tempat bagi orang-orang yang menyombongkan diri?'),
(4119, '039:061', '68', 39, 61, 'Dan Allah menyelamatkan orang-orang yang bertakwa karena kemenangan mereka, mereka tiada disentuh oleh azab (neraka dan tidak pula) mereka berduka cita.'),
(4120, '039:062', '68', 39, 62, 'Allah menciptakan segala sesuatu dan Dia memelihara segala sesuatu.'),
(4121, '039:063', '68', 39, 63, 'Kepunyaan-Nya-lah kunci-kunci (perbendaharaan) langit dan bumi. Dan orang-orang yang kafir terhadap ayat-ayat Allah, mereka itulah orang-orang yang merugi.'),
(4122, '039:064', '68', 39, 64, 'Katakanlah: \"Maka apakah kamu menyuruh aku menyembah selain Allah, hai orang-orang yang tidak berpengetahuan?\"'),
(4123, '039:065', '68', 39, 65, 'Dan sesungguhnya telah diwahyukan kepadamu dan kepada (nabi-nabi) yang sebelummu. \"Jika kamu mempersekutukan (Tuhan), niscaya akan hapuslah amalmu dan tentulah kamu termasuk orang-orang yang merugi.'),
(4124, '039:066', '68', 39, 66, 'Karena itu, maka hendaklah Allah saja kamu sembah dan hendaklah kamu termasuk orang-orang yang bersyukur\".'),
(4125, '039:067', '68', 39, 67, 'Dan mereka tidak mengagungkan Allah dengan pengagungan yang semestinya padahal bumi seluruhnya dalam genggaman-Nya pada hari kiamat dan langit digulung dengan tangan kanan-Nya. Maha Suci Tuhan dan Maha Tinggi Dia dari apa yang mereka persekutukan.'),
(4126, '039:068', '68', 39, 68, 'Dan ditiuplah sangkakala, maka matilah siapa yang di langit dan di bumi kecuali siapa yang dikehendaki Allah. Kemudian ditiup sangkakala itu sekali lagi maka tiba-tiba mereka berdiri menunggu (putusannya masing-masing).'),
(4127, '039:069', '68', 39, 69, 'Dan terang benderanglah bumi (padang mahsyar) dengan cahaya (keadilan) Tuhannya; dan diberikanlah buku (perhitungan perbuatan masing-masing) dan didatangkanlah para nabi dan saksi-saksi dan diberi keputusan di antara mereka dengan adil, sedang mereka tidak dirugikan.'),
(4128, '039:070', '68', 39, 70, 'Dan disempurnakan bagi tiap-tiap jiwa (balasan) apa yang telah dikerjakannya dan Dia lebih mengetahui apa yang mereka kerjakan.'),
(4129, '039:071', '68', 39, 71, 'Orang-orang kafir dibawa ke neraka Jahannam berombong-rombongan. Sehingga apabila mereka sampai ke neraka itu dibukakanlah pintu-pintunya dan berkatalah kepada mereka penjaga-penjaganya: \"Apakah belum pernah datang kepadamu rasul-rasul di antaramu yang membacakan kepadamu ayat-ayat Tuhanmu dan memperingatkan kepadamu akan pertemuan dengan hari ini?\" Mereka menjawab: \"Benar (telah datang)\". Tetapi telah pasti berlaku ketetapan azab terhadap orang-orang yang kafir.'),
(4130, '039:072', '68', 39, 72, 'Dikatakan (kepada mereka): \"Masukilah pintu-pintu neraka Jahannam itu, sedang kamu kekal di dalamnya\" Maka neraka Jahannam itulah seburuk-buruk tempat bagi orang-orang yang menyombongkan diri.'),
(4131, '039:073', '68', 39, 73, 'Dan orang-orang yang bertakwa kepada Tuhan dibawa ke dalam surga berombong-rombongan (pula). Sehingga apabila mereka sampai ke surga itu sedang pintu-pintunya telah terbuka dan berkatalah kepada mereka penjaga-penjaganya: \"Kesejahteraan (dilimpahkan) atasmu. Berbahagialah kamu! maka masukilah surga ini, sedang kamu kekal di dalamnya\".'),
(4132, '039:074', '68', 39, 74, 'Dan mereka mengucapkan: \"Segala puji bagi Allah yang telah memenuhi janji-Nya kepada kami dan telah (memberi) kepada kami tempat ini sedang kami (diperkenankan) menempati tempat dalam surga di mana saja yang kami kehendaki; maka surga itulah sebaik-baik balasan bagi orang-orang yang beramal\".'),
(4133, '039:075', '68', 39, 75, 'Dan kamu (Muhammad) akan melihat malaikat-mmlaikat berlingkar di sekeliling ´Arsy bertasbih sambil memuji Tuhannya; dan diberi putusan di antara hamba-hamba Allah dengan adil dan diucapkan: \"Segala puji bagi Allah, Tuhan semesta alam\".'),
(4134, '040:001', '68', 40, 1, 'Haa Miim.'),
(4135, '040:002', '68', 40, 2, 'Diturunkan Kitab ini (Al Quran) dari Allah Yang Maha Perkasa lagi Maha Mengetahui,'),
(4136, '040:003', '68', 40, 3, 'Yang Mengampuni dosa dan Menerima taubat lagi keras hukuman-Nya. Yang mempunyai karunia. Tiada Tuhan (yang berhak disembah) selain Dia. Hanya kepada-Nya-lah kembali (semua makhluk).'),
(4137, '040:004', '68', 40, 4, 'Tidak ada yang memperdebatkan tentang ayat-ayat Allah, kecuali orang-orang yang kafir. Karena itu janganlah pulang balik mereka dengan bebas dari suatu kota ke kota yang lain memperdayakan kamu.'),
(4138, '040:005', '68', 40, 5, 'Sebelum mereka, kaum Nuh dan golongan-golongan yang bersekutu sesudah mereka telah mendustakan (rasul) dan tiap-tiap umat telah merencanakan makar terhadap rasul mereka untuk menawannya dan mereka membantah dengan (alasan) yang batil untuk melenyapkan kebenaran dengan yang batil itu; karena itu Aku azab mereka. Maka betapa (pedihnya) azab-Ku?'),
(4139, '040:006', '68', 40, 6, 'Dan demikianlah telah pasti berlaku ketetapan azab Tuhanmu terhadap orang-orang kafir, karena sesungguhnya mereka adalah penghuni neraka.'),
(4140, '040:007', '68', 40, 7, '(Malaikat-malaikat) yang memikul ´Arsy dan malaikat yang berada di sekelilingnya bertasbih memuji Tuhannya dan mereka beriman kepada-Nya serta memintakan ampun bagi orang-orang yang beriman (seraya mengucapkan): \"Ya Tuhan kami, rahmat dan ilmu Engkau meliputi segala sesuatu, maka berilah ampunan kepada orang-orang yang bertaubat dan mengikuti jalan Engkau dan peliharalah mereka dari siksaan neraka yang menyala-nyala,'),
(4141, '040:008', '68', 40, 8, 'ya Tuhan kami, dan masukkanlah mereka ke dalam surga ´Adn yang telah Engkau janjikan kepada mereka dan orang-orang yang saleh di antara bapak-bapak mereka, dan isteri-isteri mereka, dan keturunan mereka semua. Sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana,'),
(4142, '040:009', '68', 40, 9, 'dan peliharalah mereka dari (balasan) kejahatan. Dan orang-orang yang Engkau pelihara dari (pembalasan) kejahatan pada hari itu maka sesungguhnya telah Engkau anugerahkan rahmat kepadanya dan itulah kemenangan yang besar\".'),
(4143, '040:010', '68', 40, 10, 'Sesungguhnya orang-orang yang kafir diserukan kepada mereka (pada hari kiamat): \"Sesungguhnya kebencian Allah (kepadamu) lebih besar daripada kebencianmu kepada dirimu sendiri karena kamu diseru untuk beriman lalu kamu kafir\".'),
(4144, '040:011', '68', 40, 11, 'Mereka menjawab: \"Ya Tuhan kami Engkau telah mematikan kami dua kali dan telah menghidupkan kami dua kali (pula), lalu kami mengakui dosa-dosa kami. Maka adakah sesuatu jalan (bagi kami) untuk keluar (dari neraka)?\"'),
(4145, '040:012', '68', 40, 12, 'Yang demikian itu adalah karena kamu kafir apabila Allah saja disembah. Dan kamu percaya apabila Allah dipersekutukan. Maka putusan (sekarang ini) adalah pada Allah Yang Maha Tinggi lagi Maha Besar.'),
(4146, '040:013', '68', 40, 13, 'Dialah yang memperlihatkan kepadamu tanda-tanda (kekuasaan)-Nya dan menurunkan untukmu rezeki dari langit. Dan tiadalah mendapat pelajaran kecuali orang-orang yang kembali (kepada Allah).'),
(4147, '040:014', '68', 40, 14, 'Maka sembahlah Allah dengan memurnikan ibadat kepada-Nya, meskipun orang-orang kafir tidak menyukai(nya).'),
(4148, '040:015', '68', 40, 15, '(Dialah) Yang Maha Tinggi derajat-Nya, Yang mempunyai ´Arsy, Yang mengutus Jibril dengan (membawa) perintah-Nya kepada siapa yang dikehendaki-Nya di antara hamba-hamba-Nya, supaya dia memperingatkan (manusia) tentang hari pertemuan (hari kiamat).'),
(4149, '040:016', '68', 40, 16, '(Yaitu) hari (ketika) mereka keluar (dari kubur); tiada suatupun dari keadaan mereka yang tersembunyi bagi Allah. (Lalu Allah berfirman): \"Kepunyaan siapakah kerajaan pada hari ini?\" Kepunyaan Allah Yang Maha Esa lagi Maha Mengalahkan.'),
(4150, '040:017', '68', 40, 17, 'Pada hari ini tiap-tiap jiwa diberi balasan dengan apa yang diusahakannya. Tidak ada yang dirugikan pada hari ini. Sesungguhnya Allah amat cepat hisabnya.'),
(4151, '040:018', '68', 40, 18, 'Berilah mereka peringatan dengan hari yang dekat (hari kiamat yaitu) ketika hati (menyesak) sampai di kerongkongan dengan menahan kesedihan. Orang-orang yang zalim tidak mempunyai teman setia seorangpun dan tidak (pula) mempunyai seorang pemberi syafa´at yang diterima syafa´atnya.'),
(4152, '040:019', '68', 40, 19, 'Dia mengetahui (pandangan) mata yang khianat dan apa yang disembunyikan oleh hati.'),
(4153, '040:020', '68', 40, 20, 'Dan Allah menghukum dengan keadilan. Dan sembahan-sembahan yang mereka sembah selain Allah tiada dapat menghukum dengan sesuatu apapun. Sesungguhnya Allah Dialah Yang Maha Mendengar lagi Maha Melihat.'),
(4154, '040:021', '68', 40, 21, 'Dan apakah mereka tidak mengadakan perjalanan di muka bumi, lalu memperhatikan betapa kesudahan orang-orang yang sebelum mereka. Mereka itu adalah lebih hebat kekuatannya daripada mereka dan (lebih banyak) bekas-bekas mereka di muka bumi, maka Allah mengazab mereka disebabkan dosa-dosa mereka. Dan mereka tidak mempunyai seorang pelindung dari azab Allah.'),
(4155, '040:022', '68', 40, 22, 'Yang demiklan itu adalah karena telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang nyata lalu mereka kafir; maka Allah mengazab mereka. Sesungguhnya Dia Maha Kuat lagi Maha Keras hukuman-Nya.'),
(4156, '040:023', '68', 40, 23, 'Dan sesungguhnya telah Kami utus Musa dengan membawa ayat-ayat Kami dan keterangan yang nyata,'),
(4157, '040:024', '68', 40, 24, 'kepada Fir´aun, Haman dan Qarun; maka mereka berkata: \"(Ia) adalah seorang ahli sihir yang pendusta\".'),
(4158, '040:025', '68', 40, 25, 'Maka tatkala Musa datang kepada mereka membawa kebenaran dari sisi Kami mereka berkata: \"Bunuhlah anak-anak orang-orang yang beriman bersama dengan dia dan biarkanlah hidup wanita-wanita mereka\". Dan tipu daya orang-orang kafir itu tak lain hanyalah sia-sia (belaka).'),
(4159, '040:026', '68', 40, 26, 'Dan berkata Fir´aun (kepada pembesar-pembesarnya): \"Biarkanlah aku membunuh Musa dan hendaklah ia memohon kepada Tuhannya, karena sesungguhnya aku khawatir dia akan menukar agamamu atau menimbulkan kerusakan di muka bumi\".'),
(4160, '040:027', '68', 40, 27, 'Dan Musa berkata: \"Sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu dari setiap orang yang menyombongkan diri yang tidak beriman kepada hari berhisab\".'),
(4161, '040:028', '68', 40, 28, 'Dan seorang laki-laki yang beriman di antara pengikut-pengikut Fir´aun yang menyembunyikan imannya berkata: \"Apakah kamu akan membunuh seorang laki-laki karena dia menyatakan: \"Tuhanku ialah Allah padahal dia telah datang kepadamu dengan membawa keterangan-keterangan dari Tuhanmu. Dan jika ia seorang pendusta maka dialah yang menanggung (dosa) dustanya itu; dan jika ia seorang yang benar niscaya sebagian (bencana) yang diancamkannya kepadamu akan menimpamu\". Sesungguhnya Allah tidak menunjuki orang-orang yang melampaui batas lagi pendusta.'),
(4162, '040:029', '68', 40, 29, '(Musa berkata): \"Hai kaumku, untukmulah kerajaan pada hari ini dengan berkuasa di muka bumi. Siapakah yang akan menolong kita dari azab Allah jika azab itu menimpa kita!\" Fir´aun berkata: \"Aku tidak mengemukakan kepadamu, melainkan apa yang aku pandang baik; dan aku tiada menunjukkan kepadamu selain jalan yang benar\".'),
(4163, '040:030', '68', 40, 30, 'Dan orang yang beriman itu berkata: \"Hai kaumku, sesungguhnya aku khawatir kamu akan ditimpa (bencana) seperti peristiwa kehancuran golongan yang bersekutu.'),
(4164, '040:031', '68', 40, 31, '(Yakni) seperti keadaan kaum Nuh, ´Aad, Tsamud dan orang-orang yang datang sesudah mereka. Dan Allah tidak menghendaki berbuat kezaliman terhadap hamba-hamba-Nya.'),
(4165, '040:032', '68', 40, 32, 'Hai kaumku, sesungguhnya aku khawatir terhadapmu akan siksaan hari panggil-memanggil.'),
(4166, '040:033', '68', 40, 33, '(yaitu) hari (ketika) kamu (lari) berpaling ke belakang, tidak ada bagimu seorangpun yang menyelamatkan kamu dari (azab) Allah, dan siapa yang disesatkan Allah, niscaya tidak ada baginya seorangpun yang akan memberi petunjuk.'),
(4167, '040:034', '68', 40, 34, 'Dan sesungguhnya telah datang Yusuf kepadamu dengan membawa keterangan-keterangan, tetapi kamu senantiasa dalam keraguan tentang apa yang dibawanya kepadamu, hingga ketika dia meninggal, kamu berkata: \"Allah tidak akan mengirim seorang (rasulpun) sesudahnya. Demikianlah Allah menyesatkan orang-orang yang melampaui batas dan ragu-ragu.'),
(4168, '040:035', '68', 40, 35, '(Yaitu) orang-orang yang memperdebatkan ayat-ayat Allah tanpa alasan yang sampai kepada mereka. Amat besar kemurkaan (bagi mereka) di sisi Allah dan di sisi orang-orang yang beriman. Demikianlah Allah mengunci mati hati orang yang sombong dan sewenang-wenang.'),
(4169, '040:036', '68', 40, 36, 'Dan berkatalah Fir´aun: \"Hai Haman, buatkanlah bagiku sebuah bangunan yang tinggi supaya aku sampai ke pintu-pintu,'),
(4170, '040:037', '68', 40, 37, '(yaitu) pintu-pintu langit, supaya aku dapat melihat Tuhan Musa dan sesungguhnya aku memandangnya seorang pendusta\". Demikianlah dijadikan Fir´aun memandang baik perbuatan yang buruk itu, dan dia dihalangi dari jalan (yang benar); dan tipu daya Fir´aun itu tidak lain hanyalah membawa kerugian.'),
(4171, '040:038', '68', 40, 38, 'Orang yang beriman itu berkata: \"Hai kaumku, ikutilah aku, aku akan menunjukkan kepadamu jalan yang benar.'),
(4172, '040:039', '68', 40, 39, 'Hai kaumku, sesungguhnya kehidupan dunia ini hanyalah kesenangan (sementara) dan sesungguhnya akhirat itulah negeri yang kekal.'),
(4173, '040:040', '68', 40, 40, 'Barangsiapa mengerjakan perbuatan jahat, maka dia tidak akan dibalasi melainkan sebanding dengan kejahatan itu. Dan barangsiapa mengerjakan amal yang saleh baik laki-laki maupun perempuan sedang ia dalam keadaan beriman, maka mereka akan masuk surga, mereka diberi rezeki di dalamnya tanpa hisab.'),
(4174, '040:041', '68', 40, 41, 'Hai kaumku, bagaimanakah kamu, aku menyeru kamu kepada keselamatan, tetapi kamu menyeru aku ke neraka?'),
(4175, '040:042', '68', 40, 42, '(Kenapa) kamu menyeruku supaya kafir kepada Allah dan mempersekutukan-Nya dengan apa yang tidak kuketahui padahal aku menyeru kamu (beriman) kepada Yang Maha Perkasa lagi Maha Pengampun?'),
(4176, '040:043', '68', 40, 43, 'Sudah pasti bahwa apa yang kamu seru supaya aku (beriman) kepadanya tidak dapat memperkenankan seruan apapun baik di dunia maupun di akhirat. Dan sesungguhnya kita kembali kepada Allah dan sesungguhnya orang-orang yang melampaui batas, mereka itulah penghuni neraka.'),
(4177, '040:044', '68', 40, 44, 'Kelak kamu akan ingat kepada apa yang kukatakan kepada kamu. Dan aku menyerahkan urusanku kepada Allah. Sesungguhnya Allah Maha Melihat akan hamba-hamba-Nya\".'),
(4178, '040:045', '68', 40, 45, 'Maka Allah memeliharanya dari kejahatan tipu daya mereka, dan Fir´aun beserta kaumnya dikepung oleh azab yang amat buruk.'),
(4179, '040:046', '68', 40, 46, 'Kepada mereka dinampakkan neraka pada pagi dan petang, dan pada hari terjadinya Kiamat. (Dikatakan kepada malaikat): \"Masukkanlah Fir´aun dan kaumnya ke dalam azab yang sangat keras\".'),
(4180, '040:047', '68', 40, 47, 'Dan (ingatlah), ketika mereka berbantah-bantah dalam neraka, maka orang-orang yang lemah berkata kepada orang-orang yang menyombongkan diri: \"Sesungguhnya kami adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan dari kami sebahagian azab api neraka?\"'),
(4181, '040:048', '68', 40, 48, 'Orang-orang yang menyombongkan diri menjawab: \"Sesungguhnya kita semua sama-sama dalam neraka karena sesungguhnya Allah telah menetapkan keputusan antara hamba-hamba-(Nya)\".'),
(4182, '040:049', '68', 40, 49, 'Dan orang-orang yang berada dalam neraka berkata kepada penjaga-penjaga neraka Jahannam: \"Mohonkanlah kepada Tuhanmu supaya Dia meringankan azab dari kami barang sehari\".'),
(4183, '040:050', '68', 40, 50, 'Penjaga Jahannam berkata: \"Dan apakah belum datang kepada kamu rasul-rasulmu dengan membawa keterangan-keterangan?\" Mereka menjawab: \"Benar, sudah datang\". Penjaga-penjaga Jahannam berkata: \"Berdoalah kamu\". Dan doa orang-orang kafir itu hanyalah sia-sia belaka.'),
(4184, '040:051', '68', 40, 51, 'Sesungguhnya Kami menolong rasul-rasul Kami dan orang-orang yang beriman dalam kehidupan dunia dan pada hari berdirinya saksi-saksi (hari kiamat),'),
(4185, '040:052', '68', 40, 52, '(yaitu) hari yang tidak berguna bagi orang-orang zalim permintaan maafnya dan bagi merekalah laknat dan bagi merekalah tempat tinggal yang buruk.'),
(4186, '040:053', '68', 40, 53, 'Dan sesungguhnya telah Kami berikan petunjuk kepada Musa; dan Kami wariskan Taurat kepada Bani Israil,'),
(4187, '040:054', '68', 40, 54, 'untuk menjadi petunjuk dan peringatan bagi orang-orang yang berfikir.'),
(4188, '040:055', '68', 40, 55, 'Maka bersabarlah kamu, karena sesungguhnya janji Allah itu benar, dan mohonlah ampunan untuk dosamu dan bertasbihlah seraya memuji Tuhanmu pada waktu petang dan pagi.'),
(4189, '040:056', '68', 40, 56, 'Sesungguhhnya orang-orang yang memperdebatkan tentang ayat-ayat Allah tanpa alasan yang sampai kepada mereka tidak ada dalam dada mereka melainkan hanyalah (keinginan akan) kebesaran yang mereka sekali-kali tiada akan mencapainya, maka mintalah perlindungan kepada Allah. Sesungguhnya Dia Maha Mendengar lagi Maha Melihat.'),
(4190, '040:057', '68', 40, 57, 'Sesungguhnya penciptaan langit dan bumi lebih besar daripada penciptaan manusia akan tetapi kebanyakan manusia tidak mengetahui.'),
(4191, '040:058', '68', 40, 58, 'Dan tidaklah sama orang yang buta dengan orang yang melihat, dan tidaklah (pula sama) orang-orang yang beriman serta mengerjakan amal saleh dengan orang-orang yang durhaka. Sedikit sekali kamu mengambil pelajaran.'),
(4192, '040:059', '68', 40, 59, 'Sesungguhnya hari kiamat pasti akan datang, tidak ada keraguan tentangnya, akan tetapi kebanyakan manusia tiada beriman.'),
(4193, '040:060', '68', 40, 60, 'Dan Tuhanmu berfirman: \"Berdoalah kepada-Ku, niscaya akan Kuperkenankan bagimu. Sesungguhnya orang-orang yang menyombongkan diri dari menyembah-Ku akan masuk neraka Jahannam dalam keadaan hina dina\".'),
(4194, '040:061', '68', 40, 61, 'Allah-lah yang menjadikan malam untuk kamu supaya kamu beristirahat padanya; dan menjadikan siang terang benderang. Sesungguhnya Allah benar-benar mempunyal karunia yang dilimpahkan atas manusia, akan tetapi kebanyakan manusia tidak bersyukur.'),
(4195, '040:062', '68', 40, 62, 'Yang demikian itu adalah Allah, Tuhanmu, Pencipta segala sesuatu, tiada Tuhan (yang berhak disembah) melainkan Dia; maka bagaimanakah kamu dapat dipalingkan?'),
(4196, '040:063', '68', 40, 63, 'Seperti demikianlah dipalingkan orang-orang yang selalu mengingkari ayat-ayat Allah.'),
(4197, '040:064', '68', 40, 64, 'Allah-lah yang menjadikan bumi bagi kamu tempat menetap dan langit sebagai atap, dan membentuk kamu lalu membaguskan rupamu serta memberi kamu rezeki dengan sebahagian yang baik-baik. Yang demikian itu adalah Allah Tuhanmu, Maha Agung Allah, Tuhan semesta alam.'),
(4198, '040:065', '68', 40, 65, 'Dialah Yang hidup kekal, tiada Tuhan (yang berhak disembah) melainkan Dia; maka sembahlah Dia dengan memurnikan ibadat kepada-Nya. Segala puji bagi Allah Tuhan semesta alam.'),
(4199, '040:066', '68', 40, 66, 'Katakanlah (ya Muhammad): \"Sesungguhnya aku dilarang menyembah sembahan yang kamu sembah selain Allah setelah datang kepadaku keterangan-keterangan dari Tuhanku; dan aku diperintahkan supaya tunduk patuh kepada Tuhan semesta alam.'),
(4200, '040:067', '68', 40, 67, 'Dialah yang menciptakan kamu dari tanah kemudian dari setetes mani, sesudah itu dari segumpal darah, kemudian dilahirkannya kamu sebagai seorang anak, kemudian (kamu dibiarkan hidup) supaya kamu sampai kepada masa (dewasa), kemudian (dibiarkan kamu hidup lagi) sampai tua, di antara kamu ada yang diwafatkan sebelum itu. (Kami perbuat demikian) supaya kamu sampai kepada ajal yang ditentukan dan supaya kamu memahami(nya).'),
(4201, '040:068', '68', 40, 68, 'Dialah yang menghidupkan dan mematikan, maka apabila Dia menetapkan sesuatu urusan, Dia hanya bekata kepadanya: \"Jadilah\", maka jadilah ia.'),
(4202, '040:069', '68', 40, 69, 'Apakah kamu tidak melihat kepada orang-orang yang membantah ayat-ayat Allah? Bagaimanakah mereka dapat dipalingkan?'),
(4203, '040:070', '68', 40, 70, '(Yaitu) orang-orang yang mendustakan Al Kitab (Al Quran) dan wahyu yang dibawa oleh rasul-rasul Kami yang telah Kami utus. Kelak mereka akan mengetahui,'),
(4204, '040:071', '68', 40, 71, 'ketika belenggu dan rantai dipasang di leher mereka, seraya mereka diseret,'),
(4205, '040:072', '68', 40, 72, 'ke dalam air yang sangat panas, kemudian mereka dibakar dalam api,'),
(4206, '040:073', '68', 40, 73, 'kemudian dikatakan kepada mereka: \"Manakah berhala-berhala yang selalu kamu persekutukan,'),
(4207, '040:074', '68', 40, 74, '(yang kamu sembah) selain Allah?\" Mereka menjawab: \"Mereka telah hilang lenyap dari kami, bahkan kami dahulu tiada pernah menyembah sesuatu\". Seperti demikianlah Allah menyesatkan orang-orang kafir.'),
(4208, '040:075', '68', 40, 75, 'Yang demikian itu disebabkan karena kamu bersuka ria di muka bumi dengan tidak benar dan karena kamu selalu bersuka ria (dalam kemaksiatan).'),
(4209, '040:076', '68', 40, 76, '(Dikatakan kepada mereka): \"Masuklah kamu ke pintu-pintu neraka Jahannam, sedang kamu kekal di dalamnya. Maka itulah seburuk-buruk tempat bagi orang-orang yang sombong\".'),
(4210, '040:077', '68', 40, 77, 'Maka bersabarlah kamu, sesungguhnya janji Allah adalah benar; maka meskipun Kami perlihatkan kepadamu sebagian siksa yang Kami ancamkan kepada mereka ataupun Kami wafatkan kamu (sebelum ajal menimpa mereka), namun kepada Kami sajalah mereka dikembalikan.'),
(4211, '040:078', '68', 40, 78, 'Dan sesungguhnya telah Kami utus beberapa orang rasul sebelum kamu, di antara mereka ada yang Kami ceritakan kepadamu dan di antara mereka ada (pula) yang tidak Kami ceritakan kepadamu. Tidak dapat bagi seorang rasul membawa suatu mukjizat, melainkan dengan seizin Allah; maka apabila telah datang perintah Allah, diputuskan (semua perkara) dengan adil. Dan ketika itu rugilah orang-orang yang berpegang kepada yang batil.'),
(4212, '040:079', '68', 40, 79, 'Allahlah yang menjadikan binatang ternak untuk kamu, sebagiannya untuk kamu kendarai dan sebagiannya untuk kamu makan.'),
(4213, '040:080', '68', 40, 80, 'Dan (ada lagi) manfaat-manfaat yang lain pada binatang ternak itu untuk kamu dan supaya kamu mencapai suatu keperluan yang tersimpan dalam hati dengan mengendarainya. Dan kamu dapat diangkut dengan mengendarai binatang-binatang itu dan dengan mengendarai bahtera.'),
(4214, '040:081', '68', 40, 81, 'Dan Dia memperlihatkan kepada kamu tanda-tanda (kekuasaan-Nya); maka tanda-tanda (kekuasaan) Allah yang manakah yang kamu ingkari?'),
(4215, '040:082', '68', 40, 82, 'Maka apakah mereka tiada mengadakan perjalanan di muka bumi lalu memperhatikan betapa kesudahan orang-orang yang sebelum mereka. Adalah orang-orang yang sebelum mereka itu lebih hebat kekuatannya dan (lebih banyak) bekas-bekas mereka di muka bumi, maka apa yang mereka usahakan itu tidak dapat menolong mereka.'),
(4216, '040:083', '68', 40, 83, 'Maka tatkala datang kepada mereka rasul-rasul (yang diutus kepada) mereka dengan membawa ketarangan-keterangan, mereka merasa senang dengan pengetahuan yang ada pada mereka dan mereka dikepung oleh azab Allah yang selalu mereka perolok-olokkan itu.'),
(4217, '040:084', '68', 40, 84, 'Maka tatkala mereka melihat azab Kami, mereka berkata: \"Kami beriman hanya kepada Allah saja, dan kami kafir kepada sembahan-sembahan yang telah kami persekutukan dengan Allah\".'),
(4218, '040:085', '68', 40, 85, 'Maka iman mereka tiada berguna bagi mereka tatkala mereka telah melihat siksa Kami. Itulah sunnah Allah yang telah berlaku terhadap hamba-hamba-Nya. Dan di waktu itu binasalah orang-orang kafir.'),
(4219, '041:001', '68', 41, 1, 'Haa Miim.'),
(4220, '041:002', '68', 41, 2, 'Diturunkan dari Tuhan Yang Maha Pemurah lagi Maha Penyayang.'),
(4221, '041:003', '68', 41, 3, 'Kitab yang dijelaskan ayat-ayatnya, yakni bacaan dalam bahasa Arab, untuk kaum yang mengetahui,'),
(4222, '041:004', '68', 41, 4, 'yang membawa berita gembira dan yang membawa peringatan, tetapi kebanyakan mereka berpaling, tidak mau mendengarkan.'),
(4223, '041:005', '68', 41, 5, 'Mereka berkata: \"Hati kami berada dalam tutupan (yang menutupi) apa yang kamu seru kami kepadanya dan telinga kami ada sumbatan dan antara kami dan kamu ada dinding, maka bekerjalah kamu; sesungguhnya kami bekerja (pula)\".'),
(4224, '041:006', '68', 41, 6, 'Katakanlah: \"Bahwasanya aku hanyalah seorang manusia seperti kamu, diwahyukan kepadaku bahwasanya Tuhan kamu adalah Tuhan yang Maha Esa, maka tetaplah pada jalan yang lurus menuju kepada-Nya dan mohonlah ampun kepada-Nya. Dan kecelakaan besarlah bagi orang-orang yang mempersekutukan-Nya,'),
(4225, '041:007', '68', 41, 7, '(yaitu) orang-orang yang tidak menunaikan zakat dan mereka kafir akan adanya (kehidupan) akhirat.'),
(4226, '041:008', '68', 41, 8, 'Sesungguhnya orang-orang yang beriman dan beramal saleh, mereka mendapat pahala yang tiada putus-putusnya\".'),
(4227, '041:009', '68', 41, 9, 'Katakanlah: \"Sesungguhnya patutkah kamu kafir kepada Yang menciptakan bumi dalam dua masa dan kamu adakan sekutu-sekutu bagi-Nya? (Yang bersifat) demikian itu adalah Rabb semesta alam\".'),
(4228, '041:010', '68', 41, 10, 'Dan dia menciptakan di bumi itu gunung-gunung yang kokoh di atasnya. Dia memberkahinya dan Dia menentukan padanya kadar makanan-makanan (penghuni)nya dalam empat masa. (Penjelasan itu sebagai jawaban) bagi orang-orang yang bertanya.'),
(4229, '041:011', '68', 41, 11, 'Kemudian Dia menuju kepada penciptaan langit dan langit itu masih merupakan asap, lalu Dia berkata kepadanya dan kepada bumi: \"Datanglah kamu keduanya menurut perintah-Ku dengan suka hati atau terpaksa\". Keduanya menjawab: \"Kami datang dengan suka hati\".'),
(4230, '041:012', '68', 41, 12, 'Maka Dia menjadikannya tujuh langit dalam dua masa. Dia mewahyukan pada tiap-tiap langit urusannya. Dan Kami hiasi langit yang dekat dengan bintang-bintang yang cemerlang dan Kami memeliharanya dengan sebaik-baiknya. Demikianlah ketentuan Yang Maha Perkasa lagi Maha Mengetahui.'),
(4231, '041:013', '68', 41, 13, 'Jika mereka berpaling maka katakanlah: \"Aku telah memperingatkan kamu dengan petir, seperti petir yang menimpa kaum ´Aad dan Tsamud\".'),
(4232, '041:014', '68', 41, 14, 'Ketika para rasul datang kepada mereka dari depan dan belakang mereka (dengan menyerukan): \"Janganlah kamu menyembah selain Allah\". Mereka menjawab: \"Kalau Tuhan kami menghendaki tentu Dia akan menurunkan malaikat-malaikat-Nya, maka sesungguhnya kami kafir kepada wahyu yang kamu diutus membawanya\".'),
(4233, '041:015', '68', 41, 15, 'Adapun kaum ´Aad maka mereka menyombongkan diri di muka bumi tanpa alasan yang benar dan berkata: \"Siapakah yang lebih besar kekuatannya dari kami?\" Dan apakah mereka itu tidak memperhatikan bahwa Allah Yang menciptakan mereka adalah lebih besar kekuatan-Nya daripada mereka? Dan adalah mereka mengingkari tanda-tanda (kekuatan) Kami.'),
(4234, '041:016', '68', 41, 16, 'Maka Kami meniupkan angin yang amat gemuruh kepada mereka dalam beberapa hari yang sial, karena Kami hendak merasakan kepada mereka itu siksaan yang menghinakan dalam kehidupan dunia. Dan Sesungguhnya siksa akhirat lebih menghinakan sedang mereka tidak diberi pertolongan.'),
(4235, '041:017', '68', 41, 17, 'Dan adapun kaum Tsamud, maka mereka telah Kami beri petunjuk tetapi mereka lebih menyukai buta (kesesatan) daripada petunjuk, maka mereka disambar petir azab yang menghinakan disebabkan apa yang telah mereka kerjakan.'),
(4236, '041:018', '68', 41, 18, 'Dan Kami selamatkan orang-orang yang beriman dan mereka adalah orang-orang yang bertakwa.'),
(4237, '041:019', '68', 41, 19, 'Dan (ingatlah) hari (ketika) musuh-musuh Allah di giring ke dalam neraka, lalu mereka dikumpulkan semuanya.'),
(4238, '041:020', '68', 41, 20, 'Sehingga apabila mereka sampai ke neraka, pendengaran, penglihatan dan kulit mereka menjadi saksi terhadap mereka tentang apa yang telah mereka kerjakan.'),
(4239, '041:021', '68', 41, 21, 'Dan mereka berkata kepada kulit mereka: \"Mengapa kamu menjadi saksi terhadap kami?\" Kulit mereka menjawab: \"Allah yang menjadikan segala sesuatu pandai berkata telah menjadikan kami pandai (pula) berkata, dan Dialah yang menciptakan kamu pada kali pertama dan hanya kepada-Nya lah kamu dikembalikan\".'),
(4240, '041:022', '68', 41, 22, 'Kamu sekali-sekali tidak dapat bersembunyi dari kesaksian pendengaran, penglihatan dan kulitmu kepadamu bahkan kamu mengira bahwa Allah tidak mengetahui kebanyakan dari apa yang kamu kerjakan.'),
(4241, '041:023', '68', 41, 23, 'Dan yang demikian itu adalah prasangkamu yang telah kamu sangka kepada Tuhanmu, Dia telah membinasakan kamu, maka jadilah kamu termasuk orang-orang yang merugi.'),
(4242, '041:024', '68', 41, 24, 'Jika mereka bersabar (menderita azab) maka nerakalah tempat diam mereka dan jika mereka mengemukakan alasan-alasan, maka tidaklah mereka termasuk orang-orang yang diterima alasannya.'),
(4243, '041:025', '68', 41, 25, 'Dan Kami tetapkan bagi mereka teman-teman yang menjadikan mereka memandang bagus apa yang ada di hadapan dan di belakang mereka dan tetaplah atas mereka keputusan azab pada umat-umat yang terdahulu sebelum mereka dari jinn dan manusia, sesungguhnya mereka adalah orang-orang yang merugi.'),
(4244, '041:026', '68', 41, 26, 'Dan orang-orang yang kafir berkata: \"Janganlah kamu mendengar dengan sungguh-sungguh akan Al Quran ini dan buatlah hiruk-pikuk terhadapnya, supaya kamu dapat mengalahkan mereka\".'),
(4245, '041:027', '68', 41, 27, 'Maka sesungguhnya Kami akan merasakan azab yang keras kepada orang-orang kafir dan Kami akan memberi balasan kepada mereka dengan seburuk-buruk pembalasan bagi apa yang telah mereka kerjakan.'),
(4246, '041:028', '68', 41, 28, 'Demikianlah balasan terhadap musuh-musuh Allah, (yaitu) neraka; mereka mendapat tempat tinggal yang kekal di dalamnya sebagai balasan atas keingkaran mereka terhadap ayat-ayat Kami.'),
(4247, '041:029', '68', 41, 29, 'Dan orang-orang kafir berkata: \"Ya Rabb kami perlihatkanlah kepada kami dua jenis orang yang telah menyesatkan kami (yaitu) sebagian dari jinn dan manusia agar kami letakkan keduanya di bawah telapak kaki kami supaya kedua jenis itu menjadi orang-orang yang hina\".'),
(4248, '041:030', '68', 41, 30, 'Sesungguhnya orang-orang yang mengatakan: \"Tuhan kami ialah Allah\" kemudian mereka meneguhkan pendirian mereka, maka malaikat akan turun kepada mereka dengan mengatakan: \"Janganlah kamu takut dan janganlah merasa sedih; dan gembirakanlah mereka dengan jannah yang telah dijanjikan Allah kepadamu\".'),
(4249, '041:031', '68', 41, 31, 'Kamilah pelindung-pelindungmu dalam kehidupan dunia dan akhirat; di dalamnya kamu memperoleh apa yang kamu inginkan dan memperoleh (pula) di dalamnya apa yang kamu minta.'),
(4250, '041:032', '68', 41, 32, 'Sebagai hidangan (bagimu) dari Tuhan Yang Maha Pengampun lagi Maha Penyayang.'),
(4251, '041:033', '68', 41, 33, 'Siapakah yang lebih baik perkataannya daripada orang yang menyeru kepada Allah, mengerjakan amal yang saleh, dan berkata: \"Sesungguhnya aku termasuk orang-orang yang menyerah diri?\"'),
(4252, '041:034', '68', 41, 34, 'Dan tidaklah sama kebaikan dan kejahatan. Tolaklah (kejahatan itu) dengan cara yang lebih baik, maka tiba-tiba orang yang antaramu dan antara dia ada permusuhan seolah-olah telah menjadi teman yang sangat setia.'),
(4253, '041:035', '68', 41, 35, 'Sifat-sifat yang baik itu tidak dianugerahkan melainkan kepada orang-orang yang sabar dan tidak dianugerahkan melainkan kepada orang-orang yang mempunyai keuntungan yang besar.'),
(4254, '041:036', '68', 41, 36, 'Dan jika syetan mengganggumu dengan suatu gangguan, maka mohonlah perlindungan kepada Allah. Sesungguhnya Dialah yang Maha Mendengar lagi Maha Mengetahui.'),
(4255, '041:037', '68', 41, 37, 'Dan di antara tanda-tanda kekuasaan-Nya ialah malam, siang, matahari dan bulan. Janganlah sembah matahari maupun bulan, tapi sembahlah Allah Yang menciptakannya, Jika Ialah yang kamu hendak sembah.'),
(4256, '041:038', '68', 41, 38, 'Jika mereka menyombongkan diri, maka mereka (malaikat) yang di sisi Tuhanmu bertasbih kepada-Nya di malam dan siang hari, sedang mereka tidak jemu-jemu.'),
(4257, '041:039', '68', 41, 39, 'Dan di antara tanda-tanda-Nya (Ialah) bahwa kau lihat bumi kering dan gersang, maka apabila Kami turunkan air di atasnya, niscaya ia bergerak dan subur. Sesungguhnya Tuhan Yang menghidupkannya, Pastilah dapat menghidupkan yang mati. Sesungguhnya Dia Maha Kuasa atas segala sesuatu.'),
(4258, '041:040', '68', 41, 40, 'Sesungguhnya orang-orang yang mengingkari ayat-ayat Kami, mereka tidak tersembunyi dari Kami. Maka apakah orang-orang yang dilemparkan ke dalam neraka lebih baik, ataukah orang-orang yang datang dengan aman sentosa pada hari Kiamat? Perbuatlah apa yang kamu kehendaki; Sesungguhnya Dia Maha Melihat apa yang kamu kerjakan.'),
(4259, '041:041', '68', 41, 41, 'Sesungguhnya orang-orang yang mengingkari Al Quran ketika Al Quran itu datang kepada mereka, (mereka itu pasti akan celaka), dan sesungguhnya Al Quran itu adalah kitab yang mulia.'),
(4260, '041:042', '68', 41, 42, 'Yang tidak datang kepadanya (Al Quran) kebatilan baik dari depan maupun dari belakangnya, yang diturunkan dari Rabb Yang Maha Bijaksana lagi Maha Terpuji.'),
(4261, '041:043', '68', 41, 43, 'Tidaklah ada yang dikatakan (oleh orang-orang kafir) kepadamu itu selain apa yang sesungguhnya telah dikatakan kepada rasul-rasul sebelum kamu. Sesungguhnya Rabb-mu benar-benar mempunyai ampunan dan hukuman yang pedih.'),
(4262, '041:044', '68', 41, 44, 'Dan jikalau Kami jadikan Al Quran itu suatu bacaan dalam bahasa selain Arab, tentulah mereka mengatakan: \"Mengapa tidak dijelaskan ayat-ayatnya?\" Apakah (patut Al Quran) dalam bahasa asing sedang (rasul adalah orang) Arab? Katakanlah: \"Al Quran itu adalah petunjuk dan penawar bagi orang-orang mukmin. Dan orang-orang yang tidak beriman pada telinga mereka ada sumbatan, sedang Al Quran itu suatu kegelapan bagi mereka. Mereka itu adalah (seperti) yang dipanggil dari tempat yang jauh\".'),
(4263, '041:045', '68', 41, 45, 'Dan sesungguhnya telah Kami berikan kepada Musa Taurat lalu diperselisihkan tentang Taurat itu. Kalau tidak ada keputusan yang telah terdahulu dari Rabb-mu, tentulah orang-orang kafir itu sudah dibinasakan. Dan Sesungguhnya mereka terhadap Al Quran benar-benar dalam keragu-raguan yang membingungkan.'),
(4264, '041:046', '68', 41, 46, 'Barangsiapa yang mengerjakan amal yang saleh maka (pahalanya) untuk dirinya sendiri dan barangsiapa mengerjakan perbuatan jahat, maka (dosanya) untuk dirinya sendiri; dan sekali-kali tidaklah Rabb-mu menganiaya hamba-hamba-Nya.'),
(4265, '041:047', '68', 41, 47, 'Kepada-Nya-lah dikembalikan pengetahuan tentang hari Kiamat. Dan tidak ada buah-buahan keluar dari kelopaknya dan tidak seorang perempuanpun mengandung dan tidak (pula) melahirkan, melainkan dengan sepengetahuan-Nya. Pada hari Tuhan memanggil mereka: \"Dimanakah sekutu-sekutu-Ku itu?\", mereka menjawab: \"Kami nyatakan kepada Engkau bahwa tidak ada seorangpun di antara kami yang memberi kesaksian (bahwa Engkau punya sekutu)\".'),
(4266, '041:048', '68', 41, 48, 'Dan hilang lenyaplah dari mereka apa yang selalu mereka sembah dahulu, dan mereka yakin bahwa tidak ada bagi mereka satu jalan keluarpun.'),
(4267, '041:049', '68', 41, 49, 'Manusia tidak jemu memohon kebaikan, dan jika mereka ditimpa malapetaka dia menjadi putus asa lagi putus harapan.'),
(4268, '041:050', '68', 41, 50, 'Dan jika Kami merasakan kepadanya sesuatu rahmat dari Kami sesudah dia ditimpa kesusahan, pastilah dia berkata: \"Ini adalah hakku, dan aku tidak yakin bahwa hari Kiamat itu akan datang. Dan jika aku dikembalikan kepada Tuhanku maka sesungguhnya aku akan memperoleh kebaikan pada sisi-Nya\". Maka Kami benar-benar akan memberitakan kepada orang-orang kafir apa yang telah mereka kerjakan dan akan Kami rasakan kepada mereka azab yang keras.'),
(4269, '041:051', '68', 41, 51, 'Dan apabila Kami memberikan nikmat kepada manusia, ia berpaling dan menjauhkan diri; tetapi apabila ia ditimpa malapetaka, maka ia banyak berdoa.'),
(4270, '041:052', '68', 41, 52, 'Katakanlah: \"Bagaimana pendapatmu jika (Al Quran) itu datang dari sisi Allah, kemudian kamu mengingkarinya. Siapakah yang lebih sesat daripada orang yang selalu berada dalam penyimpangan yang jauh?\"'),
(4271, '041:053', '68', 41, 53, 'Kami akan memperlihatkan kepada mereka tanda-tanda (kekuasaan) Kami di segala wilayah bumi dan pada diri mereka sendiri, hingga jelas bagi mereka bahwa Al Quran itu adalah benar. Tiadakah cukup bahwa sesungguhnya Tuhanmu menjadi saksi atas segala sesuatu?'),
(4272, '041:054', '68', 41, 54, 'Ingatlah bahwa sesungguhnya mereka adalah dalam keraguan tentang pertemuan dengan Tuhan mereka. Ingatlah bahwa sesungguhnya Dia Maha Meliputi segala sesuatu.'),
(4273, '042:001', '68', 42, 1, 'Haa Miim.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(4274, '042:002', '68', 42, 2, '´Ain Siin Qaaf.'),
(4275, '042:003', '68', 42, 3, 'Demikianlah Allah Yang Maha Perkasa lagi Maha Bijaksana, mewahyukan kepada kamu dan kepada orang-orang sebelum kamu.'),
(4276, '042:004', '68', 42, 4, 'Kepunyaan-Nya-lah apa yang ada di langit dan apa yang ada di bumi. Dan Dialah yang Maha Tinggi lagi Maha Besar.'),
(4277, '042:005', '68', 42, 5, 'Hampir saja langit itu pecah dari sebelah atas (karena kebesaran Tuhan) dan malaikat-malaikat bertasbih serta memuji Tuhan-nya dan memohonkan ampun bagi orang-orang yang ada di bumi. Ingatlah, bahwa sesungguhnya Allah Dialah Yang Maha Pengampun lagi Penyayang.'),
(4278, '042:006', '68', 42, 6, 'Dan orang-orang yang mengambil pelindung-pelindung selain Allah, Allah mengawasi (perbuatan) mereka; dan kamu (ya Muhammad) bukanlah orang yang diserahi mengawasi mereka.'),
(4279, '042:007', '68', 42, 7, 'Demikianlah Kami wahyukan kepadamu Al Quran dalam bahasa Arab, supaya kamu memberi peringatan kepada ummul Qura (penduduk Mekah) dan penduduk (negeri-negeri) sekelilingnya serta memberi peringatan (pula) tentang hari berkumpul (kiamat) yang tidak ada keraguan padanya. Segolongan masuk surga, dan segolongan masuk Jahannam.'),
(4280, '042:008', '68', 42, 8, 'Dan kalau Allah menghendaki niscaya Allah menjadikan mereka satu umat (saja), tetapi Dia memasukkan orang-orang yang dikehendaki-Nya ke dalam rahmat-Nya. Dan orang-orang yang zalim tidak ada bagi mereka seorang pelindungpun dan tidak pula seorang penolong.'),
(4281, '042:009', '68', 42, 9, 'Atau patutkah mereka mengambil pelindung-pelindung selain Allah? Maka Allah, Dialah pelindung (yang sebenarnya) dan Dia menghidupkan orang-orang yang mati, dan Dia adalah Maha Kuasa atas segala sesuatu.'),
(4282, '042:010', '68', 42, 10, 'Tentang sesuatu apapun kamu berselisih, maka putusannya (terserah) kepada Allah. (Yang mempunyai sifat-sifat demikian) itulah Allah Tuhanku. Kepada-Nya lah aku bertawakkal dan kepada-Nya-lah aku kembali.'),
(4283, '042:011', '68', 42, 11, '(Dia) Pencipta langit dan bumi. Dia menjadikan bagi kamu dari jenis kamu sendiri pasangan-pasangan dan dari jenis binatang ternak pasangan-pasangan (pula), dijadikan-Nya kamu berkembang biak dengan jalan itu. Tidak ada sesuatupun yang serupa dengan Dia, dan Dialah yang Maha Mendengar dan Melihat.'),
(4284, '042:012', '68', 42, 12, 'Kepunyaan-Nya-lah perbendaharaan langit dan bumi; Dia melapangkan rezeki bagi siapa yang dikehendaki-Nya dan menyempitkan(nya). Sesungguhnya Dia Maha Mengetahui segala sesuatu.'),
(4285, '042:013', '68', 42, 13, 'Dia telah mensyari´atkan bagi kamu tentang agama apa yang telah diwasiatkan-Nya kepada Nuh dan apa yang telah Kami wahyukan kepadamu dan apa yang telah Kami wasiatkan kepada Ibrahim, Musa dan Isa yaitu: Tegakkanlah agama dan janganlah kamu berpecah belah tentangnya. Amat berat bagi orang-orang musyrik agama yang kamu seru mereka kepadanya. Allah menarik kepada agama itu orang yang dikehendaki-Nya dan memberi petunjuk kepada (agama)-Nya orang yang kembali (kepada-Nya).'),
(4286, '042:014', '68', 42, 14, 'Dan mereka (ahli kitab) tidak berpecah belah, kecuali setelah datang pada mereka ilmu pengetahuan, karena kedengkian di antara mereka. Kalau tidaklah karena sesuatu ketetapan yang telah ada dari Tuhanmu dahulunya (untuk menangguhkan azab) sampai kepada waktu yang ditentukan, pastilah mereka telah dibinasakan. Dan sesungguhnya orang-orang yang diwariskan kepada mereka Al-Kitab (Taurat dan Injil) sesudah mereka, benar-benar berada dalam keraguan yang menggoncangkan tentang kitab itu.'),
(4287, '042:015', '68', 42, 15, 'Maka karena itu serulah (mereka kepada agama ini) dan tetaplah sebagai mana diperintahkan kepadamu dan janganlah mengikuti hawa nafsu mereka dan katakanlah: \"Aku beriman kepada semua Kitab yang diturunkan Allah dan aku diperintahkan supaya berlaku adil diantara kamu. Allah-lah Tuhan kami dan Tuhan kamu. Bagi kami amal-amal kami dan bagi kamu amal-amal kamu. Tidak ada pertengkaran antara kami dan kamu, Allah mengumpulkan antara kita dan kepada-Nya-lah kembali (kita)\".'),
(4288, '042:016', '68', 42, 16, 'Dan orang-orang yang membantah (agama) Allah sesudah agama itu diterima maka bantahan mereka itu sia-sia saja, di sisi Tuhan mereka. Mereka mendapat kemurkaan (Allah) dan bagi mereka azab yang sangat keras.'),
(4289, '042:017', '68', 42, 17, 'Allah-lah yang menurunkan kitab dengan (membawa) kebenaran dan (menurunkan) neraca (keadilan). Dan tahukah kamu, boleh jadi hari kiamat itu (sudah) dekat?'),
(4290, '042:018', '68', 42, 18, 'Orang-orang yang tidak beriman kepada hari kiamat meminta supaya hari itu segera didatangkan dan orang-orang yang beriman merasa takut kepadanya dan mereka yakin bahwa kiamat itu adalah benar (akan terjadi). Ketahuilah bahwa sesungguhnya orang-orang yang membantah tentang terjadinya kiamat itu benar-benar dalam kesesatan yang jauh.'),
(4291, '042:019', '68', 42, 19, 'Allah Maha lembut terhadap hamba-hamba-Nya; Dia memberi rezeki kepada yang di kehendaki-Nya dan Dialah Yang Maha Kuat lagi Maha Perkasa.'),
(4292, '042:020', '68', 42, 20, 'Barang siapa yang menghendaki keuntungan di akhirat akan Kami tambah keuntungan itu baginya dan barang siapa yang menghendaki keuntungan di dunia Kami berikan kepadanya sebagian dari keuntungan dunia dan tidak ada baginya suatu bahagianpun di akhirat.'),
(4293, '042:021', '68', 42, 21, 'Apakah mereka mempunyai sembahan-sembahan selain Allah yang mensyariatkan untuk mereka agama yang tidak diizinkan Allah? Sekiranya tak ada ketetapan yang menentukan (dari Allah) tentulah mereka telah dibinasakan. Dan sesungguhnya orang-orang yang zalim itu akan memperoleh azab yang amat pedih.'),
(4294, '042:022', '68', 42, 22, 'Kamu lihat orang-orang yang zalim sangat ketakutan karena kejahatan-kejahatan yang telah mereka kerjakan, sedang siksaan menimpa mereka. Dan orang-orang yang beriman serta mengerjakan amal saleh (berada) di dalam taman-taman surga, mereka memperoleh apa yang mereka kehendaki di sisi Tuhan mereka. Yang demikian itu adalah karunia yang besar.'),
(4295, '042:023', '68', 42, 23, 'Itulah (karunia) yang (dengan itu) Allah menggembirakan hamba-hamba-Nya yang beriman dan mengerjakan amal yang saleh. Katakanlah: \"Aku tidak meminta kepadamu sesuatu upahpun atas seruanku kecuali kasih sayang dalam kekeluargaan\". Dan siapa yang mengerjakan kebaikan akan Kami tambahkan baginya kebaikan pada kebaikannya itu. Sesungguhnya Allah Maha Pengampun lagi Maha Mensyukuri.'),
(4296, '042:024', '68', 42, 24, 'Bahkan mereka mengatakan: \"Dia (Muhammad) telah mengada-adakan dusta terhadap Allah\". Maka jika Allah menghendaki niscaya Dia mengunci mati hatimu; dan Allah menghapuskan yang batil dan membenarkan yang hak dengan kalimat-kalimat-Nya (Al Quran). Sesungguhnya Dia Maha Mengetahui segala isi hati.'),
(4297, '042:025', '68', 42, 25, 'Dan Dialah yang menerima taubat dari hamba-hamba-Nya dan memaafkan kesalahan-kesalahan dan mengetahui apa yang kamu kerjakan,'),
(4298, '042:026', '68', 42, 26, 'dan Dia memperkenankan (doa) orang-orang yang beriman serta mengerjakan amal yang saleh dan menambah (pahala) kepada mereka dari karunia-Nya. Dan orang-orang yang kafir bagi mereka azab yang sangat keras.'),
(4299, '042:027', '68', 42, 27, 'Dan jikalau Allah melapangkan rezeki kepada hamba-hamba-Nya tentulah mereka akan melampaui batas di muka bumi, tetapi Allah menurunkan apa yang dikehendaki-Nya dengan ukuran. Sesungguhnya Dia Maha Mengetahui (keadaan) hamba-hamba-Nya lagi Maha Melihat.'),
(4300, '042:028', '68', 42, 28, 'Dan Dialah Yang menurunkan hujan sesudah mereka berputus asa dan menyebarkan rahmat-Nya. Dan Dialah Yang Maha Pelindung lagi Maha Terpuji.'),
(4301, '042:029', '68', 42, 29, 'Di antara (ayat-ayat) tanda-tanda-Nya ialah menciptakan langit dan bumi dan makhluk-makhluk yang melata Yang Dia sebarkan pada keduanya. Dan Dia Maha Kuasa mengumpulkan semuanya apabila dikehendaki-Nya.'),
(4302, '042:030', '68', 42, 30, 'Dan apa saja musibah yang menimpa kamu maka adalah disebabkan oleh perbuatan tanganmu sendiri, dan Allah memaafkan sebagian besar (dari kesalahan-kesalahanmu).'),
(4303, '042:031', '68', 42, 31, 'Dan kamu tidak dapat melepaskan diri (dari azab Allah) di muka bumi, dan kamu tidak memperoleh seorang pelindung dan tidak pula penolong selain Allah.'),
(4304, '042:032', '68', 42, 32, 'Dan di antara tanda-tanda kekuasaan-Nya ialah kapal-kapal di tengah (yang berlayar) di laut seperti gunung-gunung.'),
(4305, '042:033', '68', 42, 33, 'Jika Dia menghendaki, Dia akan menenangkan angin, maka jadilah kapal-kapal itu terhenti di permukaan laut. Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaannya) bagi setiap orang yang banyak bersabar dan banyak bersyukur,'),
(4306, '042:034', '68', 42, 34, 'atau kapal-kapal itu dibinasakan-Nya karena perbuatan mereka atau Dia memberi maaf sebagian besar (dari mereka).'),
(4307, '042:035', '68', 42, 35, 'Dan supaya orang-orang yang membantah ayat-ayat (kekuasaan) Kami mengetahui bahwa mereka sekali-kali tidak akan memperoleh jalan ke luar (dari siksaan).'),
(4308, '042:036', '68', 42, 36, 'Maka sesuatu yang diberikan kepadamu, itu adalah kenikmatan hidup di dunia; dan yang ada pada sisi Allah lebih baik dan lebih kekal bagi orang-orang yang beriman, dan hanya kepada Tuhan mereka, mereka bertawakkal.'),
(4309, '042:037', '68', 42, 37, 'Dan (bagi) orang-orang yang menjauhi dosa-dosa besar dan perbuatan-perbuatan keji, dan apabila mereka marah mereka memberi maaf.'),
(4310, '042:038', '68', 42, 38, 'Dan (bagi) orang-orang yang menerima (mematuhi) seruan Tuhannya dan mendirikan shalat, sedang urusan mereka (diputuskan) dengan musyawarat antara mereka; dan mereka menafkahkan sebagian dari rezeki yang Kami berikan kepada mereka.'),
(4311, '042:039', '68', 42, 39, 'Dan ( bagi) orang-orang yang apabila mereka diperlakukan dengan zalim mereka membela diri.'),
(4312, '042:040', '68', 42, 40, 'Dan balasan suatu kejahatan adalah kejahatan yang serupa, maka barang siapa memaafkan dan berbuat baik maka pahalanya atas (tanggungan) Allah. Sesungguhnya Dia tidak menyukai orang-orang yang zalim.'),
(4313, '042:041', '68', 42, 41, 'Dan sesungguhnya orang-orang yang membela diri sesudah teraniaya, tidak ada satu dosapun terhadap mereka.'),
(4314, '042:042', '68', 42, 42, 'Sesungguhnya dosa itu atas orang-orang yang berbuat zalim kepada manusia dan melampaui batas di muka bumi tanpa hak. Mereka itu mendapat azab yang pedih.'),
(4315, '042:043', '68', 42, 43, 'Tetapi orang yang bersabar dan memaafkan, sesungguhnya (perbuatan) yang demikian itu termasuk hal-hal yang diutamakan.'),
(4316, '042:044', '68', 42, 44, 'Dan siapa yang disesatkan Allah maka tidak ada baginya seorang pemimpinpun sesudah itu. Dan kamu akan melihat orang-orang yang zalim ketika mereka melihat azab berkata: \"Adakah kiranya jalan untuk kembali (ke dunia)?\"'),
(4317, '042:045', '68', 42, 45, 'Dan kamu akan melihat mereka dihadapkan ke neraka dalam keadaan tunduk karena (merasa) hina, mereka melihat dengan pandangan yang lesu. Dan orang-orang yang beriman berkata: \"Sesungguhnya orang-orang yang merugi ialah orang-orang yang kehilangan diri mereka sendiri dan (kehilangan) keluarga mereka pada hari kiamat. Ingatlah, sesungguhnya orang-orang yang zalim itu berada dalam azab yang kekal.'),
(4318, '042:046', '68', 42, 46, 'Dan mereka sekali-kali tidak mempunyai pelindung-pelindung yang dapat menolong mereka selain Allah. Dan siapa yang disesatkan Allah maka tidaklah ada baginya satu jalanpun (untuk mendapat petunjuk).'),
(4319, '042:047', '68', 42, 47, 'Patuhilah seruan Tuhanmu sebelum datang dari Allah suatu hari yang tidak dapat ditolak kedatangannya. Kamu tidak memperoleh tempat berlindung pada hari itu dan tidak (pula) dapat mengingkari (dosa-dosamu).'),
(4320, '042:048', '68', 42, 48, 'Jika mereka berpaling maka Kami tidak mengutus kamu sebagai pengawas bagi mereka. Kewajibanmu tidak lain hanyalah menyampaikan (risalah). Sesungguhnya apabila Kami merasakan kepada manusia sesuatu rahmat dari Kami dia bergembira ria karena rahmat itu. Dan jika mereka ditimpa kesusahan disebabkan perbuatan tangan mereka sendiri (niscaya mereka ingkar) karena sesungguhnya manusia itu amat ingkar (kepada nikmat).'),
(4321, '042:049', '68', 42, 49, 'Kepunyaan Allah-lah kerajaan langit dan bumi, Dia menciptakan apa yang Dia kehendaki. Dia memberikan anak-anak perempuan kepada siapa yang Dia kehendaki dan memberikan anak-anak lelaki kepada siapa yang Dia kehendaki,'),
(4322, '042:050', '68', 42, 50, 'atau Dia menganugerahkan kedua jenis laki-laki dan perempuan (kepada siapa) yang dikehendaki-Nya, dan Dia menjadikan mandul siapa yang Dia kehendaki. Sesungguhnya Dia Maha Mengetahui lagi Maha Kuasa.'),
(4323, '042:051', '68', 42, 51, 'Dan tidak mungkin bagi seorang manusiapun bahwa Allah berkata-kata dengan dia kecuali dengan perantaraan wahyu atau dibelakang tabir atau dengan mengutus seorang utusan (malaikat) lalu diwahyukan kepadanya dengan seizin-Nya apa yang Dia kehendaki. Sesungguhnya Dia Maha Tinggi lagi Maha Bijaksana.'),
(4324, '042:052', '68', 42, 52, 'Dan demikianlah Kami wahyukan kepadamu wahyu (Al Quran) dengan perintah Kami. Sebelumnya kamu tidaklah mengetahui apakah Al Kitab (Al Quran) dan tidak pula mengetahui apakah iman itu, tetapi Kami menjadikan Al Quran itu cahaya, yang Kami tunjuki dengan dia siapa yang kami kehendaki di antara hamba-hamba Kami. Dan sesungguhnya kamu benar-benar memberi petunjuk kepada jalan yang lurus.'),
(4325, '042:053', '68', 42, 53, '(Yaitu) jalan Allah yang kepunyaan-Nya segala apa yang ada di langit dan apa yang ada di bumi. Ingatlah, bahwa kepada Allah-lah kembali semua urusan.'),
(4326, '043:001', '68', 43, 1, 'Haa Miim.'),
(4327, '043:002', '68', 43, 2, 'Demi Kitab (Al Quran) yang menerangkan.'),
(4328, '043:003', '68', 43, 3, 'Sesungguhnya Kami menjadikan Al Quran dalam bahasa Arab supaya kamu memahami(nya).'),
(4329, '043:004', '68', 43, 4, 'Dan sesungguhnya Al Quran itu dalam induk Al Kitab (Lauh Mahfuzh) di sisi Kami, adalah benar-benar tinggi (nilainya) dan amat banyak mengandung hikmah.'),
(4330, '043:005', '68', 43, 5, 'Maka apakah Kami akan berhenti menurunkan Al Quran kepadamu, karena kamu adalah kaum yang melampaui batas?'),
(4331, '043:006', '68', 43, 6, 'Berapa banyaknya nabi-nabi yang telah Kami utus kepada umat-umat yang terdahulu.'),
(4332, '043:007', '68', 43, 7, 'Dan tiada seorang nabipun datang kepada mereka melainkan mereka selalu memperolok-olokkannya.'),
(4333, '043:008', '68', 43, 8, 'Maka telah Kami binasakan orang-orang yang lebih besar kekuatannya dari mereka itu (musyrikin Mekah) dan telah terdahulu (tersebut dalam Al Quran) perumpamaan umat-umat masa dahulu.'),
(4334, '043:009', '68', 43, 9, 'Dan sungguh jika kamu tanyakan kepada mereka: \"Siapakah yang menciptakan langit dan bumi?\", niscaya mereka akan menjawab: \"Semuanya diciptakan oleh Yang Maha Perkasa lagi Maha Mengetahui\".'),
(4335, '043:010', '68', 43, 10, 'Yang menjadikan bumi untuk kamu sebagai tempat menetap dan Dia membuat jalan-jalan di atas bumi untuk kamu supaya kamu mendapat petunjuk.'),
(4336, '043:011', '68', 43, 11, 'Dan Yang menurunkan air dari langit menurut kadar (yang diperlukan) lalu Kami hidupkan dengan air itu negeri yang mati, seperti itulah kamu akan dikeluarkan (dari dalam kubur).'),
(4337, '043:012', '68', 43, 12, 'Dan Yang menciptakan semua yang berpasang-pasangan dan menjadikan untukmu kapal dan binatang ternak yang kamu tunggangi.'),
(4338, '043:013', '68', 43, 13, 'Supaya kamu duduk di atas punggungnya kemudian kamu ingat nikmat Tuhanmu apabila kamu telah duduk di atasnya; dan supaya kamu mengucapkan: \"Maha Suci Tuhan yang telah menundukkan semua ini bagi kami padahal kami sebelumnya tidak mampu menguasainya,'),
(4339, '043:014', '68', 43, 14, 'dan sesungguhnya kami akan kembali kepada Tuhan kami\".'),
(4340, '043:015', '68', 43, 15, 'Dan mereka menjadikan sebahagian dari hamba-hamba-Nya sebagai bahagian daripada-Nya. Sesungguhnya manusia itu benar-benar pengingkar yang nyata (terhadap rahmat Allah).'),
(4341, '043:016', '68', 43, 16, 'Patutkah Dia mengambil anak perempuan dari yang diciptakan-Nya dan Dia mengkhususkan buat kamu anak laki-laki.'),
(4342, '043:017', '68', 43, 17, 'Padahal apabila salah seorang di antara mereka diberi kabar gembira dengan apa yang dijadikan sebagai misal bagi Allah Yang Maha Pemurah; jadilah mukanya hitam pekat sedang dia amat menahan sedih.'),
(4343, '043:018', '68', 43, 18, 'Dan apakah patut (menjadi anak Allah) orang yang dibesarkan dalam keadaan berperhiasan sedang dia tidak dapat memberi alasan yang terang dalam pertengkaran.'),
(4344, '043:019', '68', 43, 19, 'Dan mereka menjadikan malaikat-malaikat yang mereka itu adalah hamba-hamba Allah Yang Maha Pemurah sebagai orang-orang perempuan. Apakah mereka menyaksikan penciptaan malaika-malaikat itu? Kelak akan dituliskan persaksian mereka dan mereka akan dimintai pertanggung-jawaban.'),
(4345, '043:020', '68', 43, 20, 'Dan mereka berkata: \"Jikalau Allah Yang Maha Pemurah menghendaki tentulah kami tidak menyembah mereka (malaikat)\". Mereka tidak mempunyai pengetahuan sedikitpun tentang itu, mereka tidak lain hanyalah menduga-duga belaka.'),
(4346, '043:021', '68', 43, 21, 'Atau adakah Kami memberikan sebuah kitab kepada mereka sebelum Al Quran, lalu mereka berpegang dengan kitab itu?'),
(4347, '043:022', '68', 43, 22, 'Bahkan mereka berkata: \"Sesungguhnya kami mendapati bapak-bapak kami menganut suatu agama, dan sesungguhnya kami orang-orang yang mendapat petunjuk dengan (mengikuti) jejak mereka\".'),
(4348, '043:023', '68', 43, 23, 'Dan demikianlah, Kami tidak mengutus sebelum kamu seorang pemberi peringatanpun dalam suatu negeri, melainkan orang-orang yang hidup mewah di negeri itu berkata: \"Sesungguhnya kami mendapati bapak-bapak kami menganut suatu agama dan sesungguhnya kami adalah pengikut jejak-jejak mereka\".'),
(4349, '043:024', '68', 43, 24, '(Rasul itu) berkata: \"Apakah (kamu akan mengikutinya juga) sekalipun aku membawa untukmu (agama) yang lebih (nyata) memberi petunjuk daripada apa yang kamu dapati bapak-bapakmu menganutnya?\" Mereka menjawab: \"Sesungguhnya kami mengingkari agama yang kamu diutus untuk menyampaikannya\".'),
(4350, '043:025', '68', 43, 25, 'Maka Kami binasakan mereka maka perhatikanlah bagaimana kesudahan orang-orang yang mendustakan itu.'),
(4351, '043:026', '68', 43, 26, 'Dan ingatlah ketika Ibrahim berkata kepada bapaknya dan kaumnya: \"Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu sembah,'),
(4352, '043:027', '68', 43, 27, 'tetapi (aku menyembah) Tuhan Yang menjadikanku; karena sesungguhnya Dia akan memberi hidayah kepadaku\".'),
(4353, '043:028', '68', 43, 28, 'Dan (lbrahim a. s.) menjadikan kalimat tauhid itu kalimat yang kekal pada keturunannya supaya mereka kembali kepada kalimat tauhid itu.'),
(4354, '043:029', '68', 43, 29, 'Tetapi Aku telah memberikan kenikmatan hidup kepada mereka dan bapak-bapak mereka sehingga datanglah kepada mereka kebenaran (Al Quran) dan seorang rasul yang memberi penjelasan.'),
(4355, '043:030', '68', 43, 30, 'Dan tatkala kebenaran (Al Quran) itu datang kepada mereka, mereka berkata: \"Ini adalah sihir dan sesungguhnya kami adalah orang-orang yang mengingkarinya\".'),
(4356, '043:031', '68', 43, 31, 'Dan mereka berkata: \"Mengapa Al Quran ini tidak diturunkan kepada seorang besar dari salah satu dua negeri (Mekah dan Thaif) ini?\"'),
(4357, '043:032', '68', 43, 32, 'Apakah mereka yang membagi-bagi rahmat Tuhanmu? Kami telah menentukan antara mereka penghidupan mereka dalam kehidupan dunia, dan kami telah meninggikan sebahagian mereka atas sebagian yang lain beberapa derajat, agar sebagian mereka dapat mempergunakan sebagian yang lain. Dan rahmat Tuhanmu lebih baik dari apa yang mereka kumpulkan.'),
(4358, '043:033', '68', 43, 33, 'Dan sekiranya bukan karena hendak menghindari manusia menjadi umat yang satu (dalam kekafiran), tentulah kami buatkan bagi orang-orang yang kafir kepada Tuhan Yang Maha Pemurah loteng-loteng perak bagi rumah mereka dan (juga) tangga-tangga (perak) yang mereka menaikinya.'),
(4359, '043:034', '68', 43, 34, 'Dan (Kami buatkan pula) pintu-pintu (perak) bagi rumah-rumah mereka dan (begitu pula) dipan-dipan yang mereka bertelekan atasnya.'),
(4360, '043:035', '68', 43, 35, 'Dan (Kami buatkan pula) perhiasan-perhiasan (dari emas untuk mereka). Dan semuanya itu tidak lain hanyalah kesenangan kehidupan dunia, dan kehidupan akhirat itu di sisi Tuhanmu adalah bagi orang-orang yang bertakwa.'),
(4361, '043:036', '68', 43, 36, 'Barangsiapa yang berpaling dari pengajaran Tuhan Yang Maha Pemurah (Al Quran), kami adakan baginya syaitan (yang menyesatkan) maka syaitan itulah yang menjadi teman yang selalu menyertainya.'),
(4362, '043:037', '68', 43, 37, 'Dan sesungguhnya syaitan-syaitan itu benar-benar menghalangi mereka dari jalan yang benar dan mereka menyangka bahwa mereka mendapat petunjuk.'),
(4363, '043:038', '68', 43, 38, 'Sehingga apabila orang-orang yang berpaling itu datang kepada kami (di hari kiamat) dia berkata: \"Aduhai, semoga (jarak) antaraku dan kamu seperti jarak antara masyrik dan maghrib, maka syaitan itu adalah sejahat-jahat teman (yang menyertai manusia)\".'),
(4364, '043:039', '68', 43, 39, '(Harapanmu itu) sekali-kali tidak akan memberi manfaat kepadamu di hari itu karena kamu telah menganiaya (dirimu sendiri). Sesungguhnya kamu bersekutu dalam azab itu.'),
(4365, '043:040', '68', 43, 40, 'Maka apakah kamu dapat menjadikan orang yang pekak bisa mendengar atau (dapatkah) kamu memberi petunjuk kepada orang yang buta (hatinya) dan kepada orang yang tetap dalam kesesatan yang nyata?'),
(4366, '043:041', '68', 43, 41, 'Sungguh, jika Kami mewafatkan kamu (sebelum kamu mencapai kemenangan) maka sesungguhnya Kami akan menyiksa mereka (di akhirat).'),
(4367, '043:042', '68', 43, 42, 'Atau Kami memperlihatkan kepadamu (azab) yang telah Kami ancamkan kepada mereka. Maka sesungguhnya Kami berkuasa atas mereka.'),
(4368, '043:043', '68', 43, 43, 'Maka berpegang teguhlah kamu kepada agama yang telah diwahyukan kepadamu. Sesungguhnya kamu berada di atas jalan yang lurus.'),
(4369, '043:044', '68', 43, 44, 'Dan sesungguhnya Al Quran itu benar-benar adalah suatu kemuliaan besar bagimu dan bagi kaummu dan kelak kamu akan diminta pertanggungan jawab.'),
(4370, '043:045', '68', 43, 45, 'Dan tanyakanlah kepada rasul-rasul Kami yang telah Kami utus sebelum kamu: \"Adakah Kami menentukan tuhan-tuhan untuk disembah selain Allah Yang Maha Pemurah?\"'),
(4371, '043:046', '68', 43, 46, 'Dan sesunguhnya Kami telah mengutus Musa dengan membawa mukjizat-mukjizat Kami kepada Fir´aun dan pemuka-pemuka kaumnya. Maka Musa berkata: \"Sesungguhnya aku adalah utusan dari Tuhan seru sekalian alam\".'),
(4372, '043:047', '68', 43, 47, 'Maka tatkala dia datang kepada mereka dengan membawa mukjizat-mukjizat Kami dengan serta merta mereka mentertawakannya.'),
(4373, '043:048', '68', 43, 48, 'Dan tidaklah Kami perlihatkan kepada mereka sesuatu mukjizat kecuali mukjizat itu lebih besar dari mukjizat-mukjizat yang sebelumnya. Dan Kami timpakan kepada mereka azab supaya mereka kembali (ke jalan yang benar).'),
(4374, '043:049', '68', 43, 49, 'Dan mereka berkata: \"Hai ahli sihir, berdoalah kepada Tuhanmu untuk (melepaskan) kami sesuai dengan apa yang telah dijanjikan-Nya kepadamu; sesungguhnya kami (jika doamu dikabulkan) benar-benar akan menjadi orang yang mendapat petunjuk.'),
(4375, '043:050', '68', 43, 50, 'Maka tatkala Kami hilangkan azab itu dari mereka, dengan serta merta mereka memungkiri (janjinya).'),
(4376, '043:051', '68', 43, 51, 'Dan Fir´aun berseru kepada kaumnya (seraya) berkata: \"Hai kaumku, bukankah kerajaan Mesir ini kepunyaanku dan (bukankah) sungai-sungai ini mengalir di bawahku; maka apakah kamu tidak melihat(nya)?'),
(4377, '043:052', '68', 43, 52, 'Bukankah aku lebih baik dari orang yang hina ini dan yang hampir tidak dapat menjelaskan (perkataannya)?'),
(4378, '043:053', '68', 43, 53, 'Mengapa tidak dipakaikan kepadanya gelang dari emas atau malaikat datang bersama-sama dia untuk mengiringkannya?\"'),
(4379, '043:054', '68', 43, 54, 'Maka Fir´aun mempengaruhi kaumnya (dengan perkataan itu) lalu mereka patuh kepadanya. Karena sesungguhnya mereka adalah kaum yang fasik.'),
(4380, '043:055', '68', 43, 55, 'Maka tatkala mereka membuat Kami murka, Kami menghukum mereka lalu kami tenggelamkan mereka semuanya (di laut),'),
(4381, '043:056', '68', 43, 56, 'dan Kami jadikan mereka sebagai pelajaran dan contoh bagi orang-orang yang kemudian.'),
(4382, '043:057', '68', 43, 57, 'Dan tatkala putra Maryam (Isa) dijadikan perumpamnaan tiba-tiba kaummu (Quraisy) bersorak karenanya.'),
(4383, '043:058', '68', 43, 58, 'Dan mereka berkata: \"Manakah yang lebih baik tuhan-tuhan kami atau dia (Isa)?\" Mereka tidak memberikan perumpamaan itu kepadamu melainkan dengan maksud membantah saja, sebenarnya mereka adalah kaum yang suka bertengkar.'),
(4384, '043:059', '68', 43, 59, 'Isa tidak lain hanyalah seorang hamba yang Kami berikan kepadanya nikmat (kenabian) dan Kami jadikan dia sebagai tanda bukti (kekuasaan Allah) untuk Bani lsrail'),
(4385, '043:060', '68', 43, 60, 'Dan kalau Kami kehendaki benar-benar Kami jadikan sebagai gantimu di muka bumi malaikat-malaikat yang turun temurun.'),
(4386, '043:061', '68', 43, 61, 'Dan sesungguhnya Isa itu benar-benar memberikan pengetahuan tentang hari kiamat. Karena itu janganlah kamu ragu-ragu tentang kiamat itu dan ikutilah Aku. Inilah jalan yang lurus.'),
(4387, '043:062', '68', 43, 62, 'Dan janganlah kamu sekali-kali dipalingkan oleh syaitan; sesungguhnya syaitan itu musuh yang nyata bagimu.'),
(4388, '043:063', '68', 43, 63, 'Dan tatkala Isa datang membawa keterangan dia berkata: \"Sesungguhnya aku datang kepadamu dengan membawa hikmat dan untuk menjelaskan kepadamu sebagian dari apa yang kamu berselisih tentangnya, maka bertakwalah kepada Allah dan taatlah (kepada)ku\".'),
(4389, '043:064', '68', 43, 64, 'Sesungguhnya Allah Dialah Tuhanku dan Tuhan kamu maka sembahlah Dia, ini adalah jalan yang lurus.'),
(4390, '043:065', '68', 43, 65, 'Maka berselisihlah golongan-golongan (yang terdapat) di antara mereka, lalu kecelakaan yang besarlah bagi orang-orang yang zalim yakni siksaan hari yang pedih (kiamat).'),
(4391, '043:066', '68', 43, 66, 'Mereka tidak menunggu kecuali kedatangan hari kiamat kepada mereka dengan tiba-tiba sedang mereka tidak menyadarinya.'),
(4392, '043:067', '68', 43, 67, 'Teman-teman akrab pada hari itu sebagiannya menjadi musuh bagi sebagian yang lain kecuali orang-orang yang bertakwa.'),
(4393, '043:068', '68', 43, 68, '\"Hai hamba-hamba-Ku, tiada kekhawatiran terhadapmu pada hari ini dan tidak pula kamu bersedih hati.'),
(4394, '043:069', '68', 43, 69, '(Yaitu) orang-orang yang beriman kepada ayat-ayat Kami dan adalah mereka dahulu orang-orang yang berserah diri.'),
(4395, '043:070', '68', 43, 70, 'Masuklah kamu ke dalam surga, kamu dan isteri-isteri kamu digembirakan\".'),
(4396, '043:071', '68', 43, 71, 'Diedarkan kepada mereka piring-piring dari emas, dan piala-piala dan di dalam surga itu terdapat segala apa yang diingini oleh hati dan sedap (dipandang) mata dan kamu kekal di dalamnya\".'),
(4397, '043:072', '68', 43, 72, 'Dan itulah surga yang diwariskan kepada kamu disebabkan amal-amal yang dahulu kamu kerjakan.'),
(4398, '043:073', '68', 43, 73, 'Di dalam surga itu ada buah-buahan yang banyak untukmu yang sebahagiannya kamu makan.'),
(4399, '043:074', '68', 43, 74, 'Sesungguhnya orang-orang yang berdosa kekal di dalam azab neraka Jahannam.'),
(4400, '043:075', '68', 43, 75, 'Tidak diringankan azab itu dari mereka dan mereka di dalamnya berputus asa.'),
(4401, '043:076', '68', 43, 76, 'Dan tidaklah Kami menganiaya mereka tetapi merekalah yang menganiaya diri mereka sendiri.'),
(4402, '043:077', '68', 43, 77, 'Mereka berseru: \"Hai Malik biarlah Tuhanmu membunuh kami saja\". Dia menjawab: \"Kamu akan tetap tinggal (di neraka ini)\".'),
(4403, '043:078', '68', 43, 78, 'Sesungguhnya Kami benar-benar telah memhawa kebenaran kepada kamu tetapi kebanyakan di antara kamu benci pada kebenaran itu.'),
(4404, '043:079', '68', 43, 79, 'Bahkan mereka telah menetapkan satu tipu daya (jahat), maka sesungguhnya Kami menetapkan pula.'),
(4405, '043:080', '68', 43, 80, 'Apakah mereka mengira, bahwa Kami tidak mendengar rahasia dan bisikan-bisikan mereka? Sebenarnya (Kami mendengar), dan utusan-utusan (malaikat-malaikat) Kami selalu mencatat di sisi mereka.'),
(4406, '043:081', '68', 43, 81, 'Katakanlah, jika benar Tuhan Yang Maha Pemurah mempunyai anak, maka akulah (Muhammad) orang yang mula-mula memuliakan (anak itu).'),
(4407, '043:082', '68', 43, 82, 'Maha Suci Tuhan Yang empunya langit dan bumi, Tuhan Yang empunya ´Arsy, dari apa yang mereka sifatkan itu.'),
(4408, '043:083', '68', 43, 83, 'Maka biarlah mereka tenggelam (dalam kesesatan) dan bermain-main sampai mereka menemui hari yang dijanjikan kepada mereka.'),
(4409, '043:084', '68', 43, 84, 'Dan Dialah Tuhan (Yang disembah) di langit dan Tuhan (Yang disembah) di bumi dan Dialah Yang Maha Bijaksana lagi Maha Mengetahui.'),
(4410, '043:085', '68', 43, 85, 'Dan Maha Suci Tuhan Yang mempunyai kerajaan langit dan bumi; dan apa yang ada di antara keduanya; dan di sisi-Nya-lah pengetahuan tentang hari kiamat dan hanya kepada-Nya-lah kamu dikembalikan.'),
(4411, '043:086', '68', 43, 86, 'Dan sembahan-sembahan yang mereka sembah selain Allah tidak dapat memberi syafa´at; akan tetapi (orang yang dapat memberi syafa´at ialah) orang yang mengakui yang hak (tauhid) dan mereka meyakini(nya).'),
(4412, '043:087', '68', 43, 87, 'Dan sungguh jika kamu bertanya kepada mereka: \"Siapakah yang menciptakan mereka, niscaya mereka menjawab: \"Allah\", maka bagaimanakah mereka dapat dipalingkan (dari menyembah Allah)?,'),
(4413, '043:088', '68', 43, 88, 'dan (Allah mengetabui) ucapan Muhammad: \"Ya Tuhanku, sesungguhnya mereka itu adalah kaum yang tidak beriman\".'),
(4414, '043:089', '68', 43, 89, 'Maka berpalinglah (hai Muhammad) dari mereka dan katakanlah: \"Salam (selamat tinggal)\". Kelak mereka akan mengetahui (nasib mereka yang buruk).'),
(4415, '044:001', '68', 44, 1, 'Haa miim.'),
(4416, '044:002', '68', 44, 2, 'Demi Kitab (Al Quran) yang menjelaskan,'),
(4417, '044:003', '68', 44, 3, 'sesungguhnya Kami menurunkannya pada suatu malam yang diberkahi dan sesungguhnya Kami-lah yang memberi peringatan.'),
(4418, '044:004', '68', 44, 4, 'Pada malam itu dijelaskan segala urusan yang penuh hikmah,'),
(4419, '044:005', '68', 44, 5, '(yaitu) urusan yang besar dari sisi Kami. Sesungguhnya Kami adalah Yang mengutus rasul-rasul,'),
(4420, '044:006', '68', 44, 6, 'sebagai rahmat dari Tuhanmu. Sesungguhnya Dialah Yang Maha Mendengar lagi Maha Mengetahui,'),
(4421, '044:007', '68', 44, 7, 'Tuhan Yang memelihara langit dan bumi dan apa yang ada di antara keduanya, jika kamu adalah orang yang meyakini.'),
(4422, '044:008', '68', 44, 8, 'Tidak ada Tuhan (yang berhak disembah) melainkan Dia, Yang menghidupkan dan Yang mematikan (Dialah) Tuhanmu dan Tuhan bapak-bapakmu yang terdahulu.'),
(4423, '044:009', '68', 44, 9, 'Tetapi mereka bermain-main dalam keragu-raguan.'),
(4424, '044:010', '68', 44, 10, 'Maka tunggulah hari ketika langit membawa kabut yang nyata,'),
(4425, '044:011', '68', 44, 11, 'yang meliputi manusia. Inilah azab yang pedih.'),
(4426, '044:012', '68', 44, 12, '(Mereka berdoa): \"Ya Tuhan kami, lenyapkanlah dari kami azab itu. Sesungguhnya kami akan beriman\".'),
(4427, '044:013', '68', 44, 13, 'Bagaimanakah mereka dapat menerima peringatan, padahal telah datang kepada mereka seorang rasul yang memberi penjelasan,'),
(4428, '044:014', '68', 44, 14, 'kemudian mereka berpaling daripadanya dan berkata: \"Dia adalah seorang yang menerima ajaran (dari orang lain) lagi pula seorang yang gila\".'),
(4429, '044:015', '68', 44, 15, 'Sesungguhnya (kalau) Kami akan melenyapkan siksaan itu agak sedikit sesungguhnya kamu akan kembali (ingkar).'),
(4430, '044:016', '68', 44, 16, '(Ingatlah) hari (ketika) Kami menghantam mereka dengan hantaman yang keras. Sesungguhnya Kami adalah Pemberi balasan.'),
(4431, '044:017', '68', 44, 17, 'Sesungguhnya sebelum mereka telah Kami uji kaum Fir´aun dan telah datang kepada mereka seorang rasul yang mulia,'),
(4432, '044:018', '68', 44, 18, '(dengan berkata): \"Serahkanlah kepadaku hamba-hamba Allah (Bani Israil yang kamu perbudak). Sesungguhnya aku adalah utusan (Allah) yang dipercaya kepadamu,'),
(4433, '044:019', '68', 44, 19, 'dan janganlah kamu menyombongkan diri terhadap Allah. Sesungguhnya aku datang kepadamu dengan membawa bukti yang nyata.'),
(4434, '044:020', '68', 44, 20, 'Dan sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu, dari keinginanmu merajamku,'),
(4435, '044:021', '68', 44, 21, 'dan jika kamu tidak beriman kepadaku maka biarkanlah aku (memimpin Bani Israil)\".'),
(4436, '044:022', '68', 44, 22, 'Kemudian Musa berdoa kepada Tuhannya: \"Sesungguhnya mereka ini adalah kaum yang berdosa (segerakanlah azab kepada mereka)\".'),
(4437, '044:023', '68', 44, 23, '(Allah berfirman): \"Maka berjalanlah kamu dengan membawa hamba-hamba-Ku pada malam hari, sesungguhnya kamu akan dikejar,'),
(4438, '044:024', '68', 44, 24, 'dan biarkanlah laut itu tetap terbelah. Sesungguhnya mereka adalah tentara yang akan ditenggelamkan\".'),
(4439, '044:025', '68', 44, 25, 'Alangkah banyaknya taman dan mata air yang mereka tinggalkan,'),
(4440, '044:026', '68', 44, 26, 'dan kebun-kebun serta tempat-tempat yang indah-indah,'),
(4441, '044:027', '68', 44, 27, 'dan kesenangan-kesenangan yang mereka menikmatinya,'),
(4442, '044:028', '68', 44, 28, 'demikianlah. Dan Kami wariskan semua itu kepada kaum yang lain.'),
(4443, '044:029', '68', 44, 29, 'Maka langit dan bumi tidak menangisi mereka dan merekapun tidak diberi tangguh.'),
(4444, '044:030', '68', 44, 30, 'Dan sesungguhnya telah Kami selamatkan Bani Israil dari siksa yang menghinakan,'),
(4445, '044:031', '68', 44, 31, 'dari (azab) Fir´aun. Sesungguhnya dia adalah orang yang sombong, salah seorang dari orang-orang yang melampaui batas.'),
(4446, '044:032', '68', 44, 32, 'Dan sesungguhnya telah Kami pilih mereka dengan pengetahuan (Kami) atas bangsa-bangsa.'),
(4447, '044:033', '68', 44, 33, 'Dan Kami telah memberikan kepada mereka di antara tanda-tanda kekuasaan (Kami) sesuatu yang di dalamnya terdapat nikmat yang nyata.'),
(4448, '044:034', '68', 44, 34, 'Sesungguhnya mereka (kaum musyrik) itu benar-benar berkata,'),
(4449, '044:035', '68', 44, 35, '\"tidak ada kematian selain kematian di dunia ini. Dan kami sekali-kali tidak akan dibangkitkan,'),
(4450, '044:036', '68', 44, 36, 'maka datangkanlah (kembali) bapak-bapak kami jika kamu memang orang-orang yang benar\".'),
(4451, '044:037', '68', 44, 37, 'Apakah mereka (kaum musyrikin) yang lebih baik ataukah kaum Tubba´ dan orang-orang yang sebelum mereka. Kami telah membinasakan mereka karena sesungguhnya mereka adalah orang-orang yang berdosa.'),
(4452, '044:038', '68', 44, 38, 'Dan Kami tidak menciptakan langit dan bumi dan apa yang ada antara keduanya dengan bermain-main.'),
(4453, '044:039', '68', 44, 39, 'Kami tidak menciptakan keduanya melainkan dengan haq, tetapi kebanyakan mereka tidak mengetahui.'),
(4454, '044:040', '68', 44, 40, 'Sesungguhnya hari keputusan (hari kiamat) itu adalah waktu yang dijanjikan bagi mereka semuanya,'),
(4455, '044:041', '68', 44, 41, 'yaitu hari yang seorang karib tidak dapat memberi manfaat kepada karibnya sedikitpun, dan mereka tidak akan mendapat pertolongan,'),
(4456, '044:042', '68', 44, 42, 'kecuali orang yang diberi rahmat oleh Allah. Sesungguhnya Dialah Yang Maha Perkasa lagi Maha Penyayang.'),
(4457, '044:043', '68', 44, 43, 'Sesungguhnya pohon zaqqum itu,'),
(4458, '044:044', '68', 44, 44, 'makanan orang yang banyak berdosa.'),
(4459, '044:045', '68', 44, 45, '(Ia) sebagai kotoran minyak yang mendidih di dalam perut,'),
(4460, '044:046', '68', 44, 46, 'seperti mendidihnya air yang amat panas.'),
(4461, '044:047', '68', 44, 47, 'Peganglah dia kemudian seretlah dia ke tengah-tengah neraka.'),
(4462, '044:048', '68', 44, 48, 'Kemudian tuangkanlah di atas kepalanya siksaan (dari) air yang amat panas.'),
(4463, '044:049', '68', 44, 49, 'Rasakanlah, sesungguhnya kamu orang yang perkasa lagi mulia.'),
(4464, '044:050', '68', 44, 50, 'Sesungguhnya ini adalah azab yang dahulu selalu kamu meragu-ragukannya.'),
(4465, '044:051', '68', 44, 51, 'Sesungguhnya orang-orang yang bertakwa berada dalam tempat yang aman,'),
(4466, '044:052', '68', 44, 52, '(yaitu) di dalam taman-taman dan mata-air-mata-air;'),
(4467, '044:053', '68', 44, 53, 'mereka memakai sutera yang halus dan sutera yang tebal, (duduk) berhadap-hadapan,'),
(4468, '044:054', '68', 44, 54, 'demikianlah. Dan Kami berikan kepada mereka bidadari.'),
(4469, '044:055', '68', 44, 55, 'Di dalamnya mereka meminta segala macam buah-buahan dengan aman (dari segala kekhawatiran),'),
(4470, '044:056', '68', 44, 56, 'mereka tidak akan merasakan mati di dalamnya kecuali mati di dunia. Dan Allah memelihara mereka dari azab neraka,'),
(4471, '044:057', '68', 44, 57, 'sebagai karunia dari Tuhanmu. Yang demikian itu adalah keberuntungan yang besar.'),
(4472, '044:058', '68', 44, 58, 'Sesungguhnya Kami mudahkan Al Quran itu dengan bahasamu supaya mereka mendapat pelajaran.'),
(4473, '044:059', '68', 44, 59, 'Maka tunggulah; sesungguhnya mereka itu menunggu (pula).'),
(4474, '045:001', '68', 45, 1, 'Haa Miim'),
(4475, '045:002', '68', 45, 2, 'Kitab (ini) diturunkan dari Allah Yang Maha Perkasa lagi Maha Bijaksana.'),
(4476, '045:003', '68', 45, 3, 'Sesungguhnya pada langit dan bumi benar-benar terdapat tanda-tanda (kekuasaan Allah) untuk orang-orang yang beriman.'),
(4477, '045:004', '68', 45, 4, 'Dan pada penciptakan kamu dan pada binatang-binatang yang melata yang bertebaran (di muka bumi) terdapat tanda-tanda (kekuasaan Allah) untuk kaum yang meyakini,'),
(4478, '045:005', '68', 45, 5, 'dan pada pergantian malam dan siang dan hujan yang diturunkan Allah dari langit lalu dihidupkan-Nya dengan air hujan itu bumi sesudah matinya; dan pada perkisaran angin terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang berakal.'),
(4479, '045:006', '68', 45, 6, 'Itulah ayat-ayat Allah yang Kami membacakannya kepadamu dengan sebenarnya; maka dengan perkataan manakah lagi mereka akan beriman sesudah (kalam) Allah dan keterangan-keterangan-Nya.'),
(4480, '045:007', '68', 45, 7, 'Kecelakaan besarlah bagi tiap-tiap orang yang banyak berdusta lagi banyak berdosa,'),
(4481, '045:008', '68', 45, 8, 'dia mendengar ayat-ayat Allah dibacakan kepadanya kemudian dia tetap menyombongkan diri seakan-akan dia tidak mendengarnya. Maka beri khabar gembiralah dia dengan azab yang pedih.'),
(4482, '045:009', '68', 45, 9, 'Dan apabila dia mengetahui barang sedikit tentang ayat-ayat Kami, maka ayat-ayat itu dijadikan olok-olok. Merekalah yang memperoleh azab yang menghinakan.'),
(4483, '045:010', '68', 45, 10, 'Di hadapan mereka neraka Jahannam dan tidak akan berguna bagi mereka sedikitpun apa yang telah mereka kerjakan, dan tidak pula berguna apa yang mereka jadikan sebagai sembahan-sembahan (mereka) dari selain Allah. Dan bagi mereka azab yang besar.'),
(4484, '045:011', '68', 45, 11, 'Ini (Al Quran) adalah petunjuk. Dan orang-orang yang kafir kepada ayat-ayat Tuhannya bagi mereka azab yaitu siksaan yang sangat pedih.'),
(4485, '045:012', '68', 45, 12, 'Allah-lah yang menundukkan lautan untukmu supaya kapal-kapal dapat berlayar padanya dengan seizin-Nya dan supaya kamu dapat mencari karunia -Nya dan mudah-mudahan kamu bersyukur.'),
(4486, '045:013', '68', 45, 13, 'Dan Dia telah menundukkan untukmu apa yang di langit dan apa yang di bumi semuanya, (sebagai rahmat) daripada-Nya. Sesungguhnya pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang berfikir.'),
(4487, '045:014', '68', 45, 14, 'Katakanlah kepada orang-orang yang beriman hendaklah mereka memaafkan orang-orang yang tiada takut hari-hari Allah karena Dia akan membalas sesuatu kaum terhadap apa yang telah mereka kerjakan.'),
(4488, '045:015', '68', 45, 15, 'Barangsiapa yang mengerjakan amal saleh, maka itu adalah untuk dirinya sendiri, dan barangsiapa yang mengerjakan kejahatan, maka itu akan menimpa dirinya sendiri, kemudian kepada Tuhanmulah kamu dikembalikan.'),
(4489, '045:016', '68', 45, 16, 'Dan sesungguhnya telah Kami berikan kepada Bani Israil Al Kitab (Taurat), kekuasaan dan kenabian dan Kami berikan kepada mereka rezeki-rezeki yang baik dan Kami lebihkan mereka atas bangsa-bangsa (pada masanya).'),
(4490, '045:017', '68', 45, 17, 'Dan Kami berikan kepada mereka keterangan-keterangan yang nyata tentang urusan (agama); maka mereka tidak berselisih melainkan sesudah datang kepada mereka pengetahuan karena kedengkian yang ada di antara mereka. Sesungguhnya Tuhanmu akan memutuskan antara mereka pada hari kiamat terhadap apa yang mereka selalu berselisih padanya.'),
(4491, '045:018', '68', 45, 18, 'Kemudian Kami jadikan kamu berada di atas suatu syariat (peraturan) dari urusan (agama itu), maka ikutilah syariat itu dan janganlah kamu ikuti hawa nafsu orang-orang yang tidak mengetahui.'),
(4492, '045:019', '68', 45, 19, 'Sesungguhnya mereka sekali-kali tidak akan dapat menolak dari kamu sedikitpun dari siksaan Allah. Dan Sesungguhnya orang-orang yang zalim itu sebagian mereka menjadi penolong bagi sebagian yang lain, dan Allah adalah pelindung orang-orang yang bertakwa.'),
(4493, '045:020', '68', 45, 20, 'Al Quran ini adalah pedoman bagi manusia, petunjuk dan rahmat bagi kaum yang meyakini.'),
(4494, '045:021', '68', 45, 21, 'Apakah orang-orang yang membuat kejahatan itu menyangka bahwa Kami akan menjadikan mereka seperti orang-orang yang beriman dan mengerjakan amal saleh, yaitu sama antara kehidupan dan kematian mereka? Amat buruklah apa yang mereka sangka itu.'),
(4495, '045:022', '68', 45, 22, 'Dan Allah menciptakan langit dan bumi dengan tujuan yang benar dan agar dibalasi tiap-tiap diri terhadap apa yang dikerjakannya, dan mereka tidak akan dirugikan.'),
(4496, '045:023', '68', 45, 23, 'Maka pernahkah kamu melihat orang yang menjadikan hawa nafsunya sebagai tuhannya dan Allah membiarkannya berdasarkan ilmu-Nya dan Allah telah mengunci mati pendengaran dan hatinya dan meletakkan tutupan atas penglihatannya? Maka siapakah yang akan memberinya petunjuk sesudah Allah (membiarkannya sesat). Maka mengapa kamu tidak mengambil pelajaran?'),
(4497, '045:024', '68', 45, 24, 'Dan mereka berkata: \"Kehidupan ini tidak lain hanyalah kehidupan di dunia saja, kita mati dan kita hidup dan tidak ada yang akan membinasakan kita selain masa\", dan mereka sekali-kali tidak mempunyai pengetahuan tentang itu, mereka tidak lain hanyalah menduga-duga saja.'),
(4498, '045:025', '68', 45, 25, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang jelas, tidak ada bantahan mereka selain dari mengatakan: \"Datangkanlah nenek moyang kami jika kamu adalah orang-orang yang benar\".'),
(4499, '045:026', '68', 45, 26, 'Katakanlah: \"Allah-lah yang menghidupkan kamu kemudian mematikan kamu, setelah itu mengumpulkan kamu pada hari kiamat yang tidak ada keraguan padanya; akan tetapi kebanyakan manusia tidak mengetahui.'),
(4500, '045:027', '68', 45, 27, 'Dan hanya kepunyaan Allah kerajaan langit dan bumi. Dan pada hari terjadinya kebangkitan, akan rugilah pada hari itu orang-orang yang mengerjakan kebathilan.'),
(4501, '045:028', '68', 45, 28, 'Dan (pada hari itu) kamu lihat tiap-tiap umat berlutut. Tiap-tiap umat dipanggil untuk (melihat) buku catatan amalnya. Pada hari itu kamu diberi balasan terhadap apa yang telah kamu kerjakan.'),
(4502, '045:029', '68', 45, 29, '(Allah berfirman): \"Inilah kitab (catatan) Kami yang menuturkan terhadapmu dengan benar. Sesungguhnya Kami telah menyuruh mencatat apa yang telah kamu kerjakan\".'),
(4503, '045:030', '68', 45, 30, 'Adapun orang-orang yang beriman dan mengerjakan amal saleh maka Tuhan mereka memasukkan mereka ke dalam rahmat-Nya (surga). Itulah keberuntungan yang nyata.'),
(4504, '045:031', '68', 45, 31, 'Dan adapun orang-orang yang kafir (kepada mereka dikatakan): \"Maka apakah belum ada ayat-ayat-Ku yang dibacakan kepadamu lalu kamu menyombongkan diri dan kamu jadi kaum yang berbuat dosa?\"'),
(4505, '045:032', '68', 45, 32, 'Dan apabila dikatakan (kepadamu): \"Sesungguhnya janji Allah itu adalah benar dan hari berbangkit itu tidak ada keraguan padanya\", niscaya kamu menjawab: \"Kami tidak tahu apakah hari kiamat itu, kami sekali-kali tidak lain hanyalah menduga-duga saja dan kami sekali-kali tidak meyakini(nya)\".'),
(4506, '045:033', '68', 45, 33, 'Dan nyatalah bagi mereka keburukan-keburukan dari apa yang mereka kerjakan dan mereka diliputi oleh (azab) yang mereka selalu memperolok-olokkannya.'),
(4507, '045:034', '68', 45, 34, 'Dan dikatakan (kepada mereka): \"Pada hari ini Kami melupakan kamu sebagaimana kamu telah melupakan pertemuan (dengan) harimu ini dan tempat kembalimu ialah neraka dan kamu sekali-kali tidak memperoleh penolong\".'),
(4508, '045:035', '68', 45, 35, 'Yang demikian itu, karena sesungguhnya kamu menjadikan ayat-ayat Allah sebagai olok-olokan dan kamu telah ditipu oleh kehidupan dunia, maka pada hari ini mereka tidak dikeluarkan dari neraka dan tidak pula mereka diberi kesempatan untuk bertaubat.'),
(4509, '045:036', '68', 45, 36, 'Maka bagi Allah-lah segala puji, Tuhan langit dan Tuhan bumi, Tuhan semesta alam.'),
(4510, '045:037', '68', 45, 37, 'Dan bagi-Nya-lah keagungan di langit dan bumi, Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(4511, '046:001', '68', 46, 1, 'Haa Miim.'),
(4512, '046:002', '68', 46, 2, 'Diturunkan Kitab ini dari Allah Yang Maha Perkasa lagi Maha Bijaksana.'),
(4513, '046:003', '68', 46, 3, 'Kami tiada menciptakan langit dan bumi dan apa yang ada antara keduanya melainkan dengan (tujuan) yang benar dan dalam waktu yang ditentukan. Dan orang-orang yang kafir berpaling dari apa yang diperingatkan kepada mereka.'),
(4514, '046:004', '68', 46, 4, 'Katakanlah: \"Terangkanlah kepadaku tentang apa yang kamu sembah selain Allah; perlihatkan kepada-Ku apakah yang telah mereka ciptakan dari bumi ini atau adakah mereka berserikat (dengan Allah) dalam (penciptaan) langit? Bawalah kepada-Ku Kitab yang sebelum (Al Quran) ini atau peninggalan dari pengetahuan (orang-orang dahulu), jika kamu adalah orang-orang yang benar\"'),
(4515, '046:005', '68', 46, 5, 'Dan siapakah yang lebih sesat daripada orang yang menyembah sembahan-sembahan selain Allah yang tiada dapat memperkenankan (doa)nya sampai hari kiamat dan mereka lalai dari (memperhatikan) doa mereka?'),
(4516, '046:006', '68', 46, 6, 'Dan apabila manusia dikumpulkan (pada hari kiamat) niscaya sembahan-sembahan itu menjadi musuh mereka dan mengingkari pemujaan-pemujaan mereka.'),
(4517, '046:007', '68', 46, 7, 'Dan apabila dibacakan kepada mereka ayat-ayat Kami yang menjelaskan, berkatalah orang-orang yang mengingkari kebenaran ketika kebenaran itu datang kepada mereka: \"Ini adalah sihir yang nyata\".'),
(4518, '046:008', '68', 46, 8, 'Bahkan mereka mengatakan: \"Dia (Muhammad) telah mengada-adakannya (Al Quran)\". Katakanlah: \"Jika aku mengada-adakannya, maka kamu tiada mempunyai kuasa sedikitpun mempertahankan aku dari (azab) Allah itu. Dia lebih mengetahui apa-apa yang kamu percakapkan tentang Al Quran itu. Cukuplah Dia menjadi saksi antaraku dan antaramu dan Dialah Yang Maha Pengampun lagi Maha Penyayang\".'),
(4519, '046:009', '68', 46, 9, 'Katakanlah: \"Aku bukanlah rasul yang pertama di antara rasul-rasul dan aku tidak mengetahui apa yang akan diperbuat terhadapku dan tidak (pula) terhadapmu. Aku tidak lain hanyalah mengikuti apa yang diwahyukan kepadaku dan aku tidak lain hanyalah seorang pemberi peringatan yang menjelaskan\".'),
(4520, '046:010', '68', 46, 10, 'Katakanlah: \"Terangkanlah kepadaku, bagaimanakah pendapatmu jika Al Quran itu datang dari sisi Allah, padahal kamu mengingkarinya dan seorang saksi dari Bani Israil mengakui (kebenaran) yang serupa dengan (yang tersebut dalam) Al Quran lalu dia beriman, sedang kamu menyombongkan diri. Sesungguhnya Allah tiada memberi petunjuk kepada orang-orang yang zalim\".'),
(4521, '046:011', '68', 46, 11, 'Dan orang-orang kafir berkata kepada orang-orang yang beriman: \"Kalau sekiranya di (Al Quran) adalah suatu yang baik, tentulah mereka tiada mendahului kami (beriman) kepadanya. Dan karena mereka tidak mendapat petunjuk dengannya maka mereka akan berkata: \"Ini adalah dusta yang lama\".'),
(4522, '046:012', '68', 46, 12, 'Dan sebelum Al Quran itu telah ada kitab Musa sebagai petunjuk dan rahmat. Dan ini (Al Quran) adalah kitab yang membenarkannya dalam bahasa Arab untuk memberi peringatan kepada orang-orang yang zalim dan memberi kabar gembira kepada orang-orang yang berbuat baik.'),
(4523, '046:013', '68', 46, 13, 'Sesungguhnya orang-orang yang mengatakan: \"Tuhan kami ialah Allah\", kemudian mereka tetap istiqamah maka tidak ada kekhawatiran terhadap mereka dan mereka tiada (pula) berduka cita.'),
(4524, '046:014', '68', 46, 14, 'Mereka itulah penghuni-penghuni surga, mereka kekal di dalamnya; sebagai balasan atas apa yang telah mereka kerjakan.'),
(4525, '046:015', '68', 46, 15, 'Kami perintahkan kepada manusia supaya berbuat baik kepada dua orang ibu bapaknya, ibunya mengandungnya dengan susah payah, dan melahirkannya dengan susah payah (pula). Mengandungnya sampai menyapihnya adalah tiga puluh bulan, sehingga apabila dia telah dewasa dan umurnya sampai empat puluh tahun ia berdoa: \"Ya Tuhanku, tunjukilah aku untuk mensyukuri nikmat Engkau yang telah Engkau berikan kepadaku dan kepada ibu bapakku dan supaya aku dapat berbuat amal yang saleh yang Engkau ridhai; berilah kebaikan kepadaku dengan (memberi kebaikan) kepada anak cucuku. Sesungguhnya aku bertaubat kepada Engkau dan sesungguhnya aku termasuk orang-orang yang berserah diri\".'),
(4526, '046:016', '68', 46, 16, 'Mereka itulah orang-orang yang Kami terima dari mereka amal yang baik yang telah mereka kerjakan dan Kami ampuni kesalahan-kesalahan mereka, bersama penghuni-penghuni surga, sebagai janji yang benar yang telah dijanjikan kepada mereka.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(4527, '046:017', '68', 46, 17, 'Dan orang yang berkata kepada dua orang ibu bapaknya: \"Cis bagi kamu keduanya, apakah kamu keduanya memperingatkan kepadaku bahwa aku akan dibangkitkan, padahal sungguh telah berlalu beberapa umat sebelumku? lalu kedua ibu bapaknya itu memohon pertolongan kepada Allah seraya mengatakan: \"Celaka kamu, berimanlah! Sesungguhnya janji Allah adalah benar\". Lalu dia berkata: \"Ini tidak lain hanyalah dongengan orang-orang dahulu belaka\".'),
(4528, '046:018', '68', 46, 18, 'Mereka itulah orang-orang yang telah pasti ketetapan (azab) atas mereka bersama umat-umat yang telah berlalu sebelum mereka dari jin dan manusia. Sesungguhnya mereka adalah orang-orang yang merugi.'),
(4529, '046:019', '68', 46, 19, 'Dan bagi masing-masing mereka derajat menurut apa yang telah mereka kerjakan dan agar Allah mencukupkan bagi mereka (balasan) pekerjaan-pekerjaan mereka sedang mereka tiada dirugikan.'),
(4530, '046:020', '68', 46, 20, 'Dan (ingatlah) hari (ketika) orang-orang kafir dihadapkan ke neraka (kepada mereka dikatakan): \"Kamu telah menghabiskan rezekimu yang baik dalam kehidupan duniawimu (saja) dan kamu telah bersenang-senang dengannya; maka pada hari ini kamu dibalasi dengan azab yang menghinakan karena kamu telah menyombongkan diri di muka bumi tanpa hak dan karena kamu telah fasik\".'),
(4531, '046:021', '68', 46, 21, 'Dan ingatlah (Hud) saudara kaum ´Aad yaitu ketika dia memberi peringatan kepada kaumnya di Al Ahqaaf dan sesungguhnya telah terdahulu beberapa orang pemberi peringatan sebelumnya dan sesudahnya (dengan mengatakan): \"Janganlah kamu menyembah selain Allah, sesungguhnya aku khawatir kamu akan ditimpa azab hari yang besar\".'),
(4532, '046:022', '68', 46, 22, 'Mereka menjawab: \"Apakah kamu datang kepada kami untuk memalingkan kami dari (menyembah) tuhan-tuhan kami? Maka datangkanlah kepada kami azab yang telah kamu ancamkan kepada kami jika kamu termasuk orang-orang yang benar\".'),
(4533, '046:023', '68', 46, 23, 'Ia berkata: \"Sesungguhnya pengetahuan (tentang itu) hanya pada sisi Allah dan aku (hanya) menyampaikan kepadamu apa yang aku diutus dengan membawanya tetapi aku lihat kamu adalah kaum yang bodoh\".'),
(4534, '046:024', '68', 46, 24, 'Maka tatkala mereka melihat azab itu berupa awan yang menuju ke lembah-lembah mereka, berkatalah mereka: \"Inilah awan yang akan menurunkan hujan kepada kami\". (Bukan!) bahkan itulah azab yang kamu minta supaya datang dengan segera (yaitu) angin yang mengandung azab yang pedih,'),
(4535, '046:025', '68', 46, 25, 'yang menghancurkan segala sesuatu dengan perintah Tuhannya, maka jadilah mereka tidak ada yang kelihatan lagi kecuali (bekas-bekas) tempat tinggal mereka. Demikianlah Kami memberi balasan kepada kaum yang berdosa.'),
(4536, '046:026', '68', 46, 26, 'Dan sesungguhnya Kami telah meneguhkan kedudukan mereka dalam hal-hal yang Kami belum pernah meneguhkan kedudukanmu dalam hal itu dan Kami telah memberikan kepada mereka pendengaran, penglihatan dan hati; tetapi pendengaran, penglihatan dan hati mereka itu tidak berguna sedikit juapun bagi mereka, karena mereka selalu mengingkari ayat-ayat Allah dan mereka telah diliputi oleh siksa yang dahulu selalu mereka memperolok-olokkannya.'),
(4537, '046:027', '68', 46, 27, 'Dan sesungguhnya Kami telah membinasakan negeri-negeri di sekitarmu dan Kami telah mendatangkan tanda-tanda kebesaran Kami berulang-ulang supaya mereka kembali (bertaubat).'),
(4538, '046:028', '68', 46, 28, 'Maka mengapa yang mereka sembah selain Allah sebagai Tuhan untuk mendekatkan diri (kepada Allah) tidak dapat menolong mereka. Bahkan tuhan-tuhan itu telah lenyap dari mereka? Itulah akibat kebohongan mereka dan apa yang dahulu mereka ada-adakan.'),
(4539, '046:029', '68', 46, 29, 'Dan (ingatlah) ketika Kami hadapkan serombongan jin kepadamu yang mendengarkan Al Quran, maka tatkala mereka menghadiri pembacaan(nya) lalu mereka berkata: \"Diamlah kamu (untuk mendengarkannya)\". Ketika pembacaan telah selesai mereka kembali kepada kaumnya (untuk) memberi peringatan.'),
(4540, '046:030', '68', 46, 30, 'Mereka berkata: \"Hai kaum kami, sesungguhnya kami telah mendengarkan kitab (Al Quran) yang telah diturunkan sesudah Musa yang membenarkan kitab-kitab yang sebelumnya lagi memimpin kepada kebenaran dan kepada jalan yang lurus.'),
(4541, '046:031', '68', 46, 31, 'Hai kaum kami, terimalah (seruan) orang yang menyeru kepada Allah dan berimanlah kepada-Nya, niscaya Allah akan mengampuni dosa-dosa kamu dan melepaskan kamu dari azab yang pedih.'),
(4542, '046:032', '68', 46, 32, 'Dan orang yang tidak menerima (seruan) orang yang menyeru kepada Allah maka dia tidak akan melepaskan diri dari azab Allah di muka bumi dan tidak ada baginya pelindung selain Allah. Mereka itu dalam kesesatan yang nyata\".'),
(4543, '046:033', '68', 46, 33, 'Dan apakah mereka tidak memperhatikan bahwa sesungguhnya Allah yang menciptakan langit dan bumi dan Dia tidak merasa payah karena menciptakannya, kuasa menghidupkan orang-orang mati? Ya (bahkan) sesungguhnya Dia Maha Kuasa atas segala sesuatu.'),
(4544, '046:034', '68', 46, 34, 'Dan (ingatlah) hari (ketika) orang-orang kafir dihadapkan kepada neraka, (dikatakan kepada mereka): \"Bukankah (azab) ini benar?\" Mereka menjawab: \"Ya benar, demi Tuhan kami\". Allah berfirman \"Maka rasakanlah azab ini disebabkan kamu selalu ingkar\".'),
(4545, '046:035', '68', 46, 35, 'Maka bersabarlah kamu seperti orang-orang yang mempunyai keteguhan hati dari rasul-rasul telah bersabar dan janganlah kamu meminta disegerakan (azab) bagi mereka. Pada hari mereka melihat azab yang diancamkan kepada mereka (merasa) seolah-olah tidak tinggal (di dunia) melainkan sesaat pada siang hari. (Inilah) suatu pelajaran yang cukup, maka tidak dibinasakan melainkan kaum yang fasik.'),
(4546, '047:001', '68', 47, 1, 'Orang-orang yang kafir dan menghalangi (manusia) dari jalan Allah, Allah menyesatkan perbuatan-perbuatan mereka.'),
(4547, '047:002', '68', 47, 2, 'Dan orang-orang mukmin dan beramal soleh serta beriman kepada apa yang diturunkan kepada Muhammad dan itulah yang haq dari Tuhan mereka, Allah menghapuskan kesalahan-kesalahan mereka dan memperbaiki keadaan mereka.'),
(4548, '047:003', '68', 47, 3, 'Yang demikian adalah karena sesungguhnya orang-orang kafir mengikuti yang bathil dan sesungguhnya orang-orang mukmin mengikuti yang haq dari Tuhan mereka. Demikianlah Allah membuat untuk manusia perbandingan-perbandingan bagi mereka.'),
(4549, '047:004', '68', 47, 4, 'Apabila kamu bertemu dengan orang-orang kafir (di medan perang) maka pancunglah batang leher mereka. Sehingga apabila kamu telah mengalahkan mereka maka tawanlah mereka dan sesudah itu kamu boleh membebaskan mereka atau menerima tebusan sampai perang berakhir. Demikianlah apabila Allah menghendaki niscaya Allah akan membinasakan mereka tetapi Allah hendak menguji sebahagian kamu dengan sebahagian yang lain. Dan orang-orang yang syahid pada jalan Allah, Allah tidak akan menyia-nyiakan amal mereka.'),
(4550, '047:005', '68', 47, 5, 'Allah akan memberi pimpinan kepada mereka dan memperbaiki keadaan mereka,'),
(4551, '047:006', '68', 47, 6, 'dan memasukkan mereka ke dalam jannah yang telah diperkenankan-Nya kepada mereka.'),
(4552, '047:007', '68', 47, 7, 'Hai orang-orang mukmin, jika kamu menolong (agama) Allah, niscaya Dia akan menolongmu dan meneguhkan kedudukanmu.'),
(4553, '047:008', '68', 47, 8, 'Dan orang-orang yang kafir, maka kecelakaanlah bagi mereka dan Allah menyesatkan amal-amal mereka.'),
(4554, '047:009', '68', 47, 9, 'Yang demikian itu adalah karena Sesungguhnya mereka benci kepada apa yang diturunkan Allah (Al Quran) lalu Allah menghapuskan (pahala-pahala) amal-amal mereka.'),
(4555, '047:010', '68', 47, 10, 'Maka apakah mereka tidak mengadakan perjalanan di muka bumi sehingga mereka dapat memperhatikan bagaimana kesudahan orang-orang sebelum mereka; Allah telah menimpakan kebinasaan atas mereka dan orang-orang kafir akan menerima (akibat-akibat) seperti itu.'),
(4556, '047:011', '68', 47, 11, 'Yang demikian itu karena sesungguhnya Allah adalah pelindung orang-orang yang beriman dan karena sesungguhnya orang-orang kafir itu tidak mempunyai pelindung.'),
(4557, '047:012', '68', 47, 12, 'Sesungguhnya Allah memasukkan orang-orang mukmin dan beramal saleh ke dalam jannah yang mengalir di bawahnya sungai-sungai. Dan orang-orang kafir bersenang-senang (di dunia) dan mereka makan seperti makannya binatang. Dan jahannam adalah tempat tinggal mereka.'),
(4558, '047:013', '68', 47, 13, 'Dan betapa banyaknya negeri yang (penduduknya) lebih kuat dari pada (penduduk) negerimu (Muhammad) yang telah mengusirmu itu. Kami telah membinasakan mereka, maka tidak ada seorang penolongpun bagi mereka.'),
(4559, '047:014', '68', 47, 14, 'Maka apakah orang yang berpegang pada keterangan yang datang dari Rabbnya sama dengan orang yang (shaitan) menjadikan dia memandang baik perbuatannya yang buruk itu dan mengikuti hawa nafsunya?'),
(4560, '047:015', '68', 47, 15, '(Apakah) perumpamaan (penghuni) jannah yang dijanjikan kepada orang-orang yang bertakwa yang di dalamnya ada sungai-sungai dari air yang tiada berubah rasa dan baunya, sungai-sungai dari air susu yang tidak berubah rasanya, sungai-sungai dari khamar yang lezat rasanya bagi peminumnya dan sungai-sungai dari madu yang disaring; dan mereka memperoleh di dalamnya segala macam buah-buahan dan ampunan dari Rabb mereka, sama dengan orang yang kekal dalam jahannam dan diberi minuman dengan air yang mendidih sehingga memotong ususnya?'),
(4561, '047:016', '68', 47, 16, 'Dan di antara mereka ada orang yang mendengarkan perkataanmu sehingga apabila mereka keluar dari sisimu orang-orang berkata kepada orang yang telah diberi ilmu pengetahuan (sahabat-sahabat Nabi): \"Apakah yang dikatakannya tadi?\" Mereka itulah orang-orang yang dikunci mati hati mereka oleh Allah dan mengikuti hawa nafsu mereka.'),
(4562, '047:017', '68', 47, 17, 'Dan orang-orang yang mau menerima petunjuk, Allah menambah petunjuk kepada mereka dan memberikan balasan ketakwaannya.'),
(4563, '047:018', '68', 47, 18, 'Maka tidaklah yang mereka tunggu-tunggu melainkan hari kiamat (yaitu) kedatangannya kepada mereka dengan tiba-tiba, karena sesungguhnya telah datang tanda-tandanya. Maka apakah faedahnya bagi mereka kesadaran mereka itu apabila Kiamat sudah datang?'),
(4564, '047:019', '68', 47, 19, 'Maka ketahuilah, bahwa sesungguhnya tidak ada Ilah (sesembahan, tuhan) selain Allah dan mohonlah ampunan bagi dosamu dan bagi (dosa) orang-orang mukmin, laki-laki dan perempuan. Dan Allah mengetahui tempat kamu berusaha dan tempat kamu tinggal.'),
(4565, '047:020', '68', 47, 20, 'Dan orang-orang yang beriman berkata: \"Mengapa tiada diturunkan suatu surat?\" Maka apabila diturunkan suatu surat yang jelas maksudnya dan disebutkan di dalamnya (perintah) perang, kamu lihat orang-orang yang ada penyakit di dalam hatinya memandang kepadamu seperti pandangan orang yang pingsan karena takut mati, dan kecelakaanlah bagi mereka.'),
(4566, '047:021', '68', 47, 21, 'Taat dan mengucapkan perkataan yang baik (adalah lebih baik bagi mereka). Apabila telah tetap perintah perang (mereka tidak menyukainya). Tetapi jikalau mereka benar (imannya) terhadap Allah, niscaya yang demikian itu lebih baik bagi mereka.'),
(4567, '047:022', '68', 47, 22, 'Maka apakah kiranya jika kamu berkuasa kamu akan membuat kerusakan di muka bumi dan memutuskan hubungan kekeluargaan?'),
(4568, '047:023', '68', 47, 23, 'Mereka itulah orang-orang yang dilaknati Allah dan ditulikan-Nya telinga mereka dan dibutakan-Nya penglihatan mereka.'),
(4569, '047:024', '68', 47, 24, 'Maka apakah mereka tidak memperhatikan Al Quran ataukah hati mereka terkunci?'),
(4570, '047:025', '68', 47, 25, 'Sesungguhnya orang-orang yang kembali ke belakang (kepada kekafiran) sesudah petunjuk itu jelas bagi mereka, syaitan telah menjadikan mereka mudah (berbuat dosa) dan memanjangkan angan-angan mereka.'),
(4571, '047:026', '68', 47, 26, 'Yang demikian itu karena sesungguhnya mereka (orang-orang munafik) itu berkata kepada orang-orang yang benci kepada apa yang diturunkan Allah (orang-orang Yahudi): \"Kami akan mematuhi kamu dalam beberapa urusan\", sedang Allah mengetahui rahasia mereka.'),
(4572, '047:027', '68', 47, 27, 'Bagaimanakah (keadaan mereka) apabila malaikat mencabut nyawa mereka seraya memukul-mukul muka mereka dan punggung mereka?'),
(4573, '047:028', '68', 47, 28, 'Yang demikian itu adalah karena sesungguhnya mereka mengikuti apa yang menimbulkan kemurkaan Allah dan karena mereka membenci keridhaan-Nya, sebab itu Allah menghapus (pahala) amal-amal mereka.'),
(4574, '047:029', '68', 47, 29, 'Atau apakah orang-orang yang ada penyakit dalam hatinya mengira bahwa Allah tidak akan menampakkan kedengkian mereka?'),
(4575, '047:030', '68', 47, 30, 'Dan kalau Kami kehendaki, niscaya Kami tunjukkan mereka kepadamu sehingga kamu benar-benar dapat mengenal mereka dengan tanda-tandanya. Dan kamu benar-benar akan mengenal mereka dari kiasan-kiasan perkataan mereka dan Allah mengetahui perbuatan-perbuatan kamu.'),
(4576, '047:031', '68', 47, 31, 'Dan sesungguhnya Kami benar-benar akan menguji kamu agar Kami mengetahui orang-orang yang berjihad dan bersabar di antara kamu, dan agar Kami menyatakan (baik buruknya) hal ihwalmu.'),
(4577, '047:032', '68', 47, 32, 'Sesungguhnya orang-orang kafir dan (yang) menghalangi manusia dari jalan Allah serta memusuhi Rasul setelah petunjuk itu jelas bagi mereka, mereka tidak dapat memberi mudharat kepada Allah sedikitpun. Dan Allah akan menghapuskan (pahala) amal-amal mereka.'),
(4578, '047:033', '68', 47, 33, 'Hai orang-orang yang beriman, taatilah Allah dan taatilah Rasul dan janganlah kamu merusakkan (pahala) amal-amalmu.'),
(4579, '047:034', '68', 47, 34, 'Sesungguhnya orang-orang kafir dan (yang) menghalangi manusia dari jalan Allah kemudian mereka mati dalam keadaan kafir, maka sekali-kali Allah tidak akan memberi ampun kepada mereka.'),
(4580, '047:035', '68', 47, 35, 'Janganlah kamu lemah dan minta damai padahal kamulah yang di atas dan Allah pun bersamamu dan Dia sekali-kali tidak akan mengurangi pahala amal-amalmu.'),
(4581, '047:036', '68', 47, 36, 'Sesungguhnya kehidupan dunia hanyalah permainan dan senda gurau. Dan jika kamu beriman dan bertakwa, Allah akan memberikan pahala kepadamu dan Dia tidak akan meminta harta-hartamu.'),
(4582, '047:037', '68', 47, 37, 'Jika Dia meminta harta kepadamu lalu mendesak kamu (supaya memberikan semuanya) niscaya kamu akan kikir dan Dia akan menampakkan kedengkianmu.'),
(4583, '047:038', '68', 47, 38, 'Ingatlah, kamu ini orang-orang yang diajak untuk menafkahkan (hartamu) pada jalan Allah. Maka di antara kamu ada yang kikir, dan siapa yang kikir sesungguhnya dia hanyalah kikir terhadap dirinya sendiri. Dan Allah-lah yang Maha Kaya sedangkan kamulah orang-orang yang berkehendak (kepada-Nya); dan jika kamu berpaling niscaya Dia akan mengganti (kamu) dengan kaum yang lain; dan mereka tidak akan seperti kamu ini.'),
(4584, '048:001', '68', 48, 1, 'Sesungguhnya Kami telah memberikan kepadamu kemenangan yang nyata,'),
(4585, '048:002', '68', 48, 2, 'supaya Allah memberi ampunan kepadamu terhadap dosamu yang telah lalu dan yang akan datang serta menyempurnakan nikmat-Nya atasmu dan memimpin kamu kepada jalan yang lurus,'),
(4586, '048:003', '68', 48, 3, 'dan supaya Allah menolongmu dengan pertolongan yang kuat (banyak).'),
(4587, '048:004', '68', 48, 4, 'Dialah yang telah menurunkan ketenangan ke dalam hati orang-orang mukmin supaya keimanan mereka bertambah di samping keimanan mereka (yang telah ada). Dan kepunyaan Allah-lah tentara langit dan bumi dan adalah Allah Maha Mengetahui lagi Maha Bijaksana,'),
(4588, '048:005', '68', 48, 5, 'supaya Dia memasukkan orang-orang mukmin laki-laki dan perempuan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya dan supaya Dia menutupi kesalahan-kesalahan mereka. Dan yang demikian itu adalah keberuntungan yang besar di sisi Allah,'),
(4589, '048:006', '68', 48, 6, 'dan supaya Dia mengazab orang-orang munafik laki-laki dan perempuan dan orang-orang musyrik laki-laki dan perempuan yang mereka itu berprasangka buruk terhadap Allah. Mereka akan mendapat giliran (kebinasaan) yang amat buruk dan Allah memurkai dan mengutuk mereka serta menyediakan bagi mereka neraka Jahannam. Dan (neraka Jahannam) itulah sejahat-jahat tempat kembali.'),
(4590, '048:007', '68', 48, 7, 'Dan kepunyaan Allah-lah tentara langit dan bumi. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.'),
(4591, '048:008', '68', 48, 8, 'Sesungguhnya Kami mengutus kamu sebagai saksi, pembawa berita gembira dan pemberi peringatan,'),
(4592, '048:009', '68', 48, 9, 'supaya kamu sekalian beriman kepada Allah dan Rasul-Nya, menguatkan (agama)Nya, membesarkan-Nya. Dan bertasbih kepada-Nya di waktu pagi dan petang.'),
(4593, '048:010', '68', 48, 10, 'Bahwasanya orang-orang yang berjanji setia kepada kamu sesungguhnya mereka berjanji setia kepada Allah. Tangan Allah di atas tangan mereka, maka barangsiapa yang melanggar janjinya niscaya akibat ia melanggar janji itu akan menimpa dirinya sendiri dan barangsiapa menepati janjinya kepada Allah maka Allah akan memberinya pahala yang besar.'),
(4594, '048:011', '68', 48, 11, 'Orang-orang Badwi yang tertinggal (tidak turut ke Hudaibiyah) akan mengatakan: \"Harta dan keluarga kami telah merintangi kami, maka mohonkanlah ampunan untuk kami\"; mereka mengucapkan dengan lidahnya apa yang tidak ada dalam hatinya. Katakanlah: \"Maka siapakah (gerangan) yang dapat menghalang-halangi kehendak Allah jika Dia menghendaki kemudharatan bagimu atau jika Dia menghendaki manfaat bagimu. Sebenarnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(4595, '048:012', '68', 48, 12, 'Tetapi kamu menyangka bahwa Rasul dan orang-orang mukmin tidak sekali-kali akan kembali kepada keluarga mereka selama-lamanya dan syaitan telah menjadikan kamu memandang baik dalam hatimu persangkaan itu, dan kamu telah menyangka dengan sangkaan yang buruk dan kamu menjadi kaum yang binasa.'),
(4596, '048:013', '68', 48, 13, 'Dan barangsiapa yang tidak beriman kepada Allah dan Rasul-Nya maka sesungguhnya Kami menyediakan untuk orang-orang yang kafir neraka yang bernyala-nyala.'),
(4597, '048:014', '68', 48, 14, 'Dan hanya kepunyaan Allah-lah kerajaan langit dan bumi. Dia memberikan ampun kepada siapa yang dikehendaki-Nya dan mengazab siapa yang dikehendaki-Nya. Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(4598, '048:015', '68', 48, 15, 'Orang-orang Badwi yang tertinggal itu akan berkata apabila kamu berangkat untuk mengambil barang rampasan: \"Biarkanlah kami, niscaya kami mengikuti kamu\"; mereka hendak merubah janji Allah. Katakanlah: \"Kamu sekali-kali tidak (boleh) mengikuti kami; demikian Allah telah menetapkan sebelumnya\"; mereka akan mengatakan: \"Sebenarnya kamu dengki kepada kami\". Bahkan mereka tidak mengerti melainkan sedikit sekali.'),
(4599, '048:016', '68', 48, 16, 'Katakanlah kepada orang-orang Badwi yang tertinggal: \"Kamu akan diajak untuk (memerangi) kaum yang mempunyai kekuatan yang besar, kamu akan memerangi mereka atau mereka menyerah (masuk Islam). Maka jika kamu patuhi (ajakan itu) niscaya Allah akan memberikan kepadamu pahala yang baik dan jika kamu berpaling sebagaimana kamu telah berpaling sebelumnya, niscaya Dia akan mengazab kamu dengan azab yang pedih\".'),
(4600, '048:017', '68', 48, 17, 'Tiada dosa atas orang-orang yang buta dan atas orang yang pincang dan atas orang yang sakit (apabila tidak ikut berperang). Dan barangsiapa yang taat kepada Allah dan Rasul-Nya; niscaya Allah akan memasukkannya ke dalam surga yang mengalir di bawahnya sungai-sungai dan barang siapa yang berpaling niscaya akan diazab-Nya dengan azab yang pedih.'),
(4601, '048:018', '68', 48, 18, 'Sesungguhnya Allah telah ridha terhadap orang-orang mukmin ketika mereka berjanji setia kepadamu di bawah pohon, maka Allah mengetahui apa yang ada dalam hati mereka lalu menurunkan ketenangan atas mereka dan memberi balasan kepada mereka dengan kemenangan yang dekat (waktunya).'),
(4602, '048:019', '68', 48, 19, 'Serta harta rampasan yang banyak yang dapat mereka ambil. Dan adalah Allah Maha Perkasa lagi Maha Bijaksana.'),
(4603, '048:020', '68', 48, 20, 'Allah menjanjikan kepada kamu harta rampasan yang banyak yang dapat kamu ambil, maka disegerakan-Nya harta rampasan ini untukmu dan Dia menahan tangan manusia dari (membinasakan)mu (agar kamu mensyukuri-Nya) dan agar hal itu menjadi bukti bagi orang-orang mukmin dan agar Dia menunjuki kamu kepada jalan yang lurus.'),
(4604, '048:021', '68', 48, 21, 'Dan (telah menjanjikan pula kemenangan-kemenangan) yang lain (atas negeri-negeri) yang kamu belum dapat menguasainya yang sungguh Allah telah menentukan-Nya. Dan adalah Allah Maha Kuasa atas segala sesuatu.'),
(4605, '048:022', '68', 48, 22, 'Dan sekiranya orang-orang kafir itu memerangi kamu pastilah mereka berbalik melarikan diri ke belakang (kalah) kemudian mereka tiada memperoleh pelindung dan tidak (pula) penolong.'),
(4606, '048:023', '68', 48, 23, 'Sebagai suatu sunnatullah yang telah berlaku sejak dahulu, kamu sekali-kali tiada akan menemukan perubahan bagi sunnatullah itu.'),
(4607, '048:024', '68', 48, 24, 'Dan Dialah yang menahan tangan mereka dari (membinasakan) kamu dan (menahan) tangan kamu dari (membinasakan) mereka di tengah kota Mekah sesudah Allah memenangkan kamu atas mereka, dan adalah Allah Maha Melihat apa yang kamu kerjakan.'),
(4608, '048:025', '68', 48, 25, 'Merekalah orang-orang yang kafir yang menghalangi kamu dari (masuk) Masjidil Haram dan menghalangi hewan korban sampai ke tempat (penyembelihan)nya. Dan kalau tidaklah karena laki-laki yang mukmin dan perempuan-perempuan yang mukmin yang tiada kamu ketahui, bahwa kamu akan membunuh mereka yang menyebabkan kamu ditimpa kesusahan tanpa pengetahuanmu (tentulah Allah tidak akan menahan tanganmu dari membinasakan mereka). Supaya Allah memasukkan siapa yang dikehendaki-Nya ke dalam rahmat-Nya. Sekiranya mereka tidak bercampur-baur, tentulah Kami akan mengazab orang-orang yag kafir di antara mereka dengan azab yang pedih.'),
(4609, '048:026', '68', 48, 26, 'Ketika orang-orang kafir menanamkan dalam hati mereka kesombongan (yaitu) kesombongan jahiliyah lalu Allah menurunkan ketenangan kepada Rasul-Nya, dan kepada orang-orang mukmin dan Allah mewajibkan kepada mereka kalimat-takwa dan adalah mereka berhak dengan kalimat takwa itu dan patut memilikinya. Dan adalah Allah Maha Mengetahui segala sesuatu.'),
(4610, '048:027', '68', 48, 27, 'Sesungguhnya Allah akan membuktikan kepada Rasul-Nya, tentang kebenaran mimpinya dengan sebenarnya (yaitu) bahwa sesungguhnya kamu pasti akan memasuki Masjidil Haram, insya Allah dalam keadaan aman, dengan mencukur rambut kepala dan mengguntingnya, sedang kamu tidak merasa takut. Maka Allah mengetahui apa yang tiada kamu ketahui dan Dia memberikan sebelum itu kemenangan yang dekat.'),
(4611, '048:028', '68', 48, 28, 'Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang hak agar dimenangkan-Nya terhadap semua agama. Dan cukuplah Allah sebagai saksi.'),
(4612, '048:029', '68', 48, 29, 'Muhammad itu adalah utusan Allah dan orang-orang yang bersama dengan dia adalah keras terhadap orang-orang kafir, tetapi berkasih sayang sesama mereka. Kamu lihat mereka ruku´ dan sujud mencari karunia Allah dan keridhaan-Nya, tanda-tanda mereka tampak pada muka mereka dari bekas sujud. Demikianlah sifat-sifat mereka dalam Taurat dan sifat-sifat mereka dalam Injil, yaitu seperti tanaman yang mengeluarkan tunasnya maka tunas itu menjadikan tanaman itu kuat lalu menjadi besarlah dia dan tegak lurus di atas pokoknya; tanaman itu menyenangkan hati penanam-penanamnya karena Allah hendak menjengkelkan hati orang-orang kafir (dengan kekuatan orang-orang mukmin). Allah menjanjikan kepada orang-orang yang beriman dan mengerjakan amal yang saleh di antara mereka ampunan dan pahala yang besar.'),
(4613, '049:001', '68', 49, 1, 'Hai orang-orang yang beriman, janganlah kamu mendahului Allah dan Rasulnya dan bertakwalah kepada Allah. Sesungguhnya Allah Maha Mendengar lagi Maha Mengetahui.'),
(4614, '049:002', '68', 49, 2, 'Hai orang-orang yang beriman, janganlah kamu meninggikan suaramu melebihi suara Nabi, dan janganlah kamu berkata kepadanya dengan suara yang keras, sebagaimana kerasnya suara sebagian kamu terhadap sebagian yang lain, supaya tidak hapus (pahala) amalanmu, sedangkan kamu tidak menyadari.'),
(4615, '049:003', '68', 49, 3, 'Sesungguhnya orang yang merendahkan suaranya di sisi Rasulullah mereka itulah orang-orang yang telah diuji hati mereka oleh Allah untuk bertakwa. Bagi mereka ampunan dan pahala yang besar.'),
(4616, '049:004', '68', 49, 4, 'Sesungguhnya orang-orang yang memanggil kamu dari luar kamar(mu) kebanyakan mereka tidak mengerti.'),
(4617, '049:005', '68', 49, 5, 'Dan kalau sekiranya mereka bersabar sampai kamu keluar menemui mereka sesungguhnya itu lebih baik bagi mereka, dan Allah Maha Pengampun lagi Maha Penyayang.'),
(4618, '049:006', '68', 49, 6, 'Hai orang-orang yang beriman, jika datang kepadamu orang fasik membawa suatu berita, maka periksalah dengan teliti agar kamu tidak menimpakan suatu musibah kepada suatu kaum tanpa mengetahui keadaannya yang menyebabkan kamu menyesal atas perbuatanmu itu.'),
(4619, '049:007', '68', 49, 7, 'Dan ketahuilah olehmu bahwa di kalanganmu ada Rasulullah. Kalau ia menuruti kemauanmu dalam beberapa urusan benar-benarlah kamu mendapat kesusahan, tetapi Allah menjadikan kamu \"cinta\" kepada keimanan dan menjadikan keimanan itu indah di dalam hatimu serta menjadikan kamu benci kepada kekafiran, kefasikan, dan kedurhakaan. Mereka itulah orang-orang yang mengikuti jalan yang lurus,'),
(4620, '049:008', '68', 49, 8, 'sebagai karunia dan nikmat dari Allah. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(4621, '049:009', '68', 49, 9, 'Dan kalau ada dua golongan dari mereka yang beriman itu berperang hendaklah kamu damaikan antara keduanya! Tapi kalau yang satu melanggar perjanjian terhadap yang lain, hendaklah yang melanggar perjanjian itu kamu perangi sampai surut kembali pada perintah Allah. Kalau dia telah surut, damaikanlah antara keduanya menurut keadilan, dan hendaklah kamu berlaku adil; sesungguhnya Allah mencintai orang-orang yang berlaku adil.'),
(4622, '049:010', '68', 49, 10, 'Orang-orang beriman itu sesungguhnya bersaudara. Sebab itu damaikanlah (perbaikilah hubungan) antara kedua saudaramu itu dan takutlah terhadap Allah, supaya kamu mendapat rahmat.'),
(4623, '049:011', '68', 49, 11, 'Hai orang-orang yang beriman, janganlah sekumpulan orang laki-laki merendahkan kumpulan yang lain, boleh jadi yang ditertawakan itu lebih baik dari mereka. Dan jangan pula sekumpulan perempuan merendahkan kumpulan lainnya, boleh jadi yang direndahkan itu lebih baik. Dan janganlah suka mencela dirimu sendiri dan jangan memanggil dengan gelaran yang mengandung ejekan. Seburuk-buruk panggilan adalah (panggilan) yang buruk sesudah iman dan barangsiapa yang tidak bertobat, maka mereka itulah orang-orang yang zalim.'),
(4624, '049:012', '68', 49, 12, 'Hai orang-orang yang beriman, jauhilah kebanyakan purba-sangka (kecurigaan), karena sebagian dari purba-sangka itu dosa. Dan janganlah mencari-cari keburukan orang dan janganlah menggunjingkan satu sama lain. Adakah seorang diantara kamu yang suka memakan daging saudaranya yang sudah mati? Maka tentulah kamu merasa jijik kepadanya. Dan bertakwalah kepada Allah. Sesungguhnya Allah Maha Penerima Taubat lagi Maha Penyayang.'),
(4625, '049:013', '68', 49, 13, 'Hai manusia, sesungguhnya Kami menciptakan kamu dari seorang laki-laki dan seorang perempuan dan menjadikan kamu berbangsa-bangsa dan bersuku-suku supaya kamu saling kenal-mengenal. Sesungguhnya orang yang paling mulia diantara kamu disisi Allah ialah orang yang paling takwa diantara kamu. Sesungguhnya Allah Maha Mengetahui lagi Maha Mengenal.'),
(4626, '049:014', '68', 49, 14, 'Orang-orang Arab Badui itu berkata: \"Kami telah beriman\". Katakanlah: \"Kamu belum beriman, tapi katakanlah ´kami telah tunduk´, karena iman itu belum masuk ke dalam hatimu; dan jika kamu taat kepada Allah dan Rasul-Nya, Dia tidak akan mengurangi sedikitpun pahala amalanmu; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang\".'),
(4627, '049:015', '68', 49, 15, 'Sesungguhnya orang-orang yang beriman itu hanyalah orang-orang yang percaya (beriman) kepada Allah dan Rasul-Nya, kemudian mereka tidak ragu-ragu dan mereka berjuang (berjihad) dengan harta dan jiwa mereka pada jalan Allah. Mereka itulah orang-orang yang benar.'),
(4628, '049:016', '68', 49, 16, 'Katakanlah: \"Apakah kamu akan memberitahukan kepada Allah tentang agamamu, padahal Allah mengetahui apa yang di langit dan apa yang di bumi dan Allah Maha Mengetahui segala sesuatu?\"'),
(4629, '049:017', '68', 49, 17, 'Mereka merasa telah memberi nikmat kepadamu dengan keislaman mereka. Katakanlah: \"Janganlah kamu merasa telah memberi nikmat kepadaku dengan keislamanmu, sebenarnya Allah, Dialah yang melimpahkan nikmat kepadamu dengan menunjuki kamu kepada keimanan jika kamu adalah orang-orang yang benar\".'),
(4630, '049:018', '68', 49, 18, 'Sesungguhnya Allah mengetahui apa yang ghaib di langit dan bumi. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
(4631, '050:001', '68', 50, 1, 'Qaaf Demi Al Quran yang sangat mulia.'),
(4632, '050:002', '68', 50, 2, '(Mereka tidak menerimanya) bahkan mereka tercengang karena telah datang kepada mereka seorang pemberi peringatan dari (kalangan) mereka sendiri, maka berkatalah orang-orang kafir: \"Ini adalah suatu yang amat ajaib\".'),
(4633, '050:003', '68', 50, 3, 'Apakah kami setelah mati dan setelah menjadi tanah (kami akan kembali lagi)?, itu adalah suatu pengembalian yang tidak mungkin.'),
(4634, '050:004', '68', 50, 4, 'Sesungguhnya Kami telah mengetahui apa yang dihancurkan oleh bumi dari (tubuh-tubuh) mereka, dan pada sisi Kamipun ada kitab yang memelihara (mencatat).'),
(4635, '050:005', '68', 50, 5, 'Sebenarnya, mereka telah mendustakan kebenaran tatkala kebenaran itu datang kepada mereka, maka mereka berada dalam keadaan kacau balau.'),
(4636, '050:006', '68', 50, 6, 'Maka apakah mereka tidak melihat akan langit yang ada di atas mereka, bagaimana Kami meninggikannya dan menghiasinya dan langit itu tidak mempunyai retak-retak sedikitpun?'),
(4637, '050:007', '68', 50, 7, 'Dan Kami hamparkan bumi itu dan Kami letakkan padanya gunung-gunung yang kokoh dan Kami tumbuhkan padanya segala macam tanaman yang indah dipandang mata,'),
(4638, '050:008', '68', 50, 8, 'untuk menjadi pelajaran dan peringatan bagi tiap-tiap hamba yang kembali (mengingat Allah).'),
(4639, '050:009', '68', 50, 9, 'Dan Kami turunkan dari langit air yang banyak manfaatnya lalu Kami tumbuhkan dengan air itu pohon-pohon dan biji-biji tanaman yang diketam,'),
(4640, '050:010', '68', 50, 10, 'dan pohon kurma yang tinggi-tinggi yang mempunyai mayang yang bersusun-susun,'),
(4641, '050:011', '68', 50, 11, 'untuk menjadi rezeki bagi hamba-hamba (Kami), dan Kami hidupkan dengan air itu tanah yang mati (kering). Seperti itulah terjadinya kebangkitan.'),
(4642, '050:012', '68', 50, 12, 'Sebelum mereka telah mendustakan (pula) kaum Nuh dan penduduk Rass dan Tsamud,'),
(4643, '050:013', '68', 50, 13, 'dan kaum Aad, kaum Fir´aun dan kaum Luth,'),
(4644, '050:014', '68', 50, 14, 'dan penduduk Aikah serta kaum Tubba´ semuanya telah mendustakan rasul-rasul maka sudah semestinyalah mereka mendapat hukuman yang sudah diancamkan.'),
(4645, '050:015', '68', 50, 15, 'Maka apakah Kami letih dengan penciptaan yang pertama? Sebenarnya mereka dalam keadaan ragu-ragu tentang penciptaan yang baru.'),
(4646, '050:016', '68', 50, 16, 'Dan sesungguhnya Kami telah menciptakan manusia dan mengetahui apa yang dibisikkan oleh hatinya, dan Kami lebih dekat kepadanya daripada urat lehernya,'),
(4647, '050:017', '68', 50, 17, '(yaitu) ketika dua orang malaikat mencatat amal perbuatannya, seorang duduk di sebelah kanan dan yang lain duduk di sebelah kiri.'),
(4648, '050:018', '68', 50, 18, 'Tiada suatu ucapanpun yang diucapkannya melainkan ada di dekatnya malaikat pengawas yang selalu hadir.'),
(4649, '050:019', '68', 50, 19, 'Dan datanglah sakaratul maut dengan sebenar-benarnya. Itulah yang kamu selalu lari daripadanya.'),
(4650, '050:020', '68', 50, 20, 'Dan ditiuplah sangkakala. Itulah hari terlaksananya ancaman.'),
(4651, '050:021', '68', 50, 21, 'Dan datanglah tiap-tiap diri, bersama dengan dia seorang malaikat penggiring dan seorang malaikat penyaksi.'),
(4652, '050:022', '68', 50, 22, 'Sesungguhnya kamu berada dalam keadaan lalai dari (hal) ini, maka Kami singkapkan daripadamu tutup (yang menutupi) matamu, maka penglihatanmu pada hari itu amat tajam.'),
(4653, '050:023', '68', 50, 23, 'Dan yang menyertai dia berkata: \"Inilah (catatan amalnya) yang tersedia pada sisiku\".'),
(4654, '050:024', '68', 50, 24, 'Allah berfirman: \"Lemparkanlah olehmu berdua ke dalam neraka semua orang yang sangat ingkar dan keras kepala,'),
(4655, '050:025', '68', 50, 25, 'yang sangat menghalangi kebajikan, melanggar batas lagi ragu-ragu,'),
(4656, '050:026', '68', 50, 26, 'yang menyembah sembahan yang lain beserta Allah maka lemparkanlah dia ke dalam siksaan yang sangat\".'),
(4657, '050:027', '68', 50, 27, 'Yang menyertai dia berkata (pula): \"Ya Tuhan kami, aku tidak menyesatkannya tetapi dialah yang berada dalam kesesatan yang jauh\".'),
(4658, '050:028', '68', 50, 28, 'Allah berfirman: \"Janganlah kamu bertengkar di hadapan-Ku, padahal sesungguhnya Aku dahulu telah memberikan ancaman kepadamu\".'),
(4659, '050:029', '68', 50, 29, 'Keputusan di sisi-Ku tidak dapat diubah dan Aku sekali-kali tidak menganiaya hamba-hamba-Ku'),
(4660, '050:030', '68', 50, 30, '(Dan ingatlah akan) hari (yang pada hari itu) Kami bertanya kepada jahannam: \"Apakah kamu sudah penuh?\" Dia menjawab: \"Masih ada tambahan?\"'),
(4661, '050:031', '68', 50, 31, 'Dan didekatkanlah surga itu kepada orang-orang yang bertakwa pada tempat yang tiada jauh (dari mereka).'),
(4662, '050:032', '68', 50, 32, 'Inilah yang dijanjikan kepadamu, (yaitu) kepada setiap hamba yang selalu kembali (kepada Allah) lagi memelihara (semua peraturan-peraturan-Nya)'),
(4663, '050:033', '68', 50, 33, '(Yaitu) orang yang takut kepada Tuhan Yang Maha Pemurah sedang Dia tidak kelihatan (olehnya) dan dia datang dengan hati yang bertaubat,'),
(4664, '050:034', '68', 50, 34, 'masukilah surga itu dengan aman, itulah hari kekekalan.'),
(4665, '050:035', '68', 50, 35, 'Mereka di dalamnya memperoleh apa yang mereka kehendaki; dan pada sisi Kami ada tambahannya.'),
(4666, '050:036', '68', 50, 36, 'Dan berapa banyaknya umat-umat yang telah Kami binasakan sebelum mereka yang mereka itu lebih besar kekuatannya daripada mereka ini, maka mereka (yang telah dibinasakan itu) telah pernah menjelajah di beberapa negeri. Adakah (mereka) mendapat tempat lari (dari kebinasaan)?'),
(4667, '050:037', '68', 50, 37, 'Sesungguhnya pada yang demikian itu benar-benar terdapat peringatan bagi orang-orang yang mempunyai akal atau yang menggunakan pendengarannya, sedang dia menyaksikannya.'),
(4668, '050:038', '68', 50, 38, 'Dan sesungguhnya telah Kami ciptakan langit dan bumi dan apa yang ada antara keduanya dalam enam masa, dan Kami sedikitpun tidak ditimpa keletihan.'),
(4669, '050:039', '68', 50, 39, 'Maka bersabarlah kamu terhadap apa yang mereka katakan dan bertasbihlah sambil memuji Tuhanmu sebelum terbit matahari dan sebelum terbenam(nya).'),
(4670, '050:040', '68', 50, 40, 'Dan bertasbihlah kamu kepada-Nya di malam hari dan setiap selesai sembahyang.'),
(4671, '050:041', '68', 50, 41, 'Dan dengarkanlah (seruan) pada hari penyeru (malaikat) menyeru dari tempat yang dekat.'),
(4672, '050:042', '68', 50, 42, '(Yaitu) pada hari mereka mendengar teriakan dengan sebenar-benarnya itulah hari ke luar (dari kubur).'),
(4673, '050:043', '68', 50, 43, 'Sesungguhnya Kami menghidupkan dan mematikan dan hanya kepada Kami-lah tempat kembali (semua makhluk).'),
(4674, '050:044', '68', 50, 44, '(Yaitu) pada hari bumi terbelah-belah menampakkan mereka (lalu mereka ke luar) dengan cepat. Yang demikian itu adalah pengumpulan yang mudah bagi Kami.'),
(4675, '050:045', '68', 50, 45, 'Kami lebih mengetahui tentang apa yang mereka katakan, dan kamu sekali-kali bukanlah seorang pemaksa terhadap mereka. Maka beri peringatanlah dengan Al Quran orang yang takut dengan ancaman-Ku.'),
(4676, '051:001', '68', 51, 1, 'Demi (angin) yang menerbangkan debu dengan kuat.'),
(4677, '051:002', '68', 51, 2, 'dan awan yang mengandung hujan,'),
(4678, '051:003', '68', 51, 3, 'dan kapal-kapal yang berlayar dengan mudah.'),
(4679, '051:004', '68', 51, 4, 'dan (malaikat-malaikat) yang membagi-bagi urusan,'),
(4680, '051:005', '68', 51, 5, 'sesungguhnya apa yang dijanjikan kepadamu pasti benar.'),
(4681, '051:006', '68', 51, 6, 'dan sesungguhnya (hari) pembalasan pasti terjadi.'),
(4682, '051:007', '68', 51, 7, 'Demi langit yang mempunyai jalan-jalan,'),
(4683, '051:008', '68', 51, 8, 'sesungguhnya kamu benar-benar dalam keadaan berbeda pendapat,'),
(4684, '051:009', '68', 51, 9, 'dipalingkan daripadanya (Rasul dan Al-Quran) orang yang dipalingkan.'),
(4685, '051:010', '68', 51, 10, 'Terkutuklah orang-orang yang banyak berdusta,'),
(4686, '051:011', '68', 51, 11, '(yaitu) orang-orang yang terbenam dalam kebodohan yang lalai,'),
(4687, '051:012', '68', 51, 12, 'mereka bertanya: \"Bilakah hari pembalasan itu?\"'),
(4688, '051:013', '68', 51, 13, '(Hari pembalasan itu) ialah pada hari ketika mereka diazab di atas api neraka.'),
(4689, '051:014', '68', 51, 14, '(Dikatakan kepada mereka): \"Rasakanlah azabmu itu. Inilah azab yang dulu kamu minta untuk disegerakan\".'),
(4690, '051:015', '68', 51, 15, 'Sesungguhnya orang-orang yang bertakwa itu berada dalam taman-taman (surga) dan mata air-mata air,'),
(4691, '051:016', '68', 51, 16, 'sambil menerima segala pemberian Rabb mereka. Sesungguhnya mereka sebelum itu di dunia adalah orang-orang yang berbuat kebaikan.'),
(4692, '051:017', '68', 51, 17, 'Di dunia mereka sedikit sekali tidur diwaktu malam.'),
(4693, '051:018', '68', 51, 18, 'Dan selalu memohonkan ampunan diwaktu pagi sebelum fajar.'),
(4694, '051:019', '68', 51, 19, 'Dan pada harta-harta mereka ada hak untuk orang miskin yang meminta dan orang miskin yang tidak mendapat bagian.'),
(4695, '051:020', '68', 51, 20, 'Dan di bumi itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang yakin.'),
(4696, '051:021', '68', 51, 21, 'dan (juga) pada dirimu sendiri. Maka apakah kamu tidak memperhatikan?'),
(4697, '051:022', '68', 51, 22, 'Dan di langit terdapat (sebab-sebab) rezekimu dan terdapat (pula) apa yang dijanjikan kepadamu.'),
(4698, '051:023', '68', 51, 23, 'Maka demi Tuhan langit dan bumi, sesungguhnya yang dijanjikan itu adalah benar-benar (akan terjadi) seperti perkataan yang kamu ucapkan.'),
(4699, '051:024', '68', 51, 24, 'Sudahkah sampai kepadamu (Muhammad) cerita tentang tamu Ibrahim (yaitu malaikat-malaikat) yang dimuliakan?'),
(4700, '051:025', '68', 51, 25, '(Ingatlah) ketika mereka masuk ke tempatnya lalu mengucapkan: \"Salaamun\". Ibrahim menjawab: \"Salaamun (kamu) adalah orang-orang yang tidak dikenal\".'),
(4701, '051:026', '68', 51, 26, 'Maka dia pergi dengan diam-diam menemui keluarganya, kemudian dibawanya daging anak sapi gemuk.'),
(4702, '051:027', '68', 51, 27, 'Lalu dihidangkannya kepada mereka. Ibrahim lalu berkata: \"Silahkan anda makan\".'),
(4703, '051:028', '68', 51, 28, '(Tetapi mereka tidak mau makan), karena itu Ibrahim merasa takut terhadap mereka. Mereka berkata: \"Janganlah kamu takut\", dan mereka memberi kabar gembira kepadanya dengan (kelahiran) seorang anak yang alim (Ishak).'),
(4704, '051:029', '68', 51, 29, 'Kemudian isterinya datang memekik lalu menepuk mukanya sendiri seraya berkata: \"(Aku adalah) seorang perempuan tua yang mandul\".'),
(4705, '051:030', '68', 51, 30, 'Mereka berkata: \"Demikianlah Tuhanmu memfirmankan\" Sesungguhnya Dialah yang Maha Bijaksana lagi Maha Mengetahui.'),
(4706, '051:031', '68', 51, 31, 'Ibrahim bertanya: \"Apakah urusanmu hai para utusan?\"'),
(4707, '051:032', '68', 51, 32, 'Mereka menjawab: \"Sesungguhnya kami diutus kepada kaum yang berdosa (kaum Luth),'),
(4708, '051:033', '68', 51, 33, 'agar kami timpakan kepada mereka batu-batu dari tanah,'),
(4709, '051:034', '68', 51, 34, 'yang ditandai di sisi Tuhanmu untuk membinasakan orang-orang yang melampaui batas\".'),
(4710, '051:035', '68', 51, 35, 'Lalu Kami keluarkan orang-orang yang beriman yang berada di negeri kaum Luth itu.'),
(4711, '051:036', '68', 51, 36, 'Dan Kami tidak mendapati negeri itu, kecuali sebuah rumah dari orang yang berserah diri.'),
(4712, '051:037', '68', 51, 37, 'Dan Kami tinggalkan pada negeri itu suatu tanda bagi orang-orang yang takut kepada siksa yang pedih.'),
(4713, '051:038', '68', 51, 38, 'Dan juga pada Musa (terdapat tanda-tanda kekuasaan Allah) ketika Kami mengutusnya kepada Fir´aun dengan membawa mukjizat yang nyata.'),
(4714, '051:039', '68', 51, 39, 'Maka dia (Fir´aun) berpaling (dari iman) bersama tentaranya dan berkata: \"Dia adalah seorang tukang sihir atau seorang gila\".'),
(4715, '051:040', '68', 51, 40, 'Maka Kami siksa dia dan tentaranya lalu Kami lemparkan mereka ke dalam laut, sedang dia melakukan pekerjaan yang tercela.'),
(4716, '051:041', '68', 51, 41, 'Dan juga pada (kisah) Aad ketika Kami kirimkan kepada mereka angin yang membinasakan,'),
(4717, '051:042', '68', 51, 42, 'angin itu tidak membiarkan satupun yang dilaluinya, melainkan dijadikannya seperti serbuk.'),
(4718, '051:043', '68', 51, 43, 'Dan pada (kisah) kaum Tsamud ketika dikatakan kepada mereka: \"Bersenang-senanglah kalian sampai suatu waktu\".'),
(4719, '051:044', '68', 51, 44, 'Maka mereka berlaku angkuh terhadap perintah Tuhannya, lalu mereka disambar petir dan mereka melihatnya.'),
(4720, '051:045', '68', 51, 45, 'Maka mereka sekali-kali tidak dapat bangun dan tidak pula mendapat pertolongan,'),
(4721, '051:046', '68', 51, 46, 'dan (Kami membinasakan) kaum Nuh sebelum itu. Sesungguhnya mereka adalah kaum yang fasik.'),
(4722, '051:047', '68', 51, 47, 'Dan langit itu Kami bangun dengan kekuasaan (Kami) dan sesungguhnya Kami benar-benar berkuasa'),
(4723, '051:048', '68', 51, 48, 'Dan bumi itu Kami hamparkan, maka sebaik-baik yang menghamparkan (adalah Kami).'),
(4724, '051:049', '68', 51, 49, 'Dan segala sesuatu Kami ciptakan berpasang-pasangan supaya kamu mengingat kebesaran Allah.'),
(4725, '051:050', '68', 51, 50, 'Maka segeralah kembali kepada (mentaati) Allah. Sesungguhnya aku seorang pemberi peringatan yang nyata dari Allah untukmu.'),
(4726, '051:051', '68', 51, 51, 'Dan janganlah kamu mengadakan tuhan yang lain disamping Allah. Sesungguhnya aku seorang pemberi peringatan yang nyata dari Allah untukmu.'),
(4727, '051:052', '68', 51, 52, 'Demikianlah tidak seorang rasulpun yang datang kepada orang-orang yang sebelum mereka, melainkan mereka mengatakan: \"Dia adalah seorang tukang sihir atau seorang gila\".'),
(4728, '051:053', '68', 51, 53, 'Apakah mereka saling berpesan tentang apa yang dikatakan itu. Sebenarnya mereka adalah kaum yang melampaui batas.'),
(4729, '051:054', '68', 51, 54, 'Maka berpalinglah kamu dari mereka dan kamu sekali-kali tidak tercela.'),
(4730, '051:055', '68', 51, 55, 'Dan tetaplah memberi peringatan, karena sesungguhnya peringatan itu bermanfaat bagi orang-orang yang beriman.'),
(4731, '051:056', '68', 51, 56, 'Dan aku tidak menciptakan jin dan manusia melainkan supaya mereka mengabdi kepada-Ku.'),
(4732, '051:057', '68', 51, 57, 'Aku tidak menghendaki rezeki sedikitpun dari mereka dan Aku tidak menghendaki supaya mereka memberi-Ku makan.'),
(4733, '051:058', '68', 51, 58, 'Sesungguhnya Allah Dialah Maha Pemberi rezeki Yang mempunyai Kekuatan lagi Sangat Kokoh.'),
(4734, '051:059', '68', 51, 59, 'Maka sesungguhnya untuk orang-orang zalim ada bagian (siksa) seperti bahagian teman mereka (dahulu); maka janganlah mereka meminta kepada-Ku untuk menyegerakannya.'),
(4735, '051:060', '68', 51, 60, 'Maka kecelakaanlah bagi orang-orang yang kafir pada hari yang diancamkan kepada mereka.'),
(4736, '052:001', '68', 52, 1, 'Demi bukit,'),
(4737, '052:002', '68', 52, 2, 'dan Kitab yang ditulis,'),
(4738, '052:003', '68', 52, 3, 'pada lembaran yang terbuka,'),
(4739, '052:004', '68', 52, 4, 'dan demi Baitul Ma´mur,'),
(4740, '052:005', '68', 52, 5, 'dan atap yang ditinggikan (langit),'),
(4741, '052:006', '68', 52, 6, 'dan laut yang di dalam tanahnya ada api,'),
(4742, '052:007', '68', 52, 7, 'sesungguhnya azab Tuhanmu pasti terjadi,'),
(4743, '052:008', '68', 52, 8, 'tidak seorangpun yang dapat menolaknya,'),
(4744, '052:009', '68', 52, 9, 'pada hari ketika langit benar-benar bergoncang,'),
(4745, '052:010', '68', 52, 10, 'dan gunung benar-benar berjalan.'),
(4746, '052:011', '68', 52, 11, 'Maka kecelakaan yang besarlah di hari itu bagi orang-orang yang mendustakan,'),
(4747, '052:012', '68', 52, 12, '(yaitu) orang-orang yang bermain-main dalam kebathilan,'),
(4748, '052:013', '68', 52, 13, 'pada hari mereka didorong ke neraka Jahannam dengan sekuat-kuatnya.'),
(4749, '052:014', '68', 52, 14, '(Dikatakan kepada mereka): \"Inilah neraka yang dahulu kamu selalu mendustakannya\".'),
(4750, '052:015', '68', 52, 15, 'Maka apakah ini sihir? Ataukah kamu tidak melihat?'),
(4751, '052:016', '68', 52, 16, 'Masukklah kamu ke dalamnya (rasakanlah panas apinya); maka baik kamu bersabar atau tidak, sama saja bagimu; kamu diberi balasan terhadap apa yang telah kamu kerjakan.'),
(4752, '052:017', '68', 52, 17, 'Sesungguhnya orang-orang yang bertakwa berada dalam surga dan kenikmatan,'),
(4753, '052:018', '68', 52, 18, 'mereka bersuka ria dengan apa yang diberikan kepada mereka oleh Tuhan mereka; dan Tuhan mereka memelihara mereka dari azab neraka.'),
(4754, '052:019', '68', 52, 19, '(Dikatakan kepada mereka): \"Makan dan minumlah dengan enak sebagai balasan dari apa yang telah kamu kerjakan\",'),
(4755, '052:020', '68', 52, 20, 'mereka bertelekan di atas dipan-dipan berderetan dan Kami kawinkan mereka dengan bidadari-bidadari yang cantik bermata jeli.'),
(4756, '052:021', '68', 52, 21, 'Dan orang-oranng yang beriman, dan yang anak cucu mereka mengikuti mereka dalam keimanan, Kami hubungkan anak cucu mereka dengan mereka, dan Kami tiada mengurangi sedikitpun dari pahala amal mereka. Tiap-tiap manusia terikat dengan apa yang dikerjakannya.'),
(4757, '052:022', '68', 52, 22, 'Dan Kami beri mereka tambahan dengan buah-buahan dan daging dari segala jenis yang mereka ingini.'),
(4758, '052:023', '68', 52, 23, 'Di dalam surga mereka saling memperebutkan piala (gelas) yang isinya tidak (menimbulkan) kata-kata yang tidak berfaedah dan tiada pula perbuatan dosa.'),
(4759, '052:024', '68', 52, 24, 'Dan berkeliling di sekitar mereka anak-anak muda untuk (melayani) mereka, seakan-akan mereka itu mutiara yang tersimpan.'),
(4760, '052:025', '68', 52, 25, 'Dan sebahagian mereka menghadap kepada sebahagian yang lain saling tanya-menanya.'),
(4761, '052:026', '68', 52, 26, 'Mereka berkata: \"Sesungguhnya kami dahulu, sewaktu berada di tengah-tengah keluarga kami merasa takut (akan diazab)\".'),
(4762, '052:027', '68', 52, 27, 'Maka Allah memberikan karunia kepada kami dan memelihara kami dari azab neraka.'),
(4763, '052:028', '68', 52, 28, 'Sesungguhnya kami dahulu menyembah-Nya. Sesungguhnya Dialah yang melimpahkan kebaikan lagi Maha Penyayang.'),
(4764, '052:029', '68', 52, 29, 'Maka tetaplah memberi peringatan, dan kamu disebabkan nikmat Tuhanmu bukanlah seorang tukang tenung dan bukan pula seorang gila.'),
(4765, '052:030', '68', 52, 30, 'Bahkan mereka mengatakan: \"Dia adalah seorang penyair yang kami tunggu-tunggu kecelakaan menimpanya\".'),
(4766, '052:031', '68', 52, 31, 'Katakanlah: \"Tunggulah, maka sesungguhnya akupun termasuk orang yang menunggu (pula) bersama kamu\".'),
(4767, '052:032', '68', 52, 32, 'Apakah mereka diperintah oleh fikiran-fikiran mereka untuk mengucapkan tuduhan-tuduhan ini ataukah mereka kaum yang melampaui batas?'),
(4768, '052:033', '68', 52, 33, 'Ataukah mereka mengatakan: \"Dia (Muhammad) membuat-buatnya\". Sebenarnya mereka tidak beriman.'),
(4769, '052:034', '68', 52, 34, 'Maka hendaklah mereka mendatangkan kalimat yang semisal Al Quran itu jika mereka orang-orang yang benar.'),
(4770, '052:035', '68', 52, 35, 'Apakah mereka diciptakan tanpa sesuatupun ataukah mereka yang menciptakan (diri mereka sendiri)?'),
(4771, '052:036', '68', 52, 36, 'Ataukah mereka telah menciptakan langit dan bumi itu?; sebenarnya mereka tidak meyakini (apa yang mereka katakan).'),
(4772, '052:037', '68', 52, 37, 'Ataukah di sisi mereka ada perbendaharaan Tuhanmu atau merekakah yang berkuasa?'),
(4773, '052:038', '68', 52, 38, 'Ataukah mereka mempunyai tangga (ke langit) untuk mendengarkan pada tangga itu (hal-hal yang gaib)? Maka hendaklah orang yang mendengarkan di antara mereka mendatangkan suatu keterangan yang nyata.'),
(4774, '052:039', '68', 52, 39, 'Ataukah untuk Allah anak-anak perempuan dan untuk kamu anak-anak laki-laki?'),
(4775, '052:040', '68', 52, 40, 'Ataukah kamu meminta upah kepada mereka sehingga mereka dibebani dengan hutang?'),
(4776, '052:041', '68', 52, 41, 'Apakah ada pada sisi mereka pengetahuan tentang yang gaib lalu mereka menuliskannya?'),
(4777, '052:042', '68', 52, 42, 'Ataukah mereka hendak melakukan tipu daya? Maka orang-orang yang kafir itu merekalah yang kena tipu daya.'),
(4778, '052:043', '68', 52, 43, 'Ataukah mereka mempunyai tuhan selain Allah. Maha Suci Allah dari apa yang mereka persekutukan.'),
(4779, '052:044', '68', 52, 44, 'Jika mereka melihat sebagian dari langit gugur, mereka akan mengatakan: \"Itu adalah awan yang bertindih-tindih\".'),
(4780, '052:045', '68', 52, 45, 'Maka biarkanlah mereka hingga mereka menemui hari (yang dijanjikan kepada) mereka yang pada hari itu mereka dibinasakan,'),
(4781, '052:046', '68', 52, 46, '(yaitu) hari ketika tidak berguna bagi mereka sedikitpun tipu daya mereka dan mereka tidak ditolong.'),
(4782, '052:047', '68', 52, 47, 'Dan sesungguhnya untuk orang-orang yang zalim ada azab selain daripada itu. Tetapi kebanyakan mereka tidak mengetahui.'),
(4783, '052:048', '68', 52, 48, 'Dan bersabarlah dalam menunggu ketetapan Tuhanmu, maka sesungguhnya kamu berada dalam penglihatan Kami, dan bertasbihlah dengan memuji Tuhanmu ketika kamu bangun berdiri,');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(4784, '052:049', '68', 52, 49, 'dan bertasbihlah kepada-Nya pada beberapa saat di malam hari dan di waktu terbenam bintang-bintang (di waktu fajar).'),
(4785, '053:001', '68', 53, 1, 'Demi bintang ketika terbenam.'),
(4786, '053:002', '68', 53, 2, 'kawanmu (Muhammad) tidak sesat dan tidak pula keliru.'),
(4787, '053:003', '68', 53, 3, 'dan tiadalah yang diucapkannya itu (Al-Quran) menurut kemauan hawa nafsunya.'),
(4788, '053:004', '68', 53, 4, 'Ucapannya itu tiada lain hanyalah wahyu yang diwahyukan (kepadanya).'),
(4789, '053:005', '68', 53, 5, 'yang diajarkan kepadanya oleh (Jibril) yang sangat kuat.'),
(4790, '053:006', '68', 53, 6, 'yang mempunyai akal yang cerdas; dan (Jibril itu) menampakkan diri dengan rupa yang asli.'),
(4791, '053:007', '68', 53, 7, 'sedang dia berada di ufuk yang tinggi.'),
(4792, '053:008', '68', 53, 8, 'Kemudian dia mendekat, lalu bertambah dekat lagi.'),
(4793, '053:009', '68', 53, 9, 'maka jadilah dia dekat (pada Muhammad sejarak) dua ujung busur panah atau lebih dekat (lagi).'),
(4794, '053:010', '68', 53, 10, 'Lalu dia menyampaikan kepada hamba-Nya (Muhammad) apa yang telah Allah wahyukan.'),
(4795, '053:011', '68', 53, 11, 'Hatinya tidak mendustakan apa yang telah dilihatnya.'),
(4796, '053:012', '68', 53, 12, 'Maka apakah kaum (musyrik Mekah) hendak membantahnya tentang apa yang telah dilihatnya?'),
(4797, '053:013', '68', 53, 13, 'Dan sesungguhnya Muhammad telah melihat Jibril itu (dalam rupanya yang asli) pada waktu yang lain,'),
(4798, '053:014', '68', 53, 14, '(yaitu) di Sidratil Muntaha.'),
(4799, '053:015', '68', 53, 15, 'Di dekatnya ada surga tempat tinggal,'),
(4800, '053:016', '68', 53, 16, '(Muhammad melihat Jibril) ketika Sidratil Muntaha diliputi oleh sesuatu yang meliputinya.'),
(4801, '053:017', '68', 53, 17, 'Penglihatannya (muhammad) tidak berpaling dari yang dilihatnya itu dan tidak (pula) melampauinya.'),
(4802, '053:018', '68', 53, 18, 'Sesungguhnya dia telah melihat sebahagian tanda-tanda (kekuasaan) Tuhannya yang paling besar.'),
(4803, '053:019', '68', 53, 19, 'Maka apakah patut kamu (hai orang-orang musyrik) menganggap al Lata dan al Uzza,'),
(4804, '053:020', '68', 53, 20, 'dan Manah yang ketiga, yang paling terkemudian (sebagai anak perempuan Allah)?'),
(4805, '053:021', '68', 53, 21, 'Apakah (patut) untuk kamu (anak) laki-laki dan untuk Allah (anak) perempuan?'),
(4806, '053:022', '68', 53, 22, 'Yang demikian itu tentulah suatu pembagian yang tidak adil.'),
(4807, '053:023', '68', 53, 23, 'Itu tidak lain hanyalah nama-nama yang kamu dan bapak-bapak kamu mengadakannya; Allah tidak menurunkan suatu keteranganpun untuk (menyembah)nya. Mereka tidak lain hanyalah mengikuti sangkaan-sangkaan, dan apa yang diingini oleh hawa nafsu mereka dan sesungguhnya telah datang petunjuk kepada mereka dari Tuhan mereka.'),
(4808, '053:024', '68', 53, 24, 'Atau apakah manusia akan mendapat segala yang dicita-citakannya?'),
(4809, '053:025', '68', 53, 25, '(Tidak), maka hanya bagi Allah kehidupan akhirat dan kehidupan dunia.'),
(4810, '053:026', '68', 53, 26, 'Dan berapa banyaknya malaikat di langit, syafa´at mereka sedikitpun tidak berguna, kecuali sesudah Allah mengijinkan bagi orang yang dikehendaki dan diridhai (Nya).'),
(4811, '053:027', '68', 53, 27, 'Sesungguhnya orang-orang yang tiada beriman kepada kehidupan akhirat, mereka benar-benar menamakan malaikat itu dengan nama perempuan.'),
(4812, '053:028', '68', 53, 28, 'Dan mereka tidak mempunyai sesuatu pengetahuanpun tentang itu. Mereka tidak lain hanyalah mengikuti persangkaan sedang sesungguhnya persangkaan itu tiada berfaedah sedikitpun terhadap kebenaran.'),
(4813, '053:029', '68', 53, 29, 'Maka berpalinglah (hai Muhammad) dari orang yang berpaling dari peringatan Kami, dan tidak mengingini kecuali kehidupan duniawi.'),
(4814, '053:030', '68', 53, 30, 'Itulah sejauh-jauh pengetahuan mereka. Sesungguhnya Tuhanmu, Dialah yang paling mengetahui siapa yang tersesat dari jalan-Nya dan Dia pulalah yang paling mengetahui siapa yang mendapat petunjuk.'),
(4815, '053:031', '68', 53, 31, 'Dan hanya kepunyaan Allah-lah apa yang ada di langit dan apa yang ada di bumi supaya Dia memberi balasan kepada orang-orang yang berbuat jahat terhadap apa yang telah mereka kerjakan dan memberi balasan kepada orang-orang yang berbuat baik dengan pahala yang lebih baik (surga).'),
(4816, '053:032', '68', 53, 32, '(Yaitu) orang-orang yang menjauhi dosa-dosa besar dan perbuatan keji yang selain dari kesalahan-kesalahan kecil. Sesungguhnya Tuhanmu maha luas ampunan-Nya. Dan Dia lebih mengetahui (tentang keadaan)mu ketika Dia menjadikan kamu dari tanah dan ketika kamu masih janin dalam perut ibumu; maka janganlah kamu mengatakan dirimu suci. Dialah yang paling mengetahui tentang orang yang bertakwa.'),
(4817, '053:033', '68', 53, 33, 'Maka apakah kamu melihat orang yang berpaling (dari Al-Quran)?'),
(4818, '053:034', '68', 53, 34, 'serta memberi sedikit dan tidak mau memberi lagi?'),
(4819, '053:035', '68', 53, 35, 'Apakah dia mempunyai pengetahuan tentang yang ghaib, sehingga dia mengetahui (apa yang dikatakan)?'),
(4820, '053:036', '68', 53, 36, 'Ataukah belum diberitakan kepadanya apa yang ada dalam lembaran-lembaran Musa?'),
(4821, '053:037', '68', 53, 37, 'dan lembaran-lembaran Ibrahim yang selalu menyempurnakan janji?'),
(4822, '053:038', '68', 53, 38, '(yaitu) bahwasanya seorang yang berdosa tidak akan memikul dosa orang lain,'),
(4823, '053:039', '68', 53, 39, 'dan bahwasanya seorang manusia tiada memperoleh selain apa yang telah diusahakannya,'),
(4824, '053:040', '68', 53, 40, 'dan bahwasanya usaha itu kelak akan diperlihatkan (kepadanya).'),
(4825, '053:041', '68', 53, 41, 'Kemudian akan diberi balasan kepadanya dengan balasan yang paling sempurna,'),
(4826, '053:042', '68', 53, 42, 'dan bahwasanya kepada Tuhanmulah kesudahan (segala sesuatu),'),
(4827, '053:043', '68', 53, 43, 'dan bahwasanya Dialah yang menjadikan orang tertawa dan menangis,'),
(4828, '053:044', '68', 53, 44, 'dan bahwasanya Dialah yang mematikan dan menghidupkan,'),
(4829, '053:045', '68', 53, 45, 'dan bahwasanya Dialah yang menciptakan berpasang-pasangan pria dan wanita.'),
(4830, '053:046', '68', 53, 46, 'dari air mani, apabila dipancarkan.'),
(4831, '053:047', '68', 53, 47, 'Dan bahwasanya Dialah yang menetapkan kejadian yang lain (kebangkitan sesudah mati),'),
(4832, '053:048', '68', 53, 48, 'dan bahwasanya Dia yang memberikan kekayaan dan memberikan kecukupan,'),
(4833, '053:049', '68', 53, 49, 'dan bahwasanya Dialah yang Tuhan (yang memiliki) bintang syi´ra,'),
(4834, '053:050', '68', 53, 50, 'dan bahwasanya Dia telah membinasakan kaum ´Aad yang pertama,'),
(4835, '053:051', '68', 53, 51, 'dan kaum Tsamud. Maka tidak seorangpun yang ditinggalkan-Nya (hidup).'),
(4836, '053:052', '68', 53, 52, 'Dan kaum Nuh sebelum itu. Sesungguhnya mereka adalah orang-orang yang paling zalim dan paling durhaka,'),
(4837, '053:053', '68', 53, 53, 'dan negeri-negeri kaum Luth yang telah dihancurkan Allah.'),
(4838, '053:054', '68', 53, 54, 'lalu Allah menimpakan atas negeri itu azab besar yang menimpanya.'),
(4839, '053:055', '68', 53, 55, 'Maka terhadap nikmat Tuhanmu yang manakah kamu ragu-ragu?'),
(4840, '053:056', '68', 53, 56, 'Ini (Muhammad) adalah seorang pemberi peringatan di antara pemberi-pemberi peringatan yang terdahulu.'),
(4841, '053:057', '68', 53, 57, 'Telah dekat terjadinya hari kiamat.'),
(4842, '053:058', '68', 53, 58, 'Tidak ada yang akan menyatakan terjadinya hari itu selain Allah.'),
(4843, '053:059', '68', 53, 59, 'Maka apakah kamu merasa heran terhadap pemberitaan ini?'),
(4844, '053:060', '68', 53, 60, 'Dan kamu mentertawakan dan tidak menangis?'),
(4845, '053:061', '68', 53, 61, 'Sedang kamu melengahkan(nya)?'),
(4846, '053:062', '68', 53, 62, 'Maka bersujudlah kepada Allah dan sembahlah (Dia).'),
(4847, '054:001', '68', 54, 1, 'Telah dekat datangnya saat itu dan telah terbelah bulan.'),
(4848, '054:002', '68', 54, 2, 'Dan jika mereka (orang-orang musyrikin) melihat suatu tanda (mukjizat), mereka berpaling dan berkata: \"(Ini adalah) sihir yang terus menerus\".'),
(4849, '054:003', '68', 54, 3, 'Dan mereka mendutakan (Nabi) dan mengikuti hawa nafsu mereka, sedang tiap-tiap urusan telah ada ketetapannya'),
(4850, '054:004', '68', 54, 4, 'Dan sesungguhnya telah datang kepada mereka beberapa kisah yang di dalamnya terdapat cegahan (dari kekafiran).'),
(4851, '054:005', '68', 54, 5, 'Itulah suatu hikmah yang sempurna maka peringatan-peringatan itu tidak berguna (bagi mereka).'),
(4852, '054:006', '68', 54, 6, 'Maka berpalinglah kamu dari mereka. (Ingatlah) hari (ketika) seorang penyeru (malaikat) menyeru kepada sesuatu yang tidak menyenangkan (hari pembalasan),'),
(4853, '054:007', '68', 54, 7, 'sambil menundukkan pandangan-pandangan mereka keluar dari kuburan seakan-akan mereka belalang yang beterbangan'),
(4854, '054:008', '68', 54, 8, 'mereka datang dengan cepat kepada penyeru itu. Orang-orang kafir berkata: \"Ini adalah hari yang berat\".'),
(4855, '054:009', '68', 54, 9, 'Sebelum mereka, telah mendustakan (pula) kamu Nuh, maka mereka mendustakan hamba Kami (Nuh) dan mengatakan: \"Dia seorang gila dan dia sudah pernah diberi ancaman).'),
(4856, '054:010', '68', 54, 10, 'Maka dia mengadu kepada Tuhannya: \"bahwasanya aku ini adalah orang yang dikalahkan, oleh sebab itu menangkanlah (aku)\".'),
(4857, '054:011', '68', 54, 11, 'Maka Kami bukakan pintu-pintu langit dengan (menurunkan) air yang tercurah.'),
(4858, '054:012', '68', 54, 12, 'Dan Kami jadikan bumi memancarkan mata air-mata air, maka bertemulah air-air itu untuk suatu urusan yang sungguh telah ditetapkan.'),
(4859, '054:013', '68', 54, 13, 'Dan Kami angkut Nuh ke atas (bahtera) yang terbuat dari papan dan paku,'),
(4860, '054:014', '68', 54, 14, 'Yang berlayar dengan pemeliharaan Kami sebagai belasan bagi orang-orang yang diingkari (Nuh).'),
(4861, '054:015', '68', 54, 15, 'Dan sesungguhnya telah Kami jadikan kapal itu sebagai pelajaran, maka adakah orang yang mau mengambil pelajaran?'),
(4862, '054:016', '68', 54, 16, 'Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.'),
(4863, '054:017', '68', 54, 17, 'Dan sesungguhnya telah Kami mudahkan Al-Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?'),
(4864, '054:018', '68', 54, 18, 'Kaum ´Aad pun mendustakan (pula). Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.'),
(4865, '054:019', '68', 54, 19, 'Sesungguhnya Kami telah menghembuskan kepada mereka angin yang sangat kencang pada hari nahas yang terus menerus,'),
(4866, '054:020', '68', 54, 20, 'yang menggelimpangkan manusia seakan-akan mereka pokok korma yang tumbang.'),
(4867, '054:021', '68', 54, 21, 'Maka alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.'),
(4868, '054:022', '68', 54, 22, 'Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?'),
(4869, '054:023', '68', 54, 23, 'Kaum Tsamudpun telah mendustakan ancaman-ancaman (itu).'),
(4870, '054:024', '68', 54, 24, 'Maka mereka berkata: \"Bagaimana kita akan mengikuti seorang manusia (biasa) di antara kita?\" Sesungguhnya kalau kita begitu benar-benar berada dalam keadaan sesat dan gila\".'),
(4871, '054:025', '68', 54, 25, 'Apakah wahyu itu diturunkan kepadanya di antara kita? Sebenarnya dia adalah seorang yang amat pendusta lagi sombong.'),
(4872, '054:026', '68', 54, 26, 'Kelak mereka akan mengetahui siapakah yang sebenarnya amat pendusta lagi sombong.'),
(4873, '054:027', '68', 54, 27, 'Sesungguhnya Kami akan mengirimkan unta betina sebagai cobaan bagi mereka, maka tunggulah (tindakan) mereka dan bersabarlah.'),
(4874, '054:028', '68', 54, 28, 'Dan beritakanlah kepada mereka bahwa sesungguhnya air itu terbagi antara mereka (dengan unta betina itu); tiap-tiap giliran minum dihadiri (oleh yang punya giliran)'),
(4875, '054:029', '68', 54, 29, 'Maka mereka memanggil kawannya, lalu kawannya menangkap (unta itu) dan membunuhnya.'),
(4876, '054:030', '68', 54, 30, 'Alangkah dahsyatnya azab-Ku dan ancaman-ancaman-Ku.'),
(4877, '054:031', '68', 54, 31, 'Sesungguhnya Kami menimpakan atas mereka satu suara yang keras mengguntur, maka jadilah mereka seperti rumput kering (yang dikumpulkan oleh) yang punya kandang binatang.'),
(4878, '054:032', '68', 54, 32, 'Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?'),
(4879, '054:033', '68', 54, 33, 'Kaum Luth-pun telah mendustakan ancaman-ancaman (nabinya).'),
(4880, '054:034', '68', 54, 34, 'Sesungguhnya Kami telah menghembuskan kepada mereka angin yang membawa batu-batu (yang menimpa mereka), kecuali keluarga Luth. Mereka Kami selamatkan sebelum fajar menyingsing,'),
(4881, '054:035', '68', 54, 35, 'sebagai nikmat dari Kami. Demikianlah Kami memberi balasan kepada orang-orang yang bersyukur,'),
(4882, '054:036', '68', 54, 36, 'Dan sesungguhnya dia (Luth) telah memperingatkan mereka akan azab-azab Kami, maka mereka mendustakan ancaman-ancaman itu.'),
(4883, '054:037', '68', 54, 37, 'Dan sesungguhnya mereka telah membujuknya (agar menyerahkan) tamunya (kepada mereka), lalu Kami butakan mata mereka, maka rasakanlah azab-Ku dan ancaman-ancaman-Ku.'),
(4884, '054:038', '68', 54, 38, 'Dan sesungguhnya pada esok harinya mereka ditimpa azab yang kekal.'),
(4885, '054:039', '68', 54, 39, 'Maka rasakanlah azab-Ku dan ancaman-ancaman-Ku.'),
(4886, '054:040', '68', 54, 40, 'Dan sesungguhnya telah Kami mudahkan Al Quran untuk pelajaran, maka adakah orang yang mengambil pelajaran?'),
(4887, '054:041', '68', 54, 41, 'Dan sesungguhnya telah datang kepada kaum Fir´aun ancaman-ancaman.'),
(4888, '054:042', '68', 54, 42, 'Mereka mendustakan mukjizat Kami semuanya, lalu Kami azab mereka sebagai azab dari Yang Maha Perkasa lagi Maha Kuasa'),
(4889, '054:043', '68', 54, 43, 'Apakah orang-orang kafirmu (hai kaum musyrikin) lebih baik dari mereka itu, atau apakah kamu telah mempunyai jaminan kebebasan (dari azab) dalam Kitab-kitab yang dahulu'),
(4890, '054:044', '68', 54, 44, 'Atau apakah mereka mengatakan: \"Kami adalah satu golongan yang bersatu yang pasti menang\".'),
(4891, '054:045', '68', 54, 45, 'Golongan itu pasti akan dikalahkan dan mereka akan mundur ke belakang.'),
(4892, '054:046', '68', 54, 46, 'Sebenarnya hari kiamat itulah hari yang dijanjikan kepada mereka dan kiamat itu lebih dahsyat dan lebih pahit.'),
(4893, '054:047', '68', 54, 47, 'Sesungguhnya orang-orang yang berdosa berada dalam kesesatan (di dunia) dan dalam neraka.'),
(4894, '054:048', '68', 54, 48, '(Ingatlah) pada hari mereka diseret ke neraka atas muka mereka. (Dikatakan kepada mereka): \"Rasakanlah sentuhan api neraka!\"'),
(4895, '054:049', '68', 54, 49, 'Sesungguhnya Kami menciptakan segala sesuatu menurut ukuran.'),
(4896, '054:050', '68', 54, 50, 'Dan perintah Kami hanyalah satu perkataan seperti kejapan mata.'),
(4897, '054:051', '68', 54, 51, 'Dan sesungguhnya telah Kami binasakan orang yang serupa dengan kamu. Maka adakah orang yang mau mengambil pelajaran?'),
(4898, '054:052', '68', 54, 52, 'Dan segala sesuatu yang telah mereka perbuat tercatat dalam buku-buku catatan'),
(4899, '054:053', '68', 54, 53, 'Dan segala (urusan) yang kecil maupun yang besar adalah tertulis.'),
(4900, '054:054', '68', 54, 54, 'Sesungguhnya orang-orang yang bertakwa itu di dalam taman-taman dan sungai-sungai,'),
(4901, '054:055', '68', 54, 55, 'di tempat yang disenangi di sisi Tuhan Yang Berkuasa.'),
(4902, '055:001', '68', 55, 1, '(Tuhan) Yang Maha Pemurah,'),
(4903, '055:002', '68', 55, 2, 'Yang telah mengajarkan al Quran.'),
(4904, '055:003', '68', 55, 3, 'Dia menciptakan manusia.'),
(4905, '055:004', '68', 55, 4, 'Mengajarnya pandai berbicara.'),
(4906, '055:005', '68', 55, 5, 'Matahari dan bulan (beredar) menurut perhitungan.'),
(4907, '055:006', '68', 55, 6, 'Dan tumbuh-tumbuhan dan pohon-pohonan kedua-duanya tunduk kepada-Nya.'),
(4908, '055:007', '68', 55, 7, 'Dan Allah telah meninggikan langit dan Dia meletakkan neraca (keadilan).'),
(4909, '055:008', '68', 55, 8, 'Supaya kamu jangan melampaui batas tentang neraca itu.'),
(4910, '055:009', '68', 55, 9, 'Dan tegakkanlah timbangan itu dengan adil dan janganlah kamu mengurangi neraca itu.'),
(4911, '055:010', '68', 55, 10, 'Dan Allah telah meratakan bumi untuk makhluk(Nya).'),
(4912, '055:011', '68', 55, 11, 'Di bumi itu ada buah-buahan dan pohon kurma yang mempunyai kelopak mayang.'),
(4913, '055:012', '68', 55, 12, 'Dan biji-bijian yang berkulit dan bunga-bunga yang harum baunya.'),
(4914, '055:013', '68', 55, 13, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4915, '055:014', '68', 55, 14, 'Dia menciptakan manusia dari tanah kering seperti tembikar,'),
(4916, '055:015', '68', 55, 15, 'dan Dia menciptakan jin dari nyala api.'),
(4917, '055:016', '68', 55, 16, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4918, '055:017', '68', 55, 17, 'Tuhan yang memelihara kedua tempat terbit matahari dan Tuhan yang memelihara kedua tempat terbenamnya'),
(4919, '055:018', '68', 55, 18, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4920, '055:019', '68', 55, 19, 'Dia membiarkan dua lautan mengalir yang keduanya kemudian bertemu,'),
(4921, '055:020', '68', 55, 20, 'antara keduanya ada batas yang tidak dilampaui masing-masing.'),
(4922, '055:021', '68', 55, 21, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4923, '055:022', '68', 55, 22, 'Dari keduanya keluar mutiara dan marjan.'),
(4924, '055:023', '68', 55, 23, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4925, '055:024', '68', 55, 24, 'Dan kepunyaan-Nya lah bahtera-bahtera yang tinggi layarnya di lautan laksana gunung-gunung.'),
(4926, '055:025', '68', 55, 25, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4927, '055:026', '68', 55, 26, 'Semua yang ada di bumi itu akan binasa.'),
(4928, '055:027', '68', 55, 27, 'Dan tetap kekal Dzat Tuhanmu yang mempunyai kebesaran dan kemuliaan.'),
(4929, '055:028', '68', 55, 28, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4930, '055:029', '68', 55, 29, 'Semua yang ada di langit dan bumi selalu meminta kepada-Nya. Setiap waktu Dia dalam kesibukan.'),
(4931, '055:030', '68', 55, 30, 'Maka nikmat Rabb-mu yang manakah yang kamu dustakan?'),
(4932, '055:031', '68', 55, 31, 'Kami akan memperhatikan sepenuhnya kepadamu hai manusia dan jin.'),
(4933, '055:032', '68', 55, 32, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4934, '055:033', '68', 55, 33, 'Hai jama´ah jin dan manusia, jika kamu sanggup menembus (melintasi) penjuru langit dan bumi, maka lintasilah, kamu tidak dapat menembusnya kecuali dengan kekuatan.'),
(4935, '055:034', '68', 55, 34, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4936, '055:035', '68', 55, 35, 'Kepada kamu, (jin dan manusia) dilepaskan nyala api dan cairan tembaga maka kamu tidak dapat menyelamatkan diri (dari padanya).'),
(4937, '055:036', '68', 55, 36, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4938, '055:037', '68', 55, 37, 'Maka apabila langit telah terbelah dan menjadi merah mawar seperti (kilapan) minyak.'),
(4939, '055:038', '68', 55, 38, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4940, '055:039', '68', 55, 39, 'Pada waktu itu manusia dan jin tidak ditanya tentang dosanya.'),
(4941, '055:040', '68', 55, 40, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4942, '055:041', '68', 55, 41, 'Orang-orang yang berdosa dikenal dengan tanda-tandannya, lalu dipegang ubun-ubun dan kaki mereka.'),
(4943, '055:042', '68', 55, 42, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4944, '055:043', '68', 55, 43, 'Inilah neraka Jahannam yang didustakan oleh orang-orang berdosa.'),
(4945, '055:044', '68', 55, 44, 'Mereka berkeliling di antaranya dan di antara air mendidih yang memuncak panasnya.'),
(4946, '055:045', '68', 55, 45, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4947, '055:046', '68', 55, 46, 'Dan bagi orang yang takut akan saat menghadap Tuhannya ada dua surga.'),
(4948, '055:047', '68', 55, 47, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?,'),
(4949, '055:048', '68', 55, 48, 'kedua surga itu mempunyai pohon-pohonan dan buah-buahan.'),
(4950, '055:049', '68', 55, 49, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4951, '055:050', '68', 55, 50, 'Di dalam kedua surga itu ada dua buah mata air yang mengalir'),
(4952, '055:051', '68', 55, 51, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4953, '055:052', '68', 55, 52, 'Di dalam kedua surga itu terdapat segala macam buah-buahan yang berpasangan.'),
(4954, '055:053', '68', 55, 53, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4955, '055:054', '68', 55, 54, 'Mereka bertelekan di atas permadani yang sebelah dalamnya dari sutera. Dan buah-buahan di kedua surga itu dapat (dipetik) dari dekat.'),
(4956, '055:055', '68', 55, 55, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4957, '055:056', '68', 55, 56, 'Di dalam surga itu ada bidadari-bidadari yang sopan menundukkan pandangannya, tidak pernah disentuh oleh manusia sebelum mereka (penghuni-penghuni surga yang menjadi suami mereka), dan tidak pula oleh jin.'),
(4958, '055:057', '68', 55, 57, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4959, '055:058', '68', 55, 58, 'Seakan-akan bidadari itu permata yakut dan marjan.'),
(4960, '055:059', '68', 55, 59, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4961, '055:060', '68', 55, 60, 'Tidak ada balasan kebaikan kecuali kebaikan (pula).'),
(4962, '055:061', '68', 55, 61, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4963, '055:062', '68', 55, 62, 'Dan selain dari dua surga itu ada dua surga lagi'),
(4964, '055:063', '68', 55, 63, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4965, '055:064', '68', 55, 64, 'Kedua surga itu (kelihatan) hijau tua warnanya.'),
(4966, '055:065', '68', 55, 65, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4967, '055:066', '68', 55, 66, 'Di dalam kedua surga itu ada dua buah mata air yang memancar.'),
(4968, '055:067', '68', 55, 67, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4969, '055:068', '68', 55, 68, 'Di dalam keduanya (ada macam-macam) buah-buahan dan kurma serta delima.'),
(4970, '055:069', '68', 55, 69, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4971, '055:070', '68', 55, 70, 'Di dalam surga itu ada bidadari-bidadari yang baik-baik lagi cantik-cantik.'),
(4972, '055:071', '68', 55, 71, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4973, '055:072', '68', 55, 72, '(Bidadari-bidadari) yang jelita, putih bersih, dipingit dalam rumah.'),
(4974, '055:073', '68', 55, 73, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4975, '055:074', '68', 55, 74, 'Mereka tidak pernah disentuh oleh manusia sebelum mereka (penghuni-penghuni surga yang menjadi suami mereka), dan tidak pula oleh jin.'),
(4976, '055:075', '68', 55, 75, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4977, '055:076', '68', 55, 76, 'Mereka bertelekan pada bantal-bantal yang hijau dan permadani-permadani yang indah.'),
(4978, '055:077', '68', 55, 77, 'Maka nikmat Tuhan kamu yang manakah yang kamu dustakan?'),
(4979, '055:078', '68', 55, 78, 'Maha Agung nama Tuhanmu Yang Mempunyai Kebesaran dan Karunia.'),
(4980, '056:001', '68', 56, 1, 'Apabila terjadi hari kiamat,'),
(4981, '056:002', '68', 56, 2, 'tidak seorangpun dapat berdusta tentang kejadiannya.'),
(4982, '056:003', '68', 56, 3, '(Kejadian itu) merendahkan (satu golongan) dan meninggikan (golongan yang lain),'),
(4983, '056:004', '68', 56, 4, 'apabila bumi digoncangkan sedahsyat-dahsyatnya,'),
(4984, '056:005', '68', 56, 5, 'dan gunung-gunung dihancur luluhkan seluluh-luluhnya,'),
(4985, '056:006', '68', 56, 6, 'maka jadilah ia debu yang beterbangan,'),
(4986, '056:007', '68', 56, 7, 'dan kamu menjadi tiga golongan.'),
(4987, '056:008', '68', 56, 8, 'Yaitu golongan kanan. Alangkah mulianya golongan kanan itu.'),
(4988, '056:009', '68', 56, 9, 'Dan golongan kiri. Alangkah sengsaranya golongan kiri itu.'),
(4989, '056:010', '68', 56, 10, 'Dan orang-orang yang beriman paling dahulu,'),
(4990, '056:011', '68', 56, 11, 'Mereka itulah yang didekatkan kepada Allah.'),
(4991, '056:012', '68', 56, 12, 'Berada dalam jannah kenikmatan.'),
(4992, '056:013', '68', 56, 13, 'Segolongan besar dari orang-orang yang terdahulu,'),
(4993, '056:014', '68', 56, 14, 'dan segolongan kecil dari orang-orang yang kemudian'),
(4994, '056:015', '68', 56, 15, 'Mereka berada di atas dipan yang bertahta emas dan permata,'),
(4995, '056:016', '68', 56, 16, 'seraya bertelekan di atasnya berhadap-hadapan.'),
(4996, '056:017', '68', 56, 17, 'Mereka dikelilingi oleh anak-anak muda yang tetap muda,'),
(4997, '056:018', '68', 56, 18, 'dengan membawa gelas, cerek dan minuman yang diambil dari air yang mengalir,'),
(4998, '056:019', '68', 56, 19, 'mereka tidak pening karenanya dan tidak pula mabuk,'),
(4999, '056:020', '68', 56, 20, 'dan buah-buahan dari apa yang mereka pilih,'),
(5000, '056:021', '68', 56, 21, 'dan daging burung dari apa yang mereka inginkan.'),
(5001, '056:022', '68', 56, 22, 'Dan ada bidadari-bidadari bermata jeli,'),
(5002, '056:023', '68', 56, 23, 'laksana mutiara yang tersimpan baik.'),
(5003, '056:024', '68', 56, 24, 'Sebagai balasan bagi apa yang telah mereka kerjakan.'),
(5004, '056:025', '68', 56, 25, 'Mereka tidak mendengar di dalamnya perkataan yang sia-sia dan tidak pula perkataan yang menimbulkan dosa,'),
(5005, '056:026', '68', 56, 26, 'akan tetapi mereka mendengar ucapan salam.'),
(5006, '056:027', '68', 56, 27, 'Dan golongan kanan, alangkah bahagianya golongan kanan itu.'),
(5007, '056:028', '68', 56, 28, 'Berada di antara pohon bidara yang tak berduri,'),
(5008, '056:029', '68', 56, 29, 'dan pohon pisang yang bersusun-susun (buahnya),'),
(5009, '056:030', '68', 56, 30, 'dan naungan yang terbentang luas,'),
(5010, '056:031', '68', 56, 31, 'dan air yang tercurah,'),
(5011, '056:032', '68', 56, 32, 'dan buah-buahan yang banyak,'),
(5012, '056:033', '68', 56, 33, 'yang tidak berhenti (berbuah) dan tidak terlarang mengambilnya.'),
(5013, '056:034', '68', 56, 34, 'dan kasur-kasur yang tebal lagi empuk.'),
(5014, '056:035', '68', 56, 35, 'Sesungguhnya Kami menciptakan mereka (bidadari-bidadari) dengan langsung'),
(5015, '056:036', '68', 56, 36, 'dan Kami jadikan mereka gadis-gadis perawan.'),
(5016, '056:037', '68', 56, 37, 'penuh cinta lagi sebaya umurnya.'),
(5017, '056:038', '68', 56, 38, '(Kami ciptakan mereka) untuk golongan kanan,'),
(5018, '056:039', '68', 56, 39, '(yaitu) segolongan besar dari orang-orang yang terdahulu.'),
(5019, '056:040', '68', 56, 40, 'dan segolongan besar pula dari orang-orang yang kemudian.'),
(5020, '056:041', '68', 56, 41, 'Dan golongan kiri, siapakah golongan kiri itu?'),
(5021, '056:042', '68', 56, 42, 'Dalam (siksaan) angin yang amat panas, dan air panas yang mendidih,'),
(5022, '056:043', '68', 56, 43, 'dan dalam naungan asap yang hitam.'),
(5023, '056:044', '68', 56, 44, 'Tidak sejuk dan tidak menyenangkan.'),
(5024, '056:045', '68', 56, 45, 'Sesungguhnya mereka sebelum itu hidup bermewahan.'),
(5025, '056:046', '68', 56, 46, 'Dan mereka terus-menerus mengerjakan dosa besar.'),
(5026, '056:047', '68', 56, 47, 'Dan mereka selalu mengatakan: \"Apakah bila kami mati dan menjadi tanah dan tulang belulang, apakah sesungguhnya kami akan benar-benar dibangkitkan kembali?'),
(5027, '056:048', '68', 56, 48, 'apakah bapak-bapak kami yang terdahulu (juga)?\"'),
(5028, '056:049', '68', 56, 49, 'Katakanlah: \"Sesungguhnya orang-orang yang terdahulu dan orang-orang yang terkemudian,'),
(5029, '056:050', '68', 56, 50, 'benar-benar akan dikumpulkan di waktu tertentu pada hari yang dikenal.'),
(5030, '056:051', '68', 56, 51, 'Kemudian sesungguhnya kamu hai orang-orang yang sesat lagi mendustakan,'),
(5031, '056:052', '68', 56, 52, 'benar-benar akan memakan pohon zaqqum,'),
(5032, '056:053', '68', 56, 53, 'dan akan memenuhi perutmu dengannya.'),
(5033, '056:054', '68', 56, 54, 'Sesudah itu kamu akan meminum air yang sangat panas.'),
(5034, '056:055', '68', 56, 55, 'Maka kamu minum seperti unta yang sangat haus minum.'),
(5035, '056:056', '68', 56, 56, 'Itulah hidangan untuk mereka pada hari Pembalasan\".'),
(5036, '056:057', '68', 56, 57, 'Kami telah menciptakan kamu, maka mengapa kamu tidak membenarkan?'),
(5037, '056:058', '68', 56, 58, 'Maka terangkanlah kepadaku tentang nutfah yang kamu pancarkan.'),
(5038, '056:059', '68', 56, 59, 'Kamukah yang menciptakannya, atau Kamikah yang menciptakannya?'),
(5039, '056:060', '68', 56, 60, 'Kami telah menentukan kematian di antara kamu dan Kami sekali-sekali tidak akan dapat dikalahkan,'),
(5040, '056:061', '68', 56, 61, 'untuk menggantikan kamu dengan orang-orang yang seperti kamu (dalam dunia) dan menciptakan kamu kelak (di akhirat) dalam keadaan yang tidak kamu ketahui.'),
(5041, '056:062', '68', 56, 62, 'Dan Sesungguhnya kamu telah mengetahui penciptaan yang pertama, maka mengapakah kamu tidak mengambil pelajaran (untuk penciptaan yang kedua)?'),
(5042, '056:063', '68', 56, 63, 'Maka terangkanlah kepadaku tentang yang kamu tanam.'),
(5043, '056:064', '68', 56, 64, 'Kamukah yang menumbuhkannya atau Kamikah yang menumbuhkannya?'),
(5044, '056:065', '68', 56, 65, 'Kalau Kami kehendaki, benar-benar Kami jadikan dia hancur dan kering, maka jadilah kamu heran dan tercengang.'),
(5045, '056:066', '68', 56, 66, '(Sambil berkata): \"Sesungguhnya kami benar-benar menderita kerugian\",'),
(5046, '056:067', '68', 56, 67, 'bahkan kami menjadi orang-orang yang tidak mendapat hasil apa-apa.'),
(5047, '056:068', '68', 56, 68, 'Maka terangkanlah kepadaku tentang air yang kamu minum.'),
(5048, '056:069', '68', 56, 69, 'Kamukah yang menurunkannya atau Kamikah yang menurunkannya?'),
(5049, '056:070', '68', 56, 70, 'Kalau Kami kehendaki, niscaya Kami jadikan dia asin, maka mengapakah kamu tidak bersyukur?'),
(5050, '056:071', '68', 56, 71, 'Maka terangkanlah kepadaku tentang api yang kamu nyalakan (dengan menggosok-gosokkan kayu).'),
(5051, '056:072', '68', 56, 72, 'Kamukah yang menjadikan kayu itu atau Kamikah yang menjadikannya?'),
(5052, '056:073', '68', 56, 73, 'Kami jadikan api itu untuk peringatan dan bahan yang berguna bagi musafir di padang pasir.'),
(5053, '056:074', '68', 56, 74, 'Maka bertasbihlah dengan (menyebut) nama Rabbmu Yang Maha Besar.'),
(5054, '056:075', '68', 56, 75, 'Maka Aku bersumpah dengan masa turunnya bagian-bagian Al-Quran.'),
(5055, '056:076', '68', 56, 76, 'Sesungguhnya sumpah itu adalah sumpah yang besar kalau kamu mengetahui.'),
(5056, '056:077', '68', 56, 77, 'Sesungguhnya Al-Quran ini adalah bacaan yang sangat mulia,'),
(5057, '056:078', '68', 56, 78, 'pada kitab yang terpelihara (Lauhul Mahfuzh),'),
(5058, '056:079', '68', 56, 79, 'tidak menyentuhnya kecuali orang-orang yang disucikan.'),
(5059, '056:080', '68', 56, 80, 'Diturunkan dari Rabbil ´alamiin.'),
(5060, '056:081', '68', 56, 81, 'Maka apakah kamu menganggap remeh saja Al-Quran ini?'),
(5061, '056:082', '68', 56, 82, 'kamu mengganti rezeki (yang Allah berikan) dengan mendustakan Allah.'),
(5062, '056:083', '68', 56, 83, 'Maka mengapa ketika nyawa sampai di kerongkongan,'),
(5063, '056:084', '68', 56, 84, 'padahal kamu ketika itu melihat,'),
(5064, '056:085', '68', 56, 85, 'dan Kami lebih dekat kepadanya dari pada kamu. Tetapi kamu tidak melihat,'),
(5065, '056:086', '68', 56, 86, 'maka mengapa jika kamu tidak dikuasai (oleh Allah)?'),
(5066, '056:087', '68', 56, 87, 'Kamu tidak mengembalikan nyawa itu (kepada tempatnya) jika kamu adalah orang-orang yang benar?'),
(5067, '056:088', '68', 56, 88, 'adapun jika dia (orang yang mati) termasuk orang-orang yang didekatkan (kepada Allah),'),
(5068, '056:089', '68', 56, 89, 'maka dia memperoleh ketenteraman dan rezeki serta jannah kenikmatan.'),
(5069, '056:090', '68', 56, 90, 'Dan adapun jika dia termasuk golongan kanan,'),
(5070, '056:091', '68', 56, 91, 'maka keselamatanlah bagimu karena kamu dari golongan kanan.'),
(5071, '056:092', '68', 56, 92, 'Dan adapun jika dia termasuk golongan yang mendustakan lagi sesat,'),
(5072, '056:093', '68', 56, 93, 'maka dia mendapat hidangan air yang mendidih,'),
(5073, '056:094', '68', 56, 94, 'dan dibakar di dalam jahannam.'),
(5074, '056:095', '68', 56, 95, 'Sesungguhnya (yang disebutkan ini) adalah suatu keyakinan yang benar.'),
(5075, '056:096', '68', 56, 96, 'Maka bertasbihlah dengan (menyebut) nama Rabbmu yang Maha Besar.'),
(5076, '057:001', '68', 57, 1, 'Semua yang berada di langit dan yang berada di bumi bertasbih kepada Allah (menyatakan kebesaran Allah). Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(5077, '057:002', '68', 57, 2, 'Kepunyaan-Nya-lah kerajaan langit dan bumi, Dia menghidupkan dan mematikan, dan Dia Maha Kuasa atas segala sesuatu.'),
(5078, '057:003', '68', 57, 3, 'Dialah Yang Awal dan Yang Akhir Yang Zhahir dan Yang Bathin; dan Dia Maha Mengetahui segala sesuatu.'),
(5079, '057:004', '68', 57, 4, 'Dialah yang menciptakan langit dan bumi dalam enam masa: Kemudian Dia bersemayam di atas ´Arsy. Dia mengetahui apa yang masuk ke dalam bumi dan apa yang keluar daripadanya dan apa yang turun dari langit dan apa yang naik kepada-Nya. Dan Dia bersama kamu di mana saja kamu berada. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
(5080, '057:005', '68', 57, 5, 'Kepunyaan-Nya-lah kerajaan langit dan bumi. Dan kepada Allah-lah dikembalikan segala urusan.'),
(5081, '057:006', '68', 57, 6, 'Dialah yang memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam. Dan Dia Maha Mengetahui segala isi hati.'),
(5082, '057:007', '68', 57, 7, 'Berimanlah kamu kepada Allah dan Rasul-Nya dan nafkahkanlah sebagian dari hartamu yang Allah telah menjadikan kamu menguasainya. Maka orang-orang yang beriman di antara kamu dan menafkahkan (sebagian) dari hartanya memperoleh pahala yang besar.'),
(5083, '057:008', '68', 57, 8, 'Dan mengapa kamu tidak beriman kepada Allah padahal Rasul menyeru kamu supaya kamu beriman kepada Tuhanmu. Dan sesungguhnya Dia telah mengambil perjanjianmu jika kamu adalah orang-orang yang beriman.'),
(5084, '057:009', '68', 57, 9, 'Dialah yang menurunkan kepada hamba-Nya ayat-ayat yang terang (Al-Quran) supaya Dia mengeluarkan kamu dari kegelapan kepada cahaya. Dan sesungguhnya Allah benar-benar Maha Penyantun lagi Maha Penyayang terhadapmu.'),
(5085, '057:010', '68', 57, 10, 'Dan mengapa kamu tidak menafkahkan (sebagian hartamu) pada jalan Allah, padahal Allah-lah yang mempusakai (mempunyai) langit dan bumi? Tidak sama di antara kamu orang yang menafkahkan (hartanya) dan berperang sebelum penaklukan (Mekah). Mereka lebih tingi derajatnya daripada orang-orang yang menafkahkan (hartanya) dan berperang sesudah itu. Allah menjanjikan kepada masing-masing mereka (balasan) yang lebih baik. Dan Allah mengetahui apa yang kamu kerjakan.'),
(5086, '057:011', '68', 57, 11, 'Siapakah yang mau meminjamkan kepada Allah pinjaman yang baik, maka Allah akan melipat-gandakan (balasan) pinjaman itu untuknya, dan dia akan memperoleh pahala yang banyak.'),
(5087, '057:012', '68', 57, 12, '(yaitu) pada hari ketika kamu melihat orang mukmin laki-laki dan perempuan, sedang cahaya mereka bersinar di hadapan dan di sebelah kanan mereka, (dikatakan kepada mereka): \"Pada hari ini ada berita gembira untukmu, (yaitu) surga yang mengalir di bawahnya sungai-sungai, yang kamu kekal di dalamnya. Itulah keberuntungan yang besar\".'),
(5088, '057:013', '68', 57, 13, 'Pada hari ketika orang-orang munafik laki-laki dan perempuan berkata kepada orang-orang yang beriman: \"Tunggulah kami supaya kami dapat mengambil sebahagian dari cahayamu\". Dikatakan (kepada mereka): \"Kembalilah kamu ke belakang dan carilah sendiri cahaya (untukmu)\". Lalu diadakan di antara mereka dinding yang mempunyai pintu. Di sebelah dalamnya ada rahmat dan di sebelah luarnya dari situ ada siksa.'),
(5089, '057:014', '68', 57, 14, 'Orang-orang munafik itu memanggil mereka (orang-orang mukmin) seraya berkata: \"Bukankah kami dahulu bersama-sama dengan kamu?\" Mereka menjawab: \"Benar, tetapi kamu mencelakakan dirimu sendiri dan menunggu (kehancuran kami) dan kamu ragu-ragu serta ditipu oleh angan-angan kosong sehingga datanglah ketetapan Allah;dan kamu telah ditipu terhadap Allah oleh (syaitan) yang amat penipu.'),
(5090, '057:015', '68', 57, 15, 'Maka pada hari ini tidak diterima tebusan dari kamu dan tidak pula dari orang-orang kafir. Tempat kamu ialah neraka. Dialah tempat berlindungmu. Dan dia adalah sejahat-jahat tempat kembali\".'),
(5091, '057:016', '68', 57, 16, 'Belumkah datang waktunya bagi orang-orang yang beriman, untuk tunduk hati mereka mengingat Allah dan kepada kebenaran yang telah turun (kepada mereka), dan janganlah mereka seperti orang-orang yang sebelumnya telah diturunkan Al Kitab kepadanya, kemudian berlalulah masa yang panjang atas mereka lalu hati mereka menjadi keras. Dan kebanyakan di antara mereka adalah orang-orang yang fasik.'),
(5092, '057:017', '68', 57, 17, 'Ketahuilah olehmu bahwa sesungguhnya Allah menghidupkan bumi sesudah matinya. Sesungguhnya Kami telah menjelaskan kepadamu tanda-tanda kebesaran (Kami) supaya kamu memikirkannya.'),
(5093, '057:018', '68', 57, 18, 'Sesungguhnya orang-orang yang membenarkan (Allah dan Rasul-Nya) baik laki-laki maupun perempuan dan meminjamkan kepada Allah pinjaman yang baik, niscaya akan dilipatgandakan (pembayarannya) kepada mereka; dan bagi mereka pahala yang banyak.'),
(5094, '057:019', '68', 57, 19, 'Dan orang-orang yang beriman kepada Allah dan Rasul-Nya, mereka itu orang-orang Shiddiqien dan orang-orang yang menjadi saksi di sisi Tuhan mereka. Bagi mereka pahala dan cahaya mereka. Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni-penghuni neraka.'),
(5095, '057:020', '68', 57, 20, 'Ketahuilah, bahwa sesungguhnya kehidupan dunia ini hanyalah permainan dan suatu yang melalaikan, perhiasan dan bermegah-megah antara kamu serta berbangga-banggaan tentang banyaknya harta dan anak, seperti hujan yang tanam-tanamannya mengagumkan para petani; kemudian tanaman itu menjadi kering dan kamu lihat warnanya kuning kemudian menjadi hancur. Dan di akhirat (nanti) ada azab yang keras dan ampunan dari Allah serta keridhaan-Nya. Dan kehidupan dunia ini tidak lain hanyalah kesenangan yang menipu.'),
(5096, '057:021', '68', 57, 21, 'Berlomba-lombalah kamu kepada (mendapatkan) ampunan dari Tuhanmu dan surga yang luasnya seluas langit dan bumi, yang disediakan bagi orang-orang yang beriman kepada Allah dan Rasul-rasul-Nya. Itulah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya. Dan Allah mempunyai karunia yang besar.'),
(5097, '057:022', '68', 57, 22, 'Tiada suatu bencanapun yang menimpa di bumi dan (tidak pula) pada dirimu sendiri melainkan telah tertulis dalam kitab (Lauhul Mahfuzh) sebelum Kami menciptakannya. Sesungguhnya yang demikian itu adalah mudah bagi Allah.'),
(5098, '057:023', '68', 57, 23, '(Kami jelaskan yang demikian itu) supaya kamu jangan berduka cita terhadap apa yang luput dari kamu, dan supaya kamu jangan terlalu gembira terhadap apa yang diberikan-Nya kepadamu. Dan Allah tidak menyukai setiap orang yang sombong lagi membanggakan diri,'),
(5099, '057:024', '68', 57, 24, '(yaitu) orang-orang yang kikir dan menyuruh manusia berbuat kikir. Dan barangsiapa yang berpaling (dari perintah-perintah Allah) maka sesungguhnya Allah Dialah Yang Maha Kaya lagi Maha Terpuji.'),
(5100, '057:025', '68', 57, 25, 'Sesungguhnya Kami telah mengutus rasul-rasul Kami dengan membawa bukti-bukti yang nyata dan telah Kami turunkan bersama mereka Al Kitab dan neraca (keadilan) supaya manusia dapat melaksanakan keadilan. Dan Kami ciptakan besi yang padanya terdapat kekuatan yang hebat dan berbagai manfaat bagi manusia, (supaya mereka mempergunakan besi itu) dan supaya Allah mengetahui siapa yang menolong (agama)Nya dan rasul-rasul-Nya padahal Allah tidak dilihatnya. Sesungguhnya Allah Maha Kuat lagi Maha Perkasa.'),
(5101, '057:026', '68', 57, 26, 'Dan sesungguhnya Kami telah mengutus Nuh dan Ibrahim dan Kami jadikan kepada keturunan keduanya kenabian dan Al Kitab, maka di antara mereka ada yang menerima petunjuk dan banyak di antara mereka fasik.'),
(5102, '057:027', '68', 57, 27, 'Kemudian Kami iringi di belakang mereka dengan rasul-rasul Kami dan Kami iringi (pula) dengan Isa putra Maryam; dan Kami berikan kepadanya Injil dan Kami jadikan dalam hati orang-orang yang mengikutinya rasa santun dan kasih sayang. Dan mereka mengada-adakan rahbaniyyah padahal kami tidak mewajibkannya kepada mereka tetapi (mereka sendirilah yang mengada-adakannya) untuk mencari keridhaan Allah, lalu mereka tidak memeliharanya dengan pemeliharaan yang semestinya. Maka Kami berikan kepada orang-orang yang beriman di antara mereka pahalanya dan banyak di antara mereka orang-orang fasik.'),
(5103, '057:028', '68', 57, 28, 'Hai orang-orang yang beriman (kepada para rasul), bertakwalah kepada Allah dan berimanlah kepada Rasul-Nya, niscaya Allah memberikan rahmat-Nya kepadamu dua bagian, dan menjadikan untukmu cahaya yang dengan cahaya itu kamu dapat berjalan dan Dia mengampuni kamu. Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(5104, '057:029', '68', 57, 29, '(Kami terangkan yang demikian itu) supaya ahli Kitab mengetahui bahwa mereka tiada mendapat sedikitpun akan karunia Allah (jika mereka tidak beriman kepada Muhammad), dan bahwasanya karunia itu adalah di tangan Allah. Dia berikan karunia itu kepada siapa yang dikehendaki-Nya. Dan Allah mempunyai karunia yang besar.'),
(5105, '058:001', '68', 58, 1, 'Sesungguhnya Allah telah mendengar perkataan wanita yang mengajukan gugatan kepada kamu tentang suaminya, dan mengadukan (halnya) kepada Allah. Dan Allah mendengar soal jawab antara kamu berdua. Sesungguhnya Allah Maha Mendengar lagi Maha Melihat.'),
(5106, '058:002', '68', 58, 2, 'Orang-orang yang menzhihar isterinya di antara kamu, (menganggap isterinya sebagai ibunya, padahal) tiadalah isteri mereka itu ibu mereka. Ibu-ibu mereka tidak lain hanyalah wanita yang melahirkan mereka. Dan sesungguhnya mereka sungguh-sungguh mengucapkan suatu perkataan mungkar dan dusta. Dan sesungguhnya Allah Maha Pemaaf lagi Maha Pengampun.'),
(5107, '058:003', '68', 58, 3, 'Orang-orang yang menzhihar isteri mereka, kemudian mereka hendak menarik kembali apa yang mereka ucapkan, maka (wajib atasnya) memerdekakan seorang budak sebelum kedua suami isteri itu bercampur. Demikianlah yang diajarkan kepada kamu, dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(5108, '058:004', '68', 58, 4, 'Barangsiapa yang tidak mendapatkan (budak), maka (wajib atasnya) berpuasa dua bulan berturut-turut sebelum keduanya bercampur. Maka siapa yang tidak kuasa (wajiblah atasnya) memberi makan enam puluh orang miskin. Demikianlah supaya kamu beriman kepada Allah dan Rasul-Nya. Dan itulah hukum-hukum Allah, dan bagi orang kafir ada siksaan yang sangat pedih.'),
(5109, '058:005', '68', 58, 5, 'Sesungguhnya orang-orang yang yang menentang Allah dan Rasul-Nya, pasti mendapat kehinaan sebagaimana orang-orang yang sebelum mereka telah mendapat kehinaan. Sesungguhnya Kami telah menurunkan bukti-bukti nyata. Dan bagi orang-orang kafir ada siksa yang menghinakan.'),
(5110, '058:006', '68', 58, 6, 'Pada hari ketika mereka dibangkitkan Allah semuanya, lalu diberitakan-Nya kepada mereka apa yang telah mereka kerjakan. Allah mengumpulkan (mencatat) amal perbuatan itu, padahal mereka telah melupakannya. Dan Allah Maha Menyaksikan segala sesuatu.'),
(5111, '058:007', '68', 58, 7, 'Tidakkah kamu perhatikan, bahwa sesungguhnya Allah mengetahui apa yang ada di langit dan di bumi? Tiada pembicaraan rahasia antara tiga orang, melainkan Dialah keempatnya. Dan tiada (pembicaraan antara) lima orang, melainkan Dialah keenamnya. Dan tiada (pula) pembicaraan antara jumlah yang kurang dari itu atau lebih banyak, melainkan Dia berada bersama mereka di manapun mereka berada. Kemudian Dia akan memberitahukan kepada mereka pada hari kiamat apa yang telah mereka kerjakan. Sesungguhnya Allah Maha mengetahui segala sesuatu.'),
(5112, '058:008', '68', 58, 8, 'Apakah tidak kamu perhatikan orang-orang yang telah dilarang mengadakan pembicaraan rahasia, kemudian mereka kembali (mengerjakan) larangan itu dan mereka mengadakan pembicaraan rahasia untuk berbuat dosa, permusuhan dan durhaka kepada Rasul. Dan apabila mereka datang kepadamu, mereka mengucapkan salam kepadamu dengan memberi salam yang bukan sebagai yang ditentukan Allah untukmu. Dan mereka mengatakan kepada diri mereka sendiri: \"Mengapa Allah tidak menyiksa kita disebabkan apa yang kita katakan itu?\" Cukuplah bagi mereka Jahannam yang akan mereka masuki. Dan neraka itu adalah seburuk-buruk tempat kembali.'),
(5113, '058:009', '68', 58, 9, 'Hai orang-orang beriman, apabila kamu mengadakan pembicaraan rahasia, janganlah kamu membicarakan tentang membuat dosa, permusuhan dan berbuat durhaka kepada Rasul. Dan bicarakanlah tentang membuat kebajikan dan takwa. Dan bertakwalah kepada Allah yang kepada-Nya kamu akan dikembalikan.'),
(5114, '058:010', '68', 58, 10, 'Sesungguhnya pembicaraan rahasia itu adalah dari syaitan, supaya orang-orang yang beriman itu berduka cita, sedang pembicaraan itu tiadalah memberi mudharat sedikitpun kepada mereka, kecuali dengan izin Allah dan kepada Allah-lah hendaknya orang-orang yang beriman bertawakkal.'),
(5115, '058:011', '68', 58, 11, 'Hai orang-orang beriman apabila dikatakan kepadamu: \"Berlapang-lapanglah dalam majlis\", maka lapangkanlah niscaya Allah akan memberi kelapangan untukmu. Dan apabila dikatakan: \"Berdirilah kamu\", maka berdirilah, niscaya Allah akan meninggikan orang-orang yang beriman di antaramu dan orang-orang yang diberi ilmu pengetahuan beberapa derajat. Dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(5116, '058:012', '68', 58, 12, 'Hai orang-orang beriman, apabila kamu mengadakan pembicaraan khusus dengan Rasul hendaklah kamu mengeluarkan sedekah (kepada orang miskin) sebelum pembicaraan itu. Yang demikian itu lebih baik bagimu dan lebih bersih; jika kamu tidak memperoleh (yang akan disedekahkan) maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(5117, '058:013', '68', 58, 13, 'Apakah kamu takut akan (menjadi miskin) karena kamu memberikan sedekah sebelum mengadakan pembicaraan dengan Rasul? Maka jika kamu tiada memperbuatnya dan Allah telah memberi taubat kepadamu maka dirikanlah shalat, tunaikanlah zakat, taatlah kepada Allah dan Rasul-Nya; dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(5118, '058:014', '68', 58, 14, 'Tidakkah kamu perhatikan orang-orang yang menjadikan suatu kaum yang dimurkai Allah sebagai teman? Orang-orang itu bukan dari golongan kamu dan bukan (pula) dari golongan mereka. Dan mereka bersumpah untuk menguatkan kebohongan, sedang mereka mengetahui.'),
(5119, '058:015', '68', 58, 15, 'Allah telah menyediakan bagi mereka azab yang sangat keras, sesungguhnya amat buruklah apa yang telah mereka kerjakan.'),
(5120, '058:016', '68', 58, 16, 'Mereka menjadikan sumpah-sumpah mereka sebagai perisai, lalu mereka halangi (manusia) dari jalan Allah; karena itu mereka mendapat azab yang menghinakan.'),
(5121, '058:017', '68', 58, 17, 'Harta benda dan anak-anak mereka tiada berguna sedikitpun (untuk menolong) mereka dari azab Allah. Mereka itulah penghuni neraka, dan mereka kekal di dalamnya.'),
(5122, '058:018', '68', 58, 18, '(Ingatlah) hari (ketika) mereka semua dibangkitkan Allah lalu mereka bersumpah kepada-Nya (bahwa mereka bukan musyrikin) sebagaimana mereka bersumpah kepadamu; dan mereka menyangka bahwa mereka akan memperoleh suatu (manfaat). Ketahuilah, bahwa sesungguhnya merekalah orang-orang pendusta.'),
(5123, '058:019', '68', 58, 19, 'Syaitan telah menguasai mereka lalu menjadikan mereka lupa mengingat Allah; mereka itulah golongan syaitan. Ketahuilah, bahwa sesungguhnya golongan syaitan itulah golongan yang merugi.'),
(5124, '058:020', '68', 58, 20, 'Sesungguhnya orang-orang yang menentang Allah dan Rasul-Nya, mereka termasuk orang-orang yang sangat hina.'),
(5125, '058:021', '68', 58, 21, 'Allah telah menetapkan: \"Aku dan rasul-rasul-Ku pasti menang\". Sesungguhnya Allah Maha Kuat lagi Maha Perkasa.'),
(5126, '058:022', '68', 58, 22, 'Kamu tak akan mendapati kaum yang beriman pada Allah dan hari akhirat, saling berkasih-sayang dengan orang-orang yang menentang Allah dan Rasul-Nya, sekalipun orang-orang itu bapak-bapak, atau anak-anak atau saudara-saudara ataupun keluarga mereka. Mereka itulah orang-orang yang telah menanamkan keimanan dalam hati mereka dan menguatkan mereka dengan pertolongan yang datang daripada-Nya. Dan dimasukan-Nya mereka ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Allah ridha terhadap mereka, dan merekapun merasa puas terhadap (limpahan rahmat)-Nya. Mereka itulah golongan Allah. Ketahuilah, bahwa sesungguhnya hizbullah itu adalah golongan yang beruntung.'),
(5127, '059:001', '68', 59, 1, 'Telah bertasbih kepada Allah apa yang ada di langit dan bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(5128, '059:002', '68', 59, 2, 'Dialah yang mengeluarkan orang-orang kafir di antara ahli kitab dari kampung-kampung mereka pada saat pengusiran yang pertama. Kamu tidak menyangka, bahwa mereka akan keluar dan merekapun yakin, bahwa benteng-benteng mereka dapat mempertahankan mereka dari (siksa) Allah; maka Allah mendatangkan kepada mereka (hukuman) dari arah yang tidak mereka sangka-sangka. Dan Allah melemparkan ketakutan dalam hati mereka; mereka memusnahkan rumah-rumah mereka dengan tangan mereka sendiri dan tangan orang-orang mukmin. Maka ambillah (kejadian itu) untuk menjadi pelajaran, hai orang-orang yang mempunyai wawasan.'),
(5129, '059:003', '68', 59, 3, 'Dan jika tidaklah karena Allah telah menetapkan pengusiran terhadap mereka, benar-benar Allah mengazab mereka di dunia. Dan bagi mereka di akhirat azab neraka.'),
(5130, '059:004', '68', 59, 4, 'Yang demikian itu adalah karena Sesungguhnya mereka menentang Allah dan Rasul-Nya. Barangsiapa menentang Allah dan Rasul-Nya, Sesungguhnya Allah sangat keras hukuman-Nya.'),
(5131, '059:005', '68', 59, 5, 'Apa saja yang kamu tebang dari pohon kurma (milik orang-orang kafir) atau yang kamu biarkan (tumbuh) berdiri di atas pokoknya, maka (semua itu) adalah dengan izin Allah; dan karena Dia hendak memberikan kehinaan kepada orang-orang fasik.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(5132, '059:006', '68', 59, 6, 'Dan apa saja harta rampasan (fai-i) yang diberikan Allah kepada Rasul-Nya (dari harta benda) mereka, maka untuk mendapatkan itu kamu tidak mengerahkan seekor kudapun dan (tidak pula) seekor untapun, tetapi Allah yang memberikan kekuasaan kepada Rasul-Nya terhadap apa saja yang dikehendaki-Nya. Dan Allah Maha Kuasa atas segala sesuatu.'),
(5133, '059:007', '68', 59, 7, 'Apa saja harta rampasan (fai-i) yang diberikan Allah kepada Rasul-Nya (dari harta benda) yang berasal dari penduduk kota-kota maka adalah untuk Allah, untuk Rasul, kaum kerabat, anak-anak yatim, orang-orang miskin dan orang-orang yang dalam perjalanan, supaya harta itu jangan beredar di antara orang-orang kaya saja di antara kamu. Apa yang diberikan Rasul kepadamu, maka terimalah. Dan apa yang dilarangnya bagimu, maka tinggalkanlah. Dan bertakwalah kepada Allah. Sesungguhnya Allah amat keras hukumannya.'),
(5134, '059:008', '68', 59, 8, '(Juga) bagi orang fakir yang berhijrah yang diusir dari kampung halaman dan dari harta benda mereka (karena) mencari karunia dari Allah dan keridhaan-Nya dan mereka menolong Allah dan Rasul-Nya. Mereka itulah orang-orang yang benar.'),
(5135, '059:009', '68', 59, 9, 'Dan orang-orang yang telah menempati kota Madinah dan telah beriman (Anshor) sebelum (kedatangan) mereka (Muhajirin), mereka (Anshor) ´mencintai´ orang yang berhijrah kepada mereka (Muhajirin). Dan mereka (Anshor) tiada menaruh keinginan dalam hati mereka terhadap apa-apa yang diberikan kepada mereka (Muhajirin); dan mereka mengutamakan (orang-orang Muhajirin), atas diri mereka sendiri, sekalipun mereka dalam kesusahan. Dan siapa yang dipelihara dari kekikiran dirinya, mereka itulah orang orang yang beruntung'),
(5136, '059:010', '68', 59, 10, 'Dan orang-orang yang datang sesudah mereka (Muhajirin dan Anshor), mereka berdoa: \"Ya Rabb kami, beri ampunlah kami dan saudara-saudara kami yang telah beriman lebih dulu dari kami, dan janganlah Engkau membiarkan kedengkian dalam hati kami terhadap orang-orang yang beriman; Ya Rabb kami, Sesungguhnya Engkau Maha Penyantun lagi Maha Penyayang\".'),
(5137, '059:011', '68', 59, 11, 'Apakah kamu tidak memperhatikan orang-orang munafik yang berkata kepada saudara-saudara mereka yang kafir di antara ahli kitab: \"Sesungguhnya jika kamu diusir niscaya kamipun akan keluar bersamamu; dan kami selama-lamanya tidak akan patuh kepada siapapun untuk (menyusahkan) kamu, dan jika kamu diperangi pasti kami akan membantu kamu\". Dan Allah menyaksikan bahwa Sesungguhnya mereka benar-benar pendusta.'),
(5138, '059:012', '68', 59, 12, 'Sesungguhnya jika mereka diusir, orang-orang munafik itu tidak akan keluar bersama mereka, dan sesungguhnya jika mereka diperangi, niscaya mereka tidak akan menolongnya; sesungguhnya jika mereka menolongnya, niscaya mereka akan berpaling lari ke belakang; kemudian mereka tidak akan mendapat pertolongan.'),
(5139, '059:013', '68', 59, 13, 'Sesungguhnya kamu dalam hati mereka lebih ditakuti daripada Allah. Yang demikian itu karena mereka adalah kaum yang tidak mengerti.'),
(5140, '059:014', '68', 59, 14, 'Mereka tidak akan memerangi kamu dalam keadaan bersatu padu, kecuali dalam kampung-kampung yang berbenteng atau di balik tembok. Permusuhan antara sesama mereka adalah sangat hebat. Kamu kira mereka itu bersatu, sedang hati mereka berpecah belah. Yang demikian itu karena sesungguhnya mereka adalah kaum yang tidak mengerti.'),
(5141, '059:015', '68', 59, 15, '(Mereka adalah) seperti orang-orang Yahudi yang belum lama sebelum mereka telah merasai akibat buruk dari perbuatan mereka, dan bagi mereka azab yang pedih.'),
(5142, '059:016', '68', 59, 16, '(Bujukan orang-orang munafik itu adalah) seperti (bujukan) shaitan ketika dia berkata kepada manusia: \"Kafirlah kamu\", maka tatkala manusia itu telah kafir, maka ia berkata: \"Sesungguhnya aku berlepas diri dari kamu, karena sesungguhnya aku takut kepada Allah, Rabb semesta Alam\".'),
(5143, '059:017', '68', 59, 17, 'Maka adalah kesudahan keduanya, bahwa sesungguhnya keduanya (masuk) ke dalam neraka, mereka kekal di dalamnya. Demikianlah balasan orang-orang yang zalim.'),
(5144, '059:018', '68', 59, 18, 'Hai orang-orang yang beriman, bertakwalah kepada Allah dan hendaklah setiap diri memperhatikan apa yang telah diperbuatnya untuk hari esok (akhirat); dan bertakwalah kepada Allah, sesungguhnya Allah Maha Mengetahui apa yang kamu kerjakan.'),
(5145, '059:019', '68', 59, 19, 'Dan janganlah kamu seperti orang-orang yang lupa kepada Allah, lalu Allah menjadikan mereka lupa kepada mereka sendiri. Mereka itulah orang-orang yang fasik.'),
(5146, '059:020', '68', 59, 20, 'Tidaklah sama penghuni-penghuni neraka dengan penghuni-penghuni jannah; penghuni-penghuni jannah itulah orang-orang yang beruntung.'),
(5147, '059:021', '68', 59, 21, 'Kalau sekiranya Kami turunkan Al-Quran ini kepada sebuah gunung, pasti kamu akan melihatnya tunduk terpecah belah disebabkan ketakutannya kepada Allah. Dan perumpamaan-perumpamaan itu Kami buat untuk manusia supaya mereka berfikir.'),
(5148, '059:022', '68', 59, 22, 'Dialah Allah Yang tiada Tuhan selain Dia, Yang Mengetahui yang ghaib dan yang nyata, Dialah Yang Maha Pemurah lagi Maha Penyayang.'),
(5149, '059:023', '68', 59, 23, 'Dialah Allah Yang tiada Tuhan selain Dia, Raja, Yang Maha Suci, Yang Maha Sejahtera, Yang Mengaruniakan Keamanan, Yang Maha Memelihara, Yang Maha Perkasa, Yang Maha Kuasa, Yang Memiliki segala Keagungan, Maha Suci Allah dari apa yang mereka persekutukan.'),
(5150, '059:024', '68', 59, 24, 'Dialah Allah Yang Menciptakan, Yang Mengadakan, Yang Membentuk Rupa, Yang Mempunyai Asmaaul Husna. Bertasbih kepada-Nya apa yang di langit dan bumi. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(5151, '060:001', '68', 60, 1, 'Hai orang-orang yang beriman, janganlah kamu mengambil musuh-Ku dan musuhmu menjadi teman-teman setia yang kamu sampaikan kepada mereka (berita-berita Muhammad), karena rasa kasih sayang; padahal sesungguhnya mereka telah ingkar kepada kebenaran yang datang kepadamu, mereka mengusir Rasul dan (mengusir) kamu karena kamu beriman kepada Allah, Tuhanmu. Jika kamu benar-benar keluar untuk berjihad di jalan-Ku dan mencari keridhaan-Ku (janganlah kamu berbuat demikian). Kamu memberitahukan secara rahasia (berita-berita Muhammad) kepada mereka, karena rasa kasih sayang. Aku lebih mengetahui apa yang kamu sembunyikan dan apa yang kamu nyatakan. Dan barangsiapa di antara kamu yang melakukannya, maka sesungguhnya dia telah tersesat dari jalan yang lurus.'),
(5152, '060:002', '68', 60, 2, 'Jika mereka menangkap kamu, niscaya mereka bertindak sebagai musuh bagimu dan melepaskan tangan dan lidah mereka kepadamu dengan menyakiti(mu); dan mereka ingin supaya kamu (kembali) kafir.'),
(5153, '060:003', '68', 60, 3, 'Karib kerabat dan anak-anakmu sekali-sekali tiada bermanfaat bagimu pada Hari Kiamat. Dia akan memisahkan antara kamu. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
(5154, '060:004', '68', 60, 4, 'Sesungguhnya telah ada suri tauladan yang baik bagimu pada Ibrahim dan orang-orang yang bersama dengan dia; ketika mereka berkata kepada kaum mereka: \"Sesungguhnya kami berlepas diri daripada kamu dari daripada apa yang kamu sembah selain Allah, kami ingkari (kekafiran)mu dan telah nyata antara kami dan kamu permusuhan dan kebencian buat selama-lamanya sampai kamu beriman kepada Allah saja. Kecuali perkataan Ibrahim kepada bapaknya: \"Sesungguhnya aku akan memohonkan ampunan bagi kamu dan aku tiada dapat menolak sesuatupun dari kamu (siksaan) Allah\". (Ibrahim berkata): \"Ya Tuhan kami hanya kepada Engkaulah kami bertawakkal dan hanya kepada Engkaulah kami bertaubat dan hanya kepada Engkaulah kami kembali\".'),
(5155, '060:005', '68', 60, 5, '\"Ya Tuhan kami, janganlah Engkau jadikan kami (sasaran) fitnah bagi orang-orang kafir. Dan ampunilah kami ya Tuhan kami. Sesungguhnya Engkaulah Yang Maha Perkasa lagi Maha Bijaksana\".'),
(5156, '060:006', '68', 60, 6, 'Sesungguhnya pada mereka itu (Ibrahim dan umatnya) ada teladan yang baik bagimu; (yaitu) bagi orang-orang yang mengharap (pahala) Allah dan (keselamatan pada) Hari Kemudian. Dan barangsiapa yang berpaling, maka sesungguhnya Allah Dialah yang Maha kaya lagi Maha Terpuji.'),
(5157, '060:007', '68', 60, 7, 'Mudah-mudahan Allah menimbulkan kasih sayang antaramu dengan orang-orang yang kamu musuhi di antara mereka. Dan Allah adalah Maha Kuasa. Dan Allah Maha Pengampun lagi Maha Penyayang.'),
(5158, '060:008', '68', 60, 8, 'Allah tidak melarang kamu untuk berbuat baik dan berlaku adil terhadap orang-orang yang tiada memerangimu karena agama dan tidak (pula) mengusir kamu dari negerimu. Sesungguhnya Allah menyukai orang-orang yang berlaku adil.'),
(5159, '060:009', '68', 60, 9, 'Sesungguhnya Allah hanya melarang kamu menjadikan sebagai kawanmu orang-orang yang memerangimu karena agama dan mengusir kamu dari negerimu, dan membantu (orang lain) untuk mengusirmu. Dan barangsiapa menjadikan mereka sebagai kawan, maka mereka itulah orang-orang yang zalim.'),
(5160, '060:010', '68', 60, 10, 'Hai orang-orang yang beriman, apabila datang berhijrah kepadamu perempuan-perempuan yang beriman, maka hendaklah kamu uji (keimanan) mereka. Allah lebih mengetahui tentang keimanan mereka;maka jika kamu telah mengetahui bahwa mereka (benar-benar) beriman maka janganlah kamu kembalikan mereka kepada (suami-suami mereka) orang-orang kafir. Mereka tiada halal bagi orang-orang kafir itu dan orang-orang kafir itu tiada halal pula bagi mereka. Dan berikanlah kepada (suami suami) mereka, mahar yang telah mereka bayar. Dan tiada dosa atasmu mengawini mereka apabila kamu bayar kepada mereka maharnya. Dan janganlah kamu tetap berpegang pada tali (perkawinan) dengan perempuan-perempuan kafir; dan hendaklah kamu minta mahar yang telah kamu bayar; dan hendaklah mereka meminta mahar yang telah mereka bayar. Demikianlah hukum Allah yang ditetapkan-Nya di antara kamu. Dan Allah Maha Mengetahui lagi Maha Bijaksana.'),
(5161, '060:011', '68', 60, 11, 'Dan jika seseorang dari isteri-isterimu lari kepada orang-orang kafir, lalu kamu mengalahkan mereka maka bayarkanlah kepada orang-orang yang lari isterinya itu mahar sebanyak yang telah mereka bayar. Dan bertakwalah kepada Allah Yang kepada-Nya kamu beriman.'),
(5162, '060:012', '68', 60, 12, 'Hai Nabi, apabila datang kepadamu perempuan-perempuan yang beriman untuk mengadakan janji setia, bahwa mereka tiada akan menyekutukan Allah, tidak akan mencuri, tidak akan berzina, tidak akan membunuh anak-anaknya, tidak akan berbuat dusta yang mereka ada-adakan antara tangan dan kaki mereka dan tidak akan mendurhakaimu dalam urusan yang baik, maka terimalah janji setia mereka dan mohonkanlah ampunan kepada Allah untuk mereka. Sesungguhnya Allah maha Pengampun lagi Maha Penyayang.'),
(5163, '060:013', '68', 60, 13, 'Hai orang-orang yang beriman, janganlah kamu jadikan penolongmu kaum yang dimurkai Allah. Sesungguhnya mereka telah putus asa terhadap negeri akhirat sebagaimana orang-orang kafir yang telah berada dalam kubur berputus asa.'),
(5164, '061:001', '68', 61, 1, 'Telah bertasbih kepada Allah apa saja yang ada di langit dan apa saja yang ada di bumi; dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(5165, '061:002', '68', 61, 2, 'Wahai orang-orang yang beriman, kenapakah kamu mengatakan sesuatu yang tidak kamu kerjakan?'),
(5166, '061:003', '68', 61, 3, 'Amat besar kebencian di sisi Allah bahwa kamu mengatakan apa-apa yang tidak kamu kerjakan.'),
(5167, '061:004', '68', 61, 4, 'Sesungguhnya Allah menyukai orang yang berperang dijalan-Nya dalam barisan yang teratur seakan-akan mereka seperti suatu bangunan yang tersusun kokoh.'),
(5168, '061:005', '68', 61, 5, 'Dan (ingatlah) ketika Musa berkata kepada kaumnya: \"Hai kaumku, mengapa kamu menyakitiku, sedangkan kamu mengetahui bahwa sesungguhnya aku adalah utusan Allah kepadamu?\" Maka tatkala mereka berpaling (dari kebenaran), Allah memalingkan hati mereka; dan Allah tidak memberi petunjuk kepada kaum yang fasik.'),
(5169, '061:006', '68', 61, 6, 'Dan (ingatlah) ketika Isa ibnu Maryam berkata: \"Hai Bani Israil, sesungguhnya aku adalah utusan Allah kepadamu, membenarkan kitab sebelumku, yaitu Taurat, dan memberi khabar gembira dengan (datangnya) seorang Rasul yang akan datang sesudahku, yang namanya Ahmad (Muhammad)\". Maka tatkala rasul itu datang kepada mereka dengan membawa bukti-bukti yang nyata, mereka berkata: \"Ini adalah sihir yang nyata\".'),
(5170, '061:007', '68', 61, 7, 'Dan siapakah yang lebih zalim daripada orang yang mengada-adakan dusta terhadap Allah sedang dia diajak kepada Islam? Dan Allah tidak memberi petunjuk kepada orang-orang zalim.'),
(5171, '061:008', '68', 61, 8, 'Mereka ingin memadamkan cahaya Allah dengan mulut (tipu daya) mereka, tetapi Allah (justru) menyempurnakan cahaya-Nya, walau orang-orang kafir membencinya\".'),
(5172, '061:009', '68', 61, 9, 'Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang benar agar Dia memenangkannya di atas segala agama-agama meskipun orang musyrik membenci.'),
(5173, '061:010', '68', 61, 10, 'Hai orang-orang yang beriman, sukakah kamu aku tunjukkan suatu perniagaan yang dapat menyelamatkanmu dari azab yang pedih?'),
(5174, '061:011', '68', 61, 11, '(yaitu) kamu beriman kepada Allah dan Rasul-Nya dan berjihad di jalan Allah dengan harta dan jiwamu. Itulah yang lebih baik bagimu, jika kamu mengetahui.'),
(5175, '061:012', '68', 61, 12, 'Niscaya Allah akan mengampuni dosa-dosamu dan memasukkanmu ke dalam jannah yang mengalir di bawahnya sungai-sungai; dan (memasukkan kamu) ke tempat tinggal yang baik di dalam jannah ´Adn. Itulah keberuntungan yang besar.'),
(5176, '061:013', '68', 61, 13, 'Dan (ada lagi) karunia yang lain yang kamu sukai (yaitu) pertolongan dari Allah dan kemenangan yang dekat (waktunya). Dan sampaikanlah berita gembira kepada orang-orang yang beriman.'),
(5177, '061:014', '68', 61, 14, 'Hai orang-orang yang beriman, jadilah kamu penolong (agama) Allah sebagaimana Isa ibnu Maryam telah berkata kepada pengikut-pengikutnya yang setia: \"Siapakah yang akan menjadi penolong-penolongku (untuk menegakkan agama) Allah?\" Pengikut-pengikut yang setia itu berkata: \"Kamilah penolong-penolong agama Allah\", lalu segolongan dari Bani Israil beriman dan segolongan lain kafir; maka Kami berikan kekuatan kepada orang-orang yang beriman terhadap musuh-musuh mereka, lalu mereka menjadi orang-orang yang menang.'),
(5178, '062:001', '68', 62, 1, 'Senantiasa bertasbih kepada Allah apa yang ada di langit dan apa yang ada di bumi. Raja, Yang Maha Suci, Yang Maha Perkasa lagi Maha Bijaksana.'),
(5179, '062:002', '68', 62, 2, 'Dialah yang mengutus kepada kaum yang buta huruf seorang Rasul di antara mereka, yang membacakan ayat-ayat-Nya kepada mereka, mensucikan mereka dan mengajarkan mereka Kitab dan Hikmah (As Sunnah). Dan sesungguhnya mereka sebelumnya benar-benar dalam kesesatan yang nyata,'),
(5180, '062:003', '68', 62, 3, 'dan (juga) kepada kaum yang lain dari mereka yang belum berhubungan dengan mereka. Dan Dialah Yang Maha Perkasa lagi Maha Bijaksana.'),
(5181, '062:004', '68', 62, 4, 'Demikianlah karunia Allah, diberikan-Nya kepada siapa yang dikehendaki-Nya; dan Allah mempunyai karunia yang besar.'),
(5182, '062:005', '68', 62, 5, 'Perumpamaan orang-orang yang dipikulkan kepadanya Taurat, kemudian mereka tiada memikulnya adalah seperti keledai yang membawa kitab-kitab yang tebal. Amatlah buruknya perumpamaan kaum yang mendustakan ayat-ayat Allah itu. Dan Allah tiada memberi petunjuk kepada kaum yang zalim.'),
(5183, '062:006', '68', 62, 6, 'Katakanlah: \"Hai orang-orang yang menganut agama Yahudi, jika kamu mendakwakan bahwa sesungguhnya kamu sajalah kekasih Allah bukan manusia-manusia yang lain, maka harapkanlah kematianmu, jika kamu adalah orang-orang yang benar\".'),
(5184, '062:007', '68', 62, 7, 'Mereka tiada akan mengharapkan kematian itu selama-lamanya disebabkan kejahatan yang telah mereka perbuat dengan tangan mereka sendiri. Dan Allah Maha Mengetahui akan orang-orang yang zalim.'),
(5185, '062:008', '68', 62, 8, 'Katakanlah: \"Sesungguhnya kematian yang kamu lari daripadanya, maka sesungguhnya kematian itu akan menemui kamu, kemudian kamu akan dikembalikan kepada (Allah), yang mengetahui yang ghaib dan yang nyata, lalu Dia beritakan kepadamu apa yang telah kamu kerjakan\".'),
(5186, '062:009', '68', 62, 9, 'Hai orang-orang beriman, apabila diseru untuk menunaikan shalat Jum´at, maka bersegeralah kamu kepada mengingat Allah dan tinggalkanlah jual beli. Yang demikian itu lebih baik bagimu jika kamu mengetahui.'),
(5187, '062:010', '68', 62, 10, 'Apabila telah ditunaikan shalat, maka bertebaranlah kamu di muka bumi; dan carilah karunia Allah dan ingatlah Allah banyak-banyak supaya kamu beruntung.'),
(5188, '062:011', '68', 62, 11, 'Dan apabila mereka melihat perniagaan atau permainan, mereka bubar untuk menuju kepadanya dan mereka tinggalkan kamu sedang berdiri (berkhotbah). Katakanlah: \"Apa yang di sisi Allah lebih baik daripada permainan dan perniagaan\", dan Allah Sebaik-baik Pemberi rezeki.'),
(5189, '063:001', '68', 63, 1, 'Apabila orang-orang munafik datang kepadamu, mereka berkata: \"Kami mengakui, bahwa sesungguhnya kamu benar-benar Rasul Allah\". Dan Allah mengetahui bahwa sesungguhnya kamu benar-benar Rasul-Nya; dan Allah mengetahui bahwa sesungguhnya orang-orang munafik itu benar-benar orang pendusta.'),
(5190, '063:002', '68', 63, 2, 'Mereka itu menjadikan sumpah mereka sebagai perisai, lalu mereka menghalangi (manusia) dari jalan Allah. Sesungguhnya amat buruklah apa yang telah mereka kerjakan.'),
(5191, '063:003', '68', 63, 3, 'Yang demikian itu adalah karena bahwa sesungguhnya mereka telah beriman, kemudian menjadi kafir (lagi) lalu hati mereka dikunci mati; karena itu mereka tidak dapat mengerti.'),
(5192, '063:004', '68', 63, 4, 'Dan apabila kamu melihat mereka, tubuh-tubuh mereka menjadikan kamu kagum. Dan jika mereka berkata kamu mendengarkan perkataan mereka. Mereka adalah seakan-akan kayu yang tersandar. Mereka mengira bahwa tiap-tiap teriakan yang keras ditujukan kepada mereka. Mereka itulah musuh (yang sebenarnya) maka waspadalah terhadap mereka; semoga Allah membinasakan mereka. Bagaimanakah mereka sampai dipalingkan (dari kebenaran)?'),
(5193, '063:005', '68', 63, 5, 'Dan apabila dikatakan kepada mereka: Marilah (beriman), agar Rasulullah memintakan ampunan bagimu, mereka membuang muka mereka dan kamu lihat mereka berpaling sedang mereka menyombongkan diri.'),
(5194, '063:006', '68', 63, 6, 'Sama saja bagi mereka, kamu mintakan ampunan atau tidak kamu mintakan ampunan bagi mereka. Sesungguhnya Allah tidak memberi petunjuk kepada orang-orang yang fasik.'),
(5195, '063:007', '68', 63, 7, 'Mereka orang-orang yang mengatakan (kepada orang-orang Anshar): \"Janganlah kamu memberikan perbelanjaan kepada orang-orang (Muhajirin) yang ada disisi Rasulullah supaya mereka bubar (meninggalkan Rasulullah)\". Padahal kepunyaan Allah-lah perbendaharaan langit dan bumi, tetapi orang-orang munafik itu tidak memahami.'),
(5196, '063:008', '68', 63, 8, 'Mereka berkata: \"Sesungguhnya jika kita telah kembali ke Madinah, benar-benar orang yang kuat akan mengusir orang-orang yang lemah dari padanya\". Padahal kekuatan itu hanyalah bagi Allah, bagi Rasul-Nya dan bagi orang-orang mukmin, tetapi orang-orang munafik itu tiada mengetahui.'),
(5197, '063:009', '68', 63, 9, 'Hai orang-orang beriman, janganlah hartamu dan anak-anakmu melalaikan kamu dari mengingat Allah. Barangsiapa yang berbuat demikian maka mereka itulah orang-orang yang merugi.'),
(5198, '063:010', '68', 63, 10, 'Dan belanjakanlah sebagian dari apa yang telah Kami berikan kepadamu sebelum datang kematian kepada salah seorang di antara kamu; lalu ia berkata: \"Ya Rabb-ku, mengapa Engkau tidak menangguhkan (kematian)ku sampai waktu yang dekat, yang menyebabkan aku dapat bersedekah dan aku termasuk orang-orang yang saleh?\"'),
(5199, '063:011', '68', 63, 11, 'Dan Allah sekali-kali tidak akan menangguhkan (kematian) seseorang apabila telah datang waktu kematiannya. Dan Allah Maha Mengenal apa yang kamu kerjakan.'),
(5200, '064:001', '68', 64, 1, 'Bertasbih kepada Allah apa yang ada di langit dan apa yang ada di bumi; hanya Allah lah yang mempunyai semua kerajaan dan semua pujian, dan Dia Maha Kuasa atas segala sesuatu.'),
(5201, '064:002', '68', 64, 2, 'Dialah yang menciptakan kamu maka di antara kamu ada yang kafir dan di antaramu ada yang mukmin. Dan Allah Maha Melihat apa yang kamu kerjakan.'),
(5202, '064:003', '68', 64, 3, 'Dia menciptakan langit dan bumi dengan haq. Dia membentuk rupamu dan dibaguskan-Nya rupamu itu dan hanya kepada Allah-lah kembali(mu).'),
(5203, '064:004', '68', 64, 4, 'Dia mengetahui apa yang ada di langit dan di bumi dan mengetahui apa yang kamu rahasiakan dan yang kamu nyatakan. Dan Allah Maha Mengetahui segala isi hati.'),
(5204, '064:005', '68', 64, 5, 'Apakah belum datang kepadamu (hai orang-orang kafir) berita orang-orang kafir terdahulu. Maka mereka telah merasakan akibat yang buruk dari perbuatan mereka dan mereka memperoleh azab yang pedih.'),
(5205, '064:006', '68', 64, 6, 'Yang demikian itu adalah karena sesungguhnya telah datang kepada mereka Rasul-Rasul mereka membawa keterangan-keterangan lalu mereka berkata: \"Apakah manusia yang akan memberi petunjuk kepada kami?\" lalu mereka ingkar dan berpaling; dan Allah tidak memerlukan (mereka). Dan Allah Maha Kaya lagi Maha Terpuji.'),
(5206, '064:007', '68', 64, 7, 'Orang-orang yang kafir mengatakan bahwa mereka sekali-kali tidak akan dibangkitkan. Katakanlah: \"Memang, demi Tuhanku, benar-benar kamu akan dibangkitkan, kemudian akan diberitakan kepadamu apa yang telah kamu kerjakan\". Yang demikian itu adalah mudah bagi Allah.'),
(5207, '064:008', '68', 64, 8, 'Maka berimanlah kamu kepada Allah dan Rasul-Nya dan kepada cahaya (Al-Quran) yang telah Kami turunkan. Dan Allah Maha Mengetahui apa yang kamu kerjakan.'),
(5208, '064:009', '68', 64, 9, '(Ingatlah) hari (dimana) Allah mengumpulkan kamu pada hari pengumpulan, itulah hari dinampakkan kesalahan-kesalahan. Dan barangsiapa yang beriman kepada Allah dan beramal saleh, niscaya Allah akan menutupi kesalahan-kesalahannya dan memasukkannya ke dalam jannah yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah keberuntungan yang besar.'),
(5209, '064:010', '68', 64, 10, 'Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni-penghuni neraka, mereka kekal di dalamnya. Dan itulah seburuk-buruk tempat kembali.'),
(5210, '064:011', '68', 64, 11, 'Tidak ada suatu musibah pun yang menimpa seseorang kecuali dengan ijin Allah; dan barangsiapa yang beriman kepada Allah niscaya Dia akan memberi petunjuk kepada hatinya. Dan Allah Maha Mengetahui segala sesuatu.'),
(5211, '064:012', '68', 64, 12, 'Dan taatlah kepada Allah dan taatlah kepada Rasul-Nya, jika kamu berpaling sesungguhnya kewajiban Rasul Kami hanyalah menyampaikan (amanat Allah) dengan terang.'),
(5212, '064:013', '68', 64, 13, '(Dialah) Allah tidak ada Tuhan selain Dia. Dan hendaklah orang-orang mukmin bertawakkal kepada Allah saja.'),
(5213, '064:014', '68', 64, 14, 'Hai orang-orang mukmin, sesungguhnya di antara isteri-isterimu dan anak-anakmu ada yang menjadi musuh bagimu maka berhati-hatilah kamu terhadap mereka dan jika kamu memaafkan dan tidak memarahi serta mengampuni (mereka) maka sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(5214, '064:015', '68', 64, 15, 'Sesungguhnya hartamu dan anak-anakmu hanyalah cobaan (bagimu), dan di sisi Allah-lah pahala yang besar.'),
(5215, '064:016', '68', 64, 16, 'Maka bertakwalah kamu kepada Allah menurut kesanggupanmu dan dengarlah serta taatlah dan nafkahkanlah nafkah yang baik untuk dirimu. Dan barangsiapa yang dipelihara dari kekikiran dirinya, maka mereka itulah orang-orang yang beruntung.'),
(5216, '064:017', '68', 64, 17, 'Jika kamu meminjamkan kepada Allah pinjaman yang baik, niscaya Allah melipat gandakan balasannya kepadamu dan mengampuni kamu. Dan Allah Maha Pembalas Jasa lagi Maha Penyantun.'),
(5217, '064:018', '68', 64, 18, 'Yang mengetahui yang ghaib dan yang nyata. Yang Maha Perkasa lagi Maha Bijaksana.'),
(5218, '065:001', '68', 65, 1, 'Hai Nabi, apabila kamu menceraikan isteri-isterimu maka hendaklah kamu ceraikan mereka pada waktu mereka dapat (menghadapi) iddahnya (yang wajar) dan hitunglah waktu iddah itu serta bertakwalah kepada Allah Tuhanmu. Janganlah kamu keluarkan mereka dari rumah mereka dan janganlah mereka (diizinkan) ke luar kecuali mereka mengerjakan perbuatan keji yang terang. Itulah hukum-hukum Allah dan barangsiapa yang melanggar hukum-hukum Allah, maka sesungguhnya dia telah berbuat zalim terhadap dirinya sendiri. Kamu tidak mengetahui barangkali Allah mengadakan sesudah itu sesuatu hal yang baru.'),
(5219, '065:002', '68', 65, 2, 'Apabila mereka telah mendekati akhir iddahnya, maka rujukilah mereka dengan baik atau lepaskanlah mereka dengan baik dan persaksikanlah dengan dua orang saksi yang adil di antara kamu dan hendaklah kamu tegakkan kesaksian itu karena Allah. Demikianlah diberi pengajaran dengan itu orang yang beriman kepada Allah dan hari akhirat. Barangsiapa bertakwa kepada Allah niscaya Dia akan mengadakan baginya jalan keluar.'),
(5220, '065:003', '68', 65, 3, 'Dan memberinya rezeki dari arah yang tiada disangka-sangkanya. Dan barangsiapa yang bertawakkal kepada Allah niscaya Allah akan mencukupkan (keperluan)nya. Sesungguhnya Allah melaksanakan urusan yang (dikehendaki)Nya. Sesungguhnya Allah telah mengadakan ketentuan bagi tiap-tiap sesuatu.'),
(5221, '065:004', '68', 65, 4, 'Dan perempuan-perempuan yang tidak haid lagi (monopause) di antara perempuan-perempuanmu jika kamu ragu-ragu (tentang masa iddahnya), maka masa iddah mereka adalah tiga bulan; dan begitu (pula) perempuan-perempuan yang tidak haid. Dan perempuan-perempuan yang hamil, waktu iddah mereka itu ialah sampai mereka melahirkan kandungannya. Dan barang -siapa yang bertakwa kepada Allah, niscaya Allah menjadikan baginya kemudahan dalam urusannya.'),
(5222, '065:005', '68', 65, 5, 'Itulah perintah Allah yang diturunkan-Nya kepada kamu, dan barangsiapa yang bertakwa kepada Allah, niscaya Dia akan menghapus kesalahan-kesalahannya dan akan melipat gandakan pahala baginya.'),
(5223, '065:006', '68', 65, 6, 'Tempatkanlah mereka (para isteri) di mana kamu bertempat tinggal menurut kemampuanmu dan janganlah kamu menyusahkan mereka untuk menyempitkan (hati) mereka. Dan jika mereka (isteri-isteri yang sudah ditalaq) itu sedang hamil, maka berikanlah kepada mereka nafkahnya hingga mereka bersalin, kemudian jika mereka menyusukan (anak-anak)mu untukmu maka berikanlah kepada mereka upahnya, dan musyawarahkanlah di antara kamu (segala sesuatu) dengan baik; dan jika kamu menemui kesulitan maka perempuan lain boleh menyusukan (anak itu) untuknya.'),
(5224, '065:007', '68', 65, 7, 'Hendaklah orang yang mampu memberi nafkah menurut kemampuannya. Dan orang yang disempitkan rezekinya hendaklah memberi nafkah dari harta yang diberikan Allah kepadanya. Allah tidak memikulkan beban kepada seseorang melainkan sekedar apa yang Allah berikan kepadanya. Allah kelak akan memberikan kelapangan sesudah kesempitan.'),
(5225, '065:008', '68', 65, 8, 'Dan berapalah banyaknya (penduduk) negeri yang mendurhakai perintah Tuhan mereka dan Rasul-rasul-Nya, maka Kami hisab penduduk negeri itu dengan hisab yang keras, dan Kami azab mereka dengan azab yang mengerikan.'),
(5226, '065:009', '68', 65, 9, 'Maka mereka merasakan akibat yang buruk dari perbuatannya, dan adalah akibat perbuatan mereka kerugian yang besar.'),
(5227, '065:010', '68', 65, 10, 'Allah menyediakan bagi mereka azab yang keras, maka bertakwalah kepada Allah hai orang-orang yang mempunyai akal; (yaitu) orang-orang yang beriman. Sesungguhnya Allah telah menurunkan peringatan kepadamu,'),
(5228, '065:011', '68', 65, 11, '(Dan mengutus) seorang Rasul yang membacakan kepadamu ayat-ayat Allah yang menerangkan (bermacam-macam hukum) supaya Dia mengeluarkan orang-orang yang beriman dan beramal saleh dari kegelapan kepada cahaya. Dan barangsiapa beriman kepada Allah dan mengerjakan amal yang saleh niscaya Allah akan memasukkannya ke dalam surga-surga yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Sesungguhnya Allah memberikan rezeki yang baik kepadanya.'),
(5229, '065:012', '68', 65, 12, 'Allah-lah yang menciptakan tujuh langit dan seperti itu pula bumi. Perintah Allah berlaku padanya, agar kamu mengetahui bahwasanya Allah Maha Kuasa atas segala sesuatu, dan sesungguhnya Allah ilmu-Nya benar-benar meliputi segala sesuatu.'),
(5230, '066:001', '68', 66, 1, 'Hai Nabi, mengapa kamu mengharamkan apa yang Allah halalkan bagimu; kamu mencari kesenangan hati isteri-isterimu? Dan Allah Maha Pengampun lagi Maha Penyayang'),
(5231, '066:002', '68', 66, 2, 'Sesungguhnya Allah telah mewajibkan kepadamu sekalian membebaskan diri dari sumpahmu dan Allah adalah Pelindungmu dan Dia Maha Mengetahui lagi Maha Bijaksana.'),
(5232, '066:003', '68', 66, 3, 'Dan ingatlah ketika Nabi membicarakan secara rahasia kepada salah seorang isterinya (Hafsah) suatu peristiwa. Maka tatkala (Hafsah) menceritakan peristiwa itu (kepada Aisyah) dan Allah memberitahukan hal itu (pembicaraan Hafsah dan Aisyah) kepada Muhammad lalu Muhammad memberitahukan sebagian (yang diberitakan Allah kepadanya) dan menyembunyikan sebagian yang lain (kepada Hafsah). Maka tatkala (Muhammad) memberitahukan pembicaraan (antara Hafsah dan Aisyah) lalu (Hafsah) bertanya: \"Siapakah yang telah memberitahukan hal ini kepadamu?\" Nabi menjawab: \"Telah diberitahukan kepadaku oleh Allah yang Maha Mengetahui lagi Maha Mengenal\".'),
(5233, '066:004', '68', 66, 4, 'Jika kamu berdua bertaubat kepada Allah, maka sesungguhnya hati kamu berdua telah condong (untuk menerima kebaikan); dan jika kamu berdua bantu-membantu menyusahkan Nabi, maka sesungguhnya Allah adalah Pelindungnya dan (begitu pula) Jibril dan orang-orang mukmin yang baik; dan selain dari itu malaikat-malaikat adalah penolongnya pula.'),
(5234, '066:005', '68', 66, 5, 'Jika Nabi menceraikan kamu, boleh jadi Tuhannya akan memberi ganti kepadanya dengan isteri yang lebih baik daripada kamu, yang patuh, yang beriman, yang taat, yang bertaubat, yang mengerjakan ibadat, yang berpuasa, yang janda dan yang perawan.'),
(5235, '066:006', '68', 66, 6, 'Hai orang-orang yang beriman, peliharalah dirimu dan keluargamu dari api neraka yang bahan bakarnya adalah manusia dan batu; penjaganya malaikat-malaikat yang kasar, keras, dan tidak mendurhakai Allah terhadap apa yang diperintahkan-Nya kepada mereka dan selalu mengerjakan apa yang diperintahkan.'),
(5236, '066:007', '68', 66, 7, 'Hai orang-orang kafir, janganlah kamu mengemukakan uzur pada hari ini. Sesungguhnya kamu hanya diberi balasan menurut apa yang kamu kerjakan.'),
(5237, '066:008', '68', 66, 8, 'Hai orang-orang yang beriman, bertaubatlah kepada Allah dengan taubatan nasuhaa (taubat yang semurni-murninya). Mudah-mudahan Rabbmu akan menutupi kesalahan-kesalahanmu dan memasukkanmu ke dalam jannah yang mengalir di bawahnya sungai-sungai, pada hari ketika Allah tidak menghinakan Nabi dan orang-orang mukmin yang bersama dia; sedang cahaya mereka memancar di hadapan dan di sebelah kanan mereka, sambil mereka mengatakan: \"Ya Rabb kami, sempurnakanlah bagi kami cahaya kami dan ampunilah kami; Sesungguhnya Engkau Maha Kuasa atas segala sesuatu\".'),
(5238, '066:009', '68', 66, 9, 'Hai Nabi, perangilah orang-orang kafir dan orang-orang munafik dan bersikap keraslah terhadap mereka. Tempat mereka adalah jahannam dan itu adalah seburuk-buruknya tempat kembali.'),
(5239, '066:010', '68', 66, 10, 'Allah membuat isteri Nuh dan isteri Luth sebagai perumpamaan bagi orang-orang kafir. Keduanya berada di bawah pengawasan dua orang hamba yang saleh di antara hamba-hamba Kami; lalu kedua isteri itu berkhianat kepada suaminya (masing-masing), maka suaminya itu tiada dapat membantu mereka sedikitpun dari (siksa) Allah; dan dikatakan (kepada keduanya): \"Masuklah ke dalam jahannam bersama orang-orang yang masuk (jahannam)\".'),
(5240, '066:011', '68', 66, 11, 'Dan Allah membuat isteri Fir´aun perumpamaan bagi orang-orang yang beriman, ketika ia berkata: \"Ya Rabbku, bangunkanlah untukku sebuah rumah di sisi-Mu dalam firdaus, dan selamatkanlah aku dari Fir´aun dan perbuatannya, dan selamatkanlah aku dari kaum yang zhalim.'),
(5241, '066:012', '68', 66, 12, 'dan (ingatlah) Maryam binti Imran yang memelihara kehormatannya, maka Kami tiupkan ke dalam rahimnya sebagian dari ruh (ciptaan) Kami, dan dia membenarkan kalimat Rabbnya dan Kitab-Kitab-Nya, dan dia adalah termasuk orang-orang yang taat.'),
(5242, '067:001', '68', 67, 1, 'Maha Suci Allah Yang di tangan-Nya-lah segala kerajaan, dan Dia Maha Kuasa atas segala sesuatu,'),
(5243, '067:002', '68', 67, 2, 'Yang menjadikan mati dan hidup, supaya Dia menguji kamu, siapa di antara kamu yang lebih baik amalnya. Dan Dia Maha Perkasa lagi Maha Pengampun,'),
(5244, '067:003', '68', 67, 3, 'Yang telah menciptakan tujuh langit berlapis-lapis. Kamu sekali-kali tidak melihat pada ciptaan Tuhan Yang Maha Pemurah sesuatu yang tidak seimbang. Maka lihatlah berulang-ulang, adakah kamu lihat sesuatu yang tidak seimbang?'),
(5245, '067:004', '68', 67, 4, 'Kemudian pandanglah sekali lagi niscaya penglihatanmu akan kembali kepadamu dengan tidak menemukan sesuatu cacat dan penglihatanmu itupun dalam keadaan payah.'),
(5246, '067:005', '68', 67, 5, 'Sesungguhnya Kami telah menghiasi langit yang dekat dengan bintang-bintang, dan Kami jadikan bintang-bintang itu alat-alat pelempar syaitan, dan Kami sediakan bagi mereka siksa neraka yang menyala-nyala.'),
(5247, '067:006', '68', 67, 6, 'Dan orang-orang yang kafir kepada Tuhannya, memperoleh azab Jahannam. Dan itulah seburuk-buruk tempat kembali.'),
(5248, '067:007', '68', 67, 7, 'Apabila mereka dilemparkan ke dalamnya mereka mendengar suara neraka yang mengerikan, sedang neraka itu menggelegak,'),
(5249, '067:008', '68', 67, 8, 'hampir-hampir (neraka) itu terpecah-pecah lantaran marah. Setiap kali dilemparkan ke dalamnya sekumpulan (orang-orang kafir), penjaga-penjaga (neraka itu) bertanya kepada mereka: \"Apakah belum pernah datang kepada kamu (di dunia) seorang pemberi peringatan?\"'),
(5250, '067:009', '68', 67, 9, 'Mereka menjawab: \"Benar ada\", sesungguhnya telah datang kepada kami seorang pemberi peringatan, maka kami mendustakan(nya) dan kami katakan: \"Allah tidak menurunkan sesuatupun; kamu tidak lain hanyalah di dalam kesesatan yang besar\".'),
(5251, '067:010', '68', 67, 10, 'Dan mereka berkata: \"Sekiranya kami mendengarkan atau memikirkan (peringatan itu) niscaya tidaklah kami termasuk penghuni-penghuni neraka yang menyala-nyala\".'),
(5252, '067:011', '68', 67, 11, 'Mereka mengakui dosa mereka. Maka kebinasaanlah bagi penghuni-penghuni neraka yang menyala-nyala.'),
(5253, '067:012', '68', 67, 12, 'Sesungguhnya orang-orang yang takut kepada Tuhannya Yang tidak nampak oleh mereka, mereka akan memperoleh ampunan dan pahala yang besar.'),
(5254, '067:013', '68', 67, 13, 'Dan rahasiakanlah perkataanmu atau lahirkanlah; sesungguhnya Dia Maha Mengetahui segala isi hati.'),
(5255, '067:014', '68', 67, 14, 'Apakah Allah Yang menciptakan itu tidak mengetahui (yang kamu lahirkan atau rahasiakan); dan Dia Maha Halus lagi Maha Mengetahui?'),
(5256, '067:015', '68', 67, 15, 'Dialah Yang menjadikan bumi itu mudah bagi kamu, maka berjalanlah di segala penjurunya dan makanlah sebahagian dari rezeki-Nya. Dan hanya kepada-Nya-lah kamu (kembali setelah) dibangkitkan.'),
(5257, '067:016', '68', 67, 16, 'Apakah kamu merasa aman terhadap Allah yang (berkuasa) di langit bahwa Dia akan menjungkir balikkan bumi bersama kamu, sehingga dengan tiba-tiba bumi itu bergoncang?,'),
(5258, '067:017', '68', 67, 17, 'atau apakah kamu merasa aman terhadap Allah yang (berkuasa) di langit bahwa Dia akan mengirimkan badai yang berbatu. Maka kelak kamu akan mengetahui bagaimana (akibat mendustakan) peringatan-Ku?'),
(5259, '067:018', '68', 67, 18, 'Dan sesungguhnya orang-orang yang sebelum mereka telah mendustakan (rasul-rasul-Nya). Maka alangkah hebatnya kemurkaan-Ku.'),
(5260, '067:019', '68', 67, 19, 'Dan apakah mereka tidak memperhatikan burung-burung yang mengembangkan dan mengatupkan sayapnya di atas mereka? Tidak ada yang menahannya (di udara) selain Yang Maha Pemurah. Sesungguhnya Dia Maha Melihat segala sesuatu.'),
(5261, '067:020', '68', 67, 20, 'Atau siapakah dia yang menjadi tentara bagimu yang akan menolongmu selain daripada Allah Yang Maha Pemurah? Orang-orang kafir itu tidak lain hanyalah dalam (keadaan) tertipu.'),
(5262, '067:021', '68', 67, 21, 'Atau siapakah dia yang memberi kamu rezeki jika Allah menahan rezeki-Nya? Sebenarnya mereka terus menerus dalam kesombongan dan menjauhkan diri?'),
(5263, '067:022', '68', 67, 22, 'Maka apakah orang yang berjalan terjungkal di atas mukanya itu lebih banyak mendapatkan petunjuk ataukah orang yang berjalan tegap di atas jalan yang lurus?'),
(5264, '067:023', '68', 67, 23, 'Katakanlah: \"Dialah Yang menciptakan kamu dan menjadikan bagi kamu pendengaran, penglihatan dan hati\". (Tetapi) amat sedikit kamu bersyukur.'),
(5265, '067:024', '68', 67, 24, 'Katakanlah: \"Dialah Yang menjadikan kamu berkembang biak di muka bumi, dan hanya kepada-Nya-lah kamu kelak dikumpulkan\".'),
(5266, '067:025', '68', 67, 25, 'Dan mereka berkata: \"Kapankah datangnya ancaman itu jika kamu adalah orang-orang yang benar?\"'),
(5267, '067:026', '68', 67, 26, 'Katakanlah: \"Sesungguhnya ilmu (tentang hari kiamat itu) hanya pada sisi Allah. Dan sesungguhnya aku hanyalah seorang pemberi peringatan yang menjelaskan\".'),
(5268, '067:027', '68', 67, 27, 'Ketika mereka melihat azab (pada hari kiamat) sudah dekat, muka orang-orang kafir itu menjadi muram. Dan dikatakan (kepada mereka) inilah (azab) yang dahulunya kamu selalu meminta-mintanya.'),
(5269, '067:028', '68', 67, 28, 'Katakanlah: \"Terangkanlah kepadaku jika Allah mematikan aku dan orang-orang yang bersama dengan aku atau memberi rahmat kepada kami, (maka kami akan masuk surga), tetapi siapakah yang dapat melindungi orang-orang yang kafir dari siksa yang pedih?\"'),
(5270, '067:029', '68', 67, 29, 'Katakanlah: \"Dialah Allah Yang Maha Penyayang kami beriman kepada-Nya dan kepada-Nya-lah kami bertawakkal. Kelak kamu akan mengetahui siapakah yang berada dalam kesesatan yang nyata\".'),
(5271, '067:030', '68', 67, 30, 'Katakanlah: \"Terangkanlah kepadaku jika sumber air kamu menjadi kering; maka siapakah yang akan mendatangkan air yang mengalir bagimu?\".'),
(5272, '068:001', '68', 68, 1, 'Nun, demi kalam dan apa yang mereka tulis,'),
(5273, '068:002', '68', 68, 2, 'berkat nikmat Tuhanmu kamu (Muhammad) sekali-kali bukan orang gila.'),
(5274, '068:003', '68', 68, 3, 'Dan sesungguhnya bagi kamu benar-benar pahala yang besar yang tidak putus-putusnya.'),
(5275, '068:004', '68', 68, 4, 'Dan sesungguhnya kamu benar-benar berbudi pekerti yang agung.'),
(5276, '068:005', '68', 68, 5, 'Maka kelak kamu akan melihat dan mereka (orang-orang kafir)pun akan melihat,'),
(5277, '068:006', '68', 68, 6, 'siapa di antara kamu yang gila.'),
(5278, '068:007', '68', 68, 7, 'Sesungguhnya Tuhanmu, Dialah Yang Paling Mengetahui siapa yang sesat dari jalan-Nya; dan Dialah Yang Paling Mengetahui orang-orang yang mendapat petunjuk.'),
(5279, '068:008', '68', 68, 8, 'Maka janganlah kamu ikuti orang-orang yang mendustakan (ayat-ayat Allah).'),
(5280, '068:009', '68', 68, 9, 'Maka mereka menginginkan supaya kamu bersikap lunak lalu mereka bersikap lunak (pula kepadamu).'),
(5281, '068:010', '68', 68, 10, 'Dan janganlah kamu ikuti setiap orang yang banyak bersumpah lagi hina,'),
(5282, '068:011', '68', 68, 11, 'yang banyak mencela, yang kian ke mari menghambur fitnah,'),
(5283, '068:012', '68', 68, 12, 'yang banyak menghalangi perbuatan baik, yang melampaui batas lagi banyak dosa,'),
(5284, '068:013', '68', 68, 13, 'yang kaku kasar, selain dari itu, yang terkenal kejahatannya,'),
(5285, '068:014', '68', 68, 14, 'karena dia mempunyai (banyak) harta dan anak.'),
(5286, '068:015', '68', 68, 15, 'Apabila dibacakan kepadanya ayat-ayat Kami, ia berkata: \"(Ini adalah) dongeng-dongengan orang-orang dahulu kala\".'),
(5287, '068:016', '68', 68, 16, 'Kelak akan Kami beri tanda dia di belalai(nya).'),
(5288, '068:017', '68', 68, 17, 'Sesungguhnya Kami telah mencobai mereka (musyrikin Mekah) sebagaimana Kami telah mencobai pemilik-pemilik kebun, ketika mereka bersumpah bahwa mereka sungguh-sungguh akanmemetik (hasil)nya di pagi hari,'),
(5289, '068:018', '68', 68, 18, 'dan mereka tidak menyisihkan (hak fakir miskin),'),
(5290, '068:019', '68', 68, 19, 'lalu kebun itu diliputi malapetaka (yang datang) dari Tuhanmu ketika mereka sedang tidur,'),
(5291, '068:020', '68', 68, 20, 'maka jadilah kebun itu hitam seperti malam yang gelap gulita.'),
(5292, '068:021', '68', 68, 21, 'lalu mereka panggil memanggil di pagi hari:'),
(5293, '068:022', '68', 68, 22, '\"Pergilah diwaktu pagi (ini) ke kebunmu jika kamu hendak memetik buahnya\".'),
(5294, '068:023', '68', 68, 23, 'Maka pergilah mereka saling berbisik-bisik.'),
(5295, '068:024', '68', 68, 24, '\"Pada hari ini janganlah ada seorang miskinpun masuk ke dalam kebunmu\".'),
(5296, '068:025', '68', 68, 25, 'Dan berangkatlah mereka di pagi hari dengan niat menghalangi (orang-orang miskin) padahal mereka (menolongnya).'),
(5297, '068:026', '68', 68, 26, 'Tatkala mereka melihat kebun itu, mereka berkata: \"Sesungguhnya kita benar-benar orang-orang yang sesat (jalan),'),
(5298, '068:027', '68', 68, 27, 'bahkan kita dihalangi (dari memperoleh hasilnya)\".'),
(5299, '068:028', '68', 68, 28, 'Berkatalah seorang yang paling baik pikirannya di antara mereka: \"Bukankah aku telah mengatakan kepadamu, hendaklah kamu bertasbih (kepada Tuhanmu)?\"'),
(5300, '068:029', '68', 68, 29, 'Mereka mengucapkan: \"Maha Suci Tuhan kami, sesungguhnya kami adalah orang-orang yang zalim\".'),
(5301, '068:030', '68', 68, 30, 'Lalu sebahagian mereka menghadapi sebahagian yang lain seraya cela mencela.'),
(5302, '068:031', '68', 68, 31, 'Mereka berkata: \"Aduhai celakalah kita; sesungguhnya kita ini adalah orang-orang yang melampaui batas\".'),
(5303, '068:032', '68', 68, 32, 'Mudah-mudahan Tuhan kita memberikan ganti kepada kita dengan (kebun) yang lebih baik daripada itu; sesungguhnya kita mengharapkan ampunan dari Tuhan kita.'),
(5304, '068:033', '68', 68, 33, 'Seperti itulah azab (dunia). Dan sesungguhnya azab akhirat lebih besar jika mereka mengetahui.'),
(5305, '068:034', '68', 68, 34, 'Sesungguhnya bagi orang-orang yang bertakwa (disediakan) surga-surga yang penuh kenikmatan di sisi Tuhannya.'),
(5306, '068:035', '68', 68, 35, 'Maka apakah patut Kami menjadikan orang-orang Islam itu sama dengan orang-orang yang berdosa (orang kafir)?'),
(5307, '068:036', '68', 68, 36, 'Atau adakah kamu (berbuat demikian): bagaimanakah kamu mengambil keputusan?'),
(5308, '068:037', '68', 68, 37, 'Atau adakah kamu mempunyai sebuah kitab (yang diturunkan Allah) yang kamu membacanya?,'),
(5309, '068:038', '68', 68, 38, 'bahwa di dalamnya kamu benar-benar boleh memilih apa yang kamu sukai untukmu.'),
(5310, '068:039', '68', 68, 39, 'Atau apakah kamu memperoleh janji yang diperkuat dengan sumpah dari Kami, yang tetap berlaku sampai hari kiamat; sesungguhnya kamu benar-benar dapat mengambil keputusan (sekehendakmu)?'),
(5311, '068:040', '68', 68, 40, 'Tanyakanlah kepada mereka: \"Siapakah di antara mereka yang bertanggung jawab terhadap keputusan yang diambil itu?\"'),
(5312, '068:041', '68', 68, 41, 'Atau apakah mereka mempunyai sekutu-sekutu? Maka hendaklah mereka mendatangkan sekutu-sekutunya jika mereka adalah orang-orang yang benar.'),
(5313, '068:042', '68', 68, 42, 'Pada hari betis disingkapkan dan mereka dipanggil untuk bersujud; maka mereka tidak kuasa,'),
(5314, '068:043', '68', 68, 43, '(dalam keadaan) pandangan mereka tunduk ke bawah, lagi mereka diliputi kehinaan. Dan sesungguhnya mereka dahulu (di dunia) diseru untuk bersujud, dan mereka dalam keadaan sejahtera.'),
(5315, '068:044', '68', 68, 44, 'Maka serahkanlah (ya Muhammad) kepada-Ku (urusan) orang-orang yang mendustakan perkataan ini (Al Quran). Nanti Kami akan menarik mereka dengan berangsur-angsur (ke arah kebinasaan) dari arah yang tidak mereka ketahui,'),
(5316, '068:045', '68', 68, 45, 'dan Aku memberi tangguh kepada mereka. Sesungguhnya rencana-Ku amat tangguh.'),
(5317, '068:046', '68', 68, 46, 'Apakah kamu meminta upah kepada mereka, lalu mereka diberati dengan hutang?'),
(5318, '068:047', '68', 68, 47, 'Ataukah ada pada mereka ilmu tentang yang ghaib lalu mereka menulis (padanya apa yang mereka tetapkan)?'),
(5319, '068:048', '68', 68, 48, 'Maka bersabarlah kamu (hai Muhammad) terhadap ketetapan Tuhanmu, dan janganlah kamu seperti orang yang berada dalam (perut) ikan ketika ia berdoa sedang ia dalam keadaan marah (kepada kaumnya).'),
(5320, '068:049', '68', 68, 49, 'Kalau sekiranya ia tidak segera mendapat nikmat dari Tuhannya, benar-benar ia dicampakkan ke tanah tandus dalam keadaan tercela.'),
(5321, '068:050', '68', 68, 50, 'Lalu Tuhannya memilihnya dan menjadikannya termasuk orang-orang yang saleh.'),
(5322, '068:051', '68', 68, 51, 'Dan sesungguhnya orang-orang kafir itu benar-benar hampir menggelincirkan kamu dengan pandangan mereka, tatkala mereka mendengar Al Quran dan mereka berkata: \"Sesungguhnya ia (Muhammad) benar-benar orang yang gila\".'),
(5323, '068:052', '68', 68, 52, 'Dan Al Quran itu tidak lain hanyalah peringatan bagi seluruh umat.'),
(5324, '069:001', '68', 69, 1, 'Hari kiamat,'),
(5325, '069:002', '68', 69, 2, 'apakah hari kiamat itu?'),
(5326, '069:003', '68', 69, 3, 'Dan tahukah kamu apakah hari kiamat itu?'),
(5327, '069:004', '68', 69, 4, 'Kaum Tsamud dan ´Aad telah mendustakan hari kiamat.'),
(5328, '069:005', '68', 69, 5, 'Adapun kaum Tsamud, maka mereka telah dibinasakan dengan kejadian yang luar biasa.'),
(5329, '069:006', '68', 69, 6, 'Adapun kaum ´Aad maka mereka telah dibinasakan dengan angin yang sangat dingin lagi amat kencang,'),
(5330, '069:007', '68', 69, 7, 'yang Allah menimpakan angin itu kepada mereka selama tujuh malam dan delapan hari terus menerus; maka kamu lihat kaum ´Aad pada waktu itu mati bergelimpangan seakan-akan mereka tunggul pohon kurma yang telah kosong (lapuk).'),
(5331, '069:008', '68', 69, 8, 'Maka kamu tidak melihat seorangpun yang tinggal di antara mereka.'),
(5332, '069:009', '68', 69, 9, 'Dan telah datang Fir´aun dan orang-orang yang sebelumnya dan (penduduk) negeri-negeri yang dijungkir balikkan karena kesalahan yang besar.'),
(5333, '069:010', '68', 69, 10, 'Maka (masing-masing) mereka mendurhakai rasul Tuhan mereka, lalu Allah menyiksa mereka dengan siksaan yang sangat keras.'),
(5334, '069:011', '68', 69, 11, 'Sesungguhnya Kami, tatkala air telah naik (sampai ke gunung) Kami bawa (nenek moyang) kamu, ke dalam bahtera,'),
(5335, '069:012', '68', 69, 12, 'agar Kami jadikan peristiwa itu peringatan bagi kamu dan agar diperhatikan oleh telinga yang mau mendengar.'),
(5336, '069:013', '68', 69, 13, 'Maka apabila sangkakala ditiup sekali tiup'),
(5337, '069:014', '68', 69, 14, 'dan diangkatlah bumi dan gunung-gunung, lalu dibenturkan keduanya sekali bentur.'),
(5338, '069:015', '68', 69, 15, 'Maka pada hari itu terjadilah hari kiamat,'),
(5339, '069:016', '68', 69, 16, 'dan terbelahlah langit, karena pada hari itu langit menjadi lemah.'),
(5340, '069:017', '68', 69, 17, 'Dan malaikat-malaikat berada di penjuru-penjuru langit. Dan pada hari itu delapan orang malaikat menjunjung ´Arsy Tuhanmu di atas (kepala) mereka.'),
(5341, '069:018', '68', 69, 18, 'Pada hari itu kamu dihadapkan (kepada Tuhanmu), tiada sesuatupun dari keadaanmu yang tersembunyi (bagi Allah).'),
(5342, '069:019', '68', 69, 19, 'Adapun orang-orang yang diberikan kepadanya kitabnya dari sebelah kanannya, maka dia berkata: \"Ambillah, bacalah kitabku (ini)\".'),
(5343, '069:020', '68', 69, 20, 'Sesungguhnya aku yakin, bahwa sesungguhnya aku akan menemui hisab terhadap diriku.'),
(5344, '069:021', '68', 69, 21, 'Maka orang itu berada dalam kehidupan yang diridhai,'),
(5345, '069:022', '68', 69, 22, 'dalam surga yang tinggi,'),
(5346, '069:023', '68', 69, 23, 'buah-buahannya dekat,'),
(5347, '069:024', '68', 69, 24, '(kepada mereka dikatakan): \"Makan dan minumlah dengan sedap disebabkan amal yang telah kamu kerjakan pada hari-hari yang telah lalu\".'),
(5348, '069:025', '68', 69, 25, 'Adapun orang yang diberikan kepadanya kitabnya dari sebelah kirinya, maka dia berkata: \"Wahai alangkah baiknya kiranya tidak diberikan kepadaku kitabku (ini).'),
(5349, '069:026', '68', 69, 26, 'Dan aku tidak mengetahui apa hisab terhadap diriku.'),
(5350, '069:027', '68', 69, 27, 'Wahai kiranya kematian itulah yang menyelesaikan segala sesuatu.'),
(5351, '069:028', '68', 69, 28, 'Hartaku sekali-kali tidak memberi manfaat kepadaku.'),
(5352, '069:029', '68', 69, 29, 'Telah hilang kekuasaanku daripadaku\".'),
(5353, '069:030', '68', 69, 30, '(Allah berfirman): \"Peganglah dia lalu belenggulah tangannya ke lehernya.'),
(5354, '069:031', '68', 69, 31, 'Kemudian masukkanlah dia ke dalam api neraka yang menyala-nyala.'),
(5355, '069:032', '68', 69, 32, 'Kemudian belitlah dia dengan rantai yang panjangnya tujuh puluh hasta.'),
(5356, '069:033', '68', 69, 33, 'Sesungguhnya dia dahulu tidak beriman kepada Allah Yang Maha Besar.'),
(5357, '069:034', '68', 69, 34, 'Dan juga dia tidak mendorong (orang lain) untuk memberi makan orang miskin.'),
(5358, '069:035', '68', 69, 35, 'Maka tiada seorang temanpun baginya pada hari ini di sini.'),
(5359, '069:036', '68', 69, 36, 'Dan tiada (pula) makanan sedikitpun (baginya) kecuali dari darah dan nanah.'),
(5360, '069:037', '68', 69, 37, 'Tidak ada yang memakannya kecuali orang-orang yang berdosa.'),
(5361, '069:038', '68', 69, 38, 'Maka Aku bersumpah dengan apa yang kamu lihat.'),
(5362, '069:039', '68', 69, 39, 'Dan dengan apa yang tidak kamu lihat.'),
(5363, '069:040', '68', 69, 40, 'Sesungguhnya Al Quran itu adalah benar-benar wahyu (Allah yang diturunkan kepada) Rasul yang mulia,'),
(5364, '069:041', '68', 69, 41, 'dan Al Quran itu bukanlah perkataan seorang penyair. Sedikit sekali kamu beriman kepadanya.');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(5365, '069:042', '68', 69, 42, 'Dan bukan pula perkataan tukang tenung. Sedikit sekali kamu mengambil pelajaran daripadanya.'),
(5366, '069:043', '68', 69, 43, 'Ia adalah wahyu yang diturunkan dari Tuhan semesta alam.'),
(5367, '069:044', '68', 69, 44, 'Seandainya dia (Muhammad) mengadakan sebagian perkataan atas (nama) Kami,'),
(5368, '069:045', '68', 69, 45, 'niscaya benar-benar Kami pegang dia pada tangan kanannya.'),
(5369, '069:046', '68', 69, 46, 'Kemudian benar-benar Kami potong urat tali jantungnya.'),
(5370, '069:047', '68', 69, 47, 'Maka sekali-kali tidak ada seorangpun dari kamu yang dapat menghalangi (Kami), dari pemotongan urat nadi itu.'),
(5371, '069:048', '68', 69, 48, 'Dan sesungguhnya Al Quran itu benar-benar suatu pelajaran bagi orang-orang yang bertakwa.'),
(5372, '069:049', '68', 69, 49, 'Dan sesungguhnya Kami benar-benar mengetahui bahwa di antara kamu ada orang yang mendustakan(nya).'),
(5373, '069:050', '68', 69, 50, 'Dan sesungguhnya Al Quran itu benar-benar menjadi penyesalan bagi orang-orang kafir (di akhirat).'),
(5374, '069:051', '68', 69, 51, 'Dan sesungguhnya Al Quran itu benar-benar kebenaran yang diyakini.'),
(5375, '069:052', '68', 69, 52, 'Maka bertasbihlah dengan (menyebut) nama Tuhanmu Yang Maha Besar.'),
(5376, '070:001', '68', 70, 1, 'Seseorang telah meminta kedatangan azab yang akan menimpa,'),
(5377, '070:002', '68', 70, 2, 'orang-orang kafir, yang tidak seorangpun dapat menolaknya,'),
(5378, '070:003', '68', 70, 3, '(yang datang) dari Allah, Yang mempunyai tempat-tempat naik.'),
(5379, '070:004', '68', 70, 4, 'Malaikat-malaikat dan Jibril naik (menghadap) kepada Tuhan dalam sehari yang kadarnya limapuluh ribu tahun.'),
(5380, '070:005', '68', 70, 5, 'Maka bersabarlah kamu dengan sabar yang baik.'),
(5381, '070:006', '68', 70, 6, 'Sesungguhnya mereka memandang siksaaan itu jauh (mustahil).'),
(5382, '070:007', '68', 70, 7, 'Sedangkan Kami memandangnya dekat (mungkin terjadi).'),
(5383, '070:008', '68', 70, 8, 'Pada hari ketika langit menjadi seperti luluhan perak,'),
(5384, '070:009', '68', 70, 9, 'dan gunung-gunung menjadi seperti bulu (yang berterbangan),'),
(5385, '070:010', '68', 70, 10, 'dan tidak ada seorang teman akrabpun menanyakan temannya,'),
(5386, '070:011', '68', 70, 11, 'sedang mereka saling memandang. Orang kafir ingin kalau sekiranya dia dapat menebus (dirinya) dari azab hari itu dengan anak-anaknya,'),
(5387, '070:012', '68', 70, 12, 'dan isterinya dan saudaranya,'),
(5388, '070:013', '68', 70, 13, 'dan kaum familinya yang melindunginya (di dunia).'),
(5389, '070:014', '68', 70, 14, 'Dan orang-orang di atas bumi seluruhnya kemudian (mengharapkan) tebusan itu dapat menyelamatkannya.'),
(5390, '070:015', '68', 70, 15, 'Sekali-kali tidak dapat, sesungguhnya neraka itu adalah api yang bergolak,'),
(5391, '070:016', '68', 70, 16, 'yang mengelupas kulit kepala,'),
(5392, '070:017', '68', 70, 17, 'yang memanggil orang yang membelakang dan yang berpaling (dari agama),'),
(5393, '070:018', '68', 70, 18, 'serta mengumpulkan (harta benda) lalu menyimpannya.'),
(5394, '070:019', '68', 70, 19, 'Sesungguhnya manusia diciptakan bersifat keluh kesah lagi kikir.'),
(5395, '070:020', '68', 70, 20, 'Apabila ia ditimpa kesusahan ia berkeluh kesah,'),
(5396, '070:021', '68', 70, 21, 'dan apabila ia mendapat kebaikan ia amat kikir,'),
(5397, '070:022', '68', 70, 22, 'kecuali orang-orang yang mengerjakan shalat,'),
(5398, '070:023', '68', 70, 23, 'yang mereka itu tetap mengerjakan shalatnya,'),
(5399, '070:024', '68', 70, 24, 'dan orang-orang yang dalam hartanya tersedia bagian tertentu,'),
(5400, '070:025', '68', 70, 25, 'bagi orang (miskin) yang meminta dan orang yang tidak mempunyai apa-apa (yang tidak mau meminta),'),
(5401, '070:026', '68', 70, 26, 'dan orang-orang yang mempercayai hari pembalasan,'),
(5402, '070:027', '68', 70, 27, 'dan orang-orang yang takut terhadap azab Tuhannya.'),
(5403, '070:028', '68', 70, 28, 'Karena sesungguhnya azab Tuhan mereka tidak dapat orang merasa aman (dari kedatangannya).'),
(5404, '070:029', '68', 70, 29, 'Dan orang-orang yang memelihara kemaluannya,'),
(5405, '070:030', '68', 70, 30, 'kecuali terhadap isteri-isteri mereka atau budak-budak yang mereka miliki, maka sesungguhnya mereka dalam hal ini tiada tercela.'),
(5406, '070:031', '68', 70, 31, 'Barangsiapa mencari yang di balik itu, maka mereka itulah orang-orang yang melampaui batas.'),
(5407, '070:032', '68', 70, 32, 'Dan orang-orang yang memelihara amanat-amanat (yang dipikulnya) dan janjinya.'),
(5408, '070:033', '68', 70, 33, 'Dan orang-orang yang memberikan kesaksiannya.'),
(5409, '070:034', '68', 70, 34, 'Dan orang-orang yang memelihara shalatnya.'),
(5410, '070:035', '68', 70, 35, 'Mereka itu (kekal) di surga lagi dimuliakan.'),
(5411, '070:036', '68', 70, 36, 'Mengapakah orang-orang kafir itu bersegera datang ke arahmu,'),
(5412, '070:037', '68', 70, 37, 'dari kanan dan dari kiri dengan berkelompok-kelompok.'),
(5413, '070:038', '68', 70, 38, 'Adakah setiap orang dari orang-orang kafir itu ingin masuk ke dalam surga yang penuh kenikmatan?,'),
(5414, '070:039', '68', 70, 39, 'sekali-kali tidak! Sesungguhnya Kami ciptakan mereka dari apa yang mereka ketahui (air mani).'),
(5415, '070:040', '68', 70, 40, 'Maka aku bersumpah dengan Tuhan Yang memiliki timur dan barat, sesungguhnya Kami benar-benar Maha Kuasa.'),
(5416, '070:041', '68', 70, 41, 'Untuk mengganti (mereka) dengan kaum yang lebih baik dari mereka, dan Kami sekali-kali tidak dapat dikalahkan.'),
(5417, '070:042', '68', 70, 42, 'Maka biarkanlah mereka tenggelam (dalam kebatilan) dan bermain-main sampai mereka menjumpai hari yang diancamkan kepada mereka,'),
(5418, '070:043', '68', 70, 43, '(yaitu) pada hari mereka keluar dari kubur dengan cepat seakan-akan mereka pergi dengan segera kepada berhala-berhala (sewaktu di dunia),'),
(5419, '070:044', '68', 70, 44, 'dalam keadaan mereka menekurkan pandangannya (serta) diliputi kehinaan. Itulah hari yang dahulunya diancamkan kepada mereka.'),
(5420, '071:001', '68', 71, 1, 'Sesungguhnya Kami telah mengutus Nuh kepada kaumnya (dengan memerintahkan): \"Berilah kaummu peringatan sebelum datang kepadanya azab yang pedih\",'),
(5421, '071:002', '68', 71, 2, 'Nuh berkata: \"Hai kaumku, sesungguhnya aku adalah pemberi peringatan yang menjelaskan kepada kamu,'),
(5422, '071:003', '68', 71, 3, '(yaitu) sembahlah olehmu Allah, bertakwalah kepada-Nya dan taatlah kepadaku,'),
(5423, '071:004', '68', 71, 4, 'niscaya Allah akan mengampuni sebagian dosa-dosamu dan menangguhkan kamu sampai kepada waktu yang ditentukan. Sesungguhnya ketetapan Allah apabila telah datang tidak dapat ditangguhkan, kalau kamu mengetahui\".'),
(5424, '071:005', '68', 71, 5, 'Nuh berkata: \"Ya Tuhanku sesungguhnya aku telah menyeru kaumku malam dan siang,'),
(5425, '071:006', '68', 71, 6, 'maka seruanku itu hanyalah menambah mereka lari (dari kebenaran).'),
(5426, '071:007', '68', 71, 7, 'Dan sesungguhnya setiap kali aku menyeru mereka (kepada iman) agar Engkau mengampuni mereka, mereka memasukkan anak jari mereka ke dalam telinganya dan menutupkan bajunya (kemukanya) dan mereka tetap (mengingkari) dan menyombongkan diri dengan sangat.'),
(5427, '071:008', '68', 71, 8, 'Kemudian sesungguhnya aku telah menyeru mereka (kepada iman) dengan cara terang-terangan,'),
(5428, '071:009', '68', 71, 9, 'kemudian sesungguhnya aku (menyeru) mereka (lagi) dengan terang-terangan dan dengan diam-diam,'),
(5429, '071:010', '68', 71, 10, 'maka aku katakan kepada mereka: ´Mohonlah ampun kepada Tuhanmu, -sesungguhnya Dia adalah Maha Pengampun-,'),
(5430, '071:011', '68', 71, 11, 'niscaya Dia akan mengirimkan hujan kepadamu dengan lebat,'),
(5431, '071:012', '68', 71, 12, 'dan membanyakkan harta dan anak-anakmu, dan mengadakan untukmu kebun-kebun dan mengadakan (pula di dalamnya) untukmu sungai-sungai.'),
(5432, '071:013', '68', 71, 13, 'Mengapa kamu tidak percaya akan kebesaran Allah?'),
(5433, '071:014', '68', 71, 14, 'Padahal Dia sesungguhnya telah menciptakan kamu dalam beberapa tingkatan kejadian.'),
(5434, '071:015', '68', 71, 15, 'Tidakkah kamu perhatikan bagaimana Allah telah menciptakan tujuh langit bertingkat-tingkat?'),
(5435, '071:016', '68', 71, 16, 'Dan Allah menciptakan padanya bulan sebagai cahaya dan menjadikan matahari sebagai pelita?'),
(5436, '071:017', '68', 71, 17, 'Dan Allah menumbuhkan kamu dari tanah dengan sebaik-baiknya,'),
(5437, '071:018', '68', 71, 18, 'kemudian Dia mengembalikan kamu ke dalam tanah dan mengeluarkan kamu (daripadanya pada hari kiamat) dengan sebenar-benarnya.'),
(5438, '071:019', '68', 71, 19, 'Dan Allah menjadikan bumi untukmu sebagai hamparan,'),
(5439, '071:020', '68', 71, 20, 'supaya kamu menjalani jalan-jalan yang luas di bumi itu\".'),
(5440, '071:021', '68', 71, 21, 'Nuh berkata: \"Ya Tuhanku, sesungguhnya mereka telah mendurhakaiku dan telah mengikuti orang-orang yang harta dan anak-anaknya tidak menambah kepadanya melainkan kerugian belaka,'),
(5441, '071:022', '68', 71, 22, 'dan melakukan tipu-daya yang amat besar\".'),
(5442, '071:023', '68', 71, 23, 'Dan mereka berkata: \"Jangan sekali-kali kamu meninggalkan (penyembahan) tuhan-tuhan kamu dan jangan pula sekali-kali kamu meninggalkan (penyembahan) wadd, dan jangan pula suwwa´, yaghuts, ya´uq dan nasr\".'),
(5443, '071:024', '68', 71, 24, 'Dan sesudahnya mereka menyesatkan kebanyakan (manusia); dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kesesatan.'),
(5444, '071:025', '68', 71, 25, 'Disebabkan kesalahan-kesalahan mereka, mereka ditenggelamkan lalu dimasukkan ke neraka, maka mereka tidak mendapat penolong-penolong bagi mereka selain dari Allah.'),
(5445, '071:026', '68', 71, 26, 'Nuh berkata: \"Ya Tuhanku, janganlah Engkau biarkan seorangpun di antara orang-orang kafir itu tinggal di atas bumi.'),
(5446, '071:027', '68', 71, 27, 'Sesungguhnya jika Engkau biarkan mereka tinggal, niscaya mereka akan menyesatkan hamba-hamba-Mu, dan mereka tidak akan melahirkan selain anak yang berbuat maksiat lagi sangat kafir.'),
(5447, '071:028', '68', 71, 28, 'Ya Tuhanku! Ampunilah aku, ibu bapakku, orang yang masuk ke rumahku dengan beriman dan semua orang yang beriman laki-laki dan perempuan. Dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kebinasaan\".'),
(5448, '072:001', '68', 72, 1, 'Katakanlah (hai Muhammad): \"Telah diwahyukan kepadamu bahwasanya: telah mendengarkan sekumpulan jin (akan Al Quran), lalu mereka berkata: Sesungguhnya kami telah mendengarkan Al Quran yang menakjubkan,'),
(5449, '072:002', '68', 72, 2, '(yang) memberi petunjuk kapada jalan yang benar, lalu kami beriman kepadanya. Dan kami sekali-kali tidak akan mempersekutukan seseorangpun dengan Tuhan kami,'),
(5450, '072:003', '68', 72, 3, 'dan bahwasanya Maha Tinggi kebesaran Tuhan kami, Dia tidak beristeri dan tidak (pula) beranak.'),
(5451, '072:004', '68', 72, 4, 'Dan bahwasanya: orang yang kurang akal daripada kami selalu mengatakan (perkataan) yang melampaui batas terhadap Allah,'),
(5452, '072:005', '68', 72, 5, 'dan sesungguhnya kami mengira, bahwa manusia dan jin sekali-kali tidak akan mengatakan perkataan yang dusta terhadap Allah.'),
(5453, '072:006', '68', 72, 6, 'Dan bahwasanya ada beberapa orang laki-laki di antara manusia meminta perlindungan kepada beberapa laki-laki di antara jin, maka jin-jin itu menambah bagi mereka dosa dan kesalahan.'),
(5454, '072:007', '68', 72, 7, 'Dan sesungguhnya mereka (jin) menyangka sebagaimana persangkaan kamu (orang-orang kafir Mekah), bahwa Allah sekali-kali tidak akan membangkitkan seorang (rasul)pun,'),
(5455, '072:008', '68', 72, 8, 'dan sesungguhnya kami telah mencoba mengetahui (rahasia) langit, maka kami mendapatinya penuh dengan penjagaan yang kuat dan panah-panah api,'),
(5456, '072:009', '68', 72, 9, 'dan sesungguhnya kami dahulu dapat menduduki beberapa tempat di langit itu untuk mendengar-dengarkan (berita-beritanya). Tetapi sekarang barangsiapa yang (mencoba) mendengar-dengarkan (seperti itu) tentu akan menjumpai panah api yang mengintai (untuk membakarnya).'),
(5457, '072:010', '68', 72, 10, 'Dan sesungguhnya kami tidak mengetahui (dengan adanya penjagaan itu) apakah keburukan yang dikehendaki bagi orang yang di bumi ataukah Tuhan mereka menghendaki kebaikan bagi mereka.'),
(5458, '072:011', '68', 72, 11, 'Dan sesungguhnya di antara kami ada orang-orang yang saleh dan di antara kami ada (pula) yang tidak demikian halnya. Adalah kami menempuh jalan yang berbeda-beda.'),
(5459, '072:012', '68', 72, 12, 'Dan sesungguhnya kami mengetahui bahwa kami sekali-kali tidak akan dapat melepaskan diri (dari kekuasaan) Allah di muka bumi dan sekali-kali tidak (pula) dapat melepaskan diri (daripada)Nya dengan lari.'),
(5460, '072:013', '68', 72, 13, 'Dan sesungguhnya kami tatkala mendengar petunjuk (Al Quran), kami beriman kepadanya. Barangsiapa beriman kepada Tuhannya, maka ia tidak takut akan pengurangan pahala dan tidak (takut pula) akan penambahan dosa dan kesalahan.'),
(5461, '072:014', '68', 72, 14, 'Dan sesungguhnya di antara kami ada orang-orang yang taat dan ada (pula) orang-orang yang menyimpang dari kebenaran. Barangsiapa yang yang taat, maka mereka itu benar-benar telah memilih jalan yang lurus.'),
(5462, '072:015', '68', 72, 15, 'Adapun orang-orang yang menyimpang dari kebenaran, maka mereka menjadi kayu api bagi neraka Jahannam.'),
(5463, '072:016', '68', 72, 16, 'Dan bahwasanya: jikalau mereka tetap berjalan lurus di atas jalan itu (agama Islam), benar-benar Kami akan memberi minum kepada mereka air yang segar (rezeki yang banyak).'),
(5464, '072:017', '68', 72, 17, 'Untuk Kami beri cobaan kepada mereka padanya. Dan barangsiapa yang berpaling dari peringatan Tuhannya, niscaya akan dimasukkan-Nya ke dalam azab yang amat berat.'),
(5465, '072:018', '68', 72, 18, 'Dan sesungguhnya mesjid-mesjid itu adalah kepunyaan Allah. Maka janganlah kamu menyembah seseorangpun di dalamnya di samping (menyembah) Allah.'),
(5466, '072:019', '68', 72, 19, 'Dan bahwasanya tatkala hamba Allah (Muhammad) berdiri menyembah-Nya (mengerjakan ibadat), hampir saja jin-jin itu desak mendesak mengerumuninya.'),
(5467, '072:020', '68', 72, 20, 'Katakanlah: \"Sesungguhnya aku hanya menyembah Tuhanku dan aku tidak mempersekutukan sesuatupun dengan-Nya\".'),
(5468, '072:021', '68', 72, 21, 'Katakanlah: \"Sesungguhnya aku tidak kuasa mendatangkan sesuatu kemudharatanpun kepadamu dan tidak (pula) suatu kemanfaatan\".'),
(5469, '072:022', '68', 72, 22, 'Katakanlah: \"Sesungguhnya aku sekali-kali tiada seorangpun dapat melindungiku dari (azab) Allah dan sekali-kali aku tiada akan memperoleh tempat berlindung selain daripada-Nya\".'),
(5470, '072:023', '68', 72, 23, 'Akan tetapi (aku hanya) menyampaikan (peringatan) dari Allah dan risalah-Nya. Dan barangsiapa yang mendurhakai Allah dan Rasul-Nya maka sesungguhnya baginyalah neraka Jahannam, mereka kekal di dalamnya selama-lamanya.'),
(5471, '072:024', '68', 72, 24, 'Sehingga apabila mereka melihat azab yang diancamkan kepada mereka, maka mereka akan mengetahui siapakah yang lebih lemah penolongnya dan lebih sedikit bilangannya.'),
(5472, '072:025', '68', 72, 25, 'Katakanlah: \"Aku tidak mengetahui, apakah azab yang diancamkan kepadamu itu dekat ataukah Tuhanku menjadikan bagi (kedatangan) azab itu masa yang panjang?\".'),
(5473, '072:026', '68', 72, 26, '(Dia adalah Tuhan) Yang Mengetahui yang ghaib, maka Dia tidak memperlihatkan kepada seorangpun tentang yang ghaib itu.'),
(5474, '072:027', '68', 72, 27, 'Kecuali kepada rasul yang diridhai-Nya, maka sesungguhnya Dia mengadakan penjaga-penjaga (malaikat) di muka dan di belakangnya.'),
(5475, '072:028', '68', 72, 28, 'Supaya Dia mengetahui, bahwa sesungguhnya rasul-rasul itu telah menyampaikan risalah-risalah Tuhannya, sedang (sebenarnya) ilmu-Nya meliputi apa yang ada pada mereka, dan Dia menghitung segala sesuatu satu persatu.'),
(5476, '073:001', '68', 73, 1, 'Hai orang yang berselimut (Muhammad),'),
(5477, '073:002', '68', 73, 2, 'bangunlah (untuk sembahyang) di malam hari, kecuali sedikit (daripadanya),'),
(5478, '073:003', '68', 73, 3, '(yaitu) seperduanya atau kurangilah dari seperdua itu sedikit.'),
(5479, '073:004', '68', 73, 4, 'atau lebih dari seperdua itu. Dan bacalah Al Quran itu dengan perlahan-lahan.'),
(5480, '073:005', '68', 73, 5, 'Sesungguhnya Kami akan menurunkan kapadamu perkataan yang berat.'),
(5481, '073:006', '68', 73, 6, 'Sesungguhnya bangun di waktu malam adalah lebih tepat (untuk khusyu´) dan bacaan di waktu itu lebih berkesan.'),
(5482, '073:007', '68', 73, 7, 'Sesungguhnya kamu pada siang hari mempunyai urusan yang panjang (banyak).'),
(5483, '073:008', '68', 73, 8, 'Sebutlah nama Tuhanmu, dan beribadatlah kepada-Nya dengan penuh ketekunan.'),
(5484, '073:009', '68', 73, 9, '(Dialah) Tuhan masyrik dan maghrib, tiada Tuhan (yang berhak disembah) melainkan Dia, maka ambillah Dia sebagai pelindung.'),
(5485, '073:010', '68', 73, 10, 'Dan bersabarlah terhadap apa yang mereka ucapkan dan jauhilah mereka dengan cara yang baik.'),
(5486, '073:011', '68', 73, 11, 'Dan biarkanlah Aku (saja) bertindak terhadap orang-orang yang mendustakan itu, orang-orang yang mempunyai kemewahan dan beri tangguhlah mereka barang sebentar.'),
(5487, '073:012', '68', 73, 12, 'Karena sesungguhnya pada sisi Kami ada belenggu-belenggu yang berat dan neraka yang menyala-nyala.'),
(5488, '073:013', '68', 73, 13, 'Dan makanan yang menyumbat di kerongkongan dan azab yang pedih.'),
(5489, '073:014', '68', 73, 14, 'Pada hari bumi dan gunung-gunung bergoncangan, dan menjadilah gunung-gunung itu tumpukan-tumpukan pasir yang berterbangan.'),
(5490, '073:015', '68', 73, 15, 'Sesungguhnya Kami telah mengutus kepada kamu (hai orang kafir Mekah) seorang Rasul, yang menjadi saksi terhadapmu, sebagaimana Kami telah mengutus (dahulu) seorang Rasul kepada Fir´aun.'),
(5491, '073:016', '68', 73, 16, 'Maka Fir´aun mendurhakai Rasul itu, lalu Kami siksa dia dengan siksaan yang berat.'),
(5492, '073:017', '68', 73, 17, 'Maka bagaimanakah kamu akan dapat memelihara dirimu jika kamu tetap kafir kepada hari yang menjadikan anak-anak beruban.'),
(5493, '073:018', '68', 73, 18, 'Langit(pun) menjadi pecah belah pada hari itu. Adalah janji-Nya itu pasti terlaksana.'),
(5494, '073:019', '68', 73, 19, 'Sesungguhnya ini adalah suatu peringatan. Maka barangsiapa yang menghendaki niscaya ia menempuh jalan (yang menyampaikannya) kepada Tuhannya.'),
(5495, '073:020', '68', 73, 20, 'Sesungguhnya Tuhanmu mengetahui bahwasanya kamu berdiri (sembahyang) kurang dari dua pertiga malam, atau seperdua malam atau sepertiganya dan (demikian pula) segolongan dari orang-orang yang bersama kamu. Dan Allah menetapkan ukuran malam dan siang. Allah mengetahui bahwa kamu sekali-kali tidak dapat menentukan batas-batas waktu-waktu itu, maka Dia memberi keringanan kepadamu, karena itu bacalah apa yang mudah (bagimu) dari Al Quran. Dia mengetahui bahwa akan ada di antara kamu orang-orang yang sakit dan orang-orang yang berjalan di muka bumi mencari sebagian karunia Allah; dan orang-orang yang lain lagi berperang di jalan Allah, maka bacalah apa yang mudah (bagimu) dari Al Quran dan dirikanlah sembahyang, tunaikanlah zakat dan berikanlah pinjaman kepada Allah pinjaman yang baik. Dan kebaikan apa saja yang kamu perbuat untuk dirimu niscaya kamu memperoleh (balasan)nya di sisi Allah sebagai balasan yang paling baik dan yang paling besar pahalanya. Dan mohonlah ampunan kepada Allah; sesungguhnya Allah Maha Pengampun lagi Maha Penyayang.'),
(5496, '074:001', '68', 74, 1, 'Hai orang yang berkemul (berselimut),'),
(5497, '074:002', '68', 74, 2, 'bangunlah, lalu berilah peringatan!'),
(5498, '074:003', '68', 74, 3, 'dan Tuhanmu agungkanlah!'),
(5499, '074:004', '68', 74, 4, 'dan pakaianmu bersihkanlah,'),
(5500, '074:005', '68', 74, 5, 'dan perbuatan dosa tinggalkanlah,'),
(5501, '074:006', '68', 74, 6, 'dan janganlah kamu memberi (dengan maksud) memperoleh (balasan) yang lebih banyak.'),
(5502, '074:007', '68', 74, 7, 'Dan untuk (memenuhi perintah) Tuhanmu, bersabarlah.'),
(5503, '074:008', '68', 74, 8, 'Apabila ditiup sangkakala,'),
(5504, '074:009', '68', 74, 9, 'maka waktu itu adalah waktu (datangnya) hari yang sulit,'),
(5505, '074:010', '68', 74, 10, 'bagi orang-orang kafir lagi tidak mudah.'),
(5506, '074:011', '68', 74, 11, 'Biarkanlah Aku bertindak terhadap orang yang Aku telah menciptakannya sendirian.'),
(5507, '074:012', '68', 74, 12, 'Dan Aku jadikan baginya harta benda yang banyak,'),
(5508, '074:013', '68', 74, 13, 'dan anak-anak yang selalu bersama dia,'),
(5509, '074:014', '68', 74, 14, 'dan Ku-lapangkan baginya (rezeki dan kekuasaan) dengan selapang-lapangnya,'),
(5510, '074:015', '68', 74, 15, 'kemudian dia ingin sekali supaya Aku menambahnya.'),
(5511, '074:016', '68', 74, 16, 'Sekali-kali tidak (akan Aku tambah), karena sesungguhnya dia menentang ayat-ayat Kami (Al Quran).'),
(5512, '074:017', '68', 74, 17, 'Aku akan membebaninya mendaki pendakian yang memayahkan.'),
(5513, '074:018', '68', 74, 18, 'Sesungguhnya dia telah memikirkan dan menetapkan (apa yang ditetapkannya),'),
(5514, '074:019', '68', 74, 19, 'maka celakalah dia! Bagaimana dia menetapkan?,'),
(5515, '074:020', '68', 74, 20, 'kemudian celakalah dia! Bagaimanakah dia menetapkan?,'),
(5516, '074:021', '68', 74, 21, 'kemudian dia memikirkan,'),
(5517, '074:022', '68', 74, 22, 'sesudah itu dia bermasam muka dan merengut,'),
(5518, '074:023', '68', 74, 23, 'kemudian dia berpaling (dari kebenaran) dan menyombongkan diri,'),
(5519, '074:024', '68', 74, 24, 'lalu dia berkata: \"(Al Quran) ini tidak lain hanyalah sihir yang dipelajari (dari orang-orang dahulu),'),
(5520, '074:025', '68', 74, 25, 'ini tidak lain hanyalah perkataan manusia\".'),
(5521, '074:026', '68', 74, 26, 'Aku akan memasukkannya ke dalam (neraka) Saqar.'),
(5522, '074:027', '68', 74, 27, 'Tahukah kamu apakah (neraka) Saqar itu?'),
(5523, '074:028', '68', 74, 28, 'Saqar itu tidak meninggalkan dan tidak membiarkan.'),
(5524, '074:029', '68', 74, 29, '(Neraka Saqar) adalah pembakar kulit manusia.'),
(5525, '074:030', '68', 74, 30, 'Dan di atasnya ada sembilan belas (malaikat penjaga).'),
(5526, '074:031', '68', 74, 31, 'Dan tiada Kami jadikan penjaga neraka itu melainkan dari malaikat: dan tidaklah Kami menjadikan bilangan mereka itu melainkan untuk jadi cobaan bagi orang-orang kafir, supaya orang-orang yang diberi Al-Kitab menjadi yakin dan supaya orang yang beriman bertambah imannya dan supaya orang-orang yang diberi Al Kitab dan orng-orang mukmin itu tidak ragu-ragu dan supaya orang-orang yang di dalam hatinya ada penyakit dan orang-orang kafir (mengatakan): \"Apakah yang dikehendaki Allah dengan bilangan ini sebagai suatu perumpamaan?\" Demikianlah Allah membiarkan sesat orang-orang yang dikehendaki-Nya dan memberi petunjuk kepada siapa yang dikehendaki-Nya. Dan tidak ada yang mengetahui tentara Tuhanmu melainkan Dia sendiri. Dan Saqar itu tiada lain hanyalah peringatan bagi manusia.'),
(5527, '074:032', '68', 74, 32, 'Sekali-kali tidak, demi bulan,'),
(5528, '074:033', '68', 74, 33, 'dan malam ketika telah berlalu,'),
(5529, '074:034', '68', 74, 34, 'dan subuh apabila mulai terang.'),
(5530, '074:035', '68', 74, 35, 'Sesungguhnya Saqar itu adalah salah satu bencana yang amat besar,'),
(5531, '074:036', '68', 74, 36, 'sebagai ancaman bagi manusia.'),
(5532, '074:037', '68', 74, 37, '(Yaitu) bagi siapa di antaramu yang berkehendak akan maju atau mundur.'),
(5533, '074:038', '68', 74, 38, 'Tiap-tiap diri bertanggung jawab atas apa yang telah diperbuatnya,'),
(5534, '074:039', '68', 74, 39, 'kecuali golongan kanan,'),
(5535, '074:040', '68', 74, 40, 'berada di dalam surga, mereka tanya menanya,'),
(5536, '074:041', '68', 74, 41, 'tentang (keadaan) orang-orang yang berdosa,'),
(5537, '074:042', '68', 74, 42, '\"Apakah yang memasukkan kamu ke dalam Saqar (neraka)?\"'),
(5538, '074:043', '68', 74, 43, 'Mereka menjawab: \"Kami dahulu tidak termasuk orang-orang yang mengerjakan shalat,'),
(5539, '074:044', '68', 74, 44, 'dan kami tidak (pula) memberi makan orang miskin,'),
(5540, '074:045', '68', 74, 45, 'dan adalah kami membicarakan yang bathil, bersama dengan orang-orang yang membicarakannya,'),
(5541, '074:046', '68', 74, 46, 'dan adalah kami mendustakan hari pembalasan,'),
(5542, '074:047', '68', 74, 47, 'hingga datang kepada kami kematian\".'),
(5543, '074:048', '68', 74, 48, 'Maka tidak berguna lagi bagi mereka syafa´at dari orang-orang yang memberikan syafa´at.'),
(5544, '074:049', '68', 74, 49, 'Maka mengapa mereka (orang-orang kafir) berpaling dari peringatan (Allah)?,'),
(5545, '074:050', '68', 74, 50, 'seakan-akan mereka itu keledai liar yang lari terkejut,'),
(5546, '074:051', '68', 74, 51, 'lari daripada singa.'),
(5547, '074:052', '68', 74, 52, 'Bahkan tiap-tiap orang dari mereka berkehendak supaya diberikan kepadanya lembaran-lembaran yang terbuka.'),
(5548, '074:053', '68', 74, 53, 'Sekali-kali tidak. Sebenarnya mereka tidak takut kepada negeri akhirat.'),
(5549, '074:054', '68', 74, 54, 'Sekali-kali tidak demikian halnya. Sesungguhnya Al Quran itu adalah peringatan.'),
(5550, '074:055', '68', 74, 55, 'Maka barangsiapa menghendaki, niscaya dia mengambil pelajaran daripadanya (Al Quran).'),
(5551, '074:056', '68', 74, 56, 'Dan mereka tidak akan mengambil pelajaran daripadanya kecuali (jika) Allah menghendakinya. Dia (Allah) adalah Tuhan Yang patut (kita) bertakwa kepada-Nya dan berhak memberi ampun.'),
(5552, '075:001', '68', 75, 1, 'Aku bersumpah demi hari kiamat,'),
(5553, '075:002', '68', 75, 2, 'dan aku bersumpah dengan jiwa yang amat menyesali (dirinya sendiri).'),
(5554, '075:003', '68', 75, 3, 'Apakah manusia mengira, bahwa Kami tidak akan mengumpulkan (kembali) tulang belulangnya?'),
(5555, '075:004', '68', 75, 4, 'Bukan demikian, sebenarnya Kami kuasa menyusun (kembali) jari jemarinya dengan sempurna.'),
(5556, '075:005', '68', 75, 5, 'Bahkan manusia itu hendak membuat maksiat terus menerus.'),
(5557, '075:006', '68', 75, 6, 'Ia berkata: \"Bilakah hari kiamat itu?\"'),
(5558, '075:007', '68', 75, 7, 'Maka apabila mata terbelalak (ketakutan),'),
(5559, '075:008', '68', 75, 8, 'dan apabila bulan telah hilang cahayanya,'),
(5560, '075:009', '68', 75, 9, 'dan matahari dan bulan dikumpulkan,'),
(5561, '075:010', '68', 75, 10, 'pada hari itu manusia berkata: \"Ke mana tempat berlari?\"'),
(5562, '075:011', '68', 75, 11, 'sekali-kali tidak! Tidak ada tempat berlindung!'),
(5563, '075:012', '68', 75, 12, 'Hanya kepada Tuhanmu sajalah pada hari itu tempat kembali.'),
(5564, '075:013', '68', 75, 13, 'Pada hari itu diberitakan kepada manusia apa yang telah dikerjakannya dan apa yang dilalaikannya.'),
(5565, '075:014', '68', 75, 14, 'Bahkan manusia itu menjadi saksi atas dirinya sendiri,'),
(5566, '075:015', '68', 75, 15, 'meskipun dia mengemukakan alasan-alasannya.'),
(5567, '075:016', '68', 75, 16, 'Janganlah kamu gerakkan lidahmu untuk (membaca) Al Quran karena hendak cepat-cepat (menguasai)nya.'),
(5568, '075:017', '68', 75, 17, 'Sesungguhnya atas tanggungan Kamilah mengumpulkannya (di dadamu) dan (membuatmu pandai) membacanya.'),
(5569, '075:018', '68', 75, 18, 'Apabila Kami telah selesai membacakannya maka ikutilah bacaannya itu.'),
(5570, '075:019', '68', 75, 19, 'Kemudian, sesungguhnya atas tanggungan Kamilah penjelasannya.'),
(5571, '075:020', '68', 75, 20, 'Sekali-kali janganlah demikian. Sebenarnya kamu (hai manusia) mencintai kehidupan dunia,'),
(5572, '075:021', '68', 75, 21, 'dan meninggalkan (kehidupan) akhirat.'),
(5573, '075:022', '68', 75, 22, 'Wajah-wajah (orang-orang mukmin) pada hari itu berseri-seri.'),
(5574, '075:023', '68', 75, 23, 'Kepada Tuhannyalah mereka melihat.'),
(5575, '075:024', '68', 75, 24, 'Dan wajah-wajah (orang kafir) pada hari itu muram,'),
(5576, '075:025', '68', 75, 25, 'mereka yakin bahwa akan ditimpakan kepadanya malapetaka yang amat dahsyat.'),
(5577, '075:026', '68', 75, 26, 'Sekali-kali jangan. Apabila nafas (seseorang) telah (mendesak) sampai ke kerongkongan,'),
(5578, '075:027', '68', 75, 27, 'dan dikatakan (kepadanya): \"Siapakah yang dapat menyembuhkan?\",'),
(5579, '075:028', '68', 75, 28, 'dan dia yakin bahwa sesungguhnya itulah waktu perpisahan (dengan dunia),'),
(5580, '075:029', '68', 75, 29, 'dan bertaut betis (kiri) dan betis (kanan),'),
(5581, '075:030', '68', 75, 30, 'kepada Tuhanmulah pada hari itu kamu dihalau.'),
(5582, '075:031', '68', 75, 31, 'Dan ia tidak mau membenarkan (Rasul dan Al Quran) dan tidak mau mengerjakan shalat,'),
(5583, '075:032', '68', 75, 32, 'tetapi ia mendustakan (Rasul) dam berpaling (dari kebenaran),'),
(5584, '075:033', '68', 75, 33, 'kemudian ia pergi kepada ahlinya dengan berlagak (sombong).'),
(5585, '075:034', '68', 75, 34, 'Kecelakaanlah bagimu (hai orang kafir) dan kecelakaanlah bagimu,'),
(5586, '075:035', '68', 75, 35, 'kemudian kecelakaanlah bagimu (hai orang kafir) dan kecelakaanlah bagimu.'),
(5587, '075:036', '68', 75, 36, 'Apakah manusia mengira, bahwa ia akan dibiarkan begitu saja (tanpa pertanggung jawaban)?'),
(5588, '075:037', '68', 75, 37, 'Bukankah dia dahulu setetes mani yang ditumpahkan (ke dalam rahim),'),
(5589, '075:038', '68', 75, 38, 'kemudian mani itu menjadi segumpal darah, lalu Allah menciptakannya, dan menyempurnakannya,'),
(5590, '075:039', '68', 75, 39, 'lalu Allah menjadikan daripadanya sepasang: laki-laki dan perempuan.'),
(5591, '075:040', '68', 75, 40, 'Bukankah (Allah yang berbuat) demikian berkuasa (pula) menghidupkan orang mati?'),
(5592, '076:001', '68', 76, 1, 'Bukankah telah datang atas manusia satu waktu dari masa, sedang dia ketika itu belum merupakan sesuatu yang dapat disebut?'),
(5593, '076:002', '68', 76, 2, 'Sesungguhnya Kami telah menciptakan manusia dari setetes mani yang bercampur yang Kami hendak mengujinya (dengan perintah dan larangan), karena itu Kami jadikan dia mendengar dan melihat.'),
(5594, '076:003', '68', 76, 3, 'Sesungguhnya Kami telah menunjukinya jalan yang lurus; ada yang bersyukur dan ada pula yang kafir.'),
(5595, '076:004', '68', 76, 4, 'Sesungguhnya Kami menyediakan bagi orang-orang kafir rantai, belenggu dan neraka yang menyala-nyala.'),
(5596, '076:005', '68', 76, 5, 'Sesungguhnya orang-orang yang berbuat kebajikan minum dari gelas (berisi minuman) yang campurannya adalah air kafur,'),
(5597, '076:006', '68', 76, 6, '(yaitu) mata air (dalam surga) yang daripadanya hamba-hamba Allah minum, yang mereka dapat mengalirkannya dengan sebaik-baiknya.'),
(5598, '076:007', '68', 76, 7, 'Mereka menunaikan nazar dan takut akan suatu hari yang azabnya merata di mana-mana.'),
(5599, '076:008', '68', 76, 8, 'Dan mereka memberikan makanan yang disukainya kepada orang miskin, anak yatim dan orang yang ditawan.'),
(5600, '076:009', '68', 76, 9, 'Sesungguhnya kami memberi makanan kepadamu hanyalah untuk mengharapkan keridhaan Allah, kami tidak menghendaki balasan dari kamu dan tidak pula (ucapan) terima kasih.'),
(5601, '076:010', '68', 76, 10, 'Sesungguhnya kami takut akan (azab) Tuhan kami pada suatu hari yang (di hari itu) orang-orang bermuka masam penuh kesulitan.'),
(5602, '076:011', '68', 76, 11, 'Maka Tuhan memelihara mereka dari kesusahan hari itu, dan memberikan kepada mereka kejernihan (wajah) dan kegembiraan hati.'),
(5603, '076:012', '68', 76, 12, 'Dan Dia memberi balasan kepada mereka karena kesabaran mereka (dengan) surga dan (pakaian) sutera,'),
(5604, '076:013', '68', 76, 13, 'di dalamnya mereka duduk bertelakan di atas dipan, mereka tidak merasakan di dalamnya (teriknya) matahari dan tidak pula dingin yang bersangatan.'),
(5605, '076:014', '68', 76, 14, 'Dan naungan (pohon-pohon surga itu) dekat di atas mereka dan buahnya dimudahkan memetiknya semudah-mudahnya.'),
(5606, '076:015', '68', 76, 15, 'Dan diedarkan kepada mereka bejana-bejana dari perak dan piala-piala yang bening laksana kaca,'),
(5607, '076:016', '68', 76, 16, '(yaitu) kaca-kaca (yang terbuat) dari perak yang telah diukur mereka dengan sebaik-baiknya.'),
(5608, '076:017', '68', 76, 17, 'Di dalam surga itu mereka diberi minum segelas (minuman) yang campurannya adalah jahe.'),
(5609, '076:018', '68', 76, 18, '(Yang didatangkan dari) sebuah mata air surga yang dinamakan salsabil.'),
(5610, '076:019', '68', 76, 19, 'Dan mereka dikelilingi oleh pelayan-pelayan muda yang tetap muda. Apabila kamu melihat mereka, kamu akan mengira mereka, mutiara yang bertaburan.'),
(5611, '076:020', '68', 76, 20, 'Dan apabila kamu melihat di sana (surga), niscaya kamu akan melihat berbagai macam kenikmatan dan kerajaan yang besar.'),
(5612, '076:021', '68', 76, 21, 'Mereka memakai pakaian sutera halus yang hijau dan sutera tebal dan dipakaikan kepada mereka gelang terbuat dari perak, dan Tuhan memberikan kepada mereka minuman yang bersih.'),
(5613, '076:022', '68', 76, 22, 'Sesungguhnya ini adalah balasan untukmu, dan usahamu adalah disyukuri (diberi balasan).'),
(5614, '076:023', '68', 76, 23, 'Sesungguhnya Kami telah menurunkan Al Quran kepadamu (hai Muhammad) dengan berangsur-angsur.'),
(5615, '076:024', '68', 76, 24, 'Maka bersabarlah kamu untuk (melaksanakan) ketetapan Tuhanmu, dan janganlah kamu ikuti orang yang berdosa dan orang yang kafir di antar mereka.'),
(5616, '076:025', '68', 76, 25, 'Dan sebutlah nama Tuhanmu pada (waktu) pagi dan petang.'),
(5617, '076:026', '68', 76, 26, 'Dan pada sebagian dari malam, maka sujudlah kepada-Nya dan bertasbihlah kepada-Nya pada bagian yang panjang dimalam hari.'),
(5618, '076:027', '68', 76, 27, 'Sesungguhnya mereka (orang kafir) menyukai kehidupan dunia dan mereka tidak memperdulikan kesudahan mereka, pada hari yang berat (hari akhirat).'),
(5619, '076:028', '68', 76, 28, 'Kami telah menciptakan mereka dan menguatkan persendian tubuh mereka, apabila Kami menghendaki, Kami sungguh-sungguh mengganti (mereka) dengan orang-orang yang serupa dengan mereka.'),
(5620, '076:029', '68', 76, 29, 'Sesungguhnya (ayat-ayat) ini adalah suatu peringatan, maka barangsiapa menghendaki (kebaikan bagi dirinya) niscaya dia mengambil jalan kepada Tuhannya.'),
(5621, '076:030', '68', 76, 30, 'Dan kamu tidak mampu (menempuh jalan itu), kecuali bila dikehendaki Allah. Sesungguhnya Allah adalah Maha Mengetahui lagi Maha Bijaksana.'),
(5622, '076:031', '68', 76, 31, 'Dan memasukkan siapa yang dikehendaki-Nya ke dalam rahmat-Nya (surga). Dan bagi orang-orang zalim disediakan-Nya azab yang pedih.'),
(5623, '077:001', '68', 77, 1, 'Demi malaikat-malaikat yang diutus untuk membawa kebaikan,'),
(5624, '077:002', '68', 77, 2, 'dan (malaikat-malaikat) yang terbang dengan kencangnya,'),
(5625, '077:003', '68', 77, 3, 'dan (malaikat-malaikat) yang menyebarkan (rahmat Tuhannya) dengan seluas-luasnya,'),
(5626, '077:004', '68', 77, 4, 'dan (malaikat-malaikat) yang membedakan (antara yang hak dan yang bathil) dengan sejelas-jelasnya,'),
(5627, '077:005', '68', 77, 5, 'dan (malaikat-malaikat) yang menyampaikan wahyu,'),
(5628, '077:006', '68', 77, 6, 'untuk menolak alasan-alasan atau memberi peringatan,'),
(5629, '077:007', '68', 77, 7, 'sesungguhnya apa yang dijanjikan kepadamu itu pasti terjadi.'),
(5630, '077:008', '68', 77, 8, 'Maka apabila bintang-bintang telah dihapuskan,'),
(5631, '077:009', '68', 77, 9, 'dan apabila langit telah dibelah,'),
(5632, '077:010', '68', 77, 10, 'dan apabila gunung-gunung telah dihancurkan menjadi debu,'),
(5633, '077:011', '68', 77, 11, 'dan apabila rasul-rasul telah ditetapkan waktu (mereka).'),
(5634, '077:012', '68', 77, 12, '(Niscaya dikatakan kepada mereka:) \"Sampai hari apakah ditangguhkan (mengazab orang-orang kafir itu)?\"'),
(5635, '077:013', '68', 77, 13, 'Sampai hari keputusan.'),
(5636, '077:014', '68', 77, 14, 'Dan tahukah kamu apakah hari keputusan itu?'),
(5637, '077:015', '68', 77, 15, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5638, '077:016', '68', 77, 16, 'Bukankah Kami telah membinasakan orang-orang yang dahulu?'),
(5639, '077:017', '68', 77, 17, 'Lalu Kami iringkan (azab Kami terhadap) mereka dengan (mengazab) orang-orang yang datang kemudian.'),
(5640, '077:018', '68', 77, 18, 'Demikianlah Kami berbuat terhadap orang-orang yang berdosa.'),
(5641, '077:019', '68', 77, 19, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5642, '077:020', '68', 77, 20, 'Bukankah Kami menciptakan kamu dari air yang hina?'),
(5643, '077:021', '68', 77, 21, 'kemudian Kami letakkan dia dalam tempat yang kokoh (rahim),'),
(5644, '077:022', '68', 77, 22, 'sampai waktu yang ditentukan,'),
(5645, '077:023', '68', 77, 23, 'lalu Kami tentukan (bentuknya), maka Kami-lah sebaik-baik yang menentukan.'),
(5646, '077:024', '68', 77, 24, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5647, '077:025', '68', 77, 25, 'Bukankah Kami menjadikan bumi (tempat) berkumpul,'),
(5648, '077:026', '68', 77, 26, 'orang-orang hidup dan orang-orang mati?'),
(5649, '077:027', '68', 77, 27, 'dan Kami jadikan padanya gunung-gunung yang tinggi, dan Kami beri minum kamu dengan air tawar?'),
(5650, '077:028', '68', 77, 28, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5651, '077:029', '68', 77, 29, '(Dikatakan kepada mereka pada hari kiamat): \"Pergilah kamu mendapatkan azab yang dahulunya kamu mendustakannya.'),
(5652, '077:030', '68', 77, 30, 'Pergilah kamu mendapatkan naungan yang mempunyai tiga cabang,'),
(5653, '077:031', '68', 77, 31, 'yang tidak melindungi dan tidak pula menolak nyala api neraka\".'),
(5654, '077:032', '68', 77, 32, 'Sesungguhnya neraka itu melontarkan bunga api sebesar dan setinggi istana.'),
(5655, '077:033', '68', 77, 33, 'Seolah-olah ia iringan unta yang kuning.'),
(5656, '077:034', '68', 77, 34, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5657, '077:035', '68', 77, 35, 'Ini adalah hari, yang mereka tidak dapat berbicara (pada hari itu),'),
(5658, '077:036', '68', 77, 36, 'dan tidak diizinkan kepada mereka minta uzur sehingga mereka (dapat) minta uzur.'),
(5659, '077:037', '68', 77, 37, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5660, '077:038', '68', 77, 38, 'Ini adalah hari keputusan; (pada hari ini) Kami mengumpulkan kamu dan orang-orang terdahulu.'),
(5661, '077:039', '68', 77, 39, 'Jika kamu mempunyai tipu daya, maka lakukanlah tipu dayamu itu terhadap-Ku.'),
(5662, '077:040', '68', 77, 40, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5663, '077:041', '68', 77, 41, 'Sesungguhnya orang-orang yang bertakwa berada dalam naungan (yang teduh) dan (di sekitar) mata-mata air.'),
(5664, '077:042', '68', 77, 42, 'Dan (mendapat) buah-buahan dari (macam-macam) yang mereka ingini.'),
(5665, '077:043', '68', 77, 43, '(Dikatakan kepada mereka): \"Makan dan minumlah kamu dengan enak karena apa yang telah kamu kerjakan\".'),
(5666, '077:044', '68', 77, 44, 'Sesungguhnya demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik.'),
(5667, '077:045', '68', 77, 45, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5668, '077:046', '68', 77, 46, '(Dikatakan kepada orang-orang kafir): \"Makanlah dan bersenang-senanglah kamu (di dunia dalam waktu) yang pendek; sesungguhnya kamu adalah orang-orang yang berdosa\".'),
(5669, '077:047', '68', 77, 47, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5670, '077:048', '68', 77, 48, 'Dan apabila dikatakan kepada mereka: \"Rukuklah, niscaya mereka tidak mau ruku´.'),
(5671, '077:049', '68', 77, 49, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan.'),
(5672, '077:050', '68', 77, 50, 'Maka kepada perkataan apakah selain Al Quran ini mereka akan beriman?'),
(5673, '078:001', '68', 78, 1, 'Tentang apakah mereka saling bertanya-tanya?'),
(5674, '078:002', '68', 78, 2, 'Tentang berita yang besar,'),
(5675, '078:003', '68', 78, 3, 'yang mereka perselisihkan tentang ini.'),
(5676, '078:004', '68', 78, 4, 'Sekali-kali tidak; kelak mereka akan mengetahui,'),
(5677, '078:005', '68', 78, 5, 'kemudian sekali-kali tidak; kelak mereka mengetahui.'),
(5678, '078:006', '68', 78, 6, 'Bukankah Kami telah menjadikan bumi itu sebagai hamparan?,'),
(5679, '078:007', '68', 78, 7, 'dan gunung-gunung sebagai pasak?,'),
(5680, '078:008', '68', 78, 8, 'dan Kami jadikan kamu berpasang-pasangan,'),
(5681, '078:009', '68', 78, 9, 'dan Kami jadikan tidurmu untuk istirahat,'),
(5682, '078:010', '68', 78, 10, 'dan Kami jadikan malam sebagai pakaian,'),
(5683, '078:011', '68', 78, 11, 'dan Kami jadikan siang untuk mencari penghidupan,'),
(5684, '078:012', '68', 78, 12, 'dan Kami bina di atas kamu tujuh buah (langit) yang kokoh,'),
(5685, '078:013', '68', 78, 13, 'dan Kami jadikan pelita yang amat terang (matahari),'),
(5686, '078:014', '68', 78, 14, 'dan Kami turunkan dari awan air yang banyak tercurah,'),
(5687, '078:015', '68', 78, 15, 'supaya Kami tumbuhkan dengan air itu biji-bijian dan tumbuh-tumbuhan,'),
(5688, '078:016', '68', 78, 16, 'dan kebun-kebun yang lebat?'),
(5689, '078:017', '68', 78, 17, 'Sesungguhnya Hari Keputusan adalah suatu waktu yang ditetapkan,'),
(5690, '078:018', '68', 78, 18, 'yaitu hari (yang pada waktu itu) ditiup sangkakala lalu kamu datang berkelompok-kelompok,'),
(5691, '078:019', '68', 78, 19, 'dan dibukalah langit, maka terdapatlah beberapa pintu,'),
(5692, '078:020', '68', 78, 20, 'dan dijalankanlah gunung-gunung maka menjadi fatamorganalah ia.'),
(5693, '078:021', '68', 78, 21, 'Sesungguhnya neraka Jahannam itu (padanya) ada tempat pengintai,'),
(5694, '078:022', '68', 78, 22, 'lagi menjadi tempat kembali bagi orang-orang yang melampaui batas,'),
(5695, '078:023', '68', 78, 23, 'mereka tinggal di dalamnya berabad-abad lamanya,'),
(5696, '078:024', '68', 78, 24, 'mereka tidak merasakan kesejukan di dalamnya dan tidak (pula mendapat) minuman,'),
(5697, '078:025', '68', 78, 25, 'selain air yang mendidih dan nanah,'),
(5698, '078:026', '68', 78, 26, 'sebagai pambalasan yang setimpal.'),
(5699, '078:027', '68', 78, 27, 'Sesungguhnya mereka tidak berharap (takut) kepada hisab,'),
(5700, '078:028', '68', 78, 28, 'dan mereka mendustakan ayat-ayat Kami dengan sesungguh-sungguhnya.'),
(5701, '078:029', '68', 78, 29, 'Dan segala sesuatu telah Kami catat dalam suatu kitab.'),
(5702, '078:030', '68', 78, 30, 'Karena itu rasakanlah. Dan Kami sekali-kali tidak akan menambah kepada kamu selain daripada azab.'),
(5703, '078:031', '68', 78, 31, 'Sesungguhnya orang-orang yang bertakwa mendapat kemenangan,'),
(5704, '078:032', '68', 78, 32, '(yaitu) kebun-kebun dan buah anggur,'),
(5705, '078:033', '68', 78, 33, 'dan gadis-gadis remaja yang sebaya,'),
(5706, '078:034', '68', 78, 34, 'dan gelas-gelas yang penuh (berisi minuman).'),
(5707, '078:035', '68', 78, 35, 'Di dalamnya mereka tidak mendengar perkataan yang sia-sia dan tidak (pula) perkataan dusta.'),
(5708, '078:036', '68', 78, 36, 'Sebagai pembalasan dari Tuhanmu dan pemberian yang cukup banyak,'),
(5709, '078:037', '68', 78, 37, 'Tuhan Yang memelihara langit dan bumi dan apa yang ada di antara keduanya; Yang Maha Pemurah. Mereka tidak dapat berbicara dengan Dia.'),
(5710, '078:038', '68', 78, 38, 'Pada hari, ketika ruh dan para malaikat berdiri bershaf-shaf, mereka tidak berkata-kata, kecuali siapa yang telah diberi izin kepadanya oleh Tuhan Yang Maha Pemurah; dan ia mengucapkan kata yang benar.'),
(5711, '078:039', '68', 78, 39, 'Itulah hari yang pasti terjadi. Maka barangsiapa yang menghendaki, niscaya ia menempuh jalan kembali kepada Tuhannya.'),
(5712, '078:040', '68', 78, 40, 'Sesungguhnya Kami telah memperingatkan kepadamu (hai orang kafir) siksa yang dekat, pada hari manusia melihat apa yang telah diperbuat oleh kedua tangannya; dan orang kafir berkata: \"Alangkah baiknya sekiranya aku dahulu adalah tanah\".'),
(5713, '079:001', '68', 79, 1, 'Demi (malaikat-malaikat) yang mencabut (nyawa) dengan keras,'),
(5714, '079:002', '68', 79, 2, 'dan (malaikat-malaikat) yang mencabut (nyawa) dengan lemah-lembut,'),
(5715, '079:003', '68', 79, 3, 'dan (malaikat-malaikat) yang turun dari langit dengan cepat,'),
(5716, '079:004', '68', 79, 4, 'dan (malaikat-malaikat) yang mendahului dengan kencang,'),
(5717, '079:005', '68', 79, 5, 'dan (malaikat-malaikat) yang mengatur urusan (dunia).'),
(5718, '079:006', '68', 79, 6, '(Sesungguhnya kamu akan dibangkitkan) pada hari ketika tiupan pertama menggoncang alam,'),
(5719, '079:007', '68', 79, 7, 'tiupan pertama itu diiringi oleh tiupan kedua.'),
(5720, '079:008', '68', 79, 8, 'Hati manusia pada waktu itu sangat takut,'),
(5721, '079:009', '68', 79, 9, 'Pandangannya tunduk.'),
(5722, '079:010', '68', 79, 10, '(Orang-orang kafir) berkata: \"Apakah sesungguhnya kami benar-benar dikembalikan kepada kehidupan semula?'),
(5723, '079:011', '68', 79, 11, 'Apakah (akan dibangkitkan juga) apabila kami telah menjadi tulang belulang yang hancur lumat?\"'),
(5724, '079:012', '68', 79, 12, 'Mereka berkata: \"Kalau demikian, itu adalah suatu pengembalian yang merugikan\".'),
(5725, '079:013', '68', 79, 13, 'Sesungguhnya pengembalian itu hanyalah satu kali tiupan saja,'),
(5726, '079:014', '68', 79, 14, 'maka dengan serta merta mereka hidup kembali di permukaan bumi.'),
(5727, '079:015', '68', 79, 15, 'Sudah sampaikah kepadamu (ya Muhammad) kisah Musa.'),
(5728, '079:016', '68', 79, 16, 'Tatkala Tuhannya memanggilnya di lembah suci ialah Lembah Thuwa;'),
(5729, '079:017', '68', 79, 17, '\"Pergilah kamu kepada Fir´aun, sesungguhnya dia telah melampaui batas,'),
(5730, '079:018', '68', 79, 18, 'dan katakanlah (kepada Fir´aun): \"Adakah keinginan bagimu untuk membersihkan diri (dari kesesatan)\".'),
(5731, '079:019', '68', 79, 19, 'Dan kamu akan kupimpin ke jalan Tuhanmu agar supaya kamu takut kepada-Nya?\"'),
(5732, '079:020', '68', 79, 20, 'Lalu Musa memperlihatkan kepadanya mukjizat yang besar.'),
(5733, '079:021', '68', 79, 21, 'Tetapi Fir´aun mendustakan dan mendurhakai.'),
(5734, '079:022', '68', 79, 22, 'Kemudian dia berpaling seraya berusaha menantang (Musa).'),
(5735, '079:023', '68', 79, 23, 'Maka dia mengumpulkan (pembesar-pembesarnya) lalu berseru memanggil kaumnya.'),
(5736, '079:024', '68', 79, 24, '(Seraya) berkata: \"Akulah tuhanmu yang paling tinggi\".'),
(5737, '079:025', '68', 79, 25, 'Maka Allah mengazabnya dengan azab di akhirat dan azab di dunia.'),
(5738, '079:026', '68', 79, 26, 'Sesungguhnya pada yang demikian itu terdapat pelajaran bagi orang yang takut (kepada Tuhannya).'),
(5739, '079:027', '68', 79, 27, 'Apakah kamu lebih sulit penciptaanya ataukah langit? Allah telah membinanya,'),
(5740, '079:028', '68', 79, 28, 'Dia meninggikan bangunannya lalu menyempurnakannya,'),
(5741, '079:029', '68', 79, 29, 'dan Dia menjadikan malamnya gelap gulita, dan menjadikan siangnya terang benderang.'),
(5742, '079:030', '68', 79, 30, 'Dan bumi sesudah itu dihamparkan-Nya.'),
(5743, '079:031', '68', 79, 31, 'Ia memancarkan daripadanya mata airnya, dan (menumbuhkan) tumbuh-tumbuhannya.'),
(5744, '079:032', '68', 79, 32, 'Dan gunung-gunung dipancangkan-Nya dengan teguh,'),
(5745, '079:033', '68', 79, 33, '(semua itu) untuk kesenanganmu dan untuk binatang-binatang ternakmu.'),
(5746, '079:034', '68', 79, 34, 'Maka apabila malapetaka yang sangat besar (hari kiamat) telah datang.'),
(5747, '079:035', '68', 79, 35, 'Pada hari (ketika) manusia teringat akan apa yang telah dikerjakannya,'),
(5748, '079:036', '68', 79, 36, 'dan diperlihatkan neraka dengan jelas kepada setiap orang yang melihat.'),
(5749, '079:037', '68', 79, 37, 'Adapun orang yang melampaui batas,'),
(5750, '079:038', '68', 79, 38, 'dan lebih mengutamakan kehidupan dunia,'),
(5751, '079:039', '68', 79, 39, 'maka sesungguhnya nerakalah tempat tinggal(nya).'),
(5752, '079:040', '68', 79, 40, 'Dan adapun orang-orang yang takut kepada kebesaran Tuhannya dan menahan diri dari keinginan hawa nafsunya,'),
(5753, '079:041', '68', 79, 41, 'maka sesungguhnya surgalah tempat tinggal(nya).'),
(5754, '079:042', '68', 79, 42, '(Orang-orang kafir) bertanya kepadamu (Muhammad) tentang hari kebangkitan, kapankah terjadinya?'),
(5755, '079:043', '68', 79, 43, 'Siapakah kamu (maka) dapat menyebutkan (waktunya)?'),
(5756, '079:044', '68', 79, 44, 'Kepada Tuhanmulah dikembalikan kesudahannya (ketentuan waktunya).'),
(5757, '079:045', '68', 79, 45, 'Kamu hanyalah pemberi peringatan bagi siapa yang takut kepadanya (hari berbangkit)'),
(5758, '079:046', '68', 79, 46, 'Pada hari mereka melihat hari berbangkit itu, mereka merasa seakan-akan tidak tinggal (di dunia) melainkan (sebentar saja) di waktu sore atau pagi hari.'),
(5759, '080:001', '68', 80, 1, 'Dia (Muhammad) bermuka masam dan berpaling,'),
(5760, '080:002', '68', 80, 2, 'karena telah datang seorang buta kepadanya.'),
(5761, '080:003', '68', 80, 3, 'Tahukah kamu barangkali ia ingin membersihkan dirinya (dari dosa),'),
(5762, '080:004', '68', 80, 4, 'atau dia (ingin) mendapatkan pengajaran, lalu pengajaran itu memberi manfaat kepadanya?'),
(5763, '080:005', '68', 80, 5, 'Adapun orang yang merasa dirinya serba cukup,'),
(5764, '080:006', '68', 80, 6, 'maka kamu melayaninya.'),
(5765, '080:007', '68', 80, 7, 'Padahal tidak ada (celaan) atasmu kalau dia tidak membersihkan diri (beriman).'),
(5766, '080:008', '68', 80, 8, 'Dan adapun orang yang datang kepadamu dengan bersegera (untuk mendapatkan pengajaran),'),
(5767, '080:009', '68', 80, 9, 'sedang ia takut kepada (Allah),'),
(5768, '080:010', '68', 80, 10, 'maka kamu mengabaikannya.'),
(5769, '080:011', '68', 80, 11, 'Sekali-kali jangan (demikian)! Sesungguhnya ajaran-ajaran Tuhan itu adalah suatu peringatan,'),
(5770, '080:012', '68', 80, 12, 'maka barangsiapa yang menghendaki, tentulah ia memperhatikannya,'),
(5771, '080:013', '68', 80, 13, 'di dalam kitab-kitab yang dimuliakan,'),
(5772, '080:014', '68', 80, 14, 'yang ditinggikan lagi disucikan,'),
(5773, '080:015', '68', 80, 15, 'di tangan para penulis (malaikat),'),
(5774, '080:016', '68', 80, 16, 'yang mulia lagi berbakti.'),
(5775, '080:017', '68', 80, 17, 'Binasalah manusia; alangkah amat sangat kekafirannya?'),
(5776, '080:018', '68', 80, 18, 'Dari apakah Allah menciptakannya?'),
(5777, '080:019', '68', 80, 19, 'Dari setetes mani, Allah menciptakannya lalu menentukannya.'),
(5778, '080:020', '68', 80, 20, 'Kemudian Dia memudahkan jalannya.'),
(5779, '080:021', '68', 80, 21, 'kemudian Dia mematikannya dan memasukkannya ke dalam kubur,'),
(5780, '080:022', '68', 80, 22, 'kemudian bila Dia menghendaki, Dia membangkitkannya kembali.'),
(5781, '080:023', '68', 80, 23, 'Sekali-kali jangan; manusia itu belum melaksanakan apa yang diperintahkan Allah kepadanya,'),
(5782, '080:024', '68', 80, 24, 'maka hendaklah manusia itu memperhatikan makanannya.'),
(5783, '080:025', '68', 80, 25, 'Sesungguhnya Kami benar-benar telah mencurahkan air (dari langit),');
INSERT INTO `quran_translation` (`translation_id`, `ayat_key`, `translation_key`, `surat`, `ayat`, `translation`) VALUES
(5784, '080:026', '68', 80, 26, 'kemudian Kami belah bumi dengan sebaik-baiknya,'),
(5785, '080:027', '68', 80, 27, 'lalu Kami tumbuhkan biji-bijian di bumi itu,'),
(5786, '080:028', '68', 80, 28, 'anggur dan sayur-sayuran,'),
(5787, '080:029', '68', 80, 29, 'zaitun dan kurma,'),
(5788, '080:030', '68', 80, 30, 'kebun-kebun (yang) lebat,'),
(5789, '080:031', '68', 80, 31, 'dan buah-buahan serta rumput-rumputan,'),
(5790, '080:032', '68', 80, 32, 'untuk kesenanganmu dan untuk binatang-binatang ternakmu.'),
(5791, '080:033', '68', 80, 33, 'Dan apabila datang suara yang memekakkan (tiupan sangkakala yang kedua),'),
(5792, '080:034', '68', 80, 34, 'pada hari ketika manusia lari dari saudaranya,'),
(5793, '080:035', '68', 80, 35, 'dari ibu dan bapaknya,'),
(5794, '080:036', '68', 80, 36, 'dari istri dan anak-anaknya.'),
(5795, '080:037', '68', 80, 37, 'Setiap orang dari mereka pada hari itu mempunyai urusan yang cukup menyibukkannya.'),
(5796, '080:038', '68', 80, 38, 'Banyak muka pada hari itu berseri-seri,'),
(5797, '080:039', '68', 80, 39, 'tertawa dan bergembira ria,'),
(5798, '080:040', '68', 80, 40, 'dan banyak (pula) muka pada hari itu tertutup debu,'),
(5799, '080:041', '68', 80, 41, 'dan ditutup lagi oleh kegelapan.'),
(5800, '080:042', '68', 80, 42, 'Mereka itulah orang-orang kafir lagi durhaka.'),
(5801, '081:001', '68', 81, 1, 'Apabila matahari digulung,'),
(5802, '081:002', '68', 81, 2, 'dan apabila bintang-bintang berjatuhan,'),
(5803, '081:003', '68', 81, 3, 'dan apabila gunung-gunung dihancurkan,'),
(5804, '081:004', '68', 81, 4, 'dan apabila unta-unta yang bunting ditinggalkan (tidak diperdulikan)'),
(5805, '081:005', '68', 81, 5, 'dan apabila binatang-binatang liar dikumpulkan,'),
(5806, '081:006', '68', 81, 6, 'dan apabila lautan dijadikan meluap'),
(5807, '081:007', '68', 81, 7, 'dan apabila ruh-ruh dipertemukan (dengan tubuh)'),
(5808, '081:008', '68', 81, 8, 'dan apabila bayi-bayi perempuan yang dikubur hidup-hidup ditanya,'),
(5809, '081:009', '68', 81, 9, 'karena dosa apakah dia dibunuh,'),
(5810, '081:010', '68', 81, 10, 'dan apabila catatan-catatan (amal perbuatan manusia) dibuka,'),
(5811, '081:011', '68', 81, 11, 'dan apabila langit dilenyapkan,'),
(5812, '081:012', '68', 81, 12, 'dan apabila neraka Jahim dinyalakan,'),
(5813, '081:013', '68', 81, 13, 'dan apabila surga didekatkan,'),
(5814, '081:014', '68', 81, 14, 'maka tiap-tiap jiwa akan mengetahui apa yang telah dikerjakannya.'),
(5815, '081:015', '68', 81, 15, 'Sungguh, Aku bersumpah dengan bintang-bintang,'),
(5816, '081:016', '68', 81, 16, 'yang beredar dan terbenam,'),
(5817, '081:017', '68', 81, 17, 'demi malam apabila telah hampir meninggalkan gelapnya,'),
(5818, '081:018', '68', 81, 18, 'dan demi subuh apabila fajarnya mulai menyingsing,'),
(5819, '081:019', '68', 81, 19, 'sesungguhnya Al Quran itu benar-benar firman (Allah yang dibawa oleh) utusan yang mulia (Jibril),'),
(5820, '081:020', '68', 81, 20, 'yang mempunyai kekuatan, yang mempunyai kedudukan tinggi di sisi Allah yang mempunyai ´Arsy,'),
(5821, '081:021', '68', 81, 21, 'yang ditaati di sana (di alam malaikat) lagi dipercaya.'),
(5822, '081:022', '68', 81, 22, 'Dan temanmu (Muhammad) itu bukanlah sekali-kali orang yang gila.'),
(5823, '081:023', '68', 81, 23, 'Dan sesungguhnya Muhammad itu melihat Jibril di ufuk yang terang.'),
(5824, '081:024', '68', 81, 24, 'Dan dia (Muhammad) bukanlah orang yang bakhil untuk menerangkan yang ghaib.'),
(5825, '081:025', '68', 81, 25, 'Dan Al Quran itu bukanlah perkataan syaitan yang terkutuk,'),
(5826, '081:026', '68', 81, 26, 'maka ke manakah kamu akan pergi?'),
(5827, '081:027', '68', 81, 27, 'Al Quran itu tiada lain hanyalah peringatan bagi semesta alam,'),
(5828, '081:028', '68', 81, 28, '(yaitu) bagi siapa di antara kamu yang mau menempuh jalan yang lurus.'),
(5829, '081:029', '68', 81, 29, 'Dan kamu tidak dapat menghendaki (menempuh jalan itu) kecuali apabila dikehendaki Allah, Tuhan semesta alam.'),
(5830, '082:001', '68', 82, 1, 'Apabila langit terbelah,'),
(5831, '082:002', '68', 82, 2, 'dan apabila bintang-bintang jatuh berserakan,'),
(5832, '082:003', '68', 82, 3, 'dan apabila lautan menjadikan meluap,'),
(5833, '082:004', '68', 82, 4, 'dan apabila kuburan-kuburan dibongkar,'),
(5834, '082:005', '68', 82, 5, 'maka tiap-tiap jiwa akan mengetahui apa yang telah dikerjakan dan yang dilalaikannya.'),
(5835, '082:006', '68', 82, 6, 'Hai manusia, apakah yang telah memperdayakan kamu (berbuat durhaka) terhadap Tuhanmu Yang Maha Pemurah.'),
(5836, '082:007', '68', 82, 7, 'Yang telah menciptakan kamu lalu menyempurnakan kejadianmu dan menjadikan (susunan tubuh)mu seimbang,'),
(5837, '082:008', '68', 82, 8, 'dalam bentuk apa saja yang Dia kehendaki, Dia menyusun tubuhmu.'),
(5838, '082:009', '68', 82, 9, 'Bukan hanya durhaka saja, bahkan kamu mendustakan hari pembalasan.'),
(5839, '082:010', '68', 82, 10, 'Padahal sesungguhnya bagi kamu ada (malaikat-malaikat) yang mengawasi (pekerjaanmu),'),
(5840, '082:011', '68', 82, 11, 'yang mulia (di sisi Allah) dan mencatat (pekerjaan-pekerjaanmu itu),'),
(5841, '082:012', '68', 82, 12, 'mereka mengetahui apa yang kamu kerjakan.'),
(5842, '082:013', '68', 82, 13, 'Sesungguhnya orang-orang yang banyak berbakti benar-benar berada dalam surga yang penuh kenikmatan,'),
(5843, '082:014', '68', 82, 14, 'dan sesungguhnya orang-orang yang durhaka benar-benar berada dalam neraka.'),
(5844, '082:015', '68', 82, 15, 'Mereka masuk ke dalamnya pada hari pembalasan.'),
(5845, '082:016', '68', 82, 16, 'Dan mereka sekali-kali tidak dapat keluar dari neraka itu.'),
(5846, '082:017', '68', 82, 17, 'Tahukah kamu apakah hari pembalasan itu?'),
(5847, '082:018', '68', 82, 18, 'Sekali lagi, tahukah kamu apakah hari pembalasan itu?'),
(5848, '082:019', '68', 82, 19, '(Yaitu) hari (ketika) seseorang tidak berdaya sedikitpun untuk menolong orang lain. Dan segala urusan pada hari itu dalam kekuasaan Allah.'),
(5849, '083:001', '68', 83, 1, 'Kecelakaan besarlah bagi orang-orang yang curang'),
(5850, '083:002', '68', 83, 2, '(yaitu) orang-orang yang apabila menerima takaran dari orang lain mereka minta dipenuhi,'),
(5851, '083:003', '68', 83, 3, 'dan apabila mereka menakar atau menimbang untuk orang lain, mereka mengurangi.'),
(5852, '083:004', '68', 83, 4, 'Tidaklah orang-orang itu menyangka, bahwa sesungguhnya mereka akan dibangkitkan,'),
(5853, '083:005', '68', 83, 5, 'pada suatu hari yang besar,'),
(5854, '083:006', '68', 83, 6, '(yaitu) hari (ketika) manusia berdiri menghadap Tuhan semesta alam?'),
(5855, '083:007', '68', 83, 7, 'Sekali-kali jangan curang, karena sesungguhnya kitab orang yang durhaka tersimpan dalam sijjin.'),
(5856, '083:008', '68', 83, 8, 'Tahukah kamu apakah sijjin itu?'),
(5857, '083:009', '68', 83, 9, '(Ialah) kitab yang bertulis.'),
(5858, '083:010', '68', 83, 10, 'Kecelakaan yang besarlah pada hari itu bagi orang-orang yang mendustakan,'),
(5859, '083:011', '68', 83, 11, '(yaitu) orang-orang yang mendustakan hari pembalasan.'),
(5860, '083:012', '68', 83, 12, 'Dan tidak ada yang mendustakan hari pembalasan itu melainkan setiap orang yang melampaui batas lagi berdosa,'),
(5861, '083:013', '68', 83, 13, 'yang apabila dibacakan kepadanya ayat-ayat Kami, ia berkata: \"Itu adalah dongengan orang-orang yang dahulu\"'),
(5862, '083:014', '68', 83, 14, 'Sekali-kali tidak (demikian), sebenarnya apa yang selalu mereka usahakan itu menutupi hati mereka.'),
(5863, '083:015', '68', 83, 15, 'Sekali-kali tidak, sesungguhnya mereka pada hari itu benar-benar tertutup dari (rahmat) Tuhan mereka.'),
(5864, '083:016', '68', 83, 16, 'Kemudian, sesungguhnya mereka benar-benar masuk neraka.'),
(5865, '083:017', '68', 83, 17, 'Kemudian, dikatakan (kepada mereka): \"Inilah azab yang dahulu selalu kamu dustakan\".'),
(5866, '083:018', '68', 83, 18, 'Sekali-kali tidak, sesungguhnya kitab orang-orang yang berbakti itu (tersimpan) dalam ´Illiyyin.'),
(5867, '083:019', '68', 83, 19, 'Tahukah kamu apakah ´Illiyyin itu?'),
(5868, '083:020', '68', 83, 20, '(Yaitu) kitab yang bertulis,'),
(5869, '083:021', '68', 83, 21, 'yang disaksikan oleh malaikat-malaikat yang didekatkan (kepada Allah).'),
(5870, '083:022', '68', 83, 22, 'Sesungguhnya orang yang berbakti itu benar-benar berada dalam kenikmatan yang besar (surga),'),
(5871, '083:023', '68', 83, 23, 'mereka (duduk) di atas dipan-dipan sambil memandang.'),
(5872, '083:024', '68', 83, 24, 'Kamu dapat mengetahui dari wajah mereka kesenangan mereka yang penuh kenikmatan.'),
(5873, '083:025', '68', 83, 25, 'Mereka diberi minum dari khamar murni yang dilak (tempatnya),'),
(5874, '083:026', '68', 83, 26, 'laknya adalah kesturi; dan untuk yang demikian itu hendaknya orang berlomba-lomba.'),
(5875, '083:027', '68', 83, 27, 'Dan campuran khamar murni itu adalah dari tasnim,'),
(5876, '083:028', '68', 83, 28, '(yaitu) mata air yang minum daripadanya orang-orang yang didekatkan kepada Allah.'),
(5877, '083:029', '68', 83, 29, 'Sesungguhnya orang-orang yang berdosa, adalah mereka yang menertawakan orang-orang yang beriman.'),
(5878, '083:030', '68', 83, 30, 'Dan apabila orang-orang yang beriman lalu di hadapan mereka, mereka saling mengedip-ngedipkan matanya.'),
(5879, '083:031', '68', 83, 31, 'Dan apabila orang-orang yang berdosa itu kembali kepada kaumnya, mereka kembali dengan gembira.'),
(5880, '083:032', '68', 83, 32, 'Dan apabila mereka melihat orang-orang mukmin, mereka mengatakan: \"Sesungguhnya mereka itu benar-benar orang-orang yang sesat\",'),
(5881, '083:033', '68', 83, 33, 'padahal orang-orang yang berdosa itu tidak dikirim untuk penjaga bagi orang-orang mukmin.'),
(5882, '083:034', '68', 83, 34, 'Maka pada hari ini, orang-orang yang beriman menertawakan orang-orang kafir,'),
(5883, '083:035', '68', 83, 35, 'mereka (duduk) di atas dipan-dipan sambil memandang.'),
(5884, '083:036', '68', 83, 36, 'Sesungguhnya orang-orang kafir telah diberi ganjaran terhadap apa yang dahulu mereka kerjakan.'),
(5885, '084:001', '68', 84, 1, 'Apabila langit terbelah,'),
(5886, '084:002', '68', 84, 2, 'dan patuh kepada Tuhannya, dan sudah semestinya langit itu patuh,'),
(5887, '084:003', '68', 84, 3, 'dan apabila bumi diratakan,'),
(5888, '084:004', '68', 84, 4, 'dan dilemparkan apa yang ada di dalamnya dan menjadi kosong,'),
(5889, '084:005', '68', 84, 5, 'dan patuh kepada Tuhannya, dan sudah semestinya bumi itu patuh, (pada waktu itu manusia akan mengetahui akibat perbuatannya).'),
(5890, '084:006', '68', 84, 6, 'Hai manusia, sesungguhnya kamu telah bekerja dengan sungguh-sungguh menuju Tuhanmu, maka pasti kamu akan menemui-Nya.'),
(5891, '084:007', '68', 84, 7, 'Adapun orang yang diberikan kitabnya dari sebelah kanannya,'),
(5892, '084:008', '68', 84, 8, 'maka dia akan diperiksa dengan pemeriksaan yang mudah,'),
(5893, '084:009', '68', 84, 9, 'dan dia akan kembali kepada kaumnya (yang sama-sama beriman) dengan gembira.'),
(5894, '084:010', '68', 84, 10, 'Adapun orang-orang yang diberikan kitabnya dari belakang,'),
(5895, '084:011', '68', 84, 11, 'maka dia akan berteriak: \"Celakalah aku\".'),
(5896, '084:012', '68', 84, 12, 'Dan dia akan masuk ke dalam api yang menyala-nyala (neraka).'),
(5897, '084:013', '68', 84, 13, 'Sesungguhnya dia dahulu (di dunia) bergembira di kalangan kaumnya (yang sama-sama kafir).'),
(5898, '084:014', '68', 84, 14, 'Sesungguhnya dia menyangka bahwa dia sekali-kali tidak akan kembali (kepada Tuhannya).'),
(5899, '084:015', '68', 84, 15, '(Bukan demikian), yang benar, sesungguhnya Tuhannya selalu melihatnya.'),
(5900, '084:016', '68', 84, 16, 'Maka sesungguhnya Aku bersumpah dengan cahaya merah di waktu senja,'),
(5901, '084:017', '68', 84, 17, 'dan dengan malam dan apa yang diselubunginya,'),
(5902, '084:018', '68', 84, 18, 'dan dengan bulan apabila jadi purnama,'),
(5903, '084:019', '68', 84, 19, 'sesungguhnya kamu melalui tingkat demi tingkat (dalam kehidupan),'),
(5904, '084:020', '68', 84, 20, 'Mengapa mereka tidak mau beriman?'),
(5905, '084:021', '68', 84, 21, 'dan apabila Al Quran dibacakan kepada mereka, mereka tidak bersujud,'),
(5906, '084:022', '68', 84, 22, 'bahkan orang-orang kafir itu mendustakan(nya).'),
(5907, '084:023', '68', 84, 23, 'Padahal Allah mengetahui apa yang mereka sembunyikan (dalam hati mereka).'),
(5908, '084:024', '68', 84, 24, 'Maka beri kabar gembiralah mereka dengan azab yang pedih,'),
(5909, '084:025', '68', 84, 25, 'tetapi orang-orang yang beriman dan beramal saleh, bagi mereka pahala yang tidak putus-putusnya.'),
(5910, '085:001', '68', 85, 1, 'Demi langit yang mempunyai gugusan bintang,'),
(5911, '085:002', '68', 85, 2, 'dan hari yang dijanjikan,'),
(5912, '085:003', '68', 85, 3, 'dan yang menyaksikan dan yang disaksikan.'),
(5913, '085:004', '68', 85, 4, 'Binasa dan terlaknatlah orang-orang yang membuat parit,'),
(5914, '085:005', '68', 85, 5, 'yang berapi (dinyalakan dengan) kayu bakar,'),
(5915, '085:006', '68', 85, 6, 'ketika mereka duduk di sekitarnya,'),
(5916, '085:007', '68', 85, 7, 'sedang mereka menyaksikan apa yang mereka perbuat terhadap orang-orang yang beriman.'),
(5917, '085:008', '68', 85, 8, 'Dan mereka tidak menyiksa orang-orang mukmin itu melainkan karena orang-orang mukmin itu beriman kepada Allah Yang Maha Perkasa lagi Maha Terpuji,'),
(5918, '085:009', '68', 85, 9, 'Yang mempunyai kerajaan langit dan bumi; dan Allah Maha Menyaksikan segala sesuatu.'),
(5919, '085:010', '68', 85, 10, 'Sesungguhnya orang-orang yang mendatangkan cobaan kepada orang-orang yang mukmin laki-laki dan perempuan kemudian mereka tidak bertaubat, maka bagi mereka azab Jahannam dan bagi mereka azab (neraka) yang membakar.'),
(5920, '085:011', '68', 85, 11, 'Sesungguhnya orang-orang yang beriman dan mengerjakan amal-amal yang saleh bagi mereka surga yang mengalir di bawahnya sungai-sungai; itulah keberuntungan yang besar.'),
(5921, '085:012', '68', 85, 12, 'Sesungguhnya azab Tuhanmu benar-benar keras.'),
(5922, '085:013', '68', 85, 13, 'Sesungguhnya Dialah Yang menciptakan (makhluk) dari permulaan dan menghidupkannya (kembali).'),
(5923, '085:014', '68', 85, 14, 'Dialah Yang Maha Pengampun lagi Maha Pengasih,'),
(5924, '085:015', '68', 85, 15, 'yang mempunyai ´Arsy, lagi Maha Mulia,'),
(5925, '085:016', '68', 85, 16, 'Maha Kuasa berbuat apa yang dikehendaki-Nya.'),
(5926, '085:017', '68', 85, 17, 'Sudahkah datang kepadamu berita kaum-kaum penentang,'),
(5927, '085:018', '68', 85, 18, '(yaitu kaum) Fir´aun dan (kaum) Tsamud?'),
(5928, '085:019', '68', 85, 19, 'Sesungguhnya orang-orang kafir selalu mendustakan,'),
(5929, '085:020', '68', 85, 20, 'padahal Allah mengepung mereka dari belakang mereka.'),
(5930, '085:021', '68', 85, 21, 'Bahkan yang didustakan mereka itu ialah Al Quran yang mulia,'),
(5931, '085:022', '68', 85, 22, 'yang (tersimpan) dalam Lauh Mahfuzh.'),
(5932, '086:001', '68', 86, 1, 'Demi langit dan yang datang pada malam hari,'),
(5933, '086:002', '68', 86, 2, 'tahukah kamu apakah yang datang pada malam hari itu?'),
(5934, '086:003', '68', 86, 3, '(yaitu) bintang yang cahayanya menembus,'),
(5935, '086:004', '68', 86, 4, 'tidak ada suatu jiwapun (diri) melainkan ada penjaganya.'),
(5936, '086:005', '68', 86, 5, 'Maka hendaklah manusia memperhatikan dari apakah dia diciptakan?'),
(5937, '086:006', '68', 86, 6, 'Dia diciptakan dari air yang dipancarkan,'),
(5938, '086:007', '68', 86, 7, 'yang keluar dari antara tulang sulbi laki-laki dan tulang dada perempuan.'),
(5939, '086:008', '68', 86, 8, 'Sesungguhnya Allah benar-benar kuasa untuk mengembalikannya (hidup sesudah mati).'),
(5940, '086:009', '68', 86, 9, 'Pada hari dinampakkan segala rahasia,'),
(5941, '086:010', '68', 86, 10, 'maka sekali-kali tidak ada bagi manusia itu suatu kekuatanpun dan tidak (pula) seorang penolong.'),
(5942, '086:011', '68', 86, 11, 'Demi langit yang mengandung hujan'),
(5943, '086:012', '68', 86, 12, 'dan bumi yang mempunyai tumbuh-tumbuhan,'),
(5944, '086:013', '68', 86, 13, 'sesungguhnya Al Quran itu benar-benar firman yang memisahkan antara yang hak dan yang bathil.'),
(5945, '086:014', '68', 86, 14, 'dan sekali-kali bukanlah dia senda gurau.'),
(5946, '086:015', '68', 86, 15, 'Sesungguhnya orang kafir itu merencanakan tipu daya yang jahat dengan sebenar-benarnya.'),
(5947, '086:016', '68', 86, 16, 'Dan Akupun membuat rencana (pula) dengan sebenar-benarnya.'),
(5948, '086:017', '68', 86, 17, 'Karena itu beri tangguhlah orang-orang kafir itu yaitu beri tangguhlah mereka itu barang sebentar.'),
(5949, '087:001', '68', 87, 1, 'Sucikanlah nama Tuhanmu Yang Maha Tingi,'),
(5950, '087:002', '68', 87, 2, 'yang menciptakan, dan menyempurnakan (penciptaan-Nya),'),
(5951, '087:003', '68', 87, 3, 'dan yang menentukan kadar (masing-masing) dan memberi petunjuk,'),
(5952, '087:004', '68', 87, 4, 'dan yang menumbuhkan rumput-rumputan,'),
(5953, '087:005', '68', 87, 5, 'lalu dijadikan-Nya rumput-rumput itu kering kehitam-hitaman.'),
(5954, '087:006', '68', 87, 6, 'Kami akan membacakan (Al Quran) kepadamu (Muhammad) maka kamu tidak akan lupa,'),
(5955, '087:007', '68', 87, 7, 'kecuali kalau Allah menghendaki. Sesungguhnya Dia mengetahui yang terang dan yang tersembunyi.'),
(5956, '087:008', '68', 87, 8, 'dan Kami akan memberi kamu taufik ke jalan yang mudah,'),
(5957, '087:009', '68', 87, 9, 'oleh sebab itu berikanlah peringatan karena peringatan itu bermanfaat,'),
(5958, '087:010', '68', 87, 10, 'orang yang takut (kepada Allah) akan mendapat pelajaran,'),
(5959, '087:011', '68', 87, 11, 'dan orang-orang yang celaka (kafir) akan menjauhinya.'),
(5960, '087:012', '68', 87, 12, '(Yaitu) orang yang akan memasuki api yang besar (neraka).'),
(5961, '087:013', '68', 87, 13, 'Kemudian dia tidak akan mati di dalamnya dan tidak (pula) hidup.'),
(5962, '087:014', '68', 87, 14, 'Sesungguhnya beruntunglah orang yang membersihkan diri (dengan beriman),'),
(5963, '087:015', '68', 87, 15, 'dan dia ingat nama Tuhannya, lalu dia sembahyang.'),
(5964, '087:016', '68', 87, 16, 'Tetapi kamu (orang-orang kafir) memilih kehidupan duniawi.'),
(5965, '087:017', '68', 87, 17, 'Sedang kehidupan akhirat adalah lebih baik dan lebih kekal.'),
(5966, '087:018', '68', 87, 18, 'Sesungguhnya ini benar-benar terdapat dalam kitab-kitab yang dahulu,'),
(5967, '087:019', '68', 87, 19, '(yaitu) Kitab-kitab Ibrahim dan Musa'),
(5968, '088:001', '68', 88, 1, 'Sudah datangkah kepadamu berita (tentang) hari pembalasan?'),
(5969, '088:002', '68', 88, 2, 'Banyak muka pada hari itu tunduk terhina,'),
(5970, '088:003', '68', 88, 3, 'bekerja keras lagi kepayahan,'),
(5971, '088:004', '68', 88, 4, 'memasuki api yang sangat panas (neraka),'),
(5972, '088:005', '68', 88, 5, 'diberi minum (dengan air) dari sumber yang sangat panas.'),
(5973, '088:006', '68', 88, 6, 'Mereka tiada memperoleh makanan selain dari pohon yang berduri,'),
(5974, '088:007', '68', 88, 7, 'yang tidak menggemukkan dan tidak pula menghilangkan lapar.'),
(5975, '088:008', '68', 88, 8, 'Banyak muka pada hari itu berseri-seri,'),
(5976, '088:009', '68', 88, 9, 'merasa senang karena usahanya,'),
(5977, '088:010', '68', 88, 10, 'dalam surga yang tinggi,'),
(5978, '088:011', '68', 88, 11, 'tidak kamu dengar di dalamnya perkataan yang tidak berguna.'),
(5979, '088:012', '68', 88, 12, 'Di dalamnya ada mata air yang mengalir.'),
(5980, '088:013', '68', 88, 13, 'Di dalamnya ada takhta-takhta yang ditinggikan,'),
(5981, '088:014', '68', 88, 14, 'dan gelas-gelas yang terletak (di dekatnya),'),
(5982, '088:015', '68', 88, 15, 'dan bantal-bantal sandaran yang tersusun,'),
(5983, '088:016', '68', 88, 16, 'dan permadani-permadani yang terhampar.'),
(5984, '088:017', '68', 88, 17, 'Maka apakah mereka tidak memperhatikan unta bagaimana dia diciptakan,'),
(5985, '088:018', '68', 88, 18, 'Dan langit, bagaimana ia ditinggikan?'),
(5986, '088:019', '68', 88, 19, 'Dan gunung-gunung bagaimana ia ditegakkan?'),
(5987, '088:020', '68', 88, 20, 'Dan bumi bagaimana ia dihamparkan?'),
(5988, '088:021', '68', 88, 21, 'Maka berilah peringatan, karena sesungguhnya kamu hanyalah orang yang memberi peringatan.'),
(5989, '088:022', '68', 88, 22, 'Kamu bukanlah orang yang berkuasa atas mereka,'),
(5990, '088:023', '68', 88, 23, 'tetapi orang yang berpaling dan kafir,'),
(5991, '088:024', '68', 88, 24, 'maka Allah akan mengazabnya dengan azab yang besar.'),
(5992, '088:025', '68', 88, 25, 'Sesungguhnya kepada Kami-lah kembali mereka,'),
(5993, '088:026', '68', 88, 26, 'kemudian sesungguhnya kewajiban Kami-lah menghisab mereka.'),
(5994, '089:001', '68', 89, 1, 'Demi fajar,'),
(5995, '089:002', '68', 89, 2, 'dan malam yang sepuluh,'),
(5996, '089:003', '68', 89, 3, 'dan yang genap dan yang ganjil,'),
(5997, '089:004', '68', 89, 4, 'dan malam bila berlalu.'),
(5998, '089:005', '68', 89, 5, 'Pada yang demikian itu terdapat sumpah (yang dapat diterima) oleh orang-orang yang berakal.'),
(5999, '089:006', '68', 89, 6, 'Apakah kamu tidak memperhatikan bagaimana Tuhanmu berbuat terhadap kaum ´Aad?'),
(6000, '089:007', '68', 89, 7, '(yaitu) penduduk Iram yang mempunyai bangunan-bangunan yang tinggi,'),
(6001, '089:008', '68', 89, 8, 'yang belum pernah dibangun (suatu kota) seperti itu, di negeri-negeri lain,'),
(6002, '089:009', '68', 89, 9, 'dan kaum Tsamud yang memotong batu-batu besar di lembah,'),
(6003, '089:010', '68', 89, 10, 'dan kaum Fir´aun yang mempunyai pasak-pasak (tentara yang banyak),'),
(6004, '089:011', '68', 89, 11, 'yang berbuat sewenang-wenang dalam negeri,'),
(6005, '089:012', '68', 89, 12, 'lalu mereka berbuat banyak kerusakan dalam negeri itu,'),
(6006, '089:013', '68', 89, 13, 'karena itu Tuhanmu menimpakan kepada mereka cemeti azab,'),
(6007, '089:014', '68', 89, 14, 'sesungguhnya Tuhanmu benar-benar mengawasi.'),
(6008, '089:015', '68', 89, 15, 'Adapun manusia apabila Tuhannya mengujinya lalu dia dimuliakan-Nya dan diberi-Nya kesenangan, maka dia akan berkata: \"Tuhanku telah memuliakanku\".'),
(6009, '089:016', '68', 89, 16, 'Adapun bila Tuhannya mengujinya lalu membatasi rezekinya maka dia berkata: \"Tuhanku menghinakanku\".'),
(6010, '089:017', '68', 89, 17, 'Sekali-kali tidak (demikian), sebenarnya kamu tidak memuliakan anak yatim,'),
(6011, '089:018', '68', 89, 18, 'dan kamu tidak saling mengajak memberi makan orang miskin,'),
(6012, '089:019', '68', 89, 19, 'dan kamu memakan harta pusaka dengan cara mencampur baurkan (yang halal dan yang bathil),'),
(6013, '089:020', '68', 89, 20, 'dan kamu mencintai harta benda dengan kecintaan yang berlebihan.'),
(6014, '089:021', '68', 89, 21, 'Jangan (berbuat demikian). Apabila bumi digoncangkan berturut-turut,'),
(6015, '089:022', '68', 89, 22, 'dan datanglah Tuhanmu; sedang malaikat berbaris-baris.'),
(6016, '089:023', '68', 89, 23, 'Dan pada hari itu diperlihatkan neraka Jahannam; dan pada hari itu ingatlah manusia, akan tetapi tidak berguna lagi mengingat itu baginya.'),
(6017, '089:024', '68', 89, 24, 'Dia mengatakan: \"Alangkah baiknya kiranya aku dahulu mengerjakan (amal saleh) untuk hidupku ini\".'),
(6018, '089:025', '68', 89, 25, 'Maka pada hari itu tiada seorangpun yang menyiksa seperti siksa-Nya.'),
(6019, '089:026', '68', 89, 26, 'dan tiada seorangpun yang mengikat seperti ikatan-Nya.'),
(6020, '089:027', '68', 89, 27, 'Hai jiwa yang tenang.'),
(6021, '089:028', '68', 89, 28, 'Kembalilah kepada Tuhanmu dengan hati yang puas lagi diridhai-Nya.'),
(6022, '089:029', '68', 89, 29, 'Maka masuklah ke dalam jama´ah hamba-hamba-Ku,'),
(6023, '089:030', '68', 89, 30, 'masuklah ke dalam surga-Ku.'),
(6024, '090:001', '68', 90, 1, 'Aku benar-benar bersumpah dengan kota ini (Mekah),'),
(6025, '090:002', '68', 90, 2, 'dan kamu (Muhammad) bertempat di kota Mekah ini,'),
(6026, '090:003', '68', 90, 3, 'dan demi bapak dan anaknya.'),
(6027, '090:004', '68', 90, 4, 'Sesungguhnya Kami telah menciptakan manusia berada dalam susah payah.'),
(6028, '090:005', '68', 90, 5, 'Apakah manusia itu menyangka bahwa sekali-kali tiada seorangpun yang berkuasa atasnya?'),
(6029, '090:006', '68', 90, 6, 'Dan mengatakan: \"Aku telah menghabiskan harta yang banyak\".'),
(6030, '090:007', '68', 90, 7, 'Apakah dia menyangka bahwa tiada seorangpun yang melihatnya?'),
(6031, '090:008', '68', 90, 8, 'Bukankah Kami telah memberikan kepadanya dua buah mata,'),
(6032, '090:009', '68', 90, 9, 'lidah dan dua buah bibir.'),
(6033, '090:010', '68', 90, 10, 'Dan Kami telah menunjukkan kepadanya dua jalan,'),
(6034, '090:011', '68', 90, 11, 'Tetapi dia tiada menempuh jalan yang mendaki lagi sukar.'),
(6035, '090:012', '68', 90, 12, 'Tahukah kamu apakah jalan yang mendaki lagi sukar itu?'),
(6036, '090:013', '68', 90, 13, '(yaitu) melepaskan budak dari perbudakan,'),
(6037, '090:014', '68', 90, 14, 'atau memberi makan pada hari kelaparan,'),
(6038, '090:015', '68', 90, 15, '(kepada) anak yatim yang ada hubungan kerabat,'),
(6039, '090:016', '68', 90, 16, 'atau kepada orang miskin yang sangat fakir.'),
(6040, '090:017', '68', 90, 17, 'Dan dia (tidak pula) termasuk orang-orang yang beriman dan saling berpesan untuk bersabar dan saling berpesan untuk berkasih sayang.'),
(6041, '090:018', '68', 90, 18, 'Mereka (orang-orang yang beriman dan saling berpesan itu) adalah golongan kanan.'),
(6042, '090:019', '68', 90, 19, 'Dan orang-orang yang kafir kepada ayat-ayat Kami, mereka itu adalah golongan kiri.'),
(6043, '090:020', '68', 90, 20, 'Mereka berada dalam neraka yang ditutup rapat.'),
(6044, '091:001', '68', 91, 1, 'Demi matahari dan cahayanya di pagi hari,'),
(6045, '091:002', '68', 91, 2, 'dan bulan apabila mengiringinya,'),
(6046, '091:003', '68', 91, 3, 'dan siang apabila menampakkannya,'),
(6047, '091:004', '68', 91, 4, 'dan malam apabila menutupinya,'),
(6048, '091:005', '68', 91, 5, 'dan langit serta pembinaannya,'),
(6049, '091:006', '68', 91, 6, 'dan bumi serta penghamparannya,'),
(6050, '091:007', '68', 91, 7, 'dan jiwa serta penyempurnaannya (ciptaannya),'),
(6051, '091:008', '68', 91, 8, 'maka Allah mengilhamkan kepada jiwa itu (jalan) kefasikan dan ketakwaannya.'),
(6052, '091:009', '68', 91, 9, 'sesungguhnya beruntunglah orang yang mensucikan jiwa itu,'),
(6053, '091:010', '68', 91, 10, 'dan sesungguhnya merugilah orang yang mengotorinya.'),
(6054, '091:011', '68', 91, 11, '(Kaum) Tsamud telah mendustakan (rasulnya) karena mereka melampaui batas,'),
(6055, '091:012', '68', 91, 12, 'ketika bangkit orang yang paling celaka di antara mereka,'),
(6056, '091:013', '68', 91, 13, 'lalu Rasul Allah (Saleh) berkata kepada mereka: (\"Biarkanlah) unta betina Allah dan minumannya\".'),
(6057, '091:014', '68', 91, 14, 'Lalu mereka mendustakannya dan menyembelih unta itu, maka Tuhan mereka membinasakan mereka disebabkan dosa mereka, lalu Allah menyama-ratakan mereka (dengan tanah),'),
(6058, '091:015', '68', 91, 15, 'dan Allah tidak takut terhadap akibat tindakan-Nya itu.'),
(6059, '092:001', '68', 92, 1, 'Demi malam apabila menutupi (cahaya siang),'),
(6060, '092:002', '68', 92, 2, 'dan siang apabila terang benderang,'),
(6061, '092:003', '68', 92, 3, 'dan penciptaan laki-laki dan perempuan,'),
(6062, '092:004', '68', 92, 4, 'sesungguhnya usaha kamu memang berbeda-beda.'),
(6063, '092:005', '68', 92, 5, 'Adapun orang yang memberikan (hartanya di jalan Allah) dan bertakwa,'),
(6064, '092:006', '68', 92, 6, 'dan membenarkan adanya pahala yang terbaik (surga),'),
(6065, '092:007', '68', 92, 7, 'maka Kami kelak akan menyiapkan baginya jalan yang mudah.'),
(6066, '092:008', '68', 92, 8, 'Dan adapun orang-orang yang bakhil dan merasa dirinya cukup,'),
(6067, '092:009', '68', 92, 9, 'serta mendustakan pahala terbaik,'),
(6068, '092:010', '68', 92, 10, 'maka kelak Kami akan menyiapkan baginya (jalan) yang sukar.'),
(6069, '092:011', '68', 92, 11, 'Dan hartanya tidak bermanfaat baginya apabila ia telah binasa.'),
(6070, '092:012', '68', 92, 12, 'Sesungguhnya kewajiban Kamilah memberi petunjuk,'),
(6071, '092:013', '68', 92, 13, 'dan sesungguhnya kepunyaan Kamilah akhirat dan dunia.'),
(6072, '092:014', '68', 92, 14, 'Maka, kami memperingatkan kamu dengan neraka yang menyala-nyala.'),
(6073, '092:015', '68', 92, 15, 'Tidak ada yang masuk ke dalamnya kecuali orang yang paling celaka,'),
(6074, '092:016', '68', 92, 16, 'yang mendustakan (kebenaran) dan berpaling (dari iman).'),
(6075, '092:017', '68', 92, 17, 'Dan kelak akan dijauhkan orang yang paling takwa dari neraka itu,'),
(6076, '092:018', '68', 92, 18, 'yang menafkahkan hartanya (di jalan Allah) untuk membersihkannya,'),
(6077, '092:019', '68', 92, 19, 'padahal tidak ada seseorangpun memberikan suatu nikmat kepadanya yang harus dibalasnya,'),
(6078, '092:020', '68', 92, 20, 'tetapi (dia memberikan itu semata-mata) karena mencari keridhaan Tuhannya yang Maha Tinggi.'),
(6079, '092:021', '68', 92, 21, 'Dan kelak dia benar-benar mendapat kepuasan.'),
(6080, '093:001', '68', 93, 1, 'Demi waktu matahari sepenggalahan naik,'),
(6081, '093:002', '68', 93, 2, 'dan demi malam apabila telah sunyi (gelap),'),
(6082, '093:003', '68', 93, 3, 'Tuhanmu tiada meninggalkan kamu dan tiada (pula) benci kepadamu.'),
(6083, '093:004', '68', 93, 4, 'Dan sesungguhnya hari kemudian itu lebih baik bagimu daripada yang sekarang (permulaan).'),
(6084, '093:005', '68', 93, 5, 'Dan kelak Tuhanmu pasti memberikan karunia-Nya kepadamu , lalu (hati) kamu menjadi puas.'),
(6085, '093:006', '68', 93, 6, 'Bukankah Dia mendapatimu sebagai seorang yatim, lalu Dia melindungimu?'),
(6086, '093:007', '68', 93, 7, 'Dan Dia mendapatimu sebagai seorang yang bingung, lalu Dia memberikan petunjuk.'),
(6087, '093:008', '68', 93, 8, 'Dan Dia mendapatimu sebagai seorang yang kekurangan, lalu Dia memberikan kecukupan.'),
(6088, '093:009', '68', 93, 9, 'Sebab itu, terhadap anak yatim janganlah kamu berlaku sewenang-wenang.'),
(6089, '093:010', '68', 93, 10, 'Dan terhadap orang yang minta-minta, janganlah kamu menghardiknya.'),
(6090, '093:011', '68', 93, 11, 'Dan terhadap nikmat Tuhanmu, maka hendaklah kamu siarkan.'),
(6091, '094:001', '68', 94, 1, 'Bukankah Kami telah melapangkan untukmu dadamu?,'),
(6092, '094:002', '68', 94, 2, 'dan Kami telah menghilangkan daripadamu bebanmu,'),
(6093, '094:003', '68', 94, 3, 'yang memberatkan punggungmu?'),
(6094, '094:004', '68', 94, 4, 'Dan Kami tinggikan bagimu sebutan (nama)mu,'),
(6095, '094:005', '68', 94, 5, 'Karena sesungguhnya sesudah kesulitan itu ada kemudahan,'),
(6096, '094:006', '68', 94, 6, 'sesungguhnya sesudah kesulitan itu ada kemudahan.'),
(6097, '094:007', '68', 94, 7, 'Maka apabila kamu telah selesai (dari sesuatu urusan), kerjakanlah dengan sungguh-sungguh (urusan) yang lain,'),
(6098, '094:008', '68', 94, 8, 'dan hanya kepada Tuhanmulah hendaknya kamu berharap.'),
(6099, '095:001', '68', 95, 1, 'Demi (buah) Tin dan (buah) Zaitun,'),
(6100, '095:002', '68', 95, 2, 'dan demi bukit Sinai,'),
(6101, '095:003', '68', 95, 3, 'dan demi kota (Mekah) ini yang aman,'),
(6102, '095:004', '68', 95, 4, 'sesungguhnya Kami telah menciptakan manusia dalam bentuk yang sebaik-baiknya.'),
(6103, '095:005', '68', 95, 5, 'Kemudian Kami kembalikan dia ke tempat yang serendah-rendahnya (neraka),'),
(6104, '095:006', '68', 95, 6, 'kecuali orang-orang yang beriman dan mengerjakan amal saleh; maka bagi mereka pahala yang tiada putus-putusnya.'),
(6105, '095:007', '68', 95, 7, 'Maka apakah yang menyebabkan kamu mendustakan (hari) pembalasan sesudah (adanya keterangan-keterangan) itu?'),
(6106, '095:008', '68', 95, 8, 'Bukankah Allah Hakim yang seadil-adilnya?'),
(6107, '096:001', '68', 96, 1, 'Bacalah dengan (menyebut) nama Tuhanmu Yang menciptakan,'),
(6108, '096:002', '68', 96, 2, 'Dia telah menciptakan manusia dari segumpal darah.'),
(6109, '096:003', '68', 96, 3, 'Bacalah, dan Tuhanmulah Yang Maha Pemurah,'),
(6110, '096:004', '68', 96, 4, 'Yang mengajar (manusia) dengan perantaran kalam,'),
(6111, '096:005', '68', 96, 5, 'Dia mengajar kepada manusia apa yang tidak diketahuinya.'),
(6112, '096:006', '68', 96, 6, 'Ketahuilah! Sesungguhnya manusia benar-benar melampaui batas,'),
(6113, '096:007', '68', 96, 7, 'karena dia melihat dirinya serba cukup.'),
(6114, '096:008', '68', 96, 8, 'Sesungguhnya hanya kepada Tuhanmulah kembali(mu).'),
(6115, '096:009', '68', 96, 9, 'Bagaimana pendapatmu tentang orang yang melarang,'),
(6116, '096:010', '68', 96, 10, 'seorang hamba ketika mengerjakan shalat,'),
(6117, '096:011', '68', 96, 11, 'bagaimana pendapatmu jika orang yang melarang itu berada di atas kebenaran,'),
(6118, '096:012', '68', 96, 12, 'atau dia menyuruh bertakwa (kepada Allah)?'),
(6119, '096:013', '68', 96, 13, 'Bagaimana pendapatmu jika orang yang melarang itu mendustakan dan berpaling?'),
(6120, '096:014', '68', 96, 14, 'Tidaklah dia mengetahui bahwa sesungguhnya Allah melihat segala perbuatannya?'),
(6121, '096:015', '68', 96, 15, 'Ketahuilah, sungguh jika dia tidak berhenti (berbuat demikian) niscaya Kami tarik ubun-ubunnya,'),
(6122, '096:016', '68', 96, 16, '(yaitu) ubun-ubun orang yang mendustakan lagi durhaka.'),
(6123, '096:017', '68', 96, 17, 'Maka biarlah dia memanggil golongannya (untuk menolongnya),'),
(6124, '096:018', '68', 96, 18, 'kelak Kami akan memanggil malaikat Zabaniyah,'),
(6125, '096:019', '68', 96, 19, 'sekali-kali jangan, janganlah kamu patuh kepadanya; dan sujudlah dan dekatkanlah (dirimu kepada Tuhan).'),
(6126, '097:001', '68', 97, 1, 'Sesungguhnya Kami telah menurunkannya (Al Quran) pada malam kemuliaan.'),
(6127, '097:002', '68', 97, 2, 'Dan tahukah kamu apakah malam kemuliaan itu?'),
(6128, '097:003', '68', 97, 3, 'Malam kemuliaan itu lebih baik dari seribu bulan.'),
(6129, '097:004', '68', 97, 4, 'Pada malam itu turun malaikat-malaikat dan malaikat Jibril dengan izin Tuhannya untuk mengatur segala urusan.'),
(6130, '097:005', '68', 97, 5, 'Malam itu (penuh) kesejahteraan sampai terbit fajar.'),
(6131, '098:001', '68', 98, 1, 'Orang-orang kafir yakni ahli Kitab dan orang-orang musyrik (mengatakan bahwa mereka) tidak akan meninggalkan (agamanya) sebelum datang kepada mereka bukti yang nyata,'),
(6132, '098:002', '68', 98, 2, '(yaitu) seorang Rasul dari Allah (Muhammad) yang membacakan lembaran-lembaran yang disucikan (Al Quran),'),
(6133, '098:003', '68', 98, 3, 'di dalamnya terdapat (isi) Kitab-kitab yang lurus.'),
(6134, '098:004', '68', 98, 4, 'Dan tidaklah berpecah belah orang-orang yang didatangkan Al Kitab (kepada mereka) melainkan sesudah datang kepada mereka bukti yang nyata.'),
(6135, '098:005', '68', 98, 5, 'Padahal mereka tidak disuruh kecuali supaya menyembah Allah dengan memurnikan ketaatan kepada-Nya dalam (menjalankan) agama yang lurus, dan supaya mereka mendirikan shalat dan menunaikan zakat; dan yang demikian itulah agama yang lurus.'),
(6136, '098:006', '68', 98, 6, 'Sesungguhnya orang-orang yang kafir yakni ahli Kitab dan orang-orang yang musyrik (akan masuk) ke neraka Jahannam; mereka kekal di dalamnya. Mereka itu adalah seburuk-buruk makhluk.'),
(6137, '098:007', '68', 98, 7, 'Sesungguhnya orang-orang yang beriman dan mengerjakan amal saleh, mereka itu adalah sebaik-baik makhluk.'),
(6138, '098:008', '68', 98, 8, 'Balasan mereka di sisi Tuhan mereka ialah surga ´Adn yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Allah ridha terhadap mereka dan merekapun ridha kepada-Nya. Yang demikian itu adalah (balasan) bagi orang yang takut kepada Tuhannya.'),
(6139, '099:001', '68', 99, 1, 'Apabila bumi digoncangkan dengan goncangan (yang dahsyat),'),
(6140, '099:002', '68', 99, 2, 'dan bumi telah mengeluarkan beban-beban berat (yang dikandung)nya,'),
(6141, '099:003', '68', 99, 3, 'dan manusia bertanya: \"Mengapa bumi (menjadi begini)?\",'),
(6142, '099:004', '68', 99, 4, 'pada hari itu bumi menceritakan beritanya,'),
(6143, '099:005', '68', 99, 5, 'karena sesungguhnya Tuhanmu telah memerintahkan (yang sedemikian itu) kepadanya.'),
(6144, '099:006', '68', 99, 6, 'Pada hari itu manusia ke luar dari kuburnya dalam keadaan bermacam-macam, supaya diperlihatkan kepada mereka (balasan) pekerjaan mereka,'),
(6145, '099:007', '68', 99, 7, 'Barangsiapa yang mengerjakan kebaikan seberat dzarrahpun, niscaya dia akan melihat (balasan)nya.'),
(6146, '099:008', '68', 99, 8, 'Dan barangsiapa yang mengerjakan kejahatan sebesar dzarrahpun, niscaya dia akan melihat (balasan)nya pula.'),
(6147, '100:001', '68', 100, 1, 'Demi kuda perang yang berlari kencang dengan terengah-engah,'),
(6148, '100:002', '68', 100, 2, 'dan kuda yang mencetuskan api dengan pukulan (kuku kakinya),'),
(6149, '100:003', '68', 100, 3, 'dan kuda yang menyerang dengan tiba-tiba di waktu pagi,'),
(6150, '100:004', '68', 100, 4, 'maka ia menerbangkan debu,'),
(6151, '100:005', '68', 100, 5, 'dan menyerbu ke tengah-tengah kumpulan musuh,'),
(6152, '100:006', '68', 100, 6, 'sesungguhnya manusia itu sangat ingkar, tidak berterima kasih kepada Tuhannya,'),
(6153, '100:007', '68', 100, 7, 'dan sesungguhnya manusia itu menyaksikan (sendiri) keingkarannya,'),
(6154, '100:008', '68', 100, 8, 'dan sesungguhnya dia sangat bakhil karena cintanya kepada harta.'),
(6155, '100:009', '68', 100, 9, 'Maka apakah dia tidak mengetahui apabila dibangkitkan apa yang ada di dalam kubur,'),
(6156, '100:010', '68', 100, 10, 'dan dilahirkan apa yang ada di dalam dada,'),
(6157, '100:011', '68', 100, 11, 'sesungguhnya Tuhan mereka pada hari itu Maha Mengetahui keadaan mereka.'),
(6158, '101:001', '68', 101, 1, 'Hari Kiamat,'),
(6159, '101:002', '68', 101, 2, 'apakah hari Kiamat itu?'),
(6160, '101:003', '68', 101, 3, 'Tahukah kamu apakah hari Kiamat itu?'),
(6161, '101:004', '68', 101, 4, 'Pada hari itu manusia adalah seperti anai-anai yang bertebaran,'),
(6162, '101:005', '68', 101, 5, 'dan gunung-gunung adalah seperti bulu yang dihambur-hamburkan.'),
(6163, '101:006', '68', 101, 6, 'Dan adapun orang-orang yang berat timbangan (kebaikan)nya,'),
(6164, '101:007', '68', 101, 7, 'maka dia berada dalam kehidupan yang memuaskan.'),
(6165, '101:008', '68', 101, 8, 'Dan adapun orang-orang yang ringan timbangan (kebaikan)nya,'),
(6166, '101:009', '68', 101, 9, 'maka tempat kembalinya adalah neraka Hawiyah.'),
(6167, '101:010', '68', 101, 10, 'Tahukah kamu apakah neraka Hawiyah itu?'),
(6168, '101:011', '68', 101, 11, '(Yaitu) api yang sangat panas.'),
(6169, '102:001', '68', 102, 1, 'Bermegah-megahan telah melalaikan kamu,'),
(6170, '102:002', '68', 102, 2, 'sampai kamu masuk ke dalam kubur.'),
(6171, '102:003', '68', 102, 3, 'Janganlah begitu, kelak kamu akan mengetahui (akibat perbuatanmu itu),'),
(6172, '102:004', '68', 102, 4, 'dan janganlah begitu, kelak kamu akan mengetahui.'),
(6173, '102:005', '68', 102, 5, 'Janganlah begitu, jika kamu mengetahui dengan pengetahuan yang yakin,'),
(6174, '102:006', '68', 102, 6, 'niscaya kamu benar-benar akan melihat neraka Jahiim,'),
(6175, '102:007', '68', 102, 7, 'dan sesungguhnya kamu benar-benar akan melihatnya dengan ´ainul yaqin.'),
(6176, '102:008', '68', 102, 8, 'kemudian kamu pasti akan ditanyai pada hari itu tentang kenikmatan (yang kamu megah-megahkan di dunia itu).'),
(6177, '103:001', '68', 103, 1, 'Demi masa.'),
(6178, '103:002', '68', 103, 2, 'Sesungguhnya manusia itu benar-benar dalam kerugian,'),
(6179, '103:003', '68', 103, 3, 'kecuali orang-orang yang beriman dan mengerjakan amal saleh dan nasehat menasehati supaya mentaati kebenaran dan nasehat menasehati supaya menetapi kesabaran.'),
(6180, '104:001', '68', 104, 1, 'Kecelakaanlah bagi setiap pengumpat lagi pencela,'),
(6181, '104:002', '68', 104, 2, 'yang mengumpulkan harta dan menghitung-hitung,'),
(6182, '104:003', '68', 104, 3, 'dia mengira bahwa hartanya itu dapat mengkekalkannya,'),
(6183, '104:004', '68', 104, 4, 'sekali-kali tidak! Sesungguhnya dia benar-benar akan dilemparkan ke dalam Huthamah.'),
(6184, '104:005', '68', 104, 5, 'Dan tahukah kamu apa Huthamah itu?'),
(6185, '104:006', '68', 104, 6, '(yaitu) api (yang disediakan) Allah yang dinyalakan,'),
(6186, '104:007', '68', 104, 7, 'yang (membakar) sampai ke hati.'),
(6187, '104:008', '68', 104, 8, 'Sesungguhnya api itu ditutup rapat atas mereka,'),
(6188, '104:009', '68', 104, 9, '(sedang mereka itu) diikat pada tiang-tiang yang panjang.'),
(6189, '105:001', '68', 105, 1, 'Apakah kamu tidak memperhatikan bagaimana Tuhanmu telah bertindak terhadap tentara bergajah?'),
(6190, '105:002', '68', 105, 2, 'Bukankah Dia telah menjadikan tipu daya mereka (untuk menghancurkan Ka´bah) itu sia-sia?'),
(6191, '105:003', '68', 105, 3, 'dan Dia mengirimkan kapada mereka burung yang berbondong-bondong,'),
(6192, '105:004', '68', 105, 4, 'yang melempari mereka dengan batu (berasal) dari tanah yang terbakar,'),
(6193, '105:005', '68', 105, 5, 'lalu Dia menjadikan mereka seperti daun-daun yang dimakan (ulat).'),
(6194, '106:001', '68', 106, 1, 'Karena kebiasaan orang-orang Quraisy,'),
(6195, '106:002', '68', 106, 2, '(yaitu) kebiasaan mereka bepergian pada musim dingin dan musim panas.'),
(6196, '106:003', '68', 106, 3, 'Maka hendaklah mereka menyembah Tuhan Pemilik rumah ini (Ka´bah).'),
(6197, '106:004', '68', 106, 4, 'Yang telah memberi makanan kepada mereka untuk menghilangkan lapar dan mengamankan mereka dari ketakutan.'),
(6198, '107:001', '68', 107, 1, 'Tahukah kamu (orang) yang mendustakan agama?'),
(6199, '107:002', '68', 107, 2, 'Itulah orang yang menghardik anak yatim,'),
(6200, '107:003', '68', 107, 3, 'dan tidak menganjurkan memberi makan orang miskin.'),
(6201, '107:004', '68', 107, 4, 'Maka kecelakaanlah bagi orang-orang yang shalat,'),
(6202, '107:005', '68', 107, 5, '(yaitu) orang-orang yang lalai dari shalatnya,'),
(6203, '107:006', '68', 107, 6, 'orang-orang yang berbuat riya,'),
(6204, '107:007', '68', 107, 7, 'dan enggan (menolong dengan) barang berguna.'),
(6205, '108:001', '68', 108, 1, 'Sesungguhnya Kami telah memberikan kepadamu nikmat yang banyak.'),
(6206, '108:002', '68', 108, 2, 'Maka dirikanlah shalat karena Tuhanmu; dan berkorbanlah.'),
(6207, '108:003', '68', 108, 3, 'Sesungguhnya orang-orang yang membenci kamu dialah yang terputus.'),
(6208, '109:001', '68', 109, 1, 'Katakanlah: \"Hai orang-orang kafir,'),
(6209, '109:002', '68', 109, 2, 'Aku tidak akan menyembah apa yang kamu sembah.'),
(6210, '109:003', '68', 109, 3, 'Dan kamu bukan penyembah Tuhan yang aku sembah.'),
(6211, '109:004', '68', 109, 4, 'Dan aku tidak pernah menjadi penyembah apa yang kamu sembah,'),
(6212, '109:005', '68', 109, 5, 'dan kamu tidak pernah (pula) menjadi penyembah Tuhan yang aku sembah.'),
(6213, '109:006', '68', 109, 6, 'Untukmu agamamu, dan untukkulah, agamaku\".'),
(6214, '110:001', '68', 110, 1, 'Apabila telah datang pertolongan Allah dan kemenangan,'),
(6215, '110:002', '68', 110, 2, 'dan kamu lihat manusia masuk agama Allah dengan berbondong-bondong,'),
(6216, '110:003', '68', 110, 3, 'maka bertasbihlah dengan memuji Tuhanmu dan mohonlah ampun kepada-Nya. Sesungguhnya Dia adalah Maha Penerima taubat.'),
(6217, '111:001', '68', 111, 1, 'Binasalah kedua tangan Abu Lahab dan sesungguhnya dia akan binasa.'),
(6218, '111:002', '68', 111, 2, 'Tidaklah berfaedah kepadanya harta bendanya dan apa yang ia usahakan.'),
(6219, '111:003', '68', 111, 3, 'Kelak dia akan masuk ke dalam api yang bergejolak.'),
(6220, '111:004', '68', 111, 4, 'Dan (begitu pula) istrinya, pembawa kayu bakar.'),
(6221, '111:005', '68', 111, 5, 'Yang di lehernya ada tali dari sabut.'),
(6222, '112:001', '68', 112, 1, 'Katakanlah: \"Dialah Allah, Yang Maha Esa.'),
(6223, '112:002', '68', 112, 2, 'Allah adalah Tuhan yang bergantung kepada-Nya segala sesuatu.'),
(6224, '112:003', '68', 112, 3, 'Dia tiada beranak dan tidak pula diperanakkan,'),
(6225, '112:004', '68', 112, 4, 'dan tidak ada seorangpun yang setara dengan Dia\".'),
(6226, '113:001', '68', 113, 1, 'Katakanlah: \"Aku berlindung kepada Tuhan Yang Menguasai subuh,'),
(6227, '113:002', '68', 113, 2, 'dari kejahatan makhluk-Nya,'),
(6228, '113:003', '68', 113, 3, 'dan dari kejahatan malam apabila telah gelap gulita,'),
(6229, '113:004', '68', 113, 4, 'dan dari kejahatan wanita-wanita tukang sihir yang menghembus pada buhul-buhul,'),
(6230, '113:005', '68', 113, 5, 'dan dari kejahatan pendengki bila ia dengki\".'),
(6231, '114:001', '68', 114, 1, 'Katakanlah: \"Aku berlindung kepada Tuhan (yang memelihara dan menguasai) manusia.'),
(6232, '114:002', '68', 114, 2, 'Raja manusia.'),
(6233, '114:003', '68', 114, 3, 'Sembahan manusia.'),
(6234, '114:004', '68', 114, 4, 'Dari kejahatan (bisikan) syaitan yang biasa bersembunyi,'),
(6235, '114:005', '68', 114, 5, 'yang membisikkan (kejahatan) ke dalam dada manusia,'),
(6236, '114:006', '68', 114, 6, 'dari (golongan) jin dan manusia.');
